(window.webpackJsonp=window.webpackJsonp||[]).push([[833],{856:function(p){p.exports={chapterTitle:null,title:"\u7b2c830\u7ae0 \u9996\u5356\u4f1a",content:"<article><h2>\u7b2c830\u7ae0 \u9996\u5356\u4f1a</h2><p>\u3000\u3000\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u5f15\u8d77\u56fd\u9645\u793e\u4f1a\u6781\u5927\u7684\u5173\u6ce8\u4ee5\u53ca\u5168\u4e16\u754c\u534e\u4eba\u7fa4\u4f53\u5f3a\u70c8\u7684\u6124\u6168\uff0c\u5404\u5730\u7684\u6297\u8bae\u3001\u793a\u5a01\u6d3b\u52a8\u6b64\u8d77\u5f7c\u4f0f\uff0c\u7f8e\u56fd\u653f\u5e9c\u4e5f\u6b63\u5f0f\u8ba4\u5b9a5\u67089\u65e5\u53d1\u751f\u5728\u5370\u5c3c\u96c5\u52a0\u8fbe\u4ee5\u5176\u4ed6\u5730\u533a\u7684\u6392\u534e\u9a9a\u4e71\u4e3a\u4e25\u91cd\u7684\u79cd\u65cf\u6b67\u89c6\u707e\u96be\uff0c\u6b63\u5f0f\u51b3\u5b9a\u63a5\u53d7\u90e8\u5206\u5370\u5c3c\u534e\u4eba\u7684\u201c\u907f\u96be\u201d\u7533\u8bf7\uff0c\u77ed\u77ed\u51e0\u5929\u65f6\u95f4\u91cc\uff0c\u5df2\u6709\u8fd1\u4e94\u4e07\u5370\u5c3c\u534e\u4eba\u5df2\u7ecf\u8f6c\u79fb\u5230\u65b0\u52a0\u5761\u3001\u6cf0\u56fd\u3001\u9a6c\u6765\u897f\u4e9a\u7b49\u56fd\u6682\u65f6\u907f\u96be\u3002</p><p>\u3000\u30005\u670815\u65e5\uff0c\u53f6\u81fb\u6c11\u4ee3\u8868\u5916\u7ecf\u8d38\u90e8\u524d\u5f80\u6cf0\u56fd\u8fdb\u884c\u7ecf\u63f4\u5408\u4f5c\u8bbf\u95ee\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5efa\u3001\u90ed\u677e\u5ef6\u3001\u9648\u6587\u806a\u7b49\u4eba\u53e6\u9053\u968f\u884c\uff0c\u5c06\u5728\u6cf0\u56fd\u66fc\u8c37\u5b89\u6392\u5370\u5c3c\u4ee5\u5176\u4ed6\u4e1c\u5357\u4e9a\u5730\u533a\u7684\u534e\u5546\u4ee3\u8868\u4e0e\u53f6\u81fb\u6c11\u4f1a\u9762\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e2\u6ca1\u6709\u968f\u884c\uff0c\u4e5f\u6ca1\u6709\u6025\u7740\u79bb\u5f00\u9999\u6e2f\u3002</p><p>\u3000\u3000\u4e3a\u7231\u8fbe\u65b0\u6b3e\u624b\u673ai198\u5728\u9999\u6e2f5\u670816\u65e5\u5468\u516d\u7684\u9996\u5356\u4f1a\uff0c\u9999\u6e2f\u5206\u516c\u53f8\u8fd9\u8fb9\u7b79\u5907\u4e86\u76db\u5927\u7684\u6d3b\u52a8\uff0c\u96c6\u56e2\u5206\u7ba1\u79fb\u52a8\u901a\u8baf\u4e8b\u4e1a\u7684\u526f\u603b\u88c1\u9c81\u5e86\u751f\u4e3a\u6b64\u4e13\u7a0b\u8d76\u5230\u9999\u6e2f\u53c2\u52a0\u6d3b\u52a8\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u541d\u556c\u5728\u9999\u6e2f\u591a\u9017\u7559\u4e24\u5929\u3002</p><p>\u3000\u3000\u4e94\u6708\u521d\uff0c\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u7684\u8054\u4fe1\u3001\u8054\u8baf\u4e24\u5bb6\u624b\u673a\u4f01\u4e1a\u76f8\u7eed\u63a8\u51fa\u56db\u6b3e\u65b0\u6b3e\u624b\u673a\uff0c\u9526\u6e56\u968f\u5373\u5728\u9999\u6e2f\u6b63\u5f0f\u542f\u52a8\u65b0\u6b3e\u624b\u673ai198\u7684\u9884\u8d2d\u4e0e\u5e7f\u544a\u5ba3\u4f20\u3002</p><p>\u3000\u3000\u5728\u8fc7\u53bb\u7684\u4e8c\u5468\u65f6\u95f4\u5185\uff0ci198\u5728\u9999\u6e2f\u901a\u8fc7\u5408\u4f5c\u9500\u552e\u5546\u4e30\u6cfd\u7535\u5668\u7684\u9884\u8d2d\u9500\u91cf\u7a81\u78341000\u53f0\uff0c\u8fd9\u6b21\u7684\u9996\u5356\u4f1a\uff0c\u9999\u6e2f\u5206\u516c\u53f8\u5728\u4e2d\u73af\u7684\u91d1\u878d\u73af\u7403\u4e2d\u5927\u53a6\u5185\u4e3a\u901a\u8fc7\u4e30\u6cfd\u7535\u5668\u9996\u6279i198\u624b\u673a\u9884\u8d2d\u7528\u6237\u4e3e\u884c\u9686\u91cd\u7684\u9996\u5356\u4f1a\u3002</p><p>\u3000\u3000\u9999\u6e2f\u5206\u516c\u53f8\u5728\u6d3b\u52a8\u73b0\u573a\u7cbe\u5fc3\u51c6\u5907\u4e86\u7cbe\u5f69\u7684\u7235\u58eb\u4e50\u961f\u6f14\u51fa\u4e0e\u65f6\u5c1a\u54c1\u9274\u9152\u4f1a\u6765\u9020\u52bf\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u8d76\u5230\u73b0\u573a\uff0c\u4f17\u591a\u4e3a\u4e86\u7b2c\u4e00\u65f6\u95f4\u62ff\u5230\u7231\u8fbei198\u7684\u9884\u8d2d\u7528\u6237\u5df2\u7ecf\u8ba9\u6d3b\u52a8\u73b0\u573a\u53d8\u5f97\u6709\u4e9b\u62e5\u6324\u3002\u6d3b\u52a8\u4e4b\u521d\u5e76\u6ca1\u6709\u63d0\u5230\u6c5f\u9edb\u513f\u4f1a\u4f5c\u4e3a\u7279\u9080\u5609\u5bbe\u4eb2\u4e34\u6d3b\u52a8\u73b0\u573a\uff0c\u4f46\u662f\u6c5f\u9edb\u513f\u7684\u5230\u6765\u8ba9\u6d3b\u52a8\u73b0\u573a\u53d8\u5f97\u70ed\u95f9\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u6ca1\u6709\u53bb\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u8981\u6253\u62db\u547c\u7b49\u4f1a\u513f\u81ea\u6709\u673a\u4f1a\uff0c\u8d70\u5230\u4e00\u8fb9\u6446\u6ee1\u7ea2\u767d\u8461\u8404\u9152\u4e0e\u9999\u69df\u8fd8\u6709\u7cbe\u7f8e\u8336\u70b9\u7684\u9910\u684c\u65c1\u3002\u6d3b\u52a8\u73b0\u573a\u867d\u8bf4\u6709\u4e9b\u62e5\u6324\uff0c\u4f46\u662f\u5e76\u4e0d\u6df7\u4e71\uff0c\u6bd5\u7adf\u901a\u8fc7\u4e30\u6cfd\u7535\u5668\u63d0\u524d\u9884\u8d2d\u7231\u8fbe\u65b0\u6b3e\u624b\u673a\u7684\u7528\u6237\u767d\u9886\u4e0e\u7cbe\u82f1\u9636\u5c42\u5c45\u591a\uff0c\u5373\u4f7f\u6765\u5bbe\u4e2d\u6709\u8bb8\u591a\u6c5f\u9edb\u513f\u7684\u6b4c\u8ff7\uff0c\u4f46\u90fd\u4e0d\u4f1a\u7279\u522b\u65e0\u5398\u5934\u7684\u72c2\u70ed\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u5404\u53d6\u4e86\u4e00\u676f\u7ea2\u9152\uff0c\u6070\u597d\u770b\u5230\u5b59\u9759\u9999\u4e5f\u5728\u573a\u5185\uff0c\u7ad9\u5728\u89d2\u843d\u91cc\u8ddf\u848b\u8587\u8bf4\u7740\u8bdd\u3002\u5510\u5a67\u5174\u594b\u7684\u62ac\u624b\u671d\u90a3\u8fb9\u6325\u4e86\u6325\uff0c\u4e4b\u524d\u6ca1\u6709\u901a\u7535\u8bdd\uff0c\u4e5f\u4e0d\u77e5\u9053\u4f1a\u5728\u8fd9\u91cc\u9047\u5230\u8c01\uff0c\u4e0a\u697c\u4e4b\u524d\uff0c\u5510\u5a67\u8fd8\u5a01\u80c1\u8fc7\u5f20\u606a\uff1a\u201c\u8981\u662f\u770b\u5230\u8bb8\u601d\uff0c\u4f60\u8981\u8ba9\u6211\u54ac\u4e24\u53e3\u3002\u201d</p><p>\u3000\u3000\u8fd8\u597d\u8bb8\u601d\u4eca\u5929\u53e6\u6709\u4e8b\u60c5\u803d\u6401\u4e86\uff0c\u5f20\u606a\u5012\u4e0d\u7528\u62c5\u5fc3\u7ed9\u5510\u5a67\u54ac\u3002</p><p>\u3000\u3000\u9999\u6e2f\u5206\u516c\u53f8\u5c06\u9996\u5356\u4f1a\u641e\u6210\u65f6\u5c1a\u54c1\u9274\u9152\u4f1a\u7684\u5f62\u5f0f\uff0c\u9664\u4e86\u6709\u65f6\u95f4\u6765\u53c2\u52a0\u6d3b\u52a8\u7684\u9884\u8d2d\u7528\u6237\u4e4b\u5916\uff0c\u4e5f\u9080\u8bf7\u5546\u754c\u3001\u65f6\u5c1a\u754c\u4e0e\u5a31\u4e50\u5708\u7684\u4e00\u4e9b\u540d\u4eba\u4e3a\u4eca\u5929\u7684\u9996\u5356\u4f1a\u9020\u52bf\u3002\u5728\u523b\u610f\u8425\u9020\u7684\u6e29\u99a8\u9152\u4f1a\u6c1b\u56f4\u91cc\uff0c\u5012\u6ca1\u6709\u5e26\u8fdb\u6765\u592a\u591a\u5173\u4e8e\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u60b2\u6124\u60c5\u7eea\uff0c\u6bd5\u7adf\u6b63\u5e38\u7684\u751f\u6d3b\u8ddf\u751f\u610f\u8fd8\u8981\u8fdb\u884c\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u624b\u7aef\u7740\u76db\u7740\u4e09\u5206\u4e4b\u4e00\u5ae3\u7ea2\u9152\u6db2\u7684\u9ad8\u811a\u73bb\u7483\u6000\u4e0e\u848b\u8587\u4e00\u8d77\u8d70\u8fc7\u6765\uff0c\u5fae\u7b11\u5e26\u7740\u4e9b\u8be7\u5f02\u7684\u8bf4\u9053\uff1a\u201c\u53ef\u6ca1\u6709\u4eba\u544a\u8bc9\u6211\u8bf4\u4f60\u4f1a\u4eb2\u4e34\u73b0\u573a\u2026\u2026\u201d\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u554a\uff0c\u6211\u80fd\u591f\u60f3\u5230\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5e78\u597d\u4e0d\u662f\u5728\u9910\u5385\u9047\u5230\u3001\u4f60\u8be7\u5f02\u7684\u770b\u5230\u6211\u8fd8\u4f1a\u4eb2\u81ea\u7528\u9910\uff0c\u201d\u5f20\u606a\u77e5\u9053\u5b59\u9759\u9999\u540e\u4e00\u53e5\u8bdd\u662f\u8bf4\u8bb8\u601d\u6ca1\u6709\u51fa\u5e2d\u4eca\u5929\u7684\u6d3b\u52a8\uff0c\u8fd8\u597d\u5510\u5a67\u8ddf\u8bb8\u601d\u5df2\u7ecf\u4e92\u76f8\u9ed8\u8ba4\u4e86\u5bf9\u65b9\u7684\u5b58\u5728\uff0c\u4ed6\u7b11\u7740\u63a9\u9970\u8fc7\u53bb\u8bf4\u9053\uff0c\u201c\u660e\u5929\u624d\u8981\u79bb\u5f00\u9999\u6e2f\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u5730\u65b9\u53ef\u4ee5\u6253\u53d1\u65f6\u95f4\uff0c\u5c31\u8ddf\u5c0f\u5a67\u6765\u8fd9\u91cc\u770b\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u4f5c\u4e3a\u5e94\u9080\u5609\u5bbe\uff0c\u5979\u4e0e\u848b\u8587\u90fd\u76db\u88c5\u51fa\u5e2d\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u8863\u7740\u6bd4\u8f83\u968f\u4fbf\uff0c\u5c31\u50cf\u4eca\u5929\u8fc7\u6765\u9886\u624b\u673a\u7684\u9884\u8d2d\u7528\u6237\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u4e16\u7eaa\u534e\u97f3\u7b97\u662f\u6709\u4e86\u68f5\u6447\u94b1\u6811\u2026\u2026\u201d\u5f20\u606a\u770b\u7740\u7ed9\u6b4c\u8ff7\u56f4\u5728\u4e2d\u95f4\u3001\u9700\u8981\u73b0\u573a\u4fdd\u5b89\u6765\u7ef4\u6301\u6b21\u5e8f\u7684\u6c5f\u9edb\u513f\uff0c\u7b11\u7740\u8ddf\u5b59\u9759\u9999\u8bf4\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u7684\u7b2c\u4e00\u5f20\u4e2a\u4eba\u4e13\u8f91\u300a\u51ac\u5b63\u6d77\u7684\u96ea\u300b\u6df7\u6742\u7740\u56fd\u8bed\u3001\u7ca4\u8bed\u6b4c\u66f2\uff0c\u867d\u7136\u6709\u7740\u76f8\u5f53\u4e0d\u9519\u7684\u6c34\u51c6\uff0c\u672c\u53ef\u4ee5\u5f97\u5230\u5e02\u573a\u7684\u8ba4\u540c\uff0c\u4f46\u662f\u4e5d\u516d\u5e74\u5e95\u7684\u97f3\u4e50\u5e02\u573a\u7ade\u4e89\u6fc0\u70c8\uff0c\u800c\u4f5c\u4e3a\u521a\u521a\u51fa\u9053\u7684\u4e16\u7eaa\u534e\u97f3\u80fd\u591f\u52a8\u7528\u7684\u8d44\u91d1\u6709\u9650\uff0c\u5728\u5e02\u573a\u5ba3\u4f20\u4e0e\u5305\u88c5\u4e0a\u7684\u7ecf\u9a8c\u4e5f\u4e0d\u591f\uff0c\u6c5f\u9edb\u513f\u5e76\u6ca1\u6709\u80fd\u591f\u505a\u5230\u4e00\u70ae\u800c\u7ea2\uff0c\u53ea\u80fd\u8bf4\u4e3a\u65e5\u540e\u7684\u6f14\u827a\u751f\u6daf\u5960\u5b9a\u4e00\u4e2a\u76f8\u5f53\u4e0d\u9519\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u6709\u7740\u660e\u8273\u7167\u4eba\u7684\u5076\u50cf\u5916\u8c8c\uff0c\u9ad8\u6311\u7684\u8eab\u6750\uff0c\u7075\u52a8\u800c\u5145\u6ee1\u4e2a\u4eba\u6c14\u8d28\u7684\u773c\u7738\uff0c\u58f0\u97f3\u7684\u8d28\u5730\u6e05\u4eae\u800c\u5bcc\u6709\u7279\u8d28\uff0c\u5531\u529f\u4e5f\u597d\uff0c\u54ea\u6709\u53ef\u80fd\u4e0d\u7ea2\uff1f\u4e5d\u4e03\u5e74\u4e0b\u534a\u5e74\uff0c\u83b7\u5f97\u5bb6\u65cf\u8d44\u91d1\u652f\u6301\u7684\u4e16\u7eaa\u534e\u97f3\u6709\u66f4\u5927\u7684\u9009\u62e9\u4f59\u5730\u6536\u8d2d\u4f18\u79c0\u7684\u539f\u521b\u97f3\u4e50\uff0c\u5b59\u9759\u9999\u4e5f\u4e0b\u5b9a\u51b3\u5fc3\u5c06\u6881\u601d\u9a8f\u3001\u6234\u6587\u9f50\u3001\u6f58\u534f\u5e86\u7b49\u9999\u6e2f\u8457\u540d\u97f3\u4e50\u4eba\u6536\u7f57\u5230\u4e16\u7eaa\u534e\u97f3\u65d7\u4e0b\uff0c\u505a\u5927\u4e16\u7eaa\u534e\u97f3\u7684\u97f3\u4e50\u4e1a\u52a1\u3002\u4e3a\u6c5f\u9edb\u513f\u7684\u97f3\u4e50\u751f\u6daf\u89c4\u5212\u7684\u65b9\u5411\u4e5f\u662f\u770b\u51c6\u6574\u4e2a\u4e9a\u6d32\u534e\u8bed\u97f3\u4e50\u5e02\u573a\uff0c\u653e\u5f03\u7ca4\u8bed\u6b4c\u66f2\u3002\u867d\u8bf4\u662f\u65e5\u540e\u53ea\u9488\u5bf9\u534e\u8bed\u97f3\u4e50\u5e02\u573a\uff0c\u4e5f\u6ca1\u6709\u523b\u610f\u7684\u8ba1\u5212\u7740\u53bb\u8d70\u4ec0\u4e48\u8def\u7ebf\u6216\u8005\u90e8\u7f72\uff0c\u4e0d\u65ad\u7684\u542c\u6b4c\u3001\u62e3\u6b4c\uff0c\u5b59\u9759\u9999\u751a\u81f3\u653e\u4efb\u6c5f\u9edb\u513f\u53c2\u4e0e\u5230\u6b4c\u66f2\u7684\u521b\u4f5c\u4e2d\u53bb\uff0c\u5f55\u97f3\u65f6\u4e5f\u6839\u636e\u6c5f\u9edb\u513f\u5f53\u65f6\u7684\u60c5\u7eea\u53bb\u5236\u4f5c\uff0c\u5e26\u7740\u6c5f\u9edb\u513f\u5f3a\u70c8\u4e2a\u4eba\u98ce\u683c\u7684\u7b2c\u4e8c\u5f20\u4e13\u8f91\u300a\u6267\u8ff7\u300b\u4e00\u7ecf\u63a8\u51fa\u4fbf\u5728\u534e\u8bed\u97f3\u4e50\u5e02\u573a\u83b7\u5f97\u6781\u4f73\u7684\u53cd\u54cd\uff0c\u6c5f\u9edb\u513f\u4e5f\u662f\u4ee5\u8fd9\u5f20\u4e13\u8f91\u5f7b\u5e95\u8f6c\u578b\u6210\u529f\uff0c\u5728\u666e\u901a\u4eba\u7684\u5370\u8c61\u91cc\u4e0d\u518d\u662f\u5e7f\u544a\u6a21\u7279\u4e86\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u534e\u97f3\u5f53\u7136\u4e5f\u83b7\u5f97\u6781\u597d\u7684\u5e02\u573a\u56de\u62a5\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5b59\u9759\u9999\u7b11\u8d77\u6765\uff0c\u6253\u5f00\u7cbe\u81f4\u7684CK\u5764\u5305\uff0c\u62ff\u51fa\u9526\u6e56\u8fd9\u6b21\u53d1\u552e\u7684\u65b0\u6b3e\u624b\u673a\uff0c\u5728\u624b\u91cc\u626c\u4e86\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u54ea\u53ca\u5f97\u4e0a\u8fd9\u68f5\u6447\u94b1\u6811\u6765\u94b1\u4fbf\u5f53\uff1f\u201d\u53c8\u95ee\u5510\u5a67\uff0c\u201c\u8fd9\u6b21\u4ed6\u662f\u4e0d\u662f\u53c8\u7ed9\u4f60\u7279\u6b8a\u7684\u5f85\u9047\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u53ea\u624b\u673a\u6211\u8fd8\u6ca1\u6709\u7528\u817b\u5473\u5462\u3002\u201d\u5510\u5a67\u7b11\u7740\u8bf4\uff0c\u5979\u7684\u90a3\u53ea\u624b\u673a\u53ea\u662f\u7ed9\u5f20\u606a\u62ff\u53bb\u8fdb\u884c\u8f6f\u786c\u4ef6\u5347\u7ea7\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u7fa1\u6155\u554a\uff0c\u6211\u8fd9\u53ea\u624b\u673a\u8fd8\u662f\u848b\u59d0\u8d70\u540e\u95e8\u9001\u7ed9\u6211\u7684\uff0c\u53ef\u60dc\u4e5f\u4e0d\u662f\u72ec\u4e00\u65e0\u4e8c\u554a\uff0c\u201d\u5b59\u9759\u9999\u7b11\u7740\u5c06\u624b\u673a\u6536\u8d77\u6765\uff0c\u201c\u8fd8\u662f\u4e0d\u62ff\u51fa\u6765\u4e22\u4eba\u73b0\u773c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8ba9\u848b\u8587\u4ee5\u540e\u518d\u4e5f\u4e0d\u9001\u65b0\u624b\u673a\u7ed9\u5b59\u59d0\u4f60\u4e86\uff0c\u5373\u4f7f\u4e0d\u80fd\u8bf4\u662f\u7a7a\u524d\uff0c\u4e5f\u7b97\u67d0\u79cd\u610f\u4e49\u4e0a\u7684\u7edd\u540e\u554a\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u624b\u91cc\u7684\u8fd9\u53ea\u624b\u673a\u662fi198\u7684\u9650\u91cf\u7eaa\u5ff5\u7248\uff0c\u6a59\u7ea2\u8272\u955c\u9762\u94a2\u7434\u9762\u677f\u4f3c\u4e4e\u84b8\u9540\u4e86\u4e00\u5c42\u8584\u8584\u7684\u91d1\u5c5e\u819c\uff0c\u6bd4\u8d77\u6b63\u5e38\u53d1\u552e\u7684\u65b0\u6b3e\u624b\u673a\uff0c\u5728\u4e8e\u6570\u5b57\u952e\u4e0b\u4e0d\u591a\u6dfb\u7f6e\u6709\u4e24\u7c92LED\u5149\u6e90\uff0c\u5728\u706f\u5149\u660f\u6697\u91cc\u6253\u5f00\u624b\u673a\u6216\u63a5\u542c\u7535\u8bdd\u91cc\uff0c\u4e24\u7c92\u4e0d\u540c\u989c\u8272\u7684LED\u5149\u6e90\u4f1a\u5206\u522b\u4eae\u8d77\uff0c\u4e0e\u84dd\u8272\u5c4f\u5e55\u4ea4\u8f89\u76f8\u6620\uff0c\u65e2\u8000\u773c\u53c8\u7f8e\u89c2\u3002\u7531\u4e8eLED\u5149\u6e90\u5bf9\u7535\u6e90\u7684\u6d88\u8017\u6709\u4e9b\u5927\uff0c\u9650\u91cf\u7248i198\u91c7\u7528\u4e1c\u829d\u6700\u65b0\u7684\u9502\u805a\u5408\u7269\u7535\u6c60\uff0c\u6574\u673a\u552e\u4ef7\u8d85\u8fc7\u4e00\u4e07\u6e2f\u5e01\uff0c\u662f\u9526\u6e56\u8bd5\u6c34\u9999\u6e2f\u9ad8\u7aef\u624b\u673a\u5e02\u573a\u7684\u4f5c\u54c1\u3002</p><p>\u3000\u3000\u201c\u5475\u2026\u2026\u201d\u5728\u4e00\u65c1\u7684\u848b\u8587\u7a81\u7136\u8f7b\u7b11\u8d77\u6765\uff0c\u773c\u7738\u5b50\u671b\u7740\u5165\u53e3\u5904\uff0c\u795e\u8272\u8fd8\u6709\u4e9b\u8be1\u5f02\u3002</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u5934\u770b\u8fc7\u53bb\uff0c\u8d6b\u7136\u770b\u89c1\u674e\u5728\u6d19\u4e0e\u674e\u99a8\u4e88\u5802\u5144\u59b9\u7ad9\u5728\u4e0d\u8fdc\u7684\u5165\u53e3\u5904\uff0c\u848b\u8587\u5f53\u7136\u4e0d\u8ba4\u8bc6\u674e\u99a8\u4e88\uff0c\u4f46\u662f\u674e\u99a8\u4e88\u6b23\u559c\u7684\u671d\u5f20\u606a\u770b\u8fc7\u6765\u7684\u773c\u795e\u4e0e\u90a3\u6b32\u626c\u624b\u6253\u62db\u547c\u800c\u53c8\u6b62\u7684\u77db\u76fe\u795e\u8272\u8ba9\u848b\u8587\u770b\u5728\u773c\u91cc\uff0c\u81ea\u7136\u77e5\u9053\u5927\u8001\u677f\u8ddf\u51fa\u73b0\u5728\u5165\u53e3\u5904\u7684\u8fd9\u4e2a\u7edd\u8272\u9753\u4e3d\u7684\u5c11\u5973\u6709\u7740\u6781\u719f\u7edc\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5927\u6982\u662f\u770b\u5230\u5510\u5a67\u7ad9\u5728\u5f20\u606a\u8eab\u8fb9\u7684\u7f18\u6545\uff0c\u674e\u99a8\u4e88\u6536\u655b\u8d77\u4e4d\u9047\u6545\u4eba\u7684\u60ca\u559c\u795e\u8272\uff0c\u53ea\u5728\u5f20\u606a\u8f6c\u8138\u770b\u8fc7\u53bb\u65f6\u5ae3\u7136\u4e00\u7b11\uff0c\u7b97\u662f\u76f8\u8bc6\u8005\u4e4b\u95f4\u7684\u5e73\u8c08\u7b11\u5bb9\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u3001\u5510\u5a67\u90fd\u80cc\u5bf9\u7740\u5165\u53e3\u5904\u800c\u7ad9\uff0c\u770b\u5230\u53ea\u662f\u674e\u99a8\u4e88\u5e73\u6de1\u7684\u7b11\u5bb9\uff0c\u89c9\u5f97\u848b\u8587\u521a\u624d\u7684\u7b11\u5bb9\u6709\u4e9b\u8be1\u5f02\uff0c\u5979\u4eec\u4e5f\u4e0d\u8ba4\u8bc6\u674e\u99a8\u4e88\uff0c\u8fd8\u4ee5\u4e3a\u674e\u99a8\u4e88\u662f\u5728\u8ddf\u848b\u8587\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u4f60\u8ba4\u8bc6\u90a3\u4e2a\u5973\u5b69\u5b50\uff1f\u771f\u6f02\u4eae\uff0c\u4eca\u5929\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u592a\u591a\u4e86\uff0c\u90fd\u663e\u5f97\u6211\u4eec\u6709\u51cb\u96f6\u7684\u60b2\u54c0\uff0c\u201d\u5b59\u9759\u9999\u5f00\u73a9\u7b11\u8bf4\u9053\uff0c\u201c\u5c31\u7b97\u4f60\u8ba4\u8bc6\uff0c\u4e5f\u5343\u4e07\u4e0d\u8981\u5e2e\u5f20\u606a\u4ecb\u7ecd\uff01\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u8fd9\u4e48\u8bf4\u7740\uff0c\u5510\u5a67\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u770b\u7740\u5f20\u606a\u5634\u89d2\u8fd8\u6d6e\u7740\u7b11\u610f\uff0c\u8bf4\u9053\uff1a\u201c\u770b\uff0c\u4ed6\u5012\u662f\u5fc3\u52a8\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u7537\u7684\u662f\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u7684\u4e13\u52a1\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u4ed6\u5728\u9999\u6e2f\u4f1a\u8dd1\u5230\u8fd9\u91cc\u6765\u51d1\u70ed\u95f9\uff0c\u201d\u5f20\u606a\u9a6c\u4e0a\u53c8\u6362\u4e86\u7a0d\u5f31\u7684\u8bed\u6c14\uff0c\u201c\u90a3\u5973\u5b69\u5b50\u4e5f\u662f\u4e09\u661f\u7684\u2026\u2026\u201d\u542c\u4e0a\u53bb\u65e2\u50cf\u9648\u8ff0\u8bed\u6c14\uff0c\u53c8\u50cf\u7591\u95ee\u8bed\u6c14\u3002</p><p>\u3000\u3000\u848b\u8587\u8fd8\u4e0d\u4f1a\u62c6\u5f20\u606a\u7684\u53f0\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u6279\u91cf\u9884\u8d2d\u7684\u7528\u6237\u5012\u51fa\u73b0\u4e86\u597d\u51e0\u5bb6\uff0c\u8fd9\u8ba9\u6211\u4eec\u4e4b\u524d\u4e00\u5343\u53f0\u7684\u9884\u8d2d\u9500\u91cf\u4e1a\u7ee9\u5b9e\u9645\u4e0a\u8981\u6253\u5f88\u5927\u7684\u6298\u6263\u3002\u4e4b\u524d\u5012\u662f\u9080\u8bf7\u4e86\u4e09\u661f\u9a7b\u6e2f\u529e\u516c\u673a\u6784\u7684\u4eba\u5458\u2026\u2026\u4e5f\u6ca1\u6709\u6307\u671b\u4ed6\u4eec\u4f1a\u6d3e\u4eba\u8fc7\u6765\uff0c\u771f\u662f\u6ca1\u60f3\u5230\u3002\u201d</p><p>\u3000\u3000\u4e4b\u524d\u6709\u65b0\u6b3e\u624b\u673a\u4ea4\u7ed9\u4e13\u4e1a\u7684\u7535\u5b50\u5a92\u4f53\u8bc4\u6d4b\u7528\uff0c\u4e0d\u8fc7\u4e5f\u4e25\u683c\u9650\u5236\u5176\u4ed6\u624b\u673a\u5382\u5546\u901a\u8fc7\u8fd9\u79cd\u9014\u5f84\u63a5\u89e6\u5230\u9526\u6e56\u7684\u65b0\u6b3e\u624b\u673a\uff0c\u50cf\u4e09\u661f\u8fd9\u4e9b\u5f00\u59cb\u91cd\u89c6\u9526\u6e56\u3001\u89c6\u9526\u6e56\u4e3a\u7ade\u4e89\u624b\u7684\u4f01\u4e1a\u60f3\u5c3d\u65e9\u62ff\u5230\u9526\u6e56\u8fd9\u6b21\u62ff\u51fa\u6765\u7684\u4e3b\u529b\u4ea7\u54c1\uff0c\u4e5f\u53ea\u80fd\u901a\u8fc7\u9884\u8d2d\u6700\u65e9\u5728\u9996\u5356\u4f1a\u4e0a\u62ff\u5230\u65b0\u624b\u673a\u3002\u674e\u5728\u6d19\u4f1a\u516c\u7136\u9732\u9762\u5374\u662f\u610f\u6599\u4e4b\u5916\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5b59\u9759\u9999\u7a81\u7136\u7701\u609f\u8fc7\u6765\uff0c\u5979\u542c\u9759\u6aac\u8bf4\u8fc7\u674e\u99a8\u4e88\uff0c\u8fd9\u5e74\u5934\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u4e0d\u5c11\uff0c\u6f02\u4eae\u800c\u8eab\u6750\u53c8\u8fd9\u4e48\u6027\u611f\u7684\uff0c\u5012\u662f\u4e0d\u591a\u89c1\uff0c\u7279\u522b\u662f\u674e\u99a8\u4e88\u7684\u8eab\u6750\u5982\u6b64\u7684\u706b\u7206\u800c\u9762\u5bb9\u53c8\u7ed9\u4eba\u6781\u81f4\u6e05\u7eaf\u7684\u611f\u89c9\uff0c\u5f80\u5f80\u4f1a\u7ed9\u4eba\u7559\u4e0b\u66f4\u6df1\u523b\u7684\u5370\u8c61\u2014\u2014\u603b\u4e4b\u5f20\u606a\u89c1\u8fc7\u4e4b\u540e\u5c31\u5f88\u96be\u5fd8\u6389\u8fd9\u5f20\u8138\uff0c\u5f53\u7136\u5f20\u606a\u8fd9\u4eba\u8d2a\u604b\u5973\u8272\uff0c\u4ed6\u7684\u60f3\u6cd5\u6839\u672c\u4e0d\u80fd\u7528\u6765\u505a\u4e00\u822c\u4eba\u7684\u6807\u51c6\u3002</p><p>\u3000\u3000\u5f20\u606a\u540e\u6765\u5728\u5317\u4eac\u3001\u5728\u6d77\u5dde\u4ee5\u53ca\u5728\u4e1c\u4eac\u7684\u51e0\u6b21\u8ddf\u674e\u99a8\u4e88\u7684\u63a5\u89e6\uff0c\u77e5\u60c5\u7684\u4eba\u5f88\u5c11\uff0c\u5b59\u9759\u9999\u81ea\u7136\u4e5f\u4e0d\u77e5\u9053\uff0c\u5979\u4e5f\u6ca1\u6709\u60f3\u5230\u5176\u4ed6\u5730\u65b9\u53bb\uff0c\u6307\u7740\u674e\u5728\u6d19\u3001\u674e\u99a8\u4e88\u90a3\u8fb9\u95ee\u5f20\u606a\uff1a\u201c\u4ed6\u4eec\u5c31\u662f\u53bb\u5e74\u79cb\u5929\u8ddf\u4f60\u5728\u5efa\u90ba\u8d77\u51b2\u7a81\u7684\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u542c\u9759\u6aac\u8bf4\u8d77\u7684\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u62bf\u7740\u5634\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9759\u6aac\u8bf4\u8d77\u65f6\uff0c\u6211\u8fd8\u4ee5\u4e3a\u8fd9\u59ae\u5b50\u778e\u8bf4\u5462\uff0c\u679c\u771f\u5982\u6b64\u3002\u201d\u8fd9\u7b11\u5bb9\u4e5f\u662f\u8be1\u5f02\u3002</p><p>\u3000\u3000\u201c\u9759\u6aac\u8bf4\u4ec0\u4e48\uff1f\u201d\u5510\u5a67\u597d\u5947\u7684\u95ee\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u9644\u5230\u5510\u5a67\u8033\u8fb9\u6084\u58f0\u8bf4\u4e86\u4e00\u53e5\uff0c\u848b\u8587\u4e5f\u5934\u51d1\u8fc7\u53bb\u542c\uff0c\u63a5\u7740\u4e09\u4e2a\u5973\u4eba\u5c31\u5403\u5403\u7684\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u80fd\u731c\u6d4b\u4e09\u4e2a\u5973\u4eba\u5728\u8bae\u8bba\u674e\u99a8\u4e88\u90a3\u582a\u55bb\u9b54\u9b3c\u8bf1\u60d1\u7684\u8c6a\u4e73\uff0c\u4ed6\u8f6c\u8eab\u4ece\u9910\u684c\u4e0a\u53c8\u62ff\u8d77\u4e00\u676f\u767d\u8461\u8404\u9152\u559d\u8d77\u6765\uff0c\u5fc3\u60f3\u7740\u8fd8\u662f\u4e0d\u8981\u5f53\u7740\u8fd9\u4e9b\u5973\u4eba\u7684\u9762\u8ddf\u674e\u99a8\u4e88\u6253\u62db\u547c\u53bb\u3002</p><p>\u3000\u3000\u867d\u8bf4\u9526\u6e56\u4e0e\u4e09\u661f\u5728\u56fd\u5185\u5e02\u573a\u5df2\u7ecf\u5f00\u59cb\u660e\u4e89\u6697\u6597\uff0c\u5f20\u606a\u4e0e\u674e\u5728\u6d19\u8fd8\u6709\u4e9b\u5c0f\u5c0f\u7684\u6069\u6028\uff0c\u4f46\u662f\u4f5c\u4e3a\u516c\u53f8\u5c42\u9762\u7684\u4ea4\u9645\u662f\u865a\u4f2a\u800c\u6577\u884d\u7684\uff0c\u9c81\u5e86\u751f\u4e0e\u8d56\u6d4e\u6625\u5728\u90a3\u8fb9\u5df2\u7ecf\u62db\u547c\u4e0a\u521a\u8fdb\u573a\u7684\u674e\u5728\u6d19\u4e0e\u674e\u99a8\u4e88\uff0c\u848b\u8587\u4e5f\u7aef\u7740\u9152\u676f\u8d70\u8fc7\u53bb\u62db\u547c\u3002</p><p>\u3000\u3000\u7531\u4e8e\u5728\u9999\u6e2f\u5e02\u573a\u53d1\u552e\uff0c\u65b0\u6b3e\u624b\u673a\u7684\u5ba3\u4f20\u5e76\u6ca1\u6709\u5f3a\u8c03i198\u662f\u76ee\u524d\u552f\u4e00\u6700\u65e9\u51fa\u73b0\u5728\u5e02\u573a\u4e0a\u7684\u5168\u4e2d\u6587\u652f\u6301\u624b\u673a\uff0c\u53ea\u6709\u4e00\u4e9b\u4e13\u4e1a\u5a92\u4f53\u7684\u8bc4\u6d4b\u62a5\u544a\u5bf9\u8054i198\u7684\u5168\u4e2d\u6587\u8f93\u5165\u529f\u80fd\u4e00\u7b14\u5e26\u8fc7\u3002\u4e0d\u4ec5\u652f\u6301\u4e2d\u6587\u663e\u793a\uff0c\u8fd8\u5f00\u53d1\u51fa\u652f\u6301\u6570\u5b57\u76d8\u4e2d\u6587\u8f93\u5165\uff0c\u624b\u673a\u8fd8\u5e26\u7740\u4e2d\u6587\u77ed\u4fe1\u529f\u80fd\u2014\u2014\u56fd\u5185\u5e02\u573a\u4e00\u76f4\u6ca1\u6709\u652f\u6301\u4e2d\u6587\u8f93\u5165\u7684\u624b\u673a\u51fa\u552e\uff0c\u7535\u4fe1\u7684\u77ed\u4fe1\u4e1a\u52a1\u6839\u672c\u5c31\u6ca1\u6709\u53d7\u5230\u8fc7\u91cd\u89c6\u3002</p><p>\u3000\u3000i198\u4e3b\u529b\u5e02\u573a\u4f9d\u65e7\u5728\u56fd\u5185\uff0c\u5bf9\u4e8e\u90a3\u4e9b\u5c06\u9526\u6e56\u89c6\u4e3a\u7ade\u4e89\u5bf9\u624b\u7684\u624b\u673a\u5382\u5546\u6700\u4e3a\u5173\u6ce8\u7684\u8fd8\u662f\u9526\u6e56\u8fd9\u6b21\u72ec\u7acb\u5f00\u53d1\u51fa\u6765\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u4e0e\u9c81\u5e86\u751f\u5bd2\u6684\uff0c\u773c\u73e0\u5b50\u8fd8\u7785\u7740\u7ad9\u5728\u8fdc\u5904\u7684\u5f20\u606a\uff0c\u4ed6\u4e07\u4e07\u6ca1\u6709\u60f3\u4f1a\u9526\u6e56\u6700\u5148\u5f00\u53d1\u51fa\u5546\u7528\u7684\u6570\u5b57\u5065\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002</p><p>\u3000\u3000\u4e5d\u56db\u5e74\u7231\u7acb\u4fe1\u5728\u4e2d\u56fd\u5e02\u573a\u7387\u5148\u63a8\u51fa\u5e26\u6709\u4e2d\u6587\u663e\u793a\u6280\u672f\u7684\u624b\u673a\uff0c\u5927\u83b7\u6210\u529f\uff0c\u53bb\u5e74\u751a\u81f3\u5c06\u6469\u6258\u7f57\u62c9\u4ece\u4e2d\u56fd\u5e02\u573a\u5360\u6709\u7387\u4e00\u5ea6\u8fbe\u523090\uff05\u7684\u795e\u575b\u4e0a\u62c9\u4e0b\u6765\uff0c\u8fd9\u4f3c\u4e4e\u5df2\u7ecf\u80fd\u591f\u8bf4\u660e\u5bf9\u4e2d\u6587\u7684\u652f\u6301\u7a0b\u5ea6\u5728\u4e00\u5b9a\u7a0b\u5ea6\u51b3\u5b9a\u4e86\u4ea7\u54c1\u5bf9\u4e2d\u56fd\u5e02\u573a\u9002\u5e94\u7a0b\u5ea6\u3002</p><p>\u3000\u3000\u6ca1\u6709\u6280\u672f\u5b9e\u529b\u7684\u56fd\u4ea7\u624b\u673a\u4f01\u4e1a\u4e5f\u5c31\u7f62\u4e86\uff0c\u4f17\u591a\u6d77\u5916\u624b\u673a\u5382\u5546\u4e00\u76f4\u8ba1\u5212\u5728\u4e2d\u56fd\u5e02\u573a\u63a8\u51fa\u5e26\u4e2d\u6587\u8f93\u5165\u529f\u80fd\u7684\u5168\u4e2d\u6587\u6570\u5b57\u624b\u673a\uff0c\u4e09\u661f\u4e5f\u60f3\u5728\u63a8\u5411\u4e2d\u56fd\u5185\u5730\u5e02\u573a\u7684\u65b0\u6b3e\u624b\u673a\u4e0a\u5c31\u79fb\u690d\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002</p><p>\u3000\u3000\u4e00\u65b9\u9762\u4e2d\u56fd\u79fb\u52a8\u8fd0\u8425\u7684\u77ed\u4fe1\u4e1a\u52a1\u5f53\u524d\u6781\u4e0d\u53d7\u91cd\u89c6\uff0c\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u7684\u5e02\u573a\u9700\u6c42\u4e0d\u662f\u7279\u522b\u7684\u8feb\u5207\uff0c\u4e00\u65b9\u9762\u76ee\u524d\u6d77\u5916\u638c\u63e1\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u7684\u51e0\u5bb6\u516c\u53f8\u5f00\u4ef7\u90fd\u5f88\u9ad8\uff0c\u6d77\u5916\u624b\u673a\u5382\u5546\u867d\u7136\u6709\u8ba1\u5212\uff0c\u4f46\u662f\u90fd\u6ca1\u6709\u7acb\u5373\u884c\u52a8\u8d77\u6765\u3002\u76f4\u5230\u4e94\u6708\u521d\u9999\u6e2f\u7684\u7535\u8baf\u4f20\u5a92\u5468\u520a\u520a\u8f7d\u4e86\u5bf9\u9526\u6e56\u5373\u5c06\u63a8\u51fa\u7684\u7231\u8fbei198\u624b\u673a\u7684\u8bc4\u6d4b\u62a5\u544a\uff0c\u8fd9\u4e9b\u6d77\u5916\u624b\u673a\u5382\u5546\u624d\u9aa4\u7136\u60ca\u9192\uff1a\u8fd9\u4e48\u4e00\u9879\u5bf9\u4e2d\u56fd\u5e02\u573a\u6709\u7740\u6781\u5927\u610f\u4e49\u7684\u6280\u672f\u7adf\u7136\u8ba9\u9526\u6e56\u5148\u7a81\u7834\u4e86\uff01</p><p>\u3000\u3000\u504f\u504f\u9999\u6e2f\u5e02\u573a\u5bf9\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e0d\u751a\u91cd\u89c6\uff0c\u8bc4\u6d4b\u62a5\u544a\u91cc\u4e5f\u53ea\u6709\u5ed6\u5ed6\u6570\u7b14\u63d0\u53ca\uff0c\u63a5\u89e6\u4e0d\u5230\u9526\u6e56\u65b0\u6b3e\u673a\u578b\u7684\u6d77\u5916\u5382\u5546\u81ea\u7136\u4e5f\u4e0d\u6e05\u695a\u9526\u6e56\u8fd9\u9879\u6280\u672f\u5230\u5e95\u8fbe\u5230\u600e\u6837\u7684\u6c34\u51c6\uff0c\u4ed6\u4eec\u5f53\u4e0b\u7684\u53cd\u5e94\u53ea\u80fd\u4e00\u65b9\u9762\u4e0e\u62e5\u6709\u8fd9\u9879\u6280\u672f\u7684\u4e00\u5bb6\u7f8e\u56fd\u516c\u53f8\u3001\u4e00\u5bb6\u52a0\u62ff\u5927\u516c\u53f8\u8fdb\u884c\u6388\u6743\u8c08\u5224\uff0c\u4e00\u65b9\u9762\u7b49\u7740\u9526\u6e56\u63a8\u51fa\u65b0\u6b3e\u624b\u673a\u4e4b\u540e\u624d\u80fd\u5bf9\u9526\u6e56\u638c\u63e1\u7684\u8fd9\u9879\u6280\u672f\u8fdb\u884c\u4e13\u9879\u7684\u8bc4\u6d4b\u3002</p><p>\u3000\u3000\u97e9\u56fd\u56fd\u5185\u7684\u79fb\u52a8\u901a\u4fe1\u90fd\u662f\u91c7\u7528CDMA\u7f51\u7edc\uff0c\u4e09\u661f\u624b\u91cc\u638c\u63e1\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u5e76\u4e0d\u9002\u5e94\u4e2d\u56fd\u7684\u79fb\u52a8\u901a\u8baf\u7f51\u7edc\uff0c\u53ef\u4ee5\u8bf4\uff0c\u4e09\u661f\u76ee\u524d\u7684\u624b\u673a\u6280\u672f\u5728\u4e2d\u56fd\u5e02\u573a\u4e5f\u53ea\u80fd\u7b97\u662f\u4e8c\u6d41\u7684\u3002</p><p>\u3000\u3000\u4ece\u7231\u8fbei19\u624b\u673a\u4e0a\uff0c\u674e\u5728\u6d19\u770b\u51fa\u9526\u6e56\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u7684\u7279\u70b9\uff0c\u91cd\u89c6\u5e02\u573a\u5f00\u53d1\u3001\u6700\u5927\u7a0b\u5ea6\u7684\u5229\u7528\u4ea7\u54c1\u8bbe\u8ba1\u6765\u5f25\u8865\u6838\u5fc3\u6280\u672f\u4e0a\u7684\u4e0d\u8db3\uff0c\u8fd9\u6070\u6070\u4e5f\u662f\u4e09\u661f\u610f\u56fe\u6253\u5f00\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u7684\u4e3b\u8981\u7b56\u7565\uff0c\u8ba9\u674e\u5728\u6d19\u5982\u4f55\u80fd\u653e\u677e\u5bf9\u9526\u6e56\u624b\u673a\u4e1a\u52a1\u7684\u5173\u6ce8\uff1f</p><p>\u3000\u3000\u674e\u5728\u6d19\u8fd9\u6b21\u5230\u9999\u6e2f\u6765\u662f\u4e13\u95e8\u4e3a\u7231\u8fbei198\u624b\u673a\u7684\u9996\u5356\u4f1a\u800c\u6765\uff0c\u5c31\u662f\u60f3\u6700\u5148\u62ff\u5230\u7231\u8fbei198\u624b\u673a\u7684\u7b2c\u4e00\u624b\u8d44\u6599\uff0c\u56e0\u4e3a\u9526\u6e56\u5728i198\u624b\u673a\u4e0a\u5df2\u7ecf\u79fb\u690d\u4e86\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u4e09\u661f\u4e0d\u5f97\u4e0d\u6539\u53d8\u63a5\u4e0b\u6765\u5728\u4e2d\u56fd\u5e02\u573a\u63a8\u51fa\u624b\u673a\u7684\u8ba1\u5212\u3002\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u4e09\u661f\uff0c\u6469\u6258\u7f57\u62c9\u3001\u7231\u7acb\u4fe1\u3001\u98de\u5229\u6d66\u7b49\u516c\u53f8\u90fd\u8981\u66f4\u6539\u5728\u4e2d\u56fd\u5e02\u573a\u63a8\u51fa\u65b0\u6b3e\u624b\u673a\u7684\u8ba1\u5212\uff0c\u81f3\u5c11\u8981\u6700\u4f18\u5148\u63a8\u51fa\u5168\u4e2d\u6587\u652f\u6301\u7684\u624b\u673a\uff0c\u8fd9\u5012\u4e0d\u662f\u8bf4\u8981\u63a8\u51fa\u5c31\u80fd\u63a8\u51fa\u7684\u3002</p><p>\u3000\u3000\u7231\u8fbei198\u624b\u673a\uff0c\u9664\u4e86\u7ade\u4e89\u5bf9\u624b\u6700\u4e3a\u91cd\u89c6\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u88ab\u6709\u610f\u63a9\u76d6\u4e4b\u5916\uff0c\u5176\u4ed6\u6027\u80fd\u90fd\u4e3a\u4e86\u914d\u5408\u5e7f\u544a\u5ba3\u4f20\u90fd\u7531\u4e13\u4e1a\u5a92\u4f53\u505a\u51fa\u8be6\u7ec6\u7684\u6d4b\u8bc4\uff0c\u674e\u5728\u6d19\u4e5f\u80fd\u6536\u96c6\u5230\u8be6\u7ec6\u7684\u8d44\u6599\u3002</p><p>\u3000\u3000i198\u5b8c\u5168\u53ef\u4ee5\u8bf4\u662fi19\u7684\u5f3a\u529b\u5347\u7ea7\u7248\uff0c\u7ee7\u627fi19\u7b80\u7ea6\u5178\u96c5\u7684\u76f4\u677f\u5916\u5f62\uff0c\u53ea\u662f\u5c06\u539f\u5148\u7565\u663e\u51cc\u5389\u7684\u7ec6\u8282\u4fee\u9970\u7684\u66f4\u5706\u6da6\u4e00\u4e9b\uff0c\u6709\u7740\u8272\u6cfd\u5ea6\u66f4\u9971\u6ee1\u7684\u84dd\u5c4f\u663e\u793a\uff0c\u66f4\u4e3a\u60a6\u8033\u7684\u548c\u5f26\u4e50\u97f3\u3002\u6539\u53d8\u6700\u591a\u7684\u5730\u65b9\u5c31\u662f\u539f\u5148i19\u624b\u673a\u4e3a\u4e86\u4fdd\u8bc1\u6574\u673a\u7684\u8f7b\u5de7\u4e0e\u7b80\u4fbf\u727a\u7272\u4e86\u5f88\u591a\u529f\u80fd\uff0c\u65f6\u9694\u534a\u5e74\u4e4b\u540e\uff0ci198\u624b\u673a\u5219\u663e\u793a\u51fa\u9526\u6e56\u5df2\u7ecf\u5b8c\u5168\u6d88\u5316\u4e86\u4ece\u5fb7\u4eea\u624b\u91cc\u627f\u63a5\u8fc7\u6765\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u751a\u81f3\u8fd8\u6709\u957f\u8db3\u7684\u8fdb\u6b65\u3002</p><p>\u3000\u3000\u6d3b\u52a8\u73b0\u573a\u8bbe\u6709\u4ea7\u54c1\u4f53\u9a8c\u533a\uff0c\u6709\u7a7f\u7740\u76ae\u8d28\u8863\u88d9\u3001\u9732\u51fa\u5927\u7247\u96ea\u767d\u808c\u80a4\u7684\u6a21\u7279\u5c0f\u59d0\u5728\u4ea7\u54c1\u4f53\u9a8c\u533a\u5411\u7528\u6237\u6f14\u793ai198\u7684\u624b\u673a\u529f\u80fd\u3002\u4e09\u661f\u9a7b\u6e2f\u673a\u6784\u5f53\u7136\u6709\u9884\u8d2di198\u624b\u673a\uff0c\u4f46\u662f\u674e\u5728\u6d19\u5374\u4e0d\u80fd\u5728\u8fd9\u91cc\u6d4b\u7528i198\u7684\u4e2d\u6587\u8f93\u5165\u6027\u80fd\uff0c\u8d76\u5de7\u4f53\u9a8c\u533a\u7684\u6a21\u7279\u5c0f\u59d0\u6f14\u793a\u4e2d\u6587\u8f93\u5165\uff0c\u6682\u505c\u4e0b\u4e0e\u848b\u8587\u3001\u9c81\u5e86\u751f\u7b49\u4eba\u7684\u5bd2\u6684\uff0c\u5c06\u6ce8\u610f\u529b\u6295\u5230\u4ea7\u54c1\u4f53\u9a8c\u533a\u53bb\u3002</p><p>\u3000\u3000\u662f\u674e\u5728\u6d19\u6240\u4e0d\u613f\u770b\u5230\u7684\u7ed3\u679c\uff0c\u773c\u775b\u770b\u4e0a\u53bb\u5c31\u80fd\u611f\u89c9\u5230\u9526\u6e56\u7684\u4e2d\u6587\u8f93\u5165\u6280\u672f\u7528\u6237\u4f53\u9a8c\u6027\u80fd\u8981\u6bd4\u4ed6\u63a5\u89e6\u7684\u90a3\u4e24\u5bb6\u516c\u53f8\u90fd\u8981\u6709\u4f18\u52bf\u5f97\u591a\uff0c\u679c\u7136\u4e2d\u56fd\u4eba\u5f00\u53d1\u4e2d\u6587\u8f93\u5165\u6280\u672f\u6709\u7740\u5929\u7136\u7684\u4f18\u52bf\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u8fd9\u65f6\u5019\u5c31\u9762\u4e34\u4e00\u4e2a\u8270\u96be\u7684\u9009\u62e9\uff1a\u5982\u4f55\u5f00\u53e3\u8ddf\u9526\u6e56\u8c08\u6280\u672f\u5f15\u8fdb\u7684\u4e8b\uff1f</p></article>"}}}]);
