(window.webpackJsonp=window.webpackJsonp||[]).push([[836],{859:function(p){p.exports={chapterTitle:null,title:"\u7b2c833\u7ae0 \u6349\u5978\u5728\u5e8a",content:"<article><h2>\u7b2c833\u7ae0 \u6349\u5978\u5728\u5e8a</h2><p>\u3000\u3000\u5f20\u606a\u63a8\u4e86\u63a8\u5367\u5ba4\u7684\u95e8\uff0c\u95e8\u6ca1\u6709\u9501\u4e0a\uff0c\u5fc3\u91cc\u4e0d\u4e89\u6c14\u7684\u72c2\u8df3\u8d77\u6765\u3002\u5367\u5ba4\u91cc\u6ca1\u6709\u4eae\u706f\uff0c\u6708\u5149\u4ece\u655e\u5f00\u7684\u7a97\u6237\u6563\u5c04\u8fdb\u6765\uff0c\u67d4\u548c\u7684\u6d12\u5728\u5929\u84dd\u8272\u7684\u534a\u5f27\u5f62\u5927\u5e8a\uff0c\u770b\u4e24\u4e2a\u5973\u4eba\u62e5\u88ab\u6a2a\u5367\u7684\u5a9a\u7136\u7761\u59ff\uff0c\u5f20\u606a\u5f97\u5230\u5c0f\u5fc3\u7684\u6342\u7740\u80f8\u53e3\uff0c\u514d\u5f97\u5267\u70c8\u7684\u5fc3\u8df3\u58f0\u5c06\u7761\u68a6\u4e2d\u7684\u4eba\u5435\u9192\u3002</p><p>\u3000\u3000\u591a\u5e74\u5919\u613f\u4e00\u591c\u5f97\u901e\uff0c\u96be\u602a\u68a6\u91cc\u90fd\u8981\u7b11\u9192\u554a\u3002</p><p>\u3000\u3000\u5efa\u90ba\u4e94\u6708\u4e0b\u65ec\u7684\u5929\u6c14\uff0c\u5df2\u7ecf\u76f8\u5f53\u708e\u70ed\u4e86\uff0c\u867d\u8bf4\u62e5\u88ab\u800c\u5367\uff0c\u4f46\u662f\u4e24\u4e2a\u5973\u4eba\u90fd\u53ea\u662f\u62ff\u8584\u7ed2\u88ab\u538b\u7740\u5c0f\u8179\uff0c\u7a7f\u7740\u7761\u88d9\u7684\u5927\u534a\u4e2a\u8eab\u5b50\u90fd\u9732\u5728\u5916\u9762\u3002\u665a\u6674\u7a7f\u7740\u6d45\u8272\u7ef8\u8d28\u7761\u88d9\u7684\u4e00\u89d2\u6380\u8d77\u6765\uff0c\u9732\u51fa\u6708\u5149\u4e0b\u4e73\u767d\u8272\u7684\u5927\u817f\u808c\u80a4\uff0c\u808c\u80a4\u6709\u5982\u7ef8\u7f0e\u4e00\u822c\u7ec6\u6ed1\uff0c\u7761\u88d9\u6b63\u597d\u4e0d\u597d\u7684\u6380\u5230\u5927\u817f\u6839\u5904\uff0c\u5728\u4e24\u817f\u4e4b\u95f4\u7559\u4e0b\u4e00\u7247\u9634\u5f71\uff0c\u8ba9\u4eba\u611f\u89c9\u91cc\u9762\u672a\u66fe\u6709\u518d\u7a7f\u4ec0\u4e48\u7684\u611f\u89c9\uff0c\u5f20\u606a\u5fc3\u91cc\u4e00\u4e50\uff1a\u4e24\u4e2a\u5927\u7f8e\u5973\u7761\u5728\u4e00\u8d77\u90fd\u4e0d\u7a7f\u5185\u88e4\uff0c\u96be\u9053\u8fd8\u6709\u4ec0\u4e48\u5185\u60c5\u4e0d\u6210\uff1f\u8bb8\u601d\u8138\u7ed9\u6563\u4e71\u7684\u4e4c\u9ed1\u957f\u53d1\u76d6\u7740\uff0c\u53ea\u9732\u51fa\u5ae3\u7136\u7684\u4e00\u89d2\u5634\u5507\uff0c\u5979\u662f\u4e00\u4e2a\u6d51\u8eab\u4e0a\u4e0b\u65e0\u4e00\u5904\u7ec6\u8282\u4e0d\u8ba9\u4eba\u5fc3\u9b42\u6fc0\u8361\u7684\u5c24\u7269\uff0c\u5f20\u606a\u7ad9\u5728\u5e8a\u8fb9\u75f4\u75f4\u7684\u770b\u4e86\u5979\u5b8c\u7f8e\u7684\u5507\u5f62\u597d\u4e45\uff0c\u624d\u60f3\u8d77\u591c\u91cc\u6f5c\u8fc7\u6765\u7684\u610f\u56fe\u3002</p><p>\u3000\u3000\u5f20\u606a\u7422\u78e8\u4e86\u8bb8\u4e45\uff0c\u89c9\u5f97\u8fd8\u662f\u5148\u5bf9\u8bb8\u601d\u4e0b\u624b\u4e3a\u597d\u3002\u8bb8\u601d\u6015\u7f9e\uff0c\u5373\u4f7f\u60ca\u9192\u4e86\u4e5f\u4f1a\u56e0\u4e3a\u6015\u5435\u9192\u7761\u5728\u8eab\u8fb9\u7684\u665a\u6674\u800c\u4efb\u81ea\u5df1\u80e1\u4f5c\u975e\u4e3a\uff1b\u665a\u6674\u6bd4\u8f83\u7eb5\u5bb9\u4ed6\uff0c\u7b49\u5c06\u8bb8\u601d\u5f97\u624b\u4e4b\u540e\uff0c\u5c31\u7b97\u5c06\u665a\u6674\u60ca\u9192\uff0c\u633a\u591a\u82b1\u4e9b\u8f6f\u78e8\u5de5\u592b\uff0c\u665a\u6674\u591a\u534a\u4f1a\u9042\u4e86\u81ea\u5df1\u7684\u5fc3\u610f\u3002\u8981\u662f\u6b21\u5e8f\u53cd\u8fc7\u6765\uff0c\u591a\u534a\u4f1a\u5c06\u8bb8\u601d\u60ca\u8d70\uff0c\u6700\u7ec8\u665a\u6674\u8981\u987e\u8651\u8bb8\u601d\u7684\u611f\u53d7\uff0c\u80af\u5b9a\u4e5f\u4e0d\u80af\u9042\u81ea\u5df1\u7684\u613f\uff0c\u5230\u5934\u6765\u8fd8\u662f\u7af9\u7bee\u5b50\u6253\u6c34\u4e00\u573a\u7a7a\uff0c\u8fd9\u6b21\u5e8f\u7edd\u4e0d\u80fd\u9519\u4e86\u3002</p><p>\u3000\u3000\u5fc3\u91cc\u90aa\u5ff5\u751f\u4e86\uff0c\u5f20\u606a\u66f4\u662f\u65e0\u6cd5\u81ea\u6211\u63a7\u5236\uff0c\u624b\u8d34\u7740\u8bb8\u601d\u7684\u7761\u88d9\u4f38\u8fdb\u53bb\uff0c\u8bb8\u601d\u5728\u7761\u68a6\u4e2d\u6709\u611f\u89c9\u4f3c\u7684\u5c06\u53cc\u817f\u5939\u7d27\u3002\u7ed9\u8bb8\u601d\u5f39\u6027\u5341\u8db3\u7684\u53cc\u817f\u5939\u4f4f\uff0c\u5f20\u606a\u66f4\u89c9\u5f97\u5fc3\u9b42\u6fc0\u8361\uff0c\u4e5f\u4e0d\u7ba1\u4f1a\u4e0d\u4f1a\u5c06\u4f73\u4eba\u60ca\u9192\uff0c\u5c06\u88ab\u5b50\u6380\u6389\uff0c\u5c06\u8fd9\u5177\u6027\u611f\u64a9\u4eba\u7684\u5a07\u8eaf\u538b\u5728\u8eab\u4e0b\uff0c\u53e6\u4e00\u53ea\u624b\u5f80\u5979\u4e30\u633a\u7684\u4e73\u623f\u4e0a\u6478\u53bb\uff0c\u9694\u7740\u4e00\u5c42\u8584\u8584\u7684\u7ef8\u8d28\u7761\u88d9\u4f20\u6765\u5f39\u8f6f\u4e30\u7fd8\u7684\u89e6\u611f\u8ba9\u5fc3\u7eea\u6fc0\u8361\u5230\u65e0\u4ee5\u590d\u52a0\u7684\u5730\u6b65\uff0c\u679c\u7136\u9762\u5bf9\u5982\u6b64\u7edd\u8272\u7684\u7f8e\u4eba\u513f\uff0c\u6709\u8fd9\u4e48\u90aa\u6076\u7684\u5ff5\u5934\u771f\u4e0d\u662f\u4e00\u822c\u7537\u4eba\u80fd\u591f\u627f\u53d7\u7684\u3002</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u4f60\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u8eab\u4e0b\u7684\u5a07\u8eaf\u52a8\u4e86\uff0c\u51cc\u4e71\u7684\u957f\u53d1\u6563\u5230\u4e24\u8fb9\uff0c\u9732\u51fa\u5b59\u9759\u6aac\u90a3\u5f20\u5996\u5b7d\u6a2a\u751f\u7684\u7f8e\u8138\u6765\uff0c\u60fa\u677e\u7684\u7761\u773c\u6563\u900f\u7740\u8ff7\u79bb\u7684\u5149\u8292\uff0c\u60f3\u5fc5\u662f\u5bf9\u5f20\u606a\u8db4\u5728\u5979\u8eab\u4e0a\u8fd9\u4ef6\u4e8b\u7591\u60d1\u4e0d\u89e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9759\u6aac\u4e5f\u6ca1\u6709\u5341\u5206\u7684\u62d2\u7edd\uff0c\u800c\u81ea\u5df1\u7684\u624b\u8fd8\u5728\u5979\u7684\u53cc\u817f\u4e4b\u95f4\uff0c\u4fbf\u60f3\u7740\u5c06\u9519\u5c31\u9519\u7684\u8fdb\u884c\u4e0b\u6765\uff0c\u8eab\u8fb9\u53c8\u4f20\u6765\u4e00\u58f0\u5a07\u547c\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u662f\u8fd9\u5fb7\u6027\uff1f\u4e0a\u56de\u8fd8\u60f3\u5bf9\u6211\u5c06\u9519\u5c31\u9519\uff0c\u8fd9\u65f6\u5019\u6545\u8ba1\u53c8\u65bd\u5230\u522b\u4eba\u8eab\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8c14\u7136\u56de\u5934\uff0c\u7761\u5728\u65c1\u8fb9\u7684\u4eba\u8d6b\u7136\u53d8\u6210\u7fdf\u4e39\u9752\u4e86\uff0c\u7ed9\u8fd9\u4e00\u5413\uff0c\u6b32\u5ff5\u9661\u6d88\uff0c\u60f3\u8981\u9003\u79bb\u5f00\uff0c\u7ed9\u9759\u6aac\u7ea4\u7ec6\u7684\u5c0f\u817f\u7eca\u4e86\u4e00\u4e0b\uff0c\u4ece\u5e8a\u4e0a\u683d\u4e86\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u53ea\u6765\u5f97\u53ca\u53d1\u51fa\u4e00\u58f0\u60e8\u53eb\u3002</p><p>\u3000\u3000\u5750\u5728\u697c\u9876\u5ead\u56ed\u8336\u5c4b\u91cc\u804a\u5929\u7684\u56db\u4e2a\u5973\u4eba\u770b\u7740\u5f20\u606a\u7761\u5f97\u597d\u597d\u7684\u7a81\u7136\u5c31\u4ece\u85e4\u6905\u4e0a\u7ffb\u8eab\u683d\u5230\u5730\u677f\u4e0a\u8fd8\u53d1\u51fa\u4e00\u58f0\u60e8\u53eb\uff0c\u90fd\u4e0d\u660e\u6240\u4ee5\u3002\u5b59\u9759\u6aac\u5658\u7740\u5634\uff0c\u770b\u7740\u5f20\u606a\u6478\u7740\u7ed9\u649e\u75bc\u7684\u989d\u5934\u6323\u624e\u7740\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u5632\u8bbd\u7684\u8bf4\u9053\uff1a\u201c\u8ddf\u6211\u4eec\u8bf4\u8bdd\u7adf\u7136\u8fd8\u80fd\u5728\u85e4\u6905\u4e0a\u7761\u7740\uff0c\u7761\u7740\u4e86\u7adf\u7136\u8fd8\u80fd\u4ece\u85e4\u6905\u4e0a\u7ffb\u5012\u4e86\uff0c\u4f60\u8fd8\u771f\u662f\u4e0d\u7b80\u5355\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u4e4d\u4ece\u68a6\u4e2d\u9192\u6765\uff0c\u5f20\u606a\u8fd8\u80fd\u8bb0\u5f97\u7761\u68a6\u91cc\u9759\u6aac\u5927\u817f\u808c\u80a4\u7684\u5f39\u6027\u4e0e\u89e6\u624b\u7684\u817b\u6ed1\uff0c\u4e00\u65f6\u95f4\u8fd8\u63d0\u4e0d\u8d77\u52c7\u6c14\u8ddf\u5979\u4e89\u53e3\u820c\u4e4b\u5229\u3002</p><p>\u3000\u3000\u201c\u505a\u68a6\u4e86\u5427\uff1f\u201d\u8bb8\u601d\u4fa7\u8fc7\u5934\u7591\u60d1\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u58f0\uff0c\u201c\u55ef\uff0c\u542c\u4f60\u4eec\u804a\u7f8e\u5bb9\u7684\u4e8b\u60c5\uff0c\u5c31\u7761\u7740\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u505a\u4ec0\u4e48\u68a6\uff0c\u7f8e\u68a6\u8fd8\u662f\u5669\u68a6\uff1f\u201d\u7fdf\u4e39\u9752\u5c31\u80cc\u5bf9\u7740\u5f20\u606a\u800c\u5750\uff0c\u79bb\u5f97\u6700\u8fd1\uff0c\u56de\u8fc7\u5934\u6765\u95ee\uff0c\u201c\u542c\u89c1\u4f60\u6709\u4e00\u9635\u5b50\u547c\u5438\u86ee\u6025\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4e94\u6708\u7684\u5efa\u90ba\u591c\u51c9\u5982\u6c34\uff0c\u5b59\u9759\u6aac\u5728\u7761\u88d9\u5916\u8fd8\u7a7f\u7740\u4e00\u4ef6\u5bbd\u5927\u7684\u683c\u5b50\u68c9\u8d28\u886c\u886b\uff0c\u5f20\u606a\u770b\u8fd9\u886c\u886b\u773c\u719f\uff0c\u4f46\u770b\u5230\u886c\u886b\u4e0b\u6446\u9732\u51fa\u6765\u767d\u5ae9\u7ea4\u7ec6\u7684\u5c0f\u817f\uff0c\u4e5f\u65e0\u4ece\u8ffd\u7a76\u5979\u4e3a\u4ec0\u4e48\u8981\u968f\u624b\u5c06\u81ea\u5df1\u4e22\u6c99\u53d1\u4e0a\u7684\u886c\u886b\u7a7f\u8d77\u6765\u3002\u7fdf\u4e39\u9752\u5979\u7a7f\u7740\u767d\u8272\u7684\u68c9\u8d28\u7761\u88e4\uff0c\u5c41\u80a1\u88f9\u5f97\u7d27\u7d27\u7684\uff0c\u5370\u51fa\u91cc\u9762\u5185\u88e4\u7684\u7ea2\u8272\u5e95\u7eb9\uff0c\u5979\u62b1\u819d\u80cc\u5bf9\u7740\u5f20\u606a\u800c\u5750\uff0c\u80cc\u81c0\u5f13\u7740\uff0c\u9732\u51fa\u7ea2\u8272\u5185\u88e4\u7684\u8fb9\u7f18\u4e0e\u4e00\u622a\u767d\u5ae9\u7684\u8170\u8089\uff0c\u4e0a\u8eab\u662f\u4ef6\u76f8\u5bf9\u5979\u6027\u611f\u8eab\u6750\u6765\u8bf4\u6709\u4e9b\u5c0f\u7684\u767d\u68c9T\u6064\u3002</p><p>\u3000\u3000\u7adf\u7136\u5728\u68a6\u91cc\u9047\u5230\u8fd9\u4e24\u4e2a\u5973\u4eba\uff0c\u8fd9\u5012\u662f\u7b97\u7f8e\u68a6\u8fd8\u662f\u5669\u68a6\uff1f\u5f20\u606a\u82e6\u7b11\u7684\u6447\u5934\uff0c\u6ca1\u60f3\u5230\u591c\u91cc\u4e0e\u8bb8\u601d\u3001\u665a\u6674\u4e09\u4e2a\u4eba\u4e4b\u95f4\u7684\u5e7d\u4f1a\uff0c\u7adf\u53c8\u786c\u751f\u751f\u7684\u7ed9\u9759\u6aac\u3001\u4e39\u9752\u63d2\u4e86\u8fdb\u6765\u3002\u665a\u996d\u524d\u8ddf\u665a\u6674\u901a\u8fc7\u7535\u8bdd\u540e\u518d\u56de\u5ea7\u4f4d\u5c31\u88ab\u544a\u4e4b\u8bb8\u601d\u5df2\u7ecf\u7b54\u5e94\u9759\u6aac\u7684\u9080\u8bf7\u591c\u91cc\u4f4f\u8fdb\u9752\u5e74\u516c\u5bd3\u53bb\uff0c\u7fdf\u4e39\u9752\u53c8\u5728\u665a\u996d\u4e4b\u540e\u4ece\u65b0\u829c\u8fd4\u56de\u5efa\u90ba\u2014\u2014\u6240\u6709\u671f\u5f85\u7684\u66a7\u6627\u6c14\u6c1b\u5c31\u8fd9\u6837\u7ed9\u7834\u574f\u6b86\u5c3d\uff0c\u5f20\u606a\u6700\u7ec8\u6ca6\u843d\u5230\u6ca1\u6709\u4e00\u4e2a\u5973\u4eba\u642d\u7406\u7684\u5730\u6b65\u3002</p><p>\u3000\u3000\u201c\u90fd\u4ec0\u4e48\u65f6\u95f4\u4e86\uff0c\u4f60\u4eec\u90fd\u4e0d\u7761\u89c9\uff1f\u201d\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\u5929\uff0c\u6708\u81f3\u4e2d\u5929\uff0c\u6821\u56ed\u91cc\u5bbf\u820d\u706f\u5149\u65e9\u5c31\u7184\u706d\u4e86\uff0c\u5c31\u7b97\u901a\u5bb5\u4f9b\u7535\u7684\u7814\u7a76\u751f\u5bbf\u820d\u5230\u8fd9\u65f6\u4e5f\u6ca1\u6709\u591a\u5c11\u623f\u95f4\u7684\u706f\u5149\u4eae\u7740\u3002</p><p>\u3000\u3000\u201c\u4f60\u8981\u7761\u5148\u53bb\u7761\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u6311\u4e2a\u4eba\u4f8d\u5bdd\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u542c\u4e86\u7fdf\u4e39\u9752\u8c8c\u4f3c\u6311\u9017\u5374\u6697\u6709\u55bb\u6307\u7684\u8fd9\u8bdd\uff0c\u5f20\u606a\u53ea\u80fd\u843d\u8352\u800c\u9003\uff0c\u5c31\u6015\u9759\u6aac\u542c\u51fa\u4ec0\u4e48\u610f\u5473\u6765\u3002\u665a\u6674\u4e0e\u8bb8\u601d\u4e5f\u662f\u5fc3\u91cc\u6709\u9b3c\uff0c\u4fcf\u8138\u5fae\u7ea2\uff0c\u987e\u5de6\u53f3\u800c\u8a00\u5176\u4ed6\u3002</p><p>\u3000\u3000\u665a\u6674\u6253\u7740\u54c8\u6b20\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u662f\u4e0d\u65e9\u4e86\uff0c\u8fd8\u662f\u65e9\u70b9\u4f11\u606f\u5427\u2026\u2026\u201d\u62d6\u7740\u8bb8\u601d\u4e0e\u7fdf\u4e39\u9752\u7684\u624b\u4e00\u8d77\u4e0b\u697c\u53bb\uff0c\u5b59\u9759\u6aac\u9f3b\u5934\u5fae\u55c5\u7740\uff0c\u9f3b\u7ffc\u76b1\u8d77\u4e86\u7ec6\u5c0f\u53ef\u7231\u7684\u76b1\u7eb9\uff0c\u4e5f\u8ddf\u7740\u4e0b\u4e86\u697c\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5230\u623f\u95f4\u91cc\u95e8\u865a\u63a9\u7740\uff0c\u542c\u89c1\u56db\u4e2a\u5973\u4eba\u4e0b\u697c\u6765\u7684\u58f0\u97f3\uff0c\u5fc3\u91cc\u60f3\u7740\u591c\u91cc\u600e\u6837\u624d\u80fd\u5c06\u8bb8\u601d\u9a97\u5230\u623f\u95f4\u91cc\u6765\u3002\u7fdf\u4e39\u9752\u4f4f\u5728\u697c\u4e0b\u7684\u516c\u5bd3\u91cc\uff0c\u63a8\u95e8\u79bb\u5f00\u540e\uff0c\u665a\u6674\u4e0e\u8bb8\u601d\u8fd8\u6709\u9759\u6aac\u53c8\u7ad9\u5728\u8fc7\u9053\u91cc\u8bf4\u4e86\u4e00\u4f1a\u513f\u8bdd\uff0c\u5f20\u606a\u5728\u623f\u95f4\u91cc\u542c\u89c1\u9759\u6aac\u8bf4\uff1a\u201c\u5ba2\u623f\u91cc\u7684\u5e8a\u6bd4\u8f83\u7a84\uff0c\u8981\u4e0d\u4f60\u4eec\u8c01\u8ddf\u6211\u7761\u4e00\u4e2a\u623f\u95f4\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bb8\u601d\u4e0b\u5348\u4e58\u98de\u673a\u592a\u7d2f\uff0c\u6015\u662f\u591c\u91cc\u7761\u4e0d\u8e0f\u5b9e\uff0c\u8fd8\u4e0d\u8ba9\u5979\u5355\u72ec\u7761\u5f20\u5e8a\u2026\u2026\u201d\u665a\u6674\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u542c\u4e86\u6697\u559c\uff0c\u542c\u89c1\u5979\u4eec\u5404\u81ea\u56de\u623f\u4f11\u606f\uff0c\u5c31\u7ed9\u8bb8\u601d\u6253\u4e86\u7535\u8bdd\uff0c\u65e0\u8d56\u7684\u8bf4\u9053\uff1a\u201c\u521a\u521a\u505a\u5669\u68a6\u60ca\u9192\u5c31\u518d\u4e5f\u7761\u4e0d\u7740\u4e86\uff0c\u4f60\u8fc7\u6765\u966a\u6211\u8bf4\u4f1a\u513f\u8bdd\u5427\u3002\u8981\u4e48\u4f60\u8fc7\u6765\uff0c\u8981\u4e48\u6211\u8fc7\u53bb\uff1f\u4f60\u90a3\u8fb9\u5899\u8584\uff0c\u8bf4\u8bdd\u6307\u4e0d\u5b9a\u4f1a\u7ed9\u5979\u4eec\u542c\u89c1\u3002\u201d</p><p>\u3000\u3000\u7b49\u4e86\u7247\u523b\uff0c\u8bb8\u601d\u63a8\u95e8\u8fdb\u6765\uff0c\u5173\u95e8\u65f6\u8fd8\u505a\u8d3c\u5fc3\u865a\u7684\u56de\u5934\u5f80\u8fc7\u9053\u91cc\u770b\u4e86\u4e24\u773c\uff0c\u4e0d\u6562\u5c06\u95e8\u53cd\u9501\u4e0a\u2014\u2014\u4e07\u4e00\u6709\u8c01\u6765\u627e\u5f20\u606a\u89c1\u95e8\u53cd\u9501\u7740\uff0c\u6709\u4e00\u767e\u5f20\u5634\u90fd\u8bf4\u4e0d\u6e05\u695a\uff0c\u63a9\u4e0a\u95e8\uff0c\u89c1\u5f20\u606a\u5634\u89d2\u6302\u7740\u574f\u7b11\uff0c\u6ca1\u597d\u6c14\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4e2a\u65e0\u8d56\uff0c\u6709\u4ec0\u4e48\u8bdd\u8981\u804a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u5e8a\u57ab\uff0c\u8ba9\u8bb8\u601d\u5750\u5230\u8eab\u8fb9\u6765\u804a\u5929\uff0c\u5f85\u5979\u8d70\u8fd1\u5c31\u5c06\u5979\u4e30\u76c8\u7684\u8eab\u5b50\u6402\u4f4f\uff0c\u8d34\u7740\u5979\u7684\u8033\u8fb9\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8ddf\u5341\u4e94\u516d\u5c81\u7684\u5c0f\u59d1\u5a18\u4e00\u6837\u4e0d\u7ecf\u9a97\uff0c\u4f60\u8bf4\u6211\u8fd9\u65f6\u5019\u4f1a\u8ddf\u4f60\u804a\u4ec0\u4e48\u5929\uff1f\u201d\u5634\u5507\u63a2\u7740\u5979\u6e29\u70ed\u6ed1\u817b\u7684\u8138\u988a\u53bb\u5bfb\u5979\u706b\u70ed\u7684\u7ea2\u5507\u3002</p><p>\u3000\u3000\u8bb8\u601d\u62bf\u7740\u5634\u5507\u65e0\u58f0\u7684\u6323\u624e\u7740\uff0c\u5b9e\u5728\u6323\u624e\u4e0d\u8fc7\u5c31\u6c42\u5f20\u606a\u5c06\u706f\u5173\u4e86\u3001\u5c06\u7a97\u5e18\u6302\u4e0a\uff0c\u623f\u5b50\u91cc\u8fd8\u6709\u5176\u4ed6\u4eba\u5728\uff0c\u4f3c\u4e4e\u623f\u95f4\u6709\u4e00\u70b9\u5149\u90fd\u4f1a\u89c9\u5f97\u5f88\u96be\u582a\u3002\u4e30\u6ee1\u7684\u4e73\u623f\u7ed9\u5f20\u606a\u7684\u624b\u63e1\u4f4f\uff0c\u6d51\u8eab\u706b\u70ed\uff0c\u4e0b\u8eab\u66f4\u662f\u6e7f\u5f97\u5389\u5bb3\uff0c\u8d8a\u662f\u6323\u624e\u5fc3\u91cc\u7684\u60c5\u6b32\u8d8a\u662f\u6d8c\u52a8\u5f97\u5389\u5bb3\uff0c\u771f\u662f\u4e2a\u65e0\u8d56\u554a\uff0c\u5f85\u5f20\u606a\u65e0\u8d56\u7684\u5c06\u5206\u8eab\u6324\u8fdb\u6765\uff0c\u90a3\u4e00\u77ac\u95f4\u8bb8\u601d\u4e5f\u60c5\u4e0d\u81ea\u7981\u53cc\u817f\u7d27\u73af\u4f4f\u4ed6\u7684\u81c0\uff0c\u53ea\u5f97\u62ff\u4e00\u53ea\u624b\u6342\u4f4f\u5634\u5507\uff0c\u4e0d\u8ba9\u81ea\u5df1\u53d1\u751f\u4e00\u4e1d\u7684\u547b\u541f\u51fa\u6765\u2014\u2014\u53e6\u4e00\u53ea\u624b\u6df1\u6df1\u7684\u6293\u4f4f\u5f20\u606a\u7684\u80cc\u808c\u91cc\u3002</p><p>\u3000\u3000\u201c\u5431\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u7740\u95e8\u7ed9\u4eba\u63a8\u5f00\uff0c\u8bb8\u601d\u6761\u4ef6\u53cd\u5c04\u4f3c\u7684\u5fc3\u810f\u8df3\u5230\u55d3\u5b50\u773c\uff0c\u7acb\u9a6c\u6293\u7d27\u5728\u8eab\u540e\u8038\u52a8\u7684\u5f20\u606a\u4e0d\u8ba9\u4ed6\u52a8\u5f39\uff0c\u4f46\u4e24\u4eba\u7684\u8eab\u5b50\u8fd8\u8fde\u5728\u4e00\u8d77\uff0c\u6e05\u6670\u65e0\u6bd4\u7684\u611f\u89c9\u5f20\u606a\u5728\u81ea\u5df1\u8eab\u4f53\u91cc\u90a3\u90e8\u4ef6\u7684\u5f62\u72b6\u4e0e\u706b\u70ed\u7684\u6e29\u5ea6\uff0c\u867d\u7136\u505c\u4e86\u52a8\u5f39\uff0c\u53ef\u662f\u90a3\u6839\u4e1c\u897f\u7adf\u7136\u8fd8\u5728\u81ea\u5df1\u7684\u4f53\u5185\u8df3\u52a8\uff0c\u771f\u662f\u53eb\u4eba\u597d\u53d7\u3002\u542c\u89c1\u6709\u4eba\u6089\u6089\u7c0c\u7c0c\u7684\u6478\u9ed1\u8d70\u8fdb\u6765\uff0c\u8d70\u8fdb\u6765\u8fd8\u968f\u540e\u95e8\u5e26\u4e0a\uff0c\u201c\u5361\u55d2\u201d\u4e00\u58f0\u8fd8\u5c06\u95e8\u7ed9\u53cd\u9501\u4e0a\uff0c\u8bb8\u601d\u5fc3\u91cc\u53c8\u7f9e\u53c8\u6025\uff0c\u65e9\u77e5\u9053\u521a\u624d\u5c31\u5e94\u8be5\u5148\u5c06\u95e8\u7ed9\u53cd\u9501\u4e0a\u5c31\u4e0d\u7528\u6ca6\u5165\u5982\u6b64\u5c34\u5c2c\u7684\u5883\u5730\uff0c\u504f\u504f\u6b64\u65f6\u7684\u8eab\u4f53\u53c8\u654f\u611f\u5f97\u7d27\u3002</p><p>\u3000\u3000\u201c\u5440\u2026\u2026\u201d\u6478\u9ed1\u8fdb\u623f\u95f4\u7684\u90a3\u4eba\u78b0\u5230\u684c\u89d2\uff0c\u5403\u75db\u7684\u558a\u4e86\u4e00\u58f0\u3002\u542c\u662f\u665a\u6674\u7684\u5a07\u547c\u58f0\uff0c\u8bb8\u601d\u624b\u6390\u5230\u5f20\u606a\u5927\u817f\u5185\u4fa7\uff0c\u8ba9\u4ed6\u4e0d\u8981\u53d1\u51fa\u58f0\u97f3\u3002\u5f20\u606a\u867d\u7136\u5f88\u671f\u5f85\u4e09\u4eba\u5927\u88ab\u540c\u5e8a\uff0c\u4f46\u662f\u8fd9\u65f6\u5019\u7ed9\u665a\u6674\u649e\u5230\u8fd8\u662f\u86ee\u5c34\u5c2c\u7684\uff0c\u518d\u8bf4\u9759\u6aac\u8fd8\u662f\u623f\u5b50\u91cc\uff0c\u5f20\u606a\u6709\u51e0\u4e2a\u80c6\u5b50\u6562\u5728\u8fd9\u623f\u5b50\u91cc\u80e1\u4f5c\u975e\u4e3a\uff1f</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8bb8\u601d\u90fd\u6452\u606f\u5b81\u795e\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u624d\u542c\u89c1\u665a\u6674\u5728\u9ed1\u6697\u91cc\u7b49\u4e86\u7247\u523b\u6ca1\u542c\u89c1\u5c4b\u5b50\u91cc\u6709\u4eba\u542d\u58f0\u53ea\u5f53\u5f20\u606a\u4e0d\u5728\u5c4b\u5b50\u91cc\uff0c\u5c31\u62b1\u6028\u7684\u81ea\u8a00\u81ea\u8bed\uff1a\u201c\u8fd9\u5077\u5fc3\u8d3c\u679c\u7136\u80c6\u5927\u5984\u4e3a\u7684\u6e9c\u5230\u8bb8\u601d\u5c0f\u9a9a\u8e44\u5b50\u623f\u95f4\u91cc\u53bb\uff0c\u4e0d\u7528\u8fc7\u6765\u67e5\u591c\u5c31\u5e94\u8be5\u60f3\u5230\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u775b\u8fd9\u8bdd\u4e00\u8bf4\uff0c\u5f20\u606a\u5927\u817f\u5185\u4fa7\u5c31\u7ed9\u8bb8\u601d\u6390\u5f97\u751f\u75bc\uff0c\u8fd8\u4e0d\u80fd\u53d1\u51fa\u4e00\u70b9\u58f0\u54cd\uff0c\u4e0b\u8eab\u7ed9\u73af\u7d27\u7740\u5374\u9500\u9b42\u5f97\u8981\u547d\uff0c\u4e5f\u6108\u53d1\u7684\u80c0\u5927\u3002\u8bb8\u601d\u8eab\u4f53\u7684\u611f\u89c9\u4e5f\u654f\u611f\u5230\u6781\u70b9\uff0c\u53ea\u7b49\u7740\u665a\u6674\u79bb\u5f00\u518d\u597d\u597d\u7684\u6559\u8bad\u8fd9\u65e0\u8d56\u4e00\u901a\uff0c\u6ca1\u60f3\u5230\u5728\u9ed1\u6697\u91cc\u7684\u665a\u6674\u53c8\u8bf4\u4e86\u4e00\u53e5\u8ba9\u5979\u9b42\u98de\u9b44\u6563\u7684\u8bdd\uff1a\u201c\u8fd9\u5077\u5fc3\u8d3c\u4e0d\u4f1a\u4eca\u665a\u7edd\u4e86\u5ff5\u60f3\u6b7b\u7761\u8fc7\u53bb\u4e86\u5427\uff1f\u201d\u8fc7\u4e86\u51e0\u79d2\u5c31\u6709\u4e00\u53ea\u624b\u4f38\u8fc7\u6765\u6478\u5728\u81ea\u5df1\u7684\u8170\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u808c\u80a4\u5728\u7537\u4eba\u4e2d\u4e5f\u7b97\u96be\u5f97\u7684\u7ec6\u817b\uff0c\u4f46\u4e0e\u8bb8\u601d\u582a\u6bd4\u5a74\u513f\u5a07\u5ae9\u7684\u808c\u80a4\u76f8\u6bd4\u8fd8\u8981\u5dee\u8bb8\u591a\uff0c\u665a\u6674\u5165\u624b\u5c31\u77e5\u9053\u662f\u8bb8\u601d\u8eba\u5728\u8fd9\u91cc\uff0c\u5979\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\u8bb8\u601d\u80fd\u80c6\u5927\u5984\u4e3a\u5230\u6e9c\u5230\u5f20\u606a\u7684\u623f\u95f4\u91cc\u6765\u5077\u60c5\uff0c\u5fc3\u91cc\u5413\u4e00\u8df3\u624b\u5c31\u6ca1\u6709\u53ca\u65f6\u62bd\u5f00\uff0c\u5374\u611f\u89c9\u5230\u8bb8\u601d\u7684\u8eab\u4f53\u5728\u90a3\u4e00\u77ac\u95f4\u7adf\u7136\u7ef7\u7d27\u75c9\u631b\u8d77\u6765\u3002</p><p>\u3000\u3000\u8fd9\u5c0f\u9a9a\u8e44\u5b50\uff01\u665a\u6674\u5f53\u7136\u6e05\u695a\u8bb8\u601d\u8fd9\u65f6\u8eab\u4f53\u7684\u53cd\u5e94\u662f\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u80fd\u8fd9\u65f6\u5019\u6253\u65ad\u4ed6\u4eec\uff0c\u7ad9\u8d77\u6765\u8981\u8d70\uff0c\u624b\u5374\u7ed9\u8bb8\u601d\u7d27\u7d27\u6293\u4f4f\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u8bb8\u601d\u6761\u4ef6\u53cd\u5c04\u7684\u60f3\u8981\u6293\u4f4f\u4ec0\u4e48\u4e1c\u897f\uff0c\u8ddf\u6eba\u6c34\u7684\u4eba\u9047\u5230\u4e00\u6839\u7a3b\u8349\u4f3c\u7684\u611f\u89c9\uff0c\u5927\u6982\u6709\u5341\u591a\u79d2\u6301\u7eed\u7684\u5267\u70c8\u611f\u89c9\uff0c\u624d\u60ca\u7f9e\u7684\u677e\u5f00\u624b\uff0c\u62c9\u4e86\u88ab\u5b50\u5c06\u6574\u4e2a\u4eba\u8fde\u5934\u90fd\u811a\u90fd\u8499\u5728\u88ab\u5b50\u91cc\uff0c\u8fd8\u987a\u5e26\u5c06\u5f20\u606a\u8e39\u4e0b\u5e8a\u3002</p><p>\u3000\u3000\u665a\u6674\u5c06\u706f\u6253\u5f00\uff0c\u770b\u89c1\u5f20\u606a\u6b63\u614c\u624b\u614c\u811a\u7684\u8ddf\u8bb8\u601d\u62a2\u88ab\u5b50\u4f46\u662f\u62a2\u4e0d\u8fc7\uff0c\u6307\u7740\u5f20\u606a\u8fd8\u7a7f\u7740\u96e8\u8863\u7684\u4e0b\u8eab\u8bf4\u9053\uff1a\u201c\u4e11\u6b7b\u4e86\uff0c\u8d76\u7d27\u627e\u6837\u4e1c\u897f\u906e\u906e\u3002\u201d\u5f20\u606a\u53ea\u5f97\u5148\u627e\u4ef6\u6795\u5934\u6321\u5728\u8eab\u524d\u3002</p><p>\u3000\u3000\u201c\u8d81\u7740\u9759\u6aac\u719f\u7761\u60f3\u8fc7\u6765\u67e5\u591c\uff0c\u6ca1\u60f3\u5230\u662f\u8bb8\u601d\u8fd9\u5c0f\u9a9a\u8e44\u5b50\u6309\u8010\u4e0d\u4f4f\u8dd1\u5230\u4f60\u7684\u623f\u91cc\u6765\uff0c\u65e9\u77e5\u9053\u8fd9\u6837\u5c31\u4e0d\u8fc7\u6765\u3002\u201d\u665a\u6674\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u5c0f\u606a\u90a3\u5bb6\u4f19\u9a97\u6211\u8fc7\u6765\u7684\u2026\u2026\u201d\u8bb8\u601d\u8499\u7740\u88ab\u5b50\u74ee\u58f0\u74ee\u6c14\u7684\u5c0f\u58f0\u4e3a\u81ea\u5df1\u8fa9\u89e3\uff0c\u201c\u5c0f\u606a\u662f\u4e2a\u65e0\u8d56\uff0c\u6211\u7ed9\u9a97\u8fc7\u6765\u5c31\u5b8c\u86cb\u4e86\uff0c\u4e22\u8138\u4e22\u6b7b\u4eba\uff0c\u665a\u6674\u59d0\u4f60\u4e0d\u8bb8\u7b11\u8bdd\u6211\uff01\u201d</p><p>\u3000\u3000\u201c\u968f\u4fbf\u4f60\u4eec\uff0c\u6211\u56de\u53bb\u7761\u89c9\u4e86\uff0c\u4f60\u4eec\u65e9\u4e0a\u4e0d\u8981\u7761\u8fc7\u5934\u2026\u2026\u201d\u665a\u6674\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u665a\u6674\u59d0\u2026\u2026\u201d\u5f20\u606a\u89c1\u665a\u6674\u6ca1\u6709\u751f\u6c14\u6837\u5b50\uff0c\u4f38\u624b\u62c9\u8fc7\u5979\uff0c\u793a\u610f\u5979\u4e5f\u7559\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u5c31\u77e5\u9053\u4e0d\u5b66\u597d\u2026\u2026\u201d\u665a\u6674\u770b\u61c2\u5f20\u606a\u773c\u6674\u91cc\u7684\u610f\u601d\uff0c\u77a5\u773c\u770b\u4e86\u62ff\u88ab\u5b50\u7d27\u7d27\u8499\u7740\u81ea\u5df1\u7684\u8bb8\u601d\u4e00\u773c\uff0c\u5fc3\u60f3\u81ea\u5df1\u7559\u4e0b\u6765\uff0c\u8bb8\u601d\u80af\u5b9a\u4e0d\u4f1a\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u9b42\u4e00\u8361\uff0c\u53cc\u817f\u4e4b\u95f4\u6f6e\u70ed\u5f97\u5389\u5bb3\uff0c\u5fc3\u60f3\u8981\u4e0d\u662f\u9759\u6aac\u90a3\u4e2b\u5934\u8fd8\u5728\u623f\u5b50\u91cc\uff0c\u5012\u662f\u4e0d\u60f3\u62d2\u7edd\u8fd9\u4e2a\u5c0f\u51a4\u5bb6\uff0c\u4f38\u624b\u6478\u5230\u5f20\u606a\u7684\u4e0b\u8eab\u63e1\u4e86\u63e1\uff0c\u9644\u5230\u5f20\u606a\u7684\u8033\u6735\u8f7b\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u603b\u8981\u7ed9\u6211\u7559\u4e00\u70b9\u70b9\u7684\u989c\u9762\uff0c\u4e0d\u8981\u7ed9\u9759\u6aac\u90a3\u59ae\u5b50\u9192\u6765\u53d1\u89c9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u521a\u8d70\uff0c\u8bb8\u601d\u6380\u8d77\u88ab\u5b50\u50cf\u56db\u811a\u7ae0\u9c7c\u4f3c\u7684\u7f20\u5230\u5f20\u606a\u7684\u8eab\u540e\uff0c\u6028\u9053\uff1a\u201c\u4f60\u8ba9\u6211\u4ee5\u540e\u600e\u4e48\u8ddf\u665a\u6674\u59d0\u89c1\u9762\u554a\uff1f\u4f60\u4e2a\u65e0\u8d56\uff01\u201d</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u4ec0\u4e48\u65f6\u5019\u4f60\u6765\u6349\u6211\u8ddf\u665a\u6674\u7684\u5978\u5c31\u662f\u4e86\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u6ca1\u7b49\u4ed6\u8bdd\u97f3\u843d\uff0c\u80a9\u5934\u5c31\u7ed9\u8bb8\u601d\u72e0\u72e0\u7684\u54ac\u4e86\u4e00\u53e3\uff0c\u521a\u8981\u53eb\uff0c\u8bb8\u601d\u5c31\u4f38\u624b\u6342\u4f4f\u4ed6\u7684\u5634\uff0c\u5728\u8033\u6735\u8f7b\u58f0\u8bf4\uff1a\u201c\u4e0d\u8bb8\u53eb\uff0c\u8981\u5fcd\u7740\u3002\u201d\u5f20\u606a\u5fcd\u7740\u75db\uff0c\u53cd\u624b\u6402\u7740\u8bb8\u601d\u5149\u6e9c\u6e9c\u7684\u8170\u80a2\uff0c\u5728\u5979\u4e0d\u7740\u4e1d\u7f15\u7684\u5ae9\u6ed1\u81c0\u8089\u4e0a\u8f7b\u8f7b\u7684\u629a\u5f04\u7740\uff0c\u8ba9\u8bb8\u601d\u62ff\u5fae\u51c9\u7684\u624b\u6307\u8f7b\u629a\u80a9\u5934\u7ed9\u54ac\u7684\u5730\u65b9\uff0c\u6216\u8bb8\u662f\u81ea\u5df1\u592a\u8d2a\u5a6a\u4e86\uff0c\u4f46\u662f\u53c8\u6709\u51e0\u4e2a\u7537\u4eba\u80fd\u62b5\u5f97\u4f4f\u8fd9\u79cd\u8bf1\u60d1\u3002</p><p>\u3000\u3000\u5728\u623f\u95f4\u91cc\u6e29\u5b58\u4e86\u7247\u523b\uff0c\u8bb8\u601d\u5f53\u7136\u4e0d\u6562\u7559\u4e0b\u6765\u8fc7\u591c\uff0c\u7a7f\u8863\u670d\u9b3c\u9b3c\u795f\u795f\u7684\u6e9c\u56de\u81ea\u5df1\u7684\u623f\u95f4\u4e86\u3002</p><p>\u3000\u3000\u7761\u68a6\u91cc\u611f\u89c9\u6709\u4e00\u5177\u4e30\u8174\u767d\u5ae9\u7684\u5a07\u8eaf\u8d34\u8db4\u5728\u81ea\u5df1\u7684\u8eab\u4e0a\uff0c\u5ae9\u6ed1\u7684\u624b\u4f38\u51fa\u5185\u88e4\u91cc\u7f20\u63e1\u7740\u81ea\u5df1\u7684\u4e0b\u4f53\uff0c\u5f20\u606a\u7741\u5f00\u60fa\u677e\u7684\u7761\u773c\uff0c\u662f\u665a\u6674\u4e30\u8174\u7f8e\u4e3d\u7684\u8138\u5e9e\u6df1\u60c5\u7684\u51dd\u671b\u7740\u81ea\u5df1\uff1a\u201c\u60f9\u5f97\u4eba\u5bb6\u4e00\u591c\u90fd\u6ca1\u6709\u7761\u8e0f\u5b9e\uff0c\u5fc3\u91cc\u7ed9\u585e\u4e86\u56e2\u706b\u4f3c\u7684\uff0c\u771f\u662f\u51a4\u5bb6\u554a\u2026\u2026\u8bb8\u601d\u8ddf\u9759\u6aac\u521a\u51fa\u53bb\uff0c\u4f60\u8981\u597d\u597d\u7684\u4f3a\u5019\u4eba\u5bb6\u2026\u2026\u201d\u5c06\u5f20\u606a\u7684\u5185\u88e4\u892a\u4e0b\uff0c\u63e1\u4f4f\u90a3\u6839\u4e1c\u897f\u5bf9\u81ea\u5df1\u4e0b\u4f53\u4e3b\u52a8\u7684\u5750\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u51e0\u4e2a\u80c6\u5b50\u6562\u4e0d\u597d\u597d\u7684\u4f3a\u5019\u665a\u6674\uff0c\u80fd\u4e0d\u80fd\u5b9e\u73b0\u4eba\u751f\u7684\u68a6\u60f3\uff0c\u6307\u671b\u8bb8\u601d\u4e0d\u662f\u53ef\u80fd\u7684\uff0c\u90a3\u5c31\u53ea\u80fd\u6307\u671b\u665a\u6674\u79ef\u6781\u914d\u5408\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8bb8\u601d\u8138\u76ae\u5b50\u592a\u5ae9\uff0c\u4e0d\u6562\u8ddf\u665a\u6674\u89c1\u9762\uff0c\u81f3\u5c11\u4eca\u5929\u4e0d\u6562\u5728\u5176\u4ed6\u4eba\u7684\u9762\u524d\u8ddf\u665a\u6674\u89c1\u9762\uff0c\u4e0a\u5348\u5c31\u8eb2\u5230\u4e16\u7eaa\u9526\u6e56\u91cc\u53bb\u4e86\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u8fd9\u4e9b\u65f6\u95f4\u7684\u51c6\u5907\uff0c\u6e56\u4e1c\u65b0\u57ce\u5546\u5708\u9879\u76ee\u7684\u4f4f\u5b85\u89c4\u5212\u533a\u57df\u62c6\u8fc1\u5de5\u4f5c\u5df2\u7ecf\u5b8c\u6210\uff0c\u773c\u4e0b\u6b63\u5f00\u5c55\u571f\u5730\u5e73\u6574\u5de5\u4f5c\uff0c\u5341\u4e8c\u680b\u9ad8\u5c42\u516c\u5bd3\u697c\u4e0e\u522b\u5885\u4f4f\u5b85\u533a\u9a6c\u4e0a\u5c31\u53ef\u4ee5\u52a8\u624b\u65bd\u5de5\u4e86\uff0c\u8bb8\u601d\u7684\u5de5\u4f5c\u5ba4\u627f\u63fd\u4e86\u4f4f\u5b85\u9879\u76ee\u7684\u65bd\u5de5\u8bbe\u8ba1\uff0c\u84dd\u56fe\u90fd\u5df2\u7ecf\u51fa\u6bd5\u3002\u53e6\u5916\u5728\u4f4f\u5b85\u9879\u76ee\u4e1c\u5317\u89d2\u4e34\u8fd1\u4e1c\u534e\u5927\u9053\u7684\u4e00\u4fa7\u8fd8\u8981\u5efa\u9020\u4e00\u5ea7\u5927\u578b\u7684\u5546\u4e1a\u5efa\u7b51\u4f5c\u4e3a\u6e56\u4e1c\u65b0\u57ce\u5546\u5708\u65e9\u671f\u7684\u5546\u4e1a\u914d\u5957\uff0c\u8bbe\u8ba1\u4efb\u52a1\u81ea\u7136\u4e5f\u4ea4\u7ed9\u8bb8\u601d\u7684\u5de5\u4f5c\u5ba4\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u5546\u4e1a\u9879\u76ee\u4ece\u65e9\u671f\u8bbe\u8ba1\u5f00\u59cb\u5c31\u8981\u4e0e\u4e16\u7eaa\u9526\u6e56\u9910\u996e\u5a31\u4e50\u96c6\u56e2\u7684\u7b56\u5212\u56e2\u961f\u5408\u4f5c\uff0c\u6240\u4ee5\u8bb8\u601d\u4e0e\u5de5\u4f5c\u5ba4\u7684\u8bbe\u8ba1\u4eba\u5458\u8981\u5728\u5efa\u90ba\u5b8c\u6210\u8bbe\u8ba1\u5de5\u4f5c\uff0c\u8fd9\u6070\u597d\u9042\u4e86\u5f20\u606a\u7684\u5fc3\u613f\uff0c\u665a\u6674\u8fd9\u6bb5\u65f6\u95f4\u4e5f\u662f\u8981\u5c06\u5927\u91cf\u7684\u7cbe\u529b\u90fd\u6295\u5165\u5230\u5efa\u90ba\u5206\u6821\u7684\u7b79\u5907\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u5f20\u606a\u7684\u60a0\u7136\u81ea\u5f97\uff0c\u5468\u747e\u745c\u4e0e\u8c22\u6c49\u660e\u7b49\u4eba\u5219\u9677\u5165\u7a7a\u524d\u7684\u6050\u614c\u5f53\u4e2d\u3002</p><p>\u3000\u3000\u4ece\u7ae0\u5dde\u90a3\u6761\u7ebf\u6df1\u67e5\u4e0b\u53bb\uff0c\u51e0\u4e4e\u6ca1\u6709\u53ef\u80fd\u4f1a\u89e6\u52a8\u5230\u5468\u747e\u73ba\u7684\u7b4b\u9aa8\uff0c\u800c\u4efb\u4f55\u60f3\u8c03\u67e5\u4e09\u4e95\u9a7b\u534e\u673a\u6784\u7684\u4eba\u90fd\u5fc5\u987b\u8981\u8003\u8651\u4e09\u4e95\u7269\u4ea7\u8fd9\u5bb6\u8d85\u7ea7\u4f01\u4e1a\u5728\u4e2d\u65e5\u5173\u7cfb\u4e4b\u95f4\u6240\u8d77\u5230\u7684\u5de8\u5927\u4f5c\u7528\u4ee5\u53ca\u8c03\u67e5\u4e09\u4e95\u53ef\u80fd\u4ea7\u751f\u7684\u6df1\u523b\u5f71\u54cd\uff0c\u8c01\u80fd\u60f3\u5230\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u867d\u7136\u9ad8\u5c42\u53ca\u4e3b\u6d41\u5a92\u4f53\u90fd\u8fdb\u884c\u4e86\u51b7\u5904\u7406\u7ed9\u4e88\u6e29\u548c\u7684\u5916\u4ea4\u56de\u5e94\u4f46\u6b64\u6b21\u4e8b\u4ef6\u7edd\u975e\u5bf9\u4e2d\u592e\u9ad8\u5c42\u4ee5\u53ca\u519b\u65b9\u9ad8\u7ea7\u5c06\u9886\u5168\u65e0\u89e6\u52a8\u2014\u2014\u6070\u6070\u4ece\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u9a7b\u534e\u4e8b\u52a1\u6240\u8fd9\u6761\u7ebf\u67e5\u4e0b\u53bb\u5c31\u4e00\u4e0b\u5b50\u7ed9\u5468\u747e\u73ba\u81f4\u547d\u4e4b\u51fb\u3002</p><p>\u3000\u3000\u4e2d\u592e\u751a\u81f3\u4e0d\u518d\u8c28\u614e\u5bf9\u5f85\u4e2d\u65e5\u5173\u7cfb\u4e5f\u8981\u5f7b\u5e95\u8c03\u67e5\u7a00\u571f\u4ea7\u4e1a\u4e0a\u5b58\u5728\u7684\u95ee\u9898\uff0c\u6bd4\u8d77\u4e2d\u65e5\u5173\u7cfb\uff0c\u5468\u747e\u73ba\u4e2a\u4eba\u53c8\u4f55\u8db3\u9053\u54c9\uff1f\u773c\u4e0b\u6700\u597d\u7684\u7ed3\u679c\u5c31\u662f\u5468\u747e\u73ba\u8fd9\u4e00\u5012\u80fd\u5341\u5e74\u516b\u5e74\u4e0d\u9192\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u505c\u6b62\u7528\u836f\uff01\u201d\u5468\u747e\u745c\u5373\u4f7f\u6027\u5b50\u8584\u51c9\uff0c\u542c\u5230\u4e08\u592b\u8c22\u6c49\u660e\u5982\u6b64\u7684\u5efa\u8bae\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u60ca\u8bb6\u4e07\u5206\uff0c\u201c\u4ed6\u6bd5\u7adf\u662f\u6211\u7684\u4eb2\u54e5\u54e5\uff0c\u4f60\u7adf\u7136\u5efa\u8bae\u6211\u4e0b\u8fd9\u6837\u7684\u51b3\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u662f\u8bf4\u5b8c\u5168\u505c\u6b62\u7528\u836f\uff0c\u800c\u662f\u91c7\u53d6\u4fdd\u7559\u6cbb\u7597\uff0c\u5728\u7a00\u571f\u6848\u6ca1\u6709\u4e86\u7ed3\u4e4b\u524d\uff0c\u65e0\u8bba\u6700\u7ec8\u7684\u7ed3\u679c\u5982\u4f55\uff0c\u53ea\u8981\u8c03\u67e5\u4e0b\u53bb\u5c31\u4f1a\u76f4\u63a5\u5f71\u54cd\u5230\u4f60\uff0c\u201d\u8c22\u6c49\u660e\u82e6\u53e3\u5a46\u5fc3\u7684\u8bf4\u7740\uff0c\u4f46\u662f\u773c\u795e\u7684\u5149\u8292\u53c8\u662f\u90a3\u6837\u7684\u65e0\u60c5\uff0c\u201c\u6211\u60f3\u4f60\u54e5\u54e5\u8981\u662f\u6709\u610f\u8bc6\u4e5f\u4f1a\u8ba9\u6211\u4eec\u8fd9\u4e48\u505a\u7684\uff0c\u96be\u9053\u4f60\u54e5\u54e5\u5e0c\u671b\u9192\u8fc7\u6765\u5c31\u7ed9\u56fd\u5b89\u5c40\u5e26\u8d70\u63a5\u53d7\u8c03\u67e5\uff1f\u201d</p><p>\u3000\u3000\u8fde\u591c\u8d76\u5230\u5efa\u90ba\uff0c\u5468\u747e\u745c\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u53bb\u4f11\u606f\u7247\u523b\uff0c\u773c\u6674\u91cc\u5e03\u6ee1\u8840\u4e1d\uff0c\u5176\u4ed6\u4eba\u90fd\u7b49\u5728\u5916\u9762\u7684\u5385\u91cc\uff0c\u7b49\u7740\u5979\u6765\u62ff\u4e3b\u610f\uff0c\u73b0\u5728\u5c31\u52a8\u624b\u672f\u8fd8\u662f\u6709\u6062\u590d\u610f\u8bc6\u7684\u5e0c\u671b\uff0c\u53ea\u662f\u5927\u5bb6\u5fc3\u91cc\u90fd\u6e05\u695a\uff0c\u8fd9\u65f6\u5019\u53c8\u4e0d\u662f\u5468\u747e\u73ba\u6062\u590d\u610f\u8bc6\u7684\u65f6\u673a\u2026\u2026</p></article>"}}}]);
