(window.webpackJsonp=window.webpackJsonp||[]).push([[838],{861:function(p){p.exports={chapterTitle:null,title:"\u7b2c835\u7ae0 \u5e02\u5bb9\u6267\u6cd5",content:"<article><h2>\u7b2c835\u7ae0 \u5e02\u5bb9\u6267\u6cd5</h2><p>\u3000\u3000\u591c\u98ce\u547c\u5578\uff0c\u5bc6\u5bc6\u7684\u68a7\u6850\u53f6\u5728\u5934\u9876\u788e\u54cd\uff0c\u6c99\u6c99\u58f0\u4e0d\u65ad\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u6d74\u540e\u5c0f\u8dd1\u8fc7\u6765\uff0c\u6e7f\u53d1\u8d34\u5728\u6cdb\u7ea2\u7684\u8138\u988a\u4e0a\uff0c\u8dd1\u8fc7\u6765\u6c14\u5598\u5401\u5401\uff0c\u79c0\u7f8e\u7684\u8138\u86cb\u770b\u4e0a\u53bb\u9c9c\u5ae9\u660e\u4e3d\uff0c\u5f20\u606a\u5012\u662f\u60f3\u4e0d\u5230\u8c22\u5bb6\u7684\u5c0f\u5973\u9b54\u5934\u4e5f\u6709\u5973\u4eba\u5473\u7684\u65f6\u523b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e3a\u4ec0\u4e48\u53c8\u80fd\u6070\u5de7\u9047\u4e0a\uff1f\u201d\u8c22\u5b50\u5609\u5fae\u62bf\u7740\u7c89\u6da6\u7684\u5634\u5507\uff0c\u773c\u77b3\u70af\u7136\u7684\u76ef\u7740\u7ad9\u5728\u8857\u8fb9\u957f\u6905\u4e0a\u7684\u9648\u9759\u4e0e\u5f20\u606a\uff0c\u773c\u795e\u91cc\u6709\u79cd\u4e0d\u52a0\u63a9\u9970\u7684\u66a7\u6627\u7684\u731c\u7591\u3002</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u4ec0\u4e48\uff0c\u5c31\u662f\u6070\u5de7\u9047\u4e0a\uff0c\u201d\u9648\u9759\u8138\u988a\u5fae\u7ea2\u7684\u7ad9\u8d77\u6765\u62c9\u8fc7\u5b50\u5609\u7684\u6390\u4e86\u4e00\u628a\uff0c\u53c8\u8f6c\u8fc7\u8eab\u95ee\u5f20\u606a\uff0c\u201c\u8981\u4e0d\u8981\u4e00\u8d77\u518d\u5403\u4e9b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u624d\u4e0d\u7ba1\u8c22\u5b50\u5609\u7684\u80e1\u8a00\u4e71\u8bed\uff0c\u770b\u7740\u5df7\u5b50\u53e3\u6709\u591c\u644a\u8f66\u6446\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8981\u662f\u4e0d\u71ac\u591c\uff0c\u5df7\u5b50\u53e3\u7684\u591c\u644a\u5012\u6709\u4e00\u5bb6\u4e0d\u9519\u7684\uff0c\u7b97\u6211\u8bf7\u4f60\u4eec\u2026\u2026\u4e0a\u56de\u5728\u5df7\u5b50\u53e3\u7684\u4e00\u5bb6\u591c\u644a\u5403\u8fc7\u591c\u5bb5\u5fd8\u4e86\u5e26\u94b1\uff0c\u8fd9\u6b21\u603b\u4e0d\u80fd\u5c06\u94b1\u8fd8\u7ed9\u4eba\u5bb6\u5c31\u8d70\u6389\u3002\u201d</p><p>\u3000\u3000\u591c\u644a\u8f66\u5e26\u7740\u4e00\u4e2a\u906e\u96e8\u68da\uff0c\u6536\u62fe\u5f97\u5e72\u51c0\uff0c\u4e24\u5f20\u6298\u53e0\u684c\uff0c\u644a\u4e3b\u662f\u4e2a\u6e05\u79c0\u7684\u5c11\u5987\uff0c\u770b\u4e0a\u53bb\u50cf\u644a\u4e3b\u5973\u513f\u7684\u5c0f\u5973\u5b69\u5b50\u53ea\u6709\u4e94\u516d\u5c81\u7684\u6a21\u6837\uff0c\u61c2\u4e8b\u5e2e\u5fd9\u9012\u7897\u7b77\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u9759\u3001\u8c22\u5b50\u5609\u5750\u4e00\u684c\uff0c\u5218\u660e\u8f89\u4e0e\u9648\u9759\u7684\u52a9\u624b\u5f88\u81ea\u89c9\u5750\u5230\u53e6\u4e00\u684c\u4e0a\uff0c\u5f20\u606a\u62db\u624b\u8ba9\u5218\u660e\u8f89\u4ed6\u4eec\u4e00\u8d77\u5750\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u91cc\u5c31\u4e24\u5f20\u684c\u5b50\uff0c\u6211\u4eec\u5206\u5f00\u5750\uff0c\u4eba\u5bb6\u53ef\u80fd\u4f1a\u5c11\u505a\u4e86\u751f\u610f\u2026\u2026\u201d\u5218\u660e\u8f89\u4fbf\u4e0e\u9648\u9759\u7684\u52a9\u624b\u4e00\u8d77\u5750\u8fc7\u6765\uff0c\u867d\u8bf4\u5f20\u606a\u6beb\u4e0d\u4ecb\u8482\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u662f\u60f6\u60f6\u3001\u4e0d\u80fd\u9759\u4e0b\u5fc3\u6765\u3002</p><p>\u3000\u3000\u867d\u8bf4\u4e24\u5e74\uff0c\u9526\u6e56\u4ece\u789f\u673a\u4e1a\u52a1\u4e0a\u4e00\u5e74\u4e5f\u635e\u5230\u8d85\u8fc7\u5341\u4ebf\u7684\u8d85\u989d\u5229\u6da6\uff0c\u4f46\u662f\u4e24\u5e74\u7684\u9526\u6e56\u8fd8\u53ea\u9650\u4e8e\u6d77\u5dde\u4e00\u9685\uff0c\u53ea\u51a0\u7740\u4e00\u4e2a\u592e\u89c6\u6807\u738b\u7684\u540d\u5934\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u867d\u7136\u5728\u7535\u5b50\u5de5\u4e1a\u767e\u5f3a\u699c\u4e0a\u7684\u6392\u540d\u8fd8\u4e0d\u7a81\u663e\uff0c\u4f46\u662f\u5728\u4e1a\u5185\u7684\u5730\u4f4d\u5df2\u7ecf\u8fdc\u975e\u4e24\u5e74\u524d\u7684\u9526\u6e56\u80fd\u591f\u76f8\u6bd4\uff0c\u5c31\u50cf\u8fd9\u6b21\u5728\u5317\u4eac\u4e3e\u884c\u7684\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u8bba\u575b\uff0c\u4e01\u69d0\u8fd8\u662f\u52c9\u4e3a\u5176\u96be\u7684\u62bd\u51fa\u7a7a\u524d\u53bb\u53c2\u4f1a\uff0c\u800c\u79d1\u738b\u9ad8\u79d1\u53ea\u662f\u56e0\u4e3a\u7279\u4f8b\u3001\u56e0\u4e3a\u5176\u5728\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u91cc\u7684\u7279\u6b8a\u5730\u4f4d\u624d\u5e94\u9080\u5217\u5e2d\uff0c\u4f46\u6839\u672c\u6ca1\u6709\u53d1\u8a00\u7684\u673a\u4f1a\u2014\u2014\u6b64\u65f6\u7684\u9526\u6e56\u751a\u81f3\u80fd\u6df1\u523b\u7684\u5f71\u54cd\u4e1c\u6d77\u7701\u7684\u7ecf\u6d4e\u51b3\u7b56\u3002\u5468\u4e00\u5e73\u3001\u6768\u4e91\u3001\u848b\u8587\u4e09\u4eba\u5f53\u5e74\u4e0e\u81ea\u5df1\u4e00\u8d77\u6700\u65e9\u52a0\u5165\u9526\u6e56\uff0c\u90fd\u65e9\u5df2\u7ecf\u5404\u6321\u4e00\u9762\uff0c\u4e5f\u8fdc\u975e\u6b64\u65f6\u7684\u81ea\u5df1\u80fd\u591f\u76f8\u6bd4\u7684\u3002</p><p>\u3000\u3000\u56de\u60f3\u5f80\u4e8b\u79cd\u79cd\uff0c\u5218\u660e\u8f89\u5fc3\u91cc\u767e\u611f\u4ea4\u96c6\uff0c\u6c89\u9ed8\u7740\u542c\u522b\u4eba\u8bf4\u8bdd\uff0c\u4e5f\u5c11\u63d2\u8bdd\u3002</p><p>\u3000\u3000\u9664\u4e86\u7cbd\u5b50\u4e4b\u5916\uff0c\u591c\u5bb5\u8f66\u8fd8\u63d0\u4f9b\u677e\u5b50\u7cd5\u3001\u6842\u82b1\u7cd5\u7b49\u7cd5\u70b9\uff0c\u8fd8\u6709\u7a00\u7ca5\u5728\u5c0f\u9505\u91cc\u71ac\u7740\u3002\u5f20\u606a\u8981\u4e86\u5496\u5561\u4e0e\u677e\u5b50\u7cd5\uff0c\u5c1d\u8fc7\u4e4b\u540e\uff0c\u53e3\u611f\u76f8\u5f53\u4e0d\u9519\uff0c\u611f\u89c9\u50cf\u662f\u81ea\u5236\u7684\u7cd5\u70b9\uff0c\u4f46\u662f\u5728\u8fd9\u91cc\u5750\u4e0b\u6765\u4e4b\u540e\uff0c\u5f20\u606a\u5df2\u7ecf\u7ed9\u8c22\u5b50\u5609\u5632\u7b11\u8bf4\u662f\u201c\u5373\u4f7f\u5403\u591c\u5bb5\u4e5f\u4f1a\u8003\u8651\u644a\u4e3b\u5916\u8c8c\u7684\u5bb6\u4f19\u201d\uff0c\u5f20\u606a\u5012\u4e0d\u4fbf\u62ff\u8fd9\u4e2a\u8bdd\u9898\u8ddf\u644a\u4e3b\u642d\u8bdd\uff0c\u5fc3\u60f3\u81ea\u5df1\u5728\u8c22\u5b50\u5609\u773c\u91cc\u4f55\u65f6\u7adf\u6210\u4e86\u4e00\u4e2a\u597d\u8272\u4e4b\u5f92\uff1f</p><p>\u3000\u3000\u5012\u662f\u9648\u9759\u5bf9\u5b88\u7740\u644a\u5b50\u7684\u5c0f\u5973\u5b69\u5b50\u86ee\u611f\u5174\u8da3\uff0c\u9017\u7740\u5c0f\u5973\u5b69\u5b50\u8bf4\u8bdd\uff0c\u4e5f\u8ddf\u5c11\u5987\u804a\u4e86\u51e0\u53e5\uff0c\u624d\u6653\u5f97\u8fd9\u6a21\u6837\u6e05\u79c0\u7684\u5c11\u5987\u59d3\u7f57\uff0c\u6709\u4e2a\u5f88\u5927\u4f17\u7684\u540d\u5b57\uff1a\u7f57\u6885\u3002\u7f57\u6885\u539f\u662f\u4e00\u5bb6\u5de5\u5382\u98df\u5802\u7684\u7cd5\u70b9\u5e08\u5085\uff0c\u4ece\u5de5\u5382\u4e0b\u5c97\u540e\uff0c\u4e00\u65f6\u627e\u4e0d\u5230\u522b\u7684\u5de5\u4f5c\uff0c\u53ea\u80fd\u4e5d\u70b9\u949f\u8fc7\u540e\u63a8\u7740\u591c\u5bb5\u8f66\u5230\u8fd9\u91cc\u6323\u4e9b\u751f\u6d3b\u8d39\uff0c\u5973\u513f\u4e22\u5728\u5bb6\u91cc\u4e5f\u6ca1\u6709\u4eba\u7167\u5e94\uff0c\u591c\u91cc\u5e26\u51fa\u6765\u8fd8\u80fd\u5e2e\u4e9b\u5fd9\u3002</p><p>\u3000\u3000\u770b\u5c11\u5987\u7f57\u6885\u7709\u5934\u5fae\u9501\uff0c\u5f20\u606a\u5fc3\u60f3\u5979\u6216\u8bb8\u751f\u6d3b\u53e6\u6709\u82e6\u5904\uff0c\u53ea\u4e0d\u8fc7\u4ea4\u6d45\u8a00\u672a\u6df1\uff0c\u4e5f\u4e0d\u4fbf\u6253\u542c\u4ec0\u4e48\uff0c\u603b\u4e4b\u4e94\u516d\u5c81\u7684\u5c0f\u5973\u5b69\u5b50\u8ddf\u5927\u4eba\u71ac\u591c\u5230\u51cc\u6668\uff0c\u603b\u4e0d\u662f\u5e38\u4eba\u60f3\u8fc7\u7684\u751f\u6d3b\u3002\u7f57\u6885\u65f6\u4e0d\u65f6\u6253\u53d1\u5979\u5973\u513f\u5230\u5df7\u5b50\u53e3\u5916\u7684\u8def\u8fb9\u53bb\u653e\u54e8\uff0c\u62c5\u5fc3\u5e02\u5bb9\u6267\u6cd5\u961f\u7684\u4eba\u8fc7\u6765\u2014\u2014\u8fd9\u91cc\u591c\u95f4\u63a8\u7740\u8f66\u51fa\u6765\u6446\u644a\u7684\u4eba\u5c31\u6015\u5e02\u5bb9\u6267\u6cd5\u961f\u7684\u4eba\uff0c\u770b\u60c5\u5f62\u5c0f\u5973\u5b69\u5b50\u5012\u662f\u5df7\u5b50\u53e3\u51e0\u5bb6\u591c\u644a\u5171\u540c\u7684\u5c0f\u54e8\u5175\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c\u5efa\u90ba\u5e02\u653f\u5e9c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u4e0b\u8bbe\u57ce\u5e02\u7ba1\u7406\u5c40\uff0c\u7531\u5e02\u653f\u7ba1\u7406\u5c40\u8d1f\u8d23\u5e02\u5bb9\u7ba1\u7406\u7684\u804c\u80fd\u3002</p><p>\u3000\u3000\u5c0f\u5973\u5b69\u5b50\u624d\u4e94\u516d\u5c81\uff0c\u73a9\u6027\u5927\uff0c\u5f20\u606a\u4e0e\u9648\u9759\u5979\u4eec\u5728\u8fd9\u91cc\u804a\u7740\u5929\uff0c\u5c31\u770b\u89c1\u5c0f\u5973\u5b69\u5b50\u5fd8\u8bb0\u5979\u5988\u5988\u4ea4\u5f85\u7ed9\u5979\u7684\u4efb\u52a1\uff0c\u4e00\u4e2a\u4eba\u62ff\u7740\u5c0f\u6c99\u5305\u8e72\u5728\u8def\u7259\u4e0a\u63b7\u7740\u73a9\u3002</p><p>\u3000\u3000\u8fde\u5403\u591c\u5bb5\u8fb9\u804a\u5929\uff0c\u8bf4\u8d77\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4eca\u5e74\u8fd8\u6ca1\u6709\u53d1\u4e0b\u4e00\u5f20\u624b\u673a\u724c\u7167\uff0c\u5f20\u606a\u8ddf\u9648\u9759\u900f\u9732\u4e9b\u5185\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u56fd\u5185\u5176\u4ed6\u5382\u5546\u7684\u6280\u672f\u6761\u4ef6\u8fd8\u4e0d\u8db3\u591f\u6210\u719f\uff0c\u4eca\u5e74\u7684\u624b\u673a\u724c\u7167\u53ef\u80fd\u4f1a\u503e\u5411\u7ed9\u5916\u8d44\u4f01\u4e1a\uff0c\u4fc3\u4f7f\u5916\u8d44\u624b\u673a\u5382\u5546\u5c06\u751f\u4ea7\u57fa\u5730\u8fc1\u79fb\u5230\u56fd\u5185\u6765\uff0c\u6240\u4ee5\u8bf4\u6765\u624b\u673a\u724c\u7167\u7684\u53d1\u653e\u53ef\u80fd\u4f1a\u62d6\u4e00\u62d6\u2026\u2026\u4e0d\u8fc7\u73b0\u9636\u6bb5\u624b\u673a\u91cc\u7684\u5229\u6da6\u5c45\u9ad8\u4e0d\u4e0b\uff0c\u5916\u8d44\u5382\u5546\u4e5f\u4e0d\u4f1a\u6025\u4e8e\u5c06\u751f\u4ea7\u57fa\u5730\u8fc1\u5230\u56fd\u5185\u6765\u554a\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u4e3a\u4e86\u4fdd\u62a4\u8054\u8baf\u3001\u8054\u4fe1\u3001\u4e1c\u5174\u4e09\u5bb6\u4f01\u4e1a\u7684\u5229\u76ca\uff0c\u4e0d\u8fc7\u5bf9\u4f60\u4eec\u6765\u8bf4\u4e5f\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u673a\u4f1a\u3002\u201d</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u6b64\u65f6\u8fd8\u6ca1\u6709\u62ff\u624b\u673a\u724c\u7167\u7684\u673a\u4f1a\uff0c\u6682\u65f6\u4e0d\u7ed9\u56fd\u5185\u5382\u5546\u53d1\u724c\u7167\uff0c\u90a3\u79d1\u738b\u9ad8\u79d1\u4f9d\u65e7\u662f\u56db\u5927\u56fd\u4ea7\u624b\u673a\u5382\u5546\u4e4b\u5916\u7684\u7b2c\u4e94\u5bb6\uff0c\u79d1\u738b\u9ad8\u79d1\u4e5f\u53ef\u4ee5\u5229\u7528\u8fd9\u6bb5\u65f6\u95f4\u6253\u4e0b\u66f4\u575a\u5b9e\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u2026\u2026\u6280\u672f\u8fd9\u4e00\u5757\u5c06\u662f\u6700\u5927\u7684\u8fdb\u5165\u95e8\u69db\uff0c\u65e0\u8bba\u4ece\u54ea\u65b9\u9762\u6765\u8bf4\uff0c\u9526\u6e56\u90fd\u4f1a\u534f\u540c\u8054\u4fe1\u3001\u8054\u8baf\u8fd8\u6709\u4e1c\u5174\u9650\u5236\u6295\u673a\u8005\u8fdb\u5165\u8fd9\u4e2a\u884c\u4e1a\u2014\u2014\u4f60\u4eec\u4e5f\u6e05\u695a\uff0c\u6295\u673a\u8005\u53ea\u4f1a\u8ba9\u6574\u4e2a\u884c\u4e1a\u53d8\u5f97\u66f4\u52a0\u6df7\u4e71\uff0c\u53ea\u4f1a\u8ba9\u4e2d\u56fd\u7684\u5e02\u573a\u53d8\u6210\u6d77\u5916\u6280\u672f\u4f9b\u7ed9\u5546\u7684\u9644\u5eb8\u5e02\u573a\u3002\u6d77\u5dde\u7684\u5546\u4e1a\u94f6\u884c\u4f1a\u4e13\u95e8\u4e3a\u9ad8\u79d1\u4f01\u4e1a\u7684\u6280\u672f\u7814\u7a76\u5212\u51fa\u4e00\u4e2a\u4e13\u9879\u7684\u8d37\u6b3e\u989d\u5ea6\u6765\uff0c\u79d1\u738b\u8981\u662f\u51c6\u5907\u5728\u624b\u673a\u4e1a\u52a1\u4e0a\u505a\u5f3a\u505a\u5927\uff0c\u53ef\u4ee5\u5411\u6d77\u5546\u884c\u7533\u8bf7\u4e00\u4e9b\u8d37\u6b3e\u52a0\u5f3a\u7814\u53d1\u4e0a\u7684\u5de5\u4f5c\u3002\u201d</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u7684\u5e95\u5b50\u6bd4\u8f83\u8584\uff0c\u867d\u7136\u5df2\u7ecf\u8fde\u7eed\u51e0\u4e2a\u6708\u4ece\u8d34\u724c\u624b\u673a\u4e1a\u52a1\u4e0a\u83b7\u5f97\u4e0d\u9519\u7684\u76c8\u5229\uff0c\u4f46\u662f\u9700\u8981\u82b1\u94b1\u7684\u5730\u65b9\u592a\u591a\u4e86\uff0c\u5de5\u5382\u6269\u5efa\u3001\u8bbe\u5907\u5f15\u8fdb\u3001\u4eba\u5458\u62db\u8058\u57f9\u8bad\u3001\u5e02\u573a\u6e20\u9053\u7684\u5efa\u8bbe\u4e0e\u5ba3\u4f20\uff0c\u6ca1\u6709\u4e00\u5904\u4e0d\u662f\u6295\u5165\u5de8\u989d\u8d44\u91d1\u7684\uff0c\u5206\u5230\u6280\u672f\u7814\u7a76\u4e0a\u7684\u6295\u5165\u5c31\u6781\u4e3a\u6709\u9650\u4e86\uff0c\u5728\u5efa\u90ba\u7684\u624b\u673a\u8bbe\u8ba1\u5de5\u4f5c\u5ba4\u914d\u5957\u4e86\u8fd1\u5341\u591a\u540d\u5458\u5de5\uff0c\u4f46\u662f\u53ea\u505c\u7559\u5728\u6d45\u5c42\u6b21\u7684\u4ea7\u54c1\u8bbe\u8ba1\u5c42\u6b21\u4e0a\u3002\u5f20\u606a\u7684\u610f\u601d\u4e5f\u518d\u660e\u663e\u4e0d\u8fc7\uff0c\u79d1\u738b\u9ad8\u79d1\u5bf9\u624b\u673a\u7684\u6280\u672f\u6295\u5165\u4ec5\u505c\u7559\u5728\u8fd9\u4e00\u5c42\u6b21\uff0c\u5f88\u53ef\u80fd\u5c31\u62ff\u4e0d\u5230\u6700\u4e3a\u5173\u952e\u7684\u624b\u673a\u724c\u7167\u3002</p><p>\u3000\u3000\u8fd9\u8fb9\u6b63\u804a\u5f97\u6b22\uff0c\u644a\u4e3b\u7f57\u6885\u7a81\u7136\u8dd1\u8fc7\u6765\u8981\u6536\u62fe\u7897\u789f\uff0c\u8c22\u5b50\u5609\u60ca\u8c14\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8fd8\u6ca1\u6709\u5403\u597d\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u5934\u671b\u53bb\uff0c\u6709\u4e24\u8f86\u8f66\u8eab\u4fa7\u9762\u6d82\u7740\u201c\u5e02\u5bb9\u6267\u6cd5\u201d\u7ea2\u5b57\u7684\u76ae\u5361\u505c\u5728\u8def\u7259\u4e0a\uff0c\u9664\u4e86\u4e24\u8f86\u76ae\u5361\u4e4b\u5916\uff0c\u5341\u591a\u540d\u7a7f\u7740\u7070\u7eff\u8272\u5236\u670d\u7684\u4eba\u6b63\u997f\u864e\u6251\u7f8a\u4f3c\u7684\u671d\u8fd9\u8fb9\u51b2\u6765\uff0c\u5728\u8def\u7259\u8fb9\u73a9\u5f97\u8d77\u6027\u3001\u5fd8\u6389\u653e\u54e8\u8d23\u4efb\u7684\u5c0f\u5973\u5b69\u5b50\u4e0d\u77e5\u6240\u63aa\u7684\u7ad9\u5728\u90a3\u91cc\u2014\u2014\u5979\u663e\u7136\u73a9\u4e86\u8d77\u6027\u3001\u5fd8\u4e86\u8981\u5728\u8def\u8fb9\u653e\u54e8\u4e86\u3002\u90a3\u4e9b\u53ea\u5728\u5730\u4e0a\u94fa\u4e00\u5f20\u6cb9\u5e03\u5356\u4e9b\u5c0f\u73a9\u827a\u513f\u7684\u644a\u4e3b\u52a8\u4f5c\u6781\u5feb\u7684\u94fa\u7740\u4e1c\u644a\u5b50\u5c31\u6e9c\uff0c\u7f57\u6885\u5374\u6765\u4e0d\u53ca\u63a8\u7740\u591c\u5bb5\u8f66\u9003\u79bb\uff0c\u5f20\u606a\u751a\u81f3\u90fd\u6765\u4e0d\u53ca\u5c06\u5634\u91cc\u7684\u90a3\u53e3\u677e\u5b50\u7cd5\u56bc\u54bd\u4e0b\u53bb\uff0c\u5c31\u6709\u4e09\u56db\u4e2a\u6267\u6cd5\u961f\u7684\u961f\u5458\u5c06\u591c\u5bb5\u8f66\u4e00\u628a\u6293\u4f4f\uff0c\u4e3a\u9996\u7684\u90a3\u4eba\u4e00\u8fb9\u6307\u6325\u624b\u4e0b\u7684\u961f\u5458\u53bb\u8ffd\u56db\u5904\u4e71\u9003\u7684\u644a\u4e3b\uff0c\u4e00\u8fb9\u4e0d\u8010\u70e6\u7684\u6307\u6325\u4eba\u5904\u7f6e\u591c\u5bb5\u8f66\uff1a\u201c\u4e1c\u897f\u90fd\u4e22\u6389\uff0c\u8f66\u5b50\u4e22\u76ae\u5361\u91cc\u62d6\u8d70\uff0c\u4e0d\u5c06\u8fd9\u4e9b\u5201\u6c11\u7684\u5bb6\u4f19\u90fd\u7ed9\u7838\u70c2\uff0c\u6574\u4e0d\u5e72\u51c0\u2026\u2026\u201d</p><p>\u3000\u3000\u7f57\u6885\u521a\u5f00\u59cb\u6709\u4e9b\u53d1\u6123\uff0c\u5f85\u542c\u5230\u8d56\u4ee5\u751f\u5b58\u7684\u591c\u5bb5\u8f66\u8981\u7ed9\u62d6\u8d70\uff0c\u7a81\u7136\u9192\u8fc7\u795e\u6765\u6293\u4f4f\u8f66\u628a\u624b\u6574\u4e2a\u4eba\u8d56\u5750\u5230\u5730\u4e0a\uff0c\u568e\u5555\u5927\u54ed\u8d77\u6765\uff1a\u201c\u4e0d\u8981\u5c06\u6211\u7684\u8f66\u62c9\u8d70\uff0c\u6211\u4e0b\u56de\u518d\u4e5f\u4e0d\u51fa\u6765\u6446\u644a\u4e86\uff0c\u8fd9\u8f66\u662f\u501f\u4eba\u5bb6\u7684\uff0c\u7ed9\u4f60\u4eec\u62c9\u8d70\uff0c\u6211\u62ff\u4ec0\u4e48\u8fd8\u2026\u2026\u201d\u5c0f\u5973\u5b69\u5b50\u8fd9\u65f6\u5019\u4e5f\u8dd1\u8fc7\u6765\uff0c\u5e2e\u7740\u5979\u5988\u5988\u4e00\u8d77\u6293\u4f4f\u8f66\u628a\u4e0d\u8ba9\u4eba\u5c06\u591c\u5bb5\u8f66\u62d6\u8d70\uff0c\u545c\u545c\u7684\u54ed\u4e86\u8d77\u6765\uff1a\u201c\u90fd\u662f\u56e1\u56e1\u7684\u9519\uff0c\u56e1\u56e1\u4e0b\u6b21\u518d\u4e5f\u4e0d\u8d2a\u73a9\u4e86\uff0c\u4f60\u4eec\u4e0d\u8981\u5c06\u8f66\u62c9\u8d70\uff0c\u56e1\u56e1\u4e0b\u6b21\u518d\u4e5f\u4e0d\u8d2a\u73a9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e24\u540d\u6267\u6cd5\u961f\u5458\u5c06\u6bcd\u5973\u4fe9\u7684\u624b\u4ece\u8f66\u628a\u624b\u4e0a\u786c\u63b0\u5f00\uff0c\u62c9\u5728\u4e00\u8fb9\uff0c\u53c8\u6709\u4e24\u540d\u6267\u6cd5\u961f\u5458\u5c06\u8f66\u4e0a\u7684\u7cd5\u70b9\u3001\u94dd\u9505\u91cc\u7684\u7ca5\u3001\u7cbd\u5b50\u3001\u8336\u53f6\u86cb\u8fd8\u6709\u996e\u6599\u7b49\u7b49\u7edf\u7edf\u62ff\u5851\u6599\u5e03\u5305\u7740\u4e22\u5df7\u5b50\u53e3\u7684\u5783\u573e\u7b52\u91cc\uff0c\u5c06\u591c\u5bb5\u8f66\u62ac\u5230\u76ae\u5361\u7684\u540e\u8f66\u53a2\u91cc\uff0c\u53c8\u8dd1\u8fc7\u6765\u8981\u5c06\u6298\u53e0\u684c\u6905\u62ff\u8d70\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u770b\u4e0d\u8fc7\u53bb\uff0c\u5bf9\u7740\u8981\u8fc7\u6765\u62ff\u8d70\u6298\u53e0\u684c\u7684\u6267\u6cd5\u961f\u5458\u8bf4\u9053\uff1b\u201c\u4f60\u4eec\u77e5\u4e0d\u77e5\u9053\u4ec0\u4e48\u53eb\u6587\u660e\u6267\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4ed6\u5988\u7684\u5c11\u7ba1\u95f2\u4e8b\u3002\u4ec0\u4e48\u53eb\u6587\u660e\u6267\u6cd5\uff1f\u8fd9\u4e9b\u5201\u6c11\u8981\u4e0d\u80fd\u4e00\u6b21\u5c06\u4ed6\u4eec\u6574\u6015\u4e86\uff0c\u4ed6\u4eec\u5929\u5929\u51fa\u6765\u8ddf\u4f60\u6253\u6e38\u51fb\uff0c\u8c01\u4ed6\u5988\u53d7\u5f97\u4e86\uff1f\u8fd8\u6709\uff0c\u4f60\u4eec\u8d2d\u4e70\u4e0d\u6cd5\u644a\u8d29\u7684\u98df\u54c1\uff0c\u5c31\u662f\u8ddf\u6211\u4eec\u6267\u6cd5\u961f\u8fc7\u4e0d\u53bb\uff0c\u8bf7\u4f60\u4eec\u79bb\u5f00\u5c31\u662f\u5ba2\u6c14\u7684\u2026\u2026\u8981\u4e0d\u4f60\u4eec\u8fd9\u4e9b\u4eba\u8d2a\u4fbf\u5b9c\u60ef\u7740\u4ed6\u4eec\uff0c\u5efa\u90ba\u54ea\u6765\u8fd9\u4e48\u591a\u5783\u573e\uff1f\u201d\u6267\u6cd5\u961f\u4e3a\u9996\u90a3\u4eba\u65e9\u77a5\u7740\u9648\u9759\u4eba\u957f\u5f97\u5a07\u8273\uff0c\u6328\u5728\u8fd9\u8fb9\u5c31\u6ca1\u6709\u8d70\u8fdc\uff0c\u542c\u89c1\u9648\u9759\u7ad9\u8d77\u6765\u8981\u6253\u62b1\u4e0d\u5e73\uff0c\u6a2a\u7709\u6012\u773c\u5c31\u8bad\u65a5\u3002</p><p>\u3000\u3000\u9648\u9759\u6c14\u5f97\u4fcf\u8138\u53d1\u767d\uff0c\u5979\u5a07\u6ef4\u6ef4\u7684\u4e00\u4e2a\u5973\u5b50\uff0c\u53c8\u4e0d\u80fd\u8d70\u5230\u8ddf\u524d\u8ddf\u8fd9\u8170\u5706\u8180\u9614\u7684\u6c49\u5b50\u4e89\u5f3a\u6597\u72e0\uff0c\u9648\u9759\u7684\u52a9\u7406\u7ad9\u8d77\u6765\u4e89\u8bba\uff1a\u201c\u4f60\u4eec\u7684\u9886\u5bfc\u662f\u8c01\uff0c\u6709\u4f60\u4eec\u8fd9\u4e48\u6267\u6cd5\u7684\u5417\uff1f\u6211\u4eec\u8981\u6253\u7535\u8bdd\u8ddf\u4f60\u4eec\u9886\u5bfc\u6295\u8bc9\u4f60\u4eec\u91ce\u86ee\u6267\u6cd5\u7684\u884c\u4e3a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u91ce\u86ee\u6267\u6cd5\uff1f\u6295\u8bc9\uff01\u201d\u90a3\u4eba\u62ac\u811a\u5c31\u5c06\u6298\u53e0\u684c\u8e22\u5230\u4e00\u8fb9\uff0c\u201c\u4f60\u4eec\u4ed6\u5988\u7684\u7ed9\u6211\u6295\u8bc9\u53bb\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u907f\u4e0d\u53ca\uff0c\u7ed9\u684c\u4e0a\u6380\u7ffb\u7684\u5496\u5561\u6cfc\u4e86\u4e00\u8eab\uff0c\u5218\u660e\u8f89\u5927\u817f\u5916\u4fa7\u7ed9\u8e22\u98de\u7684\u684c\u89d2\u649e\u4e86\u4e00\u4e0b\uff0c\u649e\u5f97\u751f\u75bc\uff0c\u4e00\u65f6\u6ca1\u6709\u53cd\u5e94\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d77\u4e86\u6027\u5b50\uff0c\u521a\u8981\u53d1\u4f5c\uff0c\u5c31\u542c\u89c1\u8eab\u540e\u4e00\u58f0\u6247\u8033\u5149\u7684\u8106\u54cd\uff0c\u8f6c\u5934\u770b\u89c1\uff0c\u7f57\u6885\u51b2\u7740\u4e00\u540d\u6267\u6cd5\u961f\u5458\u53d1\u75af\u7684\u53c8\u6293\u53c8\u54ac\uff1a\u201c\u4f60\u4eec\u8fd9\u4e9b\u755c\u751f\uff0c\u4f60\u4eec\u7adf\u7136\u52a8\u624b\u6253\u5b69\u5b50\u2026\u2026\u201d\u90a3\u540d\u6267\u6cd5\u961f\u5458\u4e3e\u8d77\u624b\u638c\uff1a\u201c\u5c0f\u755c\u751f\u54ac\u6211\u2026\u2026\u201d\u53e6\u4e00\u540d\u6267\u6cd5\u961f\u5458\u8fc7\u6765\u5c06\u7f57\u6885\u6293\u4f4f\u6441\u5728\u5730\u4e0a\u8ba9\u5979\u65e0\u6cd5\u52a8\u5f39\uff0c\u5f20\u606a\u8fd9\u624d\u770b\u89c1\u5c0f\u5973\u5b69\u5b50\u767d\u5ae9\u7684\u8138\u6e05\u6670\u7684\u9732\u51fa\u4e94\u4e2a\u624b\u6307\u5370\uff0c\u7ed9\u6253\u4e86\u53d1\u8499\uff0c\u8dcc\u5750\u5728\u5730\u4e0a\u5374\u6ca1\u6709\u54ed\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u5927\u6068\uff0c\u8d70\u8fc7\u6765\u90a3\u540d\u6267\u6cd5\u961f\u5458\u7684\u8eab\u540e\u4e00\u628a\u63ea\u4f4f\u4ed6\u7684\u5934\u53d1\u5c06\u4ed6\u6380\u5012\u53c8\u8865\u4e0a\u4e00\u811a\u8e29\u4e0a\u4ed6\u7684\u809a\u5b50\uff1a\u201c\u755c\u751f\uff0c\u8981\u6253\u67b6\u4f60\u51b2\u6211\u6765\uff01\u201d\u5176\u4ed6\u6267\u6cd5\u961f\u5458\u770b\u89c1\u8fd9\u91cc\u52a8\u8d77\u6765\u624b\uff0c\u5f20\u7259\u821e\u722a\u7684\u8981\u6251\u8fc7\u6765\uff0c\u53ca\u65f6\u8d76\u5230\u7684\u5085\u4fca\u6293\u8d77\u6298\u53e0\u684c\u53cc\u624b\u4e00\u5206\u5c31\u62c6\u6563\u4e86\uff0c\u559d\u4f4f\u90a3\u4e9b\u6267\u6cd5\u961f\u5458\uff1a\u201c\u8c01\u6562\u4e0a\u6765\uff01\u201d\u53c8\u4e00\u628a\u6293\u4f4f\u5c06\u7f57\u6885\u6441\u5012\u5728\u5730\u4e0a\u7684\u90a3\u540d\u6267\u6cd5\u961f\u5458\u731b\u7136\u63a8\u5f00\uff0c\u90a3\u540d\u6267\u6cd5\u961f\u5458\u8dcc\u4e86\u4e00\u8de4\u722c\u8d77\u6765\u53c8\u662f\u4e00\u8de4\u624d\u8e09\u8dc4\u7ad9\u4f4f\uff0c\u8fd9\u624d\u8ba9\u6267\u6cd5\u961f\u5176\u4ed6\u7684\u4eba\u4e0d\u6562\u5192\u7136\u51b2\u4e0a\u6765\u3002</p><p>\u3000\u3000\u8ddf\u7740\u5085\u4fca\u8fc7\u6765\u7684\u4e24\u540d\u4fdd\u536b\u5c06\u5f20\u606a\u4e0e\u5c0f\u5973\u5b69\u5b50\u62a4\u5728\u8eab\u540e\uff0c\u5f20\u606a\u8865\u4e86\u4e00\u811a\u5c06\u7ed9\u4ed6\u63ea\u4f4f\u5934\u53d1\u8e22\u51fa\u53bb\u7684\u6267\u6cd5\u961f\u5458\u8e22\u4e86\u51fa\u53bb\uff0c\u9648\u9759\u3001\u8c22\u5b50\u5609\u3001\u5218\u660e\u8f89\u4ed6\u4eec\u4e5f\u805a\u5230\u8fd9\u8fb9\u6765\u3002</p><p>\u3000\u3000\u7f57\u6885\u8fd9\u65f6\u5019\u8d70\u8fc7\u6765\u5c06\u5973\u513f\u6402\u5728\u6000\u91cc\uff1b\u5012\u662f\u5c0f\u5973\u5b69\u5b50\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u54ed\u51fa\u58f0\u6765\uff0c\u5f20\u606a\u62c5\u5fc3\u5979\u7ed9\u4e00\u5df4\u638c\u6253\u574f\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u662f\u8c01\uff0c\u6562\u52a8\u624b\u6253\u5e02\u5bb9\u6267\u6cd5\u961f\u5458\uff1f\u201d\u6267\u6cd5\u961f\u4e3a\u9996\u7684\u90a3\u4eba\u89c1\u8fd9\u4f1a\u513f\u5de5\u592b\u4e24\u540d\u961f\u5458\u7ed9\u6253\u4e86\uff0c\u9f3b\u5b50\u90fd\u7ed9\u6c14\u6b6a\u4e86\uff0c\u4f46\u662f\u521a\u5f00\u4e00\u54c4\u800c\u6563\u7684\u644a\u4e3b\u89c1\u8fd9\u9762\u95f9\u4e86\u4e8b\uff0c\u53c8\u90fd\u805a\u4e86\u56de\u6765\uff0c\u52a0\u4e0a\u5176\u4ed6\u8def\u8fc7\u56f4\u89c2\u7684\u5b66\u751f\uff0c\u4e5f\u6ca1\u6709\u547d\u4ee4\u624b\u4e0b\u4e00\u54c4\u800c\u4e0a\u5c06\u8fd9\u4e9b\u6562\u8ddf\u6267\u6cd5\u8fc7\u4e0d\u53bb\u7684\u5f53\u573a\u6253\u7ffb\u6389\uff0c\u53ea\u8bf4\u9053\uff1a\u201c\u5c06\u8fd9\u4e9b\u90fd\u5e26\u5230\u961f\u91cc\u53bb\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u533a\u6267\u6cd5\u961f\u7684\u5206\u7ba1\u9886\u5bfc\u662f\u8c01\uff1f\u8981\u627e\u8c01\u53eb\u8fc7\u6765\u5c31\u80fd\u8ba9\u4f60\u4eec\u8fd9\u4e9b\u4eba\u5b66\u4f1a\u6587\u660e\u6267\u6cd5\uff1f\u201d\u5f20\u606a\u7ad9\u51fa\u6765\u8bf4\u9053\uff0c\u4ed6\u53ea\u5f53\u8fd9\u4e9b\u4eba\u662f\u9ad8\u65b0\u533a\u5e02\u653f\u6267\u6cd5\u961f\u7684\u3002</p><p>\u3000\u3000\u201c\u53e3\u6c14\u5012\u4e0d\u5c11\uff0c\u4f60\u6709\u79cd\u7ed9\u8001\u5b50\u5c06\u7f57\u541b\u53eb\u8fc7\u6765\uff0c\u8001\u5b50\u5c31\u77e5\u9053\u4ec0\u4e48\u53eb\u6587\u660e\u6267\u6cd5\uff01\u201d\u90a3\u4eba\u7709\u5934\u4e00\u626c\uff0c\u90a3\u795e\u6001\u4f3c\u4e4e\u5634\u91cc\u7684\u4e00\u53e3\u6c14\u4e0b\u4e00\u79d2\u949f\u5c31\u8981\u5410\u5230\u5f20\u606a\u7684\u8138\u4e0a\uff0c\u201c\u4f60\u4ee5\u4e3a\u9ad8\u65b0\u533a\u8ba4\u8bc6\u51e0\u4e2a\u4eba\u5c31\u8ddf\u8001\u5b50\u53eb\u56a3\uff01\u8001\u5b50\u53eb\u5218\u519b\uff0c\u628a\u8bdd\u645e\u5728\u8fd9\u91cc\uff0c\u4eca\u5929\u5e02\u6267\u6cd5\u961f\u8fc7\u6765\u7a81\u51fb\u6267\u6cd5\uff0c\u4e24\u540d\u5e02\u6267\u6cd5\u961f\u7684\u961f\u5458\u7ed9\u4f60\u4eec\u52a8\u624b\u6253\u4e86\uff0c\u627e\u8c01\u6765\u90fd\u4e0d\u6210\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u60f3\u8981\u7f57\u4e66\u8bb0\u4eb2\u81ea\u6559\u5bfc\u4f60\u4eec\u5982\u4f55\u6587\u660e\u6267\u6cd5\uff1f\u201d\u5f20\u606a\u4e0d\u8ba4\u8bc6\u8fd9\u4e2a\u5218\u519b\u662f\u8c01\uff0c\u542c\u5230\u4ed6\u7684\u8bdd\u6c14\u6781\u800c\u7b11\uff0c\u6ca1\u60f3\u5230\u662f\u5e02\u653f\u7ba1\u7406\u5c40\u4f1a\u7a81\u7136\u5230\u8fd9\u91cc\u6765\u7a81\u51fb\u6267\u6cd5\uff0c\u591a\u5c11\u6709\u4e9b\u8e4a\u8df7\uff0c\u773c\u91cc\u4e5f\u7ba1\u4e0d\u4e86\u592a\u591a\uff0c\u4fa7\u5934\u5429\u5490\u5085\u4fca\uff0c\u201c\u4f60\u7ed9\u7f57\u4e66\u8bb0\u6253\u4e2a\u7535\u8bdd\uff0c\u5c06\u60c5\u51b5\u7ed9\u7f57\u4e66\u8bb0\u53cd\u5e94\u4e00\u4e0b\u2026\u2026\u201d\u4ed6\u62ff\u51fa\u7535\u8bdd\u62e8\u6253120\uff0c\u5c0f\u5973\u5b69\u5b50\u8fd9\u65f6\u5019\u90fd\u8fd8\u6ca1\u6709\u54ed\u51fa\u58f0\u6765\uff0c\u8fd8\u662f\u5148\u9001\u533b\u9662\u68c0\u67e5\u4e00\u4e0b\u59a5\u5f53\uff0c\u4f46\u662f\u8fd9\u4e9b\u6267\u6cd5\u961f\u7684\u5bb6\u4f19\u864e\u89c6\u7708\u7708\uff0c\u53ea\u6015\u4e0d\u4f1a\u540c\u610f\u4ed6\u4eec\u5f00\u8f66\u79bb\u5f00\uff0c\u53ea\u80fd\u5148\u901a\u77e5\u533b\u9662\u6d3e\u6551\u62a4\u8f66\u6765\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u5218\u519b\u4e5f\u4e0d\u662f\u552c\u5927\u7684\uff0c\u89c1\u5085\u4fca\u3001\u5f20\u606a\u5728\u90a3\u91cc\u90fd\u62ff\u51fa\u624b\u673a\u6765\u6253\u7535\u8bdd\uff0c\u4ed6\u4e5f\u62ff\u51fa\u4f53\u79ef\u7855\u5927\u7684\u6469\u6258\u7f57\u62c9\u624b\u673a\u6253\u7535\u8bdd\u62c9\u4eba\u8fc7\u6765\uff1a\u201c\u961f\u91cc\u73b0\u5728\u8fd8\u6709\u591a\u5c11\u4e2a\u4eba\uff0c\u90fd\u4ed6\u5988\u90fd\u8001\u5b50\u5230\u5b66\u5e9c\u5df7\u6765\uff0c\u771f\u4ed6\u5988\u7684\u6666\u6c14\uff0c\u8001\u5b50\u521a\u624d\u5e26\u7740\u4eba\u5230\u5b66\u5e9c\u5df7\u6765\u6267\u6cd5\uff0c\u7ae0\u9c7c\u8ddf\u80a5\u82b1\u732b\u7ed9\u4eba\u6253\u4e86\u2026\u2026\u201d</p></article>"}}}]);
