(window.webpackJsonp=window.webpackJsonp||[]).push([[84],{107:function(p){p.exports={chapterTitle:null,title:"\u7b2c84\u7ae0 \u671f\u4e2d\u8003\u8bd5\u6210\u7ee9",content:"<article><h2>\u7b2c84\u7ae0 \u671f\u4e2d\u8003\u8bd5\u6210\u7ee9</h2><p>\u3000\u3000\u5f20\u606a\u6653\u5f97\u4e0b\u5348\u7ee7\u7eed\u901b\u8857\u5c06\u662f\u4e00\u4e2a\u611a\u8822\u7684\u884c\u4e3a\uff0c\u5403\u8fc7\u4e2d\u996d\u4e4b\u540e\uff0c\u5c31\u63d0\u8bae\u5230\u4e1c\u65b9\u5e7f\u573a\u8fb9\u7684\u5496\u5561\u9986\u4f53\u606f\u4e00\u4e0b\uff0c\u4e5f\u8ba9\u5927\u5bb6\u4eab\u53d7\u4e00\u4e0b\u5c0f\u8d44\u7684\u60c5\u8c03\u3002\u5c0f\u8d44\u60c5\u8c03\u5728\u4e5d\u56db\u5e74\u8fd8\u4e0d\u662f\u65f6\u884c\u7684\u8bcd\u8bed\uff1b\u4e00\u76f4\u5230\u5341\u51e0\u5e74\u540e\uff0c\u56fd\u5185\u7684\u5496\u5561\u9986\u90fd\u5c5e\u4e8e\u5e73\u6c11\u52ff\u8fd1\u7684\u9ad8\u6863\u573a\u6240\uff0c\u4e5d\u56db\u5e74\u66f4\u662f\u5bb0\u4eba\u4e0d\u5546\u91cf\uff0c\u5c31\u8fde\u4ee4\u5c0f\u71d5\u4e5f\u62b1\u6028\u6731\u5c0f\u519b\u53ea\u5728\u8868\u767d\u7684\u90a3\u4e00\u5929\u624d\u53bb\u8fc7\u4e00\u56de\u5496\u5561\u9986\uff0c\u53ea\u662f\u8bb8\u7ef4\u6709\u4e9b\u7591\u8651\uff0c\u5f20\u606a\u82b1\u94b1\u90fd\u4e0d\u5e26\u7728\u773c\u7684\uff0c\u8bb8\u601d\u5374\u504f\u504f\u6ca1\u6709\u963b\u62e6\u4ed6\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u6253\u7b97\u5728\u5496\u5561\u5385\u7a0d\u4f5c\u4f11\u61a9\u4e4b\u540e\uff0c\u4fbf\u76f4\u63a5\u79bb\u5f00\u7701\u57ce\uff0c\u8bb8\u601d\u5c06\u5979\u59b9\u59b9\u8bb8\u7ef4\u62c9\u5230\u4e00\u65c1\uff0c\u60f3\u585e\u7ed9\u5979\u4e9b\u94b1\uff0c\u6539\u5584\u4e00\u4e0b\u5927\u5b66\u7684\u751f\u6d3b\uff0c\u4e0d\u60f3\u5979\u6253\u5de5\u592a\u8f9b\u82e6\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u6b7b\u6d3b\u90fd\u4e0d\u80af\u8981\uff0c\u5f20\u606a\u5fc3\u60f3\u8bb8\u7ef4\u5bf9\u8bb8\u601d\u7684\u4ee5\u524d\u4e8b\u6216\u8bb8\u77e5\u9053\u4e00\u4e9b\uff0c\u5bf9\u8bb8\u601d\u73b0\u5728\u7684\u72b6\u51b5\u5927\u6982\u4e5f\u6709\u4e0d\u597d\u7684\u8054\u60f3\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4e0e\u8bb8\u7ef4\u63a8\u6421\u7740\uff0c\u6c14\u6c1b\u7a81\u7136\u53d8\u5f97\u51dd\u6ede\u8d77\u6765\uff0c\u5927\u5bb6\u90fd\u505c\u4e0b\u6765\u4e0d\u8bf4\u8bdd\uff0c\u89c9\u5f97\u5f88\u5947\u602a\u3002\u59d0\u59d0\u7ed9\u8bfb\u5927\u5b66\u7684\u59b9\u59b9\u751f\u6d3b\u8d39\uff0c\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u3002\u770b\u7740\u8bb8\u601d\u96be\u53d7\u5f97\u5feb\u8981\u54ed\u7684\u6837\u5b50\uff0c\u5f20\u606a\u5fc3\u91cc\u4e5f\u5f88\u96be\u53d7\uff0c\u52c9\u5f3a\u7b11\u7740\u8bf4\uff1a\u201c\u4e00\u5343\u5143\u90fd\u8fd8\u4e0d\u591f\u559d\u987f\u5496\u5561\uff0c\u8bb8\u7ef4\u59d0\u5f53\u6210\u591a\u7a00\u7f55\u4f3c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u5c31\u662f\u773c\u7a9d\u5b50\u6d45\uff0c\u4e00\u5343\u5143\u5bf9\u666e\u901a\u4eba\u6765\u8bf4\uff0c\u4e09\u56db\u4e2a\u6708\u7684\u5de5\u8d44\uff0c\u5f53\u7136\u4e86\u4e0d\u5f97\u3002\u201d\u8bb8\u7ef4\u8138\u8272\u6709\u4e9b\u51b7\uff0c\u5979\u8bf4\u670d\u4e0d\u4e86\u81ea\u5df1\u6536\u59d0\u59d0\u8bb8\u601d\u7684\u94b1\uff0c\u5014\u5728\u90a3\u91cc\uff0c\u5927\u5bb6\u90fd\u6709\u4e9b\u4e0b\u4e0d\u4e86\u53f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u8146\u7740\u8138\u770b\u7740\u8bb8\u7ef4\u575a\u6bc5\u6709\u4f59\u3001\u5a07\u5a9a\u4e0d\u8db3\u7684\u4fcf\u8138\uff0c\u4e5f\u80fd\u4f53\u4f1a\u5979\u7684\u5fc3\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u8bb8\u7ef4\u59d0\u4e0d\u662f\u4e00\u76f4\u5728\u6253\u5de5\u5417\uff1f\u6211\u6253\u7b97\u7ed9\u4f60\u4ecb\u7ecd\u4e00\u4efd\u5de5\u4f5c\uff0c\u53ef\u4ee5\u62b5\u4f60\u597d\u51e0\u4efd\u96f6\u5de5\uff0c\u201d\u89c1\u8bb8\u7ef4\u522b\u8fc7\u8138\u6ca1\u6709\u7406\u4f1a\u81ea\u5df1\uff0c\u4e5f\u4e0d\u6c14\u59a5\uff0c\u7ee7\u7eed\u8bf4\uff0c\u201c\u60f3\u8bf7\u8bb8\u7ef4\u59d0\u5e2e\u5fd9\u7167\u987e\u4e00\u4e2a\u4eca\u5e74\u624d\u56db\u5c81\u7684\u5c0f\u5973\u5b69\uff0c\u5979\u5728\u4eca\u5e74\u590f\u5929\u53d1\u751f\u7684\u4e00\u8d77\u8f66\u7978\u4e2d\u53d7\u4e86\u60ca\u5413\uff0c\u81f3\u4eca\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u4e5f\u5c31\u662f\u6240\u8c13\u7684\u5931\u8bed\u75c7\u2026\u2026\u201d</p><p>\u3000\u3000\u82b7\u5f64\u7684\u906d\u9047\u80fd\u89e6\u52a8\u8bb8\u7ef4\u67d4\u8f6f\u7684\u5185\u5fc3\uff0c\u5979\u8f6c\u8fc7\u8138\u6765\uff0c\u8868\u60c5\u7f13\u548c\u591a\u4e86\uff0c\u95ee\u9053\uff1a\u201c\u6211\u80fd\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5979\u7684\u5988\u5988\u662f\u8bb8\u601d\u59d0\u516c\u53f8\u7684\u8001\u603b\uff0c\u5de5\u4f5c\u5f88\u5fd9\u788c\uff0c\u4e5f\u5f88\u8f9b\u82e6\uff0c\u5979\u9700\u8981\u6709\u6211\u4eec\u80fd\u4fe1\u4efb\u7684\u4eba\u8010\u5fc3\u7684\u966a\u62a4\uff0c\u5f53\u7136\u9700\u8981\u5177\u5907\u4e00\u4e9b\u5e7c\u513f\u6559\u80b2\u65b9\u9762\u7684\u77e5\u8bc6\uff0c\u201d\u5f20\u606a\u70b9\u4e86\u4e00\u53e0\u94b1\uff0c\u653e\u5230\u684c\u4e0a\uff0c\u63a8\u5230\u8bb8\u7ef4\u7684\u9762\u524d\uff0c\u201c\u4f60\u8981\u80fd\u80dc\u4efb\u8fd9\u4efd\u5de5\u4f5c\uff0c\u8fd8\u9700\u8981\u63a5\u53d7\u4e00\u4e9b\u4e13\u95e8\u7684\u57f9\u8bad\uff0c\u5e0c\u671b\u4f60\u80fd\u81ea\u5df1\u8054\u7cfb\u76f8\u5173\u57f9\u8bad\u673a\u6784\uff0c\u6216\u8005\u7531\u8bb8\u601d\u59d0\u5148\u8054\u7cfb\u597d\u518d\u901a\u77e5\u4f60\uff0c\u8fd9\u662f\u57f9\u8bad\u7684\u8d39\u7528\uff0c\u4f60\u5148\u7559\u4e0b\uff0c\u5176\u4ed6\u7684\u5de5\u4f5c\uff0c\u8bf7\u4ece\u4eca\u5929\u90fd\u8f9e\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7a81\u7136\u6210\u719f\u7684\u8bed\u8c03\uff0c\u8ba9\u8bb8\u7ef4\u6709\u4e9b\u4e0d\u9002\u5e94\u3002\u4e0d\u4ec5\u4ec5\u5979\uff0c\u6c5f\u9edb\u513f\u3001\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\uff0c\u4ee5\u53ca\u4e00\u8fb9\u7684\u6731\u5c0f\u519b\u90fd\u6ca1\u6709\u60f3\u5230\u8fd9\u4e2a\u5341\u516d\u5c81\u7684\u5c11\u5e74\u4f1a\u8fd9\u4e48\u4e00\u672c\u6b63\u7ecf\u7684\u8c08\u4e00\u4ef6\u4e8b\uff0c\u4e5f\u8ba9\u82b7\u5f64\u7684\u4e8b\u60c5\u5438\u5f15\u4f4f\uff0c\u90fd\u6452\u4f4f\u6c14\u4e0d\u8bf4\u8bdd\uff0c\u773c\u795e\u5374\u9f13\u52b1\u8bb8\u7ef4\u7b54\u5e94\u4e0b\u6765\u3002</p><p>\u3000\u3000\u65e0\u8bba\u4ece\u5de5\u4f5c\u7684\u5438\u5f15\u529b\uff0c\u8fd8\u662f\u4ece\u7406\u89e3\u59d0\u59d0\u76ee\u524d\u7684\u5904\u5883\uff0c\u8bb8\u7ef4\u90fd\u96be\u4ee5\u62d2\u7edd\uff0c\u53ea\u662f\u4e0d\u9002\u5e94\u5f20\u606a\u8bf4\u8bdd\u7684\u8bed\u6c14\uff0c\u770b\u7740\u9762\u524d\u4e00\u53e0\u94b1\uff0c\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u597d\u3002</p><p>\u3000\u3000\u8bb8\u601d\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u6e29\u67d4\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u82b7\u5f64\u4e00\u76f4\u62d2\u7edd\u964c\u751f\u4eba\u7684\u7167\u987e\uff0c\u8fd8\u4e0d\u77e5\u9053\u80fd\u4e0d\u80fd\u63a5\u53d7\u4f60\uff0c\u603b\u5f52\u8981\u8bd5\u4e00\u8bd5\u624d\u80fd\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u201c\u80fd\u4e0d\u80fd\u5148\u89c1\u4e00\u89c1\u82b7\u2026\u2026\u201d\u8bb8\u7ef4\u6ca1\u6709\u542c\u6e05\u51fa\u82b7\u5f64\u7684\u540d\u5b57\u3002</p><p>\u3000\u3000\u201c\u5c0f\u5973\u5b69\u53eb\u82b7\u5f64\uff0c\u5982\u679c\u5979\u80fd\u63a5\u53d7\u4f60\uff0c\u6211\u4eec\u4f1a\u5e0c\u671b\u4f60\u9a6c\u4e0a\u5c31\u80fd\u5e2e\u4e0a\u5fd9\uff0c\u90a3\u4f60\u5c31\u6ca1\u6709\u8fdb\u884c\u4e13\u4e1a\u57f9\u8bad\u7684\u65f6\u95f4\u4e86\uff0c\u6240\u4ee5\u5e0c\u671b\u4f60\u80fd\u5728\u89c1\u9762\u524d\u4ec0\u4e48\u51c6\u5907\u90fd\u5148\u505a\u597d\u2026\u2026\u201d\u5f20\u606a\u4e00\u4ed8\u4e0d\u80af\u4e3a\u533a\u533a\u51e0\u5343\u94b1\u5c31\u6d6a\u8d39\u4e00\u70b9\u65f6\u95f4\u7684\u59ff\u6001\u679c\u7136\u5f88\u6709\u5bcc\u5bb6\u5b50\u5f1f\u7684\u6d3e\u5934\uff0c\u5f88\u80fd\u8ff7\u60d1\u4eba\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u72b9\u72b9\u8c6b\u8c6b\u7684\u6536\u4e0b\u94b1\uff0c\u5f20\u606a\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u8bb8\u601d\u59d0\u8fd9\u6bb5\u65f6\u95f4\u53ef\u80fd\u6ca1\u6709\u65f6\u95f4\u6765\u7701\u57ce\uff0c\u82b7\u5f64\u8ddf\u5979\u5988\u5988\u5468\u672b\u4f1a\u7ecf\u5e38\u5230\u6d77\u5dde\u53bb\uff0c\u4f60\u56de\u6d77\u5dde\u518d\u4e00\u8d77\u89c1\u9762\uff0c\u6216\u8bb8\u66f4\u65b9\u4fbf\u4e00\u4e9b\uff0c\u6240\u6709\u7684\u8f66\u8239\u8d39\u7528\uff0c\u90fd\u53ef\u4ee5\u4ece\u516c\u53f8\u62a5\u9500\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u4e0b\u5348\u4e24\u70b9\u949f\uff0c\u5f20\u606a\u4ed8\u8fc7\u8d26\uff0c\u4e0e\u8bb8\u7ef4\u5979\u4eec\u544a\u522b\uff0c\u8ba9\u5979\u4eec\u5728\u5496\u5561\u5385\u518d\u591a\u5750\u4e00\u4f1a\u513f\uff0c\u4e34\u8d70\u4e0d\u5fd8\u5077\u5077\u7684\u591a\u7784\u6c5f\u9edb\u513f\u4e00\u773c\uff0c\u89c1\u5979\u773c\u7751\u90fd\u6cdb\u7740\u7ea2\u6655\uff0c\u5fc3\u60f3\u771f\u662f\u5999\u4eba\u513f\uff0c\u90fd\u4e0d\u6653\u5f97\u4ee5\u540e\u6ca1\u6709\u6ca1\u673a\u4f1a\u518d\u89c1\u3002</p><p>\u3000\u3000\u63d0\u7740\u5927\u5305\u5c0f\u5305\u7684\u4e1c\u897f\uff0c\u8d70\u51fa\u4e1c\u65b9\u5e7f\u573a\uff0c\u5f20\u606a\u95ee\u8bb8\u601d\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u6211\u521a\u521a\u90a3\u756a\u8bdd\u80fd\u4e0d\u80fd\u5c06\u8bb8\u7ef4\u59d0\u552c\u4f4f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u554a\u2026\u2026\u201d\u8bb8\u601d\u67d4\u8f6f\u7684\u5185\u5fc3\u6d8c\u51fa\u8bb8\u591a\u611f\u89e6\uff0c\u5f20\u606a\u867d\u7136\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u5374\u662f\u90a3\u79cd\u4f53\u8d34\u7684\u3001\u8ba9\u4eba\u5fc3\u5b89\u7684\u7537\u5b69\u5b50\uff0c\u4ed6\u6e05\u695a\u81ea\u5df1\u90a3\u4e9b\u65e0\u6cd5\u8bf4\u51fa\u53e3\u7684\u89e3\u91ca\uff0c\u6240\u4ee5\u624d\u60f3\u65b9\u8bbe\u6cd5\u7684\u8ba9\u8bb8\u7ef4\u63a5\u89e6\u82b7\u5f64\u7684\u65b9\u5f0f\u4e86\u89e3\u81ea\u5df1\u73b0\u5728\u7684\u72b6\u51b5\u3002\u8bb8\u7ef4\u4e5f\u592a\u4e45\u6ca1\u6709\u56de\u6d77\u5dde\u4e86\uff0c\u4e0d\u6653\u5f97\u7236\u6bcd\u6709\u591a\u62c5\u5fc3\uff0c\u8ba9\u5979\u56de\u4e00\u8d9f\u504f\u504f\u8981\u7f16\u51fa\u8fd9\u79cd\u7684\u501f\u53e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u8bb8\u601d\u7edd\u7f8e\u7684\u9762\u5bb9\u4e0a\u6ee1\u662f\u60c6\u6005\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8bb8\u601d\u59d0\uff0c\u8981\u662f\u611f\u52a8\u7684\u60f3\u54ed\uff0c\u4e0a\u8f66\u4e4b\u540e\uff0c\u6211\u7684\u80f8\u819b\u53ef\u4ee5\u501f\u4f60\u4e00\u5c0f\u4f1a\u513f\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u72d7\u5634\u91cc\u5410\u4e0d\u51fa\u8c61\u7259\uff01\u201d\u8bb8\u601d\u5f00\u989c\u800c\u7b11\uff0c\u5a07\u55d4\u58f0\u67d4\uff0c\u7b11\u9765\u5982\u82b1\uff0c\u201c\u8c01\u611f\u52a8\u4e86\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d76\u592a\u9633\u843d\u5c71\u4e4b\u524d\u56de\u5230\u6d77\u5dde\uff0c\u65b0\u8f66\u505c\u5728\u516c\u53f8\u697c\u4e0b\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u6d77\u6cf0\u8d38\u6613\uff0c\u65b0\u6d77\u901a\u5927\u53a6\u91cc\u5468\u65e5\u52a0\u73ed\u7684\u516c\u53f8\u4e0d\u5c11\uff0c\u6b63\u521a\u4e0a\u7684\u4e0b\u73ed\u65f6\u95f4\uff0c\u5927\u53a6\u697c\u4e0a\u7684\u5e7f\u573a\u4e0a\uff0c\u4eba\u6d41\u4e0d\u5c11\u3002\u5168\u65b0\u5965\u8fea\u8f66\u6216\u8bb8\u4e0d\u80fd\u5438\u5f15\u4eba\u4eec\u7684\u6ce8\u610f\uff0c\u65b0\u6d77\u901a\u5927\u53a6\u7684\u505c\u8f66\u573a\u91cc\u8fd8\u6709\u4e09\u8f86\u5954\u9a70\u505c\u7740\uff0c\u4f46\u662f\u52a0\u4e0a\u7279\u79cd\u519b\u724c\uff0c\u7ed9\u522b\u4eba\u7684\u611f\u89c2\u5c31\u8fe5\u7136\u4e0d\u540c\uff0c\u7ecf\u8fc7\u7684\u4eba\u90fd\u5fcd\u4e0d\u4f4f\u56de\u5934\u770b\u51e0\u773c\uff0c\u6bd4\u8bb8\u601d\u7684\u56de\u5934\u7387\u4e0d\u5f97\u5dee\u3002</p><p>\u3000\u3000\u770b\u89c1\u848b\u8587\u4e0e\u5468\u4e00\u5e73\u8d70\u51fa\u65b0\u6d77\u901a\u7684\u95e8\u5385\uff0c\u770b\u5230\u6302\u519b\u724c\u7684\u5965\u8fea\u8f66\u505c\u5728\u5e7f\u573a\u4e0a\uff0c\u4e5f\u5f80\u8fd9\u8fb9\u591a\u770b\u4e86\u4e24\u773c\uff0c\u5f20\u606a\u6447\u4e0b\u8f66\u7a97\uff0c\u5524\u4ed6\u4eec\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8f66\uff1f\u201d\u848b\u8587\u4e0d\u63a9\u9970\u8138\u4e0a\u7684\u8be7\u5f02\uff0c\u201c\u8fd9\u8f66\u724c\u53ef\u96be\u641e\uff0c\u519b\u5206\u533a\u4e5f\u5c31\u4e24\u4e09\u90e8\u5427\uff1f\u201d</p><p>\u3000\u3000\u519b\u724c\u52a0\u4e0a\u5965\u8fea\u8f66\uff0c\u6d77\u5dde\u519b\u5206\u533a\u53ea\u6709\u4e24\u90e8\uff0c\u519b\u5206\u533a\u53f8\u4ee4\u9648\u664b\u5f3a\u5927\u6821\u90a3\u8f86\u5965\u8fea\u6392\u91cf3.2L\uff0c\u6309\u8bf4\u6709\u4e9b\u8d85\u89c4\u683c\uff0c\u4f46\u662f\u5728\u5730\u533a\u638c\u63e1\u5b9e\u6743\uff0c\u53c8\u662f\u5e02\u59d4\u5e38\u59d4\u4e4b\u4e00\uff0c\u8d85\u70b9\u89c4\u683c\u90fd\u662f\u53ef\u4ee5\u8c05\u89e3\u7684\uff0c\u4f46\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\u8bf4\uff0c\u4ed6\u7684\u519b\u724c\u53f7\u7801\u6570\u5b57\u8981\u6bd4\u8fd9\u90e8\u8f66\u5c0f\u5f97\u591a\u3002\u5f53\u7136\u4e00\u822c\u4eba\u4e0d\u4f1a\u770b\u6ce8\u610f\u592a\u7ec6\u5fae\u7684\u7ec6\u8282\uff0c\u5f20\u606a\u6ca1\u6709\u89e3\u91ca\uff0c\u8981\u662f\u89e3\u91ca\u4e86\u5bf9\u65b9\u8fd8\u4e0d\u7406\u89e3\uff0c\u70ab\u8000\u4e0d\u6210\uff0c\u5374\u53d8\u6210\u5356\u5f04\u4e86\u3002</p><p>\u3000\u3000\u201c\u76db\u946b\u7684\u53f6\u603b\u9001\u7684\u4eba\u60c5\uff0c\u65e0\u6cd5\u767d\u6536\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5bf9\u848b\u8587\u8bf4\uff1a\u201c\u8bb8\u601d\u8fd8\u6709\u4e9b\u4e8b\u60c5\uff0c\u4f60\u5f00\u8f66\u9001\u6211\u56de\u5bb6\uff0c\u8f66\u7ed9\u4f60\u7528\u3002\u201d</p><p>\u3000\u3000\u201c\u5f20\u7ecf\u7406\u6ca1\u6709\u9a7e\u7167\u554a\uff1f\u201d\u5468\u4e00\u5e73\u8be7\u5f02\u7684\u95ee\u3002</p><p>\u3000\u3000\u6ca1\u6709\u9a7e\u7167\u5f00\u8fd9\u8f66\u4e5f\u6ca1\u6709\u5173\u7cfb\uff0c\u8f66\u7978\u8ba9\u81ea\u5df1\u83ab\u540d\u5176\u5999\u7684\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u53c8\u4eb2\u773c\u76ee\u7779718\u7279\u5927\u4ea4\u901a\u4e8b\u6545\uff0c\u5bf9\u5f00\u8f66\u6709\u4e9b\u53d1\u5fe4\uff0c\u81f3\u5c11\u5728\u5f90\u5b66\u5e73\u3001\u8c22\u665a\u6674\u7b49\u4eba\u7684\u5fc3\u76ee\uff0c\u5e94\u8be5\u4e0d\u4f1a\u559c\u6b22\u8fdd\u7ae0\u9a7e\u9a76\u7684\u4eba\u3002</p><p>\u3000\u3000\u201c\u55ef\u3002\u201d\u5f20\u606a\u7cca\u5f04\u5e94\u4e86\u4e00\u58f0\uff0c\u6ca1\u6709\u591a\u89e3\u91ca\u4ec0\u4e48\uff0c\u4e5d\u56db\u5e74\u62ff\u9a7e\u8bc1\u7684\u4eba\u5f88\u5c11\uff0c\u5c31\u7b97\u7279\u6743\u9636\u5c42\u4e5f\u4e0d\u89c1\u5f97\u4e2a\u4e2a\u90fd\u6709\u9a7e\u7167\u3002</p><p>\u3000\u3000\u848b\u8587\u8d1f\u8d23\u4e0e\u57ce\u5357\u533a\u653f\u5e9c\u6d3d\u8c08\u627f\u5305\u7ecf\u8425\u65b0\u5149\u9020\u7eb8\u5382\u7684\u4e8b\u52a1\u6709\u5341\u591a\u5929\u7684\u65f6\u95f4\uff0c\u5fc3\u91cc\u5df2\u7ecf\u6e10\u6e10\u80fd\u63a5\u53d7\u5f20\u606a\u8fd8\u662f\u4e00\u540d\u9ad8\u4e2d\u751f\u7684\u4e8b\u5b9e\uff0c\u8fd8\u9887\u4e3a\u914d\u5408\u9ed8\u5951\u7684\u7ee7\u7eed\u5e2e\u5f20\u606a\u7cca\u5f04\u5176\u4ed6\u4eba\u3002\u848b\u8587\u89c1\u5f20\u606a\u8ba9\u81ea\u5df1\u4ee3\u66ff\u8bb8\u601d\u9001\u4ed6\u56de\u5bb6\uff0c\u5fc3\u60f3\u5f20\u77e5\u884c\u6216\u8bb8\u771f\u4e0d\u77e5\u9053\u8bb8\u601d\u5176\u5b9e\u5728\u7ed9\u4ed6\u513f\u5b50\u6253\u5de5\uff0c\u4e0d\u7136\u4e0d\u9700\u8981\u591a\u6b64\u4e00\u4e3e\u3002</p><p>\u3000\u3000\u8bb8\u601d\u63a8\u95e8\u4e0b\u8f66\uff0c\u8fd9\u4e24\u5929\u5979\u966a\u5f20\u606a\u53bb\u7701\u57ce\uff0c\u6bd4\u5e73\u65f6\u5728\u516c\u53f8\u8001\u6c14\u6a2a\u79cb\u7684\u88c5\u626e\u8981\u660e\u4e3d\u9c9c\u8273\u4e00\u4e9b\uff0c\u6b64\u65f6\u7684\u5979\u5a07\u8273\u660e\u5a9a\uff0c\u8d70\u51fa\u8f66\u95e8\u5c31\u8ba9\u848b\u8587\u76f8\u5f62\u5931\u8272\uff0c\u7acb\u5373\u53c8\u6bd4\u5965\u8fea\u8f66\u5f15\u4eba\u6ce8\u76ee\u4e86\uff0c\u5468\u4e00\u5e73\u5728\u65c1\u8fb9\u5567\u5567\u5482\u5634\uff1a\u201c\u8bb8\u52a9\u7406\u4eca\u5929\u53ef\u771f\u6f02\u4eae\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u7b11\u4e86\u7b11\uff0c\u89c1\u5f20\u606a\u624b\u4f38\u51fa\u8f66\u7a97\u5916\u8f7b\u6572\u8f66\u95e8\uff0c\u4fef\u8fc7\u8eab\u6765\u95ee\u4ec0\u4e48\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5370\u7740\u7cbe\u7f8e\u56fe\u6848\u7684\u5851\u6599\u888b\u9012\u7ed9\u5979\uff1a\u201c\u7ed9\u4f60\u7684\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5168\u7a0b\u966a\u5f20\u606a\u901b\u4e1c\u65b9\u5e7f\u573a\uff0c\u5f53\u7136\u77e5\u9053\u5851\u6599\u888b\u88c5\u7740\u4e00\u5bf9\u9576\u94bb\u8033\u5760\uff0c\u867d\u7136\u4e0d\u662f\u8d35\u5f97\u79bb\u8c31\uff0c\u4f46\u662f\u5f20\u606a\u6311\u9009\u65f6\u76f8\u5f53\u7684\u7ec6\u5fc3\uff0c\u62ff\u81ea\u5df1\u7684\u8033\u6735\u6bd4\u8f83\uff0c\u8fd8\u5600\u5495\u4e86\u4e00\u53e5\u201c\u8001\u5988\u6234\u8fd9\u4e2a\u86ee\u62db\u773c\u7684\u201d\uff0c\u5f53\u65f6\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u662f\u66ff\u81ea\u5df1\u6311\u9009\u7684\uff0c\u5fc3\u8bf4\u5f20\u606a\u4e0d\u60f3\u8ba9\u522b\u4eba\u77e5\u9053\u4ed6\u7684\u5fc3\u601d\uff0c\u8fd8\u771f\u6ca1\u4eba\u80fd\u731c\u5230\u3002\u7565\u6709\u60ca\u7f9e\u7684\u770b\u4e86\u5f20\u606a\uff0c\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u63a5\u53d7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5374\u5c06\u5851\u6599\u888b\u585e\u8bb8\u601d\u624b\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u848b\u59d0\u4e5f\u6709\u793c\u7269\u7684\u3002\u201d\u7b49\u848b\u8587\u5750\u5230\u8eab\u8fb9\uff0c\u9012\u7ed9\u5979\u4e00\u53ea\u9970\u54c1\u76d2\uff0c\u201c\u966a\u8bb8\u601d\u901b\u4e86\u534a\u5929\u8857\uff0c\u7f8e\u4e3d\u7684\u5927\u59d0\u59d0\u90fd\u6709\u793c\u7269\u2026\u2026\u201d\u5374\u8ba9\u5468\u4e00\u5e73\u5c06\u4e00\u7bb1\u98df\u54c1\u6367\u4e0b\u8f66\uff0c\u201c\u7537\u540c\u80de\u5c31\u5305\u6db5\u4e00\u4e0b\uff0c\u5206\u70b9\u4e1c\u897f\u5403\u5403\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d76\u7740\u6d77\u6cf0\u8d38\u6613\u7684\u5176\u4ed6\u4eba\u51fa\u5927\u53a6\u4e4b\u524d\uff0c\u8ba9\u848b\u8587\u5f00\u8f66\u79bb\u5f00\u3002\u8d76\u5230\u5bb6\u91cc\uff0c\u5148\u7ed9\u5510\u5a67\u6302\u4e86\u7535\u8bdd\uff0c\u901a\u77e5\u5979\u8fc7\u6765\u642c\u793c\u7269\uff0c\u624d\u4e0b\u697c\u5c06\u8f66\u91cc\u4eca\u5929\u72c2\u8d2d\u7684\u4e00\u5806\u4e1c\u897f\u642c\u56de\u5bb6\uff0c\u6881\u683c\u73cd\u770b\u7740\u8fd9\u5806\u4e1c\u897f\uff0c\u95ee\u5f20\u606a\uff1a\u201c\u82b1\u4e86\u591a\u5c11\u94b1\uff0c\u8fd9\u4e48\u591a\u4e00\u5806\u4e1c\u897f\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ed9\u665a\u6674\u59d0\u6253\u5de5\u5462\uff0c\u4f60\u4ee5\u4e3a\u4f60\u513f\u5b50\u5728\u516c\u53f8\u662f\u767d\u6df7\u7684\uff1f\u201d\u5f20\u606a\u4eca\u5929\u5927\u91c7\u8d2d\uff0c\u4e5f\u6709\u8ba9\u7238\u5988\u9010\u6b65\u63a5\u53d7\u81ea\u5df1\u4f1a\u5f88\u6709\u94b1\u8fd9\u4e00\u4e8b\u5b9e\uff0c\u4e0d\u7136\u641e\u4e86\u4e00\u5806\u94b1\uff0c\u85cf\u7740\u6396\u7740\u4e0d\u80fd\u82b1\uff0c\u4e5f\u662f\u76f8\u5f53\u90c1\u95f7\u7684\u4e8b\u3002\u5728\u8def\u4e0a\u8ddf\u848b\u8587\u6253\u8fc7\u9884\u9632\u9488\uff0c\u8ba9\u5979\u914d\u5408\u6f14\u620f\u3002</p><p>\u3000\u3000\u848b\u8587\u4e0a\u697c\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u7684\u4f4f\u6240\uff0c\u867d\u7136\u4e09\u5ba4\u4e00\u5385\uff0c\u4f46\u662f\u5185\u90e8\u88c5\u6f62\uff0c\u8fd8\u6bd4\u4e0d\u4e0a\u81ea\u5df1\u5bb6\uff0c\u867d\u7136\u5b8b\u57f9\u660e\u6ca1\u6709\u8ddf\u5979\u63d0\u8d77\u8bb8\u9e3f\u4f2f\u5bf9\u5f20\u77e5\u884c\u80cc\u666f\u7684\u63a8\u6d4b\uff0c\u4f46\u662f\u5979\u5728\u884c\u653f\u673a\u4f1a\u6d78\u6deb\u4e86\u4e03\u516b\u5e74\uff0c\u770b\u5230\u5f20\u606a\u867d\u7136\u5e74\u7eaa\u8fd8\u5c0f\uff0c\u4f46\u4ed6\u7684\u65e9\u6167\u3001\u6210\u719f\uff0c\u5b8c\u5168\u662f\u5929\u624d\u5c11\u5e74\u624d\u80fd\u5177\u5907\u7684\u80fd\u529b\uff0c\u7279\u522b\u662f\u8c22\u665a\u6674\u5bf9\u4ed6\u4e0d\u52a0\u4fdd\u7559\u7684\u4fe1\u4efb\uff0c\u80fd\u611f\u89c9\u5230\u4ed6\u5728\u90a3\u5c42\u5173\u7cfb\u7f51\u4e2d\u7684\u91cd\u8981\u6027\u6216\u8bb8\u4e0d\u6bd4\u5f20\u77e5\u884c\u5dee\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5bb6\u591a\u4e86\u4e9b\u5bfb\u5e38\u5c11\u5e74\u4eba\u7684\u4e60\u6027\uff0c\u5c11\u4e86\u4e9b\u516c\u53f8\u91cc\u624d\u4f1a\u663e\u9732\u51fa\u6765\u7684\u9510\u5229\u4e0e\u5706\u6ed1\uff0c\u848b\u8587\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u7422\u78e8\u4e86\u4e00\u4f1a\u513f\u5f20\u606a\u8fd9\u4eba\uff0c\u7422\u78e8\u4e0d\u900f\uff0c\u5c06\u676f\u4e2d\u7684\u8336\u6c34\u559d\u6389\uff0c\u5c31\u544a\u8f9e\u79bb\u53bb\uff0c\u65b0\u516c\u53f8\u7684\u6ce8\u518c\u624b\u7eed\u5feb\u8981\u529e\u5b8c\u4e86\uff0c\u63a5\u4e0b\u6765\u5c31\u8981\u76f4\u63a5\u8ddf\u57ce\u5357\u533a\u653f\u5e9c\u76f4\u63a5\u7b7e\u8ba2\u627f\u5305\u7ecf\u8425\u65b0\u5149\u9020\u7eb8\u5382\u7684\u5408\u540c\uff0c\u8fd8\u6709\u4e00\u4e9b\u7ec6\u8282\u5904\u6ca1\u6709\u6572\u5b9a\uff0c\u8fd9\u4e24\u5929\u8f7b\u677e\u4e0d\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5012\u662f\u5f00\u7740\u5965\u8fea\u8f66\u56de\u5bb6\uff0c\u4e00\u5b9a\u4f1a\u8ba9\u4e00\u76f4\u4ee5\u4e3a\u8ba4\u4e3a\u81ea\u5df1\u6500\u9644\u4ed6\u5bb6\u7684\u4e08\u592b\u3001\u516c\u7239\u5403\u60ca\u5427\u3002</p><p>\u3000\u3000\u848b\u8587\u521a\u8d70\u4e00\u4f1a\u513f\uff0c\u5510\u5a67\u5c31\u62c9\u7740\u5979\u5988\u5988\u987e\u5efa\u840d\u8d76\u8fc7\u6765\u6311\u793c\u7269\u3002\u987e\u5efa\u840d\u8ddf\u6881\u683c\u73cd\u5750\u5728\u5ba2\u5385\u91cc\u804a\u5929\uff0c\u5510\u5a67\u6253\u5f00\u5f20\u606a\u623f\u95f4\u7684\u95e8\uff0c\u770b\u7740\u5e8a\u4e0a\u4e00\u5806\u4e1c\u897f\uff0c\u6ee1\u8138\u6b23\u559c\uff0c\u5374\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff1a\u201c\u8fd8\u7b97\u4f60\u77e5\u60c5\u8bc6\u8da3\uff0c\u77e5\u9053\u8981\u8ba8\u597d\u59d1\u5976\u5976\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u7740\u989d\u5934\u5927\u53f9\uff1a\u201c\u5931\u671b\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u5fcd\u4e0d\u4f4f\u6251\u4e0a\u6765\u4eb2\u6211\u4e00\u53e3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u505a\u68a6\uff0c\u5e2e\u4f60\u8bf4\u4e86\u8fd9\u4e48\u8c0e\u8bdd\uff0c\u4f60\u62a5\u7b54\u4e00\u4e0b\u4e5f\u662f\u5e94\u8be5\u7684\uff0c\u201d\u5510\u5a67\u5fae\u6602\u7740\u5934\uff0c\u9a84\u50b2\u800c\u81ea\u5f97\uff0c\u5ae9\u817b\u5982\u7389\u7684\u8138\u4e0a\u6d0b\u6ea2\u7740\u9752\u6625\u7684\u6c14\u606f\uff0c\u201c\u4f60\u5988\u95ee\u4f60\u7684\u671f\u4e2d\u8003\u8bd5\u6210\u7ee9\uff0c\u6211\u90fd\u5e2e\u4f60\u6492\u8c0e\u4e86\uff0c\u5bb3\u6211\u73b0\u5728\u90fd\u4e0d\u6562\u770b\u4f60\u5988\u7684\u773c\u775b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e00\u4ed8\u7ed9\u51fb\u8d25\u7684\u6837\u5b50\uff0c\u76f4\u76f4\u7684\u5012\u5728\u5e8a\u4e0a\uff0c\u8737\u7740\u8eab\u4f53\uff0c\u770b\u5510\u5a67\u62ff\u7740\u9970\u7269\u3001\u5764\u5305\u3001\u8863\u670d\u7ad9\u5728\u5e8a\u524d\u6bd4\u5212\uff0c\u4e00\u8fb9\u8fd8\u60ca\u5947\u7684\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u6211\u559c\u6b22\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5bb6\u77ed\u6682\u7684\u707e\u96be\u5e76\u6ca1\u6709\u7ed9\u5510\u5a67\u5355\u7eaf\u7684\u5fc3\u91cc\u7559\u4e0b\u592a\u6df1\u7684\u4f24\u75d5\uff0c\u76f8\u6bd4\u8bb8\u601d\u7684\u5a07\u7f9e\u59a9\u5a9a\uff0c\u5510\u5a67\u7684\u7f8e\u8c8c\u8ba9\u4eba\u66f4\u591a\u7684\u611f\u89c9\u5230\u90a3\u79cd\u65e0\u5904\u4e0d\u5728\u7684\u9752\u7eaf\u6c14\u606f\u4e0e\u6d3b\u529b\uff0c\u675f\u7740\u9a6c\u5c3e\u8fab\uff0c\u4fca\u4fcf\u7684\u8138\u6ca1\u4e00\u70b9\u7455\u75b5\uff0c\u773c\u795e\u6e05\u6f88\u660e\u4eae\uff0c\u7a7f\u7740\u6d0b\u7ea2\u8272\u7f69\u8863\uff0c\u725b\u4ed4\u88e4\uff0c\u5c06\u4fee\u957f\u7ea4\u7ec6\u7684\u5927\u817f\u7ef7\u76f4\uff0c\u7ad9\u5728\u623f\u95f4\u91cc\u8d70\u6765\u8d70\u53bb\uff0c\u4eff\u4f5b\u4e00\u53ea\u6b22\u5feb\u7684\u5c0f\u9e7f\u56db\u8e44\u8f7b\u8e29\u5728\u5f20\u606a\u7684\u5185\u5fc3\u6df1\u5904\u3002</p><p>\u3000\u3000\u5510\u5a67\u7ec8\u4e8e\u5fcd\u4e0d\u4f4f\u8981\u8bd5\u7a7f\u90a3\u4e9b\u8863\u670d\uff0c\u5f20\u606a\u60f3\u8d56\u7740\u4e0d\u8d70\uff0c\u7ed9\u5979\u4e09\u62f3\u4e24\u817f\u8d76\u51fa\u623f\u95f4\u3002\u5f20\u606a\u53ea\u6709\u62ff\u7740\u7ed9\u987e\u5efa\u840d\u7684\u793c\u7269\u5230\u5ba2\u5385\u91cc\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u6709\u793c\u7269\uff1f\u201d\u987e\u5efa\u840d\u9ad8\u5174\u7684\u63a5\u8fc7\u9970\u54c1\u76d2\uff0c\u662f\u4e00\u4e32\u73cd\u73e0\u9879\u88e2\uff0c\u56fd\u5185\u73cd\u73e0\u517b\u6b96\u5f88\u65e9\u5c31\u76db\u884c\uff0c\u73cd\u73e0\u8d2c\u503c\u5f97\u5389\u5bb3\uff0c\u5341\u51e0\u5e74\u540e\u51e0\u4e4e\u770b\u4e0d\u5230\u8857\u4e0a\u6709\u8c01\u4f1a\u6234\u73cd\u73e0\u9879\u88e2\uff0c\u4f46\u5728\u4e5d\u56db\u5e74\u6234\u73cd\u73e0\u9879\u88e2\uff0c\u4f9d\u7136\u663e\u5f97\u73e0\u5149\u5b9d\u6c14\u3001\u6070\u96c5\u5927\u65b9\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u5c31\u7ed9\u987e\u5a76\u5a76\u5e26\u4e86\u4e00\u4ef6\u793c\u7269\uff1f\u201d\u6881\u683c\u73cd\u62ac\u5934\u770b\u7740\u513f\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u8038\u8038\u80a9\uff0c\u8868\u793a\u5c31\u4e00\u4ef6\u3002\u6881\u683c\u73cd\u62c9\u7740\u987e\u5efa\u840d\u7684\u624b\u8bf4\uff1a\u201c\u665a\u6674\u7ed9\u5c0f\u606a\u4e5f\u7b97\u5de5\u8d44\uff0c\u4ed6\u90fd\u62ff\u53bb\u4e70\u4e86\u793c\u7269\uff0c\u770b\u5230\u90fd\u662f\u4f60\u5bb6\u5a67\u5a67\u7684\u793c\u7269\uff0c\u6211\u8fd9\u505a\u5988\u7684\u90fd\u611f\u5230\u5fc3\u9178\uff0c\u4f60\u5c31\u4e0d\u8981\u5acc\u793c\u7269\u5c11\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u987e\u5efa\u840d\u7b11\u7740\u8bf4\uff1a\u201c\u770b\u6765\u4ee5\u540e\u4f1a\u6162\u6162\u4e60\u60ef\u7684\uff0c\u6211\u6536\u4e00\u4ef6\u793c\u7269\uff0c\u5c31\u5fc3\u6ee1\u610f\u8db3\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u5510\u5a67\u6362\u4e86\u4e00\u8eab\u8863\u670d\uff0c\u63a8\u95e8\u51fa\u6765\uff1a\u201c\u5988\uff0c\u6881\u59e8\uff0c\u4f60\u770b\u6211\u8fd9\u8eab\u8863\u670d\u600e\u4e48\u6837\uff1f\u5f20\u606a\u7684\u773c\u5149\u8fd8\u771f\u4e0d\u8d56\uff0c\u5c31\u77e5\u9053\u6211\u559c\u6b22\u8fd9\u6837\u7684\u8863\u670d\u2026\u2026\u201d</p><p>\u3000\u3000\u725b\u4ed4\u5939\u514b\uff0c\u6d45\u7070\u8272\u7684\u6bdb\u8349\u9886\u895f\uff0c\u886c\u5f97\u5510\u5a67\u7684\u8138\u86cb\u5a07\u5ae9\u52a8\u4eba\uff0c\u795e\u60c5\u4fcf\u76ae\u7684\u663e\u6446\u81ea\u5df1\u7684\u65b0\u8863\u670d\uff0c\u91cc\u9762\u662f\u7eaf\u9ed1\u6742\u91d1\u8272\u827a\u672f\u5b57\u7684T\u6064\uff0c\u6d0b\u7070\u8272\u767e\u8936\u5e73\u7ed2\u516c\u4e3b\u77ed\u88d9\uff0c\u9ed1\u8272\u9f50\u819d\u7684\u8936\u76b1\u9ed1\u8272\u7b52\u9774\uff0c\u66f4\u663e\u51fa\u5b8c\u7f8e\u817f\u5f62\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8863\u670d\u672c\u8be5\u5c31\u662f\u7ed9\u5029\u5029\u7a7f\u7684\uff0c\u8981\u4e0d\u662f\u4e2d\u5348\u521a\u770b\u5230\u5029\u5029\uff0c\u6211\u90fd\u6000\u7591\u5979\u8ddf\u7740\u53bb\u7701\u57ce\u4e86\u2026\u2026\u201d\u6881\u683c\u73cd\u5567\u5567\u7684\u79f0\u8d5e\uff0c\u201c\u5c0f\u606a\u6311\u8863\u670d\u86ee\u6709\u773c\u5149\u7684\uff0c\u770b\u5c3a\u5bf8\u4e5f\u6b63\u5408\u9002\u3002\u201d</p><p>\u3000\u3000\u5510\u5029\u53c8\u6362\u4e86\u4ef6\u7eaf\u9ed1\u5927\u5f00\u9886\u7684\u77ed\u8896\u7ed2\u7ebf\u8863\u51fa\u6765\uff0c\u9886\u895f\u7f00\u7740\u6d0b\u7070\u6742\u9ed1\u7684\u6bdb\u8349\uff0c\u9732\u51fa\u521d\u96ea\u822c\u51c0\u767d\u7684\u7ea4\u7ec6\u80f3\u81c2\uff0c\u886c\u5f97\u5510\u5a67\u6108\u53d1\u7684\u4fcf\u4e3d\u52a8\u4eba\uff0c\u5f20\u606a\u90fd\u4e0d\u5f97\u4e0d\u4f69\u670d\u81ea\u5df1\u7684\u773c\u5149\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u63a8\u95e8\u8fdb\u6765\uff0c\u60ca\u8bb6\u7684\u8bf4\uff1a\u201c\u6211\u4eec\u5bb6\u600e\u4e48\u591a\u4e86\u4e2a\u8fd9\u4e48\u7f8e\u4e3d\u7684\u5c0f\u516c\u4e3b\uff0c\u662f\u5f20\u606a\u4ece\u54ea\u91cc\u9a97\u56de\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u53d4\uff0c\u201d\u5510\u5a67\u5a07\u7f9e\u7684\u5524\u8fc7\u5f20\u77e5\u884c\uff0c\u76ee\u5149\u8f6c\u5230\u54a7\u7740\u5634\u574f\u7b11\u7684\u5f20\u606a\u8138\u4e0a\uff0c\u7709\u5934\u9a84\u50b2\u7684\u626c\u4e86\u626c\uff0c\u201c\u5c31\u51ed\u5f20\u606a\u8fd8\u60f3\u9a97\u5230\u6211\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u8f6c\u5934\u5bf9\u987e\u5efa\u840d\u8bf4\uff1a\u201c\u5e02\u91cc\u665a\u4e0a\u8981\u53ec\u5f00\u4e34\u65f6\u7684\u5e38\u59d4\u4f1a\u8bae\uff0c\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u80fd\u7ed3\u675f\uff0c\u987e\u59d0\u8ddf\u5029\u5029\u5c31\u5728\u8fd9\u91cc\u5403\u996d\u5427\uff0c\u4e0d\u8981\u7ba1\u5510\u5e02\u957f\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8ddf\u5510\u5a67\u8fd9\u65f6\u5019\u4e0a\u95e8\uff0c\u5c31\u6ca1\u6253\u7b97\u7a7a\u7740\u809a\u5b50\u56de\u53bb\u3002\u201d\u987e\u5efa\u840d\u6beb\u4e0d\u89c1\u5916\u7684\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u4e24\u5bb6\u5173\u7cfb\u5df2\u7ecf\u4eb2\u5bc6\u5230\u5c31\u7b97\u987e\u5efa\u840d\u3001\u5510\u5a67\u7559\u4e0b\u6765\u5403\u996d\u4e5f\u4e0d\u9700\u8981\u523b\u610f\u51c6\u5907\u7684\u5730\u6b65\uff0c\u996d\u684c\u5c31\u6bd4\u5e73\u65f6\u591a\u6dfb\u4e86\u4e24\u4e2a\u83dc\u3002\u987e\u5efa\u840d\u662f\u5fc3\u91cc\u85cf\u4e0d\u4f4f\u5fc3\u4e8b\u7684\u4eba\uff0c\u996d\u684c\u4e0a\u4e0d\u53ef\u907f\u514d\u7684\u5bf9\u5510\u5b66\u8c26\u76ee\u524d\u5de5\u4f5c\u4e0a\u7684\u56f0\u5883\u62b1\u6028\u8d77\u6765\uff1a\u201c\u6211\u5bb6\u8001\u5510\u4e0a\u56de\u7ed9\u90a3\u4e2a\u59d3\u4e07\u7684\u8001\u4e0d\u6b7b\u6b3a\u8d1f\u5f97\u591f\u6b20\uff0c\u6c14\u5f97\u51e0\u5929\u90fd\u6ca1\u7761\u597d\u89c9\uff0c\u5f53\u526f\u5e02\u957f\u65f6\uff0c\u5c31\u5fcd\u6c14\u541e\u58f0\uff0c\u5f53\u4e0a\u5e02\u957f\u8fd8\u5f97\u5fcd\u6c14\u541e\u58f0\uff0c\u6211\u5bb6\u8001\u5510\u624d\u56db\u5341\u56db\uff0c\u5934\u53d1\u4e24\u8fb9\u90fd\u6709\u4e9b\u82b1\u767d\u4e86\uff0c\u597d\u591a\u6bd4\u4ed6\u5927\u4e00\u8f6e\u7684\u4eba\uff0c\u5934\u53d1\u90fd\u4e4c\u9ed1\u62b9\u4eae\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u5bf9\u4e8e\u5973\u4eba\u5bb6\u8fd9\u7c7b\u7684\u62b1\u6028\uff0c\u4ed6\u65e0\u6cd5\u4e5f\u4e0d\u4f1a\u63d2\u5634\u3002</p><p>\u3000\u3000\u987e\u5efa\u840d\u6240\u8bf4\u7684\u4e0a\u4e00\u56de\uff0c\u662f\u6307\u5e02\u5e38\u59d4\u63d0\u4ea4\u7684\u5e02\u76f4\u673a\u5173\u4eba\u5458\u8c03\u6574\u7684\u51b3\u8bae\u7ed9\u5e02\u4eba\u5927\u65e0\u7406\u5426\u51b3\u7684\u4e8b\uff0c\u5468\u5bcc\u660e\u5347\u4e0a\u4e66\u8bb0\uff0c\u5510\u5b66\u8c26\u4ee3\u5e02\u957f\uff0c\u540d\u4e49\u4e0a\u662f\u6d77\u5dde\u5e02\u7684\u4e00\u4e8c\u628a\u624b\uff0c\u4f46\u662f\u6839\u57fa\u90fd\u4e0d\u7a33\u56fa\uff0c\u5e38\u59d4\u5176\u4ed6\u6210\u5458\u90fd\u5404\u6000\u5fc3\u601d\uff0c\u524d\u4e24\u5e74\u7ed9\u4e01\u5411\u5c71\u6324\u5230\u5e02\u4eba\u5927\u7684\u4e07\u5411\u524d\u8fd9\u65f6\u6446\u51fa\u592a\u4e0a\u7687\u7684\u59ff\u6001\uff0c\u8bf4\u767d\u4e86\uff0c\u5c31\u662f\u6b3a\u8d1f\u5468\u5bcc\u660e\u3001\u5510\u5b66\u8c26\u4e24\u4eba\u6839\u57fa\u6d45\u3002\u52a0\u4e0a\u90a3\u6b21\u5e02\u76f4\u673a\u5173\u4eba\u5458\u8c03\u6574\u662f\u5e02\u5e38\u59d4\u5185\u90e8\u5c31\u5b58\u5728\u4e89\u8bae\uff0c\u6240\u4ee5\u4e07\u5411\u524d\u8fd9\u4e48\u641e\uff0c\u4e5f\u6ca1\u6709\u56de\u51fb\u7684\u529e\u6cd5\u3002</p><p>\u3000\u3000\u4eca\u5929\u6628\u65f6\u53ec\u5f00\u7684\u5e02\u5e38\u59d4\u4f1a\u8bae\uff0c\u4e3b\u8981\u8fd8\u662f\u8ba8\u8bba\u4e0a\u4e00\u6b21\u5e02\u76f4\u673a\u5173\u4eba\u5458\u8c03\u6574\u7684\u95ee\u9898\uff0c\u76f8\u6bd4\u5927\u534a\u4e2a\u6708\u524d\uff0c\u5c40\u52bf\u5411\u5468\u5bcc\u660e\u3001\u5510\u5b66\u8c26\u8fd9\u8fb9\u503e\u659c\uff0c\u8d77\u5173\u952e\u4f5c\u7528\u7684\u8fd8\u662f\u6d77\u88d5\u516c\u53f8\u6709\u610f\u4ecb\u5165\u65b0\u5149\u9020\u7eb8\u5382\u7ecf\u8425\u7684\u6d88\u606f\u5728\u5c0f\u8303\u56f4\u5185\u4f20\u5f00\u3002</p><p>\u3000\u3000\u4e01\u5411\u5c71\u7684\u610f\u5916\u843d\u9a6c\uff0c\u800c\u4e14\u6848\u60c5\u5f02\u5e38\u4e25\u91cd\uff0c\u4f7f\u5f97\u4e01\u5411\u5c71\u8fd9\u4e00\u7cfb\u7684\u5b98\u5458\u5728\u7701\u91cc\u7684\u4e0a\u5c42\u8d44\u6e90\u5b8c\u5168\u65ad\u7edd\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u6d77\u5dde\u5e02\u5b98\u573a\u4e0e\u7701\u57ce\u7684\u8054\u7cfb\u5728\u67d0\u79cd\u7a0b\u5ea6\u51fa\u73b0\u7684\u65ad\u5c42\uff0c718\u7279\u5927\u4ea4\u901a\u4e8b\u6545\uff0c\u66f4\u50cf\u4e00\u7247\u9634\u973e\u7684\u4e91\u5c42\u7b3c\u7f69\u5728\u6d77\u5dde\u5e02\u5b98\u573a\u7684\u4e0a\u7a7a\u3002</p><p>\u3000\u3000\u53ea\u8981\u5e38\u59d4\u91cc\u8c01\u80fd\u4e0e\u7701\u91cc\u91cd\u65b0\u5efa\u7acb\u8d77\u8fd9\u5c42\u8054\u7cfb\uff0c\u751a\u81f3\u53ea\u9700\u8981\u7ed9\u7701\u59d4\u4e66\u8bb0\u3001\u7701\u957f\u7b49\u4eba\u7559\u4e0b\u8f83\u6df1\u7684\u5370\u8c61\uff0c\u5c31\u80fd\u5728\u660e\u540e\u5e74\u7684\u8c03\u6574\u4e2d\u5360\u636e\u7edd\u5bf9\u7684\u4f18\u52bf\uff0c\u751a\u81f3\u53d6\u4ee3\u5468\u5bcc\u660e\u90fd\u4e0d\u662f\u6ca1\u6709\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5c31\u662f\u5728\u8fd9\u6837\u7684\u6c1b\u56f4\u91cc\uff0c\u6d77\u5dde\u5e02\u5b98\u573a\u51fa\u73b0\u5f53\u524d\u5fae\u5999\u7684\u5c40\u52bf\uff0c\u7ed9\u5468\u5bcc\u660e\u3001\u5510\u5b66\u8c26\u7684\u5de5\u4f5c\u589e\u52a0\u4e86\u5f88\u5927\u7684\u963b\u529b\u3002</p><p>\u3000\u3000\u6d77\u88d5\u516c\u53f8\u6709\u610f\u4ecb\u5165\u65b0\u5149\u9020\u7eb8\u5382\u7ecf\u8425\u7684\u6d88\u606f\uff0c\u5bf9\u90a3\u4e9b\u653f\u6cbb\u654f\u611f\u7684\u4eba\u6765\u8bf4\uff0c\u65e0\u5f02\u5929\u964d\u72c2\u96f7\uff0c\u610f\u5473\u7740718\u7279\u5927\u4ea4\u901a\u4e8b\u6545\u7684\u9634\u973e\u70df\u6d88\u4e91\u6563\uff0c\u751a\u81f3\u4e0d\u96be\u8ba9\u4eba\u8054\u60f3\u5230\u5468\u5bcc\u660e\u3001\u5510\u5b66\u8c26\u4e0e\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u653f\u6cd5\u59d4\u4e66\u8bb0\u5f90\u5b66\u5e73\u4e4b\u95f4\u7684\u5173\u7cfb\u5df2\u7ecf\u4fee\u590d\u3002</p><p>\u3000\u3000\u5728\u4e01\u5411\u5c71\u6848\u6ca1\u6709\u6c34\u843d\u77f3\u51fa\u4e4b\u524d\uff0c\u5bf9\u7701\u91cc\u6765\u8bf4\uff0c\u5510\u5b66\u8c26\u3001\u5468\u5bcc\u660e\u662f\u7ecf\u5f97\u4f4f\u8003\u9a8c\u7684\u4eba\uff0c\u52a0\u4e0a\u5f90\u5b66\u5e73\u8fd9\u5c42\u5173\u7cfb\uff0c\u65e0\u7591\u5c06\u7a33\u5b9a\u4ed6\u4eec\u4e24\u4eba\u5728\u6d77\u5dde\u7684\u5730\u4f4d\u3002\u867d\u7136\u5f90\u5b66\u5e73\u5728\u7701\u91cc\u4f4d\u5c45\u7b2c\u4e09\uff0c\u4f46\u662f\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u7701\u59d4\u4e66\u8bb0\u4e0e\u7701\u957f\u90fd\u4e0d\u5927\u53ef\u80fd\u62c5\u7740\u653f\u6cbb\u98ce\u9669\u518d\u6311\u9009\u5176\u4ed6\u4eba\u66ff\u4ee3\u5510\u5b66\u8c26\u3001\u5468\u5bcc\u660e\u3002</p><p>\u3000\u3000\u770b\u8d77\u6765\u4e0d\u7b97\u592a\u91cd\u8981\u7684\u4e00\u4ef6\u4e8b\uff0c\u5c31\u8ba9\u6d77\u5dde\u5e02\u7684\u4e0d\u7a33\u5b9a\u56e0\u7d20\u5f88\u5927\u7a0b\u5ea6\u4e0a\u6d88\u9664\u4e86\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u770b\u4e86\u513f\u5b50\u4e00\u773c\uff0c\u5fc3\u91cc\u4e0d\u786e\u5b9a\u4ed6\u5c0f\u5c0f\u5e74\u7eaa\u662f\u5426\u771f\u7684\u770b\u900f\u6d77\u5dde\u5e02\u5b98\u573a\u7684\u5fae\u5999\uff0c\u4f46\u662f\u7531\u6d77\u88d5\u516c\u53f8\u4ecb\u5165\u65b0\u5149\u9020\u7eb8\u5382\u7ecf\u8425\u8fd9\u62db\u68cb\uff0c\u771f\u7684\u5999\u4e0d\u53ef\u8a00\u3002</p><p>\u3000\u3000\u5e02\u653f\u5e9c\u6709\u51e0\u8f86\u5c0f\u8f66\u591c\u91cc\u5c31\u505c\u5728\u673a\u5173\u5927\u9662\u5185\uff0c\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u77e5\u884c\u901a\u77e5\u53f8\u673a\u9001\u987e\u5efa\u840d\u4e0e\u88f9\u7740\u5927\u5806\u793c\u7269\u7684\u5510\u5a67\u56de\u53bb\uff0c\u5dee\u4e0d\u591a\u516b\u70b9\u7684\u65f6\u5019\uff0c\u63a5\u5230\u5510\u5b66\u8c26\u7684\u7535\u8bdd\uff0c\u5f97\u77e5\u8fd9\u6b21\u4e34\u65f6\u5e38\u59d4\u4f1a\u8bae\u5f02\u5e38\u7684\u987a\u5229\uff0c\u5e02\u76f4\u673a\u5173\u4eba\u5458\u8c03\u6574\u65b9\u6848\u53ea\u4f5c\u5c11\u8bb8\u7684\u4fee\u6539\u5c31\u6beb\u65e0\u963b\u529b\u7684\u83b7\u5f97\u901a\u8fc7\u3002\u5e02\u5e38\u59d4\u6beb\u65e0\u963b\u529b\u901a\u8fc7\u7684\u51b3\u8bae\uff0c\u8c05\u4e07\u5411\u524d\u518d\u4e5f\u65e0\u80c6\u65e0\u7406\u5426\u51b3\uff0c\u5426\u5219\u5c31\u53ef\u80fd\u8ba9\u4ed6\u63d0\u524d\u5f7b\u5e95\u9000\u4f11\u3002\u5510\u5b66\u8c26\u6700\u540e\u8fd8\u8bf4\uff1a\u201c\u66ff\u5468\u4e66\u8bb0\u4e0e\u6211\u7292\u52b3\u7292\u52b3\u4f60\u5bb6\u5c0f\u5b50\uff0c\u771f\u662f\u5e2e\u4e0a\u5fd9\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4f9d\u7740\u89d2\u67dc\u800c\u7ad9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e0d\u80fd\u518d\u7292\u52b3\u4e86\uff0c\u8fd9\u5c0f\u5b50\u8fd9\u6bb5\u65f6\u95f4\u6536\u4e0d\u4f4f\u5fc3\uff0c\u671f\u4e2d\u8003\u8bd5\u5c45\u7136\u80fd\u6709\u4e00\u95e8\u4e0d\u53ca\u683c\uff0c\u8fd8\u7f3a\u8003\u4e00\u95e8\u2026\u2026\u201d\u5f20\u77e5\u884c\u7785\u7740\u7ad9\u5728\u65c1\u8fb9\u5fc3\u865a\u60f3\u6e9c\u7684\u5f20\u606a\uff0c\u77aa\u4e86\u77aa\u773c\uff0c\u8ba9\u4ed6\u8001\u5b9e\u5728\u65c1\u8fb9\u542c\u7740\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\u5427\uff0c\u5f20\u606a\u7684\u82f1\u8bed\u3001\u8bed\u6587\u4e0d\u662f\u6bd4\u5510\u5a67\u8fd8\u8003\u5f97\u597d\uff0c\u5510\u5a67\u4e0d\u4f1a\u7ed9\u4f60\u5bb6\u5c0f\u5b50\u4e70\u901a\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u82f1\u8bed\u3001\u8bed\u6587\u3001\u6570\u5b57\u4e09\u95e8\u529f\u8bfe\u662f\u4e0d\u9519\uff0c\u4ed6\u4eec\u8bf4\u614c\u4e5f\u662f\u6709\u6280\u5de7\u7684\uff0c\u5c31\u53ea\u63d0\u8fd9\u4e09\u95e8\u529f\u8bfe\uff0c\u5176\u4ed6\u7684\u529f\u8bfe\u8f7b\u8f7b\u4e00\u5e26\uff0c\u6211\u90fd\u758f\u5ffd\u95ee\u4e86\uff0c\u4eca\u5929\u9047\u5230\u4e00\u4e2d\u7684\u6821\u957f\u738b\u708e\u658c\uff0c\u8bf4\u662f\u8981\u4e0b\u4e0b\u5468\u53ec\u5f00\u65b0\u751f\u5bb6\u957f\u4f1a\uff0c\u8fd9\u624d\u6653\u5f97\uff0c\u4e0d\u7136\u8981\u7b49\u5230\u53bb\u5f00\u5bb6\u957f\u4f1a\u4e22\u4e86\u4eba\u624d\u77e5\u9053\u5b9e\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u606a\u81f3\u5c11\u6709\u628a\u63e1\u5ffd\u60a0\u4f60\u8ddf\u683c\u73cd\u4e0d\u53bb\u53c2\u52a0\u4ec0\u4e48\u5bb6\u957f\u4f1a\uff0c\u4ed6\u662f\u5343\u865a\u4e00\u5931\uff0c\u6ca1\u60f3\u5230\u738b\u708e\u658c\u8fd9\u4eba\u86ee\u4f1a\u5bc6\u5207\u8054\u7cfb\u6211\u4eec\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u7238\u7238\u653e\u4e0b\u7535\u8bdd\uff0c\u8146\u7740\u8138\u8bf4\uff1a\u201c\u600e\u4e48\u8bf4\u4e5f\u80fd\u529f\u8fc7\u76f8\u62b5\u5427\uff0c\u518d\u8bf4\u8fd9\u6bb5\u65f6\u95f4\u9003\u8bfe\u591a\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u4f60\u4eec\u7684\u4e8b\u60c5\uff1f\u665a\u775b\u59d0\u53ef\u6ca1\u6709\u65f6\u95f4\u7ba1\u8fd9\u8fb9\u7684\u4e8b\u60c5\uff0c\u90fd\u662f\u6211\u5728\u64cd\u5fc3\u3002\u518d\u8bf4\u4e86\uff0c\u5bf9\u6709\u5174\u8da3\u7684\u4e1c\u897f\uff0c\u5c31\u7b97\u9003\u8bfe\uff0c\u4e5f\u4f1a\u82b1\u65f6\u95f4\u8865\u4e0a\uff0c\u6ca1\u5174\u8da3\u7684\u4e1c\u897f\uff0c\u5750\u5728\u6559\u5ba4\u91cc\u4e5f\u662f\u6d6a\u8d39\u65f6\u95f4\uff0c\u5f53\u521d\u4fdd\u8bc1\u4e09\u95e8\u529f\u8bfe\uff0c\u4e0d\u662f\u8fd8\u7b97\u8fc7\u5f97\u53bb\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u6ee1\u8138\u65e0\u5948\uff0c\u5f53\u7136\u4e0d\u4f1a\u677f\u7740\u8138\u6559\u8bad\u5f20\u606a\uff0c\u8981\u8ddf\u4ed6\u8bf4\u9053\u7406\u5427\uff0c\u9053\u7406\u8ba9\u4ed6\u62a2\u7740\u8bf4\u5b8c\u4e86\uff0c\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u8bf4\u8bf4\u8fd9\u4e24\u5929\u5728\u7701\u57ce\u7684\u60c5\u51b5\u5427\uff0c\u6ca1\u60f3\u7740\u7ed9\u4f60\u8001\u5b50\u634e\u4ef6\u793c\u7269\uff1f\u201d</p><p>\u3000\u3000\u201c\u6015\u4f60\u8bef\u4f1a\uff0c\u5c31\u5fd8\u4e86\uff0c\u4e5f\u6ca1\u7ed9\u5510\u4f2f\u4f2f\u51c6\u5907\u3002\u201d\u5f20\u606a\u644a\u644a\u624b\u3002</p><p>\u3000\u3000\u201c\u6211\u80fd\u8bef\u4f1a\u4ec0\u4e48\uff0c\u8bef\u4f1a\u4f60\u56e0\u4e3a\u671f\u4e2d\u6210\u7ee9\u5f88\u7cdf\u7cd5\uff0c\u62ff\u4e1c\u897f\u8ba8\u597d\u6211\uff1f\u201d\u5f20\u77e5\u884c\u89c1\u5f20\u606a\u5f88\u8bda\u6073\u7684\u70b9\u70b9\u5934\uff0c\u8ba9\u59bb\u5b50\u5728\u65c1\u8fb9\u62bf\u7740\u5634\u7b11\uff0c\u65e0\u5948\u7684\u6447\u4e86\u6447\u5934\uff0c\u201c\u5f97\uff0c\u8bf4\u8bf4\u4f60\u5728\u5f90\u4e66\u8bb0\u5bb6\u7684\u60c5\u51b5\u3002\u201d</p><p>\u3000\u3000\u201c\u627f\u5305\u7ecf\u8425\u65b0\u5149\u9020\u7eb8\u5382\uff0c\u662f\u665a\u6674\u59d0\u7b54\u5e94\u4e0b\u6765\u7684\uff0c\u6700\u8fd1\u624d\u7531\u665a\u6674\u59d0\u8ddf\u5f90\u4f2f\u4f2f\u63d0\u8d77\uff0c\u8fd9\u6b21\u5728\u7701\u57ce\uff0c\u4e5f\u8c08\u5230\u8fd9\u4ef6\u4e8b\uff0c\u5f90\u4f2f\u4f2f\u7684\u610f\u89c1\u5f88\u660e\u786e\uff0c\u4e0d\u80fd\u8fdd\u89c4\u6709\u5173\u653f\u7b56\uff0c\u80fd\u5c3d\u5feb\u89e3\u51b3\u95ee\u9898\uff0c\u5bf9\u9020\u7eb8\u5382\u7684\u804c\u5de5\u8981\u6709\u4ea4\u5f85\u3002\u201d</p></article>"}}}]);
