(window.webpackJsonp=window.webpackJsonp||[]).push([[840],{863:function(p){p.exports={chapterTitle:null,title:"\u7b2c837\u7ae0 \u91cd\u5927\u5229\u76ca",content:"<article><h2>\u7b2c837\u7ae0 \u91cd\u5927\u5229\u76ca</h2><p>\u3000\u3000\u5df7\u5b50\u53e3\u95f9\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u9759\uff0c\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u4e5f\u5192\u5145\u56f4\u89c2\u7684\u5b66\u751f\u5728\u4e00\u65c1\u89c2\u671b\u5f62\u52bf\u3002\u5f85\u4eba\u7fa4\u8ddf\u6267\u6cd5\u961f\u5458\u90fd\u5404\u81ea\u6563\u53bb\uff0c\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u4e5f\u4e58\u8f66\u79bb\u5f00\uff0c\u770b\u7740\u5f20\u606a\u966a\u738b\u7ef4\u5747\u5750\u8fdb\u7f57\u541b\u7684\u8f66\u91cc\uff0c\u675c\u98de\u4e0e\u9648\u9759\u3001\u5218\u660e\u8f89\u3001\u8c22\u5b50\u5609\u90fd\u4e0d\u7b97\u964c\u751f\uff0c\u8bf7\u4ed6\u4eec\u53bb1978\u559d\u9152\u2014\u2014\u5f20\u606a\u8981\u8ddf\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u5728\u4e00\u8d77\uff0c\u4e00\u65f6\u534a\u4f1a\u513f\u4e5f\u79bb\u4e0d\u5f00\u3002</p><p>\u3000\u3000\u8fd9\u4e8b\u60c5\u5728\u7f57\u541b\u8d76\u5230\u4e4b\u540e\u5c31\u5e72\u51c0\u5229\u843d\u7684\u5904\u7406\u6389\u4e86\uff0c\u4e5f\u6ca1\u6709\u803d\u6401\u591a\u5c11\u65f6\u95f4\uff0c\u5750\u8fdb\u7f57\u541b\u7684\u8f66\u91cc\uff0c\u5f20\u606a\u770b\u4e86\u770b\u8155\u8868\uff0c\u624d\u591c\u91cc\u5341\u70b9\u534a\uff0c\u6b63\u662f\u9644\u8fd1\u9ad8\u6821\u5bbf\u820d\u7184\u706f\u7684\u65f6\u95f4\uff0c\u4f46\u5bf9\u4ed6\u4e0e\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u6765\u8bf4\uff0c\u65f6\u95f4\u8fd8\u5c1a\u65e9\u3002</p><p>\u3000\u3000\u8f66\u7a97\u5916\u7684\u591c\u8272\u6e05\u8ff7\uff0c\u8def\u706f\u6563\u53d1\u51fa\u67d4\u548c\u7684\u5149\u8292\u4ece\u8f66\u7a97\u91cc\u900f\u5c04\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u5230\u5efa\u90ba\u65f6\uff0c\u591c\u91cc\u7ecf\u8fc7\u8fd9\u91cc\uff0c\u5dee\u4e0d\u591a\u4e5f\u662f\u8fd9\u65f6\u95f4\uff0c\u4e1c\u534e\u5927\u9053\u4e0a\u7684\u884c\u4eba\u4e0e\u8f66\u8f86\u5c31\u8981\u7a00\u5c11\u8bb8\u591a\uff0c\u8fc7\u4e86\u5341\u4e8c\u70b9\uff0c\u5c31\u51e0\u4e4e\u770b\u4e0d\u5230\u6709\u8f66\u8f86\u5f80\u4e1c\u884c\u9a76\u2014\u2014\u518d\u5f80\u4e1c\u5c31\u662f\u90ca\u533a\u3001\u662f\u4e61\u4e0b\u3002\u8fc7\u53bb\u4e00\u5e74\u7684\u53d8\u5316\u662f\u663e\u8457\u7684\uff0c\u4e0d\u4ec5\u4ec5\u662f\u4f53\u73b0\u4e0a\u7684\u5e02\u91cc\u7edf\u8ba1\u7684\u6570\u636e\u4e0a\uff0c\u770b\u770b\u8f66\u7a97\u5916\u7684\u8f66\u8f86\uff0c\u6211\u662f\u5f88\u671f\u5f85\u4e09\u56db\u5e74\u4e4b\u540e\u8fd9\u7247\u571f\u5730\u4f1a\u53d8\u6210\u4ec0\u4e48\u6a21\u6837\uff0c\u5f53\u7136\uff0c\u5176\u4ed6\u4eba\u5bf9\u6b64\u4e5f\u662f\u5f88\u671f\u5f85\u2026\u2026\u201d\u7f57\u541b\u671b\u7740\u8f66\u7a97\u5916\u7684\u591c\u8272\uff0c\u8bed\u5e26\u53cc\u5173\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u795e\u8272\u7a0d\u6de1\uff0c\u53ea\u662f\u4fa7\u7740\u503e\u542c\u7f57\u541b\u7684\u8bdd\uff0c\u4ece\u8bdd\u91cc\u77e5\u9053\u6027\u683c\u5f3a\u52bf\u7684\u4ed6\u4e5f\u611f\u5230\u8bb8\u591a\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u7684\u6743\u529b\u7ed3\u6784\u5728\u4e5d\u516b\u5e74\u76f8\u6bd4\u5176\u4ed6\u5730\u65b9\u5c31\u6709\u5176\u7279\u6b8a\u4e4b\u5904\uff1a\u5e02\u59d4\u5e38\u59d4\u6210\u5458\u91cc\uff0c\u5e02\u653f\u5e9c\u5360\u4e86\u4e09\u540d\uff0c\u5305\u62ec\u5e02\u59d4\u526f\u4e66\u8bb0\u517c\u5e02\u957f\u3001\u5305\u62ec\u4e66\u8bb0\u526f\u4e66\u8bb0\u517c\u5e38\u52a1\u526f\u5e02\u957f\u3001\u5305\u62ec\u5e02\u59d4\u5e38\u59d4\u517c\u526f\u5e02\u957f\uff0c\u8fd9\u4e5f\u662f\u4e2d\u592e\u5728\u4e2d\u5fc3\u57ce\u5e02\u52a0\u5f3a\u7ecf\u6d4e\u5efa\u8bbe\u5de5\u4f5c\u7684\u8981\u6c42\uff0c\u5bfc\u81f4\u5efa\u90ba\u5e02\u5e9c\u5728\u5e02\u91cc\u7684\u529b\u91cf\u76f8\u5bf9\u5f3a\u52bf\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\uff0c\u9526\u6e56\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5efa\u90ba\u5e02\u53cd\u601d\u8fd9\u4e9b\u5e74\u6765\u5728\u9ad8\u65b0\u4ea7\u4e1a\u53d1\u5c55\u4e0a\u7684\u6218\u7565\u5e03\u5c40\uff0c\u957f\u671f\u5728\u5efa\u90ba\u8d1f\u8d23\u7ecf\u6d4e\u5de5\u4f5c\u7684\u8096\u660e\u5efa\u81ea\u7136\u8981\u4e3a\u5efa\u90ba\u5e02\u9ad8\u65b0\u4ea7\u4e1a\u7684\u53d1\u5c55\u6ede\u540e\u627f\u62c5\u4e00\u5b9a\u7684\u8d23\u4efb\uff0c\u7f57\u541b\u968f\u540e\u53c8\u5c06\u5e02\u59d4\u5e38\u59d4\u517c\u526f\u5e02\u957f\u738b\u7ef4\u5747\u62c9\u62e2\u8fc7\u6765\u4e00\u8d77\u63a8\u52a8\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u7b97\u662f\u6682\u65f6\u5c06\u8096\u660e\u5efa\u538b\u5236\u5f97\u65e0\u6cd5\u52a8\u5f39\u3002</p><p>\u3000\u3000\u4f46\u662f\u8fd9\u4ec5\u4ec5\u662f\u77ed\u6682\u7684\u73b0\u8c61\uff0c\u4e00\u65b9\u9762\uff0c\u738b\u7ef4\u5747\u4e0d\u518d\u62c5\u4efb\u526f\u5e02\u957f\uff0c\u800c\u662f\u6539\u4efb\u9ad8\u65b0\u533a\u515a\u5de5\u59d4\u4e66\u8bb0\u517c\u7ba1\u59d4\u4f1a\u4e3b\u4efb\uff0c\u6309\u7167\u60ef\u4f8b\uff0c\u5e02\u653f\u5e9c\u5c06\u6709\u65b0\u7684\u526f\u5e02\u957f\u8fdb\u5165\u5e02\u59d4\u5e38\u59d4\u540d\u5355\u4e4b\u5217\uff1b\u6700\u4e3b\u8981\u7684\uff0c\u56fd\u5185\u6709\u7740\u5e02\u59d4\u4e66\u8bb0\u7ba1\u5e72\u90e8\u3001\u5e02\u957f\u7ba1\u7ecf\u6d4e\u7684\u5206\u5de5\u6a21\u5f0f\uff0c\u7f57\u541b\u4e5f\u4e0d\u53ef\u80fd\u957f\u671f\u7684\u5c06\u624b\u635e\u8fc7\u754c\u76f4\u63a5\u5e72\u9884\u5e02\u653f\u5e9c\u7684\u7ecf\u6d4e\u5de5\u4f5c\u2014\u2014\u7ecf\u6d4e\u5de5\u4f5c\u603b\u5f52\u8981\u7531\u5e02\u5e9c\u4e3b\u5bfc\u3002</p><p>\u3000\u3000\u8096\u660e\u5efa\u96cc\u4f0f\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\uff0c\u5e76\u4e0d\u7518\u5fc3\u7ed9\u7f57\u541b\u538b\u5236\u4ee5\u81f4\u4ed6\u5728\u5e02\u653f\u5e9c\u91cc\u90fd\u6ca1\u6709\u8db3\u591f\u7684\u8bdd\u8bed\u6743\u3002\u5e74\u521d\u4ed6\u4e0e\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u8054\u5408\u63a8\u52a8\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u5c31\u662f\u4ed6\u60f3\u91cd\u65b0\u593a\u56de\u5728\u7ecf\u6d4e\u5de5\u4f5c\u4e0a\u4e3b\u5bfc\u6743\u7684\u4e00\u6b21\u52aa\u529b\u3002\u8fd9\u6b21\u5e02\u653f\u6267\u6cd5\u6cd5\u5230\u5b66\u5e9c\u5df7\u6765\u6267\u6cd5\uff0c\u8c8c\u4f3c\u8096\u660e\u5efa\u662f\u56db\u6708\u7ecf\u8fc7\u8fd9\u91cc\u968f\u53e3\u5429\u5490\u4e86\u4e00\u53e5\uff0c\u4ed6\u8fd9\u201c\u968f\u53e3\u5429\u5490\u201d\u5c31\u6709\u6781\u9ad8\u7684\u6280\u5de7\u5728\u5185\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u60f3\u63d2\u624b\u6570\u5b57\u957f\u5eca\u6784\u60f3\u7684\u4e8b\u52a1\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u4e0d\u7518\u5fc3\u5e76\u975e\u5355\u7eaf\u7684\u6743\u529b\u6597\u4e89\uff0c\u8fd8\u6d89\u53ca\u5230\u5404\u79cd\u590d\u6742\u7684\u5229\u76ca\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u8ba1\u5212\u4eca\u540e\u4e94\u5e74\u4f1a\u5c06\u6295\u5165\u8d85\u8fc7\u767e\u4ebf\u7684\u5e02\u653f\u5efa\u8bbe\u8d44\u91d1\u5230\u9ad8\u65b0\u533a\u91cc\uff0c\u751a\u81f3\u8981\u8fdc\u8fdc\u8d85\u8fc7\u4e2d\u5fc3\u57ce\u533a\u767d\u6c5f\u533a\u3002</p><p>\u3000\u3000\u5e02\u653f\u6295\u8d44\u53ef\u4ee5\u8bf4\u662f\u5728\u4e0e\u5b98\u573a\u52fe\u8fde\u7684\u6240\u6709\u5229\u5bb3\u5173\u7cfb\u4e2d\u6700\u80fd\u76f4\u63a5\u8f93\u9001\u5229\u76ca\u7684\uff0c\u9ad8\u65b0\u533a\u5728\u5e02\u653f\u6295\u8d44\u4e0a\u5212\u8d70\u6700\u5927\u7684\u4e00\u5757\u86cb\u7cd5\uff0c\u5374\u9650\u5236\u90a3\u4e9b\u864e\u89c6\u7708\u7708\u7684\u5229\u76ca\u96c6\u56e2\u6d89\u8db3\u8fdb\u6765\uff0c\u5c82\u80fd\u4e0d\u906d\u4eba\u5fcc\u6068\uff1f</p><p>\u3000\u3000\u5e02\u653f\u6295\u8d44\u8fd8\u4e0d\u662f\u6700\u4e3b\u8981\u7684\u95ee\u9898\uff0c\u5176\u5b9e\u4ece\u56db\u6708\u521d\u56fd\u52a1\u9662\u5ba3\u5e03\u57ce\u9547\u804c\u5de5\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u4ee5\u6765\uff0c\u5f88\u591a\u4eba\u90fd\u610f\u8bc6\u5230\u653f\u5e9c\u624b\u91cc\u63e1\u7740\u7684\u6700\u8bf1\u4eba\u7684\u86cb\u7cd5\u4e0d\u518d\u662f\u5176\u4ed6\uff0c\u800c\u662f\u957f\u671f\u6ca1\u6709\u5f97\u5230\u8db3\u591f\u91cd\u89c6\u7684\u571f\u5730\u8d44\u6e90\u3002</p><p>\u3000\u3000\u4e2d\u5fc3\u57ce\u5e02\u767d\u6c5f\u533a\u4eba\u5c45\u73af\u5883\u6210\u719f\uff0c\u5373\u4f7f\u6709\u4e9b\u65e7\u57ce\u533a\u8981\u6539\u5efa\uff0c\u5f00\u53d1\u5546\u901a\u5e38\u53ea\u80fd\u96f6\u96f6\u788e\u788e\u7684\u50cf\u6253\u8865\u4e01\u5f0f\u7684\u62ff\u5730\u8fdb\u884c\u5730\u4ea7\u5f00\u53d1\uff0c\u800c\u5efa\u90ba\u5e02\u542f\u52a8\u6570\u5b57\u957f\u5eca\u6784\u60f3\uff0c\u4e00\u4e0b\u5b50\u5c31\u5c06\u4e0a\u767e\u5e73\u65b9\u516c\u91cc\u7684\u571f\u5730\u5212\u5165\u9ad8\u65b0\u533a\uff0c\u5373\u4f7f\u5546\u4e1a\u4f4f\u5b85\u533a\u89c4\u6a21\u5360\u4e0d\u523030\uff05\uff0c\u53ef\u5f00\u53d1\u7684\u571f\u5730\u9762\u79ef\u4e5f\u5c06\u8fdc\u8fdc\u8d85\u8fc7\u5176\u4ed6\u56db\u4e2a\u57ce\u533a\u7684\u603b\u548c\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u62ff\u4e0b\u9ad8\u65b0\u533a\u6e56\u4e1c\u5546\u5708\u9879\u76ee\u7684\u65f6\u673a\u975e\u5e38\u7684\u597d\uff0c\u5c31\u662f\u5728\u56fd\u52a1\u9662\u6b63\u5f0f\u542f\u52a8\u57ce\u9547\u804c\u5de5\u4f4f\u623f\u5236\u5ea6\u6539\u9769\u4e4b\u524d\u7684\u51e0\u4e2a\u6708\uff0c\u53c8\u5c06\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u8ddf\u7cbe\u5178\u5730\u4ea7\u7684\u6ce8\u610f\u529b\u9a97\u5230\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u4e0a\u53bb\u3002\u6362\u4f5c\u4e5d\u516b\u5e74\u56db\u6708\u4e4b\u540e\u518d\u542f\u52a8\u8fd9\u4e2a\u9879\u76ee\uff0c\u4e16\u7eaa\u9526\u6e56\u82e5\u8fd8\u80fd\u5982\u6b64\u8f7b\u677e\u7684\u62ff\u4e0b\u8fd9\u4e2a\u9879\u76ee\uff0c\u53ea\u6015\u4f1a\u6709\u4eba\u5c06\u5b98\u53f8\u6253\u5230\u56fd\u52a1\u9662\u53bb\u2014\u2014\u4e5d\u516b\u5e74\u56db\u6708\u4e4b\u540e\uff0c\u56fd\u5185\u7684\u623f\u5730\u4ea7\u5e02\u573a\u7531\u4e8e\u57ce\u9547\u804c\u5de5\u4f4f\u5236\u5ea6\u6539\u9769\u800c\u5f97\u5230\u91cd\u65b0\u7684\u5ba1\u89c6\uff0c\u4e4b\u524d\u56fd\u5185\u7684\u623f\u5730\u4ea7\u5e02\u573a\u9700\u6c42\u662f\u53d7\u5230\u4e25\u91cd\u538b\u5236\u7684\uff0c\u56db\u6708\u4e4b\u540e\uff0c\u867d\u7136\u5e02\u573a\u4e0d\u4f1a\u6709\u7a81\u7136\u660e\u663e\u7684\u53d8\u5316\uff0c\u4f46\u662f\u7a0d\u6709\u4e9b\u7ecf\u6d4e\u5e38\u8bc6\u7684\u4eba\u90fd\u80fd\u770b\u5230\u5e9e\u5927\u5230\u60ca\u4eba\u7684\u5546\u54c1\u4f4f\u5b85\u5e02\u573a\u6f5c\u5728\u9700\u6c42\uff0c\u571f\u5730\u81ea\u7136\u5c31\u6210\u4e86\u4e89\u593a\u7684\u6838\u5fc3\u8d44\u6e90\u3002\u90a3\u4e9b\u6709\u7ecf\u6d4e\u773c\u5149\u7684\u65e2\u5f97\u5229\u76ca\u8005\uff0c\u81ea\u7136\u4f1a\u5c06\u76ee\u5149\u5148\u653e\u5230\u56fd\u5185\u7ecf\u6d4e\u53d1\u5c55\u6700\u6d3b\u8dc3\u7684\u51e0\u4e2a\u4e2d\u5fc3\u57ce\u5e02\u53bb\u3002</p><p>\u3000\u3000\u867d\u7136\u8ba9\u4e16\u7eaa\u9526\u6e56\u5212\u8d70\u6e56\u4e1c\u5546\u5708\u9879\u76ee\u4e24\u5e73\u65b9\u516c\u91cc\u7684\u571f\u5730\uff0c\u4f46\u662f\u9ad8\u65b0\u533a\u8fd8\u9884\u7559\u6709\u8d85\u8fc7\u5341\u500d\u7684\u5546\u4e1a\u4f4f\u5b85\u89c4\u5212\u571f\u5730\uff0c\u8fd9\u4e00\u7247\u571f\u5730\uff0c\u5176\u5b9e\u4e0e\u5efa\u90ba\u5e02\u4e2d\u5fc3\u7684\u8ddd\u79bb\u5e76\u4e0d\u9065\u8fdc\uff0c\u4e0e\u6700\u6838\u5fc3\u7684\u5546\u5708\u65b0\u5e02\u8857\u4e0d\u8d85\u8fc7\u5341\u516c\u91cc\uff0c\u4e0e\u5efa\u90ba\u5e02\u526f\u57ce\u5e02\u4e2d\u5fc3\u5e9c\u9752\u8def\u4e5f\u6070\u6070\u53ea\u6709\u5341\u516c\u91cc\uff0c\u5f62\u6210\u6781\u4f73\u7684\u57ce\u5e02\u5e03\u5c40\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u6570\u5b57\u957f\u5eca\u6784\u60f3\u542f\u52a8\u8fd1\u4e00\u5e74\u7684\u65f6\u95f4\u4ee5\u6765\uff0c\u6210\u5c31\u6590\u7136\uff0c\u4f7f\u5f97\u5efa\u90ba\u5728\u56fd\u5185\u5176\u4ed6\u57ce\u5e02\u6df1\u53d7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5f71\u54cd\u7684\u540c\u65f6\u7ecf\u6d4e\u4fdd\u6301\u5f3a\u52b2\u7684\u589e\u6da8\u3002\u4ee5\u9526\u6e56\u4e3a\u9996\u7ec4\u7ec7\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e3b\u5bfc\u7740\u56fd\u5185\u7684\u624b\u673a\u4ea7\u4e1a\u5e03\u5c40\u4ece\u4e00\u5f00\u59cb\u5c31\u5728\u5efa\u90ba\u3001\u6d77\u5dde\u96c6\u4e2d\uff0c\u4e0d\u4ec5\u56fd\u4ea7\u624b\u673a\u5382\u5546\u53ca\u914d\u5957\u5382\u5546\uff0c\u751a\u81f3\u8fde\u4e09\u661f\u3001\u8bfa\u57fa\u4e9a\u7b49\u5916\u8d44\u624b\u673a\u5382\u5546\u90fd\u8003\u8651\u5230\u5efa\u90ba\u6295\u8d44\uff0c\u4ee5\u83b7\u5f97\u96f6\u914d\u4ef6\u3001\u4ea7\u54c1\u7814\u53d1\u4ee5\u4e13\u4e1a\u4eba\u624d\u4e0a\u7684\u4fbf\u5229\u3002\u65b0\u5174\u7684\u57ce\u5e02\uff0c\u7ecf\u6d4e\u7684\u9ad8\u901f\u589e\u6da8\u52bf\u5fc5\u5bfc\u81f4\u5927\u91cf\u5916\u6765\u4eba\u5458\u6d8c\u5165\u8be5\u5730\u533a\uff0c\u7279\u522b\u662f\u9ad8\u65b0\u4ea7\u4e1a\u7684\u5174\u8d77\uff0c\u4f1a\u5f15\u5165\u5927\u91cf\u7684\u9ad8\u7d20\u8d28\u3001\u9ad8\u6536\u5165\u4eba\u7fa4\uff0c\u4f4f\u5b85\u5e02\u573a\u7684\u9700\u6c42\u5c06\u8fdb\u4e00\u6b65\u7684\u6269\u5927\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4e0d\u53bb\u8003\u8651\u672a\u6765\u56fd\u5185\u623f\u5730\u4ea7\u5e02\u573a\u975e\u7406\u6027\u7684\u98de\u6da8\uff0c\u9ad8\u65b0\u533a\u5546\u4e1a\u4f4f\u5b85\u89c4\u5212\u571f\u5730\u6700\u4fdd\u5b88\u7684\u53bb\u4f30\u8ba1\u4e5f\u5c06\u6709500\u4ebf\u52301000\u4ebf\u7684\u6f5c\u5728\u4ef7\u503c\u2014\u2014\u5f20\u606a\u51e0\u4e4e\u80fd\u80af\u5b9a\u5efa\u90ba\u7684\u5546\u4e1a\u5730\u4ea7\u4e0e\u4f4f\u5b85\u5730\u4ea7\u7684\u4ef7\u683c\u4f1a\u63d0\u524d\u4e00\u6b65\u98de\u6da8\uff0c\u7279\u522b\u662f\u71d5\u5f52\u6e56\u4e1c\u7247\u8fd9\u4e00\u5757\u6838\u5fc3\u533a\u57df\uff0c\u65b0\u7684\u9ad8\u65b0\u533a\u884c\u653f\u4e2d\u5fc3\u4e0e\u6e56\u4e1c\u5546\u5708\u4ee5\u53ca\u884c\u653f\u4e2d\u5fc3\u4e0e\u6e56\u4e1c\u5546\u5708\u9879\u4e4b\u95f4\u7684\u5927\u578b\u516c\u5f00\u7eff\u5730\u914d\u5957\u9879\u76ee\u90fd\u5df2\u7ecf\u6b63\u5f0f\u542f\u52a8\uff0c\u71d5\u5f52\u6e56\u7684\u5468\u8fb9\u73af\u5883\u4e5f\u5728\u8fdb\u4e00\u6b65\u7684\u6574\u6cbb\u4e4b\u4e2d\uff0c\u6b64\u65f6\u7528\u6765\u5f00\u53d1\u5546\u4e1a\u3001\u4f4f\u5b85\u5730\u4ea7\u7684\u4f18\u52bf\u5df2\u7ecf\u975e\u5e38\u7684\u660e\u663e\u3002</p><p>\u3000\u3000\u8981\u8bf4\u4e0a\u767e\u4ebf\u7684\u5e02\u653f\u6295\u8d44\uff0c\u6027\u683c\u5f3a\u52bf\u3001\u4e3a\u5efa\u90ba\u5e02\u7ecf\u6d4e\u53d1\u5c55\u4eb2\u624b\u62df\u5b9a\u9ad8\u901f\u53d1\u5c55\u84dd\u56fe\u7684\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u8fd8\u80fd\u6342\u5f97\u4f4f\uff0c\u4e0d\u8ba9\u90a3\u4e9b\u5229\u76ca\u7fa4\u4f53\u8d2a\u592a\u591a\uff1b\u90a3\u4e9b\u4fdd\u5b88\u4f30\u7b97\u5c31\u6709\u4e0a\u5343\u4ebf\u6f5c\u5728\u4ef7\u503c\u7684\u5546\u4e1a\u4f4f\u5b85\u571f\u5730\uff0c\u5c31\u7b97\u8d75\u6d4e\u4e1c\u603b\u7406\u4eb2\u81ea\u6765\u6342\uff0c\u4e5f\u4f1a\u6709\u4e0d\u8ba1\u5176\u6570\u7684\u5229\u76ca\u56e2\u4f53\u4f1a\u50cf\u95fb\u5230\u8840\u8165\u5473\u7684\u725b\u8747\u4e00\u6837\u60f3\u65b9\u8bbe\u6cd5\u8fc7\u6765\u5438\u8840\u2014\u2014\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u5efa\u90ba\u5730\u65b9\u4e0a\u7684\u5229\u76ca\u56e2\u4f53\uff0c\u90a3\u4e9b\u80fd\u591f\u5c06\u624b\u4f38\u8fdb\u5efa\u90ba\u6765\u7684\uff0c\u90fd\u4e0d\u4f1a\u653e\u5f03\u5c1d\u8bd5\uff0c\u8fd9\u4e5f\u8ba9\u5efa\u90ba\u7ed9\u62d6\u5165\u66f4\u590d\u6742\u7684\u5229\u76ca\u7ea0\u7f20\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u5728\u7ecf\u6d4e\u6709\u7ba1\u7406\u7279\u6743\uff0c\u7ecf\u6d4e\u5de5\u4f5c\u53d7\u4e2d\u592e\u7684\u76f4\u63a5\u7ba1\u8f96\uff0c\u5efa\u90ba\u5e02\u7684\u4e3b\u8981\u515a\u653f\u9886\u5bfc\u4efb\u547d\uff0c\u4e5f\u662f\u53d7\u4e2d\u592e\u76f4\u63a5\u5e72\u9884\uff0c\u7701\u91cc\u751a\u81f3\u5bf9\u8fd9\u79cd\u66f4\u590d\u6742\u7684\u5229\u76ca\u7ea0\u7f20\u90fd\u4f1a\u611f\u5230\u5f88\u65e0\u529b\u3002\u9526\u6e56\u80fd\u5c06\u6d77\u5dde\u7ecf\u8425\u5f97\u8ddf\u94c1\u6876\u4e00\u6837\uff0c\u6b63\u662f\u7531\u4e8e\u7701\u4e00\u7ea7\u7684\u7f13\u51b2\uff1b\u4f46\u662f\u5efa\u90ba\u5e02\uff0c\u8fd9\u79cd\u7f13\u51b2\u4f5c\u7528\u8981\u5c0f\u5f97\u591a\uff0c\u53e6\u5916\uff0c\u9526\u6e56\u5bf9\u5efa\u90ba\u8fd9\u6837\u7684\u4e2d\u5fc3\u57ce\u5e02\u7684\u5f71\u54cd\u4e0e\u4f5c\u7528\u4e5f\u76f8\u5bf9\u6709\u9650\u3002</p><p>\u3000\u3000\u56db\u6708\uff0c\u56fd\u52a1\u9662\u542f\u52a8\u57ce\u9547\u804c\u5de5\u4f4f\u623f\u5236\u5ea6\u6539\u9769\uff0c\u56db\u6708\uff0c\u8096\u660e\u5efa\u7ecf\u8fc7\u5b66\u5e9c\u5df7\u201c\u968f\u53e3\u5429\u5490\u201d\u4e00\u53e5\u5c31\u8ba9\u5e02\u653f\u7ba1\u7406\u5c40\u6d3e\u6267\u6cd5\u961f\u8fdb\u5165\u9ad8\u65b0\u533a\u8fdb\u884c\u5e02\u5bb9\u6574\u987f\u6267\u6cd5\uff0c\u5f20\u606a\u8fd8\u8bb0\u5f97\u56db\u6708\u4e2d\u65ec\u675c\u98de\u66fe\u8ddf\u4ed6\u8bf4\u8d77\u8fc7\u6709\u4e9b\u751f\u5728\u7070\u8272\u5939\u5c42\u4e2d\u7684\u52bf\u529b\u5bf9\u5b66\u5e9c\u5df7\u864e\u89c6\u7708\u7708\u2014\u2014\u8fd9\u91cc\u9762\u4e0d\u53ef\u80fd\u6ca1\u6709\u8054\u7cfb\u3002</p><p>\u3000\u3000\u5f88\u660e\u663e\uff0c\u7f57\u541b\u5728\u7ecf\u6d4e\u5de5\u4f5c\u4e0a\u7684\u51fa\u8272\u8868\u73b0\uff0c\u4ee5\u53ca\u5728\u515a\u5185\u4f18\u4e8e\u8096\u660e\u5efa\u7684\u5730\u4f4d\uff0c\u786e\u5b9a\u4ed6\u773c\u4e0b\u5728\u5efa\u90ba\u5e02\u91cc\u7684\u5730\u4f4d\uff0c\u4f46\u662f\u773c\u4e0b\u6709\u660e\u663e\u7684\u8ff9\u8c61\u8868\u660e\u8096\u660e\u5efa\u6709\u610f\u8054\u5408\u90a3\u4e9b\u5bf9\u9ad8\u65b0\u533a\u864e\u89c6\u7708\u7708\u7684\u5229\u76ca\u7fa4\u4f53\u6765\u91cd\u65b0\u633d\u56de\u5728\u5e02\u91cc\u7684\u9893\u52bf\u3002</p><p>\u3000\u3000\u7f57\u541b\u82e5\u662f\u8fc7\u4e8e\u521a\u786c\uff0c\u4e0e\u90a3\u4e9b\u5229\u76ca\u56e2\u4f53\uff0c\u4e0e\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u7b49\u4eba\u6495\u7834\u8138\u76ae\uff0c\u7ed3\u5c40\u6216\u8bb8\u8981\u6bd4\u5f90\u5b66\u5e73\u8981\u597d\uff0c\u4f46\u662f\u7ed9\u8c03\u79bb\u5efa\u90ba\u5e76\u975e\u96be\u4ee5\u60f3\u8c61\u7684\u7ed3\u5c40\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5f20\u606a\u5bf9\u7f57\u541b\u7684\u671f\u5f85\u4e5f\u6ca1\u6709\u90a3\u4e48\u9ad8\uff0c\u4ed6\u4e4b\u524d\u7ed9\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u5728\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u4e0a\u4e0b\u7684\u5957\uff0c\u603b\u4f1a\u53d1\u6325\u4f5c\u7528\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5373\u4f7f\u5efa\u90ba\u5e02\u518d\u6362\u4e00\u4efb\u65b0\u7684\u5e02\u957f\uff0c\u5e76\u4e0d\u80fd\u6539\u53d8\u80cc\u540e\u5b58\u5728\u50cf\u5438\u8840\u8747\u7fa4\u4f3c\u7684\u5229\u76ca\u7fa4\u4f53\u8fd9\u4e00\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u7279\u522b\u662f\u968f\u7740\u623f\u5730\u4ea7\u5e02\u573a\u9010\u6e10\u706b\u70ed\uff0c\u7279\u522b\u5f53\u56fd\u6709\u571f\u5730\u8f6c\u8ba9\u91d1\u6210\u4e3a\u5730\u65b9\u8d22\u653f\u6536\u5165\u7684\u4e3b\u8981\u6765\u6e90\u65f6\uff0c\u5730\u65b9\u4e0a\u7684\u5b98\u50da\u7fa4\u4f53\u5c31\u4f1a\u53d1\u73b0\u53ef\u4ee5\u4ece\u56fd\u6709\u571f\u5730\u8d44\u6e90\u91cc\u6e90\u6e90\u4e0d\u65ad\u7684\u5438\u8840\u80fd\u591f\u6700\u5927\u9650\u5ea6\u7684\u63d0\u9ad8\u4ed6\u4eec\u7684\u201c\u9633\u5149\u6536\u5165\u201d\u3001\u6539\u5584\u4ed6\u4eec\u7684\u751f\u6d3b\u3002</p><p>\u3000\u3000\u8c01\u8981\u63d0\u51fa\u6253\u538b\u623f\u5730\u4ea7\uff0c\u4e0d\u8bf4\u4ea7\u4e1a\u4e0d\u4ea7\u4e1a\u7684\u95ee\u9898\uff0c\u9996\u5148\u5c31\u662f\u8ddf\u5730\u65b9\u4e0a\u7684\u6574\u4e2a\u5b98\u50da\u7fa4\u4f53\u8fc7\u4e0d\u53bb\u3002</p><p>\u3000\u3000\u73b0\u5728\u9526\u6e56\u4e5f\u7b97\u662f\u7fc5\u8180\u786c\u4e86\uff0c\u8ddf\u67d0\u4e2a\u7701\u957f\u3001\u67d0\u4e2a\u7701\u59d4\u4e66\u8bb0\u3001\u67d0\u4e2a\u90e8\u59d4\u91cc\u7684\u5934\u8111\u7c97\u7740\u55d3\u5b50\u8bf4\u8bdd\uff0c\u90fd\u4e0d\u4f1a\u6709\u591a\u5927\u95ee\u9898\uff0c\u751a\u81f3\u66fe\u5982\u5723\u3001\u8d75\u6d4e\u4e1c\u7b49\u5927\u4eba\u7269\u5bf9\u9526\u6e56\u63d0\u51fa\u4ec0\u4e48\u4e0d\u5408\u7406\u7684\u8981\u6c42\uff0c\u9526\u6e56\u4e00\u6837\u53ef\u4ee5\u786c\u7740\u5934\u76ae\u9876\u56de\u53bb\uff0c\u6bd5\u7adf\u5927\u5bb6\u90fd\u4e0d\u662f\u5c0f\u5b69\u5b50\uff0c\u4e0d\u4f1a\u8a00\u8bed\u7a0d\u6709\u4e0d\u5bf9\u5c31\u4f1a\u8131\u5149\u8180\u5b50\u4e0a\uff0c\u4f46\u662f\u5f20\u606a\u4e0d\u5f97\u4e0d\u6df1\u523b\u7684\u611f\u53d7\u5230\uff0c\u8981\u8ddf\u6574\u4e2a\u65e2\u5f97\u5229\u76ca\u56e2\u4f53\u505a\u6597\u4e89\uff0c\u65e0\u7591\u5c31\u662f\u87b3\u81c2\u5f53\u8f66\uff0c\u53ea\u80fd\u5bc4\u5e0c\u671b\u60c5\u51b5\u80fd\u6162\u6162\u53d8\u597d\u3002\u5373\u4f7f\u8ddf\u4e00\u64ae\u4eba\u6597\u4e89\uff0c\u4e5f\u8ddf\u8dd1\u9a6c\u62c9\u677e\u4f3c\u7684\uff0c\u4e0d\u8dd1\u5230\u6700\u540e\u7cbe\u75b2\u529b\u7aed\uff0c\u90fd\u65e0\u6cd5\u9884\u6d4b\u8c01\u5230\u5e95\u80fd\u538b\u8fc7\u8c01\uff0c\u5c31\u7b97\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u4e2d\u4e0d\u65ad\u7684\u5c06\u90a3\u64ae\u4eba\u4e2d\u7684\u5bf9\u624b\u62d6\u4e0b\u9a6c\uff0c\u4f46\u4e5f\u4f1a\u4e0d\u65ad\u7684\u6709\u4eba\u52a0\u5165\u5230\u90a3\u4e00\u64ae\u4eba\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u4eca\u5929\u665a\u4e0a\u5e02\u653f\u7ba1\u7406\u5c40\u6267\u6cd5\u961f\u7684\u526f\u961f\u957f\u5218\u519b\u4ee5\u53ca\u90a3\u4e2a\u52a8\u624b\u6253\u4eba\u7684\u6267\u6cd5\u961f\u5458\uff0c\u8bf4\u5230\u5e95\u53ea\u662f\u7ed9\u727a\u7272\u6389\u7684\u70ae\u7070\u5c0f\u89d2\u8272\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u8fd8\u4e0d\u662f\u5f88\u6e05\u695a\u8096\u660e\u5efa\u59ff\u6001\u80fd\u591f\u5fae\u5999\u8f6c\u53d8\u5f97\u5230\u7684\u6700\u76f4\u63a5\u52a9\u529b\u662f\u6765\u81ea\u54ea\u91cc\uff0c\u4f46\u770b\u7f57\u541b\u7709\u5934\u5fae\u9501\u7684\u6a21\u6837\uff0c\u4f3c\u4e4e\u8eab\u4e0a\u6240\u53d7\u7684\u538b\u529b\u5e76\u4e0d\u8f7b\u677e\u3002</p><p>\u3000\u3000\u8fd9\u5e74\u5934\uff0c\u8c01\u8eab\u4e0a\u538b\u529b\u90fd\u4e0d\u8f7b\u677e\uff0c\u5728\u9001\u7f57\u541b\u8fd4\u56de\u4f4f\u5904\uff0c\u5f20\u606a\u60f3\u8d77\u4e00\u4ef6\u4e8b\u8ddf\u4ed6\u8bf4\u9053\uff1a\u201c\u603b\u88c5\u5907\u90e8\u6253\u7b97\u8ba9\u9526\u6e56\u8ddf\u5efa\u90ba\u7684\u4e00\u4e9b\u7814\u7a76\u673a\u6784\u5bf9\u7a00\u571f\u7684\u6c11\u7528\u6280\u672f\u53d1\u5c55\u524d\u666f\u505a\u4e00\u4e2a\u5c0f\u89c4\u6a21\u7684\u6c47\u62a5\uff0c\u603b\u88c5\u5907\u90e8\u7684\u526f\u90e8\u957f\u5d14\u6587\u6bc5\u5c06\u519b\u8fd1\u671f\u4f1a\u5230\u5efa\u90ba\u6765\uff0c\u6211\u89c9\u5f97\u8fd9\u4e8b\u8fd8\u662f\u77e5\u4f1a\u4e00\u58f0\u5e02\u91cc\u2026\u2026\u7b49\u603b\u88c5\u5907\u90e8\u7684\u6001\u5ea6\u5b9a\u4e0b\u6765\u4e4b\u540e\uff0c\u9526\u6e56\u77ff\u4e1a\u5c31\u53ef\u4ee5\u6b63\u5f0f\u7b79\u5907\u4e86\uff0c\u56fd\u5185\u7684\u516c\u53f8\uff0c\u9526\u6e56\u53ea\u4f1a\u5360\u5f88\u5c0f\u6bd4\u4f8b\u7684\u80a1\u4efd\uff0c\u4f46\u662f\u8fd9\u4e00\u5757\u5c06\u6765\u80fd\u4ea7\u751f\u7684\u5229\u6da6\u4e0d\u4f1a\u592a\u5c11\uff0c\u6211\u5efa\u8bae\u5efa\u90ba\u5e02\u91cc\u4ee5\u57ce\u5546\u884c\u6216\u9ad8\u65b0\u533a\u7684\u540d\u4e49\u5360\u4e00\u4e9b\u80a1\u4efd\u2026\u2026\u201d</p><p>\u3000\u3000\u6d89\u53ca\u5230\u77ff\u4ea7\u8d44\u6e90\u7684\u6295\u8d44\uff0c\u9526\u6e56\u77ff\u4e1a\u60f3\u8981\u771f\u6b63\u7684\u53d1\u5c55\u58ee\u5927\uff0c\u56fd\u5185\u516c\u53f8\u7684\u8d44\u672c\u6027\u8d28\u4e0d\u80fd\u76f4\u63a5\u89e6\u72af\u5f53\u524d\u7684\u4f53\u5236\uff0c\u62c9\u5efa\u90ba\u5e02\u57ce\u5546\u884c\u5165\u80a1\u4e5f\u597d\u3001\u62c9\u9ad8\u65b0\u533a\u5165\u80a1\u4e5f\u597d\uff0c\u751a\u81f3\u4f1a\u8ba9\u6d77\u5dde\u5e02\u65b9\u9762\u4e5f\u4ee5\u56fd\u6709\u8d44\u4ea7\u7684\u5f62\u5f0f\u5165\u80a1\uff0c\u8fd9\u4e48\u505a\uff0c\u90fd\u80fd\u591f\u4fdd\u8bc1\u9526\u6e56\u53ea\u8981\u5f88\u5c0f\u6bd4\u4f8b\u7684\u6301\u80a1\u5c31\u80fd\u83b7\u5f97\u5bf9\u9526\u6e56\u77ff\u4e1a\u7684\u63a7\u5236\u6743\u3002\u6bd4\u8d77\u6574\u4e2a\u7a00\u571f\u6218\u7565\u5728\u672a\u6765\u80fd\u7ed9\u9526\u6e56\u4ea7\u751f\u7684\u5de8\u5927\u5229\u76ca\uff0c\u9526\u6e56\u77ff\u4e1a\u5728\u56fd\u5185\u90e8\u5206\u7684\u76f4\u63a5\u5229\u6da6\uff0c\u6bd5\u7adf\u662f\u5c0f\u5934\u3002</p><p>\u3000\u3000\u56fd\u5185\u90e8\u5206\u7684\u5408\u4f5c\u6a21\u5f0f\uff0c\u5f20\u606a\u4e4b\u524d\u5c31\u8ddf\u7f57\u541b\u8ba8\u8bba\u8fc7\uff0c\u6ce8\u518c\u8d44\u91d1\u989d\u5ea6\u4e0d\u9ad8\uff0c\u771f\u6b63\u8fdb\u884c\u8fd0\u4f5c\u7684\u5de8\u989d\u8d44\u91d1\u7531\u9526\u6e56\u4ee5\u516c\u53f8\u503a\u6216\u5176\u4ed6\u7684\u5f62\u5f0f\u63d0\u4f9b\u7ed9\u56fd\u5185\u516c\u53f8\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5efa\u90ba\u5e02\u53ea\u9700\u8981\u51fa\u8d44\u51e0\u767e\u4e07\u7684\u8d44\u91d1\uff0c\u5c31\u80fd\u5206\u4eab\u65e5\u540e\u53ef\u80fd\u4f1a\u6709\u7684\u5de8\u989d\u5229\u6da6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u4e3a\u4e86\u80fd\u8ba9\u8fd9\u4e2a\u65b9\u6848\u6210\u529f\u83b7\u5f97\u5404\u65b9\u9762\u7684\u652f\u6301\u505a\u51fa\u6700\u5927\u7684\u8ba9\u6b65\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u6240\u8c13\u7a00\u571f\u6c11\u7528\u6280\u672f\u53d1\u5c55\u524d\u666f\u7684\u5c0f\u89c4\u6a21\u6c47\u62a5\u4e5f\u662f\u9526\u6e56\u77ff\u4e1a\u65b9\u6848\u6d89\u53ca\u5230\u56fd\u5185\u76f8\u5f53\u5229\u76ca\u65b9\u7684\u9996\u6b21\u76f8\u805a\uff0c\u80fd\u5728\u7ae0\u5dde\u3001\u4e09\u4e95\u6848\u8fd8\u6ca1\u6709\u7ed3\u6848\u4e4b\u524d\uff0c\u5c31\u80fd\u63a8\u8fdb\u5230\u8fd9\u4e00\u6b65\uff0c\u4e5f\u662f\u6709\u53f6\u5bb6\u5728\u80cc\u540e\u652f\u6301\u7684\u53f6\u5efa\u658c\u7684\u6d3b\u52a8\u80fd\u529b\u7684\u4f53\u73b0\u3002</p><p>\u3000\u3000\u201c\u5d14\u6587\u6bc5\u5c06\u519b\u8fc7\u6765\u554a\uff0c\u201d\u7f57\u541b\u60f3\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff0c\u201c\u5c4a\u65f6\u6211\u5217\u5e2d\u4e00\u4e0b\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u8fd8\u662f\u4ea4\u7ed9\u9ad8\u65b0\u533a\u53bb\u505a\u2026\u2026\u201d\u8fd9\u4e5f\u662f\u7f57\u541b\u6b64\u65f6\u7684\u5c34\u5c2c\uff0c\u738b\u7ef4\u5747\u8d1f\u8d23\u9ad8\u65b0\u533a\u4e4b\u540e\uff0c\u4ed6\u5728\u5e02\u653f\u5e9c\u5c31\u627e\u4e0d\u5230\u4ee3\u8a00\u4eba\uff0c\u6709\u4e9b\u6d3b\u52a8\u603b\u4e0d\u80fd\u76f4\u63a5\u4ee5\u5e02\u59d4\u7684\u540d\u4e49\u53c2\u52a0\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u738b\u7ef4\u5747\u5728\u5e02\u59d4\u5927\u9662\u524d\u5c31\u4e0b\u4e86\u8f66\uff0c\u6ca1\u6709\u5fc5\u8981\u8fd9\u4e48\u665a\u8fd8\u53bb\u7f57\u541b\u4f4f\u6240\u53e8\u6270\uff0c\u5f20\u606a\u53c8\u8ddf\u738b\u7ef4\u5747\u8f6c\u8eab\u94bb\u8fdb\u540e\u9762\u8ddf\u7740\u7684\u5965\u8fea\u8f66\u91cc\uff0c\u59da\u6587\u76db\u4e5f\u5728\u8f66\u91cc\u2014\u2014\u5085\u4fca\u8fd8\u5e26\u7740\u53f8\u673a\u5f00\u7740\u5954\u9a70\u8ddf\u5728\u540e\u9762\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u5750\u8fdb\u8f66\u91cc\u5c31\u95ee\u59da\u6587\u76db\uff1a\u201c\u5c0f\u5973\u5b69\u53bb\u533b\u9662\u68c0\u67e5\u7684\u7ed3\u679c\u6709\u6ca1\u6709\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u521a\u624d\u5728\u8f66\u4e0a\uff0c\u7f57\u541b\u6ca1\u6709\u63d0\u5230\u6267\u6cd5\u961f\u76ee\u524d\u66b4\u9732\u51fa\u6765\u7684\u95ee\u9898\uff0c\u5f20\u606a\u8c28\u5b88\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u4fbf\u5bf9\u5e02\u653f\u6267\u6cd5\u961f\u63d0\u4ec0\u4e48\u6279\u8bc4\u610f\u89c1\u3002\u5f53\u524d\u7684\u6267\u653f\u8005\u6bd5\u7adf\u5bf9\u57ce\u5e02\u7ba1\u7406\u6709\u4e00\u79cd\u6025\u529f\u8fd1\u5229\u7684\u6001\u5ea6\uff0c\u7f57\u541b\u4e5f\u4e0d\u80fd\u514d\u4fd7\u3002\u738b\u7ef4\u5747\u5f88\u6e05\u695a\u4ed6\u4e0d\u80fd\u5728\u5f20\u606a\u9762\u524d\u56de\u907f\u8fd9\u4e2a\u8bdd\u9898\uff0c\u4e0d\u7136\u4ed6\u4eca\u665a\u8d76\u8fc7\u6765\u5c31\u6ca1\u6709\u610f\u4e49\u4e86\u3002</p><p>\u3000\u3000\u7f57\u541b\u6216\u8bb8\u5c06\u5efa\u90ba\u5f53\u6210\u4ed6\u653f\u6cbb\u751f\u6daf\u4e2d\u7684\u8df3\u677f\uff0c\u738b\u7ef4\u5747\u4e0d\u80fd\uff0c\u738b\u7ef4\u5747\u5fc5\u987b\u5c06\u6839\u90fd\u624e\u5728\u8fd9\u91cc\uff0c\u770b\u95ee\u9898\u5c31\u4e0d\u80fd\u6025\u529f\u8fd1\u5229\uff0c\u81f3\u5c11\u5728\u5f20\u606a\u9762\u524d\u4e0d\u80fd\u6025\u529f\u8fd1\u5229\u3002</p><p>\u3000\u3000\u4e0d\u8bf4\u80cc\u540e\u7684\u7ea0\u845b\uff0c\u4eca\u665a\u6d89\u53ca\u5230\u7684\u95ee\u9898\u5c31\u4ece\u8868\u9762\u770b\u4e5f\u5f88\u6709\u5178\u578b\u6027\uff0c\u5e02\u653f\u6267\u6cd5\u7684\u5c3a\u5ea6\u3001\u4e0b\u5c97\u5973\u5de5\u7684\u751f\u5b58\u3002</p><p>\u3000\u3000\u867d\u8bf4\u7f57\u541b\u8981\u8096\u660e\u5efa\u8d1f\u8d77\u8d23\u4efb\u6765\uff0c\u4f46\u662f\u9ad8\u65b0\u533a\u8981\u4e89\u53d6\u4e3b\u52a8\uff0c\u79ef\u6781\u5173\u6ce8\u662f\u80af\u5b9a\u8981\u7684\uff0c\u59da\u6587\u76db\u8bf4\u9053\uff1a\u201c\u5c0f\u5973\u5b69\u5b50\u6709\u8f7b\u5fae\u8111\u9707\u8361\uff0c\u8fd8\u8981\u4f4f\u9662\u89c2\u5bdf\u4e00\u591c\uff0c\u624d\u80fd\u653e\u5fc3\u786e\u8ba4\u4e0d\u4f1a\u7559\u4e0b\u4ec0\u4e48\u540e\u9057\u75c7\uff01\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u662f\u8d1f\u8d23\u4efb\u7684\u6001\u5ea6\uff0c\u5e02\u91cc\u4e0d\u4f1a\u4e0d\u7ba1\uff0c\u4e0d\u8fc7\u5176\u4ed6\u4e8b\u60c5\uff0c\u6211\u4eec\u7684\u5de5\u4f5c\u8981\u66f4\u79ef\u6781\u4e00\u4e9b\u2026\u2026\u201d\u738b\u7ef4\u5747\u770b\u7740\u5f20\u606a\u7ed9\u67d3\u4e86\u5496\u5561\u6e0d\u6ca1\u6709\u6362\u7684\u886c\u886b\uff0c\u201c\u4e0d\u7136\u5149\u8fd9\u886c\u886b\uff0c\u533a\u91cc\u5c31\u8d54\u4e0d\u8d77\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u53ef\u6ca1\u6562\u53eb\u533a\u91cc\u8d54\uff1f\u67d3\u4e0a\u56fe\u6848\u5012\u662f\u4e0d\u592a\u96be\u770b\uff0c\u201d\u5f20\u606a\u4f4e\u5934\u770b\u4e86\u770b\u886c\u886b\u4e0a\u7684\u5496\u5561\u6e0d\uff0c\u8ddf\u738b\u7ef4\u5747\u7b11\u7740\u8bf4\uff0c\u201c\u4e1c\u534e\u533a\u5927\u7247\u7684\u533a\u57df\u90fd\u5212\u5165\u9ad8\u65b0\u533a\u4e86\uff0c\u539f\u5148\u9ad8\u65b0\u533a\u6ca1\u6709\u7684\u4e0b\u5c97\u804c\u5de5\u95ee\u9898\uff0c\u73b0\u5728\u4e5f\u8ba9\u4f60\u5934\u75bc\u4e86\u5427\uff1f\u6700\u8fd1\u6211\u4e5f\u611f\u89c9\u5230\u8fd9\u4e2a\u95ee\u9898\u6709\u4e9b\u7a81\u51fa\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e8b\u60c5\u4e5f\u662f\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u4ed6\u4eec\u95f9\u51fa\u6765\u7684\uff0c\u201d\u738b\u7ef4\u5747\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u6025\u7740\u5728\u71d5\u5f52\u6e56\u5357\u5cb8\u4e0a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u4e3a\u4e86\u8d76\u901f\u5ea6\uff0c\u8ba9\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u76f4\u63a5\u5c06\u516b\u4e00\u5370\u67d3\u5382\u5168\u76d8\u63a5\u4e0b\u2026\u2026\u516b\u4e00\u5370\u67d3\u5382\u662f\u71d5\u5f52\u6e56\u7684\u4e00\u4e2a\u6c61\u67d3\u6e90\uff0c\u5173\u505c\u6216\u8fc1\u5730\u91cd\u5efa\u90fd\u662f\u597d\u4e8b\uff0c\u4f46\u662f\u592a\u6025\u8e81\u3002\u4eca\u5e74\u5e02\u91cc\u7684\u5370\u67d3\u884c\u4e1a\u6574\u4f53\u6ed1\u5761\u5f97\u5389\u5bb3\uff0c\u516b\u4e00\u5370\u67d3\u5382\u90a3\u4e48\u5927\u7684\u4e00\u5bb6\u5382\uff0c\u8fd1\u4e09\u5343\u53f7\u4eba\u4e00\u4e0b\u5b50\u90fd\u4e0b\u4e86\u5c97\uff0c\u800c\u4e14\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5c06\u5382\u5b50\u63a5\u8fc7\u6765\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4e3a\u8fd9\u4e09\u5343\u804c\u5de5\u638f\u5b89\u7f6e\u8d39\u3002\u8fd9\u4e8b\u6211\u8ddf\u8096\u660e\u5efa\u6709\u8fc7\u4e89\u6267\uff0c\u4ed6\u6253\u5305\u7968\u8bf4\u4e0d\u8981\u9ad8\u65b0\u533a\u89e3\u51b3\uff0c\u6211\u4e5f\u5c31\u6ca1\u6709\u518d\u575a\u6301\u3002\u4f60\u770b\u4ed6\u4eec\u89e3\u51b3\u6210\u4ec0\u4e48\u6837\u5b50\u2026\u2026\u201d\u738b\u7ef4\u5747\u5bf9\u8fd9\u4e8b\u4e5f\u662f\u6709\u4e9b\u6028\u8a00\u7684\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u4ec5\u6709\u6028\u8a00\uff0c\u201c\u6700\u540e\u8fd8\u662f\u533a\u91cc\u6324\u51fa\u4e00\u4e9b\u8d22\u653f\u8d44\u91d1\uff0c\u5c31\u7b97\u6bcf\u4e2a\u4e0b\u5c97\u804c\u5de5\u6bcf\u4e2a\u6708\u53d1\u4e00\u767e\u5143\u751f\u6d3b\u8d39\uff0c\u6bcf\u4e2a\u6708\u5c31\u8981\u989d\u5916\u591a\u82b1\u6389\u4e09\u5341\u4e07\uff0c\u5173\u952e\u8fd9\u4e00\u767e\u5143\u7684\u751f\u6d3b\u8d39\u5bf9\u8fd9\u4e9b\u4e0b\u5c97\u804c\u5de5\u7684\u751f\u6d3b\u56f0\u96be\u4e5f\u662f\u676f\u6c34\u8f66\u85aa\uff0c\u5173\u952e\u8fd8\u662f\u8981\u5c3d\u5feb\u5b89\u6392\u5c31\u4e1a\uff0c\u6211\u56de\u53bb\u770b\u533a\u91cc\u80fd\u4e0d\u80fd\u518d\u6324\u51fa\u4e9b\u94b1\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53bb\u5e74\u4ece\u6a61\u6811\u56ed\u8bb9\u6389\u4e00\u7b14\u94b1\u7684\u7530\u529b\u5c71\uff0c\u738b\u4e66\u8bb0\u8fd8\u6709\u5370\u8c61\u5427\uff1f\u4ed6\u4eca\u5e74\u8fd8\u662f\u5168\u56fd\u4eba\u5927\u4ee3\u8868\u5427\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u8981\u88c5\u7cca\u6d82\u4e86\uff0c\u8c01\u80fd\u4ece\u9526\u6e56\u8bb9\u5230\u94b1\uff1f\u201d\u738b\u7ef4\u5747\u8bf4\u9053\u3002\u9526\u6e56\u771f\u8981\u4ecb\u610f\u7530\u529b\u5c71\u8bb9\u94b1\u4e00\u4e8b\uff0c\u7530\u529b\u5c71\u4eca\u5e74\u54ea\u53ef\u80fd\u8fd8\u662f\u5168\u56fd\u4eba\u5927\u4ee3\u8868\uff1f\u4e0d\u5355\u738b\u7ef4\u5747\u4e0d\u7cca\u6d82\uff0c\u7f57\u541b\u7b49\u4eba\u90fd\u4e0d\u7cca\u6d82\uff0c\u6709\u4e9b\u620f\u662f\u505a\u7ed9\u4e0b\u9762\u4eba\u770b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u5c06\u90a3\u7b14\u8d44\u91d1\u7528\u6765\u641e\u4e00\u4e2a\u4e0b\u5c97\u804c\u5de5\u57f9\u8bad\u4e0e\u518d\u5c31\u4e1a\u7684\u6c11\u95f4\u670d\u52a1\u6240\uff0c\u533a\u91cc\u53ef\u4ee5\u627e\u7530\u529b\u5c71\u3002\u7530\u529b\u5c71\u8981\u662f\u518d\u8fc7\u6765\u8bb9\u9526\u6e56\u4e00\u7b14\u94b1\uff0c\u4e5f\u65e0\u6240\u8c13\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u7ef4\u5747\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1b\u201c\u8fd9\u6837\u4e5f\u597d\uff0c\u9526\u6e56\u73b0\u5728\u4e5f\u4e0d\u6c42\u540d\uff0c\u4e0d\u8fc7\u9526\u6e56\u505a\u7684\u4e8b\u60c5\uff0c\u4e0a\u9762\u4eba\u603b\u662f\u4e0d\u80fd\u95ed\u7740\u773c\u775b\u7684\uff0c\u201d\u5bf9\u59da\u6587\u76db\u8bf4\uff0c\u201c\u4f60\u5c06\u8fd9\u4e8b\u60c5\u8bb0\u5728\u5fc3\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u4e0b\u5c97\u804c\u5de5\u518d\u5c31\u4e1a\u8fd9\u4e8b\u4e0e\u59da\u6587\u76db\u76ee\u524d\u5206\u7ba1\u7684\u5de5\u4f5c\u6ca1\u6709\u7279\u522b\u7684\u5173\u8fde\uff0c\u4f46\u662f\u4e0e\u9526\u6e56\u6709\u5173\u7684\u4e8b\u60c5\uff0c\u738b\u7ef4\u5747\u8fd8\u662f\u5c3d\u53ef\u80fd\u5b89\u6392\u59da\u6587\u76db\u53bb\u505a\uff0c\u8ba9\u59da\u6587\u76db\u7d27\u5bc6\u7684\u5d4c\u5165\u9526\u6e56\u7684\u4eba\u8109\u7f51\u7edc\u91cc\uff0c\u8fdb\u800c\u4f7f\u5f97\u59da\u6587\u76db\u6574\u4e2a\u987e\u5bb6\u90fd\u6210\u4e3a\u652f\u6491\u9526\u6e56\u7684\u5e55\u540e\u652f\u67f1\u4e4b\u4e00\uff0c\u8fd9\u4e5f\u6709\u5229\u738b\u7ef4\u5747\u81ea\u8eab\u7684\u53d1\u5c55\u2014\u2014\u738b\u7ef4\u5747\u5728\u4ed5\u9014\u4e0a\u8fd8\u6ca1\u6709\u80fd\u529b\u53bb\u83b7\u5f97\u987e\u5bb6\u7684\u5168\u529b\u652f\u6301\uff0c\u6240\u6709\u4eba\u4e8b\u7f51\u7edc\u90fd\u9700\u8981\u7d27\u5bc6\u7684\u56f4\u7ed5\u9526\u6e56\u8fd9\u6839\u4e3b\u7ebf\u53bb\u5c55\u5f00\u3002</p><p>\u3000\u3000\u8f66\u5b50\u518d\u7ecf\u8fc7\u5b66\u5e9c\u5df7\uff0c\u5f20\u606a\u4e0e\u59da\u6587\u76db\u4e00\u8d77\u4e0b\u4e86\u8f66\uff0c\u738b\u7ef4\u5747\u8981\u76f4\u63a5\u56de\u4f4f\u6240\uff0c\u59da\u6587\u76db\u4e5f\u4e0d\u7528\u9001\u738b\u7ef4\u5747\u56de\u4f4f\u5904\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u59da\u6587\u76db\u5e76\u80a9\u5f80\u5df7\u5b50\u91cc\u8d70\u53bb\uff0c\u59da\u6587\u76db\u5012\u6709\u4e9b\u66ff\u5f20\u606a\u6253\u62b1\u4e0d\u5e73\uff1a\u201c\u8096\u660e\u5efa\u4ed6\u4eec\u6ca1\u6709\u64e6\u5e72\u51c0\u7684\u5c41\u80a1\uff0c\u8fd8\u8981\u9526\u6e56\u6765\u5e2e\u5fd9\uff0c\u5509\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u613f\u610f\u62ff\u8fd9\u7b14\u94b1\u51fa\u6765\uff0c\u8ddf\u4ed6\u4eec\u6ca1\u6709\u5173\u7cfb\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u201c\u73b0\u5728\u5404\u5730\u7684\u57ce\u5e02\u7ba1\u7406\u601d\u8def\u6709\u4e9b\u7c97\u7cd9\uff0c\u5f88\u5c11\u6709\u4eba\u613f\u610f\u53bb\u60f3\u80cc\u540e\u6df1\u5c42\u6b21\u7684\u539f\u56e0\uff0c\u4e0d\u662f\u4ed6\u4eec\u60f3\u4e0d\u5230\uff0c\u800c\u662f\u80cc\u540e\u6df1\u5c42\u6b21\u7684\u539f\u56e0\u4f1a\u89e6\u53ca\u5230\u4ed6\u4eec\u7684\u5229\u76ca\u2014\u2014\u4ed6\u4eec\u5b81\u53ef\u7b80\u5355\u7684\u5c06\u95ee\u9898\u5f52\u7ed3\u5230\u5e02\u653f\u6267\u6cd5\u961f\u4e0e\u65e0\u8bc1\u644a\u8d29\u4e4b\u95f4\u7684\u8868\u9762\u77db\u76fe\u4e0a\u3002\u65e0\u8bc1\u644a\u8d29\u5360\u9053\u7ecf\u8425\uff0c\u4e0d\u542c\u529d\u963b\u3001\u5f71\u54cd\u4ea4\u901a\u3001\u7834\u574f\u5e02\u5bb9\u5e02\u8c8c\uff0c\u5e02\u5bb9\u6267\u6cd5\u961f\u7ba1\u7406\u65f6\u5373\u4f7f\u7c97\u66b4\u4e00\u4e9b\u4e5f\u60c5\u53ef\u6709\u539f\u3002\u8fd9\u7b97\u54ea\u95e8\u5b50\u6df7\u5e10\u9053\u7406\uff1f\u8089\u98df\u8005\u9119\u4e5f\uff01\u9526\u6e56\u4e5f\u505a\u4e0d\u4e86\u592a\u591a\u7684\u4e8b\u60c5\uff0c\u80fd\u505a\u4e00\u4e9b\u662f\u4e00\u4e9b\uff0c\u603b\u5f52\u8fd8\u662f\u8981\u5e02\u91cc\u4ee5\u540e\u505a\u5de5\u4f5c\u80fd\u79ef\u6781\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u6709\u4e9b\u5de5\u4f5c\u662f\u8981\u5e02\u653f\u5e9c\u76f4\u63a5\u51fa\u9762\u7684\uff0c\u7f57\u541b\u4e66\u8bb0\u4eca\u5929\u665a\u4e0a\u5fc3\u91cc\u53ea\u6015\u4e5f\u4e0d\u75db\u5feb\uff0c\u5c31\u7b97\u4e0a\u9762\u5c06\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u8fd9\u4e9b\u9489\u5b50\u90fd\u6316\u6389\uff0c\u6309\u7167\u4f20\u7edf\uff0c\u65b0\u8fc7\u6765\u7684\u5e02\u957f\u8ddf\u7f57\u541b\u4e66\u8bb0\u4e00\u56e2\u548c\u6c14\u7684\u53ef\u80fd\u6027\u4e5f\u4e0d\u5927\uff0c\u5de5\u4f5c\u4e5f\u4e0d\u89c1\u5f97\u5c31\u4f1a\u597d\u505a\u2026\u2026\u201d\u59da\u6587\u76db\u5bf9\u56fd\u5185\u7684\u73b0\u5b9e\u5012\u6709\u6e05\u9192\u7684\u8ba4\u8bc6\uff0c\u5bf9\u672a\u6765\u7684\u671f\u5f85\u4e5f\u6bd4\u5f20\u606a\u5c11\u4e9b\u7406\u60f3\u4e3b\u4e49\u3002</p><p>\u3000\u3000\u201c\u4e0e\u4eba\u6597\uff0c\u5176\u4e50\u65e0\u7a77\u2014\u2014\u4e2d\u56fd\u5c31\u662f\u8fd9\u4f20\u7edf\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u9aa1\u9a6c\u8d5b\u8dd1\uff0c\u770b\u8c01\u8dd1\u5f97\u8fc7\u8c01\uff0c\u9526\u6e56\u4e5f\u8981\u52a0\u4e00\u97ad\u63d0\u63d0\u901f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u9526\u6e56\u6700\u8fd1\u8fd8\u6709\u4ec0\u4e48\u66f4\u5927\u7684\u8ba1\u5212\u4e0d\u6210\uff1f\u201d\u59da\u6587\u76db\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u597d\u5947\u7684\u95ee\u9053\u3002</p></article>"}}}]);
