(window.webpackJsonp=window.webpackJsonp||[]).push([[840],{863:function(p){p.exports={chapterTitle:null,title:"\u7b2c667\u7ae0 \u597d\u4eba\u505a\u4e0d\u5f97",content:"<article><h2>\u7b2c667\u7ae0 \u597d\u4eba\u505a\u4e0d\u5f97</h2><p>\u3000\u3000\u7b2c\u4e8c\u5929\u65e9\u6668\uff0c\u7530\u529b\u5c71\u8d77\u65e9\u5230\u4e1c\u5927\u7684\u7537\u751f\u5bbf\u820d\u6536\u8863\u670d\uff0c\u5728\u7814\u7a76\u751f\u516c\u5bd3\u6ca1\u6709\u9047\u5230\u5f20\u606a\uff0c\u53ea\u77e5\u9053\u4ed6\u4f4f\u8fd9\u680b\u697c\u5c31\u662f\uff0c\u6536\u8fc7\u4e00\u8f6e\u8863\u670d\u9001\u56de\u6d17\u8863\u5e97\u91cc\uff0c\u770b\u5230\u539f\u5148\u5370\u67d3\u5382\u7684\u6253\u5b57\u5458\u5f20\u6587\u4e3d\u3001\u4e00\u4e2a\u6f02\u6f02\u4eae\u4eae\u7684\u5973\u5b69\u5b50\u5728\u6d17\u8863\u5e97\u91cc\u5e2e\u5fd9\u71a8\u8863\u670d\uff0c\u76b1\u7740\u7709\u5934\u95ee\u5979\uff1a\u201c\u4f60\u4e0d\u7528\u4e0a\u73ed\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u60f3\u53bb\u2026\u2026\u6628\u5929\u665a\u4e0a\u786c\u7ed9\u90a3\u4e2a\u732a\u5934\u5f20\u7ecf\u7406\u786c\u7ed9\u62c9\u53bb\u966a\u5ba2\u6237\u5403\u996d\uff0c\u996d\u684c\u4e0a\u90a3\u4e2a\u732a\u5934\u8fd8\u95ee\u6211\u2018\u665a\u4e0a\u7ee7\u7eed\u52a0\u73ed\u5bb6\u91cc\u4f1a\u4e0d\u4f1a\u653e\u5fc3\u2019\u4e4b\u7c7b\u7684\u8bdd\uff0c\u6211\u4e00\u6fc0\u52a8\u5c31\u5c06\u4e00\u7897\u7ea2\u70e7\u8089\u6cfc\u4ed6\u8138\u4e0a\u4e86\uff0c\u4eca\u5929\u8fd8\u600e\u4e48\u53bb\u4e0a\u73ed\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8981\u51fa\u53bb\uff0c\u4f60\u966a\u6211\u8d70\u4e00\u8d9f\u3002\u201d\u4e16\u9053\u5982\u6b64\uff0c\u7530\u529b\u5c71\u5482\u5482\u5634\u4e5f\u4e0d\u8bf4\u591a\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u8981\u8fd8\u662f\u5728\u732a\u5934\u5f20\u90a3\u6837\u7684\u5bb6\u4f19\u624b\u4e0b\u505a\u4e8b\uff0c\u6211\u53ef\u4e0d\u5e72\uff1b\u6211\u5c31\u5e2e\u5a1f\u5b50\u59d0\u5728\u5e97\u91cc\u71a8\u8863\u670d\u5f97\u4e86\uff0c\u53ea\u8981\u7ba1\u6211\u6709\u7897\u996d\u5403\uff1b\u8fc7\u4e00\u4e24\u5e74\u5c31\u628a\u81ea\u5df1\u5ac1\u51fa\u53bb\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7530\u529b\u5c71\u53ea\u5f53\u5f20\u606a\u4e0e\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u8ba4\u8bc6\uff0c\u5c06\u6750\u6599\u4ea4\u7ed9\u5f20\u606a\uff0c\u4e5f\u662f\u5e0c\u671b\u4ed6\u80fd\u5c06\u6750\u6599\u4ea4\u5230\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u624b\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u6628\u5929\u7684\u8bdd\uff0c\u4ed6\u8bb0\u5728\u5fc3\u91cc\uff0c\u6a61\u6811\u56ed\u90a3\u91cc\u603b\u662f\u8981\u8dd1\u4e00\u8dd1\uff0c\u4e0d\u8dd1\u53ef\u4e0d\u662f\u4e00\u70b9\u6307\u671b\u90fd\u6ca1\u6709\uff1f</p><p>\u3000\u3000\u7530\u529b\u5c71\u4e0e\u539f\u4e3d\u4e30\u5370\u67d3\u5382\u7684\u9752\u5e74\u5973\u5de5\u5f20\u6587\u4e3d\u8d76\u5230\u6a61\u6811\u56ed\u8bbe\u5728\u4e3d\u4e30\u56ed\u533a\u7684\u7b79\u5907\u5904\uff0c\u5e0c\u671b\u6a61\u6811\u56ed\u80fd\u4e3a\u4e3d\u4e30\u5370\u67d3\u5382\u7684\u4e0b\u5c97\u804c\u5de5\u6324\u51fa\u51e0\u4e2a\u5de5\u4f5c\u5c97\u4f4d\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u8fd9\u8fb9\uff0c\u6bd4\u4e00\u822c\u5de5\u5382\u8981\u665a\u534a\u4e2a\u70b9\u624d\u5230\u5de5\u4f5c\u65f6\u95f4\uff0c\u7530\u529b\u4ed6\u4eec\u8d76\u5230\u65f6\u8fd9\u91cc\u624d\u9646\u7eed\u6709\u804c\u5458\u51fa\u5165\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u7b79\u5907\u5904\u7ecf\u7406\u8d75\u9e4f\u8ba4\u8bc6\u7530\u529b\u5c71\uff0c\u5f53\u521d\u4ed6\u4e0e\u738b\u7ef4\u5747\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u60f3\u8fdb\u4e3d\u4e30\u5370\u67d3\u5382\u53c2\u89c2\uff0c\u5c31\u662f\u7ed9\u7530\u529b\u5c71\u9886\u4eba\u5835\u5728\u95e8\u53e3\uff0c\u626d\u6421\u4e2d\uff0c\u5bb3\u4ed6\u886c\u886b\u7684\u94ae\u6263\u90fd\u6389\u4e86\u4e24\u7c92\u3002\u4ed6\u6628\u5929\u591c\u91cc\u7ed9\u5f20\u606a\u627e\u8fc7\u6765\u8bf4\u8d77\u4e3d\u4e30\u5370\u67d3\u5382\u4e0b\u5c97\u804c\u5de5\u7684\u4e8b\u60c5\uff0c\u770b\u5230\u7530\u529b\u5c71\uff0c\u5f53\u7136\u4e0d\u6562\u9a6c\u864e\uff0c\u5fd9\u9080\u8bf7\u7530\u529b\u5c71\u5750\u4ed6\u7684\u8f66\u8fdb\u56ed\u533a\uff0c\u6ca1\u60f3\u5230\u65c1\u8fb9\u90a3\u4e2a\u6f02\u6f02\u4eae\u4eae\u7684\u5973\u5b69\u5b50\u8ddf\u7530\u529b\u5c71\u662f\u4e00\u8d77\u7684\uff0c\u6628\u591c\u91cc\u5012\u6ca1\u6709\u542c\u5f20\u606a\u63d0\u8d77\u8fc7\u3002</p><p>\u3000\u3000\u867d\u7136\u6628\u5929\u5c31\u6709\u4e9b\u671f\u5f85\uff0c\u7530\u529b\u5c71\u5374\u662f\u8fd9\u65f6\u5019\u624d\u8c41\u7136\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u60f3\uff1a\u603b\u7b97\u4e5f\u6709\u778e\u732b\u78b0\u5230\u6b7b\u8017\u5b50\u7684\u65f6\u5019\u3002</p><p>\u3000\u3000\u201c\u7530\u5382\u957f\uff0c\u8fd9\u90fd\u662f\u6211\u4eec\u5e73\u65f6\u5de5\u4f5c\u6ca1\u6709\u505a\u5230\u4f4d\uff0c\u8fd8\u8981\u52b3\u4f60\u4eb2\u81ea\u8dd1\u4e00\u8d9f\uff0c\u201d\u8981\u4e0d\u662f\u7530\u529b\u5c71\u4e00\u5927\u65e9\u5c31\u5728\u56ed\u533a\u95e8\u53e3\u5019\u7740\u4ed6\uff0c\u8d75\u9e4f\u90fd\u6253\u7b97\u4e0a\u5348\u5230\u5317\u95e8\u6d17\u8863\u623f\u8d70\u4e00\u8d9f\uff0c\u6bd5\u7adf\u662f\u5f20\u606a\u4eb2\u81ea\u4ea4\u5f85\u7684\u4e8b\u60c5\uff1b\u4e00\u5230\u529e\u516c\u5ba4\uff0c\u5c31\u76f4\u5954\u4e3b\u9898\uff0c\u4e5f\u4e0d\u803d\u6401\u5f7c\u6b64\u7684\u65f6\u95f4\uff0c\u201c\u6a61\u6811\u56ed\u6b63\u6253\u7b97\u6210\u7acb\u7269\u7ba1\u4e2d\u5fc3\u3002\u6a61\u6811\u56ed\u7684\u60c5\u51b5\uff0c\u7530\u5382\u957f\u60a8\u591a\u5c11\u4e5f\u6709\u4e9b\u6e05\u695a\uff0c\u4e24\u4e2a\u521b\u4e1a\u56ed\u533a\u3001\u6838\u5fc3\u7814\u53d1\u533a\u4ee5\u53ca\u9752\u5e74\u516c\u5bd3\u7684\u540e\u52e4\u7269\u4e1a\u7ba1\u7406\uff0c\u4e8b\u60c5\u53c8\u591a\u53c8\u6742\u3002\u6211\u6b63\u6101\u4e0d\u77e5\u9053\u600e\u4e48\u89e3\u51b3\u8fd9\u4e2a\u5934\u75bc\u7684\u95ee\u9898\uff0c\u7530\u5382\u957f\u60a8\u7b97\u662f\u7ed9\u6211\u4eec\u96ea\u4e2d\u9001\u70ad\u6765\u4e86\u3002\u6211\u8fd9\u8fb9\u6709\u4efd\u5de5\u4f5c\u5c97\u4f4d\u7684\u8868\u683c\uff0c\u7530\u5382\u957f\uff0c\u60a8\u5148\u5e2e\u6211\u770b\u770b\uff0c\u9002\u5408\u63a8\u8350\u4ec0\u4e48\u4eba\u6765\uff0c\u4f60\u5c3d\u7ba1\u63a8\u8350\u2026\u2026\u85aa\u6c34\u53ef\u80fd\u4e0d\u4f1a\u592a\u9ad8\u3002\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u63a5\u8fc7\u8868\u683c\uff0c\u6709\u8fd1\u56db\u5341\u4e2a\u5de5\u4f5c\u5c97\u4f4d\uff0c\u6c34\u7535\u4fee\u7ef4\u4e5f\u8981\u7b97\u6280\u672f\u5de5\u79cd\uff0c\u5927\u591a\u6570\u662f\u56ed\u827a\u7ef4\u62a4\u3001\u6e05\u6d01\u536b\u751f\u3001\u5b89\u4fdd\u4e4b\u7c7b\u7684\u666e\u901a\u5de5\u52e4\u5c97\u4f4d\u3002\u4e5f\u4e0d\u80fd\u8981\u6c42\u592a\u9ad8\uff0c\u8fd9\u5e74\u5934\uff0c\u6709\u4efd\u5de5\u4f5c\u4e0d\u5bb9\u6613\uff0c\u7279\u522b\u662f\u90a3\u4e48\u5e74\u8fc7\u56db\u5341\u5c81\u7684\u4e2d\u5e74\u804c\u5de5\uff0c\u518d\u627e\u4e00\u4efd\u5de5\u4f5c\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u7684\u3002</p><p>\u3000\u3000\u7ffb\u5230\u540e\u9762\u6709\u85aa\u8d44\u7684\u8be6\u7ec6\u4ecb\u7ecd\uff0c\u55ec\uff0c\u8d75\u9e4f\u5634\u91cc\u8bf4\u85aa\u6c34\u4e0d\u9ad8\uff0c\u5c06\u57fa\u672c\u5de5\u8d44\u3001\u623f\u8d34\u3001\u8f66\u8d34\u3001\u4f19\u98df\u8865\u8d34\u52a0\u4e00\u5757\uff0c\u4e00\u540d\u666e\u901a\u5de5\u52e4\u4eba\u5458\u7684\u6708\u6536\u5165\u6bd4\u4e4b\u524d\u5370\u67d3\u5382\u7684\u719f\u7ec3\u5de5\u8fd8\u8981\u9ad8\u51fa\u4e00\u622a\u3002</p><p>\u3000\u3000\u5f20\u6587\u4e3d\u51d1\u8fc7\u5934\u6765\u770b\u7740\uff0c\u5de5\u8d44\u5f85\u9047\u867d\u8bf4\u4e0d\u9519\uff0c\u4f46\u662f\u8fd9\u4e9b\u6e05\u6d01\u536b\u751f\u4e4b\u7c7b\u7684\u5de5\u52e4\u5c97\u4f4d\uff0c\u5bf9\u5979\u8fd9\u6837\u624d\u4e8c\u5341\u4e09\u56db\u5c81\u7684\u5973\u5b69\u5b50\u6765\u8bf4\uff0c\u672a\u514d\u6709\u4e9b\u4e0d\u9002\u5408\uff0c\u5fc3\u91cc\u6323\u624e\u7740\u72b9\u8c6b\u7740\uff0c\u4e0d\u6653\u5f97\u600e\u4e48\u529e\u624d\u597d\u3002\u5012\u662f\u660e\u767d\u5979\u5fc3\u610f\u4f3c\u7684\uff0c\u8d75\u9e4f\u53c8\u4ece\u62bd\u5c49\u91cc\u62ff\u51fa\u51e0\u4efd\u8868\u683c\u9012\u7ed9\u7530\u529b\u5c71\uff0c\u8bf4\u9053\uff1a\u201c\u8fd8\u6709\u4e00\u4e9b\u5de5\u4f5c\u5c97\u4f4d\uff0c\u8981\u6c42\u53ef\u80fd\u7a0d\u9ad8\u4e00\u4e0b\uff0c\u5bf9\u539f\u4e3d\u4e30\u5382\u7684\u804c\u5de5\uff0c\u6211\u4eec\u53ef\u4ee5\u653e\u5bbd\u4e00\u4e0b\u8981\u6c42\u3002\u5185\u52e4\u79d8\u4e66\u3001\u6587\u5b57\u79d8\u4e66\u4e4b\u7c7b\u5c97\u4f4d\uff0c\u9ad8\u4e2d\u3001\u4e2d\u4e13\u6bd5\u4e1a\u7684\u5973\u5b69\u5b50\uff0c\u4e5f\u53ef\u4ee5\u8003\u8651\uff0c\u82f1\u8bed\u57fa\u7840\u65b9\u9762\uff0c\u9ad8\u4e2d\u3001\u4e2d\u4e13\u6bd5\u4e1a\u7684\u4eba\u603b\u4f1a\u6709\u4e9b\u57fa\u7840\uff0c\u8fdb\u6765\u4e4b\u540e\u518d\u8003\u8651\u52a0\u5f3a\u57f9\u8bad\u4e00\u4e0b\u2026\u2026\u6211\u628a\u6a61\u6811\u56ed\u8fd8\u7a7a\u7f3a\u7684\u804c\u4f4d\u4ee5\u53ca\u8fd1\u671f\u8981\u62db\u8058\u7684\u804c\u4f4d\uff0c\u90fd\u8ba9\u7530\u5382\u957f\u60a8\u770b\u4e00\u4e0b\uff0c\u603b\u4e4b\uff0c\u8fd9\u4e9b\u804c\u4f4d\u90fd\u4f1a\u4f18\u5148\u5f55\u53d6\u539f\u4e3d\u4e30\u5382\u7684\u804c\u5de5\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u5012\u6ca1\u6709\u60f3\u5230\u80fd\u4e00\u4e0b\u5b50\u5b89\u6392\u8fd9\u4e48\u591a\u4eba\u7684\u5de5\u4f5c\uff0c\u6fc0\u52a8\u5f97\u90fd\u6709\u4e9b\u4e0d\u6653\u5f97\u8bf4\u4ec0\u4e48\u597d\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8fd8\u6709\u4e00\u4e2a\u8bf7\u6c42\uff0c\u8fd8\u60f3\u8bf7\u7530\u5382\u957f\u52c9\u4e3a\u5176\u96be\u2026\u2026\u201d\u8d75\u9e4f\u6309\u7740\u5f20\u606a\u7684\u5429\u5490\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6a61\u6811\u56ed\u60f3\u8058\u8bf7\u7530\u5382\u957f\u62c5\u4efb\u7269\u7ba1\u4e2d\u5fc3\u7684\u526f\u7ecf\u7406\uff0c\u7269\u7ba1\u4e2d\u5fc3\u6682\u65f6\u4e0d\u4f1a\u6709\u7ecf\u7406\uff0c\u6240\u4ee5\u662f\u60f3\u8bf7\u7530\u5382\u957f\u8d1f\u8d23\u6a61\u6811\u56ed\u7269\u7ba1\u4e2d\u5fc3\u7684\u5de5\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u7530\u529b\u5c71\u53d1\u4e86\u4e00\u4f1a\u6123\uff0c\u8fdf\u7591\u7684\u8bf4\u9053\uff0c\u201c\u6211\u80fd\u4e0d\u80fd\u63a8\u8350\u66f4\u5408\u9002\u7684\u4eba\u9009\uff1f\u5f53\u7136\uff0c\u8fd9\u79cd\u9ad8\u7ea7\u804c\u52a1\uff0c\u4e0d\u8be5\u6211\u6765\u80e1\u8bf4\u516b\u9053\u3002\u6a61\u6811\u56ed\u5e2e\u6211\u4eec\u8fd9\u4e48\u5927\u7684\u5fd9\uff0c\u4f46\u662f\u4e3d\u4e30\u5382\u8fd8\u6709\u8fd1\u767e\u53f7\u4eba\u7684\u5de5\u4f5c\u6ca1\u6709\u7740\u843d\uff0c\u53e6\u5916\uff0c\u6211\u8fd8\u6709\u4e9b\u522b\u7684\u4e8b\u60c5\u8981\u505a\uff0c\u627f\u8499\u6a61\u6811\u56ed\u770b\u5f97\u8d77\u6211\uff0c\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u8d75\u9e4f\u542c\u7530\u529b\u5c71\u8fd9\u4e48\u8bf4\uff0c\u5e94\u4e86\u4e00\u58f0\uff0c\u4ece\u62bd\u5c49\u91cc\u62ff\u51fa\u4e00\u5f20\u8868\u683c\uff0c\u4ea4\u7ed9\u968f\u7530\u529b\u5c71\u8fc7\u6765\u7684\u5f20\u6587\u4e3d\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u662f\u4e00\u4efd\u5de5\u4f5c\u63a8\u8350\u8868\uff0c\u9ebb\u70e6\u5f20\u5c0f\u59d0\u5e2e\u6211\u53bb\u6587\u5370\u5ba4\u590d\u5370\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u6587\u4e3d\u6123\u4e86\u6123\uff0c\u641e\u4e0d\u6e05\u8d75\u9e4f\u4e3a\u4ec0\u4e48\u6307\u4f7f\u5979\u53bb\u5e72\u6d3b\uff0c\u4e5f\u4e0d\u597d\u8bf4\u4ec0\u4e48\uff0c\u63a5\u8fc7\u8868\u683c\u51fa\u529e\u516c\u5ba4\u627e\u6587\u5370\u5ba4\uff0c\u8fd9\u65f6\u5019\u5012\u6709\u95f2\u60c5\u597d\u597d\u7684\u6b23\u8d4f\u6a61\u6811\u56ed\u7684\u529e\u516c\u73af\u5883\uff0c\u89c9\u5f97\u8fd9\u624d\u6709\u5927\u4f01\u4e1a\u7684\u73b0\u4ee3\u6c14\u6d3e\uff0c\u5b8c\u5168\u90fd\u770b\u4e0d\u51fa\u539f\u5148\u5370\u67d3\u5382\u7834\u843d\u7684\u75d5\u8ff9\uff0c\u5c31\u7279\u522b\u5411\u5f80\u80fd\u8fdb\u6765\u5de5\u4f5c\uff0c\u4f3c\u4e4e\u6587\u5b57\u79d8\u4e66\u8fd8\u53ef\u4ee5\u80dc\u4efb\u7684\u3002</p><p>\u3000\u3000\u5c06\u5f20\u6587\u4e3d\u652f\u4f7f\u51fa\u529e\u516c\u5ba4\uff0c\u8d75\u9e4f\u5c06\u7530\u529b\u5c71\u4ea4\u7ed9\u5f20\u606a\u53c8\u4ece\u5f20\u606a\u624b\u91cc\u8f6c\u4ea4\u5230\u4ed6\u624b\u91cc\u7684\u6750\u6599\u62ff\u51fa\u6765\uff0c\u5bf9\u7530\u529b\u5c71\u8bf4\uff1a\u201c\u7530\u5382\u957f\u8bf4\u6709\u4e9b\u522b\u7684\u4e8b\u60c5\uff0c\u662f\u6307\u8fd9\u4e9b\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u770b\u5230\u6750\u6599\u5728\u8d75\u9e4f\u624b\u91cc\uff0c\u624d\u786e\u5b9a\u5f20\u606a\u539f\u6765\u4e0e\u6a61\u6811\u56ed\u8fd9\u8fb9\u7684\u5173\u7cfb\u66f4\u5bc6\u5207\u4e9b\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6bd5\u7adf\u662f\u4ece\u5e02\u7eba\u7ec7\u603b\u516c\u53f8\u529e\u4e86\u9000\u517b\u624b\u7eed\u4e0b\u6765\u7684\uff0c\u6709\u4f59\u529b\u505a\u4e9b\u4e8b\u60c5\uff0c\u867d\u7136\u77e5\u9053\u6709\u4e9b\u4e8b\u60c5\u5f88\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7530\u5382\u957f\u6253\u7b97\u4e3a\u5168\u5e02\u7684\u4e0b\u5c97\u804c\u5de5\u7b79\u5907\u4e00\u4e2a\u4fc3\u8fdb\u518d\u5c31\u4e1a\u7684\u670d\u52a1\u6240\uff0c\u6211\u4eec\u90fd\u5f88\u656c\u4f69\u7530\u5382\u957f\u60a8\uff0c\u201d\u8981\u662f\u7530\u529b\u5c71\u7b54\u5e94\u62c5\u4efb\u6a61\u6811\u56ed\u7269\u7ba1\u4e2d\u5fc3\u7684\u526f\u7ecf\u7406\uff0c\u6309\u7167\u5f20\u606a\u7684\u5429\u5490\uff0c\u8d75\u9e4f\u4e0d\u4f1a\u62ff\u51fa\u8fd9\u4efd\u6750\u6599\u51fa\u6765\uff0c\u53ea\u4f1a\u5c3d\u53ef\u80fd\u7684\u5728\u6a61\u6811\u56ed\u7684\u8303\u56f4\u4e4b\u5185\u4e3a\u4e3d\u4e30\u5382\u7684\u4e0b\u5c97\u804c\u5de5\u5b89\u6392\u4e00\u4e9b\u5de5\u4f5c\u673a\u4f1a\u3002\u4e0d\u80fd\u5bf9\u4eba\u6709\u8fc7\u4efd\u7684\u82db\u6c42\uff0c\u7530\u529b\u5c71\u80fd\u505a\u5230\u4eca\u5929\u8fd9\u4e00\u6b65\u5c31\u5f88\u4e86\u4e0d\u8d77\u4e86\uff0c\u4f46\u662f\u7530\u529b\u5c71\u8fd8\u80fd\u575a\u6301\u8d70\u4e0b\u53bb\uff0c\u5f20\u606a\u5f53\u7136\u4e5f\u4e0d\u4f1a\u8896\u624b\u65c1\u89c2\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u8d75\u9e4f\u8981\u5c06\u5f20\u6587\u4e3d\u652f\u8d70\u624d\u80fd\u8ddf\u7530\u529b\u5c71\u8bf4\uff0c\u201c\u529e\u8fd9\u4e2a\u670d\u52a1\u6240\u7684\u7ecf\u8d39\uff0c\u6211\u4eec\u6a61\u6811\u56ed\u53ef\u4ee5\u5168\u989d\u8d5e\u52a9\uff0c\u4e0d\u8fc7\u6709\u4e24\u4e2a\u5c0f\u5c0f\u7684\u8981\u6c42\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u8981\u6c42\uff1f\u201d\u7530\u529b\u5c71\u771f\u662f\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u80fd\u4e00\u4e0b\u5b50\u83b7\u5f97\u8fd9\u4e48\u591a\uff0c\u4ed6\u8fd8\u6253\u7b97\u5148\u770b\u7740\u4e3d\u4e30\u5382\u7684\u4e0b\u5c97\u804c\u5de5\u90fd\u6709\u4e86\u7740\u843d\u4e4b\u540e\uff0c\u5c31\u626f\u5f00\u8138\u76ae\u8ddf\u5e02\u91cc\u8c08\u518d\u5c31\u4e1a\u4fc3\u8fdb\u670d\u52a1\u6240\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u4fc3\u8fdb\u518d\u5c31\u4e1a\u7684\u670d\u52a1\u6240\uff0c\u603b\u5f52\u662f\u8981\u6302\u5e02\u67d0\u4e2a\u90e8\u95e8\u540d\u4e0b\uff0c\u65e0\u8bba\u662f\u5e02\u603b\u5de5\u4f1a\u4e5f\u597d\u3001\u5e02\u52b3\u52a8\u5c40\u4e5f\u597d\uff0c\u8fd9\u4efd\u6750\u6599\uff0c\u5e0c\u671b\u7530\u5382\u957f\u7a0d\u7a0d\u4fee\u6539\u4e00\u4e0b\uff0c\u6709\u4e9b\u6570\u636e\uff0c\u522b\u4eba\u9700\u8981\u63a9\u8033\u76d7\u94c3\uff0c\u5c31\u8ba9\u4ed6\u4eec\u63a9\u8033\u76d7\u94c3\u597d\u4e86\uff0c\u73b0\u5728\u56fd\u5bb6\u90fd\u6ca1\u6709\u7ed9\u4e0b\u5c97\u8fd9\u4e2a\u95ee\u9898\u5b9a\u8c03\uff0c\u5c31\u6015\u6709\u4e9b\u653f\u5e9c\u5b98\u5458\u4e0d\u5927\u5bb9\u6613\u63a5\u53d7\u3002\u4e0d\u5360\u7528\u4ed6\u4eec\u7684\u7ecf\u8d39\uff0c\u8fd8\u80fd\u505a\u4e9b\u5b9e\u9645\u7684\u5de5\u4f5c\uff0c\u603b\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u963b\u529b\u3002\u9664\u4e86\u8fd9\u4e2a\u4e4b\u5916\uff0c\u8fd8\u6709\u4e00\u4e2a\u8981\u6c42\uff0c\u5c31\u662f\u7530\u5382\u957f\u8ddf\u5e02\u91cc\u6709\u5173\u90e8\u95e8\u8c08\u7684\u65f6\u5019\uff0c\u53ef\u4e0d\u8981\u8bf4\u662f\u6a61\u6811\u56ed\u4e3b\u52a8\u63d0\u4f9b\u7ecf\u8d39\u3002\u8981\u4e0d\u8fd9\u6837\uff0c\u8fd9\u51e0\u5929\uff0c\u7530\u5382\u957f\u5e26\u7740\u5341\u51e0\u4e8c\u5341\u540d\u5de5\u4eba\u5230\u6211\u8fd9\u91cc\u6765\u505a\u505a\u5ba2\uff1f\u8fd8\u662f\u8981\u7530\u5382\u957f\u60a8\u505a\u56de\u6076\u4eba\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u7530\u529b\u5c71\u4e0d\u89e3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u7530\u5382\u957f\u5c06\u8fd9\u4efd\u6750\u6599\u4ea4\u7ed9\u8fc7\u8c01\uff0c\u5c31\u662f\u8c01\u8fd9\u4e48\u5429\u5490\u6211\u7684\uff0c\u6211\u4e5f\u5927\u4e0d\u6e05\u695a\u4ed6\u5fc3\u91cc\u662f\u600e\u4e48\u60f3\u7684\uff0c\u603b\u4e4b\u8981\u8ba9\u6a61\u6811\u56ed\u770b\u8d77\u6765\u6bd4\u8f83\u4e3a\u96be\u5c31\u662f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u60f3\u8d77\u5f20\u606a\u6628\u5929\u8981\u4ed6\u4eca\u5929\u9886\u4e9b\u5de5\u4eba\u6765\u5835\u6a61\u6811\u56ed\u7684\u5927\u95e8\uff0c\u8fd8\u53ea\u5f53\u4ed6\u662f\u5f00\u73a9\u7b11\uff0c\u6ca1\u60f3\u5230\u8d75\u9e4f\u4eca\u5929\u771f\u4e00\u672c\u6b63\u7ecf\u7684\u63d0\u8d77\u8fd9\u4e2a\u8981\u6c42\u3002\u542c\u8d75\u9e4f\u7684\u53e3\u6c14\uff0c\u5f20\u606a\u7684\u5429\u5490\u662f\u4e0d\u5bb9\u4ed6\u4eec\u8fd9\u4e9b\u6a61\u6811\u56ed\u7684\u7ba1\u7406\u5c42\u7f6e\u7591\u7684\uff0c\u771f\u4e0d\u6e05\u695a\u90a3\u4e2a\u9752\u5e74\u662f\u4ec0\u4e48\u4eba\uff0c\u8fd8\u771f\u662f\u795e\u79d8\u83ab\u6d4b\u5462\u3002</p><p>\u3000\u3000\u201c\u884c\uff0c\u8fd9\u4e2a\u6076\u4eba\u6211\u505a\u5f97\u3002\u201d\u7530\u529b\u5c71\u4e00\u53e3\u7b54\u5e94\u4e0b\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u4e3a\u4e86\u4e0d\u5f71\u54cd\u4f60\u4eec\u7684\u5de5\u4f5c\uff0c\u6709\u4ec0\u4e48\u9700\u8981\u6ce8\u610f\u7684\uff0c\u4f60\u8ddf\u6211\u8bf4\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7530\u529b\u5c71\u4e2d\u5348\u5c31\u9886\u7740\u5341\u4e03\u516b\u540d\u4e2d\u5e74\u5973\u5de5\u5230\u6a61\u6811\u56ed\u5458\u5de5\u9910\u5385\u6765\u6297\u8bae\u4e86\uff0c\u8fd8\u5148\u5728\u9910\u5385\u5403\u8fc7\u996d\uff0c\u5927\u4e2d\u5348\u7684\uff0c\u53c8\u8dd1\u5230\u4e1c\u534e\u5927\u9053\u8fb9\u5c06\u4e3d\u4e30\u56ed\u533a\u5927\u95e8\u7ed9\u5835\u4e0a\uff0c\u626f\u8d77\u201c\u4e0b\u5c97\u5de5\u4eba\u8981\u751f\u5b58\u3001\u8981\u5de5\u4f5c\u201d\u7684\u6a2a\u5e45\u3002</p><p>\u3000\u3000\u4e3d\u4e30\u56ed\u533a\u5927\u95e8\u79bb\u5b66\u5e9c\u5df7\u5df7\u53e3\u4e5f\u5c31\u4e00\u91cc\u591a\u8def\uff0c\u6709\u4e2a\u98ce\u5439\u8349\u52a8\uff0c\u5b66\u5e9c\u5df7\u8fd9\u8fb9\u4e5f\u5c31\u77e5\u9053\uff0c\u5b59\u9759\u6aac\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\uff0c\u5f20\u606a\u4e0d\u53ef\u80fd\u8fd9\u4e48\u53cd\u590d\u65e0\u5e38\u5427\uff1f\u6253\u4ed6\u624b\u673a\uff0c\u4e00\u76f4\u5360\u7ebf\uff0c\u5b59\u9759\u6aac\u6ca1\u6709\u8010\u5fc3\uff0c\u5c31\u9a91\u7740\u5355\u8f66\u76f4\u63a5\u5230\u7814\u7a76\u751f\u516c\u5bd3\u627e\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5934\u80a9\u5939\u7740\u624b\u673a\u7ed9\u5979\u6253\u5f00\u95e8\uff0c\u8ba9\u5979\u8fdb\u53bb\uff0c\u5634\u91cc\u6ca1\u6709\u505c\u6b62\u8bb2\u7535\u8bdd\uff1a\u201c\u54e6\uff0c\u738b\u5e02\u957f\uff0c\u6211\u6653\u5f97\u8fd9\u4e8b\u4e86\uff0c\u5de5\u4eba\u4ee3\u8868\u65e2\u7136\u63d0\u51fa\u610f\u89c1\uff0c\u8981\u5e2e\u7740\u4fc3\u8fdb\u6211\u4eec\u7684\u5de5\u4f5c\uff0c\u6211\u4eec\u5f53\u7136\u8981\u5438\u53d6\u6539\u8fdb\u3002\u4f60\u8ddf\u7f57\u4e66\u8bb0\u8bf4\u4e00\u4e0b\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u9ebb\u70e6\u4ed6\u64cd\u5fc3\u4e86\u3002\u5de5\u4eba\u4ee3\u8868\u63d0\u51fa\u8fd9\u4e9b\u6761\u4ef6\u4e5f\u662f\u60c5\u6709\u53ef\u539f\u7684\uff0c\u56db\u4e94\u4e2a\u6708\u6ca1\u6709\u65b0\u5de5\u4f5c\u5c97\u4f4d\uff0c\u6362\u8c01\u90fd\u8981\u6025\uff0c\u6211\u4eec\u5b8c\u5168\u80fd\u591f\u7406\u89e3\u5de5\u4eba\u4ee3\u8868\u7684\u5fc3\u60c5\uff0c\u800c\u4e14\u8fd9\u4e9b\u6761\u4ef6\u4e5f\u5f88\u5408\u7406\u5427\u3002\u53ef\u4ee5\u7684\u8bdd\uff0c\u8bf7\u5e02\u52b3\u52a8\u90e8\u95e8\u6216\u5e02\u603b\u5de5\u4f1a\u51fa\u9762\u534f\u8c03\u4e00\u4e0b\uff0c\u6a61\u6811\u56ed\u8fd9\u8fb9\uff0c\u6211\u770b\u5c31\u8ba9\u8d75\u9e4f\u4eb2\u81ea\u51fa\u9762\u8c08\u597d\u4e86\uff0c\u80fd\u591f\u63a5\u53d7\u7684\u6761\u4ef6\uff0c\u6211\u4eec\u90fd\u4f1a\u5c3d\u529b\u63a5\u53d7\u7684\u2026\u2026\u4e0d\u4e3a\u96be\uff0c\u4e0d\u4e3a\u96be\uff0c\u771f\u7684\u4e0d\u4e3a\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u6f14\u90a3\u51fa\u620f\u554a\uff1f\u201d\u5b59\u9759\u6aac\u5750\u5728\u5f20\u606a\u7684\u5e8a\u4e0a\u7b49\u4ed6\u5c06\u7535\u8bdd\u8bb2\u5b8c\uff0c\u624d\u786e\u5b9a\u4ed6\u4e5f\u6b63\u662f\u8ddf\u522b\u4eba\u8bf4\u8fd9\u4e8b\uff0c\u201c\u6211\u600e\u4e48\u770b\u4e0d\u660e\u767d\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u771f\u662f\u597d\u4eba\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u8bf4\u6211\u662f\u597d\u4eba\u6765\u4e86\uff1f\u201d\u5f20\u606a\u644a\u4e86\u644a\u624b\uff0c\u8dd1\u8fc7\u53bb\u5c06\u95e8\u5173\u4e0a\uff0c\u9644\u5230\u5b59\u9759\u6aac\u7684\u8033\u6735\u6839\u4e0a\u8bf4\u9053\uff0c\u201c\u8fd9\u8fd8\u662f\u5728\u6f14\u620f\uff0c\u4e0d\u8981\u51fa\u53bb\u4e71\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u597d\u50cf\u771f\u662f\u591a\u5927\u7684\u79d8\u5bc6\u4f3c\u7684\uff0c\u6e29\u70ed\u7684\u9f3b\u606f\u5439\u7740\u8033\u6839\u75d2\u75d2\u7684\uff0c\u5b59\u9759\u6aac\u7f8e\u7738\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4e3a\u4ec0\u4e48\u554a\uff0c\u4f60\u4e0d\u89c9\u5f97\u591a\u6b64\u4e00\u4e3e\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u554a\uff0c\u6628\u5929\u56de\u6765\u770b\u62a5\u7eb8\uff0c\u201d\u5f20\u606a\u8db4\u5230\u5e8a\u4e0a\uff0c\u5c06\u5e8a\u5934\u4e00\u4efd\u62a5\u7eb8\u62ff\u8fc7\u6765\u9012\u7ed9\u5b59\u9759\u6aac\u770b\uff0c\u201c\u770b\u4e86\u4e0a\u9762\u4e00\u7bc7\u62a5\u9053\uff0c\u5c31\u60f3\u7740\u8fd8\u662f\u6f14\u4e00\u51fa\u620f\u4e3a\u597d\u2026\u2026\u201d\u4ed6\u5219\u53cc\u624b\u62b1\u5934\u4ef0\u8eba\u5728\u5e8a\u4e0a\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u83ab\u660e\u5176\u5999\uff0c\u201d\u5b59\u9759\u6aac\u561f\u7740\u7c89\u6da6\u7684\u5634\u5507\uff0c\u770b\u4e86\u62a5\u7eb8\u4e0a\u7684\u62a5\u9053\u4e4b\u540e\uff0c\u770b\u7740\u5f20\u606a\uff0c\u773c\u775b\u6709\u7740\u96be\u4ee5\u7f6e\u4fe1\u7684\u9707\u60ca\uff0c\u201c\u600e\u4e48\u53ef\u80fd\u6709\u8fd9\u79cd\u4e8b\u60c5\u53d1\u751f\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u4e00\u4e2a\u795e\u5947\u7684\u56fd\u5ea6\uff0c\u4ec0\u4e48\u60c5\u51b5\u4e0d\u80fd\u53d1\u751f\uff1f\u201d\u5f20\u606a\u4ef0\u8eba\u7740\u6487\u5634\u7b11\u4e86\u4e00\u7b11\uff0c\u6709\u4e9b\u9634\u51b7\u3002</p><p>\u3000\u3000\u524d\u6bb5\u65f6\u95f4\uff0c\u5a92\u4f53\u62a5\u9053\u4e86\u5efa\u90ba\u5e02\u5efa\u6750\u5546\u4eba\u90b5\u5174\u5149\u5341\u4e94\u5e74\u6765\u503e\u5bb6\u8361\u4ea7\u5171\u62ff\u51fa\u4e24\u767e\u4e07\u8d44\u4ea7\u51fa\u6765\u505a\u6148\u5584\u7684\u4e8b\u8ff9\u4e4b\u540e\u3002\u5a92\u4f53\u4e3a\u4e86\u5438\u5f15\u56fd\u4eba\u7684\u76ee\u5149\uff0c\u7528\u4e0a\u201c\u6148\u5584\u8d85\u4eba\u201d\u8fd9\u4e2a\u8ba9\u4eba\u5f88\u65e0\u8bed\u7684\u5b57\u773c\u3002\u77ed\u77ed\u5341\u6570\u5185\uff0c\u5f15\u6765\u6570\u767e\u540d\u7d22\u6350\u6c42\u52a9\u8005\u6324\u7834\u90b5\u5174\u5149\u7684\u5bb6\u95e8\uff0c\u7d22\u6350\u603b\u989d\u8d85\u8fc7300\u591a\u4e07\u3002\u8fd8\u6709\u4e00\u540d\u767d\u8840\u75c5\u60a3\u8005\u624b\u6301\u76d6\u77406\u679a\u5f53\u5730\u653f\u5e9c\u516c\u7ae0\u7684\u6c42\u52a9\u4fe1\u6765\u627e\u90b5\u5174\u5149\u201c\u5949\u65e8\u7d22\u6350\u201d\u3002\u5341\u591a\u540d\u7d22\u6350\u8005\u627e\u90b5\u5174\u5149\u7d22\u6350\u4e0d\u6210\uff0c\u4e00\u9f50\u8dd1\u5230\u90b5\u5174\u5149\u72ec\u5c45\u7684\u8001\u6bcd\u4eb2\u90a3\u91cc\u7d22\u6350\u5435\u95f9\uff0c\u90b5\u5174\u5149\u7684\u6bcd\u4eb2\u5728\u7ea0\u7eb7\u4e2d\u6c14\u5f97\u591a\u6b21\u5410\u8840\uff0c\u6628\u5929\u5728\u533b\u9662\u75c5\u6545\u3002</p><p>\u3000\u3000\u6628\u5929\u7684\u62a5\u7eb8\u62a5\u9053\u7684\u662f\u90b5\u5174\u5149\u6bcd\u4eb2\u7ed9\u7d22\u6350\u8005\u6c14\u6b7b\u7684\u65b0\u95fb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u662f\u9047\u8f66\u7978\u65e9\uff0c\u6ca1\u6709\u9047\u4e0a\u7f51\u7edc\u903c\u6350\u7684\u58ee\u4e3e\u3002</p><p>\u3000\u3000\u201c\u7530\u529b\u5c71\u8981\u641e\u4fc3\u8fdb\u4e0b\u5c97\u804c\u5de5\u518d\u5c31\u4e1a\u7684\u670d\u52a1\u6240\uff0c\u6211\u8981\u662f\u8896\u624b\u65c1\u89c2\uff0c\u5c31\u592a\u51b7\u6f20\u65e0\u60c5\u4e86\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4f46\u662f\u8fd9\u672c\u662f\u653f\u5e9c\u804c\u80fd\u8303\u56f4\u5185\u5e94\u505a\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u592a\u4e3b\u52a8\uff0c\u4e0d\u80fd\u8ba8\u597d\u5230\u653f\u5e9c\u7684\u5b98\u8001\u7237\u4eec\uff0c\u8fd8\u8981\u906d\u4eba\u5fcc\u6068\u3002\u201d</p><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u5b59\u9759\u6aac\u7591\u60d1\u4e0d\u89e3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u505a\u4e0d\u597d\uff0c\u6211\u4eec\u5e2e\u4ed6\u4eec\u505a\u4e86\uff0c\u8fd9\u662f\u5728\u6253\u4ed6\u4eec\u7684\u8138\uff1b\u8fd8\u6709\u5c31\u662f\u8fd9\u4e2a\u7f18\u6545\u2026\u2026\u201d\u5f20\u606a\u671d\u5b59\u9759\u6aac\u624b\u91cc\u7684\u62a5\u7eb8\u5476\u5476\u5634\uff0c\u201c\u8fd9\u4e2a\u5e74\u4ee3\uff0c\u597d\u4eba\u8fd8\u505a\u4e0d\u5f97\u2026\u2026\u201d\u5c31\u7b97\u7ed9\u65b0\u829c\u5e02\u7684\u6350\u8d60\uff0c\u57fa\u672c\u4e0a\u4e5f\u6390\u7740\u65f6\u673a\uff0c\u50cf\u8fd9\u6b21\u635f\u8d60\u4e00\u5343\u4e07\u7ed9\u65b0\u829c\u5e02\u7684\u804c\u4e1a\u57f9\u8bad\u4e0e\u65c5\u6e38\u5b66\u6821\uff0c\u6070\u597d\u4e5f\u662f\u7701\u5e02\u8ba8\u8bba\u4ed6\u7238\u8fdb\u5e38\u59d4\u7684\u65f6\u673a\u3002\u5a92\u4f53\u5bf9\u516c\u4f17\u8fdb\u884c\u5ba3\u4f20\uff0c\u5f53\u7136\u662f\u7ed9\u4e16\u7eaa\u9526\u6e56\u51a0\u4ee5\u4e50\u5584\u597d\u65bd\u7684\u7f8e\u8a8a\uff0c\u65b0\u829c\u4e2d\u5c42\uff0c\u591a\u5c11\u77e5\u9053\u4e16\u7eaa\u9526\u6e56\u4e0e\u5f20\u5bb6\u7684\u5173\u7cfb\uff0c\u4e16\u7eaa\u9526\u6e56\u80fd\u62ff\u8fd9\u4e9b\u94b1\u51fa\u6765\uff0c\u5f20\u77e5\u884c\u5f80\u8d70\u5c0f\u8df3\u4e00\u6b65\uff0c\u4e5f\u5bb9\u6613\u7406\u89e3\uff0c\u5927\u5bb6\u90fd\u4e0d\u4f1a\u6709\u4ec0\u4e48\u62b5\u89e6\u5fc3\u7406\uff0c\u4e91\u6c60\u9152\u4e1a\u7684\u6210\u7ee9\u8fd8\u6446\u5728\u90a3\u91cc\uff0c\u518d\u8bf4\u4e4b\u524d\u91cd\u5efa\u57ce\u9632\u6c5f\u5824\u65f6\uff0c\u4e16\u7eaa\u9526\u6e56\u8fd8\u6350\u4e86\u516b\u767e\u4e07\u5462\uff0c\u89e3\u4e86\u5e02\u91cc\u7684\u71c3\u7709\u4e4b\u6025\u3002\u65b0\u829c\u9ad8\u5c42\u4e0e\u7701\u91cc\u66f4\u662f\u5fc3\u77e5\u809a\u660e\u8fd9\u7b14\u94b1\u662f\u9526\u6e56\u51fa\u7684\uff0c\u4ed6\u4eec\u6070\u6070\u80fd\u66f4\u51c6\u786e\u7684\u7406\u89e3\u5f20\u606a\u8fd9\u4e48\u505a\u7684\u771f\u5b9e\u52a8\u673a\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6320\u4e86\u6320\u5934\uff0c\u77aa\u773c\u770b\u7740\u5f20\u606a\uff0c\u611f\u6168\u7684\u8bf4\uff1a\u201c\u597d\u590d\u6742\u554a\uff0c\u4f60\u8111\u888b\u74dc\u5b50\u91cc\u5230\u5e95\u88c5\u4e86\u4ec0\u4e48\u4e1c\u897f\uff0c\u5feb\u8ba9\u6211\u770b\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5934\u4f38\u8fc7\u6765\u6446\u5230\u5b59\u9759\u6aac\u7684\u80f8\u524d\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4f38\u624b\u6293\u7740\u5f20\u606a\u7684\u5934\u53d1\u63c9\u4e86\u63c9\uff0c\u773c\u7738\u4f4e\u5782\u7740\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u611f\u89c9\u4ed6\u7684\u89c6\u7ebf\u6b63\u76ef\u7740\u81ea\u5df1\u4e30\u633a\u7684\u80f8\u812f\u5728\u770b\uff0c\u6362\u4f5c\u5f80\u65f6\u4f1a\u5c06\u4ed6\u595a\u843d\u4e00\u756a\uff0c\u8fd9\u65f6\u5019\uff0c\u5374\u597d\u60f3\u5c06\u4ed6\u6402\u8fdb\u6000\u91cc\uff0c\u8ba9\u4ed6\u7684\u8138\u8d34\u7740\u81ea\u5df1\u4e30\u6ee1\u7684\u80f8\u90e8\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u514b\u5236\u7740\u5fc3\u91cc\u7684\u7eee\u4e3d\u9050\u60f3\uff0c\u8ba9\u5f20\u606a\u8eba\u597d\uff0c\u770b\u7740\u4ed6\uff1a\u201c\u771f\u662f\u8d8a\u6765\u8d8a\u4e0d\u660e\u767d\u4f60\u5462\u2026\u2026\u201d\u4e24\u5e74\u524d\u7684\u51ac\u5b63\uff0c\u57281978\u76f8\u9047\u65f6\uff0c\u5f20\u606a\u662f\u90a3\u4e2a\u7a7f\u7eaa\u68b5\u5e0c\u886c\u886b\u7684\u5e72\u51c0\u7537\u4eba\uff0c\u8ddf\u7740\u53f6\u5efa\u658c\u8fc7\u6765\uff0c\u8c01\u4e5f\u4e0d\u544a\u8bc9\u81ea\u5df1\u4ed6\u7684\u8eab\u4efd\uff0c\u8ddf\u4e00\u56e2\u8ff7\u4f3c\u7684\uff0c\u8fd9\u4e24\u5e74\uff0c\u81ea\u4ee5\u4e3a\u6e10\u6e10\u77e5\u9053\u4e00\u4e9b\u4e8b\u60c5\uff0c\u5374\u53d1\u73b0\u8fd9\u4e2a\u7537\u4eba\u5728\u81ea\u5df1\u773c\u91cc\u8fd8\u662f\u4e00\u56e2\u8ff7\uff0c\u8bf1\u4eba\u8feb\u5207\u60f3\u89e3\u5f00\u7684\u8ff7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u65e2\u7136\u77e5\u9053\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u5b59\u9759\u6aac\u4e5f\u6ca1\u6709\u7406\u7531\u7ee7\u7eed\u8d56\u5728\u5bbf\u820d\u65f6\u8ddf\u5f20\u606a\u7c98\u5728\u4e00\u8d77\uff0c\u518d\u5c31\u9152\u5427\u91cc\u8fd8\u6709\u597d\u4e9b\u5de5\u4f5c\u8981\u51c6\u5907\uff0c\u5c31\u4e0e\u5f20\u606a\u4e00\u8d77\u51fa\u4e86\u7814\u7a76\u751f\u516c\u5bd3\uff0c\u4e0b\u697c\u65f6\u8fd8\u9047\u5230\u6797\u51b0\u6765\u7740\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8981\u53bb\u9152\u5427\uff0c\u5f20\u606a\u8981\u53bb\u56fe\u4e66\u9986\uff0c\u4fe9\u4eba\u5408\u9a91\u4e00\u8f86\u5355\u5c71\u5728\u5408\u6b22\u5c71\u4e1c\u5c71\u811a\u4e0b\u5206\u5f00\u3002\u5355\u8f66\u8ba9\u7ed9\u5b59\u9759\u6aac\u9a91\u8d70\uff0c\u5f20\u606a\u7ee7\u7eed\u5f80\u524d\u8d70\uff0c\u6ca1\u8d70\u4e24\u8d70\uff0c\u5c31\u542c\u5230\u6709\u5f15\u64ce\u4f4e\u6c89\u7684\u8f70\u9e23\u58f0\u4ece\u8fdc\u5904\u4f20\u6765\uff0c\u626d\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4e00\u8f86\u660e\u9ec4\u8272\u7684\u6cd5\u62c9\u5229550\u9a6c\u62c9\u5185\u7f57\u8dd1\u8f66\u4ece\u4e1c\u95e8\u51b2\u8fdb\u6765\uff0c\u8fc7\u6821\u95e8\u65f6\u4e5f\u6ca1\u6709\u4e1d\u6beb\u51cf\u901f\uff0c\u4f3c\u4e4e\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u8fd9\u8f66\u78d5\u7740\u78b0\u7740\u90a3\u91cc\u5c31\u662f\u5341\u51e0\u4e8c\u5341\u4e07\u7684\u635f\u5931\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u5728\u56fd\u5185\u5f00\u7740\u4e00\u8f86\u6cd5\u62c9\u5229550\u9a6c\u62c9\u5185\u7f57\uff0c\u5373\u4f7f\u9f9f\u901f\u884c\u9a76\u4e5f\u80fd\u5438\u5f15\u7edd\u5927\u591a\u6570\u4eba\u7684\u76ee\u5149\uff0c\u4f55\u51b5\u8f66\u901f\u80fd\u5e26\u7740\u8def\u9762\u4e0a\u7684\u843d\u53f6\u90fd\u98d8\u6d6e\u8d77\u6765\u3002</p><p>\u3000\u3000\u8fd9\u8f66\u8d3c\u4e0d\u4fbf\u5b9c\uff0c\u62b5\u4e24\u4e09\u90e8\u5954\u9a70W140\u3002</p><p>\u3000\u3000\u8def\u4e0a\u7684\u5b66\u751f\u7eb7\u7eb7\u907f\u8ba9\u5230\u8def\u7259\u4e0a\uff0c\u5b59\u9759\u6aac\u4e5f\u4e0b\u4e86\u5355\u8f66\u8ba9\u5230\u8def\u7259\u4e0a\u3002\u7ad9\u5230\u8def\u7259\u4e0a\uff0c\u5b59\u9759\u6aac\u60f3\u8d77\u4ef6\u4e8b\u5fd8\u4e86\u6ca1\u8ddf\u5f20\u606a\u8bf4\uff0c\u671d\u5f20\u606a\u62db\u624b\u8ba9\u4ed6\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u8fd9\u5e74\u5934\uff0c\u8c01\u90fd\u6068\u4e0d\u5f97\u5c06\u56a3\u5f20\u4e24\u4e2a\u5b57\u523b\u5230\u8138\u4e0a\u3002</p><p>\u3000\u3000\u6709\u94b1\u4eba\u73a9\u9177\uff0c\u9664\u4e86\u540d\u8f66\u7f8e\u5973\u4e4b\u5916\uff0c\u4f3c\u4e4e\u6ca1\u6709\u66f4\u56a3\u5f20\u7684\u5229\u5668\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u5f97\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8d34\u7740\u8def\u7259\u8d70\uff0c\u4ed6\u7684\u5c0f\u8eab\u5b50\u9aa8\u53ef\u7ecf\u4e0d\u8d77\u90a3\u6a2a\u51b2\u76f4\u649e\u7684\u6cd5\u5229\u62c9\u649e\u4e00\u4e0b\u3002\u79bb\u5b59\u9759\u6aac\u8fd8\u9694\u7740\u51e0\u7c73\u5462\uff0c\u5c31\u770b\u89c1\u90a3\u8f86\u660e\u9ec4\u8272\u7684\u6cd5\u62c9\u5229\u5728\u5b59\u9759\u6aac\u9aa4\u7136\u5239\u4f4f\uff0c\u4e00\u4e2a\u6234\u7740\u86e4\u87c6\u955c\u7684\u9752\u5e74\u63a2\u51fa\u5934\u6765\uff0c\u62ff\u6709\u7740\u4e00\u80a1\u602a\u8154\u8c03\u7684\u666e\u901a\u8bdd\u95ee\u5b59\u9759\u6aac\uff1a\u201c\u5c0f\u59d0\uff0c\u4f60\u4eec\u5b66\u6821\u7684\u7559\u5b66\u751f\u516c\u5bd3\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u3000\u3000\u55ec\uff0c\u8fd9\u5bb6\u4f19\u8f66\u901f\u5f00\u8fd9\u4e48\u5feb\uff0c\u773c\u775b\u90fd\u5fd8\u8bb0\u5f80\u8def\u4e24\u4fa7\u641c\u7d22\u7f8e\u5973\u3002</p><p>\u3000\u3000\u770b\u7740\u8fd9\u5bb6\u4f19\u8ddf\u5b59\u9759\u6aac\u642d\u8baa\uff0c\u5f20\u606a\u4e5f\u88c5\u7740\u662f\u6beb\u65e0\u76f8\u5e72\u7684\u4eba\u5728\u65c1\u8fb9\u770b\u70ed\u95f9\u3002\u53ea\u662f\u8dd1\u8f66\u6025\u901f\u9aa4\u505c\u5c06\u8def\u4e0a\u7684\u6d6e\u5c18\u90fd\u5e26\u52a8\u8d77\u6765\uff0c\u8981\u6342\u7740\u53e3\u9f3b\uff0c\u8ba9\u5f20\u606a\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u723d\uff0c\u5f80\u8f66\u91cc\u770b\u4e86\u4e00\u773c\uff0c\u8fd9\u4e2a\u56a3\u5f20\u9752\u5e74\u8eab\u8fb9\u7adf\u7136\u8fd8\u5750\u7740\u4e00\u4e2a\u59ff\u8272\u4e1d\u6beb\u4e0d\u5dee\u4e8e\u5b59\u9759\u6aac\u7684\u660e\u8273\u5973\u90ce\u3002</p><p>\u3000\u3000\u5973\u90ce\u8138\u4e0a\u6ca1\u6709\u5e26\u58a8\u955c\uff0c\u662f\u90a3\u79cd\u97e9\u56fd\u7f8e\u5973\u7684\u5178\u578b\u8138\u5f62\uff0c\u4e0d\u6653\u5f97\u662f\u4e0d\u662f\u6574\u8fc7\u5bb9\uff0c\u5373\u4f7f\u6574\u8fc7\u5bb9\uff0c\u5927\u6982\u7537\u4eba\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u770b\u8fd9\u4e48\u4e00\u5f20\u7f8e\u8273\u7684\u8138\u3002\u5f20\u606a\u89c6\u7ebf\u5f80\u4e0b\u79fb\u4e86\u79fb\uff0c\u9f3b\u8840\u5dee\u70b9\u5c31\u51b2\u51fa\u6765\uff0c\u4ed6\u4e00\u76f4\u4ee5\u4e3a\u665a\u6674\u7684\u80f8\u591f\u4e30\u6ee1\u4e86\uff0c\u603b\u89c9\u5973\u4eba\u771f\u8981\u6709\u4e2aG\u7f69\u6000\uff0c\u4e5f\u591f\u5938\u5f20\u7684\uff0c\u5374\u5b8c\u5168\u7684\u6ca1\u6709\u9884\u6599\u5230\u6709\u7740\u5929\u4f7f\u8138\u86cb\u7684\u5973\u4eba\u518d\u6765\u4e2aG\u7f69\u676f\u7684\u5927\u80f8\u7adf\u4f1a\u7ed9\u4eba\u8fd9\u4e48\u5f3a\u70c8\u7684\u89c6\u89c9\u51b2\u51fb\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u65f6\u95f4\u4e0d\u5bf9\uff0c\u5f20\u606a\u90fd\u6000\u7591\u662f\u97e9\u56fd\u90a3\u4e2a\u53eb\u6797\u667a\u6167\u7684\u8f66\u6a21\u5230\u56fd\u5185\u6765\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5b59\u9759\u6aac\u6ca1\u6709\u770b\u5230\u7ad9\u5728\u5979\u8eab\u5b50\u4fa7\u540e\u5f20\u606a\u90a3\u526f\u732a\u54e5\u6837\uff0c\u4f46\u662f\u5750\u5728\u6cd5\u62c9\u5229\u8dd1\u8f66\u91cc\u7684\u8fd9\u4e2a\u9752\u5e74\u8086\u65e0\u5fcc\u60ee\u7684\u5728\u5979\u8eab\u4e0a\u6253\u91cf\u7740\u7684\u773c\u795e\u8ba9\u5979\u809a\u5b50\u91cc\u7a9d\u4e86\u4e00\u56e2\u706b\uff0c\u5374\u5de7\u7b11\u5ae3\u7136\u7684\u8bf4\uff0c\u201c\u4f60\u627e\u7559\u5b66\u751f\u516c\u5bd3\u554a\uff0c\u524d\u9762\u5c94\u8def\u53e3\u5f80\u53f3\u62d0\u76f4\u5f00\u5c31\u884c\u4e86\u2026\u2026\u201d\u8981\u542c\u5b59\u9759\u6aac\u7684\u8bdd\uff0c\u4f1a\u76f4\u63a5\u5f00\u51fa\u5317\u95e8\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u8def\u75f4\uff0c\u4e0d\u7ba1\u591a\u7b80\u5355\u7684\u8def\u90fd\u4f1a\u8dd1\u9519\uff0c\u5c0f\u59d0\u80fd\u4e0d\u80fd\u9001\u6211\u4eec\u4e00\u7a0b\uff1f\u201d\u90a3\u9752\u5e74\u5c06\u58a8\u955c\u6458\u4e0b\u6765\uff0c\u9732\u51fa\u5178\u578b\u7684\u97e9\u5f0f\u5355\u773c\u76ae\u773c\u775b\uff0c\u975e\u5e38\u6709\u81ea\u4fe1\u7684\u9080\u8bf7\u5b59\u9759\u6aac\u5750\u4e0a\u4ed6\u7684\u6cd5\u5229\u62c9\uff0c\u4ed6\u90fd\u65e0\u6cd5\u60f3\u8c61\u8fd9\u4e16\u754c\u8fd8\u4f1a\u6709\u51e0\u4e2a\u5973\u4eba\u6709\u673a\u4f1a\u5374\u4f1a\u62d2\u7edd\u5750\u8fdb\u6cd5\u5229\u62c9\u9a6c\u62c9\u5185\u7f57\u8dd1\u8f66\uff0c\u8f6c\u8fc7\u5934\u5429\u5490\u4ed6\u8eab\u4fa7\u7684\u8273\u4e3d\u5973\u90ce\uff0c\u201c\u5de7\u8d1e\uff0c\u4f60\u5750\u540e\u9762\u53bb\u2026\u2026\u201d</p></article>"}}}]);
