(window.webpackJsonp=window.webpackJsonp||[]).push([[844],{867:function(p){p.exports={chapterTitle:null,title:"\u7b2c670\u7ae0 \u5634\u8138",content:"<article><h2>\u7b2c670\u7ae0 \u5634\u8138</h2><p>\u3000\u3000\u542c\u674e\u5728\u6d19\u53eb\u56a3\u7740\u8bf4\u4ec0\u4e48\u5916\u4ea4\u7ea0\u7eb7\uff0c\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u5fe4\u3002\u8fd9\u5e74\u5934\uff0c\u5e72\u8b66\u5bdf\u662f\u6ecb\u6da6\uff0c\u4f46\u662f\u5de5\u4f5c\u4e0a\u4e5f\u6709\u96f7\u533a\uff0c\u4e00\u6015\u6c11\u65cf\u7ea0\u7eb7\uff0c\u4e8c\u6015\u5916\u4e8b\u7ea0\u7eb7\uff0c\u90fd\u770b\u7740\u4e0a\u4efb\u624d\u534a\u5e74\u591a\u7684\u6240\u957f\u4f55\u7eaa\u4e91\u3002</p><p>\u3000\u3000\u4e0e\u674e\u5728\u6d19\u5728\u4e00\u8d77\u7684\u90a3\u4e2a\u97e9\u56fd\u9752\u5e74\uff0c\u5728\u4f55\u7eaa\u4e91\u4ed6\u4eec\u8d76\u6765\u4e4b\u524d\uff0c\u5df2\u7ecf\u7535\u8bdd\u901a\u77e5\u4e86\u4e1c\u6d77\u5927\u5b66\u7684\u5916\u4e8b\u529e\u516c\u5ba4\u3001\u97e9\u56fd\u9a7b\u4e0a\u6d77\u7684\u603b\u9886\u4e8b\u9986\uff0c\u4f46\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u4e5f\u6ca1\u6709\u80c6\u91cf\u5728\u7535\u8bdd\u91cc\u5c06\u4e8b\u60c5\u8bf4\u7684\u591a\u4e25\u91cd\uff0c\u53ea\u8bf4\u53d1\u751f\u4e86\u4e9b\u5c0f\u7ea0\u7eb7\uff0c\u5e0c\u671b\u4e1c\u6d77\u5927\u5b66\u5916\u4e8b\u529e\u516c\u5ba4\u4e0e\u603b\u9886\u4e8b\u9986\u51fa\u9762\u89e3\u51b3\u3002</p><p>\u3000\u3000\u8eab\u4e3a\u4e09\u661f\u96c6\u56e2\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\uff0c\u674e\u5728\u6d19\u7684\u8eab\u4efd\u6bd5\u7adf\u7279\u6b8a\uff1b\u53ea\u6015\u4e0d\u9700\u8981\u591a\u5c11\u65f6\u95f4\u5c31\u4f1a\u60ca\u52a8\u4e1c\u6d77\u7701\u3001\u5efa\u90ba\u5e02\u7684\u5916\u4e8b\u90e8\u95e8\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u4ed6\u4eec\u552f\u72ec\u6ca1\u6709\u901a\u77e5\u4e09\u661f\u96c6\u56e2\u5728\u5efa\u90ba\u7684\u534a\u5bfc\u4f53\u516c\u53f8\uff0c\u8fd9\u672c\u5e94\u8be5\u662f\u4ed6\u4eec\u6700\u76f4\u63a5\u7684\u6551\u63f4\u5bf9\u8c61\u3002\u60f3\u5fc5\u4ed6\u4eec\u6015\u6253\u7535\u8bdd\u65f6\u7ed9\u56f4\u89c2\u7684\u5b66\u751f\u77e5\u9053\u4ed6\u4eec\u7684\u8eab\u4efd\uff0c\u5f71\u54cd\u4e09\u661f\u96c6\u56e2\u5728\u4e2d\u56fd\u7684\u4f01\u4e1a\u5f62\u8c61\u3002\u8fd8\u6709\u5c31\u662f\u674e\u5065\u7199\u5bb6\u65cf\u5728\u97e9\u56fd\u6b63\u9677\u5165\u9003\u7a0e\u6848\u7684\u4e11\u95fb\u4e4b\u4e2d\uff0c\u4ed6\u4eec\u7a0d\u6709\u4e9b\u5934\u8111\u5c31\u4e0d\u4f1a\u60f3\u5377\u5165\u4ec0\u4e48\u4fae\u534e\u6848\u7684\u4e11\u95fb\uff0c\u771f\u8981\u95f9\u51fa\u4ec0\u4e48\u5927\u7684\u65b0\u95fb\uff0c\u5c31\u662f\u4ed6\u4eec\u97e9\u56fd\u5a92\u4f53\u7684\u53cd\u5e94\u4e5f\u5f88\u96be\u5224\u65ad\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u674e\u5728\u6d19\u4ed6\u4eec\u5230\u5e95\u77e5\u9053\u62ff\u634f\u5206\u5bf8\uff0c\u8fd9\u65f6\u5019\u56b7\u56b7\u7740\u5916\u4ea4\u7ea0\u7eb7\uff0c\u4e0d\u8fc7\u662f\u8981\u7ed9\u4f55\u7eaa\u4e91\u4ed6\u4eec\u65bd\u538b\uff0c\u4ee5\u4e3a\u4f55\u7eaa\u4e91\u4f1a\u4e0e\u4e2d\u56fd\u7684\u666e\u901a\u8b66\u5bdf\u90e8\u95e8\u4e00\u6837\u4f1a\u5c48\u670d\u4ed6\u4eec\u7684\u6deb\u5a01\u4e4b\u4e0b\uff0c\u7eb5\u5bb9\u4ed6\u4eec\u5728\u4e2d\u56fd\u80e1\u4f5c\u975e\u4e3a\u3002</p><p>\u3000\u3000\u5f20\u606a\u90fd\u65e0\u6cd5\u60f3\u8c61\u5f53\u4e8b\u4eba\u8981\u662f\u6362\u4f5c\u522b\u4eba\uff0c\u8981\u662f\u53d1\u751f\u5728\u5b66\u5e9c\u5df7\u4e4b\u5916\u7684\u5730\u65b9\uff0c\u4ee5\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\u56fd\u5185\u653f\u5e9c\u5bf9\u97e9\u56fd\u53cb\u4eba\u7684\u53cb\u5584\u7a0b\u5ea6\uff0c\u6307\u4e0d\u5b9a\u771f\u8981\u8ba9\u4ed6\u4eec\u5f97\u901e\u4e0d\u53ef\u3002</p><p>\u3000\u3000\u5982\u679c\uff0c\u4e00\u4e2a\u56fd\u5bb6\u5bb3\u6015\u5916\u4ea4\u7ea0\u7eb7\u8fde\u56fd\u4eba\u7684\u6b63\u5f53\u6743\u76ca\u90fd\u4e0d\u6562\u7ef4\u62a4\uff0c\u90a3\u79bb\u5927\u56fd\u4e4b\u8def\u5c31\u8fd8\u6709\u76f8\u5f53\u957f\u7684\u4e00\u6bb5\u8ddd\u79bb\u8981\u8d70\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u54fc\u2026\u2026\u201d\u4f55\u7eaa\u4e91\u76ee\u5149\u9510\u5229\u7684\u76ef\u4e86\u674e\u5728\u6d19\u4e00\u773c\uff0c\u54fc\u4e86\u4e00\u58f0\uff0c\u4e25\u5389\u7684\u559d\u65a5\u9053\uff0c\u201c\u4ec0\u4e48\u53eb\u5916\u4ea4\u7ea0\u7eb7\uff0c\u4f60\u5f53\u4e2d\u56fd\u7684\u8b66\u5bdf\u90fd\u662f\u552c\u5927\u7684\uff01\u8001\u5b9e\u8ddf\u6211\u4eec\u56de\u6d3e\u51fa\u6240\u5c06\u4e8b\u60c5\u7ecf\u8fc7\u4ecb\u7ecd\u6e05\u695a\uff0c\u4e0d\u8001\u5b9e\u5c31\u94d0\u7740\u4f60\u8d70\u3002\u201d\u5de6\u53f3\u770b\u4e86\u4e24\u773c\uff0c\u5429\u5490\u624b\u4e0b\u7684\u5e72\u8b66\uff0c\u201c\u90fd\u5e26\u4e0a\u8f66\u3002\u201d</p><p>\u3000\u3000\u4e00\u4e2a\u6c11\u8b66\u4f38\u624b\u8981\u8fc7\u6765\u63ea\u5f20\u606a\u7684\u80f3\u81c2\uff0c\u4f55\u7eaa\u4e91\u4f38\u624b\u4e00\u6325\uff1a\u201c\u8fd9\u4e2a\u6211\u6765\uff0c\u4f60\u4eec\u5148\u628a\u4e09\u4e2a\u97e9\u56fd\u4f6c\u5148\u5e26\u4e0a\u8f66\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u60f3\u7740\u8981\u5c06\u674e\u5728\u6d19\u90a3\u5f20\u540d\u7247\u4e22\u51fa\u6765\uff0c\u53ea\u8981\u8fd9\u4ef6\u4e8b\u80fd\u8feb\u4f7f\u674e\u5728\u6d19\u5c48\u670d\u5c31\u53ef\u4ee5\u4e86\uff0c\u8003\u8651\u5230\u56fd\u5185\u7684\u653f\u6cbb\u73af\u5883\u4ee5\u53ca\u76f8\u5173\u5b98\u5458\u53ef\u80fd\u7684\u6001\u5ea6\uff0c\u5c31\u662f\u8fd9\u65f6\u5019\u63ed\u9732\u674e\u5728\u6d19\u7684\u8eab\u4efd\uff0c\u4e5f\u4e0d\u5927\u53ef\u80fd\u95f9\u51fa\u591a\u5927\u7684\u5f71\u54cd\uff0c\u6d88\u606f\u603b\u53ef\u4ee5\u4e8b\u540e\u7531\u9a6c\u83b2\u8857\u6d3e\u51fa\u6240\u7684\u6c11\u8b66\u5634\u91cc\u4f20\u51fa\u6765\u3002</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u966a\u7740\u5f20\u606a\u3001\u5b59\u9759\u6aac\u4e0b\u697c\u68af\uff0c\u4e0e\u5f20\u606a\u79bb\u5f97\u8fd1\uff0c\u5c0f\u58f0\u95ee\u4ed6\uff1a\u201c\u8be5\u4e0d\u4f1a\u770b\u4e0a\u90a3\u4e2a\u97e9\u56fd\u5973\u4eba\u4e86\u5427\uff1f\u6211\u597d\u4e0d\u5bb9\u6613\u5feb\u71ac\u5230\u526f\u5c40\u7684\u4f4d\u5b50\uff0c\u53ea\u76fc\u671b\u60a8\u8001\u4e0d\u8981\u4e00\u5c0f\u5fc3\u53c8\u628a\u6211\u8fd9\u4e2a\u4f4d\u5b50\u7ed9\u6574\u6ca1\u4e86\u3002\u201d</p><p>\u3000\u3000\u4ec0\u4e48\u8ddf\u4ec0\u4e48\uff1f\u4e00\u8154\u70ed\u6795\uff0c\u7ed9\u4ed6\u6c61\u8511\u6210\u8fd9\u6837\uff0c\u5f20\u606a\u771f\u662f\u90c1\u95f7\uff0c\u6325\u4e86\u6325\u624b\u8ba9\u4f55\u7eaa\u4e91\u8be5\u5e72\u561b\u5e72\u561b\u53bb\u3002\u5b59\u9759\u6aac\u5728\u4e00\u65c1\u5403\u5403\u7684\u7b11\u7740\uff0c\u5634\u91cc\u8fd8\u7ee7\u7eed\u6c61\u8511\u5f20\u606a\uff1a\u201c\u5c31\u662f\u8fd9\u6837\u7684\uff0c\u8fd9\u5c0f\u5b50\u5c31\u662f\u597d\u8272\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5b59\u9759\u6aac\u5148\u5750\u5230\u8b66\u8f66\u91cc\uff0c\u5c31\u5750\u5728\u674e\u5728\u6d19\u4ed6\u4eec\u4e09\u4eba\u5bf9\u9762\uff0c\u773c\u775b\u5374\u7785\u7740\u90a3\u4e2a\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u770b\uff0c\u771f\u4e0d\u770b\u51fa\u90a3\u5f20\u63a5\u8fd1\u65e0\u7455\u7684\u8138\u6709\u6574\u8fc7\u5bb9\u7684\u75d5\u8ff9\uff1b\u597d\u5427\uff0c\u4e5f\u4e0d\u80fd\u4e0d\u627f\u8ba4\u97e9\u56fd\u5973\u4eba\u5f53\u4e2d\u4e5f\u6709\u5929\u751f\u4e3d\u8d28\u3001\u7eaf\u5929\u7136\u7684\u5c24\u7269\u5b58\u5728\u3002\u53ea\u662f\u4e0d\u6653\u5f97\u5979\u90a3\u5bf9\u8f66\u524d\u706f\u4f3c\u7684\u5927\u80f8\u662f\u600e\u4e48\u957f\u6210\u7684\uff0c\u5f20\u606a\u5fc3\u60f3\u5927\u6982\u4e5f\u4e0d\u4f1a\u6709\u54ea\u4e2a\u6b63\u5e38\u7684\u5973\u4eba\u4f1a\u5c06\u81ea\u5df1\u6709\u80f8\u90e8\u9686\u5230\u5982\u6b64\u58ee\u89c2\u7684\u7a0b\u5ea6\uff0c\u591a\u534a\u4e5f\u662f\u81ea\u7136\u751f\u6210\u7684\u3002\u771f\u662f\u81ea\u7136\u751f\u6210\uff0c\u90a3\u5c31\u66f4\u5947\u5999\u4e86\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u674e\u5728\u6d19\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4e25\u5389\u7684\u559d\u65a5\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\uff0c\u201d\u5f20\u606a\u6487\u7740\u5634\u8f7b\u8511\u7684\u7b11\u4e86\u7b11\uff0c\u771f\u8981\u5728\u8b66\u8f66\u91cc\u628a\u8fd9\u5c0f\u5b50\u7ed9\u6253\u4f24\u4e86\uff0c\u81ea\u5df1\u4e5f\u8981\u5f04\u51fa\u4e9b\u4f24\u75d5\u51fa\u6765\u624d\u80fd\u63a9\u4eba\u8033\u76ee\u3002\u5f20\u606a\u4e0d\u5c51\u505a\u8fd9\u79cd\u65e0\u804a\u7684\u4e8b\u60c5\uff0c\u5bf9\u674e\u5728\u6d19\u6311\u8845\u7684\u773c\u795e\u89c6\u82e5\u65e0\u7779\u3002\u5012\u662f\u97e9\u56fd\u5927\u80f8\u7f8e\u4eba\u6b64\u65f6\u62ff\u97e9\u8bed\u671d\u674e\u5728\u6d19\u62b1\u6028\uff1a\u201c\u4f60\u603b\u662f\u8981\u60f9\u662f\u751f\u975e\uff0c\u8fd9\u4ef6\u4e8b\u603b\u662f\u4f60\u6709\u9519\u5728\u5148\uff0c\u4f60\u8ddf\u4ed6\u4eec\u9053\u6b49\uff0c\u6211\u53ef\u4e0d\u60f3\u5230\u5efa\u90ba\u7684\u7b2c\u4e00\u5929\u8fd8\u8981\u53bb\u8b66\u5bdf\u5c40\u4f53\u9a8c\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u4ec0\u4e48\uff0c\u4f60\u8981\u8ddf\u6211\u4eec\u5728\u540c\u4e00\u7acb\u573a\u4e0a\u3002\u201d\u674e\u5728\u6d19\u538b\u4f4e\u7740\u58f0\u97f3\u7533\u65a5\uff0c\u795e\u8272\u5374\u66f4\u52a0\u4e25\u5389\u3002</p><p>\u3000\u3000\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u4e00\u8138\u59d4\u5c48\u7684\u4f4e\u7740\u5934\u4e0d\u8bf4\u8bdd\uff0c\u6709\u7740\u5a07\u655b\u7684\u695a\u695a\u53ef\u601c\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6400\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u5634\u5df4\u9644\u5728\u5f20\u606a\u7684\u8033\u8fb9\u95ee\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8b66\u8f66\u7684\u540e\u8f66\u53a2\u91cc\u5c31\u90a3\u4e48\u5927\u70b9\u7a7a\u95f4\uff0c\u5f20\u606a\u62ac\u811a\u80fd\u8e39\u674e\u5728\u6d19\u7684\u8138\u4e0a\u53bb\uff0c\u5b59\u9759\u6aac\u58f0\u97f3\u5f88\u5c0f\uff0c\u5bf9\u9762\u7684\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u770b\u7740\u5f20\u606a\uff0c\u62ff\u97e9\u8bed\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u4f60\u80fd\u542c\u61c2\u97e9\u8bed\uff0c\u4e5f\u5e0c\u671b\u4f60\u4e0d\u8981\u987a\u4fbf\u8f6c\u8ff0\u522b\u4eba\u7684\u8bdd\uff0c\u597d\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4fa7\u8fc7\u5934\uff0c\u7b11\u7740\u8ddf\u5b59\u9759\u6aac\u8bf4\uff1a\u201c\u4eba\u5bb6\u4e0d\u8ba9\u544a\u8bc9\u4f60\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u7741\u773c\u561f\u7740\u5634\u3002</p><p>\u3000\u3000\u201c\u5582\uff0c\u201d\u5f20\u606a\u62ff\u751f\u6da9\u7684\u97e9\u8bed\u62db\u547c\u5bf9\u9762\u7684\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\uff0c\u201c\u4f60\u53eb\u674e\u5de7\u8d1e\uff1f\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5de7\u8d1e\u662f\u5bb6\u91cc\u4eba\u79f0\u5524\u5979\u7684\u540d\u5b57\uff0c\u5979\u8138\u8f6c\u5f00\u770b\u5411\u522b\u5904\uff0c\u5373\u4f7f\u6028\u6068\u5802\u5144\u65e0\u793c\u5728\u5148\uff0c\u4e5f\u6ca1\u6709\u9053\u7406\u8fd9\u65f6\u5019\u7406\u4f1a\u5f20\u606a\u7684\u642d\u8baa\uff1b\u518d\u8bf4\u5f20\u606a\u7684\u773c\u795e\u4e5f\u591f\u65e0\u793c\u7684\uff0c\u5979\u8fd9\u4e48\u60f3\u7740\uff0c\u53c8\u53cc\u81c2\u62ac\u8d77\u6765\u62b1\u7740\u80f8\u90e8\u906e\u63a9\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u4e0d\u8010\u70e6\u7684\u95ee\u5750\u5728\u8f66\u5c3e\u7684\u6c11\u8b66\uff1a\u201c\u600e\u4e48\u8fd8\u4e0d\u5f00\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u6025\u4ec0\u4e48\uff1f\u201d\u6709\u4f55\u7eaa\u4e91\u505a\u793a\u8303\uff0c\u9a6c\u83b2\u8857\u7684\u6c11\u8b66\u80c6\u5b50\u90fd\u5927\u4e86\u8d77\u6765\uff0c\u4e0d\u8010\u70e6\u7684\u77aa\u4e86\u674e\u5728\u6d19\u4e00\u773c\uff0c\u201c\u5c31\u4f60\u5c0f\u5b50\u4e8b\u591a\uff0c\u4e0d\u8981\u7b49\u7b14\u5f55\u90fd\u505a\u597d\u624d\u80fd\u8d70\uff1f\u201d</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u4e0e\u4e24\u540d\u6c11\u8b66\u5728\u97e9\u56fd\u9910\u5385\u5c06\u51e0\u540d\u89c1\u8bc1\u4eba\u627e\u8fc7\u6765\u505a\u7b14\u5f55\uff0c\u603b\u8981\u5148\u5750\u5b9e\u674e\u5728\u6d19\u4ed6\u4eec\u6709\u8fb1\u9a82\u7684\u884c\u4e3a\u5728\u5148\u3002</p><p>\u3000\u3000\u8d76\u5de7\u5f53\u65f6\u5e2e\u5f20\u606a\u4ed6\u4eec\u70b9\u9910\u7684\u4f8d\u5e94\u751f\u662f\u5e97\u4e3b\u62db\u6765\u7684\u4e00\u540d\u671d\u9c9c\u65cf\u5973\u5b69\uff0c\u80fd\u542c\u5f97\u61c2\u97e9\u8bed\uff0c\u4e5f\u89c1\u8bc1\u4e86\u4e8b\u60c5\u53d1\u751f\u7684\u5168\u90e8\u7ecf\u8fc7\uff0c\u5979\u7684\u8bc1\u8a00\u6700\u6709\u529b\u4e86\uff0c\u62ff\u5230\u5979\u7684\u8bc1\u8bcd\uff0c\u4f55\u7eaa\u4e91\u624d\u8f7b\u677e\u4e0b\u6765\uff0c\u8fd9\u4ef6\u4e8b\u5230\u73b0\u5728\u5c31\u7b80\u5355\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u8f66\u53a2\u91cc\u7b49\u7740\u4e5f\u6709\u4e9b\u4e0d\u8010\u70e6\uff0c\u4e0d\u8fc7\u4e5f\u8981\u4f55\u7eaa\u4e91\u8fd9\u65f6\u5019\u5c06\u5de5\u4f5c\u505a\u7ec6\u4e86\uff0c\u514d\u5f97\u522b\u4eba\u62ff\u5e3d\u5b50\u6765\u6263\uff0c\u627e\u4e0d\u5230\u5408\u9002\u7684\u4e1c\u897f\u62b5\u6321\u3002</p><p>\u3000\u3000\u542c\u7740\u5916\u9762\u7684\u52a8\u9759\uff0c\u5df2\u7ecf\u805a\u4e86\u597d\u4e9b\u5b66\u751f\u8fc7\u6765\u770b\u70ed\u95f9\uff0c\u53c8\u6709\u4e00\u9635\u9a9a\u4e71\uff0c\u542c\u7740\u6709\u4eba\u8d70\u8fd1\u8b66\u8f66\uff0c\u8ddf\u5b88\u5728\u8b66\u8f66\u5916\u9762\u7684\u6c11\u8b66\u8bf4\u8bdd\uff0c\u8bed\u6c14\u5f88\u4e0d\u53cb\u597d\uff0c\u51e0\u4e4e\u662f\u62ff\u6559\u8bad\u7684\u8bed\u6c14\u5728\u5927\u543c\u5927\u53eb\uff0c\u5f20\u606a\u4ed6\u4eec\u5728\u8f66\u53a2\u91cc\u90fd\u542c\u5f97\u4e00\u6e05\u4e8c\u695a\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u4e1c\u5927\u5916\u4e8b\u5904\u7684\u4f55\u6653\u5149\u526f\u5904\u957f\uff0c\u542c\u8bf4\u6211\u4eec\u5b66\u6821\u4e00\u540d\u97e9\u56fd\u7c4d\u7684\u7559\u5b66\u751f\u7ed9\u4f60\u4eec\u6263\u8d77\u6765\u4e86\u3002\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4f60\u4eec\u4e0d\u4f1a\u5148\u8c03\u67e5\u6e05\u695a\uff0c\u4e00\u5b9a\u8981\u5148\u6263\u4eba\u5417\uff1f\u5f04\u51fa\u56fd\u9645\u7ea0\u7eb7\u600e\u4e48\u529e\uff0c\u4f60\u4eec\u8d1f\u5f97\u8d77\u8d23\u4efb\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f55\u8001\u5e08\uff0c\u6211\u4eec\u5728\u8f66\u91cc\u2026\u2026\u201d\u674e\u5728\u6d19\u65c1\u8fb9\u7684\u97e9\u56fd\u9752\u5e74\u542c\u5230\u5916\u8fb9\u7684\u58f0\u97f3\uff0c\u671d\u5916\u9762\u558a\u9053\uff0c\u201c\u4f55\u8001\u5e08\uff0c\u6211\u662f\u91d1\u5357\u52c7\uff0c\u6211\u4eec\u90fd\u5728\u8f66\u91cc\uff0c\u6253\u6211\u4eec\u7684\u5b66\u751f\u4e5f\u5728\u8f66\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5c06\u8b66\u8f66\u6253\u5f00\uff0c\u6211\u5148\u5c06\u8fd9\u540d\u7559\u5b66\u751f\u8ddf\u4ed6\u7684\u4e24\u540d\u670b\u53cb\u5e26\u8d70\u2026\u2026\u201d\u8b66\u8f66\u5c3e\u95e8\u7ed9\u4e00\u4e2a\u7a7f\u897f\u670d\u7684\u4e2d\u5e74\u4eba\u9aa4\u7136\u62c9\u5f00\uff0c\u8f66\u5916\u5b88\u7740\u6c11\u8b66\u6ca1\u6765\u5f97\u53ca\u5236\u6b62\u4ed6\uff0c\u8fd9\u65f6\u5019\u4f55\u7eaa\u4e91\u62ff\u5230\u89c1\u8bc1\u4eba\u7684\u7b14\u5f55\u6324\u51fa\u97e9\u56fd\u9910\u5385\uff0c\u8d70\u4e86\u8fc7\u6765\uff0c\u559d\u65a5\u9053\uff1a\u201c\u5582\uff0c\u5582\uff0c\u4f60\u662f\u8c01\uff1f\u201d</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u5916\u4e8b\u5904\u526f\u5904\u957f\u4f55\u6653\u5149\u6ca1\u6709\u7406\u4f1a\u4f55\u7eaa\u4e91\u5728\u540e\u9762\u558a\u53eb\uff0c\u722c\u4e0a\u8b66\u8f66\uff0c\u89c6\u7ebf\u626b\u8fc7\u5f20\u606a\u3001\u5b59\u9759\u6aac\uff0c\u5f88\u4e0d\u5ba2\u6c14\u7684\u6559\u8bad\u9053\uff1a\u201c\u4f60\u4eec\u662f\u4e1c\u5927\u7684\u5b66\u751f\uff1f\u4f60\u4eec\u7adf\u7136\u6709\u80c6\u5b50\u8ddf\u52a8\u4eba\u6253\u4eba\u4e86\uff0c\u5403\u4e86\u8c79\u5b50\u80c6\u4e86\uff0c\u4f60\u4eec\u8fd9\u662f\u7834\u574f\u56fd\u9645\u5173\u7cfb\uff0c\u4f60\u4eec\u7684\u884c\u4e3a\u4f1a\u8ba9\u56fd\u9645\u53cb\u4eba\u5bf9\u4e2d\u56fd\u5bd2\u5fc3\uff0c\u4e0d\u6015\u5b66\u6821\u5c06\u4f60\u4eec\u5f00\u9664\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7709\u5934\u5fae\u5fae\u4e00\u76b1\u7740\uff0c\u6700\u770b\u4e0d\u60ef\u8fd9\u79cd\u4e00\u770b\u5230\u5916\u56fd\u4eba\u5c31\u8111\u5b50\u53d1\u70ed\u4ee5\u4e3a\u662f\u81ea\u5df1\u7956\u5b97\u6765\u4e86\u9a82\u4e0d\u5f97\u6253\u4e0d\u5f97\u8fd8\u4e71\u6263\u5e3d\u5b50\u7684\u5bb6\u4f19\uff0c\u770b\u7740\u4f55\u7eaa\u4e91\u4ece\u540e\u9762\u8d76\u8fc7\u6765\uff0c\u6de1\u6de1\u8bf4\u9053\uff1a\u201c\u4f60\u5c06\u4ed6\u62d6\u4e0b\u53bb\u3002\u201d</p><p>\u3000\u3000\u4f55\u7eaa\u4e91\u5f53\u771f\u4e0d\u5ba2\u6c14\uff0c\u4e00\u628a\u6293\u5728\u4f55\u6653\u5149\u7684\u88e4\u8170\u5e26\u4e0a\u731b\u7684\u4ece\u8f66\u4e0a\u62fd\u4e0b\u6765\uff0c\u4f55\u6653\u5149\u4e0d\u6ce8\u610f\uff0c\u80f3\u818a\u8098\u513f\u731b\u7684\u649e\u5230\u8f66\u53a2\u5e95\u5ea7\uff0c\u5dee\u70b9\u95e8\u7259\u78d5\u6389\u4e24\u9897\uff0c\u8e09\u8dc4\u7740\u5dee\u70b9\u6454\u5728\u5730\u4e0a\uff0c\u72fc\u72c8\u4e0d\u582a\u3002\u4f55\u7eaa\u4e91\u8bad\u65a5\u9053\uff1a\u201c\u4f60\u518d\u59a8\u788d\u6211\u4eec\u6267\u884c\u8b66\u52a1\uff0c\u5c0f\u5fc3\u6211\u5c06\u4f60\u94d0\u8d77\u6765\u3002\u201d\u53c8\u56de\u8fc7\u5934\u6765\u5c06\u8b66\u8f66\u7684\u8f66\u7ed9\u5173\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u4e1c\u5927\u5916\u4e8b\u5904\u7684\u4f55\u6653\u5149\u526f\u5904\u957f\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\u4f60\u4eec\u8fd9\u4e48\u91ce\u86ee\u6267\u6cd5\u4f1a\u5f15\u8d77\u5916\u4e8b\u7ea0\u7eb7\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5988\u7684\uff0c\u4f60\u54ea\u53ea\u773c\u775b\u770b\u89c1\u6211\u4eec\u91ce\u86ee\u6267\u6cd5\u4e86\u2026\u2026\u201d\u4f55\u7eaa\u4e91\u5fcd\u5230\u73b0\u5728\uff0c\u8d76\u5de7\u53c8\u6709\u8fd9\u5bb6\u4f19\u4e0d\u77e5\u6b7b\u6d3b\u649e\u4ed6\u67aa\u53e3\u4e0a\uff0c\u5728\u8f66\u5916\u4e5f\u5fcd\u4e0d\u4f4f\u66b4\u4e86\u7c97\u53e3\uff0c\u201c\u8001\u5b50\u5728\u5bcc\u6625\u9601\u94d0\u4e86\u4e09\u5341\u591a\u4e2a\u65e5\u672c\u9b3c\u5b50\u3001\u5341\u591a\u4e2a\u97e9\u56fd\u68d2\u5b50\uff0c\u4e5f\u6ca1\u89c1\u4ec0\u4e48\u5916\u4e8b\u7ea0\u7eb7\uff0c\u8001\u5b50\u662f\u4f60\u552c\u5927\u7684\uff01\u53eb\u4f60\u4ec0\u4e48\u5f20\u5904\u957f\u8fc7\u6765\uff0c\u6216\u8005\u53eb\u4f60\u4eec\u4e1c\u5927\u7684\u6821\u9886\u5bfc\u8fc7\u6765\uff1b\u4f60\uff0c\u6055\u4e0d\u63a5\u5f85\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5728\u8f66\u53a2\u91cc\u542c\u4e86\u76f4\u597d\u7b11\uff0c\u770b\u7740\u5bf9\u9762\u7684\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u8fd9\u4e24\u4e2a\u97e9\u56fd\u68d2\u5b50\uff0c\u60f3\u5fc5\u4ed6\u4eec\u5230\u5efa\u90ba\u6765\u4e5f\u542c\u8bf4\u8fc7\u5bcc\u6625\u9601\u4e70\u6625\u4e8b\u4ef6\uff0c\u4ed6\u4eec\u8fd9\u65f6\u7684\u8868\u60c5\u8fd8\u771f\u662f\u6709\u8da3\u3002</p></article>"}}}]);
