(window.webpackJsonp=window.webpackJsonp||[]).push([[846],{869:function(p){p.exports={chapterTitle:null,title:"\u7b2c843\u7ae0 \u5206\u6b67",content:"<article><h2>\u7b2c843\u7ae0 \u5206\u6b67</h2><p>\u3000\u3000\u8fd9\u5e74\u5934\uff0c\u4efb\u4f55\u957f\u811a\u7684\u52a8\u7269\u90fd\u6ca1\u6709\u6d88\u606f\u8dd1\u5f97\u5feb\uff0c\u5728\u5efa\u90ba\u5e02\u63a5\u5230\u901a\u77e5\u7684\u7b2c\u4e8c\u5929\uff0c\u4e09\u4e95\u643a\u624bNEC\u7535\u5b50\u6b32\u5411\u534e\u590f\u7535\u5b50\u63d0\u4f9b\u6280\u672f\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u6676\u5706\u5382\u65b9\u6848\u7684\u4e00\u4e9b\u7ec6\u8282\u5c31\u4f20\u5230\u76f8\u5173\u4eba\u7684\u8033\u6735\u91cc\u3002</p><p>\u3000\u3000\u6682\u5b9a\u540d\u4e3a\u534e\u590fNEC\u7684\u6676\u5706\u5382\u9879\u76ee\uff0c\u7531NEC\u6301\u80a1\u4e0d\u8d85\u8fc730\uff05\uff0c\u63d0\u4f9b0.35\u5fae\u7c73\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\uff0c\u9879\u76ee\u6240\u9700\u7684\u5de5\u7a0b\u6280\u672f\u7ba1\u7406\u4eba\u5458\u90fd\u5168\u90e8\u7531NEC\u4ece\u65e5\u672c\u5de5\u5382\u6d3e\u9063\uff0c\u5e76\u7531NEC\u7535\u5b50\u627f\u62c5\u9879\u76ee\u5efa\u6210\u540e\u5168\u90e8\u7684\u4ea7\u54c1\u9500\u552e\u5de5\u4f5c\uff0c\u800c\u534e\u590f\u7535\u5b50\u4e0e\u56fd\u5185\u76f8\u5173\u90e8\u95e8\u63d0\u4f9b\u9879\u76ee\u5efa\u6210\u6240\u9700\u7684\u8d44\u91d1\u4ee5\u53ca\u5176\u4ed6\u5fc5\u5907\u6761\u4ef6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e09\u4e95\u8d64\u88f8\u88f8\u7684\u9732\u51fa\u4ed6\u4eec\u610f\u56fe\u5e72\u6d89516\u6848\u7684\u5ce5\u5d58\u7528\u5fc3\uff0c\u867d\u7136\u4f1a\u8bf1\u4f7f\u4e00\u4e9b\u4eba\u4e89\u5148\u6050\u540e\u7684\u53bb\u4e89\u98df\u4ed6\u4eec\u629b\u51fa\u7684\u8bf1\u9975\uff0c\u4f46\u540c\u6837\u4e5f\u4f1a\u5f15\u8d77\u53e6\u4e00\u4e9b\u4eba\u7684\u4e0d\u6ee1\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u968f\u7236\u4eb2\u56de\u5230\u5317\u4eac\u624d\u77e5\u9053\u534e\u590fNEC\u5408\u8d44\u9879\u76ee\u7684\u8be6\u60c5\uff0c\u62cd\u8d77\u684c\u5b50\u9a82\u5a18\uff1a\u201c\u8fd9\u4e9b\u4eba\u8fd8\u6709\u70b9\u9aa8\u6c14\u6ca1\u6709\uff0c\u522b\u4eba\u4e22\u6839\u9aa8\u5934\u5c31\u6251\u4e0a\u53bb\u6447\u5c3e\u4e5e\u601c\uff1f\u201d</p><p>\u3000\u3000\u5d14\u6587\u6bc5\u5fae\u8e59\u7740\u7709\u5934\uff0c\u5fc3\u91cc\u5bf9\u6b64\u867d\u7136\u4e5f\u5927\u4e3a\u4e0d\u6ee1\uff0c\u5374\u4e0d\u4f1a\u8f7b\u6613\u7684\u559c\u6012\u4e8e\u8272\u3002</p><p>\u3000\u3000\u6709\u4e9b\u4eba\u5c06\u5229\u76ca\u4ea4\u6362\u770b\u5f97\u5f88\u91cd\uff0c\u8ba4\u4e3a\u53ea\u8981\u80fd\u6362\u53d6\u5728\u6676\u5706\u9879\u76ee\u4e0a\u7684\u5229\u76ca\uff0c\u5728\u5176\u4ed6\u5730\u65b9\u653e\u5f03\u4e00\u4e9b\u539f\u5219\u3001\u727a\u7272\u4e00\u4e9b\u5229\u76ca\u662f\u503c\u5f97\u7684\u2014\u2014\u800c\u4e14\u8fd9\u79cd\u60f3\u6cd5\u5728\u90e8\u59d4\u91cc\u8fd8\u76f8\u5f53\u6709\u5e02\u573a\uff0c\u4e09\u4e95\u4e5f\u662f\u770b\u900f\u8fd9\u79cd\u5fc3\u601d\u3002</p><p>\u3000\u3000\u518d\u8bf4\u4e86\uff0c\u867d\u7136\u5927\u5bb6\u90fd\u4e00\u518d\u7684\u5f3a\u8c03\uff0c\u56fd\u5bb6\u5229\u76ca\u662f\u6700\u91cd\u8981\u7684\uff0c\u4f46\u662f\u5b9e\u9645\u64cd\u4f5c\u4e2d\uff0c\u5927\u5bb6\u90fd\u770b\u91cd\u5c0f\u5708\u5b50\u5185\u7684\u5229\u76ca\uff0c\u5730\u65b9\u6709\u5730\u65b9\u7684\u5229\u76ca\uff0c\u90e8\u59d4\u6709\u90e8\u59d4\u7684\u5229\u76ca\uff0c\u8fd9\u5c4a\u5e02\u957f\u6709\u8fd9\u5c4a\u5e02\u957f\u7684\u5229\u76ca\uff0c\u8fd9\u5c4a\u90e8\u957f\u6709\u8fd9\u5c4a\u90e8\u957f\u7684\u5229\u76ca\uff0c\u5f88\u5c11\u6709\u4eba\u4f1a\u5e2e\u522b\u4eba\u53bb\u8003\u8651\uff0c\u5e2e\u540e\u6765\u8005\u53bb\u8003\u8651\u3002</p><p>\u3000\u3000\u5d14\u6587\u6bc5\u624b\u6258\u7740\u4e0b\u9894\u60f3\u4e86\u7247\u523b\uff0c\u8ba9\u90c1\u6052\u79bb\u5f00\u81ea\u5df1\u7684\u4e66\u623f\uff0c\u62ff\u8d77\u7535\u8bdd\u62e8\u5230\u56fd\u9632\u90e8\u957f\u8096\u4f1f\u70c8\u4e0a\u5c06\u7684\u5bb6\u91cc\uff1a\u201c516\u6848\u7684\u4fa6\u67e5\u5de5\u4f5c\u53ef\u4ee5\u6682\u7f13\uff0c\u751a\u81f3\u7ed3\u6848\u65f6\u53ef\u4ee5\u8f7b\u63cf\u6de1\u5199\u4e00\u756a\uff0c\u7167\u987e\u67d0\u4e9b\u4eba\u5bf9516\u6848\u53ef\u80fd\u4f1a\u5f71\u54cd\u4e2d\u65e5\u6b63\u5e38\u7ecf\u6d4e\u5173\u7cfb\u7684\u62c5\u5fe7\uff0c\u4e0d\u8fc7\u6211\u4ee5\u4e3a\u9526\u6e56\u63d0\u51fa\u7684\u8ba1\u5212\u9700\u8981\u52a0\u901f\u8fdb\u884c\uff0c\u5927\u56fd\u6218\u7565\u9700\u65e9\u8c0b\u7b97\uff0c\u6211\u4eec\u4e0d\u80fd\u5341\u5e74\u3001\u4e8c\u5341\u5e74\u540e\u8fd8\u7ed9\u522b\u4eba\u634f\u4f4f\u4e03\u5bf8\uff01\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u30005\u670831\u65e5\uff0c\u53d1\u5c55\u8ba1\u5212\u59d4\u3001\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4ee5\u53ca\u534e\u590f\u7535\u5b50\u3001\u65e5\u672cNEC\u7535\u5b50\u3001\u4e09\u4e95\u7269\u4ea7\u5230\u4e2d\u6676\u5fae\u82af\u5efa\u8bbe\u5de5\u5382\u7684\u53c2\u89c2\u9635\u5bb9\u76f8\u5f53\u7684\u8c6a\u534e\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u803f\u91cd\u9633\u3001\u53d1\u5c55\u8ba1\u5212\u59d4\u6392\u540d\u7b2c\u4e00\u7684\u526f\u4e3b\u4efb\u6210\u664b\u6770\u4ee5\u53ca\u534e\u590f\u7535\u5b50\u7684\u8463\u4e8b\u957f\u845b\u5efa\u5fb7\u966a\u540cNEC\u7535\u5b50\u682a\u5f0f\u4f1a\u793e\u793e\u957f\u5c0f\u6cfd\u96c4\u53ca\u534a\u5bfc\u4f53\u90e8\u95e8\u9ad8\u5c42\u4ee5\u53ca\u4e09\u4e95\u7269\u4ea7\u4e2d\u56fd\u533a\u603b\u4ee3\u8868\u6c60\u4f50\u79c0\u85cf\u3001\u603b\u987e\u95ee\u5927\u91ce\u4fe1\u884c\u7b49\u4eba\u62b5\u8fbe\u5efa\u90ba\uff0c\u5730\u65b9\u4e0a\u7531\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u3001\u5e02\u957f\u8096\u660e\u5efa\u4ee5\u53ca\u5e02\u59d4\u5e38\u59d4\u3001\u9ad8\u65b0\u533a\u515a\u5de5\u59d4\u4e66\u8bb0\u738b\u7ef4\u5747\u5168\u7a0b\u966a\u540c\uff0c\u7701\u59d4\u4e66\u8bb0\u9676\u664b\u4e0e\u7701\u957f\u674e\u8fdc\u6e56\u53ea\u51fa\u5e2d\u7684\u63a5\u5f85\u4eea\u5f0f\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u5e74\uff0c\u9676\u664b\u4e60\u60ef\u5728\u5e55\u540e\u5bf9\u7ecf\u6d4e\u5de5\u4f5c\u8fdb\u884c\u8c0b\u7bc7\u5e03\u5c40\uff0c\u8bb8\u591a\u91cd\u8981\u7684\u63a5\u5f85\u6d3b\u52a8\uff0c\u591a\u662f\u51fa\u5e2d\u4e00\u4e0b\u5c31\u544a\u9000\uff0c\u8fd9\u6b21\u4e5f\u4e0d\u610f\u5916\uff1b\u53ea\u4e0d\u8fc7\u674e\u8fdc\u6e56\u5306\u5306\u51fa\u5e2d\u4e86\u63a5\u5f85\u4eea\u5f0f\u5c31\u544a\u8f9e\u79bb\u5f00\u8fd8\u662f\u9887\u4e3a\u4ee4\u4eba\u610f\u5916\u7684\u3002\u7edd\u5927\u591a\u6570\u4eba\u4ee5\u4e3a\u65e0\u8bba\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u5408\u8d44\u9879\u76ee\u6700\u7ec8\u4f1a\u4e0d\u4f1a\u5b9a\u5740\u5efa\u90ba\uff0c\u7531\u4e8e\u5efa\u90ba\u7ecf\u6d4e\u7ba1\u7406\u5de5\u4f5c\u7684\u72ec\u7acb\u6027\u8d28\uff0c\u674e\u8fdc\u6e56\u7684\u6de1\u51fa\u4e5f\u7b97\u4e0d\u4e0a\u592a\u610f\u5916\uff1b\u53ea\u6709\u5c11\u90e8\u5206\u4eba\u80fd\u731c\u5230\u674e\u8fdc\u6e56\u8fd9\u662f\u503e\u5411\u9526\u6e56\u7684\u8868\u6001\uff0c\u4e5f\u6216\u8bb8\u5bf9\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u5408\u8d44\u9879\u76ee\u524d\u5f71\u5e76\u4e0d\u770b\u597d\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c0f\u5b50\u6ca1\u6709\u51fa\u73b0\u554a\uff01\u201d\u6c60\u4f50\u79c0\u85cf\u4fa7\u7740\u5934\u4e0e\u674e\u5728\u6d19\u4f4e\u58f0\u8bf4\u9053\uff0c\u4ed6\u4eec\u4e24\u4eba\u90fd\u4e0d\u6ee1\u4e09\u5341\u5c81\uff0c\u5374\u5206\u522b\u662f\u4e09\u4e95\u4e0e\u4e09\u661f\u5728\u4e2d\u56fd\u7684\u9ad8\u5c42\u7ba1\u7406\u4eba\u5458\uff0c\u4ea4\u6e38\u4e5f\u662f\u4e2d\u65b9\u9ad8\u5c42\uff0c\u5728\u4e00\u7fa4\u4e2d\u8001\u5e74\u5b98\u5458\u7684\u56f4\u62e5\u4e4b\u4e2d\uff0c\u81ea\u7136\u5f88\u663e\u76ee\u3002\u6c60\u4f50\u79c0\u85cf\u4e0b\u98de\u673a\u5c31\u671f\u5f85\u518d\u770b\u5230\u90a3\u4e2a\u582a\u79f0\u62e5\u6709\u5947\u8ff9\u4e4b\u624b\u7684\u9752\u5e74\uff0c\u4e0a\u56de\u5728\u4e1c\u4eac\u7535\u5b50\u5c55\u524d\u5c06\u4ed6\u758f\u5ffd\u6389\uff0c\u771f\u662f\u5931\u7b56\uff01\u53ea\u4e0d\u8fc7\u4ece\u673a\u573a\u4e00\u76f4\u5230\u53c2\u89c2\u73b0\u573a\u90fd\u6ca1\u6709\u770b\u5230\u90a3\u4e2a\u4eba\u7684\u8eab\u5f71\u3002</p><p>\u3000\u3000\u201c\u4e2d\u56fd\u5185\u5730\u7684\u5a92\u4f53\u4e0a\u8fd8\u6ca1\u6709\u51fa\u73b0\u8fc7\u4ed6\u7684\u76f4\u63a5\u62a5\u9053\u2026\u2026\u201d\u674e\u5728\u6d19\u8bf4\u9053\uff0c\u4ed6\u60f3\u8d77\u4ed6\u7684\u53d4\u53d4\u4e09\u661f\u7684\u5e55\u540e\u638c\u6743\u8005\u674e\u5065\u7199\u6765\uff0c\u674e\u5065\u7199\u4e5f\u662f\u82e5\u6709\u53ef\u80fd\u5c31\u7edd\u4e0d\u4f1a\u5728\u516c\u5f00\u573a\u5408\u9732\u9762\u7684\u6027\u683c\uff0c\u518d\u8bf4\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u6676\u5706\u9879\u76ee\u9009\u5740\u5b9a\u5728\u5efa\u90ba\uff0c\u7edd\u5bf9\u4f1a\u6324\u538b\u4e2d\u6676\u5fae\u82af\u7684\u53d1\u5c55\u7a7a\u95f4\uff0c\u90a3\u5bb6\u4f19\u5927\u6982\u5df2\u7ecf\u7ed9\u8fd9\u6d88\u606f\u6c14\u70b8\u4e86\u809a\u5b50\u8eb2\u5230\u4e00\u65c1\u56fe\u4e2a\u773c\u4e0d\u89c1\u5fc3\u9759\u5427\uff1f</p><p>\u3000\u3000\u80fd\u8ba9\u90a3\u5bb6\u4f19\u5fc3\u91cc\u4e0d\u75db\u5feb\uff0c\u674e\u5728\u6d19\u603b\u662f\u4e0d\u4f1a\u653e\u5f03\u673a\u4f1a\u7684\uff0c\u4ed6\u53c8\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u6211\u60f3\u4e2d\u65b9\u7684\u5b98\u5458\u51fa\u9762\u9080\u8bf7\uff0c\u4ed6\u603b\u4e0d\u4f1a\u907f\u800c\u4e0d\u89c1\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u7709\u5934\u5fae\u626c\uff0c\u62db\u624b\u4e0e\u53d1\u5c55\u8ba1\u5212\u59d4\u7684\u7ec4\u7ec7\u5b98\u5458\u8bf4\u8d77\u6b64\u4e8b\uff0c\u845b\u5efa\u5fb7\u5728\u65c1\u8fb9\u542c\u4e86\u4e5f\u9887\u4e3a\u4e0d\u60a6\uff0c\u6311\u523a\u7684\u8bf4\u9053\uff1a\u201c\u803f\u90e8\u957f\u4e0e\u6210\u4e3b\u4efb\u90fd\u4eb2\u81ea\u5230\u5efa\u90ba\u6765\uff0c\u4ed6\u8eb2\u7740\u4e0d\u89c1\u9762\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u4e0d\u59a5\uff1f\u201d</p><p>\u3000\u3000\u7f57\u541b\u4f1a\u6b22\u8fce\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u6676\u5706\u9879\u76ee\u653e\u5728\u5efa\u90ba\uff0c\u4f46\u662f\u603b\u5f52\u8981\u7ef4\u62a4\u9526\u6e56\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u606a\u8ba1\u5212\u662f\u8981\u51fa\u5e2d\u7684\uff0c\u65b0\u829c\u90a3\u8fb9\u4e34\u65f6\u6709\u4e8b\uff0c\u6628\u5929\u591c\u91cc\u8d76\u53bb\u65b0\u829c\u4e86\uff0c\u8fd9\u4e8b\u662f\u4e0d\u662f\u4e8b\u5148\u8ddf\u803f\u90e8\u957f\u3001\u6210\u4e3b\u4efb\u6253\u8fc7\u62db\u547c\u4e86\uff1f\u201d</p><p>\u3000\u3000\u803f\u91cd\u9633\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u5173\u7d27\u7684\uff0c\u4ed6\u6ca1\u6709\u62d2\u7edd\u6211\u4eec\u53c2\u89c2\u4e2d\u6676\u5fae\u82af\u5c31\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u8138\u8272\u8baa\u7136\uff0c\u8fd9\u4e9b\u8bdd\u65e0\u5fae\u662f\u8bf4\u4ed6\u6839\u672c\u6ca1\u6709\u8d44\u683c\u8ba9\u5f20\u606a\u8ddf\u4ed6\u63d0\u524d\u6253\u62db\u547c\uff0c\u6311\u523a\u7684\u8bdd\u4e0d\u8fc7\u662f\u81ea\u627e\u6ca1\u8da3\uff1b\u4ed6\u603b\u4e0d\u80fd\u8bf4\u5f20\u606a\u662f\u6545\u610f\u8eb2\u5230\u65b0\u829c\u53bb\u7684\uff0c\u867d\u7136\u5927\u5bb6\u5fc3\u91cc\u90fd\u6e05\u695a\u5f20\u606a\u5c31\u662f\u6545\u610f\u8eb2\u5230\u65b0\u829c\u53bb\u7684\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u97e9\u56fd\u6709\u6676\u5706\u5382\uff0c\u4e0d\u8fc7\u5728\u5efa\u90ba\u6295\u8d44\u5efa\u8bbe\u4e86\u82af\u7247\u5c01\u88c5\u3001\u6210\u54c1\u68c0\u6d4b\u5de5\u5382\uff0c\u53ef\u4ee5\u8bf4\u662f\u6676\u5706\u5236\u9020\u7684\u540e\u6bb5\u5de5\u5e8f\uff0c\u8fd9\u4e5f\u662f\u674e\u5728\u6d19\u51fa\u73b0\u5728\u53c2\u89c2\u961f\u4f0d\u91cc\u7684\u76f4\u63a5\u7406\u7531\u3002\u4e09\u661f\u5728\u5efa\u90ba\u7684\u5de5\u5382\u53ef\u4ee5\u4e3a\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u5408\u8d44\u6676\u5706\u5de5\u5382\u63d0\u4f9b\u540e\u6bb5\u5c01\u88c5\u3001\u6210\u54c1\u68c0\u6d4b\u670d\u52a1\uff0c\u9526\u6e56\u5728\u6d77\u5dde\u6709\u82af\u7247\u5c01\u88c5\u3001\u68c0\u6d4b\u5de5\u5382\uff0c\u4e09\u65b9\u7684\u7ade\u4e89\u5173\u7cfb\u7b97\u662f\u7ea0\u7f20\u5230\u7535\u5b50\u4ea7\u4e1a\u94fe\u7684\u4e2d\u7aef\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u867d\u7136\u77e5\u9053\u6765\u610f\u4e0d\u5584\uff0c\u4e2d\u6676\u5fae\u82af\u8fd9\u8fb9\u4e5f\u4e0d\u80fd\u5c06\u654c\u610f\u660e\u6643\u6643\u7684\u6446\u5728\u8138\u4e0a\uff0c\u6bd5\u7adf\u8981\u7167\u987e\u90e8\u59d4\u9886\u5bfc\u7684\u60c5\u7eea\uff0c\u518d\u8bf4\u9526\u6e56\u4e0e\u803f\u91cd\u9633\u7684\u5173\u7cfb\u6b63\u5728\u6539\u5584\u4e2d\uff0c\u5f53\u7136\u8fd9\u79cd\u6539\u5584\u4e5f\u662f\u6709\u9650\u7684\u3002</p><p>\u3000\u3000\u5728\u4fe1\u606f\u4ea7\u4e1a\u6276\u6301\u56fd\u8d44\u8fd8\u662f\u6c11\u8d44\u7684\u95ee\u9898\uff0c\u803f\u91cd\u9633\u59cb\u7ec8\u503e\u5411\u524d\u8005\uff0c\u65e0\u8bba\u4ed6\u62c5\u4efb\u90ae\u7535\u90e8\u90e8\u957f\u8fd8\u662f\u6539\u4efb\u4fe1\u606f\u4ea7\u4e1a\u90e8\u90e8\u957f\uff0c\u4ed6\u90fd\u5927\u529b\u63a8\u52a8\u56fd\u6709\u8d44\u672c\u5728\u53ef\u80fd\u7684\u9886\u57df\u91cc\u5bf9\u4fe1\u606f\u4ea7\u4e1a\u5b9e\u73b0\u5784\u65ad\u7ecf\u8425\uff0c\u5728\u4e00\u4e9b\u4e0d\u5f97\u4e0d\u653e\u5f00\u7684\u9886\u57df\uff0c\u624d\u4f1a\u60f3\u5230\u8ba9\u56fd\u6709\u8d44\u672c\u4e0e\u6c11\u8425\u8d44\u672c\u516c\u5e73\u7ade\u4e89\u7684\u95ee\u9898\uff0c\u8fd9\u4e5f\u662f\u4ed6\u62c5\u4efb\u90ae\u7535\u90e8\u4ee5\u6765\u6700\u8ba9\u4eba\u57a2\u75c5\u7684\u5730\u65b9\uff0c\u8fde\u8d75\u6d4e\u4e1c\u603b\u7406\u5bf9\u4ed6\u7684\u4fdd\u5b88\u4e5f\u751a\u4e3a\u4e0d\u6ee1\uff0c\u4f46\u8fd9\u4e9b\u5e76\u4e0d\u5f71\u54cd\u4ed6\u7ee7\u7eed\u9886\u5bfc\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u4e0d\u8fc7\u8fd1\u671f\u6765\uff0c\u803f\u91cd\u9633\u5bf9\u9526\u6e56\u5728\u4fe1\u606f\u4ea7\u4e1a\u7684\u6770\u51fa\u8868\u73b0\u4e5f\u53d1\u51fa\u4e00\u4e9b\u80af\u5b9a\u7684\u58f0\u97f3\uff0c\u8fd9\u662f\u6b8a\u4e3a\u96be\u5f97\u7684\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u5728\u5efa\u90ba\uff0c\u5c31\u7531\u4ed6\u8ddf\u67f3\u5fd7\u6210\u51fa\u9762\u4ee3\u8868\u4e2d\u6676\u5fae\u82af\u51fa\u5e2d\u4e86\u63a5\u5f85\u6d3b\u52a8\u3002\u53ea\u4e0d\u8fc7\u54ea\u4e9b\u53ef\u4ee5\u53c2\u89c2\u7684\u3001\u54ea\u4e9b\u4e0d\u53ef\u4ee5\u53c2\u89c2\u7684\u3001\u54ea\u4e9b\u53ef\u4ee5\u8d70\u8fd1\u53c2\u89c2\u3001\u54ea\u4e9b\u8981\u7ad9\u5230\u4e8c\u5341\u7c73\u751a\u81f3\u4e94\u5341\u7c73\u5916\u53c2\u89c2\uff0c\u90fd\u4e8b\u5148\u4e0e\u90e8\u59d4\u65b9\u9762\u7ec4\u7ec7\u6b64\u6b21\u53c2\u89c2\u6d3b\u52a8\u7684\u8d1f\u8d23\u4eba\u8fdb\u884c\u6c9f\u901a\uff0c\u7edd\u4e0d\u4f1a\u5bb9\u5fcdNEC\u7535\u5b50\u3001\u4e09\u4e95\u7269\u4ea7\u7684\u4ee3\u8868\u7c4d\u6b64\u7aa5\u6d4b\u4e2d\u6676\u5fae\u82af\u7684\u5546\u4e1a\u673a\u5bc6\u3002</p><p>\u3000\u3000\u8d70\u9a6c\u89c2\u82b1\u7684\u53c2\u89c2\u8fc7\u6b63\u5728\u5efa\u8bbe\u4e2d\u7684\u5382\u533a\uff0c\u53c2\u89c2\u5382\u533a\u8fc7\u7a0b\u4e2d\uff0c\u67f3\u5fd7\u6210\u751a\u81f3\u6d3e\u51fa\u8bb8\u591a\u5458\u5de5\u76ef\u4f4f\u53c2\u89c2\u56e2\u7684\u6240\u6709\u6210\u5458\uff0c\u8ddf\u9632\u8d3c\u4f3c\u7684\uff0c\u66f4\u591a\u7684\u6d3b\u52a8\u662f\u5b89\u6392\u5728\u529e\u516c\u533a\u7684\u4f1a\u8bae\u5ba4\u91cc\u3002\u8bf7\u4e86\u4e00\u4e9b\u4e3b\u6d41\u5a92\u4f53\u7684\u8bb0\u8005\u5230\u573a\uff0c\u4e5f\u6295\u5176\u6240\u597d\u7684\u8bf7\u9886\u5bfc\u591a\u53d1\u8a00\u597d\u4e0a\u7535\u89c6\u3001\u4e0a\u62a5\u7eb8\uff0c\u90fd\u662f\u4e9b\u7a7a\u6d1e\u7684\u53d1\u5c55\u8a00\u8bba\uff0c\u4e0d\u8fc7\u5f88\u597d\u6253\u53d1\u65f6\u95f4\uff0c\u665a\u9910\u4ee5\u53ca\u665a\u9910\u540e\u7684\u4f11\u95f2\u6d3b\u52a8\u4e5f\u5b89\u6392\u5f97\u5f88\u5468\u5230\uff0c\u5c31\u662f\u4e0d\u80fd\u8ba9\u8fd9\u4e9b\u4eba\u770b\u5230\u5b9e\u8d28\u7684\u4e1c\u897f\uff0c\u4e00\u5929\u7684\u53c2\u89c2\u6d3b\u52a8\u5c31\u8fd9\u6837\u7ed9\u7cca\u5f04\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5efa\u90ba\u5e02\u91cc\u5b89\u6392\u53c2\u89c2\u56e2\u961f\u53c2\u89c2\u6570\u5b57\u957f\u5eca\u6784\u60f3\u6218\u7565\u542f\u52a8\u4ee5\u6765\u7684\u6210\u5c31\uff0c\u4e0b\u5348\u5c31\u4e3e\u884c\u6676\u5706\u9879\u76ee\u524d\u671f\u7684\u53ef\u884c\u6027\u7814\u8ba8\u4f1a\uff0c\u803f\u91cd\u9633\u70b9\u540d\u9080\u8bf7\u5b59\u5c1a\u4e49\u3001\u67f3\u5fd7\u6210\u4e5f\u53c2\u52a0\u4e0b\u5348\u7684\u7814\u8ba8\u4f1a\u3002</p><p>\u3000\u3000\u201c\u610f\u56fe\u660e\u663e\u3001\u52a8\u4f5c\u8fc5\u901f\uff0c\u5f20\u606a\u7684\u5224\u65ad\u5f88\u51c6\u554a\uff01\u201d\u5b59\u5c1a\u4e49\u5bf9\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u6b64\u6b21\u5408\u4f5c\u6709\u8fd9\u6837\u7684\u611f\u89e6\uff0c\u4ed6\u4e0e\u67f3\u5fd7\u6210\u867d\u8bf4\u4e00\u8d77\u8fc7\u6765\u53c2\u52a0\u8fd9\u4e2a\u7814\u7a76\u4f1a\uff0c\u5374\u6253\u5b9a\u4e3b\u610f\u5750\u5230\u4e0d\u60f9\u4eba\u77a9\u76ee\u7684\u89d2\u843d\u91cc\u4fdd\u6301\u6c89\u9ed8\uff0c\u5176\u4ed6\u4eba\u5927\u6982\u4e5f\u4e0d\u4f1a\u5f3a\u4eba\u6240\u96be\u8981\u4ed6\u4eec\u5728\u7814\u8ba8\u4f1a\u4e0a\u53d1\u8a00\uff0c\u5b59\u5c1a\u4e49\u4e0e\u67f3\u5fd7\u6210\u5728\u4e0b\u9762\u4f4e\u58f0\u8ba8\u8bba\u3002</p><p>\u3000\u3000\u201c\u4e09\u4e95\u7269\u4ea7\u771f\u8981\u7b97\u662f\u4e00\u53ea\u5f3a\u5927\u7684\u65e0\u5f62\u4e4b\u624b\u554a\uff0c\u6ca1\u6709\u4e09\u4e95\u7269\u4ea7\u7684\u76f4\u63a5\u63a8\u52a8\uff0c\u53ea\u6015\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u524d\u671f\u63a5\u89e6\u5c31\u8981\u62d6\u5ef6\u5927\u534a\u5e74\u7684\u65f6\u95f4\u3002\u201d\u67f3\u5fd7\u6210\u5fae\u5fae\u4e00\u53f9\uff0c\u4f5c\u4e3a\u56fd\u5185\u7b2c\u4e00\u5bb6\u6676\u5706\u9879\u76ee\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u524d\u671f\u7b79\u5907\u4e5f\u82b1\u4e86\u597d\u4e9b\u65f6\u95f4\uff0c\u201c\u4ed6\u4eec\u63a5\u4e0b\u6765\u7684\u63a8\u8fdb\u901f\u5ea6\u4f30\u8ba1\u4e5f\u4f1a\u6bd4\u6211\u4eec\u5feb\u8bb8\u591a\u3002\u201d</p><p>\u3000\u3000\u201c\u8c01\u8ba9\u6211\u4eec\u4e0d\u8bc6\u62ac\u4e3e\u62d2\u7edd\u56fd\u8d44\u5165\u80a1\u5462\uff1f\u201d\u5b59\u5c1a\u4e49\u8f7b\u7b11\u7740\uff0c\u201c\u4e09\u4e95\u7269\u4ea7\u8fd9\u6b21\u629b\u51fa\u8fd9\u7c92\u751c\u67a3\uff0c\u7684\u786e\u80fd\u5c06\u90a3\u4e9b\u4e2d\u95f4\u6d3e\u90fd\u62c9\u62e2\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u4f5c\u4e3a\u534e\u590f\u7535\u5b50\u7684\u6210\u5458\u4f01\u4e1a\u8054\u4fe1\u7535\u5b50\u7684\u603b\u88c1\u8096\u745e\u6c11\u4e5f\u5728\u6b64\u6b21\u7684\u53c2\u89c2\u9635\u5bb9\u4e4b\u5217\uff0c\u7814\u8ba8\u4f1a\u4e0a\uff0c\u4ed6\u5c31\u5750\u5728\u5b59\u5c1a\u4e49\u3001\u67f3\u5fd7\u6210\u65c1\u8fb9\uff0c\u542c\u7740\u4ed6\u4eec\u5728\u90a3\u91cc\u7a83\u7a83\u79c1\u8bed\uff0c\u542c\u4e0d\u6e05\u4ed6\u4eec\u5728\u8bf4\u4ec0\u4e48\u3002\u5012\u662f\u8fc7\u4e86\u7247\u523b\uff0c\u5b59\u5c1a\u4e49\u4e3b\u52a8\u627e\u4ed6\u8bf4\u8bdd\uff1a\u201c\u534e\u590f\u7535\u5b50\u8fd9\u6b21\u4e0eNEC\u5408\u8d44\u6676\u5706\u9879\u76ee\uff0c\u5927\u6982\u8981\u8054\u4fe1\u8fd9\u4e9b\u6210\u5458\u4f01\u4e1a\u5206\u644a\u6295\u8d44\u5427\uff1f\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u5fae\u5fae\u82e6\u7b11\uff0c\u9526\u6e56\u5bf9\u534e\u590f\u7535\u5b50\u7684\u60c5\u51b5\u4e86\u82e5\u6307\u638c\u3002</p><p>\u3000\u3000\u534e\u590f\u7535\u5b50\u5bf9\u65d7\u4e0b\u6210\u5458\u4f01\u4e1a\u7684\u6574\u5408\u8fd8\u6ca1\u6709\u6700\u7ec8\u5b8c\u6210\uff0c\u8d22\u6743\u4e0e\u6295\u8d44\u6743\u9650\u8fd8\u6ca1\u6709\u5f7b\u5e95\u7684\u6536\u4e0a\u53bb\uff0c\u8981\u4e0eNEC\u5408\u8d44\u5efa\u6676\u5706\u5382\uff0c\u6240\u9700\u8981\u7684\u8d44\u91d1\u603b\u91cf\u5e9e\u5927\uff0c\u867d\u7136\u4f1a\u901a\u8fc7\u94f6\u884c\u8d37\u6b3e\u89e3\u51b3\u5176\u4e2d\u7684\u5927\u90e8\u5206\uff0c\u4f46\u662f\u81ea\u8eab\u4e5f\u8981\u51c6\u5907\u4e00\u4e9b\u542f\u52a8\u8d44\u91d1\uff0c\u8fd9\u4e5f\u5c11\u8bf4\u8981\u5341\u4ebf\u4e8c\u5341\u4ebf\u7684\u8d44\u91d1\u3002\u845b\u5efa\u5fb7\u8fd9\u6b21\u5c06\u65d7\u4e0b\u8d22\u52a1\u5bbd\u88d5\u7684\u6210\u5458\u4f01\u4e1a\u8d1f\u8d23\u4eba\u90fd\u62c9\u4e86\u8fc7\u6765\uff0c\u5c31\u662f\u8981\u4ed6\u4eec\u4ee5\u6210\u5458\u4f01\u4e1a\u7684\u540d\u4e49\u5bf9\u9879\u76ee\u8fdb\u884c\u6ce8\u8d44\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u5bf9\u534e\u590f\u7535\u5b50\u77e5\u6839\u77e5\u5e95\uff0c\u77e5\u9053\u8981\u4e0d\u662f\u4ed7\u7740\u56fd\u8d44\u7684\u8eab\u4efd\uff0c\u8981\u4e0d\u662f\u4f9d\u8d56\u90e8\u59d4\u5728\u4ea7\u4e1a\u653f\u7b56\u4e0a\u5bf9\u534e\u590f\u7535\u5b50\u7684\u503e\u659c\uff0c\u534e\u590f\u7535\u5b50\u771f\u4e0d\u582a\u9526\u6e56\u4e00\u51fb\uff0c\u4f01\u4e1a\u7ba1\u7406\u3001\u6280\u672f\u521b\u65b0\u3001\u4eba\u624d\u5f15\u8fdb\u3001\u5e02\u573a\u8425\u9500\u3001\u54c1\u724c\u5efa\u8bbe\u7b49\u5404\u65b9\u9762\u7684\u6c34\u5e73\u90fd\u5dee\u592a\u8fdc\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u4fa7\u8fc7\u5934\u6765\uff0c\u95ee\u8096\u745e\u6c11\uff1a\u201c\u8096\u603b\u5bf9\u534e\u590f\u7535\u5b50\u4e0eNEC\u7684\u8fd9\u6b21\u5408\u4f5c\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u8096\u745e\u6c11\u4f9d\u65e7\u53ea\u80fd\u82e6\u7b11\uff0c\u5728\u884c\u5bb6\u9762\u524d\u65e0\u6cd5\u8bf4\u51a0\u5195\u5802\u7687\u7684\u5047\u8bdd\u5957\u8bdd\uff0c\u5fc3\u91cc\u7684\u771f\u5b9e\u60f3\u6cd5\u5374\u53c8\u600e\u4e48\u80fd\u8ba9\u7ade\u4e89\u5bf9\u624b\u77e5\u9053\uff0c\u751a\u81f3\u611f\u89c9\u5230\u67f3\u5fd7\u6210\u8fd9\u4e48\u95ee\u662f\u5728\u595a\u843d\u81ea\u5df1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u91cc\u4fe1\u4ea7\u90e8\u4e0e\u534e\u590f\u7535\u5b50\u5185\u90e8\u7684\u5b98\u5458\u5728\u5bbe\u9986\u91cc\u805a\u96c6\u5728\u4e00\u8d77\u8ba8\u8bba\u8fd9\u6b21\u7684\u5408\u4f5c\u9879\u76ee\uff0c\u8096\u745e\u6c11\u624d\u5c06\u4ed6\u7684\u610f\u89c1\u8bf4\u51fa\u6765\uff1a\u201c\u9879\u76ee\u6240\u9700\u8981\u7684\u5de5\u7a0b\u6280\u672f\u7ba1\u7406\u56e2\u961f\u90fd\u7531\u65e5\u65b9\u6d3e\u9063\uff0c\u5408\u4f5c\u8ba1\u5212\u4e2d\u53c8\u6ca1\u6709\u57f9\u517b\u56fd\u5185\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u6761\u6b3e\uff0c\u6240\u8c13\u7684\u63d0\u4f9b\u5b8c\u6574\u7684\u5236\u7a0b\u6280\u672f\u53ea\u662f\u4e00\u53e5\u7a7a\u8bdd\uff0c\u4e00\u65e6\u65e5\u65b9\u5c06\u5de5\u7a0b\u6280\u672f\u7ba1\u7406\u56e2\u961f\u8c03\u56de\u65e5\u672c\uff0c\u7559\u4e0b\u4e00\u644a\u6280\u672f\u6587\u4ef6\u53c8\u6709\u4ec0\u4e48\u7528\uff1f\u53e6\u5916\uff0c\u9879\u76ee\u5efa\u6210\u7684\u4ea7\u54c1\u9500\u552e\u90fd\u7531NEC\u8d1f\u8d23\uff0cNEC\u7535\u5b50\u5728\u65e5\u672c\u6709\u81ea\u5df1\u7684\u6676\u5706\u5382\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9879\u76ee\u5efa\u6210\u5c31\u4e0eNEC\u5728\u65e5\u672c\u7684\u6676\u5706\u5382\u5b58\u5728\u7ade\u4e89\u5173\u7cfb\uff0c\u6211\u4eec\u80fd\u6307\u671bNEC\u7684\u9500\u552e\u90e8\u95e8\u504f\u5411\u6211\u4eec\u5417\uff1f\u201d</p><p>\u3000\u3000\u867d\u7136\u8bf4\u4f1a\u524d\u8981\u5927\u5bb6\u76f4\u6292\u5df1\u89c1\uff0c\u90fd\u662f\u5185\u90e8\u5b98\u5458\uff0c\u4f46\u662f\u771f\u542c\u5230\u6709\u4eba\u63d0\u51fa\u4e0d\u540c\u610f\u89c1\uff0c\u845b\u5efa\u5fb7\u5fc3\u91cc\u8fd8\u662f\u5f88\u4e0d\u75db\u5feb\uff0c\u8fd9\u51e0\u4e4e\u4e3a\u4e3a\u5b98\u8005\u7684\u901a\u75c5\uff0c\u4ed6\u7565\u6c89\u7740\u8138\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u53ea\u5c06\u773c\u5149\u76ef\u5728\u4e0d\u597d\u7684\u65b9\u9762\u3002\u4e2d\u592e\u8981\u4e0a\u6676\u5706\u9879\u76ee\u662f\u4ece\u4e03\u5341\u5e74\u4ee3\u672b\u671f\u5c31\u5f00\u59cb\uff0c\u641e\u4e86\u4e09\u56de\uff0c\u4e09\u56de\u90fd\u4ee5\u5931\u8d25\u800c\u544a\u7ec8\uff0c\u4f46\u662f\u4e2d\u592e\u4ecd\u7136\u4e0d\u60dc\u6210\u672c\u7684\u8981\u4e0a\u6676\u5706\u9879\u76ee\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u96be\u602a\u6ca1\u6709\u4ece\u524d\u4e09\u6b21\u7684\u5931\u8d25\u91cc\u6ca1\u6709\u5438\u53d6\u8db3\u591f\u7684\u7ecf\u9a8c\u6559\u8bad\u5417\uff0c\u96be\u602a\u6ca1\u6709\u610f\u8bc6\u5230\u5176\u4e2d\u7684\u56f0\u96be\u5417\uff1f\u96c6\u6210\u7535\u8def\u82af\u7247\u5236\u9020\u662f\u7535\u5b50\u4ea7\u4e1a\u7684\u57fa\u77f3\uff0c\u5c31\u662f\u7838\u9505\u5356\u94c1\u4e5f\u8981\u641e\u3002\u867d\u7136\u8fd9\u6b21\u7684\u5408\u4f5c\u8fd8\u6709\u4e9b\u4e0d\u5c3d\u4eba\u610f\u7684\u5730\u65b9\uff0c\u4f46\u662f\u6bd4\u8d77\u4ee5\u524d\u51e0\u6b21\u4e0d\u592a\u6210\u529f\u7684\u5408\u4f5c\uff0c\u5df2\u7ecf\u6709\u4e86\u5f88\u5927\u7684\u8fdb\u6b65\u3002\u8fd9\u4e2a\u673a\u4f1a\u4e5f\u662f\u5f97\u4e4b\u4e0d\u6613\uff0c\u8981\u4e0d\u662f516\u6848\uff0c\u4e09\u4e95\u4e5f\u4e0d\u4f1a\u4e3b\u52a8\u793a\u597d\uff0c\u6211\u4eec\u8981\u6293\u4f4f\u8fd9\u4e2a\u673a\u4f1a\u3002\u6280\u672f\u3001\u7ba1\u7406\u56e2\u961f\u3001\u5e02\u573a\u9500\u552e\u90fd\u662f\u6211\u4eec\u7684\u5f31\u9879\u4e0d\u5047\uff0c\u4f46\u662f\u6211\u4eec\u4e0d\u8003\u8651\u65e5\u540e\u60f3\u65b9\u8bbe\u6cd5\u7684\u53bb\u8865\u8db3\uff0c\u96be\u9053\u6307\u671b\u4eba\u5bb6\u73b0\u5728\u5c31\u5168\u90e8\u767d\u7ed9\u6211\u4eec\u5417\uff1f\u4e2d\u592e\u51b3\u7b56\u7ec4\u5efa\u534e\u590f\u7535\u5b50\u4fe1\u606f\u4ea7\u4e1a\u96c6\u56e2\uff0c\u5c31\u662f\u5bc4\u671b\u6211\u4eec\u534e\u590f\u7535\u5b50\u80fd\u5728\u56fd\u5185\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u4e0a\u627f\u62c5\u5927\u7684\u8d23\u4efb\uff0c\u4e0d\u80fd\u56e0\u4e3a\u8981\u81ea\u5df1\u53bb\u514b\u670d\u4e00\u4e9b\u56f0\u96be\uff0c\u5c31\u6709\u6240\u754f\u60e7\uff0c\u773c\u775b\u5c31\u6d88\u6781\u7684\u7d27\u76ef\u5728\u4e0d\u597d\u7684\u5730\u65b9\u2026\u2026\u201d\u845b\u5efa\u5fb7\u8bdd\u91cc\u85cf\u5200\u5939\u5251\uff0c\u5c06\u8096\u745e\u6c11\u7684\u8d28\u7591\u53cd\u9a73\u4e86\u4e00\u901a\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u77a5\u773c\u770b\u7740\u803f\u91cd\u9633\u7684\u8138\u8272\u96be\u8fa8\u559c\u6012\uff0c\u4e0d\u8fc7\u845b\u5efa\u5fb7\u6697\u5939\u5200\u68cd\u7684\u8bdd\u8ba9\u4ed6\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u4e0b\u5b9a\u51b3\u5fc3\u8bf4\u51fa\u6765\uff1a\u201c\u56fd\u5185\u5df2\u6709\u4e2d\u6676\u5fae\u82af\uff0c\u6211\u4eec\u518d\u4e0eNEC\u5408\u8d44\u5efa0.35\u5fae\u7c73\u7684\u6676\u5706\u5382\u5c31\u6709\u91cd\u590d\u5efa\u8bbe\u4e4b\u5acc\u3002\u53e6\u5916\uff0c\u5c31\u73b0\u6709\u7684\u60c5\u51b5\u6765\u770b\uff0c\u4e2d\u6676\u5fae\u82af\u53d1\u5c55\u7684\u6761\u4ef6\u8981\u4f18\u8d8a\u5f97\u591a\uff0c\u4ed6\u4eec\u53bb\u5e74\u82b1\u4e86\u5927\u529b\u6c14\u4ece\u53f0\u79ef\u7535\u3001\u521b\u65b0\u3001\u5fb7\u4eea\u7b49\u6676\u5706\u5382\u4e00\u5171\u5f15\u8fdb\u6765\u8d85\u8fc7\u4e8c\u767e\u540d\u7684\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u6280\u672f\u4eba\u5458\uff0c\u8fd9\u4e48\u5e9e\u5927\u7684\u5de5\u7a0b\u6280\u672f\u7ba1\u7406\u56e2\u961f\u4fdd\u8bc1\u4e86\u4e2d\u6676\u5fae\u82af\u7684\u5efa\u8bbe\u4e0e\u5f00\u5de5\u9700\u8981\u2014\u2014NEC\u53ea\u540c\u610f\u6d3e\u906350\u4eba\u7684\u56e2\u961f\uff0c\u89c4\u6a21\u4e0a\u8981\u5c11\u8bb8\u591a\u3002\u4e2d\u6676\u5fae\u82af\u5728\u5de5\u5382\u5efa\u8bbe\u7684\u540c\u65f6\uff0c\u5c31\u5728\u6a61\u6811\u56ed\u91cc\u6295\u5165\u5de8\u8d44\u5148\u6253\u9020\u4e9a\u6d32\u4e00\u6d41\u7684\u7814\u7a76\u6676\u5706\u5236\u7a0b\u6280\u672f\u5b9e\u9a8c\u5e73\u53f0\uff0c\u62db\u8058\u5927\u91cf\u7684\u9ad8\u7d20\u8d28\u7855\u58eb\u3001\u535a\u58eb\u4eba\u5458\u8fdb\u884c\u6280\u672f\u529b\u91cf\u7684\u68af\u961f\u57f9\u517b\u2014\u2014NEC\u53ea\u540c\u610f\u6211\u4eec\u4f7f\u7528\u4ed6\u4eec\u63d0\u4f9b\u7684\u6280\u672f\uff0c\u9650\u5236\u6211\u4eec\u6d89\u8db3\u540e\u7eed\u7684\u7814\u53d1\uff0c\u540e\u7eed\u6280\u672f\u529b\u91cf\u7684\u68af\u961f\u57f9\u517b\u4e5f\u65e0\u4ece\u8c08\u8d77\u3002\u9526\u6e56\u81ea\u8eab\u5728\u96c6\u6210\u7535\u8def\u8bbe\u8ba1\u4e0a\u7684\u6280\u672f\u4f18\u52bf\u4ee5\u53ca\u4e0e\u5fb7\u4eea\u7684\u5bc6\u5207\u5173\u7cfb\uff0c\u5c06\u4fdd\u8bc1\u4e2d\u6676\u5fae\u82af\u5efa\u6210\u540e\u5c31\u6709\u4e00\u5b9a\u7684\u5e02\u573a\u9700\u6c42\u2014\u2014\u800c\u6211\u4eec\u5728\u8fd9\u4e00\u5757\u5f88\u8584\u5f31\uff0c\u53ea\u80fd\u4ece\u4ee3\u5de5\u5e02\u573a\u62ff\u8ba2\u5355\uff0c\u800c\u662f\u4ee3\u5de5\u5e02\u573a\u7684\u9500\u552e\u4e5f\u88abNEC\u63a7\u5236\u3002\u773c\u4e0b\u56fd\u5185\u8981\u53d1\u5c55\u6676\u5706\u5236\u9020\u4ea7\u4e1a\uff0c\u653f\u7b56\u5411\u4e2d\u6676\u5fae\u82af\u503e\u659c\u4f3c\u4e4e\u66f4\u66f4\u5408\u9002\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u96be\u602a\u4f60\u5e0c\u671b\u770b\u5230\u56fd\u5185\u7684\u4fe1\u606f\u4ea7\u4e1a\u88ab\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u6293\u4f4f\u4e03\u5bf8\uff1f\u201d\u845b\u5efa\u5fb7\u6beb\u4e0d\u5ba2\u6c14\u7684\u5389\u58f0\u6307\u8d23\u8096\u745e\u6c11\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e5f\u662f\u803f\u91cd\u9633\u5fc3\u4e2d\u7684\u75db\uff0c\u8bf4\u4e86\u8fd9\u53e5\u72e0\u8bdd\uff0c\u8fd8\u523b\u610f\u77a5\u4e86\u803f\u91cd\u9633\u4e00\u773c\uff0c\u803f\u91cd\u9633\u53ea\u662f\u7709\u5934\u5fae\u8df3\uff0c\u5012\u4e5f\u6ca1\u6709\u5176\u4ed6\u7684\u53cd\u5e94\u3002</p><p>\u3000\u3000\u201c\u8981\u4e48\u7ed9\u6c11\u8425\u4f01\u4e1a\u6293\u4f4f\u4e03\u5bf8\uff0c\u8981\u4e48\u7ed9\u65e5\u672c\u4eba\u6293\u4f4f\u4e03\u5bf8\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u96be\u4ee5\u53d6\u820d\u554a\u2026\u2026\u201d\u8fd9\u65f6\u5019\u8054\u8baf\u603b\u88c1\u5b59\u5fd7\u521a\u5750\u5728\u53f0\u4e0b\u4e0d\u9634\u4e0d\u9633\u7684\u5e2e\u8096\u745e\u6c11\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u5c06\u845b\u5efa\u5fb7\u80f8\u53e3\u7684\u8840\u6c14\u5dee\u70b9\u9876\u6cb8\u4e86\u3002</p><p>\u3000\u3000\u5728\u534e\u590f\u7535\u5b50\u8fd9\u4e48\u591a\u5bb6\u6210\u5458\u4f01\u4e1a\u4e2d\uff0c\u8054\u8baf\u7684\u89c4\u6a21\u4e0e\u76c8\u5229\u80fd\u529b\u5728\u7535\u5b50\u5de5\u4e1a\u90e8\u65f6\u5c31\u975e\u5e38\u7a81\u51fa\uff0c\u4e5f\u662f\u8d22\u529b\u6700\u5145\u6c9b\u7684\u4e00\u5bb6\u3002\u534e\u590f\u7535\u5b50\u8981\u4e0eNEC\u5408\u8d44\u5efa\u6676\u5706\u5382\uff0c\u8054\u8baf\u80af\u5b9a\u8981\u7ed9\u9876\u51fa\u6765\u5f53\u51a4\u5927\u5934\uff0c\u5b59\u5fd7\u521a\u5bf9\u8fd9\u4e2a\u9879\u76ee\u7684\u524d\u666f\u4e5f\u5f88\u4e0d\u770b\u597d\uff0c\u4f46\u662f\u8eab\u5728\u4f53\u5236\u5185\uff0c\u8054\u8baf\u6700\u7ec8\u8981\u4e0d\u8981\u638f\u94b1\uff0c\u8981\u638f\u591a\u5c11\u94b1\uff0c\u90fd\u662f\u4e0a\u9762\u62cd\u677f\uff0c\u7531\u4e0d\u5f97\u4ed6\u505a\u4e3b\uff0c\u5fc3\u91cc\u6b63\u59d4\u5c48\u7740\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u597d\uff0c\u89c1\u8096\u745e\u6c11\u6311\u5934\u62b5\u5236\u8fd9\u4e2a\u9879\u76ee\uff0c\u81ea\u7136\u8981\u58f0\u63f4\u4ed6\u4e00\u4e0b\u3002\u66f4\u6df1\u5c42\u6b21\u7684\u539f\u56e0\u4e5f\u662f\u5b59\u5fd7\u521a\u6253\u5fc3\u773c\u91cc\u770b\u4e0d\u8d77\u845b\u5efa\u5fb7\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u77e5\u9053\u4ed6\u5bf9\u534e\u590f\u7535\u5b50\u5185\u90e8\u7684\u63a7\u5236\u529b\u8fd8\u8fdc\u8fdc\u8fbe\u4e0d\u5230\u81ea\u5df1\u671f\u5f85\u7684\u6c34\u5e73\uff0c\u803f\u91cd\u9633\u5728\u573a\uff0c\u4ed6\u4e5f\u4e0d\u4fbf\u62ff\u634f\u59ff\u6001\uff0c\u538b\u7740\u80f8\u53e3\u7684\u4e00\u56e2\u6076\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u8096\u660e\u5efa\u5e02\u957f\u6709\u8fc7\u6c9f\u901a\uff0c\u9879\u76ee\u5b9a\u5728\u5efa\u90ba\uff0c\u5730\u65b9\u653f\u5e9c\u9664\u4e86\u4ee5\u571f\u5730\u3001\u914d\u5957\u8d44\u6e90\u6ce8\u8d44\u5916\uff0c\u53e6\u5916\u8fd8\u53ef\u4ee5\u8003\u8651\u62ff\u51fa\u4e00\u90e8\u5206\u8d44\u91d1\u51fa\u6765\uff0c\u8fd9\u6837\u5c31\u80fd\u5e2e\u6211\u4eec\u5206\u644a\u6389\u4e00\u4e9b\u538b\u529b\u2026\u2026\u201d\u5e0c\u671b\u8fd9\u6837\u80fd\u5316\u89e3\u6389\u5185\u90e8\u7684\u538b\u529b\uff0c\u8bf4\u5230\u8fd9\u91cc\uff0c\u773c\u775b\u7785\u7740\u803f\u91cd\u9633\uff0c\u4fe1\u606f\u90e8\u5185\u90e8\u6700\u7ec8\u8fd8\u662f\u8981\u4ed6\u62ff\u4e3b\u610f\u3002</p><p>\u3000\u3000\u803f\u91cd\u9633\u5fae\u655b\u7740\u773c\u775b\uff0c\u8fc7\u4e86\u534a\u664c\u624d\u8bf4\u9053\uff1a\u201c\u524d\u671f\u5de5\u4f5c\u5148\u505a\u8d77\u6765\uff0c\u5c3d\u53ef\u80fd\u7684\u4e89\u53d6\u4e00\u4e9b\u6709\u5229\u6761\u4ef6\u2026\u2026\u201d\u6001\u5ea6\u5012\u662f\u503e\u5411\u63a8\u52a8\u5408\u8d44\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4fe1\u606f\u90e8\u5185\u90e8\u7684\u4f1a\u8bae\u4e89\u6267\u4e0d\u4e0b\uff0c\u76f4\u81f3\u6df1\u6668\u624d\u6563\u4f1a\uff0c\u4ece\u4f1a\u573a\u51fa\u6765\uff0c\u5b59\u5fd7\u521a\u8981\u558a\u8096\u745e\u6c11\uff0c\u8d76\u5de7\u8096\u745e\u6c11\u6b63\u63a5\u4e00\u4e2a\u7535\u8bdd\u3002\u4e1c\u5174\u7684\u5ed6\u7ea2\u96f7\u8d70\u5230\u4ed6\u8eab\u8fb9\uff0c\u62ff\u624b\u8098\u9876\u4e86\u9876\u4ed6\uff0c\u8bf4\u8981\u51fa\u53bb\u53e6\u627e\u5730\u65b9\u8bf4\u8bdd\u53bb\u3002</p><p>\u3000\u3000\u5ed6\u7ea2\u96f7\u4e0e\u5b59\u5fd7\u521a\u90fd\u662f\u539f\u7535\u5b50\u5de5\u4e1a\u90e8\u7cfb\u7edf\u7684\uff0c\u4e4b\u524d\u76f8\u4e92\u6709\u7ade\u4e89\uff0c\u4f46\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u7ed9\u6574\u4e2a\u7684\u5e76\u5165\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e4b\u540e\uff0c\u4e1c\u5174\u3001\u8054\u8baf\u53c8\u7ed9\u5e76\u5165\u534e\u590f\u7535\u5b50\uff0c\u5ed6\u7ea2\u96f7\u4e0e\u5b59\u5fd7\u521a\u5c31\u90fd\u6210\u4e86\u5c11\u6570\u6d3e\uff0c\u5728\u5185\u90e8\u7684\u7ade\u4e89\u4e2d\u5c31\u4f1a\u7ad9\u5728\u540c\u4e00\u6218\u7ebf\u4e0a\uff0c\u4e24\u4eba\u5f80\u6765\u5c31\u5bc6\u5207\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u6df1\u591c\uff0c\u6487\u5f00\u5404\u81ea\u7684\u4e0b\u5c5e\u4ece\u5bbe\u9986\u51fa\u6765\uff0c\u8fd9\u591c\u8272\u6070\u5230\u597d\u5904\u3002</p><p>\u3000\u3000\u201c\u8096\u745e\u6c11\u4eca\u5929\u662f\u5403\u9519\u836f\u4e86\uff1f\u90e8\u91cc\u5927\u5bb6\u5fc3\u77e5\u809a\u660e\uff0c\u6613\u4e91\u98de\u662f\u90e8\u91cc\u516c\u5f00\u503e\u5411\u6276\u6301\u9526\u6e56\u7684\uff0c\u803f\u91cd\u9633\u8fd9\u6b21\u6ca1\u6709\u8ba9\u6613\u4e91\u98de\u53c2\u4e0e\u8fdb\u6765\uff0c\u6001\u5ea6\u5df2\u7ecf\u5f88\u660e\u663e\u4e86\u2026\u2026\u8be5\u4e0d\u4f1a\u8096\u745e\u6c11\u7ed9\u9526\u6e56\u6536\u4e70\u4e86\u5427\uff1f\u201d\u5ed6\u7ea2\u96f7\u89c9\u5f97\u5947\u602a\uff0c\u5728\u8f66\u91cc\u8ddf\u5b59\u5fd7\u521a\u8bf4\u9053\uff0c\u201c\u8fd8\u6709\u554a\uff0c\u4f60\u4e5f\u771f\u662f\u7684\uff0c\u975e\u8981\u5f53\u4f17\u7ed9\u845b\u5efa\u5fb7\u96be\u582a\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u201c\u845b\u5efa\u5fb7\u80fd\u5948\u4f55\u6211\uff1f\u4e0d\u8ba9\u6211\u5728\u534e\u590f\u7535\u5b50\u6df7\u4e0b\u53bb\uff0c\u6211\u5c31\u56de\u5730\u65b9\uff0c\u201d\u5b59\u5fd7\u521a\u7709\u5934\u5fae\u626c\uff0c\u201c\u8096\u745e\u6c11\u662f\u60f3\u505a\u4e9b\u4e8b\u60c5\uff0c\u7ed9\u845b\u5efa\u5fb7\u8fd9\u4e9b\u4eba\u5835\u7740\uff0c\u5fc3\u91cc\u5835\u7740\u614c\uff0c\u6709\u4e9b\u8bdd\u662f\u4e0d\u5410\u4e0d\u5feb\u3002\u5e2e\u9526\u6e56\u8bf4\u8bdd\u5012\u4e5f\u672a\u5fc5\uff0c\u8981\u662f\u845b\u5efa\u5fb7\u80fd\u8ba9\u534e\u590f\u7535\u5b50\u590d\u5236\u9526\u6e56\u7684\u6a21\u5f0f\uff0c\u6211\u770b\u8096\u745e\u6c11\u4e5f\u4f1a\u5f88\u5fe0\u5fc3\u7684\u8ddf\u7740\u845b\u5efa\u5fb7\uff0c\u5173\u952e\u845b\u5efa\u5fb7\u6709\u8fd9\u4e2a\u80fd\u8010\u5417\uff1f\u201d</p><p>\u3000\u3000\u5b59\u5fd7\u521a\u6487\u5634\u800c\u7b11\uff0c\u4ed6\u5bf9\u8096\u745e\u6c11\u7684\u60c5\u51b5\u6bd4\u8f83\u6e05\u695a\u3002\u8096\u745e\u6c11\u56e0\u80fd\u529b\u7a81\u7136\u800c\u5f97\u5230\u63d0\u62d4\uff0c\u6ca1\u6709\u4ec0\u4e48\u80cc\u666f\uff0c\u624d\u56db\u5341\u5c81\u51fa\u5934\u5c31\u5230\u53f8\u5c40\u7ea7\uff0c\u5728\u90e8\u59d4\u91cc\u76f8\u5f53\u7684\u663e\u773c\uff0c\u8d1f\u8d23\u7ec4\u5efa\u8054\u4fe1\uff0c\u8054\u4fe1\u7684\u624b\u673a\u4e1a\u52a1\u4e5f\u7684\u786e\u6700\u4e3a\u51fa\u8272\uff0c\u4f46\u662f\u7ba1\u7406\u56fd\u4f01\u6709\u5982\u6234\u7740\u9563\u94d0\u8df3\u821e\uff0c\u8054\u4fe1\u7684\u8bf8\u591a\u4e8b\u60c5\u90fd\u8f6e\u4e0d\u5230\u8096\u745e\u6c11\u53d1\u53f7\u53f8\u4ee4\uff0c\u505a\u5230\u8fd9\u4e00\u6b65\uff0c\u4e5f\u662f\u8096\u745e\u6c11\u7684\u6781\u9650\u4e86\uff1b\u5f53\u8054\u4fe1\u7684\u4e1a\u7ee9\u5e73\u7a33\u4e0b\u6765\uff0c\u522b\u4eba\u5c31\u4f1a\u60ef\u6027\u7684\u4ee5\u4e3a\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u8fd9\u65f6\u5019\u5c31\u4f1a\u51f8\u663e\u51fa\u8096\u745e\u6c11\u6027\u5b50\u7684\u4e0d\u5408\u7fa4\u6765\u3002</p><p>\u3000\u3000\u5ed6\u7ea2\u96f7\u4e0d\u5927\u5173\u5fc3\u8096\u745e\u6c11\u7684\u6b7b\u6d3b\uff0c\u6bd5\u7adf\u5e73\u65e5\u6ca1\u4ec0\u4e48\u5173\u8054\uff0c\u4ed6\u95ee\u5b59\u5fd7\u521a\uff1a\u201c\u4e0eNEC\u5408\u8d44\u9879\u76ee\uff0c\u96c6\u56e2\u91cc\u4f1a\u8ba9\u8c01\u7275\u5934\uff0c\u8fd8\u662f\u8bf4\u8ba1\u59d4\u4f1a\u63d2\u4e00\u811a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff1f\u8c01\u7231\u6765\u8c01\u6765\u3002\u8981\u662f\u719f\u6089\u7684\u4eba\uff0c\u6211\u4f1a\u529d\u4ed6\u4e0d\u8981\u53bb\u8d9f\u8fd9\u4e2a\u6d51\u6c34\uff1f\u8981\u662f\u770b\u8c01\u4e0d\u987a\u773c\uff0c\u6211\u5012\u4f1a\u987a\u624b\u63a8\u4ed6\u4e00\u628a\uff0c\u770b\u4ed6\u600e\u6837\u5c06\u8fd9\u644a\u5b50\u7838\u70c2\u5728\u81ea\u5df1\u7684\u624b\u91cc\uff0c\u201d\u5b59\u5fd7\u521a\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u8bf4\u90e8\u91cc\u9664\u4e86\u4f60\u6211\u8fd8\u6709\u8096\u745e\u6c11\u4e4b\u5916\uff0c\u8fd8\u6709\u8c01\u66f4\u4e86\u89e3\u9526\u6e56\u7684\u5389\u5bb3\u4e4b\u5904\uff1f\u845b\u5efa\u5fb7\u6597\u4e0d\u8fc7\u90a3\u5c0f\u5b50\uff0c\u8fdf\u65e9\u4f1a\u7ed9\u90a3\u5c0f\u5b50\u73a9\u6b7b\u3002\u803f\u91cd\u9633\u7684\u6001\u5ea6\u4e5f\u4e0d\u4f1a\u6c38\u8fdc\u90fd\u4e00\u6210\u4e0d\u53d8\uff0c\u66f4\u4e0d\u53ef\u80fd\u6c38\u8fdc\u9738\u7740\u90a3\u4e2a\u4f4d\u7f6e\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u6709\u522b\u7684\u6d88\u606f\u6765\u6e90\uff1f\u201d\u5ed6\u7ea2\u96f7\u7591\u60d1\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u54ea\u6709\u4ec0\u4e48\u6d88\u606f\u6765\u6e90\uff1f\u201d\u5b59\u5fd7\u521a\u8bf4\u9053\uff0c\u201c\u4e0a\u9762\u7684\u77db\u76fe\uff0c\u4e3b\u8981\u8fd8\u662f\u96c6\u4e2d\u5728\u8def\u7ebf\u65b9\u9488\u4e0a\uff0c\u6211\u4eec\u6709\u65f6\u5019\u4e5f\u53ea\u80fd\u770b\u5230\u8def\u7ebf\u65b9\u9488\u7684\u53d8\u5316\uff0c\u800c\u770b\u4e0d\u5230\u80cc\u540e\u7684\u77db\u76fe\u8f83\u91cf\u3002516\u6848\u662f\u519b\u65b9\u4eb2\u81ea\u63a8\u52a8\u7684\uff0c\u8bf4\u5230\u5e95\uff0c\u4f60\u76f8\u4fe1\u5d14\u6587\u6bc5\u4f1a\u56e0\u4e3a\u4ed6\u513f\u5b50\u88ab\u6263\u62bc\u7684\u4e8b\u60c5\u641e\u8fd9\u4e48\u5927\u7684\u52a8\u9759\uff1f\u975e\u5e38\u679c\u65ad\u7684\u52a8\u4f5c\u662f\u53d1\u751f\u5728\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e4b\u540e\uff0c\u6211\u770b\u662f\u4e0a\u9762\u6709\u4e9b\u4eba\u7684\u7acb\u573a\u5728\u6084\u7136\u53d1\u5c55\u8f6c\u53d8\u3002\u4e0eNEC\u5408\u8d44\u9879\u76ee\uff0c\u73b0\u5728\u662f\u5f88\u96be\u963b\u6b62\u4e86\uff0c\u4f46\u662f\u8fd9\u4e2a\u5408\u8d44\u9879\u76ee\u80fd\u505a\u6210\u5012\u4e5f\u7f62\u4e86\uff0c\u8981\u662f\u518d\u6b21\u505a\u7838\uff0c\u53ea\u6015\u5c31\u4e0d\u4f1a\u518d\u8f7b\u6613\u8bf4\u58f0\u2018\u5438\u53d6\u7ecf\u9a8c\u6559\u8bad\u2019\u5c31\u80e1\u6df7\u8fc7\u53bb\u3002\u4f60\u8ba4\u4e3a\u8fd9\u6b21\u5408\u8d44\u9879\u76ee\u7684\u6210\u529f\u673a\u7387\u6709\u591a\u5927\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u4e1c\u534e\u95e8\u5916\u7684\u661f\u6e56\u56fd\u9645\u793e\u533a\u91cc\uff0c\u4e09\u661f\u4e3a\u5728\u5efa\u90ba\u5de5\u4f5c\u7684\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u79df\u4e0b\u591a\u5957\u9ad8\u7ea7\u516c\u5bd3\uff0c\u674e\u5728\u6d19\u6765\u5f80\u5efa\u90ba\u7684\u6b21\u6570\u591a\u4e86\uff0c\u5c31\u5728\u793e\u533a\u91cc\u5907\u4e0b\u4e00\u5957\u8c6a\u534e\u516c\u5bd3\uff0c\u5012\u662f\u5f88\u65b9\u4fbf\u3002</p><p>\u3000\u3000\u6709\u8bb8\u591a\u9644\u8fd1\u9ad8\u6821\u7684\u5e74\u8f7b\u5973\u5b69\u5b50\u559c\u6b22\u5230\u56fd\u9645\u793e\u533a\u6765\u5f53\u53e3\u8bed\u5bb6\u6559\uff0c\u674e\u5728\u6d19\u591c\u91cc\u56de\u5230\u516c\u5bd3\uff0c\u5012\u662f\u770b\u5230\u4e00\u4e9b\u975e\u6d32\u9ed1\u4eba\u6402\u7740\u5a07\u5ae9\u7684\u4e2d\u56fd\u59d1\u5a18\u5728\u793e\u533a\u91cc\u7ec3\u4e60\u53e3\u8bed\uff0c\u56de\u5230\u516c\u5bd3\u91cc\uff0c\u8ba9\u52a9\u7406\u627e\u6765\u91d1\u5357\u52c7\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u6709\u6ca1\u6709\u5174\u8da3\u627e\u4e00\u540d\u53e3\u8bed\u5bb6\u6559\uff0c\u90fd\u662f\u86ee\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u3002\u201d</p><p>\u3000\u3000\u91d1\u5357\u52c7\u5230\u4e2d\u56fd\u6765\u751f\u6d3b\u591a\u5e74\uff0c\u9664\u4e86\u4ed6\u7684\u8138\u5f62\u4e4b\u5916\uff0c\u51e0\u4e4e\u770b\u4e0d\u5230\u4ed6\u8eab\u4e0a\u6709\u97e9\u56fd\u4eba\u7684\u75d5\u8ff9\uff0c\u53ea\u8bf4\u9053\uff1a\u201c\u4f60\u8981\u6709\u5174\u8da3\uff0c\u591c\u91cc\u6211\u5e2e\u4f60\u627e\u4e24\u4e2a\u5973\u5b69\u5b50\u6765\uff0c\u6211\u5c31\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u674e\u5728\u6d19\u7b11\u4e86\u7b11\uff0c\u8bf4\u8d77\u6b63\u4e8b\uff0c\u201c\u6211\u4eec\u7ed9\u9526\u6e56\u53d1\u51fa\u7684\u6b63\u5f0f\u8981\u6c42\u6280\u672f\u5408\u4f5c\u7684\u7535\u51fd\u6709\u56de\u590d\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u9ec4\u660f\u65f6\u5206\u53d1\u6765\u4f20\u771f\uff0c\u6211\u8fd9\u5c31\u7ed9\u4f60\u62ff\u590d\u5370\u4ef6\u53bb\u2026\u2026\u201d\u91d1\u5357\u52c7\u53bb\u62ff\u4f20\u771f\u590d\u5370\u4ef6\u7ed9\u674e\u5728\u6d19\u770b\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u8d2a\u5a6a\u7684\u5bb6\u4f19\uff01\u201d\u674e\u5728\u6d19\u76b1\u7740\u7709\u5934\uff0c\u624b\u6307\u91cd\u91cd\u7684\u6441\u5728\u590d\u5370\u4ef6\u4e0a\uff0c\u201c\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u8feb\u4e8e\u4e09\u4e95\u7684\u538b\u529b\u4f1a\u8ba9\u8c08\u5224\u8f7b\u677e\u4e9b\u5462\uff0c\u4ed6\u4eec\u7adf\u7136\u8fd8\u6709\u91ce\u5fc3\u6765\u89ca\u89ce\u6211\u4eec\u7684\u95ea\u5b58\u6280\u672f\uff01\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u8ba4\u4e3a\u4e09\u4e95\u4e3b\u5bfc\u7684\u8fd9\u6b21\u5408\u4f5c\u65e0\u6cd5\u5bf9\u4ed6\u4eec\u9020\u6210\u5b9e\u8d28\u6027\u7684\u5a01\u80c1\uff0c\u6628\u5929\u53bb\u4e2d\u6676\u5fae\u82af\u53c2\u89c2\u65f6\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u9ad8\u5c42\u4f3c\u4e4e\u4e5f\u5f88\u8f7b\u677e\uff0c\u5e76\u6ca1\u6709\u5982\u4e34\u5927\u654c\u7684\u610f\u6001\uff0c\u201d\u91d1\u5357\u52c7\u8bf4\u9053\uff0c\u201c\u4e09\u4e95\u7684\u8fd9\u6b21\u52a8\u4f5c\u4f1a\u8bf1\u4f7f\u4e00\u4e9b\u4eba\u7ad9\u5230\u9526\u6e56\u7684\u5bf9\u7acb\u9762\u2014\u2014\u5176\u5b9e\u8fd9\u4e9b\u4eba\u65e9\u5c31\u7ad9\u5728\u9526\u6e56\u7684\u5bf9\u7acb\u9762\uff0c\u4f46\u4e5f\u4f1a\u8ba9\u4e00\u4e9b\u4eba\u5bf9\u9526\u6e56\u4ea7\u751f\u540c\u60c5\uff0c\u8fd9\u5176\u4e2d\u7684\u5229\u5f0a\u5f88\u96be\u53bb\u5206\u6790\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e09\u4e95\u603b\u4e0d\u4f1a\u6709\u635f\u5931\u5c31\u662f\u4e86\uff0c\u4e09\u6d41\u7684\u6280\u672f\u52a0\u4e86\u51e0\u5341\u540d\u6280\u672f\u4eba\u5458\uff0c\u5356\u51fa\u8fd9\u4e48\u597d\u7684\u4ef7\u94b1\uff0c\u5927\u6982\u8bbe\u5907\u4e5f\u4f1a\u7531NEC\u7684\u5de5\u5382\u6dd8\u6c70\u7ed9\u4e2d\u56fd\u5427\uff0c\u66f4\u4e0d\u7528\u8bf4\u80fd\u524a\u5f31516\u6848\u7684\u8d1f\u9762\u5f71\u54cd\u4e86\uff0c\u201d\u674e\u5728\u6d19\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u4e8c\u5341\u591a\u5e74\u524d\u5c31\u4e0b\u51b3\u5fc3\u5b66\u4e60\u4e09\u4e95\uff0c\u53ef\u60dc\u5230\u73b0\u5728\u8fd8\u8fdc\u8fdc\u4e0d\u80fd\u8ddf\u4eba\u5bb6\u6bd4\u554a\u3002\u201d</p></article>"}}}]);
