(window.webpackJsonp=window.webpackJsonp||[]).push([[85],{107:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e09\u8282 \u5927\u52bf\u6240\u8d8b",content:"<article><h2>\u7b2c\u56db\u5341\u4e09\u8282 \u5927\u52bf\u6240\u8d8b</h2><p>\u4e0d\u53bb\u7406\u4f1a\u8fd9\u4e9b\uff0c\u4ed6\u8d81\u8fd9\u4e2a\u673a\u4f1a\uff0c\u5c06\u90a3\u526f\u51b0\u51b7\u7684\u6de1\u84dd\u8272\u624b\u5957\u53d6\u4e86\u51fa\u6765\uff0c\u8bf7\u8001\u77ee\u4eba\u5e2e\u5fd9\u9274\u5b9a\u3002\u8001\u77ee\u4eba\u62ff\u7740\u7422\u78e8\u4e86\u4e00\u4f1a\uff0c\u8bf4\uff1a\u201c\u5bd2\u51b0\u624b\u5957\u3002\u201d</p><p>\u4ed6\u6234\u4e0a\u624b\u5957\uff0c\u5ff5\u4e86\u4e00\u4e2a\u5b57\u7b26\uff0c\u5c06\u624b\u638c\u644a\u5f00\u7ed9\u743c\u6069\u770b\uff0c\u624b\u5957\u4e0a\u5df2\u7ecf\u7f13\u7f13\u5347\u8d77\u767d\u8272\u7684\u51b7\u6c14\uff0c\u5fae\u5fae\u51dd\u7ed3\u8d77\u4e00\u5c42\u5bd2\u971c\u3002\u8001\u77ee\u4eba\u4f38\u624b\uff0c\u63e1\u4f4f\u684c\u4e0a\u7684\u6c34\u676f\u3002\u201c\u6ce2\u201d\u7684\u4e00\u58f0\u8f7b\u5fae\u88c2\u54cd\uff0c\u6c34\u676f\u77ac\u95f4\u788e\u6210\u4e86\u4e03\u516b\u5757\u3002</p><p>\u8001\u77ee\u4eba\u5c06\u624b\u5957\u53d6\u4e0b\uff0c\u8fd8\u7ed9\u743c\u6069\uff0c\u544a\u8bc9\u4ed6\u6fc0\u53d1\u529b\u91cf\u7684\u5492\u8bed\u3002\u743c\u6069\u9ed8\u8bb0\u5728\u5fc3\uff0c\u53c8\u628a\u90a3\u67c4\u5315\u9996\u53d6\u4e86\u51fa\u6765\uff0c\u8bf7\u4ed6\u9274\u5b9a\u3002\u8001\u77ee\u4eba\u63a5\u8fc7\u5315\u9996\uff0c\u6f2b\u4e0d\u7ecf\u5fc3\u5730\u770b\u4e86\u770b\uff0c\u9661\u7136\u8138\u8272\u4e00\u50f5\u3002</p><p>\u201c\u8fd9\u5315\u9996\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\u7684\uff1f\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\u743c\u6069\u81ea\u7136\u4e0d\u4f1a\u8001\u5b9e\u56de\u7b54\uff0c\u4ed6\u672c\u6765\u60f3\u8bf4\u662f\u4e34\u884c\u524d\u5bfc\u5e08\u7ed9\u7684\uff0c\u4f46\u968f\u5373\u60f3\u5230\u8fd9\u7406\u7531\u8bf4\u4e0d\u901a\uff0c\u5982\u679c\u771f\u662f\u5bfc\u5e08\u7ed9\u7684\uff0c\u54ea\u91cc\u8fd8\u9700\u8981\u62ff\u6765\u9274\u5b9a\u3002\u6700\u540e\u4ed6\u968f\u4fbf\u7f16\u4e86\u4e2a\u7406\u7531\uff0c\u8bf4\u662f\u8def\u4e0a\u5728\u5e97\u4e2d\u770b\u89c1\uff0c\u5e97\u4e3b\u4e5f\u4e0d\u8bc6\u8d27\uff0c\u4f4e\u4ef7\u51fa\u552e\uff0c\u4ed6\u89c9\u5f97\u6709\u4e9b\u540d\u5802\uff0c\u5c31\u82b1\u94b1\u4e70\u4e86\u4e0b\u6765\u3002</p><p>\u8001\u77ee\u4eba\u53c8\u770b\u4e86\u534a\u5929\uff0c\u4ece\u684c\u5b50\u62bd\u5c49\u91cc\u53d6\u51fa\u4e00\u526f\u6df1\u84dd\u8272\u773c\u955c\uff0c\u4eff\u4f5b\u662f\u6c34\u6676\u5236\u4f5c\u7684\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u6234\u4e0a\u53c8\u770b\u4e86\u4e00\u4f1a\uff0c\u6700\u7ec8\u70b9\u70b9\u5934\uff0c\u201c\u56de\u8fd4\u5315\u9996\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u6361\u5230\u597d\u4e1c\u897f\u4e86\u3002\u201d</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u56de\u8fd4\u5315\u9996\u662f\u4ec0\u4e48\u4e1c\u897f\uff0c\u7ecf\u8001\u77ee\u4eba\u89e3\u91ca\u624d\u660e\u767d\u3002\u8fd9\u67c4\u5315\u9996\u4e2d\u704c\u6ce8\u4e86\u4e00\u4e2a\u7c7b\u4f3c\u56de\u8fd4\u771f\u8a00\u7684\u6cd5\u672f\uff0c\u5177\u4f53\u7528\u6cd5\u662f\u9884\u5148\u7528\u5315\u9996\u5272\u7834\u624b\u6307\uff0c\u6ef4\u4e00\u6ef4\u8840\u51fa\u6765\uff0c\u7528\u7425\u73c0\u5c01\u4f4f\uff0c\u653e\u7f6e\u5728\u67d0\u5904\u3002\u4ee5\u540e\u4e07\u4e00\u9047\u4e0a\u5371\u9669\uff0c\u53ea\u8981\u63e1\u7740\u5315\u9996\u5ff5\u8bf5\u5492\u8bed\uff0c\u5c31\u80fd\u81ea\u52a8\u4f20\u9001\u5230\u7425\u73c0\u7684\u653e\u7f6e\u5730\u70b9\u3002</p><p>\u548c\u4f20\u9001\u672f\u6bd4\u8d77\u6765\uff0c\u8fd9\u56de\u8fd4\u672f\u5341\u5206\u4e0d\u591f\u7075\u6d3b\uff0c\u5b83\u53ea\u80fd\u56fa\u5b9a\u5730\u4f20\u9001\u5230\u9884\u5148\u6307\u5b9a\u7684\u5730\u70b9\uff0c\u800c\u4e0d\u80fd\u5982\u4f20\u9001\u672f\u4e00\u822c\u968f\u5fc3\u6240\u6b32\u81ea\u7531\u6307\u5b9a\u3002\u800c\u4e14\u4e5f\u5f88\u4e0d\u5b89\u5168\uff0c\u5982\u679c\u90a3\u679a\u5c01\u7740\u8840\u7684\u7425\u73c0\u88ab\u4eba\u53d1\u73b0\u3001\u9500\u6bc1\uff0c\u90a3\u56de\u8fd4\u672f\u81ea\u7136\u5c31\u4e0d\u80fd\u518d\u751f\u6548\uff1b\u5982\u679c\u6709\u4eba\u6076\u4f5c\u5267\uff0c\u628a\u90a3\u679a\u7425\u73c0\u4e22\u5230\u5927\u6d77\u91cc\u53bb\uff0c\u5230\u65f6\u5019\u8fd9\u8fb9\u4eba\u4e00\u56de\u8fd4\uff0c\u7ed3\u679c\u53d1\u73b0\u8fd4\u56de\u5230\u4e86\u6d77\u5e95\uff0c\u8eab\u8fb9\u90fd\u662f\u9ca8\u9c7c\u2026\u2026</p><p>\u867d\u7136\u6709\u79cd\u79cd\u4e0d\u5982\u610f\uff0c\u4f46\u5230\u5e95\u662f\u591a\u4e86\u4e00\u9879\u4fdd\u547d\u7684\u62a4\u7b26\uff0c\u4f55\u51b5\u539f\u672c\u5c31\u662f\u767d\u5f97\u6765\u7684\uff0c\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u597d\u62b1\u6028\u3002\u4f20\u9001\u672f\u81ea\u7136\u597d\uff0c\u4f46\u90a3\u4e0d\u662f\u4ed6\u76ee\u524d\u53ef\u4ee5\u53bb\u8003\u8651\u7684\u4e8b\u60c5\uff0c\u592a\u9065\u8fdc\u4e86\u3002</p><p>\u8001\u77ee\u4eba\u8fd9\u91cc\u5c31\u6709\u7425\u73c0\uff0c\u73b0\u573a\u53d6\u6765\uff0c\u743c\u6069\u7528\u5315\u9996\u5272\u7834\u6307\u5c16\uff0c\u6ef4\u4e86\u4e00\u6ef4\u8840\u51fa\u6765\uff0c\u5c01\u5728\u7425\u73c0\u4e2d\uff0c\u6536\u8fdb\u53e3\u888b\u3002\u8001\u77ee\u4eba\u53c8\u5c06\u6fc0\u53d1\u56de\u8fd4\u771f\u8a00\u7684\u5492\u8bed\u6559\u7ed9\u743c\u6069\uff0c\u4ed6\u629a\u6469\u7740\u5315\u9996\uff0c\u4e00\u526f\u604b\u604b\u4e0d\u820d\u7684\u6837\u5b50\uff0c\u8ba9\u743c\u6069\u5f88\u6000\u7591\u8001\u77ee\u4eba\u662f\u4e0d\u662f\u4e0d\u60f3\u628a\u5315\u9996\u8fd8\u7ed9\u81ea\u5df1\u4e86\u3002\u6bd5\u7adf\uff0c\u77ee\u4eba\u65cf\u867d\u7136\u7d20\u6709\u8c6a\u723d\u597d\u5ba2\u7684\u98ce\u8bc4\uff0c\u4f46\u5176\u5bf9\u8d22\u5b9d\u7684\u8d2a\u5a6a\u5c0f\u6c14\uff0c\u4e5f\u662f\u58f0\u540d\u5728\u5916\u7684\u3002</p><p>\u4f46\u6700\u540e\uff0c\u8001\u77ee\u4eba\u8fd8\u662f\u4e00\u8138\u820d\u4e0d\u5f97\u5730\u628a\u5315\u9996\u8fd8\u7ed9\u4e86\u743c\u6069\u3002\u8fd9\u79cd\u9274\u5b9a\u9b54\u6cd5\u7269\u54c1\u7684\u6d3b\uff0c\u81ea\u7136\u662f\u8981\u6536\u94b1\u7684\uff0c\u8001\u77ee\u4eba\u867d\u7136\u548c\u743c\u6069\u76f8\u8c08\u751a\u6b22\uff0c\u4f46\u6536\u8d77\u94b1\u6765\u53ef\u662f\u6beb\u4e0d\u5ba2\u6c14\uff0c\u5145\u5206\u5c55\u793a\u77ee\u4eba\u4e00\u65cf\u8457\u540d\u7684\u8d2a\u5a6a\u4e60\u6027\u3002\u9274\u5b9a\u8d39\u548c\u4e09\u4ef6\u5deb\u5e08\u888d\uff0c\u8fde\u5e26\u90a3\u4efd\u7425\u73c0\u7684\u94b1\u5728\u5185\uff0c\u4e00\u53e3\u6c14\u6536\u4e86\u743c\u6069\u4e5d\u679a\u91d1\u5e01\uff0c\u8fd8\u8bf4\u662f\u62b9\u53bb\u4e86\u96f6\u5934\u3002</p><p>\u5929\u8272\u4e0d\u65e9\uff0c\u743c\u6069\u8d77\u8eab\u544a\u8f9e\u3002\u8001\u77ee\u4eba\u4f3c\u4e4e\u5f88\u96be\u5f97\u9047\u5230\u4eba\u7c7b\u5deb\u5e08\uff0c\u6216\u8005\u8bf4\u4ee4\u4ed6\u60f3\u8d77\u4e86\u5f53\u5e74\u53bb\u4eba\u7c7b\u57ce\u5e02\u5b66\u4e60\u9b54\u6cd5\u7684\u5f80\u4e8b\uff0c\u5c45\u7136\u8fd8\u6709\u4e9b\u7559\u604b\u3002\u4f46\u4ed6\u53c8\u4e0d\u662f\u7f8e\u5973\uff0c\u743c\u6069\u81ea\u7136\u61d2\u5f97\u6709\u5174\u8da3\u2014\u2014\u4e8b\u5b9e\u4e0a\uff0c\u8fd8\u6b63\u6709\u4e2a\u7f8e\u5973\u5728\u65c5\u5e97\u91cc\u7b49\u4ed6\u5462\u3002\u5306\u5306\u8bf4\u4e86\u51e0\u53e5\uff0c\u4fbf\u9003\u4e86\u51fa\u6765\u3002</p><p>\u203b\u203b\u203b</p><p>\u5728\u56de\u6765\u7684\u8def\u4e0a\uff0c\u4ed6\u987a\u4fbf\u53c8\u4e70\u4e86\u4e9b\u5176\u4ed6\u8863\u7269\uff0c\u56de\u5230\u65c5\u5e97\u7684\u65f6\u5019\uff0c\u5929\u8272\u5df2\u7ecf\u6e10\u6e10\u9ed1\u4e86\u3002\u6885\u83f2\u65af\u5b88\u5728\u623f\u95f4\u91cc\uff0c\u770b\u7740\u90a3\u679a\u9ece\u660e\u4e4b\u77f3\u3002</p><p>\u65c5\u5e97\u91cc\u4e5f\u6709\u665a\u9910\u4f9b\u5e94\uff0c\u743c\u6069\u8ba9\u670d\u52a1\u751f\u9001\u4e86\u4e24\u4efd\u4e0a\u6765\u3002\u5176\u95f4\u743c\u6069\u5bf9\u6885\u83f2\u65af\u8bf4\u8d77\u4eca\u5929\u5728\u77ee\u4eba\u795e\u6bbf\u7684\u906d\u9047\uff0c\u987a\u4fbf\u8bf7\u6559\u4e3a\u4f55\u8fd9\u4e9b\u77ee\u4eba\u795e\u7947\u80fd\u5171\u805a\u4e00\u5802\u3002\u800c\u4e14\u90a3\u5ea7\u795e\u6bbf\u540d\u4e3a\u201c\u83ab\u62c9\u4e01\u7194\u7089\u4e4b\u6bbf\u201d\uff0c\u663e\u7136\u5e94\u8be5\u662f\u83ab\u62c9\u4e01\u7684\u795e\u6bbf\uff0c\u4e3a\u4f55\u540c\u65f6\u585e\u8fdb\u90a3\u4e48\u591a\u77ee\u4eba\u795e\u7947\u7684\u5851\u50cf\u8fdb\u53bb\u3002\u5728\u4eba\u7c7b\u795e\u6bbf\u4e2d\uff0c\u8fd9\u662f\u4e0d\u53ef\u60f3\u8c61\u7684\u3002</p><p>\u201c\u953b\u9b42\u8005\u83ab\u62c9\u4e01\u662f\u77ee\u4eba\u7684\u4e3b\u795e\u3002\u201d\u6885\u83f2\u65af\u542c\u4e86\u743c\u6069\u7684\u63cf\u8ff0\uff0c\u4e1d\u6beb\u4e0d\u4ee5\u4e3a\u610f\uff0c\u5e73\u9759\u56de\u7b54\u3002</p><p>\u201c\u4e3b\u795e\uff1f\u201d\u743c\u6069\u91cd\u590d\u7740\u8fd9\u4e2a\u8bcd\uff0c\u4ed6\u4e0d\u751a\u660e\u767d\uff0c\u56e0\u4e3a\u4ee5\u524d\u4f3c\u4e4e\u6ca1\u542c\u8fc7\u3002</p><p>\u201c\u77ee\u4eba\u7684\u795e\u7947\u548c\u6211\u4eec\u4eba\u7c7b\u4e0d\u540c\uff0c\u201d\u6885\u83f2\u65af\u89e3\u91ca\uff0c\u201c\u6211\u4eec\u7684\u8bf8\u795e\u2026\u2026\u6bd4\u8f83\u6563\u4e71\uff0c\u201d\u5979\u8f7b\u8f7b\u8e59\u8d77\u7709\u5934\uff0c\u601d\u7d22\u7740\uff0c\u52aa\u529b\u9009\u62e9\u4e86\u8fd9\u4e2a\u6bd4\u8f83\u5408\u9002\u7684\u5f62\u5bb9\uff0c\u201c\u4f46\u77ee\u4eba\u8bf8\u795e\u5374\u662f\u6bd4\u8f83\u6709\u7ec4\u7ec7\u5316\u7684\u3002\u201d</p><p>\u5979\u6447\u4e86\u6447\u5934\uff0c\u4f3c\u4e4e\u5bf9\u81ea\u5df1\u7684\u8fd9\u4e2a\u5f62\u5bb9\u4e5f\u4e0d\u751a\u6ee1\u610f\uff0c\u4f46\u4e00\u65f6\u4e5f\u60f3\u4e0d\u51fa\u66f4\u5408\u9002\u7684\u8bcd\u6765\u4ee3\u66ff\uff0c\u53ea\u597d\u4f5c\u7f62\u3002\u201c\u6211\u4eec\u7684\u8bf8\u795e\uff0c\u5e76\u6ca1\u6709\u67d0\u4e00\u4e2a\u9886\u8896\uff0c\u6216\u8005\u8bf4\u4e00\u4e2a\u6700\u9ad8\u4f4d\u9636\u8005\uff0c\u4f46\u77ee\u4eba\u8bf8\u795e\u662f\u6709\u7684\uff0c\u5c31\u662f\u83ab\u62c9\u4e01\u3002\u83ab\u62c9\u4e01\u662f\u6240\u6709\u77ee\u4eba\u795e\u7947\u7684\u9886\u8896\uff0c\u8fd9\u4fbf\u662f\u4e3b\u795e\u3002\u201d</p><p>\u83ab\u62c9\u4e01\u4e0d\u4f46\u662f\u77ee\u4eba\u8bf8\u795e\u7684\u9886\u8896\uff0c\u540c\u65f6\u8fd8\u662f\u6240\u6709\u77ee\u4eba\u5c0a\u5949\u7684\u795e\u7947\u3002\u6309\u7167\u77ee\u4eba\u7684\u8bf4\u6cd5\uff0c\u6240\u6709\u77ee\u4eba\u7684\u7075\u9b42\u90fd\u662f\u83ab\u62c9\u4e01\u4ece\u4ed6\u7684\u7194\u7089\u4e2d\u953b\u9020\u51fa\u6765\u7684\uff0c\u6240\u4ee5\u83ab\u62c9\u4e01\u53c8\u6709\u5c0a\u79f0\u201c\u953b\u9b42\u8005\u201d\u3002\u4e00\u4e2a\u77ee\u4eba\u53ef\u80fd\u4f1a\u4fe1\u4ef0\u5176\u4ed6\u67d0\u4f4d\u77ee\u4eba\u795e\uff0c\u4f46\u4ed6\u540c\u65f6\u4e5f\u53ef\u4ee5\u7b97\u662f\u83ab\u62c9\u4e01\u7684\u4fe1\u5f92\uff0c\u4fbf\u5982\u743c\u6069\u78b0\u89c1\u7684\u8001\u77ee\u4eba\u4e39\u5409\u5c14\u3002</p><p>\u76f8\u5bf9\u4eba\u7c7b\u7684\u8bf8\u795e\u6765\u8bf4\uff0c\u77ee\u4eba\u795e\u7cfb\u6bd4\u8f83\u56e2\u7ed3\u7d27\u5bc6\uff0c\u5982\u679c\u8981\u8bf4\u6709\u77db\u76fe\uff0c\u5927\u6982\u5c31\u662f\u4ee5\u62c9\u675c\u683c\u548c\u6df1\u5730\xb7\u675c\u83c8\u4e3a\u4ee3\u8868\u7684\u7070\u77ee\u4eba\u795e\u6bd4\u8f83\u7279\u7acb\u72ec\u884c\uff0c\u548c\u5176\u4ed6\u77ee\u4eba\u795e\u7947\u6bd4\u8f83\u4e0d\u5408\u3002\u4e0d\u8fc7\u5c31\u76ee\u524d\u6765\u8bf4\uff0c\u5927\u4f53\u8fd8\u7b97\u76f8\u5b89\u65e0\u4e8b\uff0c\u81f3\u5c11\u8fd8\u80fd\u653e\u5728\u540c\u4e00\u5ea7\u795e\u6bbf\u91cc\u63a5\u53d7\u4fe1\u5f92\u671d\u62dc\u3002\u6ca1\u6709\u6076\u52a3\u5230\u4eba\u7c7b\u8bf8\u795e\u90a3\u6837\uff0c\u52a8\u4e0d\u52a8\u5c31\u4f60\u6b7b\u6211\u6d3b\uff0c\u5f04\u5f97\u5929\u7ffb\u5730\u8986\u3002</p><p>\u201c\u62c9\u675c\u683c\uff1f\u201d\u743c\u6069\u542c\u5230\u6885\u83f2\u65af\u63d0\u8d77\u8fd9\u4e2a\u540d\u5b57\uff0c\u60f3\u8d77\u5c31\u662f\u8001\u77ee\u4eba\u5deb\u5e08\u4fe1\u4ef0\u7684\u795e\u7947\uff0c\u201c\u4f60\u8bf4\u4ed6\u662f\u7070\u77ee\u4eba\u795e\uff1f\u4ed6\u4e0d\u662f\u77ee\u4eba\u7684\u9b54\u6cd5\u795e\u4e48\uff1f\u201d</p><p>\u201c\u9b54\u6cd5\u795e\uff1f\u201d\u6885\u83f2\u65af\u7565\u60f3\u4e86\u60f3\uff0c\u6bd5\u7adf\u5979\u5bf9\u77ee\u4eba\u7684\u4fe1\u4ef0\u5176\u5b9e\u4e5f\u4e0d\u662f\u7279\u522b\u719f\u6089\uff0c\u201c\u4e5f\u52c9\u5f3a\u53ef\u4ee5\u8fd9\u4e48\u8bf4\u4e86\uff0c\u201d\u5979\u8bc4\u4ef7\u8bf4\uff0c\u201c\u4e0d\u8fc7\u5176\u5b9e\u4ed6\u4e3b\u8981\u7684\u8eab\u4efd\u662f\u7070\u77ee\u4eba\u7684\u5b88\u62a4\u795e\u3002\u201d</p><p>\u7070\u77ee\u4eba\u8fd9\u4e2a\u8bcd\u743c\u6069\u5012\u662f\u77e5\u9053\u3002\u77ee\u4eba\u662f\u4e2a\u6cdb\u79f0\uff0c\u5176\u4e2d\u5305\u62ec\u76fe\u77ee\u4eba\u3001\u91d1\u77ee\u4eba\u548c\u7070\u77ee\u4eba\u4e09\u5927\u7c7b\uff0c\u4ee5\u53ca\u4e00\u4e9b\u6570\u91cf\u7a00\u5c11\u5230\u53ef\u4ee5\u5ffd\u7565\u4e0d\u8ba1\u7684\u5176\u4ed6\u65cf\u7fa4\u3002\u524d\u4e24\u5927\u7c7b\u77ee\u4eba\u57fa\u672c\u53ef\u4ee5\u4e00\u89c6\u540c\u4ec1\uff0c\u5dee\u522b\u4e0d\u5927\uff0c\u4f46\u7070\u77ee\u4eba\u4fbf\u6bd4\u8f83\u7279\u522b\u4e9b\uff0c\u4ed6\u4eec\u662f\u5c45\u4f4f\u5728\u5e7d\u6697\u5730\u57df\u7684\uff0c\u5f88\u5c11\u5728\u5730\u8868\u51fa\u73b0\u3002\u4e00\u822c\u4eba\u8bf4\u77ee\u4eba\uff0c\u5176\u5b9e\u6307\u7684\u662f\u76fe\u77ee\u4eba\u548c\u91d1\u77ee\u4eba\uff0c\u4e0d\u5305\u62ec\u7070\u77ee\u4eba\u5728\u5185\u3002</p><p>\u6bd4\u8d77\u5176\u4ed6\u77ee\u4eba\uff0c\u7070\u77ee\u4eba\u7684\u540d\u58f0\u666e\u904d\u8981\u6076\u52a3\u4e9b\uff0c\u7d20\u6709\u201c\u90aa\u6076\u201d\u7684\u8bc4\u4ef7\u3002\u76fe\u77ee\u4eba\u548c\u91d1\u77ee\u4eba\u5f80\u5f80\u76f8\u5904\u878d\u6d3d\uff0c\u4f46\u5bf9\u7070\u77ee\u4eba\u4e5f\u6bd4\u8f83\u654c\u89c6\u3002\u62c9\u675c\u683c\u4f5c\u4e3a\u7070\u77ee\u4eba\u7684\u5b88\u62a4\u795e\uff0c\u548c\u5176\u4ed6\u77ee\u4eba\u795e\u7947\u5173\u7cfb\u81ea\u7136\u4e5f\u5c31\u4e0d\u4f73\u3002\u867d\u7136\u4ed6\u540d\u4e49\u4e0a\u662f\u9b54\u6cd5\u795e\uff0c\u4f46\u5176\u4ed6\u76fe\u77ee\u4eba\u548c\u7070\u77ee\u4eba\u4e2d\u7684\u65bd\u6cd5\u8005\uff0c\u90fd\u7f55\u6709\u5d07\u62dc\u4ed6\u7684\u3002</p><p>\u7070\u77ee\u4eba\u5934\u53d1\u548c\u76ae\u80a4\u5168\u662f\u7070\u6697\u8272\u7684\uff0c\u975e\u5e38\u5bb9\u6613\u8fa8\u8ba4\u3002\u743c\u6069\u4eca\u5929\u9047\u5230\u7684\u90a3\u4e2a\u8001\u77ee\u4eba\u5deb\u5e08\u663e\u7136\u4e0d\u662f\u7070\u77ee\u4eba\uff0c\u4ed6\u5374\u4fe1\u4ef0\u62c9\u675c\u683c\uff0c\u4e5f\u7b97\u662f\u975e\u5e38\u5c11\u89c1\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u6885\u83f2\u65af\u5bf9\u4e8e\u8fd9\u4e9b\u5012\u4e5f\u5e76\u4e0d\u5728\u610f\uff0c\u4f46\u5f53\u743c\u6069\u63d0\u5230\u201c\u96f7\u9e23\u4e4b\u5b50\u201d\u7684\u65f6\u5019\uff0c\u5979\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u201c\u5927\u52bf\u6240\u8d8b\u3002\u201d\u5979\u8bf4\u3002</p><p>\u201c\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u6ca1\u542c\u660e\u767d\u5979\u7684\u610f\u601d\u3002</p><p>\u201c\u4e0a\u53e4\u4f20\u8bf4\uff0c\u5728\u5927\u7ea6\u516d\u5343\u5e74\u524d\uff0c\u77ee\u4eba\u65cf\u66fe\u7ecf\u5728\u5927\u9646\u4e0a\u5efa\u7acb\u8fc7\u5e9e\u5927\u7684\u5e1d\u56fd\uff0c\u201d\u5c11\u5973\u89e3\u91ca\uff0c\u201c\u4f46\u540e\u6765\u4eba\u7c7b\u5d1b\u8d77\uff0c\u77ee\u4eba\u65cf\u88ab\u8feb\u9000\u5c45\u6df1\u5c71\u3002\u201d</p><p>\u77ee\u4eba\u65cf\u9000\u5c45\u6df1\u5c71\uff0c\u5949\u884c\u4fdd\u5b88\u5c01\u95ed\u7684\u4f5c\u98ce\uff0c\u548c\u4eba\u7c7b\u793e\u4f1a\u65ad\u7edd\u5f80\u6765\u3002\u521d\u65f6\u8fd8\u80fd\u76f8\u5b89\u65e0\u4e8b\uff0c\u4f46\u968f\u7740\u4eba\u7c7b\u7e41\u884d\u65e5\u591a\uff0c\u6269\u5f20\u52a0\u5267\uff0c\u539f\u6709\u7684\u5730\u57df\u4e0d\u80fd\u5bb9\u7eb3\uff0c\u4e0d\u53ef\u907f\u514d\u5730\u53c8\u5f00\u59cb\u5f00\u53d1\u5c71\u533a\uff0c\u4fb5\u5165\u77ee\u4eba\u65cf\u7684\u5730\u76d8\u3002</p><p>\u5982\u4eca\u5728\u4eba\u7c7b\u7684\u57ce\u5e02\u4e2d\uff0c\u77ee\u4eba\u7684\u8eab\u5f71\u5df2\u7ecf\u8d8a\u6765\u8d8a\u5e38\u89c1\uff0c\u5305\u62ec\u5546\u4eba\u3001\u5de5\u5320\u3001\u4f63\u5175\u3001\u5192\u9669\u8005\uff0c\u751a\u81f3\u79fb\u5c45\u800c\u6765\u7684\u5e73\u6c11\u767e\u59d3\uff1b\u800c\u5728\u77ee\u4eba\u805a\u5c45\u5730\uff0c\u4e5f\u6e10\u6e10\u80fd\u770b\u5230\u4eba\u7c7b\u7684\u8eab\u5f71\u3002\u4fbf\u5982\u8fd9\u5ea7\u963f\u5fb7\u5df4\u57ce\uff0c\u4ee5\u524d\u7eaf\u7136\u5c31\u662f\u77ee\u4eba\u7684\u5730\u76d8\uff0c\u6839\u672c\u4e0d\u5141\u8bb8\u4eba\u7c7b\u6765\u5f80\uff0c\u5982\u4eca\u4e5f\u52a0\u5165\u4e86\u94f6\u6708\u8054\u90a6\uff0c\u4e5f\u63a5\u7eb3\u4eba\u7c7b\u7684\u5546\u65c5\u548c\u6e38\u5ba2\u4e86\u3002\u4eba\u7c7b\u548c\u77ee\u4eba\uff0c\u4ea4\u5f80\u8d8a\u6765\u8d8a\u5bc6\u5207\uff0c\u8d8a\u6765\u8d8a\u9891\u7e41\u3002</p><p>\u8fd9\u79cd\u5c40\u9762\u7684\u6539\u53d8\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u8981\u5f52\u529f\u4e8e\u96f7\u9e23\u4e4b\u5b50\u3002\u662f\u4ed6\u4eec\u5021\u5bfc\u653e\u5f03\u77ee\u4eba\u65cf\u7684\u4fdd\u5b88\u4f5c\u98ce\uff0c\u4e3b\u52a8\u8fdb\u5165\u4eba\u7c7b\u793e\u4f1a\uff0c\u5b66\u4e60\u9b54\u6cd5\u548c\u4eba\u7c7b\u7684\u6587\u660e\uff0c\u53c2\u8003\u501f\u9274\u4eba\u7c7b\u7684\u793e\u4f1a\u4f53\u5236\uff0c\u4fc3\u4f7f\u4eba\u7c7b\u548c\u77ee\u4eba\u4ea4\u6d41\u6765\u5f80\u7684\u52a0\u901f\u3002</p><p>\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u4ed6\u5927\u7ea6\u660e\u767d\u4e86\u6885\u83f2\u65af\u521a\u624d\u6240\u8bf4\u7684\u201c\u5927\u52bf\u6240\u8d8b\u201d\u662f\u4ec0\u4e48\u610f\u601d\u3002</p><p>\u4eba\u7c7b\u7684\u7e41\u884d\u3001\u6269\u5c55\uff0c\u8ba9\u77ee\u4eba\u65cf\u5df2\u7ecf\u65e0\u6cd5\u7ee7\u7eed\u95ed\u5173\u9501\u56fd\u4e0b\u53bb\u3002\u957f\u6b64\u4ee5\u5f80\uff0c\u88ab\u541e\u5e76\uff0c\u6216\u8005\u8bf4\u201c\u878d\u5408\u201d\u662f\u4e0d\u53ef\u907f\u514d\u7684\u4e8b\u60c5\uff0c\u4eba\u7c7b\u539f\u672c\u5c31\u662f\u6781\u5177\u4fb5\u7565\u6027\u7684\u79cd\u65cf\u3002\u743c\u6069\u751a\u81f3\u53ef\u4ee5\u60f3\u50cf\uff0c\u767e\u5e74\u4e4b\u540e\uff0c\u53ea\u6015\u5927\u9646\u4e0a\u5c31\u6ca1\u6709\u201c\u77ee\u4eba\u201d\u8fd9\u4e2a\u72ec\u7acb\u7684\u79cd\u65cf\u4e86\u3002</p><p>\u65e2\u7136\u878d\u5408\u5df2\u7ecf\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u4e0d\u53ef\u9006\u8f6c\uff0c\u90a3\u4e0e\u5176\u5750\u5b88\u7b49\u5f85\uff0c\u4e0d\u5982\u79ef\u6781\u4e3b\u52a8\uff0c\u4e89\u53d6\u66f4\u5927\u7684\u5229\u76ca\u3002\u77ee\u4eba\u4e3b\u795e\u83ab\u62c9\u4e01\u964d\u4e0b\u96f7\u9e23\u795d\u798f\uff0c\u8bde\u751f\u96f7\u9e23\u4e4b\u5b50\uff0c\u53ef\u80fd\u76ee\u7684\u4e5f\u5728\u4e8e\u6b64\u5427\u3002</p><p>\u5728\u521a\u624d\u548c\u8001\u77ee\u4eba\u7684\u8c08\u8bdd\u4e2d\uff0c\u743c\u6069\u4e5f\u77e5\u9053\uff1a\u96f7\u9e23\u795d\u798f\u4e4b\u5e74\u51fa\u751f\u7684\u77ee\u4eba\u662f\u96f7\u9e23\u4e4b\u5b50\uff0c\u4f46\u5176\u540e\u51fa\u751f\u7684\u77ee\u4eba\uff0c\u4f9d\u7136\u8fd8\u662f\u666e\u901a\u7684\u77ee\u4eba\uff0c\u5e76\u6ca1\u6709\u8868\u73b0\u51fa\u591a\u5c11\u9b54\u6cd5\u5929\u8d4b\u3002\u96f7\u9e23\u4e4b\u5b50\u867d\u7136\u5929\u8d4b\u5f3a\u5927\uff0c\u4f46\u8fd9\u79cd\u5929\u8d4b\u66f4\u591a\u6765\u81ea\u795e\u8d50\uff0c\u53ea\u6015\u4e5f\u4e0d\u80fd\u7e41\u884d\u7ee7\u627f\u4e0b\u53bb\u3002\u76ee\u524d\u77ee\u4eba\u65cf\u7684\u5deb\u5e08\u9636\u5c42\uff0c\u5c31\u662f\u9760\u8fd9\u4e9b\u201c\u96f7\u9e23\u4e4b\u5b50\u201c\u652f\u6491\u7740\uff0c\u4ed6\u4eec\u540e\u7ee7\u65e0\u4eba\u3002</p><p>\u4ec5\u4ec5\u4e00\u4ee3\u4eba\uff0c\u80fd\u505a\u4ec0\u4e48\u5462\uff1f</p><p>\u77ee\u4eba\u7684\u5bff\u547d\u6bd4\u4eba\u7c7b\u957f\u5f97\u591a\uff0c\u4e00\u822c\u80fd\u6d3b\u5230\u56db\u767e\u5c81\u3002\u96f7\u9e23\u795d\u798f\u662f\u516d\u5341\u516d\u5e74\u524d\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u4e9b\u96f7\u9e23\u4e4b\u5b50\u4eec\uff0c\u5927\u6982\u8fd8\u80fd\u53d1\u6325\u4f5c\u7528\u7684\u65f6\u95f4\u662f\u4e09\u767e\u5e74\u3002\u8fc7\u4e86\u8fd9\u6bb5\u65f6\u95f4\uff0c\u96f7\u9e23\u4e4b\u5b50\u51cb\u96f6\u53bb\u4e16\uff0c\u77ee\u4eba\u65cf\u53d7\u5148\u5929\u6761\u4ef6\u7684\u9650\u5236\uff0c\u5728\u9b54\u6cd5\u4e0a\u7684\u6210\u5c31\u80af\u5b9a\u4f1a\u6bd4\u73b0\u5728\u9000\u6b65\u3002</p><p>\u96be\u9053\u8fd9\u4e09\u767e\u5e74\u5185\uff0c\u77ee\u4eba\u548c\u4eba\u7c7b\u5c31\u8981\u5168\u9762\u878d\u5408\u4e48\u2026\u2026\u8fd8\u662f\u8bf4\u672a\u96e8\u7ef8\u7f2a\uff0c\u65e9\u4f5c\u51c6\u5907\uff1f</p><p>\u53cd\u6b63\uff0c\u5927\u52bf\u6240\u8d8b\uff0c\u4e0d\u53ef\u9006\u8f6c\uff0c\u53ea\u80fd\u79ef\u6781\u8fce\u4e0a\u3002</p><p>\u743c\u6069\u601d\u7d22\u7740\uff0c\u5012\u662f\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\u6765\u3002\u5982\u679c\u77ee\u4eba\u65cf\u548c\u4eba\u7c7b\u771f\u7684\u878d\u5408\uff0c\u90a3\u5728\u5b97\u6559\u4fe1\u4ef0\u9886\u57df\uff0c\u52bf\u5fc5\u6709\u4e00\u573a\u4e89\u593a\uff0c\u4fbf\u5982\u4eba\u7c7b\u6709\u4eba\u7c7b\u7684\u6218\u795e\uff0c\u77ee\u4eba\u6709\u77ee\u4eba\u7684\u6218\u795e\u2014\u2014\u4ee5\u524d\u5404\u62dc\u5404\u7684\uff0c\u4e92\u4e0d\u76f8\u5e72\uff0c\u5982\u4eca\u878d\u5408\u4e3a\u4e00\uff0c\u96be\u9053\u8fd8\u80fd\u5bb9\u5fcd\u4e24\u4e2a\u6218\u795e\u5e76\u7acb\u4e0d\u6210\uff1f</p></article>"}}}]);
