(window.webpackJsonp=window.webpackJsonp||[]).push([[852],{875:function(p){p.exports={chapterTitle:null,title:"\u7b2c849\u7ae0 \u9152\u4f1a\u4e0a\u7684\u501f\u52bf",content:"<article><h2>\u7b2c849\u7ae0 \u9152\u4f1a\u4e0a\u7684\u501f\u52bf</h2><p>\u3000\u3000\u51fa\u5e2d\u8fd9\u6837\u7684\u9152\u4f1a\uff0c\u5f20\u606a\u8fd8\u662f\u5c3d\u53ef\u80fd\u7684\u4f4e\u8c03\uff0c\u4e0a\u53f0\u53d1\u8a00\u662f\u7edd\u8ba1\u4e0d\u4f1a\u7684\uff0c\u4e5f\u4e0d\u4f1a\u60f9\u4eba\u77a9\u76ee\u7684\u62c9\u7740\u9648\u6587\u806a\u7b49\u4eba\u957f\u804a\u4e0b\u53bb\uff0c\u4e8c\u5341\u591a\u5c81\u7684\u9752\u5e74\u7ed9\u4e00\u7fa4\u8eab\u5bb6\u5de8\u4ebf\u7684\u4e2d\u8001\u5e74\u56f4\u5728\u4e00\u8d77\u7684\u611f\u89c9\u975e\u5e38\u7684\u7a81\u5140\u3002\u4e0e\u9648\u6587\u806a\u7b49\u4eba\u7565\u52a0\u5bd2\u6684\uff0c\u66f4\u591a\u65f6\u5019\u8fd8\u662f\u4e0e\u5510\u5a67\u7ad9\u5728\u89d2\u843d\u91cc\u54c1\u5c1d\u7740\u9152\u4f1a\u51c6\u5907\u7684\u7f8e\u98df\uff0c\u867d\u8bf4\u4e0e\u76db\u88c5\u51fa\u5e2d\u3001\u660e\u8273\u7167\u4eba\u7684\u5510\u5a67\u7ad9\u5728\u4e00\u8d77\uff0c\u4f1a\u66f4\u60f9\u4eba\u77a9\u76ee\uff0c\u751a\u81f3\u906d\u4eba\u5fcc\u6068\uff0c\u4f46\u8fd9\u79cd\u60f9\u4eba\u77a9\u76ee\u4e0d\u4f1a\u663e\u5f97\u7a81\u5140\u3002\u4eca\u5929\u7684\u9152\u4f1a\u9080\u8bf7\u8bb8\u591a\u9999\u6e2f\u653f\u5546\u540d\u6d41\uff0c\u4ed5\u5973\u5982\u4e91\uff0c\u4e5f\u591a\u6709\u7fe9\u7fe9\u4e16\u5bb6\u516c\u5b50\u5728\u573a\uff0c\u5f20\u606a\u6df7\u8ff9\u5176\u4e2d\uff0c\u666e\u901a\u4eba\u603b\u770b\u4e0d\u51fa\u592a\u591a\u7684\u5f02\u5e38\u6765\u7684\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7ad9\u5728\u4e00\u65c1\u7b11\u7740\u8bf4\uff1a\u201c\u5929\u4e0b\u8be5\u6709\u591a\u5c11\u7236\u6bcd\u54ed\u7740\u558a\u7740\u60f3\u5c06\u5973\u513f\u96f6\u5241\u6574\u53ea\u7684\u5356\u7ed9\u4f60\uff1f\u201d\u7fdf\u4e39\u9752\u8fd8\u662f\u4e0b\u5348\u90a3\u8eab\u88c5\u626e\uff0c\u6df1\u7d2b\u8272\u7684\u5957\u88d9\uff0c\u767d\u8272\u957f\u8896\u886c\u886b\u4e0b\u6446\u675f\u5230\u5957\u88d9\u91cc\uff0c\u7ea4\u8170\u7684\u8170\u80a2\u8ba9\u80f8\u81c0\u7684\u66f2\u7ebf\u6108\u53d1\u7684\u8bf1\u4eba\uff0c\u5728\u9152\u4f1a\u73b0\u573a\u6bd4\u8d77\u534e\u4e3d\u76db\u88c5\u7684\u5510\u5a67\uff0c\u5979\u66f4\u662f\u6563\u53d1\u7740\u522b\u4e00\u79cd\u8ff7\u4eba\u7684\u97f5\u5473\uff0c\u4f1a\u8ba9\u4eba\u7981\u4e0d\u4f4f\u5f80\u529e\u516c\u5ba4\u91cc\u7684\u98ce\u6d41\u97f5\u4e8b\u4e0a\u8054\u60f3\u3002</p><p>\u3000\u3000\u201c\u4eba\u5bb6\u7684\u773c\u795e\u8fd8\u5728\u5f80\u8fd9\u91cc\u98d8\u2026\u2026\u201d\u5510\u5a67\u7c89\u6da6\u7684\u5c0f\u5634\u9644\u5728\u5f20\u606a\u7684\u8033\u8fb9\u8f7b\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5510\u5a67\u8fd9\u4e48\u8bf4\u7740\uff0c\u5f20\u606a\u53ef\u4e0d\u6562\u56de\u5934\u53bb\u770b\uff0c\u4ed6\u534a\u4f9d\u7740\u89d2\u843d\u91cc\u7684\u6728\u5236\u7ea2\u6f06\u6258\u67b6\uff0c\u5fc3\u60f3\u9648\u5a77\u5a75\u5927\u6982\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\u591a\u4e00\u4e9b\u597d\u5947\u5fc3\u7f62\u4e86\uff0c\u4ed6\u624d\u4e0d\u4fe1\u9648\u6587\u806a\u8fd9\u65f6\u5019\u6709\u5fc3\u601d\u73a9\u8054\u59fb\u90a3\u5957\u628a\u620f\u3002\u5bf9\u7fdf\u4e39\u9752\u4e0e\u5510\u5a67\u7684\u5632\u7b11\u54c2\u7136\u4e0d\u7406\uff0c\u624d\u60f3\u8d77\u5728\u673a\u573a\u5230\u9152\u5e97\u7684\u8def\u4e0a\uff0c\u53f6\u5efa\u658c\u8bf4\u8d77\u6c5f\u9edb\u513f\u4f1a\u51fa\u5e2d\u9152\u4f1a\uff0c\u8fd9\u4f1a\u513f\u5374\u6ca1\u6709\u770b\u5230\u5979\u4e0e\u5b59\u9759\u9999\u7b49\u4eba\u7684\u8eab\u5f71\uff0c\u6b63\u5f85\u95ee\u5510\u5a67\uff0c\u5c31\u770b\u89c1\u6c5f\u9edb\u513f\u7684\u8eab\u5f71\u51fa\u73b0\u5728\u5bb4\u4f1a\u5385\u7684\u95e8\u53e3\uff0c\u7a7f\u7740\u7f00\u6ee1\u4eae\u94f6\u8272\u8d34\u7247\u7684\u62fd\u5730\u957f\u88d9\uff0c\u5c31\u50cf\u4e00\u5c3e\u4eba\u9c7c\u6e38\u5165\u9152\u4f1a\u73b0\u573a\uff0c\u987f\u65f6\u5c06\u9152\u4f1a\u91cc\u4f17\u4eba\u7684\u76ee\u5149\u5438\u5f15\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u9edb\u513f\u5979\u4eec\u600e\u4e48\u624d\u8fc7\u6765\uff1f\u201d\u5f20\u606a\u7591\u60d1\u7684\u95ee\u5510\u5a67\u3002</p><p>\u3000\u3000\u201c\u5148\u8981\u53bb\u7406\u5de5\u5927\u53c2\u52a0\u6f14\u51fa\uff0c\u542c\u8bf4\u9152\u4f1a\u4e0a\u5979\u8fd8\u8981\u6709\u6f14\u5531\u2026\u2026\u201d\u5510\u5a67\u8bf4\u9053\uff0c\u201c\u4f60\u770b\u5979\u597d\u50cf\u5f88\u8f9b\u82e6\u554a\u3002\u201d</p><p>\u3000\u3000\u9694\u7740\u6512\u52a8\u7684\u4eba\u5934\u770b\u8fc7\u53bb\uff0c\u6c5f\u9edb\u513f\u773c\u76ae\u5b50\u53d1\u8f6f\uff0c\u660e\u4e3d\u9274\u4eba\u7684\u8138\u86cb\u4e0a\u75b2\u6001\u8f83\u6df1\uff0c\u8981\u51c6\u5907\u7b2c\u4e09\u5f20\u4e13\u8f91\uff0c\u53c8\u8981\u51fa\u5e2d\u5404\u79cd\u5546\u4e1a\u6d3b\u52a8\uff0c\u8f9b\u82e6\u90a3\u662f\u4e00\u5b9a\u7684\u3002\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u770b\u5230\u5b59\u9759\u9999\u4e0e\u8bb8\u7ef4\u4e5f\u968f\u6c5f\u9edb\u513f\u51fa\u73b0\u5728\u5bb4\u4f1a\u5385\u73b0\u573a\uff0c\u6c5f\u9edb\u513f\u7ed9\u8ba4\u51fa\u5979\u7684\u4e00\u4e9b\u4eba\u56f4\u4f4f\u8d70\u4e0d\u8fc7\u6765\uff0c\u5f20\u606a\u62db\u624b\u8ba9\u5b59\u9759\u9999\u3001\u8bb8\u7ef4\u8fc7\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u5265\u524a\u52b3\u5de5\u5012\u662f\u5f88\u6709\u5fc3\u5f97\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u770b\u4e86\u5510\u5a67\u4e00\u773c\uff0c\u79c0\u76f4\u7684\u7709\u5934\u8f7b\u626c\uff0c\u53c8\u4fa7\u5934\u6324\u5151\u5f20\u606a\uff1a\u201c\u5c0f\u5a67\u8fd9\u4e48\u6253\u626e\uff0c\u4f60\u8fd8\u6709\u5fc3\u601d\u5bf9\u5176\u4ed6\u5973\u5b69\u5b50\u601c\u82b1\u60dc\u7389\uff1f\u201d\u6ca1\u7b49\u5f20\u606a\u8fa9\u89e3\uff0c\u5b59\u9759\u9999\u626d\u5934\u5429\u5490\u8bb8\u7ef4\uff0c\u201c\u4f60\u53bb\u627e\u848b\u59d0\uff0c\u5c31\u8bf4\u5927\u8001\u677f\u4eca\u5929\u601c\u82b1\u60dc\u7389\u8981\u8d66\u514d\u9edb\u513f\u7684\u82e6\u5dee\u4e8b\uff0c\u4f60\u8fd8\u8981\u8ddf\u5979\u8bf4\uff0c\u8d39\u7528\u54b1\u4eec\u53ef\u4e0d\u4f1a\u5c11\u6536\u2014\u2014\u7b49\u4f1a\u513f\u6211\u4eec\u53e6\u627e\u5730\u65b9\u559d\u4e00\u676f\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u82e6\u7b11\uff0c\u90ed\u677e\u5ef6\u8d70\u5c06\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u9648\u5148\u751f\u5e0c\u671b\u80fd\u4e3a\u606a\u5c11\u4f60\u5f15\u8350\u51e0\u4f4d\u670b\u53cb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u9152\u676f\u653e\u4e0b\uff0c\u8ba9\u7fdf\u4e39\u9752\u8bf7\u53f6\u81fb\u6c11\u7b49\u4eba\u5230\u9694\u58c1\u7684\u4f11\u606f\u5ba4\u7a0d\u5750\u3002</p><p>\u3000\u3000\u4eca\u5929\u7684\u9152\u4f1a\uff0c\u9664\u4e86\u9999\u6e2f\u7684\u653f\u5546\u540d\u6d41\uff0c\u90ed\u677e\u5ef6\u3001\u9648\u6587\u806a\u8fd8\u51fa\u9762\u9080\u8bf7\u4e00\u4e9b\u4e1c\u5357\u4e9a\u7684\u534e\u5546\u4ee3\u8868\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\u9526\u6e56\u5728\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e2d\u4e3a\u534e\u4eba\u534e\u4fa8\u540c\u80de\u6240\u505a\u7684\u8d21\u732e\u3002\u5f20\u606a\u4e0d\u60f3\u5728\u9152\u4f1a\u4e0a\u592a\u60f9\u4eba\u77a9\u76ee\uff0c\u9152\u4f1a\u8fdb\u884c\u5230\u534a\u9014\uff0c\u5c31\u53e6\u627e\u5730\u65b9\u8bf7\u90ed\u677e\u5ef6\u3001\u9648\u6587\u806a\u4ee3\u4e3a\u5f15\u8350\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5d14\u6587\u70e8\u3001\u4f55\u7167\u594e\u3001\u9a6c\u6587\u6e20\u7686\u51fa\u8eab\u4e1c\u5357\u4e9a\u8457\u5404\u7684\u534e\u4eba\u5bb6\u65cf\uff0c\u4f55\u7167\u594e\u3001\u9a6c\u6587\u6e20\u90fd\u662f\u5370\u5c3c\u534e\u4eba\uff0c\u4f55\u5bb6\u3001\u9a6c\u5bb6\u4e0e\u9648\u6587\u806a\u6240\u5c5e\u7684\u9648\u5bb6\u90fd\u662f\u5370\u5c3c\u6709\u540d\u7684\u534e\u4eba\u5bb6\u65cf\uff0c\u5d14\u6587\u70e8\u662f\u83f2\u5f8b\u5bbe\u534e\u4eba\uff0c\u4f46\u662f\u59bb\u5b50\u4f55\u7167\u7199\u662f\u5370\u5c3c\u4f55\u5bb6\u7684\u5b50\u5973\uff0c\u4e0e\u4f55\u7167\u594e\u662f\u5802\u5144\u59b9\u3002</p><p>\u3000\u3000\u4f55\u7167\u594e\u3001\u4f55\u5bb6\u5728\u5370\u5c3c\u4e3b\u8425\u8d85\u5546\u3001\u9152\u5e97\u3001\u623f\u5730\u4ea7\u4e1a\u52a1\uff0c\u5728\u8fd9\u6b21\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e2d\uff0c\u540d\u4e0b\u5728\u96c5\u52a0\u8fbe\u5730\u533a\u5c31\u6709\u516b\u5bb6\u5927\u578b\u8d85\u5546\u88ab\u66b4\u5f92\u6253\u7838\u70e7\u6bc1\uff0c\u7531\u4e8e\u4f55\u5bb6\u5728\u96c5\u52a0\u8fbe\u7ecf\u8425\u9152\u5e97\u90fd\u662f\u9ad8\u89c4\u683c\u7684\u661f\u7ea7\u9152\u5e97\uff0c\u9a9a\u4e71\u53d1\u751f\u65f6\u6709\u5916\u56fd\u4eba\u5165\u4f4f\uff0c\u5012\u662f\u907f\u5f00\u4e86\u52ab\u96be\uff0c\u5bb6\u4eba\u4e5f\u90fd\u53ca\u65f6\u79bb\u5f00\u5370\u5c3c\uff0c\u4f46\u662f\u65d7\u4e0b\u6240\u8058\u534e\u4eba\u5458\u5de5\u6b7b\u4f24\u516d\u5341\u4f59\u4eba\uff0c\u635f\u5931\u53ef\u8c13\u60e8\u91cd\u3002</p><p>\u3000\u3000\u9a6c\u6587\u6e20\u9a6c\u5bb6\u62e5\u6709\u9a6c\u516d\u7532\u6d77\u5ce1\u533a\u57df\u7684\u7b2c\u4e8c\u5927\u6d77\u8fd0\u4f01\u4e1a\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\uff0c\u4e1a\u52a1\u65e9\u5c31\u4e0d\u9650\u4e8e\u5370\u5c3c\u56fd\u5185\u6d77\u8fd0\u3001\u5185\u6cb3\u8fd0\u52a1\uff0c\u6b64\u6b21\u906d\u53d7\u7684\u76f4\u63a5\u635f\u5931\u4e0d\u7b97\u7279\u522b\u4e25\u91cd\uff0c\u4e0d\u8fc7\u9a6c\u6587\u6e20\u4e0e\u5bb6\u4eba\u5374\u7ecf\u5386\u4e86\u98ce\u9669\uff0c\u53e6\u5916\u5728\u96c5\u52a0\u8fbe\u4e5f\u6709\u5341\u591a\u540d\u8239\u5458\u5931\u8e2a\uff0c\u8fd9\u4e9b\u8239\u5458\u7edd\u5927\u591a\u6570\u90fd\u4e0d\u662f\u5370\u5c3c\u534e\u4eba\uff0c\u800c\u662f\u9a6c\u5bb6\u4ece\u83f2\u5f8b\u5bbe\u3001\u9a6c\u6765\u897f\u4e9a\u8058\u8bf7\u7684\u534e\u4eba\u8239\u5458\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u6216\u8bb8\u8fd8\u4e0d\u77e5\u9053\u9526\u6e56\u5176\u5b9e\u4e0e\u6211\u4eec\u9a6c\u5bb6\u4e00\u76f4\u90fd\u6709\u4e1a\u52a1\u5f80\u6765\u7684\u2026\u2026\u201d\u9648\u6587\u806a\u4ecb\u7ecd\u5230\u9a6c\u6587\u6e20\u65f6\uff0c\u9a6c\u6587\u6e20\u5bbd\u539a\u7684\u624b\u638c\u63e1\u7d27\u5f20\u606a\u7684\u624b\uff0c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u7565\u60f3\u4e86\u4e00\u4e0b\uff0c\u624d\u8fdf\u7591\u7684\u95ee\u9053\uff0c\u201c\u9a6c\u5148\u751f\u662f\u8bf4\u65b0\u5149\u7eb8\u4e1a\u4ece\u9a6c\u6765\u897f\u4e9a\u8fdb\u53e3\u7684\u5546\u54c1\u6728\u6d46\uff1f\u201d</p><p>\u3000\u3000\u201c\u5bf9\uff0c\u5c31\u662f\u9526\u6e56\u4ece\u9a6c\u6765\u897f\u4e9a\u8fdb\u53e3\u7684\u6728\u6d46\u662f\u7531\u5357\u6d0b\u7684\u8239\u53ea\u8fd0\u8f93\u2026\u2026\u201d\u9a6c\u6587\u6e20\u9053\u8bf4\u9053\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u8fd1\u51e0\u5e74\u6765\u6bcf\u5e74\u90fd\u8981\u4ece\u4e1c\u5357\u4e9a\u7b49\u5730\u8fdb\u53e3\u5927\u91cf\u7684\u5546\u54c1\u6728\u6d46\uff0c\u4e0e\u4e91\u6e90\u96c6\u56e2\u5408\u4f5c\u4e4b\u540e\uff0c\u9a6c\u6765\u897f\u4e9a\u5c31\u6210\u4e3a\u65b0\u5149\u7eb8\u4e1a\u7684\u539f\u6599\u6d46\u6765\u6e90\u5730\uff0c\u59d4\u6258\u5357\u6d0b\u6d77\u8fd0\u8fd0\u8f93\u3002\u867d\u7136\u65b0\u5149\u7eb8\u4e1a\u4e5d\u4e03\u5e74\u8981\u4ece\u6d77\u5916\u8fdb\u53e3\u8d85\u8fc71\u4ebf\u7f8e\u5143\u5546\u54c1\u6728\u6d46\uff0c\u4f46\u662f\u8fd9\u70b9\u8fd0\u8f93\u91cf\u76f8\u6bd4\u5357\u6d0b\u6d77\u8fd0\u5168\u5e74\u7684\u4e1a\u52a1\u91cf\uff0c\u6839\u672c\u5c31\u4e0d\u503c\u5f97\u4e00\u63d0\uff0c\u8981\u4e0d\u662f\u53d1\u751f\u8fd9\u6837\u7684\u53d8\u6545\uff0c\u9a6c\u6587\u6e20\u90fd\u610f\u8bc6\u4e0d\u5230\u4ed6\u4e0e\u9526\u6e56\u8fd8\u6709\u4e1a\u52a1\u5f80\u6765\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u9a6c\u6587\u6e20\u5148\u63d0\u53ca\uff0c\u5f20\u606a\u4e5f\u610f\u8bc6\u4e0d\u5230\u9526\u6e56\u8ddf\u5357\u6d0b\u6d77\u8fd0\u6709\u4e1a\u52a1\u5f80\u6765\uff0c\u6400\u7740\u9a6c\u6587\u6e20\u7684\u624b\u4e00\u8d77\u5750\u4e0b\u3002</p><p>\u3000\u3000\u201c\u96c5\u52a0\u8fbe\u53d1\u751f\u6392\u534e\u9a9a\u4e71\u65f6\uff0c\u6211\u4e0e\u5bb6\u4eba\u7ed9\u51e0\u5341\u540d\u571f\u8457\u66b4\u6c11\u56f0\u5728\u57ce\u90ca\u7684\u522b\u5885\u91cc\u8fdb\u51fa\u4e0d\u5f97\uff0c\u867d\u6709\u51e0\u540d\u4fdd\u9556\u4e5f\u4e0d\u6d4e\u4e8e\u4e8b\uff0c\u751a\u81f3\u6709\u4e24\u540d\u4fdd\u9556\u5fc3\u751f\u754f\u60e7\u8d81\u591c\u9003\u8d70\u4e86\uff0c\u201d\u9a6c\u6587\u6e20\u56de\u5fc6\u8d77\u4e94\u6708\u5341\u65e5\u88ab\u56f0\u5bb6\u4e2d\u7684\u60c5\u5f62\uff0c\u201c\u524d\u5f80\u673a\u573a\u7684\u9053\u8def\u90fd\u7ed9\u66b4\u6c11\u8bbe\u7f6e\u4e86\u8def\u969c\uff0c\u5341\u4e00\u65e5\u591c\u91cc\uff0c\u4e0d\u7518\u5fc3\u7a7a\u624b\u800c\u8fd4\u7684\u66b4\u6c11\u589e\u52a0\u5230\u4e0a\u767e\u4eba\uff0c\u56f4\u805a\u5728\u95e8\u5916\uff0c\u90a3\u65f6\u771f\u6709\u8eab\u5982\u98d8\u840d\u7ed9\u98ce\u96e8\u6467\u6b8b\u7684\u65e0\u529b\u611f\uff0c\u76f4\u5230\u5341\u4e00\u65e5\u6df1\u591c\uff0c\u5728\u56fd\u9645\u8206\u8bba\u7684\u538b\u529b\uff0c\u7eb5\u5bb9\u66b4\u6c11\u7684\u5370\u5c3c\u519b\u8b66\u4e0d\u5f97\u4e0d\u51fa\u9762\u5e73\u606f\u9a9a\u4e71\uff0c\u6211\u4e0e\u5bb6\u4eba\u624d\u6709\u673a\u4f1a\u9003\u79bb\u5370\u5c3c\u2026\u2026\u62b5\u8fbe\u65b0\u52a0\u5761\u540e\uff0c\u624d\u77e5\u9053\u9526\u6e56\u505a\u51fa\u6781\u5927\u7684\u52aa\u529b\u624d\u5c06\u65b0\u95fb\u8d44\u6599\u53ca\u65f6\u7684\u4f20\u51fa\u5370\u5c3c\u56fd\u5883\u5f15\u8fdb\u56fd\u9645\u793e\u4f1a\u5bf9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u5173\u6ce8\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5f88\u4e0d\u5fcd\u5fc3\u542c\u5f53\u4e8b\u4eba\u56de\u5fc6\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u5b9e\u51b5\uff0c\u60b2\u4f24\u7684\u5fae\u62bf\u7740\u5634\u5507\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u4eca\u5929\u9648\u6587\u806a\u3001\u90ed\u677e\u5ef6\u51fa\u9762\u9080\u8bf7\u7684\u4e1c\u5357\u4e9a\u534e\u5546\u4ee3\u8868\u90fd\u662f\u5bf9\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u6709\u7740\u6df1\u5165\u9aa8\u9ad3\u4e4b\u75db\u7684\u4eba\uff0c\u5f88\u591a\u60c5\u7eea\u90fd\u4e0d\u9700\u8981\u523b\u610f\u7684\u7528\u8a00\u8bed\u6765\u8868\u8fbe\u3001\u6765\u4f20\u8fbe\u3002</p><p>\u3000\u3000\u53f6\u81fb\u6c11\u80fd\u6e05\u695a\u7684\u8ba4\u8bc6\u5230\u8fd9\u79cd\u63ba\u6742\u7740\u5f3a\u70c8\u4e0d\u5b89\u7684\u60b2\u4f24\u60c5\u7eea\uff0c\u6709\u5229\u4e8e\u9526\u6e56\u5546\u4e8b\u4ece\u4e1c\u5357\u4e9a\u534e\u4eba\u534e\u4fa8\u90a3\u91cc\u805a\u96c6\u66f4\u591a\u7684\u529b\u91cf\uff0c\u7279\u522b\u6b64\u65f6\u6b63\u503c\u534e\u4eba\u8d44\u672c\u4ece\u5370\u5c3c\u5927\u89c4\u6a21\u7684\u6050\u614c\u64a4\u79bb\uff0c\u5f97\u5230\u9648\u3001\u4f55\u3001\u9a6c\u8bf8\u5bb6\u65cf\u7684\u4fe1\u4efb\uff0c\u9526\u6e56\u5546\u4e8b\u5c31\u80fd\u5438\u53d6\u8db3\u591f\u591a\u7684\u7ecf\u8425\u8d44\u672c\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u4f1a\u9762\u65e0\u7591\u662f\u6210\u529f\u7684\uff0c\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u3001\u4f55\u7167\u594e\u90fd\u4e3b\u52a8\u8ddf\u9526\u6e56\u62c9\u8fd1\u4e86\u5173\u7cfb\u2014\u2014\u5c31\u8fde\u5d14\u6587\u70e8\u4e5f\u8868\u793a\u8981\u8d2d\u5356\u9526\u6e56\u5546\u4e8b\u7684\u516c\u53f8\u503a\uff0c\u5173\u952e\u9648\u9a6c\u4f55\u5d14\u518d\u52a0\u90ed\u5b59\u7b49\u4eba\u5728\u4e1c\u5357\u4e9a\u534e\u5546\u5708\u5185\u7684\u5f71\u54cd\u5de8\u5927\u3002\u867d\u8bf4\u9526\u6e56\u5546\u4e8b\u8fd9\u65f6\u5019\u8981\u53d1\u5c55\u7efc\u5408\u5546\u793e\u6a21\u5f0f\u4f1a\u53d7\u5230\u5f88\u5927\u7684\u9650\u5236\uff0c\u5374\u4e5f\u9762\u4e34\u6781\u5927\u7684\u673a\u9047\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u5c0f\u5b50\u771f\u662f\u4e00\u4e2a\u4f1a\u501f\u52bf\u7684\u5bb6\u4f19\uff01</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u7a0d\u6df1\uff0c\u5f20\u606a\u9001\u9648\u6587\u806a\u3001\u90ed\u677e\u5ef6\u3001\u9a6c\u6587\u6e20\u7b49\u4eba\u53ca\u5bb6\u4eba\u79bb\u5f00\u534a\u5c9b\u9152\u5e97\u2014\u2014\u9152\u4f1a\u65e9\u5c31\u5728\u4e5d\u70b9\u534a\u4e4b\u524d\u5c31\u505c\u6b62\u4e86\uff0c\u7edd\u5927\u591a\u6570\u7684\u5e94\u9080\u5609\u5bbe\u90fd\u79bb\u5f00\u534a\u5c9b\u9152\u5e97\u3002</p><p>\u3000\u3000\u9001\u8d70\u4f17\u4eba\uff0c\u5f20\u606a\u8d70\u56de\u9152\u5e97\uff0c\u4e0e\u53f6\u5efa\u658c\u5404\u81ea\u56de\u623f\uff0c\u5510\u5a67\u6ca1\u6709\u5728\u5c4b\u91cc\u7b49\u4ed6\uff0c\u6253\u7535\u8bdd\u8fc7\u53bb\u624d\u77e5\u9053\u5979\u3001\u76db\u590f\u4e0e\u5b59\u9759\u9999\u3001\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u7b49\u51e0\u4e2a\u5973\u5b69\u5b50\u90fd\u5728\u9152\u5e97\u9876\u5c42\u7684\u9152\u5427\u91cc\u805a\u996e\uff0c\u5927\u6982\u5f88\u65e9\u5c31\u4ece\u9152\u4f1a\u79bb\u5f00\u4e86\uff0c\u6c5f\u9edb\u513f\u5979\u4eec\u4e5f\u6ca1\u6709\u6025\u7740\u79bb\u5f00\u534a\u5c9b\u9152\u5e97\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e58\u7535\u68af\u53bb\u9876\u697c\u7684\u9152\u5427\uff0c\u53f6\u5efa\u658c\u6bd4\u4ed6\u8fdf\u4e00\u811a\u4e0a\u697c\u6765\u3002</p><p>\u3000\u3000\u4e00\u7fa4\u82b1\u679d\u62db\u5c55\u7684\u5973\u5b69\u5b50\u5750\u5728\u9152\u5427\u91cc\u4f1a\u683c\u5916\u7684\u60f9\u4eba\u77a9\u76ee\uff0c\u5373\u4f7f\u662f\u534a\u5c9b\u9152\u5e97\u9876\u697c\u7684\u9ad8\u7ea7\u9152\u5427\u91cc\uff0c\u770b\u5230\u7f8e\u5973\u6709\u52c7\u6c14\u4e0a\u524d\u642d\u8baa\u7684\u7537\u4eba\u7edd\u4e0d\u5728\u5c11\u6570\uff0c\u800c\u4e14\u6c5f\u9edb\u513f\u8fd8\u7b3c\u7f69\u7740\u7480\u74a8\u7684\u660e\u661f\u5149\u8292\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u8d70\u8fdb\u9152\u5427\u91cc\uff0c\u6b63\u6709\u540d\u8863\u7740\u8003\u7a76\u7684\u4e2d\u5e74\u4eba\u6316\u7740\u9152\u5427\u7ad9\u5728\u5973\u5b69\u5b50\u684c\u8fb9\u8bf4\u8bdd\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u8d70\u8fc7\u53bb\uff0c\u90a3\u540d\u4e2d\u5e74\u7537\u5b50\u5c31\u7aef\u7740\u9152\u676f\u8d70\u5f00\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u8bf4\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u770b\u4e86\u90a3\u540d\u4e2d\u5e74\u7537\u5b50\u4e00\u773c\uff0c\u89c9\u5f97\u6709\u4e9b\u773c\u719f\uff0c\u4ed6\u5750\u5230\u5510\u5a67\u8eab\u8fb9\uff0c\u95ee\u90a3\u4e2a\u4e2d\u5e74\u7537\u5b50\u521a\u624d\u7ad9\u5728\u8fd9\u91cc\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u4ed6\u662f\u82f1\u738b\u56fd\u9645\u7684\u603b\u88c1\u6768\u6210\u5b88\uff0c\u4ed6\u53ef\u662f\u5355\u7740\u5b59\u59d0\u7684\u9762\u8981\u5c06\u9edb\u513f\u59d0\u6316\u5230\u82f1\u738b\u56fd\u9645\u53bb\u5462\u2026\u2026\u8fd8\u8bf4\u6211\u6709\u5f53\u5076\u50cf\u660e\u661f\u7684\u6f5c\u8d28\u3002\u201d\u5510\u5a67\u5a07\u67d4\u4e00\u7b11\uff0c\u5e2e\u7740\u53f6\u5efa\u658c\u4e0e\u5f20\u606a\u5012\u9152\u3002\u5979\u5c06\u90a3\u8eab\u6027\u611f\u7684\u665a\u793c\u670d\u88d9\u6362\u6389\uff0c\u8fd8\u539f\u4e3a\u725b\u4ed4\u88e4\u3001T\u6064\u886b\u7684\u6e05\u7eaf\u5b66\u751f\u5973\u6253\u626e\uff0c\u81ea\u7531\u81ea\u5728\u7684\u5750\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u8bf4\u662f\u8c01\u5462\uff0c\u96be\u602a\u89c9\u5f97\u773c\u719f\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6768\u6210\u5b88\u773c\u775b\u8fd8\u770b\u7740\u8fd9\u91cc\uff0c\u82f1\u738b\u56fd\u9645\u53ef\u4ee5\u8bf4\u662f\u534e\u4eba\u4e16\u754c\u6700\u5927\u89c4\u6a21\u7684\u6f14\u827a\u7ecf\u6d4e\u516c\u53f8\uff0c\u9999\u6e2f\u6f14\u827a\u5708\u90a3\u4e9b\u9a87\u4eba\u542c\u95fb\u7684\u4e11\u95fb\uff0c\u5012\u6709\u516b\u6210\u8ddf\u82f1\u738b\u56fd\u9645\u6709\u5173\uff0c\u201c\u6768\u6210\u5b88\u4eb2\u81ea\u8fc7\u6765\u6316\u89d2\uff0c\u8fd9\u53ef\u8bc1\u660e\u4e86\u9edb\u513f\u59d0\u7684\u6f14\u827a\u6f5c\u529b\u6b63\u9010\u6e10\u7684\u53d1\u6325\u51fa\u6765\u2026\u2026\u201d</p></article>"}}}]);
