(window.webpackJsonp=window.webpackJsonp||[]).push([[854],{877:function(p){p.exports={chapterTitle:null,title:"\u7b2c679\u7ae0 \u53cd\u5e94",content:"<article><h2>\u7b2c679\u7ae0 \u53cd\u5e94</h2><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u8fd1\u4e00\u5e74\u6765\u6709\u8fc7\u54ea\u4e9b\u60ca\u4eba\u4e4b\u4e3e\u3002</p><p>\u3000\u3000\u4e0e\u98de\u5229\u6d66\u516c\u53f8\u4ea4\u6362\u4e13\u5229\u6280\u672f\uff0c\u4e0e\u5fb7\u4eea\u5408\u8d44\u6210\u7acb\u789f\u673a\u673a\u82af\u5236\u9020\u5de5\u5382\uff0c\u5728\u4e5d\u4e03\u5e74\uff0c\u5728\u7535\u5b50\u4ea7\u4e1a\u9886\u57df\uff0c\u56fd\u5185\u62e5\u6709\u5143\u5668\u4ef6\u5236\u9020\u80fd\u529b\u7684\u4f01\u4e1a\u6709\u5982\u51e4\u6bdb\u9e9f\u89d2\u4e00\u6837\u73cd\u7a00\uff0c\u7231\u8fbe\u96c6\u56e2\u505a\u5230\u4e86\uff0c\u800c\u4e14\u4e0e\u5fb7\u4eea\u5404\u636e\u4e00\u534a\u80a1\u6743\u3001\u8bbe\u7acb\u5728\u6d77\u5dde\u3001\u96c7\u5458\u5927\u534a\u4e3a\u534e\u4eba\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u7684\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u6240\u62e5\u6709\u673a\u82af\u5236\u9020\u6838\u5fc3\u6280\u672f\u7684\u5168\u90e8\u77e5\u8bc6\u4ea7\u6743\uff0c\u53c2\u4e0e\u65b0\u4e00\u4ee3\u789f\u673a\u7684\u7814\u53d1\uff0c\u5960\u5b9a\u7231\u8fbe\u96c6\u56e2\u5728\u789f\u673a\u4ea7\u4e1a\u7684\u5730\u4f4d\u65e0\u4eba\u80fd\u591f\u64bc\u52a8\u3002</p><p>\u3000\u3000\u4e09\u6708\u4e2d\uff0c\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u9648\u4fe1\u751f\u8f6c\u4efb\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\uff0c\u7231\u8fbe\u96c6\u56e2\u82b1\u8d39\u4e94\u5343\u4e07\u7f8e\u5143\u5de8\u8d44\u4ece\u5fb7\u4eea\u624b\u4e2d\u5f15\u8fdb\u5165\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u5168\u5957\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u53c8\u5728\u5efa\u90ba\u6295\u5165\u5341\u4ebf\u8ba1\u7684\u5de8\u8d44\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u83b7\u5f97\u90ae\u7535\u90e8\u53d1\u653e\u7684\u56db\u5f20\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u724c\u7167\u4e2d\u7684\u4e00\u5f20\uff0c\u6602\u9996\u9614\u6b65\u7684\u8fc8\u6b65\u8de8\u8fdb\u624b\u673a\u4ea7\u4e1a\u3002</p><p>\u3000\u3000\u4e94\u6708\u521d\uff0c\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u789f\u673a\u4e1a\u52a1\u501f\u58f3\u4e0a\u5e02\uff0c\u5728\u6052\u751f\u6307\u6570\u6700\u75af\u72c2\u4e4b\u9645\u4ece\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u75af\u72c2\u5377\u8d70\u56db\u5341\u4ebf\uff0c\u582a\u79f0\u8fd1\u5341\u5e74\u6765\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u6700\u6210\u529f\u7684\u8d44\u672c\u8fd0\u4f5c\u4e4b\u4e00\uff0c\u4e5f\u662f\u76ee\u524d\u7ea2\u7b79\u80a1\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e00\u6b21\u7b79\u96c6\u8d44\u91d1\u6700\u591a\u7684\u4e00\u6b21\u8d44\u672c\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u5a92\u4f53\u90fd\u9884\u6d4b\u7231\u8fbe\u96c6\u56e2\u5c06\u662f\u8fd9\u5c4a\u592e\u89c6\u6807\u738b\u6700\u6709\u529b\u7684\u7ade\u593a\u8005\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u5927\u8086\u7684\u62a5\u9053\u7231\u8fbe\u96c6\u56e2\u7684\u4e1a\u7ee9\uff0c\u4eca\u5929\u8fc7\u6765\u53c2\u52a0\u7ade\u6807\u7684\u4f01\u4e1a\u4ee3\u8868\uff0c\u5bf9\u9999\u96ea\u6d77\u6216\u8bb8\u4e0d\u719f\u6089\uff0c\u8981\u662f\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u60c5\u51b5\u4e0d\u80fd\u505a\u5230\u4e86\u5982\u6307\u638c\uff0c\u90a3\u4e5f\u592a\u5c38\u4f4d\u7d20\u9910\u4e86\u3002</p><p>\u3000\u3000\u5728\u7b79\u5907\u5927\u534a\u5e74\u4e4b\u540e\uff0c\u7231\u8fbei08\u3001i19\u4e24\u6b3e\u624b\u673a\u624d\u59d7\u7136\u4e0a\u5e02\uff0c\u752b\u4e0a\u5e02\u5c31\u60ca\u8273\u5168\u56fd\uff0c\u8fd8\u6ca1\u6709\u54ea\u5bb6\u5a92\u4f53\u6216\u8005\u673a\u6784\u7ed9\u51fa\u7231\u8fbe\u4e24\u6b3e\u624b\u673a\u5728\u5341\u6708\u4efd\u7684\u5177\u4f53\u9500\u91cf\uff0c\u4f46\u662fi19\u624b\u673a\u5e38\u5e38\u5728\u76db\u946b\u73af\u7403\u7535\u5668\u95e8\u5e97\u7684\u624b\u673a\u4e13\u5356\u533a\u5356\u65ad\u8d27\u5374\u662f\u4e0d\u4e89\u7684\u4e8b\u5b9e\uff0c\u8ba9\u5728\u573a\u7684\u4f01\u4e1a\u4ee3\u8868\u3001\u5a92\u4f53\u8bb0\u8005\uff08\u5982\u679c\u5a92\u4f53\u8bb0\u8005\u90fd\u914d\u5907\u4e86\u624b\u673a\uff09\u5c06\u624b\u673a\u4ece\u516c\u6587\u5305\u6216\u8863\u515c\u6216\u8170\u95f4\u7684\u624b\u673a\u5957\u91cc\u62ff\u51fa\u6765\uff0c\u4e5f\u7edd\u4e0d\u4e4fi19\u624b\u673a\u7684\u7edd\u8273\u8eab\u5f71\u3002</p><p>\u3000\u3000\u5a92\u4f53\u5c06i19\u624b\u673a\u55bb\u4e3a\u7231\u8fbe\u96c6\u56e2\u7ee7\u789f\u673a\u4e4b\u540e\u53c8\u4e00\u655b\u8d22\u5229\u5668\uff0c\u6216\u8bb8i19\u8fc7\u4e8e\u60ca\u8273\uff0c\u5c06i08\u624b\u673a\u538b\u7684\u98ce\u91c7\u5168\u65e0\uff1b\u4e8b\u5b9e\u4e0a\uff0ci08\u624b\u673a\u4ee5\u5176\u7a33\u5065\u7684\u673a\u578b\u3001\u7a33\u4e1a\u7684\u8d28\u91cf\u4e0e\u76f8\u5bf9\u4f4e\u5ec9\u7684\u552e\u4ef7\uff0c\u752b\u4e0a\u5e02\uff0c\u9500\u91cf\u5c31\u9886\u5148\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u63a8\u51fa\u7684\u51e0\u6b3e\u56fd\u4ea7\u624b\u673a\u3002</p><p>\u3000\u3000\u5a92\u4f53\u90fd\u9884\u6d4b\u7231\u8fbe\u96c6\u56e2\u4f1a\u5728\u624b\u673a\u4ea7\u4e1a\u9886\u57df\u9ad8\u6b4c\u731b\u8fdb\uff0c\u6240\u4ee5\u624d\u5927\u80c6\u9884\u6d4b\u7231\u8fbe\u96c6\u56e2\u4f1a\u679c\u65ad\u51fa\u624b\u5c06\u8fd9\u5c4a\u7684\u592e\u89c6\u6807\u738b\u63fd\u5165\u6000\u91cc\u7528\u4e8e\u64ad\u653e\u4ed6\u4eec\u7684\u624b\u673a\u5e7f\u544a\u3002</p><p>\u3000\u3000\u4ee4\u4f17\u4eba\u610f\u6599\u4e0d\u5230\u7684\uff0c\u7231\u8fbe\u4e0e\u9999\u96ea\u6d77\u5728\u4e0a\u5348\u7684\u7ade\u6807\u4e2d\u8868\u6f14\u4e86\u4e00\u51fa\u7cbe\u5f69\u7684\u201c\u53cc\u4eba\u7a7a\u4e2d\u63a5\u529b\u704c\u7bee\u201d\uff0c\u5c06\u5176\u4ed6\u4f01\u4e1a\u90fd\u6643\u70b9\u8fc7\u53bb\uff0c\u6210\u529f\u7684\u5c06\u9999\u96ea\u6d77\u9001\u4e0a\u592e\u89c6\u6807\u738b\u5b9d\u5ea7\uff0c\u4e0a\u5348\u7684\u7ade\u6807\u7ed3\u675f\uff0c\u7231\u8fbe\u53c8\u7a81\u7136\u5ba3\u5e03\u5982\u6b64\u60ca\u4e16\u9a87\u4fd7\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u5c06\u82b1\u8d39\u4e94\u5343\u4e07\u7f8e\u5143\u4ece\u5fb7\u4eea\u624b\u4e2d\u5f15\u8fdb\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u5168\u5957\u624b\u673a\u5236\u9020\u6280\u672f\u5411\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u4ea7\u4e1a\u56ed\u533a\u7684\u4f01\u4e1a\u516c\u5f00\u3002</p><p>\u3000\u3000\u542c\u5230\u82cf\u6d25\u4e1c\u7a81\u7136\u5ba3\u5e03\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u6240\u6709\u5728\u573a\u7684\u4eba\u90fd\u60ca\u8c14\u7684\u6123\u4f4f\uff0c\u96be\u4ee5\u7f6e\u4fe1\uff0c\u6000\u7591\u81ea\u5df1\u662f\u542c\u9519\u4e86\uff0c\u6216\u8005\u8bf4\u82cf\u6d25\u4e1c\u4ed6\u7a81\u7136\u53d1\u4e86\u795e\u7ecf\u3002</p><p>\u3000\u3000\u9664\u4e86\u7231\u8fbe\u96c6\u56e2\u8fd0\u8425\u90e8\u603b\u88c1\u82cf\u6d25\u4e1c\u4e4b\u5916\uff0c\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u9648\u4fe1\u751f\u5c31\u7ad9\u5728\u4ed6\u8eab\u540e\uff0c\u8fd8\u6709\u53e6\u4e24\u540d\u7231\u8fbe\u96c6\u56e2\u7684\u9ad8\u7ea7\u804c\u5458\uff0c\u4e00\u4e3a\u7231\u8fbe\u96c6\u56e2\u5e02\u573a\u603b\u76d1\u53ca\u9500\u552e\u516c\u53f8\u603b\u7ecf\u7406\u6768\u4e91\uff0c\u4e00\u4e3a\u7231\u8fbe\u96c6\u56e2\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u5468\u4e00\u5e73\uff0c\u90fd\u7ad9\u5728\u4e00\u65c1\uff0c\u770b\u4ed6\u4eec\u7684\u8868\u60c5\uff0c\u8981\u4e48\u4e00\u8d77\u53d1\u4e86\u795e\u7ecf\u75c5\uff0c\u8981\u4e48\u82cf\u6d25\u4e1c\u5c31\u662f\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u5728\u975e\u5e38\u4e25\u8083\u7684\u5ba3\u5e03\u8fd9\u9879\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u201c\u82cf\u603b\uff0c\u8bf7\u95ee\u521a\u624d\u662f\u4e0d\u662f\u6211\u7684\u8033\u6735\u7a81\u7136\u5931\u7075\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ece\u5fb7\u4eea\u624b\u91cc\u8d2d\u5f97\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u4e0d\u662f\u7231\u8fbe\u96c6\u56e2\u624b\u673a\u4e1a\u52a1\u7684\u6839\u57fa\u5417\uff1f\u4e0d\u662f\u7231\u8fbe\u96c6\u56e2\u5411\u624b\u673a\u4ea7\u4e1a\u9ad8\u6b4c\u731b\u8fdb\u7684\u5229\u5668\u5417\uff1f\u7231\u8fbe\u96c6\u56e2\u600e\u4e48\u4f1a\u7a81\u7136\u51b3\u5b9a\u5c06\u76f8\u5173\u6280\u672f\u5411\u5916\u516c\u5f00\uff1f\u201d</p><p>\u3000\u3000\u201c\u6240\u8c13\u7684\u516c\u5f00\u662f\u6307\u4ec0\u4e48\uff0c\u5176\u4ed6\u4f01\u4e1a\u53ef\u4ee5\u4efb\u610f\u4f7f\u7528\uff1f\u201d</p><p>\u3000\u3000\u201c\u6536\u8d39\u8fd8\u662f\u514d\u8d39\uff1f\u201d</p><p>\u3000\u3000\u201c\u6240\u8c13\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u56ed\u533a\u7684\u4f01\u4e1a\u662f\u6307\u4e4b\u524d\u5165\u9a7b\u7684\u6240\u6709\u4f01\u4e1a\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u4e4b\u540e\u5230\u8fd9\u4e24\u4e2a\u56ed\u533a\u6295\u8d44\u6ce8\u518c\u7684\u4f01\u4e1a\u7b97\u4e0d\u7b97\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u5168\u90e8\u516c\u5f00\uff0c\u8fd8\u662f\u53ea\u516c\u5f00\u65e0\u5173\u7d27\u8981\u7684\u90e8\u5206\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u7684\u8bb0\u6027\u4e0d\u9519\uff0c\u7231\u8fbe\u96c6\u56e2\u4e3a\u83b7\u5f97\u76f8\u5173\u6280\u672f\u652f\u4ed8\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\uff0c\u7231\u8fbe\u662f\u8981\u5c06\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u6280\u672f\u8d44\u4ea7\u4e0e\u4e24\u4e2a\u56ed\u533a\u7684\u4f01\u4e1a\u5171\u4eab\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u96c6\u56e2\u4e3a\u4ec0\u4e48\u8981\u505a\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u7231\u8fbe\u96c6\u56e2\u7684\u7ba1\u7406\u5c42\u96c6\u56e2\u75af\u4e86\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u662f\u8bf4\u6d77\u5dde\u4e0e\u5efa\u90ba\u7684\u5730\u65b9\u653f\u5e9c\u7ed9\u4f60\u4eec\u7279\u522b\u7684\u538b\u529b\uff0c\u5f3a\u8feb\u7231\u8fbe\u96c6\u56e2\u505a\u51fa\u5982\u6b64\u532a\u5937\u6240\u601d\u7684\u51b3\u5b9a\uff1f\u201d</p><p>\u3000\u3000\u65e0\u6570\u7684\u95ee\u9898\u4e00\u4e0b\u5b50\u7206\u53d1\u51fa\u6765\uff0c\u56f4\u5728\u82cf\u6d25\u4e1c\u4ed6\u4eec\u5468\u56f4\u7684\u8bb0\u8005\u90fd\u4e89\u5148\u6050\u5413\u7684\u5c06\u8111\u6d77\u4e2d\u95ea\u8fc7\u7684\u7b2c\u4e00\u4e2a\u7591\u60d1\u95ee\u51fa\u6765\uff0c\u573a\u9762\u987f\u65f6\u4ece\u8be7\u7136\u7684\u9759\u5bc2\u8f6c\u53d8\u6210\u6cb8\u53cd\u76c8\u5929\u7684\u70ed\u95f9\u3002\u90a3\u4e9b\u56f4\u5835\u5176\u4ed6\u4f01\u4e1a\u4ee3\u8868\u91c7\u8bbf\u7684\u8bb0\u8005\u4e5f\u987e\u4e0d\u4e0a\u592a\u591a\uff0c\u76f4\u63a5\u5c06\u91c7\u8bbf\u5bf9\u8c61\u4e22\u6389\uff0c\u8f6c\u8eab\u56f4\u8fc7\u6765\u3002</p><p>\u3000\u3000\u90a3\u4e9b\u7ed9\u91c7\u8bbf\u7684\u4f01\u4e1a\u4ee3\u8868\u4e5f\u4e00\u65f6\u987e\u4e0d\u4e0a\u8868\u793a\u4e00\u4e0b\u7ed9\u5ffd\u89c6\u7684\u60c5\u7eea\uff0c\u4ed6\u4eec\u540c\u6837\u6492\u817f\u4e5f\u56f4\u4e86\u8fc7\u6765\uff0c\u8ba9\u4eba\u532a\u5937\u6240\u601d\u7684\u58f0\u660e\u554a\uff01</p><p>\u3000\u3000\u4e4b\u524d\u5df2\u7ecf\u8d70\u5f00\u86ee\u8fdc\u7684\u4f01\u4e1a\u4ee3\u8868\u4e5f\u90fd\u56f4\u4e86\u56de\u6765\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u672c\u6765\u5c31\u662f\u8981\u770b\u82cf\u6d25\u4e1c\u600e\u4e48\u56de\u5e94\u8bb0\u8005\u5bf9\u4e0a\u5348\u7ade\u6807\u7684\u7591\u95ee\uff0c\u8fd8\u7279\u610f\u4e0e\u9648\u9759\u9009\u4e86\u4e00\u5904\u597d\u7684\u5730\u65b9\u770b\u597d\u620f\uff0c\u542c\u5230\u82cf\u6d25\u4e1c\u7a81\u7136\u5ba3\u5e03\u8fd9\u6837\u7684\u51b3\u5b9a\uff0c\u96be\u4ee5\u7f6e\u4fe1\u7684\u5634\u5df4\u5fae\u5f20\u7740\uff0c\u770b\u4e86\u770b\u9648\u9759\u3001\u5b50\u5609\u3001\u90c1\u840d\uff0c\u8fd8\u6709\u5176\u4ed6\u4e24\u540d\u5b88\u5728\u4f1a\u573a\u7684\u79d1\u738b\u5de5\u4f5c\u4eba\u5458\uff0c\u4ed6\u90fd\u4e0d\u77e5\u9053\u8981\u5c06\u5fc3\u91cc\u7684\u7591\u60d1\u8ddf\u8c01\u8bc9\u8bf4\u3002</p><p>\u3000\u3000\u9526\u6e56\u5411\u79d1\u738b\u9ad8\u79d1\u63d0\u4f9b\u624b\u673a\u7ec4\u914d\u4ef6\u3001\u6280\u672f\u65b9\u6848\uff0c\u652f\u6301\u79d1\u738b\u9ad8\u79d1\u8fdb\u884c\u624b\u673a\u4ee3\u5de5\u5305\u9500\u4e1a\u52a1\uff0c\u96be\u9053\u4e0d\u662f\u4ed6\u4eec\u60f3\u6210\u4e3a\u624b\u673a\u7ec4\u914d\u4ef6\u4e0e\u6280\u672f\u65b9\u6848\u7684\u4f9b\u5e94\u5546\u5417\uff1f\u5f53\u4ed6\u4eec\u5c06\u76f8\u5173\u6280\u672f\u516c\u5f00\uff0c\u4ed6\u4eec\u8fd8\u5982\u4f55\u7ef4\u6301\u5728\u56fd\u4ea7\u624b\u673a\u7ec4\u914d\u4ef6\u3001\u6280\u672f\u65b9\u6848\u4f9b\u5e94\u5546\u7684\u5784\u65ad\u5730\u4f4d\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u6ca1\u6709\u5c06\u7591\u60d1\u7684\u76ee\u5149\u6295\u5411\u82cf\u6d25\u4e1c\u6216\u8005\u9648\u4fe1\u751f\uff0c\u800c\u662f\u770b\u5411\u8ba9\u6574\u4e2a\u9526\u6e56\u3001\u6574\u4e2a\u789f\u673a\u4ea7\u4e1a\u90fd\u5904\u4e8e\u4ed6\u4e00\u4eba\u9634\u5f71\u4e4b\u4e0b\u7684\u90a3\u4e2a\u4eba\uff0c\u90a3\u4e2a\u4eba\u5634\u91cc\u6b63\u53fc\u7740\u6839\u8349\u830e\u7ad9\u5728\u4f1a\u573a\u7684\u51fa\u53e3\u5904\u3002</p><p>\u3000\u3000\u9648\u9759\u4e5f\u770b\u8fc7\u53bb\uff0c\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u611f\u89c9\u81ea\u5df1\u5feb\u8981\u75af\u4e86\uff0c\u5929\u554a\uff0c\u4ed6\u4e2d\u9014\u8fdb\u573a\uff0c\u4ece\u54ea\u91cc\u6458\u6765\u4e00\u6839\u8349\u4e00\u76f4\u8854\u5728\u5634\u8fb9\uff1f</p><p>\u3000\u3000\u8c22\u5b50\u5609\u762a\u7740\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u4ed6\u90a3\u6b7b\u6837\uff0c\u8fd8\u60f3\u5b66\u5468\u6da6\u53d1\uff1f\u201d</p><p>\u3000\u3000\u4ed6\u4f9d\u95e8\u800c\u7acb\uff0c\u5634\u91cc\u8854\u7740\u4e00\u6839\u8349\uff0c\u53ef\u4e0d\u6b63\u662f\u5468\u6da6\u53d1\u5728\u7535\u5f71\u300a\u8d4c\u795e\u300b\u91cc\u7684\u5f62\u8c61\uff1f</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e5f\u6ca1\u6709\u6025\u7740\u79bb\u5f00\u4f1a\u573a\uff0c\u7231\u8fbe\u96c6\u56e2\u5c06\u662f\u8054\u4fe1\u9762\u5bf9\u7684\u6700\u91cd\u91cf\u7ea7\u7684\u5bf9\u624b\uff0c\u8054\u4fe1\u6700\u5927\u7684\u5a01\u80c1\u53cd\u800c\u4e0d\u662f\u90a3\u4e9b\u6d77\u5916\u624b\u673a\u5382\u5546\uff0c\u8096\u745e\u6c11\u4e0d\u53ef\u80fd\u4e0d\u5173\u5fc3\u4ed6\u4eec\u4f1a\u5bf9\u5a92\u4f53\u8bf4\u4ec0\u4e48\uff0c\u82cf\u6d25\u4e1c\u5ba3\u5e03\u8fd9\u9879\u51b3\u5b9a\uff0c\u4ed6\u8111\u5b50\u8f70\u4e86\u4e00\u4e0b\u3002\u8981\u77e5\u9053\u4ed6\u4e4b\u524d\u8d39\u5c3d\u5343\u8f9b\u4e07\u82e6\u60f3\u8ba9\u8054\u4fe1\u5f15\u8fdb\u6838\u5fc3\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u800c\u4e0d\u4ec5\u4ec5\u53ea\u662f\u6d77\u5916\u624b\u673a\u5382\u5546\u7684\u8d34\u724c\u6e20\u9053\uff0c\u5de8\u989d\u7684\u5f15\u8fdb\u8d44\u91d1\u662f\u4e00\u56de\u4e8b\uff0c\u5173\u952e\u662f\u6d77\u5916\u624b\u673a\u5382\u5546\u6839\u672c\u5c31\u4e0d\u613f\u610f\u56fd\u5185\u7684\u4f01\u4e1a\u8131\u79bb\u4ed6\u4eec\u7684\u6280\u672f\u63a7\u5236\u3002</p><p>\u3000\u3000\u6d77\u5916\u624b\u673a\u5382\u5546\u4e0d\u4ec5\u60f3\u76f4\u63a5\u901a\u8fc7\u4ed6\u4eec\u7684\u624b\u673a\u4ea7\u54c1\u51fa\u53e3\u5230\u4e2d\u56fd\u6765\u725f\u5229\uff0c\u66f4\u5e0c\u671b\u901a\u8fc7\u6280\u672f\u5784\u65ad\u3001\u901a\u8fc7\u624b\u673a\u7ec4\u914d\u4ef6\u7684\u4f9b\u5e94\u6216\u8005\u9ad8\u989d\u7684\u6280\u672f\u8bb8\u53ef\u8d39\u7528\u4ece\u56fd\u5185\u7684\u624b\u673a\u4f01\u4e1a\u6e90\u6e90\u4e0d\u65ad\u7684\u5438\u98df\u5229\u6da6\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u534a\u4e2a\u4e13\u5bb6\u7ea7\u4eba\u58eb\uff0c\u8096\u745e\u6c11\u5f53\u7136\u6e05\u695a\uff0c\u7231\u8fbei19\u624b\u673a\u5728\u6280\u672f\u4e0a\u6709\u4e00\u5b9a\u7684\u63d0\u9ad8\uff0c\u4f46\u662fi08\u624b\u673a\u5219\u5b8c\u5168\u662f\u57fa\u4e8e\u4ed6\u4eec\u4ece\u5fb7\u4eea\u5f15\u8fdb\u7684\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u53ea\u8981\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u56ed\u533a\u7684\u4f01\u4e1a\u6709\u80fd\u529b\u5b8c\u5168\u7684\u6d88\u5316\u8fd9\u4e9b\u6280\u672f\uff0c\u5c31\u80fd\u5728\u4e0d\u957f\u7684\u65f6\u95f4\u91cc\u751f\u4ea7\u51fa\u4e0ei08\u76f8\u5ab2\u7f8e\u7684\u624b\u673a\u4ea7\u54c1\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u6267\u7167\u662f\u4e00\u4e2a\u95ee\u9898\uff0c\u4f46\u662f\u8054\u4fe1\u3001\u4e1c\u5174\u3001\u8054\u8baf\u4ed6\u4eec\u4e09\u5bb6\u62e5\u6709\u624b\u673a\u6267\u7167\u7684\u4f01\u4e1a\u5374\u53ef\u4ee5\u5b9e\u73b0\u624b\u673a\u4ea7\u54c1\u767e\u5206\u4e4b\u4e94\u5341\u4ee5\u4e0a\u7684\u56fd\u4ea7\u5316\u7387\uff0c\u66f4\u591a\u7684\u4f01\u4e1a\u867d\u7136\u6ca1\u6709\u624b\u673a\u6267\u7167\uff0c\u4f46\u662f\u4ed6\u4eec\u53ef\u4ee5\u4e3a\u624b\u673a\u5382\u5546\u63d0\u4f9b\u7ec4\u914d\u4ef6\uff0c\u6210\u4e3a\u7ec4\u914d\u4ef6\u5382\u5546\uff0c\u6216\u8005\u50cf\u79d1\u738b\u9ad8\u79d1\u90a3\u6837\u6210\u4e3a\u624b\u673a\u4ee3\u5de5\u5305\u9500\u5546\u4e5f\u662f\u4e00\u79cd\u9009\u62e9\u3002</p><p>\u3000\u3000\u8981\u6653\u5f97\u672a\u6765\u79fb\u52a8\u901a\u8baf\u7684\u5e02\u573a\u5230\u5e95\u6709\u591a\u5927\uff01</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u5341\u6708\u4e2d\u65ec\uff0c\u7b2c\u4e00\u5343\u4e07\u4e2a\u7684\u79fb\u52a8\u901a\u8baf\u7528\u6237\u5728\u4e1c\u6d77\u4ea7\u751f\uff0c\u4e00\u5343\u4e07\u662f\u5341\u5e74\u79ef\u7d2f\u4e0b\u6765\u7684\u4e00\u4e2a\u6570\u5b57\uff0c\u76f8\u6bd4\u8f83\u56fd\u5185\u5341\u4e09\u4ebf\u4eba\u53e3\u7684\u5e9e\u5927\u57fa\u6570\uff0c\u770b\u4e0a\u53bb\u8fd8\u6709\u4e9b\u53ef\u601c\uff0c\u4f46\u662f\u4e00\u5343\u4e07\u8fd9\u4e2a\u6570\u5b57\u5f53\u4e2d\uff0c\u4eca\u5e74\u4e00\u5230\u5341\u6708\u5c31\u8d21\u732e\u4e86\u56db\u6210\u3002</p><p>\u3000\u3000\u8fd9\u610f\u5473\u7740\u4ec0\u4e48\uff1f\u8fd9\u610f\u5473\u7740\u56fd\u5185\u7684\u79fb\u52a8\u901a\u8baf\u5e02\u573a\u6b63\u5728\u5448\u51e0\u4f55\u7ea7\u6570\u7684\u4e95\u55b7\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u4e3a\u4ec0\u4e48\u7a81\u7136\u5c31\u53d1\u75af\u4e86\uff1f\u8fd9\u662f\u8096\u745e\u6c11\u56de\u8fc7\u795e\u7684\u7b2c\u4e00\u4e2a\u5ff5\u5934\uff0c\u4ed6\u6653\u5f97\uff0c\u7231\u8fbe\u96c6\u56e2\u7531\u4e8e\u5de5\u827a\u6c34\u5e73\u7684\u9650\u5236\uff0ci19\u624b\u673a\u7684\u4ea7\u91cf\u4e00\u76f4\u65e0\u6cd5\u6709\u5927\u7684\u7a81\u7834\uff0c\u4ed6\u4eec\u8fd1\u671f\u7684\u4e3b\u8981\u76c8\u5229\u70b9\u8fd8\u662f\u505c\u7559\u5728i08\u624b\u673a\u4e0a\uff0c\u6280\u672f\u4e00\u65e6\u516c\u5f00\uff0c\u9664\u4e86\u5728\u54c1\u724c\u4e0e\u5e02\u573a\u8425\u9500\u4e0a\u7684\u4f18\u52bf\u4e4b\u5916\uff0c\u7231\u8fbe\u5728i08\u624b\u673a\u4e0a\u7684\u5176\u4ed6\u6240\u6709\u4f18\u52bf\u5c31\u90fd\u5c06\u8361\u7136\u65e0\u5b58\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u7a76\u7adf\u60f3\u505a\u4ec0\u4e48\uff01</p><p>\u3000\u3000\u8096\u745e\u6c11\u5176\u5b9e\u6700\u60f3\u51b2\u8fdb\u4eba\u7fa4\u7684\u5185\u5c42\u63ea\u4f4f\u82cf\u6d25\u4e1c\u7684\u9886\u53e3\u95ee\u4e2a\u7a76\u7adf\u7684\u4eba\uff0c\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u82cf\u6d25\u4e1c\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\u5df2\u7ecf\u7ed9\u56f4\u7684\u6c34\u6cc4\u4e0d\u901a\uff0c\u7ed9\u5835\u5728\u89d2\u843d\u91cc\uff0c\u8096\u745e\u6c11\u76ee\u5149\u641c\u7d22\u90a3\u4e2a\u5c06\u9526\u6e56\u7f6e\u4e8e\u4ed6\u9634\u5f71\u4e4b\u4e0b\u7684\u9752\u5e74\uff0c\u770b\u5230\u5f20\u606a\u65f6\uff0c\u4ed6\u4e0e\u5f20\u606a\u4e4b\u95f4\u5df2\u7ecf\u7ed9\u4eba\u5899\u5835\u4f4f\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u8096\u745e\u6c11\u60f3\u51b2\u8fdb\u53bb\uff0c\u6240\u6709\u6709\u5fc3\u60f3\u8fdb\u5165\u624b\u673a\u4ea7\u4e1a\u3001\u5374\u7531\u4e8e\u65e2\u65e0\u6280\u672f\u53c8\u65e0\u624b\u673a\u724c\u7167\u7684\u7f18\u6545\u4e0d\u5f97\u4e0d\u770b\u7740\u8fd9\u4e00\u5757\u53ef\u80fd\u662f\u56fd\u5185\u6700\u4e30\u7f8e\u7684\u5e02\u573a\u6d41\u53e3\u6c34\u4e0d\u6b62\u7684\u4f01\u4e1a\u7684\u4ee3\u8868\uff0c\u90fd\u60f3\u7740\u51b2\u5230\u82cf\u6d25\u4e1c\u7684\u8ddf\u524d\u95ee\u4e00\u4e2a\u4ed4\u7ec6\u3002</p><p>\u3000\u3000\u624b\u673a\u6267\u7167\u62ff\u4e0d\u5230\uff0c\u53ef\u4ee5\u5148\u4ece\u624b\u673a\u7ec4\u914d\u5546\u5f00\u59cb\uff0c\u638c\u63e1\u4e86\u5236\u9020\u6280\u672f\uff0c\u7b49\u4ee5\u540e\u4e89\u53d6\u5230\u724c\u7167\uff0c\u5c31\u4e00\u5207\u90fd\u6c34\u5230\u6e20\u6210\u4e86\uff0c\u5173\u952e\u8981\u5148\u627e\u5230\u4e00\u4e2a\u8fdb\u5165\u95e8\u69db\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u4ed6\u4eec\u4e5f\u6ca1\u6709\u79bb\u5f00\uff0c\u4ed6\u542c\u5230\u82cf\u6d25\u4e1c\u7684\u8bdd\uff0c\u4e4d\u4ee5\u4e3a\u81ea\u5df1\u7684\u6c49\u8bed\u4e0d\u884c\u542c\u9519\u4e86\uff0c\u91d1\u5357\u52c7\u4e5f\u662f\uff0c\u674e\u99a8\u4e88\u5374\u6781\u529b\u907f\u514d\u7ed9\u522b\u4eba\u7684\u6324\u5230\uff0c\u542c\u662f\u542c\u5230\u82cf\u6d25\u4e1c\u7684\u8bdd\uff0c\u4e5f\u80fd\u591f\u7406\u89e3\uff0c\u53ea\u662f\u5979\u5bf9\u5546\u4e1a\u4e0a\u7684\u4e8b\u60c5\u4e0d\u5927\u5173\u5fc3\uff0c\u6240\u4ee5\u4e5f\u5c31\u6ca1\u6709\u8868\u73b0\u51fa\u591a\u5927\u7684\u60ca\u8be7\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5f53\u7136\u770b\u4e0d\u900f\u7231\u8fbe\u96c6\u56e2\u7684\u610f\u56fe\u662f\u4ec0\u4e48\uff0c\u4f46\u662f\u4ed6\u80fd\u770b\u5230\u7231\u8fbe\u96c6\u56e2\u7684\u8fd9\u9879\u51b3\u5b9a\u4f1a\u7ed9\u4e09\u661f\u65e2\u5b9a\u7684\u8ba1\u5212\u9020\u6210\u591a\u5927\u7684\u5f71\u54cd\uff1f\u51e0\u4e4e\u4e0d\u518d\u53ef\u80fd\u901a\u8fc7\u8f93\u5165\u4e8c\u6d41\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u4ece\u4e2d\u56fd\u7684\u79fb\u52a8\u901a\u8baf\u5e02\u573a\u5206\u5229\uff0c\u90a3\u5c31\u53ea\u80fd\u76f4\u63a5\u901a\u8fc7\u8f93\u5165\u4ea7\u54c1\u6765\u5206\u4eab\u4e2d\u56fd\u7684\u901a\u52a8\u901a\u8baf\u5e02\u573a\uff0c\u53ef\u662f\u8be5\u6b7b\u7684\u914d\u989d\u3001\u8be5\u6b7b\u7684\u5e02\u573a\u58c1\u5792\uff0c\u5c06\u9650\u5236\u4ea7\u54c1\u7684\u5927\u89c4\u6a21\u8f93\u5165\uff0c\u51b5\u4e14\u4e09\u661f\u6b64\u65f6\u6240\u638c\u63e1\u7684\u624b\u673a\u5236\u9020\u6280\u672f\u5e76\u6ca1\u6709\u7279\u522b\u5927\u7684\u4f18\u52bf\u3002\u60f3\u5fc5\u5176\u4ed6\u5916\u8d44\u624b\u673a\u5382\u5546\u90fd\u4f1a\u6709\u8fd9\u6837\u7684\u56f0\u6270\u5427\uff1f</p><p>\u3000\u3000\u201c\u8be5\u6b7b\u7684\u6df7\u86cb\uff0c\u4ed6\u7a76\u7adf\u60f3\u73a9\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u674e\u5728\u6d19\u4f4e\u58f0\u7528\u97e9\u8bed\u9a82\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u7684\u786e\u5f88\u4ee4\u4eba\u8d39\u89e3\u3002\u201d\u91d1\u5357\u52c7\u8bf4\u9053\uff0c\u201c\u4f3c\u4e4e\u5bf9\u4ed6\u4eec\u5e76\u6ca1\u6709\u4ec0\u4e48\u597d\u5904\u2026\u2026\u6211\u7684\u5bfc\u5e08\u8fd8\u5224\u65ad\u8bf4\u9526\u6e56\u6709\u6210\u4e3a\u56fd\u5185\u624b\u673a\u7ec4\u914d\u4ef6\u4f9b\u5e94\u5546\u7684\u91ce\u5fc3\uff0c\u8fd9\u4e2a\u53e3\u5b50\u6253\u5f00\uff0c\u50cf\u6211\u4eec\u8fd9\u6837\u7684\u6d77\u5916\u5382\u5546\u4e00\u65f6\u53c8\u7ed9\u4f53\u5236\u7684\u969c\u788d\u5361\u4f4f\u8fdb\u4e0d\u6765\uff0c\u8fd9\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u4e0d\u6653\u5f97\u9526\u6e56\u80fd\u4ece\u4e2d\u635e\u53d6\u591a\u5c11\u5229\u6da6\u2026\u2026\u4f38\u624b\u5c31\u53ef\u4ee5\u635e\u94b1\u7684\u7f8e\u4e8b\uff0c\u4ed6\u4eec\u4f3c\u4e4e\u5c31\u60f3\u8fd9\u6837\u4e3b\u52a8\u7684\u653e\u5f03\u6389\u4e86\uff01\u771f\u7684\u5f88\u4ee4\u4eba\u8d39\u89e3\u3002\u201d</p><p>\u3000\u3000\u201c\u96be\u9053\u4ed6\u4eec\u5c31\u4e0d\u53ef\u4ee5\u4e00\u5f00\u59cb\u5c31\u5411\u5f80\u91d1\u5b57\u5854\u7684\u9876\u7aef\uff1f\u201d\u4eba\u6d41\u6c79\u6d8c\uff0c\u674e\u99a8\u4e88\u53ea\u662f\u7aed\u529b\u907f\u514d\u7ed9\u522b\u4eba\u78b0\u5230\uff0c\u60f3\u8981\u9003\u51fa\u53bb\uff0c\u5374\u53c8\u4e0d\u53ef\u80fd\uff0c\u542c\u5230\u5728\u6d19\u4e0e\u5357\u52c7\u7684\u8ba8\u8bba\uff0c\u5fcd\u4e0d\u4f4f\u63d2\u4e86\u4e00\u53e5\u8bdd\uff0c\u4ed6\u4eec\u9aa8\u5b50\u91cc\u603b\u662f\u85d0\u89c6\u8fd9\u4e2a\u56fd\u5bb6\u3002</p><p>\u3000\u3000\u4eba\u7fa4\u6c79\u6d8c\uff0c\u6bcf\u4e2a\u4eba\u90fd\u62a2\u7740\u5c06\u5fc3\u91cc\u5de8\u5927\u7684\u7591\u60d1\u4e0e\u60ca\u8be7\u8868\u8fbe\u51fa\u6765\uff0c\u8fd8\u6709\u65e0\u6570\u4eba\u62a2\u7740\u6324\u5230\u91cc\u9762\uff0c\u8d8a\u6765\u8d8a\u62e5\u6324\uff0c\u82cf\u6d25\u4e1c\u8003\u8651\u5230\u5ba3\u5e03\u8fd9\u9879\u51b3\u5b9a\u4f1a\u5f15\u8d77\u5f88\u5927\u7684\u8f70\u52a8\uff0c\u4f46\u6ca1\u6709\u60f3\u5230\u4f1a\u8fd9\u4e48\u6fc0\u70c8\uff0c\u53ea\u5f97\u9ad8\u558a\u7740\u8ba9\u5927\u5bb6\u4e0d\u8981\u5f80\u524d\u6324\uff0c\u53ef\u4ee5\u501f\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u5730\u65b9\u4e34\u65f6\u4e3e\u529e\u4e00\u4e2a\u8bb0\u8005\u4f1a\uff0c\u8ba9\u5927\u5bb6\u90fd\u6062\u590d\u6b21\u5e8f\uff0c\u907f\u5f97\u4ed6\u4eec\u5728\u6700\u91cc\u9762\u7ed9\u6324\u5728\u8089\u9171\u3002</p></article>"}}}]);
