(window.webpackJsonp=window.webpackJsonp||[]).push([[857],{880:function(p){p.exports={chapterTitle:null,title:"\u7b2c682\u7ae0 \u90fd\u60f3\u591a\u8981\u4e00\u4e9b",content:"<article><h2>\u7b2c682\u7ae0 \u90fd\u60f3\u591a\u8981\u4e00\u4e9b</h2><p>\u3000\u3000\u7389\u6e0a\u6f6d\u7684\u73af\u5883\u6e05\u5e7d\uff0c\u4f46\u662f\u5341\u4e00\u6708\u5317\u4eac\u4e2d\u5348\u7684\u5929\u7a7a\u901a\u5e38\u4f1a\u56e0\u4e3a\u98ce\u6c99\u800c\u9634\u6c89\u6666\u6697\uff0c\u4e0e\u9634\u6c89\u6666\u6697\u5929\u7a7a\u582a\u6bd4\u7684\u5c31\u662f\u8c22\u5251\u5357\u3001\u674e\u5728\u6d19\u3001\u91d1\u5357\u52c7\u7684\u8138\u8272\u4e86\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u9887\u4e3a\u65e0\u804a\uff0c\u53c8\u4e0d\u60f3\u8ddf\u7740\u4e24\u540d\u4fdd\u9556\u53bb\u901b\u8857\u3002</p><p>\u3000\u3000\u5728\u9c9c\u8273\u7684\u7ea2\u8863\u7ed2\u8863\u5916\u7a7f\u7740\u9ed1\u8272\u5916\u5957\uff0c\u674e\u99a8\u4e88\u5b89\u9759\u7684\u5750\u5728\u897f\u9910\u5385\u7cbe\u81f4\u7684\u5e03\u6905\u4e0a\uff0c\u770b\u7740\u7a97\u5916\u5165\u79cb\u540e\u7684\u7ea2\u67d3\u6811\u53f6\uff0c\u6253\u53d1\u5348\u9910\u540e\u65e0\u804a\u7684\u65f6\u5149\u3002</p><p>\u3000\u3000\u8c22\u5357\u5251\u3001\u674e\u5728\u6d19\u4ed6\u4eec\u7684\u5fc3\u601d\u5c31\u6ca1\u6709\u4ed6\u4eec\u60a0\u95f2\u4e86\uff0c\u5728\u8fc7\u6765\u7528\u9910\u7684\u8def\u4e0a\uff0c\u8c22\u5357\u5251\u5df2\u7ecf\u5c06\u6d88\u606f\u7535\u8bdd\u901a\u77e5\u4e86\u5927\u8205\u5468\u747e\u73ba\u4e0e\u4e8c\u53d4\u8c22\u6c49\u9756\uff0c\u90fd\u8ba9\u9526\u6e56\u75af\u72c2\u7684\u51b3\u5b9a\u5413\u4e86\u4e00\u8df3\uff0c\u4e8c\u53d4\u8c22\u6c49\u9756\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u662f\u8ba9\u4ed6\u5bc6\u5207\u6ce8\u610f\u9526\u6e56\u7684\u52a8\u5411\uff0c\u5927\u8205\u5468\u747e\u73ba\u5374\u544a\u8bc9\u4ed6\uff1a\u201c\u4e0d\u8981\u62b1\u4ec0\u4e48\u5e7b\u60f3\uff0c\u79d1\u738b\u662f\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u5185\u7684\u4f01\u4e1a\u4e0d\u5047\uff0c\u4f46\u662f\u4f60\u60f3\u4e00\u60f3\uff0c\u79d1\u738b\u5728\u6d77\u5dde\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\u72ec\u7acb\u6ce8\u518c\u4e86\u51e0\u5bb6\u4f01\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u51a0\u4ee5\u79d1\u738b\u540d\u4e49\u7684\u51e0\u5bb6\u516c\u53f8\uff0c\u5c31\u79d1\u738b\u9ad8\u79d1\u80fd\u83b7\u5f97\u6388\u6743\uff1f\u201d\u8c22\u5b50\u5609\u5403\u7740\u9999\u8349\u51b0\u6fc0\u51cc\uff0c\u7a81\u7136\u60f3\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u6b6a\u7740\u5934\u8111\u770b\u7740\u9648\u9759\uff0c\u201c\u90a3\u5bb6\u4f19\u6709\u673a\u4f1a\u603b\u4e0d\u4f1a\u653e\u5f03\u6076\u5fc3\u4eba\u7684\u2026\u2026\u54e6\uff0c\u73b0\u5728\u60f3\u8d77\u4ed6\u9760\u7740\u95e8\u53e3\u53fc\u6839\u8349\u7684\u6b7b\u6837\uff0c\u6211\u80c3\u53e3\u5c31\u51fa\u5947\u7684\u597d\u554a\uff01\u201d\u72e0\u72e0\u7684\u6316\u4e86\u4e00\u5927\u5319\u51b0\u6fc0\u51cc\u541e\u4e0b\u53bb\uff0c\u541e\u4e0b\u53bb\u5c31\u540e\u6094\u4e86\uff0c\u4ece\u5589\u5499\u4e00\u76f4\u5230\u80c3\uff0c\u80a0\u5b50\u90fd\u51b0\u5f97\u8981\u6253\u7ed3\u8d77\u6765\uff0c\u5468\u8eab\u5bd2\u6bdb\u90fd\u51b0\u5f97\u8981\u7acb\u8d77\u6765\uff0c\u5f20\u7740\u5634\u3001\u6342\u7740\u55d3\u5b50\u65e0\u58f0\u7684\u53eb\u7740\u3002</p><p>\u3000\u3000\u9648\u9759\u5a07\u7b11\u7740\uff0c\u5979\u80fd\u60f3\u5230\u5f20\u606a\u7279\u610f\u5634\u91cc\u8854\u6839\u8349\u7ad9\u5728\u4f1a\u573a\u95e8\u53e3\uff0c\u5927\u6982\u5c31\u662f\u7279\u610f\u8981\u6076\u5fc3\u5979\u4eec\u7684\uff0c\u8ba9\u4eba\u54ed\u7b11\u4e0d\u5f97\u3002\u4e0e\u90c1\u840d\u642f\u4e86\u4e00\u5319\u70ed\u6c64\u8ba9\u5b50\u5609\u559d\u4e0b\u53bb\u6696\u4e00\u4e0b\u80c3\uff0c\u5317\u4eac\u5165\u79cb\u540e\u7684\u6c14\u6e29\u5c31\u964d\u4e86\u5f88\u4f4e\uff0c\u5927\u53e3\u541e\u54bd\u51b0\u6fc0\u51cc\u7684\u611f\u89c9\u8fd8\u771f\u4e0d\u597d\u53d7\u3002\u4e09\u4eba\u65c1\u82e5\u65e0\u4eba\u7684\u5728\u9910\u5385\u7684\u89d2\u843d\u7b11\u95f9\u7740\uff0c\u6e05\u8106\u5a07\u7f8e\u7684\u58f0\u97f3\u4e0e\u660e\u8273\u7167\u4eba\u3001\u5317\u4eac\u8857\u5934\u4e5f\u6781\u5c11\u89c1\u7684\u5bb9\u989c\uff0c\u60f9\u5f97\u5e97\u91cc\u5176\u4ed6\u98df\u5ba2\u9891\u9891\u4fa7\u76ee\u770b\u6765\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u5b50\u5609\u8bf4\u7684\u53ef\u80fd\u6027\uff0c\u90c1\u840d\u6b63\u8272\u95ee\u9648\u9759\uff1a\u201c\u4ed6\u5bb6\u4f19\u4eb2\u53e3\u7b54\u5e94\u6240\u6709\u7684\u6280\u672f\u90fd\u5411\u79d1\u738b\u9ad8\u79d1\u516c\u5f00\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4ed6\u662f\u8fd9\u4e48\u8bf4\u6765\u7740\u7684\uff0c\u6211\u8ddf\u5b50\u5609\u8fd8\u4ee5\u4e3a\u4ed6\u5728\u5f00\u73a9\u7b11\u5462\uff0c\u6ca1\u60f3\u5230\u4ed6\u4eec\u4eca\u5929\u771f\u7684\u53d1\u5e03\u8fd9\u4e48\u7684\u58f0\u660e\uff0c\u201d\u9648\u9759\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u7684\u5fd7\u5411\u5f88\u8fdc\u5927\uff0c\u6211\u4eec\u662f\u53ea\u6562\u56f4\u7740\u7a9d\u5de2\u98de\u7684\u5c0f\u71d5\u96c0\u554a\uff0c\u6240\u4ee5\u4f1a\u89c9\u5f97\u4ed6\u4eec\u5f88\u75af\u72c2\uff0c\u201d\u90c1\u840d\u82e5\u6709\u6240\u601d\u7684\u8bf4\u9053\uff0c\u201c\u4e0e\u5176\u53bb\u731c\u6d4b\u9526\u6e56\u7684\u610f\u56fe\uff0c\u8fd8\u4e0d\u5982\u8001\u8001\u5b9e\u5b9e\u7684\u53bb\u60f3\u6211\u4eec\u600e\u6837\u624d\u80fd\u505a\u5230\u66f4\u597d\u3002\u9648\u4fe1\u751f\u521a\u624d\u7684\u58f0\u660e\uff0c\u5bf9\u6280\u672f\u8d44\u4ea7\u516c\u5f00\u6388\u6743\u65b9\u6848\u7684\u57fa\u672c\u6846\u67b6\u505a\u4e86\u7b80\u5355\u7684\u8bf4\u660e\uff1a\u4e0d\u53ef\u80fd\u65e0\u9650\u5236\u7684\u91cd\u590d\u6388\u6743\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5e7f\u6cdb\u7684\u6388\u6743\uff0c\u800c\u662f\u6709\u9488\u5bf9\u6027\u7684\u3001\u6709\u9009\u62e9\u6027\u7684\u6388\u6743\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u5f88\u53ef\u80fd\u6ca1\u6709\u51e0\u5bb6\u516c\u53f8\u80fd\u5f97\u5230\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u201d\u5b50\u5609\u5fae\u6602\u7740\u5934\u8bf4\u9053\uff0c\u201c\u5c0f\u9759\u603b\u662f\u4e0e\u4eba\u4e0d\u540c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53bb\u2026\u2026\u201d\u9648\u9759\u5728\u684c\u4e0b\u8e22\u4e86\u8fc7\u53bb\uff0c\u4e0d\u8ba9\u5b50\u5609\u80e1\u8bf4\u516b\u9053\uff0c\u201c\u4f60\u7684\u5634\u5df4\u8981\u7f1d\u8d77\u6765\u624d\u80fd\u8ba9\u4eba\u5b89\u9759\u3002\u201d</p><p>\u3000\u3000\u90c1\u840d\u4e0d\u6653\u5f97\u9648\u9759\u4e0e\u5f20\u606a\u4e4b\u95f4\u53d1\u751f\u8fc7\u600e\u6837\u7684\u6545\u4e8b\uff0c\u6216\u8bb8\u662f\u5b50\u5609\u8fd9\u4e2b\u5934\u80e1\u4e71\u62ff\u7537\u5973\u5173\u7cfb\u5f00\u73a9\u7b11\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u4e0d\u8fc7\u9648\u9759\u770b\u4e0a\u53bb\u6709\u4e9b\u8584\u7f9e\u7684\u610f\u6001\uff0c\u5012\u662f\u8010\u4eba\u5bfb\u5473\u3002\u90c1\u840d\u8f7b\u8f7b\u4e00\u53f9\uff0c\u8c01\u90fd\u770b\u7684\u51fa\u6765\uff0c\u4ece\u79d1\u738b\u9ad8\u79d1\u6210\u7acb\uff0c\u5927\u96c5\u96c6\u56e2\u4e0e\u6b63\u6cf0\u96c6\u56e2\u4e4b\u95f4\u7684\u9e3f\u6c9f\u5c31\u5f88\u96be\u5f25\u8865\uff0c\u50cf\u8c22\u5251\u5357\u4e0e\u9648\u9759\u90fd\u662f\u90a3\u79cd\u7ed9\u5bb6\u65cf\u675f\u7f1a\u4f4f\u624b\u811a\u7684\u4eba\uff0c\u8fd9\u65f6\u5019\u5927\u6982\u5c31\u5269\u4e00\u5f20\u8138\u76ae\u6ca1\u6709\u6700\u540e\u6495\u7834\u7f62\u4e86\uff0c\u611f\u60c5\u5df2\u7ecf\u662f\u5f7b\u5e95\u51b7\u5374\u4e0b\u6765\u4e86\u5427\u3002\u8c22\u5251\u5357\u4e5f\u534a\u516c\u5f00\u5c06\u4ed6\u5728\u60e0\u5c71\u7684\u90a3\u4e2a\u53eb\u5a49\u5a77\u7684\u5973\u4eba\u5e26\u5230\u6d77\u5dde\u6765\u4f4f\uff0c\u9648\u9759\u5012\u4e5f\u4e1d\u6beb\u65e0\u788d\u3002\u8fd9\u8c6a\u95e8\u95f4\u7684\u7537\u5973\u60c5\u4e8b\uff0c\u666e\u901a\u4eba\u8fd8\u771f\u662f\u96be\u4ee5\u7406\u89e3\u3002</p><p>\u3000\u3000\u90c1\u840d\u624b\u629a\u7740\u989d\u5934\uff0c\u60f3\u8d77\u90a3\u4e2a\u8fc7\u5e74\u624d\u6ee1\u4e8c\u5341\u5c81\u7684\u5c0f\u7537\u4eba\uff0c\u5728\u5979\u773c\u91cc\uff0c\u62e5\u6709\u6743\u52bf\u4e0e\u8d22\u5bcc\u7684\u7537\u4eba\uff0c\u624d\u662f\u6709\u9b45\u529b\u7684\uff0c\u90a3\u4e2a\u5c0f\u7537\u4eba\uff0c\u65e0\u7591\u662f\u8fd9\u4e2a\u4e16\u754c\u4e0a\u6700\u6709\u9b45\u529b\u7684\u7537\u4eba\u4e4b\u4e00\u4e86\uff0c\u5012\u4e0d\u6653\u5f97\u4ec0\u4e48\u6837\u7684\u5973\u4eba\u624d\u80fd\u591f\u4eab\u53d7\u8fd9\u6837\u7684\u6781\u54c1\u7537\u4eba\u3002</p><p>\u3000\u3000\u90c1\u840d\u7a7a\u7a7a\u7684\u60f3\u4e86\u4e00\u9635\uff0c\u5c06\u8033\u9b13\u7684\u53d1\u4e1d\u634b\u5230\u8033\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u60f3\u5b50\u5609\u7684\u8bdd\u5462\uff0c\u8bf4\u4e0d\u5b9a\u771f\u6709\u4e00\u5929\uff0c\u6211\u4eec\u80fd\u53cd\u8fc7\u6765\u5c06\u79d1\u738b\u541e\u5e76\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u516d\u54e5\u8fd8\u4e0d\u8981\u7ed9\u6b3a\u8d1f\u8eb2\u5230\u5899\u89d2\u91cc\u53bb\u54ed\u9f3b\u5b50\uff1f\u201d\u8c22\u5b50\u5609\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8bf4\u9053\uff0c\u795e\u6001\u95f4\u66f4\u6ca1\u6709\u540c\u6837\u8c22\u5251\u5357\u7684\u610f\u601d\uff0c\u5bf9\u90c1\u840d\u8bbe\u60f3\u7684\u672a\u6765\u8fd8\u5f88\u5411\u5f80\uff0c\u201c\u4e0d\u8fc7\u771f\u7684\u5f88\u6709\u8da3\uff0c\u6307\u4e0d\u5b9a\u90a3\u5bb6\u4f19\u4e5f\u5f88\u4e50\u610f\u770b\u5230\u8fd9\u6837\u7684\u7ed3\u5c40\u5462\u3002\u6211\u6562\u80af\u5b9a\uff0c\u90a3\u5bb6\u4f19\u4e00\u5b9a\u4e0d\u4f1a\u8ba9\u592a\u591a\u7684\u516c\u53f8\u83b7\u5f97\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u90a3\u6211\u4eec\u8fd8\u662f\u62e5\u6709\u4f18\u52bf\u5462\u3002\u9526\u6e56\u5373\u4f7f\u518d\u5927\u516c\u65e0\u79c1\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u5de7\u5999\u7684\u5c06\u4ed6\u4eec\u7684\u5f71\u54cd\u529b\u5f3a\u52bf\u7684\u6e17\u900f\u8fdb\u6280\u672f\u516c\u5f00\u800c\u884d\u751f\u51fa\u6765\u7684\u4ea7\u4e1a\u96c6\u7fa4\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8054\u4fe1\u3001\u8054\u8baf\u6216\u8005\u4e1c\u5174\uff0c\u5bf9\u4e8e\u4ed6\u4eec\u6765\u8bf4\uff0c\u6ca1\u6709\u4efb\u4f55\u9644\u52a0\u6761\u4ef6\u7684\u83b7\u5f97\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u624d\u662f\u6700\u7406\u60f3\u7684\u7ed3\u5c40\uff0c\u5f53\u7136\uff0c\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u4e0b\u5348\u8fd8\u8981\u53bb\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u53c2\u52a0\u4e0b\u5348\u7684\u7ade\u6807\uff0c\u5148\u8ba9\u8054\u4fe1\u7684\u4e00\u540d\u526f\u603b\u524d\u5f80\u7231\u8fbe\u96c6\u56e2\u5728\u4e2d\u5173\u6751\u7684\u5206\u516c\u53f8\uff0c\u4e0e\u7231\u8fbe\u96c6\u56e2\u7684\u4eba\u63a5\u89e6\uff0c\u7231\u8fbe\u8bf4\u8981\u4e3a\u6280\u672f\u516c\u5f00\u907f\u514d\u88ab\u6ee5\u7528\u7684\u5883\u5730\u800c\u6210\u7acb\u4e00\u4e2a\u4e13\u95e8\u7684\u5ba1\u67e5\u673a\u6784\uff0c\u6700\u8054\u60f3\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u8054\u4fe1\u80fd\u6d3e\u51fa\u4ee3\u8868\u52a0\u5165\u5230\u8fd9\u4e2a\u673a\u6784\u91cc\uff0c\u624d\u80fd\u53d1\u6325\u66f4\u5927\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u60f3\u5230\u8fd9\u91cc\uff0c\u8096\u745e\u6c11\u7a81\u7136\u60f3\u5230\uff0c\u8981\u662f\u8054\u8baf\u3001\u4e1c\u5174\u4e5f\u6709\u8fd9\u4e9b\u60f3\u6cd5\uff0c\u5c82\u4e0d\u662f\u8fd9\u4e2a\u673a\u6784\u5dee\u4e0d\u591a\u62e5\u6709\u884c\u4e1a\u534f\u4f1a\u7684\u5730\u4f4d\uff1f</p><p>\u3000\u3000\u8096\u745e\u6c11\u5df2\u7ecf\u4ece\u521d\u542c\u5230\u7231\u8fbe\u96c6\u56e2\u516c\u5e03\u7684\u51b3\u5b9a\u800c\u4ea7\u751f\u5de8\u5927\u7684\u9707\u60ca\u4e2d\u51b7\u9759\u4e0b\u6765\uff0c\u5728\u601d\u8003\u8054\u4fe1\u5229\u76ca\u7684\u540c\u65f6\uff0c\u4e5f\u80fd\u6e10\u6e10\u5f80\u6df1\u91cc\u53bb\u60f3\u7231\u8fbe\u96c6\u56e2\u7684\u610f\u56fe\uff0c\u6216\u8005\u8bf4\u4ed6\u4eec\u505a\u51fa\u8fd9\u9879\u60ca\u4eba\u7684\u51b3\u5b9a\u80fd\u83b7\u5f97\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5f88\u96be\u53bb\u60f3\u8c61\u7231\u8fbe\u96c6\u56e2\u8fd9\u9879\u51b3\u5b9a\u4f1a\u4ea7\u751f\u591a\u4e48\u6df1\u8fdc\u7684\u5f71\u54cd\uff0c\u53ef\u80fd\u4e0d\u9700\u8981\u591a\u4e45\uff0c\u6574\u4e2a\u56fd\u4ea7\u624b\u673a\u4ea7\u4e1a\u90fd\u6446\u8131\u4e0d\u4e86\u8fd9\u79cd\u5f71\u54cd\uff1b\u8fd9\u5c31\u662f\u4ed6\u4eec\u7684\u91ce\u5fc3\u5417\uff1f</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u8981\u6682\u65f6\u5c06\u8fd9\u4e9b\u4e71\u7cdf\u7cdf\u7684\u60f3\u6cd5\u4ece\u8111\u6d77\u91cc\u90fd\u6446\u8131\u6389\uff0c\u4e0b\u5348\u8fd8\u8981\u53c2\u52a0\u592e\u89c6\u7684\u5e7f\u544a\u7ade\u6807\u5462\uff0c\u8096\u745e\u6c11\u4e58\u8f66\u62b5\u8fbe\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\uff1b\u5176\u4ed6\u4f01\u4e1a\u4ee3\u8868\u4e5f\u90fd\u8d76\u4e86\u56de\u6765\u3002\u4e5f\u6709\u8bb8\u591a\u8bb0\u8005\u8d81\u7740\u4e2d\u5348\u4f11\u606f\u7684\u65f6\u95f4\uff0c\u8dd1\u5230\u7231\u8fbe\u96c6\u56e2\u5317\u4eac\u5206\u516c\u53f8\u53bb\u56f4\u8ffd\u5835\u622a\uff0c\u4e0d\u8fc7\u90fd\u6ca1\u6709\u4ec0\u4e48\u6536\u83b7\uff1a\u90fd\u8bf4\u8981\u5c06\u5a92\u4f53\u7684\u7126\u70b9\u8fd8\u7ed9\u4eca\u5929\u7684\u62db\u5546\u5927\u4f1a\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u8bf4\u8282\u594f\u7ed9\u6253\u4e71\u4e86\uff0c\u6216\u8005\u8bf4\u5f88\u591a\u4f01\u4e1a\u4ee3\u8868\u7684\u5fc3\u601d\u7ed9\u5206\u6563\u4e86\uff0c\u4e0b\u5348\u5bf9\u592e\u89c6\u4e00\u5957\u665a\u70b9\u9ec4\u91d1\u65f6\u6bb5\u5e7f\u544a\u7ade\u6807\uff0c\u4e5f\u6ca1\u6709\u4e4b\u524d\u9884\u60f3\u4e2d\u7684\u6fc0\u70c8\u3002</p><p>\u3000\u3000\u4e5f\u53ea\u6709\u5e02\u573a\u603b\u76d1\u53ca\u9500\u552e\u516c\u53f8\u603b\u7ecf\u7406\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u53c2\u52a0\u4e0b\u5348\u7684\u5e7f\u544a\u7ade\u6807\uff0c\u5012\u662f\u4e3a\u4e86\u8868\u8fbe\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u656c\u610f\uff0c\u7231\u8fbe\u96c6\u56e2\u770b\u4e2d\u7684\u5e7f\u544a\u65f6\u6bb5\uff0c\u53ea\u8981\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u4e3e\u724c\u62a5\u4ef7\uff0c\u8ffd\u4ef7\u8005\u5c31\u5be5\u5be5\u65e0\u51e0\u3002\u7231\u8fbe\u96c6\u56e2\u5012\u6ca1\u6709\u8868\u73b0\u5f97\u592a\u8d2a\u5fc3\uff0c\u4ee5\u4f4e\u4ef7\u7ade\u4e0b\u4e24\u6761\u5e7f\u544a\u4e4b\u540e\uff0c\u5c31\u6536\u624b\u4e0d\u52a8\uff1b\u5f53\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u526f\u603b\u88c1\u674e\u653f\u54f2\u4ee3\u8868\u4e09\u661f\u7ade\u4ef7\u65f6\uff0c\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u624d\u53c8\u6d3b\u8dc3\u8d77\u6765\uff0c\u7231\u8fbe\u96c6\u56e2\u751a\u81f3\u5c06\u5468\u516d\u665a\u95f4\u516b\u70b9\u64ad\u653e\u3001\u5468\u65e5\u4e0a\u5348\u5341\u70b9\u91cd\u64ad\u7684\u4e00\u6863\u9752\u5c11\u5e74\u667a\u529b\u7ade\u731c\u8282\u76ee\u7684\u51a0\u540d\u6743\u4e0e\u4e09\u661f\u8ffd\u9010\u5230\u56db\u5343\u4e07\u7684\u9ad8\u4f4d\u5e76\u6700\u7ec8\u62ff\u4e0b\u6765\uff0c\u8fd9\u4e5f\u662f\u4e0b\u5348\u7ade\u6807\u8fbe\u5230\u7684\u7b2c\u4e00\u4e2a\u9ad8\u6f6e\uff0c\u5750\u5728\u53f0\u4e0a\u7684\u94b1\u6587\u4e1c\u7b49\u4eba\u8138\u8272\u624d\u7a0d\u7a0d\u7f13\u4e86\u7f13\u3002</p><p>\u3000\u3000\u5916\u8d44\u54c1\u724c\u4ee3\u8868\u7684\u51fa\u624b\uff0c\u6768\u4e91\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u7684\u6d3b\u8dc3\uff0c\u8fd9\u65f6\u5019\u7ade\u6807\u573a\u5185\u7684\u6c14\u6c1b\u624d\u70ed\u70c8\u8d77\u6765\u3002</p><p>\u3000\u3000\u5750\u5728\u573a\u5185\u4e00\u76f4\u9ed8\u9ed8\u5173\u6ce8\u7ade\u6807\u7684\u674e\u5728\u6d19\uff0c\u8138\u8272\u59cb\u7ec8\u5f88\u9634\u6c89\uff0c\u4ece\u4eca\u5929\u7684\u60c5\u51b5\u770b\u6765\uff0c\u4e09\u661f\u8fdb\u5165\u8fd9\u4e2a\u56fd\u5bb6\u5e02\u573a\u7684\u6b65\u4f10\u6bd4\u60f3\u8c61\u4e2d\u8981\u8270\u96be\u4e00\u4e9b\uff0c\u7231\u8fbe\u96c6\u56e2\u7684\u51b3\u5b9a\uff0c\u51e0\u4e4e\u8ba9\u4e09\u661f\u5931\u53bb\u81ea\u4ee5\u4e3a\u5f88\u6709\u628a\u63e1\u7684\u6280\u672f\u4f18\u52bf\uff0c\u4f3c\u4e4e\u771f\u7684\u5bf9\u8fd9\u4e2a\u56fd\u5bb6\u592a\u8f7b\u89c6\u4e86\uff0c\u4e5f\u6216\u8bb8\u7231\u8fbe\u96c6\u56e2\u662f\u8fd9\u4e2a\u56fd\u5bb6\u7684\u5f02\u6570\uff0c\u5374\u53c8\u662f\u4e09\u661f\u8de8\u8fdb\u8fd9\u4e2a\u56fd\u5bb6\u65e0\u6cd5\u5ffd\u89c6\u7684\u5f02\u6570\u3002</p><p>\u3000\u3000\u7231\u8fbe\u96c6\u56e2\u5bf9\u5a92\u4f53\u5c01\u9501\u6d88\u606f\u7684\u575a\u51b3\u6001\u5ea6\uff0c\u5a92\u4f53\u8bb0\u8005\u4eec\u867d\u7136\u6ee1\u8179\u610f\u89c1\uff0c\u4f46\u4e5f\u4e0d\u5f97\u4e0d\u5c06\u7cbe\u529b\u6295\u653e\u5230\u592e\u89c6\u7684\u62db\u5546\u5927\u4f1a\u8fd9\u8fb9\uff0c\u8fd9\u6837\u4e5f\u597d\uff0c\u514d\u5f97\u7ed9\u4e24\u8fb9\u5206\u6563\u4e86\u7cbe\u529b\uff0c\u4e24\u8fb9\u7684\u62a5\u9053\u90fd\u505a\u4e0d\u597d\u3002</p><p>\u3000\u3000\u5bf9\u4f01\u4e1a\u4ee3\u8868\u6765\u8bf4\uff0c\u8981\u662f\u4ec0\u4e48\u5185\u5e55\u6d88\u606f\u8ddf\u4ed6\u4eec\u6ca1\u6709\u76f4\u63a5\u7684\u5229\u5bb3\u5173\u7cfb\uff0c\u4ed6\u4eec\u53ef\u4e0d\u4f1a\u90a3\u4e48\u70ed\u5207\uff0c\u8fd9\u65f6\u5019\u53bb\u60f3\u76f4\u63a5\u627e\u7231\u8fbe\u96c6\u56e2\u6d3d\u8c08\u6280\u672f\u6388\u6743\u7684\u4e8b\u60c5\u3002\u8096\u745e\u6c11\u4e0b\u5348\u4ee3\u8868\u8054\u4fe1\u7ade\u4e0b\u4e24\u676115\u79d2\u7684\u5e7f\u544a\uff0c\u672c\u6765\u8981\u5b89\u6392\u63a5\u53d7\u90ae\u7535\u90e8\u4e0b\u5c5e\u7684\u62a5\u793e\u8bb0\u8005\u91c7\u8bbf\uff0c\u8096\u745e\u6c11\u6ca1\u6709\u5fc3\u60c5\u7559\u4e0b\u6765\uff0c\u5c06\u63a5\u53d7\u91c7\u8bbf\u7684\u4e8b\u60c5\u4ea4\u7ed9\u52a9\u624b\uff0c\u4ed6\u76f4\u63a5\u5750\u8f66\u8d76\u5f80\u4e2d\u5173\u6751\uff0c\u5728\u505c\u8f66\u573a\u65f6\uff0c\u78b0\u5230\u8054\u8baf\u7684\u8001\u603b\u5b59\u5fd7\u521a\u7ad9\u5728\u505c\u8f66\u573a\u91cc\uff0c\u4f3c\u4e4e\u5728\u7b49\u4eba\u3002</p><p>\u3000\u3000\u90ae\u7535\u90e8\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u4e00\u76f4\u4e0d\u5927\u548c\u7766\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u90ae\u7535\u90e8\u5bf9\u901a\u4fe1\u4ea7\u4e1a\u7684\u5784\u65ad\u8ba9\u7535\u5b50\u5de5\u4e1a\u90e8\u5f88\u6709\u610f\u89c1\uff0c\u800c\u90ae\u7535\u90e8\u53c8\u773c\u998b\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u9762\u7684\u4f01\u4e1a\u8981\u8fdc\u8fdc\u5f3a\u8fc7\u90ae\u7535\u90e8\u7684\u4e0b\u5c5e\u4f01\u4e1a\u3002\u8096\u745e\u6c11\u770b\u5230\u5b59\u5fd7\u521a\uff0c\u6253\u7b97\u4f4e\u5934\u5750\u8fdb\u8f66\u91cc\uff0c\u60f3\u4e0d\u5230\u5b59\u5fd7\u521a\u4e3b\u52a8\u8ddf\u4ed6\u6253\u62db\u547c\uff1a\u201c\u8096\u603b\u4e5f\u662f\u53bb\u7231\u8fbe\u7684\u5206\u516c\u53f8\u5427\uff0c\u7b49\u4e00\u4e0b\u4e1c\u5174\u7684\u5ed6\u7ea2\u96f7\u5427\uff0c\u6211\u4eec\u8fd9\u4e5f\u662f\u8d76\u7740\u8fc7\u53bb\u5462\u3002\u201d</p></article>"}}}]);
