(window.webpackJsonp=window.webpackJsonp||[]).push([[859],{882:function(p){p.exports={chapterTitle:null,title:"\u7b2c684\u7ae0 \u79d1\u738b\u9ad8\u79d1\u7684\u4f18\u5f85",content:"<article><h2>\u7b2c684\u7ae0 \u79d1\u738b\u9ad8\u79d1\u7684\u4f18\u5f85</h2><p>\u3000\u3000\u81ea\u4ece8\u65e5\u9ec4\u660f\uff0c\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u8fdb\u5165\u4f4d\u4e8e\u4e2d\u5173\u6751\u6d77\u6dc0\u4e2d\u8def\u7684\u7231\u8fbe\u5927\u53a6\u6d3d\u8c08\u5230\u6df1\u591c\u4ee5\u6765\uff0c\u56db\u5bb6\u516c\u53f8\u9ad8\u5c42\u63a5\u4e0b\u6765\u8fde\u7eed\u4e24\u5929\u53c8\u90fd\u5728\u7231\u8fbe\u5927\u53a6\u78b0\u9762\uff0c\u6bcf\u6b21\u90fd\u662f\u8f66\u5b50\u76f4\u63a5\u5f00\u5230\u53f0\u9636\u4e0b\u8fdb\u5165\u5927\u5385\uff0c\u53c8\u662f\u8f66\u5b50\u76f4\u63a5\u5f00\u5230\u53f0\u9636\u4e0b\u5c06\u4eba\u63a5\u8d70\uff0c\u6ca1\u6709\u7ed9\u5a92\u4f53\u8bb0\u8005\u91c7\u8bbf\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5982\u6b64\uff0c\u5b88\u5728\u7231\u8fbe\u5927\u53a6\u697c\u4e0b\u7684\u8bb0\u8005\u5374\u662f\u8d8a\u6765\u8d8a\u591a\uff0c\u5927\u5bb6\u90fd\u76f8\u4fe1\u56db\u5bb6\u516c\u53f8\u9ad8\u5c42\u5982\u6b64\u5bc6\u96c6\u800c\u7d27\u6025\u7684\u78cb\u5546\u4e00\u5b9a\u662f\u4e0e\u7231\u8fbe\u516c\u5f00\u624b\u673a\u6280\u672f\u7684\u51b3\u5b9a\u6709\u5173\u3002</p><p>\u3000\u3000\u867d\u7136\u6ca1\u6709\u4e00\u70b9\u6d88\u606f\u6cc4\u9732\u51fa\u6765\uff0c\u4f46\u662f\u4e00\u70b9\u90fd\u4e0d\u59a8\u788d\u5a92\u4f53\u7684\u60f3\u8c61\u529b\uff0c\u4ec5\u8fd9\u4e9b\u60f3\u8c61\u529b\u5c31\u8db3\u591f\u5199\u51fa\u597d\u4e9b\u7cbe\u5f69\u6587\u7ae0\u4e86\u3002</p><p>\u3000\u3000\u9664\u4e86\u5a92\u4f53\u8bb0\u8005\u4e4b\u5916\uff0c\u53c2\u52a0\u5b8c\u592e\u89c6\u5e7f\u544a\u62db\u5546\u5927\u4f1a\u7684\u4f01\u4e1a\u4ee3\u8868\u4e5f\u6ca1\u6709\u6025\u7740\u79bb\u5f00\u5317\u4eac\uff0c\u7279\u522b\u662f\u90a3\u4e9b\u6709\u7740\u76f8\u5173\u6280\u672f\u80cc\u666f\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u66f4\u662f\u4f1a\u6d3e\u5de5\u4f5c\u4eba\u5458\u7559\u5b88\u5317\u4eac\u7b49\u5019\u6d88\u606f\u3002</p><p>\u3000\u3000\u624b\u673a\u4ea7\u4e1a\u91cc\u5230\u5e95\u8574\u85cf\u7740\u591a\u4e30\u539a\u7684\u5229\u6da6\uff0c\u53c8\u4e0d\u662f\u7edd\u8ba1\u63a2\u542c\u4e0d\u5230\u7684\u79d8\u5bc6\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u65e9\u5728\u4e5d\u4e03\u5e74\u4e4b\u524d\uff0c\u56fd\u5185\u5c31\u6709\u8bb8\u591a\u5382\u5546\u4e3a\u4ece\u90ae\u7535\u90e8\u83b7\u5f97\u4e00\u5f20\u624b\u673a\u751f\u4ea7\u8bb8\u53ef\u6267\u7167\u800c\u767e\u822c\u52aa\u529b\u3002\u5c06\u7535\u5b50\u5de5\u4e1a\u90e8\u6bcf\u5e74\u6392\u5b9a\u7684\u7535\u5b50\u4f01\u4e1a\u767e\u5f3a\u699c\u62ff\u51fa\u6765\uff0c\u624b\u6307\u538b\u7740\u4e00\u4e00\u5386\u6570\u8fc7\u53bb\uff0c\u770b\u6709\u591a\u5c11\u5bb6\u4f01\u4e1a\u7edd\u8ba1\u5bf9\u8fd9\u4e2a\u4ea7\u4e1a\u6ca1\u6709\u52a8\u8fc7\u5fc3\u601d\uff0c\u5f53\u521d\u8fd8\u4e0d\u662f\u7ed9\u90ae\u7535\u90e8\u201c\u4e0d\u5177\u4f53\u6280\u672f\u6761\u4ef6\u201d\u4e00\u53e5\u8bdd\u7ed9\u6253\u53d1\u5f00\uff1f</p><p>\u3000\u3000\u5230\u4eca\u5e74\uff0c\u90ae\u7535\u90e8\u62d6\u4e0d\u8fc7\u53bb\uff0c\u624d\u53d1\u653e\u56db\u5f20\u6267\u7167\uff0c\u5374\u662f\u50e7\u591a\u7ca5\u5c11\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u8054\u4fe1\u662f\u90ae\u7535\u90e8\u7684\u5ae1\u751f\u5b50\uff0c\u8fd8\u5c06\u7b2c\u5341\u4e8c\u7814\u7a76\u6240\u7f6e\u5165\u8054\u4fe1\uff0c\u52a0\u5f3a\u8054\u4fe1\u7684\u6280\u672f\u529b\u91cf\uff1b\u8054\u8baf\u4e0e\u4e1c\u5174\u4ece\u5e7f\u5dde\u901a\u4fe1\u7814\u7a76\u6240\u83b7\u5f97\u6570\u5b57\u79fb\u52a8\u901a\u8baf\u624b\u673a\u57fa\u672c\u6280\u672f\u867d\u7136\u8ddd\u5546\u4e1a\u5316\u751f\u4ea7\u8fd8\u6709\u5f88\u9065\u8fdc\u7684\u4e00\u6bb5\u8ddd\u79bb\uff0c\u4f46\u81f3\u5c11\u6bd4\u8054\u4fe1\u8981\u5f3a\u5f97\u591a\uff0c\u90ae\u7535\u90e8\u4e5f\u4e0d\u80fd\u539a\u989c\u65e0\u803b\u5c06\u8fd9\u4e24\u5bb6\u6487\u5f00\uff1b\u771f\u6b63\u7684\u6709\u9b44\u529b\u3001\u6709\u51b3\u5fc3\u3001\u6709\u624b\u6bb5\u8fd8\u662f\u7231\u8fbe\u96c6\u56e2\uff0c\u82b1\u8d39\u4e94\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\u4ece\u5fb7\u4eea\u624b\u4e2d\u6536\u8d2d\u624b\u673a\u5236\u9020\u6280\u672f\uff0c\u53c8\u5728\u5efa\u90ba\u542f\u52a8\u56fd\u5185\u6700\u5927\u7684\u79d1\u6280\u7814\u53d1\u6295\u8d44\u9879\u76ee\u2014\u2014\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u90ae\u7535\u90e8\u4e0d\u5f97\u4e0d\u5c06\u6700\u540e\u4e00\u5f20\u73cd\u8d35\u7684\u6267\u7167\u53d1\u7ed9\u7231\u8fbe\u96c6\u56e2\u3002</p><p>\u3000\u3000\u5176\u4ed6\u7684\u4f01\u4e1a\u5374\u7ed9\u4e00\u9053\u6240\u8c13\u7684\u201c\u6280\u672f\u95e8\u69db\u201d\u6321\u5728\u7ebf\u5916\uff0c\u4f46\u662f\u771f\u6b63\u5177\u5907\u6280\u672f\u6761\u4ef6\u7684\u4e5f\u4ec5\u7231\u8fbe\u4e00\u5bb6\u800c\u5df2\uff0c\u5176\u4ed6\u4f01\u4e1a\u81ea\u7136\u6709\u6ee1\u8179\u6028\u8a00\u3002\u6028\u8a00\u5f52\u6028\u8a00\uff0c\u4f53\u5236\u6240\u5f62\u6210\u7684\u58c1\u5792\u3001\u969c\u788d\u81ea\u7136\u4e0d\u53ef\u80fd\u7ed9\u6028\u8a00\u51b2\u6e83\uff0c\u8ba9\u4eba\u610f\u6599\u4e0d\u5230\u7684\uff0c\u8fd9\u9053\u95e8\u69db\u7adf\u8ba9\u7231\u8fbe\u96c6\u56e2\u4e00\u6bb5\u7b80\u77ed\u7684\u58f0\u660e\u8f7b\u677e\u63a8\u5e73\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u6bb5\u7b80\u77ed\u7684\u58f0\u660e\uff0c\u5176\u5b9e\u5c06\u90ae\u7535\u90e8\u3001\u7535\u5b50\u5de5\u4e1a\u90e8\u90fd\u63a8\u5230\u5fae\u5999\u7684\u5904\u5883\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u6709\u5fc3\u3001\u6709\u5b9e\u529b\u4e0a\u624b\u673a\u9879\u76ee\u7edd\u5927\u591a\u6570\u7684\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u7684\u4f01\u4e1a\uff0c\u6bcf\u5e74\u6392\u5b9a\u7684\u7535\u5b50\u4f01\u4e1a\u767e\u5f3a\u699c\uff0c\u4e5f\u90fd\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u4f01\u4e1a\u5c45\u591a\u3002\u7231\u8fbe\u5c06\u6280\u672f\u516c\u5f00\uff0c\u7535\u5b50\u5de5\u4e1a\u90e8\u591a\u5bb6\u4f01\u4e1a\u90fd\u6709\u80fd\u529b\u5438\u6536\u3001\u6d88\u5316\u76f8\u5173\u6280\u672f\uff0c\u4e14\u751f\u4ea7\u6761\u4ef6\u6954\u5408\u7a0b\u5ea6\u4e5f\u9ad8\uff0c\u81ea\u7136\u6700\u4e3a\u53d7\u76ca\u3002</p><p>\u3000\u3000\u6d89\u53ca\u5230\u901a\u4fe1\u4ea7\u4e1a\u53d1\u5c55\u4e00\u8d2f\u7684\u4fdd\u5b88\u601d\u8def\uff0c\u90ae\u7535\u90e8\u5e76\u4e0d\u60f3\u53d1\u653e\u592a\u591a\u7684\u624b\u673a\u724c\u7167\uff0c\u8981\u6709\u9009\u62e9\uff0c\u90ae\u7535\u90e8\u5b81\u53ef\u7231\u8fbe\u4e0d\u516c\u5f00\u6280\u672f\u3002\u4f46\u662f\u7231\u8fbe\u8981\u8fd9\u4e48\u505a\uff0c\u90ae\u7535\u90e8\u7684\u5b98\u5458\u4e5f\u4e0d\u4f1a\u50bb\u5230\u516c\u5f00\u53bb\u62b5\u5236\u3001\u53bb\u6307\u8d23\u3002\u975e\u4f46\u4e0d\u80fd\u53bb\u62b5\u5236\u3001\u53bb\u6307\u8d23\uff0c\u8fd8\u8981\u5927\u58f0\u7684\u79f0\u8d5e\u7231\u8fbe\u7684\u8fd9\u79cd\u884c\u4e3a\uff0c\u8fd9\u79cd\u884c\u4e3a\u867d\u7136\u8ba9\u90ae\u7535\u90e8\u7684\u5904\u5883\u8f83\u4e3a\u5c34\u5c2c\uff0c\u4f46\u662f\u56fd\u5185\u4ea7\u4e1a\u7684\u5de8\u5927\u4fc3\u8fdb\u4f5c\u7528\u662f\u663e\u800c\u6613\u89c1\u7684\u3002</p><p>\u3000\u3000\u9632\u6b62\u6280\u672f\u88ab\u6ee5\u7528\uff0c\u76f8\u5173\u6280\u672f\u53ea\u6388\u6743\u7ed9\u6709\u76f8\u5173\u6280\u672f\u80cc\u666f\u7684\u4f01\u4e1a\u4f7f\u7528\uff0c\u7231\u8fbe\u96c6\u56e2\u63d0\u8bae\u6210\u7acb\u4e00\u4e2a\u7c7b\u4f3c\u5ba1\u67e5\u59d4\u5458\u4f1a\u7684\u673a\u6784\u53bb\u8fdb\u884c\u6388\u6743\u5ba1\u67e5\uff0c\u5230\u8fd9\u91cc\uff0c\u5e76\u6ca1\u6709\u8fc7\u5206\u7684\u5730\u65b9\uff0c\u4f46\u662f\u4ee5\u52a0\u5165\u5ba1\u67e5\u59d4\u5458\u4f1a\u4e3a\u6761\u4ef6\uff0c\u9650\u5236\u8054\u4fe1\u6216\u8005\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u8d34\u724c\u4ee3\u5de5\u7684\u884c\u4e3a\uff0c\u751a\u81f3\u5c06\u624b\u673a\u8d34\u724c\u8bb8\u53ef\u7684\u6743\u529b\u5b9e\u9645\u7f6e\u5165\u8fd9\u4e2a\u5ba1\u67e5\u59d4\u5458\u4f1a\uff0c\u5c31\u6709\u4e9b\u6108\u8d8a\u4e86\u2014\u2014\u7ec6\u7a76\u4e0b\u6765\uff0c\u8fd9\u672c\u5e94\u8be5\u662f\u90ae\u7535\u90e8\u624d\u5177\u6709\u7684\u6743\u529b\u3002</p><p>\u3000\u3000\u4f46\u662f\u90ae\u7535\u90e8\u7eb5\u5bb9\u8054\u4fe1\u7ed9\u79d1\u738b\u9ad8\u79d1\u8d34\u724c\uff0c\u5b9e\u9645\u4e0a\u4e5f\u662f\u540c\u65f6\u5c06\u8fd9\u4e9b\u6743\u529b\u4e0b\u653e\u7ed9\u8054\u8baf\u3001\u4e1c\u5174\u3001\u7231\u8fbe\u4e09\u5bb6\u4f01\u4e1a\u4e86\uff0c\u90ae\u7535\u90e8\u60f3\u6536\u56de\u8fd9\u4e9b\u6743\u529b\uff0c\u5c31\u662f\u5728\u6253\u81ea\u5df1\u7684\u8138\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u60f3\u4fc3\u6210\u6b64\u4e8b\uff0c\u5c31\u6ca1\u6709\u76f4\u63a5\u6c47\u62a5\u8bf4\u8fd9\u9879\u5efa\u8bae\u662f\u7231\u8fbe\u6240\u63d0\uff0c\u4e8b\u5b9e\u4e0a\u4e5f\u662f\u8054\u8baf\u7684\u5b59\u5fd7\u521a\u5c06\u5f20\u606a\u7684\u610f\u56fe\u6311\u660e\u3002</p><p>\u3000\u3000\u4fc3\u6210\u6b64\u4e8b\u4e5f\u80fd\u9650\u5236\u5168\u90e8\u6280\u672f\u96c6\u4e2d\u6388\u6743\u7684\u51fa\u73b0\uff0c\u4e5f\u5c31\u80fd\u9632\u6b62\u7535\u5b50\u5de5\u4e1a\u90e8\u4e0b\u5c5e\u4f01\u4e1a\u5f88\u5feb\u5c31\u638c\u63e1\u6240\u6709\u6280\u672f\u8dd1\u4e0a\u95e8\u8ba8\u8981\u624b\u673a\u6267\u7167\uff0c\u8fd9\u6070\u6070\u662f\u90ae\u7535\u90e8\u4e0d\u613f\u770b\u5230\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u90ae\u7535\u90e8\u5185\u90e8\u8ba8\u8bba\u8bb8\u4e45\uff0c\u6700\u7ec8\u51b3\u5b9a\uff0c\u65e2\u7136\u624b\u673a\u8d34\u724c\u4ee3\u5de5\u7684\u6743\u529b\u65e2\u7136\u5df2\u7ecf\u5b9e\u9645\u4e0b\u653e\u5230\u4f01\u4e1a\uff0c\u7531\u4f01\u4e1a\u518d\u96c6\u4e2d\u5230\u5ba1\u67e5\u59d4\u5458\u4f1a\u91cc\uff0c\u5e76\u4e0d\u662f\u4e0d\u80fd\u63a5\u53d7\u7684\u884c\u4e3a\u3002\u4f46\u662f\u90ae\u7535\u90e8\u8981\u6c42\u80fd\u53d1\u6325\u66f4\u5927\u7684\u4f5c\u7528\uff0c\u5ba1\u67e5\u59d4\u5458\u4f1a\u5728\u4f01\u4e1a\u4ee3\u8868\u4e0e\u653f\u5e9c\u4ee3\u8868\u4e4b\u5916\uff0c\u8fd8\u8981\u52a0\u4e0a\u90ae\u7535\u90e8\u7684\u4ee3\u8868\uff0c\u540c\u65f6\u9650\u5236\u7231\u8fbe\u5bf9\u8fd9\u4e2a\u5ba1\u67e5\u59d4\u5458\u4f1a\u6709\u4ec0\u4e48\u7279\u6b8a\u7684\u63a7\u5236\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u4e5f\u6e05\u695a\uff0c\u5728\u4e5d\u4e03\u5e74\uff0c\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u5bf9\u8fd9\u79cd\u62e5\u6709\u7279\u6b8a\u6743\u529b\u7684\u673a\u6784\u8981\u6c42\u76f4\u63a5\u7684\u63a7\u5236\u6743\u65e0\u7591\u662f\u5f88\u788d\u773c\u7684\u3002</p><p>\u3000\u3000\u56db\u5bb6\u516c\u53f8\u9ad8\u5c42\u6301\u7eed\u4e24\u5929\u90fd\u5728\u7231\u8fbe\u96c6\u56e2\u78cb\u5546\uff0c\u7b2c\u4e09\u5929\uff0c\u90ae\u7535\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u526f\u90e8\u957f\u80e1\u4e3a\u6bc5\u4e5f\u540c\u65f6\u51fa\u73b0\u5728\u7231\u8fbe\u5927\u53a6\uff0c\u4e5f\u5c06\u4f17\u4eba\u7684\u597d\u5947\u5fc3\u60ac\u540a\u5230\u6781\u70b9\uff0c\u5a92\u4f53\u8bb0\u8005\u4e5f\u90fd\u518d\u6b21\u71c3\u8d77\u6597\u5fd7\u805a\u62e5\u8fc7\u6765\uff0c\u5fc3\u91cc\u90fd\u5728\u60f3\uff1a\u4eca\u5929\u603b\u8be5\u6709\u4e00\u4e2a\u9636\u6bb5\u6027\u7684\u7ed3\u8bba\u51fa\u6765\u3002\u4e3a\u6b64\u4e8b\u7559\u5b88\u5728\u5317\u4eac\u7684\u4f01\u4e1a\u4ee3\u8868\u5f97\u77e5\u6613\u4e91\u98de\u3001\u80e1\u4e3a\u6bc5\u540c\u65f6\u51fa\u73b0\u5728\u7231\u8fbe\u5927\u53a6\u7684\u6d88\u606f\uff0c\u4e5f\u90fd\u7eb7\u7eb7\u8d76\u8fc7\u6765\uff0c\u60f3\u8981\u7b2c\u4e00\u65f6\u95f4\u77e5\u9053\u6709\u4ec0\u4e48\u7ed3\u8bba\u51fa\u6765\u3002</p><p>\u3000\u3000\u592e\u89c6\u62db\u5546\u5927\u4f1a\u7ed3\u675f\u4e4b\u540e\uff0c\u8c22\u5251\u5357\u3001\u90c1\u840d\u5148\u56de\u6d77\u5dde\u4e86\u3002\u90c1\u840d\u8981\u9648\u9759\u4e0e\u5b50\u5609\u7559\u4e0b\u6765\uff0c\u5979\u4eec\u731c\u6d4b\u4e0d\u4f1a\u6709\u592a\u591a\u7684\u4f01\u4e1a\u80fd\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u4f46\u662f\u5979\u4eec\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\uff0c\u79d1\u738b\u9ad8\u79d1\u83b7\u5f97\u7684\u6bd4\u5979\u4eec\u60f3\u8c61\u7684\u591a\u5f97\u591a\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0e\u5b50\u5609\u8d76\u5230\u7231\u8fbe\u5927\u53a6\uff0c\u7231\u8fbe\u5927\u53a6\u5e95\u5c42\u5927\u5385\u91cc\u5df2\u7ecf\u4eba\u6ee1\u4e3a\u60a3\uff0c\u8d76\u5230\u8fd9\u91cc\u624d\u77e5\u9053\u6613\u4e91\u98de\u3001\u80e1\u4e3a\u6bc5\u4e24\u4f4d\u526f\u90e8\u957f\u6ca1\u6709\u9017\u7559\u591a\u4e45\u5c31\u5df2\u7ecf\u79bb\u5f00\uff0c\u7559\u5f00\u524d\u53ec\u5f00\u4e86\u4e00\u4e2a\u7b80\u77ed\u7684\u8bb0\u8005\u4f1a\uff0c\u867d\u7136\u6ca1\u6709\u900f\u9732\u4ec0\u4e48\u5b9e\u8d28\u6027\u7684\u5185\u5bb9\uff0c\u542c\u8005\u5374\u80fd\u5224\u65ad\u51fa\u76ee\u524d\u4ece\u90ae\u7535\u90e8\u83b7\u5f97\u624b\u673a\u6267\u7167\u7684\u56db\u5bb6\u4f01\u4e1a\u5df2\u7ecf\u5c31\u6280\u672f\u6388\u6743\u65b9\u5f0f\u5f62\u6210\u7edf\u4e00\u610f\u89c1\u4e86\uff0c\u5c31\u662f\u4e0d\u6653\u5f97\u7231\u8fbe\u96c6\u56e2\u8fd8\u4f1a\u4e0d\u4f1a\u7ee7\u7eed\u5356\u5173\u5b50\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5bb6\u4f19\u4e00\u5b9a\u4f1a\u5356\u8db3\u5173\u5b50\u7684\uff01\u201d\u8c22\u5b50\u5609\u8fd9\u4e24\u5929\u5fc3\u90fd\u60ac\u7740\uff0c\u8bf4\u8bdd\u65f6\u90fd\u6709\u4e9b\u54ac\u7259\u5207\u9f7f\u4e86\uff0c\u8981\u662f\u6280\u672f\u6388\u6743\u7684\u65b9\u5f0f\u7531\u56db\u5bb6\u516c\u53f8\u4e00\u8d77\u5546\u5b9a\uff0c\u79d1\u738b\u9ad8\u79d1\u8fd8\u80fd\u4eab\u53d7\u7279\u6743\u5417\uff1f\u5979\u5fc3\u91cc\u90fd\u6709\u4e9b\u52a8\u6447\u3002</p><p>\u3000\u3000\u9648\u9759\u5ae3\u7136\u800c\u7b11\uff0c\u66f4\u6b23\u6170\u770b\u5230\u9526\u6e56\u5982\u6b64\u7684\u6c14\u5ea6\uff0c\u5373\u4f7f\u5c06\u8fd9\u90e8\u5206\u6280\u672f\u516c\u5f00\u51fa\u6765\uff0c\u9526\u6e56\u4e5f\u5e94\u8be5\u6709\u8db3\u591f\u7684\u4fe1\u5fc3\u5427\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u7b49\u4e09\u5bb6\u516c\u53f8\u9ad8\u5c42\u79bb\u5f00\u7231\u8fbe\u5927\u53a6\u65f6\uff0c\u53ea\u7b80\u77ed\u7684\u8bf4\u660e\u56db\u5bb6\u516c\u53f8\u5c31\u6280\u672f\u6388\u6743\u516c\u5f00\u7684\u65b9\u6848\u5f62\u6210\u7edf\u4e00\u7684\u610f\u89c1\uff0c\u4f46\u662f\u5177\u4f53\u7684\u65b9\u6848\u8fd8\u8981\u7b49\u5230\u6d77\u5dde\u3001\u7b49\u5230\u7231\u8fbe\u96c6\u56e2\u7ecf\u9500\u5546\u5927\u4f1a\u524d\u5915\u624d\u80fd\u6b63\u5f0f\u516c\u5e03\uff0c\u4e0b\u6b21\u89c1\u9762\u5c31\u662f\u5728\u6d77\u5dde\u4e86\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u3001\u5b59\u5fd7\u521a\u3001\u5ed6\u7ea2\u96f7\u7b49\u4e09\u5bb6\u516c\u53f8\u9ad8\u5c42\u76f8\u7ee7\u79bb\u5f00\uff0c\u5a92\u4f53\u8bb0\u8005\u4e0e\u4f01\u4e1a\u4ee3\u8868\u4e5f\u90fd\u7eb7\u7eb7\u79bb\u5f00\uff0c\u9648\u9759\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u53bb\u627e\u5f20\u606a\uff0c\u8fd8\u662f\u82cf\u6d25\u4e1c\u5148\u770b\u5230\u5979\u4eec\uff0c\u8bf7\u5de5\u4f5c\u4eba\u5458\u8fc7\u6765\u9080\u8bf7\u5979\u4eec\u5230\u697c\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5341\u4e8c\u5c42\u697c\u7684\u5927\u5385\u91cc\u7b49\u5019\u5979\u4eec\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u4eec\u4e00\u4e2a\u56de\u5efa\u90ba\uff0c\u4e00\u4e2a\u56de\u6d77\u5dde\u4e86\u5462\uff0c\u600e\u4e48\u4e86\uff0c\u8fd8\u662f\u6253\u7b97\u8ba9\u6211\u8bf7\u4f60\u4eec\u5750\u706b\u8f66\u4e00\u8d77\u56de\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f6010\u67086\u65e5\u665a\u4e0a\u8bf4\u7684\u8bdd\uff0c\u5230\u5e95\u7b97\u4e0d\u7b97\u6570\uff1f\u201d\u8c22\u5b50\u5609\u7741\u773c\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u90fd\u8fc7\u53bb\u8fd9\u4e48\u4e94\u516d\u5929\u4e86\uff0c\u4f60\u4eec\u8fd8\u5ff5\u5ff5\u4e0d\u5fd8\u554a\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u89c1\u8c22\u5b50\u5609\u7709\u5934\u7ea0\u7ed3\u8d77\u6765\uff0c\u62db\u547c\u5979\u4eec\u5230\u81ea\u5df1\u5728\u8fd9\u8fb9\u7684\u4e34\u65f6\u529e\u516c\u5ba4\u53bb\uff0c\u5c06\u4e00\u4efd\u6587\u4ef6\u9012\u7ed9\u9648\u9759\uff0c\u201c\u56db\u5bb6\u5f62\u6210\u7684\u7edf\u4e00\u610f\u89c1\uff0c\u5c31\u79d1\u738b\u9ad8\u79d1\u6709\u8d44\u683c\u6700\u5148\u770b\u5230\u2026\u2026\u8fd9\u662f\u4eca\u5929\u4e0b\u5348\u7684\u4f1a\u8bae\u7eaa\u8981\uff0c\u867d\u7136\u53ea\u662f\u590d\u5370\u4ef6\uff0c\u4f46\u8fd8\u662f\u4e0d\u80fd\u8ba9\u4f60\u4eec\u5e26\u8d70\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u63a5\u8fc7\u6587\u4ef6\uff0c\u4e0e\u5b50\u5609\u5750\u5230\u6c99\u53d1\u7ec6\u770b\u8d77\u6765\uff1b\u5f20\u606a\u5230\u9694\u58c1\u5e2e\u5979\u4eec\u5012\u5496\u5561\u53bb\u3002</p><p>\u3000\u3000\u4f1a\u8bae\u7eaa\u8981\u8d8a\u5f80\u540e\u7ffb\uff0c\u9648\u9759\u5fc3\u91cc\u7684\u60ca\u8bb6\u8d8a\u6df1\uff0c\u4e5f\u6709\u7740\u6df1\u6df1\u7684\u56f0\u60d1\u3002</p><p>\u3000\u3000\u6839\u636e\u4f1a\u8bae\u7eaa\u8981\u7684\u8981\u6c42\uff0c\u83b7\u5f97\u624b\u673a\u6267\u7167\u7684\u4f01\u4e1a\u82e5\u6709\u9700\u8981\u4e5f\u9700\u5c06\u751f\u4ea7\u59d4\u6258\u7ed9\u5728\u624b\u673a\u4e3b\u8981\u7ec4\u4ef6\u751f\u4ea7\u4e0e\u7814\u53d1\u4e0a\u62e5\u6709\u4e24\u5e74\u7ecf\u9a8c\u4ee5\u4e0a\u7684\u4f01\u4e1a\u4ee3\u5de5\uff0c\u8fd9\u4e00\u6761\u4ef6\u7684\u7ea6\u5b9a\uff0c\u8ba9\u5927\u89c4\u6a21\u7684\u624b\u673a\u8d34\u724c\u751f\u4ea7\u63a8\u79fb\u5230\u4e5d\u4e5d\u5e74\u4e4b\u540e\u3002\u79d1\u738b\u9ad8\u79d1\u4e8b\u5b9e\u4e0a\u6210\u4e3a\u8fd1\u671f\u60df\u4e00\u4e00\u5bb6\u62e5\u6709\u8d34\u724c\u751f\u4ea7\u624b\u673a\u8d44\u683c\u7684\u4f01\u4e1a\uff0c\u4e5f\u5c06\u662f\u60df\u4e00\u4e0d\u9700\u8981\u901a\u8fc7\u5ba1\u67e5\u59d4\u5458\u4f1a\u5ba1\u67e5\u5c31\u5c06\u83b7\u5f97\u5168\u90e8\u6280\u672f\u6388\u6743\u7684\u4f01\u4e1a\uff0c\u8fd9\u4e9b\u6761\u4ef6\u7684\u7ea6\u675f\u7b80\u76f4\u5c31\u662f\u8ba9\u79d1\u738b\u9ad8\u79d1\u62e5\u6709\u4e86\u201c\u7b2c\u4e94\u5f20\u624b\u673a\u6267\u7167\u201d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5496\u5561\u9012\u7ed9\u51fa\u9648\u9759\u3001\u5b50\u5609\uff0c\u4ed6\u4e5f\u7aef\u4e86\u4e00\u6000\u5496\u5561\u4f9d\u7740\u529e\u516c\u684c\u4e00\u89d2\u800c\u5f20\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u4e0d\u662f\u5bf9\u9648\u9759\u6709\u4ec0\u4e48\u522b\u7684\u4f01\u56fe\uff1f\u201d\u8c22\u5b50\u5609\u7741\u7740\u660e\u79c0\u7684\u773c\u7738\u770b\u7740\u5f20\u606a\uff0c\u4e00\u8138\u7684\u56f0\u60d1\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u91cc\u4e00\u53e3\u5496\u5561\u521a\u54bd\u5230\u55d3\u5b50\u773c\uff0c\u731b\u7136\u545b\u4e86\u51fa\u6765\uff0c\u4e00\u5c0f\u534a\u55b7\u5230\u5496\u5561\u676f\u91cc\uff0c\u4e00\u5c0f\u534a\u55b7\u5230\u8863\u670d\u8896\u7ba1\u4e0a\uff0c\u8fd9\u4e2b\u5934\u8fd8\u771f\u662f\u8bed\u4e0d\u60ca\u4eba\u6b7b\u4e0d\u4f11\u3002</p><p>\u3000\u3000\u5f20\u606a\u72fc\u72c8\u4e0d\u582a\u7684\u8f6c\u8eab\u5c06\u676f\u91cc\u653e\u684c\u4e0a\uff0c\u62bd\u51fa\u7eb8\u5dfe\u5c06\u8896\u7ba1\u4e0a\u7684\u5496\u5561\u6e0d\uff0c\u597d\u7b11\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4ee5\u4e3a\u6211\u6709\u4ec0\u4e48\u522b\u7684\u4f01\u56fe\uff1f\u201d</p><p>\u3000\u3000\u89c1\u5f20\u606a\u5982\u6b64\u72fc\u72c8\uff0c\u9648\u9759\u62bf\u5634\u6291\u7740\u7b11\uff0c\u773c\u542b\u8584\u7f9e\uff0c\u6ee1\u9762\u6625\u8272\u6b63\u6d53\uff0c\u767d\u7699\u7684\u808c\u80a4\u4f3c\u6577\u4e86\u4e00\u5c42\u7c89\u4f3c\u7684\u5fae\u7ea2\uff0c\u624b\u522b\u5728\u80cc\u540e\u6390\u7740\u5b50\u5609\u4e0d\u8ba9\u5979\u80e1\u8bf4\u516b\u9053\uff1b\u8c22\u5b50\u5609\u72e1\u9ee0\u7684\u7b11\u7740\uff0c\u5728\u5979\u773c\u91cc\uff0c\u5f20\u606a\u7684\u52a8\u673a\u7684\u786e\u503c\u5f97\u6000\u7591\u554a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53d7\u4e0d\u5230\u8fd9\u4e2b\u5934\u8fd9\u6837\u770b\u4eba\u7684\u773c\u795e\uff0c\u8bf4\u9053\uff1a\u201c\u5e94\u8be5\u8bf4\u79d1\u738b\u9ad8\u79d1\u4ecb\u5165\u7684\u65f6\u673a\u5f88\u597d\uff0c\u662f\u5728\u9526\u6e56\u52aa\u529b\u5173\u4e0a\u8fd9\u9053\u53e3\u5b50\u4e4b\u524d\u5c31\u4ecb\u5165\u8d34\u724c\u4ee3\u5de5\u4e1a\u52a1\uff0c\u867d\u8bf4\u8fd8\u6ca1\u6709\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u4f46\u662f\u8fd9\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\u5374\u5f88\u5173\u952e\uff0c\u5c31\u5df2\u7ecf\u6210\u4e86\u65e2\u9042\u7684\u4e8b\u5b9e\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u8054\u4fe1\u5728\u56db\u5bb6\u4f01\u4e1a\u91cc\u5404\u65b9\u9762\u7684\u529b\u91cf\u6700\u5f31\uff0c\u5374\u53c8\u662f\u83b7\u5f97\u90ae\u7535\u90e8\u76f4\u63a5\u652f\u6301\u7684\u4f01\u4e1a\uff0c\u90ae\u7535\u90e8\u8fd8\u9700\u8981\u4e00\u5f20\u8138\u76ae\u6765\u906e\u7f9e\uff0c\u6211\u4eec\u4e0d\u80fd\u5c06\u90ae\u7535\u90e8\u7684\u8fd9\u5f20\u8138\u76ae\u786c\u751f\u751f\u7ed9\u6495\u4e0b\u6765\u2026\u2026\u4f60\u4eec\u8981\u7406\u89e3\u6210\u5176\u4ed6\u7684\u4f01\u56fe\uff0c\u6211\u4e5f\u6ca1\u529e\u6cd5\u3002\u201d\u5f20\u606a\u644a\u644a\u624b\uff0c\u65e0\u53ef\u5948\u4f55\u7684\u5750\u5230\u9648\u9759\u5bf9\u9762\u7684\u6c99\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u7684\u5b58\u5728\uff0c\u8054\u4fe1\u9664\u4e86\u4ece\u79d1\u738b\u9ad8\u79d1\u6536\u53d6\u5de8\u989d\u7684\u8d34\u724c\u8d39\u7528\u4e4b\u5916\uff0c\u8fd8\u80fd\u5c06\u79d1\u738b\u9ad8\u79d1\u7684\u4e1a\u7ee9\u6570\u636e\u76f4\u63a5\u5e76\u5165\u8054\u4fe1\uff0c\u66f4\u4f55\u51b5\u8054\u4fe1\u5df2\u7ecf\u4e0e\u79d1\u738b\u9ad8\u79d1\u7b7e\u7f72\u4e86\u4ee3\u5de5\u5305\u9500\u7684\u5408\u4f5c\u534f\u8bae\uff0c\u7231\u8fbe\u3001\u8054\u8baf\u3001\u4e1c\u5174\u5728\u8fd9\u65b9\u9762\u5374\u662f\u8981\u505a\u51fa\u8ba9\u6b65\u7684\u3002</p><p>\u3000\u3000\u867d\u7136\u542c\u5f20\u606a\u8fd9\u4e48\u89e3\u91ca\uff0c\u9648\u9759\u5f53\u7136\u4e0d\u4f1a\u8ba4\u4e3a\u8fd9\u4e00\u5207\u90fd\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u6b64\u65f6\u7684\u79d1\u738b\u9ad8\u79d1\u592a\u5fae\u4e0d\u8db3\u9053\u4e86\uff0c\u751a\u81f3\u4e0d\u8db3\u4ee5\u8ba9\u9526\u6e56\u3001\u8054\u8baf\u3001\u8054\u4fe1\u3001\u4e1c\u5174\u56db\u5bb6\u516c\u53f8\u5728\u8c08\u5224\u65f6\u6b63\u800c\u516b\u7ecf\u7684\u63d0\u8d77\uff0c\u8981\u6ca1\u6709\u5f20\u606a\u5c45\u4e2d\u5e37\u5e44\uff0c\u79d1\u738b\u9ad8\u79d1\u600e\u4e48\u53ef\u80fd\u83b7\u5f97\u6b64\u65f6\u7684\u4f18\u5f85\uff1f\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5374\u53c8\u4e0d\u80fd\u8bf4\u4ec0\u4e48\uff0c\u8981\u8bf4\u611f\u8c22\u7684\u8bdd\uff0c\u597d\u50cf\u771f\u8bf4\u4ed6\u6709\u4ec0\u4e48\u522b\u7684\u4f01\u56fe\u3002\u9648\u9759\u4fcf\u8138\u6709\u4e9b\u5fae\u70e7\uff0c\u8f6c\u8138\u770b\u7740\u7a97\u5916\u660e\u51c0\u6e05\u6f88\u7684\u5929\u7a7a\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u52c9\u52c9\u5f3a\u5f3a\u63a5\u53d7\u5f20\u606a\u7684\u89e3\u91ca\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f97\u77e5\u79d1\u738b\u9ad8\u79d1\u83b7\u5f97\u5982\u6b64\u7684\u5f85\u9047\uff0c\u9648\u9759\u4e0e\u5b50\u5609\u4e00\u523b\u90fd\u4e0d\u60f3\u5728\u5317\u4eac\u6ede\u7559\uff0c\u8fd9\u65f6\u5019\u8d76\u53bb\u673a\u573a\u4e58\u98de\u673a\u5df2\u7ecf\u6765\u4e0d\u53ca\uff0c\u53ea\u6709\u968f\u5f20\u606a\u591c\u95f4\u4e58\u706b\u8f66\u8fd4\u56de\u5efa\u90ba\u3002</p><p>\u3000\u3000\u5f85\u9648\u9759\u4e0e\u5b50\u5609\u8fd4\u56de\u5bbe\u9986\u6536\u62fe\u884c\u674e\uff0c\u5f20\u606a\u5c06\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u627e\u6765\uff0c\u4ed6\u811a\u5e95\u62b9\u6cb9\u6e9c\u5f97\u98de\u5feb\uff0c\u5374\u5c06\u65e0\u5c3d\u7684\u9ebb\u70e6\u7559\u7ed9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u3002</p><p>\u3000\u3000\u201c\u597d\u5427\uff0c\u8fd9\u65f6\u5019\u4f60\u4eec\u5e94\u8be5\u6709\u4e00\u79cd\u5154\u5b50\u88ab\u72d7\u8ffd\u7740\u54ac\u7684\u7d27\u8feb\u611f\u4e86\u5427\uff0c\u201d\u5f20\u606a\u6413\u7740\u624b\uff0c\u5bf9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u90e8\u5206\u6280\u672f\u516c\u5f00\u4e4b\u540e\uff0c\u6211\u4eec\u8fd8\u60f3\u7ee7\u7eed\u957f\u65f6\u95f4\u7684\u4fdd\u6301\u6280\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u5c31\u4e0d\u662f\u4e00\u4ef6\u8f7b\u677e\u81ea\u5982\u7684\u4e8b\u60c5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7684\u786e\u4e0d\u8f7b\u677e\uff0c\u8c01\u8ba9\u6211\u4eec\u9009\u62e9\u7684\u5bf9\u624b\u662f\u97e9\u56fd\u4e09\u661f\u3001\u662f\u65e5\u672c\u7d22\u5c3c\u3001\u662f\u7f8e\u56fdGE\uff1f\u4ed6\u4eec\u90fd\u7ad9\u5728\u4ea7\u4e1a\u91d1\u5b57\u5854\u9876\u7aef\u7684\u7535\u5b50\u5de8\u4eba\u3002\u4ece\u516b\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\uff0c\u56fd\u5185\u2018\u81ea\u4e3b\u7814\u53d1\u4e0e\u6280\u672f\u5f15\u8fdb\u2019\u76f8\u7ed3\u5408\u7684\u4ea7\u4e1a\u53d1\u5c55\u65b9\u9488\u5c31\u53d1\u751f\u626d\u66f2\uff0c\u5230\u73b0\u5728\u5df2\u7ecf\u6210\u4e86\u2018\u5f15\u8fdb\u4e3a\u4e3b\u3001\u81ea\u4e3b\u7814\u53d1\u4e3a\u8f85\u2019\u7684\u4ea7\u4e1a\u53d1\u5c55\u683c\u5c40\uff0c\u5374\u662f\u5982\u6b64\uff0c\u4f7f\u5f97\u56fd\u5185\u7684\u6280\u672f\u57fa\u7840\u3001\u4ea7\u4e1a\u57fa\u7840\u4e0e\u897f\u65b9\u56fd\u5bb6\u7684\u5dee\u8ddd\u8d8a\u6765\u8d8a\u5927\uff0c\u4f9d\u8d56\u6027\u4e5f\u8d8a\u6765\u8d8a\u5f3a\u3002\u6ca1\u6709\u8fd9\u4e2a\u57fa\u7840\u5728\uff0c\u9526\u6e56\u662f\u65e0\u6cd5\u72ec\u81ea\u8d70\u4e0a\u4ea7\u4e1a\u91d1\u5b57\u5854\u9876\u7aef\u7684\uff0c\u201d\u9648\u4fe1\u751f\u770b\u4e86\u770b\u81ea\u5df1\u5728\u8bb0\u5f55\u672c\u4e0a\u8bb0\u5f55\u7684\u5de5\u4f5c\u5b89\u6392\uff0c\u8bf4\u9053\uff0c\u201c\u7b49\u4e01\u69d0\u660e\u5929\u5230\u5317\u4eac\uff0c\u6211\u4eec\u5c31\u76f4\u63a5\u98de\u7f8e\u56fd\u53bb\uff0c\u79fb\u52a8\u901a\u8baf\u5b9e\u9a8c\u5ba4\u4e5f\u662f\u5230\u4e86\u6302\u724c\u7684\u65f6\u5019\u4e86\u2026\u2026\u5fb7\u4eea\u90a3\u8fb9\uff0c\u8fd8\u662f\u8981\u518d\u63a5\u89e6\u4e00\u4e0b\uff0c\u6bd5\u7adf\u8fd9\u4e2a\u4ea7\u4e1a\u96c6\u7fa4\u53d1\u5c55\u8d77\u6765\uff0c\u5bf9\u5fb7\u4eea\u6269\u5927\u9ad8\u7aef\u82af\u7247\u5e02\u573a\u7684\u4efd\u989d\u6709\u7740\u6781\u5927\u7684\u597d\u5904\u3002\u5fb7\u4eea\u4e00\u76f4\u90fd\u6709\u524a\u51cf\u4e2d\u4f4e\u7aef\u4e1a\u52a1\u7684\u51b2\u52a8\uff0c\u867d\u7136\u5728\u65b0\u5174\u5e02\u573a\u4e0a\u4e2d\u4f4e\u7aef\u82af\u7247\u957f\u671f\u90fd\u662f\u4e3b\u6d41\uff0c\u4f46\u662f\u5fb7\u4eea\u5728\u4e2d\u4f4e\u7aef\u82af\u7247\u5236\u9020\u4e0a\uff0c\u6bd4\u8d77\u4e9a\u6d32\u7684\u82af\u7247\u5236\u9020\u5546\uff0c\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u4f18\u52bf\u4e86\uff0c\u8981\u662f\u5fb7\u4eea\u8fd8\u4e0d\u60f3\u5f7b\u5e95\u653e\u5f03\u8fd9\u5757\u86cb\u7cd5\uff0c\u90a3\u5c31\u53ea\u6709\u5c06\u8fd9\u4e9b\u4e1a\u52a1\u90fd\u8f6c\u79fb\u5230\u4e9a\u6d32\u6765\uff0c\u6211\u770b\u6211\u4eec\u7684\u673a\u4f1a\u5f88\u5927\u554a\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e58\u8f66\u8fd4\u56de\u5bbe\u9986\u6536\u62fe\u884c\u88c5\uff0c\u5728\u8f66\u4e0a\uff0c\u9648\u9759\u4e0e\u90c1\u840d\u901a\u8fc7\u7535\u8bdd\uff0c\u544a\u8bc9\u5979\u8fd9\u4e2a\u632f\u594b\u4eba\u5fc3\u7684\u6d88\u606f\uff0c\u90c1\u840d\u5728\u7535\u8bdd\u90a3\u5934\u6c89\u9ed8\u4e86\u8bb8\u4e45\uff0c\u624d\u538b\u6291\u7684\u53d1\u51fa\u4e00\u58f0\u6b22\u547c\uff1a\u201c\u8fd9\u6837\u7684\u5c0f\u7537\u4eba\uff0c\u592a\u60f9\u4eba\u7231\u4e86\u3002\u201d\u90c1\u840d\u5728\u7535\u8bdd\u91cc\u53d1\u8086\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u8868\u8fbe\u5979\u6291\u5236\u4e0d\u4f4f\u7684\u6fc0\u52a8\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5218\u660e\u8f89\u8f9e\u804c\u540e\uff0c\u4eba\u8fd8\u6ca1\u6709\u79bb\u5f00\u6d77\u5dde\u2026\u2026\u201d</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u4ecb\u5165\u624b\u673a\u4ee3\u5de5\u4e1a\u52a1\uff0c\u4f46\u662f\u9500\u552e\u8fd8\u662f\u5229\u7528\u6d77\u6cf0\u7684\u5206\u9500\u6e20\u9053\uff0c\u90c1\u840d\u53ef\u4e0d\u60f3\u5728\u8fd9\u4e0a\u9762\u53d7\u5236\u4e8e\u4eba\u3002</p><p>\u3000\u3000\u542c\u90c1\u840d\u8fd9\u65f6\u5019\u63d0\u5230\u5218\u660e\u8f89\uff0c\u9648\u9759\u5f53\u7136\u80fd\u660e\u767d\u5979\u7684\u610f\u601d\uff0c\u5218\u660e\u8f89\u4ece\u9526\u6e56\u8df3\u69fd\u51fa\u6765\uff0c\u5c06\u81ea\u5df1\u641e\u5f97\u5f88\u72fc\u72c8\u4e0d\u582a\uff0c\u4f46\u662f\u4ed6\u672c\u4eba\u7684\u80fd\u529b\u8fd8\u662f\u503c\u5f97\u80af\u5b9a\u7684\u3002\u4f5c\u4e3a\u7ed9\u5f20\u606a\u6316\u6398\u51fa\u6765\u7684\u56db\u4eba\u4e4b\u4e00\uff0c\u8981\u4e0d\u662f\u5728\u5173\u952e\u65f6\u523b\u610f\u5fd7\u52a8\u6447\uff0c\u6b64\u65f6\u7559\u5728\u9526\u6e56\uff0c\u5730\u4f4d\u4e0d\u4f1a\u6bd4\u6768\u4e91\u4f4e\u592a\u591a\u3002\u5c24\u4e3a\u91cd\u8981\u7684\u662f\uff0c\u5218\u660e\u8f89\u7b49\u4eba\u4e00\u5f00\u59cb\u8ddf\u5f20\u606a\u5c31\u662f\u505a\u7231\u7acb\u4fe1\u624b\u673a\u7684\u4ee3\u7406\u8d77\u6b65\u7684\uff0c\u53ef\u4ee5\u8bf4\uff0c\u6d77\u6cf0\u7684\u624b\u673a\u5206\u9500\u6e20\u9053\uff0c\u8fd8\u662f\u5f20\u606a\u7387\u9886\u5218\u660e\u8f89\u7b49\u4eba\u6253\u4e0b\u7684\u57fa\u7840\uff0c\u79d1\u738b\u9ad8\u79d1\u8981\u4e0d\u60f3\u5728\u9500\u552e\u6e20\u9053\u4e0a\u53d7\u5236\u4e8e\u6d77\u6cf0\uff0c\u5173\u952e\uff0c\u6d77\u6cf0\u7684\u9500\u552e\u7f51\u8fd8\u53ea\u8986\u76d6\u4e1c\u6d77\u7701\uff0c\u79d1\u738b\u9ad8\u79d1\u8981\u771f\u6b63\u7684\u53d1\u5c55\u8d77\u6765\uff0c\u5c31\u8981\u9010\u6b65\u7684\u5efa\u7acb\u81ea\u5df1\u7684\u9500\u552e\u6e20\u9053\uff0c\u5218\u660e\u8f89\u81ea\u7136\u662f\u5f88\u6709\u7528\u7684\u4e00\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0e\u5218\u660e\u8f89\u5171\u4e8b\u4e5f\u6709\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u5c31\u9526\u6e56\u5bf9\u5218\u660e\u8f89\u7684\u6001\u5ea6\uff0c\u4e4b\u524d\u4e5f\u95ee\u8fc7\u5f20\u606a\u3002\u9526\u6e56\u4e0d\u63a5\u7eb3\u5218\u660e\u8f89\u56de\u9526\u6e56\uff0c\u662f\u5218\u660e\u8f89\u5728\u9526\u6e56\u627e\u4e0d\u5230\u9002\u5408\u4ed6\u7684\u4f4d\u5b50\uff0c\u9526\u6e56\u8fd8\u4e0d\u81f3\u4e8e\u6c14\u5ea6\u72ed\u7a84\u5230\u5bb9\u4e0d\u4e0b\u5218\u660e\u8f89\u8fd9\u4e48\u4e00\u4e2a\u5c0f\u4eba\u7269\u3002</p><p>\u3000\u3000\u9648\u9759\u60f3\u7740\u8fd8\u8981\u4e0e\u5f20\u606a\u4e00\u8d77\u4e58\u706b\u8f66\u56de\u5efa\u90ba\uff0c\u8fd8\u8981\u518d\u786e\u8ba4\u4e00\u4e0b\u4ed6\u7684\u610f\u89c1\uff0c\u4e5f\u6ca1\u6709\u5728\u7535\u8bdd\u91cc\u8ddf\u90c1\u840d\u660e\u8bf4\uff0c\u53ea\u662f\u8bf4\u81ea\u5df1\u518d\u8003\u8651\u4e00\u4e0b\uff0c\u660e\u5929\u7ed9\u5979\u7b54\u590d\u3002</p><p>\u3000\u3000\u90c1\u840d\u5728\u7535\u8bdd\u90a3\u5934\u81ea\u7136\u8bf4\u597d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u6b21\u4e58\u706b\u8f66\uff0c\u8fd8\u5982\u6765\u65f6\u7684\u6563\u6f2b\u95f2\u9002\uff0c\u53ea\u4e0d\u8fc7\u9648\u9759\u3001\u8c22\u5b50\u5609\u66f4\u591a\u4e9b\u4ea2\u594b\u7684\u60c5\u7eea\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u7528\u4e0d\u7528\u5218\u660e\u8f89\uff0c\u5f20\u606a\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u4e14\u4e0d\u8bf4\u5218\u660e\u8f89\u4e5f\u4e3a\u4eca\u65e5\u7684\u9526\u6e56\u7acb\u4e0b\u6c57\u9a6c\u529f\u52b3\uff0c\u8fd9\u70b9\u5bb9\u4eba\u7684\u6c14\u5ea6\u4e5f\u662f\u6709\u7684\u3002</p><p>\u3000\u3000\u9887\u4e3a\u9057\u61be\u7684\uff0c\u8fd9\u8d9f\u5217\u8f66\u4e0d\u53ef\u80fd\u518d\u673a\u7f18\u5de7\u5408\u7684\u4e0e\u90a3\u4f4d\u97e9\u56fd\u5927\u80f8\u7f8e\u5973\u674e\u99a8\u4e88\u76f8\u9047\uff0c\u5f20\u606a\u5fc3\u60f3\u5979\u5927\u6982\u5df2\u7ecf\u8fd4\u56de\u97e9\u56fd\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7740\u54c8\u6b20\u8981\u56de\u4ed6\u7684\u5305\u53a2\u53bb\u7761\u89c9\uff0c\u8c22\u5b50\u5609\u5374\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u653e\u8fc7\u4ed6\u3002</p><p>\u3000\u3000\u8bf4\u767d\u4e86\uff0c\u79d1\u738b\u9ad8\u79d1\u8fd8\u53ea\u662f\u58f3\u4f01\u4e1a\u3002\u72ec\u7acb\u7684\u9500\u552e\u6e20\u9053\uff0c\u6ca1\u6709\uff1b\u6280\u672f\u57fa\u7840\u4e5f\u5b8c\u5168\u6ca1\u6709\uff1b\u751f\u4ea7\u554a\uff0c\u4e00\u53f0\u6ce8\u5851\u673a\u8fd8\u662f\u79df\u7528\u9526\u6e56\u65d7\u4e0b\u7cbe\u5bc6\u6a21\u5177\u5de5\u5382\u7684\uff0c\u6240\u8c13\u7684\u7ec4\u88c5\u4e5f\u51e0\u4e4e\u662f\u62ff\u9526\u6e56\u7684\u534a\u6210\u54c1\u624b\u673a\u8fdb\u884c\u6700\u7b80\u5355\u7684\u624b\u5de5\u4f5c\u4e1a\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u4e0d\u4ec5\u53ef\u80fd\u662f\u4e24\u5e74\u5185\u60df\u4e00\u4e00\u5bb6\u5f97\u5230\u624b\u673a\u4ee3\u5de5\u9500\u552e\u8bb8\u53ef\u7684\u4f01\u4e1a\uff0c\u8fd8\u83b7\u5f97\u9526\u6e56\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u4f46\u662f\uff0c\u5982\u4f55\u6700\u5927\u9650\u5ea6\u7684\u5229\u7528\u597d\u773c\u4e0b\u7684\u4f18\u52bf\uff0c\u4e5f\u662f\u4ee4\u9648\u9759\u3001\u8c22\u5b50\u5609\u5979\u4eec\u4ea2\u594b\u4e4b\u4f59\u6700\u5934\u75bc\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u96be\u5f97\u5f20\u606a\u81ea\u5df1\u9001\u4e0a\u95e8\u6765\uff0c\u8c22\u5b50\u5609\u5f53\u7136\u4e5f\u4e0d\u5ba2\u6c14\uff0c\u5c31\u5c06\u4ed6\u63ea\u5728\u5305\u53a2\u91cc\u5c31\u5dee\u4e25\u5211\u8feb\u4f9b\u4e86\u3002</p><p>\u3000\u3000\u201c\u6211\u4e00\u4e2a\u5927\u7537\u4eba\uff0c\u6df1\u66f4\u534a\u591c\u8fd8\u8ddf\u4f60\u4eec\u6df7\u5728\u4e00\u8d77\uff0c\u6210\u4ec0\u4e48\u4f53\u7edf\uff1f\u201d\u8f66\u7a97\u5916\u591c\u8272\u5df2\u6df1\uff0c\u5f20\u606a\u6253\u7740\u54c8\u6b20\u8981\u56de\u4ed6\u7684\u5305\u53a2\u7761\u89c9\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u90fd\u6ca1\u6709\u5acc\u5f03\u4f60\uff0c\u4f60\u626d\u626d\u634f\u634f\u505a\u4ec0\u4e48\uff0c\u5927\u4e0d\u4e86\u8ba9\u4f60\u5728\u8fd9\u91cc\u7761\u4e0b\u6765\u5c31\u662f\u2026\u2026\u201d\u8c22\u5b50\u5609\u4e0d\u5c51\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u5efa\u90ba\u4e0e\u5317\u4eac\u4e4b\u95f4\u7684\u7a7a\u8c03\u5217\u8f66\u8fd8\u6ca1\u6709\u9ad8\u7ea7\u5305\u53a2\uff0c\u8981\u6709\uff0c\u90a3\u5c31\u8981\u6302\u4e13\u5217\u8f66\u53a2\u4e86\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u5962\u671b\u4eab\u53d7\u5982\u6b64\u5962\u534e\u7684\u4eab\u53d7\uff0c\u90a3\u4e5f\u592a\u5174\u5e08\u52a8\u4f17\u4e86\u3002\u666e\u901a\u7684\u8f6f\u5367\u5305\u53a2\u6709\u56db\u4e2a\u94fa\u4f4d\uff0c\u53c8\u4e0d\u662f\u4e09\u4e2a\u4eba\u6324\u4e00\u5f20\u94fa\uff0c\u5f20\u606a\u624d\u4e0d\u7a00\u7f55\u3002</p><p>\u3000\u3000\u5f20\u606a\u6323\u624e\u7740\u56de\u4ed6\u7684\u5305\u53a2\u7761\u89c9\u53bb\u4e86\uff0c\u8c22\u5b50\u5609\u561f\u7740\u5634\uff0c\u8ddf\u9648\u9759\u62b1\u6028\u9053\uff1a\u201c\u8981\u662f\u6211\u4e0d\u5728\uff0c\u8fd9\u5bb6\u4f19\u6307\u4e0d\u5b9a\u5c31\u8d56\u7740\u4e0d\u80af\u8d70\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u80e1\u8bf4\u516b\u9053\u4ec0\u4e48\uff0c\u201d\u9648\u9759\u4e5f\u6015\u591c\u91cc\u4e0e\u5f20\u606a\u72ec\u5904\uff0c\u5c31\u6015\u90a3\u4efd\u5fc3\u601d\u6536\u85cf\u4e0d\u4e86\uff0c\u7f9e\u607c\u7684\u4e0d\u8ba9\u5b50\u5609\u80e1\u8bf4\u516b\u9053\uff0c\u201c\u4f60\u603b\u4e0d\u80fd\u6307\u671b\u522b\u4eba\u5e2e\u4f60\u5c06\u8def\u90fd\u94fa\u5e73\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u53d1\u6101\u554a\uff0c\u201d\u8c22\u5b50\u5609\u6367\u8fc7\u9648\u9759\u7684\u8111\u888b\uff0c\u5c06\u5979\u7684\u957f\u53d1\u6293\u5f97\u4e71\u84ec\u84ec\u7684\uff0c\u53c8\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u771f\u662f\u53d1\u6101\u554a\uff0c\u6211\u4eec\u8be5\u4ece\u54ea\u91cc\u5165\u624b\u624d\u597d\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u53d1\u6101\u626f\u81ea\u5df1\u7684\u5934\u53d1\u5c31\u597d\u4e86\uff01\u201d\u9648\u9759\u8fb9\u6323\u624e\u8fb9\u6297\u8bae\uff0c\u5c06\u7ed9\u5b50\u5609\u6293\u4e71\u7684\u5934\u53d1\u7406\u987a\uff0c\u8ba9\u5b50\u5609\u5b89\u5206\u7684\u5750\u597d\uff0c\u8bf4\u9053\uff0c\u201c\u9526\u6e56\u7684\u610f\u56fe\uff0c\u4e5f\u662f\u8981\u5728\u6d77\u5dde\u3001\u5efa\u90ba\u5f62\u6210\u4e00\u4e2a\u5e9e\u5927\u7684\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u4e00\u5207\u53d1\u5c55\u987a\u5229\u7684\u8bdd\uff0c\u624b\u673a\u751f\u4ea7\u6240\u9700\u8981\u7684\u6240\u6709\u96f6\u914d\u4ef6\u3001\u5e94\u7528\u6280\u672f\uff0c\u8fd9\u4e2a\u4ea7\u4e1a\u96c6\u7fa4\u90fd\u80fd\u591f\u63d0\u4f9b\u3002\u79d1\u738b\u9ad8\u79d1\u867d\u7136\u83b7\u5f97\u5168\u90e8\u6280\u672f\u7684\u6388\u6743\uff0c\u4f46\u662f\u4e0d\u5e94\u8be5\u8d2a\u591a\u6c42\u5168\uff0c\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u8d2a\u591a\u6c42\u5168\uff0c\u773c\u4e0b\u6700\u4e3b\u8981\u7684\uff0c\u8fd8\u662f\u5438\u6536\u3001\u6d88\u5316\u7ec4\u88c5\u4e0a\u7684\u6280\u672f\uff0c\u4e5f\u6709\u5fc5\u8981\u9a6c\u4e0a\u6210\u7acb\u4e00\u5bb6\u624b\u673a\u8bbe\u8ba1\u516c\u53f8\u2026\u2026\u54e6\uff0c\u5bf9\u4e86\uff0c\u4f60\u8fd9\u6b21\u53ef\u4ee5\u5c06\u4f60\u7684\u96f6\u82b1\u94b1\u6b63\u5f0f\u62ff\u51fa\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u8c22\u5b50\u5609\u6324\u7740\u7709\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u7684\u8d26\u6237\u5df2\u7ecf\u7ed9\u5c01\u5927\u534a\u5e74\u4e86\uff0c\u597d\u5427\uff0c\u867d\u7136\u5f88\u5934\u75bc\uff0c\u8fd8\u662f\u8981\u518d\u9762\u5bf9\u90a3\u4e2a\u8001\u987d\u56fa\u7684\u2026\u2026\u6211\u8981\u5c06\u4ed6\u4eec\u90fd\u8e29\u5728\u811a\u4e0b\u3002\u201d</p></article>"}}}]);
