(window.webpackJsonp=window.webpackJsonp||[]).push([[86],{109:function(p){p.exports={chapterTitle:null,title:"\u7b2c86\u7ae0 \u9020\u7eb8\u5382\u65b0\u7684\u5f00\u59cb",content:"<article><h2>\u7b2c86\u7ae0 \u9020\u7eb8\u5382\u65b0\u7684\u5f00\u59cb</h2><p>\u3000\u3000\u201c\u4e3a\u4ec0\u4e48\u8981\u8fd9\u4e48\u590d\u6742\uff1f\u201d\u5b8b\u57f9\u660e\u6709\u4e9b\u7591\u60d1\uff0c\u53cd\u6b63\u662f\u7531\u5468\u590d\u8d1f\u8d23\u9020\u7eb8\u5382\u7684\u751f\u4ea7\u7ba1\u7406\uff0c\u641e\u4e0d\u660e\u767d\u5f20\u606a\u4e3a\u4ec0\u4e48\u575a\u6301\u8981\u5c06\u5468\u590d\u8c03\u5165\u9526\u6e56\u3002</p><p>\u3000\u3000\u201c\u7269\u5c3d\u5176\u7528\u3001\u4eba\u5c3d\u5176\u6750\uff0c\u5c31\u6211\u6240\u4e86\u89e3\u7684\u60c5\u51b5\uff0c\u5468\u5382\u957f\u5728\u65b0\u5149\u9020\u7eb8\u5382\u62ff\u7684\u90a3\u4efd\u5de5\u8d44\u6709\u4e9b\u59d4\u5c48\u4ed6\u7684\u624d\u80fd\uff0c\u6240\u4ee5\u60f3\u5230\u4ee5\u9526\u6e56\u516c\u53f8\u501f\u7528\u7684\u65b9\u5f0f\uff0c\u65b9\u4fbf\u7ed9\u5468\u5382\u957f\u63d0\u9ad8\u5de5\u8d44\u3002\u201d</p><p>\u3000\u3000\u65b0\u5149\u9020\u7eb8\u5382\u5c5e\u4e8e\u56fd\u6709\u4f01\u4e1a\uff0c\u5c31\u7b97\u7ed9\u9526\u6e56\u516c\u53f8\u627f\u5305\uff0c\u5468\u590d\u662f\u4e8b\u4e1a\u7f16\u5236\u7684\u5728\u804c\u5e72\u90e8\uff0c\u6240\u4ee5\u5468\u590d\u5728\u65b0\u5149\u9020\u7eb8\u5382\u7684\u57fa\u672c\u5de5\u8d44\u4e0e\u5c97\u4f4d\u6d25\u8d34\u662f\u56fa\u5b9a\u7684\uff0c\u7531\u4e8e\u65b0\u5149\u9020\u7eb8\u5382\u7684\u76c8\u5229\u662f\u7531\u9526\u6e56\u516c\u53f8\u4e0e\u57ce\u5357\u533a\u653f\u5e9c\u516d\u56db\u5206\u6210\uff0c\u4ece\u4e00\u5e74\u7684\u76c8\u5229\u4e2d\u62ff\u51fa\u591a\u5c11\u7ed9\u5458\u5de5\u53d1\u653e\u798f\u5229\u5956\u91d1\u7684\u5206\u914d\u65b9\u6848\u5c31\u9700\u8981\u7531\u57ce\u5357\u533a\u653f\u5e9c\u4e0e\u9526\u6e56\u516c\u53f8\u4e00\u8d77\u8ba8\u8bba\u540c\u610f\u624d\u80fd\u901a\u8fc7\u3002</p><p>\u3000\u3000\u65b0\u5149\u9020\u7eb8\u5382\u76ee\u524d\u4fdd\u7559\u4e0b\u6765\u7684\u7ba1\u7406\u4eba\u5458\u5f53\u4e2d\uff0c\u5468\u590d\u7406\u6240\u5f53\u7136\u7684\u80fd\u62ff\u5230\u6700\u9ad8\u7684\u5956\u91d1\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u6bd4\u5176\u4ed6\u4eba\u9ad8\u591a\u5c11\u3002\u542c\u5f20\u606a\u7684\u53e3\u6c14\uff0c\u662f\u8981\u7ed9\u5468\u590d\u5927\u5e45\u5ea6\u6da8\u5de5\u8d44\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u77e5\u9053\u848b\u8587\u5728\u9526\u6e56\u516c\u53f8\u7684\u85aa\u8d44\uff0c\u77e5\u9053\u7ed9\u9526\u6e56\u516c\u53f8\u501f\u7528\uff0c\u85aa\u8d44\u4e0d\u4f1a\u6bd4\u848b\u8587\u5dee\u591a\u5c11\uff0c\u800c\u5de5\u4f5c\u5173\u7cfb\u7559\u5728\u9020\u7eb8\u5382\uff0c\u7edd\u5bf9\u62ff\u4e0d\u5230\u8fd9\u4e48\u591a\u7684\u5de5\u8d44\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u533a\u91cc\u7ed9\u5468\u590d\u529e\u505c\u804c\u7559\u85aa\u7684\u624b\u7eed\uff0c\u501f\u7528\u7ed9\u9526\u6e56\u516c\u53f8\uff0c\u4e5f\u7b97\u662f\u7ed9\u65b0\u5149\u9020\u7eb8\u5382\u8282\u7ea6\u4e00\u4e2a\u4eba\u7684\u5f00\u9500\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6beb\u4e0d\u5ba2\u6c14\u7684\u8bf4\uff1a\u201c\u90a3\u6211\u4ece\u73b0\u5728\u8d77\u5c31\u5e2e\u5468\u5382\u957f\u5f53\u6210\u81ea\u5df1\u4eba\u4f7f\u5524\u4e86\u3002\u201d</p><p>\u3000\u3000\u5468\u590d\u6709\u4e9b\u50bb\uff0c\u81ea\u5df1\u7684\u547d\u8fd0\u5c31\u7ed9\u773c\u524d\u8fd9\u5c11\u5e74\u4e0e\u5b8b\u57f9\u660e\u4e09\u8a00\u4e24\u8bed\u95f4\u7ed9\u51b3\u5b9a\u4e86\uff0c\u53c8\u4e0d\u80fd\u5f53\u4f17\u8d28\u7591\u5b8b\u57f9\u660e\uff0c\u53ea\u6709\u95f7\u58f0\u4e0d\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff1a\u201c\u79bb\u9152\u5e2d\u5f00\u59cb\u7684\u65f6\u95f4\u4e0d\u957f\u4e86\uff0c\u5f20\u606a\u4e5f\u8fc7\u6765\u4e86\uff0c\u662f\u4e0d\u662f\u6211\u4eec\u73b0\u5728\u5c31\u8fc7\u53bb\uff0c\u4e0d\u80fd\u8ba9\u738b\u4e66\u8bb0\u7b49\u6025\u4e86\uff1f\u201d</p><p>\u3000\u3000\u665a\u4e0a\u7684\u9152\u5e2d\u7531\u57ce\u5357\u533a\u653f\u5e9c\u5bb4\u8bf7\uff0c\u81ea\u7136\u662f\u770b\u5728\u8c22\u665a\u6674\u7684\u9762\u5b50\u4e0a\uff0c\u4e0d\u7136\u54ea\u6709\u653f\u5e9c\u5bb4\u8bf7\u4f01\u4e1a\u7684\u9053\u7406\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u51fa\u9762\u4e0d\u5408\u9002\uff0c\u738b\u5c90\u6c5f\u4e66\u8bb0\u4ed6\u4eec\u597d\u51e0\u4e2a\u4eba\u90fd\u8ba4\u8bc6\u6211\uff0c\u4e0d\u597d\u4ecb\u7ecd\uff0c\u5c31\u7531\u848b\u59d0\u4ee3\u8868\u516c\u53f8\u966a\u665a\u6674\u59d0\u3001\u8521\u59d0\u3001\u5468\u603b\u53bb\u5427\u3002\u201d\u89c1\u665a\u6674\u70b9\u5934\u7b54\u5e94\uff0c\u5f20\u606a\u5bf9\u5468\u590d\u8bf4\uff1a\u201c\u5468\u5382\u957f\u4e0e\u848b\u59d0\u5148\u7559\u4e00\u4e0b\uff0c\u4f60\u4eec\u7b49\u4f1a\u513f\u518d\u53bb\u9152\u5e97\u3002\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u90fd\u628a\u81ea\u5df1\u4e22\u7ed9\u9526\u6e56\u516c\u53f8\u4e86\uff0c\u5468\u590d\u53ea\u6709\u95f7\u58f0\u7b54\u5e94\u3002</p><p>\u3000\u3000\u5468\u590d\u5728\u53c2\u89c2\u9526\u6e56\u516c\u53f8\u7684\u65f6\u5019\uff0c\u8fdb\u5165\u5f20\u606a\u7684\u529e\u516c\u5ba4\uff0c\u4f46\u662f\u4e4b\u524d\u8c01\u4e5f\u6ca1\u6709\u4ecb\u7ecd\u8bf4\u8fd9\u662f\u5f20\u606a\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u7531\u78e8\u7802\u73bb\u7483\u4e0e\u5916\u9762\u7684\u529e\u516c\u57df\u76f8\u9694\uff0c\u8f6c\u89d2\u5f27\u5f62\u98d8\u7a97\u662f\u4e00\u5ea7\u5c0f\u578b\u7684\u7acb\u4f53\u7a7a\u4e2d\u82b1\u56ed\uff0c\u6700\u4e0b\u5c42\u662f\u94a2\u5316\u73bb\u7483\u7684\u6c34\u6c60\uff0c\u51e0\u5c3e\u53eb\u4e0d\u51fa\u540d\u5b57\u7684\u89c2\u8d4f\u9c7c\u5728\u5f02\u5e38\u4e30\u8302\u7684\u6c34\u8349\u95f4\u7a7f\u68ad\uff0c\u6c34\u6c60\u4e0a\u7528\u4e0d\u9508\u94a2\u6761\u67b6\u8d77\u4e00\u4e2a\u5e73\u53f0\uff0c\u653e\u7f6e\u7740\u5468\u590d\u540c\u6837\u4e0d\u6653\u5f97\u540d\u5b57\u7684\u89c2\u8d4f\u82b1\u8349\u3002\u8fd9\u95f4\u529e\u516c\u5ba4\u5dee\u4e0d\u591a\u6709\u56db\u5341\u4e2a\u5e73\u65b9\uff0c\u5357\u4fa7\u4e0e\u897f\u4fa7\u90fd\u901a\u900f\u7684\u73bb\u7483\u5e55\u5899\uff0c\u6a31\u6843\u7ea2\u7684\u5b9e\u6728\u529e\u516c\u684c\u6905\u9760\u8fd1\u98d8\u7a97\uff0c\u897f\u4fa7\u73bb\u7483\u5e55\u5899\u4e0b\u653e\u7f6e\u7740\u4e00\u7ec4\u6c99\u53d1\u4e0e\u7ebf\u6761\u7c97\u72b7\u7684\u73bb\u7483\u51e0\uff0c\u65c1\u8fb9\u8fd8\u6709\u4e00\u4e2a\u5c0f\u578b\u5427\u53f0\u3002</p><p>\u3000\u3000\u5468\u590d\u4e00\u76f4\u5728\u60f3\u4f7f\u7528\u8fd9\u95f4\u529e\u516c\u5ba4\u7684\u4eba\u4f1a\u662f\u8c01\uff0c\u60f3\u4e0d\u5230\u53ea\u662f\u4e00\u4e2a\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf7\u4e09\u4eba\u5230\u897f\u4fa7\u7684\u6c99\u53d1\u4e0a\u5750\u4e0b\u6765\uff0c\u62ff\u8fc7\u6765\u4e00\u4efd\u6587\u4ef6\uff0c\u9012\u7ed9\u5468\u590d\u9762\u524d\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6211\u8349\u62df\u7684\u8058\u7528\u5408\u540c\uff0c\u5468\u5382\u957f\u770b\u770b\uff0c\u6709\u4ec0\u4e48\u4e0d\u6ee1\u610f\u7684\u5730\u65b9\uff0c\u5e0c\u671b\u5c3d\u5feb\u63d0\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u590d\u8fdf\u7591\u7684\u63a5\u8fc7\u6765\uff0c\u968f\u624b\u7ffb\u770b\u7740\uff0c\u770b\u5230\u85aa\u8d44\u90a3\u4e00\u6863\uff0c\u4efb\u4ed6\u5fc3\u5e73\u5982\u6c34\uff0c\u4e5f\u5fcd\u4e0d\u4f4f\u5fc3\u810f\u731b\u8df3\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u848b\u8587\u4f38\u8fc7\u5934\u6765\u770b\u4e86\u770b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5466\uff0c\u6bd4\u6211\u7684\u5de5\u8d44\u8fd8\u9ad8\u4e00\u5343\u591a\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u590d\u521a\u521a\u8fd8\u4ee5\u4e3a\u5408\u540c\u4e0a\u7684\u6570\u5b57\u591a\u5199\u4e86\u4e00\u4e2a\u96f6\uff0c\u542c\u848b\u8587\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u66f4\u52a0\u4e0d\u5e73\u9759\u7684\u8df3\u52a8\u8d77\u6765\u3002</p><p>\u3000\u3000\u5728\u4e5d\u56db\u5e74\uff0c\u4e2d\u5c0f\u89c4\u6a21\u7684\u56fd\u6709\u4f01\u4e1a\uff0c\u957f\u671f\u6548\u76ca\u4f4e\u4e0b\uff0c\u5176\u7ba1\u7406\u4eba\u5458\u6bcf\u5e74\u7684\u6536\u5165\u8fbe\u5230\u5341\u4e07\uff0c\u53ea\u8981\u4f1a\u94bb\u8425\uff0c\u4e5f\u4e0d\u662f\u4e00\u4ef6\u592a\u56f0\u96be\u7684\u4e8b\u60c5\uff0c\u4f46\u662f\u5408\u6cd5\u7684\u6536\u5165\u8981\u8fbe\u5230\u5341\u4e07\uff0c\u90a3\u662f\u7edd\u65e0\u4ec5\u6709\u7684\u3002</p><p>\u3000\u3000\u59dc\u660e\u8bda\u6709\u4e01\u5411\u5c71\u5728\u80cc\u540e\u6491\u8170\uff0c\u4ed6\u5728\u65b0\u4e30\u96c6\u56e2\u4e00\u624b\u906e\u5929\uff0c\u7ed9\u81ea\u5df1\u6bcf\u5e74\u53d1\u7684\u5956\u91d1\u4e5f\u4e0d\u6562\u8d85\u8fc7\u5341\u4e07\uff0c\u800c\u8fd9\u4e00\u6761\u6781\u53ef\u80fd\u6210\u4e3a\u8d77\u8bc9\u4ed6\u7684\u7f6a\u540d\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u6837\uff0c\u8fd9\u4efd\u5408\u540c\u5468\u5382\u957f\u8fd8\u80fd\u6ee1\u610f\uff1f\u201d\u5f20\u606a\u772f\u8d77\u773c\u775b\u770b\u7740\u5468\u590d\u8138\u4e0a\u7684\u9762\u5bb9\u3002</p><p>\u3000\u3000\u5468\u590d\u7a81\u7136\u89c9\u5f97\u5f20\u606a\u7684\u5e74\u9f84\u5bf9\u81ea\u5df1\u6765\u8bf4\u4e0d\u518d\u5b58\u5728\u4ec0\u4e48\u969c\u788d\uff0c\u65e2\u7136\u5b8b\u57f9\u660e\u90fd\u8ba4\u53ef\u5f20\u606a\u7684\u5730\u4f4d\uff0c\u81ea\u5df1\u53c8\u6709\u4ec0\u4e48\u4e86\u4e0d\u8d77\u7684\u3002</p><p>\u3000\u3000\u848b\u8587\u5728\u65c1\u8fb9\u63a8\u4e86\u4ed6\u4e00\u628a\uff1a\u201c\u8981\u6ca1\u4ec0\u4e48\u610f\u89c1\uff0c\u8fd9\u5728\u8fd9\u91cc\u7b7e\u5b57\uff0c\u8d76\u7740\u5341\u4e00\u6708\u4efd\uff0c\u8fd8\u80fd\u62ff\u534a\u4e2a\u6708\u7684\u5de5\u8d44\u3002\u201d</p><p>\u3000\u3000\u5468\u590d\u5fd9\u4ece\u516c\u6587\u5305\u91cc\u62ff\u51fa\u7b14\uff0c\u5728\u7b7e\u5b57\u4e4b\u524d\uff0c\u8fd8\u662f\u5c06\u5408\u540c\u4ece\u5230\u5c3e\u7ec6\u8bfb\u4e86\u4e00\u904d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6025\u4e0d\u8e81\uff0c\u7b49\u5468\u590d\u7b7e\u5b8c\u5b57\uff0c\u5c06\u501f\u7528\u5408\u540c\u4ea4\u7ed9\u8bb8\u601d\uff0c\u8fd8\u8981\u7b49\u5468\u590d\u5c06\u5de5\u4f5c\u5173\u7cfb\u4ece\u9020\u7eb8\u5382\u8131\u79bb\u51fa\u6765\uff0c\u5728\u533a\u91cc\u529e\u7406\u505c\u804c\u7559\u85aa\u7eed\uff0c\u624d\u80fd\u5b8c\u6210\u6574\u4e2a\u501f\u7528\u5408\u540c\u3002\u53ea\u8981\u5468\u590d\u7b7e\u5b57\uff0c\u5176\u4ed6\u5012\u4e0d\u7d27\u8981\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u5468\u590d\u8bf4\uff1a\u201c\u4f60\u4e0e\u848b\u59d0\u5c31\u4ee3\u8868\u9526\u6e56\u516c\u53f8\u8d1f\u8d23\u65b0\u5149\u9020\u7eb8\u5382\u7684\u751f\u4ea7\u4e0e\u8d22\u52a1\u7ba1\u7406\uff0c\u91c7\u9500\u4e24\u5757\u4f1a\u5265\u79bb\u51fa\u6765\u3002\u4f60\u4e5f\u6e05\u695a\uff0c\u6211\u4eec\u5bf9\u65b0\u5149\u9020\u7eb8\u5382\u7684\u7ba1\u7406\u4eba\u5458\u6709\u7edd\u5bf9\u7684\u4efb\u514d\u6743\u9650\uff0c\u540e\u5929\u5728\u8fd9\u91cc\u53ec\u96c6\u9020\u7eb8\u5382\u6240\u6709\u7684\u7ba1\u7406\u4eba\u5458\u5f00\u4f1a\uff0c\u5e0c\u671b\u5728\u6b64\u4e4b\u524d\uff0c\u4f60\u7ed9\u6211\u63d0\u4f9b\u4e00\u4efd\u6709\u5173\u6240\u6709\u7ba1\u7406\u4eba\u5458\u7684\u8bc4\u4ef7\u62a5\u544a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u90a3\u91cc\u53c8\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u665a\u4e0a\u7684\u9152\u5bb4\uff0c\u5468\u5382\u957f\u662f\u4e0d\u80fd\u591a\u559d\u9152\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5468\u590d\u7b2c\u4e8c\u5929\u665a\u4e0a\u4e4b\u524d\u5c31\u5c06\u9020\u7eb8\u5382\u5f53\u524d\u7ba1\u7406\u4eba\u5458\u7684\u8bc4\u4ef7\u62a5\u544a\u63d0\u4ea4\u4e0a\u6765\u3002\u5f53\u7136\uff0c\u60f3\u8981\u5468\u590d\u8fd9\u65f6\u5019\u5c31\u63d0\u4ea4\u4e00\u4efd\u5ba2\u89c2\u7684\u62a5\u544a\u90a3\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u6bd5\u7adf\u90fd\u662f\u4ed6\u671d\u5915\u76f8\u5904\u7684\u540c\u4e8b\uff0c\u8fd9\u4efd\u62a5\u544a\u6781\u53ef\u80fd\u65ad\u9001\u522b\u4eba\u7684\u804c\u5de5\u751f\u6daf\uff0c\u81ea\u7136\u53ea\u8bc4\u8ff0\u4f18\u70b9\u4e0e\u80fd\u529b\uff0c\u7f3a\u70b9\u4e0e\u6bdb\u75c5\u53ea\u6311\u4e00\u4e9b\u4e0d\u7d27\u8981\u7684\u634e\u5e26\u800c\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u9020\u7eb8\u5382\u7684\u719f\u6089\u7a0b\u5ea6\u662f\u5468\u590d\u65e0\u6cd5\u60f3\u8c61\u7684\uff0c\u56e0\u4e3a\u6881\u683c\u73cd\u8c03\u5165\u9020\u7eb8\u5382\u7684\u4e8b\u60c5\u5e76\u6ca1\u6709\u53d1\u751f\uff0c\u4e5f\u4e0d\u4f1a\u518d\u53d1\u751f\u3002</p><p>\u3000\u3000\u661f\u671f\u5929\uff0c\u9020\u7eb8\u5382\u6240\u6709\u7684\u5de5\u7a0b\u5e08\u3001\u79d1\u957f\u4ee5\u4e0a\u7684\u4e8c\u5341\u591a\u540d\u7ba1\u7406\u4eba\u5458\u90fd\u88ab\u53ec\u96c6\u5230\u65b0\u6d77\u901a\u5927\u53a6\uff0c\u4ed6\u4eec\u8fdb\u6765\u4e4b\u524d\uff0c\u8fd8\u5927\u58f0\u8bf4\u7b11\uff0c\u8d70\u5230\u5341\u516b\u697c\u7684\u529e\u516c\u5ba4\uff0c\u7531\u884c\u653f\u52a9\u7406\u5f15\u9886\u7740\u8d70\u8fdb\u4f1a\u8bae\u5ba4\uff0c\u90fd\u7981\u4e0d\u4f4f\u7ec6\u58f0\u7ec6\u6c14\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5c31\u8d22\u52a1\u79d1\u957f\u4e0e\u526f\u5382\u957f\uff0c\u8fd8\u6709\u53f8\u673a\u5468\u6587\u6b66\u966a\u5468\u590d\u5230\u8fc7\u9526\u6e56\u516c\u53f8\uff0c\u5176\u4ed6\u4eba\u5bf9\u4ed6\u4eec\u63cf\u8ff0\u9526\u6e56\u516c\u53f8\u591a\u4e48\u5177\u5907\u5927\u516c\u53f8\u7684\u6c14\u6d3e\uff0c\u90fd\u5c06\u7591\u5c06\u4fe1\uff0c\u4eb2\u773c\u76ee\u7779\u4e4b\u540e\uff0c\u5bb3\u6015\u547c\u5438\u91cd\u4e86\u5c31\u4f1a\u5f15\u8d77\u9526\u6e56\u516c\u53f8\u8fd9\u4f4d\u5e74\u8f7b\u8c8c\u7f8e\u7684\u884c\u653f\u52a9\u91cf\u7684\u9119\u89c6\uff0c\u66f4\u4e0d\u8981\u63d0\u5927\u58f0\u8bf4\u7b11\u4e86\u3002</p><p>\u3000\u3000\u5750\u4f1a\u8bae\u5ba4\u91cc\uff0c\u5e74\u8f7b\u8c8c\u7f8e\u7684\u884c\u653f\u52a9\u7406\u9000\u51fa\u53bb\u4e4b\u540e\uff0c\u5927\u5bb6\u624d\u7a0d\u5fae\u8f7b\u677e\u4e00\u70b9\uff0c\u4f1a\u8bae\u5ba4\u4e0e\u5916\u9762\u529e\u516c\u533a\u57df\u91c7\u7528\u900f\u660e\u7684\u94a2\u5316\u73bb\u7483\u9694\u65ad\uff0c\u5750\u5728\u4f1a\u8bae\u5ba4\u91cc\uff0c\u53ef\u4ee5\u6253\u91cf\u6574\u95f4\u516c\u53f8\u3002</p><p>\u3000\u3000\u8d22\u52a1\u79d1\u957f\u7b11\u7740\u8bf4\uff1a\u201c\u80fd\u5750\u5230\u8fd9\u91cc\u529e\u516c\uff0c\u54ea\u6015\u4e0d\u62ff\u5de5\u8d44\uff0c\u4e5f\u5fc3\u7518\u60c5\u613f\u3002\u8001\u5468\uff0c\u4f60\u7684\u5de5\u4f5c\u5173\u7cfb\u8c03\u56de\u533a\u91cc\uff0c\u4eba\u7ed9\u9526\u6e56\u516c\u53f8\u501f\u7528\uff0c\u7ed9\u4f60\u5f00\u4e24\u5343\u5143\u7684\u5de5\u8d44\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e24\u5343\u5143\uff1f\uff01\u201d\u5f20\u606a\u7ad9\u5728\u4f1a\u8bae\u5ba4\u95e8\u53e3\uff0c\u201c\u4e09\u4e2a\u6708\u4e4b\u540e\uff0c\u5728\u5ea7\u6709\u54ea\u4e00\u4e2a\u4eba\u7684\u6708\u85aa\u4e0d\u80fd\u8fbe\u5230\u4e24\u5343\u5143\uff0c\u90a3\u8868\u660e\u6211\u5bf9\u4ed6\u7684\u8868\u73b0\u6781\u4e0d\u6ee1\u610f\uff0c\u5c4a\u65f6\u8fd8\u8981\u8bf7\u4ed6\u4e3b\u52a8\u63d0\u4ea4\u79bb\u804c\u7533\u8bf7\uff0c\u514d\u5f97\u53cc\u65b9\u9762\u5b50\u592a\u96be\u770b\u3002\u201d</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u606a\u4e0e\u65b0\u5149\u9020\u7eb8\u5382\u7ba1\u7406\u4eba\u5458\u89c1\u9762\u7684\u7b2c\u4e00\u53e5\u8bdd\uff0c\u4ed6\u7ad9\u5728\u4f1a\u8bae\u5ba4\u7684\u95e8\u53e3\uff0c\u848b\u8587\u4e0e\u8bb8\u601d\u62ff\u7740\u6587\u4ef6\u5939\u7ad9\u5728\u4ed6\u7684\u8eab\u540e\uff0c\u5c31\u7b97\u5468\u590d\u8fd9\u65f6\u5019\u8bf4\u51fa\u4ed6\u662f\u624d\u8bfb\u9ad8\u4e2d\u7684\u5c11\u5e74\u4eba\uff0c\u4e5f\u6ca1\u6709\u8c01\u4f1a\u76f8\u4fe1\u3002</p><p>\u3000\u3000\u201c\u9020\u7eb8\u5382\u7684\u65e5\u5e38\u7ba1\u7406\u7531\u5468\u590d\u4e0e\u848b\u8587\u8d1f\u8d23\uff0c\u5728\u5ea7\u7684\u5404\u4f4d\u4e0e\u6211\u89c1\u9762\u7684\u673a\u4f1a\u4e0d\u4f1a\u592a\u591a\uff0c\u66f4\u591a\u7684\u53ef\u80fd\u662f\u5728\u4f60\u4eec\u804c\u52a1\u3001\u85aa\u8d44\u5f97\u5230\u5927\u5e45\u5ea6\u63d0\u5347\uff0c\u6216\u8005\u6211\u5e0c\u671b\u4f60\u4eec\u5f53\u4e2d\u7684\u54ea\u4e00\u4f4d\u4ece\u9020\u7eb8\u5382\u6eda\u86cb\u7684\u65f6\u5019\uff0c\u4f60\u4eec\u624d\u4f1a\u89c1\u5230\u6211\u3002\u201d\u5f20\u606a\u5728\u4f1a\u8bae\u684c\u524d\u5750\u4e0b\uff0c\u5f88\u7ec5\u58eb\u7ed9\u848b\u8587\u62c9\u5f00\u65c1\u8fb9\u7684\u6905\u5b50\uff0c\u4e0b\u9762\u8fd8\u5269\u4f59\u7684\u4e00\u70b9\u7b11\u5bb9\u5728\u8fd9\u4e00\u523b\u5c31\u6d88\u5931\u6020\u5c3d\u4e86\uff0c\u8fde\u4e00\u76f4\u4ee5\u6765\u6253\u5fc3\u91cc\u90fd\u4e0d\u662f\u5f88\u91cd\u89c6\u5f20\u606a\u7684\u5468\u590d\u4e5f\u7981\u4e0d\u4f4f\u6452\u4f4f\u547c\u5438\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u5f00\u59cb\u5c31\u6ca1\u6709\u6253\u7b97\u7ed9\u4ed6\u4eec\u53d1\u8a00\u7684\u673a\u4f1a\uff0c\u6253\u5f00\u848b\u8587\u9012\u5230\u4ed6\u9762\u524d\u7684\u6587\u4ef6\u5939\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u4e8e\u5404\u4f4d\u4e4b\u524d\u5728\u65b0\u5149\u9020\u7eb8\u5382\u7684\u5de5\u4f5c\uff0c\u6211\u624b\u5934\u6709\u4e24\u4efd\u62a5\u544a\uff0c\u4e00\u4efd\u662f\u5468\u5382\u957f\u6628\u5929\u665a\u4e0a\u63d0\u4ea4\u7ed9\u6211\u7684\uff0c\u4ece\u8fd9\u4efd\u62a5\u544a\u91cc\uff0c\u6211\u770b\u5230\u5404\u4f4d\u7684\u5de5\u4f5c\u80fd\u529b\u4e0e\u4f18\u826f\u7684\u54c1\u8d28\uff0c\u8fd8\u6709\u4e00\u4efd\u62a5\u544a\u6709\u4e00\u4e9b\u4e0d\u597d\u7684\u8bc4\u4ef7\uff0c\u4f46\u8fd9\u4e9b\u90fd\u4e0d\u4f1a\u4f5c\u4e3a\u6211\u5bf9\u4f60\u4eec\u8bc4\u4ef7\u7684\u4f9d\u636e\uff0c\u9664\u4e86\u4e1a\u52a1\u79d1\u89e3\u6563\u3001\u4e1a\u52a1\u79d1\u957f\u4f5c\u4e3a\u5382\u957f\u5ba4\u52a9\u7406\u7559\u7528\u4e4b\u5916\uff0c\u5176\u4ed6\u79d1\u5ba4\u6682\u65f6\u4e0d\u4f5c\u8c03\u6574\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e1a\u52a1\u79d1\u89e3\u6563\u4e4b\u540e\uff0c\u9020\u7eb8\u5382\u7684\u9500\u552e\u7531\u8c01\u5b8c\u5168\uff0c\u4e1a\u52a1\u79d1\u7684\u5458\u5de5\u5982\u4f55\u5b89\u7f6e\uff1f\u201d\u4e1a\u52a1\u79d1\u957f\u6ca1\u60f3\u5230\u5f00\u4f1a\u7b2c\u4e00\u4e2a\u6d88\u606f\u5bf9\u4ed6\u6765\u8bf4\u5c31\u662f\u5669\u8017\u3002</p></article>"}}}]);
