(window.webpackJsonp=window.webpackJsonp||[]).push([[86],{109:function(p){p.exports={chapterTitle:null,title:"\u7b2c1072\u7ae0 \u8c01\u5149\u7740\u5c41\u80a1",content:"<article><h2>\u7b2c1072\u7ae0 \u8c01\u5149\u7740\u5c41\u80a1</h2><p>\u3000\u3000\u5f20\u606a\u5bf9\u5efa\u8d85\u9ad8\u697c\u7684\u5174\u8da3\u4e0d\u5927\uff0c\u518d\u8bf4\u4e8c\u767e\u7c73\u9ad8\u7684\u8d85\u9ad8\u5c42\u5efa\u7b51\u8bbe\u8ba1\u65b9\u6848\u5ba1\u67e5\u65f6\u4f1a\u8fdb\u884c\u8d85\u9650\u5ba1\u67e5\uff0c\u5ba1\u67e5\u5468\u671f\u957f\uff0c\u8bbe\u8ba1\u51fa\u56fe\u7684\u5468\u671f\u4e5f\u957f\uff0c\u80fd\u5728\u4e24\u5e74\u65f6\u95f4\u5185\u7834\u571f\u52a8\u5de5\u5c31\u7b97\u662f\u6bd4\u8f83\u987a\u5229\u7684\uff1b\u5c31\u7b97\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u4eba\u518d\u6025\u5207\uff0c\u65b9\u6848\u5ba1\u67e5\u4e0e\u8bbe\u8ba1\u51fa\u56fe\u7684\u5468\u671f\u4e5f\u4e0d\u53ef\u80fd\u4f4e\u4e8e\u4e00\u5e74\uff0c\u518d\u52a0\u4e00\u4e24\u5e74\u7684\u5efa\u8bbe\u65bd\u5de5\u5468\u671f\uff0c\u771f\u6b63\u8981\u5efa\u6210\u6295\u5165\u4f7f\u7528\uff0c\u5dee\u4e0d\u591a\u8981\u5728\u4e09\u5e74\u4e4b\u540e\u4e86\uff0c\u5bf9\u7f13\u89e3\u9526\u6e56\u6b64\u65f6\u5728\u5efa\u90ba\u7684\u529e\u516c\u7528\u5730\u7d27\u5f20\u60c5\u51b5\u6ca1\u6709\u592a\u5927\u7684\u4f5c\u7528\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u5efa\u8bbe\u5468\u671f\u957f\uff0c\u5341\u4e8c\u4ebf\u7684\u6295\u8d44\u5206\u6563\u5230\u4e09\u5e74\u6216\u8005\u66f4\u957f\u7684\u5efa\u8bbe\u5468\u671f\u4e4b\u540e\uff0c\u76f8\u6bd4\u8f83\u9526\u6e56\u6b64\u65f6\u7684\u8d44\u91d1\u6d41\u91cf\uff0c\u8c08\u4e0d\u4e0a\u6709\u591a\u5c11\u8d44\u91d1\u538b\u529b\uff0c\u7279\u522b\u662f\u524d\u671f\u8bbe\u8ba1\u5ba1\u67e5\u4e0e\u51fa\u56fe\u7684\u4e00\u5e74\u591a\u751a\u81f3\u66f4\u957f\u65f6\u95f4\u91cc\u6240\u5360\u7528\u7684\u8d44\u91d1\u90fd\u76f8\u5f53\u6709\u9650\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u9526\u6e56\u957f\u8fdc\u7684\u4ea7\u4e1a\u5e03\u5c40\uff0c\u9700\u8981\u5c06\u603b\u90e8\u4e0e\u51b3\u7b56\u7684\u6838\u5fc3\u673a\u6784\u8bbe\u5728\u5efa\u90ba\uff0c\u6709\u8fd9\u4e48\u4e00\u680b\u5145\u95e8\u9762\u7684\u603b\u90e8\u5927\u53a6\uff0c\u4e5f\u7b26\u5408\u9526\u6e56\u9ad8\u5c42\u7684\u610f\u613f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6e05\u695a\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7b49\u5b98\u5458\u771f\u6b63\u5173\u5fc3\u7684\u4e0d\u662f\u8fd9\u680b\u697c\u5efa\u591a\u9ad8\uff0c\u800c\u662f\u5173\u5fc3\u8fd9\u680b\u697c\u80fd\u6709\u591a\u5927\u7684\u5f71\u54cd\u529b\uff0c\u5bf9\u6e56\u4e1c\u533a\u57df\u5feb\u901f\u5efa\u8bbe\u6210\u5efa\u90ba\u65b0\u7684\u5546\u8d38\u91d1\u878d\u4e2d\u5fc3\u6709\u591a\u5927\u7684\u63a8\u52a8\u4f5c\u7528\u3002</p><p>\u3000\u3000\u53ea\u662f\u56fd\u5185\u4eba\u5bf9\u5efa\u7b51\u7684\u5ba1\u7f8e\u89c2\u6bd4\u8f83\u5c40\u9650\uff0c\u4f3c\u4e4e\u90fd\u8ba4\u4e3a\u6700\u9ad8\u7684\u5efa\u7b51\u624d\u662f\u6700\u7f8e\u7684\u5efa\u7b51\uff0c\u624d\u80fd\u7b97\u5730\u6807\u5efa\u7b51\uff0c\u53ea\u6709\u6469\u80a9\u63a5\u8e35\u7684\u6469\u5929\u5927\u53a6\u697c\u7fa4\u624d\u4ee3\u8868\u73b0\u4ee3\u90fd\u5e02\u7684\u6c14\u5ea6\uff0c\u5c31\u662f\u5728\u8fd9\u4e2a\u8ba4\u8bc6\u80cc\u666f\u4e0b\uff0c\u5e02\u91cc\u4e0e\u9ad8\u65b0\u533a\u90fd\u5bc4\u5e0c\u671b\u9526\u6e56\u80fd\u6295\u8d44\u7ed9\u6e56\u4e1c\u533a\u57df\u5efa\u4e00\u5ea7\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u6469\u5929\u5927\u53a6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u8ba4\u540c\u8fd9\u79cd\u5efa\u7b51\u5ba1\u7f8e\u89c2\uff0c\u4f46\u662f\u4e5f\u4e0d\u4f1a\u8dd1\u8fc7\u53bb\u8bf4\u670d\u5e02\u91cc\u3001\u9ad8\u65b0\u533a\u53bb\u6539\u53d8\u4ed6\u4eec\u7684\u89c2\u5ff5\u3002\u53cd\u6b63\u7f57\u541b\u4e0e\u738b\u7ef4\u5747\u7b49\u4eba\u771f\u6b63\u5173\u5fc3\u7684\u662f\u5f71\u54cd\u529b\uff0c\u4e00\u680b\u697c\uff0c\u9526\u6e56\u638f\u51fa\u516d\u5343\u4e07\u7684\u8bbe\u8ba1\u8d39\u5f15\u8bf1\u56fd\u5185\u5916\u7684\u9876\u7ea7\u8bbe\u8ba1\u5e08\u4e8b\u52a1\u6240\u53bb\u7ade\u6807\uff0c\u8db3\u4ee5\u5f62\u6210\u6bd4\u5b9e\u9645\u5efa\u4e00\u680b\u4e8c\u767e\u4e94\u516d\u5341\u7c73\u7684\u6469\u5929\u5927\u53a6\u66f4\u5927\u7684\u5f71\u54cd\u529b\uff0c\u8fd9\u6837\u4e5f\u80fd\u5728\u8bbe\u8ba1\u65b9\u6848\u6709\u66f4\u591a\u7684\u9009\u62e9\u4f59\u5730\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f20\u53e3\u8bf4\u51fa\u516d\u5343\u4e07\u7684\u8bbe\u8ba1\u8d39\uff0c\u5176\u4ed6\u4eba\u90fd\u975e\u5e38\u60ca\u8bb6\uff0c\u738b\u7ef4\u5747\u5374\u662f\u7709\u5f00\u773c\u7b11\uff0c\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u4ea4\u7ed9\u4ed6\u7684\u4efb\u52a1\u7b97\u662f\u8d85\u989d\u5b8c\u6210\uff0c\u53c8\u7b11\u9a82\u5f20\u606a\u4e4b\u524d\u5728\u90a3\u91cc\u54ed\u7a77\uff0c\u4ed6\u4e0e\u59da\u6587\u76db\u665a\u4e0a\u8fd8\u6709\u4e8b\u60c5\uff0c\u665a\u4e0a\u4e0d\u80fd\u4e00\u8d77\u5403\u996d\uff0c\u5c31\u5148\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u4e0e\u59da\u6587\u76db\u79bb\u5f00\uff0c\u5f20\u606a\u5c31\u9001\u4ed6\u4eec\u5230\u7535\u68af\u53e3\u3002</p><p>\u3000\u3000\u770b\u7740\u7535\u68af\u95e8\u9876\u4e0a\u663e\u793a\u7535\u68af\u5728\u4e0b\u884c\uff0c\u5f20\u606a\u65e0\u5948\u7684\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u8ddf\u90b5\u81f3\u521a\u8bf4\u9053\uff1a\u201c\u5efa\u9020\u4e8c\u767e\u4e94\u516d\u5341\u7c73\u7684\u9ad8\u697c\uff0c\u5728\u5efa\u90ba\u53ef\u80fd\u662f\u7a7a\u524d\u7684\uff0c\u4f46\u662f\u5728\u5168\u56fd\u6216\u8005\u5728\u4e9a\u6d32\u8303\u56f4\uff0c\u6839\u672c\u5c31\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u8c08\u4e0d\u4e0a\u6709\u4ec0\u4e48\u5f71\u54cd\u529b\uff0c\u4f46\u662f\u9664\u4e86\u90a3\u4e00\u680b\u680b\u52c7\u4e89\u5168\u7403\u6216\u533a\u57df\u7b2c\u4e00\u9ad8\u5ea6\u7684\u8d85\u7ea7\u6469\u5929\u5927\u697c\u4e4b\u5916\uff0c\u5efa\u7b51\u53f2\u4e0a\u4e0d\u662f\u5c31\u627e\u4e0d\u5230\u5176\u4ed6\u7684\u7ecf\u5178\u4e4b\u4f5c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5408\u8f84\u5c31\u4f60\u662f\u4e00\u4e2a\u9ad8\u5c1a\u7684\u4eba\uff0c\u4e00\u4e2a\u7eaf\u7cb9\u7684\u4eba\uff0c\u4e00\u4e2a\u6709\u9053\u5fb7\u7684\u4eba\uff0c\u4e00\u4e2a\u8131\u79bb\u4e86\u4f4e\u7ea7\u8da3\u5473\u7684\u4eba\uff1f\u201d\u53f6\u5c0f\u6850\u80cc\u8bf5\u300a\u7eaa\u5ff5\u767d\u6c42\u6069\u300b\u6765\u6253\u8da3\u5f20\u606a\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u5149\u60f3\u7740\u5efa\u591a\u9ad8\u7684\u9ad8\u697c\uff0c\u4e5f\u6ca1\u6709\u610f\u601d\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u90b5\u5fc3\u6587\u8be5\u8fc7\u6765\u63a5\u4f60\u4e86\uff0c\u4f60\u5feb\u51c6\u5907\u4e0b\u73ed\u53bb\u5427\uff0c\u4f60\u662f\u5b55\u5987\uff0c\u6211\u6597\u5634\u4e5f\u4e0d\u8ddf\u5b55\u5987\u6597\u2026\u2026\u201d\u5f20\u606a\u54a7\u5634\u7b11\u7740\u8ba9\u53f6\u5c0f\u6850\u633a\u7740\u5927\u809a\u5b50\u56de\u529e\u516c\u5ba4\u53bb\u3002\u770b\u5230\u5728\u8fd9\u91cc\u5b9e\u4e60\u7684\u4f55\u5f26\u4ece\u529e\u516c\u5ba4\u91cc\u8d70\u51fa\u6765\uff0c\u770b\u5979\u7684\u6a21\u6837\u662f\u51c6\u5907\u4e0b\u73ed\u4e86\uff0c\u5f20\u606a\u62db\u547c\u5979\u665a\u4e0a\u4e00\u8d77\u5403\u996d\u3002</p><p>\u3000\u3000\u4f55\u5f26\u77e5\u9053\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\u8981\u56de\u7f8e\u56fd\u53bb\uff0c\u5927\u5bb6\u8981\u4e00\u8d77\u5403\u996d\uff0c\u5979\u53c8\u53bb\u5c06\u9648\u5983\u84c9\u62c9\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u8981\u6293\u7d27\u65f6\u95f4\u8ddf\u90b5\u81f3\u521a\u591a\u8bf4\u4e24\u53e5\u8bdd\uff0c\u4e0d\u80fd\u8ba9\u4ed6\u5c06\u81ea\u5df1\u7684\u610f\u601d\u7406\u89e3\u5c94\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u4e0a\u6d77\u6d66\u4e1c\u91d1\u8302\u5927\u53a6\u53f7\u79f0\u662f\u4e2d\u534e\u7b2c\u4e00\u9ad8\u697c\uff0c\u6295\u8d44\u4e94\u5341\u4ebf\u521a\u521a\u5efa\u6210\uff0c\u6211\u4eec\u4e0d\u53ef\u80fd\u5728\u5efa\u90ba\u5efa\u6bd4\u91d1\u8302\u5927\u53a6\u66f4\u9ad8\u7684\u5efa\u7b51\uff0c\u4e89\u8fd9\u53e3\u6c14\u6ca1\u6709\u4e00\u70b9\u610f\u601d\u2014\u2014\u4e89\u4e86\u4e5f\u4f24\u7b4b\u75db\u9aa8\u2026\u2026\u6211\u5728\u8003\u8651\u8fd9\u680b\u697c\u8981\u5982\u4f55\u53bb\u5efa\u65f6\uff0c\u8003\u8651\u6211\u4eec\u7684\u8a00\u4e0e\u884c\u8981\u4e00\u81f4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6709\u4e9b\u8d39\u89e3\u554a\uff0c\u201d\u90b5\u81f3\u521a\u7b11\u7740\u95ee\u9053\uff0c\u201c\u5e02\u91cc\u3001\u9ad8\u65b0\u533a\u8981\u5e94\u4ed8\u4e00\u4e0b\uff0c\u6211\u80fd\u7406\u89e3\uff0c\u5b9e\u9645\u53bb\u8bbe\u8ba1\u65f6\uff0c\u6211\u4eec\u8fd8\u662f\u8981\u6709\u81ea\u6211\u4e3b\u5f20\u7684\uff0c\u8fd9\u4e2a\u591a\u5c11\u6709\u4e9b\u9633\u5949\u9634\u8fdd\u554a\uff0c\u4e0d\u77e5\u9053\u600e\u6837\u624d\u80fd\u505a\u5230\u8a00\u884c\u4e00\u81f4\uff0c\u8fd8\u662f\u8bf4\u6211\u7406\u4f1a\u9519\u4f60\u7684\u610f\u601d\u4e86\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u6ca1\u826f\u5fc3\u5728\u4e00\u65c1\u7b11\u4e86\u8d77\u6765\uff0c\u7b49\u7740\u5f20\u606a\u89e3\u91ca\uff1b\u4f55\u5f26\u3001\u9648\u5983\u84c9\u5728\u4e00\u8fb9\u62bf\u7740\u5634\u800c\u7b11\u3002</p><p>\u3000\u3000\u79e6\u521a\u4e0e\u65f6\u5b66\u658c\u4ed6\u4eec\u770b\u5230\u6574\u4e2a\u51b3\u7b56\u8fc7\u7a0b\u4e2d\u5f20\u606a\u6240\u5904\u7684\u4e3b\u5bfc\u5730\u4f4d\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4eec\u7b2c\u4e00\u6b21\u4eb2\u773c\u770b\u5230\u5f20\u606a\u5904\u7406\u9526\u6e56\u7684\u4e8b\u52a1\uff0c\u5bf9\u4ed6\u4eec\u5fc3\u91cc\u7684\u7591\u95ee\u591a\u5c11\u7b97\u662f\u4e2a\u89e3\u7b54\uff0c\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u624d\u66f4\u89c9\u5f97\u60ca\u8bb6\uff0c\u8fd9\u65f6\u5019\u66f4\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8981\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u662f\u8bf4\u8fd9\u4e2a\uff0c\u201d\u5f20\u606a\u8ddf\u90b5\u81f3\u521a\u89e3\u91ca\u9053\uff0c\u201c\u7f57\u541b\u3001\u738b\u7ef4\u5747\u4ec0\u4e48\u5fc3\u601d\uff0c\u6211\u80fd\u4e0d\u4e86\u89e3\uff1f\u4ed6\u4eec\u53ea\u8981\u8fbe\u5230\u4ed6\u4eec\u7684\u610f\u613f\uff0c\u624d\u4e0d\u7ba1\u6211\u4eec\u600e\u4e48\u6298\u817e\u3002\u6211\u662f\u8bf4\uff0c\u6211\u5728\u8003\u8651\u8fd9\u680b\u697c\u600e\u4e48\u5efa\u65f6\uff0c\u540c\u65f6\u8003\u8651\u5230\u7231\u8fbe\u7684\u4ea7\u54c1\u5f00\u53d1\u3001\u8bbe\u8ba1\u7406\u5ff5\uff0c\u53d1\u73b0\u4e24\u8005\u6709\u5171\u901a\u7684\u5730\u65b9\uff1a\u4e00\u5473\u7684\u5f3a\u8c03\u4ea7\u54c1\u6280\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u8fd9\u4e2a\u4f18\u52bf\u4e5f\u4f1a\u5f88\u5bb9\u6613\u7ed9\u540e\u6765\u8005\u8d85\u8d8a\uff0c\u552f\u6709\u7ecf\u5178\u7684\u8bbe\u8ba1\u53ea\u80fd\u591f\u88ab\u6a21\u4eff\u3001\u88ab\u501f\u9274\uff1b\u5bf9\u4e8e\u5efa\u7b51\u800c\u8a00\uff0c\u4e00\u5473\u7684\u5f3a\u8c03\u5efa\u7b51\u7684\u9ad8\u5ea6\uff0c\u4e5f\u592a\u65e0\u8da3\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u90b5\u81f3\u521a\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u4ed6\u7684\u786e\u6ca1\u6709\u7ad9\u5230\u8fd9\u79cd\u9ad8\u5ea6\u53bb\u8003\u8651\u8fc7\u8fd9\u4e9b\u95ee\u9898\uff0c\u5f20\u606a\u662f\u8981\u5c06\u4e00\u4e9b\u7406\u5ff5\u6027\u7684\u4e1c\u897f\u6e17\u5165\u5230\u9526\u6e56\u7684\u4f01\u4e1a\u6587\u5316\u9aa8\u5b50\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u6211\u5bf9\u662f\u4e0d\u662f\u7b2c\u4e00\u9ad8\u697c\uff0c\u6ca1\u6709\u4efb\u4f55\u7684\u60f3\u6cd5\u3002\u80fd\u5426\u6210\u4e3a\u6e56\u4e1c\u5730\u533a\uff0c\u4e43\u81f3\u5efa\u90ba\u3001\u4e1c\u6d77\u751a\u81f3\u534e\u4e1c\u5730\u533a\u6807\u5fd7\u6027\u7684\u5efa\u7b51\uff0c\u8fd8\u5728\u4e8e\u8bbe\u8ba1\u4e0a\u3002\u9080\u8bf7\u56fd\u5185\u5916\u8bbe\u8ba1\u5e08\u4e8b\u52a1\u9662\u7ade\u6807\u65f6\uff0c\u5728\u5efa\u7b51\u9762\u79ef\u3001\u697c\u9ad8\u751a\u81f3\u5360\u5730\u9762\u79ef\uff0c\u90fd\u4e0d\u8981\u7ed9\u592a\u4e25\u683c\u7684\u9650\u5236\uff0c\u5373\u4f7f\u9884\u7b97\u4e5f\u662f\u6709\u5546\u91cf\u7684\u4f59\u5730\u2014\u2014\u81f3\u5c11\u8981\u505a\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u7b49\u697c\u5efa\u6210\u4e4b\u540e\uff0c\u6211\u4eec\u8981\u80fd\u4ece\u8fd9\u680b\u697c\u76f4\u89c2\u7684\u5f97\u5230\u4e0e\u2018\u7b80\u7ea6\u3001\u65f6\u5c1a\u3001\u751f\u6d3b\u2019\u4ea7\u54c1\u5f00\u53d1\u7406\u5ff5\u76f8\u7c7b\u4f3c\u7684\u611f\u53d7\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u90b5\u81f3\u521a\u70b9\u4e86\u70b9\u5934\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u6562\u60c5\u4f60\u521a\u624d\u771f\u662f\u5728\u552c\u5f04\u738b\u7ef4\u5747\u554a\uff0c\u8bf4\u4ec0\u4e48\u65b9\u65b9\u6b63\u6b63\u3001\u600e\u4e48\u7701\u94b1\u600e\u4e48\u8bbe\u8ba1\u2014\u2014\u8981\u6309\u7167\u4f60\u7684\u60f3\u6cd5\uff0c\u5341\u4e8c\u4ebf\u7684\u9884\u7b97\uff0c\u6211\u770b\u771f\u7684\u5f88\u7384\uff0c\u8fd9\u660e\u660e\u662f\u600e\u4e48\u8d39\u94b1\u600e\u4e48\u6765\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f5c\u4e3a\u603b\u90e8\u5927\u53a6\uff0c\u5bf9\u9526\u6e56\u5185\u90e8\u7684\u5458\u5de5\u6765\u8bf4\uff0c\u603b\u662f\u6709\u5f88\u591a\u7cbe\u795e\u4e0a\u7684\u610f\u4e49\uff0c\u5962\u4f88\u4e00\u4e9b\uff0c\u603b\u662f\u6709\u4e9b\u610f\u4e49\u7684\uff0c\u201d\u5f20\u606a\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8bf4\u4e86\u8fd9\u4e48\u591a\uff0c\u4f60\u4e0d\u8981\u7ed9\u6211\u641e\u51fa\u6765i19\u9020\u578b\u7684\u8bbe\u8ba1\u65b9\u6848\uff0c\u90a3\u6837\u7684\u8bdd\uff0c\u6211\u521a\u624d\u7684\u90a3\u756a\u8bdd\u53ef\u5c31\u5bf9\u4f60\u767d\u8bf4\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5f00\u4e86\u53e3\uff0c\u6e56\u4e1c\u533a\u57df\u5c06\u7531\u7231\u8fbe\u96c6\u56e2\u6295\u8d44\u5efa\u4e1c\u6d77\u7b2c\u4e00\u9ad8\u697c\u7684\u6d88\u606f\u624d\u4ece\u5e02\u91cc\u4ee5\u5185\u5e55\u6216\u5c0f\u9053\u6d88\u606f\u7684\u5f62\u5f0f\u6d41\u4f20\u51fa\u6765\u3002</p><p>\u3000\u3000\u516b\u56db\u5e74\u6240\u5efa\u7684\u5efa\u90ba\u5927\u9152\u5e97\u662f\u56fd\u5185\u7b2c\u4e00\u5ea7\u771f\u6b63\u610f\u4e49\u4e0a\u7684\u6469\u5929\u5927\u53a6\uff0c\u4e09\u5341\u4e94\u5c42\uff0c\u4e00\u767e\u4e00\u5341\u516b\u7c73\u9ad8\uff0c\u8ba9\u5efa\u90ba\u4eba\u4e3a\u4e4b\u9a84\u50b2\u4e86\u5341\u591a\u5e74\u3002</p><p>\u3000\u3000\u968f\u4e4b\u4e0a\u6d77\u3001\u5e7f\u5dde\u3001\u6df1\u5733\u7b49\u4e00\u7ebf\u57ce\u5e02\u7684\u5d1b\u8d77\uff0c\u5728\u5927\u9646\u5d1b\u8d77\u7684\u6469\u5929\u5927\u53a6\u8d8a\u6765\u8d8a\u591a\uff0c\u800c\u5efa\u90ba\u7684\u7ecf\u6d4e\u5728\u516b\u5341\u5e74\u4ee3\u672b\u671f\u3001\u4e5d\u5341\u5e74\u4ee3\u521d\u671f\uff0c\u76f8\u6bd4\u5176\u4ed6\u4e2d\u5fc3\u57ce\u5e02\uff0c\u53d1\u5c55\u76f8\u5bf9\u6ede\u540e\uff0c\u5efa\u90ba\u5927\u9152\u5e97\u4ece\u201c\u5927\u9646\u7b2c\u4e00\u9ad8\u697c\u201d\u9000\u4f4d\u5230\u201c\u534e\u4e1c\u7b2c\u4e00\u9ad8\u697c\u201d\uff0c\u518d\u9000\u4f4d\u5230\u201c\u4e1c\u6d77\u7701\u7b2c\u4e00\u9ad8\u697c\u201d\uff0c\u4e5d\u56db\u3001\u4e5d\u4e94\u5e74\u65f6\uff0c\u60e0\u5c71\u8fde\u7eed\u5efa\u8d77\u51e0\u5ea7\u8d85\u8fc7\u4e00\u767e\u4e94\u5341\u7c73\u7684\u6469\u5929\u5927\u53a6\uff0c\u5efa\u90ba\u5927\u9152\u5e97\u53c8\u9000\u4f4d\u5230\u201c\u5efa\u90ba\u7b2c\u4e00\u9ad8\u697c\u201d\u7684\u4f4d\u5b50\u4e0a\u3002</p><p>\u3000\u3000\u5efa\u90ba\u4eba\u5185\u5fc3\u6df1\u5904\uff0c\u66f4\u4e0d\u5e0c\u671b\u5efa\u90ba\u5927\u9152\u5e97\u6210\u7acb\u5efa\u90ba\u7b2c\u4e00\u9ad8\u697c\uff0c\u5185\u5fc3\u8feb\u5207\u7684\u5e0c\u671b\u4e00\u5ea7\u5efa\u90ba\u7684\u65b0\u5730\u6807\uff0c\u6765\u5ba3\u793a\u5efa\u90ba\u7684\u7ecf\u6d4e\u53d1\u5c55\u6c34\u5e73\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c412\u7c73\u9ad8\u7684\u91d1\u8302\u5927\u53a6\u5728\u4e0a\u6d77\u6d66\u4e1c\u5efa\u6210\uff0c\u5efa\u90ba\u4e5f\u7ec8\u4e8e\u6709\u4e00\u5ea7\u6469\u5929\u5927\u53a6\u4ece\u5efa\u90ba\u5927\u996d\u5e97\u624b\u91cc\u593a\u8fc7\u65b0\u5730\u6807\u7684\u5934\u8854\uff0c\u53ef\u6068\u7684\u662f\uff0c\u56fd\u8d38\u4e2d\u5fc3\u53ea\u6709\u4e00\u767e\u56db\u5341\u516b\u7c73\u9ad8\uff0c\u5728\u4e1c\u6d77\u7701\u5185\u9887\u4e3a\u77e5\u540d\u7684\u897f\u5e99\u80e1\u540c\u8bba\u575b\u4e0a\uff0c\u5efa\u90ba\u4eba\u90fd\u4e0d\u597d\u610f\u601d\u8ddf\u5916\u5730\u4eba\u8bf4\u5efa\u90ba\u7684\u5730\u6807\u8bb0\u5f55\u5237\u65b0\u4e86\uff0c\u5374\u662f\u7ecf\u5e38\u7ed9\u522b\u4eba\u62ff\u8fd9\u4e2a\u6765\u595a\u843d\u3002</p><p>\u3000\u3000\u8d77\u521d\u7684\u8c23\u4f20\u4e5f\u6ca1\u6709\u592a\u79bb\u8c31\uff0c\u751a\u81f3\u76f8\u5bf9\u6765\u8bf4\u8fd8\u6709\u4e9b\u4fdd\u5b88\uff0c\u53ea\u8bf4\u8981\u5efa\u4e24\u767e\u7c73\u5de6\u53f3\u9ad8\u5ea6\u7684\u6469\u5929\u5927\u53a6\uff0c\u5177\u4f53\u591a\u9ad8\uff0c\u8fd8\u8981\u770b\u5177\u4f53\u7684\u8bbe\u8ba1\u65b9\u6848\uff0c\u5927\u4f53\u4e0a\uff0c\u7ad9\u5728\u697c\u9876\u80fd\u770b\u5230\u5efa\u90ba\u5168\u5883\u3002\u6d88\u606f\u5728\u5e02\u4e95\u95f4\u3001\u5728\u8bba\u575b\u4e0a\u4f20\u6765\u4f20\u53bb\u5c31\u53d8\u4e86\u5473\uff0c\u5148\u662f\u8bf4\u7ad9\u5728\u6469\u5929\u5927\u697c\u9876\u5e94\u8be5\u80fd\u770b\u5230\u5efa\u90ba\u5168\u5883\uff0c\u63a5\u7740\u53c8\u8bf4\u80fd\u770b\u5230\u65b0\u829c\u5e02\u7684\u5929\u4e91\u5c71\uff0c\u63a5\u7740\u53c8\u8bf4\u5e94\u8be5\u80fd\u770b\u5230\u91d1\u5c71\u5e02\u7684\u91d1\u5c71\u6e56\uff0c\u90a3\u5dee\u4e0d\u591a\u8981\u7834\u4e09\u767e\u7c73\u7684\u9ad8\u5ea6\u8bb0\u5f55\u4e86\u2026\u2026</p><p>\u3000\u3000\u8bba\u575b\u5982\u5e02\u4e95\uff0c\u6709\u80e1\u5439\u4e71\u4f83\u7684\uff0c\u81ea\u7136\u4e5f\u6709\u6247\u8138\u8d28\u7591\u7684\uff0c\u8bba\u575b\u4e0a\u95f9\u817e\u4e86\u4e03\u516b\u5929\uff0c\u6cb8\u6cb8\u626c\u626c\u7684\u4e0d\u4f11\uff0c\u5f53\u4e2d\u8fd8\u771f\u6709\u4eba\u6076\u4f5c\u5267\u7684\u5236\u4f5c\u4e00\u5e45\u6548\u679c\u56fe\u653e\u5230\u7f51\u4e0a\u4ee5\u8bc1\u660e\u6e56\u4e1c\u5730\u533a\u771f\u7684\u8981\u5efa\u6469\u5929\u5927\u53a6\u3002\u53ea\u662f\u56fe\u4e0a\u8fd9\u680b\u6469\u5929\u5927\u53a6\u7684\u6548\u679c\u56fe\u770b\u4e0a\u53bb\u771f\u8ddf\u7231\u8fbei19\u624b\u673a\u4f3c\u7684\uff0c\u76f4\u677f\u6241\u5e73\u5916\u5f62\uff0c\u949b\u5408\u91d1\u7070\u7684\u4e3b\u8272\u8c03\uff0c\u9876\u7aef\u8bbe\u8ba1\u6210\u6db2\u6676\u5c4f\u5f62\u72b6\u7684\u5de8\u5927\u901a\u98ce\u5b54\uff0c\u6548\u679c\u56fe\u4e0a\u8fd8\u8c61\u5f81\u6027\u7684\u4f9d\u7167\u624b\u673a\u952e\u76d8\u7684\u5e03\u5c40\u753b\u51fa\u51e0\u6247\u4eae\u706f\u7684\u7a97\u6237\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u4e0d\u8fc7\u7ed9\u8bba\u575b\u4e0a\u4e89\u5435\u63d0\u4f9b\u63a8\u6ce2\u52a9\u6f9c\u7684\u7d20\u6750\u7f62\u4e86\uff0c\u76f4\u5230\u7231\u8fbe\u96c6\u56e2\u4e0e\u5efa\u90ba\u5e02\u653f\u4f1a\u8054\u5408\u53d1\u5e03\u58f0\u660e\uff0c\u5ba3\u5e03\u4ee5\u516d\u5343\u4e07\u7684\u5de8\u8d44\u5168\u7403\u5f81\u96c6\u6469\u5929\u5927\u53a6\u7684\u8bbe\u8ba1\u65b9\u6848\uff0c\u5411\u5168\u7403\u77e5\u540d\u8bbe\u8ba1\u5e08\u4e8b\u52a1\u6240\u53d1\u51fa\u9080\u8bf7\u51fd\uff0c\u8d28\u7591\u58f0\u624d\u5e73\u606f\u4e0b\u6765\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u8054\u5408\u58f0\u660e\u4e5f\u5ba3\u5e03\u6469\u5929\u5927\u53a6\u7684\u8bbe\u8ba1\u9ad8\u5ea6\u4e0d\u4f1a\u8d85\u8fc7\u71d5\u5f52\u5c71\u7684\u6d77\u62e8\uff0c\u4e5f\u5c31\u662f\u8bf4\u6469\u5929\u5927\u53a6\u7684\u8bbe\u8ba1\u9ad8\u5ea6\u88ab\u9650\u5b9a\u5728\u4e8c\u767e\u56db\u5341\u516b\u7c73\u4ee5\u4e0b\u3002</p><p>\u3000\u3000\u867d\u8bf4\u6469\u5929\u5927\u53a6\u6ca1\u6709\u60f3\u8c61\u4e2d\u90a3\u4e48\u9ad8\uff0c\u5efa\u90ba\u4eba\u5374\u6ca1\u6709\u56e0\u6b64\u800c\u6cc4\u6c14\uff0c\u516d\u5343\u4e07\u7684\u5de8\u8d44\u8bbe\u8ba1\u8d39\u6210\u4e86\u65b0\u7684\u8ba8\u8bba\u7126\u70b9\uff0c\u5a92\u4f53\u7684\u53cd\u5e94\u5728\u9884\u6599\u4e4b\u4e2d\uff0c\u4e92\u8054\u7f51\u4e0a\uff0c\u4e0d\u4ec5\u662f\u5efa\u90ba\u4eba\u8db3\u8ff9\u6240\u53ca\u7684\u8bba\u575b\uff0c\u5efa\u7b51\u8bbe\u8ba1\u76f8\u5173\u7684\u8bba\u575b\u4e0a\uff0c\u6709\u8bb8\u591a\u7f51\u6c11\u90fd\u7eb7\u7eb7\u81ea\u53d1\u7684\u8ba8\u8bba\u8d77\u6469\u5929\u5927\u53a6\u7684\u8bbe\u8ba1\u65b9\u6848\uff0c\u897f\u5e99\u8bba\u575b\u5efa\u90ba\u4eba\u7248\u5c06\u7f51\u4e0a\u6240\u6709\u7684\u8bbe\u8ba1\u65b9\u6848\u6536\u96c6\u8d77\u6765\u641e\u4e86\u4e00\u6b21\u6295\u7968\uff0c\u7ed3\u679c\u5374\u662f\u6700\u521d\u90a3\u4e2a\u6a21\u4effi19\u5916\u5f62\u7684\u6076\u4f5c\u5267\u8bbe\u8ba1\u65b9\u6848\u5728\u7f51\u4e0a\u5f97\u5230\u7684\u54cd\u5e94\u547c\u58f0\u6700\u9ad8\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u662f\u5076\u5c14\u770b\u5230\u53f6\u5c0f\u6850\u7684\u7535\u8111\u4e0a\u7adf\u7136\u62ff\u8fd9\u5f20\u6548\u679c\u56fe\u5f53\u7535\u8111\u5c4f\u4fdd\u6216\u5899\u7eb8\u624d\u77e5\u9053\u6b64\u4e8b\uff0c\u5fc3\u60c5\u90c1\u95f7\u5f97\u4e0d\u884c\uff0c\u4e0d\u77e5\u9053\u662f\u8c01\u5c06\u4ed6\u8ddf\u90b5\u81f3\u521a\u8bf4\u7684\u90a3\u756a\u6469\u5929\u5927\u697c\u8bbe\u8ba1\u8981\u4e0e\u4f01\u4e1a\u7406\u5ff5\u8a00\u884c\u4e00\u81f4\u7684\u8bdd\u4f20\u5230\u53f6\u5c0f\u6850\u7684\u8033\u6735\u91cc\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u8c01\u6076\u4f5c\u5267\u7684\u5236\u4f5c\u8fd9\u5e45\u6548\u679c\u56fe\u3002</p><p>\u3000\u3000\u201c\u8981\u4e0d\u8981\u5c06\u8fd9\u4e2a\u8bbe\u8ba1\u65b9\u6848\u53d1\u7ed9\u5404\u4e2a\u8001\u603b\u4eec\u8ba8\u8bba\u4e00\u4e0b\uff0c\u8bf4\u4e0d\u5b9a\u5341\u4e94\u7968\u5bf9\u5341\u56db\u7968\u5c31\u901a\u8fc7\u4e86\u2026\u2026\u201d\u53f6\u5c0f\u6850\u6253\u8da3\u5f20\u606a\u9053\u3002</p><p>\u3000\u3000\u4f55\u5f26\u4e0e\u9648\u5983\u84c9\u7b11\u5f97\u8be1\u5f02\uff0c\u5f20\u606a\u771f\u4e0d\u77e5\u9053\u4e24\u4e2a\u5c11\u5973\u8ddf\u4e2a\u5b55\u5987\u6df7\u5728\u4e00\u8d77\u4f1a\u6709\u8fd9\u4e48\u591a\u4e0d\u6b63\u7ecf\u7684\u4e8b\u60c5\u8981\u505a\uff0c\u7ed9\u595a\u843d\u7740\uff0c\u4e5f\u53ea\u662f\u8146\u7740\u8138\u5f53\u6210\u6ca1\u6709\u8fd9\u56de\u4e8b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u3001\u6797\u96ea\u4ed6\u4eec\u7684\u52a8\u4f5c\u5f88\u5feb\uff0c\u6d77\u7c9f\u79d1\u6280\u505c\u76d8\u4e09\u5929\u5c31\u53ec\u5f00\u4e34\u65f6\u80a1\u4e1c\u5927\u4f1a\u901a\u8fc7\u6536\u8d2d\u521b\u57df\u7f51\u5427\u8d44\u4ea7\u7684\u51b3\u8bae\uff0c\u4e5d\u6708\u5341\u516b\u65e5\u5c31\u8fdb\u5165\u5b9e\u8d28\u7684\u8d44\u4ea7\u4ea4\u5272\u9636\u6bb5\uff0c\u9664\u4e86\u4ee4\u5c0f\u71d5\u3001\u65f6\u5b66\u658c\u7b49\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\u64a4\u51fa\u4e4b\u5916\uff0c\u866b\u4ff1\u4e50\u90e8\u6838\u5fc3\u6210\u5458\u90fd\u64a4\u51fa\u5916\uff0c\u5176\u4f59\u7684\u7f51\u5427\u7ba1\u7406\u56e2\u961f\u4e0e\u6280\u672f\u56e2\u961f\u90fd\u7531\u6d77\u7c9f\u79d1\u6280\u63a5\u6536\uff0c\u6d77\u7c9f\u79d1\u6280\u6210\u7acb\u5168\u8d44\u5b50\u516c\u53f8\u7ecf\u8425\u7f51\u5427\u4e1a\u52a1\uff0c\u5ef6\u7528\u521b\u57df\u7684\u540d\u79f0\uff0c\u5168\u79f0\u4e3a\u521b\u57df\u4fe1\u606f\u65b0\u79d1\u6280\u6709\u9650\u516c\u53f8\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u8fd9\u6b21\u7ec8\u4e8e\u5728\u6d77\u7c9f\u79d1\u6280\u5185\u90e8\u83b7\u5f97\u4e9b\u5b9e\u6743\uff0c\u62c5\u4efb\u521b\u57df\u4fe1\u606f\u65b0\u79d1\u6280\u6709\u9650\u516c\u53f8\u7684\u603b\u7ecf\u7406\uff0c\u52a8\u7528\u4e24\u4e2a\u4ebf\u7684\u53d1\u5c55\u8d44\u91d1\uff0c\u5927\u89c4\u6a21\u7684\u5728\u5efa\u90ba\u53d1\u5c55\u76f4\u8425\u7f51\u5427\u4e0e\u8fde\u9501\u7f51\u5427\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u5373\u4f7f\u52a0\u4e86\u201c\u4fe1\u606f\u65b0\u79d1\u6280\u201d\u7684\u5b57\u6837\uff0c\u4e5f\u65e0\u6cd5\u626d\u8f6c\u7f51\u5427\u4ea7\u4e1a\u79d1\u6280\u542b\u91cf\u5b9e\u5219\u6709\u9650\u7684\u4e8b\u5b9e\uff0c\u53ea\u4e0d\u8fc7\u9010\u6e10\u72c2\u70ed\u7684\u8bc1\u5238\u5e02\u573a\u4ee5\u53ca\u90a3\u4e9b\u4e2d\u5c0f\u6563\u6237\u80a1\u6c11\u4e0d\u4f1a\u7406\u667a\u7684\u8ba4\u8bc6\u5230\u8fd9\u4e00\u70b9\u3002</p><p>\u3000\u3000\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u5728\u8bc1\u5238\u5e02\u573a\u91cd\u65b0\u516c\u5f00\u4ea4\u6613\u540e\uff0c\u80a1\u4ef7\u8fde\u7eed\u5c01\u4e86\u4e09\u4e2a\u6da8\u505c\u7248\uff0c\u624d\u5c0f\u5e45\u56de\u8c03\uff0c\u5bf9\u53c2\u4e0e\u5b9a\u5411\u589e\u53d1\u7684\u5b8f\u4fe1\u6295\u8d44\u4e0e\u7cbe\u5178\u5730\u4ea7\uff0c\u7eb8\u9762\u8d22\u5bcc\u5728\u4e0d\u5230\u5341\u5929\u65f6\u95f4\u91cc\u5c31\u7206\u6da8\u4e86\u8fd1\u4e24\u4ebf\u3002\u4e5f\u96be\u602a\u8bc1\u5238\u5e02\u573a\u4f1a\u6709\u8fd9\u4e48\u5927\u7684\u8bf1\u60d1\u529b\u4e86\uff0c\u7cbe\u5178\u5730\u4ea7\u53bb\u5e74\u5728\u8f6f\u56ed\u79d1\u6280\u56ed\u4e0a\u82b1\u4e86\u8fd9\u4e48\u591a\u5fc3\u601d\uff0c\u505a\u4e86\u8fd9\u4e48\u591a\u5c0f\u52a8\u4f5c\uff0c\u6700\u540e\u5377\u5230\u624b\u7684\u90fd\u6ca1\u6709\u4e00\u4e2a\u4ebf\u3002</p><p>\u3000\u3000\u6709\u4e00\u70b9\u4ee4\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u611f\u5230\u6709\u5c0f\u5c0f\u7684\u4e0d\u723d\uff0c\u5728\u6d77\u7c9f\u79d1\u6280\u63a5\u624b\u521b\u57df\u7f51\u5427\u8d44\u4ea7\u4e4b\u540e\uff0c\u5df2\u7ecf\u5c31\u8bfb\u5927\u56db\u7684\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u90fd\u8fdb\u5165\u521b\u57df\u4fe1\u606f\u65b0\u79d1\u6280\u6709\u9650\u516c\u53f8\uff0c\u8fd8\u6302\u8d77\u5b9e\u4e60\u7ecf\u7406\u7684\u5934\u8854\u3002</p><p>\u3000\u3000\u539f\u5148\u7684\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\uff0c\u7531\u4e8e\u5c06\u6ce8\u518c\u5546\u6807\u53ca\u90e8\u5206\u65e0\u5f62\u8d44\u4ea7\u90fd\u79fb\u4ea4\u7ed9\u6d77\u7c9f\u79d1\u6280\uff0c\u4e5f\u6b63\u5f0f\u66f4\u540d\u4e3a\u4e1c\u5927\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\uff0c\u529e\u516c\u573a\u6240\u4e5f\u6682\u65f6\u642c\u5230\u4e1c\u5927\u7684\u8d44\u6e90\u697c\u91cc\uff0c\u4e0e\u7f51\u6821\u7684\u57f9\u8bad\u57fa\u5730\u6328\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u4e5d\u6708\u4e0b\u65ec\u7684\u5efa\u90ba\uff0c\u5929\u6c14\u5f00\u59cb\u8f6c\u51c9\uff0c\u8d70\u5728\u68a7\u6850\u6811\u4e0b\uff0c\u5915\u9633\u659c\u7167\uff0c\u6709\u5149\u5f71\u4ece\u53f6\u9699\u95f4\u6f0f\u843d\u4e0b\u6765\uff0c\u7ad9\u5728\u7199\u7199\u6518\u6518\u7684\u4eba\u6d41\u4e4b\u4e2d\uff0c\u770b\u7740\u8fce\u9762\u8d70\u6765\u7684\u4e00\u5f20\u5f20\u964c\u751f\u7684\u9762\u5b54\uff0c\u5f20\u606a\u66f4\u5bb9\u6613\u8fdb\u5165\u6df1\u5ea6\u601d\u8003\u7684\u72b6\u6001\u3002\u9648\u5983\u84c9\u8fd9\u6bb5\u65f6\u95f4\u4e00\u6709\u7a7a\u5c31\u8981\u53bb\u7ecf\u7814\u4e2d\u5fc3\u5b9e\u4e60\uff0c\u5f20\u606a\u4e0b\u5348\u65f6\u95f4\u6216\u8eb2\u5728\u516c\u5bd3\u91cc\u770b\u4e66\uff0c\u6216\u5904\u7406\u4e00\u4e9b\u4e8b\u52a1\uff0c\u6216\u8005\u8d70\u5230\u5b66\u5e9c\u5df7\u627e\u4e00\u5bb6\u6709\u843d\u5730\u7a97\u7684\u5496\u5561\u5385\uff0c\u770b\u8857\u4e0a\u7684\u4eba\u6d41\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u8fdb\u5496\u5561\u5385\uff0c\u770b\u5230\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u4e24\u4e2a\u5bb6\u4f19\u8ddf\u4e00\u4e9b\u4eba\u4e5f\u5750\u5728\u4e34\u7a97\u7684\u5ea7\u4f4d\uff0c\u7709\u5934\u5fae\u5fae\u4e00\u76b1\uff0c\u4e5f\u6ca1\u6709\u538c\u6076\u5230\u8981\u8f6c\u8eab\u8d70\u5f00\uff0c\u518d\u8bf4\u8981\u8d70\u4eba\u4e5f\u5f97\u662f\u4ed6\u4eec\u8d70\u4eba\u3002</p><p>\u3000\u3000\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u521a\u624d\u8fd8\u9ad8\u58f0\u9614\u8bba\uff0c\u770b\u5230\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u58f0\u97f3\u655b\u4e86\u655b\uff0c\u4ed6\u4eec\u90fd\u77e5\u9053\u5efa\u90ba\u8fd8\u6709\u8c01\u662f\u4ed6\u4eec\u60f9\u4e0d\u8d77\uff0c\u5f20\u606a\u80af\u5b9a\u8981\u7b97\u4e00\u4e2a\uff0c\u5176\u4ed6\u4eba\u5374\u662f\u4e0d\u61c2\uff0c\u8fd8\u5728\u90a3\u91cc\u7ee7\u7eed\u8dbe\u9ad8\u6c14\u626c\u7684\u9ad8\u8c08\u9614\u8bba\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u4e86\u4e00\u4f1a\u513f\uff0c\u539f\u6765\u662f\u80e1\u91d1\u661f\u544a\u8bc9\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u5185\u5e55\u6d88\u606f\uff0c\u8ba9\u4ed6\u4eec\u5728\u6d77\u7c9f\u79d1\u6280\u6536\u8d2d\u521b\u57df\u7f51\u5427\u8d44\u4ea7\u505c\u724c\u4e4b\u524d\u4e70\u8fdb\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u7968\u8d5a\u4e86\u4e09\u4e2a\u6da8\u505c\u7248\u3002</p><p>\u3000\u3000\u8fd9\u6216\u8bb8\u5c31\u662f\u4e5d\u4e5d\u5e74\u4e2d\u56fd\u8bc1\u5238\u5e02\u573a\u7684\u67d0\u79cd\u73b0\u5b9e\u4e86\uff0c\u5185\u5e55\u4ea4\u6613\u5df2\u7ecf\u662f\u4e00\u79cd\u660e\u76ee\u5f20\u80c6\u7684\u884c\u4e3a\uff0c\u4e5f\u6709\u90a3\u4e48\u4e00\u5927\u7fa4\u4eba\u4e13\u95e8\u9760\u7740\u6216\u771f\u6216\u5047\u7684\u5185\u5e55\u6d88\u606f\u7092\u80a1\u3002\u8bc1\u76d1\u90e8\u95e8\u5bf9\u6b64\u4e5f\u6ca1\u6709\u4ec0\u4e48\u6253\u51fb\u529b\u5ea6\uff0c\u5c31\u7b97\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4e0d\u5426\u8ba4\u662f\u4ed6\u4eec\u900f\u9732\u5185\u5e55\u6d88\u606f\u7ed9\u65c1\u4eba\uff0c\u4e5f\u6839\u672c\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u5927\u7684\u628a\u67c4\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u7740\u4ed6\u4eec\u5728\u90a3\u91cc\u9ad8\u8c08\u9614\u8bba\uff0c\u4e5f\u53ea\u5f53\u662f\u559d\u5496\u5561\u65f6\u7684\u4f50\u6599\uff0c\u80e1\u91d1\u661f\u3001\u9648\u52c7\u867d\u7136\u6709\u4e9b\u5fe4\u5f20\u606a\uff0c\u4f46\u662f\u5fc3\u91cc\u8fd8\u662f\u6709\u4e9b\u4e0d\u670d\u6c14\u7684\uff0c\u4e5f\u6709\u5fc3\u70ab\u8000\u3002</p><p>\u3000\u3000\u542c\u4ed6\u4eec\u90a3\u91cc\u8c08\u8bba\uff0c\u5f20\u606a\u5012\u662f\u77e5\u9053\u80e1\u91d1\u661f\u3001\u9648\u52c7\u4ed6\u4eec\u5728\u6d77\u7c9f\u79d1\u6280\u501f\u58f3\u4e0a\u5e02\u4e4b\u524d\u5c31\u5927\u91cf\u4e70\u5165\u5f53\u65f6\u8fd8\u662fST\u601d\u6e90\u5236\u836f\u7684\u80a1\u7968\uff0c\u8ba1\u7b97\u6da8\u5e45\uff0c\u7d2f\u8ba1\u5df2\u7ecf\u4e03\u500d\u4e4b\u591a\u4e86\u3002\u770b\u7740\u80e1\u91d1\u661f\u4e0e\u9648\u52c7\u7684\u4e00\u8138\u5f97\u610f\u4e0e\u70ab\u8000\uff0c\u5f20\u606a\u5fc3\u91cc\u6697\u53f9\uff1a\u5e74\u8f7b\u4eba\u5c31\u662f\u8f7b\u6d6e\uff0c\u7adf\u7136\u8fd9\u4e48\u968f\u4fbf\u5c31\u5c06\u8fd9\u4e48\u91cd\u8981\u7684\u6d88\u606f\u6cc4\u9732\u7ed9\u5bf9\u624b\u77e5\u9053\uff0c\u65e5\u540e\u53ea\u6015\u8fde\u6b7b\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u6b7b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u731c\u5230\u6797\u96ea\u3001\u4e25\u6587\u4ecb\u4e0e\u80e1\u5b97\u5e86\u6709\u6743\u94b1\u4ea4\u6613\uff0c\u4f46\u662f\u8fd9\u79cd\u4e8b\u60c5\u6293\u5230\u628a\u67c4\u4e0d\u5bb9\u6613\uff0c\u5373\u4f7f\u6709\u8bb8\u591a\u9634\u635f\u7684\u62db\u6570\uff0c\u4f46\u662f\u5728\u53cc\u65b9\u90fd\u6ca1\u6709\u5f7b\u5e95\u6495\u7834\u8138\u7684\u60c5\u51b5\u4e0b\u4e0d\u80fd\u662f\u9526\u6e56\u4e3b\u52a8\u7684\u53bb\u7834\u574f\u5efa\u90ba\u5e02\u653f\u6cbb\u52bf\u529b\u7684\u5e73\u8861\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u60f3\u7740\uff1a\u4ee5\u6797\u96ea\u3001\u4e25\u6587\u4ecb\u7684\u5fc3\u601d\uff0c\u5b81\u53ef\u76f4\u63a5\u5f80\u80e1\u5b97\u5e86\u6000\u91cc\u585e\u94b1\uff0c\u4e5f\u4e0d\u5927\u4f1a\u901a\u8fc7\u900f\u9732\u5185\u5e55\u6d88\u606f\u7684\u65b9\u5f0f\u7ed9\u80e1\u5b97\u5e86\u8f93\u9001\u5229\u76ca\uff0c\u6bd5\u7adf\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u7684\u64cd\u4f5c\u5bf9\u4ed6\u4eec\u6765\u8bf4\u76f8\u5f53\u7684\u91cd\u8981\uff0c\u7279\u522b\u662f\u524d\u671f\u4e00\u5b9a\u8981\u4e25\u683c\u63a7\u5236\u6d88\u606f\u6269\u6563\u624d\u80fd\u4fdd\u8bc1\u4ed6\u4eec\u7684\u5229\u76ca\u6700\u5927\u5316\uff0c\u96be\u9053\u8bf4\u80e1\u5b97\u5e86\u4e5f\u662f\u76f4\u63a5\u5229\u76ca\u4eba\uff1f</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\uff0c\u653f\u5e9c\u5b98\u5458\u5728\u4f01\u4e1a\u65f6\u6301\u6709\u5e72\u80a1\u4e5f\u662f\u5341\u5206\u666e\u904d\u7684\u4e8b\u60c5\uff0c\u8981\u8bf4\u662f\u80e1\u5b97\u5e86\u5728\u6d77\u7c9f\u79d1\u6280\u91cc\u6301\u6709\u5e72\u80a1\uff0c\u90a3\u5e94\u8be5\u662f\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u65f6\uff0c\u90a3\u65f6\u80e1\u5b97\u5e86\u8868\u73b0\u6700\u4e3a\u79ef\u6781\uff0c\u8096\u660e\u5efa\u5462\uff1f\u8096\u660e\u5efa\u5f53\u65f6\u4e5f\u662f\u9f0e\u529b\u652f\u6301\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u7684\u4eba\u3002</p><p>\u3000\u3000\u8981\u67e5\u5230\u8bc1\u636e\u4e5f\u96be\uff0c\u80e1\u5b97\u5e86\u3001\u8096\u660e\u5efa\u4e5f\u4e0d\u53ef\u80fd\u76f4\u63a5\u5c06\u80a1\u6743\u653e\u5230\u81ea\u5df1\u7684\u540d\u4e0b\uff0c\u5f20\u606a\u7709\u5934\u8f7b\u62e7\u7740\uff0c\u8eab\u5b50\u504f\u504f\u4fa7\u8fc7\u6765\uff0c\u770b\u5230\u80e1\u91d1\u661f\u80f8\u524d\u522b\u7740\u521b\u57df\u5b9e\u4e60\u7ecf\u7406\u7684\u94ed\u724c\uff0c\u60f3\u7740\u80e1\u91d1\u661f\u4f1a\u4e0d\u4f1a\u501f\u673a\u5c31\u8fdb\u5165\u6d77\u7c9f\u79d1\u6280\u6216\u8005\u7cbe\u5178\u5730\u4ea7\u6216\u8005\u5b8f\u4fe1\u7cfb\u4f01\u4e1a\u5de5\u4f5c\uff0c\u4ed6\u4eec\u518d\u901a\u8fc7\u79cd\u79cd\u624b\u6bb5\u5c06\u5b9e\u9645\u4e0a\u5f52\u80e1\u5b97\u5e86\u6240\u6709\u7684\u80a1\u6743\u9010\u6b65\u8f6c\u79fb\u5230\u80e1\u91d1\u661f\u540d\u4e0b\uff1f</p><p>\u3000\u3000\u559d\u8fc7\u5496\u5561\uff0c\u79bb\u5f00\u5496\u5561\u5385\uff0c\u9001\u4ed6\u53bb\u673a\u573a\u7684\u8f7f\u8f66\u65e9\u5c31\u505c\u5728\u8fd9\u91cc\u7b49\u5019\uff0c\u770b\u5230\u8857\u4e0a\u884c\u4eba\u8be7\u5f02\u7684\u770b\u7740\u81ea\u5df1\u94bb\u8fdb\u5954\u9a70\u8f66\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u81ea\u5df1\u662f\u4e0d\u662f\u6362\u4e00\u8f86\u666e\u901a\u4eba\u4e0d\u719f\u6089\u7684\u739b\u838e\u62c9\u8482\u56db\u95e8\u8f7f\u8f66\uff1f</p><p>\u3000\u3000\u9999\u6e2f\u5927\u5b66\uff0c\u4e2d\u79cb\u4e0e\u56fd\u5e86\u8282\u90fd\u6709\u4e00\u5929\u7684\u5047\u671f\uff0c\u52a0\u4e0a\u53cc\u4f11\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5185\u5730\u7684\u9ec4\u91d1\u5468\u4e03\u5929\u957f\u5047\uff0c\u4e5f\u7b97\u662f\u76f8\u5f53\u4e0d\u9519\u3002\u5510\u5a67\u8d76\u56de\u6765\u9887\u4e3a\u9ebb\u70e6\uff0c\u518d\u8bf4\u6c5f\u9edb\u513f\u7684\u9996\u4e2a\u4e2a\u4eba\u6f14\u5531\u4f1a\u4e5f\u5c06\u5728\u56fd\u5e86\u8282\u671f\u95f4\u5728\u9999\u6e2f\u5f00\u5531\uff0c\u5f20\u606a\u4fbf\u8d76\u5230\u9999\u6e2f\u53bb\u4e0e\u5510\u5a67\u6c47\u5408\uff0c\u53ea\u53ef\u60dc\u9648\u5983\u84c9\u8981\u56de\u6d77\u5dde\uff0c\u4e0d\u80fd\u8ddf\u4ed6\u4e00\u8d77\u53bb\u9999\u6e2f\u3002</p><p>\u3000\u3000\u516d\u70b9\u949f\u4ece\u5efa\u90ba\u8d77\u98de\uff0c\u62b5\u8fbe\u9999\u6e2f\u540e\u529e\u7406\u624b\u7eed\u51fa\u5883\u5df2\u7ecf\u5feb\u5230\u4e5d\u70b9\u949f\u4e86\uff0c\u7531\u4e8e\u53f6\u5efa\u658c\u9694\u5929\u8981\u98de\u91d1\u5c71\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u4ece\u673a\u573a\u51fa\u6765\uff0c\u5c31\u76f4\u63a5\u8d76\u5230\u6e2f\u5c9b\u6d45\u6c34\u6e7e\u7684\u5f71\u6e7e\u56ed\uff0c\u4e0e\u53f6\u5efa\u658c\u89c1\u9762\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u7684\u4f4f\u6240\u4e00\u76f4\u90fd\u5728\u5f71\u6e7e\u56ed\u9ad8\u7ea7\u516c\u5bd3\u5185\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u8d76\u8fc7\u53bb\uff0c\u76f4\u63a5\u5230\u5f71\u6e7e\u56ed\u7684\u79c1\u4eba\u4f1a\u6240\u627e\u4ed6\u4eec\uff0c\u5728\u90a3\u91cc\u4e5f\u4e0d\u6b62\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u4e24\u4eba\uff0c\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u4e0e\u5979\u6bcd\u4eb2\u90fd\u4f4f\u5728\u5f71\u6e7e\u56ed\u516c\u7ea7\u516c\u5bd3\u91cc\uff0c\u5728\u5f20\u606a\u4e0e\u5510\u5a67\u8d76\u5230\u4e4b\u524d\uff0c\u5979\u4eec\u4e5f\u5728\u4f1a\u6240\u91cc\u559d\u9152\u804a\u5929\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u684c\u4e0a\u6709\u70b9\u5fc3\uff0c\u637b\u8d77\u6765\u5c31\u5f80\u5634\u91cc\u585e\uff0c\u8ddf\u6c5f\u9edb\u513f\u8bf4\u8bdd\u4e5f\u8bed\u97f3\u542b\u6df7\uff0c\u5c06\u4e00\u6574\u53ea\u86cb\u55d2\u585e\u8fdb\u5634\u91cc\uff0c\u54bd\u4e0b\u53bb\uff0c\u624d\u8ddf\u5927\u5bb6\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u4e1c\u822a\u5546\u52a1\u8231\u63d0\u4f9b\u7684\u665a\u9910\u8ddf\u72d7\u98df\u4f3c\u7684\uff0c\u4e5f\u96be\u4e3a\u5085\u4fca\u5c06\u6211\u90a3\u4efd\u4e5f\u5403\u4e0b\u53bb\u4e86\uff0c\u6211\u90fd\u5feb\u997f\u6302\u4e86\uff0c\u201d\u53c8\u770b\u7740\u6c5f\u9edb\u513f\u624b\u91cc\u7aef\u7740\u7ea2\u9152\u676f\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u6211\u5728\u8f66\u4e0a\u8fd8\u8ddf\u5510\u5a67\u8bf4\u6f14\u5531\u4f1a\u4e4b\u524d\u4e00\u5b9a\u770b\u4e0d\u5230\u4f60\uff0c\u4f60\u591a\u534a\u4f1a\u5c06\u81ea\u5df1\u9501\u8d77\u6765\u95ed\u95e8\u82e6\u7ec3\u6b4c\u5589\uff0c\u4f60\u771f\u662f\u592a\u8ba9\u6211\u5931\u671b\u4e86\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u559d\u4e86\u4e00\u4e9b\u9152\uff0c\u4fcf\u8138\u8273\u82e5\u6843\u82b1\uff0c\u597d\u4e45\u6ca1\u89c1\u6709\u89c1\u5230\u5f20\u606a\u4e86\uff0c\u7ed9\u4ed6\u8fd9\u4e48\u770b\u7740\uff0c\u773c\u795e\u4e0b\u610f\u8bc6\u7684\u8981\u95ea\u8eb2\u5f00\uff0c\u672c\u6765\u662f\u8981\u5728\u5f55\u97f3\u68da\u7ec3\u6b4c\u7684\uff0c\u8fd8\u662f\u542c\u5230\u8fd9\u5bb6\u4f19\u8981\u6765\uff0c\u624d\u6ca1\u6709\u575a\u6301\u7ed9\u62c9\u4e86\u8fc7\u6765\u559d\u9152\uff0c\u53ea\u662f\u6e29\u5a49\u800c\u7b11\uff0c\u6ca1\u6709\u542d\u58f0\u3002</p><p>\u3000\u3000\u8ddf\u5927\u5bb6\u968f\u4fbf\u7684\u6253\u8fc7\u62db\u547c\uff0c\u53c8\u62ff\u4e86\u4e00\u4e9b\u70b9\u5fc3\u5c31\u7740\u7ea2\u9152\u704c\u8fdb\u809a\u5b50\u91cc\uff0c\u4e0d\u89c9\u5f97\u90a3\u4e48\u997f\u4e86\uff0c\u5f20\u606a\u624d\u8ddf\u53f6\u5efa\u658c\u5750\u5230\u4e00\u8fb9\u8bf4\u8bdd\uff0c\u5c06\u4ed6\u5bf9\u80e1\u5b97\u5e86\u53ef\u4ee5\u5728\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5360\u5e72\u80a1\u7684\u731c\u6d4b\u544a\u8bc9\u53f6\u5efa\u658c\u3002</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u5927\u5bb6\u90fd\u5728\u6d77\u6c34\u91cc\uff0c\u4e0d\u77e5\u9053\u8c01\u7a7f\u88e4\u5b50\uff0c\u8c01\u6ca1\u7a7f\u88e4\u5b50\uff0c\u7b49\u7740\u6d77\u6c34\u9000\u4e0b\u53bb\uff0c\u90a3\u4e9b\u5149\u5c41\u80a1\u5c31\u4f1a\u9732\u51fa\u539f\u5f62\u2026\u2026\u201d\u53f6\u5efa\u658c\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u53ea\u6015\u5149\u7740\u5c41\u80a1\u7684\u4eba\u4f1a\u7aed\u5c3d\u5168\u529b\u7684\u963b\u6b62\u6d77\u6c34\u9000\u4e0b\u53bb\u554a\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6f6e\u6765\u6f6e\u5f80\uff0c\u53c8\u5c82\u662f\u4eba\u529b\u80fd\u963b\u6321\u5f97\u4e86\uff0c\u6211\u5012\u662f\u66f4\u5e0c\u671b\u4ed6\u4eec\u5728\u9000\u6f6e\u65f6\u5f3a\u6491\u4f4f\u554a\uff0c\u4ed6\u4eec\u6491\u5f97\u8d8a\u4e45\uff0c\u5269\u4e0b\u6765\u7684\u4e8b\u60c5\uff0c\u5bf9\u6211\u4eec\u6765\u8bf4\u6216\u8bb8\u5c31\u5269\u4e0b\u8f7b\u8f7b\u4e00\u62e8\u4e86\u2026\u2026\u201d</p></article>"}}}]);
