(window.webpackJsonp=window.webpackJsonp||[]).push([[86],{108:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u56db\u8282 \u6587\u5b57\u6e38\u620f",content:"<article><h2>\u7b2c\u56db\u5341\u56db\u8282 \u6587\u5b57\u6e38\u620f</h2><p>\u4e0d\u8fc7\u8fd9\u4e9b\u4e8b\u60c5\u4e0e\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u65e0\u5173\uff0c\u968f\u53e3\u8bf4\u8bf4\uff0c\u4e5f\u5c31\u653e\u4e0b\u3002\u665a\u996d\u6bd5\uff0c\u6536\u62fe\u6574\u7406\u597d\u4e1c\u897f\uff0c\u4e5f\u4fbf\u65e9\u65e9\u4f11\u606f\uff0c\u660e\u5929\u4e00\u65e9\u4fbf\u8981\u542f\u7a0b\u8d76\u8def\uff0c\u9a6c\u8f66\u90fd\u5df2\u7ecf\u505c\u5728\u65c5\u5e97\u540e\u9662\u4e86\u3002\u548c\u6628\u665a\u4e00\u6837\uff0c\u743c\u6069\u518d\u4e00\u6b21\u4eab\u53d7\u4e86\u5c11\u5973\u6a31\u5507\u7684\u6e29\u987a\u670d\u4f8d\uff0c\u4f46\u5f53\u4ed6\u8bd5\u56fe\u5f97\u5bf8\u8fdb\u5c3a\uff0c\u60f3\u771f\u6b63\u4e00\u4eb2\u82b3\u6cfd\u7684\u65f6\u5019\uff0c\u5374\u88ab\u575a\u51b3\u5730\u963b\u6b62\u4e86\u3002\u4e24\u4eba\u5982\u4eca\u7684\u5173\u7cfb\u5fae\u5999\uff0c\u82e5\u5373\u82e5\u79bb\uff0c\u743c\u6069\u4e5f\u4e0d\u60f3\u5f3a\u6765\uff0c\u6015\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u2014\u2014\u518d\u8bf4\u4ed6\u7ec8\u7a76\u6709\u4e9b\u80c6\u602f\uff0c\u4e07\u4e00\u6885\u83f2\u65af\u771f\u53d1\u8d77\u706b\u4e00\u5251\u780d\u8fc7\u6765\uff0c\u743c\u6069\u81ea\u5ea6\u662f\u4e07\u4e07\u8eb2\u4e0d\u5f00\u3002\u53ea\u80fd\u6682\u65f6\u6253\u6d88\u5ff5\u5934\u3002</p><p>\u6885\u83f2\u65af\u8fd9\u4e00\u591c\u7761\u5f97\u5f88\u5b89\u8be6\uff0c\u6ca1\u6709\u518d\u505a\u5947\u602a\u7684\u6076\u68a6\u3002\u7b2c\u4e8c\u5929\uff0c\u4e24\u4eba\u4e58\u9a6c\u8f66\u4e0a\u8def\uff0c\u79bb\u5f00\u963f\u5fb7\u5df4\u57ce\uff0c\u5f00\u59cb\u524d\u5f80\u535a\u5f97\u4e4b\u95e8\u3002\u96c7\u6765\u7684\u8f66\u592b\u5012\u662f\u4e00\u628a\u597d\u624b\uff0c\u9a7e\u8f66\u5341\u5206\u7a33\u5f53\uff0c\u800c\u4e14\u6781\u6709\u804c\u4e1a\u9053\u5fb7\uff0c\u4ece\u6765\u4e0d\u591a\u7ba1\u95f2\u4e8b\uff0c\u5373\u4f7f\u4ed6\u591a\u6b21\u542c\u5230\u540e\u9762\u8f66\u53a2\u91cc\u53d1\u51fa\u7684\u6deb\u7cdc\u58f0\u54cd\u3002</p><p>\u957f\u8def\u6f2b\u6f2b\uff0c\u65c5\u9014\u5bc2\u5bde\uff0c\u603b\u8981\u6709\u4e9b\u5a31\u4e50\u6d88\u9063\u3002\u5bf9\u4e8e\u6885\u83f2\u65af\uff0c\u743c\u6069\u662f\u89ca\u89ce\u5df2\u4e45\uff0c\u867d\u7136\u5982\u4eca\u53ef\u4ee5\u7b97\u662f\u521d\u6b65\u5f97\u624b\uff0c\u4f46\u4ed6\u81ea\u7136\u4e0d\u4f1a\u5c31\u6b64\u6ee1\u8db3\u3002\u4e00\u8def\u4e0a\uff0c\u743c\u6069\u7f13\u6162\u800c\u5c0f\u5fc3\u7ffc\u7ffc\u5730\u5b9e\u65bd\u7740\u4ed6\u7684\u8c03\u6559\u8ba1\u5212\uff0c\u867d\u7136\u76f4\u5230\u5230\u8fbe\u535a\u5f97\u4e4b\u95e8\uff0c\u4ed6\u8fd8\u662f\u6ca1\u6709\u6210\u529f\u5730\u5403\u5230\u6885\u83f2\u65af\uff0c\u4f46\u81f3\u5c11\u5728\u4ed6\u7684\u6089\u5fc3\u6307\u5bfc\u4e0b\uff0c\u5973\u5723\u6b66\u58eb\u7684\u6280\u5de7\u662f\u8d8a\u6765\u8d8a\u9ad8\u660e\u4e86\u3002</p><p>\u4e24\u4eba\u5728\u8f66\u53a2\u4e2d\uff0c\u901a\u5e38\u662f\u743c\u6069\u5728\u770b\u4ed6\u7684\u9b54\u6cd5\u4e66\uff0c\u6e29\u4e60\u5b66\u8fc7\u7684\u6cd5\u672f\uff0c\u4f46\u9a6c\u8f66\u5b9e\u5728\u5e76\u975e\u4e00\u4e2a\u826f\u597d\u7684\u5b66\u4e60\u73af\u5883\u3002\u5546\u65c5\u901a\u9053\u867d\u7136\u8f83\u4e3a\u5bbd\u655e\u5e73\u5766\uff0c\u7ec8\u7a76\u4e5f\u4e0d\u662f\u6c34\u6ce5\u8def\uff0c\u9a6c\u8f66\u66f4\u4e0d\u80fd\u548c\u6c7d\u8f66\u76f8\u6bd4\uff0c\u8fd8\u662f\u9887\u4e3a\u98a0\u7c38\u6447\u6643\u3002\u6240\u4ee5\u743c\u6069\u7684\u5b66\u4e60\u8ba1\u5212\u5f80\u5f80\u662f\u4e2d\u9014\u592d\u6298\uff0c\u6539\u4e3a\u53e6\u4e00\u79cd\u953b\u70bc\u3002</p><p>\u535a\u5f97\u4e4b\u95e8\u7684\u761f\u75ab\u5df2\u7ecf\u6301\u7eed\u5f88\u4e45\uff0c\u6bcf\u5929\u90fd\u6709\u4eba\u6b7b\u53bb\uff0c\u6885\u83f2\u65af\u81ea\u7136\u5341\u5206\u7126\u6025\uff0c\u4e00\u8def\u4e0a\u51e0\u4e4e\u662f\u9a6c\u4e0d\u505c\u8e44\u3002\u867d\u7136\u7ecf\u8fc7\u94f6\u6708\u57ce\u3001\u6df1\u6c34\u57ce\u8fd9\u79cd\u8457\u540d\u7684\u5927\u57ce\u5e02\uff0c\u4e5f\u90fd\u662f\u5306\u5306\u800c\u8fc7\uff0c\u6839\u672c\u6ca1\u6709\u89c2\u8d4f\u7684\u673a\u4f1a\u3002\u4ece\u963f\u5fb7\u5df4\u57ce\u5230\u6df1\u6c34\u57ce\u7684\u4e00\u6bb5\u8def\u7a0b\uff0c\u56e0\u4e3a\u6709\u94f6\u6708\u8054\u90a6\u548c\u6df1\u6c34\u57ce\u7684\u519b\u961f\u6cbf\u9014\u62a4\u536b\uff0c\u6253\u51fb\u76d7\u532a\uff0c\u6240\u4ee5\u5012\u8fd8\u5e73\u5b89\uff1b\u4ece\u6df1\u6c34\u57ce\u5230\u535a\u5f97\u4e4b\u95e8\uff0c\u8def\u4e0a\u5c31\u649e\u4e0a\u4e86\u597d\u51e0\u8d77\u5f3a\u76d7\u3002</p><p>\u5f53\u7136\uff0c\u4ed6\u4eec\u90fd\u5f88\u4e0d\u5e78\uff0c\u56e0\u4e3a\u9047\u4e0a\u4e86\u6885\u83f2\u65af\u3002</p><p>\u201c\u524d\u9762\u8fd8\u6709\u591a\u5c11\u4eba\u57cb\u4f0f\uff1f\u201d\u5c11\u5973\u95ee\uff0c\u5979\u624b\u4e2d\u7684\u94f6\u5251\u62b5\u5728\u4e00\u4e2a\u5f3a\u76d7\u7684\u8116\u5b50\u4e0a\uff0c\u8eab\u65c1\u5168\u662f\u88ab\u780d\u65ad\u5934\u9885\u7684\u5f3a\u76d7\u3002</p><p>\u201c\u4f60\u2026\u2026\u4f60\u4fdd\u8bc1\u2026\u2026\u5982\u679c\u6211\u8bf4\u4e86\u5c31\u653e\u4e86\u6211\u3002\u201d\u5f3a\u76d7\u6218\u6218\u5162\u5162\u5730\u8ba8\u4ef7\u8fd8\u4ef7\uff0c\u4ed6\u770b\u51fa\u8fd9\u4e2a\u5c11\u5973\u4f3c\u4e4e\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u81f3\u5c11\u662f\u4e2a\u6b63\u4e49\u4eba\u58eb\u3002</p><p>\u743c\u6069\u5728\u65c1\u8fb9\u4ee5\u601c\u60af\u7684\u773c\u5149\u770b\u7740\u5f3a\u76d7\u3002</p><p>\u201c\u6211\u4fdd\u8bc1\u3002\u201d\u6885\u83f2\u65af\u7acb\u523b\u56de\u7b54\u3002</p><p>\u5f3a\u76d7\u5982\u8499\u5927\u8d66\uff0c\u7acb\u523b\u628a\u6240\u77e5\u9053\u7684\u60c5\u51b5\u5168\u90fd\u4e00\u4e94\u4e00\u5341\u5766\u767d\u51fa\u6765\u3002\u5f53\u4ed6\u8bf4\u5b8c\u6700\u540e\u4e00\u4e2a\u5b57\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u201c\u8c22\u8c22\u3002\u201d\u5979\u8bf4\uff0c\u4e00\u5251\u6325\u51fa\uff0c\u5f3a\u76d7\u7684\u8111\u888b\u5c31\u5495\u549a\u6eda\u843d\u5728\u5730\uff0c\u4e24\u773c\u5706\u7741\uff0c\u6b7b\u4e0d\u7791\u76ee\u3002</p><p>\u8fd9\u79cd\u573a\u666f\u89c1\u5f97\u591a\u4e86\uff0c\u743c\u6069\u4e5f\u5c31\u4e60\u4ee5\u4e3a\u5e38\uff0c\u53ea\u662f\u4ed6\u8fd8\u662f\u6709\u4e9b\u5947\u602a\u3002\u7ec8\u4e8e\u6709\u4e00\u6b21\uff0c\u4ed6\u63d0\u51fa\u4e86\u7591\u95ee\u3002</p><p>\u201c\u5176\u5b9e\u4f60\u6ca1\u5fc5\u8981\u8fd9\u6837\u554a\uff0c\u201d\u5deb\u5e08\u5efa\u8bae\uff0c\u201c\u4f60\u5b8c\u5168\u53ef\u4ee5\u5728\u4e0d\u6b3a\u9a97\u4ed6\u4eec\u7684\u524d\u63d0\u4e0b\u8fbe\u5230\u76ee\u7684\u7684\u3002\u201d</p><p>\u6885\u83f2\u65af\u9759\u9759\u7b49\u5f85\u7740\u4e0b\u6587\u3002</p><p>\u201c\u6bd4\u5982\u8bf4\uff0c\u4f60\u53ef\u4ee5\u5bf9\u4ed6\u4eec\u8bf4\uff1a\u2018\u5982\u679c\u4f60\u4eec\u8001\u5b9e\u56de\u7b54\u51fa\u6211\u6240\u6709\u7684\u95ee\u9898\uff0c\u6211\u5c31\u9976\u4e86\u4f60\u4eec\uff0c\u5426\u5219\u5c31\u6740\u3002\u2019\u7136\u540e\u7b49\u4f60\u95ee\u5b8c\u60f3\u8981\u7684\u95ee\u9898\uff0c\u518d\u95ee\u4e00\u53e5\uff1a\u5929\u751f\u6709\u591a\u5c11\u9897\u661f\u661f\u2014\u2014\u4ed6\u4eec\u80af\u5b9a\u7b54\u4e0d\u51fa\uff0c\u7136\u540e\u4f60\u5c31\u53ef\u4ee5\u540d\u6b63\u8a00\u987a\u5730\u52a8\u624b\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u7684\u78a7\u7eff\u773c\u7738\u91cc\u6cdb\u7740\u5fae\u5fae\u51b7\u7b11\u3002</p><p>\u201c\u6587\u5b57\u6e38\u620f\uff0c\u8bed\u8a00\u8bef\u5bfc\uff0c\u8fd9\u662f\u9b54\u9b3c\u7684\u5c0f\u628a\u620f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u662f\u5723\u6b66\u58eb\uff0c\u4e0d\u5c51\u4e8e\u5982\u6b64\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\uff0c\u201c\u4f46\u4f60\u73b0\u5728\u8fd9\u4e48\u505a\u4e0d\u662f\u660e\u663e\u7684\u6b3a\u9a97\u4e48\uff1f\u201d</p><p>\u201c\u4f60\u53ef\u4ee5\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u201d\u6885\u83f2\u65af\u7406\u6240\u5f53\u7136\u5730\u56de\u7b54\uff0c\u201c\u7b28\u86cb\u624d\u4f1a\u76f8\u4fe1\u6211\u4f1a\u771f\u7684\u7b54\u5e94\u3002\u201d</p><p>\u5deb\u5e08\u89c9\u5f97\u6709\u4e00\u79cd\u6df1\u6df1\u7684\u65e0\u529b\u611f\uff0c\u201c\u4f46\u4ed6\u4eec\u4e0d\u77e5\u9053\u554a\u3002\u201d\u4ed6\u52c9\u5f3a\u8bf4\u3002</p><p>\u201c\u90a3\u662f\u4ed6\u4eec\u7b28\u3002\u201d</p><p>\u201c\u4f46\u4e16\u754c\u4e0a\u672c\u6765\u5c31\u6709\u4eba\u806a\u660e\u6709\u4eba\u7b28\u2026\u2026\u8fd9\u4e0d\u7b97\u4ec0\u4e48\u9519\u5427\u3002\u201d</p><p>\u5c11\u5973\u5fae\u5fae\u70b9\u5934\uff0c\u201c\u7b28\u4e0d\u662f\u9519\uff0c\u4f46\u65e2\u7b28\u8fd8\u53c8\u6562\u51fa\u6765\u5f53\u5f3a\u76d7\uff0c\u90a3\u4e0d\u5c31\u662f\u81ea\u5df1\u627e\u6b7b\u4e48\uff1f\u201d</p><p>\u743c\u6069\u65e0\u8a00\u53ef\u7b54\uff0c\u4ed6\u53ea\u80fd\u5e86\u5e78\u81ea\u5df1\u867d\u7136\u4e0d\u662f\u4e2a\u597d\u4eba\uff0c\u5374\u4f3c\u4e4e\u8fd8\u806a\u660e\u3002</p><p>\u203b\u203b\u203b</p><p>\u5927\u7ea6\u5728\u8def\u4e0a\u8d70\u4e86\u8fd1\u4e00\u4e2a\u6708\uff0c\u4e24\u4eba\u7ec8\u4e8e\u5230\u8fbe\u4e86\u535a\u5f97\u4e4b\u95e8\u3002\u5f53\u743c\u6069\u8d70\u8fdb\u57ce\u95e8\u7684\u65f6\u5019\uff0c\u4ed6\u90fd\u6709\u4e9b\u4e0d\u6562\u76f8\u4fe1\u8fd9\u5c31\u662f\u90a3\u5ea7\u4f20\u8bf4\u4e2d\u7684\u8d38\u6613\u5927\u90fd\u5e02\u3002\u8857\u9053\u4e0a\u8352\u51c9\u51b7\u6e05\uff0c\u5bb6\u5bb6\u6237\u6237\u5173\u95e8\u95ed\u6237\uff0c\u552f\u4e00\u80fd\u770b\u89c1\u7684\u4eba\u5f71\uff0c\u662f\u90a3\u4e9b\u5de1\u903b\u7684\u58eb\u5175\uff0c\u4ed6\u4eec\u6234\u7740\u7684\u5934\u76d4\u662f\u9ed1\u8272\u7684\uff0c\u4e24\u8fb9\u9576\u7740\u7ea2\u8272\u6761\u7eb9\u3002\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u8fd9\u662f\u535a\u5f97\u4e4b\u95e8\u7684\u536b\u5175\u7279\u6709\u88c5\u626e\u3002</p><p>\u5230\u5904\u90fd\u5b89\u9759\u5f97\u53ef\u6015\u3002</p><p>\u4e0d\u4f1a\u4eba\u90fd\u5df2\u7ecf\u6b7b\u5149\u4e86\u5427\u3002\u743c\u6069\u5fc3\u91cc\u60f3\uff0c\u4e0b\u610f\u8bc6\u5730\u770b\u4e86\u770b\u6885\u83f2\u65af\u80cc\u4e0a\u80cc\u7740\u7684\u9ece\u660e\u4e4b\u77f3\uff0c\u53d1\u73b0\u5973\u5723\u6b66\u58eb\u5df2\u7ecf\u8fc8\u5f00\u811a\u6b65\u3002</p><p>\u201c\u6211\u4eec\u53bb\u54ea\u91cc\uff1f\u201d\u743c\u6069\u8fde\u5fd9\u8ddf\u4e0a\uff0c\u95ee\u3002</p><p>\u201c\u5e02\u653f\u5e9c\u3002\u201d\u5c11\u5973\u7b80\u77ed\u5730\u56de\u7b54\u3002</p></article>"}}}]);
