(window.webpackJsonp=window.webpackJsonp||[]).push([[861],{884:function(p){p.exports={chapterTitle:null,title:"\u7b2c686\u7ae0 \u653f\u4f01\u5173\u7cfb",content:"<article><h2>\u7b2c686\u7ae0 \u653f\u4f01\u5173\u7cfb</h2><p>\u3000\u3000\u5f20\u606a\u5341\u4e09\u65e5\u5269\u706b\u8f66\u8fd4\u56de\u5efa\u90ba\uff0c\u521a\u4e0b\u706b\u8f66\u5c31\u7ed9\u59da\u6587\u76db\u5e26\u7740\u5e02\u59d4\u7684\u5c0f\u8f66\u622a\u4f4f\u53bb\u89c1\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\uff0c\u521a\u5230\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u529e\u516c\u5ba4\u5927\u697c\uff0c\u5c31\u63a5\u5230\u674e\u8fdc\u6e56\u79d8\u4e66\u9ad8\u771f\u7684\u7535\u8bdd\uff0c\u674e\u8fdc\u6e56\u77e5\u9053\u4ed6\u4eca\u5929\u56de\u5efa\u90ba\uff0c\u4e5f\u6025\u7740\u60f3\u89c1\u4ed6\u3002</p><p>\u3000\u3000\u7f57\u541b\u6bd5\u7adf\u8981\u5c0a\u91cd\u674e\u8fdc\u6e56\u662f\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\uff0c\u542c\u5f20\u606a\u8bf4\u660e\u60c5\u51b5\u4e4b\u540e\uff0c\u4e5f\u6ca1\u6709\u62ff\u634f\u4ec0\u4e48\uff0c\u5c31\u4e00\u540c\u4e58\u8f66\u53bb\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u51b3\u5b9a\u5927\u80c6\u800c\u7280\u5229\uff0c\u4e14\u4e0d\u7ba1\u5bf9\u9526\u6e56\u672a\u6765\u4f1a\u4ea7\u751f\u600e\u6837\u7684\u5f71\u54cd\uff0c\u4f46\u5bf9\u5efa\u90ba\u5e02\uff0c\u5bf9\u4e1c\u6d77\u7701\u7684\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u5c06\u6709\u7acb\u7aff\u89c1\u5f71\u7684\u4fc3\u8fdb\u4f5c\u7528\u3002\u8fd9\u51e0\u5929\u5230\u9ad8\u65b0\u533a\u54a8\u8be2\u6ce8\u518c\u7684\u4f01\u4e1a\u4e0d\u8ba1\u5176\u6570\uff0c\u5fc3\u6025\u7684\u516c\u53f8\u751a\u81f3\u5df2\u7ecf\u5f00\u59cb\u6295\u8d44\u6ce8\u518c\u516c\u53f8\u4e86\u3002</p><p>\u3000\u3000\u5728\u5f20\u606a\u4e0e\u7f57\u541b\u8fd8\u6ca1\u6709\u5230\u6765\u4e4b\u524d\uff0c\u674e\u8fdc\u6e56\u53cc\u624b\u53cd\u526a\u5728\u8eab\u540e\uff0c\u770b\u7740\u7a97\u5916\u7684\u6811\u5f71\u5a46\u5a11\u3002</p><p>\u3000\u3000\u5386\u6570\u8fd9\u4e00\u5e74\u6765\u65f6\u95f4\u91cc\uff0c\u9526\u6e56\u6709\u8fc7\u54ea\u4e9b\u60ca\u4e16\u9a87\u4fd7\u7684\u4e3e\u63aa\u3002</p><p>\u3000\u3000\u4e09\u6708\uff0c\u9526\u6e56\u575a\u51b3\u7684\u62b5\u5236\u4ed3\u4fc3\u4e0a\u5236\u6d46\u9879\u76ee\uff0c\u5373\u4f7f\u662f\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u7684\u8584\u9762\u90fd\u4e0d\u80fd\u8ba9\u4ed6\u4eec\u504f\u79bb\u65e2\u5b9a\u7684\u8f68\u9053\uff0c\u8fd9\u70b9\u8ba9\u674e\u8fdc\u6e56\u6709\u6240\u53d7\u632b\uff0c\u4ed6\u751a\u81f3\u8ba4\u4e3a\u9526\u6e56\u5c31\u662f\u5728\u62b5\u89e6\u4ed6\uff0c\u7684\u786e\u4e5f\u60f3\u6572\u5c71\u9707\u864e\u6765\u7740\u3002\u4e94\u6708\u521d\uff0c\u7231\u8fbe\u7535\u5b50\u5230\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u501f\u58f3\u4e0a\u5e02\uff0c\u5957\u73b0\u56db\u5341\u4ebf\u3002\u5012\u4e0d\u662f\u8bf4\u9526\u6e56\u638c\u63e1\u5982\u6b64\u5e9e\u5927\u7684\u8d22\u5bcc\uff0c\u800c\u662f\u5f53\u65f6\u5904\u4e8e\u9999\u6e2f\u56de\u5f52\u524d\u5915\uff0c\u5bf9\u9526\u6e56\u6572\u5c71\u9707\u864e\uff0c\u8bf4\u4e0d\u5b9a\u4f20\u5230\u9999\u6e2f\u5c31\u6210\u8d1f\u9762\u65b0\u95fb\uff0c\u674e\u8fdc\u6e56\u624d\u9690\u5fcd\u4e0b\u6765\u4e0e\u9526\u6e56\u6682\u65f6\u548c\u89e3\u3002</p><p>\u3000\u3000\u4e94\u6708\uff0c\u542f\u52a8\u5341\u4ebf\u5143\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u76f4\u63a5\u4fc3\u52a8\u5efa\u90ba\u5e02\u3001\u4e1c\u6d77\u7701\u4e0a\u9a6c\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u5341\u6708\u53c8\u4ee5\u8d8a\u79c0\u63a7\u80a1\u7684\u540d\u4e49\u542f\u52a8\u540d\u4e3a\u4e2d\u6676\u8587\u82af\u7684\u767e\u4ebf\u5143\u6676\u5706\u5382\u9879\u76ee\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u674e\u8fdc\u6e56\u5df2\u7ecf\u660e\u767d\u9009\u62e9\u4e0e\u9526\u6e56\u901a\u529b\u5408\u4f5c\uff0c\u624d\u662f\u7b26\u5408\u81ea\u5df1\u653f\u6cbb\u524d\u9014\u7684\u9009\u62e9\uff0c\u9526\u6e56\u8fd9\u6b21\u53c8\u5728\u5317\u4eac\u5531\u8fd9\u51fa\uff0c\u7b80\u76f4\u8ba9\u4eba\u60ca\u559c\u4e86\u3002</p><p>\u3000\u3000\u9ad8\u771f\u6572\u95e8\u8fdb\u6765\u544a\u8bc9\u674e\u8fdc\u6e56\uff0c\u7f57\u541b\u3001\u5f20\u606a\u7684\u8f66\u5df2\u7ecf\u8fdb\u4e86\u505c\u8f66\u573a\uff0c\u79d8\u4e66\u957f\u9646\u6587\u592b\u53bb\u8fce\u63a5\u4e86\u3002\u867d\u7136\u4ed6\u770b\u4e0d\u5f97\u5f20\u606a\u5c11\u5e74\u5f97\u610f\u7684\u56a3\u6a2a\uff0c\u4f46\u8fd9\u65f6\u5019\u8fd8\u662f\u8981\u5c06\u8fd9\u6837\u7684\u5fc3\u601d\u6536\u655b\u8d77\u6765\uff0c\u4e0d\u6562\u968f\u610f\u5728\u674e\u8fdc\u6e56\u9762\u524d\u6cc4\u9732\u534a\u70b9\u5bf9\u9526\u6e56\u7684\u4e0d\u6ee1\u3002\u4ed6\u51ed\u4ec0\u4e48\u5bf9\u9526\u6e56\u4e0d\u6ee1\uff1f\u53c8\u6ca1\u6709\u4e1a\u52a1\u4e0a\u7684\u8054\u7cfb\uff0c\u674e\u8fdc\u6e56\u53ef\u4e0d\u662f\u4ec0\u4e48\u7b28\u86cb\uff0c\u5f00\u53e3\u8bf4\u8bdd\u4e4b\u524d\u5c31\u7b97\u4ed4\u7ec6\u7422\u78e8\uff0c\u90fd\u672a\u5fc5\u80fd\u7792\u8fc7\u674e\u8fdc\u6e56\u7280\u5229\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u90e8\u59d4\u5c31\u6709\u4e0d\u5c11\u7535\u5b50\u4f01\u4e1a\u5728\u5efa\u90ba\uff0c\u9526\u6e56\u7684\u51b3\u5b9a\u5f88\u60ca\u4eba\uff0c\u641e\u5f97\u5f88\u591a\u4eba\u90fd\u5c06\u7eb8\u6761\u76f4\u63a5\u9012\u5230\u6211\u90a3\u91cc\u4e86\u3002\u4e0d\u8981\u8bf4\u8fd9\u4e9b\u90e8\u59d4\u4f01\u4e1a\uff0c\u5efa\u90ba\u5730\u65b9\u4e0a\u7684\u7535\u5b50\u4f01\u4e1a\u4e5f\u4e0d\u5c11\uff0c\u90fd\u7ed9\u6211\u538b\u7740\u4e86\uff0c\u4e0d\u80fd\u8ba9\u8fd9\u4e9b\u70e6\u788e\u7684\u4e8b\u60c5\u53bb\u5e72\u6270\u9526\u6e56\u7684\u51b3\u7b56\u3002\u201d\u7f57\u541b\u8fb9\u4e0a\u53f0\u9636\uff0c\u8fb9\u4e0e\u5f20\u606a\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u542c\u4e86\u5fae\u5fae\u4e00\u7b11\uff0c\u8981\u4e0d\u662f\u770b\u5230\u4ed6\u7684\u773c\u775b\u5728\u95ea\u70c1\uff0c\u8fd8\u771f\u5f53\u4ed6\u771f\u5fc3\u5b9e\u610f\u7684\u5728\u8bf4\u8fd9\u4e8b\uff0c\u5fc3\u60f3\u8981\u771f\u4e0d\u60f3\u5e72\u6270\u9526\u6e56\u7684\u51b3\u5b9a\uff0c\u8fd9\u4e9b\u4e8b\u7d22\u6027\u8fde\u63d0\u90fd\u4e0d\u8981\u63d0\u3002\u96be\u9053\u5802\u5802\u7684\u5efa\u90ba\u5e02\u59d4\u4e66\u8fd8\u8981\u9080\u529f\u4e0d\u6210\uff1f</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5e72\u6270\u4e0d\u4e86\u9526\u6e56\u3002\u6240\u6709\u7684\u4e8b\uff0c\u90fd\u4f1a\u4ea4\u7ed9\u4e00\u4e2a\u7c7b\u4f3c\u5ba1\u67e5\u59d4\u5458\u4f1a\u7684\u4e13\u95e8\u673a\u6784\u53bb\u6267\u884c\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u8fd8\u662f\u89c1\u5230\u674e\u7701\u957f\u4e4b\u540e\u4e00\u8d77\u8be6\u7ec6\u89e3\u91ca\u5427\uff0c\u8fd9\u91cc\u9762\u7684\u5173\u7cfb\uff0c\u7ad9\u5728\u8fd9\u91cc\u8981\u89e3\u91ca\u6e05\u695a\uff0c\u5c11\u4e0d\u4e86\u8981\u803d\u6401\u5c0f\u534a\u5c0f\u65f6\u7684\u65f6\u95f4\u2026\u2026\u201d\u4e0e\u7f57\u541b\u5e76\u80a9\u8d70\u8fdb\u674e\u8fdc\u6e56\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u5f90\u6c34\u5e73\u8d70\u540e\uff0c\u674e\u8fdc\u6e56\u6ca1\u6709\u642c\u8fdb\u5f90\u6c34\u5e73\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u53ea\u662f\u8ba9\u4eba\u5c06\u95e8\u5916\u7684\u94ed\u724c\u6362\u4e86\u4e00\u4e0b\uff0c\u4ed6\u8fd8\u7559\u5728\u4ed6\u539f\u5148\u62c5\u4efb\u5e38\u52a1\u526f\u7701\u957f\u7684\u529e\u516c\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u8bf7\u5f20\u606a\u3001\u7f57\u541b\u5750\u5230\u4ed6\u529e\u516c\u5ba4\u4e00\u89d2\u7684\u6c99\u53d1\uff0c\u4ed6\u4e5f\u968f\u610f\u7684\u5750\u4e0b\u6765\uff0c\u5c31\u8feb\u4e0d\u6025\u5207\u7684\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u516c\u5e03\u7684\u6280\u672f\u5217\u8868\uff0c\u6211\u5df2\u7ecf\u8bf7\u4e00\u4e9b\u4e13\u5bb6\u770b\u8fc7\uff0c\u5927\u5bb6\u90fd\u5f88\u8d5e\u8d4f\u9526\u6e56\u7684\u884c\u4e3a\uff0c\u6211\u60f3\u542c\u542c\u9526\u6e56\u62df\u51fa\u5177\u4f53\u7684\u6280\u672f\u516c\u5f00\u65b9\u6848\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u9ad8\u771f\u4e00\u773c\uff0c\u6653\u5f97\u4ed6\u4e0e\u8c22\u5bb6\u8d70\u5f97\u4eb2\u70ed\uff0c\u4f46\u662f\u6ca1\u6709\u4e8b\u5b9e\u7684\u8bc1\u636e\uff0c\u518d\u8bf4\u8fd9\u6839\u672c\u7b97\u4e0d\u4e0a\u4ec0\u4e48\uff0c\u8c01\u90fd\u6709\u7ed3\u8bc6\u4ed6\u4eba\u7684\u6743\u5229\uff0c\u8d70\u5f97\u4eb2\u70ed\uff0c\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u56e0\u6b64\u53d7\u5230\u6307\u8d23\u3002\u5f53\u7136\u4e0e\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u6628\u5929\u4e0b\u5348\u624d\u8fbe\u6210\u7684\u7edf\u4e00\u610f\u89c1\u4e5f\u6ca1\u6709\u5fc5\u8981\u7edd\u5bf9\u4fdd\u5bc6\uff0c\u53ea\u8981\u5bf9\u5a92\u4f53\u4fdd\u6301\u4e9b\u60ac\u5ff5\u4e86\uff0c\u6709\u4e9b\u6d88\u606f\u4ece\u5c0f\u9053\u4f20\u51fa\u6765\uff0c\u4f1a\u66f4\u52a0\u7684\u5f15\u8d77\u60ac\u5ff5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u5728\u610f\u9ad8\u771f\u5728\u573a\uff0c\u5c06\u56db\u5bb6\u516c\u53f8\u6628\u5929\u4e0b\u5348\u624d\u5f62\u6210\u7edf\u4e00\u610f\u89c1\u7684\u4e8b\u60c5\u6c47\u62a5\u7ed9\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u542c\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u8bf4\uff0c\u5176\u4ed6\u4e09\u5bb6\u624b\u673a\u5382\u5546\u4e5f\u4f1a\u9009\u62e9\u5728\u4e1c\u6d77\u5efa\u5382\u3001\u5efa\u7814\u53d1\u673a\u6784\uff1f\u201d\u674e\u8fdc\u6e56\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5efa\u5382\u5012\u6ca1\u6709\u660e\u8bf4\uff0c\u7531\u5404\u5bb6\u81ea\u5df1\u8003\u8651\uff0c\u7814\u53d1\u673a\u6784\u80af\u5b9a\u8981\u5efa\u5728\u5efa\u90ba\u6216\u8005\u662f\u6d77\u5dde\uff0c\u6211\u770b\u8fd8\u662f\u5efa\u5728\u5efa\u90ba\u5c45\u591a\uff0c\u6bd5\u7adf\u5efa\u90ba\u5728\u6280\u672f\u4e0e\u4eba\u529b\u8d44\u6e90\u4e0a\u7684\u4f18\u52bf\uff0c\u8fd8\u4e0d\u662f\u6d77\u5dde\u80fd\u5ab2\u7f8e\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6280\u672f\u516c\u5f00\u65b9\u6848\u91cc\uff0c\u6d77\u5dde\u4e0e\u5efa\u90ba\u7684\u91cd\u70b9\u5e94\u8be5\u8981\u9519\u5f00\u4e9b\uff0c\u907f\u514d\u4e24\u5730\u7684\u4ea7\u4e1a\u96c6\u7fa4\u540c\u8d28\u5316\u592a\u4e25\u91cd\uff0c\u8fd9\u65b9\u9762\u8fd8\u9700\u8981\u5efa\u90ba\u4e0e\u6d77\u5dde\u534f\u8c03\u4e00\u4e0b\uff0c\u5982\u4f55\u5c06\u516c\u5f00\u7684\u6280\u672f\u5f52\u7c7b\u5212\u5206\uff0c\u9002\u5408\u5efa\u90ba\u7684\uff0c\u5c31\u91cd\u70b9\u6388\u6743\u7ed9\u5efa\u90ba\u9ad8\u65b0\u533a\u7684\u4f01\u4e1a\uff0c\u9002\u5408\u6d77\u5dde\u7684\uff0c\u5c31\u91cd\u70b9\u6388\u6743\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u7684\u4f01\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u9ad8\u771f\u88c5\u4f5c\u8ba4\u771f\u5b66\u4e60\u7684\u6837\u5b50\uff0c\u5fc3\u91cc\u542c\u5f97\u5374\u6697\u6697\u5fc3\u60ca\uff0c\u5fc3\u60f3\uff1a\u8fd9\u4ee5\u540e\u5728\u4e1c\u6d77\u8fd8\u6709\u8c01\u6709\u80fd\u529b\u6273\u5012\u9526\u6e56\uff1f\u8c22\u5bb6\u6b63\u6cf0\u96c6\u56e2\u53ea\u6015\u662f\u671b\u5c18\u83ab\u53ca\u4e86\u3002\u80fd\u7ed9\u674e\u8fdc\u6e56\u5f53\u79d8\u4e66\uff0c\u603b\u4e0d\u4f1a\u662f\u611a\u7b28\u5982\u732a\u7684\u8822\u6750\uff0c\u8ba9\u4ed6\u4eec\u770b\u4e0a\u53bb\u611a\u8822\u7684\uff0c\u800c\u662f\u8df3\u4e0d\u8131\u6743\u6b32\u3001\u91d1\u94b1\u4e0e\u8d22\u5bcc\u7684\u5708\u5957\u7f62\u4e86\u3002\u9526\u6e56\u518d\u98ce\u5149\uff0c\u81ea\u5df1\u53c8\u4eab\u53d7\u4e0d\u5230\u4e00\u70b9\u597d\u5904\uff0c\u8fd8\u4e0d\u5982\u4e0e\u8c22\u5bb6\u3001\u4e0e\u6797\u96ea\u591a\u4eb2\u8fd1\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u53bb\u7ba1\u9ad8\u771f\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u5c06\u4e00\u4e9b\u5177\u4f53\u60c5\u51b5\u8ddf\u674e\u8fdc\u6e56\u3001\u7f57\u541b\u6c47\u62a5\u8fc7\uff0c\u53c8\u8ba8\u8bba\u4e86\u4e00\u4e9b\u5982\u4f55\u53bb\u63a8\u52a8\u7701\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\u5f80\u524d\u53d1\u5c55\u7684\u8bdd\u9898\uff0c\u4e5f\u8c08\u5230\u5efa\u90ba\u5e02\u4e0e\u7701\u91cc\u6d3e\u4ee3\u8868\u52a0\u5165\u5ba1\u67e5\u59d4\u5458\u4f1a\u7684\u4e8b\u60c5\uff1b\u6b64\u5916\uff0c\u8fd8\u8981\u6d77\u5dde\u5e02\u91cc\u6d3e\u51fa\u4ee3\u8868\u3002</p><p>\u3000\u3000\u8c08\u5230\u8fd9\u91cc\uff0c\u9ad8\u771f\u88c5\u4f5c\u65e0\u610f\u7684\u5934\u5f80\u524d\u51d1\u4e86\u51d1\uff0c\u5e45\u5ea6\u6ca1\u6709\u5f88\u5927\uff0c\u4f46\u5df2\u7ecf\u5438\u8d77\u522b\u4eba\u7684\u6ce8\u610f\u3002\u5f20\u606a\u7709\u5934\u6324\u7740\uff0c\u770b\u5411\u522b\u5904\u3002</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u91cc\u5012\u597d\u529e\uff0c\u59da\u6587\u76db\u4e4b\u524d\u6709\u5728\u90ae\u7535\u5c40\u7535\u4fe1\u653f\u52a1\u53f8\u7684\u5de5\u4f5c\u7ecf\u9a8c\uff0c\u6709\u7740\u6280\u672f\u80cc\u666f\uff0c\u4e5f\u7684\u786e\u627e\u4e0d\u5230\u6bd4\u4ed6\u66f4\u5408\u9002\u7684\u4eba\u9009\u4e86\u3002</p><p>\u3000\u3000\u674e\u8fdc\u6e56\u773c\u775b\u77a5\u8fc7\u9ad8\u771f\uff0c\u77e5\u9053\u8981\u662f\u5c06\u9ad8\u771f\u786c\u585e\u5230\u5ba1\u67e5\u59d4\u5458\u4f1a\u91cc\u6307\u671b\u4ed6\u80fd\u505a\u4e9b\u4ec0\u4e48\uff0c\u603b\u4e0d\u80fd\u767d\u6302\u4e00\u4e2a\u540d\u5427\uff1f\u8ddf\u9646\u6587\u592b\u8bf4\u9053\uff1a\u201c\u4f60\u8bb0\u4e00\u4e0b\uff0c\u770b\u8fd9\u4e8b\u662f\u4e0d\u662f\u8bf7\u5206\u7ba1\u7684\u80e1\u526f\u7701\u957f\u63a8\u8350\u4e00\u4e0b\u4eba\u9009\u5408\u9002\uff1f\u201d</p><p>\u3000\u3000\u4e0e\u674e\u8fdc\u6e56\u7684\u8fd9\u6b21\u89c1\u9762\u53ef\u8c13\u5bbe\u4e3b\u5c3d\u6b22\uff0c\u8fd8\u7559\u5728\u7701\u653f\u5e9c\u7684\u5c0f\u98df\u5802\u91cc\u7528\u8fc7\u9910\u624d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u5e0c\u671b\u5efa\u90ba\u4e0e\u6d77\u5dde\u5728\u53d1\u5c55\u624b\u673a\u4ea7\u4e1a\u65f6\u8981\u6709\u6240\u4fa7\u91cd\uff0c\u5728\u6d77\u5dde\uff0c\u6bd5\u7adf\u6ca1\u6709\u51e0\u6240\u9ad8\u7b49\u9662\u6821\uff0c\u5e08\u8d44\u529b\u91cf\u4e5f\u9887\u4e3a\u4e00\u822c\uff0c\u53ef\u4e0d\u8981\u6307\u671b\u6d77\u5dde\u5927\u5b66\u80fd\u4e3a\u9526\u6e56\u7684\u53d1\u5c55\u6e90\u6e90\u4e0d\u65ad\u7684\u8f93\u9001\u5927\u89c4\u6a21\u7684\u5408\u683c\u65b0\u5458\u5de5\uff0c\u8981\u5c06\u5927\u91cf\u7684\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u62db\u8058\u5230\u8fd8\u6b63\u5904\u4e8e\u7ecf\u6d4e\u4e95\u55b7\u524d\u671f\u7684\u6d77\u5dde\uff0c\u96be\u5ea6\u8fd8\u662f\u6709\u4e00\u4e9b\u7684\uff1b\u90a3\u5c31\u4e24\u5730\u8fdb\u884c\u5206\u5de5\u597d\u4e86\u3002\u79bb\u5f00\u7701\u653f\u5e9c\u4e4b\u5916\uff0c\u5f20\u606a\u4e0e\u7f57\u541b\u5c31\u4e00\u76f4\u5728\u8c08\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u90fd\u6ca1\u6709\u65f6\u95f4\u505c\u987f\u4e00\u4e0b\u56de\u4e00\u8d9f\u5b66\u6821\uff0c\u4e0e\u7f57\u541b\u5206\u5f00\u4e4b\u540e\uff0c\u4e0b\u5348\u53c8\u76f4\u63a5\u4e58\u8f66\u56de\u6d77\u5dde\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0e\u6d77\u5dde\u5e02\u91cc\u7684\u6c9f\u901a\u5f88\u7545\u901a\uff0c\u8be6\u7ec6\u7684\u60c5\u51b5\uff0c\u5df2\u7ecf\u7531\u4e01\u69d0\u5411\u5510\u5b66\u8c26\u7b49\u4eba\u8fdb\u884c\u4e86\u8bf4\u660e\u3002</p><p>\u3000\u3000\u6d77\u5dde\u7684\u4f01\u4e1a\u8fde\u7eed\u4e24\u5c4a\u593a\u4e0b\u6807\u738b\u5e7f\u544a\uff0c\u53c8\u8fde\u7eed\u4e24\u6b21\u5728\u6d77\u5dde\u53ec\u5f00\u58f0\u52bf\u6d69\u5927\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u3002</p><p>\u3000\u3000\u4e4b\u524d\u7231\u8fbe\u3001\u79d1\u738b\u65b0\u7b79\u5907\u8425\u9500\u4f53\u7cfb\uff0c\u51e0\u4e4e\u6240\u6709\u5bf9\u789f\u673a\u4ea7\u4e1a\u4ea7\u751f\u5174\u8da3\u7684\u7ecf\u9500\u5546\u90fd\u8d76\u5230\u6d77\u5dde\u6765\u4e89\u540d\u989d\u3002\u4eca\u5e74\uff0c\u52a0\u4e0a\u65b0\u5143\u7535\u5668\uff0c\u8425\u9500\u4f53\u7cfb\u90fd\u5df2\u7ecf\u786e\u5b9a\u5e76\u7a33\u5b9a\u4e0b\u6765\u4e86\uff0c\u7ecf\u9500\u5546\u8981\u4e0d\u662f\u7279\u6b8a\u60c5\u51b5\u4e5f\u4e0d\u4f1a\u8f7b\u6613\u6362\u6389\u3002</p><p>\u3000\u3000\u4e4b\u524d\u5927\u5bb6\u90fd\u9884\u6d4b\uff0c\u4eca\u5e74\u7684\u70ed\u95f9\u7a0b\u5ea6\u80af\u5b9a\u4e0d\u80fd\u8ddf\u53bb\u5e74\u76f8\u63d0\u5e76\u8bba\uff0c\u5374\u6ca1\u6709\u60f3\u9526\u6e56\u5728\u592e\u89c6\u6885\u5730\u4e9a\u4e2d\u5fc3\u629b\u51fa\u90a3\u6837\u7684\u51b3\u5b9a\uff0c\u5e76\u8bf4\u660e\u5177\u4f53\u65b9\u6848\u4f1a\u5728\u6d77\u5dde\u3001\u5728\u7ecf\u9500\u5546\u5927\u4f1a\u524d\u516c\u5e03\uff0c\u4ece\u5341\u4e09\u65e5\u8d77\uff0c\u5f80\u6d77\u5dde\u805a\u96c6\u7684\u7ecf\u9500\u5546\u4e0e\u7535\u5b50\u5382\u5546\u4e5f\u8d8a\u6765\u8d8a\u591a\uff0c\u770b\u60c5\u5f62\uff0c\u4eca\u5e74\u7684\u89c4\u6a21\u4e0d\u4f1a\u5dee\u8fc7\u53bb\u5e74\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u4ece\u53bb\u5e74\u8d77\uff0c\u7ecf\u9500\u5546\u5927\u4f1a\uff0c\u66f4\u591a\u662f\u5411\u7ecf\u9500\u5546\u5c55\u793a\u9526\u6e56\u6700\u65b0\u7684\u4ea7\u54c1\uff0c\u5f20\u606a\u8ba9\u6768\u4e91\u8fd9\u6b21\u4e5f\u5c06\u65b0\u5143\u7535\u5668\u62c9\u4e00\u6279\u8fdb\u6765\uff0c\u641e\u4e00\u4e2a\u5c0f\u578b\u7684\u4ea7\u54c1\u5c55\u793a\u4f1a\uff0c\u5e0c\u671b\u80fd\u7ef4\u6301\u4e0b\u53bb\u5e76\u9010\u6e10\u7684\u53bb\u6269\u5927\u89c4\u6a21\u4e0e\u5f71\u54cd\u529b\uff0c\u6700\u7ec8\u80fd\u5f62\u6210\u4e00\u4e2a\u8f83\u5927\u89c4\u6a21\u3001\u8f83\u5927\u5f71\u54cd\u7684\u5c55\u4f1a\u3002</p><p>\u3000\u3000\u79d1\u738b\u8fd9\u65f6\u5019\u6700\u4e3b\u8981\u7684\u4efb\u52a1\u662f\u7ef4\u6301\u7ecf\u9500\u5546\u3001\u6e20\u9053\u5546\u7684\u7a33\u5b9a\uff0c\u4e00\u65e6\u6e20\u9053\u89c4\u6a21\u840e\u7f29\uff0c\u4e4b\u524d\u501f\u7ed9\u79d1\u738b\u4f7f\u7528\u7684\u4fdd\u8bc1\u91d1\u89c4\u6a21\u4e5f\u52bf\u5fc5\u968f\u4e4b\u840e\u7f29\uff0c\u6ca1\u6709\u5176\u4ed6\u8d44\u91d1\u6765\u6e90\u652f\u6491\uff0c\u79d1\u738b\u8d44\u91d1\u94fe\u5c31\u6b64\u65ad\u88c2\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u6709\u8fd9\u4e48\u91cd\u7684\u5fc3\u4e8b\u538b\u5728\u5fc3\u4e0a\uff0c\u8c22\u5251\u5357\u4e0a\u5348\u53bb\u60e0\u5c71\uff0c\u5403\u4e86\u4e00\u987f\u4e2d\u996d\u5c31\u56de\u6d77\u5dde\uff0c\u8fd8\u662f\u4e0e\u5f20\u606a\u524d\u540e\u811a\u8d76\u5230\u76ca\u9686\u9547\u7535\u5b50\u5de5\u4e1a\u56ed\u533a\uff0c\u4ed6\u5374\u6ca1\u6709\u60f3\u5230\u4ed6\u521a\u8d76\u56de\u6d77\u5dde\uff0c\u9648\u9759\u5c31\u8ddf\u4ed6\u63d0\u51fa\u8981\u8f9e\u6389\u5979\u5728\u79d1\u738b\u6240\u6709\u7684\u884c\u653f\u804c\u52a1\u3002</p></article>"}}}]);
