(window.webpackJsonp=window.webpackJsonp||[]).push([[869],{892:function(p){p.exports={chapterTitle:null,title:"\u7b2c693\u7ae0 \u68cb\u5b50",content:"<article><h2>\u7b2c693\u7ae0 \u68cb\u5b50</h2><p>\u3000\u3000\u5341\u4e00\u6708\u5341\u516b\u65e5\uff0c\u9526\u6e56\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u4e0e\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u8054\u624b\u516c\u5e03\u624b\u673a\u5236\u9020\u6280\u672f\u516c\u5f00\u7684\u6700\u7ec8\u8be6\u7ec6\u65b9\u6848\u3002\u5c06\u7531\u76f8\u5173\u65b9\u7ec4\u5efa\u4e00\u4e2a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u673a\u6784\u6765\u5168\u6743\u8d1f\u8d23\uff0c\u53ea\u8981\u8be5\u534f\u4f1a\u4e0d\u8fdd\u80cc\u516c\u5f00\u7ea6\u5b9a\u7684\u51e0\u6761\u539f\u5219\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u5c06\u4f1a\u628a\u76f8\u5173\u6280\u672f\u6c38\u8fdc\u6388\u6743\u7ed9\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u3002</p><p>\u3000\u3000\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u540c\u65f6\u627f\u8bfa\uff0c\u9664\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7ea6\u5b9a\u7684\u6761\u4ef6\u4e4b\u540e\uff0c\u9526\u6e56\u4e0d\u4f1a\u9644\u52a0\u63d0\u51fa\u4efb\u4f55\u6761\u4ef6\uff0c\u5373\u4f7f\u5728\u8fd9\u4e9b\u516c\u5f00\u7684\u6570\u5b57\u624b\u673a\u5236\u9020\u6280\u672f\u4e0a\u884d\u751f\u51fa\u6765\u7684\u4e13\u5229\u6280\u672f\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u8981\u6c42\u4eab\u53d7\u4efb\u4f55\u6743\u76ca\u3002</p><p>\u3000\u3000\u5728\u8fd9\u4e2a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e2d\uff0c\u9996\u6b21\u786e\u5b9a\u7684\u6709\u6295\u7968\u6743\u7684\u4ee3\u8868\u53ea\u6709\u4e03\u5bb6\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u53ea\u7b97\u4e00\u5bb6\uff0c\u53ea\u62e5\u6709\u4e00\u7968\u6295\u7968\u6743\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u9526\u6e56\u662f\u5c06\u4ed6\u4eec\u82b1\u8d39\u4e5d\u725b\u4e8c\u864e\u4e4b\u529b\u4ece\u5fb7\u4eea\u624b\u4e2d\u5e76\u8d2d\u7684\u76f8\u5173\u6570\u5b57\u624b\u673a\u5236\u9020\u57fa\u7840\u6280\u672f\u5b8c\u5168\u8d21\u732e\u51fa\u6765\u4e86\u3002</p><p>\u3000\u3000\u5176\u4ed6\u516d\u5bb6\u4ee3\u8868\uff0c\u8054\u4fe1\u7535\u5b50\u7b97\u4e00\u5bb6\uff0c\u8054\u8baf\u7535\u5b50\u7b97\u4e00\u5bb6\uff0c\u4e1c\u5174\u7535\u5b50\u7b97\u4e00\u5bb6\uff0c\u5efa\u90ba\u5e02\u9ad8\u65b0\u533a\u7b97\u4e00\u5bb6\uff0c\u6d77\u5dde\u9526\u6e56\u5de5\u4e1a\u65b0\u533a\u7b97\u4e00\u5bb6\uff0c\u8fd8\u6709\u4e00\u5bb6\u4ee3\u8868\u5c31\u662f\u4ece\u8054\u4fe1\u7535\u5b50\u83b7\u5f97\u624b\u54c1\u724c\u4ee3\u5de5\u5305\u9500\u6388\u6743\u7684\u79d1\u738b\u9ad8\u79d1\u3002</p><p>\u3000\u3000\u4e0a\u4e00\u6b21\u83b7\u5f97\u5168\u90e8\u7684\u6280\u672f\u6388\u6743\uff0c\u5bf9\u79d1\u738b\u9ad8\u79d1\u6765\u8bf4\u662f\u4e00\u9879\u51fa\u4e4e\u610f\u6599\u7684\u60ca\u559c\uff0c\u8fd9\u4e00\u6b21\u5c06\u79d1\u738b\u9ad8\u79d1\u4f5c\u4e3a\u4e03\u5bb6\u4ee3\u8868\u4e4b\u4e00\u7eb3\u5165\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\uff0c\u9648\u9759\u4e5f\u662f\u5728\u5177\u4f53\u65b9\u6848\u516c\u5e03\u4e4b\u524d\u624d\u5f97\u5230\u901a\u77e5\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\u60c5\u8fd8\u662f\u8054\u4fe1\u7684\u5357\u65b9\u516c\u53f8\u603b\u7ecf\u7406\u5468\u5174\u4e1c\u6700\u5148\u7535\u8bdd\u901a\u77e5\u90c1\u840d\uff0c\u90c1\u840d\u591a\u5c11\u6709\u4e9b\u53d7\u5ba0\u82e5\u60ca\u2014\u2014\u8fd9\u91cc\u7684\u53d7\u5e9e\u82e5\u60ca\u4e5f\u4e0d\u662f\u53d7\u8054\u4fe1\u6216\u53d7\u5468\u5174\u4e1c\u7684\u5ba0\uff0c\u8c01\u90fd\u6653\u5f97\u80cc\u540e\u4fc3\u6210\u6b64\u4e8b\u662f\u8c01\uff0c\u5e94\u4ed8\u8fc7\u5468\u5174\u4e1c\uff0c\u90c1\u840d\u8ddf\u9648\u9759\u4e00\u672c\u6b63\u7ecf\u7684\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u732e\u8eab\u90fd\u503c\u5f97\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u6ca1\u6709\u732e\u8eab\u7684\u6253\u7b97\uff0c\u6253\u7535\u8bdd\u7ed9\u5f20\u606a\uff0c\u5e0c\u671b\u80fd\u8bf7\u4ed6\u62bd\u65f6\u95f4\u51fa\u6765\u4e00\u8d77\u5171\u8fdb\u5348\u9910\u6216\u665a\u9910\u7565\u5f53\u9762\u8868\u8fbe\u4e00\u4e0b\u611f\u8c22\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7535\u8bdd\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4e0d\u6028\u6211\u5c06\u4f60\u4eec\u62ff\u51fa\u6765\u5f53\u68cb\u5b50\u7528\uff0c\u5c31\u8c22\u5929\u8c22\u5730\u4e86\uff1b\u5f53\u7136\uff0c\u6709\u673a\u4f1a\u80fd\u8ba9\u4f60\u9080\u8bf7\u4e00\u8d77\u7528\u9910\uff0c\u6211\u662f\u6b7b\u6d3b\u90fd\u4e0d\u4f1a\u62d2\u7edd\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u6709\u4e9b\u4e0d\u5927\u660e\u767d\u5f20\u606a\u6240\u8bf4\u7684\u201c\u68cb\u5b50\u201d\u662f\u4f55\u610f\uff0c\u65e2\u7136\u8bf4\u662f\u8981\u5f53\u9762\u611f\u8c22\uff0c\u4e5f\u5c31\u4e0d\u7528\u5728\u7535\u8bdd\u91cc\u8ffd\u6839\u7a76\u5e95\u4e86\u3002</p><p>\u3000\u3000\u5341\u516b\u65e5\uff0c\u5f20\u606a\u53c8\u4e34\u65f6\u56de\u5efa\u90ba\u53bb\u4e86\uff1b\u65e2\u7136\u4f5c\u4e3a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u4e03\u5bb6\u4ee3\u8868\u4e4b\u4e00\uff0c\u79d1\u738b\u9ad8\u79d1\u7684\u4e8b\u52a1\u5219\u662f\u52a0\u500d\u7e41\u5fd9\u4e86\u3002\u79d1\u738b\u9ad8\u79d1\u4f5c\u4e3a\u4e00\u5bb6\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u6839\u57fa\u7684\u7535\u5b50\u4f01\u4e1a\uff0c\u7a81\u7136\u5c31\u5728\u4e1a\u5185\u83b7\u5f97\u5982\u6b64\u5f70\u663e\u7684\u5730\u4f4d\uff0c\u81ea\u7136\u4e5f\u627f\u53d7\u4e86\u76f8\u5f53\u5927\u7684\u538b\u529b\u3002\u5373\u4f7f\u7d27\u6025\u8ddf\u7236\u4eb2\u6c42\u6551\uff0c\u4ece\u5927\u96c5\u96c6\u56e2\u4e34\u65f6\u501f\u4e86\u4e00\u4e9b\u7cbe\u5175\u5f3a\u5c06\u8fc7\u6765\uff0c\u9648\u9759\u5374\u4e5f\u8981\u4e03\u624b\u516b\u811a\u7684\u5fd9\u4e71\u5f00\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u505c\u6b47\u4e0b\u6765\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u9664\u4e86\u5076\u5c14\u7684\u7535\u8bdd\u8054\u7edc\uff0c\u9648\u9759\u4e5f\u6ca1\u6709\u4e0e\u5f20\u606a\u89c1\u9762\u7684\u673a\u4f1a\uff0c\u4e00\u76f4\u5230\u4e8c\u5341\u4e09\u65e5\uff0c\u5728\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6b63\u5f0f\u6210\u7acb\u7684\u524d\u4e00\u5929\uff0c\u9648\u9759\u624d\u6709\u673a\u4f1a\u9047\u5230\u5f20\u606a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u867d\u7136\u8ba1\u5212\u4e2d\u4e8c\u5341\u4e94\u65e5\u662f\u7231\u8fbe\u96c6\u56e2\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u3001\u4e8c\u5341\u516d\u65e5\u662f\u79d1\u738b\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u3001\u65b0\u5143\u7535\u5668\u4e5f\u501f\u52bf\u8ba1\u5212\u5728\u4e8c\u5341\u4e03\u65e5\u4e3e\u529e\u7ecf\u9500\u5546\u5927\u4f1a\uff0c\u4f46\u662f\u4e09\u5bb6\u516c\u53f8\u5728\u5404\u5730\u90fd\u6709\u786e\u5b9a\u7684\u7ecf\u9500\u5546\uff0c\u53ec\u5f00\u7ecf\u9500\u5546\u5927\u4f1a\uff0c\u4e0d\u8fc7\u662f\u786e\u5b9a\u4e0b\u4e00\u5e74\u5ea6\u7684\u5408\u4f5c\u5408\u540c\u800c\u5df2\uff0c\u4ec5\u6709\u5c11\u90e8\u5206\u7684\u7ecf\u9500\u5546\u4f1a\u7ed9\u6dd8\u6c70\u6389\u6362\u4e0a\u65b0\u7684\u3002\u4e4b\u524d\u4e24\u5c4a\u90fd\u662f\u501f\u52a9\u592e\u89c6\u6807\u738b\u7684\u5149\u73af\u9020\u52bf\u5c06\u4ece\u4e8b\u7535\u5668\u9500\u552e\u7684\u5ba2\u5546\u90fd\u5438\u5f15\u5230\u6d77\u5dde\u6765\u4f9b\u6311\u9009\uff0c\u6240\u4ee5\u4ec5\u4ec5\u662f\u4ece\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u89c4\u6a21\u6765\u770b\uff0c\u4eca\u5e74\u4e09\u5bb6\u516c\u53f8\u7684\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u89c4\u6a21\u52a0\u8d77\u6765\uff0c\u4e5f\u662f\u8fdc\u8fdc\u65e0\u6cd5\u8ddf\u524d\u5e74\u6216\u53bb\u5e74\u7684\u6807\u738b\u7ecf\u9500\u540c\u76db\u4f1a\u76f8\u6bd4\u7684\uff0c\u4e0d\u8fc7\u4e8c\u5341\u4e94\u65e5\u4e4b\u524d\uff0c\u6d77\u5dde\u5e02\u5c31\u5df2\u7ecf\u70ed\u95f9\u975e\u5e38\uff0c\u4e1d\u6beb\u4e0d\u5f31\u4e8e\u5f80\u5e74\u3002</p><p>\u3000\u3000\u6570\u5b57\u624b\u673a\u57fa\u7840\u6280\u672f\u516c\u5f00\u65b9\u6848\u7684\u516c\u5e03\u4e0e\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u7acb\uff0c\u5c06\u56fd\u5185\u5176\u4ed6\u4f01\u4e1a\u7684\u987e\u8651\u5c31\u5b8c\u5168\u6253\u6d88\u6389\u4e86\u3002\u666e\u901a\u516c\u4f17\u6216\u8bb8\u8fd8\u4e0d\u662f\u7279\u522b\u5173\u6ce8\u6b64\u4e8b\uff0c\u4f46\u4e1a\u5185\u7684\u76ee\u5149\u51e0\u4e4e\u90fd\u6389\u5934\u770b\u5411\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u5728\u5341\u516b\u65e5\u6280\u672f\u516c\u5f00\u65b9\u6848\u516c\u5e03\u4e4b\u540e\uff0c\u51e0\u4e4e\u6240\u6709\u6709\u5fc3\u67d3\u6307\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u7684\u7535\u5b50\u5382\u5546\u90fd\u6d3e\u51fa\u4ee3\u8868\u62b5\u8fbe\u6d77\u5dde\u6216\u8005\u4e3a\u83b7\u5f97\u6280\u672f\u6388\u6743\u6216\u8005\u60f3\u51ed\u501f\u81ea\u8eab\u5728\u4e1a\u5185\u7684\u5f71\u54cd\u529b\u76f4\u63a5\u53c2\u4e0e\u5230\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u91cc\u53bb\u3002</p><p>\u3000\u3000\u8fdb\u5165\u4e8c\u5341\u65e5\u4e4b\u540e\uff0c\u6d77\u5dde\u7684\u5bbe\u9986\u90fd\u5df2\u7ecf\u7206\u6ee1\u3002</p><p>\u3000\u3000\u4e0d\u4ec5\u6709\u56fd\u5185\u5382\u5546\uff0c\u50cf\u4e09\u661f\u8fd9\u6837\u55c5\u5230\u9526\u6e56\u5a01\u80c1\u7684\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u534e\u516c\u53f8\u7684\u4ee3\u8868\uff0c\u4e5f\u5230\u6d77\u5dde\u6765\u89c2\u671b\u5f62\u52bf\u3002\u674e\u5728\u6d19\u4e00\u884c\u4eba\uff0c\u5728\u77e5\u9053\u90ae\u7535\u90e8\u90e8\u957f\u803f\u91cd\u9633\u4e0e\u90ae\u7535\u90e8\u7684\u5176\u4ed6\u5b98\u5458\u8fd8\u6709\u4e1c\u6d77\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u674e\u8fdc\u6e56\u53ca\u5efa\u90ba\u5e02\u59d4\u4e66\u8bb0\u7f57\u541b\u7b49\u4e00\u884c\u5730\u65b9\u653f\u5e9c\u5b98\u5458\u5c06\u5217\u5e2d\u4e8c\u5341\u56db\u65e5\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u63ed\u724c\u4eea\u5f0f\u7684\u6d88\u606f\u4e4b\u540e\uff0c\u4ed6\u4eec\u4e5f\u5728\u524d\u4e00\u5929\uff0c\u4e5f\u5c31\u662f\u4e8c\u5341\u4e09\u65e5\uff0c\u518d\u4e00\u6b21\u6765\u5230\u6d77\u5dde\u3002</p><p>\u3000\u3000\u9526\u6e56\u518d\u4e0d\u9700\u8981\u5411\u897f\u57ce\u996d\u5e97\u501f\u5bb4\u4f1a\u5385\u4f5c\u4e3a\u4e34\u65f6\u7684\u4ea7\u54c1\u9648\u5217\u5ba4\u4e86\uff0c\u5c06\u6c99\u7530\u897f\u7247\u4e94\u91cc\u6eaa\u5e7f\u573a\u4e00\u4fa7\u7684\u9526\u6e56\u5927\u53a6\u6574\u4f53\u4ece\u4e16\u7eaa\u9526\u6e56\u8d2d\u5165\u4e4b\u540e\uff0c\u5c31\u4f5c\u4e3a\u9526\u6e56\u63a7\u80a1\u53ca\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7684\u7814\u53d1\u4e2d\u5fc3\u9a7b\u5730\uff0c\u62ff\u51fa\u6574\u6574\u4e00\u5343\u591a\u5e73\u65b9\u7c73\u7684\u4e00\u5c42\u697c\u4f5c\u4e3a\u6d3b\u52a8\u573a\u5730\u3002</p><p>\u3000\u3000\u6295\u5f71\u4eea\u6295\u5c04\u51fa\u6765\u7684\u753b\u9762\u76f4\u63a5\u6295\u5c04\u5230\u4e00\u9762\u7c89\u5237\u5f97\u96ea\u767d\u7684\u5899\u4e0a\uff0c\u662f\u7231\u8fbe\u7535\u5b50\u7684\u7535\u89c6\u5e7f\u544a\uff0c\u753b\u9762\u4e0a\u6c5f\u9edb\u513f\u4ece\u67d3\u7740\u9752\u82d4\u7684\u77f3\u5f84\u4e0a\u62fe\u9636\u800c\u4e0a\uff0c\u4ee5\u7a7a\u95f4\u4ee3\u8868\u65f6\u95f4\u7684\u8f6c\u6362\u8868\u73b0\u8c61\u5c71\u7f8e\u666f\u9ec4\u680c\u6811\u6797\u4ece\u5165\u79cb\u5230\u971c\u540e\u7684\u666f\u8272\u53d8\u5316\uff0c\u4ee5\u6b64\u663e\u793a\u65b0\u751f\u6d3b\u4e4b\u7f8e\uff0c\u753b\u9762\u52a0\u4ee5\u65c1\u767d\u5f0f\u7684\u5e7f\u544a\u8bcd\uff1a\u201c\u89c6\u542c\u65b0\u751f\u6d3b\uff0c\u7231\u8fbe\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u4eca\u5929\u624d\u4e8c\u5341\u4e09\u65e5\uff0c\u4ea7\u54c1\u9648\u5217\u5ba4\u5f53\u7136\u4e0d\u4f1a\u53ea\u5411\u7ecf\u9500\u5546\u5c55\u793a\uff0c\u5df2\u7ecf\u63d0\u524d\u4e24\u5929\u5bf9\u8d76\u8fc7\u6765\u53c2\u52a0\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6210\u7acb\u5960\u5178\u7684\u4f01\u4e1a\u4ee3\u8868\u5f00\u653e\u3002</p><p>\u3000\u3000\u201c\u4f3c\u4e4e\u5e94\u8be5\u6539\u6210\u7231\u8fbe\u96c6\u56e2\u5427\uff1f\u201d\u770b\u7740\u5e55\u5899\u4e0a\u6295\u5c04\u7684\u552f\u7f8e\u7684\u5e7f\u544a\u753b\u9762\uff0c\u542c\u5e7f\u544a\u8bcd\u8fd9\u4e48\u8bf4\uff0c\u738b\u5f69\u73b2\u7591\u60d1\u7684\u95ee\u6881\u6587\u6c5f\u3002\u5728\u5979\u770b\u6765\uff0c\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u662f\u8d22\u52a1\u4e0a\u4e0e\u9526\u6e56\u6ca1\u6709\u76f4\u63a5\u7684\u5173\u8054\uff0c\u5e7f\u544a\u8bcd\u4f3c\u4e4e\u6539\u4e3a\u201c\u89c6\u542c\u65b0\u751f\u6d3b\uff0c\u7231\u8fbe\u96c6\u56e2\u201d\u66f4\u5408\u9002\u4e9b\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u65e2\u662f\u516c\u53f8\u540d\u79f0\uff0c\u4e5f\u662f\u54c1\u724c\u540d\u79f0\uff0c\u7231\u8fbe\u96c6\u56e2\u4ec5\u4ec5\u662f\u516c\u53f8\u540d\u79f0\uff0c\u4f46\u662f\u516c\u53f8\u540d\u79f0\u4e0e\u54c1\u724c\u540d\u79f0\u5e76\u4e0d\u603b\u662f\u4e00\u81f4\u7684\u2026\u2026\u201d\u5f20\u606a\u6070\u5de7\u542c\u5230\u4e86\uff0c\u4fbf\u8d70\u8fc7\u6765\u89e3\u91ca\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u4eca\u5929\u590f\u5929\u5df2\u7ecf\u4ece\u6e05\u534e\u5927\u5b66\u8ba1\u7b97\u673a\u7cfb\u6bd5\u4e1a\u540e\uff0c\u5c31\u6b63\u5f0f\u5230\u6606\u817e\u5728\u7ebf\u4e3b\u6301\u7f51\u7ad9\u6846\u67b6\u6280\u672f\u4e0a\u7684\u5de5\u4f5c\uff0c\u4e0e\u738b\u5f69\u73b2\u7684\u5173\u7cfb\u7a33\u5b9a\u53d1\u5c55\u7740\u3002\u738b\u5f69\u73b2\u7684\u7236\u4eb2\u738b\u65ed\u741b\u662f\u90ae\u7535\u90e8\u8ba1\u5efa\u53f8\u7684\u5b98\u5458\uff0c\u660e\u5929\u4f1a\u968f\u540c\u90ae\u7535\u90e8\u90e8\u957f\u803f\u91cd\u9633\u4e00\u540c\u5230\u6d77\u5dde\u6765\u53c2\u52a0\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u7acb\u5927\u4f1a\u3002\u738b\u5f69\u73b2\u4e5f\u9002\u9022\u60f3\u5230\u6d77\u5dde\u6765\u770b\u4e00\u770b\uff0c\u6881\u6587\u6c5f\u4fbf\u60f3\u7740\u4e0e\u738b\u5f69\u73b2\u5355\u72ec\u5230\u6d77\u5dde\u6765\u73a9\u4e00\u8d9f\u4e5f\u53ef\u4ee5\u3002\u524d\u5929\u4e0e\u5bb6\u91cc\u901a\u7535\u8bdd\u65f6\u8bf4\u53ca\u6b64\u4e8b\uff0c\u7ed9\u4ed6\u5988\u8bad\u4e86\u4e00\u901a\uff0c\u51c6\u5cb3\u7236\u968f\u90e8\u957f\u53d7\u9080\u5230\u6d77\u5dde\u6765\u53c2\u52a0\u6d3b\u52a8\uff0c\u4ed6\u4e0e\u5973\u53cb\u738b\u5f69\u73b2\u4fe9\u4eba\u4e5f\u5230\u6d77\u5dde\u6765\u6e38\u73a9\uff0c\u600e\u4e48\u5c31\u53ef\u4ee5\u5c06\u51c6\u5cb3\u6bcd\u4e00\u4e2a\u4eba\u4e22\u5317\u4eac\u5462\uff1f\u6881\u6587\u6c5f\u542c\u4e86\u6559\u8bad\uff0c\u8fde\u5fd9\u5c06\u738b\u5f69\u73b2\u4e0e\u5979\u5988\u4e00\u8d77\u63d0\u524d\u5230\u6628\u5929\u9ec4\u660f\u4e4b\u524d\u5e26\u5230\u6d77\u5dde\u6765\u2014\u2014\u738b\u65ed\u741b\u8fd8\u8981\u7b49\u5230\u4e8c\u5341\u56db\u65e5\u968f\u90ae\u7535\u90e8\u7684\u5b98\u5458\u96c6\u4f53\u4e58\u98de\u673a\u8d76\u6765\u53c2\u52a0\u4e0b\u5348\u7684\u5178\u793c\u3002\u6628\u5929\u4e0b\u5348\u6881\u6587\u6c5f\u524d\u817f\u521a\u8e0f\u4e0b\u98de\u673a\uff0c\u5f20\u606a\u4ed6\u5988\u6881\u56fd\u73cd\u5e26\u7740\u53f8\u673a\u5f00\u8f66\u5230\u673a\u573a\u63a5\u4ed6\u4eec\uff0c\u5c31\u544a\u8bc9\u4ed6\u8bf4\uff1a\u201c\u4f60\u7238\u3001\u4f60\u5988\u8fd9\u65f6\u5019\u4e5f\u5feb\u4e0a\u98de\u673a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e8b\u60c5\u5c31\u8fd9\u6837\u53d8\u6210\u7684\u201c\u53cc\u65b9\u5bb6\u957f\u78b0\u9762\u4f1a\u201d\u3002</p><p>\u3000\u3000\u5f20\u606a\u6653\u5f97\u4ed6\u5988\u5bf9\u5bb6\u65cf\u91cc\u7684\u4e8b\u6700\u70ed\u5207\uff0c\u5c31\u8fde\u4ed6\u5988\u7a81\u7136\u4ece\u65b0\u829c\u98ce\u98ce\u706b\u706b\u7684\u8d76\u56de\u5230\u6d77\u5dde\uff0c\u4ed6\u90fd\u4e0d\u6653\u5f97\u7ed9\u5413\u4e86\u4e00\u8df3\u3002\u5c0f\u53d4\u5f20\u77e5\u975e\u5bb6\u7684\u5c0f\u697c\u6536\u62fe\u8fc7\u4e4b\u540e\uff0c\u5f20\u5955\u4e0e\u5218\u5a77\u4e5f\u6ca1\u6709\u518d\u4f4f\u8fdb\u53bb\uff0c\u8fd9\u6b21\u6536\u62fe\u51fa\u6765\uff0c\u5c31\u7ed9\u738b\u65ed\u741b\u4e0e\u5c0f\u8205\u6881\u56fd\u76db\u4e24\u5bb6\u4eba\u4f4f\u3002</p><p>\u3000\u3000\u738b\u65ed\u741b\u8fd8\u8981\u7b49\u5230\u660e\u5929\u624d\u5230\u5317\u4eac\uff0c\u6881\u6587\u6c5f\u5c31\u4eca\u5929\u5148\u5e26\u7740\u5bb6\u4eba\u4e0e\u738b\u5f69\u73b2\u8fd8\u6709\u738b\u5f69\u73b2\u5979\u5988\u5988\u53c2\u89c2\u9526\u6e56\u7684\u4ea7\u54c1\u9648\u5217\u5ba4\uff0c\u660e\u5929\u5c31\u53ef\u4ee5\u4e0d\u7528\u53c2\u4e0e\u8fdb\u6765\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u5de5\u592b\uff0c\u5f20\u606a\u53ea\u770b\u5230\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u8fd8\u5728\u8fd9\u91cc\u8f6c\u60a0\uff0c\u5176\u4ed6\u4eba\u90fd\u4e0d\u89c1\u4e86\uff0c\u542c\u5230\u738b\u5f69\u73b2\u4e0e\u6881\u6587\u6c5f\u8fd8\u5728\u4e3a\u516c\u53f8\u4e0e\u54c1\u724c\u7684\u6982\u5ff5\u6df7\u6dc6\uff0c\u8d70\u8fc7\u6765\u7ed9\u89e3\u91ca\u4e86\u4e00\u4e0b\uff1a\u201c\u5f88\u591a\u4eba\u5c06\u516c\u53f8\u4e0e\u54c1\u724c\u7684\u6982\u5ff5\u6df7\u6dc6\uff0c\u5374\u4e0d\u6653\u5f97\u516c\u53f8\u7684\u6982\u5ff5\u4e3b\u8981\u662f\u9488\u5bf9\u516c\u53f8\u7684\u7ecf\u9500\u5546\u4e0e\u4e0a\u4e0b\u6e38\u5ba2\u6237\uff0c\u54c1\u724c\u5374\u662f\u8981\u6700\u76f4\u63a5\u7684\u9488\u5bf9\u5e02\u573a\uff0c\u5f88\u591a\u5728\u54c1\u724c\u8fd0\u8425\u3001\u516c\u53f8\u5f62\u8c61\u5851\u9020\u4e0a\u5e38\u5e38\u662f\u753b\u864e\u7c7b\u72ac\u2026\u2026\u201d\u53c8\u6325\u4e86\u6325\u624b\uff0c\u89c9\u5f97\u8fd9\u65f6\u5019\u8c08\u8bba\u8fd9\u4e9b\u5feb\u65e0\u804a\u7684\uff0c\u53c8\u95ee\u738b\u5f69\u73b2\uff0c\u201c\u6628\u5929\u591c\u91cc\u8fd8\u4e60\u60ef\uff1f\u201d</p><p>\u3000\u3000\u201c\u5317\u4eac\u7684\u98ce\u6c99\u5f88\u5927\uff0c\u4e5f\u5f88\u5e72\u71e5\uff0c\u6d77\u5dde\u7684\u79cb\u5929\u8981\u6e29\u6da6\u591a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u5bd2\u6684\u51e0\u53e5\u770b\u89c1\u9648\u9759\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u6084\u7136\u7ad9\u5728\u4e00\u65c1\u5728\u542c\u4ed6\u4eec\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u55ec\uff0c\u600e\u4e48\u4e00\u70b9\u58f0\u97f3\u90fd\u6ca1\u6709\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u53c8\u7ed9\u6881\u6587\u6c5f\u3001\u738b\u5f69\u73b2\u4ecb\u7ecd\u9648\u9759\u7684\u8eab\u4efd\uff0c\u201c\u9648\u9759\u5c0f\u59d0\u662f\u79d1\u738b\u9ad8\u79d1\u7684\u8463\u4e8b\u957f\uff0c\u5149\u5f69\u593a\u76ee\u7684\u5546\u754c\u65b0\u661f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f3c\u4e4e\u606a\u5c11\u4f60\u6700\u6ca1\u6709\u8d44\u683c\u8bf4\u8fd9\u53e5\u4e8b\uff0c\u8981\u4e48\u5c31\u662f\u5728\u8bbd\u523a\u6211\u2026\u2026\u201d\u9648\u9759\u5ae3\u7136\u800c\u7b11\uff0c\u53c8\u542c\u5f20\u606a\u7ed9\u5979\u4ecb\u7ecd\u6881\u6587\u6c5f\u3001\u738b\u5f69\u73b2\uff0c\u7b11\u7740\u4e0e\u4ed6\u4eec\u63e1\u624b\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u3001\u738b\u5f69\u73b2\u544a\u8f9e\u8d70\u5411\u522b\u5904\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u9648\u9759\u62bf\u7740\u5507\u4f3c\u4e4e\u8981\u8bf4\u4ec0\u4e48\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u6709\u4ec0\u4e48\u8bdd\u4f1a\u8bf4\u4e0d\u51fa\u53e3\uff0c\u8be5\u4e0d\u4f1a\u8981\u8d56\u6389\u7b54\u5e94\u8bf7\u6211\u7684\u996d\u5c40\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u8fd9\u70b9\u5c0f\u5fc3\u773c\uff1f\u201d\u9648\u9759\u62bf\u5634\u7b11\u7740\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5728\u60f3\uff0c\u662f\u8bf7\u4f60\u5c06\u521a\u624d\u516c\u53f8\u4e0e\u54c1\u724c\u7684\u8bdd\u9898\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff0c\u8fd8\u662f\u8bf7\u4f60\u544a\u8bc9\u6211\u4ec0\u4e48\u53eb\u2018\u68cb\u5b50\u2019\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4f60\u95ee\u8fd9\u4e2a\u554a\uff0c\u201d\u5f20\u606a\u4e0e\u9648\u9759\u8d70\u5230\u7a0d\u50fb\u9759\u7684\u89d2\u843d\u8bf4\u8bdd\uff0c\u201c\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u90ae\u7535\u90e8\u90fd\u4e0d\u53ef\u80fd\u5bb9\u5fcd\u5c06\u4ed6\u4eec\u5728\u901a\u4fe1\u884c\u4e1a\u91cc\u5784\u65ad\u5df2\u4e45\u7684\u884c\u653f\u6743\u529b\u6700\u5148\u5206\u7ed9\u4e00\u5bb6\u6c11\u8425\u4f01\u4e1a\u7684\uff0c\u6211\u5fc5\u987b\u653e\u5f03\u5bf9\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u76f4\u63a5\u63a7\u5236\uff0c\u6709\u65f6\u5019\u662f\u5fc5\u987b\u8981\u8bc6\u65f6\u52a1\u7684\u3002\u5f53\u7136\uff0c\u6211\u540c\u65f6\u4e0d\u5e0c\u671b\u90ae\u7535\u90e8\u5728\u534f\u4f1a\u91cc\u6709\u76f4\u63a5\u7684\u8bdd\u8bed\u6743\uff0c\u4e5f\u4e0d\u5e0c\u671b\u7535\u5b50\u5de5\u4e1a\u90e8\u5728\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u91cc\u6709\u76f4\u63a5\u7684\u8bdd\u8bed\u6743\uff0c\u552f\u4e00\u529e\u6cd5\u5c31\u662f\u5229\u7528\u4e24\u4e2a\u90e8\u59d4\u4e4b\u95f4\u7684\u77db\u76fe\uff0c\u5c06\u4f60\u4eec\u79d1\u738b\u9ad8\u79d1\u518d\u6b21\u62ac\u51fa\u6765\u5e73\u8861\u4ed6\u4eec\u7684\u77db\u76fe\u2026\u2026\u201d</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u5f20\u606a\u4e3b\u8981\u8003\u8651\u5230\u4e00\u65e6\u90ae\u7535\u90e8\u7535\u5b50\u4e1a\u52a1\u4e0e\u7535\u5b50\u5de5\u4e1a\u90e8\u5728\u660e\u5e74\u5408\u5e76\u6210\u4fe1\u606f\u4ea7\u4e1a\u90e8\uff0c\u8fd9\u65f6\u5019\u4e0d\u80fd\u76f4\u63a5\u6d88\u9664\u8fd9\u65f6\u5019\u4e24\u4e2a\u90e8\u59d4\u7684\u76f4\u63a5\u6295\u7968\u6743\uff0c\u5230\u65f6\u5019\u8bf4\u4e0d\u5b9a\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5728\u534f\u4f1a\u91cc\u5c31\u6709\u4e24\u7968\u7684\u51b3\u5b9a\u6743\uff0c\u52a0\u4e0a\u4e1c\u5174\u3001\u8054\u8baf\u3001\u8054\u4fe1\u4e09\u5bb6\u516c\u53f8\u5c4a\u65f6\u90fd\u4f1a\u5f52\u5230\u4fe1\u606f\u4ea7\u4e1a\u90e8\u65d7\u4e0b\u7ba1\u8f96\uff0c\u5c82\u4e0d\u662f\u8bf4\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5c31\u5c06\u5b9e\u9645\u638c\u63e1\u6570\u5b57\u624b\u673a\u6280\u672f\u534f\u4f1a\uff1f</p><p>\u3000\u3000\u5bfc\u81f4\u8fd9\u6837\u7684\u7ed3\u5c40\uff0c\u5f20\u606a\u7684\u521d\u8877\u8fd8\u6709\u591a\u5927\u7684\u610f\u4e49\u5b58\u5728\uff1f</p><p>\u3000\u3000\u5728\u516c\u5f00\u7684\u8be6\u7ec6\u65b9\u6848\uff0c\u9664\u4e86\u79d1\u738b\u9ad8\u79d1\u4e4b\u5916\uff0c\u8fd1\u671f\u5185\u5c06\u6392\u65a5\u5176\u4ed6\u7535\u5b50\u4f01\u4e1a\u8fdb\u5165\u624b\u673a\u751f\u4ea7\u9886\u57df\uff0c\u9664\u975e\u80fd\u4ece\u90ae\u7535\u90e8\u518d\u5ea6\u83b7\u5f97\u65b0\u7684\u4e00\u5f20\u624b\u673a\u5236\u9020\u724c\u7167\uff0c\u603b\u4e4b\u7231\u8fbe\u3001\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u90fd\u5c06\u53d7\u5230\u8be5\u534f\u4f1a\u76f8\u5173\u7684\u7ea6\u675f\u5728\u8fd1\u671f\u65e0\u6cd5\u5411\u5176\u4ed6\u5382\u5546\u6388\u6743\u8d34\u724c\u3002</p><p>\u3000\u3000\u5728\u8bb8\u591a\u4eba\u773c\u91cc\uff0c\u79d1\u738b\u9ad8\u79d1\u7684\u4e00\u6b65\u767b\u5929\u6709\u4e9b\u83ab\u660e\u5176\u5999\u4e86\uff0c\u4f46\u662f\u5f88\u591a\u4eba\u5c06\u79d1\u738b\u9ad8\u79d1\u8fdb\u5165\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u8ba4\u4e3a\u662f\u90ae\u7535\u90e8\u4e3a\u81ea\u5df1\u591a\u635e\u53d6\u7684\u4e00\u7968\u3002</p><p>\u3000\u3000\u4e1c\u5174\u3001\u8054\u8baf\u662f\u7535\u5b50\u5de5\u4e1a\u90e8\u7684\u4f01\u4e1a\uff0c\u53ea\u6709\u8054\u4fe1\u4e00\u5bb6\u662f\u90ae\u7535\u90e8\u7684\u3002\u4f5c\u4e3a\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u4ea7\u4e1a\u7684\u4e3b\u7ba1\u884c\u653f\u90e8\u95e8\uff0c\u90ae\u7535\u90e8\u5728\u8fd9\u4e2a\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u91cc\u7684\u5f71\u54cd\u529b\u7adf\u7136\u6bd4\u7535\u5b50\u5de5\u4e1a\u90e8\u8fd8\u5f31\uff0c\u591a\u5c11\u4f1a\u6709\u4e9b\u4e0d\u7518\u5fc3\uff0c\u9762\u5b50\u4e5f\u62b9\u4e0d\u5f00\u3002\u79d1\u738b\u9ad8\u79d1\u5728\u83b7\u5f97\u81ea\u5df1\u624b\u673a\u6267\u7167\u4e4b\u524d\uff0c\u53ea\u80fd\u7ed9\u8054\u4fe1\u8d34\u724c\uff0c\u4e5f\u5c31\u662f\u8bf4\u79d1\u738b\u9ad8\u79d1\u8981\u662f\u501f\u6b64\u673a\u9047\u80fd\u83b7\u5f97\u5927\u7684\u53d1\u5c55\uff0c\u8868\u9762\u4e0a\u7684\u5e02\u573a\u4efd\u989d\u6216\u8bb8\u8fd8\u53ef\u4ee5\u8bf4\u662f\u90ae\u7535\u90e8\u62ff\u53bb\u906e\u7f9e\u7684\u6811\u53f6\uff0c\u4f46\u662f\u5de8\u989d\u7684\u8d34\u724c\u6536\u8d39\uff0c\u5374\u662f\u5b9e\u5b9e\u5728\u5728\u7684\u6536\u76ca\uff0c\u90ae\u7535\u90e8\u4e5f\u4e0d\u4f1a\u76f8\u4fe1\u79d1\u738b\u9ad8\u79d1\u77ed\u65f6\u95f4\u91cc\u80fd\u8131\u79bb\u638c\u63e1\u3002</p><p>\u3000\u3000\u5012\u662f\u8ba9\u79d1\u738b\u9ad8\u79d1\u4e00\u4e0b\u5b50\u538b\u8fc7\u4e1a\u5185\u7684\u65e0\u6570\u5bb6\u5382\u5546\uff0c\u5149\u5f69\u5728\u9661\u7136\u95f4\u5c31\u593a\u76ee\u8d77\u6765\u3002</p></article>"}}}]);
