(window.webpackJsonp=window.webpackJsonp||[]).push([[870],{893:function(p){p.exports={chapterTitle:null,title:"\u7b2c694\u7ae0 \u665a\u9910",content:"<article><h2>\u7b2c694\u7ae0 \u665a\u9910</h2><p>\u3000\u3000\u4ea7\u54c1\u9648\u5217\u5385\u7684\u4e00\u9762\u5899\u4f1a\u7528\u6295\u5f71\u4eea\u4e0d\u505c\u7684\u6eda\u52a8\u64ad\u653e\u8fd9\u4e24\u5e74\u591a\u6765\u9526\u6e56\u4e3a\u54c1\u724c\u5ba3\u4f20\u53ca\u65d7\u4e0b\u4ea7\u54c1\u5ba3\u4f20\u6240\u5236\u4f5c\u7684\u5404\u79cd\u98ce\u683c\u7684\u5e7f\u544a\u753b\u9762\u3002</p><p>\u3000\u3000\u9648\u9759\u6700\u559c\u6b22\u5229\u7528\u6c99\u7530\u53e4\u8857\u4f5c\u80cc\u666f\u7684\u90a3\u4e00\u7cfb\u5217\u5e7f\u544a\uff0c\u53e4\u5df7\u3001\u9752\u82d4\u77f3\u8857\u3001\u6591\u9a73\u7684\u9662\u5899\u3001\u9752\u6f88\u6d41\u6c34\u3001\u77f3\u6865\u6216\u6728\u6865\uff0c\u6625\u65e5\u8f7b\u96e8\uff0c\u5076\u5c14\u624d\u4f1a\u8d70\u8fc7\u53bb\u7684\u884c\u4eba\u6491\u7740\u4f1e\u3002\u8fd9\u662f\u4e00\u5e74\u591a\u4ee5\u524d\uff0c\u5e7f\u544a\u5236\u4f5c\u4eba\u9a6c\u5728\u6b8b\u65e7\u7684\u6c99\u7530\u9752\u7816\u77f3\u5df7\u91cc\u5f88\u662f\u8d39\u4e86\u4e00\u756a\u5fc3\u601d\uff0c\u624d\u5c06\u6b8b\u65e7\u9752\u7816\u77f3\u5df7\u91cc\u60ca\u4eba\u7684\u7f8e\u5bfb\u89c5\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u770b\u5230\u5899\u9762\u4e0a\u7684\u6f7a\u6f7a\u6d41\u6c34\u7684\u753b\u9762\uff0c\u9648\u9759\u4e0e\u5f20\u606a\u5e76\u80a9\u7ad9\u7740\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u53bb\u5e74\u5230\u6d77\u5dde\u6765\u4e4b\u524d\uff0c\u53ef\u60f3\u4e0d\u5230\u6d77\u5dde\u4f1a\u8fd9\u4e48\u7f8e\u2026\u2026\u518d\u8fc7\u5341\u5e74\u4e8c\u5341\u5e74\uff0c\u57ce\u5e02\u8ba9\u94a2\u7b4b\u6c34\u6ce5\u7684\u68ee\u6797\u5360\u636e\uff0c\u4ece\u8f66\u6c34\u9a6c\u9f99\u7684\u5fd9\u788c\u5927\u8857\u62d0\u8fc7\uff0c\u7adf\u6709\u4e00\u5904\u9752\u7816\u77f3\u5df7\u7b49\u7740\u4f60\uff0c\u5927\u6982\u4e00\u5929\u75b2\u60eb\u7684\u5fc3\u60c5\u90fd\u4f1a\u7ed9\u5f7b\u5e95\u6d17\u7a7a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u624b\u63d2\u5728\u84dd\u8272\u5916\u5957\u7684\u5927\u515c\u91cc\uff0c\u4eff\u4f5b\u5076\u5c14\u7ecf\u8fc7\u8fd9\u91cc\u4e0d\u7ecf\u610f\u8fdb\u6765\u770b\u4e00\u773c\u7684\u5b66\u751f\u6a21\u6837\uff0c\u8bf4\u9053\uff0c\u201c\u90a3\u53ef\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u5546\u4e1a\u5f00\u53d1\u4e0e\u4fdd\u7559\u5efa\u7b51\u7684\u539f\u97f5\u5473\uff0c\u672c\u8eab\u5c31\u6709\u7740\u5f88\u96be\u5e73\u8861\u7684\u77db\u76fe\uff0c\u5373\u4f7f\u8fd9\u7247\u5efa\u7b51\u90fd\u4fdd\u7559\u4e0b\u6765\uff0c\u4f46\u662f\u90fd\u53d8\u6210\u706f\u7ea2\u9152\u7eff\u7684\u5546\u4e1a\u573a\u6240\uff0c\u5927\u6982\u4e5f\u672a\u5fc5\u80fd\u5c3d\u5982\u4eba\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u7684\u786e\u5982\u6b64\u5462\uff0c\u201d\u9648\u9759\u60f3\u7740\u5f20\u606a\u7684\u8bdd\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u80fd\u5c06\u8fd9\u7247\u5efa\u7b51\u4fdd\u5b58\u4e0b\u6765\uff0c\u5df2\u7ecf\u5c3d\u4e86\u5f88\u5927\u7684\u52aa\u529b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u5bf9\u6c99\u7530\u7684\u53e4\u5efa\u7b51\u5f88\u611f\u5174\u8da3\uff0c\u5230\u6d77\u5dde\u8fd9\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u5bf9\u6c99\u7530\u4e1c\u7247\u53e4\u5efa\u7b51\u7fa4\u4e4b\u6240\u4ee5\u5f97\u4ee5\u4fdd\u5b58\u4e0b\u6765\u7684\u7f18\u7531\u81ea\u7136\u4e5f\u5f88\u6e05\u695a\u3002</p><p>\u3000\u3000\u9648\u9759\u8fd9\u4e00\u5e74\u591a\u6765\uff0c\u4e0e\u5f20\u606a\u7684\u4ea4\u96c6\u4e0d\u591a\uff0c\u4e00\u5e74\u4e4b\u95f4\u76f8\u9047\u7684\u673a\u4f1a\u5c48\u6307\u53ef\u6570\uff0c\u4e0d\u8fc7\u6bcf\u56de\u5bc2\u5be5\u4e4b\u65f6\u559c\u6b22\u5230\u6c99\u7530\u4e1c\u533a\u7684\u9752\u7816\u5df7\u5b50\u91cc\u8d70\u4e00\u8d70\uff0c\u5c31\u4f1a\u60f3\u5230\u8fd9\u4e2a\u6709\u7740\u5929\u7eb5\u4e4b\u624d\u7684\u7537\u4eba\u5fc3\u91cc\u7684\u7eaf\u771f\u4e4b\u5904\u3002</p><p>\u3000\u3000\u6362\u4f5c\u5176\u4ed6\u4eba\uff0c\u867d\u7136\u4f1a\u89c9\u5f97\u8fd9\u4e00\u7247\u5efa\u7b51\u7fa4\u7ed9\u62c6\u6389\u5f88\u53ef\u60dc\uff0c\u5374\u4e0d\u4f1a\u8d39\u5c3d\u5468\u7ae0\u7684\u53bb\u505a\u4e9b\u4ec0\u4e48\uff0c\u800c\u4ed6\u5c31\u5728\u9526\u6e56\u8fd8\u5728\u8d77\u6b65\u4e4b\u521d\u3001\u5904\u5904\u8fd8\u7f3a\u4e4f\u8d44\u91d1\u4e4b\u65f6\u5c31\u4e0d\u8ba1\u6210\u672c\u7684\u6295\u5165\u5de8\u8d44\uff0c\u907f\u514d\u90a3\u4e00\u7247\u7684\u53e4\u5efa\u7b51\u7fa4\u6ca6\u5165\u88ab\u62c6\u6bc1\u7684\u547d\u8fd0\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u9648\u9759\u660e\u4eae\u7f8e\u4e3d\u7684\u7738\u5b50\uff0c\u6709\u7740\u5e7d\u8fdc\u7684\u60c5\u601d\uff0c\u4e0d\u6e05\u695a\u5979\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u5012\u662f\u8c08\u8d77\u6c99\u7530\u7684\u53e4\u5efa\u7b51\u7fa4\u8fd8\u662f\u9887\u6709\u611f\u89e6\u7684\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u60f3\u5341\u5e74\u4e8c\u5341\u5e74\u4e4b\u540e\uff0c\u8fd8\u6709\u4e00\u5904\u5e7d\u9759\u7684\u65e7\u8857\u53e4\u5df7\uff0c\u5c31\u9700\u8981\u5e02\u8d22\u653f\u5bf9\u4e00\u90e8\u5206\u5efa\u7b51\u8fdb\u884c\u989d\u5916\u7684\u4fdd\u62a4\uff0c\u5c31\u7b97\u65c5\u6e38\u5f00\u53d1\uff0c\u4e5f\u8981\u5f88\u8282\u5236\u624d\u884c\uff0c\u603b\u4e4b\u4e0d\u662f\u4e00\u4ef6\u5bb9\u6613\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u662f\u554a\u3002\u201d\u9648\u9759\u53cc\u624b\u62b1\u80f8\uff0c\u4e00\u624b\u6258\u7740\u79c0\u6c14\u7684\u4e0b\u9894\uff0c\u9732\u51fa\u7684\u4e00\u5c0f\u622a\u624b\u8155\u7c89\u767d\u5982\u96ea\uff0c\u50cf\u79cb\u6c34\u4e00\u822c\u5e7d\u8fdc\u6e05\u6f88\u7684\u7738\u5b50\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u8fc7\u6765\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u4ed6\u4eec\u8981\u5148\u79bb\u5f00\u53bb\u5403\u665a\u996d\uff0c\u5f20\u606a\u4ed6\u5988\u8fd8\u6709\u5176\u4ed6\u4eba\u90fd\u5728\u697c\u524d\u7684\u4e94\u91cc\u6eaa\u5e7f\u573a\u7b49\u4ed6\u4eec\u5462\u3002</p><p>\u3000\u3000\u770b\u7740\u6881\u6587\u6c5f\u4e0e\u738b\u5f69\u73b2\u79bb\u5f00\uff0c\u9648\u5217\u5385\u91cc\u7684\u4eba\u4e5f\u6e10\u6e10\u79bb\u5f00\uff0c\u4ece\u897f\u8fb9\u7684\u843d\u5730\u73bb\u7483\u7a97\u5916\u659c\u5c04\u8fdb\u6765\u5927\u7247\u7684\u5915\u9633\u5149\u8f89\u94fa\u5728\u5730\u677f\u4e0a\uff0c\u9648\u9759\u60f3\u8d77\u8bf7\u5403\u996d\u7684\u4e8b\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u65e9\u5c31\u8bf4\u8d77\u8981\u8bf7\u4f60\u5403\u996d\u7565\u8868\u8c22\u610f\uff0c\u597d\u4e9b\u5929\u6ca1\u6709\u673a\u4f1a\u9047\u5230\u4f60\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u6ca1\u6709\u7a7a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5f53\u7136\u6709\u7a7a\uff0c\u4e0d\u8fc7\u4f60\u597d\u50cf\u6709\u4e9b\u75b2\u5026\u5462\uff0c\u505a\u5973\u5f3a\u4eba\u4e0d\u5bb9\u6613\u5427\uff0c\u8981\u4e0d\u8981\u8d76\u56de\u53bb\u8865\u7761\u4e00\u89c9\uff1f\u201d\u5f20\u606a\u5173\u5fc3\u7684\u8bf4\u9053\uff0c\u9648\u9759\u660e\u8273\u7684\u5bb9\u989c\u591a\u4e86\u4e00\u4e9b\u8fc7\u52b3\u4e4b\u540e\u7684\u6194\u60b4\uff0c\u5012\u5206\u5916\u6dfb\u4e86\u4e00\u4e9b\u8ba9\u4eba\u601c\u60dc\u7684\u59a9\u5a9a\uff1b\u8fd9\u4e9b\u5929\uff0c\u79d1\u738b\u9ad8\u79d1\u627f\u62c5\u7684\u538b\u529b\u53ef\u4e0d\u5bb9\u5c0f\u89c6\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u8fd8\u4e0d\u662f\u8981\u602a\u4f60\uff0c\u201d\u9648\u9759\u5a07\u55d4\u7684\u62b1\u6028\u4e86\u4e00\u53e5\uff0c\u8fd9\u4e48\u62b1\u6028\u7740\uff0c\u591a\u5c11\u6709\u4e9b\u6253\u60c5\u9a82\u4fcf\u7684\u610f\u5473\uff0c\u5fc3\u95f4\u6d8c\u51fa\u4e9b\u7f9e\u8d67\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u8d76\u56de\u53bb\u4e5f\u7761\u4e0d\u7740\uff0c\u603b\u4f1a\u6709\u8bb8\u591a\u4e8b\u8dd1\u51fa\u6765\uff0c\u8fd8\u4e0d\u5982\u8bf7\u4f60\u5403\u996d\uff0c\u7b97\u662f\u5077\u61d2\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u597d\u5427\uff0c\u6211\u8fd9\u4eba\u5b9e\u5728\u6e05\u95f2\u7684\u5f88\uff0c\u4f60\u8981\u4e0d\u8bf7\u6211\u5403\u996d\uff0c\u6211\u8fd8\u53e6\u627e\u5730\u65b9\u6253\u53d1\u65f6\u95f4\uff0c\u201d\u5f20\u606a\u644a\u644a\u624b\uff0c\u201c\u8981\u4e0d\u662f\u4f60\u5e2e\u6211\u6321\u7740\uff0c\u6211\u94c1\u5b9a\u8981\u7ed9\u6211\u5988\u62c9\u8fc7\u53bb\u5f53\u4e09\u966a\uff0c\u8fd9\u51e0\u5929\u662f\u6211\u8868\u54e5\u4e0e\u4ed6\u5973\u670b\u53cb\u53cc\u65b9\u5bb6\u957f\u5728\u6d77\u5dde\u89c1\u9762\uff0c\u6211\u5988\u70ed\u5fc3\u5f97\u4e0d\u5f97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u9759\u5ae3\u7136\u4e00\u7b11\uff0c\u660e\u5929\u90ae\u7535\u90e8\u90e8\u957f\u803f\u91cd\u9633\u3001\u7701\u957f\u674e\u8fdc\u6e56\u90fd\u8981\u5230\u6d77\u5dde\u6765\uff0c\u90fd\u597d\u50cf\u90fd\u8ddf\u5f20\u606a\u4ed6\u6ca1\u4ec0\u4e48\u5173\u7cfb\u4f3c\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u9759\u4e5f\u662f\u521a\u529e\u5b8c\u4e8b\u7ecf\u8fc7\u8fd9\u91cc\u5230\u9526\u6e56\u7684\u4ea7\u54c1\u9648\u5217\u5385\u6765\u770b\u4e00\u773c\uff0c\u4e0e\u5979\u4e00\u8d77\u7684\uff0c\u8fd8\u6709\u521a\u4ece\u5927\u96c5\u96c6\u56e2\u501f\u8c03\u8fc7\u6765\u7684\u4e00\u540d\u7ecf\u7406\uff0c\u7ba1\u7406\u8d22\u52a1\u3001\u884c\u653f\u3001\u4eba\u529b\u8d44\u6e90\u65b9\u9762\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u505c\u5728\u697c\u4e0b\u4e0d\u662f\u9648\u9759\u90a3\u8f86\u7ea2\u8272\u5954\u9a70\uff0c\u800c\u662f\u4e00\u8f86\u9ed1\u8272\u798f\u7279\uff0c\u9648\u9759\u8ba9\u53f8\u673a\u9001\u90a3\u540d\u7ecf\u7406\u5148\u56de\u53bb\u3002\u8bf4\u597d\u53bb\u524d\u8857\u90a3\u8fb9\u627e\u9910\u9986\u5403\u996d\uff0c\u5979\u5750\u5f20\u606a\u7684\u8f66\u53bb\uff0c\u7528\u8fc7\u9910\u665a\u4e0a\u6b65\u884c\u8d70\u56de\u5e9c\u5929\u5927\u53a6\u697c\u4e0a\u7684\u4f4f\u5904\u90fd\u6ca1\u6709\u51e0\u6b65\u8def\u3002</p><p>\u3000\u3000\u5915\u9633\u665a\u7167\uff0c\u5f20\u606a\u4e0e\u9648\u9759\u8d70\u5230\u505c\u8f66\u573a\u91cc\uff0c\u4e00\u8d77\u94bb\u8fdb\u8f66\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u6d77\u5dde\u81ea\u5df1\u5f00\u8f66\uff0c\u8fd8\u662f\u665a\u6674\u5f53\u751f\u65e5\u793c\u7269\u9001\u7ed9\u4ed6\u7684\u90a3\u8f86\u94f6\u7070\u8272Volvo\u8f7f\u8dd1\uff0c\u867d\u7136\u7b97\u4e0d\u662f\u5f88\u9ad8\u6863\u7684\u8f66\uff0c\u4f46\u662f\u201c\u6d77F12345\u201d\u7684\u724c\u7167\u5374\u8db3\u591f\u98ce\u5149\u4e86\u2014\u2014\u724c\u7167\u8fd8\u662f\u5c0f\u53d4\u5f20\u77e5\u975e\u9001\u7ed9\u4ed6\u7684\u3002</p><p>\u3000\u3000\u773c\u4e0b\uff0c\u5e02\u91cc\u65e9\u5c31\u660e\u786e\u5c06\u6c99\u7530\u4e1c\u7247\u7684\u6574\u4f53\u5efa\u7b51\u5f53\u6210\u5e02\u7ea7\u6587\u5316\u9057\u8ff9\u5efa\u7b51\u7fa4\u4fdd\u5b58\u4e0b\u6765\uff0c\u4e5f\u5141\u8bb8\u6295\u8d44\u4eba\u5bf9\u6cbf\u8857\u5efa\u7b51\u8fdb\u884c\u5546\u4e1a\u6027\u6295\u8d44\u4fee\u7f2e\u3002</p><p>\u3000\u3000\u524d\u8857\u6539\u62d3\u5efa\u5de5\u7a0b\u3001\u758f\u6e2f\u6cb3\u6e05\u6de4\u5de5\u7a0b\uff0c\u4f7f\u5f97\u8fd9\u4e00\u5e26\u7684\u73af\u5883\u5927\u4e3a\u6539\u5584\uff0c\u4e39\u4e95\u5df7\u53ef\u4ee5\u8bf4\u662f\u6c99\u7530\u4e1c\u897f\u4e24\u533a\u7684\u5206\u754c\u70b9\uff0c\u5f20\u606a\u5f53\u521d\u6295\u5165\u5de8\u8d44\u4fee\u7f2e\u7684\u56db\u5c42\u7eaf\u6728\u7ed3\u6784\u7684\u68cb\u9986\u6070\u597d\u662f\u6c99\u7530\u524d\u8857\u7684\u4e2d\u95f4\u70b9\u3002</p><p>\u3000\u3000\u68cb\u9986\u4fee\u7f2e\u4e4b\u540e\uff0c\u4f5c\u4e3a\u516c\u76ca\u6027\u7684\u68cb\u827a\u535a\u7269\u9986\u6709\u9650\u5236\u7684\u5bf9\u5916\u5f00\u653e\uff0c\u4f46\u662f\u68cb\u827a\u535a\u7269\u9986\u80cc\u540e\u3001\u4e39\u4e95\u5df7\u7684\u5357\u4fa7\u5c0f\u534a\u6bb5\u5148\u5f00\u53d1\u51fa\u6765\u7684\u51e0\u5bb6\u9910\u996e\u5e97\u4f5c\u4e3a\u4e39\u4e95\u5df7\u5546\u4e1a\u5316\u6539\u9020\u7684\u793a\u8303\u6027\u5546\u4e1a\u8fd0\u8425\u53d6\u5f97\u76f8\u5f53\u5927\u7684\u6210\u529f\uff0c\u4e5f\u9010\u6e10\u8ba9\u6295\u8d44\u4eba\u610f\u8bc6\u5230\u6c99\u7530\u524d\u8857\u6cbf\u8857\u53e4\u5efa\u7b51\u7684\u5546\u4e1a\u524d\u666f\uff0c\u5e02\u91cc\u660e\u786e\u66f4\u6539\u4e86\u89c4\u5212\u4e4b\u540e\uff0c\u5357\u4fa7\u4e34\u6cb3\u7684\u6c99\u7530\u524d\u8857\u5317\u4fa7\u4e34\u8857\u5efa\u7b51\u5c31\u6709\u9646\u7eed\u7ed9\u6295\u8d44\u4eba\u770b\u4e2d\uff0c\u6539\u9020\u6210\u9910\u996e\u5a31\u4e50\u6587\u5316\u4f11\u95f2\u573a\u6240\u3002</p><p>\u3000\u3000\u8fd9\u4e00\u7247\uff0c\u9664\u4e86\u516b\u9526\u73cd\u4e4b\u5916\uff0c\u8fd8\u6709\u51e0\u5bb6\u4e0d\u9519\u7684\u9910\u9986\uff0c\u6709\u6210\u4e3a\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u4e4b\u5916\u6d77\u5dde\u53e6\u4e00\u5904\u9910\u996e\u4e1a\u805a\u96c6\u5730\u7684\u8d8b\u52bf\uff0c\u5012\u662f\u4e39\u4e95\u5df7\u4ee5\u897f\u7684\u65b0\u5efa\u7b51\u5efa\u6210\u4e4b\u540e\u6ca1\u6709\u591a\u6295\u8d44\u8005\u7406\u4f1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u9759\u627e\u4e86\u4e00\u5bb6\u897f\u9910\u5385\u4e34\u897f\u7a97\u7684\u5ea7\u4f4d\u5750\u4e0b\u7528\u9910\u3002</p><p>\u3000\u3000\u9648\u9759\u5c06\u5916\u5957\u8131\u4e0b\u653e\u5728\u4e00\u65c1\u7684\u6905\u5b50\u4e0a\uff0c\u7a7f\u7740\u6de1\u7d2b\u8272\u7684\u7ed2\u7ebf\u8863\uff0c\u7ed2\u7ebf\u8863\u662f\u7d27\u8eab\u7684\uff0c\u4e30\u633a\u7684\u80f8\u90e8\u4e0e\u7ea4\u7ec6\u7684\u8170\u808c\uff0c\u6709\u7740\u51e0\u4e4e\u62ff\u773c\u795e\u90fd\u80fd\u611f\u89c9\u7684\u60ca\u4eba\u5f39\u6027\uff0c\u5f20\u606a\u8f7b\u54bd\u4e86\u4e00\u53e3\u553e\u6db2\uff0c\u5fcd\u7740\u6ca1\u4f38\u624b\u53bb\u6309\u4e00\u6309\u3002</p><p>\u3000\u3000\u9648\u9759\u7565\u6709\u4e9b\u75b2\u60eb\uff0c\u7cbe\u81f4\u800c\u7f8e\u4e3d\u7684\u5bb9\u989c\u5374\u4e1d\u6beb\u4e0d\u4e3a\u4e4b\u51cf\u8272\uff0c\u5c06\u7ed2\u7ebf\u8863\u7684\u8896\u7ba1\u7a0d\u634b\u8d77\u4e9b\uff0c\u9732\u51fa\u534a\u622a\u7c89\u767d\u7684\u5c0f\u81c2\uff0c\u8116\u5b50\u4fee\u957f\uff0c\u808c\u80a4\u5149\u6da6\u5982\u7389\u3002\u5f20\u606a\u4ed4\u7ec6\u53bb\u6b23\u8d4f\u773c\u524d\u7684\u7f8e\u8272\uff0c\u770b\u5f97\u51fa\u9648\u9759\u7684\u8138\u4e0a\u6709\u7740\u4e00\u79cd\u96be\u4ee5\u8a00\u55bb\u7684\u843d\u5bde\u7684\u7f8e\uff1b\u6709\u4e00\u62b9\u5915\u9633\u4ece\u7a97\u5916\u5c04\u8fdb\u6765\uff0c\u843d\u5728\u9648\u9759\u7684\u8138\u4e0a\uff0c\u843d\u5bde\u800c\u4f18\u96c5\u3002</p><p>\u3000\u3000\u201c\u770b\u4ec0\u4e48\uff1f\u201d\u9648\u9759\u70b9\u8fc7\u9910\uff0c\u5c06\u83dc\u5355\u8fd8\u7ed9\u4f8d\u5e94\u751f\uff0c\u770b\u7740\u5f20\u606a\u6e05\u6f88\u7684\u773c\u7738\u770b\u7740\u81ea\u5df1\uff0c\u4e0d\u662f\u521a\u624d\u90a3\u79cd\u7ed9\u9b45\u60d1\u4f4f\u7684\u773c\u795e\u2014\u2014\u5373\u4f7f\u521a\u624d\u7ed9\u8fd9\u5bb6\u4f19\u6709\u51e0\u5206\u8272\u772f\u772f\u7684\u76ef\u7740\u80f8\u90e8\u5728\u770b\uff0c\u5fc3\u91cc\u4e00\u70b9\u90fd\u6ca1\u6709\u6392\u65a5\u7684\u610f\u613f\uff0c\u8fd9\u4e48\u60f3\u7740\uff0c\u5fc3\u91cc\u5c31\u6709\u4e9b\u53d1\u75d2\uff0c\u8fd9\u65f6\u5019\u62ac\u624b\u5728\u5f20\u606a\u773c\u524d\u6325\u4e86\u6325\uff0c\u201c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u6709\u4ec0\u4e48\u66f4\u597d\u770b\u7684\uff1f\u201d\u5f20\u606a\u6536\u56de\u76ee\u5149\uff0c\u77a5\u4e86\u7a97\u5916\u5915\u9633\u4e00\u773c\uff0c\u201c\u521a\u624d\uff0c\u5915\u9633\u843d\u5728\u4f60\u7684\u8138\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9648\u9759\u611f\u89c9\u5fc3\u91cc\u67d0\u5904\u4e0d\u4e89\u6c14\u7684\u7ed9\u6253\u52a8\u4e86\uff0c\u4e0d\u662f\u5e94\u8be5\u6307\u8d23\u4ed6\u8bf4\u8bdd\u8f7b\u6d6e\u624d\u662f\uff1f\u53c8\u63a9\u9970\u7684\u7b11\u7740\u95ee\uff0c\u201c\u606a\u5c11\u4e00\u5411\u90fd\u662f\u8fd9\u6837\u8ba8\u597d\u5973\u5b69\u5b50\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u968f\u53e3\u8bf4\u8bf4\uff0c\u53ef\u6ca1\u6709\u60f3\u7740\u8981\u8ba8\u597d\u4f60\u3002\u201d\u5f20\u606a\u5b09\u8138\u800c\u7b11\uff0c\u4ece\u4f8d\u5e94\u751f\u624b\u91cc\uff0c\u70b9\u4e86\u4e00\u4efd\u70ad\u706b\u70e4\u660e\u867e\u3001\u7ea2\u9152\u8fde\u9aa8\u725b\u6392\u3002</p><p>\u3000\u3000\u7528\u9910\u65f6\uff0c\u5012\u662f\u4e00\u672c\u6b63\u7ecf\u7684\u8c08\u8bba\u5546\u4e1a\u4e0a\u7684\u4e8b\u60c5\uff0c\u5728\u9648\u5217\u5385\u65f6\uff0c\u9648\u9759\u5c31\u542c\u5230\u5f20\u606a\u4e00\u756a\u5173\u4e8e\u516c\u53f8\u4e0e\u54c1\u724c\u7684\u8a00\u8bba\uff0c\u5728\u9648\u5217\u5385\u91cc\uff0c\u8fd8\u6ca1\u6709\u673a\u4f1a\u542c\u5f20\u606a\u7ec6\u8bf4\uff0c\u7528\u9910\u65f6\uff0c\u4fbf\u62ff\u51fa\u6765\u5f53\u6210\u8bdd\u9898\u8c08\u8bba\u3002</p><p>\u3000\u3000\u9648\u9759\u5f88\u65e9\u5c31\u53bb\u6b27\u6d32\u7559\u5b66\uff0c\u8fd9\u4e24\u5e74\u56de\u5230\u56fd\u5185\uff0c\u624d\u6653\u5f97\u8981\u5c06\u56fd\u5916\u90a3\u4e9b\u5148\u8fdb\u7684\u5e02\u573a\u7406\u8bba\u7528\u5728\u56fd\u5185\uff0c\u8fd8\u662f\u4e00\u9879\u5f88\u6709\u6311\u6218\u6027\u4e0e\u521b\u9020\u6027\u7684\u4e8b\u4e1a\uff0c\u8ba4\u771f\u4ea4\u8c08\u8fc7\uff0c\u624d\u6653\u5f97\u5f20\u606a\u8eab\u4e0a\u5177\u5907\u7684\u5e76\u4e0d\u4ec5\u4ec5\u662f\u5bf9\u5546\u4e1a\u7684\u5929\u751f\u654f\u9510\uff0c\u4ed6\u672c\u8eab\u5bf9\u5e02\u573a\u8fd0\u8425\u7684\u7814\u7a76\u4e5f\u6781\u4e3a\u6df1\u5165\u3002</p><p>\u3000\u3000\u201c\u65e9\u77e5\u9053\u8fd9\u6837\uff0c\u4e5f\u4e0d\u7528\u51fa\u56fd\u7559\u5b66\u4e86\uff0c\u62dc\u4f60\u4e3a\u5e08\u5f97\u4e86\u3002\u201d\u7528\u8fc7\u9910\uff0c\u9648\u9759\u62db\u547c\u4f8d\u5e94\u751f\u8fc7\u6765\u4e70\u5355\uff0c\u7b11\u7740\u8ddf\u5f20\u606a\u5f00\u73a9\u7b11\u3002\u7684\u786e\uff0c\u5979\u9047\u5230\u5f88\u591a\u559c\u6b22\u5728\u5979\u9762\u524d\u5938\u5938\u5176\u8c08\u7684\u4eba\uff0c\u6709\u5546\u754c\u65b0\u79c0\u3001\u6709\u5b66\u672f\u754c\u7684\u5b66\u8005\uff0c\u4f46\u90fd\u5bf9\u5546\u4e1a\u672c\u8d28\u6027\u7684\u601d\u8003\u90fd\u8fdc\u8fdc\u4e0d\u53ca\u5f20\u606a\u6765\u5f97\u6df1\u523b\u3002\u4e4b\u524d\u6253\u7b97\u7528\u8fc7\u9910\u5c31\u56de\u53bb\u7761\u89c9\uff0c\u867d\u7136\u5728\u9910\u9986\u8c08\u4e86\u5f88\u4e45\uff0c\u9648\u9759\u8fd9\u65f6\u5019\u8fd8\u662f\u610f\u72b9\u672a\u5c3d\uff0c\u8fd8\u60f3\u591a\u542c\u5f20\u606a\u8bf4\u8bf4\u8bdd\uff0c\u53c8\u4e00\u540c\u79bb\u5f00\u897f\u9910\u5385\u3002</p></article>"}}}]);
