(window.webpackJsonp=window.webpackJsonp||[]).push([[874],{897:function(p){p.exports={chapterTitle:null,title:"\u7b2c698\u7ae0 \u900f\u4e00\u53e3\u6c14",content:"<article><h2>\u7b2c698\u7ae0 \u900f\u4e00\u53e3\u6c14</h2><p>\u3000\u3000\u5df2\u7ecf\u662f\u5341\u4e00\u6708\u4e0b\u65ec\u4e86\uff0c\u8c61\u5c71\u7684\u666f\u81f4\u6781\u4e3a\u8010\u770b\u3002\u8f66\u9a76\u8fdb\u8c61\u5c71\uff0c\u67ab\u6811\u3001\u9ec4\u680c\u6811\u7684\u679d\u5934\u7ea2\u53f6\u4eff\u4f5b\u6f2b\u5c71\u904d\u91ce\u7684\u706b\u7130\uff0c\u4ece\u67cf\u6cb9\u8def\u4fa7\u6b67\u751f\u4e0a\u53bb\u7684\u77f3\u9636\u67d3\u7740\u4e9b\u8bb8\u9752\u82d4\uff0c\u4e5f\u6709\u7ed9\u706b\u7130\u71c3\u7740\u4f3c\u7684\u611f\u89c9\uff0c\u8def\u8fb9\u843d\u4e00\u4e9b\u7ea2\u8910\u8272\u7684\u53f6\u5b50\uff0c\u968f\u98ce\u7ffb\u6eda\uff0c\u8fdc\u5904\u6709\u5bfa\u5e99\u7684\u60ac\u94c3\u5728\u98ce\u4e2d\u8f7b\u54cd\u2026\u2026</p><p>\u3000\u3000\u201c\u55ec\uff0c\u679c\u771f\u4e0d\u900a\u8272\u7535\u89c6\u91cc\u770b\u5230\u7684\u753b\u9762\u3002\u201d\u674e\u99a8\u4e88\u5706\u6da6\u5149\u6d01\u7684\u4e0b\u9894\u78d5\u5728\u8f66\u7a97\u4e0a\u51dd\u671b\u7740\u6f2b\u5c71\u904d\u91ce\u7684\u706b\u7130\u4f3c\u7684\u9ec4\u680c\u6811\u6797\uff0c\u773c\u524d\u7684\u7f8e\u666f\u540c\u6837\u51fa\u73b0\u5728\u7231\u8fbe\u789f\u673a\u7684\u7535\u89c6\u5e7f\u544a\u91cc\u3002</p><p>\u3000\u3000\u751f\u6d3b\u5728\u8fd9\u6837\u7684\u5bb6\u65cf\u91cc\uff0c\u674e\u99a8\u4e88\u611f\u5230\u4e00\u79cd\u7ed9\u957f\u671f\u7981\u9522\u4f4f\u7684\u538b\u6291\uff0c\u4eca\u5929\u5979\u4e5f\u6ca1\u6709\u6307\u671b\u80fd\u6446\u8131\u4ed6\u4eec\uff0c\u53ea\u662f\u4e00\u65f6\u8d77\u4e86\u6027\u5b50\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u4f1a\u649e\u4e0a\u5f20\u606a\uff0c\u8fd8\u7a00\u91cc\u7cca\u6d82\u7684\u7ed9\u5f20\u606a\u62d0\u9a97\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u5fae\u7fd8\uff0c\u6302\u7740\u4f3c\u7b11\u975e\u7b11\u7684\u8f7b\u677e\u8868\u60c5\uff0c\u5c06\u674e\u99a8\u4e88\u62d0\u9a97\u51fa\u6765\uff0c\u5979\u8fd8\u4e00\u4e2a\u52b2\u7684\u62c5\u5fc3\u4ed6\u8ddf\u7740\u9003\u51fa\u6765\u4f1a\u8ba9\u4eca\u5929\u7684\u63a5\u5f85\u6d3b\u52a8\u53d8\u5f97\u4e00\u56e2\u7cdf\uff0c\u8fd9\u65f6\u5019\u5012\u6c89\u6d78\u5728\u8c61\u5c71\u6df1\u79cb\u7684\u666f\u8272\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5f53\u521d\u51b3\u5b9a\u5c06\u6d77\u5dde\u6709\u4ee3\u8868\u6027\u7684\u666f\u8272\u6444\u5165\u5e7f\u544a\u5c31\u6709\u4e3a\u6d77\u5dde\u65c5\u6e38\u634e\u5e26\u5ba3\u4f20\u4e4b\u610f\uff0c\u4e0d\u8fc7\u4eca\u5929\u4e0d\u662f\u5468\u672b\uff0c\u4e5f\u6709\u4e09\u4e09\u4e24\u4e24\u7684\u4eba\u7fa4\u6cbf\u7740\u67cf\u6cb9\u8def\u6b65\u884c\u767b\u5c71\u770b\u7ea2\u53f6\u3002</p><p>\u3000\u3000\u5c06\u8f66\u505c\u5728\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u7684\u5e7f\u573a\u4e0a\uff0c\u5f20\u606a\u966a\u674e\u99a8\u4e88\u6b65\u884c\u8d70\u8fdb\u68ee\u6797\u516c\u56ed\uff0c\u7a7f\u8fc7\u9ec4\u680c\u6811\u56ed\uff0c\u7ad9\u5728\u5357\u5d16\u8fb9\u770b\u5d16\u811a\u4e0b\u5c0f\u6c5f\u7684\u98ce\u666f\uff0c\u5c71\u524d\u51cc\u4e71\u7684\u5efa\u7b51\u6089\u5df2\u62c6\u9664\uff0c\u5df2\u7ecf\u5efa\u6210\u5927\u7247\u7684\u690d\u7269\u56ed\uff0c\u518d\u8fdc\u5904\u5219\u662f\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\uff0c\u5c0f\u6c5f\u8d34\u7740\u8c61\u5c71\u897f\u5c71\u811a\u62d0\u4e86\u4e00\u4e2a\u5927\u5f2f\uff0c\u5c06\u76ca\u9686\u9547\u897f\u5357\u89d2\u90fd\u5305\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u4e0d\u6653\u5f97\u674e\u99a8\u4e88\u7684\u201c\u51fa\u9003\u201d\u4f1a\u4e0d\u4f1a\u8ba9\u674e\u5728\u6d19\u7684\u5fc3\u60c5\u53d8\u5f97\u7cdf\u7cd5\uff0c\u5f20\u606a\u53ef\u4e0d\u7ba1\u4ed6\uff0c\u5728\u8c61\u5c71\u505c\u7559\u7247\u523b\uff0c\u89c1\u674e\u99a8\u4e88\u63d0\u51fa\u8981\u53bb\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u53bb\u770b\uff0c\u4fbf\u5f00\u8f66\u4e0b\u5c71\u62d0\u5230\u5357\u5d16\u4e0b\uff0c\u4ece\u690d\u7269\u56ed\u91cc\u7a7f\u8fc7\u53bb\uff0c\u8fdb\u540e\u95e8\u8fdb\u4e86\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\uff0c\u8d70\u5230\u665a\u6674\u7684\u529e\u516c\u5ba4\uff0c\u8d6b\u7136\u53d1\u73b0\u8c22\u5b50\u5609\u4e5f\u5750\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u8c22\u5b50\u5609\u6b63\u8981\u6307\u8d23\u5f20\u606a\u5c06\u5979\u968f\u4fbf\u4e22\u5728\u9648\u5217\u5385\u7684\u5351\u52a3\u4e4b\u4e3e\uff0c\u770b\u5230\u8ddf\u5728\u5f20\u606a\u8eab\u540e\u8d70\u8fdb\u6765\u7684\u674e\u99a8\u4e88\uff0c\u660e\u79c0\u7684\u7738\u5b50\u4e0d\u53ef\u601d\u5f02\u7684\u77aa\u5706\u4e86\uff1a\u600e\u4e48\u53ef\u80fd\uff1f\u8fd9\u5c0f\u5b50\u8f6c\u8eab\u8d70\u5f00\u5c31\u662f\u8981\u5c06\u88ab\u89c6\u4e3a\u4e09\u661f\u516c\u4e3b\u7684\u674e\u99a8\u4e88\u62d0\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u4e09\u661f\u7684\u674e\u99a8\u4e88\u5c0f\u59d0\uff1f\u201d\u665a\u6674\u7591\u60d1\u7684\u770b\u7740\u8ddf\u5f20\u606a\u8d70\u8fdb\u6765\u7684\u8fd9\u4e2a\u59ff\u8272\u7adf\u7136\u4e1d\u6beb\u4e0d\u5f31\u4e8e\u8bb8\u601d\u3001\u6c5f\u9edb\u513f\u7684\u5e74\u8f7b\u7f8e\u5973\uff0c\u201c\u542c\u8bf4\u4f60\u4ece\u9526\u6e56\u5927\u53a6\u5931\u8e2a\u4e86\uff0c\u4e09\u661f\u9a7b\u534e\u516c\u53f8\u7684\u4e13\u52a1\u674e\u5728\u6d19\u5dee\u70b9\u5c31\u8981\u62a5\u8b66\u2026\u2026\u201d\u521a\u624d\u8fd8\u542c\u5b50\u5609\u8bf4\u8d77\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u201c\u6253\u6270\u4e86\uff0c\u201d\u674e\u99a8\u4e88\u6709\u4e9b\u4e0d\u5b89\u7684\u97a0\u8eac\u6253\u62db\u547c\uff0c\u201c\u6211\u4efb\u6027\u7684\u9003\u51fa\u6765\u900f\u4e00\u53e3\u6c14\uff0c\u5374\u7ed9\u4f60\u4eec\u6dfb\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u9ebb\u70e6\u5012\u662f\u672a\u5fc5\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u674e\u5728\u6d19\u4e13\u52a1\u4e5f\u5fc5\u987b\u8981\u71ac\u8fc7\u4e8c\u5341\u56db\u5c0f\u65f6\u624d\u80fd\u62a5\u8b66\uff0c\u201d\u665a\u6674\u542c\u674e\u99a8\u4e88\u8fd9\u4e48\u8bf4\uff0c\u5012\u662f\u6709\u4e9b\u540c\u75c5\u76f8\u601c\u7684\u611f\u89e6\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u53ea\u662f\u597d\u5947\uff0c\u4f60\u600e\u4e48\u4f1a\u8ddf\u7740\u5f20\u606a\u9003\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u5148\u751f\u6bcf\u5929\u7ed9\u516c\u53f8\u7684\u4e8b\u52a1\u538b\u5728\u80a9\u4e0a\uff0c\u538b\u529b\u5f88\u5927\uff0c\u4e5f\u9700\u8981\u9003\u51fa\u6765\u900f\u4e00\u53e3\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u5dee\u70b9\u6251\u54e7\u7b11\u51fa\u58f0\uff0c\u7ed9\u6253\u8d25\u4f3c\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4f3c\u6028\u4f3c\u55d4\uff0c\u8fd9\u5bb6\u4f19\u7adf\u662f\u7528\u8fd9\u4e48\u62d9\u52a3\u7684\u501f\u53e3\u5c31\u5c06\u674e\u99a8\u4e88\u7ed9\u62d0\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8fd9\u65f6\u5019\u4e5f\u4e0d\u591a\u52a0\u89e3\u91ca\u4ec0\u4e48\uff0c\u5750\u8fc7\u53bb\uff0c\u95ee\u8c22\u5b50\u5609\u600e\u4e48\u4f1a\u4e2d\u5348\u5230\u8fd9\u91cc\u6765\u3002</p><p>\u3000\u3000\u201c\u9648\u9759\u95f2\u4e0d\u4e0b\u53bb\uff0c\u6211\u53ea\u6709\u81ea\u5df1\u627e\u5730\u65b9\u6253\u53d1\u65f6\u95f4\uff0c\u4e0d\u7ed9\u5979\u6dfb\u4e71\u2026\u2026\u201d\u8c22\u5b50\u5609\u8bf4\u9053\uff0c\u867d\u7136\u5979\u4ee5\u4e2a\u4eba\u7684\u540d\u4e49\u6b63\u5f0f\u5165\u8d44\u79d1\u738b\u9ad8\u79d1\uff0c\u6301\u670925\uff05\u7684\u80a1\u6743\uff0c\u4f46\u662f\u7e41\u7410\u800c\u7ec6\u788e\u7684\u516c\u53f8\u4e8b\u52a1\uff0c\u5979\u662f\u4e0d\u4f1a\u53c2\u4e0e\u7684\uff0c\u88ab\u5f20\u606a\u7529\u5f00\uff0c\u5979\u53c8\u4e0d\u80fd\u7559\u5728\u9526\u6e56\u5927\u53a6\u7ed9\u9648\u9759\u6dfb\u4e71\uff0c\u4fbf\u5230\u8fd9\u91cc\u6765\u627e\u665a\u6674\uff0c\u6ca1\u60f3\u5230\u5728\u8fd9\u91cc\u5c06\u5f20\u606a\u902e\u5230\u6b63\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u6324\u7740\u7709\u5934\u6b63\u60f3\u627e\u4e2a\u501f\u53e3\u89e3\u91ca\u4e0a\u5348\u4e3a\u4ec0\u4e48\u8981\u5c06\u5979\u4e22\u5728\u9648\u5217\u5385\u800c\u8fd9\u65f6\u5019\u53c8\u4e0e\u674e\u99a8\u4e88\u540c\u65f6\u51fa\u73b0\u5b66\u6821\u91cc\uff0c\u88e4\u515c\u91cc\u7684\u624b\u673a\u5c31\u5584\u89e3\u4eba\u610f\u7684\u9707\u52a8\u8d77\u6765\uff0c\u662f\u82cf\u6d25\u4e1c\u6253\u6765\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u8fd9\u65f6\u5019\uff0c\u5e02\u59d4\u5e02\u653f\u5e9c\u5df2\u7ecf\u5c06\u803f\u91cd\u9633\u3001\u674e\u8fdc\u6e56\u7b49\u4eba\u63a5\u5230\u4e2d\u5348\u7528\u9910\u7684\u5317\u5c71\u996d\u5e97\uff0c\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u7acb\u6d3b\u52a8\u7b97\u662f\u6b63\u5f0f\u5f00\u59cb\u4e86\u3002</p><p>\u3000\u3000\u5bf9\u6d77\u5dde\u5e02\u59d4\u5e02\u653f\u5e9c\u6765\u8bf4\uff0c\u8fd9\u53ef\u4e0d\u4ec5\u4ec5\u662f\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u7acb\u4eea\u5f0f\uff0c\u800c\u662f\u4e00\u4e2a\u4ee4\u4eba\u5f02\u5e38\u5174\u594b\u7684\u5927\u578b\u62db\u5546\u6d3b\u52a8\uff0c\u6d3b\u52a8\u4e2d\u8981\u4e0e\u8bb8\u591a\u5bb6\u4f01\u4e1a\u7b7e\u7f72\u5230\u6d77\u5dde\u6765\u7684\u6295\u8d44\u534f\u8bae\u3002</p><p>\u3000\u3000\u6839\u636e\u5341\u516b\u65e5\u5c31\u516c\u5e03\u7684\u6570\u5b57\u624b\u673a\u6280\u672f\u516c\u5f00\u65b9\u6848\u91cc\u89c4\u5b9a\uff0c\u53ea\u6709\u5728\u6d77\u5dde\u5e02\u5de5\u4e1a\u65b0\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u533a\u6295\u8d44\u6ce8\u518c\u7684\u4f01\u4e1a\u5b9e\u4f53\u624d\u6709\u8d44\u683c\u83b7\u5f97\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6280\u672f\u6388\u6743\uff0c\u800c\u4e14\u6280\u672f\u6388\u6743\u5bf9\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u4e0e\u5efa\u90ba\u9ad8\u65b0\u533a\u7684\u4f01\u4e1a\u4f1a\u6709\u4e0d\u540c\u7684\u4fa7\u91cd\u8303\u56f4\uff0c\u4ece\u800c\u907f\u514d\u5927\u591a\u6570\u4f01\u4e1a\u9009\u62e9\u53bb\u5efa\u90ba\u9ad8\u65b0\u533a\u6295\u8d44\u6ce8\u518c\u3002</p><p>\u3000\u3000\u4ece\u5341\u516b\u65e5\u5230\u4eca\u5929\u624d\u8fc7\u53bb\u4e0d\u5230\u4e00\u661f\u671f\u7684\u65f6\u95f4\uff0c\u6d77\u5dde\u5e02\u62db\u5546\u90e8\u95e8\u63a5\u6536\u5230\u7684\u6295\u8d44\u54a8\u8be2\u751a\u81f3\u8d85\u8fc7\u4e5d\u516d\u5e74\u5168\u5e74\u603b\u548c\uff0c\u6295\u8d44\u8005\u4e2d\u4e0d\u4ec5\u6709\u4e2a\u4eba\u6295\u8d44\u8005\uff0c\u4e5f\u6709\u50cf\u4e2d\u5929\u79d1\u6280\u8fd9\u6837\u7684\u5927\u578b\u7535\u5b50\u96c6\u56e2\u3002</p><p>\u3000\u3000\u9526\u6e56\u629b\u51fa\u4e00\u4e2a\u8ba9\u4eba\u65e0\u6cd5\u62d2\u7edd\u7684\u8bf1\u9975\uff1a\u4e0d\u4ec5\u53ef\u4ee5\u514d\u8d39\u4f7f\u7528\u76f8\u5173\u7684\u624b\u673a\u5236\u884c\u4e1a\u6280\u672f\uff0c\u5e76\u4e14\u5728\u76f8\u5173\u6280\u672f\u6709\u6240\u7a81\u7834\u53c8\u5c06\u83b7\u5f97\u5b8c\u6574\u7684\u77e5\u8bc6\u4ea7\u6743\uff0c\u800c\u4e0d\u7528\u62c5\u5fc3\u9526\u6e56\u8fd9\u65f6\u5019\u4f1a\u8df3\u51fa\u6765\u8981\u6c42\u4eab\u53d7\u4ec0\u4e48\u6743\u76ca\u3002</p><p>\u3000\u3000\u91d1\u5357\u52c7\u4e0a\u5348\u5728\u9526\u6e56\u5927\u53a6\u9648\u5217\u5385\u4e0e\u674e\u5728\u6d19\u7684\u4ea4\u8c08\u4e2d\u8bf4\u5230\u9526\u6e56\u5176\u5b9e\u662f\u5229\u7528\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u672a\u6765\u5f88\u6709\u53ef\u80fd\u4f1a\u5c06\u677e\u6563\u7684\u56fd\u4ea7\u624b\u673a\u5382\u5546\u62e7\u6210\u4e00\u80a1\u7ef3\u6765\u5171\u540c\u62b5\u5fa1\u5916\u8d44\u54c1\u724c\u7684\u5165\u4fb5\u3002\u679c\u771f\u662f\u5bf9\u624b\u6700\u4e86\u89e3\u5bf9\u624b\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u8fd9\u4e9b\u4f01\u4e1a\u521a\u5f00\u59cb\u7684\u6295\u8d44\u989d\u5ea6\u90fd\u5f88\u6709\u9650\uff0c\u4ece\u51e0\u5341\u4e07\u5230\u51e0\u767e\u4e07\u4e0d\u7b49\uff0c\u6ca1\u6709\u54ea\u5bb6\u516c\u53f8\u5728\u4ec0\u4e48\u90fd\u6ca1\u6709\u5e95\u7684\u60c5\u51b5\u7838\u51fa\u4e0a\u4ebf\u5143\u7684\u8d44\u91d1\uff0c\u591a\u662f\u6295\u8d44\u6210\u7acb\u50cf\u79d1\u6280\u516c\u53f8\u3001\u7814\u53d1\u516c\u53f8\u4e4b\u7c7b\u7684\u5e26\u7814\u53d1\u6027\u8d28\u7684\u673a\u6784\u6765\u5438\u6536\u6d88\u5316\u9526\u6e56\u8fd9\u6b21\u516c\u5f00\u7684\u6570\u5b57\u624b\u673a\u5236\u9020\u6280\u672f\u3002\u4e00\u65e6\u5c06\u8fd9\u4e9b\u6280\u672f\u5438\u5f15\u3001\u6d88\u5316\u4e3a\u5df1\u7528\uff0c\u5e76\u786e\u5b9a\u6709\u5e7f\u9614\u7684\u5e02\u573a\u524d\u666f\uff0c\u8fd9\u4e9b\u6295\u8d44\u624d\u4f1a\u8003\u8651\u8fdb\u4e00\u6b65\u8ffd\u52a0\u6295\u8d44\u5efa\u5382\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5982\u6b64\uff0c\u6295\u8d44\u610f\u5411\u603b\u989d\u4e5f\u9ad8\u8fbe\u4e24\u4ebf\u591a\uff1b\u5f53\u7136\uff0c\u6f5c\u5728\u7684\u8ffd\u52a0\u6295\u8d44\u989d\u5ea6\u6bd4\u8fd9\u4e2a\u6570\u636e\u8981\u5e9e\u5927\u5f97\u591a\u3002\u53ef\u4ee5\u8bf4\uff0c\u53ea\u8981\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u8fd0\u8425\u5f97\u5f53\uff0c\u6216\u8bb8\u4f1a\u76f4\u63a5\u5c06\u6d77\u5dde\u5e02\u7684\u7ecf\u6d4e\u518d\u63a8\u4e0a\u4e00\u4e2a\u5168\u65b0\u7684\u53f0\u9636\uff0c\u5c06\u5728\u6d77\u5dde\u5e02\u5f62\u6210\u4e00\u4e2a\u4e0e\u5efa\u90ba\u5e02\u4e92\u8865\u7684\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u3002</p><p>\u3000\u3000\u4e00\u4e0b\u5b50\u8981\u5438\u7eb3\u8fd9\u4e48\u591a\u7684\u6295\u8d44\uff0c\u6ce8\u518c\u6210\u7acb\u8fd9\u4e48\u591a\u5bb6\u516c\u53f8\uff0c\u5bf9\u6d77\u5dde\u5e02\u7684\u4eba\u529b\u8d44\u6e90\u4e5f\u662f\u4e00\u79cd\u8003\u9a8c\uff0c\u5f53\u7136\u4e5f\u4f1a\u66f4\u8fc5\u901f\u3001\u66f4\u6709\u529b\u7684\u5438\u5f15\u5916\u7701\u5e02\u7684\u4f18\u79c0\u4eba\u624d\u6c47\u805a\u5230\u6d77\u5dde\u6765\uff1b\u4e5f\u4ee4\u6d77\u5dde\u5de5\u4e1a\u65b0\u533a\u8303\u56f4\u5185\u7684\u529e\u516c\u7528\u5730\u79df\u8d41\u9aa4\u7136\u706b\u7206\u8d77\u6765\uff0c\u79df\u91d1\u5728\u8fc7\u53bb\u4e00\u661f\u671f\u65f6\u95f4\u5185\u6fc0\u589e\u4e00\u500d\u4ecd\u7136\u4f9b\u4e0d\u5e94\u6c42\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u5c31\u4fc3\u4f7f\u5e02\u91cc\u6700\u7ec8\u51b3\u5b9a\u5f7b\u5e95\u5173\u505c\u76ca\u9686\u9547\u6700\u5927\u7684\u4e00\u5bb6\u5370\u67d3\u5382\uff0c\u7531\u6d77\u5dde\u63a7\u80a1\u8d1f\u8d23\u5c06\u8fd9\u5bb6\u5370\u67d3\u5382\u6539\u9020\u6210\u79d1\u6280\u56ed\u51fa\u79df\uff0c\u5e76\u52a0\u901f\u5728\u8c61\u5c71\u4e1c\u9e93\u7684\u9526\u6e56\u65b0\u57ce\u7684\u5efa\u8bbe\u3002</p><p>\u3000\u3000\u5bf9\u8fd9\u5bb6\u5370\u67d3\u5382\uff0c\u5f20\u606a\u5fc3\u91cc\u662f\u5f88\u6e05\u695a\u7684\uff0c\u5c31\u5728\u76ca\u9686\u9547\u7684\u4e1c\u5317\u89d2\u3002\u867d\u7136\u8fd9\u5bb6\u5370\u67d3\u5382\u6bcf\u5e74\u4e0a\u7f34\u7684\u5229\u7a0e\u5dee\u4e0d\u591a\u6709\u4e24\u4e09\u5343\u4e07\uff0c\u5374\u4e5f\u662f\u6d77\u5dde\u9664\u5c0f\u6c5f\u4e4b\u5916\u4e3b\u8981\u6c34\u7cfb\u5357\u5ddd\u6cb3\u6700\u4e3b\u8981\u7684\u6c61\u67d3\u6e90\uff0c\u51e0\u4e4e\u5c06\u5357\u5ddd\u6cb3\u4ece\u76ca\u9686\u9547\u5230\u65b0\u6865\u9547\u7684\u6cb3\u6bb5\u90fd\u53d8\u6210\u9ed1\u6c34\u2014\u2014\u8981\u771f\u4e0d\u4e0b\u51b3\u5fc3\u5173\u505c\uff0c\u5357\u5ddd\u6cb3\u5c06\u5f7b\u5e95\u7ed9\u5e9f\u6389\u3002\u4e4b\u524d\u5e02\u8d22\u653f\u7d27\u5f20\uff0c\u5c31\u7b97\u770b\u5230\u5357\u5ddd\u6cb3\u9010\u5e74\u6210\u4e3a\u9ed1\u6c34\u6cb3\uff0c\u4e5f\u7edd\u8ba1\u820d\u4e0d\u5f97\u5c06\u4e24\u4e09\u5343\u4e07\u7684\u5229\u7a0e\u7ed9\u780d\u6389\u3002\u73b0\u5728\u5e02\u91cc\u80fd\u4ece\u789f\u673a\u4ea7\u4e1a\u96c6\u7fa4\u91cc\u4e00\u4e0b\u5b50\u589e\u6536\u8fd1\u5341\u4ebf\u7684\u8d22\u653f\u6536\u5165\uff0c\u4e4b\u524d\u820d\u4e0d\u5f97\u780d\u6389\u7684\u91cd\u6c61\u67d3\u4f01\u4e1a\u4e5f\u820d\u5f97\u843d\u5200\u4e86\uff0c\u5373\u4f7f\u780d\u51fa\u6765\u7684\u4e0b\u5c97\u5de5\u4eba\uff0c\u6d77\u5dde\u4e5f\u6709\u8db3\u591f\u591a\u7684\u65b0\u589e\u4f01\u4e1a\u80fd\u591f\u5438\u6536\u3002</p><p>\u3000\u3000\u5c31\u4eba\u529b\u8d44\u6e90\u65b9\u9762\uff0c\u5efa\u90ba\u5e02\u7684\u4f18\u52bf\u66f4\u660e\u663e\u4e00\u4e9b\uff0c\u50cf\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u90fd\u5df2\u7ecf\u51b3\u5b9a\u5728\u5efa\u90ba\u6210\u7acb\u5404\u81ea\u7684\u6570\u5b57\u624b\u673a\u7814\u7a76\u4e2d\u5fc3\u6765\u6d88\u5316\u8fd9\u6b21\u516c\u5f00\u7684\u5168\u90e8\u6280\u672f\u3002\u4f46\u662f\u9664\u4e86\u5c11\u6570\u80fd\u83b7\u5f97\u5168\u90e8\u6280\u672f\u6388\u6743\u7684\u4f01\u4e1a\u4e4b\u5916\uff0c\u5176\u4ed6\u4f01\u4e1a\u53ea\u80fd\u6839\u636e\u81ea\u8eab\u7684\u6280\u672f\u80cc\u666f\u6765\u9012\u4ea4\u7533\u8bf7\uff0c\u53c8\u8981\u6839\u636e\u6280\u672f\u516c\u5f00\u65b9\u6848\u91cc\u5bf9\u6d77\u5dde\u3001\u5efa\u90ba\u7684\u4fa7\u91cd\u8303\u56f4\u4e0d\u540c\u6765\u9009\u62e9\u6ce8\u518c\u6295\u8d44\u5730\u65b9\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7b97\u662f\u5728\u5efa\u90ba\u5e02\u71d5\u5f52\u6e56\u5730\u533a\u5f62\u6210\u4e00\u4e2a\u624b\u673a\u82af\u7247\u8bbe\u8ba1\u3001\u624b\u673a\u8bbe\u8ba1\u3001\u8f6f\u4ef6\u5f00\u53d1\u7684\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u6240\u4ee5\u76f8\u5173\u6280\u672f\u53cd\u800c\u5728\u6d77\u5dde\u7684\u4f01\u4e1a\u4e0d\u5bb9\u6613\u83b7\u5f97\u6388\u6743\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u5f20\u606a\uff0c\u4f60\u8bf4\u8981\u6559\u6211\u653e\u7eb8\u9e22\uff0c\u4f60\u4e0d\u8bb8\u518d\u800d\u8d56\u76ae\u2026\u2026\u201d\u82b7\u5f64\u624b\u4e3e\u7740\u7eb8\u9e22\u5728\u8349\u576a\u4e0a\u4e71\u8dd1\uff0c\u4e0b\u5348\u7684\u6d77\u5dde\u98ce\u548c\u65e5\u4e3d\uff0c\u5403\u8fc7\u4e2d\u996d\uff0c\u5979\u4e0e\u60dc\u5bb9\u3001\u60dc\u7fbd\u4e24\u5c0f\u59d0\u59b9\u5c31\u62fd\u7740\u5f20\u606a\u51fa\u6765\u653e\u7eb8\u9e22\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u4f3c\u4e4e\u5f88\u4eab\u53d7\u5b66\u6821\u91cc\u7684\u6c1b\u56f4\uff0c\u4e5f\u4e0d\u6025\u7740\u79bb\u5f00\uff0c\u76f4\u5230\u9ec4\u660f\u65f6\u5206\uff0c\u5f20\u606a\u624d\u8ba9\u5b66\u6821\u7684\u53f8\u673a\u5c06\u5979\u9001\u56de\u4e0b\u69bb\u7684\u5bbe\u9986\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u53bb\u7406\u4f1a\u674e\u5728\u6d19\u77e5\u9053\u540e\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\u3002\u9001\u8d70\u674e\u99a8\u4e88\u4e4b\u540e\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u8fd8\u8981\u56de\u5e02\u533a\u53c2\u52a0\u63a5\u5f85\u665a\u5bb4\uff0c\u5728\u665a\u5bb4\u4e0a\u4e5f\u8981\u4e0e\u90ae\u7535\u90e8\u90e8\u957f\u803f\u91cd\u9633\u8fd9\u4e9b\u90ae\u7535\u90e8\u7684\u5b98\u5458\u6b63\u5f0f\u89c1\u9762\uff0c\u5c4a\u65f6\u4e5f\u6709\u4f1a\u7535\u5b50\u5de5\u4e1a\u90e8\u7684\u5b98\u5458\u5728\u573a\u3002</p></article>"}}}]);
