(window.webpackJsonp=window.webpackJsonp||[]).push([[875],{898:function(p){p.exports={chapterTitle:null,title:"\u7b2c872\u7ae0 \u571f\u575d\u62a4\u57ce",content:"<article><h2>\u7b2c872\u7ae0 \u571f\u575d\u62a4\u57ce</h2><p>\u3000\u3000\u4e5d\u6c5f\u6cb3\u897f\u5cb8\uff0c\u6570\u4e07\u4eba\u5f62\u6210\u4e00\u652f\u6570\u516c\u91cc\u957f\u7684\u961f\u4f0d\uff0c\u8d64\u818a\u4e0a\u9635\u3001\u6325\u9539\u8f6e\u9504\uff0c\u5c06\u6398\u8d77\u7684\u571f\u62cd\u5b9e\u5728\u773c\u524d\uff0c\u98de\u5feb\u7684\u5c06\u8eab\u524d\u7684\u571f\u575d\u62cd\u5b9e\u5792\u9ad8\uff0c\u5f62\u6210\u4e00\u6761\u571f\u9ec4\u8272\u7684\u957f\u9f99\u6a2a\u5367\u5728\u4e5d\u6c5f\u6cb3\u897f\u5cb8\u3002</p><p>\u3000\u3000\u5f20\u606a\u966a\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u4ee5\u53ca\u7701\u519b\u5206\u533a\u53f8\u4ee4\u90b1\u51ef\u5c11\u5c06\u7ad9\u5728\u4e5d\u6c5f\u6cb3\u897f\u5cb8\u7684\u4e00\u5ea7\u516b\u5c42\u697c\u9ad8\u7684\u529e\u516c\u697c\u9876\u4e0a\uff0c\u795e\u8272\u51dd\u91cd\u3001\u8138\u8272\u715e\u767d\u7684\u770b\u7740\u4e5d\u6c5f\u6cb3\u4e1c\u5cb8\u7684\u6ed4\u5929\u6d2a\u6c34\u3002</p><p>\u3000\u3000\u65ec\u6708\u4ee5\u6765\u96be\u5f97\u7684\u6674\u591c\uff0c\u661f\u6708\u6ee1\u5929\uff0c\u94f6\u8272\u7684\u6708\u8f89\u6d12\u4e0b\u6765\uff0c\u7adf\u662f\u8ba9\u4eba\u770b\u89c1\u591c\u5e55\u4e0b\u5982\u6b64\u7684\u60c5\u666f\u3002</p><p>\u3000\u3000\u8fdc\u5904\u7684\u72c2\u6d6a\u5e2d\u5377\u800c\u6765\uff0c\u72c2\u6d6a\u58f0\u5c31\u50cf\u54d7\u54d7\u5566\u5566\u7684\u72c2\u98ce\u5728\u547c\u5578\uff0c\u4e5d\u6c5f\u6cb3\u4e1c\u5cb8\u7684\u5c4b\u820d\u567c\u54e9\u556a\u5566\u7684\u7ed9\u72c2\u6d6a\u63a8\u5012\u3002</p><p>\u3000\u3000\u8ddd\u6e56\u5cb8\u6e83\u5824\u8fc7\u53bb\u4e24\u4e2a\u5c0f\u65f6\uff0c\u5728\u77e5\u9053\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u5728\u7ae0\u5dde\u89c6\u5bdf\u6c5b\u60c5\u7684\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u3001\u5728\u987a\u4e49\u89c6\u5bdf\u6c5b\u60c5\u7684\u6881\u4f1f\u6cd5\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u4e58\u519b\u673a\u8fd4\u56de\u91d1\u5c71\uff0c\u6307\u6325\u62a2\u9669\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u8fd9\u662f\u5f20\u606a\u4e0e\u6c5f\u5357\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u7684\u9996\u6b21\u76f8\u4f1a\uff0c\u6ca1\u60f3\u5230\u4f1a\u662f\u5728\u8fd9\u6837\u7684\u573a\u5408\uff0c\u6ca1\u6709\u4ec0\u4e48\u597d\u5bd2\u6684\u7684\uff0c\u4e00\u5207\u90fd\u4ee5\u62a2\u9669\u4e3a\u91cd\uff0c\u60df\u6709\u5bc4\u5e0c\u671b\u80fd\u5728\u72c2\u6d6a\u5377\u8fc7\u4e5d\u6c5f\u6cb3\u4e4b\u524d\uff0c\u7b51\u6210\u4e00\u9053\u575a\u56fa\u7684\u571f\u575d\u6765\uff0c\u624d\u80fd\u4f7f\u8eab\u540e\u7684\u8fd9\u5ea7\u57ce\u5e02\u514d\u906d\u8986\u9876\u4e4b\u707e\u3002</p><p>\u3000\u3000\u5728\u4e5d\u6c5f\u6cb3\u4e0e\u91d1\u5c71\u6e56\u4ee5\u53ca\u996e\u9a6c\u6cb3\u4e4b\u95f4\uff0c\u662f\u91d1\u5c71\u65b0\u57ce\uff0c\u9664\u4e86\u5f20\u606a\u6700\u521d\u6765\u91d1\u5c71\u53c2\u52a0\u7535\u5b50\u4ea7\u4e1a\u8bba\u575b\u4f1a\u5740\u7684\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u4e4b\u5916\uff0c\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u3001\u91d1\u5c71\u8f6f\u4ef6\u56ed\u4ee5\u53ca\u6a61\u6811\u56ed\u91d1\u5c71\u5206\u56ed\u90fd\u9009\u62e9\u5efa\u5728\u8fd9\u91cc\uff0c\u4e0e\u6668\u66e6\u7eb8\u4e1a\u9694\u6cb3\u800c\u671b\uff0c\u6b63\u5f53\u6ed4\u5929\u6d2a\u6c34\u7684\u51b2\u51fb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u77e5\u9053\u6a61\u6811\u56ed\u91d1\u5c71\u5206\u56ed\u5efa\u8bbe\u5de5\u5730\u4e0a\u7684\u5de5\u4eba\u6709\u6ca1\u6709\u6765\u5f97\u53ca\u90fd\u64a4\u79bb\u51fa\u6765\uff0c\u6b64\u65f6\u6ed4\u5929\u6d2a\u6c34\u5df2\u7ecf\u5377\u6f2b\u8fc7\u6a61\u6811\u56ed\u91d1\u5c71\u5206\u56ed\u7684\u5efa\u8bbe\u5de5\u5730\uff0c\u89c4\u5212\u5341\u516d\u5c42\u7684\u7814\u53d1\u5927\u697c\u5df2\u7ecf\u65bd\u5de5\u5230\u4e5d\u5c42\uff0c\u8fd8\u6709\u5927\u622a\u9732\u5728\u6c34\u9762\u4e4b\u4e0a\uff0c\u6ca1\u6709\u7ed9\u6d2a\u6c34\u51b2\u57ae\uff0c\u7ad9\u5728\u8fd9\u8fb9\u53ea\u80fd\u770b\u5230\u4e2a\u6a21\u7cca\u7684\u9ed1\u5f71\uff0c\u4e5f\u4e0d\u6562\u786e\u8ba4\u5c31\u662f\u91d1\u5c71\u5206\u56ed\u7684\u7814\u53d1\u5927\u697c\u3002</p><p>\u3000\u3000\u5012\u662f\u91d1\u5c71\u542f\u52a8\u65b0\u57ce\u5efa\u8bbe\u5728\u8fd9\u4e00\u8303\u56f4\u5185\u5927\u89c4\u6a21\u7684\u52a8\u8fc1\uff0c\u4f7f\u5f97\u533a\u57df\u5185\u7684\u5c45\u6c11\u5927\u5e45\u964d\u4f4e\uff0c\u800c\u5de5\u5730\u5de5\u4eba\u80fd\u591f\u6709\u7ec4\u7ec7\u6682\u65f6\u5f80\u9ad8\u5904\u6500\u767b\u907f\u6c34\uff0c\u5728\u6e83\u5824\u540e\uff0c\u8fc5\u901f\u5207\u65ad\u8f93\u5165\u6d2a\u6cdb\u533a\u7684\u8f93\u53d8\u7535\u8def\uff0c\u8fd9\u6216\u8bb8\u80fd\u591f\u6700\u5927\u9650\u5ea6\u7684\u964d\u4f4e\u4f24\u4ea1\u5427\u2026\u2026\u4f46\u662f\u7edd\u4e0d\u80fd\u8ba9\u6d2a\u6c34\u51b2\u8fc7\u4e5d\u6c5f\u6cb3\u3002</p><p>\u3000\u3000\u201c\u6c81\u56ed\u90a3\u91cc\u5168\u90e8\u7ec4\u7ec7\u758f\u6563\u4e86\u2026\u2026\u5de5\u5730\u90a3\u8fb9\u8f66\u8f86\u6709\u9650\uff0c\u53ea\u6709\u56db\u5341\u5206\u949f\u7684\u65f6\u95f4\uff0c\u6d2a\u6c34\u5c31\u51b2\u5230\u5de5\u5730\uff0c\u64a4\u51fa\u7684\u4eba\u6709\u9650\uff0c\u8fd8\u6709\u76f8\u5f53\u4e00\u90e8\u5206\u5de5\u4eba\u90fd\u56f0\u5728\u5de5\u5730\u4e0a\uff0c\u5206\u56ed\u9732\u51fa\u5730\u9762\u7684\u4e3b\u4f53\u5efa\u7b51\u90fd\u9876\u4f4f\u6d2a\u6c34\u7684\u51b2\u51fb\uff0c\u7ecf\u8fc7\u7d27\u6025\u7edf\u8ba1\uff0c\u6709\u4e03\u540d\u5de5\u4eba\u5931\u8e2a\u2026\u2026\u201d\u5085\u4fca\u8d70\u8fc7\u6765\u8f7b\u58f0\u8ddf\u5f20\u606a\u6c47\u62a5\uff0c\u201c\u8bb8\u8001\u5e08\u4ed6\u4eec\u90fd\u5b89\u987f\u5230\u5e02\u91cc\uff0c\u5f20\u5955\u521a\u521a\u9001\u8001\u5211\u5382\u957f\u8fc7\u6765\uff0c\u4e0e\u7fdf\u52a9\u7406\u5728\u697c\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u4ed6\u4eec\u80a9\u4e0a\u627f\u62c5\u7684\u8d23\u4efb\u8981\u91cd\u5927\u5f97\u5f88\uff0c\u5f20\u606a\u9996\u5148\u8981\u786e\u8ba4\u9526\u6e56\u5728\u91d1\u5c71\u7684\u4eba\u5458\u72b6\u51b5\uff0c\u707e\u96be\u53d1\u751f\u65f6\uff0c\u6b63\u503c\u5165\u591c\u4e4b\u9645\uff0c\u591c\u8272\u5e7d\u669d\uff0c\u5931\u8e2a\u7684\u4e03\u540d\u5de5\u4eba\u672a\u5fc5\u5c31\u662f\u906d\u9047\u4e0d\u5e78\uff0c\u80fd\u7d27\u6025\u8c03\u7528\u7684\u4e09\u5341\u8258\u51b2\u950b\u821f\u5df2\u7ecf\u4ece\u6e83\u5824\u9a76\u5165\u6d2a\u6cdb\u533a\u8fdb\u884c\u6551\u63f4\uff0c\u76f4\u5347\u673a\u7684\u591c\u822a\u80fd\u529b\u5dee\uff0c\u8981\u7b49\u5230\u660e\u5929\u767d\u5929\u624d\u80fd\u51fa\u52a8\uff0c\u5c31\u62c5\u5fc3\u660e\u5929\u7684\u5929\u6c14\u4f1a\u8fdb\u4e00\u6b65\u6076\u5316\u3002</p><p>\u3000\u3000\u5728\u897f\u5cb8\u6e83\u5824\u4e4b\u540e\uff0c\u6e56\u4e1c\u5cb8\u56db\u5830\u4e61\u6bb5\u6e56\u5824\u4e5f\u81ea\u7136\u6e83\u5824\uff0c\u6d2a\u6c34\u6252\u62c9\u5f00\u6cb3\u5824\uff0c\u5f62\u6210\u6570\u767e\u7c73\u5bbd\u7684\u7f3a\u53e3\uff0c\u8fc5\u901f\u6f2b\u8fc7\u56db\u5830\u4e61\u3002\u867d\u7136\u897f\u5cb8\u6e83\u5824\u4e4b\u65f6\uff0c\u9020\u6210\u6781\u5927\u7684\u6050\u614c\u4e0e\u7269\u8d44\u8c03\u5907\u6df7\u4e71\uff0c\u6668\u5830\u8def\u7f3a\u53e3\u5806\u79ef\u4e34\u65f6\u5824\u7b51\u7684\u4e8b\u60c5\u7531\u4e8e\u9632\u6c5b\u7269\u8d44\u8fd0\u8f93\u4e0d\u53ca\u65f6\uff0c\u4e24\u5c0f\u65f6\u540e\uff0c\u6d2a\u6c34\u6f2b\u8fc7\u7f3a\u53e3\u6e32\u6cc4\u4e0b\u6765\uff0c\u867d\u8bf4\u7531\u4e8e\u897f\u5cb8\u7684\u6e83\u5824\uff0c\u4f7f\u5f97\u4e1c\u6d2a\u6c34\u51b2\u51fb\u529b\u524a\u5f31\uff0c\u7f3a\u53e3\u7684\u5730\u52bf\u9887\u9ad8\uff0c\u51b2\u51fb\u529b\u8fdb\u4e00\u6b65\u7ed9\u524a\u5f31\uff0c\u4ece\u7f3a\u53e3\u5904\u6ea2\u6d41\u51fa\u7684\u6d2a\u6c34\u6ca1\u6709\u9884\u60f3\u4e2d\u7684\u6050\u6016\uff0c\u4f46\u4e5f\u5bf9\u6668\u66e6\u7eb8\u4e1a\u7684\u751f\u6d3b\u9020\u6210\u66f4\u4e25\u91cd\u7684\u5185\u6d9d\u3002\u4e00\u65f6\u65e0\u6cd5\u8c03\u6d3e\u4eba\u529b\u53bb\u5835\u7f3a\u53e3\uff0c\u53ea\u80fd\u770b\u7740\u6668\u66e6\u7eb8\u4e1a\u5468\u56f4\u7684\u6c34\u4f4d\u6162\u6162\u6da8\u9ad8\uff0c\u9664\u4e86\u5df2\u7ecf\u64a4\u51fa\u7684\u5c45\u6c11\u5916\uff0c\u8fd8\u6709\u5927\u91cf\u7684\u804c\u5de5\u53ca\u5bb6\u5c5e\u7ed9\u56f0\u5728\u90a3\u91cc\u3002\u4ed6\u4eec\u5bf9\u6d2a\u6c34\u7684\u6765\u88ad\u81f3\u5c11\u6709\u6240\u51c6\u5907\uff0c\u6668\u66e6\u7eb8\u4e1a\u4e5f\u6709\u8f66\u95f4\u88ab\u6df9\uff0c\u5168\u7ebf\u505c\u4ea7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u51cc\u6668\u4e00\u70b9\u949f\uff0c\u6d2a\u6c34\u5f62\u6210\u7684\u6d6a\u5934\u7b2c\u4e00\u6b21\u51b2\u51fb\u5230\u4e5d\u6c5f\u6cb3\u897f\u5cb8\u4e34\u65f6\u4fee\u7b51\u7684\u571f\u575d\uff0c\u8fd9\u65f6\u5929\u53c8\u8ba9\u4eba\u75db\u6068\u7684\u9634\u4e86\u4e0b\u6765\uff0c\u5f20\u606a\u966a\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u7ad9\u5728\u571f\u575d\u80cc\u540e\u7684\u516b\u5c42\u529e\u516c\u697c\u9876\u4e0a\u542c\u7740\u6d2a\u6c34\u6d6a\u5934\u51b2\u51fb\u571f\u575d\u7684\u58f0\u97f3\uff0c\u63a2\u7167\u706f\u7167\u51fa\u96ea\u767d\u7684\u6d6a\u5934\uff0c\u96ea\u767d\u5f97\u5413\u4eba\u3002</p><p>\u3000\u3000\u6307\u6325\u90e8\u7535\u8bdd\u6b64\u8d77\u5f7c\u4f0f\uff0c\u4e0d\u65ad\u571f\u575d\u7ed9\u6d2a\u6c34\u51b2\u51fa\u88c2\u53e3\u7684\u6d88\u606f\uff0c\u65e0\u6570\u6728\u6869\u3001\u6c99\u77f3\u3001\u9ebb\u888b\u80fd\u52a0\u56fa\u571f\u575d\u7684\u7269\u8d44\u7531\u6570\u4e07\u519b\u6c11\u80a9\u625b\u624b\u63a8\u7740\u5c01\u5835\u7ed9\u6d2a\u6c34\u51b2\u5f00\u7684\u8c41\u53e3\uff0c\u8fd9\u662f\u6700\u540e\u4e00\u9053\u9632\u62a4\u7ebf\uff0c\u8eab\u540e\u5c31\u662f\u4e09\u767e\u4e07\u4eba\u53e3\u3001\u6beb\u65e0\u906e\u62a4\u7684\u91d1\u5c71\u57ce\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\u53e3\u6e32\u6cc4\u800c\u4e0b\u7684\u4ebf\u5428\u6d2a\u6c34\u7ecf\u8fc7\u8fd1\u4e94\u5341\u516c\u91cc\u590d\u6742\u5730\u5f62\u7684\u7f13\u51b2\uff0c\u529b\u91cf\u5df2\u7ecf\u88ab\u524a\u5f31\uff0c\u867d\u7136\u4e0d\u65ad\u6709\u571f\u575d\u584c\u88c2\u7684\u6d88\u606f\u4f20\u6765\uff0c\u4f46\u662f\u7ec8\u7a76\u6ca1\u6709\u80fd\u5f62\u6210\u81f4\u547d\u7684\u6e83\u51b3\uff0c\u5929\u6726\u6726\u4eae\u65f6\uff0c\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u7b49\u7701\u5e02\u9886\u5bfc\u7ed9\u4f17\u4eba\u62e5\u62a4\u7740\u90fd\u4e00\u8eab\u6ce5\u6d46\u7684\u8fd4\u56de\u7d27\u6025\u9632\u6c5b\u6307\u6325\u697c\uff0c\u8d75\u6709\u4f26\u8eab\u517c\u7d27\u6025\u9632\u6c5b\u6307\u6325\u4e00\u76f4\u7559\u5b88\u697c\u91cc\uff0c\u8fd9\u4e00\u573a\u786c\u4ed7\u7b97\u662f\u6682\u65f6\u6253\u8d62\u4e86\u3002\u5411\u897f\u5317\u65b9\u5411\u5e2d\u5377\u7684\u4ebf\u5428\u6d2a\u6c34\u88ab\u4e00\u9053\u4e34\u65f6\u4fee\u7b51\u7684\u571f\u575d\u62b5\u6321\u4f4f\uff0c\u7a0d\u4f5c\u5598\u606f\u5c31\u6298\u5411\uff0c\u987a\u7740\u5730\u52bf\u3001\u6f2b\u5377\u8fc7\u996e\u9a6c\u6cb3\u4e0e\u4e5d\u6c5f\u6cb3\u4e4b\u95f4\u7684\u5e7f\u88a4\u571f\u5730\u3002</p><p>\u3000\u3000\u89c1\u4e34\u65f6\u571f\u575d\u53ca\u65f6\u5792\u6210\u53c8\u53d1\u6325\u4f5c\u7528\uff0c\u5927\u5bb6\u624d\u7a0d\u7f13\u4e00\u53e3\u6c14\uff0c\u81f3\u5c11\u80cc\u540e\u91d1\u5c71\u57ce\u6682\u65f6\u662f\u4fdd\u4f4f\u4e86\u3002\u6700\u60ac\u4eba\u5fc3\u7684\u9669\u60c5\u5df2\u8fc7\uff0c\u867d\u7136\u6e56\u6c34\u5012\u704c\u5df2\u7ecf\u5f62\u6210\u6570\u767e\u5e73\u65b9\u516c\u91cc\u7684\u6d2a\u6cdb\u533a\uff0c\u8fd8\u6709\u65e0\u6570\u7684\u4eba\u7ed9\u56f0\u5728\u6d2a\u6c34\u4e4b\u4e2d\uff0c\u5f20\u606a\u90fd\u65e0\u6cd5\u76f4\u63a5\u62d6\u4ee5\u63f4\u624b\uff0c\u53ea\u80fd\u5bc4\u5e0c\u671b\u9a7b\u519b\u4e0e\u653f\u5e9c\u7684\u6551\u63f4\u80fd\u53ca\u65f6\u8ddf\u4e0a\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u96e8\u5b63\u8fd8\u6ca1\u6709\u8fc7\u53bb\uff0c\u996e\u9a6c\u6cb3\u4e0a\u6e38\u4f1a\u968f\u65f6\u518d\u6b21\u5f62\u6210\u6d2a\u5cf0\uff0c\u5bf9\u4e0b\u6e38\u9020\u6210\u51b2\u51fb\uff0c\u9700\u8981\u5728\u6700\u77ed\u7684\u65f6\u95f4\u5185\u5c01\u5835\u91d1\u5c71\u6e56\u6e83\u51b3\u53e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u7528\u7ee7\u7eed\u7559\u5728\u7d27\u6025\u9632\u6c5b\u6307\u6325\u90e8\u91cc\uff0c\u4e0e\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u7b49\u4eba\u544a\u8f9e\uff0c\u79bb\u5f00\u524d\u8bf4\u9053\uff1a\u201c\u7701\u91cc\u4f1a\u8c03\u7528\u91d1\u5c71\u7684\u4e00\u5207\u7269\u6599\u7528\u6765\u62a2\u9669\uff0c\u9526\u6e56\u4ece\u5916\u5730\u7d27\u6025\u8d2d\u4e70\u7684\u62a2\u9669\u6551\u751f\u5668\u6750\u4f1a\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u8fd0\u6765\uff0c\u6211\u6709\u4e24\u90e8\u8f66\u5e94\u8be5\u80fd\u5728\u62a2\u9669\u6551\u63f4\u4e2d\u53d1\u6325\u4e9b\u4f5c\u7528\uff0c\u4ee5\u540e\u5c31\u7559\u7ed9\u7701\u91cc\u7528\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5b\u60c5\u4e25\u5cfb\u4ee5\u6765\uff0c\u9526\u6e56\u5df2\u7ecf\u5411\u6c5f\u5357\u7701\u7684\u91d1\u5c71\u3001\u7ae0\u5dde\u3001\u987a\u4e49\u7b49\u5730\u635f\u8d60\u4e0a\u5343\u4e07\u5143\u7684\u62a2\u9669\u9632\u6c5b\u7269\u8d44\uff0c\u9526\u6e56\u505a\u7684\u8fd9\u4e00\u5207\uff0c\u90fd\u672a\u66fe\u8ba9\u5a92\u4f53\u5ba3\u626c\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u5f20\u606a\u5728\u4e00\u5c0f\u65f6\u4e4b\u5185\u63d0\u4ea4\u4e00\u4efd\u62a2\u9669\u6551\u751f\u5668\u6750\u76ee\u5f55\u8981\u6c42\u9632\u6c5b\u6307\u6325\u90e8\u786e\u5b9a\u662f\u5426\u6025\u9700\uff0c\u8fde\u591c\u7d27\u6025\u542f\u52a8\u9526\u6e56\u5546\u4e8b\u521a\u521a\u6574\u5408\u7684\u91c7\u8d2d\u4f53\u7cfb\u5728\u56fd\u5185\u5404\u5927\u5e02\u573a\u7d27\u6025\u91c7\u8d2d\u62a2\u9669\u6551\u751f\u5668\u6750\uff0c\u52a8\u4f5c\u6700\u4e3a\u8fc5\u901f\u7684\uff0c\u5728\u5efa\u90ba\u91c7\u8d2d\u7684\u4e00\u6279\u6551\u751f\u5668\u6750\u5df2\u7ecf\u79df\u7528\u76f4\u5347\u673a\u8fde\u591c\u5f80\u91d1\u5c71\u8fd0\u9001\u8fc7\u6765\u3002\u5f20\u606a\u8fd9\u65f6\u5019\u544a\u8f9e\u79bb\u5f00\uff0c\u53c8\u5c06\u4ed6\u5728\u91d1\u5c71\u4e58\u5750\u7684\u4e24\u8f86\u608d\u9a6c\u8f66\u7559\u4e0b\u7528\u4e8e\u62a2\u9669\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u9526\u6e56\u4e5f\u662f\u635f\u5931\u60e8\u91cd\uff1a\u6a61\u6811\u56ed\u5efa\u8bbe\u5de5\u5730\u79bb\u6e83\u5824\u6e56\u5cb8\u4e0d\u8db3\u4e94\u516c\u91cc\uff0c\u53d7\u5230\u6d2a\u6c34\u7684\u7834\u574f\u529b\u76f8\u5f53\u5f3a\uff0c\u53ea\u6709\u7814\u53d1\u5927\u697c\u4e3b\u4f53\u5efa\u7b51\u62b5\u6321\u4f4f\u6d2a\u6c34\u7684\u51b2\u51fb\uff1b\u6c81\u56ed\u4e5f\u88ab\u6d2a\u6c34\u626b\u8fc7\uff0c\u9020\u6210\u5927\u9762\u79ef\u7684\u5efa\u7b51\u574d\u584c\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5e74\u7eaa\u5c1a\u6ee1\u4e8c\u5341\u5c81\u7684\u9752\u5e74\uff0c\u53ea\u6015\u65e0\u6cd5\u518d\u63d0\u66f4\u82db\u523b\u7684\u8981\u6c42\uff0c\u5c1a\u5b66\u4e49\u5fc3\u91cc\u60f3\u7740\uff0c\u6709\u4e9b\u540e\u6094\u4e4b\u524d\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u653e\u4e0b\u7701\u59d4\u4e66\u8bb0\u7684\u67b6\u5b50\u4e0e\u8fd9\u9752\u5e74\u628a\u76cf\u8a00\u8c08\uff0c\u90a3\u6216\u8bb8\u4f1a\u662f\u4eba\u751f\u5feb\u4e8b\u3002\u53ea\u6015\u4ee5\u540e\u5c31\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u4e86\uff0c\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u867d\u7136\u8eab\u540e\u4e09\u767e\u4e07\u4eba\u7684\u91d1\u5c71\u57ce\u4fdd\u4f4f\u4e86\uff0c\u4f46\u662f\u4e5d\u6c5f\u6cb3\u4e0e\u996e\u9a6c\u6cb3\u4e4b\u95f4\u7684\u5c45\u6c11\u5462\uff1f\u603b\u8981\u6709\u4eba\u4e3a\u91d1\u5c71\u6e56\u7684\u6e83\u5824\u627f\u62c5\u8d23\u4efb\uff0c\u5c1a\u5b66\u4e49\u5df2\u7ecf\u6e05\u695a\u7684\u77e5\u9053\u81ea\u5df1\u7684\u653f\u6cbb\u751f\u6daf\u5c31\u5c06\u8d70\u5230\u5c3d\u5934\uff0c\u773c\u4e0b\u6700\u7d27\u8981\u662f\u5c3d\u53ef\u80fd\u51cf\u5c11\u635f\u5931\uff0c\u5c3d\u53ef\u80fd\u633d\u6551\u4eba\u7684\u751f\u547d\uff0c\u4ee5\u5f25\u8865\u826f\u5fc3\u4e0a\u7684\u4e0d\u5b89\u3002</p><p>\u3000\u3000\u5c1a\u5b66\u4e49\u76ee\u9001\u5f20\u606a\u7b49\u4eba\u79bb\u53bb\uff0c\u7701\u59d4\u79d8\u4e66\u957f\u8fd9\u65f6\u5019\u8d70\u8fc7\u6765\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u56fd\u52a1\u9662\u529e\u516c\u5385\u7684\u7535\u8bdd\uff0c\u8d75\u603b\u7406\u8981\u8ddf\u4f60\u901a\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u5c1a\u5b66\u4e49\u5728\u63a5\u5230\u8d75\u6709\u4f26\u7684\u6c47\u62a5\u4e4b\u540e\u5c31\u7acb\u5373\u4e0a\u62a5\u4e2d\u592e\u8bf7\u6551\u589e\u6d3e\u652f\u6301\u3002\u91d1\u5c71\u6e56\u540e\u662f\u4e09\u767e\u4e07\u4eba\u53e3\u7684\u91d1\u5c71\u57ce\uff0c\u8d75\u6d4e\u4e1c\u5728\u56fd\u52a1\u9662\u4e5f\u662f\u5f7b\u591c\u96be\u7720\uff0c\u5728\u542c\u5230\u4e34\u65f6\u4fee\u5efa\u7684\u571f\u575d\u62b5\u6321\u4f4f\u6d2a\u6c34\u6d6a\u5934\uff0c\u624d\u7b97\u7a0d\u677e\u4e00\u53e3\u6c14\uff1a\u201c\u6211\u9a6c\u4e0a\u8981\u52a8\u8eab\u53bb\u957f\u6c99\u89c6\u5bdf\u957f\u6c5f\u6c5b\u60c5\uff0c\u6211\u4f1a\u59d4\u6d3e\u66fe\u5982\u5723\u526f\u603b\u7406\u4ee3\u8868\u6211\u53bb\u91d1\u5c71\u89c6\u5bdf\u62a2\u9669\u6551\u63f4\u5de5\u4f5c\u2026\u2026\u201d\u53c8\u8bed\u91cd\u5fc3\u957f\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u4e0d\u80fd\u62ff\u4eba\u6c11\u767e\u59d3\u7684\u8eab\u5bb6\u6027\u547d\u5f53\u513f\u620f\uff01\u201d</p><p>\u3000\u3000\u8fd9\u4e00\u53e5\u8bdd\u8bf4\u5f97\u5c1a\u5b66\u4e49\u540e\u80cc\u6c57\u6f7a\u6f7a\uff0c\u5012\u4e0d\u662f\u611f\u53d7\u5230\u8d75\u6d4e\u4e1c\u7ed9\u4ed6\u5728\u6743\u52bf\u4e0a\u7684\u538b\u529b\uff0c\u8eab\u4e3a\u7701\u59d4\u4e66\u8bb0\uff0c\u8eab\u4e3a\u5c01\u7586\u5927\u540f\uff0c\u53ef\u4ee5\u8bf4\u662f\u5904\u4e8e\u6743\u529b\u7684\u5dc5\u5cf0\uff0c\u4e0d\u4f1a\u5bf9\u66f4\u9ad8\u7684\u6743\u529b\u6709\u7279\u522b\u5927\u7684\u754f\u60e7\u4e4b\u611f\uff0c\u53ea\u662f\u8fd9\u771f\u662f\u76f4\u523a\u4eba\u5fc3\u7684\u4e00\u53e5\u8bdd\uff0c\u867d\u8bf4\u91d1\u5c71\u6e56\u7a81\u7136\u6e83\u5824\u80af\u5b9a\u5b58\u5728\u66f4\u76f4\u63a5\u7684\u56e0\u7d20\u2014\u2014\u8fd9\u662f\u9700\u8981\u4e8b\u540e\u8c03\u67e5\u7684\uff0c\u4f46\u662f\u5c1a\u5b66\u4e49\u4e5f\u65e0\u6cd5\u5426\u8ba4\u81ea\u5df1\u7684\u8fc7\u5931\u3002</p><p>\u3000\u3000\u65e9\u5c31\u6709\u5b66\u8005\u63d0\u51fa\u56f4\u6e56\u9020\u7530\u611a\u4e0d\u53ef\u53ca\uff0c\u4e0e\u6e56\u4e89\u5730\u4e5f\u662f\u4e2a\u5f88\u611a\u8822\u7684\u653f\u7b56\uff0c\u91d1\u5c71\u6e56\u6e83\u5824\u7684\u6839\u672c\u539f\u56e0\u5c31\u662f\u5341\u591a\u5e74\u6765\u4e0e\u6e56\u4e89\u653f\u7684\u653f\u7b56\u4f7f\u5f97\u91d1\u5c71\u6e56\u6ee9\u6d82\u3001\u6e7f\u5730\u9762\u79ef\u5927\u5e45\u51cf\u5c11\uff0c\u84c4\u6d2a\u80fd\u529b\u4e25\u91cd\u7a0d\u5f31\uff0c\u751a\u81f3\u4e0d\u8db3\u539f\u6765\u7684\u4e8c\u5206\u4e4b\u4e00\uff0c\u7ec8\u662f\u53d7\u5230\u4e25\u60e9\uff0c\u53ea\u662f\u88ab\u60e9\u7f5a\u5374\u662f\u65e0\u8f9c\u7684\u707e\u6c11\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u770b\u7740\u7a97\u5916\u5728\u9ece\u660e\u6e05\u4eae\u7684\u5149\u7ebf\u4e2d\u4e00\u591c\u5f62\u6210\u7684\u9ec4\u6d51\u6d51\u7684\u6d2a\u6c34\u6e56\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u6e83\u5824\u4f1a\u9020\u6210\u591a\u5c11\u4eba\u5458\u4f24\u4ea1\u4e00\u65f6\u8fd8\u65e0\u6cd5\u7edf\u8ba1\uff0c\u4f46\u662f\u91d1\u5c71\u5e02\u53bb\u5e74\u4e00\u5e74\u7684\u7ecf\u6d4e\u5efa\u8bbe\u6210\u679c\u51e0\u4e4e\u90fd\u6ce1\u5728\u8fd9\u6d2a\u6c34\u91cc\uff0c\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u767d\u8272\u7684\u7a79\u9876\u9732\u5728\u6c34\u9762\u4e0a\uff0c\u4ece\u8fd9\u91cc\u770b\u8fc7\u53bb\u80fd\u770b\u89c1\u7a79\u9876\u4e0a\u6324\u6ee1\u907f\u9669\u7684\u6c11\u4f17\uff0c\u9ed1\u4e4e\u4e4e\uff0c\u50cf\u8681\u7fa4\u9644\u5728\u7a79\u9876\u4e0a\uff0c\u8fd9\u65f6\u5019\u6700\u7d27\u8981\u7684\u662f\u5c06\u4eba\u4ece\u6d2a\u6c34\u56f4\u56f0\u4e2d\u6551\u51fa\u6765\u2026\u2026</p></article>"}}}]);
