(window.webpackJsonp=window.webpackJsonp||[]).push([[876],{899:function(p){p.exports={chapterTitle:null,title:"\u7b2c873\u7ae0 \u5f53\u5934\u4e00\u51fb",content:"<article><h2>\u7b2c873\u7ae0 \u5f53\u5934\u4e00\u51fb</h2><p>\u3000\u3000\u5468\u747e\u745c\u5728\u91d1\u5c71\u6e56\u6e83\u5824\u7684\u90a3\u4e00\u523b\uff0c\u4f53\u5185\u7684\u52c7\u6c14\u4e5f\u968f\u4e4b\u6e83\u5c3d\uff0c\u65e0\u6cd5\u4ece\u5bb9\u6307\u6325\u6297\u6d2a\u62a2\u9669\u5927\u5c40\uff0c\u8d75\u6709\u4f26\u4e3b\u52a8\u627f\u62c5\u8d23\u4efb\u3002\u4e8b\u540e\u80c6\u6c14\u96be\u590d\uff0c\u5468\u747e\u745c\u867d\u7a7a\u6709\u5e02\u59d4\u4e66\u8bb0\u7684\u540d\u4f4d\uff0c\u5374\u518d\u96be\u4ece\u8d75\u6709\u4f26\u624b\u91cc\u6273\u56de\u4e3b\u52a8\u6743\uff0c\u53ea\u5f97\u5728\u540e\u65b9\u534f\u8c03\u6297\u6d2a\u62a2\u9669\u7269\u8d44\u7684\u8c03\u5907\uff0c\u6210\u4e3a\u540e\u52e4\u603b\u6307\u6325\u3002\u5f97\u77e5\u8c03\u52a8\u6570\u4e07\u519b\u6c11\u5728\u4e5d\u6c5f\u6cb3\u897f\u5cb8\u7d27\u6025\u4fee\u5efa\u7684\u571f\u575d\u53d1\u6325\u4e86\u4f5c\u7528\uff0c\u5468\u747e\u745c\u7a0d\u677e\u4e00\u53e3\u6c14\u7684\u540c\u65f6\u5fc3\u91cc\u4e5f\u662f\u540e\u6094\u83ab\u53ca\uff0c\u600e\u4e48\u53ef\u4ee5\u5728\u6b64\u65f6\u5c31\u614c\u4e71\u4e86\u624b\u811a\uff1f</p><p>\u3000\u3000\u7cbe\u75b2\u529b\u7aed\u7684\u762b\u5750\u5728\u4f11\u606f\u5ba4\u7684\u6c99\u53d1\u4e0a\uff0c\u5468\u747e\u745c\u4e0d\u5f97\u4e0d\u8003\u8651\u91d1\u5c71\u6e56\u6e83\u5824\u4f1a\u5bfc\u81f4\u7684\u4e25\u91cd\u540e\u679c\uff0c\u60f3\u6253\u7535\u8bdd\u627e\u4e08\u592b\u5546\u91cf\u4e00\u4e0b\uff0c\u624d\u53d1\u73b0\u624b\u673a\u8ba9\u79d8\u4e66\u62ff\u53bb\u5145\u7535\uff0c\u4e0d\u5728\u8eab\u8fb9\uff0c\u7ad9\u8d77\u6765\u6253\u5f00\u4f11\u606f\u5ba4\u7684\u95e8\uff0c\u770b\u89c1\u79d8\u4e66\u6b63\u62ff\u7740\u5979\u7684\u624b\u673a\u8d70\u8fc7\u6765\u8981\u6572\u95e8\u3002</p><p>\u3000\u3000\u201c\u5468\u4e66\u8bb0\uff0c\u6709\u7535\u8bdd\u6253\u5230\u4f60\u7684\u624b\u673a\u4e0a\u3002\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u4ece\u79d8\u4e66\u624b\u91cc\u63a5\u8fc7\u624b\u673a\uff0c\u89c1\u662f\u5bb6\u91cc\u7684\u7535\u8bdd\uff0c\u5c06\u4f11\u606f\u5ba4\u7684\u95e8\u5173\u4e0a\uff0c\u624d\u63a5\u8d77\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u5251\u5357\u8fc7\u6765\u7684\u2026\u2026\u201d\u8c22\u6c49\u660e\u6c99\u54d1\u75b2\u60eb\u7684\u58f0\u97f3\u4f20\u8fc7\u6765\uff0c\u53ef\u89c1\u4e5f\u662f\u5f7b\u591c\u96be\u7720\uff0c\u201c\u542c\u8bf4\u6d2a\u6c34\u62b5\u6321\u4f4f\u4e86\uff0c\u65b0\u57ce\u7684\u635f\u5931\u80fd\u5927\u4f53\u770b\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u65e0\u529b\u7684\u95ed\u4e0a\u773c\u775b\uff0c\u6b32\u54ed\u65e0\u6cea\uff0c\u62ff\u4e00\u79cd\u5583\u5583\u81ea\u8bed\u7684\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u90fd\u6ce1\u5728\u6c34\u91cc\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u62c2\u6653\uff0c\u5728\u60e0\u5c71\u901a\u5f80\u91d1\u5c71\u4e0a\u7684\u56fd\u9053\u4e0a\uff0c\u4e00\u8f86\u9ed1\u8272\u5954\u9a70\u8f66\u5feb\u901f\u7684\u5f80\u91d1\u5c71\u65b9\u5411\u884c\u9a76\uff0c\u79bb\u91d1\u5c71\u5f88\u8fd1\u4e86\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u7b2c\u4e00\u65f6\u95f4\u77e5\u9053\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u51e0\u4e4e\u6ca1\u6709\u803d\u6401\uff0c\u5c31\u8ba9\u53f8\u673a\u5f00\u8f66\u76f4\u63a5\u9001\u4ed6\u53bb\u91d1\u5c71\uff0c\u4f46\u662f\u4eca\u5e74\u590f\u5b63\u4ee5\u6765\u7684\u7b2c\u4e8c\u6b21\u6d2a\u5cf0\u7ecf\u8fc7\u60e0\u5c71\uff0c\u8f6e\u6e21\u5c01\u822a\uff0c\u4ed6\u5728\u6e21\u53e3\u7b49\u5230\u51cc\u6668\u624d\u80fd\u8fc7\u6c5f\uff0c\u4e00\u76f4\u5230\u62c2\u6653\u3001\u5929\u8fb9\u9732\u51fa\u9c7c\u809a\u767d\u624d\u62b5\u8fbe\u91d1\u5c71\u8fd1\u90ca\u3002</p><p>\u3000\u3000\u8d76\u5230\u8c22\u6c49\u660e\u3001\u5468\u747e\u745c\u5728\u91d1\u5c71\u7684\u4f4f\u5904\uff0c\u4e34\u5d16\u6e56\u5cb8\u8fb9\u4e5f\u7528\u6c99\u888b\u5806\u6210\u534a\u4eba\u9ad8\u7684\u5824\u575d\uff0c\u770b\u89c1\u6709\u6c34\u4ece\u7f1d\u9699\u91cc\u6e17\u51fa\u6765\uff0c\u8c22\u6c49\u9756\u8d70\u8fdb\u522b\u5885\uff0c\u5c31\u770b\u89c1\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u7236\u5b50\u4ee5\u53ca\u7535\u5b50\u5de5\u4e1a\u56ed\u7684\u603b\u7ecf\u7406\u9648\u67f3\u8303\u5750\u5728\u5ba2\u5385\u7684\u6c99\u53d1\u4e0a\uff0c\u770b\u4e0a\u53bb\u5f88\u6194\u60b4\u3001\u75b2\u60eb\uff0c\u5931\u9b42\u843d\u9b44\uff0c\u770b\u89c1\u4ed6\u6765\u4e5f\u4e00\u58f0\u4e0d\u542d\uff0c\u8c22\u6c49\u9756\u77e5\u9053\u4e0d\u4f1a\u6709\u4ee4\u4eba\u5bbd\u6170\u7684\u6d88\u606f\u5728\u8fd9\u91cc\u7b49\u7740\u4ed6\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u6653\u5f97\u8fd9\u65f6\u5019\u662f\u770b\u4e0d\u5230\u5468\u747e\u745c\u7684\uff0c\u867d\u7136\u56fd\u5185\u5a92\u4f53\u4f1a\u5bf9\u91d1\u5c71\u6e56\u6e83\u5824\u4e00\u4e8b\u8f7b\u63cf\u6de1\u5199\uff0c\u4f1a\u597d\u597d\u7684\u5ba3\u626c\u519b\u6c11\u643a\u624b\u62b5\u5fa1\u6d2a\u9b54\u7684\u5149\u8f89\u4e8b\u8ff9\uff0c\u4f46\u662f\u91d1\u5c71\u6e56\u6e83\u5824\u5374\u52bf\u5fc5\u4f1a\u4ee4\u4e2d\u592e\u9707\u60ca\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u65e0\u7455\u53bb\u5173\u5fc3\u5468\u747e\u745c\u7684\u653f\u6cbb\u524d\u9014\u4e86\uff0c\u5176\u5b9e\u4ece\u5468\u747e\u73ba\u6d89\u6848\uff0c\u5468\u5bb6\u7684\u653f\u6cbb\u524d\u9014\u5df2\u7ecf\u7ed9\u5224\u4e86\u6b7b\u5211\uff0c\u5468\u747e\u745c\u8fd8\u7ee7\u7eed\u7559\u5728\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u7684\u4f4d\u5b50\u4e0a\uff0c\u53ea\u4e0d\u8fc7\u662f\u5b98\u573a\u81ea\u6709\u7684\u60ef\u6027\u7f62\u4e86\u3002\u8fd9\u79cd\u60ef\u6027\u8fd9\u6b21\u53ea\u6015\u4e5f\u8981\u560e\u7136\u800c\u6b62\u4e86\u3002</p><p>\u3000\u3000\u201c\u6709\u6ca1\u6709\u901a\u77e5\u845b\u5bb6\uff1f\u201d\u8c22\u6c49\u9756\u5df2\u7ecf\u731c\u5230\u4f1a\u6709\u600e\u6837\u7684\u7ed3\u679c\u5728\u91d1\u5c71\u7b49\u7740\u81ea\u5df1\uff0c\u770b\u5230\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u8fd9\u822c\u6a21\u6837\uff0c\u4ed6\u53ea\u662f\u95ee\u6709\u6ca1\u6709\u5c06\u6d88\u606f\u544a\u8bc9\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u3002</p><p>\u3000\u3000\u521a\u521a\u63a5\u5355\u6295\u4ea7\u7684\u7535\u5b50\u5de5\u4e1a\u56ed\u5382\u533a\u5c31\u7b97\u6ca1\u6709\u7ed9\u5f7b\u5e95\u7684\u51b2\u6bc1\uff0c\u4e5f\u6574\u4e2a\u7684\u90fd\u6ce1\u5728\u6d2a\u6c34\u91cc\uff0c\u94a2\u7b4b\u6df7\u51dd\u571f\u7684\u5382\u623f\u6216\u8bb8\u8fd8\u7ecf\u5f97\u4f4f\u6ce1\uff0c\u673a\u5668\u8bbe\u5907\u751f\u4ea7\u7ebf\u7edf\u7edf\u90fd\u8981\u5b8c\u86cb\uff0c\u5927\u91cf\u7684\u539f\u7269\u6599\u80fd\u4fdd\u4f4f\u4e00\u4e24\u6210\u90fd\u591f\u545b\u3002</p><p>\u3000\u3000\u6beb\u65e0\u51c6\u5907\uff0c\u6e83\u5824\u4e00\u4e2a\u534a\u5c0f\u65f6\u6d2a\u6c34\u5c31\u6df9\u53ca\u5382\u533a\uff0c\u8fd9\u4e48\u77ed\u7684\u65f6\u5019\u91cc\u751a\u81f3\u6765\u4e0d\u53ca\u5c06\u5de5\u4eba\u90fd\u64a4\u51fa\u6765\u3002</p><p>\u3000\u3000\u76f4\u63a5\u635f\u5931\u662f\u4e00\u65b9\u9762\uff0c\u8fd8\u77e5\u9053\u8981\u62d6\u5ef6\u5230\u51e0\u65f6\u624d\u80fd\u6062\u590d\u751f\u4ea7\uff0c\u65e0\u6cd5\u5b8c\u6210\u8ba2\u5355\u4f1a\u5bfc\u81f4\u5546\u8a89\u53d7\u635f\u3001\u5ba2\u6237\u6d41\u5931\u7b49\u7b49\uff0c\u5bf9\u5609\u4fe1\u7535\u5b50\u6765\u8bf4\u5219\u662f\u66f4\u6027\u8d28\u66f4\u4e25\u91cd\u7684\u635f\u5931\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f3c\u4e4e\u4e5f\u8981\u7d27\u6025\u901a\u77e5\u4e09\u661f\uff0c\u4e09\u661f\u5c06\u4e2d\u56fd\u5e02\u573a\u7684\u90e8\u5206\u7535\u5b50\u4ea7\u54c1\u751f\u4ea7\u8ba2\u5355\u4ea4\u7ed9\u91d1\u5c71\u5de5\u5382\uff0c\u8fd9\u4e9b\u8ba2\u5355\u90fd\u65e0\u6cd5\u5b8c\u6210\u4e86\u2014\u2014\u6ce2\u53ca\u5230\u7684\u8303\u56f4\u5f53\u7136\u4e5f\u8fdc\u8fdc\u4e0d\u4ec5\u9650\u4e8e\u8fd9\u4e9b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u5728\u91d1\u5c71\u4e1c\u57ce\u533a\u7684\u4e09\u677e\u4f53\u80b2\u573a\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5750\u8f66\u53bb\u90a3\u91cc\u8ddf\u8bb8\u9e3f\u4f2f\u6c47\u5408\u3002</p><p>\u3000\u3000\u4e09\u677e\u4f53\u80b2\u573a\u662f\u91d1\u5c71\u6b64\u65f6\u6700\u5927\u7684\u707e\u6c11\u5b89\u7f6e\u70b9\uff0c\u9664\u4e86\u56db\u5830\u4e61\u4e0e\u6668\u66e6\u7eb8\u4e1a\u6709\u7ec4\u7ec7\u7684\u64a4\u51fa\u4e94\u4e07\u4eba\u4e4b\u5916\uff0c\u91d1\u5c71\u65b0\u57ce\u4ee5\u53ca\u5317\u9762\u7684\u4e24\u4e2a\u4e61\u9547\u4e00\u591c\u4e4b\u95f4\u90fd\u6210\u4e86\u6d2a\u6cdb\u533a\u3002\u8fd9\u4e9b\u5730\u533a\u7684\u5e38\u4f4f\u4eba\u53e3\u52a0\u4e0a\u65b0\u57ce\u533a\u7684\u5efa\u8bbe\u5de5\u4eba\u7ea6\u6709\u4e5d\u4e07\u4f59\u4eba\uff0c\u91d1\u5c71\u6e56\u6e83\u5824\u65f6\uff0c\u5e02\u91cc\u7d27\u6025\u8c03\u52a8\u4e00\u5207\u80fd\u591f\u8c03\u7528\u7684\u673a\u52a8\u8f66\u8f86\u6551\u4eba\uff0c\u4f46\u662f\u6709\u591a\u5c11\u4eba\u53ca\u65f6\u9003\u4e86\u51fa\u6765\uff0c\u6709\u591a\u5c11\u4eba\u8fd8\u7ed9\u56f0\u5728\u6d2a\u6c34\u91cc\uff0c\u6709\u591a\u5c11\u4eba\u5df2\u7ecf\u5728\u8fd9\u573a\u707e\u96be\u4e2d\u4e27\u751f\uff0c\u90fd\u8fd8\u5728\u7d27\u6025\u7684\u7edf\u8ba1\u4e2d\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u7a9d\u6df1\u9677\u3001\u773c\u767d\u4e0a\u5e03\u6ee1\u8840\u4e1d\uff0c\u8fd9\u6bb5\u65f6\u95f4\u7cbe\u529b\u4e25\u91cd\u900f\u652f\uff0c\u4ece\u60e0\u5c71\u56de\u6765\uff0c\u53c8\u662f\u8fde\u7eed\u7684\u4e24\u5929\u4e24\u591c\u6ca1\u6709\u9616\u773c\uff0c\u55d3\u5b50\u6c99\u54d1\uff0c\u7b80\u7565\u7684\u8ddf\u8bb8\u9e3f\u4f2f\u4ed6\u4eec\u8bf4\u4e86\u4e00\u4e0b\u4e5d\u6c5f\u6cb3\u7684\u60c5\u51b5\uff0c\u89c1\u4ed6\u4eec\u75b2\u60eb\u7684\u60c5\u51b5\u4e0d\u6bd4\u81ea\u5df1\u597d\u591a\u5c11\uff0c\u770b\u7740\u707e\u6c11\u5b89\u7f6e\u5904\u7684\u6df7\u4e71\uff0c\u5f20\u606a\u4e5f\u8bf4\u4e0d\u51fa\u8981\u4ed6\u4eec\u6ce8\u610f\u8eab\u4f53\u7684\u8bdd\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7559\u5728\u4e09\u677e\u4f53\u80b2\u573a\uff0c\u4ed6\u7559\u5728\u90a3\u91cc\u4e5f\u53d1\u6325\u4e0d\u4e86\u591a\u5c11\u4f5c\u7528\uff0c\u60f3\u56de\u5e02\u5185\u627e\u5bb6\u5bbe\u9986\u4f11\u606f\uff0c\u94bb\u56de\u8f66\u91cc\u6ca1\u7b49\u8f66\u542f\u52a8\uff0c\u4ed6\u5c31\u6c89\u6c89\u7684\u7761\u4e86\u8fc7\u53bb\uff0c\u7b49\u4ed6\u9192\u8fc7\u6765\uff0c\u53d1\u73b0\u7fdf\u4e39\u9752\u5934\u6795\u7740\u4ed6\u7684\u5927\u817f\u8eab\u5b50\u8737\u7f29\u7740\u8eba\u5728\u6905\u5b50\u4e0a\u6b63\u7761\u5f97\u8212\u670d\uff0c\u4ed6\u4eec\u8fd8\u5728\u8f66\u91cc\uff0c\u8fc7\u5ea6\u7684\u75b2\u60eb\u8ba9\u5927\u5bb6\u90fd\u65e0\u6cd5\u987e\u53ca\u4eea\u6001\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u7fdf\u4e39\u9752\u7684\u5934\u8f7b\u8f7b\u7684\u6258\u8d77\uff0c\u6253\u5f00\u8f66\u95e8\u4e0b\u4e86\u8f66\uff0c\u5085\u4fca\u4e0e\u53f8\u673a\u8e72\u5728\u8f66\u65c1\u62bd\u70df\uff0c\u5176\u4ed6\u5de5\u4f5c\u4eba\u5458\u90fd\u5728\u53e6\u4e00\u8f86\u8f66\u91cc\u7761\u89c9\uff0c\u5085\u4fca\u770b\u89c1\u5f20\u606a\u4e0b\u8f66\u6765\uff0c\u7ad9\u8d77\u6765\u95ee\u9053\uff1a\u201c\u662f\u4e0d\u662f\u53bb\u623f\u95f4\u518d\u4f11\u606f\u4e00\u4e0b\uff1f\u201d\u521a\u624d\u770b\u5f20\u606a\u5728\u8f66\u91cc\u7761\u5f97\u719f\uff0c\u5230\u5bbe\u9986\u505c\u4e0b\u8f66\u4e5f\u6ca1\u6709\u53eb\u9192\u4ed6\u3002</p><p>\u3000\u3000\u201c\u8ba9\u5979\u518d\u7761\u4e00\u4f1a\u513f\u2026\u2026\u9192\u8fc7\u6765\u5c31\u518d\u96be\u7761\u7740\u4e86\u3002\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u8e72\u4e0b\u6765\uff0c\u4ece\u53f8\u673a\u624b\u91cc\u63a5\u8fc7\u70df\uff0c\u95ee\u5085\u4fca\uff0c\u201c\u51e0\u70b9\u4e86\uff1f\u201d\u5728\u5730\u4e0b\u505c\u8f66\u573a\uff0c\u770b\u4e0d\u5230\u5916\u9762\u7684\u5149\u4eae\u3002</p><p>\u3000\u3000\u201c\u5341\u4e00\u70b9\u4e86\uff0c\u201d\u5085\u4fca\u5c06\u624b\u673a\u9012\u7ed9\u4ed6\uff0c\u53c8\u5c06\u8bb0\u5f55\u672c\u62ff\u51fa\u6765\u7ffb\u7ed9\u4ed6\u770b\uff0c\u8bf4\u9053\uff0c\u201c\u5f20\u5e02\u957f\u8ddf\u4f60\u5988\u5988\u4e03\u70b9\u949f\u6253\u8fdb\u7535\u8bdd\u6765\uff0c\u5b59\u5148\u751f\u3001\u53f6\u603b\u3001\u5f90\u7701\u957f\u3001\u53f6\u90e8\u957f\u3001\u7f57\u4e66\u8bb0\u3001\u5510\u4e66\u8bb0\u3001\u6881\u7701\u957f\u3001\u8d75\u5e02\u957f\u90fd\u6253\u6709\u8fc7\u7535\u8bdd\u6765\uff0c\u90fd\u6ca1\u8ba9\u6211\u53eb\u9192\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5148\u7ed9\u6881\u4f1f\u6cd5\u56de\u7535\u8bdd\uff0c\u7535\u8bdd\u662f\u6881\u4f1f\u6cd5\u7684\u79d8\u4e66\u63a5\u7684\uff0c\u624d\u77e5\u9053\u66fe\u5982\u5723\u526f\u603b\u7406\u65e9\u6668\u4e58\u4e13\u673a\u62b5\u8fbe\u91d1\u5c71\uff0c\u6881\u4f1f\u6cd5\u6b63\u966a\u540c\u66fe\u526f\u603b\u7406\u89c6\u5bdf\u707e\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u5173\u5fc3\u707e\u60c5\u6709\u65e0\u5f02\u5e38\u3001\u6551\u751f\u62a2\u9669\u5de5\u4f5c\u6709\u6ca1\u6709\u987a\u5229\u5c55\u5f00\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u7684\u5230\u6765\uff0c\u4ed6\u5012\u6ca1\u6709\u60f3\u5230\u8981\u53bb\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u4e5f\u77e5\u9053\u6881\u4f1f\u6cd5\u966a\u5728\u66fe\u5982\u5723\u8eab\u8fb9\uff0c\u5fc3\u60f3\u8d75\u6709\u4f26\u6b64\u65f6\u6216\u8bb8\u4f1a\u6025\u5207\u627e\u673a\u4f1a\u4e0e\u66fe\u5982\u5723\u526f\u603b\u7406\u8bf4\u4e0a\u8bdd\uff0c\u4e5f\u5c31\u6ca1\u6709\u6025\u7740\u7ed9\u4ed6\u56de\u7535\u8bdd\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u767e\u4ebf\u5428\u6d2a\u6c34\u4e00\u5ea6\u5a01\u80c1\u91d1\u5c71\u57ce\uff0c\u4e3e\u56fd\u4e0a\u4e0b\u4e0d\u6653\u5f97\u6709\u591a\u5c11\u4e3a\u6b64\u5f7b\u591c\u96be\u7720\uff0c\u5f20\u606a\u770b\u4e86\u770b\u5085\u4fca\u7684\u8bb0\u5f55\uff0c\u8fd8\u8981\u597d\u4e9b\u7535\u8bdd\u8981\u56de\uff0c\u5f53\u4e2d\u7fdf\u4e39\u9752\u9192\u4e86\u8fc7\u6765\uff0c\u5927\u5bb6\u624d\u79bb\u5f00\u505c\u8f66\u573a\u8fdb\u4e86\u623f\u95f4\u91cc\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u845b\u660e\u4fe1\u624d\u4e0d\u4f1a\u53bb\u7ba1\u91d1\u5c71\u6e56\u6e83\u5824\u4f1a\u6709\u591a\u5c11\u4eba\u6d41\u79bb\u5931\u843d\uff0c\u4f1a\u6709\u591a\u5c11\u4eba\u5728\u6d2a\u6c34\u4e2d\u4e27\u751f\uff0c\u4f46\u662f\u4ed6\u4e00\u6837\u6c89\u6d78\u5728\u6b32\u54ed\u65e0\u6cea\u7684\u7edd\u671b\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u660e\u62a5\u3001\u5927\u516c\u62a5\u7b49\u9999\u6e2f\u5a92\u4f53\u90fd\u62a5\u9053\u4e86\u91d1\u5c71\u6e56\u6e83\u5824\u7684\u65b0\u95fb\uff0c\u4e0a\u5348\u5341\u70b9\uff0c\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u5f00\u76d8\u4ea4\u6613\uff0c\u5609\u4fe1\u7535\u5b50\u80a1\u4ef7\u5c31\u50cf\u6e83\u5824\u7684\u91d1\u5c71\u6e56\uff0c\u4e00\u6cc4\u5343\u91cc\u3002</p><p>\u3000\u3000\u53d7\u4e9a\u6d32\u7ecf\u6d4e\u5371\u673a\u7684\u5f71\u54cd\uff0c\u73e0\u6c5f\u4e09\u89d2\u6d32\u5730\u533a\u7535\u5b50\u4ee3\u52a0\u5de5\u4e1a\u906d\u53d7\u5de8\u5927\u6253\u51fb\uff0c\u4e09\u56db\u6708\u4efd\u624d\u6709\u590d\u82cf\u7684\u8ff9\u8c61\uff0c\u5230\u516d\u4e03\u6708\u95f4\uff0c\u6b27\u7f8e\u7ecf\u6d4e\u4f3c\u4e4e\u4e5f\u9677\u5165\u4f4e\u9761\u4e4b\u4e2d\uff0c\u4fc4\u7f57\u65af\u4e5f\u6709\u5377\u5165\u91d1\u878d\u5371\u673a\u7684\u8ff9\u8c61\uff0c\u4f7f\u5f97\u7a0d\u6709\u590d\u82cf\u8ff9\u8c61\u7684\u4e9a\u6d32\u7ecf\u6d4e\u53c8\u7ed9\u5f53\u5934\u6cfc\u4e86\u4e00\u76c6\u51b7\u6c34\uff0c\u4e0d\u8fc7\u5609\u4fe1\u7535\u5b50\u5728\u91d1\u5c71\u7684\u6295\u8d44\u4e3b\u8981\u662f\u9488\u5bf9\u5f00\u62d3\u5185\u5730\u7684\u5e02\u573a\uff0c\u800c\u9700\u6c42\u4e00\u76f4\u65fa\u76db\u7684\u5185\u5730\u5e02\u573a\u8ba9\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u91d1\u5c71\u9879\u76ee\u6000\u6709\u4e00\u5b9a\u7684\u4fe1\u5fc3\uff0c\u76f8\u4fe1\u5373\u4f7f\u4e1c\u5357\u4e9a\u5e02\u573a\u77ed\u671f\u65e0\u6cd5\u590d\u82cf\uff0c\u53ea\u8981\u9488\u5bf9\u5185\u5730\u5e02\u573a\u7684\u91d1\u5c71\u5de5\u5382\u542f\u7528\uff0c\u5609\u4fe1\u7535\u5b50\u7684\u4e1a\u7ee9\u5c31\u4f1a\u5f3a\u52bf\u7684\u590d\u82cf\uff0c\u4f7f\u5f97\u5609\u4fe1\u96c6\u56e2\u5728\u6b64\u56f0\u96be\u5c40\u9762\u4e0b\u7a0d\u5fae\u8f7b\u677e\u7684\u7ef4\u6301\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u4ef7\uff0c\u963b\u65ad\u9526\u6e56\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u89ca\u89ce\u4e4b\u5fc3\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\u3001\u5609\u4fe1\u7535\u5b50\u8054\u5408\u6b63\u6cf0\u96c6\u56e2\u6295\u8d44\u8fd1\u4e8c\u5341\u4ebf\u7684\u91d1\u5c71\u5de5\u5382\u65b0\u5efa\u6210\u5c31\u7ed9\u6d2a\u6c34\u51b2\u57ae\uff0c\u5927\u6982\u6ca1\u6709\u8fd9\u6761\u6d88\u606f\u66f4\u80fd\u5f7b\u5e95\u7684\u6467\u6bc1\u6295\u8d44\u4eba\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u4fe1\u5fc3\u4e86\u3002</p><p>\u3000\u3000\u201c\u503c\u6b64\u4e4b\u9645\uff0c\u6211\u4eec\u4e5f\u4e0d\u5f97\u4e0d\u653e\u5f03\u4e4b\u524d\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u4fe1\u5fc3\uff0c\u5609\u4fe1\u7535\u5b50\u8981\u6e21\u8fc7\u773c\u524d\u7684\u96be\u5173\uff0c\u4f3c\u4e4e\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u2026\u2026\u201d\u9999\u6e2f\u65e0\u7ebf\u7535\u89c6\u53f0\u7684\u8d22\u7ecf\u8d44\u8baf\u6b63\u64ad\u62a5\u5bf9\u8d8a\u79c0\u63a7\u80a1\u603b\u88c1\u848b\u8587\u7684\u91c7\u8bbf\u3002</p><p>\u3000\u3000\u845b\u836b\u5747\u8d70\u8fdb\u6765\u770b\u5230\u8fd9\u5219\u65b0\u95fb\uff0c\u4e5f\u61d2\u5f97\u62b1\u6028\u9526\u6e56\u8fd9\u65f6\u5019\u8fd8\u4e0d\u5fd8\u8bb0\u516c\u5f00\u7ad9\u51fa\u6765\u6345\u4ed6\u4eec\u4e00\u5200\uff0c\u4ed6\u4eec\u8fd9\u4e00\u5e74\u4ee5\u6765\u5bf9\u91d1\u5c71\u9879\u76ee\u6295\u5165\u5de8\u5927\u7684\u671f\u5f85\uff0c\u542c\u5230\u91d1\u5c71\u9879\u76ee\u7ed9\u6d2a\u6c34\u6467\u6bc1\u7684\u6d88\u606f\uff0c\u7535\u89c6\u91cc\u5bf9\u848b\u8587\u7684\u91c7\u8bbf\u542c\u8d77\u6765\u5c31\u6709\u4e9b\u4e0d\u75db\u4e0d\u75d2\u4e86\u3002</p><p>\u3000\u3000\u201c\u7b56\u7565\u90e8\u7814\u7a76\u51fa\u4ec0\u4e48\u5bf9\u7b56\u6765\u6ca1\u6709\uff1f\u201d\u845b\u660e\u4fe1\u62ac\u5934\u770b\u7740\u845b\u836b\u5747\u3002</p><p>\u3000\u3000\u51e0\u4e4e\u5c31\u5728\u4e00\u523b\u949f\u7684\u65f6\u5019\u91cc\uff0c\u5609\u4fe1\u96c6\u56e2\u5bf9\u5609\u4fe1\u7535\u5b50\u6301\u80a1\u8fd140\uff05\u7684\u5e02\u503c\u5c31\u9aa4\u51cf24\u4ebf\u6e2f\u5143\uff0c\u5609\u4fe1\u7535\u5b50\u80a1\u4ef7\u4e0d\u8db3\u4e5d\u516d\u5e74\u5e74\u5e95\u9ad8\u4f4d\u65f6\u7684\u4e09\u5206\u4e4b\u4e00\u3002\u7279\u522b\u662f\u8fd9\u6bb5\u65f6\u95f4\uff0c\u4e3a\u4e86\u963b\u65ad\u9526\u6e56\u5bf9\u5609\u4fe1\u7535\u5b50\u7684\u89ca\u89ce\u4e4b\u5fc3\uff0c\u845b\u660e\u4fe1\u52a8\u7528\u96c6\u56e2\u8fd1\u5341\u56db\u4ebf\u6e2f\u5143\u7684\u50a8\u5907\u8d44\u91d1\u589e\u6301\u5609\u4fe1\u7535\u5b50\uff0c\u8fd9\u90e8\u5206\u589e\u6301\u9aa4\u7136\u95f4\u5c31\u4e8f\u635f\u4e86\u8fd1\u4e94\u4ebf\u6e2f\u5143\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8fb9\u53ea\u80fd\u62df\u5b9a\u7c97\u7565\u7684\u65b9\u6848\uff0c\u8fd8\u8981\u770b\u91d1\u5c71\u7684\u6551\u707e\u62a2\u9669\u529b\u5ea6\uff0c\u201d\u845b\u836b\u5747\u8bf4\u9053\uff0c\u201c\u8463\u4e8b\u4eec\u90fd\u5230\u9f50\u4e86\uff0c\u5c31\u7b49\u7238\u7238\u4f60\u8fc7\u53bb\u53ec\u5f00\u4f1a\u8bae\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u5f00\u4f1a\u6709\u4ec0\u4e48\u7528\uff1f\u201d\u845b\u660e\u4fe1\u5634\u91cc\u867d\u8fd9\u4e48\u8bf4\uff0c\u4eba\u8fd8\u662f\u7ad9\u4e86\u8d77\u6765\uff0c\u53c8\u95ee\u845b\u836b\u5747\u4ed6\u4eec\u4ec0\u4e48\u65f6\u5019\u80fd\u53bb\u91d1\u5c71\u3002</p></article>"}}}]);
