(window.webpackJsonp=window.webpackJsonp||[]).push([[879],{902:function(p){p.exports={chapterTitle:null,title:"\u7b2c701\u7ae0 \u6a61\u6811\u56ed\u6280\u672f\u73ed\u5e95",content:"<article><h2>\u7b2c701\u7ae0 \u6a61\u6811\u56ed\u6280\u672f\u73ed\u5e95</h2><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u90a3\u684c\u5df2\u7ecf\u5750\u6ee1\u4eba\uff0c\u4e01\u6625\u5065\u4e0e\u4e01\u78ca\u7ad9\u8d77\u6765\u8981\u8ba9\u5ea7\u4f4d\u8ba9\u51fa\u6765\uff0c\u5f20\u606a\u62e6\u7740\u4ed6\u4eec\uff0c\u8bf4\u9053\uff1a\u201c\u52a0\u4e24\u5f20\u6905\u5b50\u6324\u4e00\u6324\u624d\u70ed\u95f9\u2026\u2026\u201d\u53c8\u8981\u5bf9\u4e01\u6625\u5065\u8bf4\u9053\uff0c\u201c\u6211\u8868\u54e5\u6881\u6587\u6c5f\u8fd9\u4e24\u5929\u4e5f\u5728\u6d77\u5dde\uff0c\u4f60\u6709\u6ca1\u6709\u8ddf\u4ed6\u8054\u7edc\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u521a\u901a\u8fc7\u7535\u8bdd\u2026\u2026\u201d\u4e01\u6625\u5065\u4e0e\u6881\u6587\u6c5f\u662f\u6e05\u534e\u8ba1\u7b97\u673a\u7cfb\u6821\u53cb\uff0c\u4e01\u6625\u5065\u4e24\u5e74\u524d\u52a0\u5165\u9526\u6e56\uff0c\u4ee5\u4ed6\u5728\u5236\u6210\u7535\u8def\u7814\u7a76\u9886\u57df\u5353\u8d8a\u7684\u624d\u534e\uff0c\u5c06\u62c5\u4efb\u5fae\u7535\u5b50\u57fa\u7840\u5b9e\u9a8c\u5ba4\u7684\u9996\u5e2d\u5de5\u7a0b\u5e08\uff0c\u662f\u8c2d\u4e91\u677e\u624b\u4e0b\u4e24\u540d\u9996\u5e2d\u5de5\u7a0b\u5e08\u4e4b\u4e00\uff0c\u5730\u4f4d\u8981\u5728\u5176\u4ed6\u4e3b\u4efb\u5de5\u7a0b\u5e08\u4e4b\u4e0a\u3002\u4e01\u78ca\u539f\u662f\u5fb7\u4eea\u4e2d\u56fd\u7814\u53d1\u4e2d\u5fc3\u7684\u9996\u5e2d\u5de5\u7a0b\u5e08\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u5408\u8d44\u6210\u7acb\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u4e4b\u540e\uff0c\u4ed6\u5c31\u7ed9\u62c9\u5230\u6d77\u5dde\u6765\uff0c\u5b9e\u884c\u9996\u5e2d\u79d1\u5b66\u5bb6\u5236\u4e4b\u540e\uff0c\u4ed6\u5c31\u62c5\u4efb\u6cf0\u5fd2\u5149\u7535\u6280\u7814\u7a76\u9662\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\u3002</p><p>\u3000\u3000\u5728\u9526\u6e56\u7684\u6280\u672f\u7814\u53d1\u4f53\u7cfb\u91cc\uff0c\u53ea\u8981\u8fdb\u884c\u57fa\u7840\u6280\u672f\u7814\u7a76\u7684\u5b9e\u9a8c\u5ba4\u624d\u5b9e\u65bd\u9996\u5e2d\u79d1\u5b66\u5bb6\u5236\uff0c\u76ee\u524d\u5c31\u6210\u6c49\u748b\u517c\u4efbESS\u5b9e\u9a8c\u5ba4\u9996\u5e2d\u79d1\u5b66\u5bb6\uff0c\u8c2d\u4e91\u677e\u62c5\u4efb\u6a61\u6811\u56ed\u5fae\u7535\u5b50\u5b9e\u9a8c\u5ba4\u9996\u5e2d\u79d1\u5b66\u5bb6\uff0c\u5f53\u7136\uff0c\u6210\u6c49\u748b\u8fd8\u6709\u5176\u4ed6\u7684\u804c\u8d23\uff0c\u662f\u5927\u9879\u76ee\u90e8\u7684\u6210\u5458\uff0c\u53c2\u4e0e\u5230\u516c\u53f8\u7684\u6218\u7565\u51b3\u7b56\uff0c\u5730\u4f4d\u8981\u9ad8\u8bb8\u591a\u3002\u5176\u4ed6\u6700\u9ad8\u7ea7\u522b\u7684\u7814\u53d1\u4eba\u5458\u5c31\u662f\u9996\u5e2d\u5de5\u7a0b\u5e08\u6216\u9996\u5e2d\u7814\u7a76\u5458\uff0c\u4e01\u69d0\u7684\u4e3b\u8981\u7cbe\u529b\u8f6c\u79fb\u5230\u6574\u4e2a\u6280\u672f\u7814\u53d1\u4f53\u7cfb\u7684\u7ba1\u7406\u4e0a\uff0c\u7edf\u8f96\u5168\u5c40\uff0c\u4e0d\u4f1a\u53bb\u505a\u5177\u4f53\u800c\u5fae\u7684\u9879\u76ee\u7814\u53d1\u3002</p><p>\u3000\u3000\u5750\u5728\u8fd9\u4e00\u684c\u7684\u4eba\uff0c\u9664\u4e86\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u662f\u9996\u5e2d\u79d1\u5b66\u5bb6\u6216\u5de5\u7a0b\u5e08\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8fd9\u6b21\u4ece\u7f8e\u56fd\u5e26\u6765\u7684\u7814\u53d1\u4eba\u5458\u5206\u4e09\u7c7b\u3002</p><p>\u3000\u3000\u4e00\u7c7b\u662f\u5e76\u8d2dESS\u4e4b\u540e\u5c31\u7531\u4e01\u6625\u5065\u7387\u9886\u524d\u5f80\u7f8e\u56fd\u5bf9M-1\u3001U-1\u8fdb\u884c\u6280\u672f\u653b\u5173\u7684\u7814\u53d1\u5c0f\u7ec4\u6210\u5458\u3002\u8fd9\u4e00\u90e8\u5206\u7814\u53d1\u4eba\u5458\uff0c\u662f\u81ea\u7231\u8fbe\u7535\u5b50\u6210\u7acb\u4e4b\u521d\u5c31\u52aa\u529b\u79ef\u6512\u4e0b\u6765\u7684\u6838\u5fc3\u6280\u672f\u529b\u91cf\u2014\u2014\u9526\u6e56\u5728\u4e4b\u524d\u751a\u81f3\u5c06\u6d77\u5dde\u5e02\u79d1\u6280\u5c40\u4e0b\u5c5e\u7684\u7535\u5b50\u7814\u7a76\u6240\u7ed9\u517c\u5e76\u8fc7\u6765\u2014\u2014\u4ed6\u4eec\u4e2d\u5927\u591a\u6570\u4eba\u90fd\u53c2\u4e0e\u65b0\u4e00\u4ee3\u89e3\u7801\u7cfb\u7edf\u7684\u5b8c\u6574\u5f00\u53d1\u5de5\u4f5c\u3002\u4ed6\u4eec\u7ed9\u62bd\u8c03\u53bb\u7f8e\u56fd\u4e4b\u540e\uff0c\u9526\u6e56\u7535\u5b50\u6280\u672f\u7814\u7a76\u9662\u7684\u4eba\u6570\u867d\u7136\u6ca1\u6709\u51cf\u5c11\u591a\u5c11\uff0c\u4f46\u662f\u6280\u672f\u529b\u91cf\u5c31\u7acb\u5373\u76f8\u5f62\u89c1\u7ecc\u4e86\uff0c\u4ece\u800c\u5c06\u5de5\u4f5c\u7684\u91cd\u5fc3\u5f7b\u5e95\u7684\u8f6c\u79fb\u5230\u6bd4\u8f83\u6d45\u5c42\u6b21\u7684\u5e94\u7528\u6280\u672f\u7684\u7814\u53d1\u4e0a\u3002\u8fd9\u4e5f\u662f\u8fd1\u4e00\u5e74\u65f6\u95f4\u6765\uff0c\u5916\u754c\u6240\u770b\u5230\u7684\uff0c\u9526\u6e56\u5728\u6280\u672f\u7814\u53d1\u4e0a\u7684\u5b9e\u529b\uff0c\u4f3c\u4e4e\u4e5f\u4ee3\u8868\u5f53\u524d\u56fd\u5185\u8f83\u4e3a\u6709\u5b9e\u529b\u7684\u6280\u672f\u529b\u91cf\u3002\u7ed9\u62bd\u8c03\u53bb\u7f8e\u56fd\u7684\u4eba\u867d\u7136\u4e0d\u591a\uff0c\u9646\u9646\u7eed\u7eed\u52a0\u8d77\u6765\uff0c\u8fd8\u4e0d\u5230\u4e94\u5341\u4eba\uff0c\u8fd9\u4e9b\u4eba\u7edd\u5927\u591a\u6570\u4e4b\u524d\u5c31\u7531\u4e8e\u6770\u51fa\u7684\u7814\u53d1\u5de5\u4f5c\u83b7\u5f97\u80a1\u6743\u6fc0\u52b1\uff0c\u4e5f\u662f\u6a61\u6811\u56ed\u5b9e\u9a8c\u57fa\u5730\u53ef\u4ee5\u4f9d\u91cd\u7684\u4e2d\u575a\u529b\u91cf\u3002</p><p>\u3000\u3000\u4e00\u7c7b\u662f\u8fd9\u6b21\u201c\u767e\u4eba\u884c\u52a8\u201d\u8ba1\u5212\u5728\u6d77\u5916\u62db\u52df\u5230\u534e\u4eba\u79d1\u5b66\u5bb6\u3002\u6a61\u6811\u56ed\u5b9e\u9a8c\u57fa\u5730\u7684\u57fa\u5efa\u76f4\u5230\u8fd1\u65e5\u624d\u5b8c\u6210\uff0c\u4e4b\u524d\u6ca1\u6709\u5f00\u5c55\u7814\u53d1\u5de5\u4f5c\u7684\u6761\u4ef6\uff0c\u9664\u4e86\u5728\u521b\u4e1a\u56ed\u533a\u5185\u6210\u7acb\u5b9e\u9a8c\u5ba4\u7b79\u5907\u5904\u7559\u5c11\u90e8\u5206\u4eba\u5f00\u5c55\u524d\u671f\u7b79\u5907\u5de5\u4f5c\u4e4b\u5916\uff0c\u8fd9\u6b21\u62db\u52df\u5230\u7684\u5927\u591a\u6570\u534e\u4eba\u79d1\u5b66\u5bb6\u90fd\u6682\u65f6\u9001\u5f80ESS\u7684\u5b9e\u9a8c\u5ba4\u3002\u5728\u90a3\u91cc\uff0c\u4ed6\u4eec\u53ef\u4ee5\u76f4\u63a5\u53c2\u4e0e\u5230\u5f53\u524d\u7684\u7814\u53d1\u5de5\u4f5c\u4e4b\u4e2d\u3002\u8fd9\u7c7b\u4eba\u4e2d\uff0c\u4e5f\u5305\u62ec\u4ece\u6b27\u7f8e\u5730\u533a\u62db\u8058\u5230\u534e\u4eba\u79d1\u5b66\u5bb6\u3002\u6210\u6c49\u748b\u6b63\u5f0f\u52a0\u76df\u9526\u6e56\u4e4b\u540e\uff0c\u5c31\u4e00\u76f4\u79ef\u6781\u7684\u5728\u6b27\u7f8e\u5730\u533a\u4e3a\u9526\u6e56\u7269\u8272\u9ad8\u6c34\u51c6\u7684\u7814\u53d1\u4eba\u5458\uff0c\u8fd9\u4e9b\u4eba\u5f53\u7136\u6709\u613f\u610f\u5230\u56fd\u5185\u6765\u5de5\u4f5c\u7684\uff0c\u4e5f\u4e0d\u613f\u610f\u56de\u56fd\u5c31\u7ee7\u7eed\u7559\u5728ESS\u7684\u5b9e\u9a8c\u5ba4\uff0c\u6bd5\u7adfESS\u4ee5\u540e\u5c06\u9526\u6e56\u4e0e\u56fd\u9645\u5148\u8fdb\u6280\u672f\u63a5\u8f68\u7684\u6700\u4e3b\u8981\u901a\u9053\u3002\u8fd9\u4e9b\u5e74\u6765\uff0c\u56fd\u5185\u8bb8\u591a\u4f18\u79c0\u4eba\u624d\u5230\u6d77\u5916\u7559\u5b66\u540e\u5927\u591a\u9009\u62e9\u7559\u5728\u6d77\u5916\u5de5\u4f5c\u3002\u4ed6\u4eec\u5728\u56fd\u5916\u53d7\u91cd\u89c6\u7684\u7a0b\u5ea6\u6bd4\u4e0d\u4e0a\u5f53\u5730\u4eba\uff0c\u4f46\u662f\u56fd\u5185\u53c8\u6ca1\u6709\u4ed6\u4eec\u65bd\u52a0\u624d\u80fd\u7684\u5e73\u53f0\uff0c\u4e0d\u5f97\u4e0d\u4e00\u76f4\u6ede\u7559\u5728\u6d77\u5916\u3002\u8fd9\u4e9b\u6d77\u5916\u7559\u5b66\u751f\u5728\u6b27\u7f8e\u7535\u5b50\u5de5\u4e1a\u7684\u7814\u53d1\u4f53\u7cfb\u5185\u5360\u636e\u7740\u6781\u5176\u91cd\u8981\u7684\u5730\u4f4d\u3002\u5fb7\u4eea\u5c06\u9664\u57fa\u5e26\u82af\u7247\u4e4b\u5916\u7684\u4e8c\u6d41\u6570\u5b57\u624b\u673a\u6280\u672f\u4e0e\u76f8\u5173\u7814\u53d1\u56e2\u961f\u90fd\u5265\u79bb\u7ed9ESS\uff0c\u516b\u5341\u4e00\u4eba\u7684\u7814\u53d1\u56e2\u961f\uff0c\u5176\u4e2d\u534e\u4eba\u5de5\u7a0b\u5e08\u5c31\u6709\u4e8c\u5341\u4e94\u4eba\u2014\u2014\u8fd9\u90e8\u534e\u4eba\u5de5\u7a0b\u5e08\u5927\u591a\u613f\u610f\u56de\u56fd\u5185\u5de5\u4f5c\uff0c\u4e5f\u5c06\u7531\u4ed6\u4eec\u4e0e\u4e00\u90e8\u5206\u6d3e\u5f80\u5185\u5730\u7684\u6b27\u7f8e\u5de5\u7a0b\u5e08\u6491\u8d77\u6a61\u6811\u56ed\u6570\u5b57\u624b\u673a\u5b9e\u9a8c\u5ba4\u7684\u9aa8\u67b6\u6765\u2014\u2014\u7531\u6b64\u53ef\u89c1\u201c\u56fd\u5185\u7684\u6559\u80b2\u4f53\u7cfb\u90fd\u5728\u7ed9\u6b27\u7f8e\u56fd\u4f53\u57f9\u517b\u4eba\u624d\u201d\u7684\u8bdd\u5e76\u975e\u662f\u65e0\u7684\u653e\u77e2\u3002</p><p>\u3000\u3000\u8fd8\u6709\u4e00\u7c7b\u4eba\uff0c\u5c31\u662f\u9526\u6e56\u8fd1\u671f\u5728\u56fd\u5185\u5927\u6279\u62db\u8058\u540e\u62e9\u4f18\u9009\u9001ESS\u5b9e\u9a8c\u5ba4\u8fdb\u884c\u57f9\u8bad\u7684\u65b0\u664b\u7814\u53d1\u4eba\u5458\u3002\u8fd9\u90e8\u5206\u4eba\u6570\u6700\u591a\uff0c\u6709\u4e24\u767e\u56db\u5341\u591a\u4eba\uff0c\u5927\u534a\u8fd8\u662f\u4eca\u5e74\u590f\u5929\u521a\u4ece\u5404\u5927\u9ad8\u6821\u6bd5\u4e1a\u7684\u5e94\u5c4a\u6bd5\u4e1a\u751f\u3002\u867d\u7136\u5f53\u4e2d\u6709\u8bb8\u591a\u7855\u58eb\u3001\u535a\u58eb\uff0c\u4f46\u9274\u4e8e\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u7684\u57fa\u7840\u8584\u5f31\uff0c\u7814\u53d1\u6c34\u5e73\u4e0e\u56fd\u9645\u5148\u8fdb\u6280\u672f\u8131\u8282\u592a\u591a\uff0c\u8fd9\u4e9b\u4eba\u5fc5\u987b\u8981\u7ecf\u8fc7\u4e00\u6bb5\u65f6\u95f4\u7684\u6dec\u7ec3\u624d\u582a\u5927\u7528\u3002\u5f20\u606a\u5012\u662f\u5f88\u671f\u5f85\u4ed6\u4eec\u6210\u4e3a\u9526\u6e56\u672a\u6765\u4f9d\u91cd\u7684\u6280\u672f\u529b\u91cf\u3002</p><p>\u3000\u3000\u8fd9\u4e09\u90e8\u5206\u4eba\u5c06\u662f\u7b2c\u4e00\u6279\u8fdb\u9a7b\u6a61\u6811\u56ed\u5b9e\u9a8c\u57fa\u5730\u7684\u6280\u672f\u529b\u91cf\uff0c\u9526\u6e56\u5c06\u4e3b\u8981\u4f9d\u8d56\u4ed6\u4eec\u536f\u8db3\u52b2\u5411\u524d\u5954\u8dd1\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8fd9\u6b21\u5e26\u5230\u6d77\u5dde\u6765\u7684\u4eba\uff0c\u4e3b\u8981\u662f\u201c\u767e\u4eba\u884c\u52a8\u201d\u8ba1\u5212\u62db\u8058\u5230\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\uff0c\u4ed6\u4eec\u4e2d\u5927\u591a\u6570\u4eba\u90fd\u8fd8\u6ca1\u6709\u5230\u6d77\u5dde\u3001\u5230\u9526\u6e56\u603b\u90e8\u6765\u8fc7\uff0c\u8fd8\u6709\u4e01\u6625\u5065\u4ed6\u4eec\u7ed9\u5916\u6d3e\u5230ESS\u8fdb\u884c\u6280\u672f\u653b\u51fb\u7684\u7814\u53d1\u5c0f\u7ec4\u6210\u5458\u4eec\uff0c\u4e00\u5171\u4e00\u767e\u56db\u5341\u591a\u4eba\u3002\u90a3\u4e9b\u7ed9\u9001\u5f80ESS\u53c2\u52a0\u57f9\u8bad\u7684\u4e8c\u767e\u56db\u5341\u591a\u540d\u65b0\u664b\u7814\u53d1\u4eba\u5458\u5c31\u7559\u5728\u5efa\u90ba\uff0c\u4ed6\u4eec\u8fd8\u8981\u5b8c\u6210\u6700\u540e\u7684\u57f9\u8bad\u3002\u4e0d\u7136\u6d69\u6d69\u8361\u8361\u56db\u767e\u591a\u4eba\u5c31\u592a\u60f9\u4eba\u77a9\u76ee\u4e86\uff0c\u5f20\u606a\u8fd8\u662f\u8981\u6c42\u4f4e\u8c03\uff0c\u4ee5\u4e0d\u5f15\u8d77\u4e09\u661f\u53ca\u5176\u4ed6\u5916\u8d44\u7535\u5b50\u5382\u5546\u7684\u6ce8\u610f\u4e3a\u4f73\uff0c\u4eca\u5929\u7684\u62db\u5f85\u6d3b\u52a8\uff0c\u4e5f\u4ec5\u4ec5\u662f\u9526\u6e56\u5185\u90e8\u4e3e\u884c\uff0c\u5916\u754c\u4e00\u70b9\u6d88\u606f\u90fd\u6ca1\u6709\u900f\u9732\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u665a\u5bb4\u7ed3\u675f\u540e\uff0c\u6709\u4e9b\u4eba\u65c5\u9014\u52b3\u987f\uff0c\u4f1a\u5148\u56de\u623f\u95f4\u4f11\u606f\uff1b\u6709\u4e9b\u4eba\u7cbe\u529b\u5145\u6c9b\uff0c\u9152\u5e97\u91cc\u5a31\u4e50\u8bbe\u5907\u5b8c\u5907\uff1b\u751a\u81f3\u6709\u4eba\u63d0\u8bae\u591c\u6e38\u8c61\u5c71\uff0c\u8fd9\u8fb9\u8d1f\u8d23\u63a5\u5f85\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u4f1a\u5c3d\u91cf\u6ee1\u8db3\u4ed6\u4eec\u7684\u9700\u6c42\u3002\u5f20\u606a\u5219\u4e0e\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4ed6\u4eec\u8ddf\u8c2d\u4e91\u677e\u8fd9\u4e9b\u9996\u5e2d\u79d1\u5b66\u5bb6\u3001\u9996\u5e2d\u5de5\u7a0b\u5e08\u79fb\u5230\u4e00\u95f4\u96c5\u5ba4\u559d\u8336\u804a\u5929\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u5c0f\u6c5f\u6e90\u5934\u4e0a\u7684\u5c71\u8336\uff0c\u770b\u4e0a\u53bb\u5f62\u788e\uff0c\u5356\u76f8\u4e0d\u597d\uff0c\u559d\u8d77\u6765\u8fd8\u4e0d\u9519\u3002\u6211\u559c\u6b22\u559d\u8fd9\u8336\uff0c\u4e0d\u7ba1\u8dd1\u5230\u54ea\u91cc\uff0c\u53ea\u8981\u662f\u8bf7\u522b\u4eba\u559d\u8336\uff0c\u90fd\u559d\u8fd9\u8336\uff0c\u4e0d\u6653\u5f97\u4f60\u4eec\u559d\u4e0d\u559d\u5f97\u60ef\uff1f\u201d\u8fd9\u4e9b\u5c71\u8336\u4e4b\u540e\u8fd8\u662f\u8bb8\u9e3f\u4f2f\u6bcf\u56de\u7ecf\u8fc7\u6d2a\u81f4\u6797\u573a\u5e26\u56de\u6765\u4e00\u4e9b\uff0c\u8f66\u91cc\u6709\u5907\uff0c\u6bcf\u56de\u8981\u559d\u8336\uff0c\u5f20\u606a\u90fd\u8ba9\u5085\u4fca\u4ece\u8f66\u91cc\u76f4\u63a5\u62ff\u51fa\u6765\u5c31\u662f\u3002</p><p>\u3000\u3000\u505a\u7814\u7a76\u7684\u4eba\uff0c\u6027\u683c\u90fd\u5f88\u6de1\uff0c\u559c\u6b22\u559d\u8336\u7684\u4e0d\u5c11\uff0c\u8c2d\u4e91\u677e\u7aef\u7740\u8336\u676f\u95fb\u7740\u9999\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u9999\u6c14\u4e0d\u6bd4\u5413\u715e\u4eba\u9999\u5dee\uff0c\u8336\u5f62\u4e0d\u597d\u770b\uff0c\u5012\u662f\u8ba9\u5b83\u660e\u73e0\u8499\u5c18\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u4e00\u7b11\uff0c\u5750\u4e0b\u6765\u4e5f\u4e0d\u662f\u4e13\u95e8\u8c08\u8336\uff0c\u5728\u5ea7\u7684\u8fd9\u4e9b\u4eba\uff0c\u5dee\u4e0d\u591a\u90fd\u662f\u76f8\u5173\u9886\u57df\u9876\u5c16\u7684\u4e13\u5bb6\uff0c\u4e4b\u524d\u5c31\u4e00\u4e00\u89c1\u8fc7\u3001\u9762\u8c08\u8fc7\uff0c\u6709\u662f\u901a\u8fc7\u201c\u767e\u4eba\u884c\u52a8\u201d\u8ba1\u5212\u62db\u63fd\u8fc7\u6765\u7684\uff0c\u4e5f\u6709\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\u8fd9\u4e9b\u9526\u6e56\u201c\u8001\u4eba\u201d\uff0c\u7aef\u8d77\u8336\u676f\u4e0e\u5927\u5bb6\u62bf\u4e86\u4e00\u53e3\u8336\uff0c\u5c31\u5c06\u8bdd\u9898\u8f6c\u79fb\u5230\u6b63\u9898\uff0c\u8bf4\u9053\uff1a\u201c\u4eca\u5e74\u5728\u5ea7\u7684\u5341\u4e8c\u4eba\uff0c\u53ef\u4ee5\u8bf4\u662f\u5341\u4e8c\u91d1\u521a\uff0c\u90fd\u662f\u5728\u76f8\u5173\u9886\u57df\u6709\u7740\u5f88\u9ad8\u6210\u5c31\u7684\u4e13\u5bb6\uff0c\u6a61\u6811\u56ed\u5b9e\u9a8c\u57fa\u5730\u3001\u9526\u6e56\u672a\u6765\u7684\u6280\u672f\u53d1\u5c55\u65b9\u5411\uff0c\u5c31\u4f9d\u8d56\u8bf8\u4f4d\u7684\u9886\u5bfc\u4e0e\u52aa\u529b\u3002\u4f60\u4eec\u5f53\u4e2d\uff0c\u8fdb\u9526\u6e56\u65f6\u95f4\u6700\u957f\u7684\u662f\u4e8e\u535a\u6d0b\uff0c\u4ed6\u968f\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u4e00\u8d77\u8fdb\u5165\u9526\u6e56\uff0c\u4ece\u6700\u65e9\u7684\u7b2c\u4e00\u6b3e\u789f\u673a\u63a7\u5236\u7cfb\u7edf\u7684\u5f00\u53d1\uff0c\u4ed6\u5c31\u53c2\u4e0e\u8fdb\u6765\u4e86\uff0c\u4e00\u76f4\u5728\u641e\u7814\u53d1\u5de5\u4f5c\uff0c\u4e01\u69d0\u4f60\u662f\u4e0d\u662f\u89c6\u4ed6\u4e3a\u5de6\u8180\u53f3\u81c2\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u5f53\u521d\u8981\u5c06\u4ed6\u6d3e\u5230\u7f8e\u56fd\u53c2\u4e0eM-1\u3001U-1\u4ea7\u54c1\u7684\u6280\u672f\u7814\u53d1\uff0c\u4e5f\u662f\u54ac\u7740\u7259\u72e0\u5fc3\u624d\u540c\u610f\u7684\uff0c\u201d\u4e01\u69d0\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u8bf4\u4e86\u5de6\u8180\uff0c\u5c31\u8981\u8bf4\u53f3\u81c2\u4e86\uff0c\u4e01\u6625\u5065\u4e5d\u4e94\u5e74\u5e74\u5e95\u90fd\u786e\u5b9a\u8fdb\u5fb7\u4eea\u4e86\uff0c\u6211\u8d76\u5728\u6625\u8282\u524d\u5750\u98de\u673a\u8d76\u5230\u6e05\u534e\uff0c\u8d70\u5230\u4ed6\u5bbf\u820d\uff0c\u5728\u4ed6\u5bbf\u820d\u91cc\uff0c\u4ed6\u8fd8\u6177\u6168\u7684\u8bf7\u6211\u5403\u4e86\u4e00\u7897\u65b9\u4fbf\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u4e01\u6625\u5065\u563f\u7136\u4e00\u7b11\uff0c\u81ea\u5632\u9053\uff1a\u201c\u505a\u6280\u672f\u7684\uff0c\u5bf9\u4eba\u60c5\u4e16\u6545\u603b\u4e0d\u662f\u592a\u719f\u6089\uff0c\u8981\u662f\u8fd9\u65f6\u5019\u4e01\u603b\u627e\u6211\u8c08\u5de5\u4f5c\u5230\u6df1\u591c\uff0c\u5bbf\u820d\u8981\u6709\u65b9\u4fbf\u9762\uff0c\u4e5f\u5c31\u51d1\u5408\u7740\u5f53\u591c\u5bb5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u839e\u5c14\u4e00\u7b11\uff0c\u60f3\u5fc5\u8fd9\u4e9b\u4e8b\uff0c\u5927\u5bb6\u90fd\u505a\u5f97\u51fa\u6765\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u5f53\u65f6\u5c0f\u4e01\u90fd\u662f\u5fb7\u4eea\u7684\u4eba\u4e86\uff0c\u6211\u7ed9\u8001\u4e01\u7f20\u4e0d\u8fc7\uff0c\u7b54\u5e94\u653e\u4eba\uff0c\u8fd9\u6b21\u53bb\u8fbe\u62c9\u65af\u9047\u5230\u5b89\u5409\u4f2f\u5148\u751f\uff0c\u4ed6\u8fd8\u8bf4\u653e\u5c0f\u4e01\u5230\u9526\u6e56\u662f\u6211\u6700\u65e9\u2018\u6295\u9760\u2019\u8fc7\u6765\u7684\u8bc1\u636e\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u5f00\u53d1M-1\u3001U-1\u4ea7\u54c1\uff0c\u4e01\u6625\u5065\u53d1\u6325\u51fa\u91cd\u5927\u7684\u4f5c\u7528\uff0c\u867d\u7136\u5e74\u7eaa\u5f88\u8f7b\uff0c\u4f46\u662f\u83b7\u5f97\u5982\u6b64\u7684\u79f0\u8d5e\u4e5f\u662f\u5e94\u5f53\u3002</p><p>\u3000\u3000\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\u662f\u9526\u6e56\u539f\u5148\u6280\u672f\u529b\u91cf\u91cc\u552f\u4e00\u83b7\u5f97\u9996\u5e2d\u5de5\u7a0b\u5e08\u5934\u8854\u8fdb\u5165\u6a61\u6811\u56ed\u5b9e\u9a8c\u57fa\u5730\u5de5\u4f5c\u7684\u7814\u53d1\u4eba\u5458\uff0c\u4ed6\u4eec\u5c06\u9886\u5bfc\u6570\u5b57\u97f3\u4e50\u3001\u97f3\u9891\u7f16\u89e3\u7801\u3001\u97f3\u9891\u5904\u7406\u7b49\u76f8\u5173\u6280\u672f\u9886\u57df\u7684\u7814\u53d1\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4e01\u6625\u5065\u3001\u4e8e\u535a\u6d0b\u4e24\u4eba\u7684\u7a81\u51fa\u8868\u73b0\uff0c\u4e5f\u4ece\u4fa7\u9762\u7a81\u51fa\u9526\u6e56\u5728\u8fc7\u53bb\u4e09\u5e74\u65f6\u95f4\u91cc\u6280\u672f\u5de5\u4f5c\u4e0a\u7684\u6210\u5c31\u3002\u6ca1\u6709\u9526\u6e56\u5bf9\u6280\u672f\u7814\u53d1\u9886\u57df\u7684\u6781\u7aef\u91cd\u89c6\uff0c\u6ca1\u6709\u9526\u6e56\u5343\u65b9\u767e\u8ba1\u7684\u6d3e\u6280\u672f\u4eba\u5458\u53c2\u4e0e\u5230\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u7684\u9ad8\u7aef\u82af\u7247\u6280\u672f\u7814\u53d1\u5de5\u4f5c\u53bb\uff0c\u662f\u65e0\u6cd5\u57f9\u517b\u51fa\u8fd9\u4e24\u540d\u4e00\u6d41\u5de5\u7a0b\u5e08\u7684\u3002</p></article>"}}}]);
