(window.webpackJsonp=window.webpackJsonp||[]).push([[885],{908:function(p){p.exports={chapterTitle:null,title:"\u7b2c882\u7ae0 \u591c\u8272\u6c79\u6d8c",content:"<article><h2>\u7b2c882\u7ae0 \u591c\u8272\u6c79\u6d8c</h2><p>\u3000\u3000\u5728\u6c5f\u5357\u7701\u4e0a\u4e0b\u90fd\u4e3a\u6551\u707e\u6b3e\u9879\u7d27\u7f3a\u6101\u715e\uff0c\u5728\u6b64\u4e4b\u524d\u5c1a\u5b66\u4e49\u5728\u4efb\u4e0a\u8981\u6c42\u5168\u7701\u4e24\u767e\u4e07\u515a\u5458\u9080\u7eb3\u7279\u6b8a\u515a\u8d39\uff0c\u4e5f\u4e0d\u8fc7\u624d\u7b79\u63aa\u5230\u4e00\u4ebf\u591a\u7684\u7279\u6b8a\u515a\u8d39\uff1b\u5f90\u5b66\u5e73\u5230\u4efb\u6ca1\u51e0\u5929\u5c31\u641e\u6765\u4e24\u4ebf\u7f8e\u5143\u7684\u4f4e\u606f\u6551\u707e\u8d37\u6b3e\uff0c\u5c31\u80fd\u8ba9\u5f90\u5b66\u5e73\u7acb\u5373\u5728\u666e\u901a\u5b98\u5458\u5e72\u90e8\u8d62\u5f97\u6781\u9ad8\u7684\u58f0\u671b\uff0c\u4ece\u800c\u987a\u63a5\u7684\u63a5\u624b\u707e\u540e\u6551\u6d4e\u4e0e\u91cd\u5efa\u5de5\u4f5c\u7684\u5927\u5c40\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u5728\u6c5f\u5357\u7701\u624e\u4e0b\u6839\u57fa\u3001\u5efa\u7acb\u5a01\u4fe1\u7684\u963b\u788d\u4e0d\u662f\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u526f\u7701\u957f\u6881\u4f1f\u6cd5\uff0c\u800c\u662f\u62c5\u5fe7\u5f90\u5b66\u5e73\u4f1a\u94c1\u8155\u6cbb\u6c5f\u5357\u7684\u5730\u65b9\u52bf\u529b\u4e0e\u4ee3\u8868\u8fd9\u4e9b\u5730\u65b9\u52bf\u529b\u7684\u5b98\u5458\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u8ba1\u5212\u4e09\u5341\u65e5\u56de\u5efa\u90ba\uff0c\u4e0d\u8fc7\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u7684\u4e13\u5458\u4e09\u5341\u65e5\u5c31\u4ece\u9999\u6e2f\u8f6c\u673a\u8d76\u5230\u91d1\u5c71\uff0c\u5f20\u606a\u5c31\u5728\u91d1\u5c71\u591a\u7559\u4e86\u4e00\u5929\uff0c\u966a\u540c\u5f90\u5b66\u5e73\u3001\u6881\u4f1f\u6cd5\u4eb2\u81ea\u63a5\u89c1\u4e86\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u7684\u4e13\u5458\uff0c\u521d\u6b65\u8c08\u59a5\u6551\u6d4e\u8d37\u6b3e\u7684\u4e8b\u5b9c\u3002</p><p>\u3000\u3000\u4ece\u516d\u6708\u4e0a\u65ec\u8fdb\u5165\u6c5b\u671f\uff0c\u8fd1\u4e24\u4e2a\u6708\u6765\uff0c\u6881\u4f1f\u6cd5\u90fd\u6ca1\u6709\u597d\u597d\u7684\u4f11\u606f\u8fc7\uff0c\u5373\u4f7f\u8fc7\u4e86\u6c5b\u671f\uff0c\u707e\u540e\u6551\u6cbb\u4e0e\u91cd\u5efa\u5de5\u4f5c\u66f4\u662f\u538b\u5f97\u4ed6\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c\u672c\u6765\u5c31\u524a\u7626\u7684\u8138\u988a\u66f4\u52a0\u7684\u6df1\u9677\uff1a\u6709\u4e86\u8fd9\u4e24\u4ebf\u7f8e\u5143\u7684\u6551\u707e\u8d37\u6b3e\uff0c\u6551\u707e\u5de5\u4f5c\u7684\u786e\u80fd\u7f13\u4e00\u53e3\u6c14\uff0c\u81f3\u5c11\u4e0d\u4f1a\u518d\u903c\u5f97\u4eba\u5598\u4e0d\u8fc7\u6c14\u6765\u3002</p><p>\u3000\u3000\u5728\u7701\u59d4\u62db\u5f85\u5bbe\u9986\u5185\uff0c\u4e0e\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u4e13\u5458\u53ca\u968f\u5458\u4eba\u5458\u7528\u8fc7\u9910\u3001\u4eb2\u5207\u4ea4\u6d41\u4e4b\u540e\uff0c\u6881\u4f1f\u6cd5\u4e0e\u5f20\u606a\u540c\u5230\u5f90\u5b66\u5e73\u5728\u7701\u59d4\u62db\u5f85\u5bbe\u9986\u5185\u7684\u4f4f\u5904\uff0c\u521a\u521a\u843d\u5ea7\uff0c\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u8c08\u8d77\u6668\u66e6\u7eb8\u4e1a\u6536\u8d2d\u6848\u6765\u3002</p><p>\u3000\u3000\u7a97\u5916\u9752\u94dc\u67f1\u5f0f\u6837\u7684\u8def\u706f\u5c04\u51fa\u660f\u9ec4\u7684\u706f\u5149\uff0c\u9662\u843d\u91cc\u79cd\u690d\u7740\u8bb8\u591a\u70ed\u5e26\u690d\u7269\uff0c\u68d5\u6988\u6811\u7684\u53f6\u5b50\u6c99\u6c99\u4f5c\u54cd\u3002</p><p>\u3000\u3000\u201c\u8fd9\u573a\u6d2a\u707e\u4e5f\u8ba9\u8bb8\u591a\u4eba\u610f\u8bc6\u5230\u4fdd\u62a4\u751f\u6001\u73af\u5883\u7684\u91cd\u8981\u6027\uff0c\u6d41\u57df\u964d\u96e8\u91cf\u53ea\u4e0e\u4e5d\u4e00\u5e74\u76f8\u5f53\uff0c\u5bf9\u6c5f\u5357\u9020\u6210\u7684\u7834\u574f\u5374\u662f\u767e\u5e74\u672a\u9047\uff0c\u5168\u7701\u7684\u76f4\u63a5\u635f\u5931\uff0c\u4fdd\u5b88\u4f30\u7b97\u4e5f\u5728\u4e09\u767e\u4ebf\u4ee5\u4e0a\uff0c\u5982\u6b64\u60e8\u75db\u7684\u7ecf\u9a8c\u6559\u8bad\uff0c\u6211\u4eec\u5f53\u8981\u7262\u8bb0\uff0c\u201d\u6881\u4f1f\u6cd5\u5750\u5728\u8d77\u5c45\u5ba4\u91cc\u7684\u5e03\u6c99\u53d1\u4e0a\uff0c\u5f90\u5b66\u5e73\u5230\u4e66\u623f\u91cc\u63a5\u4e00\u901a\u4ece\u5317\u4eac\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4ed6\u5728\u8d77\u5c45\u5ba4\u91cc\u4e0e\u5f20\u606a\u8bf4\u8bdd\uff0c\u4ed6\u8fd9\u6bb5\u65f6\u95f4\u6765\u4e5f\u5f88\u5c11\u6709\u673a\u4f1a\u89c1\u5230\u5f20\u606a\uff0c\u201c\u6c5f\u5357\u4e00\u5b9a\u8981\u5b66\u4e60\u5f90\u4e66\u8bb0\u5728\u4e1c\u6d77\u65f6\u7684\u7ecf\u9a8c\uff0c\u5929\u7136\u6797\u4fdd\u62a4\u5de5\u4f5c\u8981\u7acb\u5373\u5f00\u5c55\u8d77\u6765\uff0c\u5df2\u7ecf\u7ed9\u780d\u4f10\u5149\u7684\u5c71\u5730\u3001\u6797\u5730\uff0c\u5c31\u8981\u79cd\u690d\u5de5\u4e1a\u539f\u6599\u6797\u6765\u6ee1\u8db3\u7701\u5185\u7ecf\u6d4e\u53d1\u5c55\u7684\u9700\u8981\uff0c\u800c\u8f6e\u4f10\u5236\u5ea6\u4e00\u5b9a\u8981\u4e25\u683c\u7684\u8d2f\u5f7b\u4e0b\u53bb\u2014\u2014\u5168\u7701\u591a\u5e74\u6765\u5ffd\u89c6\u6c34\u571f\u4fdd\u6301\u7684\u5de5\u4f5c\uff0c\u81f4\u4f7f\u996e\u9a6c\u6cb3\u6cb3\u9053\u9010\u5e74\u6de4\u9ad8\uff0c\u8fd9\u662f\u996e\u9a6c\u6cb3\u6cc4\u6d2a\u6d41\u91cf\u5927\u5e45\u51cf\u5e45\u7684\u76f4\u63a5\u539f\u56e0\u3002\u9000\u7530\u8fd8\u6e56\u7684\u5de5\u4f5c\u4e5f\u8981\u7acb\u5373\u5f00\u5c55\u8d77\u6765\uff0c\u672c\u6765\u91d1\u5c71\u6e56\u662f\u53ef\u4ee5\u5bb9\u7eb3\u66f4\u591a\u7684\u6d2a\u6c34\u7684\uff0c\u8fd9\u91cc\u6709\u4e00\u70b9\uff0c\u6211\u8fd8\u8981\u4e0e\u5f20\u606a\u6c9f\u901a\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u672c\u4e0d\u4f1a\u76f4\u63a5\u8fc7\u95ee\u8c08\u5224\u7684\u5de5\u4f5c\uff0c\u4f46\u662f\u6881\u4f1f\u6cd5\u63d0\u8d77\u6765\uff0c\u4ed6\u5012\u4e0d\u80fd\u88c5\u804b\u4f5c\u54d1\uff0c\u6c5f\u5357\u7701\u7684\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u8981\u987a\u5229\u5f00\u5c55\u8d77\u6765\uff0c\u9526\u6e56\u8981\u5728\u6c5f\u5357\u7701\u624e\u6839\uff0c\u501f\u5f90\u5b66\u5e73\u6253\u538b\u6881\u4f1f\u6cd5\u7edd\u5bf9\u662f\u611a\u4e0d\u53ef\u53ca\u7684\u884c\u4e3a\uff0c\u800c\u662f\u8981\u5c3d\u53ef\u80fd\u4fc3\u8fdb\u4ed6\u4e0e\u5f90\u5b66\u5e73\u4e4b\u95f4\u7684\u901a\u529b\u5408\u4f5c\uff0c\u201c\u6881\u7701\u957f\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5429\u5490\u6211\u4e00\u58f0\u5c31\u662f\u4e86\u3002\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u4e5f\u662f\u5916\u6765\u6237\uff0c\u4e4b\u524d\u4e5f\u53d7\u5230\u5730\u65b9\u52bf\u529b\u7684\u6392\u65a5\uff0c\u53ea\u6709\u4ed6\u4e0e\u5f90\u5b66\u5e73\u901a\u529b\u5408\u4f5c\uff0c\u624d\u80fd\u8ba9\u6c5f\u5357\u7701\u7684\u5c40\u9762\u7834\u800c\u540e\u7acb\u3002</p><p>\u3000\u3000\u201c\u6668\u66e6\u7eb8\u4e1a\u4e4b\u524d\u4e5f\u6709\u8ba1\u5212\u4e0a\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\uff0c\u5728\u91d1\u5c71\u6e56\u4e1c\u5cb8\u6cb3\u6ee9\u6316\u6e20\u6392\u6c34\u5708\u4e86\u8fd1\u4e24\u4e07\u4ea9\u7684\u6797\u5730\u79cd\u690d\u539f\u6599\u6797\uff0c\u201d\u6881\u4f1f\u6cd5\u8bf4\u9053\uff0c\u201c\u8fd9\u6b21\u641e\u9000\u6797\u8fd8\u6e56\u3001\u9000\u7530\u8fd8\u6e56\uff0c\u5c06\u8fd9\u4e24\u4e07\u4ea9\u6797\u5730\u4e5f\u89c4\u5212\u5728\u5185\u2014\u2014\u4f60\u4e5f\u77e5\u9053\uff0c\u4e4b\u524d\u7684\u8c08\u5224\u4e5f\u4e0d\u53ef\u80fd\u672a\u535c\u5148\u77e5\u7684\u5c06\u8fd9\u4e2a\u56e0\u7d20\u8003\u8651\u5728\u5185\u2026\u2026\u201d</p><p>\u3000\u3000\u4e24\u4e07\u4ea9\u539f\u6599\u6797\u8d44\u4ea7\u4e0e\u5c06\u6765\u9000\u6797\u8fd8\u6e56\u65f6\u7701\u91cc\u53ef\u80fd\u7ed9\u51fa\u7684\u8865\u507f\u6b3e\u662f\u6709\u5f88\u5927\u533a\u522b\u7684\uff0c\u901a\u5e38\u7684\u505a\u6cd5\u5c31\u662f\u5c06\u4e24\u4e07\u4ea9\u539f\u6599\u6797\u8d44\u4ea7\u76f4\u63a5\u4ece\u8fd9\u6b21\u7684\u5e76\u8d2d\u65b9\u6848\u4e2d\u6263\u9664\u6389\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u8c08\u5224\u52bf\u5fc5\u56e0\u4e3a\u8fd9\u6a2a\u751f\u51fa\u6765\u7684\u679d\u8282\u800c\u7ee7\u7eed\u62d6\u5ef6\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u660e\u767d\u6881\u4f1f\u6cd5\u7684\u5fc3\u601d\uff0c\u7701\u8d22\u653f\u6349\u895f\u89c1\u8098\uff0c\u53ea\u6015\u6881\u4f1f\u6cd5\u5fc3\u91cc\u65e9\u5c06\u8ba1\u7b97\u597d\u5982\u4f55\u53bb\u82b1\u9526\u6e56\u7684\u8fd9\u7b14\u6536\u8d2d\u6b3e\u4e86\u5427\uff1f\u6881\u4f1f\u6cd5\u80af\u5b9a\u4e0d\u60f3\u770b\u5230\u8c08\u5224\u7ee7\u7eed\u62d6\u5ef6\u4e0b\u53bb\u624d\u76f4\u63a5\u627e\u4ed6\u6765\u8c08\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u60f3\u8c08\u5224\u65e0\u9650\u671f\u7684\u62d6\u5ef6\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u5165\u79cb\u540e\u5c31\u8fdb\u4e00\u6b65\u6076\u5316\uff0c\u4f46\u662f\u7b49\u4e0d\u5230\u4e5d\u4e5d\u5e74\u4e2d\uff0c\u7ecf\u6d4e\u5f62\u52bf\u5c31\u4f1a\u597d\u8f6c\uff0c\u56fd\u9645\u7eb8\u4e1a\u5de8\u5934\u6700\u8fdf\u4e0d\u4f1a\u62d6\u8fc7\u4e5d\u4e5d\u5e74\u5e95\u5c31\u4f1a\u5927\u4e3e\u8fdb\u519b\u56fd\u5185\uff0c\u7559\u7ed9\u65b0\u5149\u7eb8\u4e1a\u7684\u65f6\u95f4\u4e5f\u4e0d\u7b97\u5bbd\u88d5\uff0c\u5f20\u606a\u4e0d\u6653\u5f97\u7ed9\u5468\u6e38\u4e00\u5e74\u7684\u65f6\u95f4\u80fd\u5426\u5c06\u91d1\u5c71\u8fd9\u8fb9\u7684\u7eb8\u5382\u751f\u4ea7\u7406\u987a\u8fc7\u6765\uff1b\u8981\u80fd\u7406\u987a\u8fc7\u6765\uff0c\u5c4a\u65f6\uff0c\u9526\u6e56\u7684\u9020\u7eb8\u4e1a\u52a1\u5728\u534e\u4e1c\u5e02\u573a\u7684\u5730\u4f4d\u5c31\u4e3e\u8db3\u8f7b\u91cd\u4e0d\u5bb9\u5ffd\u89c6\uff0c\u4e5f\u5c31\u6709\u5b9e\u529b\u4e0e\u8fdb\u5165\u4e2d\u56fd\u5e02\u573a\u9020\u7eb8\u5de8\u5934\u6b63\u9762\u7ade\u4e89\u3002</p><p>\u3000\u3000\u5f20\u606a\u7a0d\u4f5c\u6c89\u541f\uff0c\u8bf4\u9053\uff1a\u201c\u5bf9\u7701\u91cc\u7684\u5de5\u4f5c\uff0c\u6211\u662f\u65e0\u9650\u5236\u652f\u6301\u7684\u2014\u2014\u90a3\u8fd9\u6837\u597d\u4e86\uff0c\u8fd9\u4e2a\u56e0\u7d20\u5c31\u6682\u65f6\u9694\u7f6e\u4e0d\u8bae\uff0c\u4e0d\u8981\u5f71\u54cd\u5230\u73b0\u6709\u7684\u8c08\u5224\u8fdb\u7a0b\u2026\u2026\u6211\u60f3\u6211\u4e0b\u6b21\u5230\u91d1\u5c71\u6765\uff0c\u5c31\u53ef\u80fd\u6b63\u5f0f\u7b7e\u7ea6\u4e86\u3002\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u8fd8\u4ee5\u4e3a\u5f20\u606a\u8981\u63d0\u989d\u5916\u7684\u6761\u4ef6\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u8fd9\u4e48\u5e72\u8106\u5229\u843d\u7684\u5c31\u7b54\u5e94\u4e0b\u6765\uff0c\u5fc3\u60f3\u4e2d\u592e\u6d3e\u5f90\u5b66\u5e73\u5230\u6c5f\u5357\u6765\u4e3b\u6301\u5de5\u4f5c\uff0c\u8003\u8651\u5230\u5f90\u5b66\u5e73\u4e0e\u9526\u6e56\u7684\u6e0a\u6e90\uff0c\u679c\u771f\u662f\u6b63\u786e\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5468\u747e\u745c\u4e00\u5bb6\u8fd8\u6ca1\u6709\u4ece\u4e34\u5d16\u6e56\u5317\u5cb8\u7684\u5e02\u59d4\u5927\u9662\u642c\u51fa\u53bb\uff0c\u5468\u747e\u745c\u867d\u8bf4\u8f9e\u53bb\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u7684\u804c\u52a1\uff0c\u4f46\u6682\u65f6\u8fd8\u4fdd\u7559\u7740\u7701\u59d4\u59d4\u5458\u3001\u5e38\u59d4\u7684\u5934\u8854\uff0c\u8fd9\u65f6\u5019\u4e0d\u4f1a\u6709\u4eba\u6025\u7740\u5c06\u5979\u4e00\u5bb6\u4eba\u8d76\u51fa\u53bb\u3002\u4e0d\u8fc7\u4e3a\u671f\u4e5f\u4e0d\u8fdc\u4e86\uff0c\u7701\u59d4\u5168\u59d4\u4f1a\u8bae\u5b9a\u5728\u4e5d\u6708\u4e0b\u65ec\u53ec\u5f00\uff0c\u5f90\u5b66\u5e73\u518d\u6709\u5bb9\u4eba\u7684\u5ea6\u91cf\uff0c\u5230\u65f6\u4e5f\u4f1a\u5c06\u5979\u8e22\u51fa\u6c5f\u5357\u53bb\uff0c\u8fd4\u56de\u4e1c\u6d77\u7684\u8def\u5df2\u7ecf\u7ed9\u5835\u7edd\u4e86\uff0c\u8fd9\u5e74\u5934\u53c8\u6839\u672c\u4e0d\u4f1a\u6709\u4eba\u7ad9\u51fa\u6765\u8bf4\u53e5\u8bdd\uff0c\u5373\u4f7f\u53bb\u5987\u8054\u3001\u603b\u5de5\u4f1a\u8fd9\u4e9b\u7684\u6e05\u6c34\u8859\u95e8\u5f53\u4e2a\u95f2\u804c\u4e5f\u4e0d\u53ef\u80fd\u4e86\u3002</p><p>\u3000\u3000\u4e34\u5d16\u6e56\u7554\uff0c\u5468\u747e\u745c\u4e0e\u8c22\u6c49\u660e\u665a\u996d\u540e\u51fa\u6765\u6563\u6b65\uff0c\u9047\u5230\u7684\u4eba\u90fd\u88c5\u6ca1\u6709\u770b\u5230\u4ed6\u4eec\u4f4e\u5934\u907f\u5f00\uff0c\u5468\u747e\u745c\u770b\u4e86\u5fc3\u91cc\u607c\u6068\uff0c\u5979\u4ece\u6ca1\u6709\u60f3\u8fc7\u8981\u5fcd\u53d7\u8fd9\u6837\u7684\u4e16\u6001\u708e\u51c9\u3002</p><p>\u3000\u3000\u201c\u5468\u5bb6\u7b97\u662f\u8981\u5f7b\u5e95\u6c89\u5bc2\u4e86\uff0c\u59d4\u5c48\u4f60\u8fd9\u4e9b\u5e74\uff0c\u201d\u5468\u747e\u745c\u770b\u7740\u4e08\u592b\u82b1\u767d\u7684\u4e24\u9b13\uff0c\u201c\u4f60\u73b0\u5728\u53ef\u4ee5\u51fa\u6765\u505a\u4e9b\u4e8b\u60c5\u4e86\uff0c\u8fd9\u6b21\u6362\u6211\u7ed9\u4f60\u5f53\u53c2\u8c0b\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u660e\u770b\u7740\u5782\u67f3\u4e0b\u7684\u6e56\u6c34\uff0c\u5728\u591c\u8272\u4e0b\uff0c\u6e56\u6c34\u6cdb\u7740\u9ed1\u73cd\u73e0\u4f3c\u7684\u5149\u6cfd\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u5341\u5e74\u6cb3\u4e1c\u3001\u4e09\u5341\u5e74\u6cb3\u897f\uff0c\u6211\u4eec\u8981\u8010\u7740\u6027\u5b50\u518d\u8707\u4f0f\u56db\u4e94\u5e74\uff0c\u5373\u4f7f\u9526\u6e56\u58f0\u52bf\u518d\u76db\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5c06\u6574\u4e2a\u5929\u90fd\u53ea\u624b\u906e\u4f4f\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5efa\u90ba\u5e02\u5e9c\u9752\u8def\u5546\u4e1a\u8857\uff0c\u6d77\u6cf0\u7535\u5668\u7684\u603b\u90e8\u5c31\u77d7\u7acb\u7684\u5e9c\u9752\u8def\u9876\u7aef\u3002</p><p>\u3000\u3000\u7f24\u7eb7\u7684\u8def\u706f\u4e0e\u502a\u8679\u706f\u5149\u7130\u4e4b\u4e0a\uff0c\u591c\u8272\u6c79\u6d8c\uff0c\u8c22\u6c49\u9756\u51ed\u7a97\u671b\u7740\u957f\u8857\u7684\u591c\u666f\u3002</p><p>\u3000\u3000\u201c\u603b\u4e0d\u80fd\u5c31\u8fd9\u4e48\u50f5\u6301\u4e0b\u53bb\uff0c\u4e09\u53d4\u4ed6\u7684\u610f\u56fe\u4e5f\u5f88\u660e\u663e\uff0c\u4ed6\u81ea\u8ba4\u4e3a\u5b9d\u5200\u672a\u8001\uff0c\u201d\u8c22\u610f\u5c06\u4e24\u53ea\u811a\u968f\u610f\u7684\u9ad8\u8df7\u5728\u529e\u516c\u684c\u4e0a\uff0c\u201c\u4f60\u4e5f\u4e0d\u80fd\u603b\u72b9\u8c6b\u4e0d\u51b3\uff0c\u8981\u4e0d\u6211\u5077\u4e2a\u61d2\u6b47\u51e0\u5e74\uff0c\u5c06\u6d77\u6cf0\u7684\u644a\u5b50\u4e22\u7ed9\u4e09\u53d4\u6765\u7ba1\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8981\u8bf4\u8fd9\u79cd\u8bdd\uff0c\u201d\u8c22\u6c49\u9756\u538b\u4f4e\u7740\u58f0\u97f3\uff0c\u8f6c\u8fc7\u5934\u6765\uff0c\u770b\u7740\u5927\u513f\u5b50\u8c22\u610f\uff0c\u201c\u6211\u662f\u4f60\u8001\u5b50\uff0c\u4f60\u5fc3\u91cc\u6253\u4ec0\u4e48\u4e3b\u610f\uff0c\u6211\u80fd\u4e0d\u6e05\u695a\uff1f\u5206\u5bb6\uff01\u8fd9\u5bb6\u662f\u8fd9\u4e48\u597d\u5206\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u8111\u5b50\u91cc\u6ca1\u6709\u52a8\u8fc7\u8fd9\u4e2a\u5ff5\u5934\u5462\uff1f\u201d\u8c22\u610f\u8138\u4e0a\u4f9d\u65e7\u6302\u7740\u6ee1\u4e0d\u5728\u4e4e\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u8fd8\u6709\u522b\u7684\u9009\u62e9\u6446\u5728\u6211\u4eec\u9762\u524d\u5417\uff1f\u91d1\u5c71\u7684\u5de5\u5382\uff0c\u4fdd\u9669\u516c\u53f8\u7406\u8d54\u6709\u9650\uff0c\u4fdd\u5b88\u4f30\u8ba1\u8fd8\u8981\u6295\u516d\u4e03\u4e2a\u4ebf\u8fdb\u53bb\u624d\u80fd\u6062\u590d\u751f\u4ea7\uff0c\u52a0\u4e0a\u7269\u6599\u635f\u5931\uff0c\u90a3\u5df2\u7ecf\u8d85\u8fc7\u6211\u4eec\u80fd\u591f\u627f\u53d7\u7684\u8303\u56f4\u2014\u2014\u4e1c\u5357\u4e9a\u7684\u7ecf\u6d4e\u6709\u8fdb\u4e00\u6b65\u6076\u5316\u7684\u8d8b\u52bf\uff0c\u5609\u4fe1\u7535\u5b50\u5728\u5e7f\u5dde\u5de5\u5382\u4f20\u7edf\u7684\u7535\u5b50\u4ea7\u54c1\u4ee3\u5de5\u4e1a\u52a1\u4e00\u843d\u5343\u4e08\uff0c\u6ca1\u6709\u4e24\u4e09\u5e74\u7684\u65f6\u95f4\u6839\u672c\u65e0\u6cd5\u6062\u590d\u5143\u6c14\uff0c\u8fd9\u6b21\u7684\u6d2a\u6c34\u53c8\u8ba9\u5609\u4fe1\u7535\u5b50\u7684\u4e1a\u52a1\u8f6c\u578b\u610f\u5916\u5931\u8d25\uff0c\u53c8\u8499\u53d7\u5de8\u5927\u635f\u5931\uff0c\u5609\u4fe1\u7535\u5b50\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u7684\u8868\u73b0\u53ef\u4ee5\u8bf4\u662f\u60e8\u4e0d\u5fcd\u7779\uff0c\u6211\u4eec\u8fd9\u65f6\u5019\u8fd8\u80fd\u4f9d\u9760\u845b\u5bb6\u5417\uff1f\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u5fae\u5fae\u4e00\u53f9\uff0c\u6b64\u65f6\u7684\u845b\u5bb6\u53ef\u4ee5\u8bf4\u662f\u81ea\u8eab\u96be\u4fdd\u4e86\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u845b\u5bb6\u5df2\u7ecf\u6ca1\u6709\u845b\u8001\u7237\u5b50\u5728\u4e16\u65f6\u7684\u5149\u666f\u4e86\uff0c\u9999\u6e2f\u4eba\u60ef\u4e86\u8d22\u5bcc\u6392\u540d\u6765\u79f0\u547c\u6e2f\u5c9b\u7684\u5bcc\u8c6a\uff0c\u5728\u5168\u7403\u534e\u5546\u805a\u96c6\u4e4b\u5730\uff0c\u845b\u8001\u7237\u5b50\u7ed9\u4eba\u5c0a\u79f0\u4e3a\u56db\u53d4\u3002\u845b\u8001\u7237\u5b50\u53bb\u4e16\uff0c\u845b\u660e\u4fe1\u6b32\u72ec\u88c1\u638c\u6743\uff0c\u81f4\u4f7f\u845b\u5bb6\u5206\u5d29\u79bb\u6790\uff0c\u5c31\u4f7f\u5f97\u845b\u5bb6\u5143\u6c14\u5927\u4f24\u3002\u53c8\u65f6\u9022\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\uff0c\u4e1a\u52a1\u96c6\u4e2d\u5728\u6e2f\u5c9b\u5730\u533a\u7684\u5609\u4fe1\u5730\u4ea7\u968f\u7740\u9999\u6e2f\u697c\u5e02\u4e5d\u4e03\u5e74\u79cb\u540e\u7684\u5d29\u6e83\u800c\u906d\u53d7\u91cd\u632b\uff0c\u4f9d\u8d56\u4e1c\u5357\u4e9a\u7535\u5b50\u4ee3\u5de5\u5e02\u573a\u7684\u5609\u4fe1\u7535\u5b50\u53d7\u521b\u7a0b\u5ea6\u4e0d\u6bd4\u5609\u4fe1\u5730\u4ea7\u7a0d\u5f31\u3002</p><p>\u3000\u3000\u6295\u8d44\u91d1\u5c71\u7535\u5b50\u56ed\u88ab\u89c6\u4e3a\u845b\u5bb6\u632f\u5174\u7684\u4e3e\u63aa\uff0c\u5609\u4fe1\u7535\u5b50\u4e0d\u4ec5\u53ef\u4ee5\u7c4d\u673a\u5c06\u4ee3\u5de5\u4e1a\u52a1\u6954\u5165\u5185\u5730\u5e02\u573a\uff0c\u5609\u4fe1\u5730\u4ea7\u4e5f\u540c\u65f6\u53ef\u4ee5\u7c4d\u673a\u5c06\u4e1a\u52a1\u62d3\u5c55\u5230\u5185\u5730\u6765\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\u3001\u5929\u707e\u4eba\u7978\uff0c\u53ef\u4ee5\u8bf4\u662f\u7ed9\u845b\u5bb6\u81f4\u547d\u7684\u4e00\u51fb\uff0c\u5373\u4f7f\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u5728\u516b\u6708\u540e\u4e0d\u518d\u5ea6\u6076\u5316\uff0c\u5609\u4fe1\u7535\u5b50\u3001\u5609\u4fe1\u5730\u4ea7\u7684\u80a1\u4ef7\u4e5f\u96be\u9003\u4e00\u843d\u5343\u4e08\u7684\u547d\u8fd0\uff0c\u53ea\u4e0d\u8fc7\u53d7\u7ecf\u6d4e\u5f62\u52bf\u6076\u5316\u7684\u5f71\u54cd\uff0c\u60e8\u6de1\u4e4b\u7a0b\u5ea6\u66f4\u6df1\u7f62\u4e86\u3002</p><p>\u3000\u3000\u6700\u4e3a\u5173\u952e\u7684\uff0c\u9526\u6e56\u4e00\u76f4\u90fd\u864e\u89c6\u7708\u7708\u7684\u8e1e\u5b88\u5728\u4e00\u65c1\uff0c\u8fd9\u624d\u662f\u6700\u4e0d\u5bb9\u5ffd\u89c6\u7684\u56e0\u7d20\u3002</p><p>\u3000\u3000\u8c22\u610f\u7ad9\u8d77\u6765\uff0c\u4f3c\u4e4e\u8981\u4fc3\u4f7f\u4ed6\u7236\u4eb2\u6700\u540e\u4e0b\u51b3\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u91d1\u5c71\u7684\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u5df2\u7ecf\u5f00\u59cb\u4e86\uff0c\u4f46\u662f\u51e0\u4e4e\u6240\u6709\u7684\u94f6\u884c\u90fd\u8eb2\u7740\u6211\u4eec\uff0c\u91d1\u5c71\u90a3\u8fb9\u7684\u5de5\u5382\u60f3\u6062\u590d\u751f\u4ea7\u5374\u7b79\u4e0d\u5230\u4e00\u5206\u94b1\u2014\u2014\u518d\u65e0\u51b3\u65ad\uff0c\u7b49\u6d88\u606f\u4f20\u5f00\u6765\uff0c\u4f1a\u8fdb\u4e00\u6b65\u5f71\u54cd\u5230\u6d77\u6cf0\u4e0e\u6b63\u6cf0\u7684\u4e1a\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u6c49\u9756\u76b1\u7740\u7709\u5934\u4e0d\u542d\u58f0\u3002</p><p>\u3000\u3000\u201c\u4e1c\u5927\u4e5f\u8981\u5f00\u5b66\u4e86\uff0c\u6211\u60f3\u90a3\u5bb6\u4f19\u8fd8\u4f1a\u7ee7\u7eed\u51fa\u73b0\u5728\u4e1c\u5927\u6821\u56ed\u91cc\uff0c\u8981\u4e0d\u6211\u5148\u53bb\u8ddf\u4ed6\u89c1\u4e00\u9762\uff1f\u201d\u8c22\u610f\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u884c\u2026\u2026\u201d\u8c22\u6c49\u9756\u65e0\u5948\u7684\u7b54\u5e94\u9053\u3002</p></article>"}}}]);
