(window.webpackJsonp=window.webpackJsonp||[]).push([[888],{911:function(p){p.exports={chapterTitle:null,title:"\u7b2c885\u7ae0 \u4e25\u6587\u4ecb\u7684\u6251\u6714\u8ff7\u79bb",content:"<article><h2>\u7b2c885\u7ae0 \u4e25\u6587\u4ecb\u7684\u6251\u6714\u8ff7\u79bb</h2><p>\u3000\u3000\u201c\u90a3\u5bb6\u4f19\u662f\u8c01\uff0c\u53e3\u6c14\u597d\u50cf\u4e0d\u5584\u554a\uff1f\u201d\u675c\u98de\u770b\u7740\u4e25\u6587\u4ecb\u63ba\u7740\u6797\u96ea\u79bb\u5f00\u5b66\u5e9c\u5df7\uff0c\u4ed6\u8d70\u8fc7\u6765\u53ea\u542c\u5230\u4e25\u6587\u4ecb\u5bf9\u5f20\u606a\u8bf4\u6700\u540e\u4e00\u53e5\u8bdd\uff0c\u53c8\u4e0d\u5c51\u7684\u8bf4\u9053\uff0c\u201c\u4ed6\u5f97\u9700\u8981\u591a\u5927\u7684\u9762\u5b50\u624d\u80fd\u8ba9\u4f60\u8bb0\u4f4f\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u5b66\u5e9c\u5df7\u6216\u8bb8\u662f\u4e0d\u671f\u800c\u9047\uff0c\u4f46\u4ed6\u6765\u5efa\u90ba\u5374\u662f\u6709\u5907\u800c\u6765\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u8bf4\u8fd9\u6837\u7684\u8bdd\u603b\u662f\u8981\u6709\u4e9b\u5e95\u6c14\u7684\uff0c\u4e25\u6587\u4ecb\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\uff0c\u4f60\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u60f3\u4e86\u51e0\u79d2\u949f\uff0c\u5f88\u964c\u751f\u7684\u4eba\u540d\u4e0e\u516c\u53f8\uff0c\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1b\u201c\u4ec0\u4e48\u6765\u5934\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e25\u6587\u4ecb\u662f\u6d59\u4e1c\u4eba\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u6700\u521d\u662f\u5728\u6d77\u5357\u7701\u9760\u7092\u5730\u76ae\u3001\u7092\u697c\u6398\u4e86\u7b2c\u4e00\u6876\u91d1\uff0c\u6d77\u5357\u697c\u5e02\u5d29\u76d8\u524d\u5915\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u5168\u8eab\u800c\u9000\uff0c\u5176\u540e\u5168\u5e08\u8f6c\u8fdb\u4eac\u6d25\u4e24\u5730\u53d1\u5c55\u623f\u5730\u4ea7\u53ca\u5e02\u653f\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\uff0c\u4e5d\u4e94\u5e74\uff0c\u56fd\u52a1\u9662\u6279\u51c6\u7ec4\u5efa\u4e24\u5bb6\u4ee5\u975e\u516c\u6709\u5236\u4e3a\u4e3b\u4f53\u7684\u5168\u56fd\u6027\u80a1\u4efd\u5236\u5546\u4e1a\u94f6\u884c\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u53c8\u662f\u4fe1\u901a\u94f6\u884c\u516d\u5927\u53d1\u8d77\u4eba\u4e4b\u4e00\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e25\u6587\u4ecb\u5934\u4e0a\u8fd8\u9876\u7740\u5168\u56fd\u653f\u534f\u59d4\u5458\u7684\u540d\u8854\uff0c\u4f60\u89c9\u5f97\u8fd9\u6765\u5934\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u5f20\u4e86\u5f20\u5634\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u679c\u771f\u662f\u4e2a\u725b\u53c9\u4eba\u7269\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u521d\u6d77\u5357\u5efa\u8bbe\u7279\u533a\uff0c\u6d77\u5357\u697c\u5e02\u75af\u72c2\u4e3a\u6709\u53f2\u4ee5\u6765\u6240\u7f55\u89c1\u3002\u4e5d\u5341\u5e74\u4ee3\u6700\u521d\u7684\u4e09\u5e74\uff0c\u6d77\u5357\u697c\u5e02\u5747\u4ef7\u6fc0\u589e\u56db\u500d\uff08\u6b64\u5904\u7684\u56db\u500d\u8fd8\u662f\u653f\u5e9c\u516c\u5f00\u6570\u636e\uff09\uff0c\u5982\u6b64\u76db\u51b5\u5438\u5f15\u5404\u7701\u5e02\u653f\u5e9c\u90fd\u7ec4\u56e2\u8d76\u8d74\u6d77\u5357\u6398\u91d1\uff0c\u5f53\u65f6\u4eba\u5f62\u5bb9\u4e3a\u201c\u5341\u4e07\u5927\u519b\u4e0b\u6d77\u5357\u3001\u5404\u5927\u8d22\u56e2\u62a2\u5730\u76d8\u201d\uff0c\u9ad8\u5cf0\u671f\u6d77\u5357\u623f\u5730\u4ea7\u4f01\u4e1a\u8d85\u8fc7\u4e94\u5343\u591a\u5bb6\uff0c\u8bb8\u591a\u4e2d\u5c0f\u6295\u8d44\u4eba\u4e5f\u662f\u4e00\u4e0d\u7559\u795e\u5c31\u6210\u4e86\u767e\u4e07\u5bcc\u7fc1\uff0c\u7136\u800c\u4e5d\u4e8c\u5e74\u4e0b\u534a\u5e74\u4e2d\u592e\u7a81\u7136\u5bf9\u6d77\u5357\u6536\u7f29\u94f6\u6839\uff0c\u4e00\u4e0b\u5b50\u5c06\u5e7b\u68a6\u51fb\u6e83\uff0c\u6d77\u5357\u697c\u5e02\u9aa4\u7136\u5d29\u76d8\u8dcc\u81f3\u51b0\u5bd2\u8c37\u5e95\uff0c\u7ed9\u603b\u4eba\u53e3\u624d\u4e03\u767e\u4e07\u7684\u6d77\u5357\u7701\u7559\u4e0b\u7a7a\u7f6e\u623f\u52a0\u70c2\u5c3e\u697c\u8d85\u8fc7\u4e24\u5343\u4e07\u5e73\u65b9\u7c73\uff0c\u53e6\u5916\u8fd8\u67092\u4ebf4\u5343\u4e07\u5e73\u65b9\u7c73\u7684\u5efa\u8bbe\u7528\u5730\u8352\u829c\uff0c\u4f7f\u5f97\u6d77\u5357\u53d1\u5c55\u94f6\u884c\u3001\u6d77\u5357\u7701\u5e02\u591a\u5bb6\u4fe1\u6258\u6295\u8d44\u516c\u53f8\u76f8\u7ee7\u7834\u4ea7\uff0c\u8bb8\u591a\u8d76\u8d74\u6d77\u5357\u6295\u8d44\u7684\u7701\u7ea7\u623f\u5730\u4ea7\u5546\u4e5f\u672a\u80fd\u53ca\u65f6\u8131\u9003\u800c\u6df1\u9677\u5176\u4e2d\uff0c\u80fd\u6390\u7740\u70b9\u5728\u697c\u5e02\u5d29\u76d8\u524d\u5915\u5168\u8eab\u800c\u9000\u7684\u9664\u4e86\u76ee\u5149\u654f\u9510\u3001\u5934\u8111\u6e05\u9192\u3001\u7ecf\u8425\u98ce\u683c\u5927\u80c6\u8c6a\u8fc8\u7684\u4f01\u4e1a\uff0c\u5c31\u53ea\u5269\u4e0b\u90a3\u4e9b\u80cc\u666f\u6df1\u539a\u3001\u4fe1\u606f\u901a\u7545\u7684\u80fd\u4eba\u4e86\u3002</p><p>\u3000\u3000\u4fe1\u901a\u94f6\u884c\u4f5c\u4e3a\u5168\u56fd\u4ec5\u6709\u7684\u4e24\u5bb6\u975e\u516c\u79c1\u5236\u5546\u4e1a\u94f6\u884c\uff0c\u53c8\u662f\u89c4\u6a21\u6700\u5927\u7684\u4e00\u5bb6\uff0c\u867d\u8bf4\u662f\u94f6\u884c\u4e1a\u8fdb\u884c\u975e\u516c\u6709\u5236\u8fd0\u8425\u7684\u8bd5\u70b9\uff0c\u4f46\u662f\u5168\u56fd\u90a3\u4e48\u591a\u5bb6\u6c11\u8425\u4f01\u4e1a\uff0c\u80fd\u8fdb\u5165\u4e2d\u592e\u89c6\u91ce\u6210\u4e3a\u53d1\u8d77\u4eba\u4f01\u4e1a\u7684\u9664\u4e86\u8d44\u91d1\u5b9e\u529b\u96c4\u539a\u4e4b\u5916\uff0c\u8fd8\u662f\u9700\u8981\u4e00\u4e9b\u4eba\u8109\u5173\u7cfb\u7684\u2014\u2014\u5c31\u5982\u540c\u53bb\u5e74\u9526\u6e56\u4ece\u539f\u90ae\u7535\u90e8\u624b\u91cc\u786c\u62a2\u4e86\u4e00\u5f20\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u7edd\u4e0d\u662f\u8868\u9762\u4e0a\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u3000\u3000\u201c\u7684\u786e\u662f\u4e2a\u725b\u53c9\u4eba\u7269\u2026\u2026\u201d\u5f20\u606a\u8f7b\u53f9\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u53ea\u662f\u4e4b\u524d\u6ca1\u6709\u542c\u8bf4\u8fc7\u3002\u201d</p><p>\u3000\u3000\u201c\u4eba\u5bb6\u4e5f\u559c\u6b22\u73a9\u4f4e\u8c03\uff0c\u201d\u5f20\u606a\u7ad9\u4e86\u8d77\u6765\uff0c\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u8981\u4e0d\u662f\u4fe1\u901a\u94f6\u884c\u516c\u5f00\u4e0a\u5e02\uff0c\u4f5c\u4e3a\u4fe1\u901a\u94f6\u884c\u7b2c\u4e00\u5927\u80a1\u4e1c\u7684\u5b8f\u4fe1\u5efa\u8bbe\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\u80cc\u540e\u7684\u4e25\u6587\u4ecb\u4e5f\u4e0d\u4f1a\u4e3a\u516c\u4f17\u6240\u719f\u77e5\uff0c\u5f20\u606a\u4e4b\u524d\u4e5f\u7684\u786e\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u8fd9\u4e48\u4e00\u53f7\u4eba\u7269\u8981\u4ecb\u5165\u5230\u5efa\u90ba\u6765\uff0c\u201c\u4e1c\u6d77\u4e5d\u5341\u5e74\u4ee3\u4e2d\u524d\u671f\u7684\u6c11\u8425\u7ecf\u6d4e\u4e0d\u7b97\u53d1\u8fbe\uff0c\u4e5f\u51fa\u4e86\u4e00\u5bb6\u6b63\u6cf0\u2014\u2014\u8fd9\u5168\u56fd\u8fd9\u4e48\u5927\u7684\u5730\u76d8\uff0c\u6bd4\u6b63\u6cf0\u5f3a\u76db\u7684\u6c11\u8425\u8fd8\u6709\u8bb8\u591a\u5bb6\uff0c\u53ea\u4e0d\u8fc7\u6709\u9ad8\u8c03\u5f97\u79bb\u8c31\u7684\uff0c\u5c31\u50cf\u725f\u5176\u4e2d\uff0c\u8fd9\u75af\u5b50\u751a\u81f3\u60f3\u7740\u5230\u9526\u6e56\u6765\u62c9\u8d44\u91d1\u53bb\u5b9e\u73b0\u4ed6\u7684\u5c06\u559c\u9a6c\u62c9\u96c5\u5c71\u70b8\u5f00\u7f3a\u53e3\u7684\u8ba1\u5212\u2014\u2014\u4e5f\u6709\u4f4e\u8c03\u5f97\u534a\u70b9\u90fd\u4e0d\u60f3\u5728\u516c\u4f17\u9762\u524d\u9732\u75d5\u8ff9\u7684\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u534a\u5e74\u6765\uff0c\u5efa\u90ba\u4e5f\u53d1\u751f\u4e9b\u8e4a\u8df7\u4e8b\u8ba9\u4eba\u770b\u4e0d\u660e\u767d\uff0c\u5230\u8fd9\u65f6\u5374\u662f\u63ed\u5f00\u9762\u7eb1\u4e86\uff0c\u6216\u8bb8\u4e5f\u662f\u590f\u5b63\u7a81\u5982\u5176\u6765\u7684\u5927\u6d2a\u6c34\u62d6\u5ef6\u4e86\u4e00\u4e0b\u4e25\u6587\u4ecb\u7684\u6b65\u4f10\u5427\uff1f\u53ea\u4e0d\u8fc7\u6b63\u662f\u62d6\u5ef6\u8fc7\u590f\u5929\uff0c\u62d6\u5ef6\u5230\u9526\u6e56\u5546\u4e8b\u5728\u5f15\u5165\u5370\u5c3c\u534e\u4eba\u8d44\u672c\u4e2d\u53d1\u6325\u5de8\u5927\u7684\u4f5c\u7528\uff0c\u8096\u660e\u5efa\u7684\u6001\u5ea6\u5c31\u8f6f\u4e0b\u6765\u4e86\uff0c\u4e25\u6587\u4ecb\u96be\u9053\u4e0d\u662f\u5728\u8d70\u8096\u660e\u5efa\u7684\u8def\u7ebf\uff1f\u5f20\u606a\u5fc3\u91cc\u4e5f\u6709\u51e0\u5206\u7591\u60d1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u6276\u6797\u96ea\u4e0a\u8f66\u540e\uff0c\u624d\u900f\u7740\u8f66\u7a97\u5f80\u8857\u8fd9\u8fb9\u770b\u4e86\u4e00\u773c\uff0c\u79bb\u5f97\u7a0d\u8fdc\uff0c\u53ea\u770b\u89c1\u5f20\u606a\u4e0e\u675c\u98de\u9762\u76ee\u6a21\u7cca\u7684\u7ad9\u5728\u8857\u4e2d\u6bb5\u8bf4\u8bdd\uff0c\u770b\u4e0d\u6e05\u4ed6\u4eec\u8138\u4e0a\u7684\u8868\u73b0\uff0c\u5fc3\u60f3\u4ed6\u4eec\u6216\u8bb8\u5728\u731c\u7591\u81ea\u5df1\u7684\u8eab\u4efd\uff0c\u4e25\u6587\u4ecb\u5374\u4e0d\u77e5\u9053\u5f20\u606a\u8111\u5b50\u91cc\u7684\u6d88\u606f\u91cf\u4e0d\u80fd\u4ee5\u5e38\u60c5\u6765\u5ea6\u4e4b\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4ee5\u4ed6\u4ece\u4e1a\u8fd1\u5341\u5e74\u7684\u773c\u5149\u6765\u5ba1\u89c6\u5b66\u5e9c\u5df7\uff0c\u90fd\u8981\u79f0\u5f97\u4e0a\u7ecf\u5178\u4e4b\u4f5c\uff0c\u4e25\u6587\u4ecb\u5429\u5490\u53f8\u673a\u5f00\u8f66\uff0c\u5bf9\u6797\u96ea\u7b11\u7740\u8bf4\uff1a\u201c\u9ebb\u70e6\u6797\u5c0f\u59d0\u966a\u6211\u8fc7\u6765\u811a\u53c2\u89c2\u5b66\u5e9c\u5df7\uff0c\u5374\u8ba9\u6797\u5c0f\u59d0\u53d7\u4e86\u82e6\uff0c\u771f\u662f\u5341\u5206\u7684\u62b1\u6b49\uff0c\u6211\u73b0\u5728\u9001\u6797\u5c0f\u59d0\u53bb\u533b\u9662\uff0c\u6797\u5c0f\u59d0\u4ee5\u540e\u4e00\u5b9a\u8981\u7ed9\u6211\u4e00\u4e2a\u8865\u507f\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u6797\u96ea\u77a5\u4e86\u4e25\u6587\u4ecb\u4e00\u773c\uff0c\u7c89\u9762\u5fae\u7ea2\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e25\u603b\u8fd9\u4e48\u8bf4\uff0c\u4e00\u5b9a\u662f\u6015\u6211\u63d0\u51fa\u4e0d\u5408\u9002\u7684\u6761\u4ef6\uff0c\u811a\u8fd8\u6709\u75bc\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u4e25\u91cd\u5230\u8981\u53bb\u533b\u9662\u7684\u7a0b\u5ea6\uff0c\u4e25\u603b\u9001\u6211\u5230\u5bb6\u697c\u4e0b\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8f66\u5230\u661f\u6d77\u522b\u5885\uff0c\u4e25\u6587\u4ecb\u8ba9\u4fdd\u9556\u4e0e\u4ed6\u4e00\u8d77\u63ba\u6797\u96ea\u8fdb\u697c\uff0c\u6797\u96ea\u5012\u662f\u89c1\u591a\u4e86\u6068\u4e0d\u5f97\u5c06\u5979\u62b1\u8fdb\u697c\u7684\u7537\u4eba\uff0c\u4e25\u6587\u4ecb\u7684\u6e29\u6587\u5c14\u96c5\u8ba9\u5979\u6709\u4e9b\u770b\u4e0d\u900f\uff0c\u8fd9\u6837\u7684\u7537\u4eba\u592a\u5c11\u4e86\uff0c\u90a3\u4e2a\u5c0f\u5b50\u4e5f\u7b97\u4e00\u4e2a\u5427\uff1f</p><p>\u3000\u3000\u770b\u7740\u4e25\u6587\u4ecb\u63a9\u95e8\u800c\u53bb\uff0c\u6797\u96ea\u5c06\u5149\u6ed1\u96ea\u5ae9\u7684\u4e0b\u9894\u5fae\u5fae\u62ac\u8d77\u6765\uff0c\u5c06\u9ad8\u8ddf\u978b\u8131\u6389\uff0c\u8d64\u8db3\u8e29\u5230\u5730\u677f\u4e0a\u2014\u2014\u54ea\u6709\u534a\u70b9\u5403\u75db\u7684\u6837\u5b50\uff0c\u8d70\u5230\u7a97\u524d\uff0c\u9694\u7740\u4e00\u5c42\u7eb1\u5e18\u773c\u775b\u76ef\u7740\u4e25\u6587\u4ecb\u7684\u8f66\u79bb\u5f00\uff0c\u95ea\u7740\u9510\u5229\u7684\u5149\u8292\uff0c\u5c06\u638c\u5fc3\u91cc\u7684\u624b\u673a\u6253\u5f00\uff0c\u62d4\u901a\u80e1\u5b97\u5e86\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u4e25\u6587\u4ecb\u521a\u521a\u79bb\u5f00\uff0c\u771f\u662f\u8ba9\u4eba\u770b\u4e0d\u900f\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u8fd9\u6b21\u5230\u5efa\u90ba\u6765\u603b\u4e0d\u53ef\u80fd\u662f\u6e38\u5c71\u73a9\u6c34\u7684\uff0c\u201d\u80e1\u5b97\u5e86\u4ece\u624b\u673a\u91cc\u4f20\u51fa\u6765\u7684\u58f0\u97f3\u6c99\u54d1\uff0c\u201c\u4f60\u8ba4\u4e3a\u4ed6\u79bb\u5f00\u540e\u4f1a\u4e0d\u4f1a\u6025\u7740\u53bb\u89c1\u8096\u660e\u5efa\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u62c5\u5fc3\u8096\u660e\u5efa\u6447\u6446\u4e0d\u5b9a\uff0c\u6211\u770b\u4e0d\u7528\u62c5\u5fc3\u8fd9\u4e2a\uff0c\u6bd4\u8d77\u653f\u7ee9\u3001\u6c11\u5fc3\uff0c\u8fd9\u5e74\u5934\u5347\u5b98\u53d1\u8d22\uff0c\u8fd8\u8981\u4f9d\u8d56\u4e0a\u5934\u6709\u4eba\uff0c\u8096\u660e\u5efa\u53c8\u4e0d\u662f\u7cca\u6d82\u4eba\uff0c\u201d\u6797\u96ea\u770b\u7740\u8f66\u5c3e\u706f\u62d6\u51fa\u7684\u5149\u8ff9\u6d88\u5931\u5728\u591c\u7a7a\u4e0b\uff0c\u8fd9\u624d\u8f6c\u8fc7\u8eab\u6765\u4f9d\u7740\u7a97\u53f0\u8bf4\u8bdd\uff0c\u201c\u4e25\u6587\u4ecb\u5fc3\u91cc\u6e05\u695a\uff0c\u4ed6\u4eec\u4e0e\u9526\u6e56\u4e0d\u662f\u4e00\u8def\u7684\uff0c\u4ed6\u4eec\u8981\u8fdb\u5efa\u90ba\uff0c\u4e0d\u53ef\u80fd\u8ddf\u9526\u6e56\u548c\u548c\u6c14\u6c14\u7684\u5750\u4e0b\u6765\u5206\u5730\u76d8\uff0c\u6211\u966a\u4e25\u6587\u4ecb\u53bb\u5b66\u5e9c\u5df7\uff0c\u6070\u5de7\u9047\u4e0a\u90a3\u5c0f\u5b50\uff0c\u4e25\u6587\u4ecb\u8bdd\u8bed\u6c14\u4e5f\u6ca1\u6709\u5341\u5206\u7684\u5ba2\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5c31\u7b97\u9ad8\u65b0\u533a\uff0c\u4e16\u7eaa\u9526\u6e56\u6240\u5360\u7684\u4e5f\u4ec5\u4ec5\u662f\u4e00\u5c0f\u5757\u5730\uff0c\u4e25\u6587\u4ecb\u53c8\u4e0d\u662f\u5929\u751f\u523a\u5934\uff0c\u8096\u660e\u5efa\u7684\u6001\u5ea6\u90fd\u6447\u6446\u4e0d\u5b9a\u4e86\uff0c\u4ed6\u4e3a\u4f55\u8fd8\u8981\u8fd9\u822c\u2014\u2014\u4f60\u5c06\u5f53\u65f6\u7684\u60c5\u5f62\u7ec6\u8bf4\u4e00\u904d\u7ed9\u6211\u542c\u542c\u2026\u2026\u201d\u80e1\u5b97\u5e86\u7ec8\u662f\u4e0d\u653e\u5fc3\uff0c\u9526\u6e56\u5546\u4e8b\u7a81\u7136\u95f4\u4e0e\u4e1c\u5357\u4e9a\u534e\u5546\u7684\u5173\u7cfb\u5bc6\u4e0d\u89c1\u9699\u8d77\u6765\uff0c\u751a\u81f3\u6709\u80fd\u529b\u5f15\u5bfc\u4ece\u5370\u5c3c\u9003\u79bb\u7684\u534e\u4eba\u8d44\u672c\u5b9a\u5411\u6d41\u5f80\u4e1c\u6d77\u2014\u2014\u5373\u4f7f\u4e2d\u592e\u91cc\u5934\u4e5f\u672a\u5fc5\u4e2a\u4e2a\u90fd\u77e5\u9053\u9526\u6e56\u5c06\u5728\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e2d\u7684\u4f5c\u4e3a\u5f52\u529f\u4e8e\u4e2d\u592e\u653f\u5e9c\u2014\u2014\u8fd9\u4f7f\u5f97\u4e4b\u524d\u4e00\u76f4\u5bf9\u9526\u6e56\u5fc3\u4f1a\u6028\u6068\u7684\u8096\u660e\u5efa\u5bf9\u9526\u6e56\u7684\u6001\u5ea6\u4e5f\u66a7\u6627\u4e0d\u660e\u8d77\u6765\uff0c\u8fd9\u662f\u80e1\u5b97\u5e86\u6700\u6015\u89c1\u5230\u7684\u5c40\u9762\uff0c\u6240\u4ee5\u4ed6\u5f88\u5173\u6ce8\u80fd\u5f71\u54cd\u8096\u660e\u5efa\u503e\u5411\u6027\u7684\u4e25\u6587\u4ecb\u5bf9\u9526\u6e56\u7684\u6001\u5ea6\u3002\u82e5\u662f\u8ba9\u8096\u660e\u5efa\u5f7b\u5e95\u5012\u5411\u7f57\u541b\uff0c\u5728\u9526\u6e56\u7684\u5f71\u54cd\u4e0b\uff0c\u4ed6\u867d\u7136\u8eab\u4e3a\u5e02\u59d4\u526f\u4e66\u8bb0\u517c\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u8981\u662f\u7ed9\u7f57\u541b\u3001\u8096\u660e\u5efa\u8054\u5408\u538b\u5236\u7740\uff0c\u90a3\u4ed6\u4ee5\u540e\u5728\u5efa\u90ba\u7684\u4efb\u671f\u5c31\u8981\u786c\u751f\u751f\u7684\u82e6\u71ac\u8fc7\u53bb\uff0c\u800c\u4e14\u4e0d\u80fd\u7ed9\u4ed6\u4eec\u63ea\u4f4f\u5c0f\u8fab\u5b50\u3002</p><p>\u3000\u3000\u6797\u96ea\u5f53\u7136\u77e5\u9053\u80e1\u5b97\u5e86\u5728\u62c5\u5fe7\u4ec0\u4e48\uff0c\u5bf9\u80e1\u5b97\u5e86\u8eab\u4e3a\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u5982\u6b64\u754f\u60e7\u9526\u6e56\uff0c\u5979\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\uff0c\u4fbf\u5c06\u5728\u5b66\u5e9c\u5df7\u4e4d\u9047\u5230\u5f20\u606a\u7684\u60c5\u5f62\u7ec6\u8bf4\u7ed9\u80e1\u5b97\u5e86\u542c\u3002\u4e25\u6587\u4ecb\u5bf9\u5f20\u606a\u8bf4\u7684\u90a3\u53e5\u8bdd\uff0c\u5373\u4f7f\u7b97\u4e0d\u4e0a\u5341\u5206\u7684\u65e0\u793c\uff0c\u4e5f\u6709\u51e0\u5206\u9488\u950b\u76f8\u5bf9\u7684\u5473\u9053\uff0c\u4e5f\u8bb8\u662f\u4e25\u6587\u4ecb\u5f3a\u52bf\u7684\u6027\u683c\u4f7f\u7136\u3002\u8fd9\u5e74\u5934\u6709\u4e25\u6587\u4ecb\u5982\u6b64\u6df1\u539a\u7684\u80cc\u666f\uff0c\u53c8\u5c06\u4f01\u4e1a\u7ecf\u8425\u5f97\u5982\u6b64\u5e9e\u5927\uff0c\u5373\u4f7f\u8868\u9762\u4e0a\u518d\u4fdd\u6301\u4f4e\u8c03\uff0c\u9aa8\u5b50\u91cc\u7684\u50b2\u6162\u603b\u662f\u4e0d\u53ef\u907f\u514d\u7684\u8981\u589e\u957f\u4e00\u4e9b\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u80fd\u60f3\u5230\u6797\u96ea\u4f1a\u7ad9\u5728\u7a97\u524d\u770b\u7740\u4ed6\u79bb\u5f00\uff0c\u8f66\u5b50\u62d0\u51fa\u661f\u6d77\u522b\u5885\u533a\uff0c\u4ed6\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u867d\u8bf4\u6797\u96ea\u662f\u4e2a\u96be\u5f97\u7684\u7f8e\u4eba\u513f\uff0c\u4f46\u8fd9\u4e48\u4e00\u4e2a\u5a07\u6ef4\u6ef4\u7684\u7f8e\u4eba\u80fd\u5750\u5728\u7cbe\u5178\u5730\u4ea7\u603b\u88c1\u7684\u4f4d\u5b50\u4e0a\uff0c\u5c06\u5979\u60f3\u5f97\u592a\u7b80\u5355\uff0c\u90a3\u771f\u662f\u592a\u611a\u8822\u4e86\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4ece\u6765\u90fd\u6ca1\u6709\u7f3a\u8fc7\u5973\u4eba\uff0c\u81ea\u7136\u4e5f\u4e0d\u5c51\u8ddf\u522b\u4eba\u5171\u540c\u4e00\u4e2a\u60c5\u5987\uff0c\u4ed6\u5750\u5728\u8f66\u91cc\uff0c\u5429\u5490\u53f8\u673a\u5f00\u8f66\u53bb\u5e02\u59d4\u5e02\u653f\u5e9c\u673a\u5173\u5bb6\u5c5e\u5927\u9662\uff0c\u4ed6\u8981\u53bb\u627e\u8096\u660e\u5efa\u3002</p><p>\u3000\u3000\u8096\u660e\u5efa\u63a8\u6389\u591c\u91cc\u7684\u5e94\u916c\uff0c\u4ed6\u6253\u7b97\u966a\u540c\u4e25\u6587\u4ecb\u4e00\u8d77\u4e0b\u53bb\u8003\u5bdf\u7684\uff0c\u53ea\u662f\u6015\u81ea\u5df1\u7684\u8eab\u4efd\u4f1a\u5438\u8d77\u592a\u591a\u7684\u5173\u6ce8\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u78b0\u5230\u5f20\u606a\u4e86\uff1f\u201d\u8096\u660e\u5efa\u8bf7\u4e25\u6587\u4ecb\u5165\u5ea7\uff0c\u542c\u4ed6\u8bf4\u8d77\u5728\u5b66\u5e9c\u5df7\u9047\u5230\u5f20\u606a\u7684\u4e8b\u60c5\uff0c\u7d27\u5f20\u7684\u5750\u76f4\u8eab\u5b50\uff0c\u542c\u4ed6\u8bf4\u5230\u79bb\u5f00\u65f6\u8bed\u6c14\u7a0d\u6709\u4e0d\u5584\uff0c\u5634\u89d2\u7684\u8138\u76ae\u5b50\u5fae\u5fae\u7684\u4e00\u8df3\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u89c9\u5f97\u5b8f\u4fe1\u5efa\u8bbe\u6295\u8d44\u5e02\u653f\u57fa\u7840\u5de5\u7a0b\u7684\u6a21\u5f0f\uff0c\u8bf4\u4e0d\u5b9a\u4f1a\u4e0e\u4e16\u7eaa\u9526\u6e56\u6709\u5408\u4f5c\u7684\u673a\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u6211\u4e5f\u4e0d\u662f\u4e89\u5f3a\u597d\u80dc\u7684\u4eba\u2026\u2026\u201d\u4e25\u6587\u4ecb\u559d\u4e86\u4e00\u53e3\u5496\u5561\uff0c\u201c\u7236\u4eb2\u8ba9\u6211\u8fc7\u6765\u8ddf\u8096\u53d4\u53d4\u60a8\u95ee\u58f0\u597d\uff0c\u4ed6\u8bf4\u4e86\uff0c\u5efa\u90ba\u6bd5\u7adf\u8ddf\u666e\u901a\u7684\u5730\u5e02\u6709\u5f88\u5927\u7684\u533a\u522b\uff0c\u6b63\u804c\u8c03\u79bb\uff0c\u526f\u804c\u9876\u4e0a\u7684\u673a\u4f1a\u4e5f\u53ea\u662f\u4e00\u534a\u4e00\u534a\u3002\u6211\u5c31\u6709\u4e9b\u4e0d\u5927\u6e05\u695a\uff0c\u8096\u53d4\u53d4\u81ea\u8dcc\u8eab\u4ef7\u8ddf\u7f57\u541b\u3001\u8ddf\u9526\u6e56\u90a3\u8fb9\u7f13\u548c\u5173\u7cfb\uff0c\u672a\u6765\u5230\u5e95\u80fd\u8d77\u591a\u5927\u7684\u4f5c\u7528\uff1f\u201d</p><p>\u3000\u3000\u8096\u660e\u5efa\u5012\u662f\u4e00\u6014\uff0c\u4e25\u6587\u4ecb\u8fd9\u6b21\u8fc7\u6765\u5374\u4e0d\u662f\u6765\u5e2e\u7740\u4fee\u5584\u81ea\u5df1\u4e0e\u7f57\u541b\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u76f8\u53cd\u8fd8\u8981\u5506\u4f7f\u7740\u81ea\u5df1\u5c06\u4e0e\u7f57\u541b\u7684\u77db\u76fe\u8fdb\u4e00\u6b65\u5347\u7ea7\u3002</p><p>\u3000\u3000\u5bf9\u5b8f\u4fe1\u5efa\u90ba\u6765\u8bf4\uff0c\u5bf9\u4e25\u6587\u4ecb\u6765\u8bf4\uff0c\u505a\u4ec0\u4e48\u51b3\u5b9a\u5176\u5b9e\u5f88\u7b80\u5355\uff0c\u54ea\u8fb9\u7684\u86cb\u7cd5\u66f4\u5927\u4e00\u4e9b\uff0c\u4ed6\u5c31\u5012\u5411\u54ea\u4e00\u8fb9\uff0c\u8fd9\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u9009\u62e9\u3002</p></article>"}}}]);
