(window.webpackJsonp=window.webpackJsonp||[]).push([[889],{912:function(p){p.exports={chapterTitle:null,title:"\u7b2c710\u7ae0 \u5b59\u9759\u6aac\u7684\u5a01\u80c1",content:"<article><h2>\u7b2c710\u7ae0 \u5b59\u9759\u6aac\u7684\u5a01\u80c1</h2><p>\u3000\u3000\u522b\u770b\u738b\u6d77\u7c9f\u5439\u5618\u8ddf\u7740\u79d1\u6280\u90e8\u7684\u8003\u5bdf\u56e2\u5230\u5370\u5ea6\u8003\u5bdf\u4e86\u4e00\u5708\uff0c\u6240\u8c13\u7684\u8003\u5bdf\u56e2\u53ea\u4e0d\u8fc7\u662f\u79d1\u6280\u90e8\u4e0b\u5c5e\u4e8b\u4e1a\u5355\u62c9\u79d1\u5b66\u6280\u672f\u4ea4\u6d41\u4e2d\u5fc3\u7ec4\u7ec7\u7684\u4e00\u6b21\u4f01\u4e1a\u6d3b\u52a8\uff0c\u53ea\u8981\u662f\u56fd\u5185\u7684\u8f6f\u4ef6\u516c\u53f8\u90fd\u53ef\u4ee5\u62a5\u540d\u53c2\u52a0\uff0c\u738b\u6d77\u7c9f\u54ac\u54ac\u7259\u62a5\u540d\u9080\u4e86\u4e00\u7b14\u4e0d\u83f2\u7684\u8d39\u7528\u53c2\u52a0\u8003\u5bdf\u56e2\uff0c\u4e0d\u8fc7\u662f\u60f3\u770b\u80fd\u4e0d\u80fd\u501f\u673a\u4e0e\u79d1\u6280\u90e8\u7684\u5b98\u5458\u62c9\u4e0a\u5173\u7cfb\uff0c\u53c2\u52a0\u8fd9\u6b21\u7684\u6d3b\u52a8\uff0c\u4e5f\u5e0c\u671b\u7c4d\u6b64\u80fd\u4e3a\u6d77\u7c9f\u79d1\u6280\u9540\u4e00\u5c42\u91d1\u3002</p><p>\u3000\u3000\u8003\u5bdf\u56e2\u7531\u79d1\u5b66\u6280\u672f\u4ea4\u6d41\u4e2d\u5fc3\u4e00\u540d\u526f\u4e3b\u4efb\u9886\u961f\uff0c\u90a3\u540d\u526f\u4e3b\u4efb\uff0c\u526f\u5c40\u7ea7\u7684\u5b98\u5458\uff0c\u7ed9\u4e2d\u8f6f\u3001\u4e1c\u8f6f\u7684\u51e0\u5bb6\u5927\u8f6f\u4ef6\u516c\u53f8\u7684\u9ad8\u5c42\u6574\u5929\u5305\u56f4\u7740\u591c\u591c\u65cc\u6b4c\uff0c\u738b\u6d77\u7c9f\u8fd8\u53ea\u4e2a\u5c0f\u89d2\u8272\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u63d2\u8db3\u3001\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u4eba\u5bb6\u7684\u51b7\u5c41\u80a1\uff0c\u4ed6\u7684\u70ed\u8138\u6839\u672c\u5c31\u6ca1\u6709\u673a\u4f1a\u8d34\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5728\u738b\u6d77\u7c9f\u770b\u6765\uff0c\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u5c31\u5c5e\u4e8e\u90a3\u79cd\u624b\u773c\u901a\u5929\u7684\u4eba\u7269\uff0c\u8fd9\u79cd\u4eba\u7269\u53ea\u8981\u77a5\u4e2a\u773c\u8272\u7ed9\u4e0b\u9762\u7684\u90e8\u95e8\uff0c\u8bf4\u4e00\u53e5\u201c\u5e94\u8be5\u7167\u987e\u4e00\u4e0b\u6d77\u7c9f\u79d1\u6280\u201d\u4e4b\u7c7b\u7684\u8bdd\uff0c\u6d77\u7c9f\u79d1\u6280\u5728\u5efa\u90ba\u5e02\u7684\u8d22\u52a1\u8f6f\u4ef6\u5e02\u573a\u4e0a\u5c31\u4e0d\u7528\u8fd9\u4e48\u8d39\u529b\u4e86\u3002</p><p>\u3000\u3000\u653f\u5e9c\u90e8\u95e8\u4e0e\u56fd\u6709\u4f01\u4e1a\u662f\u56fd\u5185\u5f53\u524d\u8d22\u52a1\u8f6f\u4ef6\u5e02\u573a\u7684\u4e3b\u8981\u8d2d\u4e70\u529b\u3002</p><p>\u3000\u3000\u5728\u5168\u56fd\u8303\u56f4\u5185\uff0c\u7528\u53cb\u3001\u91d1\u789f\u4e24\u5927\u8d22\u52a1\u8f6f\u4ef6\u516c\u53f8\u5df2\u6210\u6c14\u5019\uff0c\u4f46\u662f\u533a\u57df\u8d22\u52a1\u8f6f\u4ef6\u5e02\u573a\uff0c\u4ecd\u6709\u6781\u5927\u7684\u7a7a\u95f4\uff0c\u6070\u6070\u80e1\u5b97\u5e86\u5206\u7ba1\u5e02\u8d22\u7a0e\u3001\u56fd\u4f01\u6539\u5236\u7b49\u65b9\u9762\u7684\u5de5\u4f5c\uff0c\u5bf9\u738b\u6d77\u7c9f\u7684\u516c\u53f8\u6765\u8bf4\uff0c\u662f\u4e2a\u6781\u6709\u7528\u7684\u4eba\u3002</p><p>\u3000\u3000\u542c\u5f20\u606a\u4ecb\u7ecd\u8d77\u80e1\u91d1\u661f\u7684\u8eab\u4efd\uff0c\u738b\u6d77\u7c9f\u5fc3\u91cc\u7830\u7136\u76f4\u8df3\uff0c\u8f6c\u773c\u770b\u8fc7\u6765\uff0c\u77a5\u773c\u591a\u4e86\u770b\u80e1\u91d1\u661f\u4e24\u773c\uff0c\u5468\u8eab\u7a7f\u6234\u867d\u770b\u4e0d\u6e05\u724c\u5b50\uff0c\u51ed\u611f\u89c9\u5c31\u77e5\u9053\u90fd\u4e0d\u662f\u4f4e\u6863\u8d27\uff0c\u5fc3\u60f3\u8fd9\u4fbf\u597d\u529e\uff0c\u89c1\u4ed6\u5750\u5728\u90a3\u8fb9\u6ca1\u6709\u50cf\u5176\u4ed6\u5b66\u751f\u4f1a\u7684\u5b66\u751f\u5e72\u90e8\u8fc7\u6765\u6253\u62db\u547c\u800c\u662f\u626d\u5934\u770b\u7740\u522b\u5904\uff0c\u5927\u6982\u662f\u611f\u89c9\u5230\u8fd9\u8fb9\u5728\u8bae\u8bba\u4ed6\uff0c\u624d\u8f6c\u8fc7\u5934\u6765\u770b\u4e00\u773c\uff0c\u773c\u795e\u91cc\u4e5f\u8bf4\u4e0d\u51fa\u4ec0\u4e48\u5473\u9053\uff0c\u5f88\u5feb\u53c8\u5c06\u5934\u8f6c\u5411\u522b\u5904\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u4e5f\u6ca1\u6709\u8f7b\u6d6e\u5230\u9a6c\u4e0a\u4e22\u4e0b\u8fd9\u8fb9\u3001\u70ed\u8138\u53bb\u8d34\u90a3\u8fb9\u7684\u51b7\u5c41\u80a1\uff0c\u800c\u662f\u6682\u65f6\u6309\u4e0b\u8dc3\u8dc3\u6b32\u8bd5\u7684\u5fc3\u601d\uff0c\u8fd8\u662f\u8981\u5148\u5c06\u8fd9\u8fb9\u5e94\u4ed8\u5b8c\u3002\u4f55\u51b5\u738b\u6d77\u7c9f\u8fd8\u4e0d\u6e05\u695a\u80e1\u5b97\u5e86\u6709\u591a\u770b\u91cd\u8fd9\u4e2a\u4f84\u5b50\uff0c\u4e0e\u80e1\u91d1\u661f\u641e\u597d\u5173\u7cfb\uff0c\u672a\u5fc5\u80fd\u5f97\u5230\u80e1\u5b97\u5e86\u7684\u5f85\u89c1\uff0c\u7528\u5fc3\u4f1a\u53d7\u5230\u80e1\u5b97\u5e86\u7684\u8d28\u7591\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u603b\u4e4b\u4e0d\u80fd\u4ed3\u4fc3\u884c\u4e8b\uff0c\u8981\u597d\u597d\u7b79\u5212\u4e00\u756a\u624d\u884c\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5bf9\u5f85\u8fd9\u8fb9\u7684\u6001\u5ea6\u5c31\u53d1\u751f\u5fae\u5999\u7684\u53d8\u5316\uff0c\u4e5f\u8bb8\u4ed6\u81ea\u5df1\u4e5f\u6ca1\u6709\u89c9\u5bdf\u5230\uff0c\u4ed6\u90a3\u4e9b\u8ba9\u4eba\u8840\u8109\u8d32\u5f20\u7684\u8bdd\u5c11\u4e86\uff0c\u996d\u5c40\u5f88\u5feb\u5c31\u7ed3\u675f\u2014\u2014\u518d\u8bf4\u8499\u4e50\u3001\u675c\u98de\u4e5f\u653e\u5fc3\u4e0d\u4e0b\u7f51\u5427\u7684\u4e8b\u60c5\u2014\u2014\u7f51\u5427\u7b2c\u4e00\u5929\u8425\u4e1a\uff0c\u4ed6\u4eec\u6068\u4e0d\u80fd\u773c\u775b\u90fd\u4e0d\u7728\u4e00\u4e0b\u7684\u76ef\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u76f4\u63a5\u56de\u7f51\u5427\uff0c\u738b\u6d77\u7c9f\u5012\u6ca1\u6709\u8ddf\u7740\uff0c\u8bf4\u8981\u56de\u516c\u53f8\u3002\u4ed6\u7684\u8f66\u5b50\u505c\u5728\u5df7\u5b50\u53e3\u7684\u505c\u8f66\u573a\uff0c\u5e2d\u82e5\u7433\u9001\u4ed6\u53bb\u53d6\u8f66\u3002\u770b\u7740\u5e2d\u82e5\u7433\u4e0e\u738b\u6d77\u7c9f\u795e\u6001\u4eb2\u6635\u7684\u5e76\u80a9\u79bb\u5f00\uff0c\u8499\u4e50\u60c6\u6005\u7684\u671b\u7740\uff0c\u5f20\u606a\u5fae\u5fae\u4e00\u53f9\uff0c\u8981\u662f\u81ea\u5df1\u503e\u6155\u7684\u5973\u4eba\u8ddf\u522b\u7684\u7537\u4eba\u4eb2\u70ed\u7684\u8d70\u5728\u4e00\u8fb9\uff0c\u5fc3\u60c5\u4e5f\u4e0d\u4f1a\u597d\u53d7\uff0c\u7537\u4eba\u901a\u5e38\u5728\u8fd9\u79cd\u4e8b\u60c5\u5bb9\u6613\u72af\u8d31\uff0c\u4e5f\u5bb9\u6613\u7406\u89e3\u8499\u4e50\uff0c\u4e0e\u65f6\u5b66\u658c\u4e00\u5de6\u4e00\u53f3\u6402\u7740\u4ed6\u7684\u80a9\u8180\u5c06\u4ed6\u62fd\u8fdb\u7f51\u5427\u91cc\u3002\u65f6\u5b66\u658c\u8fd8\u4e0d\u5fd8\u529d\u5bfc\u4ed6\u60f3\u5f00\u4e9b\uff1a\u201c\u5929\u6daf\u4f55\u5904\u65e0\u82b3\u8349\uff0c\u4f55\u82e6\u8981\u5728\u4e1c\u5927\u627e\uff0c\u4e1c\u5927\u672c\u6765\u8349\u5c31\u5c11\uff0c\u4eca\u5e74\u6536\u6210\u8fd8\u4e0d\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u624d\u4e0d\u7ba1\u8499\u4e50\u7684\u5fc3\u5934\u75db\uff1b\u7ecf\u5e38\u6027\u7684\u75db\u4e00\u75db\u4e5f\u5c31\u4e60\u60ef\u3002</p><p>\u3000\u3000\u7f51\u5427\u7684\u7ecf\u7406\u5ba4\u8bbe\u5728\u4e8c\u697c\uff0c\u8499\u4e50\u5fcd\u7740\u53d7\u4f24\u7684\u611f\u89c9\u5728\u5927\u5385\u91cc\u5f20\u843d\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u5230\u7ecf\u7406\u5ba4\u4f11\u606f\u95f2\u626f\uff0c\u5b59\u9759\u6aac\u770b\u5230\u4ed6\u4eec\u56de\u6765\uff0c\u4e5f\u4e0d\u518d\u4e0a\u7f51\uff0c\u8ddf\u7740\u8fdb\u6765\uff0c\u95ee\u5f20\u606a\u4ec0\u4e48\u65f6\u95f4\u53bb\u770b\u516c\u5bd3\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u7b49\u6881\u519b\u7ed9\u4ed6\u7535\u8bdd\uff0c\u8fd9\u65f6\u5019\u53c8\u4e0d\u6025\u3002</p><p>\u3000\u3000\u675c\u98de\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u600e\u4e48\u60f3\u7740\u8981\u8bf1\u5bfc\u738b\u6d77\u7c9f\u53bb\u8ddf\u80e1\u91d1\u661f\u80e1\u63a5\u89e6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u53cc\u624b\u62b1\u7740\u540e\u8111\u52fa\uff0c\u8bf4\u9053\uff0c\u201c\u91ce\u5fc3\u8fd9\u4e1c\u897f\uff0c\u662f\u5f88\u4e0d\u7a33\u5b9a\u7684\u4e1c\u897f\uff0c\u7537\u4eba\u8981\u6ca1\u6709\u8fd9\u4e1c\u897f\uff0c\u662f\u5b8c\u5168\u4e0d\u884c\u7684\u2026\u2026\u201d\u4fa7\u8fc7\u5934\u95ee\u5b59\u9759\u6aac\uff0c\u201c\u8fd9\u4e48\u8bf4\u5bf9\u4e0d\u5bf9\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6ca1\u597d\u6c14\u7684\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u600e\u4e48\u6653\u5f97\uff1f\u4f60\u8fd9\u5bb6\u4f19\u6574\u5929\u60f3\u7740\u5728\u4e1c\u5927\u6df7\u5403\u7b49\u6b7b\uff0c\u7adf\u7136\u6df7\u8fc7\u6765\u501f\u6307\u5bfc\u5973\u5b69\u5b50\u4e0a\u7f51\u7684\u673a\u4f1a\u6765\u6478\u4eba\u5bb6\u5c0f\u624b\uff0c\u4f60\u771f\u662f\u6709\u91ce\u5fc3\u554a\uff0c\u4e00\u4e0a\u5348\u5360\u4e86\u51e0\u4e2a\u5973\u5b69\u5b50\u7684\u4fbf\u5b9c\uff1f\u8981\u4e0d\u8981\u4e5f\u6307\u5bfc\u6211\u4e0a\u7f51\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u54b3\uff0c\u201d\u4e0d\u6653\u5f97\u866b\u4ff1\u4e50\u90e8\u7684\u90a3\u4e2a\u6df7\u8d26\u5bb6\u4f19\u8fd9\u4e48\u6ca1\u6709\u9aa8\u6c14\u7adf\u7136\u5c06\u8fd9\u4e9b\u4e0d\u8db3\u4e3a\u5973\u4eba\u9053\u7684\u4e8b\u60c5\u968f\u4fbf\u62ff\u51fa\u6765\u70ab\u8000\uff0c\u89c1\u5b59\u9759\u6aac\u7738\u5b50\u95ea\u70c1\u7740\u4e0d\u5c51\u4e0e\u9119\u89c6\u7684\u773c\u795e\uff0c\u5f20\u606a\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u4fbf\u5f53\u6ca1\u6709\u95ee\u8fc7\u5979\u8fd9\u8bdd\uff0c\u6487\u8fc7\u5934\u8ddf\u675c\u98de\u7ee7\u7eed\u8bf4\u4ed6\u4eec\u7684\u4e8b\u60c5\uff0c\u201c\u91ce\u5fc3\u8fc7\u4e8e\u81a8\u80c0\uff0c\u662f\u798f\u662f\u7978\u5c31\u96be\u4ee5\u9884\u6599\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u662f\u60f3\u9001\u4e00\u9897\u5730\u96f7\u7ed9\u80e1\u5b97\u5e86\u4ed6\u4eec\u2026\u2026\u201d\u675c\u98de\u5fc3\u9886\u795e\u4f1a\u7684\u563f\u7136\u4e00\u7b11\uff0c\u201c\u7269\u4ee5\u7c7b\u805a\uff0c\u4ed6\u4eec\u8981\u771f\u662f\u4e00\u7c7b\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u8981\u611f\u8c22\u4f60\u7684\u6307\u5f15\u529f\u52b3\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u6015\u738b\u6d77\u7c9f\u8fd9\u4eba\u7684\u91ce\u5fc3\u8fd8\u4e0d\u591f\u5927\uff0c\u6405\u4e0d\u4e86\u591a\u5927\u7684\u5c40\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\u7684\u65f6\u5019\uff0c\u8138\u4e0a\u53ef\u6ca1\u6709\u4ec0\u4e48\u5bc6\u65bd\u9634\u8c0b\u7684\u9634\u51b7\uff0c\u7b11\u8d77\u6765\u8fd8\u5f88\u6709\u9633\u5149\u7684\u611f\u89c9\uff0c\u5b59\u9759\u6aac\u7741\u773c\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u60f3\u591a\u770b\u4e00\u4f1a\u513f\uff0c\u5374\u53c8\u4e0d\u4f1a\u627f\u8ba4\u7ed9\u5f20\u606a\u7b11\u5bb9\u5438\u5f15\u8fc7\u53bb\u7684\u60c5\u7eea\uff0c\u4e0d\u5c51\u7684\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u795e\u7ecf\u75c5\u2026\u2026\u201d\u8f6c\u8eab\u7ffb\u8d77\u529e\u516c\u684c\u4e0a\u7684\u6742\u5fd7\u3002</p><p>\u3000\u3000\u5728\u5b98\u573a\u8fd9\u4e2a\u5947\u602a\u7684\u4e16\u754c\u91cc\uff0c\u7ed9\u4eba\u5206\u7c7b\u7684\u89c4\u5219\u5f88\u7b80\u5355\uff0c\u8981\u4e48\u662f\u5708\u5b50\u5185\uff0c\u8981\u4e48\u662f\u5708\u5b50\u5916\uff0c\u7b80\u5355\u800c\u4e14\u7c97\u66b4\uff0c\u800c\u4e14\u7275\u8fde\u6781\u5e7f\u3002\u738b\u6d77\u7c9f\u81ea\u4ee5\u4e3a\u5df4\u7ed3\u4e0a\u80e1\u91d1\u661f\u5c31\u60f3\u8981\u501f\u7740\u80e1\u5b97\u5e86\u8fd9\u6761\u8def\u4ece\u6b64\u98de\u9ec4\u817e\u8fbe\u65e0\u5f02\u4e8e\u5f02\u60f3\u5929\u5f00\u3002\u738b\u6d77\u7c9f\u60f3\u6295\u5176\u6240\u597d\uff0c\u4ece\u80e1\u5b97\u5e86\u5206\u7ba1\u7684\u8303\u56f4\u5185\u63a5\u51e0\u7b14\u6cb9\u6c34\u4e30\u539a\u7684\u8f6f\u4ef6\u5355\u5b50\uff0c\u4f46\u662f\u6bd4\u738b\u6d77\u7c9f\u66f4\u6709\u5206\u91cf\u3001\u66f4\u80fd\u6295\u5176\u6240\u597d\u800c\u4e14\u5efa\u90ba\u7ecf\u8425\u591a\u5e74\u7684\u8f6f\u4ef6\u516c\u53f8\u5927\u6709\u4eba\u5728\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u91ce\u5fc3\u8fd9\u4e1c\u897f\u662f\u4e00\u4e2a\u751f\u547d\u529b\u5f88\u5f3a\u7684\u79cd\u5b50\uff0c\u9047\u5230\u4ec0\u4e48\u571f\u58e4\u5c31\u53d1\u4ec0\u4e48\u82bd\uff0c\u8c01\u6653\u5f97\u738b\u6d77\u7c9f\u6700\u540e\u80fd\u4e0d\u80fd\u6405\u51fa\u4e00\u756a\u5927\u5c40\u9762\u6765\u5462\uff1f\u5f20\u606a\u8fd9\u4e48\u60f3\u7740\uff0c\u4e5f\u6709\u8fd9\u6837\u7684\u671f\u5f85\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fdb\u5165\u5efa\u90ba\uff0c\u52a8\u4f5c\u5f88\u5927\uff0c\u91cd\u6765\u90fd\u6ca1\u6709\u60f3\u7740\u76f4\u63a5\u8fc7\u6df1\u7684\u6405\u548c\u8fdb\u5efa\u90ba\u5b98\u573a\u91cc\u53bb\uff0c\u4f46\u662f\u8fd9\u65f6\u5019\u5728\u8fd9\u4e9b\u65b9\u9762\u5374\u4e0d\u5f97\u4e0d\u63d0\u524d\u505a\u4e9b\u51c6\u5907\uff0c\u514d\u5f97\u7f57\u541b\u54ea\u4e00\u5929\u7a81\u7136\u8c03\u5f80\u522b\u5904\u9ad8\u5347\uff0c\u9526\u6e56\u5728\u5efa\u90ba\u5c31\u592a\u88ab\u52a8\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u8003\u8651\u7740\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u675c\u98de\u5012\u662f\u60f3\u5230\u53e6\u5916\u4e00\u4ef6\u4e8b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u5e2d\u5b66\u59d0\u8ddf\u8fd9\u59d3\u738b\u7684\u4e5f\u4e0d\u662f\u4e00\u8def\u4eba\uff0c\u8ba9\u59d3\u738b\u7684\u52a0\u5feb\u6b65\u4f10\u8d70\u504f\u3001\u8d70\u8fdc\uff0c\u4e5f\u597d\u8ba9\u5979\u4e0e\u8fd9\u59d3\u738b\u7684\u4e4b\u95f4\u65e9\u6709\u51b3\u65ad\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u5fc3\u601d\u53ef\u6ca1\u6709\u4f60\u8fd9\u4e48\u9f8c\u9f8a\u2026\u2026\u201d\u5f20\u606a\u4e0d\u4f1a\u627f\u8ba4\u4ed6\u6709\u8fd9\u79cd\u5fc3\u601d\uff0c\u8981\u4e0d\u662f\u56e0\u4e3a\u8499\u4e50\u8fd9\u4e2a\u95f7\u9a9a\u800c\u5355\u7eaf\u7684\u5bb6\u4f19\uff0c\u4ed6\u4e5f\u8bb8\u4e0d\u4f1a\u53bb\u505a\u8fd9\u79cd\u63a8\u6ce2\u52a9\u6f9c\u7684\u4e8b\u60c5\uff0c\u591a\u65b9\u9762\u7684\u56e0\u7d20\u4fc3\u4f7f\u4ed6\u79cd\u4e0b\u8fd9\u7c92\u79cd\u5b50\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u867d\u7136\u59d3\u738b\u7684\u4e0d\u62db\u4eba\u559c\u6b22\uff0c\u4f46\u662f\u4ed6\u8bf4\u7684\u8bdd\u4e5f\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u9053\u7406\uff0c\u4f60\u89c9\u5f97\u56fd\u5185\u8f6f\u4ef6\u4ea7\u4e1a\u7684\u524d\u666f\u5982\u4f55\uff1f\u201d\u675c\u98de\u8ba4\u771f\u7684\u95ee\u5f20\u606a\u7684\u610f\u89c1\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8bf4\u5462\uff0c\u201d\u5f20\u606a\u60f3\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff0c\u201c\u5c31\u62ff\u9526\u6e56\u6765\u8bf4\uff0c\u5c31\u4e00\u76f4\u90fd\u5728\u4e3a\u81ea\u5df1\u7684\u4ea7\u54c1\u505a\u5d4c\u5165\u5f0f\u8f6f\u4ef6\u7684\u5f00\u53d1\u3002\u4eca\u540e\u51e0\u5e74\uff0c\u5728\u8f6f\u4ef6\u5f00\u53d1\u4e0a\u7684\u6301\u7eed\u6295\u5165\u529b\u5ea6\u90fd\u4f1a\u975e\u5e38\u7684\u5927\uff0c\u6211\u751a\u81f3\u8ba9\u6a61\u6811\u56ed\u4e13\u95e8\u6210\u7acb\u4e00\u4e2a\u8bfe\u9898\u7ec4\u53bb\u79fb\u690dLinux\u5185\u6838\u5f00\u53d1\u64cd\u4f5c\u5e73\u53f0\u6027\u8d28\u7684\u57fa\u7840\u8f6f\u4ef6\u3002\u9526\u6e56\u662f\u5c06\u8f6f\u4ef6\u5f00\u53d1\u4f5c\u4e3a\u6838\u5fc3\u6280\u672f\u6765\u53d1\u5c55\u7684\uff0c\u6700\u7ec8\uff0c\u8f6f\u4ef6\u5f00\u53d1\u90e8\u95e8\uff0c\u5c06\u662f\u9526\u6e56\u7814\u53d1\u4f53\u7cfb\u91cc\u6700\u91cd\u8981\u7684\u5206\u652f\u4e4b\u4e00\u3002\u4f46\u662f\u9526\u6e56\u5bf9\u8f6f\u4ef6\u5f00\u53d1\u7684\u6295\u5165\uff0c\u6682\u65f6\u8fd8\u4e0d\u662f\u56e0\u4e3a\u5916\u90e8\u7684\u5e02\u573a\uff0c\u4e3b\u8981\u662f\u4e3a\u4e86\u6ee1\u8db3\u81ea\u8eab\u7684\u9700\u6c42\uff1b\u6269\u5927\u4e00\u4e9b\uff0c\u4e5f\u662f\u6ee1\u8db3\u76f8\u5173\u4ea7\u4e1a\u7684\u9700\u6c42\u2014\u2014\u8fd9\u4e5f\u662f\u9526\u6e56\u672a\u6765\u7684\u76ee\u6807\u3002\u4f46\u662f\u50cf\u9526\u6e56\u8fd9\u79cd\u4ee5\u6ee1\u8db3\u4f01\u4e1a\u81ea\u8eab\u9700\u6c42\u7684\u8f6f\u4ef6\u5f00\u53d1\u884c\u4e3a\uff0c\u662f\u4e0d\u5e94\u8be5\u5f52\u5212\u5230\u8f6f\u4ef6\u4ea7\u4e1a\u4e2d\u53bb\u7684\u3002\u8fd9\u4e00\u5757\u7684\u6295\u5165\u4e0e\u9700\u6c42\u867d\u7136\u4f1a\u8d8a\u6765\u8d8a\u5927\uff0c\u800c\u4e14\u8fd9\u4e00\u90e8\u5206\u7684\u8f6f\u4ef6\u5927\u591a\u6570\u662f\u4e0e\u786c\u4ef6\u6346\u7ed1\u9500\u552e\uff0c\u5728\u77e5\u8bc6\u4ea7\u6743\u4fdd\u62a4\u65b9\u9762\uff0c\u6709\u7740\u5929\u7136\u7684\u4f18\u52bf\uff0c\u4f46\u662f\u8fd9\u4e00\u5757\u7684\u9700\u6c42\uff0c\u5916\u90e8\u7684\u8f6f\u4ef6\u516c\u53f8\u662f\u5f88\u96be\u63d2\u624b\u8fdb\u6765\u7684\u2026\u2026\u8fd9\u4fbf\u662f\u9526\u6e56\u5bf9\u672a\u6765\u8f6f\u4ef6\u4ea7\u4e1a\u53d1\u5c55\u7684\u8ba4\u77e5\uff0c\u81f3\u4e8e\u5176\u4ed6\u7684\uff0c\u4f60\u81ea\u5df1\u53bb\u601d\u91cf\u5427\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u771f\u662f\u5c0f\u6c14\u554a\uff0c\u201d\u675c\u98de\u5934\u75bc\u7684\u76b1\u8d77\u7709\u5934\uff0c\u201c\u9526\u6e56\u7684\u6a21\u5f0f\u5bf9\u666e\u901a\u8f6f\u4ef6\u516c\u53f8\u80fd\u6709\u4ec0\u4e48\u53c2\u8003\u4ef7\u503c\uff1f\u201d</p><p>\u3000\u3000\u8499\u4e50\u63a8\u95e8\u8d70\u8fdb\u6765\uff0c\u95ee\u9053\uff1a\u201c\u5728\u8c08\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u8ba8\u8bba\u90a3\u4e2a\u8ba9\u4f60\u60c6\u6005\u53c8\u5fc3\u9178\u7684\u5bb6\u4f19\u2026\u2026\u201d\u5f20\u606a\u6ca1\u5fc3\u6ca1\u80ba\u7684\u5c31\u60f3\u7740\u8981\u53bb\u6233\u8499\u4e50\u7684\u4f24\u75a4\u3002</p><p>\u3000\u3000\u8499\u4e50\u4e5f\u4e0d\u662f\u6ca1\u6709\u4e00\u70b9\u627f\u53d7\u529b\uff0c\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u738b\u6d77\u7c9f\u554a\uff0c\u6211\u89c9\u5f97\u4ed6\u8fd9\u4e2a\u4eba\uff0c\u4eca\u5929\u627e\u6211\u4eec\u7684\u5fc3\u673a\u6709\u4e9b\u4e0d\u7eaf\uff0c\u4e0d\u8fc7\u6487\u5f00\u4ed6\u4e0d\u7eaf\u7684\u5fc3\u673a\u4e0d\u8bf4\uff0c\u8fd8\u662f\u6709\u4e00\u4e9b\u773c\u5149\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u5938\u4ed6\u5c31\u5938\u4ed6\uff0c\u4e0d\u8981\u5148\u6253\u4eba\u5bb6\u4e00\u68cd\u5b50\uff0c\u8fd9\u8bdd\uff0c\u5e2d\u5e08\u59d0\u4f30\u8ba1\u4e0d\u7231\u542c\u3002\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u4f1a\u5728\u5979\u9762\u524d\u8bf4\uff0c\u54b1\u4e0d\u662f\u81ea\u5bb6\u5144\u5f1f\u5417\uff0c\u6211\u8ddf\u4f60\u4eec\u8bf4\u8bdd\u8fd8\u8981\u906e\u906e\u63a9\u63a9\uff1f\u201d\u8499\u4e50\u8146\u7740\u8138\u62c9\u4e00\u5f20\u6905\u5b50\u5750\u4e0b\u6765\uff0c\u201c\u738b\u6d77\u7c9f\u8bf4\u7684\u8f6f\u4ef6\u5e02\u573a\uff0c\u73b0\u5728\u4e0d\u662f\u5f00\u59cb\u63d0\u5021\u4f01\u4e1a\u4fe1\u606f\u5316\u7ba1\u7406\u5417\uff1f\u4f01\u4e1a\u7ba1\u7406\u8f6f\u4ef6\u7684\u5e02\u573a\u4e5f\u5e94\u8be5\u8981\u542f\u52a8\uff1b\u81f3\u4e8e\u8d22\u52a1\u8f6f\u4ef6\uff0c\u867d\u7136\u8bf4\u7528\u53cb\u3001\u91d1\u789f\u5df2\u7ecf\u6210\u4e86\u6c14\u5019\uff0c\u4f46\u662f\u5728\u533a\u57df\u5e02\u573a\u5185\uff0c\u8fd8\u662f\u6709\u4e00\u5b9a\u5e02\u573a\u7684\u7559\u7ed9\u5730\u65b9\u4f01\u4e1a\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u770b\u4e86\u5f20\u606a\u4e00\u4f1a\u513f\uff0c\u89c1\u5f20\u606a\u4e0d\u542d\u58f0\uff0c\u6653\u5f97\u4ed6\u8fd8\u771f\u662f\u8981\u575a\u51b3\u7684\u60dc\u5b57\u5982\u91d1\uff0c\u65e0\u5948\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u5757\u7684\u8f6f\u4ef6\u7ec6\u5206\u5e02\u573a\uff0c\u6697\u85cf\u7684\u98ce\u9669\u4e0d\u5c11\uff0c\u7edd\u4e0d\u50cf\u59d3\u738b\u7684\u8bf4\u5f97\u90a3\u4e48\u65e0\u9650\u5149\u660e\u2026\u2026\u603b\u4e4b\uff0c\u6211\u4eec\u4e5f\u6709\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u5728\u505a\u8fd9\u65b9\u9762\u7684\u76f8\u5173\u5de5\u4f5c\uff0c\u8fb9\u505a\u8fb9\u6478\u7d22\u5427\u3002\u201d</p><p>\u3000\u3000\u6881\u519b\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8981\u5f20\u606a\u53bb\u770b\u4ed6\u7684\u65b0\u516c\u5bd3\u3002\u5f20\u606a\u8ba9\u675c\u98de\u3001\u8499\u4e50\u8ddf\u7740\u4e00\u8d77\u53bb\u770b\u770b\uff0c\u4e0d\u9700\u8981\u8fd9\u4e48\u7d27\u5f20\u7684\u76ef\u5728\u8fd9\u91cc\u3002\u4e0b\u697c\u65f6\uff0c\u9047\u5230\u5e2d\u82e5\u7433\u9001\u738b\u6d77\u7c9f\u56de\u6765\uff0c\u5979\u6653\u5f97\u8499\u4e50\u4ed6\u4eec\u4e0d\u559c\u6b22\u738b\u6d77\u7c9f\uff0c\u60f3\u89e3\u91ca\u4e00\u4e24\u53e5\uff0c\u53c8\u89c9\u5f97\u6709\u4e9b\u8bdd\u96be\u4ee5\u51fa\u53e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u662f\u8981\u5c06\u8fd9\u8fb9\u7684\u516c\u5bd3\u5f53\u6210\u4ed6\u5728\u5efa\u90ba\u7684\u516c\u5f00\u636e\u70b9\uff0c\u81f3\u5c11\u5e73\u65e5\u4eb2\u8fd1\u7684\u4eba\uff0c\u90fd\u4f1a\u9080\u53bb\u4f4f\u5904\u805a\u4f1a\u4ec0\u4e48\u7684\uff1b\u7f51\u5427\u8fd9\u8fb9\u4e0b\u5348\u4e5f\u6ca1\u6709\u5b89\u6392\u5e2d\u82e5\u7433\u503c\u73ed\uff0c\u4fbf\u9080\u5979\u4e00\u8d77\u53bb\u73a9\u3002\u5e2d\u82e5\u7433\u4e5f\u6ca1\u6709\u63a8\u8f9e\uff0c\u8fd8\u89e3\u91ca\u4e86\u4e00\u4e0b\uff1a\u201c\u6d77\u7c9f\u4ed6\u662f\u81ea\u5df1\u4e3b\u52a8\u60f3\u627e\u521b\u534f\u5408\u4f5c\uff0c\u65e2\u7136\u4f60\u4eec\u6ca1\u6709\u8fd9\u4e2a\u610f\u601d\uff0c\u4e5f\u5c31\u7b97\u4e86\uff0c\u6211\u4e5f\u6ca1\u6709\u8ddf\u4ed6\u8bf4\u4ec0\u4e48\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4f60\u4eec\u3001\u6211\u4eec\u7684\uff0c\u5e2d\u5b66\u59d0\u4e0d\u627f\u8ba4\u81ea\u5df1\u4e5f\u662f\u521b\u534f\u7684\u4e00\u5206\u5b50\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5e2d\u82e5\u7433\u5fc3\u91cc\u597d\u53d7\u4e00\u4e9b\uff0c\u83ab\u660e\u5176\u5999\u7684\uff0c\u5979\u7a81\u7136\u5bb3\u6015\u7ed9\u8fd9\u8fb9\u6392\u65a5\u5728\u5916\uff0c\u770b\u4e86\u8499\u4e50\u4e00\u773c\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9752\u5e74\u516c\u5bd3\u524d\u540e\u5341\u4e8c\u680b\u516c\u5bd3\u697c\uff0c\u6bcf\u56db\u680b\u697c\u4e00\u4e2a\u5355\u5143\uff0c\u4e16\u7eaa\u9526\u6e56\u7ed9\u5f20\u606a\u51c6\u5907\u7684\u516c\u5bd3\u5728\u4e1c\u5927\u4e1c\u5927\u95e8\u5317\u4fa7\uff0c\u7d27\u6328\u7740\u4e1c\u5927\u7814\u7a76\u751f\u516c\u5bd3\u697c\u524d\u7684\u7f51\u7403\u573a\u3002</p><p>\u3000\u3000\u76f4\u63a5\u8fdb\u4e86\u5c0f\u533a\uff0c\u6881\u519b\u5728\u516c\u5bd3\u697c\u524d\u7b49\u4ed6\u4eec\uff0c\u4e00\u8d77\u8fdb\u4e86\u6700\u4e1c\u4fa7\u7684\u516c\u5bd3\u697c\uff0c\u8bf4\u662f\u9876\u5c42\uff0c\u4f46\u662f\u4e0a\u697c\u65f6\uff0c\u6881\u519b\u5728\u7535\u68af\u91cc\u6309\u7684\u6570\u5b57\u662f\u201c11\u201d\uff0c\u8499\u4e50\u5947\u602a\u7684\u95ee\uff1a\u201c\u8fd9\u91cc\u7684\u516c\u5bd3\u697c\u4e0d\u90fd\u662f\u5341\u4e8c\u5c42\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4ece\u5341\u4e00\u5c42\u53ef\u4ee5\u4e0a\u5230\u5341\u4e8c\u697c\uff0c\u8fd8\u53ef\u4ee5\u76f4\u63a5\u4e0a\u5230\u697c\u9876\u7684\u9732\u53f0\u2026\u2026\u201d\u6881\u519b\u89e3\u91ca\u9053\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u8499\u4e50\u4e00\u65f6\u4e5f\u60f3\u8c61\u4e0d\u51fa\u5341\u4e00\u697c\u5230\u5341\u4e8c\u697c\u518d\u5230\u697c\u9876\u9732\u53f0\u7684\u60c5\u5f62\u3002\u4e5d\u4e03\u5e74\uff0c\u522b\u5885\u3001\u6d0b\u697c\u90fd\u4e0d\u662f\u4ec0\u4e48\u964c\u751f\u7684\u6982\u5ff5\uff0c\u534e\u4e1c\u5bcc\u88d5\u5730\u533a\u7684\u519c\u6751\u3001\u57ce\u4e61\u7ed3\u5408\u90e8\uff0c\u81ea\u5bb6\u5efa\u5c0f\u6d0b\u697c\u7684\u4eba\u5bb6\u5c31\u5f88\u591a\uff0c\u4f46\u662f\u9ad8\u7ea7\u7535\u68af\u516c\u5bd3\u5185\u7684\u590d\u5f0f\u5957\u623f\uff0c\u5374\u4e0d\u5e38\u89c1\u3002</p><p>\u3000\u3000\u7535\u68af\u5728\u5341\u4e00\u5c42\u524d\u505c\u4e0b\uff0c\u6253\u5f00\u95e8\u8fdb\u5165\u623f\u95f4\uff0c\u770b\u5230\u5927\u5385\u91cc\u5185\u4fa7\u7684\u8f6c\u89d2\u6276\u68af\uff0c\u8499\u4e50\u624d\u6653\u5f97\u600e\u4e48\u4ece\u5341\u4e00\u697c\u8d70\u5230\u5341\u4e8c\u697c\u4e0a\uff0c\u56de\u8fc7\u5934\u6765\u6376\u4e86\u5f20\u606a\u4e00\u62f3\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u4e2a\u4eba\u4f4f\u8fd9\u4e48\u5927\u4e00\u95f4\u623f\u5b50\uff0c\u4e5f\u592a\u5962\u4f88\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u8fb9\u7684\u5bb6\u4ff1\u90fd\u8fd8\u6ca1\u6709\u5e03\u7f6e\uff0c\u623f\u95f4\u91cc\u7a7a\u8361\u8361\u7684\uff0c\u5c31\u8fd9\u4e48\u770b\u7740\uff0c\u6ca1\u6709\u7279\u522b\u7684\u611f\u89c9\u3002\u5b59\u9759\u6aac\u89c1\u60ef\u5962\u534e\u7684\u751f\u6d3b\uff0c\u6bd4\u8d77\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u4ed6\u4eec\u6240\u611f\u53d7\u5230\u7684\u9707\u61be\uff0c\u5979\u5bf9\u8fd9\u4e9b\u8c6a\u534e\u7684\u88c5\u6f62\u98ce\u683c\u90fd\u53f8\u7a7a\u89c1\u60ef\u4e86\uff0c\u8f6c\u4e86\u4e00\u5708\uff0c\u8bf4\u9053\uff1a\u201c\u4e5f\u6ca1\u600e\u4e48\u82b1\u5fc3\u601d\u554a\uff0c\u6bd4\u4e39\u4e95\u5df7\u90a3\u8fb9\u7684\u623f\u5b50\u5dee\u8001\u8fdc\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u4f1a\u7ed9\u81ea\u5df1\u4fd7\u6c14\u7684\u5728\u623f\u95f4\u6574\u4e00\u4e2a\u6e38\u6cf3\u6c60\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u7ba1\u5b59\u9759\u6aac\u7684\u6279\u8bc4\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u89c9\u5f97\u633a\u597d\uff0c\u53c8\u6ca1\u8ba9\u4f60\u4f4f\u8fdb\u6765\uff0c\u4f60\u7684\u8bc4\u4ef7\uff0c\u6ca1\u4ec0\u4e48\u53c2\u8003\u4ef7\u503c\u2026\u2026\u884c\u4e86\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u597d\u770b\u7684\u4e86\uff0c\u201d\u63a8\u7740\u5b59\u9759\u6aac\u7684\u80a9\u8180\u5c31\u8981\u5f80\u5916\u8d70\uff0c\u201c\u8fd9\u680b\u697c\u91cc\u5176\u4ed6\u7684\u516c\u5bd3\uff0c\u4f60\u81ea\u5df1\u53bb\u9009\u4e00\u5957\u5427\uff01\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u6025\u7740\u60f3\u6211\u4eec\u8d70\uff1f\u201d\u5b59\u9759\u6aac\u72d0\u7591\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u53c8\u95ee\u6881\u519b\uff0c\u201c\u5728\u7535\u68af\u91cc\uff0c\u4f60\u8bf4\u8fd9\u91cc\u8fd8\u76f4\u901a\u697c\u9876\u7684\u9732\u53f0\uff0c\u9732\u53f0\u8fd8\u6ca1\u6709\u53bb\u770b\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9732\u53f0\u554a\uff0c\u542c\u8bf4\u4ece\u9732\u53f0\u80fd\u770b\u5230\u4e1c\u5927\u7684\u5408\u6b22\u5c71\uff0c\u6211\u8003\u8651\u662f\u4e0d\u662f\u5728\u9732\u53f0\u4e0a\u88c5\u4e00\u67b6\u5e26\u591c\u89c6\u7684\u671b\u8fdc\u955c\u2026\u2026\u8fd9\u5927\u767d\u5929\u7684\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u770b\u5934\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u56de\u5934\u770b\u4e86\u5e2d\u82e5\u7433\u4e00\u773c\uff0c\u82e5\u65e0\u5176\u4e8b\u7684\u8bf4\u9053\uff0c\u201c\u5176\u4ed6\u5012\u6ca1\u6709\u4ec0\u4e48\u4e86\u3002\u201d</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u542c\u4e86\u8138\u5fae\u5fae\u4e00\u7ea2\uff0c\u8499\u4e50\u5374\u7ed9\u5f20\u606a\u770b\u4f3c\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8bdd\u6233\u5f97\u5185\u5fc3\u6d41\u8840\uff0c\u770b\u7740\u5f20\u606a\u6324\u7709\u5f04\u773c\u671d\u4ed6\u5728\u7b11\uff0c\u4e5f\u6ca1\u6cd5\u8ba1\u8f83\u4ec0\u4e48\uff1b\u675c\u98de\u4e5f\u6ca1\u5fc3\u6ca1\u80ba\u7684\u8ddf\u7740\u7b11\u3002</p><p>\u3000\u3000\u201c\u7537\u4eba\u90fd\u4f60\u8fd9\u5fb7\u6027\uff0c\u201d\u5b59\u9759\u6aac\u5f53\u7136\u4e5f\u542c\u8bf4\u8fc7\u4e1c\u5927\u5408\u6b22\u5c71\u7684\u76db\u540d\uff0c\u89c1\u5f20\u606a\u7b11\u5bb9\u59d4\u7410\uff0c\u8fd8\u60f3\u8bf4\u201c\u4e0d\u770b\u4e5f\u7f62\u201d\uff0c\u8f6c\u8eab\u521a\u8981\u8d70\uff0c\u53c8\u505c\u4f4f\u811a\u6b65\uff0c\u5bf9\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u60f3\u6211\u770b\uff0c\u6211\u504f\u8981\u770b\u770b\u2026\u2026\u201d\u4e5f\u4e0d\u7406\u4f1a\u5f20\u606a\u90a3\u4ed8\u5927\u4e8b\u4e0d\u597d\u7684\u8868\u60c5\uff0c\u5f84\u76f4\u549a\u549a\u549a\u7684\u8df3\u7740\u6728\u5236\u697c\u68af\u4e0a\u4e86\u697c\u9876\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u82e6\u7b11\uff0c\u6ca1\u80fd\u5c06\u5b59\u9759\u6aac\u8fd9\u59ae\u5b50\u7ed9\u9a97\u8d70\uff0c\u53ea\u5f97\u4e5f\u9080\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u4e5f\u53bb\u697c\u9876\u7684\u9732\u53f0\u770b\u4e00\u773c\u3002</p><p>\u3000\u3000\u9732\u53f0\u624d\u662f\u8fd9\u680b\u623f\u5b50\u7684\u7cbe\u534e\u6240\u5728\uff0c\u5b8c\u5168\u5c31\u662f\u4e00\u5ea7\u7cbe\u81f4\u5fae\u578b\u7684\u56ed\u6797\u5f0f\u690d\u7269\u56ed\uff0c\u6620\u773c\u662f\u76ce\u7136\u7e41\u76db\u7684\u7eff\u8272\u690d\u7269\uff0c\u867d\u7136\u8282\u5b63\u5df2\u7ecf\u5165\u51ac\uff0c\u7136\u800c\u4e0d\u6653\u5f97\u4ece\u54ea\u91cc\u6536\u96c6\u6765\u7684\u5947\u82b1\u5f02\u8349\u6b63\u7e41\u76db\u7684\u751f\u957f\u5176\u95f4\uff0c\u4e5f\u6ca1\u6709\u5165\u51ac\u540e\u7684\u8427\u6761\uff0c\u9732\u53f0\u4e0a\u6709\u6728\u67b6\u51c9\u4ead\u3001\u6709\u6c34\u7011\u3001\u6709\u5360\u4e86\u8fd1\u534a\u9732\u53f0\u9762\u79ef\u7684\u6d45\u6c34\u6f6d\u3001\u6709\u6e56\u77f3\u5047\u5c71\u3001\u6709\u67d3\u7740\u9752\u82d4\u7684\u5375\u77f3\u5c0f\u5f84\u8fc2\u56de\u5176\u95f4\uff0c\u56db\u5468\u680f\u6746\u5904\u56f4\u7740\u9f50\u9888\u9ad8\u7684\u95ed\u5408\u5f0f\u7684\u6728\u680f\u6746\uff0c\u8fd9\u5de6\u53f3\u6ca1\u6709\u8d85\u8fc7\u5341\u4e8c\u5c42\u9ad8\u7684\u5efa\u7b51\uff0c\u5f88\u96be\u60f3\u8c61\u8fd9\u4e00\u5904\u516c\u5bd3\u697c\u7684\u697c\u9876\u85cf\u7740\u4e00\u5ea7\u6781\u5177\u539f\u751f\u6001\u7684\u5fae\u578b\u56ed\u6797\u3002</p><p>\u3000\u3000\u4ece\u8fd9\u9762\u5f80\u4e1c\u5357\u671b\u53bb\uff0c\u4e0d\u4ec5\u80fd\u770b\u5230\u4e1c\u5927\u6821\u56ed\u5185\u7684\u5408\u6b22\u5c71\uff0c\u8fd8\u80fd\u770b\u5230\u8fdc\u5904\u7684\u71d5\u5f52\u6e56\u4e0e\u9752\u5dcd\u5dcd\u7684\u71d5\u5f52\u5c71\u3002\u71d5\u5f52\u6e56\u4e0e\u5408\u6b22\u5c71\u4e4b\u95f4\u7684\u6742\u4e71\u8857\u666f\uff0c\u6070\u597d\u8ba9\u5408\u6b22\u5c71\u5c3d\u6570\u906e\u53bb\uff0c\u7559\u5728\u773c\u91cc\u53ea\u662f\u4e00\u5e45\u4e00\u5c18\u4e0d\u67d3\u7684\u5c71\u6c34\u753b\u3002</p><p>\u3000\u3000\u9009\u62e9\u8fd9\u680b\u516c\u5bd3\u697c\u4e5f\u662f\u6709\u8fd9\u79cd\u8003\u7a76\uff0c\u8fd9\u680b\u697c\u7684\u89d2\u5ea6\u6700\u597d\uff0c\u65e2\u5728\u9752\u5e74\u516c\u5bd3\u793e\u533a\u7684\u5185\u90e8\uff0c\u6ca1\u6709\u4e34\u8fd1\u4e1c\u534e\u5927\u9053\u7684\u55a7\u56a3\uff0c\u53c8\u5c06\u5b8c\u7f8e\u7684\u5c06\u6e56\u5c71\u98ce\u666f\u6444\u5165\u773c\u5e18\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u3001\u8499\u4e50\u8fd9\u624d\u771f\u6b63\u7ed9\u8fd9\u680b\u623f\u5b50\u7684\u5962\u534e\u4e4b\u5904\u552c\u4f4f\uff0c\u675c\u98de\u618b\u4e86\u534a\u5929\uff0c\u5fcd\u4e0d\u4f4f\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u5976\u5976\u7684\uff0c\u771f\u662f\u5962\u4f88\uff0c\u6709\u8fd9\u5730\u65b9\uff0c\u8c01\u9ad8\u5174\u53bb\u5408\u6b22\u5c71\u554a\uff1f\u201d</p><p>\u3000\u3000\u5c06\u4e00\u5ea7\u5fae\u578b\u7684\u4e2d\u5f0f\u56ed\u6797\u5b8c\u6574\u7684\u5efa\u5728\u697c\u9876\uff0c\u8981\u8003\u8651\u5230\u697c\u5b87\u7684\u627f\u91cd\u7ed3\u6784\uff0c\u82b1\u8d39\u7684\u4ee3\u4ef7\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u662f\u8fd9\u5ea7\u697c\u9876\u56ed\u6797\u81ea\u8eab\u7684\u9020\u4ef7\u3002</p><p>\u3000\u3000\u671b\u4e86\u4e00\u78a7\u5982\u6d17\u7684\u9752\u7a7a\uff0c\u5b59\u9759\u6aac\u4f38\u4e86\u4e00\u4e2a\u61d2\u8170\uff0c\u53cc\u624b\u5411\u540e\u6781\u529b\u5f20\u5f00\uff0c\u597d\u50cf\u8981\u5c06\u8fd9\u9752\u7a7a\u62e5\u5728\u6000\u91cc\uff0c\u8fc7\u4e86\u7247\u534a\u664c\uff0c\u7a81\u7136\u8f6c\u8fc7\u5934\u6765\u7741\u773c\u770b\u7740\u5f20\u606a\uff1a\u201c\u6211\u51b3\u5b9a\u4e86\uff0c\u6211\u53ea\u8981\u4e00\u4e2a\u623f\u95f4\uff01\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u8ba9\u5b59\u9759\u6aac\u505a\u90bb\u5c45\u5df2\u7ecf\u662f\u5f20\u606a\u5fcd\u8010\u7684\u6781\u9650\u4e86\uff0c\u540c\u4f4f\u4e00\u4e2a\u5c4b\u6a90\uff0c\u8c01\u53d7\u5f97\u8fd9\u7cbe\u7075\u4e00\u6837\u7684\u59ae\u5b50\u8bf1\u60d1\uff0c\u5f20\u606a\u6123\u4e86\u6709\u4e24\u79d2\u949f\uff0c\u6b63\u8981\u575a\u51b3\u7684\u62d2\u7edd\u6389\uff0c\u5b59\u9759\u6aac\u5374\u8f6c\u5934\u5bf9\u675c\u98de\u8bf4\uff1a\u201c\u8fd9\u680b\u623f\u5b50\u91cc\u6709\u56db\u95f4\u5367\u5ba4\uff0c\u8981\u4e0d\u4f60\u4e5f\u9009\u4e00\u95f4\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b8c\u5168\u6ca1\u610f\u89c1\uff0c\u201d\u675c\u98de\u8fd8\u6101\u600e\u4e48\u5f00\u53e3\u5462\uff0c\u671d\u5f20\u606a\u644a\u644a\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u6837\u4f60\u4e5f\u4e0d\u7528\u6101\u6ca1\u6cd5\u8ddf\u5510\u5a67\u89e3\u91ca\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u628a\u4f60\u4eec\u90fd\u8d76\u51fa\u53bb\uff0c\u6211\u8c01\u90fd\u4e0d\u7528\u89e3\u91ca\uff01\u201d\u5f20\u606a\u6ca1\u597d\u6c14\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u90a3\u6837\u4e5f\u4e0d\u884c\uff0c\u8fd9\u4e48\u5927\u7684\u623f\u5b50\uff0c\u6ca1\u4eba\u770b\u7740\uff0c\u8c01\u6653\u5f97\u4f60\u4f1a\u4e0d\u4f1a\u5e26\u4e4c\u4e03\u516b\u7cdf\u7684\u5973\u5b69\u5b50\u56de\u6765\u8fc7\u591c\uff01\u201d\u5b59\u9759\u6aac\u5472\u7259\u54a7\u5634\u7684\u8bf4\u9053\uff0c\u53c8\u6362\u4f5c\u4e00\u4ed8\u65e0\u8f9c\u7684\u795e\u8272\u770b\u7740\u5f20\u606a\uff0c\u201c\u8981\u4e0d\u4f60\u7ed9\u90a3\u4e2a\u516c\u5171\u5173\u7cfb\u5b66\u9662\u7684\u67d0\u67d0\u4eba\u4e5f\u7559\u4e2a\u623f\u95f4\uff0c\u5c06\u5979\u62c9\u8fdb\u6765\uff0c\u5927\u5bb6\u70ed\u70ed\u95f9\u95f9\u7684\u4f4f\u5728\u4e00\u8d77\u591a\u5f00\u5fc3\uff0c\u4f60\u4e5f\u6ca1\u5fc5\u8981\u5c06\u81ea\u5df1\u641e\u6210\u5b64\u5bb6\u5be1\u4eba\u4e00\u4e2a\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u662f\u665a\u6674\u3001\u8bb8\u601d\u3001\u5510\u5a67\u80fd\u70ed\u70ed\u95f9\u95f9\u7684\u4f4f\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u505a\u68a6\u90fd\u80fd\u7b11\u9192\uff0c\u8fd9\u65f6\u5019\u5374\u8981\u6781\u529b\u907f\u514d\u8fd9\u5904\u82b1\u4e86\u8bb8\u591a\u5fc3\u601d\u7684\u4f4f\u5904\u6ca6\u9677\u4e3a\u5408\u79df\u516c\u5bd3\uff0c\u60f3\u7740\u8bf4\u4ec0\u4e48\u8bdd\u8ba9\u5b59\u9759\u6aac\u77e5\u96be\u800c\u9000\uff0c\u6ca1\u60f3\u5230\u5b59\u9759\u6aac\u6446\u5934\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u5148\u4e0b\u53bb\u9009\u623f\u95f4\u4e86\uff0c\u4f60\u4eec\u4e24\u4e2a\u5927\u7537\u4eba\u4e5f\u4e0d\u4f1a\u597d\u610f\u601d\u8ddf\u6211\u4e89\u5427\uff1f\u201d\u8fd9\u4e48\u8bf4\u7740\uff0c\u5934\u53c8\u4f38\u8fc7\u6765\u9644\u5230\u5f20\u606a\u8033\u8fb9\u8f7b\u58f0\u8bf4\u9053\uff0c\u201c\u4f60\u8981\u4e0d\u540c\u610f\uff0c\u6211\u5c31\u544a\u8bc9\u6211\u7238\u90a3\u5929\u591c\u91cc\u662f\u4f60\u4e3b\u52a8\u543b\u6211\u7684\uff01\u201d\u8bf4\u7f62\u5c31\u4e0b\u53bb\u9009\u623f\u95f4\u4e86\uff0c\u518d\u4e5f\u4e0d\u7406\u4f1a\u5f20\u606a\u8138\u4e0a\u602a\u5f02\u7684\u795e\u60c5\u3002</p><p>\u3000\u3000\u5c31\u90a3\u5929\u7ed9\u83ab\u660e\u5176\u5999\u7684\u543b\u4e86\u4e00\u4e0b\uff0c\u8fd9\u65f6\u5019\u5c31\u8981\u201c\u4e27\u6743\u8fb1\u56fd\u201d\uff1f\u5f20\u606a\u5fc3\u60f3\u7edd\u4e0d\u80fd\u8ba9\u5b59\u5c1a\u4e49\u8ddf\u81ea\u5df1\u7ffb\u8138\uff0c\u53ea\u80fd\u6697\u81ea\u51b3\u5b9a\uff0c\u51b3\u4e0d\u80fd\u8ba9\u5b59\u9759\u6aac\u77e5\u9053\u6e56\u7554\u5c0f\u5c4b\u7684\u5b58\u5728\uff0c\u4e0d\u7136\u90fd\u65e0\u6cd5\u8ddf\u8bb8\u601d\u89e3\u91ca\u3002</p><p>\u3000\u3000\u675c\u98de\u671d\u5f20\u606a\u644a\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u5c06\u6211\u8d76\u8d70\uff0c\u6211\u4e5f\u662f\u6ca1\u6709\u610f\u89c1\u7684\uff0c\u8981\u662f\u5510\u5a67\u5230\u5efa\u90ba\u6765\uff0c\u6211\u8fd8\u662f\u53ef\u4ee5\u642c\u8fc7\u6765\u5ba2\u4e32\u51e0\u5929\uff0c\u5f53\u4f5c\u8fd9\u91cc\u771f\u7684\u4e0d\u6b62\u4f60\u4eec\u4fe9\u4eba\u5728\u4f4f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5feb\u6eda\u4e0b\u53bb\u5427\uff0c\u4e1c\u9762\u90a3\u95f4\u5e26\u4e66\u623f\u7684\u623f\u95f4\u4f60\u4eec\u8c01\u4e5f\u4e0d\u8bb8\u8ddf\u6211\u62a2\u2026\u2026\u201d\u5f20\u606a\u82e6\u7b11\u7740\u4f38\u817f\u53bb\u8e22\u675c\u98de\uff0c\u62cd\u4e86\u62cd\u7ed9\u5962\u534e\u5ead\u56ed\u7ed9\u552c\u4f4f\u7684\u8499\u4e50\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6ca1\u5fc5\u8981\u8fd9\u4e48\u4e00\u4ed8\u6ca1\u89c1\u8bc6\u7684\u6a21\u6837\uff0c\u521b\u57df\u7f51\u5427\u7ecf\u8425\u5f97\u597d\uff0c\u8fd9\u91cc\u770b\u4e0a\u53bb\u5962\u534e\uff0c\u4e5f\u5c31\u521b\u57df\u7f51\u5427\u4e24\u4e2a\u6708\u7684\u73b0\u91d1\u6d41\u91cf\u800c\u5df2\uff0c\u4e0d\u662f\u4ec0\u4e48\u9065\u4e0d\u53ef\u53ca\u7684\u68a6\u60f3\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u8499\u4e50\u8fd9\u65f6\u5019\u5f88\u60f3\u987a\u53e3\u8ddf\u5e2d\u82e5\u7433\u8bf4\u4e00\u53e5\uff1a\u201c\u8ddf\u6211\u5427\uff0c\u6211\u4e5f\u4f1a\u8ba9\u4f60\u4f4f\u8fdb\u8fd9\u6837\u7684\u623f\u5b50\u3002\u201d\u53ef\u60dc\u6ca1\u6709\u52c7\u6c14\u8bf4\u51fa\u53e3\uff0c\u53ea\u662f\u7070\u6e9c\u6e9c\u7684\u3001\u5931\u9b42\u843d\u9b44\u7684\u8ddf\u7740\u5f20\u606a\u8d70\u4e86\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u65e0\u803b\u7684\u8981\u6c42\u5c06\u6700\u540e\u4e00\u4e2a\u623f\u95f4\u8ba9\u7ed9\u4ed6\u3002</p></article>"}}}]);
