(window.webpackJsonp=window.webpackJsonp||[]).push([[890],{913:function(p){p.exports={chapterTitle:null,title:"\u7b2c887\u7ae0 \u4f73\u4eba\u6773\u65e0\u8e2a",content:"<article><h2>\u7b2c887\u7ae0 \u4f73\u4eba\u6773\u65e0\u8e2a</h2><p>\u3000\u3000\u8096\u6625\u94ed\u559c\u6b22\u7684\u6ce2\u6d6a\u5377\u5973\u5b69\u5b50\u4e94\u5b98\u5012\u4e0d\u662f\u591a\u4e48\u7684\u7cbe\u81f4\uff0c\u773c\u775b\u7a0d\u7ec6\u957f\u4e9b\uff0c\u5634\u5507\u7a0d\u539a\uff0c\u4f46\u662f\u76ae\u80a4\u96ea\u767d\u3001\u80f8\u5927\uff0c\u8fd9\u4e00\u70b9\u5f88\u53d7\u7537\u5b69\u5b50\u6b22\u8fce\u3002</p><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\uff0c\u5f20\u606a\u653e\u8361\u5f62\u9ab8\u65f6\u672c\u7740\u5154\u5b50\u4e0d\u5403\u7a9d\u8fb9\u8349\u7684\u51c6\u5219\uff0c\u5bf9\u56fd\u5546\u9662\u7684\u5973\u5b69\u5b50\u4ece\u6765\u90fd\u662f\u656c\u800c\u8fdc\u4e4b\u7684\uff0c\u5173\u952e\u662f\u5403\u4e86\u7a9d\u8fb9\u8349\uff0c\u5c31\u5f88\u96be\u4e00\u811a\u8e0f\u51e0\u53ea\u8239\u4e86\uff0c\u518d\u8bf4\u5916\u8bed\u5b66\u9662\u4ee5\u53ca\u5e08\u5927\u3001\u97f3\u4e50\u5b66\u9662\u7684\u6f02\u4eae\u5973\u5b69\u5b50\u66f4\u591a\u4e00\u4e9b\u2014\u2014\u5374\u662f\u9648\u5b81\u8ba9\u4ed6\u574f\u4e86\u8fd9\u6761\u89c4\u77e9\uff0c\u4e5f\u8ba9\u4ed6\u4ece\u6b64\u5c31\u5b89\u5206\u8d77\u6765\u2014\u2014\u53ea\u4e0d\u8fc7\u4e5d\u4e5d\u5e74\u79cb\u5929\u7684\u4e00\u4e2a\u6df1\u591c\u770b\u7740\u8fd9\u5973\u5b69\u5b50\u8138\u988a\u6302\u6ee1\u6cea\u6c34\u9a91\u8f66\u7ecf\u8fc7\u6cd5\u68a7\u6850\u906e\u95ed\u7684\u6821\u897f\u8def\uff0c\u6240\u4ee5\u5bf9\u8fd9\u4e2a\u53eb\u5f90\u6885\u6885\u7684\u5973\u5b69\u5b50\u7559\u4e0b\u4e86\u4e9b\u5370\u8c61\u3002\u81f3\u4e8e\u5979\u4e3a\u4f55\u6df1\u591c\u843d\u6cea\u9a91\u8f66\u8fd4\u6821\uff0c\u4e5f\u6709\u79cd\u79cd\u4f20\u8a00\u4ece\u5973\u751f\u90a3\u91cc\u4f20\u51fa\u6765\uff0c\u65e0\u975e\u7231\u6155\u865a\u8363\u5728\u5916\u9762\u5403\u4e86\u4e8f\u4e4b\u7c7b\u7684\u4f20\u8a00\uff0c\u53ea\u662f\u5404\u79cd\u4f20\u8a00\u4e2d\u5403\u4e8f\u7684\u5bf9\u8c61\u7a0d\u6709\u4e0d\u540c\u7f62\u4e86\u3002</p><p>\u3000\u3000\u5f90\u6885\u6885\u4e4d\u7136\u770b\u89c1\u5f20\u606a\u3001\u675c\u98de\u56fd\u5546\u9662\u8d6b\u8d6b\u6709\u540d\u7684\u4f20\u5947\u7537\u751f\u5750\u5728\u524d\u6392\uff0c\u987f\u65f6\u62d8\u675f\u8d77\u6765\u3002\u675c\u98de\u8fd8\u597d\u4e00\u4e9b\uff0c\u5e73\u65e5\u5728\u516c\u5171\u8bfe\u6559\u5ba4\u91cc\u8fd8\u5076\u5c14\u80fd\u9047\u89c1\uff0c\u53ea\u662f\u8fd9\u5f20\u606a\u2014\u2014\u65e0\u8bba\u662f\u5973\u751f\u5bbf\u820d\u697c\u6c14\u7403\u4e8b\u4ef6\uff0c\u8fd8\u662f\u5b66\u5e9c\u5df7\u51fa\u624b\u6bb4\u6253\u97e9\u56fd\u7559\u5b66\u751f\u4e8b\u4ef6\uff0c\u8fd8\u662f\u5475\u65a5\u540d\u6559\u6388\u5468\u747e\u73ba\u4f7f\u5176\u5f53\u5802\u5410\u8840\uff0c\u8fd8\u662f\u53d1\u5e16\u63ed\u9732\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e8b\u4ef6\u90fd\u8ba9\u4ed6\u58f0\u540d\u8d6b\u8d6b\u6216\u8005\u8bf4\u58f0\u540d\u72fc\u7c4d\u5bcc\u6709\u66f4\u591a\u7684\u4f20\u5947\u8272\u5f69\u2014\u2014\u56fd\u5546\u9662\u5012\u67099\u6210\u7684\u5973\u751f\u53ea\u77e5\u5176\u540d\u4e0d\u8bc6\u5176\u4eba\uff0c\u5c31\u662f\u4e5d\u4e03\u7ecf\u6d4e\u5b66\u4e13\u4e1a\u7684\u5973\u751f\u4e5f\u672a\u514d\u90fd\u77e5\u9053\u8fd9\u5bb6\u4f19\u539f\u6765\u5c31\u662f\u90a3\u5bb6\u4f19\u554a\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8ddf\u4ed6\u4eec\u8ba4\u8bc6\u7684\uff1f\u201d\u5f90\u6885\u6885\u4f4e\u58f0\u7684\u95ee\u8096\u6625\u94ed\u3002</p><p>\u3000\u3000\u8096\u6625\u94ed\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\uff0c\u675c\u98de\u56de\u8fc7\u5934\u6765\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u9ad8\u4e2d\u65f6\u5c31\u662f\u540c\u73ed\u540c\u5b66\uff0c\u4f60\u8bf4\u6211\u4eec\u662f\u600e\u4e48\u8ba4\u8bc6\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5f00\u8f66\u5230\u5b66\u5e9c\u5df7\u5c06\u675c\u98de\u4e22\u4e0b\u53bb\uff0c\u53c8\u5c06\u8f66\u62d0\u5230\u97f3\u4e50\u5b66\u9662\u6821\u95e8\u53e3\uff0c\u5b59\u9759\u6aac\u7a7f\u7740\u957f\u88d9\u5a77\u5a77\u7389\u7acb\u7684\u5728\u6821\u95e8\u53e3\u63a2\u5934\u63a2\u8111\u7b49\u5f20\u606a\u8fc7\u6765\u63a5\u5979\u3002\u5728\u5f20\u606a\u524d\u9762\u6709\u4e00\u8f86\u9ed1\u8272\u9ad8\u5c14\u592b\u505c\u4e0b\u6765\uff0c\u6709\u4e2a\u9752\u5e74\u7537\u4eba\u4ece\u8f66\u7a97\u91cc\u63a2\u51fa\u5934\u6765\u8ddf\u5b59\u9759\u6aac\u642d\u8baa\uff0c\u5f20\u606a\u8981\u770b\u5b59\u9759\u6aac\u7684\u7b11\u8bdd\uff0c\u5c31\u6ca1\u6709\u6025\u7740\u5c06\u8f66\u5f00\u8fc7\u53bb\uff0c\u5374\u4e0d\u6653\u5f97\u5b59\u9759\u6aac\u8ddf\u90a3\u7537\u4eba\u8bf4\u4e86\u4ec0\u4e48\uff0c\u90a3\u7537\u4eba\u5f88\u5feb\u5c31\u7f29\u56de\u5934\u7070\u6e9c\u6e9c\u7684\u5f00\u8f66\u8dd1\u4e86\uff0c\u5f20\u606a\u5fc3\u60f3\u8fd9\u4e2b\u5934\u4e0d\u4f1a\u53c8\u66b4\u7c97\u53e3\u5c06\u4eba\u5413\u8d70\u4e86\u5427\uff1f</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8fd9\u624d\u6ce8\u610f\u5230\u5f20\u606a\u6362\u4e86\u4e00\u8f86\u8f66\uff0c\u5f20\u606a\u5e2e\u5979\u6253\u5f00\u8f66\u95e8\uff0c\u5979\u5750\u4e0a\u8f66\u95ee\u5f20\u606a\uff1a\u201c\u4f60\u7684\u90a3\u4e9b\u8ddf\u73ed\u5462\uff1f\u201d\u53c8\u9aa4\u7136\u770b\u5230\u5750\u5728\u540e\u6392\u7684\u8096\u6625\u94ed\u3001\u5f90\u6885\u6885\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8ba4\u8bc6\u8096\u6625\u94ed\uff0c\u56de\u8fc7\u5934\u671d\u4ed6\u4eec\u626c\u4e86\u626c\u96ea\u767d\u7684\u5c0f\u624b\u7b97\u662f\u6253\u8fc7\u62db\u547c\uff1a\u201c\u4f60\u4eec\u4e5f\u4e00\u8d77\u53bb\u901b\u8857\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u95ee\u5979\u8ddf\u90a3\u4e9b\u642d\u8baa\u7684\u9752\u5e74\u8bf4\u4ec0\u4e48\u8bdd\u4e86\uff0c\u5b59\u9759\u6aac\u5c06\u624b\u888b\u9694\u4eea\u8868\u76d8\u4e0a\uff0c\u4e0d\u5c51\u7684\u8bf4\u9053\uff1a\u201c\u6211\u5c31\u5c06\u5305\u6253\u5f00\u6765\u7ed9\u4ed6\u770b\u4e86\u4e24\u773c\uff0c\u95ee\u4ed6\u5305\u4e0d\u5305\u5f97\u8d77\u6211\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u8bed\u4e86\uff0c\u4e0d\u80fd\u62ff\u6b63\u5e38\u601d\u7ef4\u63e3\u6d4b\u8fd9\u4e2b\u5934\u2014\u2014\u5012\u662f\u6e05\u4e3d\u660e\u8273\u7684\u5b59\u9759\u6aac\u5750\u8fdb\u8f66\u4e4b\u540e\uff0c\u5f90\u6885\u6885\u53d8\u5f97\u6c89\u9ed8\u8d77\u6765\uff0c\u4e00\u4e2a\u5f88\u91cd\u89c6\u81ea\u5df1\u76f8\u8c8c\u7684\u5973\u5b69\u5b50\u5728\u6bd4\u5979\u66f4\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u9762\u524d\uff0c\u901a\u5e38\u90fd\u4f1a\u6709\u4e9b\u4e0d\u5927\u81ea\u4fe1\u3002</p><p>\u3000\u3000\u4e5f\u6ca1\u6709\u8bf4\u5230\u4e86\u65b0\u5e02\u8857\u5c31\u5c06\u8096\u6625\u94ed\u3001\u5f90\u6885\u6885\u4e24\u4eba\u8e22\u5f00\uff0c\u4e00\u8d77\u5728\u80af\u5fb7\u57fa\u5403\u8fc7\u6d0b\u5feb\u9910\u2014\u2014\u5b59\u9759\u6aac\u559c\u6b22\uff0c\u8fd9\u79cd\u5c0f\u4e8b\u5f20\u606a\u603b\u662f\u8981\u968f\u5979\u7684\uff1b\u53c8\u4e00\u8d77\u4e1c\u65b9\u56fd\u9645\u5e7f\u573a\u4e3a\u660e\u5929\u536b\u5170\u4ee5\u53ca\u536b\u5170\u5979\u7238\u5988\u2014\u2014\u4ed6\u5988\u4e5f\u8981\u8ddf\u7740\u5230\u5efa\u90ba\u6765\u2014\u2014\u8d2d\u7f6e\u4e9b\u751f\u6d3b\u7528\u54c1\u3002\u8fd9\u4e9b\u4e8b\u60c5\u5f53\u7136\u53ef\u4ee5\u6253\u53d1\u5de5\u4f5c\u4eba\u5458\u53bb\u505a\uff0c\u4f46\u662f\u4e8b\u60c5\u90fd\u8ba9\u522b\u4eba\u4ee3\u52b3\uff0c\u751f\u6d3b\u8fd8\u6709\u4ec0\u4e48\u4e50\u8da3\uff1f</p><p>\u3000\u3000\u5728\u4e1c\u65b9\u5e7f\u573a\u4e70\u5b8c\u4e1c\u897f\u90fd\u8fc7\u4e5d\u70b9\u4e86\uff0c\u5c31\u76f4\u63a5\u5f00\u8f66\u56de\u4e86\u4e1c\u5927\uff0c\u5728\u9752\u5e74\u516c\u5bd3\u524d\u9047\u5230\u5e2d\u82e5\u7433\uff0c\u8ba9\u5e2d\u82e5\u7433\u5e2e\u7740\u9759\u6aac\u4e00\u8d77\u5c06\u4e1c\u897f\u62ff\u4e0a\u697c\u53bb\uff0c\u5f20\u606a\u5f00\u8f66\u5148\u9001\u5f90\u6885\u6885\u56de\u5bbf\u820d\uff0c\u5c06\u8f66\u505c\u5728\u5408\u6b22\u5c71\u811a\u4e0b\uff0c\u89c1\u8096\u6625\u94ed\u6574\u665a\u4e0a\u90fd\u6ca1\u7cbe\u6253\u91c7\u7684\uff0c\u7b11\u4e86\u7b11\uff0c\u638f\u51fa\u70df\u6765\u9012\u7ed9\u4ed6\u3002</p><p>\u3000\u3000\u8096\u6625\u94ed\u5728\u8f66\u91cc\u62bd\u7740\u95f7\u70df\uff0c\u8fc7\u4e86\u7247\u664c\u95ee\u5f20\u606a\uff1a\u201c\u662f\u4e0d\u662f\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u90fd\u8fd9\u4e48\u7231\u6155\u865a\u8363\uff1f\u201d</p><p>\u3000\u3000\u521a\u597d\u6709\u4e00\u7537\u4e00\u5973\u624b\u7275\u7740\u624b\u4ece\u8f66\u524d\u7ecf\u8fc7\u4e0a\u5408\u6b22\u5c71\u4e0a\u53bb\uff0c\u5f20\u606a\u671d\u8f66\u7a97\u5916\u5476\u4e86\u5476\u5634\uff0c\u95ee\u8096\u6625\u94ed\uff1a\u201c\u8fd9\u5973\u5b69\u5b50\u8981\u662f\u559c\u6b22\u4f60\uff0c\u4f60\u4f1a\u4e0d\u4f1a\u4e5f\u559c\u6b22\u4eba\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u90a3\u5973\u5b69\u5b50\u8eab\u6750\u5012\u662f\u4e0d\u9519\uff0c\u6070\u597d\u56de\u8fc7\u5934\u6765\u770b\u4ed6\u4eec\u7684\u8f66\uff0c\u6ee1\u8138\u9752\u6625\u75d8\uff0c\u8096\u6625\u94ed\u7ed9\u5413\u4e00\u54c6\u55e6\uff0c\u6447\u4e86\u6447\u5934\uff1a\u201c\u6211\u662f\u90a3\u79cd\u81ea\u8c11\u7684\u4eba\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u8fd8\u8981\u4e0d\u8981\u6211\u56de\u7b54\u4f60\u521a\u624d\u63d0\u51fa\u7684\u95ee\u9898\uff1f\u201d</p><p>\u3000\u3000\u8096\u6625\u94ed\u5fae\u5fae\u4e00\u6123\uff0c\u4fc4\u5c14\u60f3\u660e\u767d\u5f20\u606a\u5728\u8bf4\u4ec0\u4e48\uff0c\u5fc3\u60f3\u8bf4\u5973\u4eba\u7231\u6155\u865a\u8363\uff0c\u7537\u4eba\u53c8\u6709\u4f55\u4e0d\u540c\uff1f\u81ea\u5df1\u771f\u662f\u6ca1\u8d44\u683c\u62b1\u6028\u554a\uff01</p><p>\u3000\u3000\u5f20\u606a\u53c8\u81ea\u8a00\u81ea\u8bed\u3001\u5e26\u7740\u4e9b\u6076\u8da3\u5473\u7684\u8bf4\u9053\uff1a\u201c\u6bd4\u8d77\u6f02\u4eae\u7684\u5973\u5b69\u5b50\u90fd\u7231\u6155\u865a\u8363\uff0c\u66f4\u8ba9\u4eba\u96be\u4ee5\u5bb9\u5fcd\u7684\u662f\u90a3\u4e9b\u957f\u5f97\u4e0d\u548b\u6837\u7684\u5973\u5b69\u5b50\u4e5f\u7231\u6155\u865a\u8363\u2026\u2026\u201d\u62cd\u4e86\u62cd\u8096\u6625\u94ed\u7684\u80a9\u8180\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u597d\u4e86\uff0c\u8eb2\u5728\u5bbf\u820d\u770bA\u7247\u3001\u8eb2\u5728\u88ab\u7a9d\u91cc\u770b\u9ec4\u8272\u5c0f\u8bf4\u53c8\u5e7b\u60f3\u7740\u7eaf\u6d01\u5f97\u5bb9\u4e0d\u4e0b\u4e00\u70b9\u6742\u8d28\u7684\u7231\u60c5\u662f\u8ba9\u4eba\u6210\u719f\u4e0d\u4e86\u7684\u2014\u2014\u7231\u60c5\u8fd9\u4e1c\u897f\u8ddf\u9b3c\u4f3c\u7684\uff0c\u5f88\u591a\u4eba\u90fd\u4fe1\uff0c\u4f46\u8c01\u4e5f\u6ca1\u89c1\u5230\u8fc7\u2014\u2014\u4e0d\u8fc7\u6709\u4e9b\u771f\u76f8\u4e5f\u4e0d\u662f\u4f60\u6240\u60f3\u8c61\u7684\u90a3\u4e48\u6050\u6016\uff0c\u4e0d\u662f\u8bf4\u6c34\u81f3\u6e05\u5219\u65e0\u9c7c\u3001\u4eba\u81f3\u5bdf\u5219\u5f92\u5417\uff1f\u4e0d\u8bf4\u662f\u5973\u5b69\u5b50\u6709\u4e9b\u7231\u6155\u865a\u8363\u5c31\u4e00\u65e0\u662f\u5904\u7684\uff0c\u4f55\u53bb\u4f55\u4ece\uff0c\u4f60\u81ea\u5df1\u601d\u91cf\u6e05\u695a\u2026\u2026\u54e6\uff0c\u5bf9\u4e86\uff0c\u9662\u91cc\u8981\u63a5\u65b0\u751f\uff0c\u7b97\u6211\u4e00\u4e2a\uff0c\u6b63\u597d\u8981\u6253\u53d1\u4e00\u4e0b\u65e0\u804a\u65f6\u95f4\uff0c\u4f60\u5e2e\u6211\u8ddf\u9646\u5929\u53c8\u8bf4\u4e00\u4e0b\u2026\u2026\u201d\u8bf4\u7f62\u5c31\u5c06\u8096\u6625\u94ed\u8d76\u4e0b\u53bb\uff0c\u4ed6\u53c8\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u4eba\u5750\u5728\u8f66\u91cc\uff0c\u770b\u7740\u5408\u6b22\u5c71\u6811\u4e1b\u91cc\u4e00\u5bf9\u5bf9\u8eab\u5f71\u62bd\u4e86\u4e24\u6839\u70df\uff0c\u5fc3\u60f3\uff1a\u521a\u521a\u6559\u8bad\u8096\u6625\u94ed\u5012\u662f\u6709\u677f\u6709\u773c\u7684\uff0c\u81ea\u5df1\u4e0d\u662f\u4e00\u76f4\u90fd\u56e0\u4e3a\u65e0\u6cd5\u786e\u77e5\u9648\u5b81\u79bb\u5f00\u81ea\u5df1\u7684\u539f\u56e0\u800c\u4e00\u76f4\u65e0\u6cd5\u91ca\u6000\u5417\uff1f\u62bd\u4e86\u4e24\u6839\u70df\uff0c\u5c06\u70df\u5934\u5f39\u843d\u5230\u8def\u65c1\u7684\u8349\u4e1b\u91cc\uff0c\u6076\u72e0\u72e0\u7684\u60f3\uff0c\u70e7\u4e00\u628a\u706b\u5c06\u5408\u6b22\u5c71\u7684\u6811\u4e1b\u70e7\u4e2a\u7cbe\u5149\u624d\u597d\uff0c\u542f\u52a8\u8f66\u51fa\u4e86\u6821\u4e1c\u95e8\u56de\u5230\u9752\u5e74\u516c\u5bd3\u91cc\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u521a\u5c06\u4eca\u5929\u65b0\u751f\u7684\u7269\u54c1\u653e\u7f6e\u597d\uff0c\u770b\u89c1\u5f20\u606a\u8fdb\u6765\uff0c\u6b6a\u7740\u8111\u888b\u770b\u7740\u4ed6\uff1a\u201c\u4f60\u597d\u50cf\u5728\u671f\u5f85\u7740\u4ec0\u4e48\u4f3c\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u671f\u5f85\u4ec0\u4e48\uff1f\u6211\u671f\u5f85\u536b\u5170\u59d0\u8fc7\u6765\u2026\u2026\u201d\u5f20\u606a\u624b\u6258\u7740\u5b59\u9759\u6aac\u5ae9\u6ed1\u7684\u4e0b\u5df4\u5c06\u5979\u7684\u8111\u888b\u5b50\u6273\u6b63\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\u4f60\u8111\u5b50\u91cc\u5728\u60f3\u4ec0\u4e48\u4e1c\u897f\uff0c\u201d\u5b59\u9759\u6aac\u72d0\u7591\u7684\u5b9a\u775b\u770b\u4e86\u5f20\u606a\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff0c\u201c\u603b\u662f\u611f\u89c9\u4f60\u6709\u4e9b\u4e0d\u5bf9\u52b2\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u867d\u8bf4\u8fd9\u8fb9\u5e2e\u536b\u5170\u51c6\u5907\u4e86\u8bb8\u591a\u4e1c\u897f\uff0c\u4e0d\u8fc7\u536b\u5170\u4ece\u65b0\u829c\u8fc7\u6765\u65f6\uff0c\u8fd8\u662f\u5e26\u9f50\u4e86\u5bb6\u4f19\u8fc7\u6765\uff0c\u9664\u4e86\u6d17\u6f31\u7528\u54c1\u5916\uff0c\u8fd8\u6709\u5c06\u51ac\u5929\u7684\u88ab\u8925\u3001\u539a\u68c9\u88ab\u5b50\u5e26\u4e86\u4e00\u5e8a\u8fc7\u6765\u3002</p><p>\u3000\u3000\u536b\u5170\u5979\u7238\u662f\u8001\u5b9e\u5df4\u4ea4\u7684\u5de5\u4eba\uff0c\u536b\u5a76\u5230\u5e95\u662f\u5728\u5f20\u5bb6\u505a\u8fc7\u8fd1\u4e24\u5e74\u7684\u5de5\uff0c\u867d\u7136\u7638\u811a\uff0c\u4e00\u8eab\u7d20\u51c0\uff0c\u6536\u62fe\u5f97\u5e72\u51c0\uff0c\u8fd8\u6709\u4e9b\u5f90\u5a18\u534a\u8001\u7684\u611f\u89c9\uff0c\u536b\u5170\u8138\u67b6\u5b50\u968f\u5979\u5988\uff0c\u80fd\u60f3\u8c61\u536b\u5a76\u5e74\u9752\u65f6\u9664\u4e86\u7638\u817f\u4e4b\u5916\uff0c\u4e5f\u662f\u7f8e\u4eba\u513f\u4e00\u4e2a\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u662f\u653e\u5fc3\u4e0d\u4e0b\u5f20\u606a\uff0c\u624d\u501f\u9001\u536b\u5170\u4e0a\u5b66\u7684\u673a\u4f1a\u5230\u5efa\u90ba\u6765\u4f4f\u4e00\u665a\u4e0a\uff0c\u4f4f\u4e86\u4e00\u665a\u4e0a\u7b2c\u4e8c\u5929\u5c31\u56de\u65b0\u829c\u4e86\uff0c\u536b\u5170\u5012\u662f\u966a\u7740\u5979\u7238\u5988\u5728\u5efa\u90ba\u73a9\u4e86\u4e09\u5929\uff0c\u4e00\u76f4\u5230\u6b63\u5f0f\u5f00\u5b66\u524d\u4e00\u5929\u624d\u9001\u5979\u7238\u5988\u5750\u4e0a\u56de\u65b0\u829c\u7684\u6c7d\u8f66\u3002</p><p>\u3000\u3000\u536b\u5170\u9ad8\u4e2d\u8f8d\u5b66\u5de5\u4f5c\u8fc7\u4e24\u5e74\uff0c\u53c8\u662f\u8ddf\u7740\u7fdf\u4e39\u9752\uff0c\u5f00\u5b66\u62a5\u540d\u8fd9\u79cd\u5c0f\u4e8b\u81ea\u7136\u4e0d\u7528\u5bb6\u4eba\u966a\u540c\uff0c\u5f20\u606a\u62a5\u540d\u53c2\u52a0\u4e5d\u6708\u4e94\u65e5\u3001\u516d\u65e5\u7684\u56fd\u5546\u9662\u63a5\u65b0\u751f\u5de5\u4f5c\uff0c\u8fd8\u5f97\u6258\u9646\u5929\u53c8\u5982\u4eca\u662f\u9662\u5b66\u751f\u4f1a\u4e3b\u5e2d\uff0c\u4ed6\u624d\u80fd\u8ddf\u95f2\u4eba\u4f3c\u7684\u5750\u5728\u5357\u6821\u53e3\u5185\u4e3b\u5e72\u9053\u4e24\u4fa7\u7684\u65b0\u751f\u63a5\u5f85\u5904\u5019\u7740\u3002</p><p>\u3000\u3000\u4e5d\u6708\u516d\u65e5\u4e0b\u5348\uff0c\u90a3\u4e2a\u9752\u6da9\u7684\u5c11\u5e74\u63d0\u7740\u884c\u56ca\u602f\u751f\u751f\u7684\u7ad9\u5230\u4e09\u5f20\u957f\u684c\u62fc\u8d77\u6765\u5c31\u5145\u5f53\u8d77\u7684\u63a5\u5f85\u5904\u524d\uff0c\u5f20\u606a\u6ca1\u6709\u5728\u4ed6\u8eab\u8fb9\u770b\u5230\u90a3\u4e2a\u7a1a\u6c14\u672a\u8131\u3001\u773c\u7738\u7075\u52a8\u7684\u5c11\u5973\uff0c\u5f3a\u4f5c\u9547\u5b9a\u7684\u5fc3\u795e\u9aa4\u7136\u95f4\u614c\u4e71\u8d77\u6765\u2026\u2026</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u8ba4\u8bc6\u4f60\u2026\u2026\u201d\u90a3\u5c11\u5e74\u770b\u5230\u5f20\u606a\u4e5f\u53d1\u4e86\u4e00\u4f1a\u513f\u6123\uff0c\u89c9\u5f97\u751a\u81f3\u662f\u719f\u6089\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u624d\u9aa4\u7136\u60f3\u8d77\u4e24\u5e74\u524d\u9ec4\u660f\u7684\u96e8\u540e\uff0c\u60ca\u8bb6\u7684\u6307\u7740\u5f20\u606a\u8f7b\u547c\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u611f\u89c9\u5230\u81ea\u5df1\u7684\u624b\u6307\u5728\u6253\u98a4\uff0c\u4ed6\u5f3a\u5fcd\u7740\u3001\u6781\u529b\u63a7\u5236\u7740\u4e0d\u53bb\u95ee\u4ed6\u9648\u5b81\u4e3a\u4ec0\u4e48\u6ca1\u6709\u51fa\u73b0\uff1f\u5728\u8fd9\u4e2a\u65f6\u7a7a\uff0c\u81ea\u5df1\u662f\u7edd\u6ca1\u6709\u9053\u7406\u8ba4\u8bc6\u90a3\u4e2a\u7a1a\u6c14\u672a\u8131\u3001\u773c\u7738\u7075\u52a8\u7684\u5c11\u5973\u7684\u3002</p><p>\u3000\u3000\u662f\u751f\u75c5\u4e86\uff0c\u8fd8\u662f\u9ad8\u8003\u6ca1\u8003\u597d\uff1f\u96be\u9053\u81ea\u5df1\u9006\u8f6c\u4e86\u9648\u5b81\u7684\u547d\u8fd0\uff0c\u60f3\u8c61\u4e0d\u51fa\u9ad8\u8003\u843d\u699c\u5bf9\u5fc3\u9ad8\u6c14\u50b2\u7684\u9648\u5b81\u6709\u600e\u6837\u7684\u6253\u51fb\uff0c\u96be\u9053\u4eca\u751f\u4e24\u4eba\u7684\u8f68\u8ff9\u6ce8\u5b9a\u662f\u8981\u9519\u5f00\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8111\u5b50\u91cc\u4e71\u4f5c\u4e00\u56e2\uff0c\u4e5f\u65e0\u6687\u7ed9\u8fd9\u5c11\u5e74\u4e00\u4e2a\u5584\u610f\u7684\u7b11\u5bb9\uff0c\u4e5f\u60f3\u4e0d\u5230\u8981\u8ddf\u8096\u6625\u94ed\u8bf4\u4e00\u58f0\u2014\u2014\u4ed6\u5728\u8fd9\u91cc\uff0c\u5728\u68a7\u6850\u6811\u4e0b\uff0c\u7a7a\u7b49\u4e86\u4e24\u5929\uff0c\u7adf\u662f\u8fd9\u6837\u4e00\u4e2a\u4eba\u751f\u8f68\u8ff9\u7ed9\u9519\u5f00\u7684\u7ed3\u5c40\uff0c\u5f20\u606a\u5931\u9b42\u843d\u9b44\u7684\u63a8\u684c\u7ad9\u4e86\u8d77\u6765\uff0c\u671d\u6821\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u8d70\u51fa\u6821\u95e8\uff0c\u9a6c\u6d77\u9f99\u51fa\u73b0\u5728\u4ed6\u9762\u524d\uff0c\u95ee\u4ed6\u8981\u53bb\u54ea\u91cc\uff0c\u5f20\u606a\u544a\u8bc9\u4ed6\u81ea\u5df1\u5fc3\u60c5\u6709\u4e9b\u4e0d\u597d\uff0c\u4e0d\u60f3\u6709\u4eba\u8ddf\u7740\u3002</p><p>\u3000\u3000\u4eba\u751f\u80fd\u91cd\u8d70\u4e00\u8d9f\uff0c\u5f20\u606a\u5411\u6765\u4ee5\u4e3a\u662f\u4f55\u5176\u5e78\u8fd0\u7684\u4e8b\u60c5\uff0c\u5bf9\u524d\u5c18\u5f80\u4e16\u523b\u9aa8\u5165\u9ad3\u7684\u9057\u61be\uff0c\u4e5f\u62b1\u4ee5\u968f\u7f18\u7684\u59ff\u6001\uff0c\u5374\u662f\u6ca1\u6709\u60f3\u5230\u771f\u4e34\u5176\u5883\uff0c\u7ec8\u662f\u653e\u4e0d\u5f00\uff0c\u7ec8\u662f\u653e\u4e0d\u5f00\uff0c\u90a3\u524d\u5c18\u5f80\u4e8b\u7684\u8bb0\u5fc6\uff0c\u5728\u8fd9\u4e2a\u65f6\u7a7a\u672a\u66fe\u53d1\u751f\u3001\u4e5f\u4e0d\u4f1a\u53d1\u751f\u7684\u8bb0\u5fc6\u5982\u6e83\u5824\u7684\u91d1\u5c71\u6e56\u6c34\u5954\u51b3\u800c\u6765\uff0c\u963b\u4e5f\u963b\u6321\u4e0d\u4f4f\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5b66\u5e9c\u5df7\u6539\u5934\u6362\u9762\uff0c\u4f46\u4e1c\u5927\u3001\u97f3\u4e50\u5b66\u9662\u3001\u5e08\u5927\u5468\u906d\u5e76\u6ca1\u4ec0\u4e48\u5f7b\u5e95\u7684\u6539\u53d8\uff0c\u5fc3\u91cc\u7a7a\u843d\u843d\u7684\u8d70\u4e86\u8bb8\u4e45\uff0c\u4e5f\u4e0d\u6653\u5f97\u8d70\u5230\u54ea\u91cc\uff0c\u770b\u5230\u4e00\u5904\u5c0f\u5e97\uff0c\u60f3\u8d77\u66fe\u4e0e\u9648\u5b81\u5728\u6b64\u5c31\u9910\u8fc7\uff0c\u8d70\u4e86\u8fdb\u53bb\uff0c\u70b9\u4e86\u5bab\u4fdd\u9e21\u4e01\uff0c\u70b9\u4e86\u5bb6\u5e38\u8c46\u8150\uff0c\u4e5f\u770b\u4e0d\u5230\u8fd9\u5bb6\u5e97\u91cc\u8fd8\u6709\u4ec0\u4e48\u662f\u5979\u559c\u6b22\u5403\u7684\u83dc\uff0c\u53c8\u8981\u4e00\u4e9b\u5564\u9152\uff0c\u6162\u6162\u559d\u8d77\u6765\u3002\u559d\u4e86\u56db\u74f6\u6216\u662f\u4e94\u74f6\uff0c\u5c31\u5934\u6655\u76ee\u7729\uff0c\u624b\u811a\u66f4\u662f\u51b0\u5f97\u5389\u5bb3\uff0c\u5fc3\u810f\u4e5f\u7830\u7830\u4e71\u8df3\uff0c\u597d\u50cf\u8fd9\u4ed8\u8eab\u4f53\u5feb\u4e0d\u884c\u4e86\uff0c\u731b\u7136\u95f4\u770b\u7740\u5e97\u95e8\u524d\u6709\u4e2a\u719f\u6089\u7684\u80cc\u5f71\u8d70\u8fc7\u53bb\uff0c\u867d\u662f\u80cc\u666f\uff0c\u5374\u89c9\u5f97\u6709\u7740\u8bb0\u5fc6\u6df1\u5904\u7684\u719f\u6089\uff0c\u7591\u662f\u82b1\u4e86\u773c\u775b\uff0c\u60f3\u4e5f\u6ca1\u60f3\u5c31\u8ffd\u4e86\u51fa\u53bb\u2026\u2026</p></article>"}}}]);
