(window.webpackJsonp=window.webpackJsonp||[]).push([[897],{920:function(p){p.exports={chapterTitle:null,title:"\u7b2c718\u7ae0 \u7f57\u541b\u7684\u9700\u8981",content:"<article><h2>\u7b2c718\u7ae0 \u7f57\u541b\u7684\u9700\u8981</h2><p>\u3000\u3000\u6709\u8f66\u5148\u9001\u8bb8\u601d\u56de\u6e56\u7554\u5c0f\u5c4b\uff0c\u5f20\u606a\u8fd8\u57281978\u591a\u559d\u4e86\u4e00\u4f1a\u513f\u9152\uff0c\u624d\u6162\u60a0\u60a0\u7684\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u4eca\u665a\u7b97\u662f\u8868\u8fc7\u6001\u4e86\uff0c\u4e16\u7eaa\u9526\u6e56\u7684\u8463\u4e8b\u4f1a\u8bae\u660e\u5929\u8fd8\u8981\u7ee7\u7eed\u4e0b\u53bb\uff0c\u5f20\u606a\u5c31\u53ef\u4ee5\u5077\u61d2\u4e0d\u53c2\u52a0\u4e86\u3002</p><p>\u3000\u3000\u6628\u5929\u591c\u91cc\u96e8\u52a0\u96ea\uff0c\u4eca\u5929\u5374\u6708\u6717\u661f\u7a00\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u62ff\u51fa\u4e00\u74f6\u7ea2\u9152\uff0c\u5750\u5728\u9601\u697c\u4e0a\u770b\u7740\u7a97\u5916\u85cf\u6709\u4e07\u5343\u94f6\u5e01\u7684\u6e56\u6c34\u4e0e\u8fdc\u5904\u71d5\u5f52\u5c71\u7684\u9ed1\u5f71\u3002</p><p>\u3000\u3000\u201c\u5b9e\u5728\u4e0d\u884c\uff0c\u6211\u4ece\u4e16\u7eaa\u9526\u6e56\u91cc\u64a4\u51fa\u6765\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6015\u6211\u4f1a\u89c9\u5f97\u9ebb\u70e6\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u53c8\u6447\u4e86\u6447\u5934\u8bf4\u9053\uff0c\u201c\u6211\u6ca1\u6709\u8fd9\u4e48\u89c9\u5f97\u8fc7\uff0c\u90b5\u81f3\u521a\u6709\u53e5\u8bdd\u8fd8\u662f\u86ee\u5408\u6211\u7684\u5fc3\u610f\u7684\uff0c\u843d\u5728\u522b\u4eba\u624b\u91cc\uff0c\u8fd8\u4e0d\u5982\u843d\u5728\u4e16\u7eaa\u9526\u6e56\u624b\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u6e05\u695a\uff0c\u8fd9\u4e2a\u8ba1\u5212\uff0c\u6ca1\u6709\u4f60\u7684\u652f\u6301\u6839\u672c\u4e0d\u884c\uff0c\u4f46\u662f\u4f60\u7ad9\u51fa\u6765\u652f\u6301\uff0c\u5374\u4f1a\u8ba9\u522b\u4eba\u5c06\u77db\u5934\u6307\u5411\u9526\u6e56\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u5173\u7cfb\uff0c\u6211\u4e5f\u662f\u4e0d\u6015\u60f9\u9ebb\u70e6\u7684\u4e3b\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u77bb\u524d\u987e\u540e\u4e5f\u505a\u4e0d\u6210\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6709\u4e9b\u51f6\u9669\u4e0d\u662f\u60f3\u907f\u5c31\u907f\u5f97\u5f00\u7684\u3002\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u7f57\u541b\u3001\u738b\u7ef4\u5747\u8ddf\u6211\u4eec\u8fd8\u662f\u7ad9\u5728\u540c\u4e00\u9635\u8425\u7684\uff0c\u5c31\u7b97\u7f57\u541b\u9ad8\u5347\u5230\u522b\u5904\u53bb\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ba9\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u6709\u5192\u5934\u9488\u5bf9\u9526\u6e56\u7684\u673a\u4f1a\u2026\u2026\u201d\u89c1\u8bb8\u601d\u8138\u4e0a\u8fd8\u6709\u62c5\u5fe7\uff0c\u4f38\u624b\u8fc7\u53bb\u6478\u7740\u5979\u5fae\u51c9\u6ed1\u817b\u7684\u8138\u988a\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u5c31\u7b97\u4f60\u9000\u51fa\u6765\uff0c\u8981\u80fd\u5e2e\u5fd9\u63a8\u52a8\u4e00\u628a\uff0c\u6211\u4e5f\u4e0d\u53ef\u80fd\u5bf9\u4e16\u7eaa\u9526\u6e56\u8896\u624b\u65c1\u89c2\u554a\u3002\u867d\u7136\u6211\u5728\u4e16\u7eaa\u9526\u6e56\u91cc\u6ca1\u6709\u4ec0\u4e48\u80a1\u4efd\uff0c\u53ef\u6211\u4ece\u6765\u90fd\u6ca1\u6709\u4e16\u7eaa\u9526\u6e56\u770b\u6210\u9526\u6e56\u4f53\u7cfb\u4e4b\u5916\u7684\u4f01\u4e1a\uff0c\u518d\u8bf4\uff0c\u6211\u4e5f\u6709\u4e9b\u53ef\u80fd\u4f1a\u8fc7\u4e8e\u7406\u60f3\u5316\u7684\u60f3\u6cd5\u5e0c\u671b\u80fd\u901a\u8fc7\u4e16\u7eaa\u9526\u6e56\u6765\u5b9e\u73b0\uff0c\u8fd8\u6709\u4f60\u7684\u68a6\u60f3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8bb8\u601d\u4f38\u624b\u63e1\u7740\u5f20\u606a\u6478\u81ea\u5df1\u8138\u7684\u624b\uff0c\u5341\u6307\u6263\u7740\uff0c\u6e05\u6f88\u7684\u7738\u5b50\u770b\u7740\u5f20\u606a\uff0c\u559c\u6b22\u542c\u4ed6\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u53f6\u5bb6\u554a\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u8fd8\u6709\u6211\u5c0f\u53d4\u4ed6\u4eec\uff0c\u90fd\u5e0c\u671b\u4e16\u7eaa\u9526\u6e56\u80fd\u7ad9\u5230\u4e00\u4e2a\u66f4\u9ad8\u7684\u5e73\u53f0\u4e0a\uff0c\u90b5\u81f3\u521a\u6709\u6c34\u5e73\uff0c\u4e5f\u8981\u7ed9\u4e2a\u5e73\u53f0\u8ba9\u4ed6\u53d1\u6325\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\uff0c\u623f\u5730\u4ea7\u5e02\u573a\u6709\u7740\u5f88\u5f3a\u7684\u5730\u57df\u6027\u3002\u5728\u540c\u4e00\u5730\u533a\u91cc\uff0c\u90a3\u4e9b\u56fd\u6709\u4f01\u4e1a\u53c8\u5360\u636e\u76f8\u5bf9\u7684\u5f3a\u52bf\u3002\u4e16\u7eaa\u9526\u6e56\u4e0d\u662f\u5efa\u90ba\u672c\u5730\u623f\u4f01\uff0c\u66f4\u4e0d\u662f\u56fd\u6709\u4f01\u4e1a\uff0c\u800c\u4e14\u501f\u6a61\u6811\u56ed\u8ba1\u5212\u8fdb\u5165\u5efa\u90ba\u624d\u534a\u5e74\u591a\u65f6\u95f4\u3002</p><p>\u3000\u3000\u8981\u662f\u8ba9\u8fd9\u4e48\u4e00\u5bb6\u4f01\u4e1a\u5c06\u6570\u5b57\u957f\u5eca\u6784\u60f3\u4e2d\u6700\u80a5\u7f8e\u7684\u4e00\u5927\u5757\u8089\u541e\u4e0b\u53bb\uff0c\u5c31\u7b97\u90b5\u81f3\u521a\u62c9\u4e0a\u53f6\u5bb6\uff0c\u62c9\u4e0a\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\uff0c\u963b\u529b\u4e0e\u8d28\u7591\u4e5f\u4f1a\u7eb7\u81f3\u6c93\u6765\uff0c\u6bd5\u7adf\u5730\u5934\u86c7\u4e0d\u662f\u90a3\u4e48\u597d\u6b3a\u538b\u7684\u3002\u4e16\u7eaa\u9526\u6e56\u5c31\u7b97\u6709\u518d\u5b8c\u7f8e\u7684\u8ba1\u5212\uff0c\u8981\u662f\u6ca1\u6709\u5f20\u606a\u7684\u80cc\u4e66\uff0c\u7f57\u541b\u4e0e\u738b\u7ef4\u5747\u662f\u4e0d\u4f1a\u5ffd\u89c6\u8fd9\u4e2a\u8ba1\u5212\u80cc\u540e\u6240\u8574\u85cf\u7684\u98ce\u9669\u3002\u6709\u4e86\u5f20\u606a\u7684\u80cc\u4e66\u5c31\u5b8c\u5168\u4e0d\u540c\uff0c\u81f3\u5c11\u4e0d\u7528\u5f3a\u70c8\u7684\u53bb\u8d28\u7591\u4e16\u7eaa\u9526\u6709\u6ca1\u6709\u53bb\u5b9e\u65bd\u8fd9\u4e2a\u5e9e\u5927\u5f00\u53d1\u8ba1\u5212\u7684\u80fd\u529b\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u7684\u8463\u4e8b\u4f1a\u8bae\u5468\u4e8c\u7ed3\u675f\uff0c\u4e0d\u8fc7\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u90fd\u4e0d\u6025\u7740\u79bb\u5f00\u5efa\u90ba\uff0c\u5b59\u5c1a\u4e49\u8fd8\u62c5\u4efb\u4e2d\u6676\u5fae\u82af\u7684\u8463\u4e8b\u957f\uff0c\u4e2d\u6676\u5fae\u82af\u6b63\u5728\u7d27\u5f20\u7684\u7b79\u5efa\u4e2d\uff0c\u5b59\u5c1a\u4e49\u8981\u5728\u5efa\u90ba\u5fd9\u788c\u7684\u4e8b\u60c5\u4e5f\u5f88\u591a\u3002\u867d\u7136\u90b5\u81f3\u521a\u62ff\u51fa\u6765\u7684\u5f00\u53d1\u8ba1\u5212\u8ba9\u5b59\u5c1a\u4e49\u4ed6\u4eec\u7830\u7136\u5fc3\u52a8\uff0c\u4f46\u662f\u5173\u952e\u8fd8\u8981\u5148\u770b\u5e02\u91cc\u652f\u4e0d\u652f\u6301\u3002\u8bb8\u601d\u5148\u5e26\u7740\u52a9\u624b\u56de\u4e86\u6d77\u5dde\uff0c\u9526\u6e56\u56fd\u9645\u793e\u533a\u7684\u8bbe\u8ba1\u7a3f\uff0c\u5f20\u606a\u770b\u662f\u770b\u8fc7\u4e86\uff0c\u8fd8\u8981\u62ff\u56de\u6d77\u5dde\u7ed9\u5176\u4ed6\u4eba\u770b\u3002</p><p>\u3000\u3000\u5f00\u53d1\u8ba1\u5212\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u4e5f\u4e0d\u662f\u8868\u8868\u6001\u5c31\u80fd\u6e05\u95f2\u4e0b\u6765\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u5468\u56db\u627e\u738b\u7ef4\u5747\u8c08\u4e16\u7eaa\u9526\u6e56\u7684\u5f00\u53d1\u8ba1\u5212\uff0c\u738b\u7ef4\u5747\u542c\u540e\u627e\u7f57\u541b\u6c47\u62a5\u8fd9\u4e8b\uff0c\u7f57\u541b\u6253\u7535\u8bdd\u5c06\u5f20\u606a\u8bf7\u8fc7\u53bb\u65f6\uff0c\u8fd8\u662f\u738b\u7ef4\u5747\u63d0\u9192\uff0c\u624d\u5c06\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u4e5f\u8bf7\u5230\u5e02\u59d4\u53bb\u2014\u2014\u5f20\u77e5\u975e\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u96c6\u56e2\u8463\u4e8b\u957f\u3001\u90b5\u81f3\u521a\u662f\u4e16\u7eaa\u9526\u6e56\u5730\u4ea7\u96c6\u56e2\u603b\u88c1\u3002</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u770b\u4e16\u7eaa\u9526\u6e56\u7684\u5f00\u53d1\u8ba1\u5212\uff1f\u201d\u521a\u6253\u8fc7\u7535\u8bdd\u8fc7\u53bb\uff0c\u5f20\u606a\u4e0e\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u4ed6\u4eec\u8d76\u5230\u8fd8\u6709\u6bb5\u65f6\u95f4\uff0c\u7f57\u541b\u6c89\u541f\u4e86\u7247\u523b\uff0c\u51b3\u5b9a\u5728\u4ed6\u4eec\u8d76\u8fc7\u6765\u4e4b\u524d\uff0c\u4e0e\u738b\u7ef4\u5747\u5148\u8ba8\u8bba\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u4e16\u7eaa\u9526\u6e56\u63d0\u51fa\u7684\u8fd9\u79cd\u5927\u9762\u79ef\u3001\u6210\u89c4\u6a21\u7684\u89c4\u5212\u5f00\u53d1\u6a21\u5f0f\u8fd8\u662f\u6bd4\u8f83\u521b\u65b0\u7684\uff0c\u800c\u9ad8\u65b0\u533a\u60f3\u5728\u71d5\u5f52\u6e56\u4e1c\u5cb8\u5f62\u6210\u65b0\u7684\u526f\u57ce\u5e02\u4e2d\u5fc3\uff0c\u4e5f\u9700\u8981\u4e00\u5230\u4e24\u5bb6\u4f01\u4e1a\u53bb\u5e26\u52a8\u6574\u4e2a\u7684\u5efa\u8bbe\u901f\u5ea6\u4e0e\u89c4\u6a21\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u7ef4\u5747\u8bf4\u7684\u4e0d\u591a\uff0c\u7f57\u541b\u8fd8\u662f\u80fd\u542c\u51fa\u4ed6\u7684\u610f\u89c1\u503e\u5411\uff0c\u4ed6\u5750\u5728\u529e\u516c\u684c\u540e\u9762\uff0c\u624b\u6307\u6572\u7740\u529e\u516c\u684c\uff0c\u772f\u7740\u773c\u775b\uff0c\u8ba9\u4eba\u770b\u4e0d\u51fa\u4ed6\u5728\u60f3\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u738b\u7ef4\u5747\u591a\u5c11\u80fd\u731c\u5230\uff0c\u4e16\u7eaa\u9526\u6e56\u63d0\u51fa\u6765\u7684\u5f00\u53d1\u6a21\u5f0f\uff0c\u8bf4\u521b\u65b0\u4e5f\u53ef\u4ee5\uff0c\u8bf4\u6781\u5ea6\u5192\u9669\u4e5f\u884c\uff0c\u770b\u8c01\u8bf4\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d76\u8fc7\u6765\uff0c\u4e5f\u662f\u89e3\u7b54\u7f57\u541b\u5bf9\u5f00\u53d1\u8ba1\u5212\u7684\u4e00\u4e9b\u7591\u60d1\u3002\u5f20\u606a\u4e5f\u660e\u786e\u8868\u793a\uff0c\u8fd9\u4e2a\u5f00\u53d1\u8ba1\u5212\u4e2d\u9996\u6279\u5f00\u53d1\u51fa\u6765\u7684\u9ad8\u6863\u4f4f\u5b85\uff0c\u9526\u6e56\u5c06\u5168\u9762\u56de\u8d2d\u7528\u6765\u7ed9\u90a3\u4e9b\u4ece\u6d77\u5916\u5f52\u6765\u7684\u4e2d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u5b89\u5bb6\u843d\u6237\u3002\u6b64\u65f6\u7684\u9752\u5e74\u516c\u5bd3\uff0c\u8fd8\u53ea\u662f\u4ed6\u4eec\u4e34\u65f6\u843d\u811a\u7684\u5bbf\u820d\u3002\u8fd9\u4e9b\u4e2d\u9ad8\u7ea7\u7814\u53d1\u4eba\u5458\u4ece\u6d77\u5916\u5f52\u6765\uff0c\u5927\u591a\u6570\u90fd\u62d6\u5bb6\u5e26\u53e3\uff0c\u5728\u6d77\u5916\u7684\u5bb6\u4eba\u4e5f\u4f1a\u5f88\u5feb\u5230\u5efa\u90ba\u6765\u56e2\u805a\uff0c\u4e3a\u4ed6\u4eec\u89e3\u51b3\u751f\u6d3b\u540e\u987e\u4e4b\u5fe7\u662f\u9526\u6e56\u4e0e\u5efa\u90ba\u5e02\u59d4\u5e02\u653f\u5e9c\u7684\u5171\u540c\u627f\u8bfa\u3002\u5c31\u50cf\u8fd9\u6b21\u9ad8\u65b0\u533a\u51c6\u5907\u8054\u5408\u9526\u6e56\u4e0e\u5176\u4ed6\u4f01\u4e1a\u6210\u7acb\u4e00\u4e2a\u9f13\u52b1\u6d77\u5916\u7559\u5b66\u4eba\u5458\u56de\u56fd\u521b\u4e1a\u7684\u57fa\u91d1\uff0c\u4e3a\u8fd9\u4e9b\u6d77\u5916\u5f52\u6765\u4eba\u5458\u89e3\u51b3\u751f\u6d3b\u4e0a\u7684\u540e\u987e\u4e4b\u5fe7\uff0c\u662f\u6700\u57fa\u672c\u7684\u6761\u4ef6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u90b5\u81f3\u521a\u8fd8\u662f\u5c0f\u53d4\u5f20\u77e5\u975e\u8be6\u7ec6\u89e3\u91ca\u4e86\u7f57\u541b\u3001\u738b\u7ef4\u5747\u7684\u7591\u70b9\uff0c\u7f57\u541b\u6c89\u541f\u4e86\u7247\u523b\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u8ba1\u5212\u554a\uff0c\u8fd8\u8981\u62ff\u5230\u5e38\u59d4\u4f1a\u8bae\u4e0a\u53bb\u8ba8\u8bba\u2026\u2026\u201d</p><p>\u3000\u3000\u9879\u76ee\u592a\u5927\uff0c\u800c\u4e14\u623f\u5730\u4ea7\u5f00\u53d1\u662f\u76f8\u5bf9\u8f83\u654f\u611f\u7684\u4ea7\u4e1a\uff0c\u53ef\u80fd\u4f1a\u5f15\u6765\u8f83\u591a\u7684\u975e\u8bae\u4e0e\u8d28\u7591\uff0c\u7f57\u541b\u4e5f\u4e0d\u80fd\u4e00\u8a00\u51b3\u5b9a\u3002\u7f57\u541b\u4e5f\u6ca1\u6709\u660e\u786e\u8868\u793a\u4ed6\u662f\u652f\u6301\u8fd8\u662f\u53cd\u5bf9\uff0c\u5173\u952e\u8fd8\u662f\u8981\u770b\u5176\u4ed6\u5e38\u59d4\u5bf9\u8fd9\u9879\u5f00\u53d1\u8ba1\u5212\u7684\u53cd\u5e94\u5f3a\u70c8\u7a0b\u5ea6\u4e86\u3002\u8981\u662f\u8096\u660e\u5efa\u6495\u7834\u8138\u76ae\u8ddf\u7f57\u541b\u5531\u53cd\u8c03\uff0c\u7f57\u541b\u4e5f\u4e0d\u53ef\u80fd\u786c\u7740\u5934\u76ae\u652f\u6301\u8fd9\u9879\u5f00\u53d1\u8ba1\u5212\uff1b\u4f30\u8ba1\u53cd\u5bf9\u7684\u58f0\u97f3\u4e0d\u4f1a\u5c0f\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u6240\u8c13\uff0c\u90b5\u81f3\u521a\u4e0e\u5f20\u77e5\u975e\u4e5f\u6ca1\u6709\u5962\u671b\u7f57\u541b\u5149\u7740\u8180\u5b50\u8df3\u51fa\u6765\u652f\u6301\u4ed6\u4eec\uff0c\u7f57\u541b\u3001\u738b\u7ef4\u5747\u665a\u4e0a\u90fd\u6709\u5e94\u916c\uff0c\u4ed6\u4eec\u5c31\u5148\u544a\u8f9e\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5728\u8f66\u91cc\uff0c\u5f20\u77e5\u975e\u591a\u5c11\u6709\u4e9b\u524d\u9014\u672a\u535c\u7684\u5fd0\u5fd1\uff0c\u8bf4\u9053\uff1a\u201c\u5e02\u91cc\u51e0\u4e2a\u4eba\uff0c\u662f\u4e0d\u662f\u6293\u7d27\u65f6\u95f4\u518d\u62dc\u8bbf\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u51b3\u5b9a\u5927\u4e3e\u8f6c\u8fdb\u5efa\u90ba\u4e4b\u540e\uff0c\u7279\u522b\u662f\u6a61\u6811\u56ed\u8ba1\u5212\u6b63\u5f0f\u542f\u52a8\u4e4b\u540e\uff0c\u5f20\u606a\u7edd\u4e0d\u4f1a\u611a\u6627\u5230\u4ee5\u4e3a\u5c06\u6240\u6709\u7684\u5e0c\u671b\u5bc4\u6258\u5728\u7f57\u541b\u3001\u738b\u7ef4\u5747\u4e24\u4e2a\u4eba\u8eab\u4e0a\u5c31\u53ef\u4ee5\u7684\u3002</p><p>\u3000\u3000\u5b98\u573a\u89c4\u5219\u5f88\u7b80\u5355\uff0c\u4e0d\u5728\u5708\u5b50\u91cc\u5c31\u5728\u5708\u5b50\u5916\uff0c\u9526\u6e56\u4e0e\u4e16\u7eaa\u9526\u6e56\u90fd\u662f\u5916\u6765\u6237\uff0c\u4f46\u662f\u8981\u5728\u5efa\u90ba\u624e\u6839\u7acb\u8db3\uff0c\u5c31\u8981\u5efa\u7acb\u5e7f\u6cdb\u7684\u4eba\u8109\u5708\u5b50\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u53d7\u9650\u4e8e\u81ea\u8eab\u7684\u6027\u8d28\uff0c\u4e0d\u4f1a\u5927\u8086\u7684\u53bb\u7ecf\u8425\u4eba\u8109\uff0c\u5c31\u6a61\u6811\u56ed\u672c\u8eab\u6765\u8bf4\uff0c\u770b\u4e0a\u53bb\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u5fc5\u8981\u3002\u4ece\u4e00\u4e2a\u4e2a\u5206\u62c6\u5f00\u6765\u7684\u5177\u4f53\u9879\u76ee\uff0c\u9526\u6e56\u5728\u5efa\u90ba\u7684\u9879\u76ee\u90fd\u6ca1\u6709\u5fc5\u8981\u53bb\u7ecf\u8425\u4ec0\u4e48\u4eba\u8109\uff0c\u4f46\u662f\u4ece\u9526\u6e56\u5728\u5efa\u90ba\u7684\u6574\u4f53\u5e03\u5c40\u4e0a\uff0c\u53c8\u5fc5\u987b\u6709\u6240\u7ecf\u8425\u3002\u4e16\u7eaa\u9526\u6e56\u5c31\u7b80\u5355\u591a\u4e86\uff0c\u65e0\u8bba\u662f\u9910\u996e\u5a31\u4e50\u8fd8\u662f\u5730\u4ea7\u5f00\u53d1\uff0c\u5bf9\u5730\u65b9\u884c\u653f\u8d44\u6e90\u7684\u4f9d\u8d56\u6027\u975e\u5e38\u7684\u5927\uff0c\u6ca1\u6709\u5e7f\u6cdb\u7684\u4eba\u8109\u5173\u7cfb\uff0c\u4e16\u7eaa\u9526\u6e56\u5c31\u5f88\u96be\u5728\u5efa\u90ba\u7acb\u8db3\u3002</p><p>\u3000\u3000\u50cf\u53f6\u5bb6\u3001\u5b59\u5c1a\u4e49\u5bb6\u5728\u5efa\u90ba\u90fd\u6709\u4e0d\u9519\u7684\u4eba\u8109\uff0c\u4f46\u662f\u8fd9\u4e9b\u4eba\u8109\u662f\u5206\u6563\u7684\uff0c\u4e0d\u80fd\u6709\u6548\u7684\u5f62\u6210\u4e00\u4e2a\u5708\u5b50\uff0c\u5f71\u54cd\u529b\u5c31\u5927\u5927\u7684\u7ed9\u524a\u5f31\u4e86\u3002\u8fd9\u65b9\u9762\u7684\u5de5\u4f5c\uff0c\u5c0f\u53d4\u5f20\u77e5\u975e\u5c31\u4e00\u76f4\u5728\u505a\uff0c\u4e5f\u4e0d\u662f\u8bf4\u9664\u4e86\u7f57\u541b\u3001\u738b\u7ef4\u5747\u4e4b\u5916\uff0c\u5c31\u518d\u6ca1\u6709\u652f\u6301\u7684\u58f0\u97f3\u4e86\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u5f20\u606a\u4e0d\u4f1a\u5bf9\u4e16\u7eaa\u9526\u6e56\u8896\u624b\u65c1\u89c2\u7684\u539f\u56e0\u4e4b\u4e00\uff0c\u5728\u5efa\u90ba\u56f4\u7ed5\u4e16\u7eaa\u9526\u6e56\u5efa\u7acb\u5e7f\u6cdb\u7684\u4eba\u8109\u5708\u5b50\u76f8\u5bf9\u8981\u5bb9\u6613\u5f97\u591a\u3002\u4e16\u7eaa\u9526\u6e56\u662f\u9526\u6e56\u4f53\u7cfb\u7684\u91cd\u8981\u8865\u5145\uff0c\u867d\u7136\u5f20\u606a\u575a\u6301\u4e0d\u5bf9\u4e16\u7eaa\u9526\u6e56\u6301\u80a1\uff0c\u7531\u4e8e\u5c0f\u53d4\u4e0e\u8bb8\u601d\u7684\u7f18\u6545\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u5fc5\u8981\u518d\u76f4\u63a5\u5bf9\u4e16\u7eaa\u9526\u6e56\u6301\u80a1\u4e86\u3002</p><p>\u3000\u3000\u8d22\u5bcc\u4e0d\u662f\u7b80\u5355\u7684\u6570\u503c\u6e38\u620f\uff0c\u53ea\u80fd\u4fdd\u8bc1\u4e00\u5b9a\u7684\u5f71\u54cd\u529b\uff0c\u8fd8\u9700\u8981\u575a\u6301\u4e3a\u9526\u6e56\u8d26\u6237\u4e0a\u591a\u589e\u52a0\u4e00\u4ebf\u6216\u4e24\u4ebf\u7684\u6570\u5b57\u589e\u6da8\u5417\uff1f</p><p>\u3000\u3000\u201c\u7f57\u541b\u60f3\u8981\u4ec0\u4e48\uff0c\u9664\u4e86\u738b\u7ef4\u5747\u4e4b\u5916\uff0c\u5e02\u59d4\u5e38\u59d4\u91cc\u80af\u5b9a\u8fd8\u4f1a\u6709\u4eba\u77e5\u9053\uff0c\u201d\u5f20\u606a\u6ca1\u6709\u76f4\u63a5\u5426\u5b9a\u5c0f\u53d4\u7684\u5efa\u8bae\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u53c8\u5230\u5e74\u5e95\u4e86\uff0c\u5404\u5730\u4e00\u5e74\u7684\u7ecf\u6d4e\u7edf\u8ba1\u6570\u5b57\u4e5f\u4f1a\u5f88\u5feb\u51fa\u53f0\uff0c\u5c0f\u53d4\u80fd\u4e0d\u80fd\u627e\u4e2a\u62a5\u793e\uff0c\u5148\u5199\u4e00\u4e9b\u8fd9\u9884\u6d4b\u65b9\u9762\u7684\u6587\u7ae0\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u9519\uff0c\u8fd9\u65b9\u9762\u7684\u6587\u7ae0\u8981\u597d\u597d\u7684\u5938\u4e00\u4e0b\u7f57\u541b\u9886\u5bfc\u6709\u65b9\uff0c\u201d\u90b5\u81f3\u521a\u660e\u767d\u5f20\u606a\u60f3\u505a\u4ec0\u4e48\uff0c\u53d7\u4e1c\u5357\u4e9a\u91d1\u878d\u98ce\u66b4\u7684\u5f71\u54cd\uff0c\u51fa\u53e3\u4f01\u4e1a\u53d7\u632b\u4e25\u91cd\uff0c\u5927\u91cf\u9500\u5f80\u56fd\u9645\u5e02\u573a\u7684\u5546\u54c1\u8f6c\u9500\u56fd\u5185\uff0c\u5df2\u7ecf\u6709\u901a\u8d27\u7d27\u7f29\u7684\u8d8b\u52bf\uff0c\u5bf9\u5730\u65b9\u653f\u5e9c\u5b98\u5458\u6765\u8bf4\u6700\u5173\u952e\u7684\u7ecf\u6d4e\u6570\u636e\uff0c\u6295\u8d44\u989d\u589e\u6da8\u5f88\u591a\u5730\u533a\u4eca\u5e74\u90fd\u6781\u53ef\u80fd\u662f\u8d1f\u6570\u3002\u5bf9\u4e8e\u90a3\u4e9b\u6295\u8d44\u96f6\u589e\u6da8\u751a\u81f3\u8d1f\u589e\u6da8\u7684\u57ce\u5e02\uff0c\u5efa\u90ba\u5e02\u7684\u7ecf\u6d4e\u5efa\u8bbe\u6210\u5c31\u71a0\u71a0\u751f\u8f89\uff0c\u8fd9\u4e9b\u5f53\u7136\u8981\u5f52\u5230\u7f57\u541b\u7684\u6b63\u786e\u9886\u5bfc\u4e4b\u4e0b\u540d\u4e0b\uff0c\u90b5\u81f3\u521a\u53c8\u95ee\u9053\uff0c\u201c\u662f\u4e0d\u662f\u8fd8\u8981\u5c06\u660e\u5e74\u7684\u7ecf\u6d4e\u72b6\u51b5\u9884\u6d4b\u5f97\u66f4\u4e25\u91cd\u4e00\u4e9b\uff1f\u7535\u89c6\u5a92\u4f53\u4e5f\u53ef\u4ee5\u9020\u52bf\u3002\u201d</p><p>\u3000\u3000\u652f\u6301\u4e16\u7eaa\u9526\u6e56\u5b9e\u65bd\u8fd9\u4e2a\u8ba1\u5212\uff0c\u660e\u5e74\u5c31\u53ef\u4ee5\u4e3a\u5efa\u90ba\u623f\u5730\u4ea7\u4e1a\u76f4\u63a5\u589e\u52a0\u516d\u5230\u516b\u4ebf\u7684\u6295\u8d44\uff0c\u5728\u6295\u8d44\u589e\u6da8\u5f88\u53ef\u80fd\u968f\u65f6\u7531\u4e8e\u4e9a\u6d32\u7ecf\u6d4e\u5f62\u52bf\u7684\u6076\u5316\u800c\u505c\u6b62\u7684\u60c5\u51b5\uff0c\u6bcf\u4e00\u4e2a\u50cf\u4e16\u7eaa\u9526\u6e56\u8fd9\u6837\u7684\u5927\u89c4\u6a21\u6295\u8d44\uff0c\u5c31\u663e\u5f97\u5c24\u5176\u73cd\u60dc\u3002</p></article>"}}}]);
