(window.webpackJsonp=window.webpackJsonp||[]).push([[897],{920:function(p){p.exports={chapterTitle:null,title:"\u7b2c894\u7ae0 \u4e25\u6587\u4ecb\u7684\u610f\u56fe",content:"<article><h2>\u7b2c894\u7ae0 \u4e25\u6587\u4ecb\u7684\u610f\u56fe</h2><p>\u3000\u3000\u4e25\u6587\u4ecb\u6ca1\u6709\u63a9\u9970\u4ed6\u7684\u610f\u56fe\uff0c\u4ed6\u51fa\u73b0\u5728\u6d77\u5dde\u5c31\u662f\u610f\u5728\u9526\u57ce\u5730\u4ea7\uff0c\u610f\u5728\u9526\u57ce\u5730\u4ea7\u65d7\u4e0b\u4f4d\u4e8e\u6d77\u5dde\u5e02\u4e2d\u5fc3\u533a\u57df\u7684\u8d85\u8fc7\u4e8c\u5341\u4e07\u5e73\u65b9\u7c73\u7684\u5546\u4e1a\u5730\u4ea7\uff0c\u4e0e\u8c22\u5251\u5357\u9047\u4e0a\u7b97\u662f\u5de7\u5408\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0d\u8ba4\u4e3a\u8fd9\u7b97\u662f\u597d\u4e8b\u4e0a\u95e8\uff0c\u4e0e\u4e25\u6587\u4ecb\u4ed3\u4fc3\u63a5\u89e6\u4e4b\u540e\uff0c\u4ed6\u641c\u96c6\u6765\u5b8f\u4fe1\u5730\u4ea7\u7684\u8d44\u6599\u7814\u7a76\u8fc7\uff0c\u867d\u7136\u4e0d\u591f\u8be6\u7ec6\uff0c\u4f46\u4e5f\u77e5\u9053\u8fd9\u662f\u4e00\u5934\u6f5c\u85cf\u7684\u3001\u6781\u6709\u8010\u5fc3\u7684\u5de8\u9cc4\u3002\u8fd9\u79cd\u5de8\u9cc4\u4e3b\u52a8\u627e\u4e0a\u95e8\u6765\uff0c\u53ef\u4e0d\u662f\u70ed\u5fc3\u6765\u505a\u96f7\u950b\uff0c\u8c22\u5251\u5357\u751a\u81f3\u6000\u7591\u4e25\u6587\u4ecb\u5df2\u7ecf\u77e5\u9053\u8c22\u5bb6\u95f9\u5206\u5bb6\u7684\u6d88\u606f\uff0c\u8d81\u7740\u8fd9\u8fb9\u6700\u8f6f\u5f31\u65e0\u529b\u7684\u65f6\u5019\u51fa\u51fb\uff0c\u81ea\u7136\u662f\u60f3\u4ee5\u6700\u4f4e\u5ec9\u7684\u4ef7\u683c\u5c06\u9526\u57ce\u5730\u4ea7\u541e\u5e76\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7d27\u6025\u8d76\u5230\u91d1\u5c71\uff0c\u4e5f\u9080\u8bf7\u845b\u660e\u4fe1\u3001\u6797\u96ea\u4e00\u540c\u8d76\u8d74\u91d1\u5c71\uff0c\u8fd9\u65f6\u5019\uff0c\u4ed6\u4eec\u591a\u5c11\u6709\u4e9b\u5171\u60a3\u96be\u3001\u540c\u8fdb\u9000\u7684\u60c5\u8c0a\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u5171\u9677\u5728\u4e00\u4e2a\u5371\u5c40\u91cc\u82e6\u82e6\u6323\u624e\u7740\u3002</p><p>\u3000\u3000\u5728\u96e8\u5b63\u7ed3\u675f\u4e4b\u540e\uff0c\u91d1\u5c71\u6e56\u9762\u5411\u57ce\u533a\u7684\u6e56\u5824\u6574\u56fa\u5de5\u7a0b\u8fc5\u901f\u6295\u5165\u8d44\u91d1\u542f\u52a8\u8d77\u6765\uff0c\u6e56\u5824\u5c31\u662f\u72fc\u7c4d\u4e0d\u582a\u7684\u6d2a\u6cdb\u533a\uff0c\u6d2a\u6c34\u9000\u53bb\uff0c\u9053\u8def\u6e05\u6de4\u5de5\u4f5c\u5df2\u7ecf\u5b8c\u6210\uff0c\u91d1\u5c71\u56fd\u9645\u4f1a\u5c55\u4e2d\u5fc3\u4e5f\u6295\u5165\u8d44\u91d1\u8fdb\u884c\u4fee\u7f2e\uff0c\u4f46\u662f\u770b\u4e0a\u53bb\u4ecd\u5341\u5206\u7684\u51c4\u51c9\uff0c\u7eff\u5316\u690d\u7269\u6089\u906d\u7834\u574f\uff0c\u7ed9\u6d2a\u6c34\u51b2\u584c\u7684\u623f\u5c4b\u4e5f\u89e6\u76ee\u60ca\u5fc3\u3002</p><p>\u3000\u3000\u91d1\u5c71\u7535\u5b50\u5de5\u4e1a\u56ed\u4ecd\u7ed9\u7d22\u8ba8\u8d37\u6b3e\u7684\u4f9b\u5e94\u5546\u56f4\u56f0\u7740\uff0c\u6ca1\u6709\u5de8\u989d\u7684\u8d44\u91d1\u6295\u5165\uff0c\u751f\u4ea7\u6062\u590d\u5de5\u4f5c\u65e0\u4ece\u8c08\u8d77\uff0c\u4f46\u662f\u518d\u62d6\u5ef6\u4e0b\u53bb\uff0c\u5c06\u9677\u5165\u66f4\u65e0\u6cd5\u81ea\u62d4\u7684\u56f0\u5883\u4e4b\u4e2d\u3002\u6c5f\u5357\u7701\u59d4\u5168\u59d4\u4f1a\u8bae\u5373\u5c06\u53ec\u5f00\uff0c\u5468\u747e\u745c\u8eab\u4e0a\u6700\u540e\u7684\u653f\u6cbb\u5149\u73af\u4e5f\u5373\u5c06\u7ed9\u5265\u8131\u6389\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0e\u845b\u660e\u4fe1\u3001\u6797\u96ea\u89c1\u8fc7\u9762\u4e4b\u540e\uff0c\u624d\u77e5\u9053\u4e25\u6587\u4ecb\u4e4b\u524d\u5c31\u4e0e\u4ed6\u4eec\u63a5\u89e6\u8fc7\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u610f\u5728\u6574\u4e2a\u9526\u57ce\u5730\u4ea7\u554a\uff01\u201d\u8c22\u6c49\u660e\u8f7b\u8f7b\u7684\u4e00\u53f9\uff0c\u201c\u4e5f\u975e\u5584\u7c7b\uff01\u201d</p><p>\u3000\u3000\u201c\u5bf9\u8fd9\u4e9b\u8111\u5b50\u91cc\u8f6c\u7740\u8d81\u706b\u6253\u52ab\u5ff5\u5934\u7684\u4eba\uff0c\u6211\u770b\u8fd8\u662f\u5e72\u8106\u5229\u843d\u7684\u62d2\u7edd\u6389\u597d\u4e86\u3002\u201d\u845b\u836b\u5747\u7559\u5b88\u91d1\u5c71\u7535\u5b50\u56ed\uff0c\u813e\u6c14\u8d8a\u6765\u8d8a\u6025\u8e81\uff0c\u6574\u5929\u7ed9\u90a3\u4e9b\u8ba8\u503a\u7684\u4f9b\u5e94\u5546\u56f4\u56f0\u7740\uff0c\u80fd\u6709\u4ec0\u4e48\u597d\u813e\u6c14\u8fd8\u771f\u662f\u89c1\u9b3c\u4e86\uff0c\u845b\u5bb6\u4e5f\u4e0d\u662f\u5728\u9999\u6e2f\u7edd\u7136\u627e\u4e0d\u5230\u63f4\u52a9\uff0c\u4f46\u662f\u6709\u7740\u8d81\u706b\u6253\u52ab\u7684\u4eba\u5c45\u591a\uff0c\u8fd9\u65f6\u5019\u89c1\u5185\u5730\u53c8\u5192\u51fa\u4e00\u4e2a\u60f3\u8d81\u706b\u6253\u52ab\u7684\u5bb6\u4f19\uff0c\u5fc3\u60c5\u603b\u4e0d\u4f1a\u7279\u522b\u7684\u597d\u3002</p><p>\u3000\u3000\u201c\u5b8f\u4fe1\u5efa\u8bbe\u8fd8\u662f\u4fe1\u901a\u94f6\u884c\u7684\u6700\u5927\u5355\u4e00\u80a1\u4e1c\u2026\u2026\u201d\u5750\u5728\u7535\u5b50\u56ed\u7684\u4f1a\u8bae\u5ba4\u91cc\uff0c\u4e34\u65f6\u4f9b\u7535\u7535\u8def\u652f\u6491\u4e0d\u8d77\u5927\u529f\u7387\u7684\u4e2d\u592e\u529f\u7387\uff0c\u4f1a\u8bae\u5ba4\u91cc\u53ea\u6709\u4e00\u53f0\u7535\u6247\u5439\u7740\uff0c\u6797\u96ea\u90fd\u89c9\u5f97\u9f3b\u5c16\u6709\u6c57\u661f\u5b50\u6e17\u51fa\u6765\u4e86\uff0c\u5979\u8fd9\u65f6\u5019\u5012\u662f\u660e\u767d\u90a3\u5929\u5728\u5b66\u5e9c\u5df7\u5076\u9047\u4e25\u6587\u4ecb\u5bf9\u5f20\u606a\u7684\u6001\u5ea6\u4f1a\u5982\u6b64\u7684\u51b7\u6de1\uff0c\u4e25\u6587\u4ecb\u65e9\u5c31\u60f3\u5230\u5b8f\u4fe1\u5efa\u8bbe\u633a\u8fdb\u4e1c\u6d77\u4f1a\u4e0e\u9526\u6e56\u6709\u5229\u76ca\u51b2\u7a81\uff0c\u8fde\u8868\u9762\u7684\u6577\u884d\u90fd\u541d\u556c\u505a\u51fa\u6765\u3002</p><p>\u3000\u3000\u6797\u96ea\u6709\u80e1\u5b97\u5e86\u3001\u6709\u845b\u5efa\u5fb7\uff0c\u4e5f\u6709\u5176\u4ed6\u7684\u4e00\u4e9b\u6d88\u606f\u6765\u6e90\uff0c\u5bf9\u5b8f\u4fe1\u5efa\u8bbe\u7684\u80cc\u666f\u4e86\u89e3\u66f4\u6df1\u4e00\u4e9b\uff0c\u5979\u81ea\u77e5\u5728\u8fd9\u4e9b\u51f6\u6076\u7684\u3001\u80c3\u53e3\u5947\u5927\u65e0\u6bd4\u7684\u5de8\u9cc4\u9762\u524d\uff0c\u7cbe\u5178\u5730\u4ea7\u8fd8\u662f\u4e56\u4e56\u7684\u5b88\u597d\u5c0f\u6ce5\u7a23\u7684\u672c\u5206\u66f4\u6709\u5229\u53ef\u56fe\u3002\u4e25\u6587\u4ecb\u8fd9\u6837\u7684\u4eba\uff0c\u62d2\u7edd\u4ed6\u5c31\u662f\u5f97\u7f6a\u4ed6\uff0c\u4ed6\u4e5f\u7edd\u4e0d\u4f1a\u541d\u556c\u505a\u843d\u4e95\u4e0b\u77f3\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u6211\u4eec\u9700\u8981\u8003\u8651\u7684\u3002\u201d\u8c22\u665a\u5c71\u77e5\u9053\u6797\u96ea\u63d0\u9192\u8fd9\u4e48\u4e00\u53e5\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u56fd\u4eba\u7d20\u6765\u90fd\u4e60\u60ef\u505a\u4e24\u4ef6\u4e8b\u60c5\uff1a\u9526\u4e0a\u6dfb\u82b1\u4e0e\u843d\u4e95\u4e0b\u77f3\uff0c\u4ed6\u4eec\u5728\u56fd\u5185\u7684\u878d\u8d44\u6e20\u9053\u51e0\u4e4e\u7ed9\u5f7b\u5e95\u7684\u5c01\u5835\u4e0a\u4e86\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u8eab\u4e3a\u4fe1\u901a\u94f6\u884c\u7684\u7b2c\u4e00\u5927\u5355\u4e00\u80a1\u4e1c\uff0c\u8fd9\u7edd\u4e0d\u662f\u4ed6\u4eec\u80fd\u591f\u5ffd\u89c6\u7684\u3002\u4fe1\u901a\u94f6\u884c\u867d\u7136\u4e0d\u80fd\u8ddf\u56db\u5927\u56fd\u6709\u5546\u4e1a\u94f6\u884c\u76f8\u6bd4\uff0c\u4f46\u662f\u8eab\u4e3a\u56fd\u5185\u4ec5\u6709\u7684\u5341\u516d\u5bb6\u5168\u56fd\u6027\u7684\u5546\u4e1a\u94f6\u884c\u4e4b\u4e00\uff0c\u89c4\u6a21\u5b9e\u529b\u4e5f\u7edd\u975e\u5730\u65b9\u4e0a\u7684\u5546\u4e1a\u94f6\u884c\u80fd\u591f\u76f8\u63d0\u5e76\u8bba\u7684\uff0c\u53ea\u8981\u83b7\u5f97\u4fe1\u901a\u94f6\u884c\u7684\u8d37\u6b3e\u652f\u6301\uff0c\u76f8\u4fe1\u80fd\u5f88\u5feb\u5c31\u6e21\u8fc7\u5f53\u524d\u7684\u5371\u673a\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u5251\u5357\u5374\u662f\u6000\u7591\u6797\u96ea\u4e0e\u4e25\u6587\u4ecb\u6709\u6240\u9ed8\u5951\uff0c\u4e25\u6587\u4ecb\u4e5f\u6ca1\u6709\u5426\u8ba4\u4e0e\u6797\u96ea\u6709\u8f83\u6df1\u7684\u63a5\u89e6\uff0c\u8c22\u5251\u5357\u5bf9\u6797\u96ea\u7684\u751f\u6d3b\u4f5c\u98ce\u6709\u6240\u542c\u95fb\uff0c\u8fd9\u4e5f\u52a0\u6df1\u4ed6\u5bf9\u6797\u96ea\u7684\u731c\u7591\uff0c\u4ed6\u6c89\u9ed8\u7740\u6ca1\u6709\u542d\u58f0\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u767d\u5929\u7684\u63a5\u89e6\uff0c\u5e76\u6ca1\u6709\u5546\u8bae\u51fa\u9488\u5bf9\u5b8f\u4fe1\u5efa\u8bbe\u7684\u7edf\u4e00\u7b56\u7565\uff0c\u8c22\u5251\u5357\u62c5\u5fc3\u7cbe\u5178\u5730\u4ea7\u5148\u7ed9\u5b8f\u4fe1\u5efa\u8bbe\u5206\u5316\u6389\uff0c\u591c\u91cc\u4ed6\u8fd8\u662f\u4f4f\u5230\u4e34\u5d16\u6e56\u5317\u5cb8\u7684\u5e02\u59d4\u522b\u5885\u5927\u9662\u91cc\uff0c\u7528\u8fc7\u665a\u9910\uff0c\u4e0e\u7236\u4eb2\u8c22\u6c49\u660e\u4ee5\u53ca\u8c22\u665a\u5c71\u5750\u5728\u8d77\u5c45\u5ba4\u91cc\u7814\u7a76\u5f53\u524d\u7684\u5bf9\u7b56\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u6253\u5f00\u7535\u89c6\u770b\u4e00\u4e0b\u2026\u2026\u201d\u5468\u747e\u745c\u8d70\u4e86\u8fdb\u6765\uff0c\u62ff\u8d77\u8336\u5385\u4e0a\u7684\u9065\u63a7\u5668\uff0c\u6253\u5f00\u7535\u89c6\uff0c\u8c03\u5230\u7701\u7535\u89c6\u53f0\u6b63\u64ad\u653e\u65b0\u5149\u7eb8\u4e1a\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u65b0\u95fb\uff0c\u7b7e\u5b57\u4eea\u5f0f\u662f\u4eca\u5929\u4e0b\u5348\u4e3e\u884c\u7684\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u6d88\u606f\u95ed\u585e\uff0c\u6ca1\u6709\u4eba\u4f1a\u4e3b\u52a8\u60f3\u5230\u8981\u5c06\u6d88\u606f\u544a\u8bc9\u4ed6\u4eec\u4e86\u3002</p><p>\u3000\u3000\u955c\u5934\u6709\u6355\u6349\u5230\u5f20\u606a\u85cf\u5728\u4f17\u4eba\u4e2d\u7684\u8eab\u5f71\uff0c\u867d\u7136\u53ea\u9732\u51fa\u85cf\u5728\u4eba\u7fa4\u91cc\u7684\u4fa7\u8138\uff0c\u4f46\u662f\u5bf9\u4e8e\u8c22\u5251\u5357\u4ed6\u4eec\u6765\u8bf4\u8ba4\u8bc6\u4ed6\u5df2\u7ecf\u662f\u8db3\u591f\u4e86\uff0c\u5c31\u7b97\u5f20\u606a\u53ea\u9732\u4e2a\u7709\u89d2\u90fd\u5f88\u89c9\u5f97\u719f\u6089\uff0c\u539f\u6765\u4ed6\u4eca\u5929\u4e5f\u5230\u91d1\u5c71\u4e86\u3002</p><p>\u3000\u3000\u9526\u6e56\u6ca1\u6709\u8d81\u673a\u524a\u5f31\u5bf9\u6668\u66e6\u7eb8\u4e1a\u7684\u6536\u8d2d\u6761\u4ef6\uff0c\u65b0\u95fb\u91cc\u4e5f\u6ca1\u6709\u907f\u8bb3\u7684\u5c06\u4e3b\u8981\u7684\u6536\u8d2d\u6761\u4ef6\u90fd\u516c\u5e03\u51fa\u6765\uff0c\u60f3\u5fc5\u5728\u9526\u6e56\u5728\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u95ee\u9898\u5df2\u7ecf\u53d6\u5f97\u666e\u901a\u804c\u5de5\u7684\u8c05\u89e3\uff0c\u6216\u8bb8\u65c1\u4eba\u5f88\u96be\u7406\u89e3\u9526\u6e56\u4e3a\u4ec0\u4e48\u4e0d\u8d81\u673a\u538b\u4ef7\uff0c\u4e0e\u5f20\u606a\u6253\u4e86\u597d\u4e9b\u5e74\u4ea4\u9053\u7684\u8c22\u5251\u5357\uff0c\u5012\u662f\u731c\u5230\u9526\u6e56\u4e0d\u82db\u6c42\u5c0f\u5229\u4e4b\u4f59\u6025\u4e8e\u5728\u7eb8\u4e1a\u4e0a\u8fdb\u884c\u5168\u56fd\u6027\u7684\u5e03\u5c40\u3002</p><p>\u3000\u3000\u7535\u89c6\u67dc\u65c1\u77ee\u811a\u6728\u51e0\u4e0a\u7684\u7535\u8bdd\u54cd\u4e86\u8d77\u6765\uff0c\u5468\u747e\u745c\u5c31\u5728\u7ad9\u5728\u7535\u8bdd\u673a\u65c1\uff0c\u5979\u4e60\u60ef\u6027\u7684\u7b49\u4e86\u7247\u523b\uff0c\u8c22\u665a\u5c71\u5750\u5728\u6c99\u53d1\u4e0a\u4e5f\u6ca1\u6709\u52a8\u9759\uff0c\u5979\u624d\u60f3\u8d77\u81ea\u5df1\u5df2\u7ecf\u4e0d\u662f\u53f1\u54a4\u98ce\u4e91\u7684\u653f\u754c\u5973\u5f3a\u4eba\u4e86\uff0c\u5fc3\u91cc\u6709\u4e9b\u9eef\u7136\uff0c\u62ff\u8d77\u8bdd\u7b52\u63a5\u542c\u8d77\u6765\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u5c06\u8bdd\u7b52\u9012\u7ed9\u5251\u5357\uff1a\u201c\u7cbe\u5178\u7684\u6797\u5c0f\u59d0\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5947\u602a\u6797\u96ea\u5c06\u7535\u8bdd\u6253\u5230\u5ea7\u673a\u4e0a\uff0c\u4ed6\u7ad9\u8d77\u6765\u53bb\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u8c22\u603b\u6709\u6ca1\u6709\u770b\u5230\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u65b0\u95fb\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u521a\u5728\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u518d\u4e0b\u4e00\u57ce\uff0c\u4f60\u6709\u4ec0\u4e48\u60f3\u6cd5\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u5251\u5357\u5f88\u65e9\u5c31\u7edd\u4e86\u4e0e\u9526\u6e56\u4e89\u5f3a\u6597\u80dc\u7684\u5fc3\u601d\uff0c\u4f46\u662f\u5728\u4eca\u5e74\u590f\u5b63\u4e4b\u524d\uff0c\u6bcd\u4eb2\u8fd8\u5728\u4efb\u4e0a\uff0c\u5927\u8205\u4e5f\u6ca1\u6709\u7ed9\u7275\u6d89\u5230516\u6848\u4e2d\uff0c\u5173\u7cfb\u5bc6\u5207\u7684\u5609\u4fe1\u96c6\u56e2\u5143\u6c14\u4e5f\u672a\u5927\u4f24\uff0c\u9526\u6e56\u518d\u5f3a\u52bf\uff0c\u4ed6\u4eec\u7684\u81ea\u4fdd\u80fd\u529b\u8fd8\u662f\u6709\u7684\uff0c\u73b0\u5728\u5219\u771f\u53ef\u8c13\u5f62\u52bf\u6bd4\u4eba\u5f3a\u554a\uff01\u8c22\u5251\u5357\u53ea\u662f\u4e0d\u6e05\u695a\u6797\u96ea\u6253\u8fd9\u901a\u7535\u8bdd\u8fc7\u6765\u7684\u610f\u56fe\uff0c\u96be\u9053\u662f\u8981\u8ddf\u81ea\u5df1\u8ba8\u8bba\u4e00\u4e0b\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u95ee\u9898\uff1f\u8c22\u5251\u5357\u624d\u4e0d\u76f8\u4fe1\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u4f1a\u6709\u4ec0\u4e48\u628a\u67c4\u7ed9\u6797\u96ea\u6349\uff0c\u5c31\u7b97\u6536\u8d2d\u7684\u80cc\u540e\u662f\u5f90\u5b66\u5e73\u6700\u540e\u4fc3\u6210\u4e86\u6b64\u4e8b\uff0c\u8fd9\u4e5f\u662f\u5f97\u5230\u4e2d\u592e\u9ed8\u8ba4\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u662f\u521a\u521a\u5728\u7535\u89c6\u521a\u770b\u5230\u8fd9\u65b0\u95fb\uff0c\u201d\u6797\u96ea\u5728\u7535\u8bdd\u90a3\u5934\u7b49\u4e86\u7247\u523b\uff0c\u672a\u542c\u89c1\u8c22\u5251\u5357\u6709\u56de\u5e94\uff0c\u201c\u73b0\u5728\u80fd\u5426\u7ea6\u8c22\u603b\u51fa\u6765\u8c08\u4e00\u8c08\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8ddf\u7236\u4eb2\u8fd8\u6709\u8c22\u665a\u5c71\u6253\u8fc7\u62db\u547c\uff0c\u5c31\u5f00\u8f66\u51fa\u53bb\u89c1\u6797\u96ea\uff0c\u4ed6\u4e5f\u7684\u786e\u4e5f\u60f3\u77e5\u9053\u6797\u96ea\u4e0e\u4e25\u6587\u4ecb\u4e4b\u524d\u5230\u5e95\u6709\u6ca1\u6709\u5f62\u6210\u9ed8\u5951\u3002</p><p>\u3000\u3000\u5728\u91d1\u5c71\u5927\u9152\u5e97\u697c\u4e0b\u7684\u5496\u5561\u5385\u91cc\uff0c\u8c22\u5251\u5357\u89c1\u5230\u6797\u96ea\u4e0e\u6d77\u7c9f\u79d1\u738b\u7684\u603b\u88c1\u738b\u6d77\u7c9f\uff0c\u4e0e\u738b\u6d77\u7c9f\u63a5\u89e6\u8fc7\u51e0\u6b21\u4e4b\u540e\uff0c\u4ed6\u5c31\u89c9\u5f97\u738b\u6d77\u7c9f\u8fd9\u4eba\u7684\u80fd\u529b\u5e76\u6ca1\u6709\u60f3\u8c61\u4e2d\u90a3\u4e48\u5f3a\uff0c\u5374\u662f\u4e0d\u77e5\u9053\u6797\u96ea\u4e3a\u4ec0\u4e48\u4e0d\u9057\u4f59\u529b\u7684\u4ee5\u6d77\u7c9f\u79d1\u6280\u4e3a\u4e2d\u5fc3\u6765\u542f\u52a8\u4e24\u5730\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u89c1\u738b\u6d77\u7c9f\u4e0d\u63a9\u9970\u4e0e\u6797\u96ea\u4e4b\u95f4\u7684\u4eb2\u6635\uff0c\u53ea\u5f53\u4f5c\u6ca1\u6709\u770b\u89c1\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u7684\u4eba\u4e5f\u4f4f\u5728\u8fd9\u91cc\uff0c\u521a\u770b\u5230\u4ed6\u4eec\u8fdb\u53bb\u2026\u2026\u201d\u6797\u96ea\u7a7f\u7740\u5b9d\u84dd\u8272\u7684\u7f0e\u8d28\u957f\u88d9\uff0c\u4f4e\u80f8\u3001\u9732\u80cc\uff0c\u5149\u6d01\u96ea\u767d\u7684\u8116\u5b50\u56f4\u7740\u4e00\u65b9\u4e1d\u5dfe\uff0c\u770b\u4e0a\u53bb\u9ad8\u8d35\u660e\u8273\uff0c\u8bf7\u8c22\u5251\u5357\u5750\u4e0b\u9053\uff0c\u201c\u4eca\u5929\u5927\u5bb6\u90fd\u91d1\u5c71\u6765\u5f00\u4f1a\uff0c\u542c\u8bf4\u662f\u4f60\u63d0\u8bae\u7684\uff0c\u4f46\u662f\u4e0b\u5348\u4f60\u7684\u8bdd\u53c8\u989d\u5916\u7684\u5c11\uff0c\u6211\u60f3\u7740\uff1a\u4f60\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u8bdd\u5355\u72ec\u8981\u8ddf\u6211\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8fd8\u5728\u60f3\u6797\u5c0f\u59d0\u6709\u6ca1\u6709\u4ec0\u4e48\u8bdd\u8981\u79c1\u4e0b\u91cc\u8ddf\u6211\u8bf4\u5462\u3002\u201d\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\uff1a\u6797\u96ea\u521a\u624d\u5e94\u8be5\u662f\u8ddf\u5f20\u606a\u4ed6\u4eec\u6b63\u9762\u9047\u4e0a\u4e86\u5427\uff1f</p><p>\u3000\u3000\u201c\u6211\u60f3\u77e5\u9053\u4f60\u5982\u4f55\u6b63\u9762\u8bc4\u4ef7\u79d1\u738b\u4e0e\u5609\u4fe1\u7684\u5173\u7cfb\uff1f\u201d\u6797\u96ea\u660e\u4eae\u8ff7\u4eba\u7684\u773c\u7738\u5b50\u4e00\u7741\uff0c\u770b\u7740\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u9762\u5bf9\u5b8f\u4fe1\u5efa\u8bbe\u4f38\u51fa\u6765\u7684\u89e6\u624b\uff0c\u4e09\u5bb6\u8981\u4e48\u5171\u8fdb\u9000\uff0c\u8981\u4e48\u4e24\u5bb6\u7ed3\u5408\u8d77\u6765\u727a\u7272\u53e6\u4e00\u5bb6\u7684\u5229\u76ca\uff0c\u5609\u4fe1\u5728\u9526\u57ce\u6301\u6709\u7684\u80a1\u4efd\u6700\u5927\uff0c\u727a\u7272\u5609\u4fe1\u7684\u5229\u76ca\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\u59a5\u534f\uff0c\u4e5f\u610f\u5473\u7740\u80fd\u6700\u5927\u7684\u4fdd\u7559\u7cbe\u5178\u4e0e\u79d1\u738b\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e0d\u80fd\u786e\u5b9a\u6797\u96ea\u4e4b\u524d\u6709\u6ca1\u6709\u8ddf\u845b\u660e\u4fe1\u3001\u845b\u836b\u5747\u7236\u5b50\u63a5\u89e6\u8fc7\uff0c\u53ea\u662f\u5f88\u5e73\u5e38\u7684\u8bf4\u9053\uff1a\u201c\u79d1\u738b\u4e0e\u5609\u4fe1\u5408\u4f5c\u8fd9\u51e0\u5e74\uff0c\u4e00\u76f4\u90fd\u662f\u76f8\u4e92\u4fe1\u4efb\u3001\u76f8\u4e92\u652f\u6301\u7684\u2026\u2026\u201d\u7edd\u53e3\u4e0d\u63d0\u4ed6\u4e0e\u845b\u836b\u5747\u7684\u79c1\u4ea4\u3002</p><p>\u3000\u3000\u6797\u96ea\u8bf4\u9053\uff1a\u201c\u9664\u4e86\u9526\u57ce\u5730\u4ea7\u53ef\u4ee5\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\u5408\u4f5c\u4e4b\u5916\uff0c\u91d1\u5c71\u7535\u5b50\u56ed\u6291\u6216\u91d1\u5c71\u8f6f\u4ef6\u56ed\u751a\u81f3\u6709\u53ef\u80fd\u83b7\u5f97\u4e0e\u65e5\u672c\u4e09\u4e95\u5408\u4f5c\uff0c\u4f60\u53c8\u5982\u4f55\u8bc4\u4ef7\u79d1\u738b\u4e0e\u5609\u4fe1\u7684\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u542c\u5230\u6797\u96ea\u8fd9\u4e48\u8bf4\uff0c\u8c22\u5251\u5357\u4e0b\u610f\u8bc6\u7684\u7741\u4e86\u7741\u773c\u775b\uff0c\u72d0\u7591\u7684\u770b\u7740\u6797\u96ea\u90a3\u5f20\u5996\u8273\u8ba9\u4eba\u96be\u4ee5\u4fe1\u4efb\u7684\u8138\u86cb\uff0c\u5927\u540d\u9f0e\u9f0e\u7684\u65e5\u7cfb\u7535\u5b50\u5382\u5546\u5de8\u5934\u7d22\u5c3c\u3001\u677e\u4e0b\u3001NEC\u3001\u590f\u666e\u90fd\u662f\u4e09\u4e95\u65d7\u4e0b\u7684\u6210\u5458\u4f01\u4e1a\uff0c\u91d1\u5c71\u7535\u5b50\u56ed\u5f53\u521d\u4e3a\u4e86\u83b7\u5f97\u4e09\u661f\u7684\u8ba2\u5355\u505a\u51fa\u8bb8\u591a\u7684\u59a5\u534f\uff0c\u8c22\u5251\u5357\u53ef\u4ece\u5934\u90fd\u6ca1\u6709\u5962\u671b\u80fd\u83b7\u5f97\u4e09\u4e95\u7684\u5408\u4f5c\uff0c\u4ed6\u538b\u6291\u4f4f\u5fc3\u91cc\u7684\u5174\u594b\uff0c\u95ee\u9053\uff1a\u201c\u80fd\u4e0e\u4e09\u4e95\u8fdb\u884c\u600e\u6837\u7684\u5408\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5609\u4fe1\u7535\u5b50\u80fd\u63d0\u4f9b\u7684\u6240\u6709\u6280\u672f\uff0c\u4e09\u4e95\u90fd\u80fd\u63d0\u4f9b\uff0c\u751a\u81f3\u613f\u610f\u63d0\u4f9b\u6bd4\u5609\u4fe1\u7535\u5b50\u66f4\u5148\u8fdb\u7684\u6280\u672f\uff0c\u800c\u4e14\u8fd9\u4e9b\u6280\u672f\u53ef\u4ee5\u76f4\u63a5\u63d0\u4f9b\u7ed9\u79d1\u738b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6797\u5c0f\u59d0\u8ddf\u6211\u5f00\u73a9\u7b11\u5427\uff0c\u201d\u8c22\u5251\u5357\u89c9\u5f97\u8fd9\u4e9b\u6761\u4ef6\u8fc7\u4e8e\u532a\u5937\u6240\u601d\u4e86\uff0c\u5f53\u521d\u845b\u660e\u4fe1\u7236\u5b50\u5c31\u9632\u5907\u7740\u8c22\u5bb6\u5bf9\u79d1\u738b\u7684\u63a7\u80a1\u6743\uff0c\u6ca1\u6709\u5c06\u6280\u672f\u76f4\u63a5\u7f6e\u5165\u79d1\u738b\u540d\u4e0b\uff0c\u800c\u662f\u53e6\u8d77\u7089\u7076\u5efa\u8bbe\u91d1\u5c71\u7535\u5b50\u56ed\uff0c\u8981\u662f\u80fd\u83b7\u5f97\u4e09\u4e95\u7684\u6280\u672f\u652f\u6301\uff0c\u4ed6\u4eec\u5b8c\u5168\u53ef\u4ee5\u5c06\u5609\u4fe1\u7535\u5b50\u7529\u5230\u4e00\u8fb9\uff0c\u4f46\u662f\u4ed6\u76f8\u4fe1\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u53d1\u751f\u5929\u4e0b\u6389\u9985\u997c\u8fd9\u79cd\u597d\u4e8b\uff0c\u538b\u6291\u7740\u5fc3\u91cc\u7684\u6fc0\u52a8\uff0c\u8bed\u6c14\u5e73\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u6797\u5c0f\u59d0\u4f55\u65f6\u53c8\u6210\u4e86\u4e09\u4e95\u7684\u4ee3\u8a00\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c22\u603b\u660e\u5929\u80fd\u5426\u62bd\u51fa\u65f6\u95f4\u968f\u6211\u4e00\u8d77\u53bb\u5317\u4eac\u89c1\u4e09\u4e95\u7684\u6c60\u4f50\u79c0\u85cf\uff1f\u6211\u5bf9\u7535\u5b50\u884c\u4e1a\u4e0d\u751a\u719f\u6089\uff0c\u5c45\u4e2d\u4f20\u8bdd\u603b\u662f\u4e0d\u5927\u65b9\u4fbf\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u8fd8\u662f\u9700\u8981\u4f60\u4eec\u89c1\u9762\u8c08\u624d\u5408\u9002\uff1b\u53e6\u5916\uff0c\u534e\u590f\u7535\u5b50\u5bf9\u79d1\u738b\u4e0e\u4e09\u4e95\u7684\u5408\u4f5c\u4e5f\u5f88\u611f\u5174\u8da3\uff01\u201d\u6797\u96ea\u624d\u4e0d\u4fe1\u8fd9\u4e9b\u6761\u4ef6\u4f1a\u8ba9\u8c22\u5251\u5357\u4e0d\u52a8\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u5973\u4eba\u5728\u697c\u4e0b\u5496\u5561\u5385\u91cc\u4e0e\u79d1\u738b\u7684\u8c22\u5251\u5357\u89c1\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u521a\u624d\u8fdb\u9152\u5e97\u91cc\u770b\u5230\u7cbe\u5178\u5730\u4ea7\u7684\u603b\u88c1\u6797\u96ea\u4e0e\u738b\u6d77\u7c9f\u8d70\u8fdb\u5e95\u697c\u5927\u5802\u5de6\u4fa7\u7684\u5496\u5561\u5385\u91cc\uff0c\u7fdf\u4e39\u9752\u4e0a\u697c\u540e\u53c8\u8ba9\u4e00\u540d\u5de5\u4f5c\u4eba\u5458\u5230\u5e95\u697c\u5927\u5802\u7684\u5496\u5561\u5385\u90a3\u91cc\u8f6c\u4e86\u4e00\u5708\uff0c\u770b\u5230\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u5728\u5496\u5561\u5385\u91cc\u662f\u4e0e\u8c22\u5251\u5357\u89c1\u9762\u3002\u7fdf\u4e39\u9752\u8d70\u8fdb\u6765\u8ddf\u5f20\u606a\u8bf4\u8fd9\u4e8b\u65f6\uff0c\u5f20\u606a\u4e0e\u5c0f\u53d4\u5f20\u77e5\u975e\u5750\u5728\u5957\u623f\u9633\u53f0\u7cbe\u81f4\u85e4\u7f16\u5706\u6905\u4e0a\u559d\u8336\u804a\u5929\u3002</p><p>\u3000\u3000\u542c\u8bf4\u6797\u96ea\u662f\u5728\u5496\u5561\u5385\u91cc\u4e0e\u8c22\u5251\u5357\u78b0\u9762\uff0c\u5f20\u606a\u6ca1\u6709\u591a\u60f3\uff0c\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\u8ddf\u5c0f\u53d4\u8bf4\u9053\uff1a\u201c\u5176\u4ed6\u7684\uff0c\u6211\u4eec\u90fd\u7ba1\u4e0d\u7740\uff0c\u90fd\u4e0d\u53bb\u7ba1\uff0c\u6211\u4eec\u62ff\u51fa\u6765\u7684\u94b1\uff0c\u5012\u662f\u8981\u7528\u597d\u4e86\u2014\u2014\u91d1\u5c71\u8fd9\u8fb9\u7684\u91cd\u5efa\u4efb\u52a1\u7e41\u91cd\uff0c\u5c0f\u53d4\u4f60\u80fd\u7559\u5728\u8fd9\u91cc\u5c31\u518d\u597d\u4e0d\u8fc7\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e4b\u524d\u8bf4\u7684\u5b8f\u4fe1\u5efa\u8bbe\uff0c\u90b5\u81f3\u521a\u7279\u610f\u6d3e\u4eba\u5230\u5929\u6d25\u3001\u5317\u4eac\u8c03\u67e5\u8fc7\uff0c\u8fd9\u91cc\u9762\u6c34\u4e0d\u6d45\uff0c\u9664\u4e86\u623f\u5730\u4ea7\u4e4b\u5916\uff0c\u4ed6\u4eec\u8fd8\u627f\u63fd\u5730\u65b9\u4e0a\u7684\u5e02\u653f\u57fa\u5efa\u5de5\u7a0b\uff0c\u800c\u4e14\u8fd0\u4f5c\u7684\u6a21\u5f0f\u56fd\u5185\u5f88\u7f55\u89c1\uff0c\u201d\u5f20\u77e5\u975e\u8bf4\u9053\uff0c\u201c\u5b8f\u4fe1\u5efa\u8bbe\u627f\u63fd\u5e02\u653f\u5de5\u7a0b\u4e4b\u540e\uff0c\u5148\u57ab\u8d44\u5efa\u8bbe\uff0c\u5de5\u7a0b\u5efa\u8bbe\u5b8c\u6210\u4e4b\u540e\uff0c\u7531\u653f\u5e9c\u4ee5\u5206\u671f\u4ed8\u6b3e\u7684\u5f62\u5f0f\u56de\u8d2d\u2026\u2026\u800c\u5b8f\u4fe1\u5efa\u8bbe\u5de5\u7a0b\u5efa\u8bbe\u6240\u57ab\u4ed8\u7684\u8d44\u91d1\u4e5f\u591a\u4e3a\u4ee5\u4fe1\u901a\u94f6\u884c\u4e3a\u4e3b\u7684\u91d1\u878d\u673a\u6784\u63d0\u4f9b\u7684\u8d37\u6b3e\uff0c\u4ed6\u4eec\u51e0\u4e4e\u53ef\u4ee5\u65e0\u9650\u7684\u5c06\u8fd9\u4e00\u6a21\u5f0f\u8fd0\u4f5c\u653e\u5927\uff0c\u57ab\u8d44\u8d37\u6b3e\u4e0e\u653f\u5e9c\u56de\u8d2d\u540e\u5206\u671f\u4ed8\u6b3e\u7684\u606f\u5dee\u5c31\u6784\u6210\u5b8f\u4fe1\u5efa\u8bbe\u7684\u66b4\u5229\u3002\u8981\u8bf4\u6709\u9650\u5236\uff0c\u90a3\u6700\u5927\u7684\u9650\u5236\u5927\u6982\u4e5f\u5c31\u662f\u5b8f\u4fe1\u5efa\u8bbe\u4e0e\u5730\u65b9\u653f\u5e9c\u5173\u7cfb\u7684\u5bc6\u5207\u7a0b\u5ea6\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u634f\u4e86\u634f\u7709\u5c16\uff0c\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u8fd0\u8425\u6a21\u5f0f\u5728\u8d22\u653f\u7f3a\u7d27\u3001\u515a\u653f\u9886\u5bfc\u53c8\u559c\u6b22\u641e\u9762\u5b50\u5de5\u7a0b\u7684\u5730\u65b9\u9887\u53d7\u6b22\u8fce\u2014\u2014\u4e1c\u6d77\u7701\u5730\u65b9\u8d22\u653f\u8fd8\u7b97\u5bbd\u88d5\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u8fdb\u6765\u8fd0\u4f5c\u7684\u4f59\u5730\u5012\u4e0d\u4f1a\u7279\u522b\u7684\u5927\uff0c\u6211\u770b\u4e25\u6587\u4ecb\u770b\u91cd\u4e1c\u6d77\u7684\u8fd8\u662f\u623f\u5730\u4ea7\u4e1a\uff0c\u6709\u5176\u4ed6\u7684\u610f\u56fe\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u201d</p><p>\u3000\u3000\u53d6\u6d88\u57ce\u9547\u804c\u5de5\u798f\u5229\u5206\u623f\u5236\u5ea6\u63a8\u52a8\u4f4f\u623f\u5546\u54c1\u5316\u6539\u9769\u6839\u636e\u5b9e\u9645\u60c5\u51b5\uff0c\u5404\u7701\u5e02\u7684\u63a8\u8fdb\u7a0b\u5ea6\u4e0d\u4e00\uff0c\u9700\u8981\u76f8\u5f53\u7684\u7f13\u51b2\u65f6\u95f4\u624d\u80fd\u53d1\u6325\u8fd9\u9879\u653f\u7b56\u5bf9\u4f4f\u623f\u5e02\u573a\u7684\u5f3a\u5927\u4fc3\u8fdb\u4f5c\u7528\u3002\u5f53\u7136\uff0c\u4e00\u5ea7\u57ce\u5e02\u7ecf\u6d4e\u7684\u6d3b\u8dc3\u7a0b\u5ea6\u4ee5\u53ca\u6d8c\u5165\u7684\u6d41\u52a8\u4eba\u53e3\u6570\u91cf\u90fd\u662f\u4fc3\u8fdb\u5730\u533a\u623f\u5730\u4ea7\u5e02\u573a\u7684\u6838\u5fc3\u56e0\u7d20\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u9634\u973e\u96f7\u4e91\u4e0b\uff0c\u56fd\u5185\u7ecf\u6d4e\u53d1\u8fbe\u7684\u4e3b\u8981\u7701\u5e02\u90fd\u5728\u79ef\u6781\u7684\u62c9\u52a8\u5185\u9700\u4fdd\u589e\u6da8\uff0c\u5bfb\u6c42\u6446\u8131\u91d1\u878d\u98ce\u66b4\u8d1f\u9762\u5f71\u54cd\u7684\u826f\u65b9\uff0c\u4e1c\u6d77\u7701\u7684\u7ecf\u6d4e\u7a0b\u5ea6\u4e3e\u56fd\u77a9\u76ee\u3002</p><p>\u3000\u3000\u5b8f\u4fe1\u5efa\u8bbe\u4e0d\u60f3\u518d\u5c40\u9650\u4e8e\u6d25\u4eac\u4e00\u9685\uff0c\u65d7\u4e0b\u7684\u623f\u5730\u4ea7\u53ca\u5e02\u653f\u57fa\u7840\u5de5\u7a0b\u4e1a\u52a1\u5411\u5168\u56fd\u5e03\u5c40\uff0c\u4e1c\u6d77\u7701\u65e0\u7591\u662f\u6700\u4f73\u7684\u524d\u54e8\u6218\u9635\u5730\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u4e25\u6587\u4ecb\u968f\u540c\u6797\u96ea\u51fa\u73b0\u5728\u5b66\u5e9c\u5e9c\uff0c\u5c31\u4e0d\u518d\u6000\u7591\u8fd9\u4e2a\uff0c\u53e6\u5916\u4ed6\u4e5f\u62c5\u5fc3\u4e25\u6587\u4ecb\u51fa\u73b0\u5728\u4e1c\u6d77\u8fd8\u6709\u5176\u7684\u610f\u56fe\u3002</p></article>"}}}]);
