(window.webpackJsonp=window.webpackJsonp||[]).push([[90],{113:function(p){p.exports={chapterTitle:null,title:"\u7b2c1076\u7ae0 \u7537\u4eba\u7684\u5fc3",content:"<article><h2>\u7b2c1076\u7ae0 \u7537\u4eba\u7684\u5fc3</h2><p>\u3000\u3000\u6392\u5b9a\u7684\u6f14\u5531\u8282\u76ee\u7ed3\u675f\u4e86\uff0c\u75f4\u604b\u7684\u6b4c\u8ff7\u5927\u591a\u4e0d\u80af\u79bb\u573a\uff0c\u6c5f\u9edb\u513f\u867d\u7136\u60e6\u8bb0\u7740\u540e\u53f0\uff0c\u4e5f\u6ca1\u6709\u4e00\u8d70\u4e86\u4e4b\uff0c\u5728\u821e\u53f0\u4e0a\u8c22\u5e55\u8c22\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u9000\u56de\u540e\u53f0\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e0d\u77e5\u9053\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\uff0c\u4e0d\u77e5\u9053\u5f20\u606a\u7684\u8138\u7ed9\u5979\u5988\u6293\u5f97\u4e25\u4e0d\u4e25\u91cd\uff0c\u8d70\u56de\u540e\u53f0\uff0c\u770b\u5230\u6709\u8b66\u5458\u5728\u73b0\u573a\u8bb0\u7b14\u5f55\uff0c\u5413\u4e86\u4e00\u8df3\uff0c\u60f3\u8981\u95ee\u4ec0\u4e48\uff0c\u770b\u5230\u5510\u5a67\u7684\u773c\u775b\u671d\u81ea\u5df1\u770b\u8fc7\u6765\uff0c\u610f\u8bc6\u5230\u81ea\u5df1\u8fd8\u7a7f\u7740\u5f20\u606a\u7684\u886c\u886b\uff0c\u8d76\u5fd9\u8eb2\u5230\u66f4\u8863\u5ba4\u5148\u6362\u8863\u670d\u53bb\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5e2e\u5979\u5c06\u8863\u670d\u62ff\u8fdb\u6765\uff0c\u6c5f\u9edb\u513f\u5c0f\u58f0\u7684\u95ee\u5979\uff1a\u201c\u5230\u5e95\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u89c1\u5f20\u606a\u6252\u9edb\u513f\u8863\u670d\u524d\u7adf\u7136\u6ca1\u6709\u89e3\u91ca\u4e00\u4e0b\uff0c\u6216\u8bb8\u662f\u65f6\u95f4\u592a\u4ed3\u4fc3\u6765\u4e0d\u53ca\u89e3\u91ca\uff0c\u53ea\u662f\u9edb\u513f\u73b0\u5728\u8fd9\u526f\u6837\u5b50\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u60f3\u5728\u5979\u5a07\u5ae9\u7684\u8138\u86cb\u72e0\u72e0\u7684\u6390\u4e86\u4e00\u4e0b\uff0c\u538b\u7740\u58f0\u97f3\u5632\u7b11\u5979\u8bf4\u9053\uff1a\u201c\u90a3\u4e2a\u6df7\u86cb\u5728\u8fc7\u9053\u91cc\u6252\u4f60\u7684\u8863\u670d\uff0c\u4f60\u90fd\u4e0d\u751f\u6c14\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9edb\u513f\u6ee1\u8138\u7eef\u7ea2\uff0c\u5fc3\u60f3\u81ea\u5df1\u771f\u5e94\u8be5\u8981\u5f88\u751f\u6c14\u624d\u662f\uff0c\u77e5\u9053\u6597\u5fc3\u773c\u5979\u8ddf\u8bb8\u7ef4\u518d\u52a0\u6797\u51b0\u4e09\u4e2a\u4eba\u90fd\u6597\u4e0d\u8fc7\u4ee4\u5c0f\u71d5\uff0c\u63a8\u4e86\u5979\u4e00\u628a\uff0c\u63a9\u8033\u76d7\u94c3\u7684\u8bf4\u9053\uff0c\u201c\u5f53\u65f6\u6709\u751f\u6c14\u6765\u7740\uff0c\u4ed6\u8bf4\u8981\u6362\u8863\u670d\uff0c\u5c31\u83ab\u660e\u5176\u5999\u7684\u4e00\u5b9a\u8981\u8ddf\u4ed6\u6362\u4e86\u8863\u670d\uff0c\u4e0a\u53f0\u4e4b\u524d\u8fd8\u4e00\u76f4\u751f\u6c14\u6765\u7740\uff0c\u8df3\u5230\u821e\u53f0\u4e0a\uff0c\u4e5f\u53ea\u80fd\u5f3a\u8feb\u81ea\u5df1\u5fd8\u6389\u8fd9\u4e8b\uff0c\u73b0\u5728\u821e\u53f0\u4e0a\u7684\u5174\u594b\u52b2\u8fd8\u6ca1\u6709\u8fc7\u5462\u2026\u2026\u4f60\u5012\u662f\u8bf4\u600e\u4e48\u56de\u4e8b\u554a\uff1f\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u9119\u5937\u7684\u77a5\u4e86\u9edb\u513f\u4e24\u773c\uff0c\u624d\u5c06\u539f\u59d4\u544a\u8bc9\u5979\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u6c5f\u9edb\u513f\u54ea\u91cc\u4f1a\u60f3\u5230\u6709\u8fd9\u4e48\u9634\u72e0\u7684\u9634\u8c0b\u7b49\u7740\u5979\u8df3\u8fdb\u53bb\uff0c\u60f3\u60f3\u90fd\u89c9\u5f97\u5fc3\u91cc\u53d1\u51c9\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u53ca\u65f6\u53d1\u73b0\uff0c\u5979\u771f\u4e0d\u77e5\u9053\u8fd9\u4e8b\u60c5\u8981\u5982\u4f55\u6536\u62fe\u3002\u6c5f\u9edb\u513f\u5012\u662f\u8ba4\u8bc6\u5370\u5c3c\u56fd\u65d7\u4e0e\u56fd\u5fbd\u56fe\u6848\u7684\uff0c\u53ea\u662f\u6f14\u5531\u4f1a\u4e2d\u95f4\u9000\u5230\u540e\u53f0\u6765\u6362\u6700\u540e\u4e00\u5957\u8863\u670d\u7684\u65f6\u95f4\u53ea\u6709\u4e00\u4e24\u5206\u949f\uff0c\u65f6\u95f4\u5982\u6b64\u7684\u4ed3\u4fc3\uff0c\u5979\u8fd8\u8981\u56de\u60f3\u63a5\u4e0b\u6765\u6f14\u5531\u6b4c\u66f2\u7684\u6b4c\u8bcd\uff0c\u8fd8\u8981\u8865\u5986\uff0c\u8fd8\u8981\u542c\u821e\u53f0\u9020\u578b\u5e08\u8bb2\u89e3\u63a5\u4e0b\u6765\u821e\u53f0\u52a8\u88c5\u7684\u8981\u9886\uff0c\u54ea\u91cc\u4f1a\u60f3\u5230\u6709\u4eba\u4f1a\u5728\u670d\u88c5\u4e0a\u52a8\u624b\u811a\uff1f\u5979\u751a\u81f3\u90fd\u6ca1\u6709\u65f6\u95f4\u5728\u5316\u5986\u955c\u91cc\u770b\u81ea\u5df1\u6700\u540e\u7684\u9020\u578b\u662f\u4ec0\u4e48\u6837\u5b50\u3002\u8fd9\u4e48\u4ed3\u4fc3\u7684\u65f6\u95f4\uff0c\u5c31\u7b97\u522b\u4eba\u89c9\u5f97\u670d\u88c5\u6709\u95ee\u9898\uff0c\u4e5f\u6839\u672c\u6ca1\u6709\u65f6\u95f4\u5f80\u6df1\u91cc\u60f3\u3002</p><p>\u3000\u3000\u201c\u5b59\u59d0\u901a\u77e5\u8b66\u65b9\u4ecb\u5165\u4e86\uff0c\u53ef\u80fd\u662f\u5bf9\u624b\u641e\u7684\u624b\u811a\uff0c\u201d\u4ee4\u5c0f\u71d5\u8ddf\u9edb\u513f\u8bf4\u5916\u9762\u7684\u60c5\u51b5\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u4f60\u5f53\u65f6\u8ddf\u5f20\u606a\u662f\u600e\u4e48\u6362\u8863\u670d\u7684\uff0c\u8ba9\u4f60\u5988\u770b\u5230\u8ddf\u53ea\u6bcd\u8001\u864e\u7684\u8981\u5403\u4eba\u4f3c\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u6c5f\u9edb\u513f\u5f53\u7136\u4e0d\u4f1a\u8ddf\u4ee4\u5c0f\u71d5\u7ec6\u8bf4\u7ed9\u5f20\u606a\u6252\u8863\u670d\u65f6\u7684\u7ec6\u8282\uff0c\u79c0\u6c14\u7684\u7f8e\u7738\u8f6c\u4e86\u8f6c\uff0c\u8bf4\u9053\uff0c\u201c\u5f53\u65f6\u6025\u7740\u8981\u51fa\u573a\uff0c\u6362\u8863\u670d\u624b\u5fd9\u811a\u4e71\u7684\uff0c\u8c01\u77e5\u9053\u6211\u5988\u770b\u6210\u4ec0\u4e48\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u4e5f\u4e0d\u77e5\u9053\u5979\u4e0e\u5f20\u606a\u662f\u600e\u4e48\u6362\u8863\u670d\u7684\uff0c\u9edb\u513f\u5979\u5988\u8ddf\u8bb8\u7ef4\u3001\u5b59\u9759\u9999\u770b\u5230\u4e86\uff0c\u4f46\u662f\u5510\u5a67\u8fdb\u6765\u540e\uff0c\u5979\u4eec\u53ea\u8bf4\u60c5\u6025\u4e4b\u4e0b\u8bef\u4f1a\u5f20\u606a\u4e86\uff0c\u4e5f\u4e0d\u8bf4\u7a76\u7adf\u662f\u600e\u4e48\u8bef\u4f1a\u5f20\u606a\u4e86\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u6362\u597d\u8863\u670d\uff0c\u62ff\u7740\u5f20\u606a\u7684\u886c\u886b\u8d70\u51fa\u53bb\uff0c\u9012\u7ed9\u5510\u5a67\uff1a\u201c\u4f60\u5e2e\u6211\u8c22\u8c22\u5f20\u606a\uff0c\u771f\u4e8f\u4e86\u4ed6\uff0c\u4e0d\u7136\u90fd\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u597d\u2026\u2026\u201d\u53c8\u671d\u5979\u5988\u57cb\u6028\u9053\uff0c\u201c\u5988\uff0c\u4f60\u4e5f\u771f\u662f\u7684\uff0c\u4e0d\u5206\u9752\u7ea2\u7682\u767d\u5c31\u90a3\u6837\uff0c\u6211\u5f53\u65f6\u6025\u7740\u4e0a\u573a\uff0c\u624b\u5fd9\u811a\u4e71\u7684\uff0c\u54ea\u6709\u65f6\u5019\u8ddf\u4f60\u89e3\u91ca\u8fd9\u4e9b\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u674e\u6708\u8339\u6b32\u8a00\u53c8\u6b62\uff0c\u5979\u77e5\u9053\u521a\u624d\u592a\u51b2\u52a8\uff0c\u9a82\u5f97\u51f6\u8fd8\u6293\u7834\u4e86\u5f20\u606a\u7684\u8138\uff0c\u8fd8\u4e00\u53e3\u553e\u6cab\u55b7\u5230\u5085\u4fca\u7684\u8138\u4e0a\uff0c\u6127\u759a\u5230\u6781\u70b9\u53c8\u4e0d\u77e5\u9053\u600e\u4e48\u529e\u624d\u597d\uff0c\u4efb\u5979\u5e73\u65f6\u518d\u6cfc\u8fa3\uff0c\u8fd9\u65f6\u5019\u90fd\u96be\u8fc7\u5f97\u8981\u54ed\u51fa\u6765\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u8138\u8f6c\u5411\u5316\u5986\u955c\uff0c\u542c\u6c5f\u9edb\u513f\u8fd9\u4e48\u8bf4\uff0c\u597d\u50cf\u5979\u5f53\u65f6\u771f\u7684\u53ea\u662f\u624b\u5fd9\u811a\u4e71\u7684\u8ba9\u5f20\u606a\u5e2e\u7740\u7a7f\u886c\u886b\uff0c\u9b3c\u624d\u4fe1\u6c5f\u9edb\u513f\u7684\u8bdd\uff0c\u77e5\u9053\u5979\u8fd9\u4e48\u8bf4\u662f\u4e3a\u4e86\u6d88\u9664\u5510\u5a67\u5fc3\u91cc\u7684\u7591\u8651\uff0c\u8fd9\u9b3c\u4e2b\u5934\u5584\u826f\u5f52\u5584\u826f\uff0c\u8111\u5b50\u4e0d\u6bd4\u8c01\u7b28\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u89c1\u4ee4\u5c0f\u71d5\u773c\u89d2\u542b\u7740\u7b11\uff0c\u60f3\u6765\u5979\u4e5f\u770b\u51fa\u7834\u7efd\uff0c\u8d76\u5fd9\u7ed9\u5979\u4f7f\u4e86\u4e2a\u773c\u8272\uff0c\u8981\u662f\u8ba9\u5f20\u606a\u540e\u9662\u8d77\u706b\uff0c\u4eca\u5929\u624d\u771f\u53eb\u4e0d\u5f97\u5b89\u5b81\u5462\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5510\u5a67\u5c06\u886c\u886b\u4ece\u9edb\u513f\u624b\u91cc\u63a5\u8fc7\u6765\uff0c\u653e\u5728\u819d\u76d6\u4e0a\u644a\u5e73\u4e86\u53c8\u6402\u5728\u6000\u91cc\uff0c\u89c1\u6c5f\u9edb\u513f\u6bcd\u4eb2\u8138\u4e0a\u795e\u8272\u6ee1\u662f\u6127\u759a\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6c5f\u963f\u59e8\uff0c\u4e5f\u602a\u5f20\u606a\u6ca1\u8ddf\u4f60\u597d\u597d\u89e3\u91ca\u6e05\u695a\uff0c\u4e07\u5e78\u662f\u6700\u62c5\u5fc3\u7684\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\uff0c\u6ca1\u6709\u8ba9\u522b\u4eba\u9488\u5bf9\u9edb\u513f\u59d0\u7684\u9634\u8c0b\u5f97\u901e\u2026\u2026\u5927\u5bb6\u662f\u4e0d\u662f\u51c6\u5907\u51c6\u5907\u5f00\u5e86\u529f\u5bb4\uff1f\u6211\u5c06\u886c\u886b\u62ff\u7ed9\u5f20\u606a\u8fc7\u53bb\uff01\u201d</p><p>\u3000\u3000\u770b\u7740\u5510\u5a67\u79bb\u5f00\u5316\u5986\u5ba4\uff0c\u5b59\u9759\u9999\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u89c1\u5927\u5bb6\u90fd\u5782\u5934\u4e27\u6c14\u7684\uff0c\u62cd\u4e86\u62cd\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u522b\u4eba\u641e\u8fd9\u6837\u7684\u5c0f\u52a8\u4f5c\uff0c\u5927\u5bb6\u5fc3\u60c5\u7684\u786e\u4f1a\u6709\u4e9b\u4e0d\u597d\u53d7\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u6211\u4eec\u6233\u7a7f\u4e86\u654c\u4eba\u7684\u9634\u8c0b\uff0c\u6f14\u5531\u4f1a\u53c8\u5982\u671f\u7684\u83b7\u5f97\u6210\u529f\uff0c\u8fd9\u5e86\u529f\u5bb4\u66f4\u8981\u597d\u597d\u7684\u4e3e\u529e\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u5b59\u9759\u9999\u89c1\u662f\u53f6\u5efa\u658c\u7684\u7535\u8bdd\uff0c\u53ec\u624b\u5c06\u8bb8\u7ef4\u558a\u8fc7\u6765\uff0c\u8ba9\u5979\u8d1f\u8d23\u51c6\u5907\u5e86\u529f\u5bb4\u7684\u4e8b\u60c5\uff0c\u5979\u8d70\u5230\u5176\u4ed6\u623f\u95f4\u4e0e\u53f6\u5efa\u658c\u901a\u8bdd\uff0c\u5c06\u521a\u624d\u53d1\u751f\u7684\u4ee4\u4eba\u6124\u6012\u5230\u6781\u70b9\u7684\u4e8b\u60c5\u8bf4\u7ed9\u53f6\u5efa\u658c\u542c\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u662f\u82f1\u738b\u56fd\u9645\u5728\u80cc\u5730\u641e\u5c0f\u52a8\u4f5c\uff0c\u201d\u5b59\u9759\u9999\u8bf4\u9053\uff0c\u201c\u9edb\u513f\u7684\u5531\u7247\u9500\u91cf\u8fde\u7eed\u4e24\u5c4a\u593a\u51a0\uff0c\u4ed6\u4eec\u800d\u8fd9\u79cd\u9634\u8c0b\u60f3\u8981\u6bc1\u6389\u9edb\u513f\u624d\u9ad8\u5174\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u73b0\u5728\u5728\u673a\u573a\uff0c\u4e24\u4e2a\u534a\u5c0f\u65f6\u540e\u4f1a\u5230\u9999\u6e2f\uff0c\u5230\u9999\u6e2f\u624d\u8bf4\u8fd9\u4e8b\u5427\uff0c\u4f60\u5148\u6d88\u6d88\u6c14\uff0c\u4e0d\u8981\u6c14\u574f\u4e86\u8eab\u5b50\uff0c\u8ddf\u5927\u5bb6\u5f00\u5f00\u5fc3\u5fc3\u7684\u53c2\u52a0\u5e86\u529f\u5bb4\uff0c\u201d\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u91cc\u5b89\u6170\u5b59\u9759\u9999\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u4e0d\u662f\u8bf4\u5f20\u606a\u7684\u8138\u5dee\u70b9\u7834\u76f8\u5417\uff1f\u5c31\u51ed\u8fd9\u70b9\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u4f1a\u4e3a\u8fd9\u4e9b\u6df7\u86cb\u6c14\u574f\u8eab\u5b50\uff0c\u201d\u5b59\u9759\u9999\u8bf4\u9053\uff0c\u201c\u54e6\uff0c\u5f20\u606a\u90a3\u5c0f\u5b50\u4e5f\u771f\u662f\u7684\uff0c\u4ed6\u5e73\u65f6\u7259\u5c16\u5634\u5229\u7684\uff0c\u518d\u4ed3\u4fc3\u6362\u8863\u670d\u4e5f\u53ef\u4ee5\u5148\u8ddf\u9edb\u513f\u89e3\u91ca\u4e00\u4e0b\u554a\uff0c\u73b0\u5728\u95f9\u51fa\u8fd9\u4e9b\u8bef\u4f1a\u6765\uff0c\u4ed6\u8138\u4e0a\u7ed9\u6320\u4e00\u4e0b\uff0c\u641e\u5f97\u5927\u5bb6\u90fd\u5f88\u5c34\u5c2c\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u8981\u4e0d\u8981\u8bf7\u4ed6\u53c2\u52a0\u5e86\u529f\u5bb4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e00\u5b9a\u8981\u8bf7\u4ed6\u53c2\u52a0\uff0c\u4ed6\u7684\u8138\u8981\u662f\u7834\u76f8\u4e5f\u662f\u4ed6\u6d3b\u8be5\uff0c\u4ed6\u81ea\u5df1\u8c03\u620f\u5c0f\u59d1\u5a18\u5f04\u5de7\u6210\u62d9\uff0c\u8fd8\u80fd\u6028\u5f97\u4e86\u522b\u4eba\uff1f\u201d\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u90a3\u5934\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u9759\u9999\u6709\u4e9b\u7591\u60d1\u4e0d\u89e3\uff0c\u4e0d\u77e5\u9053\u53f6\u5efa\u658c\u4e3a\u4ec0\u4e48\u8bf4\u5f20\u606a\u6d3b\u8be5\u3002</p><p>\u3000\u3000\u201c\u6709\u8fd9\u4e48\u597d\u7684\u501f\u53e3\u4e0d\u7531\u5206\u8bf4\u7684\u76f4\u63a5\u5c31\u6252\u4eba\u5bb6\u5c0f\u59d1\u5a18\u7684\u8863\u670d\uff0c\u89e3\u91ca\u4e86\u591a\u6ca1\u8da3\uff1f\u201d\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u90a3\u5934\u574f\u7b11\u8d77\u6765\uff0c\u201c\u8fd9\u5c0f\u5b50\u4ed6\u6ca1\u6709\u60f3\u5230\u5c0f\u59d1\u5a18\u5979\u5988\u4e5f\u4e0d\u7531\u5206\u8bf4\u7684\u76f4\u63a5\u5c31\u4f7f\u722a\u5b50\u6320\u4ed6\u554a\uff0c\u4f60\u8bf4\u8fd9\u5c0f\u5b50\u503c\u4e0d\u503c\u5f97\u540c\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5b59\u9759\u9999\u604d\u7136\u660e\u767d\u8fc7\u6765\uff0c\u96be\u602a\u5f20\u606a\u7ed9\u674e\u6708\u8339\u6320\u4e86\u4e24\u4e0b\u5c31\u843d\u8352\u800c\u9003\uff0c\u539f\u6765\u4ed6\u4e5f\u662f\u5fc3\u865a\u554a\uff0c\u6251\u54e7\u7b11\u4e86\u8d77\u6765\uff0c\u5bf9\u7535\u8bdd\u90a3\u5934\u7684\u53f6\u5efa\u658c\u9a82\u9053\uff0c\u201c\u4f60\u4eec\u8fd9\u4e9b\u81ed\u7537\u4eba\u554a\uff0c\u6ca1\u6709\u4e00\u4e2a\u597d\u4e1c\u897f\u3002\u201d</p><p>\u3000\u3000\u201c\u53c8\u5173\u6211\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u5c31\u80af\u5b9a\u5f20\u606a\u90a3\u5c0f\u5b50\u662f\u8fd9\u4e48\u60f3\u7684\uff1f\u201d\u5b59\u9759\u9999\u53cd\u95ee\u9053\uff0c\u201c\u8fd8\u4e0d\u662f\u4f60\u4eec\u81ed\u6c14\u76f8\u6295\u3001\u7269\u4ee5\u7c7b\u805a\uff0c\u90fd\u6709\u4e00\u9897\u7325\u7410\u7684\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5f97\uff0c\u6211\u8981\u767b\u673a\u4e86\uff0c\u4e0d\u8ddf\u4f60\u6597\u5634\u2026\u2026\u201d\u53f6\u5efa\u658c\u6551\u9976\u7684\u6302\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u6302\u4e86\u7535\u8bdd\uff0c\u5728\u623f\u95f4\u5728\u8fc7\u4e86\u51e0\u79d2\u949f\uff0c\u8ba9\u81ea\u5df1\u8138\u4e0a\u7684\u7b11\u610f\u6d88\u6389\uff0c\u624d\u8d70\u8fc7\u623f\u95f4\uff0c\u8ba9\u5927\u5bb6\u52a8\u4f5c\u9ebb\u5229\u4e9b\uff0c\u5750\u8f66\u8d76\u5230\u9884\u5b9a\u597d\u7684\u534a\u5c9b\u9152\u5e97\u5f00\u5e86\u529f\u5bb4\uff0c\u89c1\u6c5f\u9edb\u513f\u8ddf\u5979\u7236\u6bcd\u4e0d\u5728\u4f11\u606f\u5ba4\uff0c\u95ee\u4ee4\u5c0f\u71d5\uff1a\u201c\u9edb\u513f\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u8138\u53ef\u80fd\u7ed9\u6293\u5f97\u86ee\u4e25\u91cd\u7684\uff0c\u8fd9\u65f6\u5019\u5728\u533b\u52a1\u5ba4\u4e0a\u836f\uff0c\u9edb\u513f\u8ddf\u5979\u7236\u6bcd\u8fc7\u53bb\u9053\u6b49\u4e86\u2026\u2026\u201d\u4ee4\u5c0f\u71d5\u8bf4\u9053\uff0c\u8fd9\u79cd\u573a\u5408\u633a\u5c34\u5c2c\u7684\uff0c\u5979\u8ddf\u8bb8\u7ef4\u8fd8\u6709\u6797\u51b0\u90fd\u4e0d\u597d\u610f\u601d\u8ddf\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u5f53\u7136\u4e0d\u4f1a\u8ddf\u5979\u4eec\u8bf4\u5f20\u606a\u90a3\u662f\u6d3b\u8be5\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff0c\u95ee\u8bb8\u7ef4\u7ed9\u5de5\u4f5c\u51c6\u5907\u7ea2\u5305\u7684\u4e8b\u60c5\u4e0d\u80fd\u5fd8\u4e86\uff0c\u867d\u7136\u53d1\u751f\u4e86\u8fd9\u6837\u4ee4\u4eba\u4e0d\u5f00\u5fc3\u7684\u4e8b\u60c5\uff0c\u7edd\u5927\u90e8\u5206\u5de5\u4f5c\u4eba\u5458\u5de5\u4f5c\u8fd8\u662f\u5162\u5162\u4e1a\u4e1a\u7684\uff0c\u4e0d\u80fd\u5bd2\u4e86\u4ed6\u4eec\u7684\u5fc3\uff0c\u53c8\u8ba9\u4eba\u51c6\u5907\u8f66\u5b50\u8f7d\u7740\u5927\u5bb6\u53bb\u9152\u5e97\uff0c\u4f11\u606f\u5ba4\u7684\u95e8\u6253\u5f00\uff0c\u770b\u89c1\u9edb\u513f\u5979\u7238\u5988\u8d70\u4e86\u8fdb\u6765\uff0c\u5b59\u9759\u9999\u6123\u4e86\u6123\uff1a\u4ee4\u5c0f\u71d5\u4e0d\u662f\u8bf4\u4ed6\u4eec\u8ddf\u9edb\u513f\u53bb\u8ddf\u5f20\u606a\u9053\u6b49\u7684\u5417\uff1f</p><p>\u3000\u3000\u201c\u9edb\u513f\u8bf4\u5979\u4e00\u4e2a\u4eba\u8fc7\u53bb\u9053\u6b49\u53ef\u80fd\u4e0d\u4f1a\u90a3\u4e48\u5c34\u5c2c\u2026\u2026\u201d\u674e\u6708\u8339\u5c34\u5c2c\u7684\u89e3\u91ca\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5b59\u9759\u9999\u60f3\u60f3\u4e5f\u662f\uff0c\u6bd5\u7adf\u662f\u8bef\u4f1a\uff0c\u8ba9\u9edb\u513f\u8bf7\u5f20\u606a\u53c2\u52a0\u5e86\u529f\u5bb4\u5c31\u53ef\u4ee5\u4e86\uff0c\u90fd\u6ca1\u6709\u5fc5\u8981\u5c06\u8fd9\u4e8b\u653e\u5728\u5fc3\u4e0a\uff0c\u60f3\u5b89\u6392\u9edb\u513f\u5979\u7238\u5988\u5148\u4e0a\u8f66\u53bb\u9152\u5e97\uff0c\u63a5\u7740\u53c8\u770b\u89c1\u5510\u5a67\u4e0e\u76db\u590f\u8d70\u8fdb\u6765\uff0c\u53c8\u6123\u4e86\u6123\uff1a\u9edb\u513f\u4e00\u4e2a\u4eba\u53bb\u89c1\u5f20\u606a\uff1f</p><p>\u3000\u3000\u201c\u5f20\u606a\u53eb\u6211\u4eec\u5e86\u529f\u5bb4\u5148\u5f00\u59cb\uff0c\u4e0d\u8981\u7b49\u4ed6\uff0c\u4ed6\u8fd8\u662f\u8981\u7b49\u4e00\u4f1a\u513f\u624d\u80fd\u53bb\u53c2\u52a0\uff0c\u81f3\u5c11\u4e0d\u80fd\u8ba9\u8138\u592a\u96be\u770b\uff01\u201d\u5510\u5a67\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5438\u7740\u51c9\u6c14\uff0c\u4e0a\u836f\u65f6\u4e0d\u5c0f\u5fc3\u5c06\u4e00\u4e9b\u836f\u6c34\u6fc0\u5230\u773c\u775b\u91cc\u4e86\uff0c\u773c\u775b\u6709\u4e9b\u6fc0\u75db\uff0c\u773c\u6cea\u90fd\u6d41\u51fa\u6765\u4e86\uff0c\u95ed\u7740\u773c\u775b\u542c\u7740\u811a\u6b65\u58f0\uff0c\u4ee5\u4e3a\u662f\u5085\u4fca\uff0c\u8bf4\u9053\uff0c\u201c\u62bd\u4e9b\u7eb8\u5dfe\u7ed9\u6211\uff0c\u8fd9\u836f\u6c34\u4e0d\u4f24\u773c\u775b\u5427\uff1f\u201d\u8bdd\u6ca1\u8bf4\u8bdd\uff0c\u5c31\u611f\u89c9\u5ae9\u6ed1\u7684\u624b\u6307\u5c16\u89e6\u5230\u81ea\u5df1\u8138\u988a\u7684\u4f24\u5904\u3002</p><p>\u3000\u3000\u5f20\u606a\u7741\u5f00\u773c\uff0c\u89c1\u6c5f\u9edb\u513f\u7ad9\u5728\u8ddf\u524d\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u662f\u53ef\u60dc\u4e86\uff0c\u4f60\u7684\u6f14\u5531\u4f1a\uff0c\u6211\u8fde\u4e00\u9053\u6b4c\u90fd\u6ca1\u6709\u542c\u5230\uff0c\u5f00\u5e86\u529f\u5bb4\u65f6\uff0c\u4f60\u8981\u8865\u507f\u6211\u4e24\u9996\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u75bc\u4e0d\u75bc\uff1f\u201d\u6c5f\u9edb\u513f\u5fc3\u75bc\u7684\u6469\u6332\u7740\u5f20\u606a\u8138\u4e0a\u7ed9\u6293\u7834\u76ae\u7684\u4f24\u75d5\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5e2e\u6211\u5988\u8fc7\u6765\u8ddf\u4f60\u9053\u6b49\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9053\u4ec0\u4e48\u6b49\u554a\uff0c\u518d\u8bf4\u6211\u4e5f\u6ca1\u5403\u4e8f\u554a\uff01\u201d\u5f20\u606a\u8146\u7740\u8138\u563b\u7b11\u7740\uff0c\u5e0c\u671b\u8fd9\u4ef6\u4e8b\u60c5\u8f7b\u63cf\u6de1\u5199\u7684\u8fc7\u53bb\u5c31\u597d\u4e86\uff0c\u6ca1\u6709\u5fc5\u8981\u653e\u5728\u5fc3\u4e0a\uff0c\u6ca1\u6709\u60f3\u5230\u9edb\u513f\u4e0b\u4e00\u4e2a\u52a8\u4f5c\u5374\u662f\u53cc\u624b\u6367\u4f4f\u81ea\u5df1\u7684\u8138\u988a\uff0c\u6123\u4e86\u6123\uff0c\u6ca1\u6709\u7b49\u4ed6\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u6c5f\u9edb\u513f\u90a3\u5a07\u6da6\u8bf1\u4eba\u7684\u7ea2\u5507\u5c31\u5370\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u6ca1\u6709\u7ed9\u8c01\u8fd9\u4e48\u5077\u88ad\u8fc7\uff0c\u6709\u4e9b\u53cd\u5e94\u8fdf\u949d\uff0c\u7b49\u4ed6\u53cd\u5e94\u8fc7\u6765\uff0c\u6c5f\u9edb\u513f\u5df2\u7ecf\u7ad9\u5230\u4e00\u8fb9\uff0c\u4fcf\u76ae\u7684\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u4f60\u4e0d\u8981\u9053\u6b49\uff0c\u90a3\u6211\u5c31\u4e0d\u9053\u6b49\u4e86\uff0c\u54e6\uff0c\u5bf9\u4e86\uff0cT\u6064\u886b\u4e0a\u5370\u5370\u5c3c\u56fd\u65d7\u3001\u56fd\u5fbd\u7684\u4e8b\u60c5\uff0c\u4f60\u53ef\u4ee5\u4e00\u53e5\u8bdd\u89e3\u91ca\u6e05\u695a\u7684\uff0c\u4f60\u5f53\u65f6\u600e\u4e48\u5c31\u6ca1\u6709\u89e3\u91ca\uff1f\u201d</p><p>\u3000\u3000\u201c\u54d0\u94db\u2026\u2026\u201d\u5f20\u606a\u4e00\u5c41\u80a1\u6ca1\u6709\u5750\u7a33\uff0c\u6574\u4e2a\u4eba\u4ece\u5706\u51f3\u4e0a\u6454\u5230\u5730\u4e0a\uff0c\u836f\u6c34\u74f6\u90fd\u6253\u7ffb\u4e86\u597d\u51e0\u4e2a\u3002</p></article>"}}}]);
