(window.webpackJsonp=window.webpackJsonp||[]).push([[902],{925:function(p){p.exports={chapterTitle:null,title:"\u7b2c722\u7ae0 \u5305\u591c\u4e09\u767e",content:"<article><h2>\u7b2c722\u7ae0 \u5305\u591c\u4e09\u767e</h2><p>\u3000\u3000\u65f6\u5b66\u658c\u5750\u5728\u90a3\u91cc\u4e00\u58f0\u4e0d\u542d\uff0c\u5f20\u606a\u89c1\u4ed6\u773c\u89d2\u4e4c\u9752\u3001\u5634\u89d2\u7ea2\u80bf\uff0c\u5410\u4e86\u4e00\u53e3\u75bc\uff0c\u7adf\u7136\u5410\u51fa\u4e00\u9897\u69fd\u7259\u51fa\u6765\uff0c\u5373\u4f7f\u8fd9\u6837\u4e5f\u4e0d\u80af\u8bf4\u4e3a\u4ec0\u4e48\u52a8\u624b\uff0c\u8170\u95f4\u80cc\u8179\u8fd8\u6709\u51e0\u4e2a\u6e05\u6670\u53ef\u89c1\u7684\u5927\u811a\u5370\u7559\u5728\u4e0a\u9762\uff0c\u65f6\u5b66\u658c\u8fd9\u65f6\u5019\u8fd8\u6342\u7740\u7ed9\u5de6\u4e0a\u8179\u76b1\u7740\u7709\u5934\u8138\u8272\u82cd\u767d\u75bc\u75db\u52b2\u513f\u8fd8\u6ca1\u6709\u7f13\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u671d\u65f6\u5b66\u658c\u6068\u6068\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u5c31\u8fd9\u70b9\u6c14\u606f\uff01\u201d\u5c06\u6750\u6599\u4e22\u529e\u516c\u684c\u4e0a\uff0c\u8d70\u5230\u6821\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u95e8\u5916\uff0c\u63a8\u4e86\u63a8\u95e8\uff0c\u95e8\u4ece\u91cc\u9762\u9501\u4e0a\u4e86\uff0c\u542c\u7740\u91cc\u9762\u6709\u4eba\u95ee\u8bdd\u3002\u5f20\u606a\u4e8c\u8bdd\u6ca1\u8bf4\uff0c\u4e00\u811a\u5c06\u95e8\u8e39\u5f00\uff0c\u534a\u6247\u529e\u516c\u5ba4\u95e8\u659c\u5012\u4e0b\u6765\uff0c\u80e1\u91d1\u661f\u3001\u90b5\u5f6c\u60ca\u8c14\u7684\u7ad9\u5728\u91cc\u9762\u770b\u8fc7\u6765\uff0c\u8fd8\u6709\u4e00\u4e2a\u4eba\u662f\u6821\u5b66\u751f\u4f1a\u4e3b\u5e2d\u9648\u52c7\uff0c\u4ed6\u5750\u7740\u56de\u5934\u770b\u8fc7\u6765\u3002\u529e\u516c\u5ba4\u91cc\u518d\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u60f3\u5fc5\u65f6\u5b66\u658c\u5c31\u8ddf\u4ed6\u4eec\u4e09\u4e2a\u4eba\u52a8\u624b\uff0c\u5f20\u606a\u7ad9\u5728\u95e8\u53e3\uff0c\u51b7\u773c\u770b\u7740\u4ed6\u4eec\u4e09\u4e2a\uff1a\u201c\u4f60\u4eec\u4ed6\u5988\u7684\u51fa\u606f\u4e86\uff0c\u4e09\u4e2a\u6253\u4e00\u4e2a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u8ba8\u516c\u9053\uff0c\u4e5f\u5148\u5206\u6e05\u662f\u975e\u597d\u4e0d\u597d\uff1f\u201d\u9648\u52c7\u7ad9\u8d77\u6765\u6321\u5728\u5f20\u606a\u524d\u9762\uff0c\u201c\u6211\u4eec\u597d\u597d\u5728\u804a\u5929\uff0c\u90a3\u5c0f\u5b50\u4e0d\u77e5\u6b7b\u6d3b\u5148\u671d\u51b2\u8fdb\u6765\u63ea\u80e1\u91d1\u661f\u7684\u8863\u9886\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e00\u628a\u63ea\u4f4f\u9648\u52c7\u7684\u8863\u9886\uff0c\u77aa\u773c\u770b\u7740\u4ed6\uff1a\u201c\u6211\u5c31\u63ea\u7740\u4f60\u4e86\uff0c\u600e\u4e48\u6837\uff1f\u8981\u4e0d\u8981\u628a\u6211\u5f80\u6b7b\u91cc\u6253\u554a\uff01\u201d\u4e00\u628a\u5c06\u4ed6\u63a8\u6421\u5f00\uff0c\u770b\u7740\u90b5\u5f6c\u8fd8\u60f3\u51b2\u4e0a\u6765\uff0c\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u9a82\u9053\uff0c\u201c\u62ac\u5934\u4e0d\u89c1\u4f4e\u5934\u89c1\uff0c\u7ed9\u81ea\u5df1\u7559\u70b9\u4f59\u5730\uff01\u201d\u624b\u8e6c\u7740\u95e8\u8fb9\u4e0a\u7684\u4e00\u5f20\u6905\u5b50\u671d\u4ed6\u8e22\u8fc7\u53bb\u3002\u8fd9\u5bb6\u4f19\u4e4b\u524d\u8fd8\u4e0e\u65f6\u5b66\u658c\u90fd\u662f\u866b\u4ff1\u4e50\u90e8\u6210\u5458\uff0c\u4e0d\u8bf4\u529d\u963b\u4e86\uff0c\u7adf\u7136\u8054\u5408\u8d77\u6765\u5bf9\u65f6\u5b66\u658c\u4e0b\u8fd9\u4e48\u72e0\u7684\u624b\uff0c\u771f\u4ed6\u5988\u7684\u4e0d\u662f\u73a9\u827a\u513f\u3002\u4e0a\u5348\u5c0f\u7ea2\u697c\u8fd9\u8fb9\u5b66\u751f\u5f88\u5c11\uff0c\u4e8c\u697c\u51e0\u4e4e\u770b\u4e0d\u5230\u4eba\uff0c\u8981\u4e0d\u662f\u8499\u4e50\u8d76\u7740\u6709\u4e8b\u8dd1\u8fc7\u6765\u770b\u5230\uff0c\u65f6\u5b66\u658c\u8fd9\u5c0f\u5b50\u5f3a\u6491\u7740\u4ec0\u4e48\u90fd\u4e0d\u8bf4\uff0c\u53ea\u6015\u5230\u4e0b\u5348\u90fd\u672a\u5fc5\u6709\u4eba\u77e5\u9053\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u56de\u5934\u770b\u89c1\u8499\u4e50\u6400\u7740\u65f6\u5b66\u658c\u8d70\u8fc7\u6765\uff0c\u65f6\u5b66\u658c\u989d\u5934\u90fd\u6e17\u51fa\u6c57\u73e0\u6765\uff0c\u5f20\u606a\u6709\u4e9b\u62c5\u5fc3\uff0c\u638f\u51fa\u8f66\u94a5\u5319\u9012\u7ed9\u8499\u4e50\u8bf4\u9053\uff1a\u201c\u4f60\u627e\u675c\u98de\u4e00\u8d77\u5f00\u8f66\u5e26\u65f6\u5b66\u658c\u53bb\u533b\u9662\u67e5\u4e00\u4e0b\uff0c\u6ca1\u4ec0\u4e48\u4e8b\u5c31\u5c06\u7259\u7ed9\u8865\u8d77\u6765\u2026\u2026\u201d\u5f20\u606a\u5728\u9752\u5e74\u516c\u5bd3\u8f66\u5e93\u91cc\u653e\u7740\u4e00\u90e8\u8f66\uff0c\u4e0d\u5e38\u7528\uff0c\u4e5f\u7559\u7740\u5907\u4e00\u65f6\u4e4b\u9700\u3002</p><p>\u3000\u3000\u56de\u5934\u5f80\u6821\u5b66\u751f\u4f1a\u529e\u516c\u5ba4\u626b\u4e86\u4e00\u773c\uff0c\u80e1\u91d1\u661f\u3001\u90b5\u5f6c\u3001\u9648\u52c7\u4e09\u4e2a\u4eba\u5927\u6982\u4e5f\u6709\u4e9b\u540e\u6015\uff0c\u53d1\u8499\u7684\u7ad9\u5728\u91cc\u9762\u4e0d\u6562\u7ad9\u51fa\u6765\uff1b\u5f20\u606a\u4e5f\u4e0d\u80fd\u51b2\u8fdb\u53bb\u63ea\u7740\u4eba\u5bb6\u5927\u6253\u51fa\u624b\uff0c\u89c1\u65f6\u5b66\u658c\u8fd9\u822c\u51c4\u60e8\u7684\u6a21\u6837\u4e5f\u4e0d\u653e\u5fc3\uff0c\u558a\u4f4f\u8499\u4e50\uff1a\u201c\u6211\u966a\u4f60\u4eec\u8fc7\u53bb\uff0c\u7b49\u4e00\u4f1a\u513f\u6211\u3002\u201d\u6253\u7535\u8bdd\u8ba9\u5085\u4fca\u76f4\u63a5\u5f00\u8f66\u8fc7\u6765\u63a5\u4ed6\u4eec\uff0c\u53c8\u8dd1\u56de\u53bb\u5c06\u6750\u6599\u62ff\u4e0a\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4e8b\uff0c\u201d\u8d70\u51fa\u5c0f\u7ea2\u697c\u7b49\u5085\u4fca\u5f00\u8f66\u8fc7\u6765\uff0c\u65f6\u5b66\u658c\u6342\u7740\u5de6\u4e0a\u8179\u7684\u624b\u653e\u4e0b\u6765\uff0c\u8fd8\u6545\u4f5c\u65e0\u4e8b\u7684\u4f38\u5c55\u4e86\u4e00\u4e0b\u8eab\u4f53\uff0c\u201c\u4e0d\u8981\u90a3\u4e48\u5938\u5f20\uff0c\u8fd9\u526f\u6a21\u6837\u8fd8\u662f\u8ba9\u6211\u8eb2\u56de\u5bbf\u820d\u91cc\u53bb\u5427\uff0c\u62b9\u70b9\u7ea2\u82b1\u6cb9\u5c31\u884c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u4ed6\u989d\u5934\u90fd\u75bc\u51fa\u6c57\u73e0\u6765\uff0c\u9762\u8272\u82cd\u767d\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u6491\u4e86\uff0c\u6211\u4eec\u53c8\u4e0d\u53bb\u7f51\u5427\uff0c\u6ca1\u4eba\u4f1a\u770b\u5230\u4f60\u8fd9\u526f\u718a\u6837\u3002\u6b63\u5f0f\u7684\u62dc\u6258\u4f60\u597d\u4e0d\u597d\uff0c\u4e0b\u6b21\u60f3\u8ddf\u8c01\u52a8\u624b\u4e4b\u524d\uff0c\u8ddf\u6211\u4eec\u62db\u547c\u4e00\u58f0\u2026\u2026\u201d</p><p>\u3000\u3000\u5085\u4fca\u8f6c\u773c\u5c31\u5f00\u8f66\u8d76\u4e86\u8fc7\u6765\uff0c\u4e0b\u8f66\u4f38\u624b\u5728\u65f6\u5b66\u658c\u5de6\u4e0a\u8179\u6309\u4e86\u6309\uff0c\u89c1\u4ed6\u62bd\u6c14\u7684\u75bc\uff0c\u6400\u7740\u4ed6\u4e0a\u8f66\uff0c\u56de\u5934\u5bf9\u5f20\u606a\u3001\u8499\u4e50\u8bf4\uff1a\u201c\u4e0d\u662f\u808b\u9aa8\u632b\u75bc\uff0c\u53ef\u80fd\u662f\u813e\u810f\u6709\u635f\u4f24\uff0c\u6211\u4eec\u9a6c\u4e0a\u5c31\u53bb\u533b\u9662\u2026\u2026\u201d</p><p>\u3000\u3000\u5916\u529b\u6bb4\u6253\u5f88\u5bb9\u6613\u9020\u6210\u813e\u810f\u7834\u88c2\u5185\u51fa\u8840\uff0c\u5085\u4fca\u7ecf\u9a8c\u8db3\uff0c\u89c1\u4ed6\u8fd9\u4e48\u5224\u65ad\uff0c\u5f20\u606a\u4e0d\u6562\u803d\u6401\uff0c\u62c9\u7740\u8499\u4e50\u8d76\u7d27\u94bb\u8fdb\u8f66\u91cc\uff0c\u76f4\u63a5\u5954\u6700\u8fd1\u7684\u5efa\u90ba\u519b\u533b\u603b\u9662\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u8fd9\u65f6\u5019\u624d\u6709\u4e9b\u5fc3\u865a\uff0c\u5e73\u8eba\u5728\u5f20\u606a\u4e0e\u8499\u4e50\u7684\u6000\u91cc\uff0c\u5374\u5f3a\u58ee\u7740\u80c6\u5b50\u8bf4\u9053\uff1a\u201c\u6211\u4e0d\u4f1a\u6b7b\u5427\uff1f\u6211\u8fd8\u662f\u5904\u7537\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u90fd\u7ed9\u6c14\u4e50\u4e86\uff0c\u8499\u4e50\u76f4\u63a5\u5c06\u65f6\u5b66\u658c\u7684\u5634\u5df4\u7ed9\u6342\u4e0a\u3002\u8d76\u5230\u5efa\u90ba\u519b\u533b\u603b\u9662\u68c0\u67e5\u771f\u662f\u813e\u810f\u7834\u88c2\u5bfc\u81f4\u8179\u5305\u819c\u4e0b\u8840\u80bf\uff0c\u6ca1\u6709\u803d\u6401\u5c31\u9001\u8fdb\u624b\u672f\u5ba4\u505a\u624b\u672f\u3002</p><p>\u3000\u3000\u4e00\u540d\u533b\u751f\u8fc7\u6765\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u4eec\u662f\u4ed6\u670b\u53cb\u5427\uff1f\u8fd9\u79cd\u4e8b\u6700\u597d\u901a\u77e5\u4ed6\u5bb6\u4eba\uff0c\u4f60\u4eec\u6709\u6ca1\u6709\u4ed6\u5bb6\u4eba\u7684\u7535\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u65f6\u5b66\u658c\u8001\u5bb6\u79bb\u8fd9\u91cc\u6709\u597d\u51e0\u767e\u516c\u91cc\u5462\uff0c\u8981\u6253\u7535\u8bdd\u7ed9\u4ed6\u5bb6\u91cc\u4e5f\u8981\u7b49\u624b\u672f\u7ed3\u675f\u518d\u8bf4\u3002</p><p>\u3000\u3000\u8499\u4e50\u62c9\u5f20\u606a\u5230\u4e00\u65c1\uff0c\u4f4e\u58f0\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u8981\u6253\u7535\u8bdd\u7ed9\u5b66\u6821\uff0c\u6253\u7ed9\u65f6\u5b66\u658c\u7684\u5e74\u7ea7\u4e3b\u4efb\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b66\u6821\u4f1a\u600e\u4e48\u5904\u7406\uff1f\u201d\u5f20\u606a\u95ee\u8499\u4e50\u3002</p><p>\u3000\u3000\u8499\u4e50\u62c5\u5fc3\u7684\u95ee\u5f20\u606a\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u4e0d\u8fc7\u662f\u5b66\u751f\u95f4\u7684\u6597\u6bb4\u800c\u5df2\uff0c\u5c31\u7b97\u8ba9\u6821\u65b9\u77e5\u9053\uff0c\u4e5f\u5b81\u591a\u8ba9\u80cc\u666f\u6df1\u539a\u7684\u80e1\u91d1\u661f\u8d54\u4e9b\u533b\u836f\u8d39\u800c\u5df2\uff0c\u6240\u8c13\u7684\u5904\u5206\uff0c\u5bf9\u4ed6\u4eec\u8fd9\u4e9b\u4eba\u6beb\u65e0\u610f\u4e49\uff0c\u641e\u4e0d\u5b9a\u8fde\u7740\u65f6\u5b66\u658c\u4e5f\u8981\u53d7\u5904\u5206\uff1b\u5173\u952e\u65f6\u5b66\u658c\u8fd9\u5bb6\u4f19\u54ac\u7d27\u7259\u5173\u4ec0\u4e48\u90fd\u4e0d\u80af\u8bf4\u3002\u8499\u4e50\u6ca1\u4ec0\u4e48\u7ecf\u9a8c\uff0c\u4e0d\u6653\u5f97\u65f6\u5b66\u658c\u7684\u60c5\u51b5\u4e25\u4e0d\u4e25\u91cd\u3002\u8981\u662f\u4e25\u91cd\uff0c\u80af\u5b9a\u4e0d\u80fd\u7792\u7740\u6821\u65b9\u79c1\u81ea\u5904\u7406\uff0c\u8981\u662f\u4e0d\u4e25\u91cd\uff0c\u90a3\u5c31\u7b49\u505a\u5b8c\u624b\u672f\u518d\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6653\u5f97\u53ca\u65f6\u9001\u8fdb\u533b\u9662\uff0c\u65f6\u5b66\u658c\u7684\u5c0f\u547d\u80af\u5b9a\u6ca1\u6709\u95ee\u9898\uff0c\u7709\u5934\u626c\u4e86\u626c\uff0c\u95ee\u5085\u4fca\uff1a\u201c\u8fd9\u4e2a\u8981\u7b97\u91cd\u4f24\u8fd8\u662f\u8f7b\u4f24\uff1f\u201d</p><p>\u3000\u3000\u201c\u632b\u4f24\u5bfc\u81f4\u7684\u813e\u5305\u819c\u4e0b\u8840\u80bf\uff0c\u5f88\u96be\u5f52\u5230\u91cd\u4f24\u91cc\u53bb\u3002\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c31\u7b97\u4e86\uff0c\u201d\u4e0d\u80fd\u5b9a\u6027\u6210\u91cd\u4f24\uff0c\u7ea0\u7f20\u4e5f\u6ca1\u6709\u610f\u601d\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u8499\u4e50\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff0c\u201c\u5c0f\u624b\u672f\u800c\u5df2\uff0c\u7b49\u505a\u5b8c\u624b\u672f\u8ba9\u65f6\u5b66\u658c\u81ea\u5df1\u51b3\u5b9a\u2026\u2026\u201d\u4e0e\u8499\u4e50\u8d70\u56de\u53bb\uff0c\u5bf9\u90a3\u533b\u751f\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u5148\u4e13\u5fc3\u505a\u624b\u672f\u5427\uff0c\u4ed6\u5bb6\u91cc\u4eba\uff0c\u6211\u4eec\u4f1a\u901a\u77e5\u7684\u3002\u201d</p><p>\u3000\u3000\u4f24\u8005\u662f\u9876\u7ea7\u7684\u5954\u9a70\u8f7f\u8f66\u9001\u8fdb\u6765\u7684\uff0c\u518d\u8bf4\u813e\u5305\u819c\u4e0b\u8840\u80bf\u53ea\u8981\u9001\u6cbb\u53ca\u65f6\uff0c\u624b\u672f\u6ca1\u4ec0\u4e48\u96be\u5ea6\uff0c\u533b\u751f\u89c1\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u5c31\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8fc7\u4e00\u4f1a\u513f\uff0c\u675c\u98de\u3001\u5e2d\u82e5\u7433\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u534e\u4ed6\u4eec\u63a5\u5230\u5f20\u606a\u7684\u7535\u8bdd\u90fd\u8d76\u4e86\u8fc7\u6765\u3002\u8179\u8154\u955c\u4fee\u8865\u624b\u672f\u53ea\u7528\u4e86\u4e00\u4e2a\u534a\u5c0f\u65f6\u5c31\u6210\u529f\u5b8c\u6210\uff0c\u65f6\u5b66\u658c\u5728\u624b\u672f\u90e8\u4f4d\u6253\u4e0a\u9ebb\u836f\uff0c\u4eba\u8fd8\u662f\u6e05\u9192\u4e86\uff0c\u770b\u7740\u5927\u5bb6\u8d70\u8fdb\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u505a\u624b\u672f\u65f6\u660e\u660e\u4e00\u70b9\u90fd\u4e0d\u75bc\uff0c\u6211\u8fd8\u662f\u4e0d\u4e89\u6c14\u7684\u6d41\u4e0b\u6e05\u7eaf\u7684\u6cea\u6c34\uff0c\u4f60\u4eec\u4e0d\u8981\u7b11\u6211\u3002\u201d\u8fd9\u65f6\u5019\u6ca1\u4eba\u6562\u9017\u4ed6\u7b11\uff0c\u6487\u8fc7\u5934\u53bb\u4e0d\u642d\u7406\u4ed6\u7684\u8bdd\uff0c\u65f6\u5b66\u658c\u53c8\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u9053\uff0c\u201c\u606a\u5927\u5c11\u4e3a\u6211\u51b2\u6012\u4e00\u51a0\u5c06\u6821\u5b66\u751f\u4f1a\u7684\u529e\u516c\u5ba4\u95e8\u8e39\u584c\u4e00\u6247\uff0c\u4f60\u4eec\u4e00\u5b9a\u8981\u8d76\u7740\u8fc7\u53bb\u77bb\u4ef0\u4e00\u4e0b\uff0c\u6700\u597d\u5e2e\u6211\u62cd\u7167\u7559\u5b58\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u662f\u8bf4\u4e3a\u4ec0\u4e48\u81ea\u4e0d\u91cf\u529b\u627e\u4e0a\u95e8\u4e00\u6311\u4e09\u5427\uff1f\u201d\u5f20\u606a\u89c1\u65f6\u5b66\u658c\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\uff0c\u4e5f\u5c31\u6ca1\u4ec0\u4e48\u62c5\u5fc3\u7684\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u54ac\u7740\u5634\u5507\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u4e5f\u662f\u6211\u81ea\u5df1\u50bb\uff0c\u4e0a\u5348\u53bb\u5c0f\u7ea2\u697c\u7ecf\u8fc7\u6821\u5b66\u751f\u4f1a\uff0c\u542c\u5230\u90a3\u5c0f\u5b50\u5728\u91cc\u9762\u8ddf\u90b5\u5f6c\u3001\u9648\u52c7\u5439\u8ddf\u5c0f\u4f9d\u505a\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u5c0f\u4f9d\uff1f\u201d\u591c\u603b\u4f1a\u7684\u4e8b\u60c5\uff0c\u5927\u5bb6\u90fd\u4e0d\u4f1a\u8ddf\u5e2d\u82e5\u7433\u8bf4\uff0c\u5e2d\u82e5\u7433\u7a81\u7136\u542c\u65f6\u5b66\u658c\u5634\u91cc\u5192\u51fa\u4e00\u4e2a\u964c\u751f\u5973\u5b69\u5b50\u7684\u540d\u5b57\uff0c\u201c\u662f\u8c01\uff0c\u54ea\u4e2a\u5b66\u6821\u7684\u5973\u5b69\u5b50\uff0c\u4f60\u662f\u56e0\u4e3a\u8fd9\u4e2a\u5973\u5b69\u5b50\u8ddf\u5b66\u751f\u4f1a\u7684\u4eba\u6253\u67b6\uff1f\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u8f6c\u8fc7\u8138\u53bb\uff0c\u6ca1\u4eba\u8ddf\u5e2d\u82e5\u7433\u89e3\u91ca\u5c0f\u4f9d\u662f\u8c01\uff0c\u4e5f\u6ca1\u4eba\u544a\u8bc9\u5e2d\u82e5\u7433\u8fd9\u4e2a\u5c0f\u4f9d\u662f\u738b\u6d77\u7c9f\u4ecb\u7ecd\u65f6\u5b66\u658c\u8ba4\u8bc6\u7684\uff1b\u8fd9\u5012\u4e0d\u662f\u5e2e\u738b\u6d77\u7c9f\u63a9\u9970\u4ec0\u4e48\uff0c\u53ea\u662f\u8fd9\u79cd\u4e8b\u60c5\u600e\u4e48\u53ef\u4ee5\u8ba9\u5973\u5b69\u5b50\u77e5\u9053\u5462\uff1f</p><p>\u3000\u3000\u5085\u4fca\u4e70\u6765\u76d2\u996d\uff0c\u5927\u5bb6\u90fd\u5728\u75c5\u623f\u91cc\u56f4\u7740\u65f6\u5b66\u658c\u7684\u75c5\u623f\u51d1\u5408\u7740\u5403\u3002\u521b\u57df\u516c\u53f8\u91cc\u6b63\u5728\u7b79\u5907\u8ba1\u7b97\u673a\u7f51\u7edc\u5b66\u6821\u7684\u4e8b\u60c5\uff0c\u65f6\u5b66\u658c\u8d1f\u8d23\u5916\u8054\u5de5\u4f5c\uff0c\u662f\u5f53\u4e4b\u65e0\u6127\u7684\u9aa8\u5e72\u3001\u4e3b\u529b\uff0c\u8fd9\u65f6\u5019\u8981\u5728\u75c5\u5e8a\u4e0a\u8eba\u4e00\u4e2a\u661f\u671f\uff0c\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u5c31\u5934\u75bc\u4e86\uff0c\u5173\u952e\u8fd8\u8981\u5e2e\u4ed6\u627e\u5176\u4ed6\u501f\u53e3\u8bf7\u4e00\u661f\u671f\u7684\u5047\u3002</p><p>\u3000\u3000\u8fd9\u4ef6\u4e8b\u4e5f\u53ea\u6709\u5c31\u8fd9\u4e48\u8fc7\u53bb\uff0c\u65f6\u5b66\u658c\u4e5f\u8fd9\u4e48\u5e0c\u671b\uff0c\u8463\u8dc3\u534e\u6124\u6124\u4e0d\u5e73\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4ef6\u4e8b\u4e0d\u80fd\u5c31\u8fd9\u6837\u7b97\u4e86\uff0c\u4ed6\u4eec\u4e0b\u624b\u4e5f\u592a\u72e0\u4e86\uff0c\u90b5\u5f6c\u90a3\u4e2a\u6742\u79cd\uff0c\u5e73\u65f6\u90fd\u8fd8\u79f0\u5144\u9053\u5f1f\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u770b\u5230\u5b66\u751f\u4f1a\u90a3\u6247\u7ed9\u8e39\u584c\u7684\u95e8\uff0c\u6211\u5c31\u5df2\u7ecf\u6d88\u6c14\u4e86\uff0c\u552f\u4e00\u6709\u4e9b\u9057\u61be\u7684\uff0c\u90a3\u95e8\u4e0d\u662f\u6211\u8e39\u7684\uff0c\u201d\u65f6\u5b66\u658c\u53c8\u8f6c\u8fc7\u5934\u95ee\u5f20\u606a\uff0c\u201c\u6211\u8bb0\u5f97\u9001\u6211\u8fc7\u6765\u7684\u8f66\u662f\u5954\u9a70S600\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u6709\u5fc3\u60c5\u5173\u5fc3\u8f66\u554a\uff01\u201d\u5e2d\u82e5\u7433\u54ed\u7b11\u4e0d\u5f97\uff0c\u5927\u5bb6\u4e5f\u662f\u4e00\u8138\u7684\u65e0\u5948\u3002</p><p>\u3000\u3000\u201c\u90a3\u8f66\u662f\u591a\u5c11\u7537\u4eba\u7684\u7ec8\u6781\u68a6\u60f3\uff0c\u6211\u600e\u4e48\u53ef\u80fd\u4e0d\u5173\u5fc3\uff1f\u201d\u65f6\u5b66\u658c\u534a\u4f9d\u7740\u6795\u5934\uff0c\u8ddf\u5f20\u606a\u5f00\u73a9\u7b11\u8bf4\uff0c\u201c\u4f60\u5012\u662f\u603b\u8ba9\u4eba\u6709\u60ca\u559c\u5462\uff0c\u4f60\u73b0\u5728\u5c31\u7b97\u6574\u4e00\u67b6\u76f4\u5347\u673a\u51fa\u6765\uff0c\u6211\u90fd\u4e0d\u4f1a\u518d\u60ca\u8bb6\u4e86\u3002\u80fd\u4e0d\u80fd\u5c0f\u5c0f\u7684\u63d0\u4e2a\u8981\u6c42\uff0c\u51fa\u9662\u65f6\u8ba9\u6211\u518d\u4f4f\u4e00\u56de\u8fd9\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u884c\uff0c\u53ea\u8981\u4f60\u8fd9\u65f6\u5019\u80fd\u5c06\u4f60\u7684\u5634\u7ed9\u6211\u95ed\u4e0a\u3002\u201d\u5f20\u606a\u4e5f\u70e6\u65f6\u5b66\u658c\u9e39\u566a\uff0c\u54ea\u91cc\u50cf\u521a\u505a\u8fc7\u813e\u810f\u4fee\u8865\u624b\u672f\u7684\u4eba\uff1f</p><p>\u3000\u3000\u8463\u8dc3\u534e\u3001\u5e2d\u82e5\u7433\u3001\u65bd\u65b0\u98de\u4ed6\u4eec\u81ea\u5df1\u4f4f\u516c\u4ea4\u8f66\u56de\u5b66\u6821\uff0c\u5954\u9a70\u8f66\u4e5f\u6324\u4e0d\u4e0b\u8fd9\u4e48\u591a\uff0c\u5f20\u606a\u4e0e\u8499\u4e50\u3001\u675c\u98de\u53c8\u7559\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u5750\u8f66\u79bb\u5f00\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e8b\u5c31\u8fd9\u4e48\u7b97\u4e86\uff1f\u201d\u8499\u4e50\u5728\u8f66\u91cc\u95ee\u5f20\u606a\u3001\u675c\u98de\u3002</p><p>\u3000\u3000\u8fd9\u4e8b\u8fd9\u65f6\u5019\u4e5f\u6e05\u695a\u4e86\uff0c\u738b\u6d77\u7c9f\u4ecb\u7ecd\u591c\u5e97\u7684\u5973\u5b69\u5b50\u7ed9\u65f6\u5b66\u658c\u8ba4\u8bc6\uff0c\u8fd9\u5973\u5b69\u5b50\u5f88\u5feb\u5c31\u8ddf\u80e1\u91d1\u661f\u52fe\u5f53\u4e0a\u4e86\uff1b\u65f6\u5b66\u658c\u4e4b\u524d\u4e00\u53a2\u60c5\u613f\u7684\u8ba4\u4e3a\u738b\u6d77\u7c9f\u7ed9\u4ecb\u7ecd\u7684\u5973\u5b69\u5b50\u5bb6\u4e16\u6e05\u767d\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u5728\u8f66\u91cc\u7b11\u7740\u8ddf\u675c\u98de\u8bf4\uff1a\u201c\u4f55\u7eaa\u4e91\u4e2d\u5348\u7ed9\u6211\u6253\u7535\u8bdd\u67e5\u5230\u90a3\u5973\u5b69\u5b50\u7684\u5e95\u7ec6\uff0c\u6ca1\u597d\u610f\u601d\u5728\u75c5\u623f\u523a\u6fc0\u65f6\u5b66\u658c\uff0c\u201d\u5bf9\u8499\u4e50\u8bf4\uff0c\u201c\u4e09\u767e\u5143\u5305\u591c\uff0c\u4ef7\u683c\u5012\u662f\u4e0d\u8d35\uff0c\u8981\u4e0d\u8981\u4ecb\u7ecd\u7ed9\u4f60\u8ba4\u8bc6\uff1f\u201d</p></article>"}}}]);
