(window.webpackJsonp=window.webpackJsonp||[]).push([[903],{926:function(p){p.exports={chapterTitle:null,title:"\u7b2c900\u7ae0 \u88ab\u4e00\u591c\u60c5",content:"<article><h2>\u7b2c900\u7ae0 \u88ab\u4e00\u591c\u60c5</h2><p>\u3000\u3000\u653e\u5f00\u7684\u60c5\u6b32\u5c31\u50cf\u6c79\u6d8c\u800c\u6765\u7684\u6f6e\u6c34\uff0c\u6b22\u7231\u4e2d\uff0c\u7fdf\u4e39\u9752\u611f\u5192\u4e5f\u4e0d\u6cbb\u800c\u6108\u4e86\uff0c\u6d51\u8eab\u6ca1\u6709\u4e1d\u6beb\u7684\u4e0d\u8212\uff0c\u6c57\u51fa\u5982\u6d46\uff0c\u5f53\u4e2d\u6d17\u4e86\u4e00\u56de\u6fa1\uff0c\u722c\u4e0a\u5e8a\u6765\uff0c\u76f8\u4f9d\u504e\u5728\u4e00\u8d77\u3002\u767d\u5929\u8fd8\u662f\u8584\u9634\u7684\u5929\u6c14\uff0c\u8fd9\u65f6\u5019\u4e91\u5f00\u6d12\u4e0b\u6708\u5149\u6765\uff0c\u900f\u7740\u8584\u7eb1\u7a97\u5e18\uff0c\u6d12\u5728\u7ef8\u8d28\u88ab\u8925\u4e0a\uff0c\u5f20\u606a\u770b\u7740\u6000\u91cc\u7684\u4f73\u4eba\uff0c\u5a07\u8eaf\u5b8c\u7f8e\u82e5\u96d5\u5851\uff0c\u652f\u8d77\u8eab\u5b50\u6765\uff0c\u7fdf\u4e39\u9752\u770b\u4ed6\u8eab\u5b50\u5728\u52a8\u3001\u773c\u775b\u7743\u7740\u81ea\u5df1\u7684\u8eab\u5b50\u5f80\u4e0b\u6ed1\uff0c\u6367\u8fc7\u4ed6\u7684\u8138\uff1a\u201c\u4f60\u8981\u770b\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u662f\u4f60\u6700\u4eb2\u5bc6\u7684\u4eba\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6df7\u86cb\u554a\uff01\u201d\u7fdf\u4e39\u9752\u8f7b\u8f7b\u7684\u9a82\u4e86\u4e00\u58f0\uff0c\u53cc\u624b\u6367\u4f4f\u7f9e\u7ea2\u7684\u8138\uff0c\u8eab\u4f53\u6a2a\u8eba\u5728\u7ef8\u8d28\u7684\u88ab\u8925\u4e0a\uff0c\u53cc\u811a\u5c48\u8d77\uff0c\u6574\u4e2a\u8eab\u5b50\u6a2a\u9648\u6210Y\u578b\uff0c\u5634\u91cc\u53c8\u6f0f\u51fa\u4e00\u53e5\uff0c\u201c\u5c31\u4eca\u5929\u665a\u4e0a\uff0c\u4f60\u8981\u8bb0\u5f97\uff0c\u5c31\u4eca\u5929\u665a\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6491\u8d77\u8eab\u4f53\uff0c\u770b\u7740\u8eab\u4e0b\u7684\u5a07\u8eaf\u6d51\u767d\u5982\u7389\uff0c\u5c31\u50cf\u662f\u8fd9\u6708\u8272\u51dd\u6210\uff0c\u5979\u53c8\u767d\u53c8\u7ec6\u7684\u53cc\u624b\u5bb3\u7f9e\u7684\u6367\u4f4f\u8138\uff0c\u4e4c\u9ed1\u4eae\u6cfd\u7684\u79c0\u53d1\u6563\u843d\u5728\u5982\u7389\u7684\u8138\u65c1\uff0c\u4e0e\u7ef8\u8d28\u88ab\u8925\u8ba9\u5979\u767d\u7699\u5982\u96ea\u7684\u808c\u80a4\u770b\u4e0a\u53bb\u66f4\u6709\u8d28\u611f\uff0c\u4e73\u5cf0\u9ad8\u8038\u6d51\u5706\uff0c\u5c31\u50cf\u5b8c\u7f8e\u7684\u53d7\u4e86\u91cd\u529b\u7684\u6c34\u6ef4\u5f62\u72b6\uff0c\u5ae3\u7ea2\u9c9c\u7f8e\u82e5\u5c11\u5973\u7684\u4e73\u5c16\u633a\u7acb\u7740\uff0c\u5e73\u5766\u5149\u6ed1\u7684\u5c0f\u8179\u5d4c\u7740\u53ef\u7231\u7684\u8110\u7a9d\uff0c\u96ea\u767d\u4fee\u957f\u53cc\u817f\u51fa\u6765\uff0c\u5c06\u5979\u4e24\u5904\u4e4b\u524d\u6700\u7f8e\u4e3d\u7684\u79c1\u5904\u5927\u80c6\u7684\u5448\u73b0\u51fa\u6765\uff0c\u4e4c\u9ed1\u6cb9\u4eae\u7684\u6bdb\u4e1b\u91cc\u662f\u5fae\u5f20\u5f00\u7684\u8776\u72b6\u5507\u7f1d\uff0c\u73ab\u7ea2\u8272\u7684\u9c9c\u4e3d\uff0c\u6709\u7740\u4e00\u79cd\u6781\u81f4\u8bf1\u4eba\u7684\u9b45\u60d1\u4e4b\u7f8e\u3002</p><p>\u3000\u3000\u201c\u597d\u4e86\u6ca1\uff1f\u201d\u7fdf\u4e39\u9752\u624b\u6367\u7740\u8138\uff0c\u5624\u549b\u7684\u8bf4\u9053\uff0c\u817f\u8981\u5408\u62e2\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u771f\u7f8e\uff0c\u90fd\u8fd8\u6ca1\u6709\u770b\u5230\u5462\u3002\u201d\u5f20\u606a\u624b\u5fd9\u6491\u4f4f\u5979\u7684\u819d\u76d6\uff0c\u4e0d\u8ba9\u5979\u7684\u817f\u62e2\u8d77\u6765\u3002\u5979\u81c0\u4e0b\u7684\u808c\u8089\u7528\u529b\u6536\u4e86\u4e00\u4e0b\uff0c\u4e24\u817f\u4e4b\u95f4\u5374\u6324\u51fa\u4e00\u6ef4\u6676\u83b9\u7684\u7389\u9732\uff0c\u5927\u6982\u5979\u4e5f\u611f\u89c9\u5230\u4e86\uff0c\u7ffb\u8eab\u6eda\u5230\u4e00\u8fb9\u5e76\u817f\u8db4\u5728\u5e8a\u6cbf\u4e0a\u4e0d\u8ba9\u5f20\u606a\u518d\u770b\uff0c\u5634\u91cc\u8f7b\u9a82\u7740\uff1a\u201c\u4f60\u4e2a\u6d51\u7403\uff0c\u6323\u817e\u4eba\u5bb6\u8fd8\u4e0d\u591f\u554a\uff0c\u6211\u7684\u9aa8\u5934\u90fd\u9165\u6389\u4e86\uff0c\u4f60\u8fd8\u60f3\u505a\u4ec0\u4e48\u2026\u2026\u201d\u8138\u95f7\u5728\u88ab\u5b50\u4e0a\uff0c\u4e5f\u4e0d\u56de\u5934\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u6d51\u5706\u7684\u81c0\u90e8\u4e30\u6ee1\u3001\u96ea\u767d\u3001\u7ec6\u817b\u3001\u5145\u6ee1\u5f39\u6027\uff0c\u5982\u6b64\u8bf1\u4eba\u7684\u6446\u5728\u773c\u524d\uff0c\u5f20\u606a\u5728\u4e0a\u9762\u8f7b\u8f7b\u62cd\u4e00\u4e0b\uff0c\u5c31\u7559\u4e0b\u7c89\u7ea2\u7684\u6d45\u5370\u5b50\uff0c\u6276\u7740\u5979\u7684\u80ef\uff0c\u8ba9\u5979\u81c0\u90e8\u62ac\u9ad8\uff0c\u53c8\u5c06\u90a3\u5b8c\u7f8e\u7684\u6c34\u6cfd\u76c8\u76c8\u7684\u79c1\u5904\u9732\u51fa\u6765\uff0c\u8ba9\u4eba\u770b\u4e86\u6b32\u706b\u711a\u8eab\uff0c\u5f20\u606a\u6276\u7740\u80a5\u5ae9\u7f8e\u81c0\u633a\u8eab\u5c31\u523a\u4e86\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8981\u62bd\u5f00\u8eab\uff0c\u5374\u8ba9\u5f20\u606a\u62e6\u8170\u62b1\u4f4f\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff1a\u201c\u4f60\u603b\u662f\u8981\u6298\u78e8\u6b7b\u4eba\uff01\u201d\u5a9a\u773c\u5982\u4e1d\u53ea\u662f\u8ba9\u5f20\u606a\u52a8\u5f97\u66f4\u6b22\u3002\u5f20\u606a\u633a\u8eab\u7ad9\u5728\u5e8a\u4e0b\u52a8\u4f5c\uff0c\u80fd\u6e05\u6670\u7684\u770b\u7740\u4e24\u4eba\u8857\u63a5\u7684\u79c1\u5904\uff0c\u53d1\u73b0\u5979\u4e24\u5c42\u76ae\u5b50\u4e4b\u95f4\u6709\u4e00\u7c92\u7c73\u7c92\u5927\u5c0f\u7684\u75e3\uff0c\u5448\u6de1\u8910\u8272\uff0c\u8fd8\u771f\u662f\u975e\u4eb2\u5bc6\u7684\u4eba\u4e0d\u80fd\u770b\u89c1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u6df1\u4e86\uff0c\u7a97\u5916\u7684\u82cd\u7a79\u6563\u7740\u8ff7\u79bb\u6726\u80e7\u7684\u5149\uff0c\u4eff\u4f5b\u90a3\u8fb9\u5c31\u662f\u5929\u5730\u7684\u8fb9\u7f18\uff0c\u8fd8\u6709\u7ec6\u5f31\u7684\u6708\u5149\u4ece\u73bb\u7483\u7a97\u5916\u900f\u51fa\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4f9d\u5728\u5f20\u606a\u7684\u6000\u91cc\uff0c\u611f\u89c9\u7740\u5bbd\u539a\u7684\u80f8\u819b\u5e26\u6765\u7684\u5b89\u5fc3\u611f\u89c9\uff0c\u53ea\u6709\u58c1\u706f\u4eae\u7740\uff0c\u706f\u5149\u5e7d\u6de1\uff0c\u5f20\u606a\u7b11\u7740\u8ddf\u5979\u8bf4\u90a3\u7c92\u75e3\u7684\u4e8b\u60c5\uff1a\u201c\u90a3\u7c92\u75e3\uff0c\u4f60\u81ea\u5df1\u4e5f\u4e0d\u5927\u5bb9\u6613\u770b\u5f97\u5230\u554a\uff01\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u62ac\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u89c1\u4ed6\u8138\u4e0a\u6302\u7740\u65e0\u8d56\u620f\u8c11\u7684\u7b11\uff0c\u6ca1\u597d\u6c14\u7684\u8bf4\uff1a\u201c\u5c31\u8bb8\u4f60\u4eec\u7537\u4eba\u81ea\u5df1\u73a9\u7537\u4eba\uff0c\u5973\u4eba\u5c31\u4e0d\u80fd\u81ea\u5df1\u8212\u670d\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u5f20\u606a\u7a81\u7136\u53d1\u73b0\u521a\u624d\u90a3\u4e2a\u67d4\u5f31\u7684\u7fdf\u4e39\u9752\u6d88\u5931\u4e86\uff0c\u5fc3\u91cc\u60f3\u8fd9\u5973\u4eba\u53d8\u5316\u8d77\u6765\u771f\u662f\u5feb\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u7684\u7cbe\u6c14\u6062\u590d\u8fc7\u6765\u5c31\u5f00\u59cb\u5f20\u7259\u821e\u722a\u4e86\uff01\u201d\u770b\u7740\u5979\u7684\u773c\u7738\uff0c\u5927\u800c\u6e05\u4eae\uff0c\u776b\u6bdb\u9ad8\u6311\uff0c\u58c1\u706f\u6295\u5c04\u4e0b\u6765\u7684\u9634\u5f71\u843d\u5728\u5979\u7684\u773c\u77b3\u4e0a\uff0c\u4f7f\u5f97\u5979\u7684\u773c\u7738\u770b\u4e0a\u53bb\u66f4\u5e7d\u6df1\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u7a81\u7136\u53d1\u73b0\u7537\u4eba\u4e5f\u662f\u4e2a\u597d\u4e1c\u897f\u554a\uff0c\u201d\u7fdf\u4e39\u9752\u5728\u5f20\u606a\u7684\u6000\u91cc\u6175\u61d2\u7684\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u554a\uff0c\u8bf4\u9519\u4e86\uff0c\u7537\u4eba\u4ece\u6765\u90fd\u4e0d\u662f\u597d\u4e1c\u897f\uff01\u201d\u6b20\u7740\u8eab\u5b50\u5c31\u8981\u7ad9\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u89c1\u7fdf\u4e39\u9752\u591f\u7740\u8eab\u5b50\u8981\u53bb\u6361\u5730\u6bef\u4e0a\u7684\u8863\u670d\uff0c\u7275\u8fc7\u5979\u7684\u624b\uff0c\u95ee\u5979\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6361\u8d77\u88d9\u5b50\u63a9\u4f4f\u8bf1\u4eba\u7684\u4e30\u80f8\u4e0e\u5e7d\u6df1\u7684\u5c0f\u8179\uff0c\u5750\u5728\u5e8a\u6cbf\u4e0a\uff0c\u51dd\u7738\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u89c1\u5979\u7684\u795e\u8272\u5f88\u8ba4\u771f\uff0c\u5012\u662f\u8ba9\u5979\u770b\u5f97\u6709\u4e9b\u5fc3\u865a\uff0c\u4ed6\u63a5\u89e6\u7684\u8fd9\u4e48\u591a\u5973\u4eba\u5f53\u4e2d\uff0c\u552f\u6709\u7fdf\u4e39\u9752\u6709\u7740\u641c\u80a0\u522e\u809a\u3001\u7aa5\u900f\u4eba\u5fc3\u7684\u773c\u775b\u3002</p><p>\u3000\u3000\u201c\u5973\u4eba\u771f\u50bb\uff0c\u4e0d\u8fc7\u5973\u4eba\u4e5f\u662f\u50bb\u4e9b\u597d\uff0c\u201d\u7fdf\u4e39\u9752\u6e29\u5b9b\u800c\u7b11\uff0c\u4f38\u624b\u6478\u4e86\u6478\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u6211\u80fd\u95ee\u4e00\u4e2a\u95ee\u9898\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6700\u559c\u6b22\u8c01\uff0c\u665a\u6674\u3001\u8bb8\u601d\u8fd8\u662f\u5510\u5a67\uff0c\u201d\u7fdf\u4e39\u9752\u505c\u987f\u4e86\u4e00\u4e0b\uff0c\u53c8\u8865\u4e0a\u4e00\u4e2a\u4eba\uff0c\u201c\u8fd8\u662f\u4f60\u5c4b\u91cc\u7684\u90a3\u4e2a\u59ae\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u6c38\u8fdc\u90fd\u4e0d\u60f3\u53bb\u9762\u5bf9\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5509\u554a\uff0c\u771f\u662f\u53ef\u60b2\u554a\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u5c31\u7b97\u8bf4\u8c0e\u4e5f\u4f1a\u8865\u4e00\u53e5\u5b89\u6170\u4e00\u4e0b\u4eba\u5bb6\u5462\uff0c\u201d\u7fdf\u4e39\u9752\u4f38\u76f4\u8170\u611f\u6168\u4e86\u4e00\u53e5\uff0c\u624b\u8fd8\u662f\u6ca1\u6709\u820d\u5f97\u653e\u5f00\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u4f60\u5f88\u6e29\u67d4\uff0c\u4e5f\u5f88\u4f53\u8d34\uff0c\u4e5f\u4e0d\u7b97\u975e\u5e38\u4e0d\u662f\u4e2a\u4e1c\u897f\uff0c\u6709\u7740\u8ff7\u4eba\u7684\u9b45\u529b\uff0c\u5c31\u50cf\u4e00\u56e2\u70c8\u706b\uff0c\u603b\u4e0d\u6b62\u4e00\u53ea\u98de\u86fe\u4f1a\u594b\u4e0d\u987e\u8eab\u7684\u6251\u8fc7\u6765\u2026\u2026\u4e0d\u4f1a\u89c9\u5f97\u4e0d\u503c\u5f97\uff0c\u521a\u624d\u53d1\u73b0\u771f\u7684\u597d\u4eab\u53d7\uff0c\u5728\u6700\u7f8e\u5999\u65f6\u523b\u5fc3\u91cc\u7adf\u7136\u6709\u79cd\u5c31\u6b64\u6b7b\u6389\u5c31\u597d\u7684\u611f\u89c9\u2014\u2014\u8ba9\u6211\u505a\u56de\u539f\u6765\u7684\u90a3\u4e2a\u6211\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u51b7\u6de1\u554a\uff01\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u58f0\uff0c\u4ed6\u90fd\u6ca1\u6709\u60f3\u5230\u90fd\u8d70\u51fa\u8fd9\u4e00\u6b65\u4e86\u8fd8\u80fd\u6062\u590d\u5230\u4e4b\u524d\u7684\u5173\u7cfb\uff0c\u770b\u7740\u7fdf\u4e39\u9752\u7684\u7738\u5b50\uff0c\u6709\u6df1\u60c5\u6d41\u9732\u7684\u6323\u624e\uff0c\u4f46\u4e5f\u6709\u5979\u7279\u6709\u7684\u51b3\u65ad\uff0c\u5c31\u50cf\u98de\u86fe\u594b\u4e0d\u987e\u8eab\u7684\u6251\u706b\uff0c\u6251\u5230\u706b\u91cc\u4e4b\u540e\uff0c\u6216\u8bb8\u4f1a\u6210\u7070\u70ec\uff0c\u6216\u8bb8\u4f1a\u8715\u53d8\u3002\u4ed6\u8f7b\u8f7b\u7684\u5c06\u7fdf\u4e39\u9752\u7684\u8eab\u5b50\u6402\u8fc7\u6765\uff0c\u7fdf\u4e39\u9752\u8eab\u5b50\u50f5\u4e86\u4e00\u4e0b\uff0c\u8fd8\u662f\u987a\u4ece\u7684\u5012\u5728\u4ed6\u7684\u6000\u91cc\uff0c\u5fae\u5fae\u4fa7\u7740\u4ef0\u8d77\u5934\u6765\uff0c\u8ba9\u96ea\u5ae9\u7684\u8138\u988a\u8d34\u7740\u4ed6\u80e1\u6e23\u5b50\u523a\u4eba\u7684\u4e0b\u9894\uff0c\u4e0d\u77e5\u9053\u8981\u4e0d\u8981\u544a\u8bc9\u4ed6\uff0c\u65b0\u829c\u90a3\u5929\u4e0b\u96e8\u7684\u9ec4\u660f\u5979\u4e0e\u536b\u5170\u5c31\u5750\u5728\u8f66\u91cc\u770b\u7740\u4ed6\u4fef\u4e0b\u8eab\u5b50\u7ed9\u90a3\u4e2a\u96e8\u4e2d\u7684\u5c11\u5973\u7cfb\u978b\u5e26\uff0c\u5fc3\u91cc\u8f7b\u8f7b\u7684\u53f9\u606f\u4e86\u4e00\u58f0\uff0c\u624b\u4f38\u5230\u8eab\u540e\uff0c\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u6709\u65f6\u5019\u4f1a\u53d1\u73b0\u4f60\u7684\u5fc3\u662f\u7a7a\u7684\uff0c\u603b\u662f\u5973\u4eba\u5bf9\u4f60\u52a8\u4e86\u771f\u60c5\uff0c\u4f60\u5fc3\u91cc\u624d\u6709\u60c5\u2014\u2014\u6216\u8bb8\u6ca1\u6709\u4e00\u4e2a\u5973\u4eba\u80fd\u771f\u6b63\u7684\u7f51\u4f4f\u4f60\u7684\u5fc3\uff0c\u6216\u8bb8\u8fd9\u4e48\u60f3\u4f1a\u6709\u4e9b\u60b2\u54c0\uff0c\u4e5f\u4f1a\u5fc3\u7518\u4e9b\u2014\u2014\u4f60\u771f\u7684\u5f88\u8ff7\u4eba\u3002\u201d</p><p>\u3000\u3000\u4ece\u6ca1\u6709\u60f3\u5230\u81ea\u5df1\u4f1a\u7ed9\u4e00\u4e2a\u5973\u4eba\u770b\u5f97\u5982\u6b64\u7684\u900f\u5f7b\uff1a\u771f\u662f\u8ff7\u604b\u8089\u4f53\u800c\u96be\u77e5\u8fd4\u9014\u554a\uff0c\u5f20\u606a\u5fc3\u91cc\u8f7b\u8f7b\u7684\u53f9\u606f\uff0c\u6ca1\u6709\u5f00\u53e3\u8bf4\u8bdd\uff0c\u53ea\u662f\u5b89\u9759\u7684\u6402\u7740\u6000\u91cc\u7684\u4f73\u4eba\u3002</p><p>\u3000\u3000\u201c\u90a3\u591c\u5728\u9752\u5c71\u516c\u5bd3\uff0c\u5373\u4f7f\u6709\u90a3\u6837\u7684\u8bef\u4f1a\uff0c\u4f60\u4e5f\u6ca1\u6709\u5f3a\u6c42\uff0c\u4f3c\u4e4e\u90a3\u4e5f\u4e0d\u80fd\u8bf4\u660e\u4f60\u8272\u80c6\u4e0d\u591f\u5305\u5929\uff1f\u201d\u7fdf\u4e39\u9752\u4ef0\u8d77\u5934\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u770b\u7740\u7fdf\u4e39\u9752\u7684\u7f8e\u4e3d\u800c\u660e\u4eae\u7684\u773c\u775b\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u5728\u68a6\u91cc\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u771f\u8fd9\u4e48\u4ee5\u4e3a\uff1f\u201d\u7fdf\u4e39\u9752\u773c\u775b\u7565\u5e26\u7591\u60d1\uff0c\u5076\u5c14\u773c\u775b\u53c8\u77aa\u4e86\u77aa\uff0c\u4f38\u624b\u6390\u4e86\u4ed6\u4e00\u4e0b\uff0c\u201c\u6211\u8981\u662f\u53ea\u6709\u5341\u516b\u5c81\uff0c\u6211\u8fd8\u4fe1\u4f60\u8fd9\u8bdd\u2014\u2014\u73b0\u5728\u4fe1\u4f60\u624d\u6709\u9b3c\u4e86\uff01\u201d\u60f3\u7740\u521a\u624d\u81ea\u5df1\u9891\u9891\u7ed9\u9001\u4e0a\u4e91\u7aef\uff0c\u81ea\u5df1\u8eab\u4f53\u7684\u6bcf\u4e00\u5904\u7ec6\u5fae\u611f\u53d7\uff0c\u8fd9\u5bb6\u4f19\u51e0\u4e4e\u90fd\u80fd\u611f\u53d7\u5230\uff0c\u90fd\u80fd\u7167\u987e\u5230\uff0c\u4fe1\u4ed6\u624d\u53eb\u6709\u9b3c\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u60f3\uff0c\u4ece\u6765\u90fd\u53ea\u6709\u5973\u4eba\u4f1a\u52a8\u771f\u60c5\uff0c\u7537\u4eba\u4f1a\u5ba0\u5973\u4eba\u3001\u4f1a\u601c\u60dc\u5973\u4eba\uff0c\u4f46\u4e5f\u6b62\u4e8e\u2018\u5ba0\u2019\u4e0e\u2018\u601c\u60dc\u2019\u2014\u2014\u4f60\u660e\u767d\u6211\u8981\u8bf4\u7684\u610f\u601d\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8981\u5c06\u6211\u8bf4\u7684\u8fd9\u4e48\u4e0d\u582a\uff01\u5c31\u8ddf\u5c01\u5efa\u793e\u4f1a\u7684\u8d2a\u6deb\u597d\u8272\u53c8\u7eb5\u6b32\u65e0\u5ea6\u7684\u66b4\u541b\u4f3c\u7684\uff01\u201d</p><p>\u3000\u3000\u201c\u672c\u8d28\u4e0a\u80fd\u597d\u591a\u5c11\uff1f\u201d\u7fdf\u4e39\u9752\u7ffb\u4e86\u4e2a\u8eab\uff0c\u8db4\u5230\u5f20\u606a\u7684\u6000\u91cc\u770b\u7740\u4ed6\u7684\u773c\u775b\u95ee\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ed6\u7684\u7b11\uff0c\u6620\u5728\u591c\u8272\u4e3a\u955c\u5e95\u7684\u73bb\u7483\u7a97\u4e0a\uff0c\u66f4\u52a0\u7684\u6a21\u7cca\uff0c\u5373\u4f7f\u7ed9\u7fdf\u4e39\u9752\u8bf4\u4e2d\u4e00\u4e9b\u63a5\u8fd1\u672c\u6027\u7684\u4e1c\u897f\uff0c\u4e5f\u6ca1\u6709\u611f\u5230\u7279\u522b\u7684\u4e0d\u582a\uff0c\u53cd\u800c\u6709\u79cd\u5982\u91ca\u91cd\u8d1f\u7684\u611f\u89c9\u3002\u4eba\u751f\u80fd\u591f\u91cd\u8d70\u4e00\u56de\u662f\u4f55\u5176\u7684\u5e78\u8fd0\uff0c\u80fd\u633d\u7559\u591a\u5c11\u9057\u61be\uff0c\u4f46\u4f5c\u4e3a\u5386\u7ecf\u6ca7\u6d77\u96be\u4e3a\u6c34\u7684\u7537\u4eba\uff0c\u96be\u9053\u8fd8\u80fd\u5962\u671b\u4ec0\u4e48\u6ca1\u6709\u6742\u8d28\u7684\u60c5\u611f\u2014\u2014\u5f20\u606a\u4ece\u6765\u90fd\u77e5\u9053\u81ea\u5df1\u662f\u4e0d\u582a\u76f4\u9762\u9648\u5b81\u7684\u79bb\u53bb\u7684\uff0c\u53ef\u60dc\u8fd9\u4e00\u5207\u90fd\u65e0\u6cd5\u8ddf\u522b\u4eba\u8bf4\u51fa\u53e3\u3002</p><p>\u3000\u3000\u9694\u7f6e\u5728\u5e8a\u5934\u67dc\u5b50\u7684\u624b\u673a\u8f7b\u9707\u4e86\u4e00\u4e0b\uff0c\u624b\u673a\u89d2\u4e0a\u7684\u7eff\u8272\u8367\u5149\u706f\u95ea\u8df3\u8d77\u6765\uff0c\u6709\u77ed\u4fe1\u606f\u53d1\u8fdb\u6765\u3002\u5f20\u606a\u770b\u4e86\u770b\u58c1\u4e0a\u7684\u5706\u949f\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e24\u70b9\u4e86\uff0c\u4ed6\u80fd\u60f3\u5230\u8c01\u4f1a\u5728\u8fd9\u91cc\u4f20\u77ed\u4fe1\u606f\u7ed9\u81ea\u5df1\uff0c\u72b9\u8c6b\u7740\u8981\u4e0d\u8981\u5f53\u7740\u7fdf\u4e39\u9752\u7684\u9762\u770b\u77ed\u4fe1\u606f\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u662f\u8bb8\u601d\u53d1\u6765\u7684\u77ed\u4fe1\u606f\u5427\uff01\u5979\u8fd9\u51e0\u5929\u5e38\u71ac\u591c\uff0c\u4f60\u8981\u8ba9\u5979\u591a\u4f11\u606f\u3002\u201d\u7fdf\u4e39\u9752\u4ece\u5f20\u606a\u6000\u91cc\u5750\u8d77\u6765\uff0c\u6361\u8d77\u5730\u6bef\u4e0a\u7684\u8863\u670d\u7a7f\u8d77\u6765\uff0c\u5f20\u606a\u624b\u4f38\u8fc7\u53bb\u5c06\u624b\u673a\u62ff\u8fc7\u6765\uff0c\u8bb8\u601d\u8981\u4e3a\u6668\u66e6\u7eb8\u4e1a\u68da\u6237\u533a\u505a\u89c4\u5212\u56fe\uff0c\u6700\u8fd1\u7ecf\u5e38\u71ac\u591c\uff0c\u53d1\u77ed\u4fe1\u606f\u662f\u770b\u770b\u5f20\u606a\u6709\u6ca1\u6709\u5165\u7761\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u624b\u673a\u4e0a\u62e8\u8bb8\u601d\u7684\u624b\u673a\u53f7\u7801\uff0c\u7fdf\u4e39\u9752\u7a7f\u597d\u8863\u670d\u5728\u5f20\u606a\u7684\u8138\u988a\u4e0a\u4eb2\u4e86\u4eb2\uff0c\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u771f\u7684\u8981\u8c22\u8c22\u4f60\uff0c\u867d\u7136\u53ea\u662f\u4e00\u591c\uff0c\u4f46\u662f\u6709\u7231\u4eba\u7684\u611f\u89c9\u771f\u7684\u5f88\u597d\u2026\u2026\u201d\u770b\u7740\u7fdf\u4e39\u9752\u5173\u95e8\u79bb\u5f00\uff0c\u8bb8\u601d\u67d4\u8106\u6e05\u4eae\u7684\u58f0\u97f3\u4ece\u8bdd\u7b52\u65f6\u4f20\u51fa\u6765\uff0c\u201c\u5582\uff0c\u5c31\u6015\u4f60\u7761\u7740\u4e86\uff0c\u5c31\u60f3\u7740\u8ddf\u4f60\u8bf4\u4e00\u4f1a\u513f\u8bdd\u2026\u2026\u201d\u5f20\u606a\u5c06\u58c1\u706f\u5173\u4e0a\uff0c\u8d70\u4e0b\u5e8a\uff0c\u5c06\u7ed2\u7a97\u5e18\u62c9\u4e0a\uff0c\u8ba9\u6c79\u6d8c\u7684\u591c\u8272\u5c06\u81ea\u5df1\u5f7b\u5e95\u7684\u6df9\u6ca1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6b21\u65e5\u9192\u6765\u65f6\u5df2\u7ecf\u662f\u5341\u70b9\u949f\u4e86\uff0c\u7fdf\u4e39\u9752\u795e\u91c7\u5955\u5955\u7684\u8d70\u8fdb\u6765\u901a\u77e5\u4ed6\u8981\u8d76\u7d27\u8d77\u5e8a\u6d17\u6f31\u624d\u8d76\u5f97\u4e0a\u53bb\u5317\u4eac\u7684\u98de\u673a\uff0c\u4ece\u5979\u8138\u4e0a\u770b\u4e0a\u53bb\u6628\u5929\u6b22\u6109\u7684\u4e00\u4e1d\u75d5\u8ff9\uff0c\u4e5f\u4e1d\u6beb\u6ca1\u6709\u611f\u5192\u7684\u8ff9\u8c61\uff0c\u542c\u5979\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5f88\u65e9\u5c31\u8d77\u5e8a\u5904\u7406\u597d\u4e9b\u4e8b\u60c5\uff0c\u5f20\u606a\u5012\u662f\u89c9\u5f97\u8be7\u5f02\uff0c\u5fc3\u60f3\u83ab\u975e\u6628\u5929\u81ea\u5df1\u53d1\u70ed\u505a\u4e86\u4e00\u573a\u7f8e\u68a6\uff0c\u5012\u662f\u7fdf\u4e39\u9752\u63a8\u95e8\u8981\u8d70\u51fa\u53bb\u4e4b\u524d\uff0c\u4ed6\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u83ab\u975e\u6211\u88ab\u4e00\u591c\u60c5\u4e86\uff1f\u201d</p></article>"}}}]);
