(window.webpackJsonp=window.webpackJsonp||[]).push([[905],{928:function(p){p.exports={chapterTitle:null,title:"\u7b2c725\u7ae0 \u5403\u8089\u4e0d\u5410\u9aa8\u5934",content:"<article><h2>\u7b2c725\u7ae0 \u5403\u8089\u4e0d\u5410\u9aa8\u5934</h2><p>\u3000\u3000\u5728\u80e1\u91d1\u661f\u4e8c\u5341\u4e00\u5c81\u751f\u65e5\u90a3\u5929\uff0c\u738b\u6d77\u7c9f\u638f\u8170\u5305\u5728\u5e0c\u5c14\u987f\u5305\u4e86\u4e00\u95f4\u5c0f\u5bb4\u4f1a\u5385\u8ba9\u80e1\u91d1\u661f\u9080\u8bf7\u540c\u5b66\u3001\u670b\u53cb\u4e00\u8d77\u8fc7\u6765\u5e2e\u4ed6\u5e86\u795d\u751f\u65e5\u2014\u2014\u4ed6\u5c31\u77e5\u9053\u5e0c\u5c14\u987f\u5927\u9152\u4f1a\u5728\u540c\u4e00\u5929\u4e3e\u884c\u5efa\u90ba\u5e02\u4f01\u4e1a\u5bb6\u8054\u8c0a\u9152\u4f1a\uff0c\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u4e5f\u5e94\u9080\u53c2\u52a0\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u5230\u5bb4\u5e2d\u7684\u5c3e\u58f0\uff0c\u5728\u5927\u5bb6\u5373\u5c06\u8f6c\u79fb\u5230\u53e6\u4e00\u5bb6\u591c\u5e97\u7ee7\u7eed\u6b22\u6b4c\u7545\u996e\u3001\u738b\u6d77\u7c9f\u6781\u529b\u63a7\u5236\u4e0d\u5c06\u5931\u671b\u7684\u60c5\u7eea\u6d41\u9732\u51fa\u6765\u4e4b\u65f6\uff0c\u8138\u819b\u7ea2\u7d2b\u3001\u5e26\u7740\u4e00\u8eab\u9152\u6c14\u7684\u80e1\u5b97\u5e86\u624d\u59d7\u59d7\u6765\u8fdf\uff0c\u968f\u80e1\u5b97\u5e86\u524d\u6765\u7684\u8fd8\u6709\u4e00\u4e2a\u4e0d\u901f\u4e4b\u5ba2\u5c31\u662f\u7cbe\u5178\u5730\u4ea7\u96c6\u56e2\u7684\u603b\u88c1\u6797\u96ea\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u521d\u770b\u5230\u6797\u96ea\u65f6\uff0c\u8fd8\u5f53\u662f\u80e1\u5b97\u5e86\u7684\u60c5\u5987\u3002\u5f53\u7136\uff0c\u4ed6\u6653\u5f97\u80e1\u5b97\u5e86\u4e0d\u53ef\u4ee5\u5e26\u7740\u60c5\u5987\u51fa\u5e2d\u5e02\u4f01\u4e1a\u5bb6\u8054\u8c0a\u9152\u4f1a\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5e26\u7740\u60c5\u5987\u51fa\u5e2d\u4ed6\u4f84\u5b50\u5e7f\u9080\u540c\u5b66\u3001\u670b\u53cb\u53c2\u52a0\u7684\u5e86\u751f\u5bb4\u4f1a\u4e0a\uff0c\u5c31\u6709\u4e9b\u7422\u78e8\u4e0d\u900f\u6797\u96ea\u7684\u8eab\u4efd\u3002</p><p>\u3000\u3000\u7cbe\u5fc3\u6253\u626e\u8fc7\u7684\u6797\u96ea\u7f8e\u4fcf\u8273\u4e3d\uff0c\u957f\u957f\u776b\u6bdb\u4e0b\u7684\u674f\u773c\u6d82\u7740\u6de1\u6de1\u7684\u7d2b\u8272\u773c\u5f71\uff0c\u987e\u76fc\u751f\u59ff\uff0c\u8fdb\u5c4b\u540e\u5c06\u5916\u5957\u8131\u4e0b\uff0c\u9732\u51fa\u7d27\u8272\u7684\u7d27\u8eab\u7ed2\u7ebf\u8863\uff0c\u8170\u95f4\u7cfb\u7f00\u6c34\u94bb\u7684\u8910\u8272\u8170\u5e26\uff0c\u7ec6\u9ad8\u8ddf\u7ea2\u8272\u76ae\u978b\uff0c\u4e30\u6ee1\u7684\u80f8\u812f\u50cf\u5c71\u5cf0\u4e00\u6837\u8038\u7acb\u7740\u3002\u5c0f\u5bb4\u4f1a\u5385\u91cc\u9664\u4e86\u738b\u6d77\u7c9f\u90fd\u662f\u4e0e\u80e1\u91d1\u661f\u5dee\u4e0d\u591a\u5e74\u9f84\u7684\u5c0f\u9752\u5e74\uff0c\u770b\u5230\u6797\u96ea\u8fd9\u4ed8\u5996\u5a06\u9a9a\u5a9a\u7684\u5973\u4eba\uff0c\u90fd\u7981\u4e0d\u4f4f\u53e3\u5e72\u820c\u71e5\uff0c\u6781\u529b\u6291\u5236\u7740\u4e0d\u53bb\u54bd\u53e3\u6c34\u3002\u5728\u80e1\u91d1\u661f\u8eab\u8fb9\u4e89\u8273\u593a\u598d\u8000\u773c\u4e86\u4e00\u665a\u4e0a\u7684\u90a3\u5973\u5b69\u5b50\u5728\u6797\u96ea\u8fdb\u6765\u540e\u5c31\u7acb\u5373\u663e\u5f97\u6709\u4e9b\u9752\u6da9\u4e86\u3002</p><p>\u3000\u3000\u201c\u80e1\u5e02\u957f\u60a8\u597d\uff0c\u6211\u662f\u91d1\u661f\u7684\u670b\u53cb\uff0c\u738b\u6d77\u7c9f\u2026\u2026\u201d\u738b\u6d77\u7c9f\u76ee\u5149\u5c31\u5728\u6797\u96ea\u5996\u5a9a\u7684\u8138\u4e0a\u505c\u4e86\u4e00\u77ac\uff0c\u4ed6\u6653\u5f97\u80e1\u5b97\u5e86\u624d\u5e94\u8be5\u662f\u53d7\u5230\u5173\u6ce8\u7684\u7126\u70b9\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u5f53\u7136\u6653\u5f97\u6bcf\u4e00\u4e2a\u7aed\u529b\u63a5\u8fd1\u4ed6\u4f84\u5b50\u7684\u4eba\u90fd\u6000\u7740\u4e00\u4e9b\u4e0d\u53ef\u544a\u4eba\u7684\u7528\u5fc3\uff0c\u80e1\u5b97\u5e86\u5e76\u4e0d\u6392\u65a5\u8fd9\u4e9b\u4eba\uff0c\u5173\u952e\u8981\u770b\u8fd9\u4e9b\u4eba\u591f\u4e0d\u591f\u806a\u660e\uff0c\u6311\u773c\u770b\u4e86\u770b\u6587\u8d28\u5f6c\u5f6c\u3001\u6234\u8272\u94f6\u7070\u8272\u955c\u6846\u773c\u955c\u7684\u738b\u6d77\u7c9f\uff0c\u5fae\u5fae\u9894\u9996\uff0c\u4fbf\u7b97\u6253\u8fc7\u62db\u547c\uff0c\u4e5f\u4e0d\u53bb\u770b\u738b\u6d77\u7c9f\u8feb\u5207\u60f3\u638f\u540d\u7247\u7684\u52a8\u4f5c\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u638f\u540d\u7247\u7684\u52a8\u4f5c\u5e76\u6ca1\u6709\u505c\u4e0b\u6765\uff0c\u4ed6\u731c\u5230\u968f\u80e1\u5b97\u5e86\u8fdb\u6765\u7684\u8fd9\u4e2a\u5996\u5a9a\u5973\u4eba\u5f88\u53ef\u80fd\u5c31\u662f\u4eca\u665a\u53c2\u52a0\u697c\u4e0b\u4f01\u4e1a\u5bb6\u8054\u8c0a\u9152\u4f1a\u7684\u5e94\u9080\u5609\u5bbe\uff0c\u53d6\u51fa\u4e00\u5f20\u540d\u7247\u9012\u7ed9\u6797\u96ea\uff1a\u201c\u655d\u4eba\u738b\u6d77\u7c9f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6d77\u7c9f\u79d1\u6280\u6709\u9650\u516c\u53f8\u2026\u2026\u201d\u6797\u96ea\u60f3\u4e86\u534a\u5929\uff0c\u6ca1\u60f3\u8d77\u542c\u8bf4\u8fc7\u8fd9\u5bb6\u516c\u53f8\uff0c\u4e5f\u5c31\u653e\u5f03\u52aa\u529b\uff0c\u5012\u662f\u4e0b\u8fb9\u7684\u5934\u8854\u770b\u4e0a\u53bb\u86ee\u552c\u4eba\u7684\uff0c\u201c\u4e1c\u6d77\u7701\u8f6f\u4ef6\u884c\u4e1a\u534f\u4f1a\u7406\u4e8b\uff0c\u201d\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u201c\u6709\u8fd9\u4e2a\u534f\u4f1a\u5417\uff0c\u6211\u600e\u4e48\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u5728\u6797\u96ea\u8f7b\u4f7b\u903c\u4eba\u7684\u6ce8\u89c6\u7740\uff0c\u738b\u6d77\u7c9f\u8001\u8138\u4e00\u7ea2\uff0c\u597d\u5728\u6797\u96ea\u6ca1\u6709\u5bf9\u4ed6\u7a77\u8ffd\u731b\u7684\u610f\u601d\uff0c\u8f6c\u8eab\u7b11\u7740\u5bf9\u80e1\u91d1\u661f\u8bf4\u9053\uff1a\u201c\u91d1\u661f\u8fc7\u751f\u65e5\u8bf7\u4e86\u8fd9\u4e48\u591a\u540c\u5b66\u3001\u670b\u53cb\uff0c\u504f\u504f\u628a\u6211\u7ed9\u5fd8\u4e86\uff0c\u96be\u9053\u5acc\u6797\u59d0\u4eba\u8001\u73e0\u9ec4\u4e0d\u914d\u8ddf\u4f60\u4eec\u8fd9\u4e9b\u5e74\u8f7b\u4eba\u5728\u4e00\u8d77\u73a9\uff1f\u201d\u4ece\u624b\u888b\u91cc\u62ff\u4e00\u53ea\u7ec6\u957f\u7684\u7cbe\u7f8e\u5305\u88c5\u76d2\uff0c\u201c\u6797\u59d0\u53ef\u6ca1\u6709\u5fd8\u4eca\u5929\u662f\u4f60\u7684\u751f\u65e5\uff0c\u201d\u5c06\u5305\u88c5\u76d2\u9012\u80e1\u91d1\u661f\u7684\u624b\u91cc\uff0c\u8bf4\u4e86\u58f0\u201c\u751f\u65e5\u5feb\u4e50\u201d\uff0c\u53c8\u65cb\u624b\u6478\u4e86\u4e00\u628a\u504e\u4f9d\u7740\u80e1\u91d1\u661f\u800c\u5750\u7684\u90a3\u5973\u5b69\u5b50\u7684\u5149\u6ed1\u8138\u86cb\uff0c\u653e\u7eb5\u7684\u5a07\u7b11\u7740\u8bf4\uff0c\u201c\u53c8\u6362\u5973\u670b\u53cb\u4e86\uff1f\u8fd9\u4e2a\u771f\u6f02\u4eae\uff0c\u5c0f\u5c0f\u5e74\u7eaa\u771f\u6709\u8273\u798f\u3002\u201d\u58f0\u97f3\u5a07\u8106\u7684\u8bf4\u7740\u8bdd\uff0c\u4e5f\u4e0d\u5fd8\u5c06\u79cb\u6c34\u8361\u6f3e\u7684\u5a9a\u773c\u629b\u7ed9\u5728\u5ea7\u7684\u5176\u4ed6\u4e8c\u5341\u5c81\u5de6\u53f3\u8840\u6c14\u65b9\u521a\u7684\u9752\u5e74\uff0c\u90b5\u5f6c\u3001\u9648\u52c7\u8fd9\u4e9b\u4eba\u54ea\u91cc\u7ecf\u5f97\u4f4f\u5982\u6b64\u7684\u52fe\u5f15\uff0c\u90fd\u8eb2\u8eb2\u95ea\u95ea\u7684\u4e0d\u65f6\u5077\u7784\u6797\u96ea\u4e30\u633a\u5982\u5c71\u7684\u4e73\u5cf0\u4e0e\u968f\u8bf4\u8bdd\u97f3\u800c\u8f7b\u5fae\u626d\u52a8\u7740\u7684\u81c0\u90e8\u3002</p><p>\u3000\u3000\u770b\u5230\u8fd9\u4e00\u5e55\uff0c\u738b\u6d77\u7c9f\u4e0d\u7531\u7684\u60f3\u8d77MBA\u6625\u5b63\u5165\u5b66\u57f9\u8bad\u73ed\u4e0a\u8ba4\u8bc6\u7684\u7fdf\u4e39\u9752\u6765\uff0c\u8fd9\u6837\u7684\u5973\u4eba\u624d\u582a\u79f0\u98a0\u5012\u4f17\u751f\u7684\u5996\u8273\u5c24\u7269\u3002\u5f53\u7136\uff0c\u738b\u6d77\u7c9f\u8fd8\u4e0d\u81f3\u4e8e\u8272\u8ff7\u4e86\u5fc3\u7a8d\u5fd8\u4e86\u4eca\u665a\u7684\u76ee\u7684\u662f\u4e0e\u80e1\u5b97\u5e86\u4eb2\u8fd1\u7136\u540e\u627e\u4e2a\u9002\u5f53\u7684\u65f6\u673a\u5c06\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5b8c\u6574\u7684\u65b9\u6848\u518d\u8be6\u7ec6\u7684\u8ddf\u80e1\u5b97\u5e86\u8bf4\u8bf4\u2014\u2014\u5173\u4e8e\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u65b9\u6848\u4ed6\u90fd\u9646\u9646\u7eed\u7eed\u7684\u8ddf\u80e1\u91d1\u661f\u704c\u8f93\u8fc7\uff0c\u53ea\u662f\u4e00\u76f4\u6ca1\u6709\u5f97\u5230\u4ec0\u4e48\u53cd\u9988\uff0c\u4e5f\u4e0d\u6e05\u695a\u80e1\u91d1\u661f\u5230\u5e95\u6709\u6ca1\u6709\u8ddf\u80e1\u5b97\u5e86\u8bf4\u8d77\u8fc7\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u80e1\u5b97\u5e86\u7a0d\u52a0\u9017\u7559\u5c31\u79bb\u5f00\u4e86\uff0c\u6797\u96ea\u5374\u6ca1\u6709\u6025\u7740\u8d70\uff0c\u8fd8\u966a\u4ed6\u4eec\u5230\u9644\u8fd1\u7684\u591c\u603b\u4f1a\u91cc\u53bb\u5531\u6b4c\u3002\u8fd9\u8ba9\u738b\u6d77\u7c9f\u5f88\u610f\u5916\uff0c\u5373\u4f7f\u4e0d\u662f\u60c5\u5987\uff0c\u80e1\u5b97\u5e86\u4f1a\u653e\u5f03\u8fd9\u6837\u7684\u5c24\u7269\u4e0d\u8d2a\u5634\uff1f\u5012\u662f\u80e1\u91d1\u661f\u4e3b\u52a8\u4ecb\u7ecd\u8d77\u6797\u96ea\u7684\u8eab\u4efd\uff0c\u738b\u6d77\u7c9f\u624d\u6653\u5f97\u5979\u539f\u6765\u662f\u7cbe\u5178\u5730\u4ea7\u96c6\u56e2\u7684\u603b\u88c1\uff0c\u5413\u4e86\u4e00\u5927\u8df3\uff0c\u90fd\u8bf4\u5efa\u90ba\u5e02\u5341\u5927\u6c11\u8425\u5730\u4ea7\u96c6\u56e2\u4e4b\u4e00\u7cbe\u5178\u5730\u4ea7\u7684\u8001\u677f\u662f\u4e2a\u624b\u773c\u901a\u5929\u7684\u7f8e\u5973\uff0c\u7edd\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u773c\u524d\u8fd9\u4f4d\u3002</p><p>\u3000\u3000\u4e00\u76f4\u73a9\u95f9\u5230\u51cc\u6668\u5c06\u80e1\u91d1\u661f\u4e0e\u4ed6\u540c\u5b66\u3001\u670b\u53cb\u9001\u56de\u53bb\u4e4b\u540e\uff0c\u738b\u6d77\u7c9f\u6700\u540e\u624d\u9a7e\u8f66\u72ec\u81ea\u79bb\u5f00\uff0c\u8111\u6d77\u91cc\u8fd8\u76d8\u7ed5\u7740\u6797\u96ea\u5996\u8273\u7684\u5bb9\u989c\uff0c\u6709\u4e9b\u53e3\u5e72\u820c\u71e5\uff0c\u60f3\u7740\u662f\u4e0d\u662f\u5728\u8def\u8fb9\u5e26\u4e2a\u5c0f\u59d0\u53bb\u5bbe\u9986\u91cc\u53d1\u6cc4\u4e00\u4e0b\uff0c\u4eea\u76d8\u8868\u4e0a\u7684\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u738b\u6d77\u7c9f\u62ff\u8d77\u6765\u89c1\u662f\u4e00\u4e2a\u964c\u751f\u7684\u53f7\u7801\u3002</p><p>\u3000\u3000\u201c\u738b\u603b\u5f00\u8f66\u559c\u6b22\u770b\u8def\u8fb9\u7684\u591c\u666f\u5417\uff0c\u8fd8\u662f\u5728\u770b\u8def\u8fb9\u7684\u91ce\u82b1\uff1f\u201d</p><p>\u3000\u3000\u6797\u96ea\u7684\u58f0\u97f3\u4ece\u624b\u673a\u91cc\u4f20\u51fa\u6765\u8ba9\u738b\u6d77\u7c9f\u6253\u4e00\u6fc0\u7075\uff0c\u8f66\u5b50\u538b\u5230\u8def\u7259\u4e0a\uff0c\u65b9\u5411\u76d8\u6709\u4e9b\u5931\u63a7\u5dee\u70b9\u5c31\u649e\u4e0a\u5927\u51b7\u5929\u7a7f\u7740\u7fbd\u7ed2\u670d\u5374\u9732\u51fa\u53ea\u7740\u9ed1\u8272\u7f51\u773c\u4e1d\u889c\u5927\u817f\u7684\u7ad9\u8857\u5973\u3002\u738b\u6d77\u7c9f\u5413\u4e86\u4e00\u58f0\u51b7\u6c57\uff0c\u5c06\u8f66\u6025\u5239\u4f4f\uff0c\u5374\u542c\u89c1\u624b\u673a\u91cc\u4f20\u6765\u653e\u7eb5\u7684\u5e78\u707e\u4e50\u7978\u7684\u7b11\u58f0\uff1b\u5374\u6c7d\u8f66\u60ca\u9b42\u7684\u7ad9\u8857\u5973\u9a82\u9a82\u51bd\u51bd\u7684\u8d70\u8fdc\u5f00\uff1a\u201c\u5988\u7684\uff0c\u627e\u5c0f\u59d0\u60f3\u5978\u5c38\u554a\uff01\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u770b\u89c1\u4e00\u8f86\u5b9d\u9a6c\u8f66\u5728\u5de6\u624b\u8fb9\u505c\u4e86\u4e0b\u6765\uff0c\u8f66\u7a97\u964d\u4e0b\uff0c\u6797\u96ea\u9732\u51fa\u5979\u90a3\u5f20\u5996\u5b7d\u822c\u7684\u8138\u3002</p><p>\u3000\u3000\u201c\u770b\u5230\u73b0\u5728\u7684\u4f60\uff0c\u6211\u4f1a\u60c5\u4e0d\u81ea\u7981\u7684\u60f3\u8d77\u56db\u4e94\u5e74\u524d\u7684\u81ea\u5df1\uff0c\u201d\u6797\u96ea\u8bf4\u9053\uff0c\u201c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u65b9\u6848\uff0c\u6211\u542c\u80e1\u5e02\u957f\u8bf4\u8fc7\uff0c\u5f88\u4e0d\u9519\u554a\u3002\u53ef\u60dc\u6ca1\u6709\u8d35\u4eba\u76f8\u52a9\uff0c\u738b\u603b\u4f60\u7a7a\u6709\u4e00\u8eab\u62b1\u8d1f\u4e5f\u5f97\u4e0d\u5230\u65bd\u5c55\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u559c\u4e0d\u81ea\u7981\uff0c\u4ed6\u4ee5\u4e3a\u8fd8\u8981\u8d39\u5c3d\u5fc3\u673a\u94bb\u8425\u624d\u53ef\u80fd\u5f97\u5230\u80e1\u5b97\u5e86\u7684\u4fe1\u4efb\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u522b\u4eba\u4e5f\u65e9\u5c31\u5728\u8f9b\u8f9b\u82e6\u82e6\u7684\u5bfb\u627e\u730e\u7269\uff0c\u8fd9\u79cd\u4e8b\u672c\u6765\u5c31\u5e94\u8be5\u4e00\u62cd\u5373\u5408\uff0c\u4e4b\u524d\u8fd8\u62c5\u5fc3\u80e1\u5b97\u5e86\u773c\u5149\u4e0d\u591f\u3001\u6c14\u9b44\u4e0d\u8db3\u5462\u3002</p><p>\u3000\u3000\u6797\u96ea\u53d6\u4e86\u4e00\u652f\u70df\u7ed9\u81ea\u5df1\u70b9\u4e0a\uff0c\u5728\u8def\u706f\u4e0b\u5410\u4e86\u4e00\u5c42\u8f7b\u7eb1\u4f3c\u7684\u70df\u96fe\uff0c\u624d\u7f13\u7f13\u8bf4\u9053\uff1a\u201c\u4f60\u51c6\u5907\u4e24\u4efd\u8f6c\u8ba9\u6d77\u7c9f\u79d1\u6280\u80a1\u6743\u7684\u7a7a\u767d\u5408\u540c\uff0c\u660e\u5929\u5e26\u4e0a\u90a3\u4efd\u5b8c\u6574\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u7b56\u5212\u5230\u7cbe\u5178\u5730\u4ea7\u6765\u627e\u6211\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8981\u8f6c\u8ba9\u591a\u5c11\u80a1\u6743\uff1f\u201d\u738b\u6d77\u7c9f\u6ca1\u60f3\u5230\u4f1a\u6765\u5f97\u8fd9\u4e48\u8f7b\u6613\uff0c\u5fcd\u4e0d\u4f4f\u5f00\u53e3\u95ee\u4e86\u4e00\u58f0\u3002</p><p>\u3000\u3000\u201c\u6211\u898140\uff05\u3001\u80e1\u5e02\u957f\u898130\uff05\uff0c\u4f60\u6709\u4ec0\u4e48\u610f\u89c1\uff1f\u201d</p><p>\u3000\u3000\u64cd\uff0c\u738b\u6d77\u7c9f\u5fc3\u91cc\u9a82\u4e86\u4e00\u58f0\uff0c\u90fd\u8bf4\u6709\u4e9b\u4eba\u5403\u8089\u4e0d\u5410\u9aa8\u5934\uff0c\u6ca1\u60f3\u5230\u6797\u96ea\u4e00\u6bdb\u4e0d\u62d4\u5f20\u53e3\u5c31\u60f3\u8981\u53bb\u6d77\u7c9f\u516c\u53f8\u4e03\u6210\u7684\u80a1\u6743\u3002\u80e1\u5b97\u5e86\u62ff\u4e09\u6210\uff0c\u738b\u6d77\u7c9f\u5c31\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u4f46\u662f\u7a81\u7136\u5192\u51fa\u6765\u7684\u6797\u96ea\u7adf\u7136\u8981\u5360\u56db\u6210\uff0c\u6bd4\u80e1\u5b97\u5e86\u8fd8\u8981\u591a\u4e00\u6210\uff0c\u738b\u6d77\u7c9f\u5c31\u6709\u4e9b\u72b9\u8c6b\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u6709\u7591\u95ee\u5417\uff1f\u201d\u6797\u96ea\u5c06\u4f18\u96c5\u7684\u5c06\u70df\u7070\u5f39\u843d\u5728\u8f66\u7a97\u5916\uff0c\u5a07\u5a9a\u7684\u7738\u5b50\u6b64\u65f6\u5374\u7efd\u51fa\u51b7\u51bd\u7684\u7738\u5149\u3002</p><p>\u3000\u3000\u50cf\u6797\u96ea\u8fd9\u4e48\u4e00\u4e2a\u770b\u4e0a\u53bb\u5a07\u6ef4\u6ef4\u7684\u5973\u4eba\u624b\u91cc\u5374\u62e5\u6709\u5efa\u90ba\u5341\u5927\u6c11\u8425\u5730\u4ea7\u516c\u53f8\u7684\u7cbe\u5178\u5730\u4ea7\uff0c\u81ea\u7136\u4e0d\u662f\u7b80\u5355\u7684\u4eba\u7269\uff0c\u738b\u6d77\u7c9f\u5bf9\u8fd9\u70b9\u8fd8\u662f\u6df1\u77e5\u7684\u3002\u738b\u6d77\u7c9f\u867d\u7136\u5bf9\u8fd9\u6837\u7684\u5229\u76ca\u5206\u914d\u6709\u7591\u95ee\uff0c\u5bf9\u65b9\u4ec0\u4e48\u90fd\u6ca1\u6709\u8868\u793a\u5374\u8981\u81ea\u5df1\u7acb\u5373\u5c06\u82e6\u5fc3\u7ecf\u8425\u4e86\u4e24\u5e74\u7684\u516c\u53f8\u90fd\u642d\u8fdb\u53bb\uff0c\u4f46\u662f\u4ed6\u6653\u5f97\u81ea\u5df1\u6ca1\u6709\u8d28\u7591\u7684\u8d44\u683c\uff0c\u4ed6\u751a\u81f3\u8fde\u80e1\u5b97\u5e86\u7684\u8054\u7cfb\u7535\u8bdd\u90fd\u6ca1\u6709\u4e00\u4e2a\u3002\u4eba\u751f\u6216\u8bb8\u5c31\u640f\u8fd9\u4e00\u56de\uff01</p><p>\u3000\u3000\u6797\u96ea\u60a0\u7136\u81ea\u5f97\u7684\u5438\u7740\u70df\uff0c\u4e5f\u6ca1\u8bf4\u8bf7\u738b\u6d77\u7c9f\u62bd\u4e00\u652f\u3002</p><p>\u3000\u3000\u201c\u884c\uff0c\u6211\u660e\u5929\u5e26\u7740\u5408\u7ea6\u6587\u672c\u53bb\u7cbe\u5178\u516c\u53f8\u627e\u4f60\uff0c\u4eba\u751f\u96be\u5f97\u4e00\u56de\u640f\uff0c\u60f3\u5fc5\u50cf\u6797\u603b\u8fd9\u6837\u7684\u5927\u4eba\u7269\u4e5f\u4e0d\u4f1a\u6b3a\u8d1f\u6211\u4eec\u8fd9\u4e9b\u65e0\u540d\u5c0f\u5352\u3002\u201d\u738b\u6d77\u7c9f\u6ca1\u6709\u72b9\u8c6b\u591a\u4e45\uff0c\u6bc5\u7136\u51b3\u7136\u7684\u4e0b\u5b9a\u51b3\u5fc3\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u660e\u5929\u5728\u516c\u53f8\u91cc\u7b49\u4f60\u3002\u201d\u6797\u96ea\u5c06\u5269\u4e0b\u4e00\u622a\u7684\u70df\u8482\u5f39\u843d\uff0c\u5429\u5490\u53f8\u673a\u5f00\u8f66\uff0c\u5b9d\u9a6c\u8f66\u542f\u52a8\uff0c\u8f66\u7a97\u7f13\u7f13\u5347\u8d77\u3002</p><p>\u3000\u3000\u770b\u7740\u6d88\u5931\u5728\u8def\u706f\u8fdc\u5904\u7684\u8f66\u5f71\uff0c\u738b\u6d77\u7c9f\u90fd\u4e0d\u6653\u5f97\u521a\u624d\u505a\u51fa\u7684\u51b3\u5b9a\u5230\u5e95\u662f\u5bf9\u662f\u9519\uff0c\u521a\u624d\u5728\u8fd9\u4e48\u4e00\u4e2a\u4f1a\u8ba9\u6240\u6709\u7537\u4eba\u90fd\u4f1a\u4ea7\u751f\u6b32\u671b\u7684\u5973\u4eba\u9762\u524d\u5374\u611f\u53d7\u4ece\u672a\u6709\u8fc7\u7684\u538b\u529b\u3002\u738b\u6d77\u7c9f\u8ba9\u81ea\u5df1\u7a0d\u7a0d\u51b7\u9759\u4e86\u4e00\u4e9b\uff0c\u5fc3\u91cc\u7684\u6b32\u671b\u662f\u5f7b\u5e95\u7684\u51b7\u5374\u4e0b\u6765\u7684\uff0c\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u9053\uff1a\u201c\u5b81\u5728\u82b1\u4e0b\u6b7b\uff0c\u505a\u9b3c\u4e5f\u98ce\u6d41\uff0c\u5c31\u7b97\u8fd9\u5a18\u4eec\u5751\u4e0a\u4e00\u628a\u4e5f\u4e0d\u51a4\u2026\u2026\u201d\u4ed6\u8fd9\u65f6\u5019\u53ea\u80fd\u8fd9\u4e48\u5b89\u6170\u81ea\u5df1\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u64cd\uff0c\u4eca\u5929\u624d\u6653\u5f97\u4ec0\u4e48\u53eb\u98ce\u9a9a\u5165\u9aa8\u4e86\uff0c\u8fd9\u5a18\u4eec\u8981\u80fd\u7761\u4e0a\u4e00\u591c\uff0c\u771f\u613f\u610f\u5c11\u6d3b\u5341\u5e74\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u6750\u6599\u5904\u7406\u90ae\u4ef6\u5230\u6df1\u591c\u51fa\u6765\u8d70\u4e00\u8d70\u987a\u4fbf\u62c9\u675c\u98de\u3001\u8499\u4e50\u4e00\u8d77\u53bb\u5403\u591c\u5bb5\uff0c\u518d\u987a\u4fbf\u53bb1978\u63a5\u5b59\u9759\u6aac\u56de\u53bb\uff0c\u8d70\u5230\u7f51\u5427\u4e8c\u697c\u542c\u89c1\u5546\u52a1\u533a\u91cc\u6709\u4e24\u4e2a\u5305\u591c\u7684\u5bb6\u4f19\u5728\u7709\u98de\u8272\u821e\u7684\u8c08\u5973\u4eba\uff0c\u5f20\u606a\u4fa7\u5934\u770b\u4e86\u4e00\u773c\uff0c\u6709\u4e9b\u5370\u8c61\uff0c\u662f\u5b66\u751f\u4f1a\u7684\u4e24\u4e2a\u5b66\u751f\u5e72\u90e8\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u4eca\u5929\u5728\u5e0c\u5c14\u987f\u5927\u9152\u5e97\u5305\u4e0b\u4e00\u4e2a\u5c0f\u5bb4\u4f1a\u5385\u4e3a\u80e1\u91d1\u661f\u5e86\u6563\u751f\u65e5\u9080\u8bf7\u4e86\u4e00\u4e9b\u5b66\u751f\u4f1a\u91cc\u7684\u72d0\u670b\u72d7\u53cb\uff0c\u5f20\u606a\u77e5\u9053\u8fd9\u4e8b\u60c5\uff0c\u4ed6\u8fd8\u77e5\u9053\u80e1\u5b97\u5e86\u4eca\u5929\u4f1a\u51fa\u5e2d\u5728\u5e0c\u5c14\u987f\u4e3e\u884c\u7684\u4f01\u4e1a\u5bb6\u8054\u8c0a\u9152\u4f1a\uff0c\u5fc3\u91cc\u60f3\uff1a\u738b\u6d77\u7c9f\u8fd8\u771f\u662f\u4f1a\u94bb\u8425\u554a\uff0c\u4ed6\u4eec\u8c08\u8bba\u7684\u90a3\u4e2a\u98ce\u9a9a\u5165\u9aa8\u7684\u5973\u4eba\u662f\u6797\u96ea\uff1f</p></article>"}}}]);
