(window.webpackJsonp=window.webpackJsonp||[]).push([[908],{931:function(p){p.exports={chapterTitle:null,title:"\u7b2c728\u7ae0 \u5f52\u6765\u524d\u5915",content:"<article><h2>\u7b2c728\u7ae0 \u5f52\u6765\u524d\u5915</h2><p>\u3000\u3000\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u4e0e\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u5bc4\u6258\u4e86\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u3001\u5e2d\u82e5\u7433\u7b49\u4eba\u5b66\u751f\u65f6\u671f\u7684\u521b\u4e1a\u68a6\u60f3\uff0c\u4ed6\u4eec\u52aa\u529b\u7684\u5c06\u8272\u5f69\u6591\u6593\u7684\u68a6\u60f3\u6295\u5c04\u5230\u73b0\u5b9e\u7684\u4e16\u754c\u4e2d\uff0c\u8f6c\u53d8\u4e00\u4e2a\u4e2a\u5177\u4f53\u800c\u7e41\u7410\u7684\u9879\u76ee\uff0c\u6700\u7ec8\u53d8\u6210\u8425\u8fd0\u7684\u4f01\u4e1a\u5b9e\u4f53\u3002</p><p>\u3000\u3000\u7cbe\u529b\u603b\u662f\u6709\u9650\u7684\uff0c\u8fd8\u4e0d\u80fd\u803d\u6401\u5b66\u4e60\u3002\u65bd\u65b0\u98de\u3001\u5e2d\u82e5\u7433\u90fd\u662f\u6bd5\u4e1a\u73ed\u7684\u5b66\u751f\uff0c\u529f\u8bfe\u5012\u662f\u8f7b\u677e\u4e0b\u6765\uff0c\u4f46\u662f\u5e2d\u82e5\u7433\u8fd8\u517c\u7740\u56fd\u5546\u9662\u4e5d\u4e03\u7ea7\u5e74\u7ea7\u4e3b\u4efb\u52a9\u7406\u7684\u5dee\u4e8b\uff0c\u8981\u5206\u4e00\u90e8\u7cbe\u529b\u8fc7\u53bb\uff1b\u65bd\u65b0\u98de\u4e3b\u8981\u5e26\u9886\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u8fdb\u884c\u8f6f\u4ef6\u5f00\u53d1\u3001\u7ed9\u7f51\u5427\u63d0\u4f9b\u6280\u672f\u575a\u6301\uff0c\u7b49\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u5f00\u529e\u4e4b\u540e\u65bd\u65b0\u98de\u4e0e\u866b\u4ff1\u4e50\u90e8\u6210\u5458\u8fd8\u53ef\u4ee5\u5ba2\u4e32\u8bb2\u5e08\uff0c\u4f46\u662f\u65e5\u5e38\u7684\u7ba1\u7406\u5de5\u4f5c\u5e2e\u4e0d\u4e0a\u591a\u5c11\u5fd9\uff0c\u675c\u98de\u3001\u8499\u4e50\u4e5f\u4e0d\u4f1a\u8ba9\u65e5\u5e38\u7e41\u7410\u7684\u7ba1\u7406\u5de5\u4f5c\u53bb\u7ea0\u7f20\u65bd\u65b0\u98de\u3002\u8499\u4e50\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4e09\u4e2a\u4eba\u90fd\u8bfb\u5927\u4e09\uff0c\u529f\u8bfe\u7a0d\u8f7b\u677e\u4e00\u4e9b\uff0c\u4f46\u662f\u4ed6\u4eec\u627f\u62c5\u7684\u5de5\u4f5c\u91cf\u8981\u6bd4\u4e00\u4e2a\u5bfb\u5e38\u7684\u516c\u53f8\u767d\u9886\u8981\u7e41\u91cd\u5f97\u591a\uff0c\u675c\u98de\u66f4\u4e0d\u7528\u8bf4\uff0c\u4f55\u51b5\u4ed6\u521a\u8fdb\u5927\u5b66\u7684\u5b66\u4e60\u538b\u529b\u6700\u5927\u7684\uff0c\u4ed6\u53c8\u4e0d\u80fd\u50cf\u5f20\u606a\u90a3\u6837\u51e0\u4e4e\u8fde\u6559\u5ba4\u95e8\u671d\u54ea\u8fb9\u5f00\u7684\u90fd\u4e0d\u77e5\u9053\u3002\u5bfb\u627e\u4e00\u4e2a\u5408\u9002\u7684\u3001\u80fd\u9ed8\u5951\u914d\u5408\u7684\u4eba\u6765\u8d1f\u8d23\u521b\u57df\u516c\u53f8\u7684\u65e5\u5e38\u7ba1\u7406\u5de5\u4f5c\u662f\u76f8\u5f53\u8feb\u5207\u7684\u3002</p><p>\u3000\u3000\u675c\u98de\u65e9\u5c31\u7785\u51c6\u4ee4\u5c0f\u71d5\u4e86\uff0c\u4f5c\u4e3a\u56fd\u5546\u9662\u521a\u6bd5\u4e1a\u51fa\u53bb\u7684\u5e08\u59d0\uff0c\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u90fd\u4e0e\u4ee4\u5c0f\u71d5\u8ba4\u8bc6\uff0c\u4ee4\u5c0f\u71d5\u5728\u5de5\u4f5c\u4e0a\u7684\u80fd\u529b\u4e5f\u662f\u503c\u5f97\u8ba4\u540c\u7684\uff0c\u53ea\u662f\u675c\u98de\u6709\u4e9b\u5fe4\u5b59\u9759\u6aac\uff0c1978\u4e5f\u521a\u91cd\u65b0\u8425\u4e1a\u6ca1\u51e0\u4e2a\u6708\uff0c\u5c31\u5c06\u4ee4\u5c0f\u71d5\u4ece1978\u6316\u8fc7\u6765\uff0c\u8fd8\u771f\u6015\u5b59\u9759\u6aac\u53d1\u98da\u3002</p><p>\u3000\u3000\u539f\u6765\u53ea\u9700\u8981\u5305\u4e0b\u516c\u5bd3\u516c\u5171\u533a\u57df\u4e00\u661f\u671f\u7684\u6e05\u6d01\u5de5\u4f5c\uff0c\u63a5\u5230\u5b59\u9759\u6aac\u66ff\u4ee4\u5c0f\u71d5\u6253\u6765\u7684\u7535\u8bdd\uff0c\u675c\u98de\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u62c9\u7740\u8499\u4e50\u4ed6\u4eec\u5c31\u8d76\u52301978\u6765\u627e\u4ee4\u5c0f\u71d5\u8c08\u8058\u8bf7\u5979\u53bb\u521b\u57df\u5de5\u4f5c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u675c\u98de\u4e0e\u8499\u4e50\u8d76\u8fc7\u6765\uff0c\u5f20\u606a\u4e0e\u8bb8\u9e3f\u4f2f\u8fd8\u5728\u89d2\u843d\u91cc\u9759\u6084\u6084\u7684\u4e0b\u68cb\uff0c\u9152\u5427\u662f\u591c\u8272\u5f25\u6f2b\u4e4b\u540e\u624d\u4f1a\u71a0\u71a0\u95ea\u5149\u7684\u573a\u6240\uff0c\u5348\u540e\u7684\u9152\u5427\u91cc\u6ca1\u6709\u4ec0\u4e48\u4eba\uff0c\u9633\u5149\u900f\u8fc7\u7a7f\u8857\u9a91\u697c\u7684\u7a7a\u9699\u6295\u5c04\u5230\u5427\u53f0\u524d\u7684\u5730\u9762\u4e0a\uff0c\u53ea\u6709\u68cb\u5b50\u843d\u5728\u6978\u6728\u68cb\u76d8\u4e5f\u53d1\u51fa\u6e05\u8106\u7684\u54cd\u58f0\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8981\u8d76\u7740\u56de\u5b66\u6821\u4e0a\u8bfe\uff0c\u770b\u7740\u675c\u98de\u4ed6\u4eec\u51e0\u4e2a\u521b\u534f\u7684\u9aa8\u5e72\u90fd\u8d76\u4e86\u8fc7\u6765\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u90fd\u8d76\u8fc7\u6765\u5e72\u4ec0\u4e48\uff0c\u4e00\u8d77\u8fc7\u6765\u7ed9\u4ee4\u5c0f\u71d5\u9762\u8bd5\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u8981\u8058\u8bf7\u7684\u603b\u7ecf\u7406\uff0c\u5f53\u7136\u8981\u6211\u4eec\u5168\u4f53\u6210\u5458\u90fd\u6ee1\u610f\u3002\u201d\u675c\u98de\u7b11\u7740\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5b59\u9759\u6aac\u9f3b\u7ffc\u5fae\u5fae\u76b1\u4e86\u76b1\uff0c\u8868\u793a\u4e0d\u4fe1\uff0c\u5c06\u73ab\u7ea2\u8272\u7684\u5c0f\u80cc\u5305\u659c\u630e\u5230\u80a9\u4e0a\u8981\u5148\u8d70\uff0c\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\uff0c\u56de\u5934\u95ee\u5f20\u606a\uff0c\u201c\u5c0f\u4f73\u5979\u4eec\u660e\u5929\u8981\u4e00\u8d77\u53bb\u4f60\u4eec\u5b66\u6821\u6253\u7f51\u7403\uff0c\u4f60\u6709\u6ca1\u6709\u7a7a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u8fd9\u51e0\u5929\u53ef\u80fd\u4f1a\u4e0b\u96ea\u554a\uff0c\u201d\u5f20\u606a\u62ac\u8d77\u5934\uff0c\u60f3\u5230\u5927\u51ac\u5929\u6253\u7f51\u7403\u53c8\u770b\u4e0d\u5230\u5b59\u9759\u6aac\u5979\u4eec\u7a7f\u7f51\u7403\u88d9\uff0c\u4e0d\u7531\u7684\u610f\u5174\u9611\u73ca\uff0c\u63d0\u4e0d\u8d77\u4ec0\u4e48\u5174\u81f4\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u660e\u5929\u4e2d\u5348\u5510\u5a67\u3001\u76db\u590f\u5979\u4eec\u5c31\u5230\u5efa\u90ba\uff0c\u770b\u5979\u4eec\u6253\u4e0d\u6253\uff0c\u201d\u53c8\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u5230\u4e1c\u5927\u6765\u6253\u7403\uff0c\u8fd8\u6015\u627e\u4e0d\u5230\u966a\u7ec3\u7684\u4eba\u624b\uff1f\u521b\u534f\u6709\u90a3\u4e48\u591a\u7cbe\u529b\u8fc7\u5269\u7684\u5bb6\u4f19\u5728\uff0c\u4e0d\u6653\u5f97\u591a\u5c11\u4eba\u613f\u610f\u966a\u4f60\u4eec\u4e00\u8d77\u8fc7\u5e73\u5b89\u591c\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\u7684\u2026\u2026\u201d\u5b59\u9759\u6aac\u5f97\u610f\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u90fd\u6781\u4e3a\u8d5e\u540c\u7684\u70b9\u5934\u3002\u65f6\u5b66\u658c\u5728\u533b\u9662\u91cc\u4f4f\u4e86\u4e00\u661f\u671f\u5c31\u75ca\u6108\u4e86\uff0c\u4eba\u4e5f\u6062\u590d\u4e86\u751f\u6c14\uff0c\u56b7\u7740\u8981\u5b59\u9759\u6aac\u4ecb\u7ecd\u97f3\u4e50\u5b66\u9662\u7684\u597d\u5973\u5b69\u5b50\u7ed9\u4ed6\u8ba4\u8bc6\uff0c\u5b59\u9759\u6aac\u62c9\u7740\u4ed6\u4eec\u5230\u97f3\u4e50\u5b66\u9662\u5973\u751f\u5bbf\u820d\u90a3\u91cc\u8f6c\u4e86\u4e00\u5708\uff0c\u770b\u5230\u97f3\u4e50\u5b66\u9662\u5973\u751f\u697c\u4e0b\u5468\u672b\u505c\u6ee1\u4e86\u7684\u5b9d\u9a6c\u3001\u5954\u9a70\uff0c\u4ed6\u4eec\u5bf9\u8fd9\u4e2a\u793e\u4f1a\u7684\u8ba4\u8bc6\u53c8\u6df1\u523b\u4e86\u4e00\u6b65\u3002\u5feb\u5230\u5b66\u671f\u672b\u4e86\uff0c\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u7684\u5de5\u4f5c\u53c8\u8fdb\u5165\u6700\u7d27\u5f20\u7684\u7b79\u5907\u65f6\u671f\uff0c\u65f6\u5b66\u658c\u4ed6\u4eec\u5012\u662f\u5145\u6ee1\u4e86\u6597\u5fd7\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u9a91\u7740\u5979\u90a3\u8f86\u6854\u9ec4\u8272\u7684\u5355\u8f66\u79bb\u5f00\uff0c\u675c\u98de\u4ed6\u4eec\u56f4\u5750\u5728\u5427\u53f0\u524d\u4e0e\u4ee4\u5c0f\u71d5\u8c08\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u90a3\u8fb9\u68cb\u6536\u5b98\u7ed3\u675f\uff0c\u5f20\u5955\u5e2e\u7740\u4ed6\u4eec\u6570\u76ee\uff0c\u5f20\u606a\u8bb0\u5f97\u5f20\u5955\u4e4b\u524d\u662f\u4e0d\u4f1a\u4e0b\u68cb\u7684\uff0c\u7b11\u7740\u95ee\u9053\uff1a\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u5b66\u8fc7\u56f4\u68cb\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u65f6\u5e38\u627e\u4e0d\u5230\u4eba\u4e0b\u68cb\uff0c\u786c\u903c\u4ed6\u5b66\u7684\uff0c\u201d\u8bb8\u9e3f\u4f2f\u7b11\u7740\u8bf4\uff0c\u201c\u73b0\u5728\u4ed6\u5148\u6446\u516d\u5b50\uff0c\u52c9\u5f3a\u80fd\u4e0b\u4e24\u76d8\u3002\u201d\u5f20\u5955\u7b11\u4e86\u7b11\uff0c\u8ba4\u771f\u7684\u6570\u76ee\uff0c\u6ca1\u6709\u5c94\u5f00\u5fc3\u601d\u642d\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u5bf9\u5f20\u5955\u8bf4\uff1a\u201c\u8bb8\u8001\u5e08\u8fd9\u6bb5\u65f6\u95f4\u4f1a\u7559\u5728\u5efa\u90ba\uff0c\u4f60\u6709\u7a7a\u95f2\u65f6\u95f4\u53ef\u4ee5\u53bb\u4e16\u7eaa\u9526\u6e56\u5e2e\u5fd9\uff0c\u8fd9\u4e8b\u5c0f\u53d4\u8ddf\u6211\u8bf4\u8fc7\uff0c\u4e0d\u6653\u5f97\u6709\u6ca1\u6709\u8ddf\u4f60\u8bf4\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u7b49\u4f1a\u513f\u6253\u7535\u8bdd\u7ed9\u5c0f\u53d4\u2026\u2026\u201d\u5f20\u5955\u8bf4\u9053\u3002</p><p>\u3000\u3000\u6570\u597d\u76ee\uff0c\u5f20\u606a\u8fd8\u662f\u8f93\u7ed9\u8bb8\u9e3f\u4f2f\u4e00\u76ee\uff0c\u5c06\u68cb\u5b50\u6536\u62fe\u8fdb\u68cb\u76d2\uff0c\u5f20\u606a\u8ba9\u5085\u4fca\u5f00\u8f66\u9001\u8bb8\u9e3f\u4f2f\u3001\u5f20\u5955\u5148\u53bb\u6797\u4e1a\u516c\u53f8\u4ea4\u63a5\u4e00\u4e9b\u4e8b\u52a1\uff0c\u665a\u4e0a\u518d\u63a5\u4ed6\u4eec\u8fc7\u6765\u4e00\u8d77\u7528\u9910\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u5427\u53f0\u91cc\uff0c\u81ea\u5df1\u716e\u8d77\u5496\u5561\u8d77\u6765\uff0c\u7b49\u7740\u5496\u5561\u9999\u6c14\u5f25\u6f2b\u51fa\u6765\uff0c\u770b\u675c\u98de\u3001\u8499\u4e50\u4e00\u672c\u6b63\u7ecf\u7684\u4e0e\u4ee4\u5c0f\u71d5\u8ba8\u8bba\u521b\u57df\u6295\u8d44\u6709\u9650\u516c\u53f8\u4e0e\u521b\u534f\u7684\u672a\u6765\u3002</p><p>\u3000\u3000\u5c06\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u529e\u6210\u4e4b\u540e\uff0c\u9664\u4e86\u89c4\u6a21\u7ee7\u7eed\u6269\u5927\u4e4b\u5916\uff0c\u5230\u660e\u5e74\uff0c\u4f1a\u8003\u8651\u7f51\u5427\u7684\u8fde\u7410\u7ecf\u8425\uff0c\u4e5f\u4f1a\u771f\u6b63\u7684\u53d1\u6325\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u6fc0\u52b1\u5927\u5b66\u751f\u521b\u4e1a\u7684\u529f\u80fd\uff0c\u8ba1\u5212\u5728\u71d5\u5f52\u6e56\u533a\u6240\u6709\u9ad8\u6821\u8303\u56f4\u4e4b\u5185\u5f00\u5c55\u5927\u5b66\u751f\u521b\u4e1a\u8ba1\u5212\u5927\u8d5b\uff0c\u83b7\u5f97\u5927\u8d5b\u4f18\u80dc\u7684\u521b\u4e1a\u8ba1\u5212\u3001\u5546\u4e1a\u65b9\u6848\uff0c\u5c06\u83b7\u5f97\u521b\u57df\u516c\u53f8\u7684\u6ce8\u8d44\u6216\u8005\u7531\u521b\u57df\u516c\u53f8\u76f4\u63a5\u51fa\u8d44\u5c06\u521b\u4e1a\u8ba1\u5212\u4e70\u4e0b\u6765\u8fd0\u8425\u3002\u8fd9\u662f\u4e00\u4e2a\u5e2e\u5fd9\u9ad8\u6821\u5b66\u751f\u5b9e\u73b0\u521b\u4e1a\u68a6\u60f3\u7684\u6784\u60f3\uff0c\u4e5f\u662f\u5f20\u606a\u5bc4\u671b\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u8fd9\u4e48\u4e00\u4e2a\u5b66\u751f\u7ec4\u7ec7\u80fd\u6316\u6398\u3001\u57f9\u517b\u51fa\u66f4\u591a\u7684\u5546\u4e1a\u4eba\u624d\u3002</p><p>\u3000\u3000\u201c\u8c08\u4e86\u600e\u4e48\u6837\u4e86\uff1f\u201d\u5f20\u606a\u62ff\u51fa\u4e03\u53ea\u676f\u5b50\uff0c\u5e2e\u675c\u98de\u3001\u4ee4\u5c0f\u71d5\u4ed6\u4eec\u90fd\u5012\u4e0a\u5496\u5561\u3002</p><p>\u3000\u3000\u201c\u5bf9\u4ed6\u4eec\u7684\u60f3\u6cd5\u8fd8\u662f\u6709\u4e00\u4e9b\u4e0d\u8ba4\u540c\u7684\u5730\u65b9\uff0c\u201d\u4ee4\u5c0f\u71d5\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4ed6\u4eec\u662f\u8001\u677f\uff0c\u4e00\u4e9b\u4e0d\u4e00\u81f4\u7684\u5730\u65b9\u53ef\u4ee5\u5ffd\u7565\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5496\u5561\u4e00\u4e00\u9012\u5230\u5404\u4eba\u7684\u624b\u91cc\uff0c\u8463\u8dc3\u534e\u63a5\u8fc7\u5496\u5561\uff0c\u7a81\u7136\u60f3\u8d77\u738b\u6d77\u7c9f\u597d\u4e9b\u5929\u6ca1\u6709\u51fa\u73b0\uff0c\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u5c31\u8bf4\u4e86\u51fa\u6765\u3002\u73b0\u5728\u5e2d\u82e5\u7433\u7ed9\u5e74\u7ea7\u529e\u4e0e\u521b\u57df\u4e24\u5934\u7684\u4e8b\u52a1\u7f20\u4f4f\u8131\u4e0d\u5f00\u8eab\uff0c\u8463\u8dc3\u534e\u4ed6\u4eec\u770b\u4e0d\u5230\u738b\u6d77\u7c9f\uff0c\u4e5f\u8bf4\u660e\u5e2d\u82e5\u7433\u597d\u4e45\u6ca1\u770b\u5230\u738b\u6d77\u7c9f\u4e86\u3002</p><p>\u3000\u3000\u201c\u4ed6\u554a\uff0c\u8df3\u4e0a\u68a7\u6850\u679d\u5934\u6210\u51e4\u51f0\u4e86\u2026\u2026\u201d\u5f20\u606a\u5c06\u5496\u5561\u9012\u7ed9\u5e2d\u82e5\u7433\uff0c\u88ab\u4fee\u6539\u8fc7\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u65b9\u6848\u5728\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u7684\u652f\u6301\u6628\u5929\u76f4\u63a5\u9012\u4ea4\u7ed9\u5e02\u59d4\uff0c\u738b\u6d77\u7c9f\u8fd9\u65f6\u5019\u5df2\u7ecf\u662f\u5efa\u90ba\u5e02\u59d4\u526f\u5e02\u957f\u3001\u5e02\u957f\u8096\u660e\u5efa\u4e0e\u5efa\u90ba\u5e02\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u5e02\u957f\u7684\u5ea7\u4e0a\u8d35\u5bbe\uff0c\u8bf4\u4ed6\u8df3\u4e0a\u68a7\u6850\u53d8\u6210\u51e4\u51f0\u4e00\u70b9\u90fd\u4e0d\u4e3a\u8fc7\u3002\u5f20\u606a\u770b\u4e86\u5e2d\u82e5\u7433\u4e00\u773c\uff0c\u770b\u5979\u4f3c\u4e4e\u8fd8\u4e0d\u77e5\u9053\u8fd9\u4e9b\u4e8b\u60c5\u3002\u4e0d\u8981\u8bf4\u738b\u6d77\u7c9f\u6b64\u65f6\u5fd9\u5f97\u4e0d\u53ef\u5f00\u4ea4\uff0c\u4ed6\u9700\u8981\u5df4\u7ed3\u7684\u5927\u4eba\u7269\u8ba9\u4ed6\u5e94\u63a5\u4e0d\u6687\uff0c\u5c31\u7b97\u4ed6\u6709\u7a7a\u95f2\u65f6\u95f4\uff0c\u4e5f\u4e0d\u5927\u53ef\u80fd\u4f1a\u8fd9\u91cc\u51fa\u73b0\u4e86\uff0c\u4ed6\u4eec\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7acb\u9879\u8ba1\u5212\u662f\u76f4\u63a5\u527d\u7a83\u4e86\u6a61\u6811\u56ed\u7684\u4e1c\u897f\uff0c\u4ed6\u5fc3\u91cc\u6709\u9b3c\uff0c\u5f53\u7136\u4f1a\u62c5\u5fc3\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u6709\u53ef\u80fd\u4e5f\u770b\u8fc7\u6a61\u6811\u56ed\u7684\u539f\u4ef6\uff0c\u53ea\u6015\u4ed6\u8fd9\u6bb5\u65f6\u95f4\u7535\u8bdd\u8054\u7cfb\u5e2d\u82e5\u7433\u7684\u6b21\u6570\u4e5f\u5c11\u4e86\uff0c\u8fd9\u662f\u4e00\u4e2a\u4eba\u4f1a\u4e0b\u610f\u8bc6\u56de\u907f\u7684\u5fc3\u75be\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u7ed9\u8499\u5728\u9f13\u91cc\uff0c\u4ec0\u4e48\u90fd\u4e0d\u6653\u5f97\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u8fd8\u66ff\u738b\u6d77\u7c9f\u4e0d\u597d\u610f\u601d\u7684\u7b11\u4e86\u7b11\uff0c\u5173\u5fc3\u8d77\u5f20\u606a\u7684\u671f\u672b\u8003\u8bd5\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5e73\u65f6\u90fd\u5f88\u5c11\u4e0a\u8bfe\u53bb\uff0c\u8bfe\u5802\u6210\u7ee9\u90a3\u5c31\u6ca1\u6709\u4e86\uff0c\u671f\u672b\u8003\u8bd5\u8981\u5f88\u8ba4\u771f\u624d\u884c\uff0c\u4f60\u6709\u6ca1\u6709\u628a\u63e1\u4e0d\u6302\u7ea2\u706f\uff1f\u201d</p><p>\u3000\u3000\u201c\u660e\u5929\u5c31\u6709\u4eba\u8fc7\u6765\u5e2e\u4ed6\u8f85\u5bfc\u529f\u8bfe\uff0c\u4e0d\u7528\u6211\u4eec\u66ff\u4ed6\u4eec\u64cd\u5fc3\u3002\u201d\u4ee4\u5c0f\u71d5\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u6211\u53ea\u662f\u8ba1\u5212\u7740\u53bb\u54ea\u91cc\u6e21\u5047\uff0c\u90fd\u5fd8\u4e86\u8fd8\u6709\u671f\u672b\u8003\u8bd5\u8fd9\u56de\u4e8b\u2026\u2026\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u201c\u518d\u8bf4\u5427\uff0c\u597d\u5b66\u751f\u603b\u6709\u90a3\u4e48\u51e0\u4e2a\u7684\uff0c\u6211\u7684\u89c6\u529b\u4e5f\u4e0d\u5dee\u3002\u201d</p><p>\u3000\u3000\u9999\u6e2f\u5927\u5b66\u7684\u5bd2\u5047\u4ece\u5723\u8bde\u524d\u5915\u5f00\u59cb\u4e00\u76f4\u6301\u7eed\u5230\u4e00\u6708\u5e95\uff0c\u800c\u5185\u5730\u4eca\u5929\u7684\u5bd2\u5047\u5dee\u4e0d\u591a\u8fd8\u8981\u8fc7\u4e00\u4e2a\u6708\u624d\u5f00\u59cb\u653e\uff0c\u60f3\u5230\u76db\u590f\u3001\u5510\u5a67\u660e\u5929\u4e2d\u5348\u5c31\u8981\u56de\u5efa\u90ba\u6765\uff0c\u5c31\u7b97\u5510\u5a67\u4e2d\u9014\u4f1a\u56de\u6d77\u5dde\uff0c\u5927\u90e8\u5206\u7684\u65f6\u95f4\u8fd8\u662f\u4f1a\u7559\u5728\u5efa\u90ba\u966a\u5f20\u606a\u3002\u60f3\u5230\u9a6c\u4e0a\u5c31\u6709\u4e24\u4e2a\u514d\u8d39\u7684\u52b3\u529b\u53ef\u4ee5\u4f7f\u5524\uff0c\u675c\u98de\u5f97\u610f\u7684\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u9664\u4e86\u8499\u4e50\uff0c\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u5e2d\u82e5\u7433\u4ed6\u4eec\u90fd\u6ca1\u6709\u89c1\u8fc7\u5510\u5a67\uff0c\u4e5f\u9887\u4e3a\u671f\u671b\u3002</p><p>\u3000\u3000\u591c\u91cc\u4e0e\u5c0f\u53d4\u3001\u90b5\u81f3\u521a\u3001\u8bb8\u9e3f\u4f2f\u4ed6\u4eec\u5403\u8fc7\u996d\u56de\u6765\u5c31\u4e0b\u4e86\u96ea\u3002\u96ea\u5f88\u5927\uff0c\u5f20\u606a\u5750\u5728\u5367\u5ba4\u91cc\u4e0e\u5510\u5a67\u901a\u7535\u8bdd\u65f6\uff0c\u770b\u7740\u7a97\u5916\u7684\u96ea\u82b1\u98d8\u843d\u5728\u7a97\u53f0\u4e0a\uff0c\u5929\u6c14\u9884\u62a5\u660e\u5929\u8fd8\u4f1a\u7ee7\u7eed\u4e0b\u96ea\uff0c\u5012\u662f\u5f88\u7b26\u5408\u5e73\u5b89\u591c\u7684\u6c1b\u56f4\u3002\u4e0d\u6653\u5f97\u9ad8\u6821\u4ece\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\u4e60\u60ef\u8fc7\u5723\u8bde\u8282\uff0c\u53ea\u662f\u53d1\u73b0\u8fd9\u65f6\u5019\u5468\u56f4\u7684\u4eba\u5bf9\u5e73\u5b89\u591c\u90fd\u5f88\u91cd\u89c6\uff0c\u5373\u4f7f\u8ba1\u7b97\u673a\u7f51\u7edc\u57f9\u8bad\u5b66\u6821\u7684\u7b79\u5907\u5de5\u4f5c\u518d\u5fd9\u788c\uff0c\u675c\u98de\u4ed6\u4eec\u4e5f\u88ab\u8feb\u8981\u505c\u4e0b\u6765\u4e00\u5929\u3002\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4ed6\u4eec\u7ea6\u4e86\u5b59\u9759\u6aac\u5979\u4eec\u7cfb\u7684\u5973\u5b69\u5b50\u4e00\u8d77\u6253\u7f51\u7403\u3002\u7f51\u7403\u6d3b\u52a8\u6216\u8bb8\u56e0\u4e3a\u4e0b\u96ea\u5929\u6c14\u800c\u4e2d\u6b62\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u66f4\u5927\u7684\u91ce\u5fc3\u662f\u7ea6\u8fd9\u4e9b\u5973\u5b69\u5b50\u4e00\u8d77\u53bb\u770b\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\uff0c\u5c31\u6307\u671b\u80fd\u4e00\u4e24\u4e2a\u7f8e\u5c11\u5973\u7684\u5185\u80fd\u4f1a\u7ed9\u8fd9\u90e8\u7535\u5f71\u6467\u6b8b\u597d\u8ba9\u4ed6\u4eec\u8d81\u865a\u800c\u5165\u3002\u5f20\u606a\u4e5f\u5728\u7535\u8bdd\u8ba4\u771f\u7684\u8ba1\u5212\u7740\u5510\u5a67\u5230\u5efa\u90ba\u540e\u7684\u6d3b\u52a8\uff0c\u5510\u5a67\u5219\u5728\u7535\u8bdd\u90a3\u5934\u4e0e\u76db\u590f\u4e00\u8fb9\u6536\u62fe\u884c\u674e\u4e00\u8fb9\u4e0e\u5f20\u606a\u80e1\u626f\u3002</p></article>"}}}]);
