(window.webpackJsonp=window.webpackJsonp||[]).push([[91],{114:function(p){p.exports={chapterTitle:null,title:"\u7b2c91\u7ae0 \u5bb6\u957f\u4f1a",content:"<article><h2>\u7b2c91\u7ae0 \u5bb6\u957f\u4f1a</h2><p>\u3000\u3000\u661f\u671f\u5929\u7684\u5bb6\u957f\u4f1a\uff0c\u9664\u4e86\u5728\u6821\u4f4f\u5bbf\u7684\u5b66\u751f\uff0c\u5176\u4ed6\u4eba\u90fd\u653e\u5047\u4e00\u5929\uff0c\u60df\u6709\u5f20\u606a\u3001\u5510\u5a67\u3001\u675c\u98de\u7279\u6b8a\uff0c\u597d\u50cf\u5b66\u6821\u8981\u8fdb\u884c\u5b66\u6821\u3001\u5bb6\u957f\u3001\u5b66\u751f\u4e09\u65b9\u6df1\u5165\u7ec6\u81f4\u7684\u4ea4\u8c08\uff0c\u624d\u80fd\u5728\u5f20\u77e5\u884c\u3001\u5510\u5b66\u8c26\uff0c\u4ee5\u53ca\u675c\u5c0f\u5c71\u9762\u524d\u8868\u73b0\u51fa\u5bf9\u5b66\u751f\u7684\u5c3d\u804c\u3002</p><p>\u3000\u3000\u6559\u5ba4\u91cc\u9646\u7eed\u7684\u8fdb\u53bb\u4e00\u4e9b\u5bb6\u957f\uff0c\u5f20\u606a\u3001\u5510\u5a67\u3001\u675c\u98de\u5728\u6559\u5ba4\u5916\u6328\u7740\u680f\u6746\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u8fdc\u8fdc\u7684\u770b\u7740\u5510\u5b66\u8c26\u7684\u5c0f\u8f66\u9a76\u8fdb\u6821\u95e8\uff0c\u5f20\u606a\u7709\u5934\u6324\u5230\u4e00\u8d77\u53bb\u4e86\uff0c\u5bf9\u5510\u5a67\u8bf4\uff1a\u201c\u6211\u7684\u5929\uff0c\u4f60\u7238\u8fd8\u771f\u6709\u8fd9\u95f2\u5de5\u592b\uff0c\u4f60\u7238\u4e00\u8fc7\u6765\uff0c\u675c\u98de\u7684\u7238\u7238\u80af\u5b9a\u8ddf\u7740\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u6ca1\u4ec0\u4e48\u62c5\u5fc3\u7684\uff0c\u201d\u675c\u98de\u5e78\u707e\u4e50\u7978\u7684\u8bf4\u7740\uff0c\u770b\u89c1\u4ed6\u8001\u5b50\u7684\u5c0f\u8f66\u7d27\u8ddf\u5728\u540e\u9762\u8fdb\u4e86\u6821\u95e8\uff0c\u201c\u5976\u5976\u7684\uff0c\u8001\u5934\u5b50\u771f\u8fc7\u6765\u4e86\uff0c\u4e0d\u662f\u8bf4\u4eca\u5929\u4e0a\u5348\u8fd8\u8981\u5f00\u4f1a\u5417\uff1f\u201d</p><p>\u3000\u3000\u770b\u7740\u5c0f\u8f66\u5728\u6559\u5b66\u697c\u524d\u7684\u7a7a\u5730\u505c\u4e0b\u6765\uff0c\u5510\u5b66\u8c26\u3001\u5f20\u77e5\u884c\u3001\u675c\u98de\u5148\u540e\u94bb\u51fa\u8f66\uff0c\u738b\u708e\u658c\u3001\u66f9\u5149\u660e\u4ece\u53e6\u4e00\u4fa7\u8fce\u8fc7\u53bb\u3002\u5510\u5a67\u5148\u8e66\u8e66\u8df3\u8df3\u7684\u4e0b\u697c\u53bb\u63a5\u5979\u7238\uff0c\u5012\u4e0d\u6653\u5f97\u8c01\u8bf4\u4e86\u4e00\u58f0\u201c\u5510\u5e02\u957f\u4e5f\u6765\u4e86\u201d\uff0c\u6559\u5ba4\u91cc\u7684\u5bb6\u957f\u90fd\u8dd1\u4e86\u51fa\u6765\uff0c\u5bfb\u5e38\u4eba\u514d\u4e0d\u5f97\u8981\u72b9\u8c6b\u4e00\u4e0b\uff0c\u6709\u4e24\u4e09\u4e2a\u4eba\u9886\u5934\u5148\u4e0b\u53bb\uff0c\u5176\u4ed6\u4eba\u90fd\u8ddf\u7740\u4e0b\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u8db4\u5728\u680f\u6746\u4e0a\u5f80\u4e0b\u770b\uff0c\u5510\u5b66\u8c26\u4ed6\u4eec\u4e09\u4eba\u7ed9\u738b\u708e\u658c\u62e6\u5728\u4e0b\u9762\u5bd2\u6684\uff0c\u5bb6\u957f\u4eec\u4e00\u4f1a\u513f\u5c31\u51b2\u5230\u697c\u4e0b\uff0c\u5c06\u4ed6\u4eec\u56f4\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u626d\u5934\u770b\u89c1\u674e\u829d\u82b3\u8fd8\u5728\u6559\u5ba4\u91cc\u5e03\u7f6e\u4f1a\u573a\uff0c\u5fc3\u60f3\u5979\u5728\u5bb6\u957f\u4f1a\u4e0a\u5927\u6982\u4e0d\u4f1a\u7ed9\u7238\u7238\u7559\u4ec0\u4e48\u60c5\u9762\u5427\uff1f\u60f3\u5230\u8fd9\u91cc\u5c31\u5934\u75bc\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u5510\u5b66\u8c26\u3001\u675c\u5c0f\u5c71\u7ed9\u4f17\u4eba\u62e5\u4e0a\u56db\u697c\uff0c\u770b\u7740\u5f20\u606a\u4e0e\u675c\u5c0f\u5c71\u7684\u513f\u5b50\u8fdc\u8fdc\u7ad9\u5728\u4e00\u8fb9\uff0c\u8138\u4e0a\u6302\u7740\u4e8b\u4e0d\u5173\u5df1\u7684\u5fae\u7b11\uff0c\u90fd\u6ca1\u597d\u610f\u601d\u5c06\u4ed6\u53eb\u8fc7\u6765\uff0c\u8ddf\u7740\u5927\u5bb6\u8fdb\u4e86\u6559\u5ba4\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u5510\u5a67\u3001\u675c\u98de\u90fd\u662f\u8d70\u8bfb\uff0c\u6ca1\u6709\u5bbf\u820d\uff0c\u675c\u98de\u63d0\u8bae\u53bb\u7537\u751f\u5bbf\u820d\u53bb\u73a9\uff0c\u5f20\u606a\u6447\u4e86\u6447\u5934\u3002\u91cd\u56de\u4e5d\u56db\u5e74\uff0c\u518d\u4e00\u6b21\u8d70\u8fdb\u6d77\u5dde\u5e02\u7b2c\u4e00\u4e2d\u5b66\uff0c\u4e0e\u5176\u4ed6\u540c\u9f84\u4eba\u4e4b\u95f4\u5b58\u5728\u7740\u5f88\u6df1\u7684\u4ee3\u6c9f\uff0c\u6ca1\u6709\u4e0e\u5176\u4ed6\u5341\u4e94\u516d\u5c81\u7684\u5c11\u5e74\u63a5\u89e6\u7684\u5174\u8da3\u3002\u4e0e\u5510\u5a67\u3001\u675c\u98de\u7684\u5173\u7cfb\u5f53\u7136\u8981\u53e6\u8bf4\uff0c\u5728\u81ea\u5df1\u51fa\u8f66\u7978\u524d\u4e00\u5929\uff0c\u8fd8\u521a\u8ddf\u675c\u98de\u6ce1\u8fc7\u9152\u5427\u5462\uff0c\u90a3\u662f\u7ef4\u6301\u4e86\u5341\u51e0\u5e74\u7684\u575a\u56fa\u53cb\u8c0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u3001\u675c\u98de\u5728\u6821\u56ed\u95f2\u901b\u80e1\u626f\uff0c\u5dee\u4e0d\u591a\u5230\u5341\u70b9\u949f\u5de6\u53f3\uff0c\u770b\u5230\u6709\u5b66\u751f\u5bb6\u957f\u5f80\u5bbf\u820d\u91cc\u8d70\uff0c\u5fc3\u60f3\u5dee\u4e0d\u5230\u8f6e\u5230\u81ea\u5df1\u6328\u8bad\u7684\u65f6\u95f4\u4e86\uff0c\u4e0e\u5510\u5a67\u3001\u675c\u98de\u5f80\u6559\u5b66\u697c\u8d70\uff0c\u770b\u5230\u7edd\u5927\u90e8\u5206\u73ed\u7ea7\u7684\u5bb6\u957f\u4f1a\u90fd\u7ed3\u675f\u4e86\u3002\u7ecf\u8fc7\u9648\u5983\u84c9\u73ed\u65f6\uff0c\u5979\u6b63\u7ad9\u5728\u8fc7\u9053\u91cc\u900f\u6c14\uff0c\u6b63\u8981\u5f80\u6559\u5ba4\u8d70\u3002\u5979\u770b\u5230\u81ea\u5df1\uff0c\u811a\u4e0b\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8138\u4e0a\u9732\u51fa\u5f88\u52c9\u5f3a\u7684\u7b11\u5bb9\u3002\u867d\u7136\u7b11\u5f97\u5f88\u52c9\u5f3a\uff0c\u4f46\u6bd4\u8f83\u4e4b\u524d\u51b7\u6de1\u7684\u6001\u5ea6\uff0c\u5df2\u7ecf\u7b97\u4e0d\u7b80\u5355\u7684\u6539\u8fdb\uff0c\u5f20\u606a\u6328\u7740\u5979\u7684\u8eab\u5b50\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u8fd9\u4e5f\u7b97\u6253\u62db\u547c\uff1f\u201d\u9648\u5983\u84c9\u50f5\u5f3a\u5728\u90a3\u91cc\uff0c\u4e0d\u6653\u5f97\u8138\u4e0a\u8981\u6446\u51fa\u4ec0\u4e48\u8868\u60c5\uff0c\u770b\u7740\u5510\u5a67\u62ff\u7740\u63d0\u7740\u996e\u6599\u4ece\u540e\u9762\u8ffd\u8fc7\u6765\uff0c\u5979\u5fd9\u8f6c\u8eab\u8fdb\u4e86\u6559\u5ba4\u3002</p><p>\u3000\u3000\u63a2\u5934\u770b\u4e86\u770b\u6559\u5ba4\uff0c\u674e\u829d\u82b3\u4eba\u4e0d\u5728\u6559\u5ba4\uff0c\u5bb6\u957f\u90fd\u56f4\u7740\u5510\u5b66\u8c26\u4ed6\u4eec\u5728\u8bf4\u8bdd\uff0c\u60f3\u5fc5\u73ed\u4e0a\u7684\u5bb6\u957f\u4f1a\u4e5f\u7ed3\u675f\u4e86\uff0c\u738b\u708e\u658c\u7ad9\u8d77\u6765\u62cd\u4e86\u62cd\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u5510\u5e02\u957f\u4e8b\u52a1\u70e6\u5fd9\uff0c\u5927\u5bb6\u8fd8\u662f\u653e\u8fc7\u5510\u5e02\u957f\u5427\u3002\u201d\u8fd9\u624d\u8ba9\u90a3\u4e9b\u5bb6\u957f\u5fc3\u4e0d\u7518\u60c5\u4e0d\u613f\u7684\u6563\u5f00\u3002</p><p>\u3000\u3000\u201c\u5510\u5e02\u957f\uff0c\u5230\u529e\u516c\u5ba4\u4f11\u606f\u4e00\u4f1a\u513f\uff0c\u8ba9\u66f9\u4e3b\u4efb\u5c06\u5510\u5a67\u5979\u4eec\u4e5f\u53eb\u5230\u529e\u516c\u5ba4\u53bb\uff0c\u201d\u738b\u708e\u658c\u626d\u5934\u770b\u89c1\u5f20\u606a\u4e09\u4eba\u5c31\u7ad9\u5728\u6559\u5ba4\uff0c\u201c\u4e09\u4e2a\u4eba\u6b63\u597d\u90fd\u8fc7\u6765\u4e86\uff0c\u201d\u53c8\u5429\u5490\u4e00\u8fb9\u7684\u653f\u6559\u5904\u4e3b\u4efb\u66f9\u5149\u660e\uff0c\u201c\u4f60\u53bb\u53eb\u4e00\u4e0b\u674e\u8001\u5e08\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u7238\u7238\u8138\u8272\u4e0d\u5584\uff0c\u8001\u8001\u5b9e\u5b9e\u7684\u8ddf\u5728\u540e\u9762\uff0c\u7ecf\u8fc7\u9648\u5983\u84c9\u73ed\u65f6\uff0c\u4fa7\u5934\u5f80\u91cc\u770b\u4e86\u4e00\u773c\uff0c\u6b63\u770b\u89c1\u9648\u5983\u84c9\u773c\u795e\u614c\u4e71\u7684\u907f\u5f00\u3002</p><p>\u3000\u3000\u738b\u708e\u658c\u5012\u662f\u86ee\u4f1a\u4eab\u53d7\uff0c\u771f\u76ae\u6c99\u53d1\u3001\u7ea2\u6728\u529e\u516c\u684c\u6905\u3001\u4e00\u5c18\u4e0d\u67d3\u7684\u5730\u677f\uff0c\u5899\u58c1\u7740\u6302\u7740\u9644\u5eb8\u98ce\u96c5\u7684\u5b57\u753b\uff0c\u529e\u516c\u5ba4\u91cc\u4fa7\u8fd8\u6709\u4e00\u4e2a\u89d2\u95e8\uff0c\u60f3\u5fc5\u662f\u4f11\u606f\u95f4\u3002\u5f20\u606a\u5fc3\u60f3\u738b\u708e\u658c\u4e0e\u674e\u829d\u82b3\u5077\u60c5\uff0c\u5728\u529e\u516c\u5ba4\u6709\u4ec0\u4e48\u4e0d\u597d\uff0c\u975e\u8981\u5230\u8fd8\u6ca1\u5b8c\u5168\u5efa\u6210\u7684\u79d1\u6559\u697c\uff0c\u96be\u9053\u8bf4\u662f\u53e4\u602a\u7684\u60c5\u8da3\uff0c\u8fd8\u662f\u8bf4\u4ed6\u4eec\u7684\u5173\u7cfb\u65e9\u5c31\u7ed9\u66f9\u5149\u660e\u76ef\u4e0a\u4e86\uff1f</p><p>\u3000\u3000\u6709\u4eba\u7684\u5730\u65b9\u5c31\u6709\u653f\u6cbb\uff0c\u8fd9\u8bdd\u771f\u662f\u4e00\u70b9\u4e0d\u5047\uff0c\u738b\u708e\u658c\u6709\u4ed6\u5728\u6559\u80b2\u5c40\u5f53\u526f\u5c40\u957f\u7684\u8001\u5a46\u6491\u7740\uff0c\u4f46\u662f\u4ed6\u8001\u5a46\u4e00\u5b9a\u4e0d\u60f3\u81ea\u5df1\u7684\u4e08\u592b\u5728\u5b66\u6821\u4e71\u641e\u7537\u5973\u5173\u7cfb\u3002\u738b\u708e\u658c\u4e0d\u53ef\u80fd\u4e0e\u4ed6\u8001\u5a46\u540c\u65f6\u8fdb\u6559\u80b2\u5c40\uff0c\u53ea\u4f1a\u4e00\u76f4\u9738\u5360\u7740\u4e00\u4e2d\u6821\u957f\u7684\u4f4d\u7f6e\u3002\u66f9\u5149\u660e\u4e5f\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u60f3\u8fdb\u6b65\uff0c\u641e\u6389\u738b\u708e\u658c\u90a3\u662f\u6700\u76f4\u63a5\u7684\u624b\u6bb5\u4e86\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u4e07\u4e3b\u4efb\u62bd\u4e0d\u51fa\u7a7a\u8fc7\u6765\u53c2\u52a0\u5bb6\u957f\u4f1a\uff0c\u201d\u738b\u708e\u658c\u4eb2\u81ea\u7ed9\u5927\u5bb6\u6c8f\u597d\u8336\uff0c\u201c\u672c\u7740\u5bf9\u5b66\u751f\u8d1f\u8d23\u7684\u6001\u5ea6\uff0c\u5b66\u6821\u8fd8\u4f1a\u4e13\u95e8\u627e\u4e07\u4e3b\u4efb\u4ea4\u6d41\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u708e\u658c\u4e0d\u6653\u662f\u5e02\u91cc\u7684\u77db\u76fe\uff0c\u5510\u5b66\u8c26\u4e0e\u4e07\u5411\u524d\u4e4b\u524d\u7684\u77db\u76fe\u90fd\u6709\u4e9b\u6fc0\u5316\u4e86\uff0c\u4e07\u52c7\u80fd\u8fc7\u6765\u624d\u6709\u9b3c\u5462\u3002\u5f20\u606a\u63a5\u8fc7\u738b\u708e\u658c\u9012\u8fc7\u6765\u7684\u8336\uff0c\u6ca1\u6709\u50cf\u675c\u98de\u90a3\u6837\u4e00\u672c\u6b63\u7ecf\u7684\u7ad9\u8d77\u6765\uff0c\u8f6c\u5934\u770b\u89c1\u674e\u829d\u82b3\u5c06\u6587\u4ef6\u5939\u62b1\u5728\u80f8\u524d\u8d70\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u674e\u8001\u5e08\u6765\u4e86\uff0c\u90a3\u4f60\u6765\u7ed9\u5510\u5e02\u957f\u3001\u675c\u4e66\u8bb0\u3001\u5f20\u79d8\u4e66\u957f\u8be6\u7ec6\u4ecb\u7ecd\u4e00\u4e0b\u5510\u5a67\u3001\u675c\u98de\uff0c\u8fd8\u6709\u5f20\u606a\u7684\u5b66\u4e60\u60c5\u51b5\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5c34\u5c2c\u7684\u8bf4\uff1a\u201c\u6211\u5bb6\u5c0f\u5b50\u7684\u6210\u7ee9\u5c31\u4e0d\u8981\u5f53\u6210\u5510\u5e02\u957f\u3001\u8001\u675c\u7684\u9762\u4ecb\u7ecd\u4e86\uff0c\u5168\u73ed\u5012\u6570\u7b2c\u4e00\uff0c\u4ed6\u7ed9\u4ed6\u8001\u5b50\u4e22\u4eba\u5462\u3002\u201d</p><p>\u3000\u3000\u201c\u5510\u5a67\u5168\u6821\u6587\u79d1\u73ed\u7b2c\u4e00\uff0c\u675c\u98de\u4e5f\u4e0d\u9519\uff0c\u8fdb\u4e86\u73ed\u7ea7\u524d\u5341\u540d\uff0c\u5f20\u606a\u867d\u7136\u603b\u6210\u7ee9\u4e0d\u662f\u5f88\u7406\u60f3\uff0c\u4f46\u662f\u82f1\u8bed\u3001\u8bed\u6587\u4e24\u79d1\u8003\u5f97\u6bd4\u5510\u5a67\u8fd8\u597d\uff0c\u8fd8\u662f\u503c\u5f97\u5510\u5a67\u3001\u675c\u98de\u597d\u597d\u5b66\u4e60\u7684\uff0c\u5c31\u662f\u504f\u79d1\u592a\u4e25\u91cd\uff0c\u201d\u738b\u708e\u658c\u6781\u529b\u60f3\u7f13\u548c\u5f20\u77e5\u884c\u7684\u5c34\u5c2c\uff0c\u201c\u5c31\u5f20\u606a\u8fd9\u5b69\u5b50\u7684\u82f1\u8bed\u4e0e\u8bed\u6587\uff0c\u8bd5\u5377\u6211\u90fd\u770b\u8fc7\uff0c\u6211\u90fd\u5fcd\u4e0d\u4f4f\u8981\u5938\u4ed6\u5728\u8fd9\u4e24\u95e8\u79d1\u76ee\u6709\u7279\u957f\u5462\uff01\u674e\u8001\u5e08\uff0c\u4f60\u8bf4\u662f\u4e0d\u662f\uff1f\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u98ce\u97f5\u6210\u719f\u7684\u8138\u6709\u4e9b\u51b7\u6de1\uff0c\u6ca1\u6253\u7b97\u7ed9\u5f20\u77e5\u884c\u9762\u5b50\uff1a\u201c\u5f20\u606a\u4e24\u95e8\u529f\u8bfe\u7684\u5e95\u5b50\u5f88\u597d\uff0c\u5c31\u7b97\u5e95\u5b50\u597d\uff0c\u4e5f\u8981\u8ba4\u771f\u5b66\u4e60\uff0c\u8fd9\u4e48\u591a\u4efb\u8bfe\u8001\u5e08\uff0c\u5df2\u7ecf\u6ca1\u6709\u8c01\u5962\u671b\u4ed6\u80fd\u4ea4\u4e00\u56de\u8bfe\u540e\u4f5c\u4e1a\uff0c\u9003\u8bfe\u7684\u6b21\u6570\u4e5f\u591a\u4e86\u4e00\u4e9b\uff0c\u5c31\u62ff\u8fd9\u6b21\u8003\u8bd5\u6765\u8bf4\uff0c\u6700\u540e\u4e00\u95e8\u653f\u6cbb\u8003\u8bd5\uff0c\u4ed6\u5c31\u6ca1\u6709\u53c2\u52a0\uff0c\u4e5f\u6ca1\u6709\u5411\u5b66\u6821\u8bf7\u5047\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u7238\u7238\u8138\u8272\u4e0d\u597d\u770b\uff0c\u5510\u5b66\u8c26\u7684\u8138\u8272\u4e5f\u6709\u4e9b\u51dd\u91cd\uff0c\u5927\u611f\u5934\u75bc\uff0c\u674e\u829d\u82b3\u8fd8\u771f\u662f\u4e00\u70b9\u9762\u5b50\u90fd\u4e0d\u7ed9\u3002</p><p>\u3000\u3000\u738b\u708e\u658c\u8138\u8272\u66f4\u96be\u770b\u4e86\uff0c\u66f9\u5149\u660e\u5728\u4e00\u65c1\u5012\u6709\u4e9b\u5e78\u707e\u4e50\u7978\uff0c\u5fc3\u60f3\uff1a\u8c01\u4e0d\u6653\u5f97\u5510\u5b66\u8c26\u4e0e\u5f20\u77e5\u884c\u5173\u7cfb\uff0c\u53c8\u6781\u559c\u6b22\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u9152\u5e2d\u4e0a\u656c\u9152\uff0c\u90a3\u53ef\u662f\u5dee\u4e0d\u591a\u6240\u6709\u6d77\u5dde\u5e02\u5904\u7ea7\u4ee5\u4e0a\u5e72\u90e8\u4eb2\u773c\u6240\u89c1\uff0c\u674e\u829d\u82b3\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u662f\u62bd\u5510\u5b66\u8c26\u4e0e\u5f20\u77e5\u884c\u7684\u5df4\u638c\u5417\uff1f</p><p>\u3000\u3000\u738b\u708e\u658c\u7ed9\u674e\u829d\u82b3\u4f7f\u4e86\u4f7f\u773c\u8272\uff0c\u4e0d\u8ba9\u5979\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff0c\u5806\u7740\u7b11\u8138\u671d\u5510\u5b66\u8c26\u3001\u5f20\u77e5\u884c\u89e3\u91ca\u9053\uff1a\u201c\u5f20\u606a\u90a3\u5929\u7f3a\u8003\uff0c\u867d\u7136\u6ca1\u6709\u5411\u5b66\u6821\u8bf7\u5047\uff0c\u4e00\u5b9a\u6709\u539f\u56e0\u7684\uff0c\u5c31\u662f\u56e0\u4e3a\u4ed6\u4e00\u95e8\u7f3a\u8003\uff0c\u6240\u4ee5\u603b\u6210\u7ee9\u624d\u6bd4\u8f83\u9760\u540e\uff0c\u4e0d\u7136\u8003\u4e2a\u516b\u4e5d\u5341\u5206\uff0c\u4e5f\u80fd\u6324\u8fdb\u73ed\u4e0a\u524d\u5341\u540d\uff0c\u6211\u770b\u4ed6\u6700\u6709\u6f5c\u529b\uff0c\u8111\u888b\u74dc\u6700\u806a\u660e\u4e0d\u8fc7\u4e86\uff0c\u53ea\u8981\u5f20\u79d8\u4e66\u957f\u4e0e\u5b66\u6821\u597d\u597d\u914d\u5408\uff0c\u8bf4\u4e0d\u5b9a\u671f\u7ec8\u8003\u8bd5\u8fd8\u80fd\u4e0e\u5510\u5a67\u8003\u4e2a\u5e76\u5217\u7b2c\u4e00\uff0c\u674e\u8001\u5e08\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u662f\u8981\u5bf9\u5f20\u606a\u8d1f\u8d23\u4efb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u5bf9\uff0c\u674e\u8001\u5e08\u8981\u4e0d\u8bf4\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u8fd9\u5c0f\u5b50\u5728\u5b66\u6821\u80e1\u4f5c\u975e\u4e3a\uff0c\u674e\u8001\u5e08\u662f\u5bf9\u5b66\u751f\u8d1f\u8d23\u7684\u597d\u8001\u5e08\u3002\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u663e\u7136\u6ca1\u6709\u60f3\u5c31\u8fd9\u6837\u653e\u8fc7\u5f20\u606a\uff0c\u90fd\u5fcd\u4e86\u534a\u5b66\u671f\u7684\u6c14\u4e86\uff0c\u4e07\u52c7\u7684\u6df7\u86cb\u513f\u5b50\u8fd8\u77e5\u9053\u8bf7\u4eba\u6284\u4f5c\u4e1a\uff0c\u5f20\u606a\u5012\u597d\uff0c\u4ece\u5934\u5230\u5c3e\u534a\u4e2a\u5b57\u90fd\u6ca1\u6709\u5199\u8fc7\uff0c\u81ea\u4e60\u8bfe\u522b\u60f3\u770b\u5230\u4ed6\u5728\u6559\u5ba4\u91cc\uff0c\u73b0\u5728\u6f14\u53d8\u5230\u8fde\u671f\u4e2d\u8003\u8bd5\u90fd\u7f3a\u8003\u7684\u7a0b\u5ea6\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u662f\u8981\u5bf9\u5f20\u606a\u8d1f\u8d23\uff0c\u624d\u5c06\u5f20\u606a\u5728\u5b66\u6821\u7684\u60c5\u51b5\u5982\u5b9e\u5411\u5f20\u79d8\u4e66\u957f\u53cd\u5e94\uff0c\u4ed6\u8fd9\u6b21\u8bed\u6587\u8003\u8bd5\u6210\u7ee9\u867d\u7136\u662f\u5168\u5e74\u7ea7\u7b2c\u4e00\uff0c\u4f46\u662f\u4ed6\u7684\u4f5c\u6587\u662f\u6709\u4e9b\u95ee\u9898\u3002\u867d\u7136\u6700\u540e\u8fd8\u662f\u7ed9\u4ed6\u9ad8\u5206\uff0c\u53ea\u662f\u8fd9\u7bc7\u4f5c\u6587\u4f3c\u4e4e\u4e0d\u662f\u4ed6\u8fd9\u79cd\u5e74\u7eaa\u80fd\u5199\u51fa\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u738b\u708e\u658c\u7684\u989d\u5934\u90fd\u5feb\u51fa\u6c57\u4e86\uff0c\u674e\u829d\u82b3\u8fd8\u4e0d\u5982\u76f4\u63a5\u8bf4\u81ea\u5df1\u7684\u4f5c\u6587\u662f\u6284\u88ad\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u90a3\u628a\u5f20\u606a\u7684\u8bed\u6587\u5377\u5b50\u62ff\u7ed9\u6211\u4eec\u770b\u770b\u2026\u2026\u201d\u5510\u5b66\u8c26\u7b11\u7740\u8bf4\uff0c\u201c\u5f20\u77e5\u884c\u5bb6\u8fd9\u6df7\u5c0f\u5b50\uff0c\u4ea4\u767d\u5377\u6211\u76f8\u4fe1\uff0c\u6284\u88ad\u522b\u4eba\u4f5c\u6587\u7684\u53ef\u80fd\u6027\u8fd8\u771f\u662f\u4e0d\u5927\u3002\u201d</p><p>\u3000\u3000\u89c1\u5510\u5b66\u8c26\u4eca\u5929\u66f4\u5173\u5fc3\u5f20\u606a\u7684\u5b66\u4e60\uff0c\u738b\u708e\u658c\u989d\u5934\u7ec8\u4e8e\u6d41\u6c57\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\u662f\u6284\u88ad\uff1f\u90a3\u7bc7\u4f5c\u6587\u6211\u770b\u8fc7\uff0c\u5199\u5f97\u975e\u5e38\u597d\uff0c\u8fd9\u6b21\u7684\u4f5c\u6587\u662f\u4ee5\u2018\u6211\u7231\u6d77\u5dde\u2019\u4e3a\u4e3b\u9898\uff0c\u7531\u5b66\u751f\u81ea\u7531\u53d1\u6325\uff0c\u8bd5\u5377\u5c31\u4e0d\u7528\u770b\u4e86\u2026\u2026\u201d\u663e\u7136\u4ed6\u4e0d\u8ba4\u4e3a\u5f20\u606a\u80fd\u5199\u51fa\u90a3\u6837\u7684\u6587\u7ae0\uff0c\u62ff\u51fa\u6765\u66f4\u4f1a\u4e22\u5f20\u77e5\u884c\u3001\u5510\u5b66\u8c26\u7684\u9762\u5b50\uff0c\u770b\u5230\u674e\u829d\u82b3\u6253\u5f00\u62b1\u5728\u6000\u91cc\u7684\u6587\u4ef6\u5939\u62ff\u51fa\u8bd5\u5377\uff0c\u8138\u90fd\u7eff\u4e86\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u6ca1\u6709\u60f3\u5230\u5510\u5b66\u8c26\u4e5f\u4f1a\u8fd9\u4e48\u5728\u610f\u5f20\u606a\u7684\u5b66\u4e60\uff0c\u77a5\u773c\u770b\u7740\u5f20\u606a\u5728\u4e00\u65c1\u7aef\u7740\u8336\u676f\u6162\u6761\u4e1d\u7406\u7684\u559d\u8336\uff0c\u62bf\u7740\u5634\u5507\u5c06\u8336\u53f6\u5254\u51fa\u6765\uff0c\u989d\u5934\u7684\u9752\u7b4b\u90fd\u5feb\u5d29\u51fa\u6765\u4e86\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u772f\u7740\u773c\u775b\uff0c\u542c\u5230\u6d77\u88d5\u516c\u53f8\u627f\u5305\u65b0\u5149\u9020\u7eb8\u5382\u7684\u6d88\u606f\uff0c\u4ed6\u628a718\u7279\u5927\u4ea4\u901a\u4e8b\u6545\u7684\u6240\u6709\u65b0\u95fb\u62a5\u9053\u91cd\u65b0\u7ffb\u51fa\u6765\u8bfb\u4e86\u4e00\u904d\uff0c\u53c8\u542c\u513f\u5b50\u8bf4\u5f20\u606a\u6709\u4e8b\u6ca1\u4e8b\u5c31\u5f80\u7701\u57ce\u8dd1\uff0c\u867d\u7136\u4e0d\u662f\u5f88\u80af\u5b9a\uff0c\u4f46\u4e5f\u80fd\u731c\u5230\u4e00\u4e9b\u7709\u76ee\uff0c\u96be\u602a\u5510\u5b66\u8c26\u4f1a\u8fd9\u4e48\u5173\u5fc3\u5f20\u77e5\u884c\u513f\u5b50\u7684\u5b66\u4e60\uff0c\u56e0\u4e3a\u4e0a\u9762\u8fd8\u6709\u4eba\u5728\u5173\u5fc3\u7740\u3002\u4e0d\u63d0\u4ed6\u7684\u8eab\u5206\uff0c\u5173\u952e\u8fd9\u4e2a\u4eba\u8fd8\u5e2e\u5510\u5b66\u8c26\u7ffb\u4e86\u60ca\u5929\u5927\u6848\uff0c\u5c06\u4ed6\u9001\u4e0a\u5e02\u957f\u7684\u5b9d\u5ea7\u3002</p><p>\u3000\u3000\u529e\u516c\u5ba4\u7684\u6c14\u6c1b\u6709\u4e9b\u6c89\u90c1\uff0c\u8fde\u513f\u5b50\u8ddf\u5510\u5a67\u90fd\u6452\u4f4f\u6c14\u4e0d\u6562\u52a8\u5f39\uff0c\u675c\u5c0f\u5c71\u5374\u504f\u504f\u770b\u89c1\u5f20\u606a\u4ece\u5bb9\u4e0d\u8feb\u7684\u996e\u7740\u8336\uff0c\u773c\u775b\u8d3c\u6e9c\u7684\u5728\u4f17\u4eba\u8138\u4e0a\u6d41\u8f6c\uff0c\u89c1\u4ed6\u5230\u8fd9\u65f6\u5019\u8fd8\u4e0d\u5fd8\u5bdf\u8a00\u89c2\u8272\uff0c\u5fcd\u4e0d\u4f4f\u7b11\u7740\u5148\u4ece\u674e\u829d\u82b3\u624b\u91cc\u62a2\u8fc7\u5377\u5b50\uff1a\u201c\u6211\u5bb6\u5c0f\u5b50\u5012\u662f\u5f88\u5d07\u62dc\u5f20\u606a\uff0c\u6211\u73b0\u5728\u8bad\u4ed6\uff0c\u4ed6\u603b\u5199\u5f20\u606a\u7684\u8bdd\u53cd\u9a73\u6211\uff0c\u6211\u5148\u770b\u770b\u4ed6\u8fd9\u4f5c\u6587\u662f\u600e\u4e48\u5199\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u2018\u57ce\u5e02\u4e4b\u79c0\u2019\uff0c\u8fd9\u9898\u76ee\u4e0d\u9519\u554a\uff0c\u5f88\u5927\u6c14\uff0c\u6211\u5bb6\u5c0f\u5b50\u8ddf\u6211\u8bf4\u4ed6\u5199\u7684\u4f5c\u6587\u9898\u76ee\u5c31\u662f\u2018\u6211\u7231\u6d77\u5dde\u2019\uff0c\u8fd8\u5f97\u610f\u6d0b\u6d0b\u7684\u81ea\u5439\u81ea\u64c2\uff0c\u6211\u5dee\u70b9\u4e00\u811a\u8e22\u7ffb\u4ed6\uff0c\u8981\u6362\u8fd9\u9898\u76ee\uff0c\u4f5c\u6587\u81f3\u5c11\u5f97\u591a\u52a0\u51e0\u5206\uff0c\u201d\u675c\u5c0f\u5c71\u7684\u8bdd\u8ba9\u529e\u516c\u5ba4\u91cc\u7684\u6c14\u6c1b\u8f7b\u677e\u4e86\u4e00\u4e9b\uff0c\u5510\u5b66\u8c26\u793a\u610f\u4ed6\u5ff5\u51fa\u6765\uff0c\u201c\u57ce\u5e02\u4e4b\u79c0\uff0c\u5e94\u8be5\u662f\u4e00\u5ea7\u57ce\u5e02\u6700\u9c9c\u660e\u7684\u7279\u5f81\u4e4b\u4e00\uff0c\u5e94\u8be5\u4f53\u73b0\u4e00\u5ea7\u57ce\u5e02\u7684\u7075\u9b42\uff0c\u662f\u8fd9\u5ea7\u57ce\u5e02\u81ea\u7136\u3001\u4eba\u6587\u6d53\u7f29\u7684\u7cbe\u534e\uff0c\u662f\u4e00\u5ea7\u57ce\u5e02\u6700\u5177\u4f53\u3001\u6700\u76f4\u63a5\u3001\u6700\u73b0\u5b9e\u7684\u540d\u7247\uff0c\u662f\u4e00\u5ea7\u57ce\u5e02\u5386\u53f2\u3001\u73b0\u5b9e\u4e0e\u672a\u6765\u7684\u7f29\u5f71\uff0c\u662f\u57ce\u5e02\u7ecf\u8425\u4e0e\u57ce\u5e02\u8425\u9500\u7684\u6838\u5fc3\u95ee\u9898\uff0c\u662f\u4e00\u5ea7\u57ce\u5e02\u66f4\u5177\u6709\u7ade\u4e89\u529b\u7684\u6838\u5fc3\u4ef7\u503c\u2026\u2026\u201d\u8bfb\u5230\u8fd9\u91cc\uff0c\u675c\u5c0f\u5c71\u770b\u4e86\u770b\u5510\u5b66\u8c26\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u4f3c\u4e4e\u5e94\u8be5\u662f\u5510\u5e02\u957f\u5199\u7684\u6587\u7ae0\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u5e02\u573a\u7ecf\u6d4e\u9010\u6b65\u6210\u719f\u7684\u9636\u6bb5\uff0c\u57ce\u5e02\u4e4b\u95f4\u7684\u7ade\u4e89\u4e5f\u9010\u6e10\u6210\u719f\u8d77\u6765\uff0c\u5f15\u5438\u66f4\u591a\u7684\u8d44\u6e90\u3001\u66f4\u591a\u7684\u6295\u8d44\u5230\u81ea\u5df1\u7684\u57ce\u5e02\uff0c\u662f\u6bcf\u4e00\u4f4d\u57ce\u5e02\u653f\u5e9c\u4e3b\u5b98\u90fd\u8981\u8003\u8651\u7684\u6838\u5fc3\u95ee\u9898\u3002\u4e5d\u56db\u5e74\uff0c\u56fd\u5185\u521a\u521a\u5f62\u6210\u57ce\u5e02\u7ecf\u8425\u7684\u6982\u5ff5\uff0c\u5f62\u6210\u5927\u9020\u5f62\u8c61\u5de5\u7a0b\u3001\u57ce\u5e02\u4e4b\u95f4\u8fdb\u884c\u70e7\u94b1\u7ade\u8d5b\u7684\u6c1b\u56f4\uff0c\u5f20\u606a\u5728\u8fd9\u7bc7\u4f5c\u6587\u91cc\uff0c\u5374\u4ece\u57ce\u5e02\u8425\u9500\u7684\u89d2\u5ea6\uff0c\u5426\u5b9a\u5f53\u65f6\u9887\u4e3a\u6d41\u884c\u7684\u201c\u70e7\u94b1\u201d\u57ce\u5e02\u89c4\u5212\u7406\u5ff5\uff0c\u4e00\u5ea7\u57ce\u5e02\u9700\u8981\u5177\u5907\u81ea\u5df1\u9c9c\u660e\u7684\u7279\u8272\uff0c\u8fd9\u4e9b\u7279\u8272\uff0c\u5c31\u662f\u57ce\u5e02\u4e4b\u79c0\uff0c\u5c31\u662f\u57ce\u5e02\u7684\u9b45\u529b\u7075\u9b42\uff0c\u5c31\u662f\u9886\u5148\u4e8e\u5468\u8fb9\u57ce\u5e02\u7684\u7ade\u4e89\u529b\u3002</p><p>\u3000\u3000\u6700\u540e\u4ee5\u4fdd\u62a4\u6c99\u7530\u8001\u8857\u4e3a\u4f8b\uff0c\u8bb2\u8ff0\u5c40\u90e8\u62c6\u9664\u3001\u91cd\u70b9\u4fee\u7f2e\u7684\u65b9\u6848\uff0c\u5bf9\u5851\u9020\u57ce\u5e02\u7279\u5f81\uff0c\u63d0\u9ad8\u57ce\u5e02\u9b45\u529b\u53ca\u7ade\u4e89\u529b\u7684\u91cd\u8981\u4f5c\u7528\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4ece\u675c\u5c0f\u5c71\u624b\u91cc\u63a5\u8fc7\u5377\u5b50\uff0c\u4f5c\u6587\u5dee\u4e0d\u591a\u53ea\u6709\u4e24\u5343\u5b57\uff0c\u5f88\u591a\u610f\u601d\u90fd\u65e0\u6cd5\u8bb2\u900f\uff0c\u4f46\u662f\u5176\u4e2d\u5168\u65b0\u7684\u57ce\u5e02\u7ade\u4e89\u7406\u5ff5\uff0c\u5374\u7ed9\u5510\u5b66\u8c26\u5f88\u6df1\u7684\u89e6\u52a8\uff0c\u7709\u5934\u51dd\u805a\u7740\uff0c\u5f88\u957f\u65f6\u5019\u9677\u5165\u601d\u8003\u4e4b\u4e2d\uff0c\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u770b\u4e86\u770b\u53cc\u624b\u4ea4\u53c9\u5750\u5728\u90a3\u91cc\u7684\u5f20\u606a\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u8ba4\u4e3a\u8fd9\u7bc7\u6587\u7ae0\u662f\u6284\u88ad\u7684\uff0c\u8fd9\u7bc7\u6587\u7ae0\u76f4\u63a5\u9488\u5bf9\u6d77\u5dde\u5e02\u7684\u57ce\u5e02\u5efa\u8bbe\u4e0e\u89c4\u5212\u63d0\u51fa\u81ea\u5df1\u7684\u89c2\u70b9\uff0c\u5510\u5b66\u8c26\u4e0e\u81ea\u5df1\u90fd\u6ca1\u6709\u8bfb\u8fc7\uff0c\u4ed6\u4ece\u54ea\u91cc\u53bb\u6284\u88ad\uff1f\u53ea\u662f\u4ed6\u80fd\u5199\u51fa\u8fd9\u6837\u7684\u6587\u7ae0\uff0c\u7684\u786e\u5f88\u96be\u8ba9\u4eba\u4e0d\u9707\u60ca\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u901a\u7bc7\u770b\u8fc7\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u5c0f\u5b50\uff0c\u603b\u6709\u4e9b\u8ba9\u4eba\u51fa\u4e4e\u610f\u6599\u7684\u60f3\u6cd5\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u7bc7\u4f5c\u6587\u7684\u786e\u5f88\u597d\uff0c\u4e5f\u5f88\u80fd\u7ed9\u6211\u542f\u53d1\uff0c\u201d\u5510\u5b66\u8c26\u62ac\u5934\u5bf9\u989d\u5934\u51fa\u6c57\u7684\u738b\u708e\u658c\u8bf4\uff0c\u201c\u8fd9\u5f20\u5377\u5b50\uff0c\u6211\u5148\u62ff\u8d70\uff0c\u6211\u770b\u4eca\u5929\u662f\u4e0d\u662f\u5c31\u5230\u8fd9\u91cc\uff1f\u5f20\u606a\u4e0d\u5b88\u6821\u89c4\u6821\u7eaa\uff0c\u4f60\u4eec\u8981\u66ff\u6211\u8ddf\u77e5\u884c\u4e25\u52a0\u7ba1\u6559\u2026\u2026\u201d</p><p>\u3000\u3000\u738b\u708e\u658c\u90fd\u50bb\u4e86\uff0c\u4e0e\u674e\u829d\u82b3\u9762\u9762\u76f8\u89d1\uff0c\u5510\u5b66\u8c26\u90fd\u8fd9\u6001\u5ea6\u4e86\uff0c\u4ee5\u540e\u8fd8\u5982\u4f55\u7ba1\u6559\uff1f\u5f20\u77e5\u884c\u5634\u89d2\u542b\u7b11\uff0c\u4f3c\u4e4e\u4e00\u70b9\u90fd\u4e0d\u4ecb\u610f\u4ed6\u513f\u5b50\u7684\u6210\u7ee9\u8fd9\u4e48\u7cdf\u7cd5\uff01</p></article>"}}}]);
