(window.webpackJsonp=window.webpackJsonp||[]).push([[910],{933:function(p){p.exports={chapterTitle:null,title:"\u7b2c729\u7ae0 \u56db\u4e2a\u4eba\u7684\u6cf0\u5766\u5c3c\u514b",content:"<article><h2>\u7b2c729\u7ae0 \u56db\u4e2a\u4eba\u7684\u6cf0\u5766\u5c3c\u514b</h2><p>\u3000\u3000\u96ea\u8fd8\u5728\u4e0b\uff0c\u8def\u4e0a\u7684\u79ef\u96ea\u5df2\u7ecf\u6e05\u9664\u6389\uff0c\u53ea\u7559\u4e0b\u6e7f\u6f09\u6f09\u7684\u8def\u9762\u5728\u5ef6\u4f38\uff0c\u8fdc\u5904\u9752\u9ed1\u8272\u7684\u5c71\u5ce6\u4f4e\u77ee\u8d77\u4f0f\uff0c\u7ed9\u7f69\u5728\u832b\u832b\u5927\u96ea\u4e2d\uff0c\u6e7f\u96ea\u6253\u5728\u8f66\u7a97\u4e0a\uff0c\u522e\u96e8\u5668\u53d1\u51fa\u6a61\u76ae\u4e0e\u73bb\u7483\u6469\u64e6\u7684\u54cd\u58f0\u3002\u8ba9\u5085\u4fca\u5f00\u8f66\u9001\u8bb8\u9e3f\u4f2f\u3001\u5f20\u5955\u56de\u6d77\u5dde\uff0c\u5f20\u606a\u4eb2\u81ea\u5f00\u8f66\u62c9\u4e0a\u675c\u98de\u3001\u9648\u5983\u84c9\u53bb\u673a\u573a\u63a5\u5510\u5a67\u3001\u76db\u590f\u3002</p><p>\u3000\u3000\u4eca\u5929\u662f\u5e73\u5b89\u591c\u53c8\u8d76\u4e0a\u5468\u672b\uff0c\u901a\u5f80\u673a\u573a\u7684\u9053\u8def\u4e0a\u6765\u5f80\u7684\u8f66\u8f86\u5f88\u591a\uff0c\u8d76\u5230\u673a\u573a\uff0c\u79bb\u98de\u673a\u9884\u5b9a\u7684\u62b5\u8fbe\u65f6\u95f4\u8fd8\u6709\u534a\u4e2a\u5c0f\u65f6\u3002\u63a5\u673a\u5927\u5385\u91cc\u5bbd\u655e\u800c\u660e\u4eae\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u3001\u9648\u5983\u84c9\u5750\u5728\u63a5\u673a\u5927\u5385\u91cc\u7684\u5851\u6599\u6905\u4e0a\u804a\u5929\uff0c\u900f\u8fc7\u843d\u5730\u73bb\u7483\u5899\u53ef\u4ee5\u770b\u5230\u62b5\u8fbe\u7684\u65c5\u5ba2\u3002\u5510\u5a67\u4e0e\u76db\u590f\u51fa\u73b0\u5728\u884c\u674e\u4f20\u9001\u5e26\u524d\uff0c\u4e5f\u540c\u65f6\u770b\u5230\u5728\u63a5\u673a\u5927\u5385\u7b49\u5019\u7684\u5f20\u606a\u4ed6\u4eec\uff0c\u683c\u5916\u5174\u594b\u7684\u8981\u8df3\u8d77\u6765\u3002</p><p>\u3000\u3000\u5510\u5a67\u7a7f\u7740\u7d2b\u9ed1\u8272\u957f\u81f3\u819d\u76d6\u7684\u7fbd\u7ed2\u5927\u8863\uff0c\u6c34\u78e8\u84dd\u7684\u725b\u4ed4\u88e4\u3001\u5496\u5561\u8272\u7684\u9ad8\u8ddf\u7b52\u9774\uff0c\u521a\u4ece\u98de\u673a\u4e0a\u4e0b\u6765\uff0c\u63a5\u673a\u5927\u5385\u91cc\u7684\u6696\u6c14\u4e5f\u8db3\uff0c\u8fd8\u6ca1\u6709\u611f\u53d7\u5230\u5efa\u90ba\u7684\u6e7f\u5bd2\uff0c\u7fbd\u7ed2\u5927\u8863\u655e\u5f00\u7740\uff0c\u9732\u51fa\u91cc\u9762\u67ff\u9ec4\u8272\u7684\u7ed2\u7ebf\u8863\uff0c\u8eab\u6750\u4fee\u957f\u5a07\u633a\uff0c\u7c89\u767d\u5a07\u8273\u7684\u8138\u4e0a\u6d0b\u6ea2\u7740\u9752\u6625\u7684\u6c14\u606f\u3002</p><p>\u3000\u3000\u770b\u7740\u5510\u5a67\u63a8\u7740\u884c\u674e\u8f66\u51fa\u6765\uff0c\u5f20\u606a\u53c9\u5f00\u624b\u7b49\u7740\u5979\u6251\u8fc7\u6765\u7684\u62e5\u62b1\uff0c\u6ca1\u60f3\u5230\u5510\u5a67\u5148\u4e0e\u9648\u5983\u84c9\u62e5\u62b1\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u201c\u5443\uff0c\u592a\u4f24\u81ea\u5c0a\u4e86\u2026\u2026\u201d\u5f20\u606a\u4e00\u624b\u63a5\u8fc7\u5510\u5a67\u7684\u884c\u674e\u8f66\uff0c\u6478\u7740\u9f3b\u5b50\u74ee\u58f0\u74ee\u6c14\u7684\u8bf4\u9053\uff0c\u6628\u5929\u5f00\u7740\u7a97\u6237\u542c\u7740\u843d\u96ea\u7684\u58f0\u97f3\u7761\u89c9\uff0c\u6709\u4e9b\u7740\u51c9\u3002\u5f20\u606a\u7a81\u7136\u60f3\u8d77\u7b49\u5b8b\u4e39\u4e39\u51e0\u5e74\u540e\u5728\u592e\u89c6\u7684\u821e\u53f0\u4e0a\u8bf4\u51fa\u8fd9\u53e5\u6709\u540d\u7684\u201c\u592a\u4f24\u81ea\u5c0a\u4e86\u201d\u7684\u8bdd\uff0c\u5510\u5a67\u5979\u4eec\u4f1a\u4e0d\u4f1a\u8ba4\u4e3a\u5b8b\u4e39\u4e39\u662f\u6284\u88ad\u81ea\u5df1\u7684\uff1f</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5510\u5a67\u5a07\u7b11\u7684\u6251\u5165\u5f20\u606a\u7684\u6000\u91cc\uff0c\u6402\u7740\u4ed6\u7684\u8170\uff0c\u4ece\u5979\u624b\u7684\u529b\u5ea6\u53ef\u4ee5\u611f\u89c9\u5230\u5979\u6df1\u5165\u9aa8\u9ad3\u7684\u601d\u5ff5\u3002\u5f20\u606a\u6402\u7740\u5510\u5a67\u7684\u8170\u80a2\uff0c\u8ba9\u5979\u66f4\u8d34\u8fd1\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u5728\u5979\u7684\u989d\u5934\u543b\u4e86\u4e00\u4e0b\uff0c\u624d\u8f7b\u8f7b\u7684\u677e\u5f00\uff0c\u4e0e\u76db\u590f\u6325\u4e86\u6325\u624b\u6253\u8fc7\u62db\u547c\uff0c\u4e0e\u675c\u98de\u5e2e\u5979\u4eec\u62ff\u7740\u884c\u674e\uff0c\u51fa\u4e86\u63a5\u673a\u5927\u5385\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u5148\u627e\u4e2a\u5c31\u8fd1\u7684\u9910\u9986\u5c06\u4e2d\u996d\u7ed9\u89e3\u51b3\u4e86\uff1f\u201d\u5f20\u606a\u4e0e\u675c\u98de\u5c06\u884c\u674e\u585e\u8fdb\u540e\u5907\u7bb1\u540e\u94bb\u8fdb\u8f66\u91cc\uff0c\u56de\u8fc7\u5934\u95ee\u5510\u5a67\u3002\u5510\u5a67\u7684\u4e0b\u5df4\u6bd4\u4e0a\u6b21\u89c1\u7565\u7626\u4e86\u4e00\u4e0b\uff0c\u5b66\u4e60\u5f88\u523b\u82e6\uff0c\u5510\u5a67\u7ed9\u81ea\u5df1\u7684\u538b\u529b\u4e5f\u5f88\u5927\uff0c\u4f46\u662f\u4e94\u5b98\u7cbe\u81f4\u4f9d\u65e7\u900f\u7740\u719f\u6089\u7684\u6e05\u7eaf\u7684\u6c14\u606f\uff0c\u773c\u7738\u5b50\u53c8\u957f\u53c8\u5a9a\uff1b\u5750\u4e86\u8fd9\u4e48\u957f\u65f6\u95f4\u7684\u98de\u673a\uff0c\u8138\u8272\u6709\u4e9b\u82cd\u767d\u8fd8\u6709\u4e9b\u75b2\u60eb\u3002</p><p>\u3000\u3000\u201c\u53bb\u5b66\u5e9c\u5df7\u5427\uff0c\u671f\u5f85\u597d\u4e45\u4e86\uff0c\u5149\u542c\u4f60\u4eec\u5728\u7535\u8bdd\u91cc\u3001\u5728\u90ae\u4ef6\u91cc\u63cf\u8ff0\u4e86\uff0c\u5341\u6708\u5e95\u56de\u5efa\u90ba\u4e5f\u662f\u5306\u5306\u5fd9\u5fd9\u6ca1\u6709\u89c1\u5230\uff0c\u201d\u5510\u5a67\u6402\u7740\u9648\u5983\u84c9\u7684\u80f3\u81c2\uff0c\u770b\u5411\u5f20\u606a\u7684\u773c\u7738\u773c\u6ce2\u6d41\u8f6c\uff0c\u7709\u68a2\u542b\u6625\uff0c\u53c8\u63a2\u8fc7\u5934\u95ee\u5750\u5728\u9648\u5983\u84c9\u53e6\u4e00\u8fb9\u7684\u76db\u590f\uff0c\u201c\u6211\u4eec\u76f4\u63a5\u5c31\u53bb\u5b66\u5e9c\u5df7\u600e\u4e48\u6837\uff1f\u5403\u8fc7\u996d\uff0c\u6211\u4eec\u53ef\u4ee5\u5148\u5750\u5230\u6a61\u6811\u56ed\u7684\u6a61\u6811\u4e0b\u770b\u71d5\u5f52\u6e56\u4e0a\u7684\u843d\u96ea\uff01\u201d\u58f0\u97f3\u5a07\u67d4\u6e05\u8106\uff0c\u5c31\u50cf\u8f66\u7a97\u5916\u7684\u843d\u96ea\u3002</p><p>\u3000\u3000\u5230\u5b66\u5e9c\u5df7\u5df2\u7ecf\u662f\u4e00\u70b9\u949f\u4e86\uff0c\u8d70\u5230\u5185\u5df7\u7684\u4e00\u5bb6\u5496\u5561\u7b80\u9910\u5e97\uff0c\u5b59\u9759\u6aac\u3001\u5c0f\u4f73\u4e00\u7fa4\u97f3\u4e50\u5b66\u9662\u7684\u5973\u5b69\u5b50\u4e0e\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u3001\u65bd\u65b0\u98de\u8fd8\u6709\u65bd\u65b0\u98de\u90a3\u4e2a\u6821\u5973\u7bee\u7403\u961f\u7684\u5973\u53cb\u7b49\u4eba\u70ed\u70ed\u95f9\u95f9\u7684\u4ece\u7b80\u9910\u5e97\u91cc\u8d70\u51fa\u6765\uff0c\u4ed6\u4eec\u76f8\u7ea6\u4e00\u8d77\u53bb\u5e02\u91cc\u770b\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\u3002</p><p>\u3000\u3000\u5510\u5a67\u5728\u9999\u6e2f\u5f88\u53d7\u5b59\u5c1a\u4e49\u592b\u5987\u7684\u7167\u987e\uff0c\u770b\u5230\u5b59\u9759\u6aac\u81ea\u7136\u5f88\u4eb2\u70ed\uff0c\u9080\u5979\u7559\u4e0b\u6765\u8bf4\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u90e8\u7535\u5f71\u9999\u6e2f\u65e9\u5c31\u4e0a\u6620\u4e86\uff0c\u6bcf\u5929\u90fd\u4f1a\u542c\u540c\u5b66\u5728\u8033\u7554\u8bf4\uff0c\u6211\u8981\u7b49\u7740\u8ddf\u8fd9\u732a\u5934\u4e00\u8d77\u53bb\u770b\u2026\u2026\u4f60\u8ddf\u6211\u4eec\u4e00\u8d77\u53bb\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u624d\u4e0d\u8981\u505a\u4f60\u4eec\u7684\u7535\u706f\u6ce1\u2026\u2026\u201d\u5b59\u9759\u6aac\u7b11\u7740\u8bf4\u9053\uff0c\u770b\u7740\u4e0a\u5348\u770b\u8fc7\u516c\u6620\u7684\u540c\u5b66\u56de\u6765\u90fd\u54ed\u5f97\u68a8\u82b1\u5e26\u96e8\uff0c\u5b59\u9759\u6aac\u4e5f\u6ca1\u6709\u628a\u63e1\u80fd\u575a\u5f3a\u4f4f\u4e0d\u611f\u6ce3\u843d\u6cea\uff0c\u5510\u5a67\u6709\u5f20\u606a\u7684\u80a9\u8180\uff0c\u81ea\u5df1\u600e\u4e48\u529e\uff1f\u65e5\u540e\u5927\u6982\u4f1a\u8bb0\u5f97\u81ea\u5df1\u5c24\u5176\u7684\u53ef\u601c\uff0c\u8fd8\u4e0d\u5982\u4e0e\u5c0f\u4f73\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u70ed\u70ed\u95f9\u95f9\u7684\u4e00\u8d77\u770b\u8fc7\u8fd9\u573a\u7535\u5f71\uff0c\u6e21\u8fc7\u4e5d\u4e03\u5e74\u7684\u5e73\u5b89\u591c\u3002</p><p>\u3000\u3000\u770b\u7740\u9648\u5983\u84c9\u4e5f\u8981\u8ddf\u4ed6\u4eec\u8d70\uff0c\u5510\u5a67\u6402\u7740\u5979\u7684\u80f3\u81c2\uff0c\u6c42\u5979\u9053\uff1a\u201c\u4f60\u4e00\u5b9a\u8981\u7559\u4e0b\u6765\u966a\u6211\u554a\uff0c\u6211\u5b81\u53ef\u4e0d\u8981\u8fd9\u732a\u5934\u3002\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u56de\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff1a\u201c\u4f1a\u4e0d\u4f1a\u89c9\u5f97\u6211\u592a\u788d\u773c\uff1f\u201d</p><p>\u3000\u3000\u201c\u6c42\u4f60\u4e86\uff0c\u4e0d\u8981\u8ba9\u5510\u5a67\u5c06\u6211\u7ed9\u629b\u4e0b\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u518d\u788d\u4e8b\uff0c\u4e5f\u6ca1\u6709\u675c\u98de\u8ddf\u76db\u590f\u4e24\u4e2a\u5978\u592b\u6deb\u5987\u788d\u4e8b\u3002\u201d</p><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\u7684\u8bb0\u5fc6\u91cc\uff0c\u5f20\u606a\u6bcf\u6b21\u770b\u8fd9\u90e8\u7535\u5f71\u90fd\u6ca1\u6709\u575a\u6301\u8fc7\u534a\u4e2a\u5c0f\u65f6\u5c31\u660f\u660f\u6b32\u7761\uff0c\u65e0\u6cd5\u60f3\u8c61\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u5b59\u9759\u6aac\u3001\u76db\u590f\u56db\u4e2a\u4eba\u5973\u5b69\u5b50\u5728\u7535\u5f71\u9662\u54ed\u6210\u6cea\u4eba\u800c\u4ed6\u4e0e\u675c\u98de\u7761\u8fc7\u4e00\u89c9\u4e4b\u540e\u7684\u60c5\u666f\uff0c\u4e5f\u6216\u8bb8\u524d\u5c18\u5f80\u4e16\u4e0d\u613f\u610f\u5728\u90a3\u4e9b\u966a\u81ea\u5df1\u770b\u8fd9\u90e8\u7535\u5f71\u7684\u5973\u5b69\u5b50\u4eec\u9762\u524d\u66b4\u9732\u8106\u5f31\u7684\u5185\u5fc3\uff0c\u4ee5\u81f4\u8fd9\u90e8\u7ed9\u76db\u8a89\u4e3a\u201c\u5904\u5973\u7ec8\u7ed3\u8005\u201d\u7684\u7231\u60c5\u7ecf\u5178\u5f71\u7247\u81ea\u5df1\u770b\u4e86\u597d\u591a\u56de\u90fd\u53ea\u662f\u770b\u8fc7\u5f00\u5934\u3002</p><p>\u3000\u3000\u5728\u5b66\u5e9c\u5df7\u7528\u8fc7\u9910\uff0c\u5c06\u884c\u674e\u9001\u8fdb\u516c\u5bd3\uff0c\u516c\u5bd3\u91cc\u8fd8\u6709\u4e00\u95f4\u5367\u5ba4\u7559\u4e0b\u6765\uff0c\u5510\u5a67\u4e0e\u76db\u590f\u53ef\u4ee5\u4f4f\u8fdb\u53bb\u3002\u4e4b\u540e\u675c\u98de\u4e0e\u76db\u590f\u5c31\u5148\u8d70\u4e86\uff0c\u8d70\u4e4b\u524d\u8fd8\u5f88\u6ca1\u826f\u5fc3\u7684\u4e22\u4e0b\u4e00\u53e5\uff1a\u201c\u5927\u5bb6\u51d1\u5728\u4e00\u8d77\u770b\u5f88\u6ca1\u6709\u611f\u89c9\u7684\u2026\u2026\u201d\u638f\u51fa\u63d0\u524d\u8d2d\u597d\u7684\u7535\u5f71\u7968\u6765\uff0c\u4e5f\u662f\u4e24\u5f20\u4e0b\u5348\u573a\u7684\uff0c\u4e09\u5f20\u9ec4\u660f\u573a\u7684\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e24\u4e2a\u72d7\u7537\u5973\uff01\u201d\u5f20\u606a\u89c1\u675c\u98de\u65e9\u5c31\u8ba1\u5212\u597d\u8ddf\u76db\u590f\u79c1\u5954\uff0c\u7b11\u7740\u9a82\u4ed6\u4eec\uff0c\u5f53\u771f\u53ea\u5269\u4e0b\u4ed6\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u4e09\u4e2a\u4eba\u4e86\u3002</p><p>\u3000\u3000\u675c\u98de\u4e0e\u76db\u590f\u5148\u53bb\u770b\u4e0b\u5348\uff0c\u5f20\u606a\u53ea\u80fd\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u5148\u53bb\u6a61\u6811\u56ed\u770b\u96ea\u666f\uff0c\u5c06\u8f66\u505c\u6a61\u6811\u56ed\u91cc\uff0c\u7ad9\u5728\u6a61\u6811\u7684\u6811\u836b\u4e0b\u770b\u7740\u6e56\u9762\u4e0a\u7684\u843d\u96ea\u3002</p><p>\u3000\u3000\u5750\u5728\u6811\u836b\u4e0b\u7684\u957f\u51f3\u4e0a\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5174\u594b\u7684\u7ad9\u5728\u6811\u836b\u5916\u8fce\u63a5\u7740\u4ece\u5929\u7a7a\u98d8\u6765\u7684\u96ea\u82b1\u3002\u5510\u5a67\u7a7f\u7740\u73ab\u7ea2\u8272\u7684\u7fbd\u7ed2\u886b\uff0c\u9648\u5983\u84c9\u7a7f\u7740\u767d\u8272\u7684\u7fbd\u7ed2\u886b\uff0c\u4e24\u4e2a\u4eba\u90fd\u7ed9\u4eba\u5f3a\u70c8\u7684\u7f8e\u7684\u5370\u8c61\uff0c\u5510\u5a67\u6d3b\u6cfc\u70ed\u60c5\uff0c\u65e0\u5904\u4e0d\u6563\u53d1\u7740\u9752\u6625\u7684\u70ed\u70c8\u6c14\u606f\uff0c\u7738\u5149\u6d41\u8f6c\u7740\u65e0\u9650\u7684\u6625\u60c5\u79cb\u610f\uff1b\u9648\u5983\u84c9\u7709\u773c\u6e05\u51bd\u5982\u79cb\u6708\uff0c\u4eff\u4f5b\u96ea\u5730\u91cc\u7684\u5e7d\u5bc2\u767d\u83b2\u3002</p><p>\u3000\u3000\u7537\u4eba\u4e4b\u95f4\u7684\u53cb\u8c0a\uff0c\u5973\u4eba\u5f88\u96be\u60f3\u8c61\uff0c\u5973\u4eba\u4e4b\u95f4\u7684\u4eb2\u5bc6\uff0c\u7537\u4eba\u4e5f\u4e0d\u8981\u8bd5\u56fe\u53bb\u63e3\u6d4b\u3002\u770b\u7740\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u4eb2\u6635\u5982\u59d0\u59b9\u7684\u7ad9\u5728\u96ea\u5730\u91cc\u4f4e\u8bed\uff0c\u5f20\u606a\u6000\u7591\u5510\u5a67\u5c06\u4ec0\u4e48\u4e8b\u60c5\u90fd\u8ddf\u9648\u5983\u84c9\u503e\u8bc9\u800c\u4e0d\u8ddf\u4ed6\u8bf4\uff0c\u5992\u5fcc\u90fd\u5992\u5fcc\u4e0d\u6765\uff0c\u5fcd\u4e0d\u4f4f\u6447\u5934\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d70\u8fdb\u7535\u5f71\u9662\u91cc\uff0c\u5f20\u606a\u60f3\u8d77\u524d\u4e16\u5728\u4e1c\u5927BBS\u4e0a\u505a\u8fc7\u7684\u4e00\u4efd\u5173\u4e8e\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\u7684\u95ee\u5377\u8c03\u67e5\uff0c\u8fd9\u4e00\u523b\uff0c\u90a3\u4efd\u7684\u95ee\u5377\u5185\u5bb9\u4e0e\u96ea\u6c34\u878d\u5316\u4f3c\u7684\u6d41\u5165\u8111\u6d77\u4e4b\u4e2d\uff0c\u6e05\u6670\u65e0\u6bd4\u3002</p><p>\u3000\u3000\u95ee\u5377\u6709\u4e2a\u95ee\u9898\u95ee\u5f71\u7247\u4e2d\u8c01\u7ed9\u4f60\u7559\u4e0b\u6700\u6df1\u523b\u7684\u5370\u8c61\uff0c\u5f20\u606a\u60f3\u8d77\u5c0f\u6d77\u8c5a\u5728\u6d77\u91cc\u6e38\uff1b\u8fd8\u6709\u7740\u95ee\u9898\u662f\u95ee\u6709\u6ca1\u6709\u5b66\u8fc7\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\u4e2d\u7ecf\u5178\u7684\u7ad9\u5728\u8239\u5934\u98de\u7fd4\u7684\u52a8\u4f5c\uff0c\u5f20\u606a\u60f3\u8d77\u66fe\u6709\u51e0\u4e2a\u5973\u751f\u63d0\u8fc7\u8fd9\u6837\u7684\u8981\u6c42\uff0c\u81ea\u5df1\u5374\u53ea\u5c06\u80a9\u8180\u501f\u7ed9\u4f55\u5f26\u8e29\u5230\u8d70\u5eca\u56f4\u680f\u4e0a\u201c\u98de\u201d\u8fc7\u3002</p><p>\u3000\u3000\u770b\u7740\u5ea7\u4f4d\u53f7\u4e5f\u6709\u4e9b\u719f\u6089\uff0c\u5728\u660f\u6697\u7684\u5149\u7ebf\u91cc\uff0c\u6478\u8fdb\u5ea7\u4f4d\u91cc\u3002\u5f85\u8981\u5750\u4e0b\u65f6\uff0c\u201c\u554a\uff01\u201d\u9694\u58c1\u7684\u5ea7\u4f4d\u4f20\u51fa\u6e05\u8106\u7684\u60ca\u53f9\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u96be\u9053\u4f1a\u89c9\u5f97\u8fd9\u5ea7\u4f4d\u53f7\u719f\u6089\uff0c\u524d\u4e16\u662f\u6400\u7740\u4e00\u4e2a\u5973\u5b69\u5b50\u7684\u624b\u8d70\u8fdb\u5ea7\u4f4d\u9047\u5230\u5b64\u96f6\u96f6\u4e00\u4e2a\u4eba\u6765\u770b\u7535\u5f71\u7684\u4f55\u5f26\uff0c\u8fd9\u4e00\u6b21\u81ea\u5df1\u4e5f\u7275\u7740\u5510\u5a67\u7684\u624b\u4e0d\u9519\uff0c\u4e0d\u8fc7\u5510\u5a67\u8fd8\u7275\u7740\u9648\u5983\u84c9\u7684\u624b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e5f\u6765\u770b\u7535\u5f71\u554a\uff1f\u201d\u5510\u5a67\u4eb2\u70ed\u8ddf\u4f55\u5f26\u62db\u62db\u624b\uff0c\u4f9d\u7740\u5f20\u606a\u7684\u8eab\u5b50\u63a2\u5934\u5f80\u4f55\u5f26\u8eab\u8fb9\u770b\u4e86\u770b\uff0c\u662f\u4e2a\u964c\u751f\u7684\u5973\u5b69\u5b50\u8ddf\u4e00\u4e2a\u7537\u5b69\u5b50\u5728\u4eb2\u70ed\u7684\u8033\u8bed\uff0c\u611f\u89c9\u201c\u4eec\u201d\u5b57\u8bf4\u591a\u4f59\u4e86\uff0c\u624b\u6342\u4e86\u4e00\u4e0b\u5507\uff0c\u538b\u4f4e\u58f0\u97f3\u95ee\uff0c\u201c\u4f60\u4e00\u4e2a\u4eba\u6765\u770b\u7535\u5f71\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u82cf\u4e00\u5a77\u4e5f\u6765\u4e86\uff0c\u4e0d\u8fc7\u4e0d\u6653\u5f97\u5979\u8ddf\u5979\u7537\u670b\u53cb\u5750\u54ea\u4e2a\u89d2\u843d\u91cc\u3002\u201d\u4f55\u5f26\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u5c31\u4e0d\u662f\u4e00\u4e2a\u4eba\u4e86\u3002\u201d</p><p>\u3000\u3000\u4e00\u76f4\u4e0e\u8fd9\u90e8\u7535\u5f71\u9519\u8eab\u800c\u8fc7\uff0c\u4ece\u672a\u6765\u6709\u5b8c\u6574\u7684\u770b\u8fc7\uff0c\u4e5f\u4e0d\u660e\u767d\u90a3\u4e48\u591a\u4eba\u611f\u6ce3\u58f0\u632f\u804b\u53d1\u8069\u5267\u9662\u5185\u573a\u7a76\u7adf\u662f\u4e3a\u4e86\u54ea\u822c\u3002\u518d\u6709\u673a\u4f1a\u5750\u5728\u5927\u5c4f\u5e55\u524d\uff0c\u770b\u7740ROSE\u767e\u5c81\u8001\u4eba\u5386\u7ecf\u6570\u5341\u5e74\u7684\u8de8\u5ea6\u4f9d\u65e7\u6e05\u695a\u8bb0\u5f97\u90a3\u6619\u82b1\u822c\u77ed\u6682\u7684\u7f8e\u597d\u65f6\u5149\uff0c\u5f20\u606a\u5c31\u9887\u6709\u611f\u89e6\uff1a\u8bb0\u5fc6\u5f80\u5f80\u662f\u6700\u597d\u7684\u8d22\u5bcc\u3002</p><p>\u3000\u3000\u201cYou jump\uff0cI jump\u201d\u8fd9\u53e5\u8bdd\u5728\u58f0\u5149\u9189\u5f71\u4e2d\u65e9\u5c31\u88ab\u5eb8\u4fd7\u7684\u8bed\u8c03\u73b7\u6c61\uff0c\u53ea\u6709\u7ecf\u5386\u6d41\u6c99\u822c\u8ba9\u4eba\u6df1\u9677\u5176\u4e2d\u65e0\u6cd5\u81ea\u62e8\u7684\u75db\u82e6\u4e4b\u540e\uff0c\u624d\u80fd\u611f\u53d7\u5230\u8fd9\u53e5\u8bdd\u4f5c\u4e3a\u8bfa\u8a00\u7684\u529b\u91cf\u3002\u5f20\u606a\u8fd9\u4e48\u60f3\u7740\u3002</p><p>\u3000\u3000\u5267\u573a\u5185\u7684\u60c5\u666f\u5b8c\u5168\u53ef\u4ee5\u7528\u5267\u573a\u524d\u5f20\u8d34\u7684\u90a3\u5f20\u6d77\u62a5\u4e0a\u7684\u5e7f\u544a\u8bed\u6765\u5f62\u5bb9\uff1a\u5929\u5730\u52a8\u5bb9\u3002\u5f20\u606a\u5bf9\u6b64\u65e9\u6709\u51c6\u5907\uff0c\u7279\u610f\u51c6\u5907\u4e86\u4e00\u76d2\u9762\u7eb8\u3002\u4ece\u6551\u751f\u8247\u653e\u4e0b\u7684\u90a3\u4e00\u523b\uff0c\u751f\u79bb\u6b7b\u522b\u7684\u9884\u611f\u4f3c\u4e4e\u5728\u5267\u573a\u5185\u5f25\u6f2b\uff0c\u5510\u5a67\u3001\u4f55\u5f26\u3001\u9648\u5983\u84c9\u4e09\u4e2a\u5973\u5b69\u5b50\u5c31\u4e0d\u505c\u7684\u4ece\u5f20\u606a\u6000\u91cc\u62bd\u53d6\u9762\u7eb8\uff0c\u8d70\u51fa\u7535\u5f71\u9662\uff0c\u4e09\u4e2a\u5973\u5b69\u5b50\u7684\u773c\u775b\u90fd\u7ea2\u80bf\u7740\uff0c\u773c\u89d2\u8fd8\u6291\u4e0d\u4f4f\u7684\u5f80\u5916\u6ea2\u6cea\u6c34\uff0c\u8868\u60c5\u5374\u6bd4\u5f20\u606a\u7684\u6c89\u9ed8\u8fd8\u6c89\u9ed8\u3002</p><p>\u3000\u3000\u6839\u636e\u8c03\u67e5\uff0c\u51e0\u4e4e\u6240\u6709\u7684\u5973\u5b69\u5b50\u770b\u5b8c\u8fd9\u90e8\u7535\u5f71\u4e4b\u540e\u90fd\u6709\u4e00\u79cd\u6291\u5236\u4e0d\u4f4f\u7684\u51b2\u52a8\u60f3\u95ee\u8eab\u8fb9\u7684\u7537\u5b69\u5b50\uff1a\u201cI jump\uff0cYou jump\uff1f\u201d\u5510\u5a67\u4e5f\u4e0d\u4f8b\u5916\uff0c\u53ea\u4e0d\u8fc7\u5728\u9648\u5983\u84c9\u3001\u4f55\u5f26\u9762\u524d\u5fcd\u4f4f\u6ca1\u6709\u95ee\u51fa\u6765\u3002</p></article>"}}}]);
