(window.webpackJsonp=window.webpackJsonp||[]).push([[911],{934:function(p){p.exports={chapterTitle:null,title:"\u7b2c730\u7ae0 You jump\uff1f",content:"<article><h2>\u7b2c730\u7ae0 You jump\uff1f</h2><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\u966a\u4e09\u4e2a\u5973\u5b69\u5b50\u8fdb\u7535\u5f71\u9662\u770b\u8fc7\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\uff0c\u6ca1\u60f3\u5230\u8fd9\u56de\u4e00\u6b21\u5c31\u8fbe\u6210\u6307\u6807\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u4e0e\u5979\u7537\u53cb\u4e5f\u5728\u7535\u5f71\u9662\u91cc\u770b\u540c\u4e00\u573a\u7684\u7535\u5f71\uff0c\u53ea\u662f\u6ca1\u5fc3\u6ca1\u80ba\u7684\u4e0e\u4f55\u5f26\u5206\u5f00\u6765\u5750\uff0c\u8d70\u51fa\u7535\u5f71\u9662\uff0c\u5f20\u606a\u966a\u4e09\u4e2a\u5973\u5b69\u5b50\u7ad9\u5728\u5267\u573a\u51fa\u53e3\u7b49\u82cf\u4e00\u5a77\u3002</p><p>\u3000\u3000\u5927\u51b7\u5929\uff0c\u4e09\u4e2a\u5973\u5b69\u5b50\u90fd\u7a7f\u7740\u539a\u539a\u7684\u5916\u5957\uff0c\u4f46\u662f\u4ece\u9732\u51fa\u7684\u5a07\u8273\u8138\u86cb\u6709\u7740\u98a0\u5012\u4f17\u751f\u3001\u503e\u57ce\u503e\u56fd\u7684\u7f8e\uff0c\u7709\u76ee\u5982\u753b\uff0c\u968f\u4fbf\u4e22\u4e00\u4e2a\u5230\u5927\u8857\u4e0a\u5c31\u8981\u5438\u5f15\u6240\u6709\u7684\u76ee\u5149\uff0c\u4f55\u51b5\u4e09\u4e2a\u4eba\u7ad9\u5728\u4e00\u8d77\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5f88\u77e5\u60c5\u8bc6\u8da3\u7684\u5c06\u7f8a\u7ed2\u5927\u8863\u7684\u9886\u5b50\u7ad6\u8d77\u6765\u906e\u4f4f\u534a\u7247\u8138\uff0c\u5f88\u59d4\u7410\u7684\u8e72\u5728\u4e00\u8fb9\u7684\u53f0\u9636\u4e0a\uff0c\u770b\u7740\u4e09\u4e2a\u5973\u5b69\u5b50\u4f4e\u8bed\u4e92\u8bc9\u4ece\u5f71\u7247\u91cc\u611f\u609f\u5230\u7684\u65f6\u5149\u6d41\u6c99\u822c\u7684\u60b2\u6b22\u54c0\u6078\uff0c\u7ea2\u80bf\u7684\u773c\u7738\u5b50\u8fd8\u6cea\u5149\u70b9\u70b9\uff0c\u4e1d\u6beb\u4e0d\u51cf\u989c\u8272\u8fd8\u6108\u6dfb\u5a07\u5a9a\u3002</p><p>\u3000\u3000\u65c1\u8fb9\u6709\u4e00\u5bf9\u60c5\u4fa3\uff0c\u5973\u5b69\u540c\u6837\u6cea\u773c\u8ff7\u79bb\u7684\u95ee\u7537\u5b69\uff1a\u201cI jump\uff0cYou jump\uff1f\u201d\u5f20\u606a\u542c\u5230\u6709\u4eba\u5728\u8eab\u5728\u95ee\u8fd9\u6837\u7684\u95ee\u9898\uff0c\u5fc3\u91cc\u4e00\u7b11\u62ac\u5934\u770b\u53bb\uff0c\u53ea\u89c1\u90a3\u7537\u5b69\u5b50\u76ee\u5149\u6e38\u79bb\uff0c\u5fc3\u601d\u4f3c\u4e4e\u5728\u5510\u5a67\u5979\u4eec\u4e09\u4e2a\u82b1\u5bb9\u6708\u8c8c\u7684\u5973\u5b69\u5b50\u8eab\u4e0a\uff0c\u60f3\u591a\u770b\u4e24\u773c\u53c8\u6015\u7ed9\u5973\u53cb\u5bdf\u89c9\uff0c\u5bf9\u5973\u53cb\u7684\u95ee\u9898\u81ea\u7136\u6709\u4e9b\u5fc3\u4e0d\u5728\u7109\u3002\u90a3\u5973\u5b69\u5b50\u6ce8\u610f\u5230\u7537\u53cb\u7684\u5f02\u6837\uff0c\u8f6c\u8138\u770b\u89c1\u5510\u5a67\u5979\u4eec\uff0c\u8138\u8272\u987f\u65f6\u9634\u6c89\u4e0b\u6765\uff0c\u6076\u72e0\u72e0\u7684\u6390\u4e86\u4e00\u4e0b\u7537\u5b69\u5b50\u7684\u80f3\u818a\u7406\u4e5f\u4e0d\u7406\u7684\u5c31\u5f80\u5916\u8d70\uff0c\u7537\u5b69\u5b50\u53ea\u6709\u4e00\u8138\u54ed\u76f8\u7684\u8ffd\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f97\uff0c\u90a3\u5973\u5b69\u5b50\u8981\u8fd8\u662f\u5904\u5973\uff0c\u90a3\u4eca\u591c\u5c31\u4e0d\u7528\u6ca6\u9677\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u54c8\u54c8\u4e00\u7b11\uff0c\u7ad9\u8d77\u6765\u4ece\u80cc\u540e\u6402\u7740\u5510\u5a67\u7684\u8170\u80a2\uff0c\u7b11\u7740\u8ddf\u4e09\u4e2a\u5973\u5b69\u5b50\u8bf4\u9053\uff1a\u201c\u5c31\u56e0\u4e3a\u4f60\u4eec\u7ad9\u5728\u8fd9\u91cc\u4e3a\u5f71\u7247\u4e2d\u5e7b\u68a6\u4f3c\u7684\u7231\u60c5\u611f\u6ce3\uff0c\u5374\u5df2\u7ecf\u6467\u6b8b\u4e86\u597d\u51e0\u5bf9\u73b0\u5b9e\u4e2d\u7684\u7231\u60c5\uff0c\u771f\u662f\u7978\u6c34\u554a\uff0c\u54b1\u4eec\u662f\u4e0d\u662f\u4f4e\u8c03\u4e00\u4e9b\uff0c\u627e\u4e2a\u5730\u65b9\u5148\u8eb2\u8d77\u6765\uff1f\u201d\u5510\u5a67\u8033\u9b13\u8d34\u7740\u5f20\u606a\u6e29\u70ed\u7684\u8138\u988a\uff0c\u5a07\u67d4\u5fae\u7f9e\u7684\u7b11\u7740\u3002</p><p>\u3000\u3000\u82cf\u4e00\u5a77\u62ff\u7740\u7eb8\u5dfe\u64e6\u7740\u773c\u7738\u8d70\u51fa\u6765\uff0c\u770b\u5230\u5510\u5a67\u3001\u9648\u5983\u84c9\u4e0e\u4f55\u5f26\u7ad9\u5728\u4e00\u8d77\uff0c\u5174\u594b\u7684\u5927\u53eb\uff1a\u201c\u4f60\u4eec\u4e09\u4e2a\u4eba\u7ad9\u5728\u4e00\u8d77\uff0c\u8fd8\u8ba9\u4e0d\u8ba9\u5176\u4ed6\u5973\u5b69\u5b50\u6d3b\u4e86\u3002\u201d\u82cf\u4e00\u5a77\u8eab\u540e\u7ad9\u7740\u4e00\u4e2a\u6e05\u79c0\u524a\u7626\u7684\u7537\u5b69\u5b50\uff0c\u662f\u82cf\u4e00\u5a77\u5728\u5e08\u5927\u7684\u7537\u53cb\uff0c\u4f5c\u4e3a\u666e\u901a\u7684\u7537\u5b69\u5b50\u4e4d\u770b\u5230\u4e09\u4e2a\u5982\u82b1\u5a07\u5a9a\u7684\u5973\u5b69\u5b50\u805a\u5728\u4e00\u8d77\u6765\u90fd\u4f1a\u60ca\u8273\u5230\u7ed9\u8ff7\u60d1\u7684\u611f\u89c9\uff0c\u82cf\u4e00\u5a77\u6b7b\u6d3b\u4e0d\u80af\u4eca\u591c\u8ddf\u5510\u5a67\u3001\u9648\u5983\u84c9\u3001\u4f55\u5f26\u5728\u4e00\u8d77\uff1a\u201c\u7070\u59d1\u5a18\u4e5f\u6709\u7231\u60c5\u68a6\u60f3\u7684\uff0c\u8981\u7834\u706d\u81f3\u5c11\u4e5f\u8981\u7b49\u5230\u660e\u5929\u2026\u2026\u201d\u7b11\u7b11\u95f9\u95f9\u7684\u62db\u8fc7\u62db\u547c\uff0c\u62c9\u7740\u7537\u53cb\u5148\u8d70\u4e86\uff0c\u5c06\u4e09\u4e2a\u5973\u5b69\u5b50\u90fd\u4e22\u7ed9\u5f20\u606a\u5904\u7406\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4eca\u591c\u662f\u5e73\u5b89\u591c\u53c8\u662f\u5468\u672b\uff0c\u65b0\u5e02\u8857\u7684\u5546\u5bb6\u5bf9\u5047\u65e5\u7ecf\u6d4e\u7684\u7ecf\u8425\u5df2\u7ecf\u6709\u4e9b\u5fc3\u5f97\uff0c\u8857\u5934\u5546\u5e97\u91cc\u8fd8\u6709\u4eba\u7a7f\u7740\u5723\u8bde\u88c5\u5728\u505a\u4fc3\u9500\uff0c\u5373\u4f7f\u5929\u7a7a\u8fd8\u98d8\u7740\u5c0f\u96ea\uff0c\u8857\u4e0a\u7684\u9752\u5e74\u7537\u5973\u5f88\u591a\uff0c\u8ddf\u8757\u7fa4\u4f3c\u7684\uff0c\u6709\u6210\u53cc\u6210\u5bf9\u7684\uff0c\u4e5f\u6709\u6210\u7fa4\u6d3b\u52a8\u7684\uff0c\u5929\u7a7a\u98d8\u4e0b\u7684\u96ea\u82b1\u4f3c\u4e4e\u6210\u4e86\u70b9\u7f00\u3002\u5f20\u606a\u4e0e\u4e09\u4e2a\u5973\u5b69\u5b50\u4e5f\u7b97\u662f\u6210\u7fa4\u6d3b\u52a8\uff0c\u4f46\u662f\u5e26\u7740\u8fd9\u4e09\u4e2a\u7ea2\u989c\u7978\u6c34\u7ea7\u7684\u5973\u5b69\u5b50\u4e00\u8d77\u901b\u8857\u7684\u538b\u529b\u592a\u5927\u4e86\uff0c\u5728\u65b0\u5e02\u8857\u7684\u5fc5\u80dc\u5ba2\u7528\u8fc7\u9910\u4e4b\u540e\uff0c\u5c31\u5f00\u8f66\u56de\u5b66\u5e9c\u5df7\u4e86\u3002</p><p>\u3000\u3000\u4eca\u591c\u7684\u5b66\u5e9c\u5df7\u4e5f\u6bd4\u4ee5\u5f80\u7684\u5468\u672b\u66f4\u52a0\u70ed\u95f9\uff0c\u5b66\u751f\u60c5\u4fa3\u5f88\u591a\uff0c\u4e0d\u8fc7\u4e5d\u5341\u5e74\u4ee3\u672b\u671f\u7684\u9ad8\u6821\u5373\u4f7f\u50cf\u5e08\u5927\u8fd9\u6837\u7537\u5973\u6bd4\u4f8b\u5f88\u8c10\u8c03\u7684\u5b66\u6821\u91cc\u5355\u8eab\u7684\u7537\u53cb\u4f3c\u4e4e\u66f4\u591a\uff0c\u521b\u57df\u7f51\u5427\u7efc\u5408\u5427\u53f0\u524d\u7684\u4f11\u95f2\u5496\u5561\u5385\u5f0f\u7684\u4f11\u606f\u533a\u57df\u4f9d\u65e7\u6709\u7740\u8bb8\u591a\u8fb9\u559d\u996e\u6599\u8fb9\u7b49\u5019\u4e0a\u7f51\u7684\u5e74\u8f7b\u5b66\u751f\u3002</p><p>\u3000\u3000\u675c\u98de\u3001\u8499\u4e50\u4ed6\u4eec\u6628\u5929\u624d\u627e\u4ee4\u5c0f\u71d5\u8c08\u5de5\u4f5c\u7684\u4e8b\u60c5\uff0c\u4ee4\u5c0f\u71d5\u4eca\u5929\u5c31\u6b63\u5f0f\u5230\u521b\u57df\u516c\u53f8\u517c\u4efb\u884c\u653f\u3001\u8d22\u52a1\u7ba1\u7406\u65b9\u9762\u7684\u804c\u52a1\u3002\u867d\u7136\u7f51\u5427\u8fd0\u8425\u5df2\u7ecf\u8058\u8bf7\u4e13\u95e8\u7684\u503c\u73ed\u7ecf\u7406\uff0c\u4f46\u662f\u7279\u8bb8\u7ecf\u8425\u6027\u8d28\u7684\u8fde\u9501\u52a0\u76df\u662f\u521b\u57df\u8003\u8651\u53d1\u5c55\u7f51\u5427\u4e8b\u4e1a\u7684\u4e00\u4e2a\u91cd\u8981\u65b9\u5411\uff0c\u4ee4\u5c0f\u71d5\u4e0d\u80fd\u4e0d\u719f\u6089\u7f51\u5427\u7684\u65e5\u5e38\u8fd0\u4f5c\u3002\u5f20\u606a\u4e0e\u4e09\u4e2a\u5973\u5b69\u5b50\u7ecf\u8fc7\u521b\u57df\u7f51\u5427\u65f6\uff0c\u770b\u5230\u6731\u5c0f\u519b\u4e5f\u88ab\u8feb\u5728\u4eca\u6837\u7684\u591c\u665a\u6765\u966a\u4ee4\u5c0f\u71d5\u3002</p><p>\u3000\u3000\u4f55\u5f26\u5148\u56de\u5bbf\u820d\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u5148\u9001\u9648\u5983\u84c9\u56de\u5bbf\u820d\uff0c\u4e24\u4eba\u7275\u7740\u624b\u5f80\u56de\u8d70\uff0c\u5929\u7a7a\u8fd8\u6f02\u7740\u7ec6\u788e\u7684\u96ea\u82b1\uff0c\u843d\u5728\u8138\u4e0a\u6709\u4e9b\u51b0\uff0c\u4e24\u53ea\u624b\u5341\u6307\u76f8\u6263\u7684\u7ede\u5728\u4e00\u8d77\uff0c\u4e3e\u5230\u8def\u706f\u5149\u4e0b\uff0c\u5510\u5a67\u7684\u624b\u4e30\u5ae9\u5a07\u8f6f\uff0c\u6bcf\u4e00\u5bf8\u90fd\u7ec6\u767d\u5230\u6781\u81f4\u3002\u8bf4\u5230\u8981\u53bb\u5408\u6b22\u5c71\u8d70\u4e00\u8d70\uff0c\u53c2\u89c2\u4e00\u4e0b\uff0c\u867d\u662f\u4e0b\u96ea\u5929\uff0c\u4e0a\u4e86\u5c71\u624d\u53d1\u73b0\u5c71\u4e0a\u6bcf\u4e00\u5904\u90fd\u6709\u60c5\u4fa3\u76f8\u4f9d\u504e\u7740\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u88ab\u8feb\u5f80\u56de\u8d70\u3002\u56de\u5230\u516c\u5bd3\uff0c\u675c\u98de\u4e0e\u5b59\u9759\u6aac\u5728\u95e8\u540e\u90fd\u7559\u4e86\u7eb8\u6761\uff0c\u5b59\u9759\u6aac\u56de\u5bbf\u820d\u7761\uff0c\u675c\u98de\u5219\u5c06\u7814\u7a76\u516c\u5bd3\u7684\u94a5\u5319\u62ff\u8fc7\u53bb\u4e86\u3002\u770b\u7740\u7eb8\u6761\u4e0a\u7684\u7559\u8a00\uff0c\u5510\u5a67\u7c89\u767d\u7684\u8138\u77ac\u65f6\u9189\u9152\u4f3c\u7684\u67d3\u6210\u7c89\u8273\u7684\u6843\u7ea2\u3002</p><p>\u3000\u3000\u5510\u5a67\u5c06\u5916\u5957\u8131\u4e0b\uff0c\u8bf4\u8981\u53bb\u716e\u5496\u5561\uff0c\u4ece\u5f20\u606a\u6000\u91cc\u6323\u624e\u51fa\u53bb\u3002\u5f20\u606a\u5750\u5728\u697c\u4e0b\u5ba2\u5385\u7684\u6c99\u53d1\u4e0a\uff0c\u770b\u7740\u5510\u5a67\u6452\u606f\u5c0f\u7ffc\u7684\u5728\u5f00\u653e\u5f0f\u7684\u53a8\u623f\u95f4\u716e\u5496\u5561\u7684\u80cc\u666f\u3002\u540c\u6837\u662f\u7ec6\u8170\u7fd8\u81c0\uff0c\u665a\u6674\u7684\u81c0\u5bbd\u539a\u5706\u719f\u5e26\u7740\u7565\u6709\u4e9b\u5938\u5f20\u7684\u8bf1\u60d1\uff0c\u8bb8\u601d\u5219\u5f39\u8f6f\u4e30\u7fd8\uff0c\u968f\u610f\u6563\u53d1\u51fa\u5973\u6027\u6781\u81f4\u7684\u6027\u611f\uff0c\u5510\u5a67\u5219\u542b\u84c4\u6536\u655b\uff0c\u5728\u725b\u4ed4\u88e4\u7684\u5305\u88f9\u4e0b\u4e00\u770b\u5c31\u6653\u5f97\u662f\u5c11\u5973\u7684\u76c8\u5b9e\u5c0f\u81c0\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u53bb\u697c\u9876\u5ead\u56ed\u7684\u8336\u5c4b\u91cc\u5c06\u70ad\u7089\u70b9\u71c3\u8d77\u6765\uff0c\u5510\u5a67\u7aef\u7740\u5496\u5561\u4e0a\u6765\uff0c\u56db\u4e0b\u91cc\u5b89\u5b89\u9759\u9759\u7684\uff0c\u4f3c\u4e4e\u611f\u89c9\u5230\u6574\u4e2a\u57ce\u5e02\u90fd\u6c89\u7761\u53bb\uff0c\u6709\u7c0c\u7c0c\u7684\u843d\u96ea\u6765\uff0c\u5f20\u606a\u5c06\u5510\u5a67\u5750\u5728\u81ea\u5df1\u524d\u9762\uff0c\u62d4\u5f04\u5979\u7684\u957f\u53d1\uff0c\u9732\u51fa\u4e00\u622a\u7c89\u5ae9\u7684\u4fee\u957f\u8116\u5b50\uff0c\u624b\u7a7f\u8fc7\u5510\u5a67\u7684\u814b\u4e0b\u6402\u7d27\u5979\u7684\u8170\u80a2\u3002\u5510\u5a67\u7684\u8138\u6da8\u5f97\u901a\u7ea2\uff0c\u5979\u80fd\u611f\u89c9\u5230\u5f20\u606a\u8de8\u4e0b\u76f4\u8038\u8038\u7684\u90a3\u6839\u574f\u4e1c\u897f\u784c\u5728\u81c0\u4e0b\uff0c\u5f20\u606a\u8fd8\u4e0d\u8ba9\u5979\u5c06\u81c0\u79fb\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e9\u5c31\u5077\u5077\u6478\u6478\u7684\u5c06\u624b\u673a\u90fd\u5173\u6389\u3001\u7535\u8bdd\u7ebf\u62d4\u6389\uff0c\u8fd9\u65f6\u5019\u6ca1\u6709\u7279\u5b9a\u7684\u78c1\u611f\u5e94\u5361\u8fdb\u5165\u7535\u68af\u90fd\u65e0\u6cd5\u6309\u4e0b\u5341\u4e00\u697c\u7684\u7535\u68af\u6309\u94ae\u3002\u8336\u5c4b\u91cc\u6ca1\u6709\u4eae\u706f\uff0c\u8fdc\u5904\u8def\u9762\u4e0a\u7684\u706f\u5149\u900f\u8fc7\u91cd\u91cd\u7684\u96ea\u5e55\uff0c\u4f20\u8fc7\u6765\u7684\u5149\u7ebf\u8f83\u6697\uff0c\u5f20\u606a\u5c06\u5510\u5a67\u7684\u8eab\u5b50\u6273\u8fc7\u6765\u543b\u5979\uff0c\u5510\u5a67\u95ed\u7740\u773c\uff0c\u7c89\u6da6\u7684\u5634\u5507\u5fae\u62bf\u7740\uff0c\u5fc3\u60c5\u6fc0\u98a4\u7740\uff0c\u5728\u5979\u8033\u7554\u8bf4\uff1a\u201c\u6211\u4eec\u56de\u623f\u95f4\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u610f\u4e71\u60c5\u8ff7\uff0c\u8ff7\u8ff7\u4e4e\u4e4e\u7ed9\u5f20\u606a\u7275\u7740\u624b\u53c8\u8fdb\u4e86\u5c4b\u3002\u5916\u9762\u8fd8\u4e0b\u7740\u96ea\uff0c\u6e29\u6696\u5982\u6625\u7684\u623f\u95f4\u91cc\u6ca1\u6709\u4eae\u706f\uff0c\u9752\u8499\u8499\u7684\u5149\u4ece\u7a97\u6237\u5916\u900f\u8fdb\u6765\uff0c\u80fd\u770b\u89c1\u5510\u5a67\u5a07\u5a9a\u5982\u6625\u7684\u8138\uff0c\u53c8\u957f\u53c8\u7738\u7684\u7738\u5b50\u6676\u7136\u95ea\u4eae\u3002\u5f20\u606a\u5c06\u5510\u5a67\u653e\u5728\u5e8a\u4e0a\uff0c\u867d\u7136\u4e0d\u662f\u7b2c\u4e00\u6b21\u89e3\u5f00\u5510\u5a67\u7684\u8863\u886b\uff0c\u4f46\u662f\u8fd9\u4e00\u6b21\u6709\u7740\u4e0d\u4e00\u6837\u7684\u610f\u56fe\uff0c\u624b\u90fd\u6709\u4e9b\u98a4\u6296\u8d77\u6765\uff0c\u6478\u4e0d\u7740\u80f8\u7f69\u80cc\u5e26\u540e\u7684\u6263\u5b50\uff0c\u6025\u5207\u7684\u5c06\u80f8\u7f69\u63a8\u9ad8\uff0c\u767d\u8038\u8038\u7684\u5806\u8d77\u4e24\u56e2\u4e73\u8089\uff0c\u9c9c\u7ea2\u7684\u84d3\u857e\u5a07\u8273\u6b32\u6ef4\uff0c\u8fd9\u65f6\u624d\u770b\u89c1\u80f8\u7f69\u662f\u524d\u6263\u5f0f\u7684\uff0c\u5f20\u606a\u4e0d\u7531\u7684\u4e3a\u81ea\u5df1\u7684\u7334\u6025\u54d1\u7b11\u8d77\u6765\u3002</p><p>\u3000\u3000\u8863\u886b\u89e3\u5f00\uff0c\u6beb\u65e0\u906e\u62e6\u7684\u9732\u51fa\u767d\u6761\u6761\u660e\u8273\u7684\u4e0a\u8eab\uff0c\u5510\u5a67\u5728\u60c5\u6b32\u8ff7\u79bb\u611f\u89c9\u5230\u6709\u4e9b\u51b7\uff0c\u624b\u6342\u8d77\u6765\u6401\u5728\u80f8\u53e3\u4e0a\uff0c\u5f20\u606a\u5507\u543b\u5728\u5979\u7684\u624b\u80cc\u4e0a\uff0c\u707c\u70ed\u7684\u5a07\u7f9e\u8ba9\u5979\u62bd\u5f00\u624b\uff0c\u5374\u8ba9\u66f4\u91cd\u8981\u7684\u4e00\u70b9\u6ed1\u5165\u5f20\u606a\u6e29\u70ed\u7684\u5507\u820c\u95f4\u3002\u5f20\u606a\u624b\u629a\u7740\u60ca\u4eba\u5f39\u8f6f\u7684\u8170\u80a2\u4e0b\u6ed1\uff0c\u6478\u5230\u725b\u4ed4\u88e4\uff0c\u5510\u5a67\u8fd8\u4e0b\u610f\u8bc6\u7684\u62a4\u5728\u90a3\u91cc\uff0c\u4e24\u8fb9\u7684\u80ef\u9aa8\u5fae\u5fae\u652f\u51fa\uff0c\u5c06\u94dc\u6263\u89e3\u5f00\uff0c\u5f20\u606a\u5355\u624b\u5c06\u5979\u53ea\u624b\u76c8\u63e1\u7684\u8170\u80a2\u6284\u4e0b\uff0c\u53e6\u4e00\u53ea\u624b\u5c06\u725b\u4ed4\u88e4\u8fde\u7740\u8584\u7ed2\u88e4\u3001\u5185\u88e4\u4e00\u5e76\u892a\u4e0b\uff0c\u6ed1\u8fc7\u4e30\u7fd8\u8089\u5b9e\u7684\u81c0\u63b0\uff0c\u5fc3\u9b42\u5dee\u70b9\u5728\u8fd9\u4e00\u523b\u90fd\u7ed9\u70b9\u7740\u3002</p><p>\u3000\u3000\u5510\u5a67\u89c9\u5bdf\u5230\u5371\u9669\uff0c\u62c9\u8fc7\u88ab\u8925\u5c06\u8d64\u88f8\u5230\u53ea\u5269\u4e0b\u819d\u76d6\u7a9d\u4ee5\u4e0b\u5c0f\u817f\u7ed9\u88e4\u5b50\u88f9\u7f20\u7740\u7684\u8eab\u4f53\u5305\u4f4f\uff0c\u5f20\u606a\u4e03\u624b\u516b\u811a\u5c06\u5510\u5a67\u7f20\u5728\u5c0f\u817f\u4e0a\u7684\u88e4\u5b50\u90fd\u626f\u4e0b\u6765\uff0c\u53c8\u5c06\u81ea\u5df1\u626f\u8131\u5f97\u5149\u6e9c\u6e9c\u5e72\u51c0\uff0c\u6478\u7740\u5510\u5a67\u5149\u6ed1\u7ea4\u7ec6\u7684\u5c0f\u817f\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u624b\u4f38\u8fdb\u4e24\u817f\u4e4b\u95f4\u3002\u5510\u5a67\u5374\u4e0b\u610f\u8bc6\u7684\u7f20\u7d27\u7740\u53cc\u817f\u4e0d\u8ba9\u5f20\u606a\u94bb\u8fdb\u5979\u4e24\u817f\u4e4b\u95f4\u3002\u5f20\u606a\u5934\u518d\u5f80\u4e0a\u94bb\uff0c\u53bb\u5bfb\u8ba9\u4eba\u5fc3\u9b42\u6fc0\u71c3\u7684\u7c89\u5507\uff0c\u5510\u5a67\u7ec6\u957f\u5ae9\u817b\u7684\u624b\u81c2\u7f20\u8fc7\u6765\uff0c\u73af\u62b1\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u5a07\u8f6f\u53d1\u70eb\u7684\u624b\u8d34\u7740\u5f20\u606a\u7684\u80cc\u80db\u3002\u5f20\u606a\u624b\u6284\u5230\u5510\u5a67\u81c0\u4e0b\uff0c\u8f7b\u8f7b\u6258\u4e86\u6258\uff0c\u8ba9\u5979\u5c06\u4e24\u817f\u5206\u5f00\uff0c\u770b\u7740\u95ed\u7740\u7684\u773c\u7738\u5b50\u7741\u5f00\uff0c\u8ff7\u79bb\u7684\u773c\u795e\u6070\u597d\u7a97\u5916\u7684\u96ea\u591c\uff0c\u8033\u7554\u4f20\u6765\u5979\u4f4e\u541f\u52a8\u60c5\u7684\u7ec6\u8bed\uff1a\u201c\u5f20\u606a\uff0c\u80fd\u95ee\u4f60\u4e00\u4e2a\u95ee\u9898\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5e94\u8be5\u662f\u90a3\u53e5\u201cI jump\uff0cYou jump\uff1f\u201d\u7684\u7ecf\u5178\u95ee\u9898\u3002\u867d\u7136\u8fd9\u95ee\u9898\u4ed6\u66fe\u7ed9\u51e0\u4e2a\u5973\u5b69\u5b50\u5728\u597d\u4e8b\u6b32\u6210\u524d\u95ee\u8fc7\uff0c\u4e5f\u90fd\u6577\u884d\u7684\u95ee\u7b54\u8fc7\uff0c\u5510\u5a67\u4e5f\u6709\u7740\u666e\u901a\u5973\u5b69\u5b50\u4e00\u6837\u7684\u654f\u611f\u5fc3\u601d\uff0c\u4f46\u662f\u5f20\u606a\u6653\u5f97\u8fd9\u6b21\u7684\u56de\u7b54\u7edd\u4e0d\u662f\u6577\u884d\uff0c\u4ed6\u77e5\u9053\u81ea\u5df1\u80fd\u575a\u5b9a\u7684\u56de\u7b54\uff1a\u201cYou jump\uff0cI jump\u3002\u201d\u6240\u4ee5\u4ed6\u52aa\u529b\u5206\u5f00\u5510\u5a67\u53cc\u817f\u7684\u624b\u8fd9\u4e00\u523b\u6ca1\u6709\u505c\u987f\u3002</p><p>\u3000\u3000\u201cShe jump\uff0cYou jump\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e00\u58f0\u6674\u5929\u9739\u96f3\u5dee\u70b9\u5c31\u5c06\u5f20\u606a\u7b2c\u4e09\u6761\u817f\u6253\u762b\u6389\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u624b\u505c\u4e0b\u6765\uff0c\u770b\u7740\u5510\u5a67\u7684\u773c\u7738\uff0c\u8ff7\u79bb\u7684\u773c\u795e\u6062\u590d\u4e86\u4e9b\u8bb8\u6e05\u4eae\uff0c\u8138\u8d34\u7740\u5979\u7684\u8138\u988a\uff0c\u8f7b\u58f0\u7684\u8bf4\uff1a\u201cYou jump\uff0cI jump\uff1bShe jump\uff0cI jump\u2026\u2026\u201d\u5f20\u606a\u6b63\u6697\u81ea\u4f24\u611f\u8fd9\u6b21\u53c8\u6ca1\u620f\u4e86\uff0c\u5510\u5a67\u7ed9\u538b\u5728\u8eab\u4e0b\u7684\u53cc\u817f\u5374\u65e0\u58f0\u7684\u5206\u5f00\u4e86\uff0c\u8ba9\u4ed6\u90a3\u8fd8\u575a\u786c\u5982\u6545\u7684\u5206\u8eab\u76f4\u63a5\u9876\u4e0a\u6cb9\u817b\u817b\u7684\u6e29\u70ed\u8f6f\u8089\uff0c\u8fd8\u5728\u4ed6\u8033\u7554\u8f7b\u8bed\uff1a\u201c\u5f20\u606a\uff0c\u4e0d\u8981\u8ba9\u6211\u75db\u2026\u2026\u201d</p></article>"}}}]);
