(window.webpackJsonp=window.webpackJsonp||[]).push([[912],{935:function(p){p.exports={chapterTitle:null,title:"\u7b2c731\u7ae0 \u67d4\u60c5\u871c\u610f",content:"<article><h2>\u7b2c731\u7ae0 \u67d4\u60c5\u871c\u610f</h2><p>\u3000\u3000\u542c\u7740\u5510\u5a67\u5835\u5728\u5589\u5499\u95f4\u7684\u4f4e\u541f\uff0c\u5a07\u8f6f\u7684\u8eab\u5b50\u5728\u9752\u8499\u8499\u7684\u5fae\u5149\u91cc\u96ea\u767d\u4e00\u7247\uff0c\u8138\u4e0a\u6d6e\u7740\u8ba9\u4eba\u5fc3\u9189\u7684\u6f6e\u7ea2\uff0c\u5fae\u95ed\u7684\u7738\u5b50\u6d41\u6cc4\u51fa\u6765\u7684\u90a3\u7ebf\u7738\u5149\u8ff7\u79bb\u9500\u9b42\u3002\u8eab\u4e0b\u7684\u5a07\u8eaf\u5728\u8f7b\u98a4\uff0c\u5728\u65e0\u610f\u8bc6\u7684\u8815\u52a8\uff0c\u5f20\u606a\u611f\u89c9\u4e0b\u8eab\u7ed9\u52d2\u5f97\u6108\u53d1\u7684\u7d27\uff0c\u8eab\u5b50\u4e00\u6c89\uff0c\u5c06\u91cc\u9762\u534a\u622a\u8f6f\u8089\u5f7b\u5e95\u7684\u7834\u5f00\u3002</p><p>\u3000\u3000\u5510\u5a67\u65e0\u58f0\u7684\u5f20\u5f00\u5634\uff0c\u4eff\u4f5b\u6d6e\u5728\u591c\u8272\u91cc\u7684\u4e00\u5c3e\u9c7c\u5728\u65e0\u58f0\u7684\u558a\u53eb\uff0c\u7709\u5934\u5728\u7d27\u8e59\u7684\u4e00\u77ac\u95f4\u53c8\u9661\u7136\u8212\u5c55\u5f00\uff0c\u56db\u80a2\u60b8\u52a8\u7740\u5c06\u5f20\u606a\u7684\u8eab\u4f53\u7f20\u7d27\uff0c\u6068\u4e0d\u80fd\u4e92\u76f8\u63c9\u8fdb\u5bf9\u65b9\u7684\u8eab\u4f53\u91cc\u53bb\u3002\u5f20\u606a\u770b\u7740\u8eab\u4e0b\u7684\u5510\u5a67\uff0c\u6709\u7740\u8bf4\u4e0d\u51fa\u7684\u601c\u60dc\uff0c\u5fc3\u91cc\u6108\u53d1\u6e05\u695a\u201cYou jump\uff0cI jump\u201d\u4e0d\u662f\u620f\u8a00\u4e4b\u540e\u7684\u51dd\u91cd\uff0c\u771f\u613f\u610f\u4e3a\u5979\u53bb\u6b7b\u3002</p><p>\u3000\u3000\u6015\u5510\u5a67\u5a07\u5ae9\u7684\u8eab\u4f53\u7ecf\u4e0d\u4f4f\u6301\u7eed\u7684\u6467\u6b8b\uff0c\u4e0d\u6562\u8d2a\u56fe\u6b22\u4e50\u518d\u53bb\u4e71\u52a8\uff0c\u53ea\u662f\u5c06\u601c\u60dc\u7684\u5c06\u5979\u6402\u5728\u6000\u91cc\uff0c\u624b\u8986\u5728\u5979\u8eab\u4e0a\u8f7b\u8f7b\u7684\u6469\u6332\u7740\uff0c\u4e24\u4eba\u7684\u8eab\u4f53\u8fd8\u8fde\u5728\u4e00\u8d77\u3002\u5510\u5a67\u7f13\u8fc7\u52b2\u6765\uff0c\u7741\u5f00\u53c8\u957f\u53c8\u5a9a\u7684\u7738\u5b50\u89c1\u5f20\u606a\u52a8\u60c5\u7684\u51dd\u89c6\u7740\u5979\uff0c\u4e0b\u8eab\u8fd8\u5bc6\u531d\u531d\u7684\u7b8d\u5728\u4e00\u8d77\uff0c\u81c0\u90e8\u8815\u52a8\u60f3\u6446\u8131\u5f20\u606a\uff0c\u5374\u6709\u4e00\u79cd\u6781\u81f4\u7684\u654f\u611f\u8ba9\u8eab\u4f53\u96be\u4ee5\u627f\u53d7\uff0c\u4e0d\u6562\u4e71\u52a8\uff0c\u5a07\u7f9e\u7684\u62c9\u8fc7\u88ab\u5b50\u76d6\u5728\u8138\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u62c9\u4f4f\u6068\u4e0d\u5f97\u8fde\u591c\u8981\u5c06\u88ab\u5355\u6d17\u6389\u7684\u5510\u5a67\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u88ab\u5355\u6d17\u4e86\u4f60\u85cf\u54ea\u5904\u522b\u4eba\u770b\u4e0d\u5230\u7684\u5730\u65b9\u667e\u5e72\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u662f\u4f60\u8fd9\u4e2a\u5927\u8272\u72fc\u5bb3\u6211\u8981\u4e22\u8138\uff0c\u201d\u5510\u5a67\u5a07\u55d4\u9053\uff0c\u62b1\u7740\u7ed9\u4e00\u56e2\u8840\u8ff9\u4e0e\u900f\u660e\u6cb9\u6027\u6db2\u4f53\u6df7\u4e71\u5408\u7740\u6fe1\u6e7f\u7684\u88ab\u5355\u4e0d\u6653\u5f97\u600e\u4e48\u529e\u624d\u597d\uff0c\u89c1\u5f20\u606a\u4e00\u8138\u7684\u8d3c\u7b11\uff0c\u6251\u8fc7\u6765\u626f\u7740\u4ed6\u8138\u988a\u4e0a\u7684\u76ae\u5b50\u5c06\u4ed6\u7684\u7b11\u5bb9\u5f3a\u884c\u62b9\u5e73\uff0c\u53c8\u4e49\u6b63\u8bcd\u4e25\u7684\u8b66\u544a\u5f20\u606a\uff0c\u201c\u4e0d\u8bb8\u4f60\u8ddf\u522b\u4eba\u8bf4\u8fd9\u4e8b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fd9\u4e0d\u8fed\u7684\u70b9\u5934\u7b54\u5e94\uff0c\u5510\u5a67\u6700\u540e\u627e\u5230\u4e00\u53ea\u7a7a\u7684\u884c\u674e\u7bb1\u5c06\u88ab\u5355\u85cf\u4e86\u8fdb\u53bb\uff0c\u4e00\u5207\u90fd\u6536\u62fe\u5f97\u4e0d\u9732\u75d5\u8ff9\uff0c\u624d\u62cd\u62cd\u624b\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u966a\u6211\u53bb\u697c\u9876\u597d\u4e0d\u597d\uff1f\u521a\u624d\u5fc3\u614c\u614c\u7684\uff0c\u90fd\u6ca1\u6709\u597d\u597d\u7684\u770b\u4e00\u773c\u7a7a\u4e2d\u56ed\u6797\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7a7a\u4e2d\u56ed\u6797\u201d\u662f\u5f20\u606a\u4e0e\u5510\u5a67\u5728\u90ae\u4ef6\u91cc\u5bf9\u697c\u9876\u9732\u53f0\u7684\u79f0\u53f7\u3002</p><p>\u3000\u3000\u8336\u5ba4\u662f\u534a\u655e\u5f00\u5f0f\u7684\uff0c\u4e0d\u6653\u5f97\u8336\u5ba4\u91cc\u7684\u70ad\u7089\u6709\u6ca1\u6709\u7184\uff0c\u5728\u5ba4\u5185\u8d64\u8eab\u88f8\u4f53\uff0c\u53ea\u88f9\u7740\u6d74\u5dfe\uff0c\u5f20\u606a\u62bd\u4e86\u4e00\u5e8a\u88ab\u5b50\uff0c\u5c06\u81ea\u5df1\u4e0e\u5510\u5a67\u5305\u88f9\u5728\u91cc\u9762\uff0c\u50cf\u8fde\u4f53\u5a74\u513f\u4f3c\u7684\u51fa\u4e86\u623f\u95f4\u722c\u4e0a\u697c\u9876\u3002\u70ad\u7089\u5c06\u7184\u672a\u7184\uff0c\u8986\u7740\u4e00\u5c42\u7070\u767d\u8272\u7684\u7070\u70ec\uff0c\u5f20\u606a\u6361\u4e86\u51e0\u5757\u6728\u70ad\u4e22\u8fdb\u53bb\uff0c\u8fd9\u539f\u6765\u662f\u53ef\u4ee5\u7528\u6765\u70e7\u70e4\u7684\u7089\u5b50\uff1b\u684c\u4e0a\u7684\u5496\u5561\u5374\u5df2\u7ecf\u51b0\u51b7\uff0c\u5f20\u606a\u5c0f\u62bf\u4e86\u4e00\u53e3\uff0c\u5c31\u5c06\u5496\u5561\u5012\u4e86\u7089\u91cc\uff0c\u542c\u7740\u70e7\u6e7f\u7684\u6728\u70ad\u6ecb\u6ecb\u4f5c\u54cd\uff0c\u5496\u5561\u9999\u6c14\u5728\u8336\u5ba4\u91cc\u5f25\u6f2b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u5510\u5a67\u5750\u5728\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u624b\u4ece\u5979\u80a9\u4e0a\u6402\u8fc7\uff0c\u8ba9\u88ab\u5b50\u5c06\u4e24\u4e2a\u4eba\u7d27\u7d27\u7684\u88f9\u5728\u91cc\u9762\uff0c\u624b\u8fd8\u80fd\u987a\u4fbf\u63e1\u7740\u5510\u5a67\u4e73\u9e3d\u4f3c\u7684\u5ae9\u4e73\uff0c\u76c8\u76c8\u4e30\u817b\u7684\u8089\u611f\u52a8\u4eba\u5fc3\u9b44\u7684\u4ece\u5fc3\u624b\u4f20\u6765\uff0c\u59cb\u7ec8\u6ca1\u6562\u8086\u610f\u653e\u7eb5\u53d1\u6cc4\u7684\u4e0b\u8eab\u53c8\u786c\u7ed1\u7ed1\u7684\u9876\u4e86\u4e0a\u53bb\u3002\u5510\u5a67\u6a2a\u7709\u6012\u76ee\uff0c\u53ea\u662f\u5750\u7740\u4e00\u52a8\u4e0d\u6562\u52a8\uff0c\u56e0\u4e3a\u5979\u53d1\u73b0\u6ca1\u6709\u88ab\u5355\u53ef\u4ee5\u518d\u6362\u4e86\u3002</p><p>\u3000\u3000\u6821\u56ed\u91cc\u5bbf\u820d\u90fd\u5df2\u7ecf\u7184\u4e86\u706f\uff0c\u5076\u5c14\u4ece\u6821\u56ed\u91cc\u4f1a\u4f20\u6765\u4e00\u58f0\u538b\u6291\u7740\u9752\u6625\u7684\u5c16\u53eb\u3002\u6b64\u65f6\u7684\u591c\u665a\uff0c\u4e00\u7247\u5b81\u9759\uff0c\u8fd8\u4f1a\u6709\u4e00\u4e9b\u83ab\u540d\u7684\u54cd\u58f0\uff0c\u53ef\u80fd\u662f\u9694\u7740\u51e0\u91cd\u697c\u7684\u6c7d\u8f66\u54cd\u58f0\uff0c\u5374\u662f\u6108\u53d1\u7684\u886c\u6258\u51fa\u591c\u7684\u5b81\u9759\u3002\u900f\u8fc7\u8336\u5ba4\u7684\u6728\u7a97\u5f80\u5916\u671b\u53bb\uff0c\u9752\u9ed1\u8272\u7684\u5408\u6b22\u5c71\u4e0a\u7684\u6811\u5f71\u6709\u5982\u526a\u5f71\uff0c\u66f4\u8fdc\u7684\u71d5\u6c34\u5f25\u6f2b\u7740\u4e00\u4e1d\u5e7d\u5149\u4f3c\u7684\u6a2a\u94fa\u5728\u773c\u524d\uff0c\u518d\u8fdc\u5904\u7684\u71d5\u5f52\u5c71\u5c31\u50cf\u6d6e\u5728\u534a\u7a7a\u4e2d\u7684\u51e0\u9053\u865a\u65e0\u7f25\u7f08\u7684\u7ebf\u6761\u3002</p><p>\u3000\u3000\u201c\u4e0a\u56de\u62c9\u7740\u76db\u590f\u5728\u9999\u6e2f\u901b\u8857\u65f6\u9047\u5230\u5979\u4e86\uff0c\u201d\u5510\u5a67\u60f3\u8d77\u90a3\u6b21\u8857\u5934\u76f8\u9047\u8bb8\u601d\u72fc\u72c8\u201c\u9003\u8d70\u201d\u7684\u6837\u5b50\uff0c\u5fae\u5fae\u62ac\u8d77\u5934\uff0c\u8138\u8d34\u7740\u5f20\u606a\u7684\u8138\u988a\uff0c\u201c\u6709\u4e9b\u5fc3\u9178\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5c31\u5728\u60f3\u8bb8\u601d\u770b\u5230\u5510\u5a67\uff0c\u5510\u5a67\u6ca1\u53ef\u80fd\u770b\u4e0d\u5230\u8bb8\u601d\uff0c\u53ea\u662f\u8fd9\u59ae\u5b50\u4e0d\u8bf4\u800c\u5df2\u3002\u542c\u5510\u5a67\u8bf4\u201c\u6709\u4e9b\u5fc3\u9178\u4e86\u201d\u8fd8\u4ee5\u4e3a\u5979\u5f88\u4ecb\u610f\u8fd9\u4ef6\u4e8b\uff0c\u6b63\u60f3\u7740\u8981\u600e\u4e48\u82b1\u8a00\u5de7\u8bed\u5c06\u8fd9\u52ab\u7ed9\u6e21\u8fc7\uff0c\u5fc3\u91cc\u53c8\u60f3\uff1a\u90a3\u521a\u624d\u5728\u6700\u5173\u952e\u7684\u65f6\u523b\uff0c\u5979\u4e5f\u6ca1\u6709\u505c\u6b62\u554a\uff1f</p><p>\u3000\u3000\u201c\u8981\u662f\u80fd\u79bb\u5f00\u4f60\uff0c\u6211\u624d\u4e0d\u8981\u7ed9\u4f60\u8fd9\u732a\u5934\u5360\u4fbf\u5b9c\u5462\u3002\u4f60\u4e0d\u8981\u5f97\u610f\uff0c\u6211\u771f\u7684\u60f3\u8fc7\u8981\u79bb\u5f00\u4f60\uff0c\u201d\u5510\u5a67\u56de\u5934\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u90a3\u4e48\u4e11\u964b\u7684\u4e00\u6839\u4e1c\u897f\u8fd8\u975e\u8981\u6233\u5230\u4eba\u5bb6\u8eab\u4f53\u91cc\u6765\uff0c\u73b0\u5728\u8fd8\u9876\u7740\u4eba\u5bb6\u2026\u2026\u201d\u5510\u5a67\u5c48\u819d\u5750\u7740\uff0c\u4e0b\u5df4\u78d5\u5230\u819d\u76d6\u4e0a\uff0c\u8f7b\u58f0\u8bf4\u7740\u3002</p><p>\u3000\u3000\u542c\u5510\u5a67\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u4e0d\u597d\u610f\u601d\u7684\u5c06\u5c41\u80a1\u5f80\u540e\u62f1\u4e86\u62f1\uff0c\u521a\u624d\u6015\u5510\u5a67\u7ecf\u53d7\u4e0d\u4f4f\uff0c\u90fd\u6ca1\u6562\u8086\u610f\u7684\u653e\u7eb5\u53d1\u6cc4\uff0c\u53cc\u81c2\u7d27\u697c\u7740\uff0c\u5c06\u5510\u5a67\u6574\u4e2a\u8eab\u5b50\u90fd\u6402\u7d27\u5230\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u4e30\u76c8\u5a07\u8f6f\uff0c\u5341\u5206\u7684\u8981\u547d\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u6211\u5c31\u5728\u60f3\u5979\u662f\u4e0d\u662f\u4e5f\u662f\u8fd9\u6837\uff0c\u5b9e\u5728\u8eab\u4e0d\u7531\u5df1\u79bb\u4e0d\u5f00\u4f60\uff0c\u540e\u6765\u60f3\u5c31\u7b97\u9047\u5230\u7b11\u4e00\u7b11\u4e5f\u65e0\u6240\u8c13\uff0c\u5973\u4eba\u4f55\u82e6\u4e3a\u96be\u5973\u4eba\uff1f\u4e00\u5207\u90fd\u662f\u4f60\u8fd9\u53ea\u732a\u5934\u5728\u5bb3\u4eba\uff0c\u90fd\u4e0d\u6653\u5f97\u4f60\u4ee5\u540e\u8981\u5bb3\u591a\u5c11\u4eba\u3002\u53ea\u6709\u5f53\u65f6\u8eab\u5b50\u50f5\u5728\u90a3\u91cc\u6ca1\u6cd5\u52a8\u5f39\uff0c\u540e\u6765\u90fd\u81ea\u5df1\u6709\u4e9b\u803f\u803f\u4e8e\u6000\uff0c\u4e0d\u6653\u5f97\u5979\u4f1a\u600e\u4e48\u770b\u6211\u3002\u201d</p><p>\u3000\u3000\u542c\u5510\u5a67\u8fd9\u756a\u8bdd\uff0c\u8ddf\u5f97\u4e86\u5927\u8d66\u4f3c\u7684\uff0c\u5f20\u606a\u5fc3\u91cc\u53c8\u662f\u611f\u52a8\u53c8\u662f\u601c\u60dc\uff0c\u5f53\u7136\u4e5f\u4e0d\u4f1a\u8ddf\u5979\u8bf4\u8bb8\u601d\u6765\u8fc7\u5efa\u90ba\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5b59\u9759\u6aac\u62b1\u819d\u5750\u5728\u5e8a\u5934\uff0c\u5bbf\u820d\u91cc\u5176\u4ed6\u4eba\u4e0e\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4ed6\u4eec\u90fd\u8fd8\u5728\u76db\u4e16\u5e74\u534eKTV\u5531\u6b4c\uff0c\u8d76\u5de7\u6881\u519b\u4e0e\u672a\u5a5a\u59bb\u5b59\u4e3d\u665a\u4e0a\u5403\u8fc7\u996d\u4e5f\u5728\u90a3\u91cc\u5531\u6b4c\uff0c\u5c31\u5e2e\u4ed6\u4eec\u628a\u5355\u7ed9\u7b7e\u4e86\u2014\u2014\u4ed6\u4eec\u8981\u5728\u90a3\u91cc\u5531\u901a\u5bb5\u3002\u5b59\u9759\u6aac\u62b1\u819d\u5750\u5728\u5e8a\u5934\u51dd\u89c6\u7740\u7a97\u5916\u6bdb\u8338\u8338\u7684\u9ed1\u5f71\uff0c\u5bbf\u820d\u9662\u5899\u4e0a\u79ef\u4e86\u4e00\u5c42\u96ea\u3002</p><p>\u3000\u3000\u6709\u65f6\u5019\u5728\u60f3\u53ea\u8981\u8086\u65e0\u5fcc\u60ee\u7684\u559c\u6b22\u4e00\u4e2a\u4eba\u5c31\u597d\u4e86\uff0c\u60f3\u4e0d\u5230\u8fd8\u4f1a\u6709\u5fc3\u9178\u7684\u611f\u89c9\u3002\u62ff\u8d77\u624b\u673a\uff0c\u62e8\u901a\u59d0\u59d0\u5b59\u9759\u9999\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u59d0\uff0c\u4f60\u5728\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u521a\u521a\u5230\u5bb6\uff0c\u9aa8\u5934\u90fd\u7d2f\u6563\u67b6\u4e86\uff0c\u4f60\u600e\u4e48\u4f1a\u7a81\u7136\u8fd9\u4e48\u665a\u60f3\u8d77\u6765\u8981\u7ed9\u6211\u6253\u7535\u8bdd\uff1f\u201d\u5b59\u9759\u9999\u5728\u7535\u8bdd\u90a3\u5934\u7591\u60d1\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u8fd8\u5728\u5de5\u4f5c\u5417\uff1f\u201d\u5b59\u9759\u6aac\u770b\u7740\u7a97\u5916\u7684\u591c\u8272\uff0c\u5fc3\u60f3\u59d0\u59d0\u6216\u8bb8\u90fd\u5fd8\u6389\u4eca\u5929\u662f\u4ec0\u4e48\u65e5\u5b50\u4e86\uff0c\u4e0d\u6653\u5f97\u516c\u53f8\u7684\u5458\u5de5\u4f1a\u4e0d\u4f1a\u6709\u610f\u89c1\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u9526\u6e56\u5bf9\u5e7f\u544a\u6b4c\u66f2\u7684\u5236\u4f5c\u8981\u6c42\u592a\u9ad8\u4e86\uff0c\u6253\u56de\u6765\u5c31\u8981\u91cd\u65b0\u5236\u4f5c\uff0c\u8981\u914d\u5408\u4ed6\u4eec\u7684\u65b0\u4ea7\u54c1\u4e0a\u5e02\uff0c\u8fd9\u8fb9\u7d2f\u6b7b\u7d2f\u6d3b\u4e5f\u8981\u6491\u7740\u554a\u3002\u201d\u5b59\u9759\u9999\u5728\u7535\u8bdd\u90a3\u5934\u7b11\u7740\u62b1\u6028\u3002</p><p>\u3000\u3000\u53c8\u8981\u60f3\u8d77\u90a3\u732a\u5934\uff0c\u5fc3\u91cc\u771f\u662f\u90c1\u7ed3\uff0c\u5b59\u9759\u6aac\u8111\u888b\u9893\u7136\u5782\u4e86\u4e0b\u6765\uff0c\u6709\u6c14\u65e0\u529b\u7684\u8bf4\u9053\uff1a\u201c\u90a3\u4f60\u6ce8\u610f\u4f11\u606f\uff0c\u6211\u4e5f\u8981\u4f11\u606f\u4e86\u3002\u201d\u6302\u4e86\u7535\u8bdd\uff0c\u53c8\u6253\u7535\u8bdd\u95ee\u8fc7\u9152\u5427\u91cc\u7684\u60c5\u51b5\uff0c\u5c31\u51b3\u5b9a\u4ec0\u4e48\u90fd\u4e0d\u60f3\u7684\u62c9\u88ab\u5b50\u8499\u7740\u5934\u7761\u4e0b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7089\u4e2d\u7684\u6728\u70ad\u70e7\u70ec\u4e86\uff0c\u88f9\u7740\u88ab\u5b50\u62b5\u6321\u4e0d\u4f4f\u591c\u5916\u7684\u5bd2\u51b7\uff0c\u5f20\u606a\u624d\u4e0e\u5510\u5a67\u56de\u5230\u623f\u95f4\u91cc\uff0c\u8bf4\u7740\u60c5\u8bdd\uff0c\u773c\u76ae\u5b50\u6c89\u7538\u7538\u7684\u91cd\u4e86\u4e4b\u540e\uff0c\u5c31\u987a\u5176\u81ea\u7136\u7684\u7761\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u6e05\u6668\u9192\u8fc7\u6765\uff0c\u5510\u5a67\u4e0d\u5728\u6000\u91cc\uff0c\u6b32\u671b\u6ca1\u6709\u5f97\u5230\u53d1\u6cc4\uff0c\u8fd9\u4e48\u4e2a\u5996\u7cbe\u53c8\u5728\u8eab\u8fb9\u62db\u60f9\u7740\uff0c\u4f1a\u66f4\u611f\u5230\u60c5\u6b32\u5728\u4f53\u5185\u706b\u70e7\u706b\u71ce\u7684\uff0c\u8d64\u811a\u8e29\u5728\u5730\u6bef\u4e0a\u8d70\u51fa\u623f\u95f4\uff0c\u7ad9\u5728\u697c\u4e0a\u7684\u8fc7\u9053\u91cc\u51ed\u501f\u770b\u7740\u697c\u4e0b\u6ecb\u6ecb\u6ecb\u4f20\u6765\u714e\u6cb9\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u201c\u5c0f\u5a67\u2026\u2026\u201d\u5f20\u606a\u558a\u9053\uff0c\u4ece\u697c\u4e0a\u65e0\u6cd5\u76f4\u63a5\u770b\u5230\u53a8\u623f\u91cc\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u5510\u5a67\u7cfb\u7740\u56f4\u88d9\u4ece\u53a8\u623f\u91cc\u8d70\u51fa\u6765\uff0c\u624b\u91cc\u6258\u7740\u4e00\u53ea\u76d8\u5b50\uff0c\u76d8\u5b50\u91cc\u6709\u4e00\u53ea\u5dee\u4e0d\u591a\u7ed9\u78b3\u5316\u4e86\u7684\u7126\u9ed1\u7684\u8377\u5305\u86cb\uff0c\u5f20\u606a\u80fd\u52c9\u5f3a\u8ba4\u8bc6\u90a3\u662f\u8377\u5c14\u86cb\u7684\u5f62\u72b6\u3002</p><p>\u3000\u3000\u201c\u5403\u4e0d\u6210\u65e9\u996d\u4e86\u2026\u2026\u201d\u5510\u5a67\u5410\u4e86\u5410\u820c\u5934\uff0c\u5f88\u4e0d\u597d\u610f\u601d\u505a\u51fa\u8fd9\u6837\u7684\u65e9\u9910\u3002</p><p>\u3000\u3000\u89c1\u5c0f\u59ae\u8d70\u8def\u795e\u6001\u81ea\u7136\u6ca1\u6709\u4ec0\u4e48\u6302\u788d\uff0c\u5f20\u606a\u5fc3\u60f3\uff1a\u4f60\u5c06\u81ea\u5df1\u5f53\u6210\u65e9\u996d\u8ba9\u6211\u5403\u597d\u4e86\uff01\u4e0d\u8fc7\u8138\u4e0a\u6ca1\u6709\u4ec0\u4e48\u4e0d\u540c\u7684\u795e\u8272\uff0c\u603b\u8981\u5148\u5c06\u8fd9\u59ae\u5b50\u9a97\u5230\u697c\u4e0a\u6765\u518d\u8bf4\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5feb\u4e0a\u6765\uff0c\u6211\u6709\u4e1c\u897f\u8981\u7ed9\u4f60\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u5174\u594b\u7684\u4e0a\u697c\u6765\uff0c\u8fdb\u4e86\u623f\u95f4\u95ee\u5f20\u606a\uff1a\u201c\u6709\u4ec0\u4e48\u4e1c\u897f\uff0c\u795e\u795e\u79d8\u79d8\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5938\u5f20\u7684\u5c06\u7761\u888d\u626f\u5f00\uff0c\u9732\u51fa\u4ec0\u4e48\u90fd\u6ca1\u6709\u7a7f\u7684\u8d64\u8eab\u88f8\u4f53\uff0c\u53cc\u81c2\u5c06\u5510\u5a67\u53ea\u7a7f\u7740\u7f8a\u7ed2\u886b\u4e0e\u4f11\u95f2\u68c9\u88e4\u7684\u8eab\u5b50\u6402\u5728\u6000\u91cc\uff0c\u5728\u5979\u8033\u7554\u8f7b\u58f0\u8bf4\uff1a\u201c\u65e9\u9910\u8ba9\u6211\u5403\u4f60\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u5728\u5f20\u606a\u6000\u91cc\u626d\u52a8\u7740\u3001\u7b11\u7740\u8981\u9003\u51fa\u53bb\uff0c\u5f20\u606a\u7684\u60c5\u6b32\u5f7b\u5e95\u7ed9\u70b9\u71c3\u4e86\uff0c\u4e24\u81c2\u4ece\u540e\u9762\u7b8d\u7740\u5979\u7684\u8170\u80a2\u8e72\u4e0b\u6765\uff0c\u76f4\u63a5\u5265\u4e0b\u5979\u7684\u88e4\u5b50\uff0c\u9732\u51fa\u7c89\u767d\u7684\u7fd8\u81c0\uff0c\u8ddf\u5265\u4e86\u58f3\u7684\u716e\u9e21\u86cb\u4f3c\u7684\uff0c\u6709\u6781\u81f4\u67d4\u7f8e\u7684\u5f27\u5f62\u66f2\u7ebf\u4e0e\u767d\u5ae9\u7684\u5927\u817f\u8854\u63a5\u8d77\u6765\uff0c\u4e24\u817f\u4e2d\u95f4\u662f\u9c9c\u8273\u6f6e\u6da6\u7684\u5ae3\u7ea2\uff0c\u5728\u6e05\u6668\u767d\u96ea\u53cd\u5c04\u7684\u6668\u5149\u91cc\uff0c\u8000\u5f97\u4eba\u773c\u775b\u53d1\u75bc\uff0c\u70e7\u5f97\u5fc3\u9b42\u8ff7\u4e71\uff0c\u5c31\u8fd9\u6837\u5f20\u606a\u5c06\u5510\u5a67\u5f53\u6210\u65e9\u9910\u8086\u610f\u7684\u72e0\u72e0\u7684\u5c1d\u4e86\u4e00\u904d\u3002</p><p>\u3000\u3000\u603b\u4e4b\u4e0d\u50cf\u4e0a\u56de\u5728\u9999\u6e2f\u90a3\u6b21\u597d\u4e8b\u7ed9\u9648\u4fe1\u751f\u7684\u624b\u673a\u6253\u65ad\uff0c\u5723\u8bde\u8282\u7684\u8fd9\u5929\u4e0a\u5348\uff0c\u5f20\u606a\u90fd\u4e0e\u5510\u5a67\u53ae\u5b88\u5728\u516c\u5bd3\u91cc\uff0c\u662f\u5510\u5a67\u5a07\u7f9e\u6ca1\u6709\u52c7\u6c14\u51fa\u53bb\u89c1\u4eba\uff0c\u8fd8\u4e00\u672c\u6b63\u7ecf\u7684\u5c06\u5979\u7684\u4e1c\u897f\u90fd\u62ff\u56de\u5230\u5ba2\u5385\u53bb\u3002\u5f20\u606a\u6e05\u6668\u5c31\u6709\u4e9b\u4e0d\u592a\u601c\u9999\u60dc\u7389\u4e86\uff0c\u4e8b\u540e\u5510\u5a67\u8d70\u8def\u5c31\u6709\u4e9b\u4e0d\u592a\u81ea\u7136\uff0c\u770b\u5230\u5f20\u606a\u5634\u89d2\u6302\u7740\u7684\u574f\u7b11\uff0c\u5510\u5a67\u4e0d\u751a\u5a07\u7f9e\u4e4b\u4f59\u81ea\u7136\u5c06\u4ed6\u6068\u5f97\u7259\u75d2\u75d2\u7684\u3002</p></article>"}}}]);
