(window.webpackJsonp=window.webpackJsonp||[]).push([[913],{936:function(p){p.exports={chapterTitle:null,title:"\u7b2c732\u7ae0 \u51c6\u5907\u5de5\u4f5c",content:"<article><h2>\u7b2c732\u7ae0 \u51c6\u5907\u5de5\u4f5c</h2><p>\u3000\u3000\u5f20\u606a\u672c\u6ca1\u6709\u5728\u516c\u5bd3\u91cc\u5927\u5bb4\u5bbe\u670b\u7684\u5fc3\u601d\uff0c\u5510\u5a67\u4e8b\u540e\u8d70\u8def\u6709\u4e9b\u4e0d\u81ea\u7136\uff0c\u8d56\u5728\u5c4b\u5b50\u6b7b\u6d3b\u4e0d\u80af\u51fa\u53bb\uff0c\u6253\u7535\u8bdd\u7ea6\u9648\u5983\u84c9\u3001\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u8fc7\u6765\u73a9\u3002\u675c\u98de\u4e0e\u76db\u590f\u4e2d\u5348\u4e4b\u524d\u4e5f\u8d76\u4e86\u56de\u6765\u2014\u2014\u4e5f\u4e0d\u6653\u5f97\u4ed6\u4eec\u6628\u5929\u591c\u91cc\u53bb\u54ea\u91cc\u9b3c\u6df7\u4e86\u3002\u8499\u4e50\u3001\u65f6\u5b66\u658c\u3001\u8463\u8dc3\u534e\u4ed6\u4eec\u966a\u5b59\u9759\u6aac\u5bbf\u820d\u7684\u5973\u5b69\u5b50\u53bb\u76db\u4e16\u5e74\u534eK\u6b4c\u5230\u51cc\u6668\u624d\u56de\u6765\uff0c\u5f20\u606a\u53bb\u6572\u95e8\u65f6\uff0c\u4ed6\u4eec\u51e0\u4e2a\u4eba\u90fd\u8fd8\u5728\u5c4b\u91cc\u8865\u89c9\u3002\u5f20\u606a\u53ea\u5f97\u62c9\u7740\u675c\u98de\u5f00\u8f66\u53bb\u9a6c\u83b2\u8857\u7684\u519c\u8d38\u5e02\u573a\u4e70\u4e86\u4e00\u5927\u5806\u98df\u6750\u56de\u6765\u3002\u8d76\u8fc7\u6765\uff0c\u4e0d\u4ec5\u4ec5\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u90fd\u5728\uff0c\u5b59\u9759\u6aac\u4e0e\u5979\u4eec\u5bbf\u820d\u7684\u51e0\u4e2a\u5973\u5b69\u5b50\u4e5f\u90fd\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u8981\u8bf4\u5b59\u9759\u6aac\u5bbf\u820d\u7684\u5973\u5b69\u5b50\uff0c\u5f20\u606a\u65e9\u5728\u4e24\u5e74\u524d\u5c31\u5728\u9999\u6e2f\u5b59\u9759\u6aac\u7684\u751f\u65e5\u5bb4\u4f1a\u4e0a\u90fd\u8ba4\u8bc6\u4e86\uff0c\u5979\u4eec\u5bbf\u820d\u4e00\u4e2a\u53eb\u5c0f\u4f73\u7684\u5973\u5b69\u5b50\u8fd8\u4e0e\u91d1\u56fd\u6d77\u7684\u52a9\u624b\u9648\u6653\u677e\u6b63\u8c08\u604b\u7231\u3002\u5f20\u606a\u8fdb\u95e8\u65f6\uff0c\u5c0f\u4f73\u8fd8\u6b63\u8ddf\u9648\u6653\u677e\u6d53\u60c5\u871c\u610f\u7684\u5728\u901a\u7535\u8bdd\uff0c\u5f20\u606a\u5c06\u7535\u8bdd\u62a2\u8fc7\u6765\uff0c\u8ba9\u9648\u6653\u677e\u9a6c\u4e0a\u8d76\u8fc7\u6765\u5403\u4e2d\u996d\u3002</p><p>\u3000\u3000\u5e2d\u82e5\u7433\u7684\u795e\u8272\u6709\u4e9b\u843d\u5bde\uff0c\u5979\u6628\u5929\u591c\u91cc\u4e5f\u662f\u4e0e\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u4e00\u8d77\u53bb\u76db\u4e16\u5e74\u534e\u5531\u6b4c\uff0c\u4eca\u5929\u4e5f\u672a\u89c1\u738b\u6d77\u7c9f\u7ea6\u5979\uff1b\u8499\u4e50\u7684\u96c0\u8dc3\u5174\u594b\u5c31\u663e\u5f97\u6709\u4e9b\u201c\u5fc3\u7406\u9634\u6697\u201d\u4e86\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5403\u996d\u65f6\uff0c\u8fd8\u8bf7\u4ee4\u5c0f\u71d5\u4e0e\u6731\u5c0f\u519b\u4e00\u8d77\u8fc7\u6765\uff0c\u6240\u5e78\u516c\u5bd3\u8db3\u591f\u5927\uff0c\u6324\u8fdb\u6765\u8fd1\u4e8c\u5341\u4eba\u4e5f\u4e0d\u89c9\u5f97\u62e5\u6324\u3002\u5927\u5bb6\u4e03\u624b\u516b\u811a\u7684\u5c06\u4e2d\u996d\u51c6\u5907\u8d77\u6765\uff0c\u5403\u8fc7\u4e2d\u996d\u53c8\u4e00\u7a9d\u8702\u7684\u6563\u5f00\uff0c\u6700\u540e\u5c31\u5269\u4e0b\u5f20\u606a\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u4e09\u4e2a\u4eba\u7559\u4e0b\u6765\u6536\u62fe\u6b8b\u5c40\u3002</p><p>\u3000\u3000\u5510\u5a67\u521d\u8bc6\u6027\u4e8b\uff0c\u5979\u8fd8\u6ca1\u6709\u5230\u8d2a\u6b22\u6c89\u6eba\u7684\u5e74\u9f84\uff0c\u4f46\u662f\u591a\u4e86\u4e9b\u6625\u610f\u6b63\u6d53\u7684\u5a07\u7f9e\uff0c\u773c\u7738\u660e\u6c8f\u800c\u59a9\u5a9a\uff0c\u8138\u5e9e\u6676\u83b9\u5254\u900f\u7684\u7c89\u5ae9\u7ea2\u6da6\uff0c\u4f3c\u4e4e\u5c06\u5ae3\u7136\u7684\u5fae\u7b11\u4e00\u76f4\u62bf\u5728\u5f27\u5ea6\u4f18\u7f8e\u7684\u67d4\u5ae9\u5634\u5507\u4e4b\u4e2d\uff0c\u900f\u51fa\u65e0\u9650\u7684\u5a07\u67d4\u751c\u7f8e\u3002\u9648\u5983\u84c9\u4e60\u60ef\u81ea\u5df1\u5305\u88f9\u5728\u4ee4\u4eba\u4e0d\u6562\u903c\u89c6\u7684\u51b7\u8273\u4e4b\u4e2d\uff0c\u5f85\u4f17\u4eba\u6563\u53bb\uff0c\u53ea\u4e0e\u5f20\u606a\u3001\u5510\u5a67\u76f8\u5904\u65f6\uff0c\u5979\u9752\u6625\u6d0b\u6ea2\u7684\u8138\u4e0a\u6563\u7740\u95f2\u6de1\u7684\u7b11\u5bb9\uff0c\u9752\u5c71\u4f3c\u7684\u9edb\u7709\u4e0b\uff0c\u773c\u7738\u6df1\u9083\u800c\u6e05\u6f88\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u975e\u5e38\u4e50\u610f\u5728\u521d\u96ea\u7684\u5348\u540e\u4e0e\u8fd9\u6837\u4e24\u4e2a\u5a07\u7f8e\u7684\u5c11\u5973\u6536\u62fe\u5348\u5bb4\u540e\u7684\u6b8b\u5c40\uff0c\u900f\u8fc7\u843d\u5730\u73bb\u7483\u7a97\u53ef\u4ee5\u770b\u89c1\u5bf9\u9762\u5c4b\u9876\u3001\u5c71\u5d16\u4e0a\u7684\u79ef\u96ea\u5728\u8000\u773c\u7684\u53cd\u5149\u3002</p><p>\u3000\u3000\u5f20\u606a\u7cfb\u7740\u56f4\u88d9\u8dea\u5728\u5730\u7816\u4e0a\u6e05\u6d01\u536b\u751f\u65f6\uff0c\u5723\u8bde\u8282\u52a0\u5468\u672b\u90fd\u8fd8\u7559\u5728\u6a61\u6811\u56ed\u52a0\u73ed\u7684\u8d75\u9e4f\u9001\u4e86\u4e00\u4efd\u6750\u6599\u8fc7\u6765\uff0c\u662f\u5fb7\u4eea\u4e0e\u65af\u9ad8\u67cf\u7684\u8c08\u5224\u56e2\u961f\u5206\u522b\u62b5\u8fbe\u5efa\u90ba\u7684\u65f6\u95f4\u4e0e\u65e5\u7a0b\u5b89\u6392\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fd9\u6bb5\u65f6\u95f4\u4e00\u7cfb\u5217\u7684\u52a8\u4f5c\uff0c\u4e0d\u4ec5\u8ba9\u56fd\u5185\u7684\u540c\u884c\u4e1a\u77a0\u76ee\u7ed3\u820c\uff0c\u4e5f\u8ba9\u9526\u6e56\u5728\u6d77\u5916\u6700\u91cd\u8981\u7684\u4e24\u4e2a\u5408\u4f5c\u4f19\u4f34\u66f4\u52a0\u7684\u522e\u76ee\u76f8\u770b\u3002</p><p>\u3000\u3000\u8c01\u90fd\u80fd\u610f\u8bc6\u5230\u9526\u6e56\u516c\u5f00\u6570\u5b57\u624b\u673a\u6280\u672f\u5c06\u7ed9\u4e2d\u56fd\u7684\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u5e26\u6765\u6781\u4e3a\u6df1\u523b\u7684\u89e6\u52a8\u4e0e\u53d8\u9769\uff0c\u9648\u4fe1\u751f\u4e5f\u65e9\u5728\u5341\u4e00\u6708\u7684\u4e2d\u65ec\u524d\u5f80\u7f8e\u56fd\u6e38\u8bf4\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u540c\u610f\u4e0e\u9526\u6e56\u5f00\u5c55\u66f4\u5e7f\u6cdb\u7684\u6280\u672f\u5408\u4f5c\u3002</p><p>\u3000\u3000\u8981\u5728\u56fd\u5185\u4fdd\u6301\u9886\u5148\uff0c\u5e76\u8fc5\u901f\u7f29\u77ed\u4e0e\u6b27\u7f8e\u7535\u5b50\u5382\u5546\u7684\u5dee\u8ddd\uff0c\u4ec5\u9760\u81ea\u8eab\u79ef\u6781\u7684\u6280\u672f\u79ef\u7d2f\uff0c\u8fd8\u6709\u4f1a\u611f\u89c9\u5230\u975e\u5e38\u7684\u5403\u529b\u3002</p><p>\u3000\u3000\u51fa\u4e8e\u8981\u5728\u5efa\u90ba\u3001\u6d77\u5dde\u5efa\u7acb\u5b8c\u5584\u7684\u7535\u5b50\u5de5\u4e1a\u57fa\u5730\u7684\u613f\u610f\uff0c\u5f20\u606a\u4e00\u76f4\u90fd\u79ef\u6781\u7684\u725f\u6c42\u5c06\u53cc\u65b9\u7684\u6280\u672f\u5408\u4f5c\u5730\u70b9\u9009\u5728\u5efa\u90ba\u6216\u6d77\u5dde\u2014\u2014\u4f18\u8d28\u800c\u5ec9\u4ef7\u7684\u4eba\u529b\u8d44\u6e90\u7684\u786e\u5177\u5907\u5f88\u5f3a\u7684\u5438\u5f15\u529b\uff1b\u8fd9\u6b21\u5fb7\u4eea\u4e0e\u65af\u9ad8\u67cf\u6d3e\u51fa\u7684\u6280\u672f\u5408\u4f5c\u8c08\u5224\u56e2\u961f\u8c08\u5224\u7684\u4e3b\u8981\u5730\u70b9\uff0c\u81ea\u7136\u4e5f\u5c31\u9009\u5728\u5efa\u90ba\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u7684\u9996\u5e2d\u8c08\u5224\u4ee3\u8868\u662f\u8001\u670b\u53cb\u827e\u9ed8\uff0c\u5fb7\u4eea\u65b9\u9762\u662f\u7531\u4e00\u76f4\u8d1f\u8d23\u6570\u5b57\u4fe1\u53f7\u5904\u7406\u5668\u5f00\u53d1\u4e0e\u5546\u4e1a\u5316\u8fd0\u4f5c\u7684\u9ad8\u7ea7\u526f\u603b\u88c1\u5fb7\u5c14\u6cd5\u897f\u4e0e\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u5468\u6b63\u6625\u7387\u9886\u3002\u5468\u6b63\u6625\u539f\u662f\u9648\u4fe1\u751f\u5728\u5fb7\u4eea\u7684\u526f\u624b\uff0c\u9648\u4fe1\u751f\u52a0\u76df\u9526\u6e56\u4e4b\u540e\uff0c\u5468\u6b63\u6625\u63a5\u66ff\u9648\u4fe1\u751f\u7684\u4f4d\u5b50\u62c5\u4efb\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u3002</p><p>\u3000\u3000\u770b\u4e86\u65e5\u7a0b\u5b89\u6392\uff0c\u5f20\u606a\u5e76\u65e0\u5f02\u8bae\uff0c\u95ee\u8fc7\u9648\u4fe1\u751f\u4e0e\u4e01\u69d0\u8fd8\u6709\u8096\u664b\u6210\u660e\u5929\u5230\u5efa\u90ba\u7684\u65f6\u95f4\u2014\u2014\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u5468\u6b63\u6625\u660e\u5929\u4e5f\u4f1a\u63d0\u524d\u5230\u5efa\u90ba\u6765\u2014\u2014\u4fbf\u8ba9\u8d75\u9e4f\u79bb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u7cfb\u5728\u8170\u95f4\u7684\u56f4\u88d9\u89e3\u5f00\uff0c\u9012\u7ed9\u5728\u53a8\u623f\u5fd9\u788c\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e9b\u529f\u8bfe\u8981\u505a\uff0c\u5c06\u8fd9\u4e9b\u529f\u8bfe\u505a\u5b8c\uff0c\u624d\u80fd\u665a\u4e0a\u966a\u4f60\u4eec\u51fa\u53bb\u73a9\u2026\u2026\u201d\u5f20\u606a\u56de\u4ed6\u4e66\u623f\u4e0e\u5367\u5ba4\u76f8\u901a\u5e26\u6709\u72ec\u7acb\u536b\u6d74\u7684\u623f\u95f4\u5c06\u6750\u6599\u62ff\u4e0b\u6765\uff0c\u5750\u5728\u697c\u4e0b\u5bbd\u655e\u660e\u4eae\u7684\u5ba2\u5385\u6c99\u53d1\u4e0a\u770b\u6750\u6599\u3002</p><p>\u3000\u3000\u5510\u5a67\u7aef\u4e86\u4e00\u676f\u70ed\u6c7d\u817e\u817e\u3001\u9999\u6c14\u6251\u9f3b\u7684\u5496\u5561\u653e\u5728\u6c99\u53d1\u524d\u7684\u73bb\u7483\u5496\u5561\u684c\u4e0a\uff0c\u4ece\u540e\u9762\u6402\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u8138\u8d34\u7740\u5f20\u606a\u7684\u8138\u988a\uff0c\u4e00\u8d77\u770b\u4ed6\u624b\u91cc\u7684\u6750\u6599\uff0c\u770b\u4e86\u4e00\u4f1a\u513f\u5c31\u89c9\u5f97\u592a\u9633\u7a74\u53d1\u80c0\uff0c\u8bf4\u9053\uff1a\u201c\u597d\u96be\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8ba9\u5510\u5a67\u5750\u5230\u81ea\u5df1\u8eab\u8fb9\uff0c\u5510\u5a67\u5374\u5a07\u7f9e\u7684\u7b11\u4e86\u7b11\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u9648\u5983\u84c9\u5728\u770b\u522b\u5904\uff0c\u8fc5\u901f\u5728\u5f20\u606a\u5507\u4e0a\u5544\u4e86\u4e00\u53e3\u5c31\u8dd1\u5230\u4e00\u8fb9\u4e0e\u9648\u5983\u84c9\u8bf4\u8bdd\u53bb\u4e86\uff0c\u5f20\u606a\u53ea\u6709\u7ee7\u7eed\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u4eba\u770b\u6750\u6599\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8fd9\u662f\u97e9\u606a\u5173\u4e8e\u9526\u6e56\u5f00\u53d1\u4e13\u7528\u578b\u5d4c\u5165\u5fae\u5904\u7406\u5668\u6280\u672f\u7684\u65b9\u6848\u62a5\u544a\u3002</p><p>\u3000\u3000\u9526\u6e56\u5c06\u4e13\u6ce8\u4e8e\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u6280\u672f\u7684\u53d1\u5c55\u3002\u5728\u6570\u5b57\u65f6\u4ee3\u5230\u6765\u4e4b\u540e\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e2d\uff0c\u6709\u51e0\u7c7b\u666e\u904d\u901a\u7528\u6027\u8d28\u7684\u6838\u5fc3\u6280\u672f\uff0c\u8fd9\u4e9b\u6838\u5fc3\u6280\u672f\uff0c\u5c31\u662f\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u672a\u6765\u8981\u91cd\u70b9\u653b\u514b\u7684\u6838\u5fc3\u76ee\u6807\u3002</p><p>\u3000\u3000\u4e0d\u53ef\u80fd\u5b8c\u5168\u4e00\u7a77\u4e8c\u767d\u7684\u4ece\u5934\u5f00\u59cb\u505a\u8d77\uff0c\u5c3d\u53ef\u80fd\u83b7\u5f97\u76f8\u5173\u6280\u672f\u7684\u57fa\u672c\u6388\u6743\uff0c\u7ad9\u5230\u524d\u4eba\u7684\u80a9\u8180\u7ee7\u7eed\u524d\u8fdb\u662f\u80af\u5b9a\u7684\u3001\u4e5f\u662f\u5fc5\u9700\u7684\u9009\u62e9\u3002\u4f46\u662f\u5982\u4f55\u9009\u62e9\u5408\u9002\u7684\u5207\u5165\u70b9\u53bb\u53d1\u5c55\u81ea\u5df1\u7684\u6280\u672f\uff0c\u59cb\u7ec8\u662f\u9526\u6e56\u6743\u8861\u7684\u7126\u70b9\u3002</p><p>\u3000\u3000\u5c31\u62ff\u5d4c\u5165\u5f0f\u5fae\u5904\u7406\u5668\u6280\u672f\u6765\u8bf4\uff0c\u9526\u6e56\u5728\u8fc7\u53bb\u4e24\u5e74\u65f6\u95f4\u91cc\uff0c\u5728VCD\u3001SVCD\u3001MP3\u7684\u5f00\u53d1\u4e2d\uff0c\u5bf9\u5e94\u7528\u4e8e\u97f3\u9891\u3001\u89c6\u9891\u7f16\u89e3\u7801\u9886\u57df\u7684\u53ef\u7f16\u7a0b\u591a\u5a92\u4f53\u5fae\u5904\u7406\u5668\u7684\u5916\u56f4\u5e94\u7528\u6280\u672f\u7814\u7a76\u5c31\u5f88\u900f\u5f7b\u3002</p><p>\u3000\u3000\u9526\u6e56\u60f3\u8981\u771f\u6b63\u7684\u8fdb\u5165\u5143\u5668\u4ef6\u5546\u7684\u884c\u5217\uff0c\u53d1\u5c55\u81ea\u5df1\u7684\u5782\u76f4\u5316\u751f\u4ea7\u4f53\u7cfb\uff0c\u8fd9\u679a\u9002\u7528\u4e8e\u591a\u6570\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u3001\u540c\u6837\u80fd\u89e3\u51b3\u65e5\u540e\u6570\u5b57\u624b\u673a\u65e5\u76ca\u591a\u6837\u5316\u591a\u5a92\u4f53\u529f\u80fd\u5904\u7406\u7684\u5d4c\u5165\u5f0f\u5fae\u5904\u7406\u5668\uff0c\u5f20\u606a\u662f\u4e0d\u4f1a\u7f6e\u4e4b\u4e0d\u7406\u7684\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u60f3\u4e00\u4e0b\u5b50\u5f00\u53d1\u51fa\u5177\u5907\u5b8c\u5168\u77e5\u8bc6\u4ea7\u6743\u7684\u5e94\u7528\u5fae\u5904\u7406\u5668\uff0c\u96be\u5ea6\u6781\u5927\uff0c\u8017\u65f6\u3001\u8017\u94b1\u3001\u8017\u4eba\u7684\u7a0b\u5ea6\u5c06\u96be\u4ee5\u60f3\u8c61\uff0c\u6bd5\u7adf\u9526\u6e56\u8fd9\u65f6\u5019\u8fd8\u4e0d\u80fd\u5c06\u56db\u767e\u591a\u540d\u7814\u53d1\u4eba\u5458\u90fd\u6295\u5165\u5230\u8fd9\u679a\u5fae\u5904\u7406\u5668\u7684\u7814\u53d1\u4e0a\u2014\u2014\u518d\u8bf4\u6709\u4e9b\u7814\u53d1\u4eba\u5458\u7684\u4e13\u4e1a\u4e5f\u4e0d\u5bf9\u53e3\uff0c\u4e5f\u5e2e\u4e0d\u4e0a\u4ec0\u4e48\u5fd9\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u6a61\u6811\u56ed\u5d4c\u5165\u5f0f\u5fae\u5904\u7406\u5668\u5f00\u53d1\u9879\u76ee\u7684\u7ec4\u7ec7\u4eba\uff0c\u97e9\u683c\u63d0\u51fa\u7684\u65b9\u6848\uff0c\u5c31\u662f\u83b7\u5f97ARM\u516c\u53f8\u7684\u6388\u6743\uff0c\u91c7\u7528ARM\u5fae\u5904\u7406\u5668\u7684\u6784\u67b6\u6765\u5f00\u53d1\u9526\u6e56\u7684\u5fae\u5904\u7406\u5668\u3002</p><p>\u3000\u3000ARM\u516c\u53f8\u662f\u5bb6\u4e3b\u8981\u51fa\u552e\u5fae\u5904\u7406\u5668\u8bbe\u8ba1\u6280\u672f\u6388\u6743\u7684\u516c\u53f8\uff0c\u4f5c\u4e3a\u77e5\u8bc6\u4ea7\u6743\u4f9b\u5e94\u5546\uff0c\u672c\u8eab\u4e0d\u76f4\u63a5\u4ece\u4e8b\u5fae\u5904\u7406\u5668\u82af\u7247\u7684\u751f\u4ea7\u4e0e\u9500\u552e\uff0c\u800c\u662f\u9760\u8f6c\u8ba9\u8bbe\u8ba1\u8bb8\u53ef\u7531\u83b7\u5f97\u6388\u6743\u516c\u53f8\u751f\u4ea7\u5404\u5177\u7279\u8272\u7684\u5fae\u5904\u7406\u5668\u82af\u7247\u3002</p><p>\u3000\u3000\u4e16\u754c\u5404\u5927\u534a\u5bfc\u4f53\u751f\u4ea7\u5546\u5305\u62ec\u5927\u540d\u9f0e\u9f0e\u7684\u5fb7\u4eea\u90fd\u4eceARM\u8d2d\u4e70\u5176\u8bbe\u8ba1\u7684ARM\u5fae\u5904\u7406\u5668\uff0c\u7136\u540e\u6839\u636e\u5404\u81ea\u4e0d\u540c\u7684\u5e94\u7528\u9886\u57df\uff0c\u52a0\u5165\u9002\u5f53\u7684\u5916\u56f4\u7535\u8def\uff0c\u4ece\u800c\u5f62\u6210\u81ea\u5df1\u7684\u5fae\u5904\u7406\u5668\u82af\u7247\u8fdb\u5165\u5e02\u573a\u3002</p><p>\u3000\u3000\u9526\u6e56\u60f3\u5f00\u53d1\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e13\u7528\u7684\u3001\u5728\u591a\u5a92\u4f53\u9886\u57df\u6709\u7740\u5353\u8d8a\u8868\u73b0\u7684\u5fae\u5904\u7406\u5668\uff0c\u4e5f\u53ef\u80fd\u91c7\u53d6\u8fd9\u79cd\u6a21\u5f0f\uff0c\u4eceARM\u516c\u53f8\u83b7\u5f97\u6280\u672f\u6388\u6743\uff0c\u5728ARM\u5fae\u5904\u7406\u7684\u6784\u67b6\u57fa\u7840\u4e0a\u6765\u5f00\u53d1\u81ea\u5df1\u7684\u5fae\u5904\u7406\u5668\u3002</p><p>\u3000\u3000\u6839\u636e\u6388\u6743\u7a0b\u5ea6\u7684\u4e0d\u540c\uff0c\u6240\u8981\u652f\u4ed8\u7684\u6388\u6743\u8d39\u7528\u5dee\u5f02\u6781\u5927\uff0c\u51e0\u5341\u4e07\u7f8e\u5143\u5230\u51e0\u767e\u4e07\u7f8e\u5143\u90fd\u6709\uff0c\u7279\u522b\u662f\u6d89\u53ca\u5230\u5927\u91cf\u67b6\u6784\u4fee\u6539\u7684\u6df1\u5ea6\u6280\u672f\u6388\u6743\uff0c\u8d39\u7528\u5c31\u53ef\u80fd\u8d85\u8fc7\u5343\u4e07\u7f8e\u5143\u3002\u5f53\u7136\uff0c\u8fd9\u79cd\u7ea7\u522b\u7684\u6388\u6743\uff0c\u53ef\u4f9b\u83b7\u5f97\u9526\u6e56\u53d1\u5c55\u7684\u4f59\u5730\u4e5f\u66f4\u5927\uff0c\u6700\u7ec8\u5f00\u53d1\u51fa\u6765\u7684\u5fae\u5904\u7406\u5668\u4ea7\u54c1\u4e5f\u4f1a\u6709\u66f4\u591a\u9526\u6e56\u81ea\u8eab\u7684\u7279\u6027\uff0c\u907f\u514d\u4f4e\u5c42\u6b21\u7684\u540c\u8d28\u5316\u5e02\u573a\u7ade\u4e89\uff0c\u4f46\u662f\uff0c\u540c\u65f6\u5bf9\u9526\u6e56\u7684\u6280\u672f\u529b\u91cf\u8981\u6c42\u4e5f\u66f4\u9ad8\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u5728\u4e3a\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e13\u95e8\u8bbe\u8ba1\u7684\u51e0\u6b3e\u5fae\u5904\u7406\u5668\u4e5f\u540c\u6837\u662f\u5728\u91c7\u7528ARM\u6388\u6743\u7684\u5fae\u5904\u7406\u5668\u6784\u67b6\u57fa\u7840\u4e0a\u5f00\u53d1\u51fa\u6765\u7684\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u5728\u8fd9\u4e9b\u65b9\u9762\u79ef\u7d2f\u7684\u5927\u91cf\u6280\u672f\u4e0e\u7ecf\u9a8c\uff0c\u662f\u9526\u6e56\u6781\u5176\u6e34\u671b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u671f\u5f85\u8fd9\u6b21\u7684\u6280\u672f\u5408\u4f5c\u8c08\u5224\u80fd\u5728\u8fd9\u4e0a\u9762\u53d6\u5f97\u7a81\u7834\uff0c\u53ef\u60dc\u9526\u6e56\u80fd\u4f9d\u4ed7\u3001\u8ba9\u5fb7\u4eea\u7fa1\u6155\u7684\u6280\u672f\u592a\u6709\u9650\u4e86\uff0c\u9526\u6e56\u8fd9\u65f6\u5019\u552f\u4e00\u80fd\u503c\u5f97\u50b2\u6162\u7684\u4e5f\u53ea\u6709\u6570\u5b57\u97f3\u4e50\u64ad\u653e\u65b9\u9762\u7684\u4e00\u7cfb\u5217\u82af\u7247\u4e0e\u4ea7\u54c1\u4e13\u5229\u6280\u672f\u3002</p><p>\u3000\u3000\u6839\u636e\u201c\u53cd\u5784\u65ad\u201d\u56fd\u9645\u516c\u7ea6\u7684\u8981\u6c42\uff0c\u4e13\u5229\u6743\u5728\u4efb\u4f55\u56fd\u5bb6\u90fd\u662f\u7981\u6b62\u6ee5\u7528\u7684\uff0c\u6709\u5f3a\u5236\u8bb8\u53ef\u7684\u7ea6\u5b9a\uff0c\u4e00\u9879\u4e13\u5229\u6280\u672f\u5fc5\u987b\u6388\u6743\u7b2c\u4e09\u65b9\u7684\u4e2a\u4eba\u6216\u673a\u6784\u4f7f\u7528\uff0c\u5f53\u521d\u65af\u9ad8\u67cf\u5c06\u5176\u7b2c\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u6388\u6743\u5fb7\u4eea\u4f7f\u7528\uff0c\u4e1c\u829d\u4e5f\u5c06\u95ea\u5b58\u7684\u4e13\u5229\u6280\u672f\u4e0e\u4e09\u661f\u5171\u4eab\u3002\u8fd9\u79cd\u6388\u6743\u4e5f\u4e0d\u662f\u514d\u8d39\u7684\u5348\u9910\uff0c\u901a\u5e38\u90fd\u662f\u4e0e\u5bf9\u65b9\u4e92\u901a\u6709\u65e0\u3001\u4ea4\u6362\u6388\u6743\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u56fd\u5185\u53d1\u5c55\u7684\u5382\u5546\u90fd\u89c6\u8fd9\u79cd\u516c\u7ea6\u5982\u65e0\u7269\uff0c\u4f46\u662f\u9526\u6e56\u8981\u53c2\u4e0e\u5168\u7403\u7ade\u4e89\uff0c\u5fc5\u987b\u9075\u5b88\u76f8\u5e94\u7684\u89c4\u5219\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u6b63\u5f0f\u63a8\u51fa\u6570\u5b57\u97f3\u4e50\u64ad\u653e\u5668\u4e4b\u524d\uff0c\u9664\u4e86\u6280\u672f\u3001\u751f\u4ea7\u4e0a\u7684\u5de5\u4f5c\u4e4b\u5916\uff0c\u5176\u4ed6\u9700\u8981\u505a\u7684\u51c6\u5907\u5de5\u4f5c\u8fd8\u8981\u5f88\u591a\u3002</p></article>"}}}]);
