(window.webpackJsonp=window.webpackJsonp||[]).push([[916],{939:function(p){p.exports={chapterTitle:null,title:"\u7b2c913\u7ae0 \u6bcd\u5973\u5982\u82b1",content:"<article><h2>\u7b2c913\u7ae0 \u6bcd\u5973\u5982\u82b1</h2><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u4e58\u516c\u4ea4\u5230\u6a61\u6811\u56ed\u5b9e\u9a8c\u56ed\u524d\u4e0b\u8f66\uff0c\u4ed6\u62ff\u7740\u8eab\u4efd\u5361\u4e0e\u9648\u5983\u84c9\u8fdb\u5165\u98ce\u666f\u79c0\u4e3d\u6709\u5982\u56ed\u6797\u7684\u5b9e\u9a8c\u56ed\u533a\uff0c\u56ed\u533a\u7684\u95e8\u7981\u7ba1\u7406\u7a0d\u677e\u9a70\u4e9b\uff0c\u666e\u901a\u5458\u5de5\u5728\u8282\u5047\u65e5\u8fd8\u53ef\u4ee5\u5e26\u7740\u5bb6\u4eba\u8fdb\u6765\u6e38\u73a9\u3002</p><p>\u3000\u3000\u8fdb\u5165\u5b9e\u9a8c\u56ed\u5185\u5177\u4f53\u7684\u7814\u53d1\u5927\u697c\uff0c\u95e8\u5385\u91cc\u8fd8\u6709\u4e00\u9053\u95e8\u7981\uff0c\u5c31\u5341\u5206\u7684\u4e25\u683c\uff0c\u867d\u8bf4\u6709\u9ad8\u7ea7\u522b\u7684\u8eab\u4efd\u5361\uff0c\u6ee1\u8138\u72d0\u7591\u7684\u5b89\u536b\u8fd8\u662f\u5c06\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u6321\u5728\u95e8\u53e3\uff0c\u4ed6\u4eec\u600e\u4e48\u770b\u90fd\u4e0d\u50cf\u662f\u62e5\u6709\u9ad8\u7ea7\u522b\u8eab\u4efd\u5361\u7684\u4eba\u2014\u2014\u5f20\u606a\u4e0d\u5f97\u4ee5\u6253\u7535\u8bdd\u8ba9\u6a61\u6811\u56ed\u9996\u5e2d\u79d1\u5b66\u5bb6\u8c2d\u4e91\u677e\u4eb2\u81ea\u4e0b\u697c\u6765\u63a5\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u67f3\u5fd7\u6210\u4e5f\u5728\u8fd9\u91cc\uff0c\u4ed6\u4e0e\u8c2d\u4e91\u677e\u4e00\u8d77\u6765\uff0c\u89c1\u9648\u5983\u84c9\u8ddf\u5728\u5f20\u606a\u8eab\u8fb9\uff0c\u4e5f\u6ca1\u6709\u591a\u95ee\u4ec0\u4e48\uff0c\u8ddf\u4ed6\u4eec\u5e74\u8f7b\u5f97\u8fc7\u4efd\u7684\u8001\u677f\u63e1\u4e86\u63e1\u624b\uff0c\u5c31\u5f80\u95e8\u7981\u901a\u9053\u8d70\u53bb\u3002</p><p>\u3000\u3000\u201c\u770b\u6765\u6211\u5bf9\u8fd9\u91cc\u7684\u5173\u5fc3\u592a\u5c11\u4e86\uff0c\u201d\u5f20\u606a\u8d70\u8fc7\u95e8\u7981\u901a\u9053\uff0c\u8b66\u94c3\u6ca1\u6709\u50cf\u4ed6\u62c5\u5fc3\u7684\u90a3\u6837\u523a\u8033\u7684\u54cd\u8d77\uff0c\u8ddf\u8c2d\u4e91\u677e\u611f\u6168\u7684\u8bf4\u4e00\u53e5\uff0c\u8bed\u6c14\u4e00\u8f6c\uff0c\u53c8\u6170\u95ee\u8d77\u8c2d\u4e91\u677e\u3001\u67f3\u5fd7\u6210\u6765\uff0c\u201c\u8f9b\u82e6\u4f60\u4eec\u56fd\u5e86\u5047\u8fd8\u7559\u4e0b\u6765\u5de5\u4f5c\uff0c\u9648\u4fe1\u751f\u544a\u8bc9\u6211\uff0c\u56fd\u5e86\u8282\u90a3\u5929\uff0c\u4f60\u4eec\u8fd8\u5728\u5b9e\u9a8c\u5ba4\u91cc\u8d76\u901a\u5bb5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u95ea\u5b58\u82af\u7247\u7684\u68c0\u6d4b\u901a\u4e0d\u8fc7\uff0c\u6211\u4eec\u8c01\u90fd\u65e0\u6cd5\u5b89\u5fc3\u4f11\u5047\uff0c\u4eba\u8fd8\u4e0d\u5982\u7559\u5728\u5b9e\u9a8c\u5ba4\u91cc\u5fc3\u8e0f\u5b9e\u4e9b\uff0c\u201d\u8c2d\u4e91\u677e\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u6211\u5df2\u7ecf\u8ddf\u7814\u7a76\u4eba\u5458\u8bb8\u8bfa\u4e86\uff0c\u68c0\u6d4b\u901a\u8fc7\u540e\uff0c\u5927\u5bb6\u53ef\u4ee5\u9009\u4e2a\u5730\u65b9\u597d\u597d\u7684\u653e\u677e\u4e00\u4e0b\uff0c\u8d39\u7528\u7531\u9879\u76ee\u7ec4\u4e70\u5355\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u5e94\u8be5\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u90fd\u8ba9\u4e16\u7eaa\u9526\u6e56\u5728\u65b0\u829c\u7ed9\u4f60\u4eec\u9884\u5907\u4e0b\u6700\u597d\u7684\u5ba2\u623f\uff0c\u7b49\u4f60\u4eec\u968f\u65f6\u8fc7\u53bb\uff0c\u201d\u5bf9\u67f3\u5fd7\u6210\u8bf4\u9053\uff0c\u201c\u6b63\u5f0f\u6295\u4ea7\u4e5f\u4e0d\u7528\u6025\u4e8e\u4e00\u65f6\uff0c\u4e2d\u6676\u5fae\u82af\u4e5f\u65e0\u9700\u62fc\u7740\u547d\u53bb\u69a8\u5c3d\u5458\u5de5\u6bcf\u4e00\u6ef4\u8840\u6c57\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u6211\u7684\u8fd9\u79cd\u69a8\u5c3d\u7cbe\u795e\u4f1a\u5f97\u5230\u4f60\u7684\u8d4f\u8bc6\u5462\uff0c\u4f60\u8fd8\u4e0d\u80fd\u7b97\u5408\u683c\u7684\u8d44\u672c\u5bb6\uff0c\u201d\u67f3\u5fd7\u6210\u5f00\u73a9\u7b11\u8bf4\u9053\uff0c\u53c8\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u96c6\u6210\u7535\u8def\u5236\u9020\uff0c\u4e2d\u6676\u5fae\u82af\u8ddf\u7ade\u4e89\u5bf9\u624b\u7684\u5dee\u8ddd\u592a\u5927\u4e86\uff0c\u5f88\u96be\u8f7b\u677e\u4e0b\u6765\u2014\u2014\u6211\u4e5f\u4f1a\u5b89\u6392\u5458\u5de5\u5206\u6279\u4f11\u5047\u7684\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8eab\u4e3a\u4e9a\u6d32\u6700\u5927\u96c6\u6210\u7535\u8def\u5236\u9020\u5546\u53f0\u79ef\u7535\u524d\u9996\u5e2d\u8fd0\u8425\u5b98\u67f3\u5fd7\u6210\u662f\u4e1a\u5185\u516c\u8ba4\u7684\u5efa\u5382\u80fd\u624b\uff0c\u5728\u4ed6\u9886\u5bfc\u7684\u56e2\u961f\u52aa\u529b\u4e0b\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u7b2c\u4e00\u5ea7\u6676\u5706\u5de5\u5382\u6b63\u5f0f\u52a8\u5de5\u5174\u5efa\u624d\u5341\u4e2a\u591a\u6708\uff0c\u4e5d\u6708\u4e2d\u65ec\u5c31\u8fdb\u5165\u8bd5\u751f\u4ea7\u9636\u6bb5\uff1b\u534e\u590fNEC\u7684\u5de5\u5382\u524d\u671f\u7b79\u5907\u5de5\u4f5c\u624d\u5b8c\u6210\uff0c\u9884\u8ba1\u7684\u5efa\u8bbe\u671f\u4e3a\u4e8c\u5341\u4e2a\u6708\uff0c\u4e5f\u5c31\u8981\u62d6\u5230\u4e24\u5343\u5e74\u5e95\u624d\u53ef\u80fd\u4f1a\u5bf9\u4e2d\u6676\u5fae\u82af\u4ea7\u751f\u7ade\u4e89\u2014\u2014\u5f20\u606a\u5f88\u6000\u7591\u534e\u590fNEC\u5230\u96f6\u96f6\u5e74\u5e95\u4e4b\u524d\u80fd\u751f\u4ea7\u51fa\u7b2c\u4e00\u7247\u6676\u5706\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u80fd\u591f\u5b89\u5fc3\u7684\u53bb\u65b0\u829c\u4f11\u5047\uff0c\u67f3\u5fd7\u6210\u7b49\u4eba\u5374\u8981\u4e3a\u4e2d\u6676\u5fae\u82af\u7684\u6b63\u5f0f\u6295\u4ea7\u505a\u51c6\u5907\uff0c\u8111\u5b50\u91cc\u6839\u672c\u6ca1\u6709\u4f11\u5047\u7684\u6982\u5ff5\u3002\u5f20\u606a\u8981\u6c42\u4e2d\u6676\u5fae\u82af\u80fd\u6700\u5148\u751f\u4ea7\u95ea\u5b58\u9897\u7c92\uff0c\u4e0d\u4ec5\u4e2d\u6676\u5fae\u82af\u7684\u4eba\u65e0\u6cd5\u4f11\u5047\uff0c\u6a61\u6811\u56ed\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\u8c2d\u4e91\u677e\u4ee5\u53ca\u8d1f\u8d23\u96c6\u6210\u7535\u8def\u57fa\u7840\u6280\u672f\u7814\u7a76\u7684\u4e3b\u4efb\u5de5\u7a0b\u5e08\u8212\u5f3a\u3001\u738b\u536b\u826f\u3001\u6881\u6e05\u626c\u7b49\u4eba\u4ee5\u53ca\u4ea7\u54c1\u68c0\u6d4b\u4e2d\u5fc3\u7684\u540c\u4ec1\u90fd\u65e0\u6cd5\u6b63\u5e38\u7684\u4f11\u5047\u3002</p><p>\u3000\u3000\u5f20\u606a\u5939\u8d77\u4e00\u679a\u95ea\u5b58\u9897\u7c92\uff0c\u770b\u4e0a\u53bb\u8ddf\u666e\u901a\u7684\u82af\u7247\u6709\u4ec0\u4e48\u533a\u522b\uff0c\u5374\u662f\u4e3a\u4e86\u8fd9\u679a\u82af\u7247\u8ddf\u4e1c\u829d\u524d\u524d\u540e\u540e\u7b97\u8ba1\u4e86\u534a\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u751a\u81f3\u5c06\u9526\u6e56\u6210\u7acb\u4ee5\u6765\u6240\u6709\u7684\u6280\u672f\u5e95\u5b50\u90fd\u62ff\u53bb\u8ddf\u4e1c\u829d\u505a\u4ea4\u6362\uff0c\u624d\u83b7\u5f97\u95ea\u5b58\u82af\u7247\u7684\u5b8c\u6574\u6280\u672f\u3002\u4e5f\u5c31\u5728\u6280\u672f\u4ea4\u6362\u534f\u8bae\u6b63\u5f0f\u751f\u6548\u56db\u4e2a\u6708\u540e\uff0c\u5728\u4e2d\u6676\u5fae\u82af\u7684\u751f\u4ea7\u7ebf\u6210\u529f\u7684\u8bd5\u751f\u4ea7\u51fa\u7b2c\u4e00\u627932M\u50a8\u5b58\u5bb9\u91cf\u7684\u95ea\u5b58\u9897\u7c92\u3002</p><p>\u3000\u3000\u201c\u4eca\u5e74\u7684\u826f\u54c1\u7387\u53ea\u6015\u8fd8\u65e0\u6cd5\u8fbe\u5230\u8ba9\u4eba\u6ee1\u8db3\u7684\u7a0b\u5ea6\u3002\u201d\u67f3\u5fd7\u6210\u89c1\u5f20\u606a\u4e00\u8138\u7684\u5174\u594b\uff0c\u5fcd\u4e0d\u4f4f\u8981\u7ed9\u4ed6\u6253\u4e00\u6253\u9884\u9632\u9488\u3002</p><p>\u3000\u3000\u201c\u9664\u4e86\u62bd\u53d6\u4e00\u5b9a\u6bd4\u4f8b\u7684\u7814\u53d1\u7ecf\u8d39\u5bf9\u534a\u5bfc\u4f53\u5b58\u50a8\u6280\u672f\u8fdb\u884c\u6df1\u5ea6\u7814\u7a76\u4e4b\u5916\uff0c\u9526\u6e56\u5bf9\u95ea\u5b58\u9897\u7c92\u91c7\u8d2d\u4ef7\u683c\u4e0d\u8981\u6c42\u4f4e\u4e8e\u4e1c\u829d\u6216\u4e09\u661f\u7684\u540c\u671f\u4ef7\u683c\uff0c\u201d\u5f20\u606a\u95ee\u67f3\u5fd7\u6210\uff0c\u201c\u8fd9\u6837\uff0c\u4e2d\u6676\u5fae\u82af\u80fd\u5426\u7ef4\u6301\u76c8\u5229\uff1f\u201d</p><p>\u3000\u3000\u201c\u82e5\u662f\u8fd9\u6837\uff0c\u80fd\u7ef4\u6301\u76c8\u5229\u2014\u2014\u6211\u4eec\u826f\u54c1\u7387\u867d\u7136\u4e00\u65f6\u95f4\u65e0\u6cd5\u63d0\u9ad8\u5230\u8ba9\u4eba\u6ee1\u610f\u7684\u7a0b\u5ea6\uff0c\u4f46\u662f\u6210\u672c\u6bd5\u7adf\u8981\u4f4e\u4e8e\u4e09\u661f\u3001\u4e1c\u829d\u8bb8\u591a\u2014\u2014\u4f46\u662f\uff0c\u201d\u67f3\u5fd7\u6210\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4ed6\u7a0d\u677e\u4e00\u53e3\u6c14\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u7231\u8fbe\u7684iplayer\u4e0e\u95ea\u5b58\u76d8\u4e0d\u662f\u6025\u7740\u964d\u4f4e\u6210\u672c\u5f00\u62d3\u66f4\u5927\u7684\u5e02\u573a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6025\uff0c\u4e5f\u4e0d\u4e8e\u6025\u4e8e\u4e00\u65f6\uff0c\u201d\u5f20\u606a\u6324\u7740\u5634\u5507\uff0c\u201c\u6211\u7ed9\u4f60\u4eec\u5b9a\u4e2a\u539f\u5219\uff0c\u5177\u4f53\u7684\u4f60\u627e\u9648\u4fe1\u751f\u4ed6\u4eec\u53bb\u5546\u91cf\u2014\u2014\u5bf9\u4e2d\u6676\u5fae\u82af\uff0c\u6211\u4eec\u7edd\u4e0d\u80fd\u6740\u9e21\u53d6\u5375\uff0c\u6211\u4e5f\u5f88\u671f\u5f85\u4e2d\u6676\u5fae\u82af\u80fd\u5229\u7528\u516d\u5230\u516b\u5e74\u7684\u65f6\u95f4\u6210\u957f\u4e3a\u4e9a\u6d32\u4e00\u6d41\u7684\u96c6\u6210\u7535\u8def\u5236\u9020\u5546\uff0c\u4e3a\u56fd\u5185\u7684\u4fe1\u606f\u4ea7\u4e1a\u5960\u5b9a\u771f\u6b63\u7a33\u56fa\u7684\u57fa\u77f3\u2026\u2026\u201d\u770b\u7740\u5915\u9633\u5149\u8f89\u4ece\u843d\u5730\u73bb\u7483\u900f\u5c04\u8fdb\u6765\uff0c\u5f20\u606a\u770b\u4e86\u770b\u8868\uff0c\u5bf9\u67f3\u5fd7\u6210\u3001\u8c2d\u4e91\u677e\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\uff0c\u7279\u522b\u662f\u4e0b\u9762\u7684\u7814\u7a76\u5458\u4eec\u8981\u6ce8\u610f\u52b3\u9038\u7ed3\u5408\uff0c\u6211\u53ef\u4e0d\u60f3\u65e5\u540e\u8ba9\u4eba\u6307\u8d23\u9526\u6e56\u6210\u529f\u7684\u80cc\u540e\u98d8\u7740\u6d53\u90c1\u7684\u8840\u8165\u5473\u2026\u2026\u201d</p><p>\u3000\u3000\u8c2d\u4e91\u677e\u3001\u67f3\u5fd7\u6210\u90fd\u7b11\u4e86\u7b11\uff0c\u5f20\u606a\u624b\u8d77\u637b\u8d77\u4e00\u679a\u95ea\u5b58\u82af\u7247\uff0c\u95ee\u9053\uff1a\u201c\u6211\u80fd\u7559\u7740\u505a\u7eaa\u5ff5\u54c1\uff1f\u201d\u6ca1\u6709\u8ba9\u4ed6\u4eec\u76f8\u9001\uff0c\u4e0e\u9648\u5983\u84c9\u5f84\u76f4\u4e0b\u697c\u8d70\u51fa\u5b9e\u9a8c\u56ed\uff0c\u5f80\u5b66\u5e9c\u5df7\u6b65\u884c\u800c\u53bb\u3002</p><p>\u3000\u3000\u5728\u5b66\u5e9c\u5df7\u53e3\u4e0a\u770b\u5230\u675c\u98de\u8d70\u5728\u524d\u9762\uff0c\u4ed6\u8eab\u8fb9\u4e24\u4e2a\u5973\u4eba\u7684\u80cc\u666f\u5341\u5206\u7684\u719f\u6089\uff0c\u8eab\u6750\u7a0d\u9ad8\u7684\u5973\u4eba\u6210\u719f\u6027\u611f\uff0c\u62ab\u7740\u6ce2\u6d6a\u5377\u7684\u957f\u53d1\uff0c\u8eab\u6750\u6027\u611f\uff0c\u7a7f\u7740\u725b\u4ed4\u88e4\u7684\u53cc\u817f\u6d51\u5706\u4fee\u957f\uff0c\u7a0d\u77ee\u7684\u5973\u5b69\u5b50\u8eab\u6750\u7ea4\u7ec6\uff0c\u8d70\u8def\u7ed9\u4eba\u4e00\u79cd\u8e2e\u7740\u811a\u5c16\u7684\u611f\u89c9\uff0c\u5341\u5206\u7684\u9752\u6625\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u6084\u6084\u7684\u8ddf\u4e0a\u53bb\u60f3\u770b\u4e2a\u7a76\u7adf\uff0c\u5374\u662f\u6ca1\u6709\u60f3\u5230\u662f\u674e\u829d\u82b3\u4e0e\u5468\u6653\u7490\u6bcd\u5973\u4fe9\uff0c\u5f20\u606a\u5f20\u4e86\u534a\u5929\u7684\u5634\uff0c\u624d\u558a\u4e86\u4e00\u58f0\uff0c\u201c\u674e\u8001\u5e08\u554a\uff0c\u4f60\u600e\u4e48\u5728\u5efa\u90ba\uff1f\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u7ed9\u5f20\u606a\u3001\u9648\u5983\u84c9\u7a81\u7136\u4ece\u540e\u9762\u5192\u51fa\u6765\u5413\u4e86\u4e00\u8df3\uff0c\u62cd\u4e86\u62cd\u80f8\u53e3\uff0c\u7b11\u7740\u95ee\uff1a\u201c\u4f60\u600e\u4e48\u8fd8\u4f1a\u7a81\u7136\u8df3\u51fa\u6765\u5413\u552c\u4eba\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u674e\u8001\u5e08\u2026\u2026\u201d\u9648\u5983\u84c9\u4e5f\u89c4\u89c4\u77e9\u77e9\u7684\u5524\u4e86\u4e00\u58f0\uff0c\u867d\u7136\u674e\u829d\u82b3\u5728\u6d77\u5dde\u4e00\u4e2d\u65f6\u6ca1\u6709\u6559\u8fc7\u5979\uff0c\u4f46\u662f\u674e\u829d\u82b3\u662f\u4e00\u4e2d\u7684\u6559\u52a1\u5904\u4e3b\u4efb\uff0c\u540e\u6765\u517c\u6821\u56e2\u59d4\u4e66\u8bb0\u6307\u5bfc\u5b66\u751f\u4f1a\u7684\u5de5\u4f5c\uff0c\u9648\u5983\u84c9\u4e0e\u5979\u4e0d\u964c\u751f\u3002</p><p>\u3000\u3000\u9752\u6625\u7f8e\u8273\u7684\u5c0f\u841d\u8389\u5468\u6653\u7490\u6b6a\u7740\u5934\u76ef\u7740\u5f20\u606a\u4e0e\u9648\u5983\u84c9\u770b\uff1a\u201c\u675c\u98de\u8bf4\u4f60\u4eca\u5929\u665a\u4e0a\u8131\u4e0d\u5f00\u8eab\uff0c\u4f60\u4eec\u5c31\u7b97\u5fd9\u5f97\u8c08\u604b\u7231\uff0c\u4e5f\u4e0d\u7528\u8eb2\u7740\u4e0d\u89c1\u4eba\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u4f60\u60f3\u7684\u90a3\u6837\u2026\u2026\u201d\u9648\u5983\u84c9\u7c89\u8138\u5fae\u7ea2\u5206\u8fa9\u9053\uff0c\u770b\u4e86\u675c\u98de\u4e00\u773c\uff0c\u89c9\u5f97\u4ed6\u7684\u884c\u8ff9\u771f\u662f\u53ef\u7591\uff0c\u96be\u9053\u8fdb\u5e02\u533a\u524d\u4ed6\u63a5\u5230\u7684\u7535\u8bdd\u662f\u674e\u829d\u82b3\u8001\u5e08\u6253\u7ed9\u4ed6\u7684\uff0c\u4ed6\u53c8\u4f55\u51b5\u9b3c\u9b3c\u795f\u795f\u7684\u4ec0\u4e48\u90fd\u4e0d\u8bf4\u5c31\u6025\u7740\u6e9c\u8d70\uff1f\u4e0d\u77e5\u9053\u8981\u4e0d\u8981\u63ed\u7a7f\u4ed6\u6765\u4e3a\u81ea\u5df1\u8fa9\u89e3\u3002</p><p>\u3000\u3000\u201c\u5468\u5e08\u59b9\u4eca\u5e74\u4e5f\u8bfb\u9ad8\u4e00\u5427\uff1f\u201d\u5f20\u606a\u5fcd\u4e0d\u4f4f\u4f38\u624b\u8981\u53bb\u62cd\u62cd\u5468\u6653\u7490\u7684\u8111\u888b\uff0c\u5468\u6653\u7490\u8111\u888b\u5fae\u5fae\u4e00\u6b6a\uff0c\u5f80\u675c\u98de\u8eab\u8fb9\u9760\u4e86\u9760\uff0c\u5f20\u606a\u5fc3\u91cc\u60f3\u675c\u98de\u5728\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u5b7d\u7f18\u4eca\u751f\u4e5f\u8981\u4e0d\u53ef\u907f\u514d\u7684\u91cd\u6f14\u5417\uff1f</p><p>\u3000\u3000\u201c\u672c\u6765\u6691\u5047\u65f6\u5c31\u7b54\u5e94\u6653\u7490\u6765\u5efa\u90ba\u73a9\u7684\uff0c\u201d\u674e\u829d\u82b3\u7b11\u76c8\u76c8\u7684\u8bf4\u9053\uff0c\u201c\u6691\u5047\u91cc\u504f\u504f\u6ca1\u6709\u80fd\u62bd\u51fa\u65f6\u95f4\u6765\uff0c\u5c31\u8d81\u7740\u56fd\u5e86\u5047\u671f\u966a\u5979\u5230\u5efa\u90ba\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u771f\u662f\u6709\u4e9b\u4e0d\u5de7\u4e86\uff0c\u6211\u521a\u4e0e\u9648\u5983\u84c9\u9001\u5510\u5a67\u4e0a\u98de\u9999\u6e2f\u7684\u98de\u673a\uff0c\u5510\u5a67\u8fd8\u4e00\u76f4\u60f3\u7740\u4f60\u4eec\u5462\uff0c\u201d\u5f20\u606a\u6ca1\u6709\u63ed\u7a7f\u675c\u98de\uff0c\u53c8\u4e0d\u9732\u75d5\u8ff9\u7684\u89e3\u91ca\u4e86\u4e00\u4e0b\u4e0e\u9648\u5983\u84c9\u7684\u5173\u7cfb\uff0c\u514d\u5f97\u5979\u592a\u5c34\u5c2c\uff0c\u201c\u4e00\u8d77\u627e\u5730\u65b9\u5403\u996d\u5427\uff0c\u6211\u6653\u5f97\u675c\u98de\u662f\u8981\u8bf7\u5ba2\u7684\uff0c\u4ed6\u8fdb\u4e1c\u5927\u4e4b\u540e\u5c31\u5f00\u516c\u53f8\uff0c\u674e\u8001\u5e08\u77e5\u9053\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u542c\u675c\u98de\u8bf4\u8fc7\uff0c\u6653\u7490\u5c31\u662f\u95f9\u7740\u8981\u8fc7\u6765\u770b\u770b\u3002\u201d\u674e\u829d\u82b3\u8bf4\u9053\u3002</p><p>\u3000\u3000\u539f\u6765\u675c\u98de\u4e00\u76f4\u8ddf\u674e\u829d\u82b3\u6709\u8054\u7cfb\u7684\uff0c\u5f20\u606a\u77a5\u89c1\u675c\u98de\u51e0\u4e4e\u8981\u627e\u4e2a\u5730\u7f1d\u94bb\u8fdb\u53bb\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u4eec\u5148\u53bb\u53c2\u89c2\u4ed6\u7684\u516c\u53f8\uff0c\u518d\u53bb\u5403\u665a\u996d\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u541b\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u89c1\u674e\u99a8\u4e88\u7a7f\u7740\u70df\u7070\u8272\u7684\u9488\u7ec7\u7d27\u8eab\u886b\u4e0e\u6df1\u84dd\u8272\u4e03\u5206\u88e4\u5728\u5915\u9633\u5149\u91cc\u5174\u594b\u7684\u671d\u81ea\u5df1\u62db\u624b\uff0c\u5de6\u624b\u8fd8\u6367\u7740\u51e0\u672c\u5370\u5c3c\u7684\u56fe\u672c\uff1b\u674e\u99a8\u4e88\u770b\u89c1\u674e\u829d\u82b3\u3001\u9648\u5983\u84c9\u3001\u5468\u6653\u7490\u7b49\u4eba\u90fd\u4e0d\u7ea6\u800c\u540c\u7684\u56de\u8fc7\u5934\u6765\uff0c\u53c8\u7f9e\u6da9\u7684\u6367\u7740\u4e66\u5728\u80f8\u524d\u6b49\u610f\u7684\u5fae\u97a0\u7740\u8eac\uff0c\u6e29\u839e\u800c\u7b11\u3002</p><p>\u3000\u3000\u6e05\u7eaf\u660e\u8273\u7684\u8138\u86cb\u4e0e\u6210\u719f\u6027\u611f\u7684\u8eab\u6750\u8ba9\u674e\u99a8\u4e88\u5728\u9ec4\u660f\u5915\u9633\u4e0b\u6709\u7740\u5f02\u6837\u7684\u5947\u9b45\u4e4b\u7f8e\uff0c\u5728\u5b66\u5e9c\u5df7\u7684\u8857\u5934\u989d\u5916\u7684\u60f9\u4eba\u77a9\u76ee\uff0c\u5f20\u606a\u90fd\u542c\u89c1\u5468\u6653\u7490\u5728\u8eab\u540e\u95ee\u675c\u98de\uff1a\u201c\u8fd9\u4e2a\u5973\u4eba\u662f\u8c01\uff0c\u600e\u4e48\u95fb\u8d77\u6765\u6709\u597d\u91cd\u7684\u68d2\u5b50\u5473\uff1f\u201d\u9648\u5983\u84c9\u90fd\u5fcd\u4e0d\u4f4f\u8981\u7b11\u51fa\u58f0\u6765\uff0c\u5c06\u5468\u6653\u7490\u6402\u5728\u6000\u91cc\uff0c\u5c0f\u58f0\u8ddf\u5979\u8bf4\uff0c\u201c\u5f20\u606a\u62d0\u8fc7\u6765\u7684\u97e9\u56fd\u670b\u53cb\u674e\u99a8\u4e88\u2026\u2026\u201d</p><p>\u3000\u3000\u6709\u5fc5\u8981\u5f3a\u8c03\u4e00\u4e2a\u201c\u62d0\u201d\u5b57\u5417\uff1f\u5f20\u606a\u5fc3\u91cc\u60f3\u7740\uff0c\u5f85\u674e\u99a8\u4e88\u8d70\u8fc7\u6765\uff0c\u7ed9\u5979\u4ecb\u7ecd\u674e\u829d\u82b3\u4e0e\u5468\u6653\u7490\uff1a\u201c\u8fd9\u662f\u6211\u4e0e\u675c\u98de\u8bfb\u4e2d\u5b66\u65f6\u7684\u674e\u829d\u82b3\u8001\u5e08\u8ddf\u5979\u5973\u513f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8bf7\u591a\u591a\u5173\u7167\u2026\u2026\u201d\u674e\u99a8\u4e88\u6e29\u67d4\u7684\u53c8\u97a0\u4e86\u4e00\u4e2a\u8eac\uff0c\u7b11\u5bb9\u6e29\u5b9b\u800c\u751c\u7f8e\uff0c\u914d\u5408\u5979\u6e05\u7eaf\u7684\u8138\u86cb\uff0c\u8ba9\u4eba\u770b\u4e0a\u53bb\u8d4f\u5fc3\u60a6\u76ee\uff0c\u5f20\u606a\u5c31\u559c\u6b22\u5979\u8fd9\u70b9\u3002</p><p>\u3000\u3000\u521b\u57df\u516c\u53f8\u9664\u4e86\u7f51\u5427\u8fd8\u7167\u5e38\u8425\u4e1a\u4e4b\u5916\uff0c\u5176\u4ed6\u90e8\u95e8\u90fd\u653e\u5047\u4f11\u606f\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u53c2\u89c2\u7684\uff0c\u65e2\u7136\u665a\u4e0a\u8981\u4e00\u8d77\u5403\u996d\uff0c\u6ca1\u6709\u9053\u7406\u4e0d\u901a\u77e5\u4e00\u8d77\u8003\u8fdb\u4e1c\u5927\u7684\u540c\u73ed\u540c\u5b66\u8096\u6625\u94ed\u3001\u674e\u5b63\u3001\u8bb8\u82e5\u8679\u7b49\u4eba\u3002\u674e\u5b63\u6709\u5bfb\u547c\u673a\uff0c\u8054\u7cfb\u5230\u4ed6\u65f6\uff0c\u6b63\u4e0e\u5973\u670b\u53cb\u8bb8\u82e5\u8679\u5728\u65b0\u5e02\u8857\u901b\u8857\uff0c\u5f53\u7136\u662f\u7acb\u5373\u5750\u8f66\u8d76\u56de\u5b66\u5e9c\u5df7\u6765\u3002\u5f20\u606a\u6253\u8096\u6625\u94ed\u5bbf\u820d\u7684\u7535\u8bdd\u8054\u7cfb\u4ed6\uff0c\u4ed6\u4e0e\u4ed6\u5973\u670b\u53cb\u5f90\u6885\u6885\u6b63\u597d\u5728\u4ed6\u5bbf\u820d\u91cc\uff0c\u7535\u8bdd\u91cc\u542c\u4e0d\u51fa\u4ed6\u5bbf\u820d\u8fd8\u6709\u5176\u4ed6\u4eba\u5b58\u5728\u7684\u6837\u5b50\uff0c\u4e5f\u4e0d\u7ba1\u8fd9\u5c0f\u5b50\u9886\u7740\u5973\u670b\u53cb\u8fd9\u65f6\u5019\u7a9d\u5728\u5bbf\u820d\u91cc\u505a\u4ec0\u4e48\uff0c\u8ba9\u4ed6\u4eec\u8d76\u7d27\u6536\u62fe\u6574\u9f50\u51fa\u6765\u5403\u996d\uff0c\u52a0\u4e0a\u5b59\u9759\u6aac\u3001\u674e\u99a8\u4e88\u3001\u9648\u5983\u84c9\uff0c\u6ee1\u6ee1\u5f53\u5f53\u7684\u6324\u4e86\u4e00\u684c\u5403\u996d\uff0c\u5f88\u662f\u70ed\u95f9\u3002</p></article>"}}}]);
