(window.webpackJsonp=window.webpackJsonp||[]).push([[916],{939:function(p){p.exports={chapterTitle:null,title:"\u7b2c735\u7ae0 \u4e3a\u4f55\u4e00\u62d6\u518d\u62d6",content:"<article><h2>\u7b2c735\u7ae0 \u4e3a\u4f55\u4e00\u62d6\u518d\u62d6</h2><p>\u3000\u3000\u5728\u97e9\u3001\u65e5\u8bbe\u7acb\u7684\u4e24\u5bb6\u5168\u8d44\u5b50\u516c\u53f8\uff0c\u76ee\u524d\u7684\u4e1a\u52a1\u8303\u56f4\u5f88\u72ed\u9698\uff0c\u89c4\u6a21\u4e5f\u5f88\u53d7\u9650\u5236\uff0c\u4f46\u662f\u9526\u6e56\u8ba1\u5212\u5728\u672a\u6765\u5c06\u5bf9\u97e9\u3001\u5bf9\u65e5\u7684\u4e1a\u52a1\u90fd\u5e76\u5165\u4e24\u5bb6\u5b50\u516c\u53f8\u7684\u6846\u67b6\u4e4b\u4e0b\uff0c\u90a3\u5b50\u516c\u53f8\u7684\u89c4\u6a21\u4e0e\u5730\u4f4d\u5c06\u9661\u7136\u63d0\u9ad8\u3002\u9648\u4fca\u8f89\u3001\u8d75\u6587\u715c\u5728\u8fc7\u53bb\u5f88\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u5c31\u5bf9\u9526\u6e56\u6709\u6bd4\u8f83\u6df1\u5165\u7684\u4e86\u89e3\uff0c\u8fd9\u662f\u7531\u4e8e\u4ed6\u4eec\u521a\u52a0\u5165\u9526\u6e56\u5c31\u83b7\u4efb\u91cd\u8981\u804c\u52a1\u7684\u7f18\u6545\uff0c\u9526\u6e56\u5d1b\u8d77\u7684\u5149\u8f89\u8ba9\u4ed6\u4eec\u8fd9\u4e9b\u719f\u6089\u5f53\u4ee3\u516c\u53f8\u5236\u5ea6\u4e0e\u8fd0\u884c\u89c4\u5f8b\u7684\u4eba\u90fd\u8981\u4e3a\u4e4b\u8d5e\u53f9\u3002\u4ed6\u4eec\u90fd\u5f88\u6e05\u695a\uff0c\u4ed6\u4eec\u4e4b\u6240\u4ee5\u521a\u52a0\u5165\u9526\u6e56\u5c31\u83b7\u4efb\u8981\u804c\uff0c\u5728\u4e8e\u9526\u6e56\u6781\u5ea6\u7f3a\u4e4f\u6d77\u5916\u6269\u5f20\u7684\u4eba\u624d\u3002\u5f53\u9526\u6e56\u5728\u6d77\u5916\u5e02\u573a\u8fd0\u8425\u6709\u4e00\u6bb5\u65f6\u95f4\u4e4b\u540e\uff0c\u8fd9\u79cd\u6025\u8feb\u5c06\u4f1a\u5f97\u5230\u7f13\u89e3\uff0c\u90a3\u4ed6\u4eec\u7684\u4f5c\u7528\u4e5f\u5c06\u4e0d\u662f\u65e0\u53ef\u66ff\u4ee3\u3002\u8981\u60f3\u771f\u6b63\u7684\u878d\u5165\u9526\u6e56\u83b7\u5c45\u9ad8\u4f4d\uff0c\u8981\u52aa\u529b\u7684\u5de5\u4f5c\uff0c\u5c55\u793a\u624d\u80fd\uff0c\u53d1\u6325\u5e94\u6709\u7684\u4f5c\u7528\uff0c\u5f53\u7136\uff0c\u4f3c\u4e4e\u83b7\u5f97\u773c\u524d\u8fd9\u4e2a\u9752\u5e74\u7684\u8ba4\u540c\u66f4\u91cd\u8981\u4e00\u4e9b\u3002\u5f53\u7136\uff0c\u7ecf\u8fc7\u521d\u6b65\u7684\u4ea4\u6d41\uff0c\u8981\u60f3\u5f97\u5230\u8ba4\u540c\uff0c\u4e5f\u53ea\u6709\u52aa\u529b\u7684\u5de5\u4f5c\u5c55\u793a\u624d\u80fd\u53d1\u6325\u5e94\u6709\u7684\u4f5c\u7528\u4e00\u6761\u8def\u53ef\u4ee5\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u9648\u4fca\u8f89\u5728\u65e5\u672c\u7d22\u5c3c\u3001\u8d75\u6587\u715c\u5728\u97e9\u56fd\u73b0\u4ee3\u663e\u793a\u6280\u672f\u682a\u5f0f\u4f1a\u793e\u7684\u4efb\u804c\u7ecf\u5386\u5f88\u611f\u5174\u8da3\uff0c\u65e5\u672c\u7d22\u5c3c\u662f\u9526\u6e56\u8981\u8ffd\u8d76\u7684\u76ee\u6807\uff0c\u97e9\u56fd\u73b0\u4ee3\u663e\u793a\u5219\u521a\u4ece\u65e5\u672c\u5f15\u8fdb\u7b2c\u4e09\u4ee3TFT\u6db2\u6676\u5c4f\u7684\u751f\u4ea7\u6280\u672f\uff0c\u4e5f\u6295\u5165\u76f8\u5f53\u5927\u7684\u4eba\u529b\u3001\u7269\u529b\u8fdb\u884c\u7b2c\u56db\u4ee3TFT\u6db2\u6676\u5c4f\u7684\u7814\u53d1\uff0c\u5929\u6653\u5f97\u5f20\u606a\u5bf9TFT\u6db2\u6676\u663e\u793a\u6280\u672f\u662f\u591a\u4e48\u7684\u773c\u998b\uff0c\u8fd9\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u672a\u6765\u6d88\u8d39\u7535\u5b50\u4ea7\u4e1a\u6700\u91cd\u8981\u7684\u4e00\u5757\u5e02\u573a\u3002</p><p>\u3000\u3000\u867d\u7136\u53f0\u6e7e\u3001\u97e9\u56fd\u591a\u5bb6\u7535\u5b50\u4f01\u4e1a\u90fd\u4ece\u65e5\u672c\u5f15\u8fdbTFT\u6db2\u6676\u5c4f\u751f\u4ea7\u6280\u672f\uff0c\u4f46\u662f\u6700\u4e3a\u5173\u952e\u7684\uff0c\u5728\u8fd9\u4e48\u591a\u5bb6\u7535\u5b50\u4f01\u4e1a\u4e2d\uff0c\u73b0\u4ee3\u663e\u793a\u6280\u672f\u682a\u5f0f\u4f1a\u793e\u7684\u6bcd\u516c\u53f8\u6b63\u7ecf\u5386\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5e26\u6765\u7684\u5de8\u632b\u800c\u9677\u5165\u4e3e\u6b65\u552f\u8270\u7684\u56f0\u5883\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u82e5\u662f\u5386\u53f2\u8fd8\u5faa\u7740\u5927\u81f4\u7684\u8f68\u8ff9\u524d\u884c\uff0c\u73b0\u4ee3\u663e\u793a\u5e94\u8be5\u4f1a\u5f88\u5feb\u4ece\u4ed6\u7684\u6bcd\u516c\u53f8\u8131\u79bb\u51fa\u6765\u843d\u5165\u503a\u6743\u94f6\u884c\u7684\u624b\u4e2d\u3002\u5bf9\u4e8e\u503a\u6743\u94f6\u884c\u6765\u8bf4\uff0c\u4e0d\u4f1a\u6709\u5c06\u4f01\u4e1a\u597d\u597d\u7ecf\u8425\u4e0b\u53bb\u7684\u610f\u613f\uff0c\u9009\u62e9\u5408\u9002\u7684\u4e70\u5bb6\u51fa\u552e\u5c3d\u53ef\u80fd\u5f25\u8865\u635f\u5931\u624d\u662f\u6700\u9996\u5148\u8981\u8003\u8651\u7684\u3002</p><p>\u3000\u3000\u53ef\u60dc\u554a\uff0c\u9526\u6e56\u6b64\u65f6\u8fd8\u6ca1\u6709\u8db3\u591f\u7684\u8d22\u529b\u5c06\u73b0\u4ee3\u663e\u793a\u4e00\u53e3\u541e\u4e0b\uff0c\u5c31\u7b97\u80fd\u7b79\u63aa\u5230\u8db3\u591f\u7684\u8d44\u91d1\uff0c\u8981\u5c06\u8fd9\u4e48\u4e00\u4e2a\u5e9e\u7136\u5927\u7269\u5438\u6536\u6d88\u5316\u4e5f\u7edd\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u5f88\u53ef\u80fd\u4f1a\u4e00\u4e0d\u5c0f\u5fc3\u7ed9\u8fd9\u4e2a\u5e9e\u7136\u5927\u7269\u62d6\u7d2f\uff0c\u8ba9\u6574\u4e2a\u9526\u6e56\u90fd\u9677\u5165\u5371\u9669\u7684\u8fb9\u7f18\u3002\u4e9a\u6d32\u7ecf\u6d4e\u52bf\u6001\u5728\u4e5d\u4e5d\u5e74\u4e4b\u524d\u4e0d\u4f1a\u5f7b\u5e95\u7684\u597d\u8f6c\uff0c\u503a\u6743\u94f6\u884c\u5728\u6b64\u4e4b\u524d\u4e5f\u4e0d\u53ef\u80fd\u627e\u5230\u5408\u9002\u7684\u4e70\u5bb6\uff0c\u5f20\u606a\u4e0d\u4f1a\u6025\u7740\u8fd9\u65f6\u5019\u5c31\u5bf9\u73b0\u4ee3\u663e\u793a\u4e0b\u624b\u3002</p><p>\u3000\u3000\u4e0d\u6025\u7740\u4e0b\u624b\uff0c\u4f46\u662f\u9526\u6e56\u8fd8\u662f\u6709\u4e9b\u5de5\u4f5c\u53ef\u4ee5\u79ef\u6781\u53bb\u505a\u7684\u3002\u73b0\u4ee3\u663e\u793a\u9677\u5165\u8d22\u52a1\u4e0a\u7684\u56f0\u5883\uff0c\u88ab\u8feb\u5bf9\u5458\u5de5\u91c7\u9093\u964d\u85aa\u88c1\u5458\u7684\u63aa\u65bd\uff0c\u5f20\u606a\u8981\u5728\u97e9\u56fd\u7684\u5b50\u516c\u53f8\u5c3d\u53ef\u80fd\u7684\u53bb\u5438\u6536\u73b0\u4ee3\u663e\u793a\u6280\u672f\u682a\u5f0f\u4f1a\u793e\u7684\u5de5\u7a0b\u6280\u672f\u4e0e\u7814\u53d1\u4eba\u5458\u3002</p><p>\u3000\u3000\u8fd9\u662f\u62bd\u7a7a\u6218\u672f\uff0c\u5de5\u7a0b\u6280\u672f\u4e0e\u7814\u53d1\u4eba\u5458\u6c38\u8fdc\u90fd\u662f\u4f18\u8d28\u7684\u8d44\u4ea7\uff0c\u6240\u4ee5\u8981\u5c3d\u53ef\u80fd\u5229\u7528\u5408\u6cd5\u7684\u624b\u6bb5\u5c06\u8fd9\u4e9b\u4f18\u8d28\u8d44\u4ea7\u5148\u62bd\u79bb\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6682\u65f6\u4e0d\u4f1a\u5c06\u4ed6\u5185\u5fc3\u7684\u60f3\u6cd5\u5766\u9732\u51fa\u6765\uff0c\u53ea\u89e3\u91ca\u9053\uff1a\u201c\u7231\u8fbe\u6676\u5c4f\u7535\u5b50\u5728STN\u6db2\u6676\u663e\u793a\u6280\u672f\u4e0a\u6709\u8bb8\u591a\u9700\u8981\u52a0\u5f3a\u7684\u5730\u65b9\uff0c\u73b0\u4ee3\u663e\u793a\u5728\u8fd9\u4e0a\u9762\u6709\u503c\u5f97\u6211\u4eec\u501f\u9274\u7684\u5730\u65b9\uff0c\u82e5\u662f\u53ef\u884c\uff0c\u5728\u97e9\u56fd\u53ef\u4ee5\u6210\u7acb\u4e00\u4e2a\u4e13\u95e8\u7684\u5b9e\u9a8c\u5ba4\u6765\u52a0\u5f3a\u4e00\u4e0bSTN\u6db2\u6676\u663e\u793a\u6280\u672f\u7684\u7814\u7a76\u2026\u2026\u201d</p><p>\u3000\u3000STN\u4e5f\u662f\u6db2\u6676\u663e\u793a\u6280\u672f\u7684\u4e00\u79cd\uff0c\u53ea\u4e0d\u8fc7\u662f\u4f4e\u6863\u7684\u4e00\u79cd\uff0c\u50cf\u5bfb\u547c\u673a\u7684\u9ed1\u767d\u663e\u793a\u5c4f\u591a\u91c7\u7528STN\u6db2\u6676\u6280\u672f\uff1b\u6db2\u6676\u663e\u793a\u5668\u3001\u6db2\u6676\u7535\u89c6\u4ee5\u53ca\u624b\u673a\u7b49\u591a\u79cd\u7535\u5b50\u4ea7\u54c1\u7684\u5f69\u8272\u663e\u793a\u5c4f\u5374\u7edd\u5927\u90e8\u5206\u90fd\u91c7\u7528TFT\u6db2\u6676\u6280\u672f\u2014\u2014\u4e24\u8005\u5728\u6280\u672f\u4e0e\u751f\u4ea7\u4e0a\u7684\u6295\u8d44\u5dee\u8ddd\u8981\u5dee\u4e0a\u4e24\u4e2a\u6570\u91cf\u7ea7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u4e2d\u5348\u4e4b\u524d\u62b5\u8fbe\u5efa\u90ba\uff0c\u4ed6\u4eec\u4e5f\u76f4\u63a5\u5230\u516c\u5bd3\u6765\u7528\u9910\u3002</p><p>\u3000\u3000\u516c\u5bd3\u8fd9\u51e0\u5929\u4f1a\u7528\u6765\u62db\u5f85\u5bb4\u5ba2\uff0c\u62db\u5f85\u5de5\u4f5c\u6709\u4e13\u95e8\u7684\u53a8\u5e08\u4e0e\u5de5\u4f5c\u4eba\u5458\u6765\u5904\u7406\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u8fd9\u6bb5\u65f6\u95f4\u6bcf\u4e2a\u6708\u90fd\u98de\u65e5\u672c\u3001\u97e9\u56fd\u4e24\u4e09\u6b21\uff0c\u4ed6\u4eec\u65e9\u5c31\u4e0e\u9648\u4fca\u8f89\u3001\u8d75\u6587\u715c\u6709\u8fc7\u8f83\u6df1\u7684\u63a5\u89e6\uff0c\u8d76\u5230\u9752\u5e74\u516c\u5bd3\uff0c\u9996\u5148\u662f\u95ee\u4ed6\u4eec\u4e24\u4eba\uff1a\u201c\u600e\u4e48\u6837\uff0c\u6709\u6ca1\u6709\u5927\u5403\u4e00\u60ca\uff1f\u201d</p><p>\u3000\u3000\u9648\u4fca\u8f89\u3001\u8d75\u6587\u715c\u54c2\u7136\u4e00\u7b11\uff0c\u8fd9\u4e2a\u95ee\u9898\u5f53\u7740\u5f20\u606a\u7684\u9762\u8fd8\u6709\u4e9b\u96be\u56de\u7b54\u3002\u8981\u8bf4\u6ca1\u6709\u5927\u5403\u4e00\u60ca\u90a3\u662f\u9a97\u4eba\uff1b\u8981\u8bf4\u6709\u5927\u5403\u4e00\u60ca\uff0c\u4f1a\u4e0d\u4f1a\u663e\u5f97\u6709\u4e9b\u4e0d\u591f\u5c0a\u91cd\uff1f</p><p>\u3000\u3000\u9648\u4fe1\u751f\u54c8\u54c8\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fd9\u65f6\u5019\u8ddf\u4ed6\u6253\u4ea4\u9053\uff0c\u606a\u5c11\u597d\u6b79\u90fd\u8fdb\u5927\u5b66\u4e86\uff1b\u6211\u521a\u8ddf\u4ed6\u6253\u4ea4\u9053\u65f6\uff0c\u4ed6\u8fd8\u5728\u8bfb\u9ad8\u4e00\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fca\u8f89\u3001\u8d75\u6587\u715c\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u8ba4\u771f\u53bb\u60f3\u8fc7\u9526\u6e56\u8d77\u521d\u8131\u80ce\u6210\u5f62\u65f6\u5927\u8001\u677f\u624d\u662f\u5341\u516d\u5c81\u7684\u5c11\u5e74\u2014\u2014\u771f\u662f\u8ba9\u4eba\u96be\u4ee5\u63a5\u53d7\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u671d\u9648\u4fe1\u751f\u644a\u7740\u624b\u8bf4\u9053\uff1a\u201c\u6ca1\u5fc5\u8981\u8feb\u4e0d\u53ca\u5f85\u7684\u63ed\u6211\u7684\u8001\u5e95\uff0c\u201d\u6307\u7740\u8096\u664b\u6210\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u8fd8\u662f\u5148\u542c\u542c\u6d77\u5916\u8fd0\u8425\u90e8\u8fd9\u6bb5\u65f6\u95f4\u6765\u7684\u5de5\u4f5c\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u664b\u6210\u662f\u7231\u8fbe\u96c6\u56e2\u6d77\u5916\u8fd0\u8425\u90e8\u603b\u88c1\uff0c\u5927\u9879\u76ee\u90e8\u6210\u5458\u4e4b\u4e00\uff0c\u8fd9\u6bb5\u65f6\u95f4\u9664\u4e86\u534f\u52a9\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u5728\u6d77\u5916\u8fdb\u884c\u6280\u672f\u6269\u5f20\u3001\u62db\u52df\u4f18\u79c0\u7684\u534e\u4eba\u79d1\u5b66\u5bb6\u4e0e\u5de5\u7a0b\u5e08\u4e4b\u5916\uff0c\u8fd8\u534f\u52a9\u9526\u6e56\u5728\u6d77\u5916\u7684\u5143\u5668\u4ef6\u4ee3\u5de5\u4e0e\u91c7\u8d2d\u4e1a\u52a1\u3002\u50cfi19\u3001i08\u4e24\u6b3e\u624b\u673a\uff0c\u5373\u4f7f\u9526\u6e56\u638c\u63e1\u4e86\u76f8\u5173\u6280\u672f\uff0c\u4f46\u662f\u9650\u4e8e\u56fd\u5185\u4ee5\u53ca\u9526\u6e56\u81ea\u8eab\u7684\u5236\u9020\u5de5\u827a\u6c34\u5e73\uff0c\u6709\u4e9b\u7684\u5143\u5668\u4ef6\u8fd8\u9700\u8981\u5728\u59d4\u6258\u65b0\u52a0\u5761\u3001\u53f0\u6e7e\u3001\u65e5\u97e9\u7684\u7535\u5b50\u5de5\u5382\u4ee3\u4e3a\u52a0\u5de5\u3002\u9526\u6e56\u53f7\u79f0i08\u624b\u673a\u56fd\u4ea7\u5316\u7387\u8fbe\u523080\uff05\uff0c\u8fd8\u662f\u6709\u76f8\u5f53\u591a\u7684\u6c34\u4efd\uff0c\u53ea\u80fd\u8bf4\u6280\u672f\u638c\u63e1\u7a0b\u5ea6\u8fbe\u523080\uff05\uff0c\u6bd5\u7adf\u8981\u5f62\u6210\u5b8c\u6574\u7684\u7535\u5b50\u4ea7\u4e1a\u4f53\u7cfb\u7edd\u4e0d\u662f\u80fd\u4e00\u8e74\u800c\u5c31\u7684\u4e8b\u60c5\u3002\u5728\u8fd9\u4e2a\u8fc7\u7a0b\u5f53\u4e2d\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u7684\u5408\u4f5c\u6700\u591a\uff0c\u9526\u6e56\u7edd\u5927\u591a\u6570\u7684\u82af\u7247\u90fd\u662f\u59d4\u6258\u5fb7\u4eea\u4ee3\u5de5\uff0c\u8fd9\u4e00\u5207\u8981\u7b49\u4e2d\u6676\u5fae\u82af\u4e0e\u6d77\u5dde\u3001\u5efa\u90ba\u7684\u6574\u4e2a\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u4f53\u7cfb\u5efa\u7acb\u8d77\u6765\u4e4b\u540e\u624d\u4f1a\u5f97\u5230\u6839\u672c\u6027\u7684\u6539\u5584\u3002\u6709\u4e9b\u6280\u672f\u6c34\u5e73\u8ddf\u4e0d\u4e0a\u7684\u96f6\u914d\u4ef6\uff0c\u8fd8\u9700\u8981\u76f4\u63a5\u5411\u6d77\u5916\u7535\u5b50\u5382\u5546\u91c7\u8d2d\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u8096\u664b\u6210\u8fd8\u6709\u4e00\u9879\u91cd\u8981\u7684\u804c\u8d23\u5c31\u662f\u7b79\u5907\u4ee3\u53f7M-1\u7684MP3\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u6d77\u5916\u4e0a\u5e02\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000MP3\u64ad\u653e\u5668\uff0c\u9526\u6e56\u65e9\u5c31\u5df2\u7ecf\u5177\u5907\u76f8\u5bf9\u6210\u719f\u7684\u6280\u672f\uff0c\u6d77\u5916\u4e0a\u5e02\u7684\u5de5\u4f5c\u5374\u4e00\u62d6\u518d\u62d6\uff0c\u73b0\u5728\u90fd\u662f\u5341\u4e8c\u6708\u5e95\u4e86\uff0c\u8fd8\u6ca1\u6709\u6700\u7ec8\u786e\u5b9a\u6d77\u5916\u4e0a\u5e02\u7684\u65f6\u95f4\u3002</p><p>\u3000\u3000\u201cM-1\u5f3a\u5927\u7684\u751f\u547d\u529b\u4e0e\u5e7f\u9614\u7684\u5e02\u573a\u524d\u666f\uff0c\u6211\u4eec\u90fd\u6df1\u4fe1\u4e0d\u7591\uff0c\u5b8c\u5168\u53ef\u4ee5\u8bf4\u662f\u4e00\u9879\u5177\u6709\u91cc\u7a0b\u7891\u610f\u4e49\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u5728\u5168\u7403\u8303\u56f4\u5185\uff0c\u9526\u6e56\u8fd8\u662f\u4e00\u5bb6\u540d\u4e0d\u89c1\u7ecf\u4f20\u7684\u516c\u53f8\u2026\u2026\u201d\u8096\u664b\u6210\u8bf4\u5230\u8fd9\u91cc\uff0c\u7a0d\u505c\u987f\u4e00\u4e0b\u770b\u4e86\u5f20\u606a\u4e00\u773c\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u70b9\u81ea\u77e5\u4e4b\u660e\u8fd8\u662f\u6709\u7684\uff0c\u542c\u5230\u8fd9\u79cd\u8bdd\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e0d\u9002\u5e94\uff0c\u5c06\u4f60\u4eec\u53ec\u96c6\u8fc7\u6765\uff0c\u96be\u9053\u6211\u662f\u4e3a\u4e86\u542c\u4f60\u4eec\u963f\u8c00\u5949\u627f\u4e0d\u6210\uff1f\u201d\u5927\u5bb6\u90fd\u54c8\u54c8\u4e00\u7b11\uff0c\u5f20\u606a\u7ee7\u7eed\u8bf4\u9053\uff0c\u201cM-1\u662f\u6b3e\u5168\u65b0\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u7231\u8fbe\u5728\u6d77\u5916\u5e02\u573a\u8fd8\u662f\u5168\u65b0\u7684\u3001\u6ca1\u6709\u4efb\u4f55\u54c1\u724c\u77e5\u540d\u5ea6\u79ef\u7d2f\u7684\u516c\u53f8\uff0c\u5e02\u573a\u63a8\u5e7f\u5de5\u4f5c\u7684\u96be\u5ea6\u5f88\u96be\u53bb\u9884\u6d4b\u5230\u5e95\u6709\u591a\u96be\uff0c\u7279\u522b\u662f\u6d77\u5916\u5e02\u573a\u7684\u5546\u4e1a\u73af\u5883\u4e0e\u56fd\u5185\u5b8c\u5168\u4e0d\u4e00\u6837\uff0c\u5728\u56fd\u5185\u81ea\u4ee5\u4e3a\u5f88\u6f02\u4eae\u7684\u5e02\u573a\u8425\u9500\u624b\u6bb5\u62ff\u5230\u6d77\u5916\u5e02\u573a\u4e0a\u53bb\u5c31\u6709\u5982\u513f\u620f\uff0c\u5728\u4e13\u5229\u6280\u672f\u4e0e\u64ad\u653e\u5185\u5bb9\u7684\u77e5\u8bc6\u4ea7\u6743\u4fdd\u62a4\u65b9\u9762\uff0c\u4e5f\u9700\u8981\u66f4\u52a0\u4e25\u683c\u4e0e\u7f1c\u5bc6\u7684\u53bb\u5bf9\u5f85\u3002\u9526\u6e56\u8981\u53c2\u4e0e\u5168\u7403\u7ade\u4e89\uff0c\u8fd9\u65f6\u5019\u5c31\u8981\u9075\u5b88\u4ed6\u4eec\u5236\u5b9a\u7684\u6e38\u620f\u89c4\u5219\u2026\u2026\u8fd9\u4e9b\u4e1c\u897f\uff0c\u6211\u5b8c\u5168\u660e\u767d\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u56fd\u5185\u8bb8\u591a\u6210\u529f\u7684\u4f01\u4e1a\u5bb6\uff0c\u5bf9\u6d77\u5916\u5e02\u573a\u5546\u4e1a\u73af\u5883\u7684\u8ba4\u8bc6\u5f88\u6d45\u8584\uff0c\u5374\u6709\u7740\u5230\u6d77\u5916\u5e02\u573a\u6380\u98ce\u9f13\u6d6a\u7684\u91ce\u5fc3\u3002\u8dd1\u5230\u522b\u4eba\u7684\u5730\u76d8\u4e0a\u5374\u4e0d\u719f\u6089\u522b\u4eba\u7684\u6e38\u620f\u89c4\u5219\uff0c\u5f88\u96be\u60f3\u8c61\u4f1a\u6709\u597d\u7684\u7ed3\u5c40\uff0c\u6240\u5e78\u9526\u6e56\u5728\u8fd9\u65b9\u9762\u6ca1\u6709\u592a\u5927\u7684\u62c5\u5fe7\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\u5bf9\u6b64\u4e5f\u6709\u6781\u5927\u7684\u611f\u89e6\uff0c\u9648\u4fe1\u751f\u6b63\u5927\u52a0\u76df\u9526\u6e56\u7684\u65f6\u95f4\u662f\u5728\u4eca\u5e74\u4e09\u6708\uff0c\u751a\u81f3\u6bd4\u4ed6\u90fd\u8981\u665a\uff0c\u7ec4\u5efa\u7231\u8fbe\u96c6\u56e2\uff0c\u82cf\u6d25\u4e1c\u5bf9\u9526\u6e56\u7684\u5d1b\u8d77\u53d1\u6325\u51fa\u81f3\u5173\u91cd\u8981\u7684\u4f5c\u7528\uff0c\u5176\u624d\u534e\u3001\u80fd\u529b\u4e5f\u6781\u4e3a\u8000\uff0c\u4f46\u662f\u6700\u540e\u820d\u82cf\u6d25\u4e1c\u800c\u7528\u9648\u4fe1\u751f\u62c5\u4efb\u96c6\u56e2\u603b\u88c1\uff0c\u56e0\u4e3a\u9648\u4fe1\u751f\u662f\u66f4\u9002\u5408\u9526\u6e56\u63a8\u52a8\u5168\u7403\u6218\u7565\u7684\u4eba\u9009\u3002\u6ca1\u6709\u9648\u4fe1\u751f\uff0c\u5f88\u96be\u60f3\u8c61\u9526\u6e56\u4f1a\u5728\u4eca\u5e74\u7684\u5168\u7403\u6280\u672f\u6269\u5f20\u6218\u7565\u4e2d\u53d6\u5f97\u5982\u6b64\u8f89\u714c\u7684\u6210\u5c31\uff0c\u8fd9\u79cd\u7528\u4eba\u7684\u9b44\u529b\u4e0e\u6c14\u5ea6\uff0c\u4e5f\u662f\u9526\u6e56\u8ba9\u4eba\u503c\u5f97\u5c0a\u656c\u4e0e\u5411\u5f80\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u5f53\u9526\u6e56\u62ff\u51fa\u8fd9\u6b3e\u4ea7\u54c1\u63a8\u5411\u5e02\u573a\uff0c\u6211\u4eec\u7684\u6d77\u5916\u540c\u884c\u4eec\u5c06\u80fd\u591f\u5229\u7528\u4ed6\u4eec\u7684\u6e38\u620f\u89c4\u5219\u5236\u9020\u8db3\u591f\u591a\u7684\u9677\u9631\u963b\u6b62\u6211\u4eec\u4ece\u8fd9\u6b3e\u5177\u6709\u91cc\u7a0b\u7891\u610f\u4e49\u7684\u65b0\u4ea7\u54c1\u4e2d\u83b7\u53d6\u6211\u4eec\u5e94\u5f97\u7684\u5229\u6da6\uff0c\u8fd9\u70b9\u51e0\u4e4e\u662f\u7edd\u5bf9\u80af\u5b9a\u7684\u3002\u5c31\u7b97\u6211\u4eec\u7684\u7ade\u4e89\u5bf9\u624b\u90fd\u5f88\u6709\u2018\u516c\u5e73\u7ade\u4e89\u2019\u7684\u7cbe\u795e\uff0c\u4ec5\u51ed\u6211\u4eec\u81ea\u5df1\u7684\u52aa\u529b\uff0c\u8981\u5c06M-1\u901a\u8fc7\u5e02\u573a\u5316\u8fd0\u4f5c\u9700\u8981\u591a\u5c11\u65f6\u95f4\u624d\u80fd\u8ba9\u5b83\u6210\u4e3a\u97f3\u9891\u64ad\u653e\u5668\u7684\u4e3b\u6d41\uff0c\u4e00\u5e74\u3001\u4e24\u5e74\uff0c\u6216\u8005\u66f4\u957f\u7684\u65f6\u95f4\uff1f\u8fd9\u4e48\u957f\u7684\u65f6\u95f4\u8db3\u591f\u8ba9\u6211\u4eec\u7684\u7ade\u4e89\u5bf9\u624b\u5f7b\u5e95\u7684\u89e3\u6790\u6211\u4eec\u7684\u4ea7\u54c1\u6280\u672f\u2018\u590d\u5236\u2019\u51fa\u7c7b\u4f3c\u7684\u7535\u5b50\u4ea7\u54c1\u51fa\u6765\u3002\u6240\u4ee5\u6211\u5b81\u53ef\u524d\u9762\u7684\u5de5\u4f5c\u505a\u5f97\u7ec6\u81f4\u4e00\u4e9b\uff0c\u8fd9\u6837\u4e5f\u6709\u65f6\u95f4\u4e3a\u6211\u4eec\u7684\u7ade\u4e89\u5bf9\u624b\u8fdb\u5165\u8fd9\u4e2a\u5e02\u573a\u5236\u9020\u6210\u66f4\u591a\u7684\u969c\u788d\u4e0e\u9677\u9631\u2026\u2026\u201d</p></article>"}}}]);
