(window.webpackJsonp=window.webpackJsonp||[]).push([[918],{941:function(p){p.exports={chapterTitle:null,title:"\u7b2c737\u7ae0 \u65af\u9ad8\u67cf\u7684\u91ce\u5fc3",content:"<article><h2>\u7b2c737\u7ae0 \u65af\u9ad8\u67cf\u7684\u91ce\u5fc3</h2><p>\u3000\u3000\u201c\u6211\u5728\u5e74\u521d\u5c31\u5411\u516c\u53f8\u8463\u4e8b\u4f1a\u5f3a\u70c8\u5efa\u8bae\u5728\u4e2d\u56fd\u6210\u7acb\u6295\u8d44\u516c\u53f8\u53d1\u5c55\u5728\u534e\u7684\u4e1a\u52a1\uff0c\u53ef\u60dc\u5f88\u5c11\u6709\u4eba\u80fd\u6e05\u6670\u7684\u770b\u5230\u4e2d\u56fd\u5e02\u573a\u8f89\u714c\u7684\u672a\u6765\uff0c\u90fd\u524a\u5c16\u4e86\u8111\u888b\u53ea\u60f3\u5728\u6b27\u7f8e\u5730\u533a\u51fa\u4eba\u5934\u5730\uff0c\u201d\u827e\u9ed8\u4e0e\u5f20\u606a\u6324\u5230\u4e00\u90e8\u8f66\u91cc\uff0c\u8f66\u961f\u521a\u521a\u542f\u52a8\u5f80\u5e02\u533a\u884c\u9a76\uff0c\u4ed6\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u62b1\u6028\u8d77\u6765\uff0c\u201c\u8981\u662f\u80fd\u6709\u9526\u6e56\u4e09\u5206\u4e4b\u4e00\u7684\u8fc5\u6377\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u62d6\u62d6\u62c9\u62c9\u5230\u4eca\u65e5\u624d\u51b3\u5b9a\u5728\u6210\u7acb\u6295\u8d44\u516c\u53f8\u6765\u8d1f\u8d23\u5728\u534e\u4e1a\u52a1\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u7740\u827e\u9ed8\u5728\u8f66\u53a2\u91cc\u5927\u58f0\u7684\u62b1\u6028\uff0c\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u7684\u8bdd\u91cc\u6709\u51e0\u5206\u77eb\u60c5\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u6240\u8bf4\u7684\u201c\u4e09\u5206\u4e4b\u4e00\u7684\u8fc5\u6377\u201d\u662f\u600e\u4e48\u8ba1\u7b97\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u79cd\u770b\u4f3c\u803f\u76f4\u7684\u6027\u683c\u4f1a\u5f88\u8ba8\u4eba\u559c\u6b22\uff0c\u8fd9\u5f80\u5f80\u662f\u4ed6\u964d\u4f4e\u8c08\u5224\u5bf9\u624b\u5fc3\u7406\u9632\u7ebf\u7684\u6709\u6548\u624b\u6bb5\u3002</p><p>\u3000\u3000\u201c\u65af\u9ad8\u67cf\u8981\u5728\u4e2d\u56fd\u6210\u7acb\u6295\u8d44\u516c\u53f8\uff1f\u201d\u5f20\u606a\u6545\u4f5c\u8be7\u5f02\u7684\u8bf4\u9053\uff0c\u201c\u65af\u9ad8\u67cf\u9500\u7ed9\u56fd\u5185\u789f\u673a\u5382\u5546\u7684SVCD\u89e3\u7801\u82af\u7247\u7531\u4ee3\u7406\u5546\u4ee3\u4e3a\u8f9b\u52b3\uff0c\u6240\u8c13\u7684\u6280\u672f\u652f\u6301\u5de5\u4f5c\u4e5f\u5341\u5206\u7684\u8f7b\u95f2\uff0c\u6709\u5fc5\u8981\u6210\u7acb\u516c\u53f8\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u827e\u9ed8\u6307\u7740\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u5c31\u5e78\u707e\u4e50\u7978\u7684\u5e0c\u671b\u6211\u4eec\u5728\u4e13\u4e1a\u5316\u7ade\u4e89\u7684\u601d\u8def\u4e0a\u94bb\u725b\u89d2\u5c16\u6c38\u8fdc\u90fd\u8d70\u4e0d\u51fa\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e24\u79cd\u5e02\u573a\u73af\u5883\uff0c\u4e24\u79cd\u8fd0\u8425\u6a21\u5f0f\uff0c\u5b70\u4f18\u5b70\u52a3\u8fd8\u4e0d\u4f1a\u9616\u68fa\u5b9a\u8bba\uff0c\u600e\u4e48\u80fd\u8bf4\u662f\u94bb\u725b\u89d2\u5c16\uff1f\u201d\u5f20\u606a\u5766\u7136\u8bf4\u9053\uff0c\u201c\u5f53\u7136\uff0c\u65af\u9ad8\u67cf\u51b3\u5b9a\u5728\u4e2d\u56fd\u6210\u7acb\u6295\u8d44\u516c\u53f8\uff0c\u6269\u5927\u5728\u534e\u4e1a\u52a1\uff0c\u6211\u4eec\u662f\u4e50\u4e8e\u770b\u5230\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4e4b\u524d\u65af\u9ad8\u67cf\u7ba1\u7406\u5c42\u52a8\u4f5c\u7f13\u6162\uff0c\u4e0d\u662f\u4ed6\u4eec\u4e0d\u91cd\u89c6\u4e2d\u56fd\u4ee5\u53ca\u5e7f\u5927\u65b0\u5174\u5730\u533a\u7684\u5e02\u573a\uff0c\u800c\u662f\u65af\u9ad8\u67cf\u7684\u7ba1\u7406\u5c42\u5c06\u65af\u9ad8\u67cf\u5b9a\u4f4d\u5728\u6570\u5b57\u89c6\u9891\u82af\u7247\u4e0e\u6280\u672f\u89e3\u51b3\u65b9\u6848\u4f9b\u5e94\u5546\u7684\u89d2\u8272\u4e0a\uff0c\u5176\u4e4b\u524d\u5728\u534e\u4e1a\u52a1\u5c31\u5982\u5f20\u606a\u6240\u8bf4\u7684\u90a3\u6837\u53ea\u9700\u8981\u529e\u4e8b\u5904\u7ea7\u522b\u7684\u529e\u4e8b\u673a\u6784\u5c31\u80fd\u80dc\u4efb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u5386\u53f2\u4e0a\u65af\u9ad8\u67cf\u7684\u53d1\u5c55\u8f68\u8ff9\u4e0d\u662f\u5f88\u6e05\u695a\uff0c\u65e2\u7136\u65af\u9ad8\u67cf\u51b3\u5b9a\u5728\u4e2d\u56fd\u6210\u7acb\u6295\u8d44\u516c\u53f8\uff0c\u6269\u5927\u5728\u534e\u4e1a\u52a1\u8303\u56f4\uff0c\u8fd9\u5df2\u7ecf\u4e0d\u540c\u4e8e\u4e00\u822c\u6b27\u7f8e\u516c\u53f8\u4e13\u4e1a\u5316\u7ade\u4e89\u7684\u8fd0\u8425\u601d\u7ef4\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u5728\u6570\u5b57\u89c6\u9891\u82af\u7247\u4e0a\u7684\u6280\u672f\u582a\u4e0eTI\u6bd4\u80a9\uff0c\u5728\u8fd9\u4e2a\u5c42\u6b21\u4e0a\uff0c\u9526\u6e56\u5bf9\u65af\u9ad8\u67cf\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u5e2e\u52a9\u3002\u65e2\u7136\u4e0d\u80fd\u8ba9\u522b\u4eba\u6709\u6c42\u4e8e\u81ea\u5df1\uff0c\u90a3\u60f3\u4ece\u522b\u4eba\u90a3\u91cc\u5f97\u5230\u597d\u5904\u7684\u53ef\u80fd\u6027\u81ea\u7136\u4e5f\u5c31\u5f88\u5c0f\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u6269\u5927\u4e1a\u52a1\u8303\u56f4\u3001\u6709\u610f\u8fdb\u5165\u65b0\u7684\u4e1a\u52a1\u9886\u57df\uff0c\u8fd9\u81ea\u7136\u662f\u5f20\u606a\u4e50\u610f\u770b\u5230\u53d8\u5316\uff0c\u8fd9\u610f\u5473\u7740\u9526\u6e56\u53ef\u4ee5\u5728\u66f4\u5e7f\u9614\u7684\u9886\u57df\u4e0e\u65af\u9ad8\u67cf\u5f00\u5c55\u66f4\u5bc6\u5207\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u8f66\u7a97\u5916\u7684\u5bd2\u98ce\u51db\u51bd\uff0c\u5f20\u606a\u7b49\u7740\u827e\u9ed8\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff0c\u827e\u9ed8\u5374\u6536\u4f4f\u4e86\u8bdd\u5323\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b49\u4e86\u4e00\u4f1a\u513f\u6ca1\u89c1\u827e\u9ed8\u5f00\u53e3\uff0c\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u89c1\u4ed6\u5634\u89d2\u4f3c\u7b11\u975e\u7b11\u7684\u7fd8\u8d77\u6765\u8fd8\u62ff\u634f\u8d77\u59ff\u6001\u6765\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u54ed\u7b11\u4e0d\u5f97\uff0c\u8bf4\u9053\uff1a\u201c\u65af\u9ad8\u67cf\u9009\u62e9\u8fd9\u4e2a\u65f6\u673a\u5728\u4e2d\u56fd\u6210\u7acb\u6295\u8d44\u516c\u53f8\uff0c\u60f3\u5fc5\u662f\u5728DVD\u7f16\u89e3\u7801\u5668\u65b9\u9762\u53d6\u5f97\u7a81\u7834\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u827e\u9ed8\u5f20\u4e86\u5f20\u5634\uff0c\u8be7\u5f02\u7684\u8bf4\u9053\uff0c\u201c\u6211\u7684\u8868\u60c5\u5e94\u8be5\u4e0d\u53ef\u80fd\u900f\u9732\u8fd9\u4e48\u591a\u7684\u6d88\u606f\u5427\uff0c\u4f60\u600e\u4e48\u4f1a\u4e00\u4e0b\u5b50\u5c31\u731c\u5230\u8981\u70b9\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8981\u4e0d\u662f\u5728\u8fd9\u91cc\u6324\u7709\u5f04\u773c\u3001\u88c5\u795e\u5f04\u9b3c\uff0c\u6211\u8fd8\u4e0d\u4f1a\u5f80\u90a3\u65b9\u9762\u53bb\u60f3\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5728\u65e2\u5b9a\u7684\u5386\u53f2\u8f68\u8ff9\u4e0a\uff0c\u65af\u9ad8\u67cf\u4e00\u76f4\u662f\u5168\u7403\u89c6\u9891\u89e3\u7801\u82af\u7247\u6700\u4e3b\u8981\u7684\u4f9b\u5e94\u5546\u4e4b\u4e00\uff0c\u5176\u89c6\u9891\u89e3\u7801\u82af\u7247\u7684\u5e94\u7528\u5305\u62ecVCD\u3001SVCD\u3001DVD\u3001\u6570\u5b57\u7535\u89c6\u7b49\u4e00\u7cfb\u5217\u7684\u89c6\u9891\u64ad\u653e\u8bbe\u5907\u3002\u65af\u9ad8\u67cf\u89e3\u51b3DVD\u7f16\u89e3\u7801\u5668\u7684\u6280\u672f\u96be\u9898\u5e94\u8be5\u662f\u8fdf\u65e9\u7684\u4e8b\u60c5\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u5bf9\u4e8e\u65af\u9ad8\u67cf\u6765\u8bf4\uff0c\u80fd\u8ba9\u4ed6\u4eec\u4e3b\u52a8\u627e\u9526\u6e56\u7684\uff0c\u6700\u6709\u53ef\u80fd\u5c31\u662f\u5728DVD\u5f71\u789f\u673a\u4e0a\u7684\u5408\u4f5c\u4e86\u3002</p><p>\u3000\u3000\u90fd\u8ba9\u5f20\u606a\u968f\u53e3\u731c\u4e2d\u4e86\uff0c\u827e\u9ed8\u81ea\u7136\u4e0d\u518d\u6545\u5f04\u7384\u865a\uff0c\u76f4\u63a5\u76f8\u544a\uff1a\u201c\u73b0\u5728\u6ca1\u6709\u54ea\u5bb6\u516c\u53f8\u5ba3\u79f0\u62e5\u6709DVD\u789f\u673a\u5168\u90e8\u7684\u4e13\u5229\u6280\u672f\uff0c\u6700\u7ec8\u90fd\u53ea\u80fd\u4ea4\u53c9\u6388\u6743\u4f7f\u7528\u76f8\u5173\u4e13\u5229\u6280\u672f\u3002\u5f88\u591a\u76f8\u5173\u4e13\u5229\u6280\u672f\u90fd\u6ca1\u6709\u8fc7\u5341\u516b\u4e2a\u6708\u7684\u4e13\u5229\u4fdd\u5bc6\u671f\uff0c\u4e5f\u5c31\u4e0d\u6e05\u695a\u6700\u7ec8\u7684\u4e13\u5229\u6280\u672f\u5206\u5e03\u60c5\u51b5\u3002\u6211\u4eec\u5728\u7f16\u89e3\u7801\u5668\u65b9\u9762\u53d6\u5f97\u4e00\u4e9b\u7a81\u7834\uff0c\u60f3\u5fc5\u6cf0\u5fd2\u5149\u7535\u7814\u7a76\u9662\u5728\u673a\u82af\u4e0a\u4e5f\u6709\u4e00\u5b9a\u7684\u6210\u5c31\u5427\u3002\u5f53\u7136\uff0c\u6bd4\u8d77\u98de\u5229\u6d66\u3001\u7d22\u5c3c\u8fd9\u4e9b\u516c\u53f8\u5728DVD\u789f\u673a\u7814\u53d1\u4e0a\u7684\u6295\u5165\u4ee5\u53ca\u53ef\u80fd\u53d6\u5f97\u7684\u6210\u5c31\uff0c\u6211\u4eec\u4e24\u5bb6\u516c\u53f8\u90fd\u6709\u5f88\u5927\u7684\u4e0d\u5982\u3002\u8981\u662f\u6211\u4eec\u4e24\u5bb6\u516c\u53f8\u4e0d\u80fd\u8054\u76df\u8d77\u6765\uff0c\u5206\u522b\u5728\u4e0e\u5176\u4ed6\u516c\u53f8\u8fdb\u884c\u4ea4\u53c9\u8bb8\u53ef\u6388\u6743\u8c08\u5224\u65f6\uff0c\u5c31\u4f1a\u53d7\u5230\u6253\u538b\uff0c\u8bf4\u4e0d\u5b9a\u8fd8\u4f1a\u7ed9\u6392\u65a5\u5728\u6700\u7ec8\u7684DVD\u4ea7\u54c1\u5e02\u573a\u4e4b\u5916\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5f20\u4e86\u5f20\u5634\uff0c\u60f3\u4e0d\u5230\u65af\u9ad8\u67cf\u516c\u53f8\u7adf\u7136\u662f\u60f3\u76f4\u63a5\u8fdb\u5165\u7ec8\u7aef\u7684\u4ea7\u54c1\u5e02\u573a\uff0c\u800c\u4e0d\u518d\u6ee1\u8db3\u5176\u6570\u5b57\u89c6\u9891\u82af\u7247\u4f9b\u5e94\u5546\u7684\u89d2\u8272\uff0c\u4e0d\u7136\u4ed6\u4eec\u53ea\u9700\u638c\u63e1\u7f16\u89e3\u7801\u5668\u65b9\u9762\u7684\u4e13\u5229\u6280\u672f\u5c31\u8db3\u591f\u4e86\uff0c\u6839\u672c\u4e0d\u9700\u8981\u4e0e\u5176\u4ed6\u7535\u5b50\u5382\u5546\u8fdb\u884c\u4ea4\u53c9\u6388\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u6c89\u541f\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4e5f\u4e0d\u7792\u4f60\u8bf4\uff0c\u6cf0\u5fd2\u5728\u673a\u82af\u4e0a\u7684\u786e\u53d6\u5f97\u4e00\u4e9b\u4e13\u5229\u6280\u672f\uff0c\u4f46\u662f\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u662f\u9526\u6e56\u4e0e\u5fb7\u4eea\u5408\u8d44\u800c\u6210\uff0c\u5404\u6301\u534a\u6570\u7684\u80a1\u6743\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e4b\u524d\u6ca1\u6709\u4e0e\u5fb7\u4eea\u5728\u8fd9\u65b9\u9762\u8fdb\u884c\u63a5\u89e6\uff1f\u4f60\u4e0e\u5fb7\u5c14\u6cd5\u897f\u5148\u751f\u540c\u4e58\u4e00\u67b6\u98de\u673a\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u5c31\u6ca1\u6709\u60f3\u8d77\u6765\u8981\u5148\u4e0e\u4ed6\u5148\u6c9f\u901a\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u827e\u9ed8\u8bf4\u9053\uff1a\u201c\u5fb7\u4eea\u8fdb\u5165\u7ec8\u7aef\u6d88\u8d39\u5e02\u573a\u6ca1\u6709\u4ec0\u4e48\u5174\u8da3\uff0c\u6211\u60f3\u53ea\u8981\u4e0d\u635f\u5bb3\u5230\u5fb7\u4eea\u7684\u5229\u76ca\uff0c\u5fb7\u4eea\u4f1a\u4e50\u610f\u8ba9\u9526\u6e56\u4e3b\u5bfc\u6cf0\u5fd2\u5149\u7535\u6280\u672f\u7814\u7a76\u9662\u7684\u8fd0\u8425\u65b9\u5411\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u9526\u6e56\u5728SVCD\u789f\u673a\u4e0a\u7684\u8bb8\u591a\u5e94\u7528\u4e0e\u8f6f\u4ef6\u4e13\u5229\u6280\u672f\u540c\u6837\u53ef\u4ee5\u9002\u7528\u5230DVD\u789f\u673a\u3002\u6211\u4eec\u65e2\u7136\u8981\u8054\u5408\u8d77\u6765\uff0c\u81ea\u7136\u5e0c\u671b\u7b79\u7801\u8bb8\u591a\u8d8a\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u9759\u5fc3\u60f3\u4e86\u60f3\uff0c\u6309\u7167\u65e2\u5b9a\u7684\u5386\u53f2\u8f68\u8ff9\u53d1\u5c55\uff0cDVD\u4e13\u5229\u6280\u672f\u8054\u76df\u8981\u7b49\u5230\u4e5d\u4e5d\u5e74\u5e74\u4e2d\u624d\u4f1a\u6b63\u5f0f\u6210\u7acb\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u5404DVD\u4e13\u5229\u6280\u672f\u62e5\u6709\u5382\u5546\u90fd\u5404\u81ea\u4e3a\u9635\u3002\u5728\u4f17\u591a\u62e5\u6709DVD\u4e13\u5229\u6280\u672f\u7684\u7535\u5b50\u5382\u5546\u4e2d\u95f4\uff0c\u9526\u6e56\u4e0e\u65af\u9ad8\u67cf\u8d77\u6b65\u8f83\u665a\uff0c\u5728\u62e5\u6709\u4e13\u5229\u6280\u672f\u6570\u91cf\u4e0e\u5173\u952e\u6027\u4e13\u5229\u6280\u672f\u65b9\u9762\u90fd\u8981\u5904\u4e8e\u4e00\u5b9a\u7684\u5f31\u52bf\uff0c\u5148\u8054\u5408\u8d77\u6765\u5012\u4e0d\u5931\u4e3a\u626d\u8f6c\u5f31\u52bf\u5730\u4f4d\u7684\u660e\u667a\u4e4b\u4e3e\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u9080\u8bf7\u827e\u9ed8\u4e00\u884c\u4eba\u5230\u5efa\u90ba\u6765\uff0c\u9664\u4e86\u8c03\u6574\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u4e09\u65b9\u5728SVCD\u89e3\u7801\u82af\u7247\u4e0a\u7684\u5408\u4f5c\u6a21\u5f0f\u4e4b\u5916\uff0c\u4e3b\u8981\u662f\u8c08\u5bfb\u6c42\u4e09\u65b9\u5728MP3\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u4e0a\u7684\u5408\u4f5c\u53ef\u80fd\uff0c\u6ca1\u60f3\u5230\u827e\u9ed8\u521a\u5230\u5efa\u90ba\u5c31\u4e3b\u52a8\u63d0\u5230\u5728DVD\u789f\u673a\u4e0a\u8fdb\u884c\u76f8\u5173\u7684\u5408\u4f5c\uff0c\u7b97\u662f\u610f\u5916\u7684\u6536\u83b7\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u534a\u4e2a\u5c0f\u65f6\u7684\u8f66\u7a0b\u5c31\u62b5\u8fbe\u4e1c\u534e\u95e8\u5185\u7684\u5e0c\u5c14\u987f\u5927\u9152\u5e97\uff0c\u62b5\u8fbe\u9152\u5e97\u4e4b\u540e\u5c31\u51c6\u5907\u7528\u9910\u3002</p><p>\u3000\u3000\u5728\u7a7a\u4e2d\u98de\u884c\u4e86\u5341\u51e0\u4e2a\u5c0f\u65f6\uff0c\u5fb7\u5c14\u6cd5\u897f\u4e0e\u827e\u9ed8\u4e00\u884c\u4eba\u8fd8\u6ca1\u6709\u5c06\u65f6\u5dee\u5012\u8fc7\u6765\uff0c\u4eba\u90fd\u5f88\u75b2\u5026\uff0c\u7528\u8fc7\u665a\u9910\u4e4b\u540e\uff0c\u5728\u9152\u5e97\u4f1a\u5ba4\u5385\u53ea\u7b80\u5355\u7684\u4ea4\u8c08\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u5b89\u6392\u4ed6\u4eec\u8fdb\u5165\u623f\u95f4\u4f11\u606f\u3002</p><p>\u3000\u3000\u5e0c\u5c14\u987f\u9152\u5e97\u662f\u8fd9\u6b21\u5546\u52a1\u4f1a\u8bae\u7684\u4e3b\u8981\u573a\u6240\uff0c\u5728\u6b64\u671f\u95f4\uff0c\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u3001\u8096\u664b\u6210\u7b49\u4eba\u4e5f\u5165\u4f4f\u5e0c\u5c14\u987f\u9152\u5e97\u3002\u65f6\u95f4\u5c1a\u65e9\uff0c\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u3001\u8096\u664b\u6210\u4e09\u4eba\u90fd\u805a\u5230\u9648\u4fe1\u751f\u623f\u95f4\u91cc\u8ba8\u8bba\u4e8b\u60c5\uff0c\u5f20\u606a\u5c06\u827e\u9ed8\u5728\u4ece\u98de\u573a\u56de\u5e02\u533a\u8def\u4e0a\u8868\u8fbe\u8981\u5408\u4f5c\u7684\u610f\u601d\u544a\u8bc9\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u65af\u9ad8\u67cf\u4e5f\u60f3\u76f4\u63a5\u751f\u4ea7\u789f\u673a\u8fdb\u5165\u7ec8\u7aef\u5e02\u573a\u554a\uff1f\u201d\u4e01\u69d0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u4e5f\u6e05\u695a\u80af\u5b9a\u6709\u5176\u4ed6\u516c\u53f8\u5728DVD\u7f16\u89e3\u7801\u6280\u672f\u4e0a\u53d6\u5f97\u7a81\u7834\uff0c\u4e0d\u518d\u662f\u4ed6\u4eec\u4e00\u679d\u72ec\u79c0\uff0c\u6240\u4ee5\u4e0d\u53ef\u80fd\u590d\u5236\u4ed6\u4eec\u6b64\u65f6\u5728\u89e3\u7801\u82af\u7247\u5e02\u573a\u4e0a\u7684\u66b4\u5229\u6a21\u5f0f\u3002\u8fdb\u5165\u6700\u7ec8\u7684\u4ea7\u54c1\u5e02\u573a\uff0c\u4e5f\u662f\u907f\u514d\u4e1a\u7ee9\u6ed1\u5761\u7684\u4e00\u79cd\u8feb\u4e0d\u5f97\u5df2\u7684\u52aa\u529b\u2026\u2026\u201d\u9648\u4fe1\u751f\u7565\u52a0\u601d\u8003\u5c31\u5927\u4f53\u80fd\u63a8\u6d4b\u51fa\u65af\u9ad8\u67cf\u7684\u5546\u4e1a\u610f\u56fe\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u65af\u9ad8\u67cf\u60f3\u8fdb\u5165\u7ec8\u7aef\u6d88\u8d39\u5e02\u573a\uff0c\u90a3\u4ed6\u4eec\u672a\u6765\u7684\u5e02\u573a\u4f18\u52bf\u5e94\u8be5\u5728\u6b27\u7f8e\u5730\u533a\uff0c\u90a3\u6211\u4eec\u662f\u4e0d\u662f\u53ef\u4ee5\u501f\u52a9\u65af\u9ad8\u67cf\u5c06MP3\u64ad\u653e\u5668\u6253\u5165\u6b27\u7f8e\u5e02\u573a\uff0c\u5c06MP3\u64ad\u653e\u5668\u7684\u4ea7\u54c1\u6280\u672f\u62c6\u5f00\u6765\u6388\u6743\u7ed9\u4ed6\u4eec\u4f7f\u7528\uff1f\u201d</p><p>\u3000\u3000\u6839\u636e\u53cd\u5784\u65ad\u56fd\u9645\u516c\u7ea6\uff0c\u7981\u6b62\u4e13\u5229\u6743\u7684\u6ee5\u7528\uff0c\u8981\u5f3a\u5236\u8bb8\u53ef\u7684\u8981\u6c42\uff0c\u4e13\u5229\u6280\u672f\u5fc5\u987b\u6388\u6743\u7b2c\u4e09\u65b9\u4f7f\u7528\uff0c\u907f\u514d\u4e00\u5bb6\u516c\u53f8\u501f\u52a9\u4e13\u5229\u6280\u672f\u5784\u65ad\u5e02\u573a\u3002</p><p>\u3000\u3000\u9526\u6e56\u8981\u53c2\u4e0e\u5168\u7403\u7ade\u4e89\uff0c\u5c31\u8981\u9075\u5b88\u6e38\u620f\u89c4\u5212\uff0c\u5728MP3\u64ad\u653e\u5668\u6b63\u5f0f\u63a8\u5411\u5e02\u573a\u4e4b\u524d\uff0c\u8981\u5148\u5c06\u76f8\u5173\u4e13\u5229\u6280\u672f\u6388\u6743\u7ed9\u7b2c\u4e09\u65b9\u4f7f\u7528\u3002\u6388\u6743\u65f6\u95f4\u662f\u62d6\u7684\u8d8a\u665a\u8d8a\u597d\uff0c\u81f3\u5c11\u5728\u9526\u6e56\u5c06MP3\u64ad\u653e\u5668\u63a8\u5411\u5e02\u573a\u4e4b\u521d\u4e0d\u4f1a\u51fa\u73b0\u7ade\u4e89\u8005\uff1b\u6388\u6743\u5bf9\u8c61\u7684\u9009\u62e9\u4e5f\u5f88\u8003\u7a76\uff0c\u65e2\u8981\u5c3d\u53ef\u80fd\u7684\u5728\u6388\u6743\u8fc7\u7a0b\u4e2d\u62ff\u4e13\u5229\u6280\u672f\u6362\u4e13\u5229\u6280\u672f\u4e92\u901a\u6709\u65e0\u2014\u2014\u6536\u53d6\u8bb8\u53ef\u8d39\u7528\u5012\u662f\u5c11\u6570\uff0c\u53c8\u8981\u9632\u6b62\u5bf9\u65b9\u6210\u4e3a\u81ea\u5df1\u7684\u76f4\u63a5\u7ade\u4e89\u8005\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u9526\u6e56\u9996\u6b21\u6d89\u53ca\u6d77\u5916\u5e02\u573a\uff0c\u65e0\u8bba\u5c06\u76f8\u5173\u6280\u672f\u6388\u6743\u7ed9\u54ea\u5bb6\u4f01\u4e1a\uff0c\u90fd\u4f1a\u8ba9\u8fd9\u5bb6\u4f01\u4e1a\u6210\u4e3a\u9526\u6e56\u7684\u76f4\u63a5\u7ade\u4e89\u8005\u3002\u6388\u6743\u7ed9\u5b9e\u529b\u5f31\u5c0f\u7684\u516c\u53f8\uff0c\u8fd8\u8981\u8003\u8651\u8fd9\u5bb6\u516c\u53f8\u4f1a\u5f88\u5bb9\u6613\u7ed9\u5176\u4ed6\u5927\u516c\u53f8\u517c\u5e76\u3002</p><p>\u3000\u3000\u542c\u8096\u664b\u6210\u8fd9\u4e48\u5efa\u8bae\uff0c\u5f20\u606a\u542c\u540e\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u524d\u671f\u7684\u76ee\u6807\u662f\u65e5\u3001\u97e9\u3001\u65b0\u52a0\u5761\u3001\u53f0\u6e7e\u7b49\u4e9a\u6d32\u53d1\u8fbe\u5730\u533a\u7684\u5e02\u573a\uff0c\u6b27\u7f8e\u5e02\u573a\u662f\u6211\u4eec\u773c\u4e0b\u529b\u4e0d\u4ece\u5fc3\u7684\u5730\u65b9\uff0c\u5c06\u6b64\u65f6\u7684\u6b27\u7f8e\u5e02\u573a\u8ba9\u7ed9\u65af\u9ad8\u67cf\u53bb\u5f00\u53d1\uff0c\u4e5f\u672a\u5c1d\u4e0d\u662f\u597d\u7684\u9009\u62e9\u2026\u2026\u201d</p></article>"}}}]);
