(window.webpackJsonp=window.webpackJsonp||[]).push([[920],{943:function(p){p.exports={chapterTitle:null,title:"\u7b2c917\u7ae0 \u5f20\u606a\u5077\u9e21\u4e0d\u6210",content:"<article><h2>\u7b2c917\u7ae0 \u5f20\u606a\u5077\u9e21\u4e0d\u6210</h2><p>\u3000\u3000\u65e0\u8bba\u79d1\u738b\u8fd8\u662f\u534e\u590f\u7535\u5b50\uff0c\u5f20\u606a\u90fd\u4e0d\u4f1a\u5c06\u5176\u89c6\u4e3a\u9526\u6e56\u672a\u6765\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u4f46\u662f\u53c8\u4e0d\u80fd\u5426\u8ba4\u6709\u4fe1\u606f\u4ea7\u4e1a\u90e8\u505a\u9760\u5c71\u7684\u534e\u590f\u7535\u5b50\u4ee5\u53ca\u6b64\u65f6\u5438\u5f15NEC\u3001\u534e\u590f\u7535\u5b50\u6ce8\u8d44\u7684\u79d1\u738b\u7684\u786e\u80fd\u7ed9\u9526\u6e56\u5236\u9020\u4e9b\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u5f20\u606a\u518d\u662f\u5584\u826f\u8f6f\u5f31\u7684\u4eba\uff0c\u4e5f\u4e0d\u4f1a\u51ed\u534e\u590f\u7535\u5b50\u3001\u79d1\u738b\u8df3\u4e0a\u8df3\u4e0b\u7684\u62db\u4eba\u5acc\uff0c\u4f46\u662f\u4e5f\u4e0d\u60f3\u4e3a\u6b64\u5927\u8d39\u5468\u7ae0\uff0c\u9526\u6e56\u7684\u529b\u91cf\u4e0d\u80fd\u968f\u610f\u7684\u5206\u6563\uff0c\u6276\u6301\u79d1\u738b\u9ad8\u79d1\u3001\u65b0\u5143\u7535\u5668\u7b49\u4f01\u4e1a\u66f4\u6709\u5229\u4e8e\u4ee5\u540e\u7684\u5e03\u5c40\u2014\u2014\u5f53\u7136\uff0c\u5bf9\u9648\u9759\u7684\u96be\u5904\uff0c\u5f20\u606a\u4e5f\u4e0d\u80fd\u88c5\u4f5c\u89c6\u800c\u4e0d\u89c1\u3002</p><p>\u3000\u3000\u542c\u9648\u9759\u8bf4\u534e\u590f\u7535\u5b50\u4e0e\u79d1\u738b\u90fd\u5728\u65bd\u52a0\u538b\u529b\u8981\u5979\u4eec\u653e\u5f03\u5bf9\u79d1\u738b\u7684\u54c1\u724c\u4f7f\u7528\u6743\uff0c\u5f20\u606a\u76b1\u7740\u7709\u5934\u601d\u91cf\u4e86\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u867d\u8bf4\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4eca\u5e74\u4e0d\u4f1a\u5bf9\u5185\u8d44\u4f01\u4e1a\u53d1\u5c55\u624b\u673a\u751f\u4ea7\u724c\u7167\uff0c\u5374\u4e0d\u662f\u8bf4\u4e0d\u53ef\u4ee5\u7533\u62a5\uff0c\u79d1\u738b\u9ad8\u79d1\u4eca\u5e74\u5c31\u63d0\u4ea4\u7533\u62a5\u6750\u6599\uff0c\u8ba9\u79d1\u738b\u9ad8\u79d1\u5728\u624b\u673a\u751f\u4ea7\u3001\u9500\u552e\u9886\u57df\u62e5\u6709\u79d1\u738b\u54c1\u724c\u4f7f\u7528\u6743\u5728\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5185\u90e8\u89c6\u4e3a\u9ed8\u8ba4\u7684\u4e8b\u5b9e\uff0c\u6211\u4f1a\u8ddf\u6613\u526f\u90e8\u957f\u989d\u5916\u77e5\u4f1a\u4e00\u58f0\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u70b9\u70b9\u5934\uff0c\u5728\u8bb8\u601d\u7684\u9762\u524d\uff0c\u5012\u662f\u4e0d\u4fbf\u5bf9\u5f20\u606a\u6d41\u9732\u51fa\u591a\u5f3a\u70c8\u7684\u611f\u60c5\u6765\uff0c\u5750\u5728\u90a3\u91cc\u53c8\u968f\u610f\u7684\u804a\u4e86\u4e00\u4f1a\u5929\uff0c\u8fc7\u4e86\u5341\u70b9\uff0c\u867d\u8bf4\u5496\u5561\u5385\u3001\u9152\u5427\u8fd8\u771f\u70ed\u95f9\uff0c\u7cbe\u54c1\u5e97\u5374\u90fd\u9646\u7eed\u7ed3\u675f\u4e00\u5929\u7684\u8425\u4e1a\uff0c\u8857\u4e0a\u8fd8\u6709\u6563\u6b65\u7684\u9752\u5e74\u7537\u5973\uff0c\u5165\u591c\u91cc\u62e5\u6324\u7684\u4eba\u6d41\u5df2\u7ecf\u6563\u5931\u4e0d\u89c1\uff0c\u89c1\u5f20\u606a\u4e0e\u8bb8\u601d\u8fd8\u6709\u665a\u6674\u4e5f\u6ca1\u6709\u8981\u79bb\u5f00\u7684\u610f\u601d\uff0c\u9648\u9759\u5fc3\u91cc\u89c9\u5f97\u5947\u602a\uff0c\u4e5f\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c31\u4e0e\u8c22\u5b50\u5609\u544a\u8f9e\u79bb\u5f00\u3002</p><p>\u3000\u3000\u79bb\u5f00\u65f6\u8fd8\u591a\u770b\u4e86\u8bb8\u601d\u8fd9\u4e2a\u5996\u5a9a\u7edd\u7f8e\u7684\u5973\u4eba\u4e00\u773c\uff0c\u5fc3\u60f3\u5f20\u606a\u591c\u91cc\u4f1a\u4e0e\u5979\u5728\u4e00\u8d77\u5427\uff0c\u5fc3\u91cc\u6cdb\u8d77\u96be\u4ee5\u540d\u72b6\u7684\u60c5\u7eea\u3002</p><p>\u3000\u3000\u9648\u9759\u4e0e\u8c22\u5b50\u5609\u79bb\u5f00\u4e4b\u540e\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u3001\u8bb8\u601d\u4e5f\u7ed3\u8d26\u79bb\u5f00\uff0c\u8bb8\u601d\u90a3\u5904\u5b85\u5b50\u5728\u4e39\u4e95\u5df7\u7684\u5317\u5df7\uff0c\u5317\u5df7\u4e3b\u8981\u96c6\u4e2d\u4e86\u6c11\u5c45\u3001\u535a\u7269\u9986\u4e0e\u65c5\u820d\uff0c\u8857\u5df7\u90fd\u4fdd\u5b88\u4e86\u6e05\u672b\u5b98\u5b85\u7684\u98ce\u8c8c\uff0c\u6253\u5f00\u9662\u95e8\uff0c\u5185\u4e2d\u53c8\u662f\u6781\u9002\u5b9c\u73b0\u4ee3\u751f\u6d3b\u7684\u5c45\u5bb6\u98ce\u683c\uff0c\u4eff\u4f5b\u4e00\u6b65\u8e0f\u5165\u53e6\u4e00\u4e2a\u4e16\u754c\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u524d\u5ead\u6574\u4e2a\u7684\u90fd\u662f\u5ea7\u6d45\u6c34\u6c60\u5b50\uff0c\u6c60\u4e2d\u7f00\u7740\u767d\u7802\u767d\u77f3\u783e\uff0c\u6f02\u7740\u51e0\u53f6\u6d6e\u840d\uff0c\u8fd8\u6709\u51e0\u5c3e\u91d1\u9c7c\u505c\u5728\u6c34\u91cc\u4e00\u52a8\u4e0d\u52a8\uff0c\u4f3c\u4e4e\u5728\u67d4\u548c\u7684\u5ead\u706f\u4e0b\u5165\u7720\u4e86\uff0c\u5f20\u606a\u3001\u8bb8\u601d\u3001\u665a\u6674\u4e09\u4eba\u8d70\u8fdb\u6765\u90fd\u6ca1\u6709\u60ca\u9192\u5b83\u4eec\u3002\u5899\u89d2\u79cd\u690d\u6709\u4e00\u7566\u7fe0\u7af9\uff0c\u9662\u95e8\u4e0e\u8d77\u5c45\u5ba4\u6709\u5927\u7406\u77f3\u78e8\u5236\u7684\u6c34\u9636\u76f8\u8fde\u3002\u8d70\u8fdb\u8d77\u5c45\u5ba4\uff0c\u5c06\u5916\u9762\u7684\u5ead\u706f\u5173\u4e0a\uff0c\u6708\u5149\u4ece\u683c\u5b50\u95e8\u5916\u900f\u8fdb\u6765\uff0c\u65b9\u65b9\u6b63\u6b63\u7684\u65b9\u683c\u5b50\u6620\u5728\u8d77\u5c45\u5ba4\u6d45\u7070\u8272\u7684\u77f3\u78e8\u5730\u94fa\u4e0a\uff0c\u715e\u662f\u597d\u770b\u3002</p><p>\u3000\u3000\u6253\u5f00\u8d77\u5c45\u5ba4\u91cc\u7684\u706f\uff0c\u5f20\u606a\u89c1\u5c4b\u5b50\u91cc\u54ea\u91cc\u50cf\u662f\u5f88\u957f\u65f6\u95f4\u6ca1\u6709\u4eba\u6253\u626b\u8fc7\u7684\u6837\u5b50\uff0c\u63a2\u5934\u770b\u4e86\u770b\u9694\u58c1\u7684\u4e66\u623f\uff0c\u4e66\u684c\u8fd8\u6709\u4e00\u672c\u672a\u8bfb\u5b8c\u7684\u4e66\u7ffb\u5728\u90a3\u91cc\uff0c\u8f6c\u8fc7\u5934\u6765\u95ee\u665a\u6674\uff1a\u201c\u6628\u5929\u4f60\u8ddf\u8bb8\u601d\u4f4f\u8fd9\u91cc\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u4e24\u4e2a\u6ca1\u6709\u4eba\u601c\u7231\u7684\u5973\u4eba\u8fd8\u80fd\u6709\u5176\u4ed6\u4ec0\u4e48\u5730\u65b9\u53ef\u53bb\uff1f\u201d\u665a\u6674\u6545\u610f\u82e6\u7740\u8138\u81ea\u6028\u81ea\u827e\u7684\u8bf4\u9053\uff0c\u201c\u901b\u8857\u901b\u7d2f\u4e86\uff0c\u5f53\u7136\u4f4f\u8fd9\u91cc\u6700\u65b9\u4fbf\u4e86\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8981\u5f80\u91cc\u8d70\uff0c\u5f20\u606a\u6293\u4f4f\u5979\u7684\u624b\uff0c\u7b11\u7740\u95ee\u9053\uff1a\u201c\u4f60\u8fd8\u8bf4\u8fd9\u91cc\u5f88\u957f\u65f6\u95f4\u6ca1\u4eba\u6253\u626b\u4e0d\u80fd\u4f4f\u4eba\uff0c\u4f60\u5728\u62c5\u5fc3\u4ec0\u4e48\u554a\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8138\u988a\u5fae\u7ea2\uff0c\u7ec6\u58f0\u7684\u8bf4\u9053\uff1a\u201c\u6211\u6709\u62c5\u5fc3\u4ec0\u4e48\uff1f\u6211\u6709\u8fd9\u4e48\u8bf4\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u521d\u65f6\u8fd8\u6ca1\u6709\u610f\u8bc6\u5230\u4ec0\u4e48\uff0c\u8ba9\u8bb8\u601d\u4e0e\u665a\u6674\u5148\u4e0a\u697c\u6d17\u6fa1\u53bb\uff0c\u89c1\u5979\u4eec\u8fd8\u8981\u5206\u5f00\u6d17\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5973\u4eba\u4e00\u8d77\u6d17\u5f97\u4e86\uff0c\u5973\u4eba\u6d17\u6fa1\u6700\u8d39\u65f6\u95f4\u2026\u2026\u201d\u8bb8\u601d\u3001\u665a\u6674\u5012\u4e5f\u6ca1\u6709\u89c9\u5f97\u4e24\u4e2a\u5973\u4eba\u4e00\u8d77\u6d17\u6fa1\u6709\u4ec0\u4e48\uff0c\u4e8c\u697c\u7684\u6d74\u5ba4\u53c8\u8db3\u591f\u7684\u5bbd\u655e\uff0c\u8bf4\u5b9e\u8bdd\uff0c\u90a3\u6d74\u7f38\u4e09\u4e2a\u4eba\u540c\u65f6\u5750\u8fdb\u53bb\u90fd\u4e0d\u4f1a\u89c9\u5f97\u6324\uff0c\u5c31\u624b\u633d\u7740\u624b\u4e00\u8d77\u4e0a\u4e86\u697c\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u4e0d\u80fd\u6b7b\u76ae\u8d56\u8138\u7684\u8ddf\u7740\u4e0a\u53bb\uff0c\u4ed6\u5230\u4e66\u623f\u91cc\u6253\u5f00\u7535\u8111\u62bd\u65f6\u95f4\u8981\u5904\u7406\u4e00\u4e0b\u4eca\u5929\u79ef\u7d2f\u4e0b\u6765\u7684\u90ae\u4ef6\u3002</p><p>\u3000\u3000\u4e66\u623f\u5317\u9762\u7684\u623f\u95f4\u539f\u672c\u662f\u5ba2\u623f\uff0c\u4e0e\u540e\u9662\u76f8\u901a\uff0c\u4e0e\u4e66\u623f\u4e4b\u95f4\u6ca1\u6709\u95e8\uff0c\u4f46\u662f\u4e3a\u4e86\u589e\u5f3a\u7a7a\u95f4\u901a\u900f\u6027\uff0c\u780c\u4e86\u534a\u5899\uff0c\u534a\u5899\u4e4b\u4e0a\u5d4c\u7740\u534a\u900f\u5149\u7684\u78e8\u7802\u73bb\u7483\uff0c\u5f20\u606a\u5728\u4e66\u623f\u91cc\u900f\u8fc7\u78e8\u7802\u73bb\u7483\u5f80\u540e\u9762\u770b\u4e86\u770b\uff0c\u611f\u89c9\u540e\u9762\u7684\u623f\u95f4\u91cc\u7a7a\u7a7a\u8361\u8361\u7684\uff0c\u89c9\u5f97\u5947\u602a\uff0c\u4ece\u8d77\u5c45\u5ba4\u7ed5\u8fdb\u53bb\u770b\u4e86\u4e00\u773c\uff0c\u5ba2\u623f\u7ed9\u8bb8\u601d\u6539\u6210\u753b\u5ba4\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ec8\u662f\u660e\u767d\u8bb8\u601d\u5728\u62c5\u5fc3\u4ec0\u4e48\uff1a\u6574\u680b\u5b85\u5b50\u53ea\u6709\u4e00\u95f4\u5e26\u5e8a\u7684\u5367\u5ba4\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u4e9b\u60c5\u70ed\uff0c\u8e51\u624b\u8e51\u811a\u7684\u8e29\u7740\u697c\u68af\u4e0a\u53bb\uff0c\u542c\u89c1\u6d74\u5ba4\u91cc\u6709\u6c34\u6d41\u7684\u58f0\u97f3\uff0c\u63e1\u7740\u95e8\u9501\u8f7b\u8f7b\u7684\u62e7\u4e86\u62e7\uff0c\u7ed9\u4ece\u91cc\u9762\u53cd\u9501\u4e0a\u4e86\uff0c\u63a5\u7740\u5c31\u542c\u89c1\u95e8\u7ed9\u4ec0\u4e48\u4e1c\u897f\u4ece\u91cc\u9762\u7838\u4e86\u4e00\u4e0b\uff0c\u4f20\u6765\u665a\u6674\u4e0e\u8bb8\u601d\u5403\u5403\u7684\u7b11\u58f0\uff0c\u4e24\u4e2a\u5973\u4eba\u540c\u65f6\u8fdb\u6d74\u5ba4\u6d17\u6fa1\u4e86\uff0c\u504f\u504f\u5c06\u4ed6\u9501\u5728\u6d74\u5ba4\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u4e86\u697c\uff0c\u6709\u4e9b\u53e3\u5e72\uff0c\u51b0\u7bb1\u91cc\u7684\u4e1c\u897f\u5012\u662f\u4e0d\u5c11\uff0c\u62ff\u4e86\u4e00\u74f6\u77ff\u6cc9\u6c34\uff0c\u5230\u4e8c\u697c\u7684\u9732\u53f0\u4e0a\uff0c\u6253\u7b97\u8737\u819d\u5750\u5230\u9732\u53f0\u4e0a\u7684\u957f\u6905\u4e0a\u7b49\u8bb8\u601d\u3001\u665a\u6674\u6d17\u5b8c\u6fa1\u51fa\u6765\uff0c\u5374\u662f\u6ca1\u6709\u60f3\u5230\u8bb8\u601d\u4e0e\u665a\u6674\u5fd8\u4e86\u5c06\u6d74\u5ba4\u4e0e\u4e8c\u697c\u9732\u53f0\u4e4b\u95f4\u7684\u7a97\u5e18\u62c9\u4e0a\u2014\u2014\u9732\u53f0\u5916\u6709\u9ad8\u8fc7\u4eba\u5934\u7684\u6728\u56f4\u680f\uff0c\u4e0d\u7528\u62c5\u5fc3\u79c1\u5bc6\u6027\u2014\u2014\u5979\u4eec\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u5230\u9732\u53f0\u4e0a\u6765\u3002\u5c06\u7ea4\u6beb\u6bd5\u5448\u7684\u5c06\u4e00\u5bf9\u7389\u4eba\u5750\u5728\u6d74\u7f38\u91cc\u6d17\u6fa1\u7684\u60c5\u5f62\u770b\u6765\u773c\u91cc\uff0c\u5f20\u606a\u7684\u773c\u73e0\u5b50\u5dee\u70b9\u8981\u6389\u51fa\u6765\u3002</p><p>\u3000\u3000\u867d\u8bf4\u665a\u6674\u4e0e\u8bb8\u601d\u90fd\u6d78\u5728\u6d6e\u7740\u6c90\u6d74\u6ce1\u6cab\u7684\u6c34\u91cc\u53ea\u6709\u80a9\u7410\u9aa8\u4ee5\u4e0a\u7684\u90e8\u4f4d\u9732\u51fa\u6765\uff0c\u4f46\u662f\u7ed9\u4eba\u7684\u611f\u5b98\u523a\u6fc0\u5374\u662f\u5b8c\u5168\u4e0d\u4e00\u6837\u7684\uff0c\u8fd9\u4f1a\u513f\u665a\u6674\u80cc\u540e\u8eab\u53bb\uff0c\u597d\u50cf\u662f\u8981\u8bb8\u601d\u5e2e\u5979\u6413\u80cc\uff0c\u8bb8\u601d\u8dea\u5728\u6c34\u91cc\uff0c\u8eab\u5b50\u633a\u76f4\uff0c\u9732\u51fa\u72b6\u6001\u59e3\u7f8e\u3001\u633a\u7fd8\u8038\u7acb\u7684\u767d\u4e73\uff0c\u5f20\u606a\u4e5f\u662f\u592a\u5fc3\u5207\uff0c\u6ca1\u6709\u6ce8\u610f\u5230\u811a\u4e0b\u7684\u82b1\u76c6\uff0c\u60f3\u66f4\u9760\u73bb\u7483\u7a97\u8fd1\u4e00\u4e9b\u770b\u6e05\u695a\uff0c\u4e00\u811a\u8e29\u5230\u82b1\u76c6\u4e0a\uff0c\u7a00\u91cc\u54d7\u5566\u7684\u4eba\u7ed9\u7eca\u5012\u5728\u5730\uff0c\u524d\u989d\u78d5\u7a97\u53f0\u4e0a\uff0c\u75bc\u75db\u4e4b\u4f59\u6ca1\u6709\u770b\u5230\u6d74\u5ba4\u91cc\u7684\u53cd\u5e94\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u5c31\u89c1\u8bb8\u601d\u4e0e\u665a\u6674\u4e24\u4eba\u88f9\u7740\u6d74\u5dfe\u6025\u51b2\u51b2\u7684\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u989d\u5934\u78d5\u7834\u4e86\uff0c\u5f20\u606a\u4f38\u624b\u6478\u4e86\u6478\uff0c\u6709\u4e9b\u8840\u8ff9\uff0c\u706b\u8fa3\u8fa3\u7684\u75bc\uff0c\u5c0f\u817f\u8fd8\u6709\u4e9b\u75bc\uff0c\u53ef\u80fd\u7ed9\u8e29\u788e\u7684\u82b1\u76c6\u5272\u7834\u4e86\u3002\u5f20\u606a\u5fc3\u865a\u6ca1\u6562\u53eb\u82e6\uff0c\u665a\u6674\u3001\u8bb8\u601d\u5c06\u4ed6\u6400\u56de\u623f\u95f4\uff0c\u62ff\u6765\u533b\u836f\u7bb1\u62ff\u9152\u7cbe\u5e2e\u4ed6\u6e05\u6d17\u4f24\u53e3\uff0c\u597d\u5728\u989d\u5934\u649e\u7834\u7684\u53e3\u5b50\u4e0e\u5c0f\u817f\u4e0a\u7ed9\u5272\u4f24\u7684\u53e3\u5b50\u90fd\u4e0d\u592a\u4e25\u91cd\uff0c\u7b80\u5355\u5904\u7406\u4e86\u4e00\u4e9b\u62ff\u7eb1\u5e03\u8d34\u88f9\u597d\u6b62\u8840\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u865a\uff0c\u8ba9\u665a\u6674\u4e0e\u8bb8\u601d\u5e2e\u4ed6\u5904\u7406\u4f24\u53e3\uff0c\u4e0d\u5927\u542d\u58f0\uff0c\u5904\u7406\u597d\u4f24\u53e3\uff0c\u665a\u6674\u6536\u62fe\u533b\u836f\u7bb1\uff0c\u4fa7\u5934\u770b\u7740\u4ed6\uff0c\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u4e0d\u8bf4\u8bdd\u4e86\uff0c\u81ea\u5df1\u4e5f\u89c9\u5f97\u4e22\u4eba\u4e86\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5509\u58f0\u53f9\u7740\u6c14\uff0c\u54ea\u91cc\u80fd\u60f3\u5230\u8c01\u4f1a\u5728\u7a97\u53f0\u4e0b\u6446\u4e0a\u82b1\u76c6\uff0c\u771f\u662f\u6d41\u5e74\u4e0d\u5229\u554a\uff1b\u8bb8\u601d\u7ad9\u5728\u4e00\u65c1\u62bf\u5634\u7b11\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f38\u4e86\u4f38\u7ed9\u8bb8\u601d\u62ff\u7eb1\u5e03\u6253\u4e0a\u8774\u8776\u7ed3\u7684\u5de6\u817f\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u7b49\u4f1a\u513f\u8fd8\u600e\u4e48\u6d17\u6fa1\uff1f\u201d</p><p>\u3000\u3000\u201c\u71ac\u4e00\u5929\u4e0d\u6d17\u6fa1\u6709\u4ec0\u4e48\uff1f\u201d\u665a\u6674\u5728\u5f20\u606a\u7684\u5c0f\u817f\u8f7b\u8f7b\u7684\u6247\u4e86\u4e00\u8bb0\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8eab\u4e0a\u810f\u4e00\u70b9\u3001\u81ed\u4e00\u70b9\uff0c\u5bf9\u6211\u4eec\u53c8\u6ca1\u6709\u4ec0\u4e48\u5f71\u54cd\uff1f\u4f60\u8fd8\u662f\u60f3\u60f3\u597d\u660e\u540e\u5929\u600e\u4e48\u51fa\u53bb\u89c1\u4eba\u5427\u3002\u5bf9\u4e86\uff0c\u542c\u8bf4\u4f60\u524d\u6bb5\u65f6\u95f4\u4ece\u674e\u99a8\u4e88\u7684\u623f\u5b50\u91cc\u51fa\u6765\u4e5f\u662f\u9f3b\u9752\u8138\u80bf\u7684\uff0c\u542c\u8bf4\u60c5\u51b5\u6bd4\u8fd9\u65f6\u5019\u8fd8\u8981\u51c4\u60e8\u4e00\u4e9b\uff0c\u8be5\u4e0d\u4f1a\u52a8\u7684\u574f\u5fc3\u601d\u6bd4\u8fd9\u6b21\u8fd8\u8981\u6076\u52a3\u5427\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8e69\u7740\u7b11\u8138\u90fd\u8981\u6da8\u901a\u7ea2\u4e86\uff0c\u8dd1\u4e0b\u697c\u62ff\u4e86\u4fdd\u9c9c\u819c\u4e0a\u6765\uff0c\u5e2e\u5f20\u606a\u5c0f\u817f\u4f24\u53e3\u4e0a\u62ff\u4fdd\u9c9c\u819c\u88f9\u4f4f\u9632\u6c34\uff0c\u8bf4\u9053\uff1b\u201c\u8fd9\u6837\u5c31\u4e0d\u7528\u6015\u6e7f\u6c34\u4e86\uff0c\u4f60\u5148\u4e0d\u8981\u6d17\u5934\uff0c\u7b49\u6d17\u8fc7\u6fa1\u6211\u5e2e\u4f60\u6d17\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u662f\u53ea\u6709\u665a\u6674\u6216\u662f\u53ea\u6709\u8bb8\u601d\uff0c\u5f20\u606a\u90fd\u80fd\u6b7b\u76ae\u8d56\u8138\u7684\u8ba9\u5979\u4eec\u5e2e\u7740\u6d17\uff0c\u53ef\u60dc\u4e24\u4e2a\u5973\u4eba\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u53ea\u80fd\u5e26\u7740\u88f9\u4e0a\u4fdd\u9c9c\u819c\u7684\u4f24\u817f\u8fdb\u6d74\u5ba4\u4e86\u3002\u7b80\u5355\u7684\u51b2\u4e86\u4e00\u4e0b\u6fa1\uff0c\u867d\u8bf4\u6d17\u4e0d\u6d17\u5934\u65e0\u6240\u8c13\uff0c\u4f46\u662f\u5c31\u7b97\u8111\u888b\u6ca1\u6709\u78d5\u7834\uff0c\u5f20\u606a\u90fd\u4e0d\u4f1a\u62d2\u7edd\u8bb8\u601d\u5e2e\u4ed6\u6d17\u5934\u7684\u2014\u2014\u53ef\u60dc\u53ea\u6d17\u4e00\u4e2a\u3002</p><p>\u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u665a\u6674\u4e0e\u8bb8\u601d\u8981\u5c06\u5f20\u606a\u5f80\u697c\u4e0b\u8d76\uff0c\u5f20\u606a\u811a\u62b5\u7740\u95e8\u4e0d\u8ba9\u5979\u4eec\u5173\u4e0a\uff0c\u6d8e\u8138\u7b11\u7740\u8bf4\uff1a\u201c\u4e00\u8d77\u8bf4\u8bf4\u8bdd\u5427\uff0c\u6709\u4e9b\u75bc\uff0c\u4e5f\u7761\u4e0d\u7740\u2014\u2014\u7ed9\u6211\u88ab\u5b50\u94fa\u5730\u677f\u4e0a\u7761\uff0c\u6700\u597d\u94fa\u539a\u4e00\u4e9b\uff0c\u6211\u6015\u5730\u677f\u592a\u786c\u3002\u201d</p><p>\u3000\u3000\u665a\u6674\u3001\u8bb8\u601d\u5012\u6ca1\u6709\u80fd\u5fcd\u5fc3\u5c06\u4ed6\u8d76\u4e0b\u697c\u53bb\uff0c\u5e2e\u4ed6\u6367\u51fa\u4e00\u5e8a\u68c9\u88ab\u94fa\u5728\u5927\u5e8a\u65c1\u7684\u5730\u677f\u4e0a\uff0c\u5f20\u606a\u521d\u65f6\u4e5f\u662f\u8001\u5b9e\uff0c\u7761\u5728\u5730\u677f\u4e0a\uff0c\u665a\u6674\u4e0e\u8bb8\u601d\u5e76\u5934\u7761\u5e8a\u4e0a\uff0c\u7184\u4e86\u706f\uff0c\u6708\u5149\u4ece\u843d\u5730\u7a97\u900f\u8fdb\u6765\uff0c\u623f\u95f4\u5c31\u50cf\u7ed9\u6d78\u5728\u6e05\u6f88\u7684\u6c34\u91cc\u3002</p><p>\u3000\u3000\u4e00\u65f6\u95f4\u4e5f\u6ca1\u6709\u7761\u610f\uff0c\u968f\u610f\u7684\u804a\u7740\u5929\uff0c\u4e5f\u804a\u674e\u99a8\u4e88\uff0c\u665a\u6674\u95ee\u4ed6\uff1a\u201c\u4f60\u5c06\u674e\u99a8\u4e88\u62d0\u8dd1\uff0c\u4e09\u661f\u5c31\u6ca1\u6709\u4eba\u6765\u627e\u5979\uff1f\u8fd8\u662f\u8bf4\u4ed6\u4eec\u8ba4\u4e3a\u4e09\u661f\u7684\u803b\u8fb1\u5df2\u7ecf\u65e0\u6cd5\u62b9\u6389\u4e86\uff0c\u4e5f\u5c31\u81ea\u66b4\u81ea\u5f03\u8d77\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u80fd\u8bf4\u62d0\u5462\uff0c\u53c8\u6ca1\u6709\u4eba\u9650\u5236\u5979\u4eba\u8eab\u81ea\u7531\uff0c\u5c31\u4e0d\u5174\u5979\u8ddf\u5bb6\u91cc\u89e3\u91ca\u6e05\u695a\u8bef\u4f1a\u4f7f\u5f97\u4e09\u661f\u5bf9\u6211\u6d88\u9664\u6210\u89c1\uff1f\u201d\u5f20\u606a\u53f9\u4e86\u4e00\u53e3\u6c14\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u4e0d\u4fe1\u6211\u7684\u8bdd\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u8c0e\u8a00\u5f88\u62d9\u52a3\u554a\uff0c\u89c9\u5f97\u4f60\u5c31\u662f\u6492\u8c0e\uff0c\u4e5f\u4e0d\u4f1a\u9a97\u8fd9\u79cd\u62d9\u52a3\u7684\u8c0e\u8a00\u554a\uff0c\u5f88\u5947\u602a\u554a\uff0c\u201d\u665a\u6674\u4fa7\u8fc7\u8eab\u5b50\u9876\u4e86\u9876\u8bb8\u601d\u7684\u80f3\u818a\uff0c\u201c\u4f60\u4fe1\u4e0d\u4fe1\u8fd9\u5c0f\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u554a\uff1f\u201d\u8bb8\u601d\u8db4\u5728\u5e8a\u6cbf\u4e0a\u63a2\u5934\u6765\u770b\u62b1\u5934\u8eba\u5728\u5730\u677f\u4e0a\u7684\u5f20\u606a\uff0c\u5f88\u5947\u602a\u7684\u611f\u89c9\uff0c\u591c\u6df1\u4eba\u9759\u65f6\u4f1a\u60f3\u8fd9\u5bb6\u4f19\uff0c\u523b\u9aa8\u94ed\u5fc3\u7684\u60f3\uff0c\u523b\u9aa8\u94ed\u5fc3\u7684\u601d\u5ff5\uff0c\u4f46\u662f\u8fd9\u79cd\u601d\u5ff5\u4e0d\u4f1a\u7ea0\u7f20\u5f97\u8ba9\u751f\u6d3b\u4e0e\u5de5\u4f5c\u65e0\u6cd5\u7ee7\u7eed\u4e0b\u53bb\uff0c\u4e5f\u6ca1\u6709\u8981\u63a7\u5236\u4ed6\u3001\u7ea0\u7f20\u5728\u4ed6\u8eab\u8fb9\u7684\u5ff5\u5934\uff0c\u5373\u4f7f\u542c\u5230\u4e9b\u4ed6\u7684\u98ce\u6d41\u97f5\u4e8b\uff0c\u4e5f\u6ca1\u6709\u5973\u4eba\u5e94\u8be5\u6709\u7684\u5ac9\u5992\u5fc3\u7406\uff0c\u771f\u662f\u5947\u602a\uff0c\u770b\u5230\u4ed6\u8fd9\u5f20\u660e\u4fca\u7684\u8138\u4e0e\u6e05\u6f88\u6df1\u9083\u7684\u773c\u775b\uff0c\u6709\u7740\u6676\u83b9\u5254\u900f\u7684\u611f\u89c9\uff0c\u4eff\u4f5b\u8fd9\u4e00\u751f\u7684\u7f8e\u5999\u5c31\u5728\u8eab\u8fb9\uff0c\u8fd9\u5230\u5e95\u8981\u7b97\u600e\u6837\u4e00\u79cd\u60c5\u611f\uff1f\u8bf4\u611f\u60c5\u6de1\u5427\uff0c\u4e24\u4e09\u5929\u4e00\u901a\u7535\u8bdd\u4eff\u4f5b\u4e5f\u5df2\u7ecf\u8db3\u591f\uff0c\u4f46\u662f\u591c\u6df1\u4eba\u9759\u65f6\u9664\u4e86\u8fd9\u5bb6\u4f19\u518d\u6ca1\u6709\u4eba\u80fd\u94bb\u8fdb\u81ea\u5df1\u7684\u5fc3\u91cc\uff0c\u8bf4\u611f\u60c5\u6d53\u5427\uff0c\u4e00\u901a\u7535\u8bdd\u7684\u8f7b\u8bed\u4f4e\u8bc9\u5c31\u8db3\u4ee5\u83b7\u5f97\u8db3\u591f\u7684\u6170\u85c9\uff0c\u53c8\u6ca1\u6709\u5bfb\u5e38\u7231\u60c5\u7537\u5973\u7684\u7ea0\u7f20\u4e0d\u4f11\uff0c\u89c1\u665a\u6674\u53c8\u62ff\u80f3\u818a\u8098\u9876\u81ea\u5df1\uff0c\u5ae3\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u624d\u4e0d\u7ba1\u8fd9\u5bb6\u4f19\u8bf4\u4e0d\u8bf4\u8c0e\u5462\uff01\u201d</p></article>"}}}]);
