(window.webpackJsonp=window.webpackJsonp||[]).push([[921],{944:function(p){p.exports={chapterTitle:null,title:"\u7b2c918\u7ae0 \u4ece\u5730\u677f\u5230\u5e8a\u7684\u8ddd\u79bb",content:"<article><h2>\u7b2c918\u7ae0 \u4ece\u5730\u677f\u5230\u5e8a\u7684\u8ddd\u79bb</h2><p>\u3000\u3000\u62e5\u88ab\u8eba\u5728\u6708\u5149\u4e0b\uff0c\u665a\u6674\u8111\u5b50\u91cc\u60f3\u7740\u5f20\u606a\u6700\u521d\u9a97\u5979\u5931\u8eab\u65f6\u8bf4\u7684\u90a3\u4e9b\u8bdd\uff0c\u4e0d\u6653\u5f97\u8fd9\u5c0f\u5b50\u5e74\u7eaa\u8f7b\u8f7b\u5bf9\u7537\u5973\u5173\u7cfb\u5374\u662f\u660e\u6f88\uff0c\u8fd8\u603b\u7ed3\u51fa\u4e09\u91cd\u5883\u754c\u6765\u3002\u665a\u6674\u4e5f\u76f8\u4fe1\u771f\u6b63\u7684\u5f02\u6027\u77e5\u5df1\u4e4b\u95f4\u624d\u5b58\u5728\u201c\u6240\u8c13\u4f0a\u4eba\u3001\u5728\u6c34\u4e00\u65b9\u201d\u7684\u6de1\u7136\uff0c\u4e0d\u4f1a\u76f8\u770b\u4e24\u538c\u3001\u4e5f\u4e0d\u4f1a\u75f4\u7f20\u60c5\u6028\uff0c\u4e5f\u53ea\u6709\u5979\u4eec\u8fd9\u4e9b\u7ecf\u5386\u8fc7\u4eba\u751f\u6ce2\u6298\u7684\u4eba\u80fd\u591f\u770b\u900f\u4e00\u4e9b\uff0c\u6709\u65f6\u5019\u7231\u4eba\u4e4b\u95f4\u6709\u7740\u7075\u7280\u4e00\u70b9\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u3000\u6ca1\u6709\u5f20\u606a\uff0c\u665a\u6674\u4e5f\u60f3\u8c61\u4e0d\u51fa\u81ea\u5df1\u8fd8\u4f1a\u6709\u53ef\u80fd\u53bb\u7231\u4e0a\u5176\u4ed6\u7684\u7537\u4eba\uff0c\u5373\u4f7f\u6ca1\u6709\u7537\u4eba\u4e5f\u4e0d\u662f\u65e0\u6cd5\u60a0\u95f2\u6e21\u8fc7\u4f59\u751f\u2014\u2014</p><p>\u3000\u3000\u201c\u6674\u59d0\uff0c\u4f60\u5728\u60f3\u4ec0\u4e48\uff1f\u201d\u8bb8\u601d\u89c1\u665a\u6674\u5634\u89d2\u5e26\u7740\u7b11\u610f\u5728\u60f3\u4ec0\u4e48\uff0c\u4fa7\u8fc7\u8eab\u6765\u8f7b\u62e5\u7740\u5979\u4e30\u8174\u7684\u8eab\u5b50\uff0c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u5f20\u606a\u90a3\u6df7\u86cb\u8ddf\u6211\u80e1\u626f\u8fc7\u7684\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u8bf4\u4ec0\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u4e5f\u6ca1\u6709\u60f3\u3002</p><p>\u3000\u3000\u665a\u6674\u4fa7\u8fc7\u5934\u6765\uff0c\u5634\u9644\u5230\u8bb8\u601d\u8033\u8fb9\uff0c\u62ff\u8584\u8695\u4e1d\u88ab\u76d6\u4f4f\u5979\u4e0e\u8bb8\u601d\u7684\u8111\u888b\uff0c\u5c06\u5f20\u606a\u4e4b\u524d\u8ddf\u5979\u80e1\u626f\u7684\u90a3\u6bb5\u7406\u8bba\u8bf4\u7ed9\u8bb8\u601d\u542c\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u8fd9\u5bb6\u4f19\u662f\u4e0d\u662f\u751f\u4e0b\u6765\u5c31\u8981\u62db\u60f9\u5973\u4eba\u7684\uff1f\u504f\u504f\u5f53\u65f6\u8fd8\u662f\u7ed9\u4ed6\u8bf4\u52a8\u4e86\uff0c\u6211\u90fd\u4e09\u5341\u5c81\u7684\u5973\u4eba\uff0c\u504f\u504f\u8fd8\u8981\u7ed9\u4e2a\u5341\u516b\u5c81\u7684\u5c0f\u6bdb\u5934\u9a97\u4e86\u8eab\uff0c\u8fd9\u65f6\u5019\u60f3\u8d77\u6765\u5c31\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u2026\u2026\u201d</p><p>\u3000\u3000\u53ea\u542c\u89c1\u4e24\u4e2a\u5973\u4eba\u8eba\u5728\u88ab\u7a9d\u91cc\u5403\u5403\u7684\u7b11\uff0c\u542c\u4e0d\u6e05\u5979\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u5f20\u606a\u54ea\u91cc\u80fd\u6309\u8010\u5f97\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u4ec0\u4e48\u4e5f\u8ba9\u6211\u542c\u542c\u554a\uff01\u201d\u8dea\u8d77\u6765\u6380\u8d77\u88ab\u5b50\u5c31\u8981\u5f80\u91cc\u94bb\uff0c\u4ed6\u6328\u7740\u8bb8\u601d\u8fd9\u8fb9\uff0c\u8bb8\u601d\u54ea\u91cc\u80af\u5b9a\u8ba9\u4ed6\u6324\u4e0a\u5979\u4e0e\u665a\u6674\u7684\u5e8a\uff0c\u62fd\u7d27\u88ab\u5b50\u3001\u811a\u62b5\u7740\u5f20\u606a\u7684\u5c0f\u809a\u5b50\u4e0d\u8ba9\u4ed6\u4e0a\u6765\uff0c\u5f20\u606a\u201c\u5440\u5440\u5440\u201d\u7684\u53eb\u4e86\u4e24\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u817f\u75bc\u2026\u2026\u201d\u8bb8\u601d\u5fc3\u91cc\u4e00\u8f6f\uff0c\u5c31\u8ba9\u5f20\u606a\u94bb\u8fdb\u88ab\u7a9d\u91cc\u6765\u3002</p><p>\u3000\u3000\u665a\u6674\u5012\u662f\u4e0d\u7ba1\u4ed6\uff0c\u8fd8\u6015\u4ed6\u52a8\u4f5c\u592a\u5c0f\u78b0\u5230\u4f24\u53e3\uff0c\u53ea\u662f\u7b11\u7740\u9a82\uff1a\u201c\u8fd9\u6df7\u86cb\uff01\u201d</p><p>\u3000\u3000\u6324\u5230\u6e29\u9999\u6696\u7389\u7684\u5a07\u8eaf\u4e4b\u95f4\uff0c\u5f20\u606a\u5fc3\u91cc\u4e5f\u6ca1\u6709\u60c5\u6b32\u6c79\u6d8c\u5230\u65e0\u6cd5\u81ea\u5df1\uff0c\u867d\u8bf4\u6709\u7740\u5c06\u665a\u6674\u3001\u8bb8\u601d\u90fd\u5265\u5e72\u51c0\u7684\u8352\u5510\u60f3\u6cd5\uff0c\u4f46\u662f\u66f4\u559c\u6b22\u5c06\u4e24\u4eba\u6402\u5728\u6000\u91cc\u7684\u90a3\u79cd\u6e29\u99a8\u5230\u6781\u70b9\u7684\u611f\u89c9\uff0c\u5c31\u6b64\u5730\u8001\u5929\u8352\u4e0b\u53bb\u4e5f\u65e0\u6240\u8c13\u2014\u2014\u7b49\u7b49\uff0c\u5c06\u5510\u5a67\u4e0e\u7fdf\u4e39\u9752\u634e\u4e0a\u518d\u5730\u8001\u5929\u8352\uff0c\u5f20\u606a\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u4ed6\u4eba\u8001\u8001\u5b9e\u5b9e\u7684\u4ef0\u8eba\u7740\uff0c\u4f38\u5f00\u624b\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u4eba\u6795\u4e00\u53ea\u6795\u5934\u592a\u6324\u4e86\uff0c\u6211\u628a\u80f3\u818a\u501f\u7ed9\u4f60\u4eec\uff1f\u201d</p><p>\u3000\u3000\u4ed6\u6324\u4e0a\u5e8a\u540e\uff0c\u8bb8\u601d\u6709\u4e9b\u653e\u4e0d\u5f00\uff0c\u8eab\u5b50\u80cc\u8fc7\u53bb\u8737\u8d77\u6765\uff0c\u89c1\u4ed6\u4e0a\u5e8a\u540e\u6ca1\u6709\u778e\u52a8\u751a\u81f3\u8fd8\u6709\u5fc3\u601d\u5728\u90a3\u91cc\u80e1\u8a00\u4e71\u8bed\uff0c\u8f6c\u8fc7\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u5f20\u606a\u671b\u8fc7\u6765\u7684\u773c\u7738\u5b50\u5728\u6708\u8272\u5e7d\u5149\u6e05\u6f88\u800c\u6df1\u9083\uff0c\u80fd\u611f\u89c9\u4ed6\u773c\u775b\u91cc\u5e7d\u6de1\u5374\u53c8\u5341\u5206\u4e86\u89e3\u7684\u60c5\u7136\uff0c\u89c1\u4ed6\u5476\u52a8\u7740\u5634\u8981\u81ea\u5df1\u9760\u8fc7\u53bb\uff0c\u6b64\u65f6\u5fc3\u95f4\u6709\u7740\u61d2\u6d0b\u6d0b\u7684\u6696\u6696\u611f\u89c9\uff0c\u5c31\u8f6c\u8fc7\u8eab\u6765\uff0c\u5c06\u4ed6\u575a\u5b9e\u7684\u80f3\u818a\u5f80\u4e0b\u62c9\u4e86\u62c9\uff0c\u57ab\u5728\u8111\u888b\u4e0b\u9762\uff0c\u8138\u988a\u5341\u5206\u81ea\u7136\u7684\u8d34\u5230\u4ed6\u80f8\u53e3\u7684\u4f4d\u7f6e\uff0c\u542c\u7740\u4ed6\u5e73\u9759\u800c\u6709\u529b\u7684\u5fc3\u8df3\u3002</p><p>\u3000\u3000\u665a\u6674\u4e5f\u4fa7\u8fc7\u8eab\u6765\u8d34\u7740\u5f20\u606a\u7684\u8eab\u4f53\u800c\u8eba\uff0c\u8138\u988a\u8d34\u7740\u4ed6\u7684\u80f8\u53e3\uff0c\u770b\u7740\u53e6\u4e00\u8fb9\u7684\u8bb8\u601d\uff0c\u770b\u7740\u5979\u6676\u83b9\u900f\u4eae\u7684\u773c\u7738\u5b50\uff0c\u7b11\u4e86\u7b11\uff0c\u95ee\u9053\uff1a\u201c\u8fd9\u53ea\u5927\u62b1\u6795\u7684\u611f\u89c9\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u4e0d\u9519\u554a\uff0c\u4e4b\u524d\u771f\u6ca1\u6709\u89c9\u5f97\u3002\u201d\u8bb8\u601d\u5ae3\u7136\u4e00\u7b11\uff0c\u6ca1\u6709\u4ec0\u4e48\u96be\u582a\uff0c\u5373\u4f7f\u5f20\u606a\u7684\u624b\u6709\u4e9b\u4e0d\u8001\u5b9e\u9694\u7740\u7ef8\u8d28\u7761\u8863\u5728\u6469\u6332\u5979\u7684\u80cc\u810a\uff0c\u75d2\u75d2\u7684\uff0c\u4e5f\u6ca1\u6709\u4e0d\u8212\u670d\u7684\u611f\u89c9\uff0c\u4e5f\u80fd\u611f\u89c9\u5230\u5f20\u606a\u53e6\u4e00\u53ea\u5728\u665a\u6674\u7684\u80cc\u808c\u4e0a\u8f7b\u629a\u7740\uff0c\u8fd9\u4e2a\u5bb6\u4f19\u554a\uff0c\u5c06\u4ed6\u5f53\u6210\u4e00\u53ea\u5927\u62b1\u6795\u5f97\u4e86\uff0c\u8eab\u5b50\u53c8\u60c5\u4e0d\u81ea\u7981\u7684\u8d34\u7d27\u4e00\u8d77\uff0c\u5f88\u81ea\u7136\u4e0b\u534a\u8eab\u7ffb\u538b\u5230\u5f20\u606a\u7684\u5927\u817f\u4e0a\uff0c\u6e29\u70ed\u6e29\u6696\u7684\u611f\u89c9\uff0c\u7684\u786e\u8981\u6bd4\u666e\u901a\u7684\u5927\u62b1\u6795\u611f\u89c9\u597d\u4e0a\u5343\u4e07\u500d\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4e0e\u665a\u6674\u90fd\u94bb\u5230\u5f20\u606a\u7684\u6000\u91cc\u3001\u8138\u988a\u8d34\u7740\u4ed6\u7684\u80f8\u53e3\u8eba\u7740\u8bf4\u8bdd\uff0c\u5f20\u606a\u53ea\u80fd\u770b\u89c1\u5979\u4eec\u4e4c\u9ed1\u6cb9\u4eae\u7684\u4e30\u76db\u957f\u53d1\uff0c\u665a\u6674\u7684\u5934\u53d1\u6709\u4e9b\u81ea\u7136\u5377\uff0c\u8bb8\u601d\u662f\u76f4\u53d1\uff0c\u665a\u6674\u7684\u5934\u53d1\u4e1d\u7c97\u4e00\u4e9b\uff0c\u8bb8\u601d\u7684\u5934\u53d1\u7ed9\u4eba\u66f4\u4e30\u76db\u7684\u611f\u89c9\uff0c\u6216\u8bb8\u662f\u56e0\u4e3a\u5979\u7684\u5934\u9885\u5c0f\u5de7\u4e00\u4e9b\u2014\u2014\u597d\u5427\uff0c\u641e\u4e86\u534a\u5929\uff0c\u6700\u7ec8\u8fd8\u662f\u4e24\u4e2a\u5973\u4eba\u4eb2\u5bc6\u65e0\u95f4\u7684\u8bf4\u7740\u8bdd\uff0c\u5f20\u606a\u8fd8\u667e\u5728\u4e00\u8fb9\u3002</p><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u653e\u8361\u5f62\u9ab8\u3001\u8086\u610f\u82b1\u4e1b\uff0c\u4e5f\u6709\u8fc7\u5c06\u4e24\u4e2a\u5973\u5b69\u5b50\u4e00\u8d77\u4ece\u9152\u5427\u9886\u5230\u9152\u5e97\u7684\u8352\u5510\u7ecf\u5386\uff0c\u5f53\u4e24\u4e2a\u5fc3\u7231\u7684\u5973\u4eba\u4e00\u5de6\u4e00\u53f3\u6e29\u987a\u7684\u8eba\u5728\u81ea\u5df1\u7684\u6000\u91cc\uff0c\u5f20\u606a\u5374\u4e0d\u6562\u653e\u8086\u7684\u53bb\u6311\u52a8\u5979\u4eec\u7684\u60c5\u6b32\uff0c\u53cd\u800c\u66f4\u52a0\u62d8\u8c28\u4e86\uff0c\u4ee5\u81f4\u7ed9\u4e24\u5177\u707c\u70ed\u987a\u6ed1\u7684\u5a07\u8eaf\u88f9\u62e5\u7740\uff0c\u4e0b\u534a\u8eab\u5374\u6ca1\u6709\u60c5\u6b32\u52c3\u53d1\u7684\u633a\u76f4\u3002\u4e0d\u6653\u5f97\u665a\u6674\u4e0e\u8bb8\u601d\u4f55\u65f6\u8bf4\u5230\u624b\u90e8\u62a4\u7406\u8fd9\u4e48\u8bdd\u9898\u4e0a\uff0c\u4f38\u51fa\u624b\u5728\u6708\u8272\u4e0b\u6bd4\u8f83\uff0c\u665a\u6674\u653e\u56de\u624b\u91cc\u65e0\u610f\u95f4\u78b0\u4e86\u5f20\u606a\u8f6f\u7ef5\u7ef5\u7684\u7537\u6839\u4e00\u4e0b\uff0c\u5934\u5fae\u5fae\u62ac\u8d77\u6765\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4fc4\u5c14\u53c8\u8db4\u5230\u5f20\u606a\u80f8\u53e3\uff0c\u5934\u51d1\u5230\u8bb8\u601d\u7684\u8033\u8fb9\u6084\u58f0\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff1a\u201c\u8fd9\u5bb6\u4f19\u86ee\u8001\u5b9e\u7684\u554a\uff0c\u521a\u624d\u6211\u4e5f\u62c5\u5fc3\u5f97\u8981\u547d\uff0c\u6307\u4e0d\u5b9a\u5510\u5a67\u90a3\u4e2b\u5934\u7ed9\u4ed6\u5389\u5bb3\u770b\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u542c\u5230\u5979\u4eec\u5728\u8033\u8bed\u4ec0\u4e48\uff0c\u53ea\u542c\u89c1\u8bb8\u601d\u5728\u5403\u5403\u7684\u4f4e\u7b11\u7740\uff0c\u95ee\u5979\u4eec\u5728\u8bf4\u4ec0\u4e48\uff0c\u5979\u4eec\u53c8\u6b7b\u6d3b\u4e0d\u80af\u8bf4\u3002\u804a\u5929\u4e00\u76f4\u804a\u5230\u51cc\u6668\u4e24\u70b9\u591a\uff0c\u4e09\u4e2a\u4eba\u90fd\u56f0\u5f97\u4e0d\u884c\uff0c\u624d\u6362\u4e86\u4e00\u4e2a\u66f4\u8212\u670d\u7684\u59ff\u52bf\u7761\u89c9\uff0c\u8bb8\u601d\u80cc\u8fc7\u53bb\uff0c\u5f20\u606a\u4ece\u80cc\u540e\u62e5\u7740\u5979\u800c\u7761\uff0c\u665a\u6674\u518d\u4ece\u80cc\u540e\u6402\u7740\u4ed6\u3002\u7761\u89c9\u4e4b\u524d\u6ca1\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u5f20\u606a\u4ece\u80cc\u540e\u6402\u7740\u8bb8\u601d\uff0c\u4e0b\u534a\u8eab\u5f88\u81ea\u7136\u5bc6\u5b9e\u65e0\u9699\u7684\u8d34\u4e0a\u5979\u5145\u6ee1\u5f39\u6027\u7684\u6e29\u70ed\u81c0\u8089\u5c31\u6709\u4e86\u53cd\u5e94\uff0c\u5f20\u606a\u7684\u624b\u5c65\u76d6\u7740\u8bb8\u601d\u5e73\u5766\u5149\u6ed1\u7684\u5c0f\u8179\uff0c\u53c8\u611f\u89c9\u5230\u665a\u6674\u8038\u7acb\u9971\u6ee1\u7684\u7855\u4e73\u8d34\u9876\u7740\u81ea\u5df1\u7684\u540e\u80cc\uff0c\u8111\u888b\u5012\u662f\u4e00\u7247\u6e05\u660e\uff0c\u4e5f\u6ca1\u6709\u8ba9\u4eba\u72c2\u8e81\u4e0d\u5b89\u7684\u60c5\u6b32\uff0c\u53ea\u662f\u4e0b\u534a\u8eab\u575a\u786c\u7684\u633a\u76f4\u7740\uff0c\u90fd\u611f\u89c9\u5230\u6709\u4e9b\u80c0\uff0c\u6ca1\u6709\u4e00\u70b9\u8981\u59a5\u534f\u9000\u7f29\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u7279\u522b\u60f3\u505a\u90a3\u4e8b\u7684\u5fc3\u601d\uff0c\u66f4\u6015\u60ca\u7740\u8bb8\u601d\u4e86\uff0c\u5c31\u8fd9\u6837\u575a\u786c\u633a\u76f4\u7684\u62b5\u7740\u8bb8\u601d\u5145\u6ee1\u5f39\u6027\u7684\u5706\u81c0\uff0c\u5c06\u5979\u6e29\u70ed\u67d4\u8f6f\u7684\u8eab\u4f53\u6402\u7d27\u5728\u6000\u91cc\u3002\u8bb8\u601d\u5f53\u7136\u4e0d\u53ef\u80fd\u7761\u7740\uff0c\u665a\u6674\u8fd8\u4ece\u540e\u9762\u62b1\u7740\u5f20\u606a\u5462\uff0c\u5979\u4e00\u52a8\u90fd\u4e0d\u6562\u52a8\uff0c\u5012\u662f\u5f20\u606a\u53ea\u662f\u8001\u8001\u5b9e\u5b9e\u7684\u5c06\u5979\u6402\u7d27\u7740\uff0c\u8ba9\u5979\u5fc3\u91cc\u6d41\u6dcc\u7740\u7f8e\u5999\u7684\u611f\u89c9\uff0c\u62ff\u8d77\u5f20\u606a\u6402\u7740\u5979\u7684\u8eab\uff0c\u653e\u5230\u5507\u8fb9\u8f7b\u8f7b\u7684\u543b\u7740\uff0c\u81c2\u8089\u7ed9\u90a3\u6839\u575a\u786c\u7684\u4e1c\u897f\u62b5\u9876\u7740\uff0c\u80fd\u591f\u611f\u89c9\u7684\u90a3\u4e1c\u897f\u7684\u7c97\u58ee\uff0c\u5fc3\u91cc\u4e5f\u62c5\u5fe7\u5f20\u606a\u8fd9\u6837\u5b50\u80fd\u4e0d\u80fd\u7761\u7740\uff0c\u5c06\u4ed6\u7684\u624b\u6367\u7740\u80f8\u53e3\uff0c\u8ba9\u4ed6\u6e29\u67d4\u7684\u73a9\u5f04\u7740\u81ea\u5df1\u7684\u767d\u4e73\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\u8fd9\u6837\u4e5f\u662f\u80fd\u7761\u7740\u7684\uff0c\u5f20\u606a\u4e5f\u4e0d\u6e05\u695a\u81ea\u5df1\u4ec0\u4e48\u65f6\u5019\u7761\u7740\u4e86\uff0c\u5165\u7761\u4e4b\u524d\u662f\u575a\u786c\u633a\u76f4\u7684\uff0c\u4e00\u89c9\u9192\u8fc7\u6765\uff0c\u8fd8\u662f\u575a\u786c\u633a\u76f4\u7684\uff0c\u7761\u89c9\u5f53\u4e2d\u662f\u4ec0\u4e48\u72b6\u51b5\u5c31\u4e0d\u6e05\u695a\u4e86\uff0c\u4ed6\u8fd8\u5c06\u8bb8\u601d\u6402\u5728\u6000\u91cc\uff0c\u80cc\u540e\u5374\u662f\u7a7a\u4e86\uff0c\u4e0d\u6653\u5f97\u665a\u6674\u4ec0\u4e48\u65f6\u5019\u8d77\u5e8a\u4e86\uff0c\u5f20\u606a\u6491\u8d77\u8eab\u4f53\u53bb\u770b\u89c1\u8bb8\u601d\u7684\u8138\uff0c\u5979\u95ed\u4e0a\u773c\u775b\u9732\u51fa\u6765\u7684\u957f\u776b\u6bdb\u5728\u8f7b\u8f7b\u7684\u98a4\u52a8\uff0c\u5f20\u606a\u5c06\u5979\u7684\u8eab\u4f53\u8f7b\u8f7b\u7684\u6273\u8f6c\u8fc7\u6765\uff0c\u8bb8\u601d\u7741\u5f00\u773c\uff0c\u5979\u7684\u773c\u775b\u6e05\u6f88\uff0c\u6709\u5c42\u96fe\u6c14\u8f7b\u7b3c\u7740\uff0c\u6709\u7740\u96be\u4ee5\u8a00\u55bb\u7684\u7075\u79c0\u4e4b\u7f8e\uff0c\u5979\u95ee\u9053\uff1a\u201c\u665a\u6674\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u6653\u5f97\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u9759\u542c\u4e86\u4e00\u4f1a\u513f\uff0c\u4e5f\u6ca1\u6709\u542c\u5230\u5916\u9762\u6709\u4ec0\u4e48\u58f0\u54cd\uff0c\u53ef\u4ee5\u4e0a\u8857\u53bb\u4e86\u3002\u8ba9\u8bb8\u601d\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e0e\u4ed6\u9762\u5bf9\u9762\u8eba\u7740\uff0c\u5728\u6e05\u6668\u7684\u971e\u5149\u91cc\u9ed8\u9ed8\u7684\u671b\u4e86\u4e00\u4f1a\u513f\uff0c\u8bb8\u601d\u7684\u773c\u77b3\u6d41\u7740\u79bb\u5149\uff0c\u521d\u7761\u9192\u7684\u8138\u80a4\u5982\u51dd\u8102\uff0c\u53cc\u624b\u6367\u8d77\u5979\u7684\u8138\u86cb\u8ba9\u4e24\u4eba\u7684\u8138\u51d1\u8fd1\u4eb2\u543b\u8d77\u6765\u3002\u5fd8\u60c5\u7684\u543b\u7740\uff0c\u4e5f\u6839\u672c\u4e0d\u62c5\u5fc3\u665a\u6674\u4f1a\u968f\u65f6\u95ef\u8fdb\u6765\uff0c\u5c06\u8bb8\u601d\u7684\u8eab\u4f53\u644a\u5f00\uff0c\u5c06\u7761\u88d9\u89e3\u5f00\uff0c\u543b\u843d\u5728\u5979\u7684\u9888\u4e0a\u3001\u4e73\u4e0a\uff0c\u543b\u5979\u67d4\u8f6f\u7684\u8170\u3001\u5979\u5927\u817f\u3001\u5c0f\u817f\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4f38\u624b\u6469\u5a11\u7740\u5f20\u606a\u7684\u8033\u9645\u53d1\u6839\uff0c\u53ea\u611f\u89c9\u6d51\u8eab\u7684\u75d2\u75d5\u968f\u7740\u5f20\u606a\u7684\u4eb2\u543b\u5728\u7d2f\u79ef\uff0c\u8eab\u4f53\u6f6e\u6da6\u800c\u6e29\u6696\u3002</p><p>\u3000\u3000\u5f20\u606a\u624b\u4ece\u8bb8\u601d\u8170\u4e0b\u6284\u8fdb\u53bb\uff0c\u5fae\u5fae\u7528\u529b\u7684\u4e00\u63d0\uff0c\u8bb8\u601d\u914d\u5408\u7684\u5c06\u81c0\u90e8\u6b20\u8d77\u6765\uff0c\u8ba9\u4ed6\u7684\u624b\u4ece\u5185\u88e4\u7684\u8fb9\u7f18\u4f38\u8fdb\u53bb\u3002\u5f20\u606a\u4e00\u8fb9\u6478\u7740\u5979\u5149\u6ed1\u5145\u6ee1\u5f39\u6027\u7684\u81c0\u8089\uff0c\u4e00\u8fb9\u5c06\u5979\u7684\u5185\u88e4\u8131\u4e0b\u6765\uff0c\u5374\u662f\u5c06\u575a\u786c\u633a\u76f4\u7684\u4e1c\u897f\u638f\u8fdb\u6765\uff0c\u8bb8\u601d\u624d\u62c5\u5fc3\u8d77\u665a\u6674\u53ef\u80fd\u968f\u65f6\u4f1a\u95ef\u8fdb\u6765\uff1a\u201c\u665a\u6674\u8fdb\u6765\u600e\u4e48\u529e\uff1f\u201d\u5f20\u606a\u8fd9\u65f6\u7ba1\u4e0d\u4e86\u8fd9\u4e48\u591a\uff0c\u62b5\u7740\u8bb8\u601d\u7684\u4e24\u817f\u95f4\u6f6e\u6e7f\u5904\u6324\u9876\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7b2c\u4e00\u58f0\u5a07\u67d4\u7684\u5598\u606f\u5c31\u7ed9\u6324\u6210\u7ec6\u788e\u7684\u547b\u541f\uff0c\u5979\u7684\u547b\u541f\u58f0\u53c8\u5a07\u53c8\u67d4\uff0c\u54bf\u54bf\u545c\u545c\u4e0d\u6210\u8c03\uff0c\u6709\u4e00\u79cd\u5f02\u6837\u7684\u67d4\u5f31\u4e4b\u7f8e\uff0c\u8eab\u4e0a\u7684\u808c\u80a4\u4e5f\u7531\u96ea\u767d\u4f3c\u4e4e\u6ea5\u4e0a\u4e00\u5c42\u4e09\u6708\u521d\u6625\u7684\u8273\u7f8e\u6843\u7ea2\u3002\u4e0b\u8eab\u4e5f\u662f\u7ed9\u8d8a\u6233\u8d8a\u7d27\uff0c\u4e0e\u522b\u7684\u5973\u4eba\u4e0d\u540c\uff0c\u5979\u7684\u7d27\u662f\u79cd\u4ece\u5934\u88f9\u5230\u5c3e\u7684\u8089\u7d27\uff0c\u4e00\u9635\u7d27\u4f3c\u4e00\u9635\u7684\u7d27\uff0c\u547b\u541f\u9661\u7136\u95f4\u4e2d\u65ad\uff0c\u624b\u6293\u7740\u5f20\u606a\u7684\u80cc\u808c\uff0c\u7ec8\u662f\u4f20\u6765\u4e00\u58f0\u6e05\u6670\u5a07\u547c\uff1a\u201c\u6211\u2014\u4e0d\u884c\u4e86\u2014\u2014\u201d\u81c0\u90e8\u5ffd\u7684\u633a\u8d77\uff0c\u8eab\u5b50\u50f5\u76f4\u7684\u7ef7\u7d27\uff0c\u5979\u7684\u5185\u58c1\u75c9\u631b\u7d27\u7d27\u7ede\u7740\u5f20\u606a\u7684\u6839\u90e8\uff0c\u6ea2\u51fa\u5927\u91cf\u7684\u6c34\u6765\u3002</p><p>\u3000\u3000\u5927\u7ea6\u505c\u987f\u4e86\u6709\u56db\u4e94\u79d2\u949f\uff0c\u8bb8\u601d\u624d\u5410\u4e86\u4e00\u58f0\u5a07\u5598\uff0c\u5f20\u606a\u6402\u7d27\u5979\u60f3\u8ba9\u5979\u4eab\u53d7\u4e00\u4e0b\u9ad8\u6f6e\u540e\u7684\u4f59\u97f5\uff0c\u8ba9\u5979\u4f11\u606f\u4e00\u4f1a\u513f\u518d\u63a5\u7740\u6765\u7b2c\u4e8c\u56de\u5408\uff0c\u6ca1\u60f3\u5230\u8bb8\u601d\u7741\u5f00\u95ed\u4e86\u534a\u664c\u7684\u773c\u7738\u5b50\uff0c\u9661\u7136\u95f4\u624b\u4f38\u5230\u5e8a\u89d2\u6293\u8d77\u8695\u4e1d\u88ab\u3001\u8eab\u5b50\u7ffb\u5377\u7740\u5c06\u88ab\u5b50\u88f9\u5728\u8eab\u4e0a\u9003\u4e0b\u4e86\u5e8a\uff0c\u5634\u91cc\u5a07\u547c\u7740\uff1a\u201c\u4e22\u6b7b\u4eba\u4e86\uff01\u201d\u5c31\u5f80\u6d74\u5ba4\u91cc\u8eb2\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u89c1\u665a\u6674\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u8d70\u4e86\u8fdb\u6765\uff0c\u5979\u5a07\u8f6f\u65e0\u529b\u7684\u4f9d\u95e8\u7ad9\u7740\uff0c\u8138\u988a\u6f6e\u7ea2\u683c\u5916\u7684\u7f8e\u4e3d\u6027\u611f\uff0c\u773c\u7738\u91cc\u6563\u7740\u8ff7\u79bb\u7684\u5149\u8292\uff0c\u5979\u770b\u7740\u8bb8\u601d\u4ed3\u7687\u7684\u88f9\u7740\u88ab\u5b50\u9003\u8fdb\u6d74\u5ba4\u91cc\uff0c\u7b11\u9a82\u7740\uff1a\u201c\u6211\u53c8\u4e0d\u662f\u8fc7\u6765\u6349\u5978\u7684\uff0c\u4f60\u8eb2\u4ec0\u4e48\u8eb2\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ec0\u4e48\u65f6\u5019\u8fdb\u6765\u7684\uff0c\u6211\u600e\u4e48\u4e0d\u77e5\u9053\uff1f\u201d\u8bb8\u601d\u5728\u6d74\u5ba4\u91cc\u95ee\u9053\uff0c\u89c9\u5f97\u5373\u4f7f\u7ed9\u665a\u6674\u770b\u5230\u4e5f\u6ca1\u6709\u4ec0\u4e48\uff0c\u8fd8\u6709\u4e9b\u5bb3\u7f9e\uff0c\u72b9\u8c6b\u7740\u4e0d\u80af\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u4e48\u4eab\u53d7\uff0c\u5f53\u7136\u4e0d\u77e5\u9053\u6211\u4ec0\u4e48\u65f6\u5019\u8fdb\u6765\u7684\uff0c\u201d\u665a\u6674\u770b\u7740\u5f20\u606a\u5728\u6668\u5149\u8d64\u88f8\u7684\u8089\u4f53\uff0c\u5bf9\u4ed6\u4eec\u521a\u624d\u7f8e\u5999\u5165\u5fae\u7684\u6027\u4e8b\u5fc3\u91cc\u6709\u79cd\u67d0\u540d\u7684\u611f\u52a8\uff0c\u8bf4\u662f\u60c5\u6b32\u7684\u51b2\u52a8\u4e5f\u4e0d\u5982\u4e0d\u53ef\uff0c\u53ea\u662f\u5979\u6e05\u695a\u77e5\u9053\u81ea\u5df1\u6b64\u65f6\u9700\u8981\u4ec0\u4e48\uff0c\u671d\u6d74\u5ba4\u90a3\u91cc\u53c8\u8bf4\u4e86\u4e00\u53e5\uff0c\u201c\u80fd\u4e0d\u80fd\u5c06\u5f20\u606a\u8ba9\u7ed9\u6211\uff0c\u6211\u4e5f\u60f3\u4e86\u2026\u2026\u201d</p></article>"}}}]);
