(window.webpackJsonp=window.webpackJsonp||[]).push([[927],{950:function(p){p.exports={chapterTitle:null,title:"\u7b2c745\u7ae0 \u8c08\u5224\u6761\u4ef6",content:"<article><h2>\u7b2c745\u7ae0 \u8c08\u5224\u6761\u4ef6</h2><p>\u3000\u3000\u738b\u6d77\u7c9f\u5f88\u65e9\u5c31\u542c\u8bf4\u8fc7\u8c22\u5251\u5357\uff0c\u4ed6\u8fdb\u4e1c\u5927\u5c31\u8bfb\u8ba1\u7b97\u673a\u79d1\u5b66\u4e0e\u6280\u672f\u7cfb\u7684\u535a\u58eb\uff0c\u5c31\u4ece\u5f88\u591a\u4eba\u5634\u91cc\u542c\u8bf4\u8fc7\u8fd9\u4e2a\u540d\u5b57\uff0c\u542c\u8bf4\u8fc7\u4ed6\u7684\u5149\u8f89\u5386\u53f2\uff1a\u8bfb\u4e66\u671f\u95f4\u521b\u529e\u4fdd\u5065\u54c1\u516c\u53f8\uff0c\u5927\u5b66\u6bd5\u4e1a\u65f6\u516c\u53f8\u8d44\u4ea7\u8fc7\u4ebf\uff0c\u53c8\u6bc5\u7136\u51b3\u7136\u5c06\u516c\u53f8\u51fa\u552e\u7ed9\u5bb6\u65cf\u4f01\u4e1a\u53ea\u8eab\u524d\u5f80\u6d77\u5916\u7559\u5b66\uff0c\u7559\u5b66\u5f52\u6765\u53c8\u5728\u77ed\u65f6\u95f4\u91cc\u501f\u52a9\u5bb6\u65cf\u7684\u52bf\u529b\u8fde\u7eed\u521b\u529e\u6d77\u6cf0\u7535\u5668\u3001\u79d1\u738b\u7535\u5668\u8fd9\u6837\u4e24\u5bb6\u5728\u4e1a\u5185\u6781\u6709\u5f71\u54cd\u529b\u7684\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u8db3\u4ee5\u79f0\u4e3a\u4e4b\u4e3a\u4f20\u5947\u7684\u521b\u4e16\u4e8b\u8ff9\u3002</p><p>\u3000\u3000\u5766\u627f\u7684\u8bf4\uff0c\u738b\u6d77\u7c9f\u4ed6\u81ea\u5df1\u4e0b\u51b3\u5fc3\u4e0b\u6d77\u521b\u529e\u516c\u53f8\u4e5f\u662f\u53d7\u8c22\u5251\u5357\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u8981\u662f\u6ca1\u6709\u673a\u4f1a\u63a5\u89e6\u5230\u5bfb\u5e38\u4eba\u65e0\u6cd5\u63a5\u89e6\u5230\u7684\u90a3\u4e2a\u795e\u79d8\u89d2\u843d\uff0c\u6b64\u65f6\u7684\u738b\u6d77\u7c9f\u5185\u5fc3\u5bf9\u8c22\u5251\u5357\u4e00\u5b9a\u6d0b\u6ea2\u7740\u5d07\u62dc\u4e4b\u60c5\u3002</p><p>\u3000\u3000\u5728\u653e\u5f03\u6d77\u7c9f\u79d1\u6280\u7684\u63a7\u80a1\u4e4b\u540e\uff0c\u6d77\u7c9f\u516c\u53f8\u5c31\u4ece\u4f53\u6821\u80cc\u540e\u72ed\u9698\u9634\u6697\u7684\u529e\u516c\u697c\u8fc1\u5230\u751c\u6c34\u4e95\u897f\u8def\u7684\u7cbe\u5178\u5927\u53a6\u4e4b\u5185\u3002\u5bbd\u655e\u660e\u4eae\u7684\u529e\u516c\u5ba4\uff0c\u4e00\u6e9c\u5230\u5730\u677f\u7684\u843d\u5730\u7a97\uff0c\u51dd\u89c6\u7740\u7a97\u5916\u68a7\u6850\u6811\u679d\u5934\u6700\u540e\u96f6\u788e\u51e0\u7247\u6811\u53f6\u7ed9\u51db\u51bd\u7684\u5bd2\u98ce\u6253\u843d\uff0c\u5728\u7a7a\u4e2d\u76d8\u65cb\u7740\u800c\u4e0b\uff0c\u8857\u4e0a\u7684\u884c\u4eba\u8138\u8272\u7ed9\u51bb\u5f97\u53d1\u9752\uff0c\u800c\u529e\u516c\u5ba4\u91cc\u6e29\u6696\u5982\u6625\uff0c\u65b0\u8058\u7684\u79d8\u4e66\u8fd8\u6709\u9752\u6da9\u7684\u5a07\u7f9e\uff0c\u738b\u6d77\u7c9f\u6b64\u65f6\u51ff\u51ff\u5b9e\u5b9e\u7684\u5728\u4eab\u53d7\u6210\u529f\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5143\u65e6\u662f\u56fd\u5185\u6cd5\u5b9a\u7684\u5047\u65e5\uff0c\u53ef\u60dc\u5f88\u591a\u4eba\u90fd\u6ca1\u6cd5\u53bb\u4eab\u53d7\u8fd9\u4e2a\u6cd5\u5b9a\u7684\u5047\u65e5\uff0c\u738b\u6d77\u7c9f\u5374\u9ad8\u5174\u5728\u8282\u5047\u65e5\u4e5f\u5750\u5230\u529e\u516c\u5ba4\u91cc\u6765\u4eab\u53d7\u6210\u529f\u4eba\u58eb\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u5728\u7cbe\u5178\u5927\u53a6\u89c1\u5230\u8c22\u5251\u5357\uff0c\u89c1\u4ed6\u8eab\u6750\u9ad8\u524a\uff0c\u6709\u5f20\u8ba9\u5973\u5b69\u5b50\u559c\u6b22\u7684\u68f1\u89d2\u5206\u660e\u7684\u8138\uff0c\u7a7f\u7740\u70df\u7070\u8272\u7684\u5916\u5957\uff0c\u5fc3\u60f3\u4e5f\u4e0d\u8fc7\u5982\u6b64\u3002</p><p>\u3000\u3000\u77e5\u9053\u6574\u5929\u5728\u5b66\u5e9c\u5df7\u6175\u6563\u7684\u8ddf\u6761\u8eba\u5728\u5899\u89d2\u6652\u592a\u9633\u61d2\u72d7\u4f3c\u7684\u5c11\u5e74\u7adf\u7136\u662f\u5e9e\u5927\u9526\u6e56\u7684\u7f14\u9020\u8005\uff0c\u5728\u5982\u6b64\u5f3a\u70c8\u7684\u5149\u8292\u4e0b\uff0c\u5176\u4ed6\u4efb\u4f55\u5c11\u5e74\u5f97\u5fd7\u7684\u4eba\u90fd\u4f1a\u663e\u5f97\u9eef\u7136\u65e0\u5149\uff0c\u738b\u6d77\u7c9f\u5bf9\u8c22\u5251\u5357\u7684\u8f7b\u89c6\u5e76\u4e0d\u662f\u4f1a\u8ba9\u4eba\u611f\u5230\u592a\u610f\u5916\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u738b\u6d77\u7c9f\u8fd8\u4ece\u6797\u96ea\u90a3\u91cc\u77e5\u9053\uff0c\u79d1\u738b\u5728\u5e02\u573a\u8425\u9500\u65b9\u9762\u7684\u8d85\u8282\u5236\u652f\u51fa\uff0c\u53ef\u80fd\u8981\u627f\u62c5\u8fd1\u4e24\u4ebf\u5143\u7684\u5de8\u989d\u635f\u5931\u3002\u6574\u6574\u4e24\u4e2a\u4ebf\u554a\uff01\u4e8f\u4ed6\u8fd8\u80fd\u5982\u6b64\u82e5\u65e0\u5176\u4e8b\u7684\u5750\u5728\u8fd9\u91cc\u8ddf\u81ea\u5df1\u8ba8\u8bba\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff1f\u7cbe\u5178\u5730\u4ea7\u4e0e\u6b63\u6cf0\u96c6\u56e2\u5728\u6d77\u5dde\u6709\u7740\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u738b\u6d77\u7c9f\u8fd8\u80fd\u514b\u5236\u7684\u4fdd\u6301\u8868\u9762\u4e0a\u7684\u8c26\u606d\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6628\u5929\u901a\u5bb5\u90fd\u5728\u770b\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u9879\u76ee\u8d44\u6599\uff0c\u9664\u4e86\u6709\u4e9b\u5927\u800c\u4e0d\u5f53\u4e4b\u5916\uff0c\u603b\u4f53\u7684\u8bbe\u60f3\u975e\u5e38\u7684\u4e0d\u9519\u3002\u8c22\u5251\u5357\u4e5f\u6653\u5f97\u5c06\u9879\u76ee\u641e\u5927\u662f\u6240\u6709\u5b98\u5458\u597d\u5927\u559c\u529f\u7684\u901a\u75c5\uff0c\u6bdb\u75c5\u5e76\u6ca1\u6709\u51fa\u5728\u6700\u521d\u7684\u9879\u76ee\u7b56\u5212\u8005\u7684\u8eab\u4e0a\uff0c\u8c22\u5251\u5357\u662f\u8fd9\u4e48\u8ba4\u4e3a\u7684\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7684\u611f\u89c9\u6ca1\u6709\u9519\uff0c\u53ea\u662f\u4ed6\u4e0d\u6653\u5f97\u8c01\u624d\u662f\u771f\u6b63\u7684\u6700\u521d\u9879\u76ee\u7b56\u5212\u8005\u3002</p><p>\u3000\u3000\u6b63\u6cf0\u96c6\u56e2\u8981\u8054\u5408\u5609\u4fe1\u7535\u5b50\u5728\u91d1\u5c71\u7b79\u5efa\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\uff0c\u8f6f\u786c\u4ef6\u662f\u76f8\u8f85\u76f8\u6210\u7684\u884c\u4e1a\uff0c\u8c22\u5251\u5357\u5c31\u5728\u8003\u8651\u6709\u6ca1\u6709\u53ef\u80fd\u5728\u91d1\u5c71\u590d\u5236\u4e00\u4e2a\u8f83\u5c0f\u89c4\u6a21\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5f62\u6210\u8f6f\u4ef6\u516c\u53f8\u7684\u96c6\u7fa4\u63d0\u4f9b\u8ba1\u7b97\u673a\u8f6f\u4ef6\u54a8\u8be2\u3001\u8f6f\u4ef6\u5f00\u53d1\u3001\u4fe1\u606f\u7cfb\u7edf\u96c6\u6210\u7b49\u65b9\u9762\u7684\u670d\u52a1\u3002\u73b0\u5728\u5168\u56fd\u5404\u5730\u90fd\u5728\u5174\u8d77\u4fe1\u606f\u5316\u5efa\u8bbe\u7684\u9ad8\u6f6e\uff0c\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u662f\u6709\u4e00\u5b9a\u5e02\u573a\u524d\u666f\u7684\u3002</p><p>\u3000\u3000\u5609\u4fe1\u7535\u5b50\u867d\u7136\u5f88\u5c11\u6d89\u53ca\u8f6f\u4ef6\u884c\u4e1a\uff0c\u4f46\u662f\u4ee5\u845b\u5bb6\u5728\u9999\u6e2f\u7684\u5f71\u54cd\u529b\uff0c\u8981\u662f\u52aa\u529b\u53bb\u8fd0\u8425\u7684\u8bdd\uff0c\u80fd\u591f\u5f62\u6210\u9999\u6e2f\u8f6f\u4ef6\u4ea7\u4e1a\u4e0e\u5185\u5730\u8f6f\u4ef6\u4ea7\u4e1a\u5728\u67d0\u79cd\u7a0b\u5ea6\u4e0a\u7684\u76f8\u4e92\u652f\u6491\u4e0e\u8054\u7cfb\u3002</p><p>\u3000\u3000\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u5b9e\u9645\u4e0a\u8981\u7b97\u5546\u4e1a\u5730\u4ea7\u9879\u76ee\uff0c\u5546\u4e1a\u5730\u4ea7\u9879\u76ee\u53c8\u662f\u5609\u4fe1\u5730\u4ea7\u7684\u5f3a\u9879\uff0c\u4e5f\u662f\u6b63\u6cf0\u96c6\u56e2\u6b64\u65f6\u60f3\u8fdb\u5165\u7684\u4ea7\u4e1a\uff0c\u8981\u662f\u6797\u96ea\u80cc\u540e\u7684\u845b\u5efa\u5fb7\u613f\u610f\u4e0d\u8f9e\u8f9b\u82e6\u7684\u8bdd\uff0c\u8ba9\u7cbe\u5178\u5730\u4ea7\u5171\u540c\u53c2\u4e0e\u91d1\u5c71\u7684\u9879\u76ee\uff0c\u4e5f\u4e0d\u662f\u4e0d\u53ef\u4ee5\u505a\u51fa\u7684\u9009\u62e9\u3002</p><p>\u3000\u3000\u5c3d\u7ba1\u8c22\u5251\u5357\u539f\u5148\u6ca1\u6709\u5230\u5efa\u90ba\u540e\u4e0e\u6797\u96ea\u8fd9\u4e2a\u5973\u4eba\u6253\u4ea4\u9053\u7684\u610f\u601d\uff0c\u8fd9\u65f6\u5019\u53c8\u6539\u53d8\u60f3\u6cd5\uff0c\u60f3\u7740\u627e\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u5c31\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u4ea4\u6d41\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u770b\u5230\u738b\u6d77\u7c9f\u773c\u775b\u91cc\u6536\u655b\u5374\u4f9d\u65e7\u80fd\u770b\u51fa\u6765\u7684\u61c8\u6020\u795e\u8272\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u591a\u5c11\u611f\u5230\u4e9b\u4e0d\u8212\u670d\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u4ea4\u6d41\u4e2d\uff0c\u4e0d\u6653\u5f97\u662f\u738b\u6d77\u7c9f\u7684\u61c8\u6020\u8fd8\u662f\u738b\u6d77\u7c9f\u4e0e\u4eba\u4ea4\u6d41\u65f6\u7684\u62d8\u8c28\uff0c\u8c22\u5251\u5357\u5e76\u6ca1\u6709\u4ece\u4ed6\u8eab\u4e0a\u611f\u53d7\u5230\u90a3\u79cd\u5e94\u8be5\u8ba9\u4eba\u76ee\u70ab\u7684\u624d\u534e\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u6ca1\u6709\u63a9\u9970\u81ea\u5df1\u7684\u610f\u56fe\uff0c\u76f4\u63a5\u8ddf\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u63d0\u51fa\u6709\u6ca1\u6709\u5728\u91d1\u5c71\u590d\u5236\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u53ef\u80fd\uff0c\u8ddf\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u76f4\u63a5\u63d0\u51fa\u6765\uff0c\u4e5f\u662f\u770b\u4e2d\u80cc\u540e\u845b\u5efa\u5fb7\u7684\u80fd\u8010\u3002</p><p>\u3000\u3000\u5728\u8c22\u5251\u5357\u773c\u91cc\uff0c\u62e5\u6709\u4e0d\u5230\u4e94\u5341\u540d\u5458\u5de5\u7684\u6d77\u7c9f\u79d1\u6280\u867d\u7136\u5728\u5efa\u90ba\u8981\u7b97\u4e00\u5bb6\u4e0a\u89c4\u6a21\u7684\u8f6f\u4ef6\u516c\u53f8\uff0c\u53ef\u60dc\u6bd4\u8d77\u5e9e\u5927\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u8ba1\u5212\uff0c\u8fd9\u6837\u89c4\u6a21\u7684\u516c\u53f8\u5c31\u50cf\u662f\u8981\u652f\u6301\u4e00\u6574\u5757\u697c\u677f\u7684\u7af9\u7b77\u5b50\u90a3\u6837\u5355\u8584\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u7684\u80fd\u8010\u751a\u81f3\u8ba9\u4e2d\u79d1\u9662\u8f6f\u4ef6\u7814\u7a76\u6240\u4e0e\u7701\u79d1\u6280\u672f\u4e0b\u5c5e\u7684\u91d1\u9f0e\u79d1\u6280\u6210\u4e3a\u6d77\u7c9f\u79d1\u6280\u63a8\u52a8\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u7684\u534f\u4f5c\u8005\uff0c\u8fd9\u6837\u7684\u80fd\u8010\u624d\u662f\u8c22\u5251\u5357\u5e0c\u671b\u501f\u91cd\u7684\u3002</p><p>\u3000\u3000\u6797\u96ea\u5f53\u7136\u6709\u5979\u7684\u987e\u8651\uff0c\u8d44\u6e90\u7ed9\u5206\u6563\u5230\u91d1\u5c71\u662f\u8096\u660e\u5efa\u3001\u80e1\u5b97\u5e86\u4e0d\u5e0c\u671b\u770b\u5230\u7684\u7ed3\u679c\u3002\u5f53\u7136\uff0c\u80e1\u5b97\u5e86\u5728\u6d77\u7c9f\u516c\u53f8\u79d8\u5bc6\u6301\u80a1\uff0c\u53ea\u8981\u83b7\u5f97\u5de8\u989d\u5229\u76ca\uff0c\u653f\u7ee9\u8981\u4e0d\u8981\u5c31\u4e0d\u662f\u4ed6\u7279\u522b\u5173\u5fc3\u7684\u4e8b\u60c5\u4e86\uff0c\u5173\u952e\u540c\u65f6\u5728\u91d1\u5c71\u590d\u5236\u4e00\u4e2a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u80fd\u5426\u589e\u52a0\u672a\u6765\u7684\u83b7\u5229\u9884\u671f\uff1f</p><p>\u3000\u3000\u4ece\u6797\u96ea\u90a3\u91cc\uff0c\u8c22\u5251\u5357\u6ca1\u6709\u5f97\u5230\u660e\u786e\u7684\u7b54\u590d\uff0c\u5728\u7cbe\u5178\u5927\u53a6\u7528\u8fc7\u4e2d\u9910\u540e\uff0c\u8d76\u5230\u5e9c\u9752\u8def\u7684\u6d77\u6cf0\u7535\u5668\u96c6\u56e2\u603b\u90e8\u627e\u8c22\u610f\u8c08\u8d77\u8fd9\u4ef6\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u867d\u7136\u8c22\u5b50\u5609\u5728\u79d1\u738b\u9ad8\u79d1\u6301\u80a1\u7684\u4e8b\u60c5\u8ba9\u5bb6\u65cf\u5185\u90e8\u4ea7\u751f\u4e9b\u88c2\u75d5\uff0c\u4e0d\u8bba\u600e\u4e48\u6837\uff0c\u6574\u4e2a\u5bb6\u65cf\u8fd8\u4fdd\u6301\u5728\u5927\u4f53\u4e00\u81f4\u7684\u65b9\u5411\u4e0a\u3002\u9526\u6e56\u53c8\u662f\u5982\u6b64\u7684\u5f3a\u52bf\uff0c\u66f4\u8981\u4fdd\u6301\u6574\u4e2a\u5bb6\u65cf\u7684\u56e2\u7ed3\uff0c\u4e5f\u53ea\u80fd\u9ed8\u8ba4\u8c22\u5b50\u5609\u5728\u79d1\u738b\u9ad8\u79d1\u6301\u80a1\u7684\u4e8b\u5b9e\u3002</p><p>\u3000\u3000\u8c22\u610f\u542c\u8c22\u5251\u5357\u8bf4\u8d77\u6765\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u7684\u4e8b\u60c5\uff0c\u601d\u91cf\u7247\u523b\u540e\u8bf4\u9053\uff1a\u201c\u845b\u5efa\u5fb7\u624d\u662f\u5e55\u540e\u7684\u5173\u952e\u4eba\u7269\uff0c\u845b\u5efa\u5fb7\u624d\u4e0d\u662f\u4f1a\u4ecb\u610f\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u662f\u5efa\u5728\u5efa\u90ba\u8fd8\u662f\u5efa\u5728\u91d1\u5c71\uff0c\u80fd\u63a8\u52a8\u5728\u5efa\u90ba\u3001\u91d1\u5c71\u540c\u65f6\u4e0a\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\u5bf9\u4ed6\u6765\u8bf4\u5c82\u4e0d\u662f\u66f4\u597d\u7684\u4e8b\u60c5\uff1f\u65e0\u975e\u662f\u9700\u8981\u4e00\u4e2a\u542c\u4e4b\u53ef\u884c\u7684\u540d\u4e49\u7f62\u4e86\u3002\u4e1c\u6d77\u7701\u63a8\u52a8\u9ad8\u65b0\u4ea7\u4e1a\u65b0\u6218\u7565\uff0c\u5c06\u6d77\u5dde\u7684\u5de5\u4e1a\u65b0\u533a\u3001\u60e0\u5c71\u5e02\u7684\u6ee8\u6c5f\u5de5\u4e1a\u56ed\u3001\u5efa\u90ba\u5e02\u7684\u6570\u5b57\u957f\u5eca\u6784\u60f3\u4e32\u6210\u4e00\u4e2a\u9ad8\u65b0\u4ea7\u4e1a\u5c0f\u6c5f\u8d70\u5eca\uff0c\u90a3\u5efa\u90ba\u5e02\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u4e0e\u91d1\u5c71\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u4e3a\u4ec0\u4e48\u5c31\u4e0d\u80fd\u4e32\u6210\u8f6f\u4ef6\u4ea7\u4e1a\u5e26\uff1f\u90a3\u4efd\u7acb\u9879\u7b56\u5212\u4e66\u6211\u6ca1\u6709\u770b\u8fc7\uff0c\u4e0d\u8fc7\u53ef\u4ee5\u7565\u52a0\u4fee\u6539\uff0c\u7acb\u610f\u8fd8\u53ef\u4ee5\u66f4\u9ad8\u4e00\u4e9b\uff0c\u8003\u8651\u5f15\u5bfc\u9999\u6e2f\u7684\u8f6f\u4ef6\u4f01\u4e1a\u5411\u5185\u5730\u57ce\u5e02\u8fdb\u884c\u4ea7\u4e1a\u8f6c\u79fb\uff0c\u4f60\u8bf4\u845b\u5efa\u5fb7\u4f1a\u4e0d\u4f1a\u52a8\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u6240\u8c13\u7684\u96be\u9898\u4e0d\u8fc7\u5982\u6b64\u7b80\u5355\u5c31\u80fd\u5316\u89e3\uff0c\u8c22\u5251\u5357\u4e5f\u4e0d\u5f97\u4e0d\u627f\u8ba4\u8c22\u610f\u5728\u4ea7\u4e1a\u5b8f\u89c2\u7684\u76ee\u5149\u6bd4\u81ea\u5df1\u66f4\u8fdc\uff0c\u8fd9\u4e2a\u4e3b\u610f\u53ef\u4ee5\u62cd\u6848\u53eb\u7edd\u4e86\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f53\u5373\u62c9\u7740\u8c22\u610f\u53bb\u627e\u4ed6\u5927\u8205\u5468\u747e\u73ba\uff0c\u4ed6\u4e0d\u60f3\u901a\u8fc7\u738b\u6d77\u7c9f\u6216\u6797\u96ea\u5c45\u4e2d\u4f20\u8bdd\uff0c\u5e0c\u671b\u80fd\u76f4\u63a5\u627e\u845b\u5efa\u5fb7\u8ba8\u8bba\u8fd9\u4e2a\u8ba1\u5212\u7684\u53ef\u884c\u6027\u3002</p><p>\u3000\u3000\u6bd4\u8f83\u8d77\u5efa\u90ba\u5e02\u8fd1\u671f\u7684\u5927\u52a8\u4f5c\uff0c\u5f88\u53ef\u80fd\u56e0\u4e3a\u9526\u6e56\u516c\u5f00\u6570\u5b57\u624b\u673a\u6280\u672f\u800c\u5728\u5efa\u90ba\u5f62\u6210\u4e00\u4e2a\u5e9e\u5927\u7684\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u6b63\u6cf0\u96c6\u56e2\u8054\u5408\u5609\u4fe1\u7535\u5b50\u5728\u91d1\u5c71\u542f\u52a8\u7535\u5b50\u5de5\u4e1a\u5236\u9020\u57fa\u5730\u7684\u8ba1\u5212\u5f97\u5230\u7684\u54cd\u5e94\u58f0\u97f3\u5c31\u53d8\u5f97\u5f88\u5f31\uff0c\u4e0d\u4ec5\u8ba9\u5468\u747e\u745c\u8d70\u9a6c\u4e0a\u4efb\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u6709\u4e9b\u7070\u7709\u571f\u8138\uff0c\u4e5f\u8ba9\u6c5f\u5357\u7701\u59d4\u591a\u4e86\u4e00\u4e9b\u8fdf\u7591\uff1b\u8fd9\u65f6\u5019\u4e5f\u9700\u8981\u5f15\u8fdb\u53e6\u4e00\u4e2a\u91cd\u5927\u9879\u76ee\u6765\u7a33\u5b9a\u6c5f\u5357\u7701\u59d4\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u53ef\u4e0d\u6653\u5f97\u4ed6\u901a\u8fc7\u738b\u6d77\u7c9f\u629b\u51fa\u53bb\u7684\u9999\u9975\u5373\u5c06\u9762\u4e34\u518d\u4e00\u6b21\u7684\u5f02\u5316\uff0c\u51b7\u5bc2\u5929\u7a7a\u518d\u6b21\u98d8\u843d\u4e0b\u5916\u5957\u94ae\u6263\u5927\u5c0f\u7684\u96ea\u82b1\uff0c\u597d\u5728\u5510\u5a67\u5728\u9ad8\u901f\u8def\u79ef\u96ea\u7ed3\u51b0\u4e4b\u524d\u8d76\u5230\u5efa\u90ba\u3002</p><p>\u3000\u3000\u5ba4\u5916\u5929\u5bd2\u5730\u51bb\u7684\uff0c\u5510\u5a67\u5728\u8f66\u91cc\u67af\u5750\u4e86\u4e09\u4e2a\u5c0f\u65f6\uff0c\u4e0b\u4e86\u8f66\u5c31\u4e0d\u9ad8\u5174\u94bb\u8fdb\u5f00\u7a7a\u8c03\u7684\u5c4b\u91cc\uff0c\u62c9\u7740\u5f20\u606a\u53bb\u6e56\u8fb9\u770b\u9ec4\u660f\u65f6\u6e56\u9762\u4e0a\u7684\u843d\u96ea\u3002\u8fd1\u5cb8\u7684\u6e56\u6c34\u65e9\u5df2\u539a\u539a\u7684\u7ed3\u4e86\u4e00\u5c42\u51b0\uff0c\u96ea\u82b1\u843d\u5728\u51b0\u4e0a\uff0c\u5f88\u5feb\u5c31\u79ef\u4e86\u8d77\u6765\uff0c\u8fd9\u5df2\u7ecf\u662f\u8fd9\u4e2a\u51ac\u5929\u7684\u7b2c\u4e09\u573a\u96ea\u4e86\u3002</p><p>\u3000\u3000\u5510\u5a67\u5fc3\u601d\u806a\u6167\uff0c\u5f20\u606a\u4e0e\u5979\u5728\u6e56\u8fb9\u6563\u6b65\uff0c\u4e5f\u4e0d\u4f1a\u523b\u610f\u7684\u7ed5\u8fc7\u6e56\u7554\u5c0f\u5c4b\u7684\u65b9\u4f4d\uff0c\u773c\u89c1\u5c4b\u6a90\u4ece\u7a00\u758f\u7684\u6811\u4e1b\u91cc\u9732\u51fa\u4e00\u89d2\uff0c\u5f20\u606a\u4ece\u8863\u515c\u91cc\u638f\u51fa\u4e00\u53ea\u7cbe\u81f4\u79c0\u7f8e\u7684MP3\u64ad\u653e\u5668\u6765\u9012\u7ed9\u5510\u5a67\uff1a\u201c\u554a\uff0c\u65b0\u5e74\u793c\u7269\uff0c\u5dee\u70b9\u5fd8\u4e86\u62ff\u7ed9\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5a67\u62bf\u5507\u4e00\u7b11\uff0c\u867d\u7136\u9644\u8fd1\u6ca1\u6709\u5176\u4ed6\u4eba\u5728\uff0c\u8fd8\u662f\u9644\u5230\u5f20\u606a\u7684\u8033\u8fb9\u8ddf\u4ed6\u54ac\u8033\u6735\u8bf4\uff1a\u201c\u4f60\u592a\u574f\u4e86\uff0c\u8d70\u5230\u8fd1\u5904\u770b\u770b\u4e5f\u4e0d\u884c\u5417\uff1f\u201d</p><p>\u3000\u3000\u8fd8\u62fd\u7740\u5510\u5a67\u7684\u624b\u63d2\u5728\u81ea\u5df1\u7684\u5916\u5957\u515c\u91cc\uff0c\u5f20\u606a\u8baa\u7136\u7684\u7b11\u4e86\u7b11\uff0c\u8fd8\u4e0e\u5510\u5a67\u5e76\u80a9\u5f80\u524d\u8d70\uff0c\u8fb9\u8d70\u8fb9\u5e2e\u5979\u5c06\u8033\u673a\u7ed9\u6234\u4e0a\u3002</p><p>\u3000\u3000\u201ciplayer\u2026\u2026\u201d\u5510\u5a67\u770b\u7740\u64ad\u653e\u5668\u4e0a\u7684\u82f1\u6587\u6807\u8bc6\uff0c\u95ee\u5f20\u606a\uff0c\u201c\u8fd9\u5c31\u662f\u4f60\u4eec\u795e\u795e\u53e8\u53e8\u4e86\u8bb8\u4e45\u5374\u4e00\u76f4\u6ca1\u89c1\u8e2a\u5f71\u7684\u65b0\u73a9\u827a\u513f\uff0c\u4f1a\u5f88\u5feb\u5c31\u4e0a\u5e02\u9500\u552e\u5417\uff0c\u4e5f\u662f\u8fd9\u4e2a\u82f1\u6587\u540d\u79f0\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u5207\u90fd\u987a\u5229\u7684\u8bdd\uff0c\u4f1a\u5f88\u5feb\u5c31\u4e0a\u5e02\u9500\u552e\u3002\u6807\u51c6\u7684\u540d\u79f0\u5e94\u8be5\u662f\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u6216\u8005\u662f\u6570\u5b57\u97f3\u4e50\u64ad\u653e\u5668\uff0ciplayer\u53ea\u662f\u6211\u4eec\u4e3a\u8fd9\u73a9\u827a\u513f\u6ce8\u518c\u7684\u82f1\u6587\u6807\u8bc6\uff0c\u8981\u80fd\u6210\u4e3a\u66ff\u4ee3\u6027\u7684\u79f0\u8c13\uff0c\u90a3\u5c31\u5411\u771f\u6b63\u7684\u6210\u529f\u8d70\u8fd1\u4e86\u4e00\u5927\u6b65\u2026\u2026\u201d\u5929\u6c14\u5bd2\u51b7\uff0c\u5f20\u606a\u8ba9\u5510\u5a67\u5c06\u624b\u63d2\u8863\u515c\u91cc\uff0c\u4ed6\u5e2e\u5979\u8c03\u8282iplayer\u7684\u6309\u94ae\uff0c\u201c\u4f60\u8ba4\u771f\u542c\u5c31\u53ef\u4ee5\u4e86\uff0c\u6211\u7279\u610f\u5e2e\u4f60\u9009\u62e9\u4f60\u559c\u6b22\u542c\u7684\u6b4c\u66f2\uff0c\u4f60\u4ee5\u540e\u559c\u6b22\u542c\u4ec0\u4e48\u6b4c\uff0c\u76f4\u63a5\u4e0b\u8f7d\u5c31\u53ef\u4ee5\u4e86\uff0c\u8fd9\u53ea\u64ad\u653e\u5668\u662f\u7279\u610f\u53d6\u6d88\u8f6f\u4ef6\u9650\u5236\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8f6f\u4ef6\u9650\u5236\uff0c\u9650\u5236\u4ec0\u4e48\uff1f\u201d\u5510\u5a67\u4e0d\u89e3\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u9632\u6b62\u4e0b\u8f7d\u6b4c\u66f2\u901a\u8fc7iplayer\u5411\u5176\u4ed6\u8ba1\u7b97\u673a\u4f20\u64ad\u7684\u76f8\u5173\u6280\u672f\uff0c\u5373\u5c06\u9762\u4e34\u7684\u6b27\u7f8e\u3001\u65e5\u97e9\u5e02\u573a\uff0c\u5728\u6570\u5b57\u7248\u6743\u4fdd\u62a4\u4e0a\u7684\u6cd5\u5f8b\u4f53\u7cfb\u975e\u5e38\u4e25\u5bc6\u800c\u82db\u523b\uff0c\u6211\u4eec\u5fc5\u987b\u8981\u9075\u5faa\u522b\u4eba\u7684\u6e38\u620f\u89c4\u5212\uff0c\u4e0d\u7136\u5f88\u5bb9\u6613\u8ba9\u4eba\u5bb6\u6293\u5230\u628a\u67c4\u8e22\u51fa\u5c40\uff0c\u201d\u5f20\u606a\u4ece\u80cc\u540e\u5c06\u5510\u5a67\u8f7b\u8f7b\u7684\u6402\u4f4f\uff0c\u62ff\u8fc7\u4e00\u53ea\u8033\u6735\u585e\u5230\u81ea\u5df1\u7684\u8033\u6735\uff0c\u542c\u7740\u97f3\u4e50\u8fd8\u7ed9\u5979\u89e3\u91ca\u4e00\u4e9b\u6280\u672f\u4e0a\u7684\u95ee\u9898\uff0c\u201c\u5f53\u7136\u8fd8\u6709\u5176\u4ed6\u7684\u4e00\u4e9b\u9650\u5236\uff0c\u6bd4\u5982\u8bf4\u9650\u5236\u4ece\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u8d2d\u4e70\u7684\u6b4c\u66f2\u53ea\u80fd\u5728\u6709\u6211\u4eec\u8bb8\u53ef\u6388\u6743\u751f\u4ea7\u3001\u9500\u552e\u7684\u786c\u4ef6\u4e0a\u64ad\u653e\u2026\u2026\u603b\u4e4b\u90fd\u662f\u4e9b\u8ba9\u4eba\u8111\u888b\u90fd\u4f1a\u70b8\u6389\u7684\u9650\u5236\u6761\u4ef6\u3002\u201d</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u6709\u4e9b\u6761\u4ef6\u662f\u4e3a\u4e86\u9002\u5e94\u6e38\u620f\u89c4\u5219\uff0c\u6709\u4e9b\u6761\u4ef6\u5219\u662f\u4e3a\u4e86\u9650\u5236\u83b7\u5f97\u751f\u4ea7\u3001\u9500\u552e\u8bb8\u53ef\u6388\u6743\u7684\u7535\u5b50\u5382\u5546\uff0c\u907f\u514d\u4ed6\u4eec\u6210\u4e3a\u9526\u6e56\u6700\u7ec8\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u8111\u888b\u8be5\u7206\u70b8\u6389\u7684\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u9526\u6e56\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u6761\u4ef6\u592a\u82db\u523b\u4e86\u2026\u2026\u201d\u827e\u9ed8\u770b\u5230\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u5728\u5317\u7f8e\u5730\u533a\u751f\u4ea7\u3001\u9500\u552e\u7684\u6388\u6743\uff0c\u5fcd\u4e0d\u4f4f\u6293\u8d77\u5934\u53d1\uff0c\u76b1\u7709\u63ea\u773c\u7684\u9732\u51fa\u75db\u82e6\u7684\u8868\u60c5\uff0c\u671d\u9648\u4fe1\u751f\u5927\u58f0\u62b1\u6028\uff0c\u201c\u606a\u5c11\u5c31\u8fd9\u4e48\u5bf9\u5f85\u4ed6\u771f\u8bda\u7684\u670b\u53cb\uff1f\u6211\u771f\u6000\u7591\u4ed6\u81ea\u5df1\u6ca1\u6709\u52c7\u6c14\u5c06\u8fd9\u4efd\u6587\u4ef6\u6446\u5230\u6211\u9762\u524d\uff0c\u624d\u5c06\u82e6\u5dee\u4e8b\u4ea4\u7ed9\u4f60\u6765\u505a\u2026\u2026\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u827e\u9ed8\u8868\u6f14\u6027\u8d28\u7684\u53cd\u5e94\uff0c\u9648\u4fe1\u751f\u89c6\u82e5\u65e0\u7779\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u6211\u521a\u63a5\u5230\u6d88\u606f\u542c\u8bf4\u606a\u5c11\u5929\u5bd2\u5730\u51bb\u7684\u966a\u5973\u5b69\u5b50\u5728\u6e56\u8fb9\u6563\u5e03\u5462\uff0c\u76f8\u6bd4\u8f83\uff0c\u6211\u73b0\u5728\u505a\u7684\u7684\u786e\u662f\u4ef6\u82e6\u5dee\u4e8b\u2026\u2026\u8fd9\u4e00\u70b9\uff0c\u6211\u4e0a\u5348\u5c31\u8ddf\u4ed6\u62b1\u6028\u8fc7\u4e86\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u6e29\u541e\u6c34\u7684\u53cd\u5e94\uff0c\u8ba9\u827e\u9ed8\u65e0\u6cd5\u7ee7\u7eed\u521a\u624d\u7684\u8868\u6f14\uff0c\u8d70\u5230\u7a97\u524d\uff0c\u7a97\u5916\u98d8\u7740\u6986\u6811\u53f6\u5927\u5c0f\u7684\u96ea\u82b1\uff0c\u767d\u832b\u832b\u7684\u4e00\u7247\uff0c\u5f80\u4e1c\u5c31\u6ca1\u6709\u9ad8\u5c42\u5efa\u7b51\uff0c\u7ad9\u5728\u5e0c\u5c14\u987f\u5927\u9152\u5e97\u7684\u9ad8\u5c42\u4e0a\uff0c\u80fd\u591f\u4e00\u76ee\u4e86\u7136\u7684\u770b\u5230\u71d5\u5f52\u6e56\u4e0e\u6e56\u8fb9\u7684\u5c71\u5ce6\uff0c\u53ea\u662f\u4e0b\u96ea\u5929\u6c14\u80fd\u770b\u5230\u7684\u8fdc\u5904\u5341\u5206\u7684\u6709\u9650\u3002</p><p>\u3000\u3000\u5373\u4f7f\u8fd9\u6837\u7684\u5929\u6c14\uff0c\u80fd\u966a\u5973\u5b69\u5b50\u5728\u6e56\u8fb9\u6563\u8d70\uff0c\u4e5f\u6bd4\u5750\u5728\u623f\u95f4\u91cc\u8ddf\u72e1\u733e\u800c\u8d2a\u5a6a\u7684\u5bf9\u624b\u8c08\u5224\u611f\u89c9\u8981\u597d\uff0c\u827e\u9ed8\u5fc3\u91cc\u60f3\u7740\u3002</p><p>\u3000\u3000\u5728\u827e\u9ed8\u6b64\u65f6\u7684\u5fc3\u91cc\uff0c\u5f20\u606a\u3001\u9648\u4fe1\u751f\u3001\u8096\u664b\u6210\u8fd9\u4e9b\u4eba\u90fd\u662f\u72e1\u733e\u800c\u8d2a\u5a6a\u7684\u5bb6\u4f19\uff0c\u82cf\u6d25\u4e1c\u4e0e\u4e01\u69d0\u73b0\u5728\u4e3b\u8981\u8d1f\u8d23\u4e0e\u5fb7\u4eea\u7684\u5fb7\u5c14\u6cd5\u897f\u7b49\u4eba\u8c08\u5224\uff0c\u60f3\u5fc5\u4ed6\u4eec\u7684\u8c08\u5224\u4f1a\u8f7b\u677e\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u5176\u4ed6\u6761\u4ef6\u867d\u7136\u8ba9\u4eba\u770b\u4e86\u5f88\u751f\u6c14\uff0c\u4f46\u662f\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u575a\u6301\u5c06\u65af\u9ad8\u67cf\u7684\u786c\u4ef6\u9500\u552e\u4e0e\u4f60\u4eec\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u6346\u7ed1\u5728\u4e00\u8d77\uff1f\u201d\u827e\u9ed8\u915d\u917f\u4e86\u4e00\u4e0b\u60c5\u7eea\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e00\u70b9\u7b80\u503c\u5c31\u662f\u65e0\u7406\u7684\u8981\u6c42\uff0c\u6211\u5b9e\u5728\u6ca1\u6709\u60f3\u5230\u4f60\u4eec\u4f1a\u8fd9\u6837\u6765\u82db\u523b\u7684\u5bf9\u5f85\u4e00\u76f4\u4ee5\u6765\u7684\u670b\u53cb\u3001\u4f19\u4f34\u2026\u2026\u201d</p><p>\u3000\u3000\u80fd\u5750\u5728\u8fd9\u91cc\uff0c\u5c31\u4e0d\u662f\u4f1a\u8f7b\u6613\u7ed9\u522b\u4eba\u8a00\u8bed\u6253\u52a8\u7684\u4eba\uff0c\u9648\u4fe1\u751f\u8138\u4e0a\u59cb\u7ec8\u6302\u7740\u6e29\u541e\u6c34\u7684\u7b11\u5bb9\uff0c\u8096\u664b\u6210\u5728\u827e\u9ed8\u53d1\u7262\u9a9a\u65f6\uff0c\u8fd8\u5fcd\u4e0d\u4f4f\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u624d\u89e3\u91ca\u9053\uff1a\u201c\u5728\u5317\u7f8e\u5730\u533a\uff0c\u7531\u4e8e\u5bf9\u76d7\u7248\u7684\u9650\u5236\u6781\u4e3a\u4e25\u683c\uff0c\u53ef\u4f9b\u5728\u7ebf\u5171\u4eab\u4e0b\u8f7d\u7684\u97f3\u4e50\u8d44\u6e90\u5341\u5206\u6709\u9650\uff0c\u8fd9\u5c06\u4e25\u91cd\u9650\u5236\u786c\u4ef6\u7684\u9500\u552e\uff0c\u6240\u4ee5\u6211\u4eec\u624d\u62ff\u51fa\u6781\u5927\u7684\u8d44\u6e90\u4e0e\u7cbe\u529b\u53bb\u505a\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u3002\u8fd9\u4e00\u5207\u90fd\u662f\u4e3a\u4e86\u63a8\u5e7f\u786c\u4ef6\u7684\u9500\u552e\uff0c\u6211\u4eec\u5e76\u6ca1\u6709\u5728\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u725f\u5229\u7684\u610f\u56fe\u3002\u6211\u4eec\u4e3a\u6b64\u5728\u65e5\u672c\u6536\u8d2d\u5728\u7ebf\u97f3\u4e50\u7f51\u7ad9\uff0c\u5f00\u8bbe\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\uff0c\u5df2\u7ecf\u4e0e\u7d22\u5c3c\u3001\u534e\u7eb3\u4e24\u5927\u5531\u7247\u516c\u53f8\u8c08\u59a5\u6570\u5b57\u7248\u6743\u7684\u6388\u6743\uff0c\u4e5f\u6b63\u4e0e\u534e\u7eb3\u3001\u767e\u4ee3\u53ca\u5176\u4ed6\u72ec\u7acb\u5531\u7247\u516c\u53f8\u3001\u97f3\u4e50\u51fa\u7248\u5546\u63a5\u89e6\uff0c\u5728\u7ebf\u97f3\u4e50\u9500\u552e\u4e0e\u786c\u4ef6\u9500\u552e\u76f8\u6346\u7ed1\u7684\u9500\u552e\u6a21\u5f0f\uff0c\u624d\u6709\u52a9\u6211\u4eec\u6253\u5f00\u8fd9\u4e2a\u5168\u65b0\u7684\u5e02\u573a\u2026\u2026\u6211\u4eec\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u5df2\u7ecf\u83b7\u5f97\u5e02\u573a\u51c6\u5165\u8bb8\u53ef\uff0c\u4f1a\u5728\u6700\u8fd1\u5c31\u5c06\u4e1a\u52a1\u63a8\u5e7f\u5230\u5317\u7f8e\u5730\u533a\uff0c\u5c31\u7b97\u6211\u4eec\u73b0\u5728\u5c31\u8c08\u59a5\u4ea7\u54c1\u6280\u672f\u8bb8\u53ef\u6388\u6743\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0d\u4f1a\u5f71\u54cd\u4f60\u4eec\u5728\u5317\u7f8e\u5730\u533a\u7684\u786c\u4ef6\u9500\u552e\u8ba1\u5212\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u53ef\u4ee5\u5728\u5317\u7f8e\u5730\u533a\u63a8\u51fa\u81ea\u5df1\u7684\u5728\u7ebf\u97f3\u4e50\u7f51\u7ad9\u63d0\u4f9b\u6b4c\u66f2\u4e0b\u8f7d\u670d\u52a1\u2026\u2026\u201d\u827e\u9ed8\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u5426\u8ba4\u65af\u9ad8\u67cf\u5728\u8fd9\u65b9\u9762\u5c06\u6765\u53ef\u80fd\u4f1a\u6bd4\u6211\u4eec\u66f4\u51fa\u8272\uff0c\u201d\u9648\u4fe1\u751f\u5634\u89d2\u542b\u7740\u6de1\u6de1\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u5173\u952e\u662f\u65f6\u95f4\uff0c\u6211\u4eec\u4e0d\u53ef\u80fd\u5c06\u5317\u7f8e\u5730\u533a\u7684\u8bb8\u53ef\u6388\u6743\u63a8\u8fdf\u5230\u7b49\u65af\u9ad8\u67cf\u4e00\u5207\u90fd\u51c6\u5907\u59a5\u5f53\u4e4b\u540e\u2026\u2026\u4f60\u4eec\u786e\u5b9a\u80fd\u5728\u534a\u5e74\u751a\u81f3\u66f4\u77ed\u7684\u65f6\u95f4\u91cc\uff0c\u5c06\u4e00\u5207\u90fd\u51c6\u5907\u59a5\u5f53\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u53ef\u4ee5\u4f5c\u4e3a\u6211\u4eec\u524d\u671f\u66ff\u4ee3\u6027\u7684\u9009\u62e9\uff0c\u4f46\u662f\u4e0d\u5e94\u8be5\u6392\u65a5\u6211\u4eec\u63a8\u51fa\u81ea\u5df1\u7684\u5728\u7ebf\u97f3\u4e50\u7f51\u7ad9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u53ea\u662f\u505a\u4e3a\u66ff\u4ee3\u6027\u7684\u9009\u62e9\uff0c\u90a3\u6211\u4eec\u4e3a\u4ec0\u4e48\u8981\u4e3a\u4e86\u65af\u9ad8\u67cf\u5728\u5317\u7f8e\u5730\u533a\u7684\u786c\u4ef6\u9500\u552e\u5374\u4e00\u5b9a\u8981\u5c06\u6211\u4eec\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u63a8\u5e7f\u5230\u5317\u7f8e\u5730\u533a\u53bb\uff1f\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u7684\u524d\u671f\u63a8\u5e7f\u8d39\u7528\u662f\u5de8\u5927\u7684\uff0c\u8981\u662f\u65af\u9ad8\u67cf\u540c\u610f\u57284\uff05\u7684\u8bb8\u53ef\u6388\u6743\u8d39\u7528\u4e4b\u5916\uff0c\u518d\u8865\u507f\u6211\u4eec\u6211\u4eec\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u5728\u5317\u7f8e\u5730\u533a\u7684\u524d\u671f\u63a8\u5e7f\u8d39\u7528\uff0c\u4f60\u7684\u6761\u4ef6\uff0c\u6211\u4eec\u4e5f\u662f\u53ef\u4ee5\u8003\u8651\u7684\uff0c\u201d\u8096\u664b\u6210\u8bf4\u9053\uff0c\u201c\u5f53\u7136\uff0c\u76f8\u5173\u7684\u8c08\u5224\u4f1a\u53d8\u5f97\u66f4\u52a0\u7e41\u70e6\uff0c\u6211\u4eec\u5012\u662f\u4e0d\u4ecb\u610f\uff0c\u6bd5\u7adf\u6211\u4eec\u5728\u5317\u7f8e\u5730\u533a\u7684\u8bb8\u53ef\u6388\u6743\u5382\u5546\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u65af\u9ad8\u67cf\u4e00\u5bb6\uff0c\u4e4b\u6240\u4ee5\u6700\u5148\u4e0e\u65af\u9ad8\u67cf\u8fdb\u884c\u8bb8\u53ef\u6388\u6743\u7684\u8c08\u5224\uff0c\u4e5f\u662f\u8003\u8651\u5230\u957f\u671f\u4ee5\u6765\u90fd\u5728\u6109\u5feb\u5408\u4f5c\u7684\u7f18\u6545\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u8fd9\u6837\u7684\u8c08\u5224\u4e2d\uff0c\u65af\u9ad8\u67cf\u4ece\u6765\u90fd\u4e0d\u4f1a\u5360\u4f18\u52bf\u7684\u3002\u9526\u6e56\u51c6\u5907\u5145\u88d5\uff0c\u53c8\u662f\u63d0\u51fa\u6761\u4ef6\u7684\u4e00\u65b9\uff0c\u800c\u827e\u9ed8\u7b49\u4eba\u5374\u8981\u968f\u65f6\u8ddf\u9526\u6e56\u6761\u4ef6\u53d8\u52a8\u6765\u8ba1\u7b97\u7ed3\u679c\u5dee\u5f02\uff0c\u65f6\u95f4\u53c8\u5f88\u7d27\u8feb\u3002\u9526\u6e56\u4e0d\u4f1a\u5c06\u6570\u5b57\u97f3\u4e50\u64ad\u653e\u5668\u5728\u5317\u7f8e\u5730\u533a\u7684\u751f\u4ea7\u3001\u9500\u552e\u8bb8\u53ef\u72ec\u5bb6\u6388\u6743\u7ed9\u65af\u9ad8\u67cf\uff0c\u800c\u65af\u9ad8\u67cf\u5728\u83b7\u5f97\u6388\u6743\u540e\u8fd8\u8981\u7ec4\u7ec7\u751f\u4ea7\u3001\u9500\u552e\uff0c\u65f6\u95f4\u5f88\u7d27\u8feb\u3002</p><p>\u3000\u3000\u827e\u9ed8\u90fd\u611f\u89c9\u5230\u8111\u888b\u5728\u53d1\u80c0\uff0c\u4f55\u6b62\u5728\u53d1\u80c0\uff0c\u7b80\u76f4\u5c31\u8981\u7206\u70b8\u6389\uff0c\u4ed6\u80fd\u660e\u767d\u9526\u6e56\u7684\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u4e00\u65e6\u65af\u9ad8\u67cf\u7b54\u5e94\u786c\u4ef6\u9500\u552e\u4e0e\u9526\u6e56\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u6346\u7ed1\u8d77\u6765\uff0c\u9996\u5148\u65af\u9ad8\u67cf\u5c31\u4e0d\u80fd\u5229\u7528\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u6765\u9650\u5236\u5317\u7f8e\u5730\u533a\u5176\u4ed6\u83b7\u5f97\u9526\u6e56\u6388\u6743\u7684\u7ade\u4e89\u8005\u3002\u8fd9\u79cd\u6346\u7ed1\u6a21\u5f0f\uff0c\u4e0d\u4ec5\u4f1a\u6709\u52a9\u63a8\u5e7f\u9526\u6e56\u5728\u5317\u7f8e\u5730\u533a\u7684\u5728\u7ebf\u97f3\u4e50\u4e1a\u52a1\u2014\u2014\u65af\u9ad8\u67cf\u6bcf\u9500\u552e\u4e00\u4ef6\u4ea7\u54c1\u90fd\u5728\u4e3a\u9526\u6e56\u7684\u5728\u7ebf\u97f3\u4e50\u4e1a\u52a1\u505a\u5ba3\u4f20\uff0c\u6700\u4e3a\u5173\u952e\u7684\uff0c\u5c31\u7b97\u9526\u6e56\u4e0d\u7126\u6025\u81ea\u5df1\u5f00\u53d1\u5317\u7f8e\u5730\u533a\u7684\u5e02\u573a\uff0c\u4f46\u662f\u53ea\u8981\u8fd9\u4e2a\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u638c\u63e1\u5728\u9526\u6e56\u624b\u91cc\uff0c\u5317\u7f8e\u5730\u533a\u7684\u5e02\u573a\u5374\u59cb\u7ec8\u671d\u9526\u6e56\u655e\u5f00\u7740\u5927\u95e8\u3002\u5c31\u7b97\u7b54\u5e94\u4ed6\u4eec\u7684\u6761\u4ef6\uff0c\u8bb8\u53ef\u6388\u6743\u8d39\u7528\u4e5f\u4e0d\u51cf\u514d\u4e00\u5206\u3002\u5c31\u7b97\u9526\u6e56\u80fd\u5728\u4ea7\u54c1\u6280\u672f\u6388\u6743\u3001\u5143\u5668\u4ef6\u5e02\u573a\u83b7\u5f97\u5de8\u989d\u5229\u6da6\uff0c\u4e5f\u4e0d\u4f1a\u5e0c\u671b\u770b\u5230\u5317\u7f8e\u5730\u533a\u7684\u4ea7\u54c1\u5e02\u573a\u7ed9\u65af\u9ad8\u67cf\u4e00\u5bb6\u9738\u5360\u3002</p><p>\u3000\u3000\u65af\u9ad8\u67cf\u521a\u521a\u60f3\u8fdb\u5165\u7ec8\u7aef\u4ea7\u54c1\u5e02\u573a\uff0c\u4ece\u9500\u552e\u6e20\u9053\u4e0e\u5e02\u573a\u7ecf\u9a8c\u4e0a\u90fd\u5f88\u8584\u5f31\uff0c\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u7edd\u4e0d\u662f\u4e00\u4e2a\u5f88\u597d\u7684\u5408\u4f5c\u8005\uff1b\u9526\u6e56\u6700\u5148\u627e\u65af\u9ad8\u67cf\u5408\u4f5c\uff0c\u81ea\u7136\u4e0d\u662f\u56e0\u4e3a\u5728\u8fc7\u53bb\u4e09\u5e74\u7684\u65f6\u95f4\u91cc\u53cc\u65b9\u90fd\u4e00\u76f4\u5408\u4f5c\u6109\u5feb\uff0c\u800c\u662f\u65af\u9ad8\u67cf\u6700\u53ef\u80fd\u7b54\u5e94\u4ed6\u4eec\u82db\u523b\u7684\u6761\u4ef6\u3002</p><p>\u3000\u3000\u9526\u6e56\u662f\u6ca1\u6709\u8d44\u683c\u5411\u4e1a\u5185\u7684\u5de8\u5934\u63d0\u51fa\u8fd9\u4e9b\u82db\u523b\u6761\u4ef6\u7684\uff0c\u827e\u9ed8\u89c9\u5f97\u521a\u5230\u5efa\u90ba\u5c31\u66b4\u9732\u65af\u9ad8\u67cf\u60f3\u8fdb\u5165\u4ea7\u54c1\u5e02\u573a\u7684\u7528\u5fc3\u597d\u50cf\u6709\u4e9b\u5931\u8bef\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u8d1f\u8d23\u4e0e\u5fb7\u4eea\u7684\u8c08\u5224\u5c31\u8f7b\u677e\u591a\u4e86\uff0c\u64ad\u653e\u5668\u672c\u8eab\u5c31\u662f\u91c7\u7528\u5fb7\u4eea\u7684\u5d4c\u5165\u5f0f\u5fae\u5904\u7406\u5668\u6280\u672f\u52a0\u4e0a\u9526\u6e56\u81ea\u8eab\u5f00\u53d1\u7684\u97f3\u9891\u7f16\u89e3\u7801\u6280\u672f\u4e3a\u57fa\u672c\u786c\u4ef6\u67b6\u6784\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u610f\u56fe\u662f\u5e0c\u671b\u5c31\u76f8\u5173\u4e13\u5229\u6280\u672f\u53cc\u65b9\u4ea4\u53c9\u8bb8\u53ef\u6388\u6743\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u9526\u6e56\u80fd\u591f\u83b7\u5f97\u6240\u6025\u9700\u7684\u5d4c\u5165\u5f0f\u5fae\u5904\u7406\u5668\u6280\u672f\uff0c\u5fb7\u4eea\u80fd\u591f\u5206\u4eab\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u7684\u534a\u5bfc\u4f53\u5143\u4ef6\u5e02\u573a\uff0c\u4f55\u51b5\u9526\u6e56\u7684\u97f3\u9891\u7f16\u89e3\u7801\u6280\u672f\u5bf9\u5fb7\u4eea\u4e5f\u5e76\u975e\u6ca1\u6709\u88e8\u76ca\u3002\u81f3\u4e8e\u57fa\u672c\u786c\u4ef6\u67b6\u6784\u6240\u6d89\u53ca\u5230\u7684\u4e13\u5229\u6280\u672f\u5982\u4f55\u53bb\u6388\u6743\u7b2c\u4e09\u65b9\u4f7f\u7528\u7684\u95ee\u9898\uff0c\u53c2\u7167SVCD\u7f16\u89e3\u7801\u6280\u672f\u7684\u5904\u7f6e\u65b9\u5f0f\uff0c\u53ef\u4ee5\u7531\u9526\u6e56\u4e0e\u5fb7\u4eea\u6210\u7acb\u5408\u8d44\u516c\u53f8\u6765\u8d1f\u8d23\u6388\u6743\u5176\u4ed6\u534a\u5bfc\u4f53\u5382\u5546\u751f\u4ea7\u3001\u9500\u552e\u6570\u5b57\u97f3\u9891\u64ad\u653e\u5668\u6240\u9700\u8981\u7684\u534a\u5bfc\u4f53\u5143\u4ef6\uff0c\u4e5f\u7531\u5408\u8d44\u516c\u53f8\u6765\u8d1f\u8d23\u540e\u7eed\u7684\u6280\u672f\u5347\u7ea7\uff0c\u4ee5\u786e\u4fdd\u5728\u76f8\u5173\u534a\u5bfc\u4f53\u5143\u4ef6\u9886\u57df\u7684\u9886\u5148\u5730\u4f4d\u3002</p><p>\u3000\u3000\u8c08\u5224\u7684\u7126\u70b9\u5c31\u662f\u5982\u4f55\u53bb\u5206\u914d\u5408\u8d44\u516c\u53f8\u7684\u6743\u76ca\uff0c\u5fb7\u4eea\u8981\u6c42\u4e0e\u9526\u6e56\u5404\u6301\u534a\u6570\u7684\u80a1\u4efd\uff0c\u867d\u7136\u5fb7\u4eea\u5728\u82af\u7247\u751f\u4ea7\u80fd\u83b7\u5f97\u66f4\u591a\u7684\u5229\u6da6\uff0c\u4f46\u662f\u9526\u6e56\u5374\u8fd8\u80fd\u4ece\u4ea7\u54c1\u6280\u672f\u8bb8\u53ef\u6388\u6743\u4e0e\u6700\u7ec8\u7684\u4ea7\u54c1\u751f\u4ea7\u3001\u9500\u552e\u4e2d\u83b7\u5229\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u5962\u671b\u80fd\u4ece\u5fb7\u4eea\u7684\u9aa8\u5934\u4e0a\u69a8\u51fa\u6c57\u6765\uff0c\u751a\u81f3\u613f\u610f\u4e3b\u52a8\u8ba9\u51fa\u4e00\u90e8\u5206\u5229\u76ca\uff0c\u6ca1\u6709\u5fb7\u4eea\uff0c\u9526\u6e56\u662f\u4e0d\u53ef\u80fd\u72ec\u81ea\u83b7\u5f97\u66f4\u9ad8\u5229\u76ca\u7684\uff0c\u53ea\u8981\u6c42\u5408\u8d44\u516c\u53f8\u53ca\u914d\u5957\u7684\u7814\u53d1\u4e2d\u5fc3\u8bbe\u5728\u5efa\u90ba\uff0c\u7814\u53d1\u4e2d\u5fc3\u4e5f\u4e3b\u8981\u5bc4\u5e0c\u671b\u5fb7\u4eea\u80fd\u6d3e\u51fa\u5f97\u529b\u7684\u7814\u53d1\u4eba\u5458\uff0c\u9526\u6e56\u5012\u662f\u53ef\u4ee5\u63d0\u4f9b\u5927\u91cf\u7684\u7855\u58eb\u751f\u3001\u535a\u58eb\u751f\u65b0\u624b\uff0c\u8fd9\u662f\u9526\u6e56\u4e00\u8d2f\u7684\u4f0e\u4fe9\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0e\u5fb7\u4eea\u7684\u8c08\u5224\u8fc7\u7a0b\u53cc\u65b9\u76f8\u5f53\u6109\u5feb\uff0c\u8c08\u5224\u7684\u7ed3\u679c\u8fd8\u8981\u7b49\u5fb7\u4eea\u603b\u90e8\u786e\u8ba4\u8fc7\u624d\u80fd\u751f\u6548\u3002</p></article>"}}}]);
