(window.webpackJsonp=window.webpackJsonp||[]).push([[928],{951:function(p){p.exports={chapterTitle:null,title:"\u7b2c925\u7ae0 \u8d35\u4eba\u521d\u4e34",content:"<article><h2>\u7b2c925\u7ae0 \u8d35\u4eba\u521d\u4e34</h2><p>\u3000\u3000\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u5f20\u606a\u7531\u4e8e\u5de5\u4f5c\u7684\u5173\u7cfb\uff0c\u5bf9\u65b0\u4ead\u8fd9\u5ea7\u57ce\u5e02\u975e\u5e38\u719f\u6089\uff0c\u4f46\u90a3\u662f\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u8bb0\u5fc6\uff0c\u522b\u4eba\u53ea\u5f53\u4ed6\u662f\u7b2c\u4e00\u6b21\u8e0f\u4e0a\u65b0\u4ead\u7684\u571f\u5730\u3002</p><p>\u3000\u3000\u65c1\u4eba\u603b\u662f\u5f88\u96be\u60f3\u8c61\u5f20\u606a\u672a\u66fe\u8fdb\u884c\u8fc7\u5b9e\u5730\u8003\u5bdf\u5c31\u505a\u51fa\u6295\u8d44\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u7684\u51b3\u7b56\uff0c\u66f4\u613f\u610f\u76f8\u4fe1\u9526\u6e56\u5176\u5b9e\u6709\u4e00\u5957\u884c\u4e4b\u6709\u6548\u7684\u96c6\u4f53\u51b3\u7b56\u673a\u5236\uff0c\u4e5f\u4f1a\u7406\u6240\u5f53\u7136\u7684\u8ba4\u4e3a\u9526\u6e56\u80fd\u53d1\u5c55\u5230\u4eca\u5929\u7684\u5730\u6b65\u5176\u5b9e\u662f\u65d7\u4e0b\u80fd\u5e78\u8fd0\u7684\u805a\u96c6\u4e86\u4e00\u5927\u7cbe\u82f1\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u6ca1\u60f3\u5230\u8981\u966a\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u5230\u6e21\u53e3\u6765\u5439\u51c9\u98ce\uff0c\u8863\u886b\u5355\u8584\uff0c\u7ad9\u5728\u5f00\u9614\u7684\u6e21\u53e3\uff0c\u7ed9\u6c5f\u98ce\u5439\u5f97\u745f\u745f\u53d1\u6296\uff0c\u770b\u7740\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4ed6\u4eec\u4ece\u6e21\u8f6e\u4e0a\u4e0b\u6765\uff0c\u8d70\u8fc7\u53bb\u6413\u7740\u624b\u95ee\u9053\uff1a\u201c\u591c\u91cc\u600e\u4e48\u4e0d\u4f4f\u5728\u6d77\u5dde\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8fd9\u4e24\u5929\u6070\u597d\u6709\u4e8b\u79bb\u5f00\u6d77\u5dde\uff0c\u5f20\u606a\u4e5f\u4e0d\u80fd\u7ed9\u53f6\u5efa\u658c\u77e5\u9053\u4ed6\u591c\u91cc\u53bb\u627e\u665a\u6674\uff0c\u8fd9\u624d\u4e00\u8d77\u8fde\u591c\u8fc7\u6c5f\u6765\uff0c\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5634\u91cc\u5374\u8bf4\u9053\uff1a\u201c\u884c\u7a0b\u7d27\uff0c\u53ea\u80fd\u5728\u65b0\u4ead\u505c\u7559\u4e24\u5929\uff0c\u65e9\u4e9b\u8fc7\u6765\u80fd\u591a\u4e86\u89e3\u4e9b\u60c5\u51b5\u2026\u2026\u201d\u4e0e\u7aa6\u552f\u6d9b\u3001\u9676\u4e66\u827a\u63e1\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u7aa6\u4e66\u8bb0\u3001\u9676\u5e02\u957f\u4f60\u4eec\u8fd9\u662f\u2026\u2026\u65e9\u77e5\u9053\u8fd9\u6837\uff0c\u6211\u4eec\u5c31\u4e0d\u5728\u6c5f\u5bf9\u8fb9\u8ddf\u5b8b\u57f9\u660e\u3001\u9646\u6ea2\u5149\u8bf4\u90a3\u4e48\u591a\u8bdd\u4e86\uff0c\u53c8\u5bb3\u7aa6\u4e66\u8bb0\u3001\u9676\u5e02\u957f\u591a\u7b49\u4e86\u4e9b\u65f6\u95f4\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0e\u53f6\u603b\u662f\u65b0\u4ead\u7684\u8d35\u5ba2\uff0c\u59da\u5e02\u957f\u4eca\u5929\u53bb\u4e86\u7701\u91cc\uff0c\u4e0d\u7136\u4e5f\u8981\u8fc7\u6765\u63a5\u4f60\uff0c\u201d\u7aa6\u552f\u6d9b\u4e1d\u6beb\u4e0d\u89c9\u5f97\u5728\u6e21\u53e3\u7b49\u4e86\u534a\u5c0f\u65f6\u6709\u4ec0\u4e48\u4e0d\u4fbf\u7684\u5730\u65b9\uff0c\u8bf7\u5f20\u606a\u4ed6\u4eec\u5f80\u5cb8\u4e0a\u8d70\uff0c\u5e02\u91cc\u8fce\u63a5\u7684\u8f66\u961f\u505c\u4e0a\u5cb8\u4e0a\uff0c\u201c\u65b0\u4ead\u7684\u6761\u4ef6\u4e0d\u6bd4\u6d77\u5dde\uff0c\u6211\u4eec\u53ea\u80fd\u66f4\u591a\u7684\u8868\u793a\u4e00\u4e0b\u8bda\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u591c\u91cc\u7684\u6c5f\u98ce\u6709\u4e9b\u6e05\u5bd2\uff0c\u65b0\u4ead\u5e02\u59d4\u529e\u7efc\u5408\u4e00\u5904\u7684\u5904\u957f\u5f20\u6885\u7a7f\u7740\u70df\u7070\u8272\u8584\u5462\u98ce\u8863\uff0c\u5728\u6e21\u53e3\u6c34\u94f6\u706f\u4e0b\u98ce\u59ff\u7ef0\u7ea6\uff0c\u98ce\u8863\u53ea\u5230\u819d\u76d6\u4e0a\uff0c\u9ed1\u8272\u4e1d\u889c\u4e0e\u8d34\u4e86\u4eae\u7247\u7684\u6c34\u6676\u9ad8\u8ddf\u978b\u4f7f\u5f97\u5c0f\u817f\u770b\u4e0a\u7ea4\u7ec6\u4fee\u957f\uff1a\u4e24\u6b21\u90fd\u89c1\u5230\u7684\u9ad8\u6674\u8fd9\u6b21\u5374\u6ca1\u6709\u51fa\u73b0\uff0c\u8fd8\u6709\u5c31\u662f\u7aa6\u552f\u6d9b\u7684\u79d8\u4e66\uff0c\u53e6\u6709\u4e24\u4e2a\u751f\u9762\u5b54\uff0c\u7aa6\u552f\u6d9b\u4e0e\u9676\u4e66\u827a\u4e5f\u6ca1\u6709\u8981\u4ecb\u7ecd\u7684\u610f\u601d\uff0c\u5e94\u8be5\u662f\u5e02\u59d4\u7684\u5de5\u4f5c\u4eba\u5458\u3002</p><p>\u3000\u3000\u6041\u5927\u7684\u6e21\u53e3\u7801\u5934\uff0c\u9664\u4e86\u968f\u5f20\u606a\u540c\u6279\u6e21\u6c5f\u6765\u7684\u8f66\u8f86\u4e0a\u5cb8\uff0c\u8fd9\u8fb9\u6ca1\u6709\u8f66\u8f86\u4e0e\u884c\u4eba\u4e0a\u6e21\u8f6e\uff0c\u8d70\u4e0a\u5cb8\uff0c\u624d\u53d1\u73b0\u65b0\u4ead\u8fd9\u8fb9\u7684\u6e21\u53e3\u4e3a\u4ed6\u4eec\u4fe9\u7684\u5230\u6765\u6682\u65f6\u5c01\u95ed\u4e86\uff0c\u591c\u91cc\u8981\u8fc7\u6c5f\u7684\u8f66\u8f86\u4e0e\u884c\u4eba\u90fd\u505c\u5728\u7b49\u5019\u533a\u5916\u7b49\u5019\u7740\u3002\u8fce\u63a5\u7684\u8f66\u961f\u5c31\u505c\u5728\u7801\u5934\u5916\uff0c\u5f53\u4e2d\u6709\u51e0\u90e8\u8b66\u8f66\uff0c\u5341\u591a\u540d\u8b66\u5bdf\u5728\u90a3\u91cc\u5e2e\u7740\u7ef4\u6301\u79e9\u5e8f\uff0c\u5cb8\u4e0a\u8fd8\u7ad9\u7740\u4e00\u7fa4\u8863\u51a0\u695a\u695a\u7684\u5b98\u5458\uff0c\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u8fd9\u8fb9\u641e\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u9759\u6765\uff0c\u597d\u5728\u7aa6\u552f\u6d9b\u77e5\u9053\u4ed6\u4eec\u7684\u4e60\u60ef\uff0c\u6ca1\u6709\u518d\u6574\u51fa\u4e00\u5927\u961f\u5a92\u4f53\u8bb0\u8005\u6765\u3002</p><p>\u3000\u3000\u771f\u662f\u591f\u5a01\u98ce\u7684\uff0c\u5f20\u606a\u4e5f\u80fd\u5766\u7136\u53d7\u4e4b\uff0c\u4f46\u4ed6\u4e5f\u6e05\u695a\u8fd9\u91cc\u7ed9\u5ef6\u8bef\u8fc7\u6c5f\u7684\u53f8\u673a\u4e0e\u65c5\u5ba2\u809a\u5b50\u91cc\u5bf9\u4ed6\u4eec\u7edd\u6ca1\u6709\u597d\u8bcd\u7684\u3002</p><p>\u3000\u3000\u9676\u4e66\u827a\u89c1\u5f20\u606a\u3001\u53f6\u5efa\u658c\u7684\u773c\u795e\u5f80\u7ad9\u5728\u5cb8\u4e0a\u7684\u90a3\u7fa4\u5b98\u5458\u8eab\u4e0a\u7743\uff0c\u89e3\u91ca\u9053\uff1a\u201c\u7aa6\u4e66\u8bb0\u5230\u6e21\u53e3\u6765\u63a5\u4f60\u4eec\uff0c\u4e0d\u6653\u5f97\u600e\u4e48\u7ed9\u5317\u971e\u53bf\u7684\u4eba\u77e5\u9053\u4e86\uff0c\u90fd\u62e5\u8fc7\u6765\u51d1\u70ed\u95f9\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u9894\u9996\u800c\u7b11\uff0c\u5e02\u59d4\u4e66\u8bb0\u5165\u5883\uff0c\u53bf\u91cc\u7684\u5b98\u5458\u600e\u4e48\u53ef\u80fd\u5750\u5f97\u4f4f\uff1f\u4ed6\u4eec\u8fc7\u6c5f\u6765\uff0c\u80af\u5b9a\u8981\u60ca\u52a8\u5317\u971e\u53bf\u7684\uff0c\u4ed6\u4e5f\u6ca1\u89c9\u5f97\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u7aa6\u552f\u6d9b\u8ddf\u9676\u4e66\u827a\u8bf4\u9053\uff1a\u201c\u4f60\u8ba9\u7530\u725f\u4e1c\u8ddf\u6211\u4eec\u8d70\uff0c\u65f6\u95f4\u4e5f\u4e0d\u65e9\u4e86\uff0c\u8ba9\u5176\u4ed6\u4eba\u90fd\u5148\u6563\u6389\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u660e\u5929\u65e9\u4e0a\u518d\u8bf4\uff0c\u201d\u53c8\u8ddf\u5f20\u606a\u3001\u53f6\u5efa\u658c\u89e3\u91ca\uff0c\u201c\u7530\u725f\u4e1c\u662f\u5317\u971e\u53bf\u59d4\u4e66\u8bb0\uff0c\u53f6\u603b\u89c1\u8fc7\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8868\u793a\u4ed6\u4e5f\u77e5\u9053\u8fd9\u4eba\u3002</p><p>\u3000\u3000\u5728\u65b0\u4ead\u5e02\u8f96\u53bf\u5730\u56fe\u4e0a\uff0c\u5730\u5f62\u72ed\u957f\u7684\u5317\u971e\u53bf\u9762\u6d77\u4e34\u6c5f\uff0c\u65b0\u4ead\u5e02\u533a\u4eba\u53e3\u624d\u4e09\u5341\u4e07\u4e0d\u5230\uff0c\u7ed9\u5317\u971e\u53bf\u5305\u88f9\u5728\u5185\u4fa7\uff0c\u65e2\u4e0d\u4e34\u6d77\u4e5f\u662f\u9762\u6c5f\uff0c\u4e1c\u5c71\u5c9b\u662f\u5317\u971e\u53bf\u7684\u884c\u653f\u8f96\u533a\uff0c\u9694\u6d77\u7684\u4e1c\u5c71\u9547\u662f\u5317\u971e\u53bf\u7684\u53bf\u57ce\uff0c\u662f\u53bf\u59d4\u53bf\u653f\u5e9c\u6240\u5728\u5730\u3002</p><p>\u3000\u3000\u5c31\u7b97\u6ca1\u6709\u524d\u4e16\u7684\u7ecf\u5386\uff0c\u5f20\u606a\u600e\u4e48\u53ef\u80fd\u4e0d\u5bf9\u5317\u971e\u53bf\u4eba\u4e8b\u6709\u6240\u7814\u7a76\u5462\uff1f</p><p>\u3000\u3000\u5f20\u6885\u62a2\u7740\u5c0f\u8dd1\u8fc7\u53bb\u558a\u4eba\uff0c\u5f20\u606a\u4e0e\u7aa6\u552f\u6d9b\u7ad9\u5728\u8f66\u524d\u8bf4\u8bdd\uff0c\u4e00\u4f1a\u513f\uff0c\u6709\u4e2a\u76ae\u80a4\u9edd\u9ed1\u3001\u4e09\u5341\u4e03\u516b\u5c81\u7684\u7537\u5b50\u8ddf\u5f20\u6885\u8d70\u8fc7\u6765\uff0c\u8eab\u9ad8\u4e0e\u7a7f\u4e86\u9ad8\u8ddf\u978b\u7684\u5f20\u6885\u76f8\u5f53\uff0c\u4e5f\u4e0d\u7b97\u77ee\u4e86\uff0c\u8eab\u6750\u58ee\u5b9e\uff0c\u7ed9\u4eba\u7684\u611f\u89c9\u5f88\u6c89\u7a33\uff0c\u5728\u56fd\u5185\uff0c\u56db\u5341\u5c81\u4e0d\u5230\u5c31\u62c5\u4efb\u533a\u53bf\u4e00\u628a\u624b\uff0c\u4e5f\u7b97\u662f\u5e74\u5c11\u6709\u4e3a\u4e4b\u4eba\u3002\u5f20\u606a\u624b\u91cc\u65e9\u5c31\u6709\u4ed6\u7684\u8d44\u6599\uff0c\u53f6\u5efa\u658c\u3001\u5c0f\u53d4\u90fd\u4ed6\u4e0e\u63a5\u89e6\u8fc7\uff0c\u8bc4\u4ef7\u57fa\u672c\u4e0a\u4e5f\u662f\u80af\u5b9a\u7684\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8ddf\u4ed6\u70b9\u5934\u7b11\u7b11\uff1a\u201c\u7530\u4e66\u8bb0\uff0c\u6211\u53c8\u8fc7\u6765\u53e8\u5520\u4f60\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u662f\u54ea\u91cc\u8bdd\uff0c\u5c31\u6015\u4f60\u4eec\u4e0d\u8fc7\u6765\u2026\u2026\u201d\u7530\u725f\u4e1c\u8ddf\u53f6\u5efa\u658c\u8bf4\u7740\u8bdd\uff0c\u773c\u775b\u53c8\u70ed\u5207\u7684\u770b\u5411\u4e00\u65c1\u7684\u5f20\u606a\uff0c\u6ca1\u6709\u4eba\u5e2e\u4ed6\u4ecb\u7ecd\uff0c\u4ed6\u4e5f\u6ca1\u6709\u5510\u7a81\u7684\u4f38\u51fa\u624b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u7528\u65c1\u4eba\u5e2e\u4ed6\u4ecb\u7ecd\uff0c\u5f84\u76f4\u4f38\u51fa\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u52b3\u7530\u4e66\u8bb0\u5728\u8fd9\u91cc\u4e45\u7b49\u4e86\uff0c\u6211\u662f\u5f20\u606a\uff0c\u4ee5\u540e\u4f1a\u6709\u9ebb\u70e6\u5230\u7530\u4e66\u8bb0\u7684\u5730\u65b9\uff0c\u8fd8\u671b\u7530\u4e66\u8bb0\u4e0d\u8981\u63a8\u6258\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u725f\u4e1c\u5c31\u6015\u9ebb\u70e6\u4e0d\u5230\u4ed6\uff0c\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u91cc\u5012\u662f\u9ad8\u5174\uff0c\u4e5f\u6015\u5f20\u606a\u8fd9\u53ea\u662f\u5ba2\u5957\u8bdd\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5728\u5317\u971e\u53bf\u5883\u5185\uff0c\u4f46\u662f\u5efa\u6e2f\u4e4b\u524d\uff0c\u80af\u5b9a\u8981\u5c06\u5efa\u6e2f\u533a\u57df\u4ee5\u53ca\u4e34\u6e2f\u5de5\u4e1a\u533a\u89c4\u5212\u533a\u57df\u90fd\u76f4\u63a5\u5212\u5165\u4e00\u4e2a\u65b0\u7684\u533a\u8fdb\u884c\u7edf\u4e00\u7ba1\u7406\uff0c\u6574\u4e2a\u5317\u971e\u53bf\u4e43\u81f3\u65b0\u4ead\u5e02\u90fd\u4f1a\u6709\u4e00\u6b21\u5927\u7684\u4eba\u4e8b\u8c03\u6574\uff0c\u7530\u725f\u4e1c\u6b64\u65f6\u8fd8\u4e0d\u6e05\u695a\u4ed6\u7ed9\u8c03\u6574\u5230\u4ec0\u4e48\u4f4d\u7f6e\u4e0a\uff0c\u4ed6\u8fd9\u65f6\u53ea\u662f\u5efa\u6e2f\u7b79\u5907\u5de5\u4f5c\u9886\u5bfc\u5c0f\u7ec4\u7684\u6210\u5458\u800c\u5df2\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5c06\u662f\u65b0\u4ead\u6709\u53f2\u4ee5\u6765\u6700\u5927\u7684\u4e00\u9879\u7efc\u5408\u6027\u5de5\u7a0b\uff0c\u4ec5\u6e2f\u53e3\u57fa\u5efa\u9884\u7b97\u5c31\u8d85\u8fc7\u4e8c\u5341\u4e94\u4ebf\u7f8e\u5143\uff0c\u8fd8\u4e0d\u5305\u62ec\u4e34\u6e2f\u5de5\u4e1a\u533a\u4e0e\u4e34\u6e2f\u65b0\u57ce\u7684\u6295\u8d44\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u65b0\u4ead\u4e24\u5343\u4e94\u767e\u4e07\u7f8e\u5143\u7684\u5e02\u653f\u5de5\u7a0b\u90fd\u5f88\u7f55\u89c1\uff0c\u7aa6\u552f\u6d9b\u80af\u5b9a\u8981\u6311\u9009\u6700\u5f97\u529b\u7684\u7cbe\u5175\u5f3a\u5c06\u6765\u8d1f\u8d23\u6b64\u4e8b\u3002\u5f53\u7136\uff0c\u5728\u9009\u4eba\u65b9\u9762\uff0c\u7aa6\u552f\u6d9b\u4e5f\u4f1a\u5c0a\u91cd\u9526\u6e56\u7684\u610f\u5411\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u6709\u4e09\u90e8\u8f66\u8ddf\u7740\u8fc7\u6c5f\u6765\uff0c\u4e3a\u4e86\u8868\u793a\u5c0a\u91cd\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u5750\u8fdb\u7aa6\u552f\u6d9b\u7684\u8001\u5f0f\u5965\u8fea\uff0c\u4ed6\u90fd\u8ba4\u4e0d\u51fa\u662f\u5965\u8fea\u54ea\u5e74\u4ea7\u7684\u8f66\u6b3e\uff0c\u4ece\u5012\u89c6\u955c\u91cc\u770b\u89c1\u6881\u519b\u94bb\u8fdb\u4ed6\u7684\u884c\u653f\u7ea7\u5954\u9a70\u8f66\u91cc\u4eab\u53d7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u4e0a\u8f66\u76f4\u63a5\u5954\u8d74\u5317\u971e\u53bf\u57ce\u4e1c\u5c71\u9547\uff0c\u5f80\u4e1c\u5c71\u9547\u5317\u57ce\u7684\u53bf\u59d4\u53bf\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u4e1c\u5c71\u5bbe\u9986\u800c\u53bb\uff0c\u7aa6\u552f\u6d9b\u3001\u9676\u4e66\u827a\u7b49\u4e00\u884c\u4eba\u4e5f\u4e0d\u53ef\u80fd\u56de\u5e02\u91cc\u53bb\uff0c\u591c\u91cc\u7559\u5bbf\u5728\u4e1c\u5c71\u5bbe\u9986\u3002\u867d\u8bf4\u4e8b\u524d\u6ca1\u6709\u62db\u547c\uff0c\u4f46\u662f\u65b0\u4ead\u5e02\u8fd9\u8fb9\u90fd\u8003\u8651\u5468\u5168\u4e86\uff0c\u518d\u8bf4\u9526\u6e56\u5546\u4e8b\u4e5d\u6708\u4e2d\u65ec\u5728\u65b0\u4ead\u6210\u7acb\u7684\u8054\u7edc\u5904\u5c31\u8bbe\u5728\u4e1c\u5c71\u5bbe\u9986\u5185\u3002</p><p>\u3000\u3000\u9664\u4e86\u524d\u671f\u7684\u5efa\u6e2f\u7b79\u5907\u5de5\u4f5c\u4e4b\u5916\uff0c\u9526\u6e56\u5546\u4e8b\u8fd8\u8981\u5728\u5efa\u6e2f\u5de5\u7a0b\u542f\u52a8\u4e4b\u524d\u5b8c\u6210\u5bf9\u4e1c\u5c71\u94a2\u94c1\u5382\u4e0e\u4e1c\u5c71\u9020\u8239\u5382\u7684\u5408\u8d44\u8c08\u5224\u3002\u8054\u7edc\u5904\u7684\u7ea7\u522b\u5f88\u9ad8\uff0c\u5468\u4e00\u5e73\u8981\u5728\u5efa\u90ba\u4e3b\u6301\u9526\u6e56\u5546\u4e8b\u5728\u56fd\u5185\u4e8b\u52a1\u7684\u5927\u5c40\uff0c\u65b0\u4ead\u7684\u8054\u7edc\u5904\u4e3b\u4efb\u7531\u4e4b\u524d\u6a61\u6811\u56ed\u884c\u653f\u90e8\u603b\u7ecf\u7406\u8d75\u9e4f\u62c5\u4efb\uff0c\u8054\u7edc\u5904\u6210\u7acb\u4e4b\u540e\u4e0d\u4e45\uff0c\u53f6\u5efa\u658c\u53c8\u7d27\u63a5\u7740\u8c03\u8c2d\u5929\u5230\u65b0\u4ead\u6765\u534f\u52a9\u8d75\u9e4f\uff0c\u52a0\u5f3a\u8054\u7edc\u5904\u4e0e\u7701\u91cc\u7684\u8054\u7cfb\u3002</p><p>\u3000\u3000\u5f20\u606a\u6765\u4e4b\u524d\u7279\u610f\u4e0d\u8ba9\u8d75\u9e4f\u3001\u8c2d\u5929\u53bb\u6e21\u53e3\u63a5\u4ed6\u4eec\uff0c\u53ea\u662f\u6ca1\u6709\u60f3\u5230\u7aa6\u552f\u6d9b\u3001\u9676\u4e66\u827a\u4f1a\u5982\u6b64\u70ed\u5207\uff0c\u8fde\u7d2f\u4ed6\u5c0f\u53d4\u3001\u6881\u519b\u4e5f\u8ddf\u7740\u591c\u91cc\u5230\u6e21\u53e3\u5439\u51b7\u98ce\u3002</p><p>\u3000\u3000\u8f66\u5230\u4e1c\u5c71\u5bbe\u9986\uff0c\u9664\u4e86\u8d75\u9e4f\u3001\u8c2d\u5929\u4e4b\u5916\uff0c\u4e1c\u5c71\u5bbe\u9986\u95e8\u53e3\u8fd8\u6709\u4e00\u7fa4\u4eba\u5728\u90a3\u91cc\u8fce\u63a5\uff0c\u5f20\u606a\u770b\u5230\u521a\u624d\u5728\u6e21\u53e3\u51fa\u73b0\u7684\u51e0\u4e2a\u4eba\u4e5f\u5728\u5176\u4e2d\uff0c\u7aa6\u552f\u6d9b\u5728\u6e21\u53e3\u65f6\u8ba9\u4ed6\u4eec\u6563\u6389\uff0c\u4ed6\u4eec\u5230\u5e95\u662f\u6ca1\u6709\u771f\u5c31\u6563\u6389\u56de\u5bb6\u53bb\u3002</p><p>\u3000\u3000\u5728\u8fd9\u7fa4\u4eba\u91cc\uff0c\u5f20\u606a\u770b\u5230\u4e00\u4e2a\u719f\u6089\u7684\u8eab\u5f71\uff0c\u8fd8\u4ee5\u4e3a\u660e\u5929\u624d\u80fd\u627e\u673a\u4f1a\u9047\u5230\u4ed6\uff0c\u5374\u662f\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u665a\u4e0a\u5c31\u770b\u5230\u4ed6\u4e86\u3002</p><p>\u3000\u3000\u4ed6\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u7530\u725f\u4e1c\u521a\u4ece\u8f66\u91cc\u4e0b\u6765\uff0c\u4e5f\u4e0d\u7528\u5f20\u606a\u5f20\u53e3\u558a\u4ed6\uff0c\u4ed6\u770b\u5230\u5f20\u606a\u770b\u5411\u4ed6\u7684\u773c\u795e\u5c31\u5c0f\u6b65\u8dd1\u4e86\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u653f\u5e9c\u529e\u526f\u4e3b\u4efb\u8499\u5b66\u5e86\u4ed6\u4eba\u5728\u4e0d\u5728\uff1f\u201d\u5f20\u606a\u4fa7\u5934\u95ee\u4e86\u7530\u725f\u4e1c\u4e00\u58f0\u3002</p><p>\u3000\u3000\u8499\u5b66\u5e86\u5728\u5317\u971e\u53bf\u4e0d\u8fc7\u662f\u65e0\u5173\u7d27\u8981\u7684\u4e00\u4e2a\u5c0f\u89d2\u8272\uff0c\u7530\u725f\u4e1c\u8ba4\u8bc6\u8499\u5b66\u5e86\uff0c\u4f46\u4e0d\u662f\u719f\u6089\uff0c\u6bd5\u7adf\u662f\u53bf\u653f\u5e9c\u529e\u7684\u4eba\uff0c\u662f\u524d\u4efb\u53bf\u957f\u7684\u79d8\u4e66\uff0c\u8d76\u5728\u524d\u4efb\u53bf\u957f\u8c03\u8d70\u4e4b\u524d\u63d0\u4e86\u526f\u79d1\uff0c\u4f46\u662f\u73b0\u4efb\u53bf\u957f\u4e0e\u524d\u4efb\u6709\u5f88\u6df1\u7684\u77db\u76fe\uff0c\u8499\u5b66\u5e86\u5c31\u4e00\u76f4\u95f2\u7f6e\u5230\u73b0\u5728\uff0c\u6b64\u65f6\u4e0d\u518d\u62c5\u4efb\u653f\u5e9c\u529e\u526f\u4e3b\u4efb\uff0c\u800c\u662f\u7ed9\u8e22\u5230\u4e1c\u5c71\u5bbe\u9986\u6765\u62c5\u4efb\u526f\u7ecf\u7406\u3002</p><p>\u3000\u3000\u7530\u725f\u4e1c\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5f20\u606a\u8981\u662f\u8ba4\u8bc6\u8499\u5b66\u5e86\uff0c\u90a3\u5e94\u8be5\u77e5\u9053\u4ed6\u6b64\u65f6\u7684\u5883\u51b5\uff0c\u4e0d\u8fc7\u4ed6\u4e0d\u4f1a\u611a\u8822\u5230\u6253\u7834\u7802\u7a9d\u95ee\u5230\u5e95\uff0c\u4ed6\u671d\u8499\u5b66\u5e86\u62db\u4e86\u62db\u624b\uff0c\u8ba9\u4ed6\u8fc7\u6765\u3002</p><p>\u3000\u3000\u8499\u5b66\u5e86\u5f53\u771f\u662f\u5c0f\u89d2\u8272\uff0c\u867d\u7136\u8fd9\u6837\u7684\u573a\u5408\u4ed6\u4e5f\u51fa\u73b0\uff0c\u5374\u662f\u6ca1\u6709\u7aa6\u552f\u6d9b\u3001\u9676\u4e66\u827a\u3001\u7530\u725f\u4e1c\u7b49\u4eba\u63a5\u89e6\u7684\u673a\u4f1a\uff0c\u4ed6\u4e5f\u6ca1\u6709\u60f3\u7740\u7ed5\u5f00\u53bf\u59d4\u526f\u79d8\u4e66\u957f\u3001\u4e1c\u5c71\u5bbe\u9986\u7ecf\u7406\u5f20\u6c49\u6cc9\u7684\u89c6\u7ebf\u53bb\u8ddf\u9886\u5bfc\u63a5\u89e6\uff0c\u518d\u8bf4\u5f20\u6c49\u6cc9\u7684\u773c\u775b\u8d3c\u6bd2\uff0c\u4e89\u5ba0\u7684\u672c\u4e8b\u4e5f\u9ad8\uff0c\u603b\u80fd\u627e\u501f\u53e3\u5c06\u6709\u5a01\u80c1\u7684\u4eba\u4ece\u9886\u5bfc\u89c6\u7ebf\u91cc\u652f\u8d70\u3002</p><p>\u3000\u3000\u8499\u5b66\u5e86\u770b\u5230\u53bf\u59d4\u4e66\u8bb0\u7530\u725f\u4e1c\u671d\u4ed6\u62db\u624b\uff0c\u4ed6\u5148\u662f\u671d\u8eab\u8fb9\u770b\u4e86\u770b\uff0c\u4ed6\u6ca1\u6709\u60f3\u5230\u7530\u725f\u4e1c\u662f\u5728\u558a\u4ed6\u8fc7\u53bb\uff0c\u89c1\u8eab\u8fb9\u4eba\u4e5f\u662f\u4e00\u5934\u96fe\u6c34\uff0c\u624d\u60f3\u53ef\u80fd\u662f\u5524\u4ed6\uff0c\u4ed6\u8fd8\u6ca1\u6709\u52a8\uff0c\u90a3\u8fb9\u773c\u775b\u8d3c\u6bd2\u7684\u5f20\u6c49\u6cc9\u5df2\u7ecf\u62a2\u5148\u8bf4\u9053\uff1a\u201c\u8499\u7ecf\u7406\uff0c\u4f60\u518d\u53bb\u68c0\u67e5\u4e00\u4e0b\u7ed9\u5ba2\u4eba\u5b89\u6392\u7684\u4f4f\u623f\u6709\u6ca1\u6709\u95ee\u9898\uff0c\u7530\u4e66\u8bb0\u6709\u4ec0\u4e48\u5429\u5490\uff0c\u6211\u6765\u529e\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u8499\u5b66\u5e86\u53ea\u5f53\u7530\u725f\u4e1c\u627e\u4eba\u5429\u5490\u4e8b\u60c5\uff0c\u867d\u8bf4\u673a\u4f1a\u96be\u5f97\uff0c\u751a\u81f3\u53ef\u4ee5\u5728\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u9762\u524d\u9732\u8138\uff0c\u4f46\u662f\u5f20\u6c49\u6cc9\u8981\u4e89\u7740\u8fc7\u53bb\uff0c\u4ed6\u4e5f\u65e0\u53ef\u5948\u4f55\uff0c\u53ea\u5f97\u8fd4\u8eab\u5f80\u540e\u9762\u8d70\u53bb\u3002</p><p>\u3000\u3000\u201c\u8499\u53d4\u53d4\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u89c1\u6709\u4eba\u558a\u4ed6\uff0c\u8499\u5b66\u5e86\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u89c1\u7aa6\u552f\u6d9b\u3001\u9676\u4e66\u827a\u3001\u7530\u725f\u4e2d\u4e09\u4eba\u56f4\u62e5\u7684\u90a3\u4e2a\u9752\u5e74\u671d\u4ed6\u62db\u624b\uff0c\u4ed6\u5fc3\u91cc\u4e00\u4e50\uff0c\u81ea\u5df1\u54ea\u91cc\u53c8\u5192\u51fa\u6765\u8fd9\u4e48\u4f84\u5b50\uff0c\u8be5\u4e0d\u4f1a\u662f\u8ba4\u9519\u4e86\u5427\uff1f\u53ea\u4e0d\u8fc7\u8fd9\u4e00\u77ac\u95f4\uff0c\u4e1c\u5c71\u5bbe\u9986\u95e8\u53e3\u7684\u6240\u6709\u4eba\u90fd\u5c06\u76ee\u5149\u805a\u5230\u4ed6\u8eab\u4e0a\uff0c\u7279\u522b\u662f\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u3001\u5e38\u59d4\u526f\u5e02\u957f\u9676\u4e66\u827a\u3001\u53bf\u59d4\u4e66\u8bb0\u7530\u725f\u4e2d\u7684\u8be7\u5f02\u773c\u795e\u4ee5\u53ca\u53bf\u59d4\u526f\u79d8\u4e66\u957f\u5f20\u6c49\u6cc9\u9707\u9a87\u7684\u773c\u795e\u5c24\u5176\u7684\u8ba9\u4eba\u611f\u89c9\u826f\u597d\u3002</p></article>"}}}]);
