(window.webpackJsonp=window.webpackJsonp||[]).push([[93],{116:function(p){p.exports={chapterTitle:null,title:"\u7b2c1078\u7ae0 \u5dee\u70b9\u95f9\u51fa\u7eef\u95fb",content:"<article><h2>\u7b2c1078\u7ae0 \u5dee\u70b9\u95f9\u51fa\u7eef\u95fb</h2><p>\u3000\u3000\u5b59\u9759\u9999\u3001\u6c5f\u9edb\u513f\u8fd8\u6709\u5510\u5a67\u5979\u4eec\u8981\u5148\u56de\u623f\u5c06\u521a\u624d\u5e86\u529f\u5bb4\u4e0a\u7684\u793c\u670d\u76db\u88c5\u6362\u6389\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u7b49\u4eba\u5148\u53bb\u9876\u697c\u4f1a\u6240\u3002\u867d\u7136\u65f6\u8fd1\u6df1\u591c\uff0c\u53ea\u662f\u79cd\u79cd\u4e8b\u60c5\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u4e5f\u4ea2\u594b\u96be\u4ee5\u9759\u4e0b\u5fc3\u6765\u4f11\u606f\u3002</p><p>\u3000\u3000\u524d\u5929\u591c\u91cc\u90a3\u4e48\u6298\u817e\u56fd\u5bb6\u8db3\u7403\u4e0e\u7537\u8db3\u56fd\u5bb6\u961f\u4ee5\u53ca\u9999\u6e2f\u5eb7\u4f53\u53d1\u5c55\u5c40\u7684\u5b98\u5458\uff0c\u81f3\u5c11\u5f71\u6e7e\u56ed\u7684\u5de5\u4f5c\u4eba\u5458\u90fd\u77e5\u9053\u5f20\u606a\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u7684\u8eab\u4efd\u663e\u8d35\uff0c\u62db\u5f85\u8d77\u6765\u989d\u5916\u7684\u5c3d\u5fc3\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u5750\u8fdb\u8c6a\u534e\u5305\u53a2\u91cc\uff0c\u7edd\u53e3\u4e0d\u5148\u63d0\u4eca\u5929\u665a\u4e0a\u6f14\u5531\u4f1a\u7684\u4e8b\u60c5\uff0c\u5148\u8c08\u8d77\u5fae\u8f6f\u6709\u610f\u6536\u8d2d\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u80a1\u6743\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6f14\u5531\u4f1a\u4e4b\u524d\u7684\u7535\u8bdd\u4f1a\u8bae\uff0c\u5b59\u5c1a\u4e49\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u6ca1\u6709\u53c2\u4e0e\uff0c\u4e5f\u4e0d\u4f1a\u76f4\u63a5\u53c2\u4e0e\uff0c\u5927\u5bb6\u90fd\u5404\u53f8\u5176\u804c\uff0c\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u6bcf\u4e00\u4ef6\u4e8b\u60c5\u90fd\u8981\u53c2\u4e0e\uff0c\u4e09\u4e2a\u8111\u888b\u90fd\u4e0d\u591f\u7528\u3002</p><p>\u3000\u3000\u8096\u664b\u6210\u6240\u8d1f\u8d23\u7684\u6d77\u5916\u8fd0\u8425\u90e8\u867d\u7136\u8d22\u52a1\u4e0a\u5f52\u9526\u6e56\u5546\u4e8b\uff0c\u76c8\u5229\u4e5f\u5e76\u5165\u9526\u6e56\u5546\u4e8b\uff0c\u8fd9\u4e48\u505a\u7684\u76ee\u7684\uff0c\u662f\u8981\u6539\u5584\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u52a1\u7ed3\u6784\uff0c\u4f46\u662f\u4e1a\u52a1\u4e0a\u8fd8\u662f\u8ddf\u7231\u8fbe\u96c6\u56e2\u5f52\u4e8e\u4e00\u5757\uff0c\u5c5e\u4e8e\u9526\u6e56\u6700\u6838\u5fc3\u7684\u6d88\u8d39\u7c7b\u7535\u5b50\u53ca\u884d\u751f\u4e1a\u52a1\u8303\u7574\u3002</p><p>\u3000\u3000\u5982\u6b64\u7684\u7ba1\u7406\u7ed3\u6784\u4e4d\u770b\u4e0a\u53bb\u6709\u4e9b\u6df7\u4e71\uff0c\u4e0d\u8fc7\u67b6\u6784\u5728\u6574\u4e2a\u9526\u6e56\u7cfb\u4e4b\u4e0a\u7684\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u51b3\u7b56\u673a\u5236\u53d1\u6325\u4e86\u5f88\u597d\u7684\u534f\u8c03\u529f\u80fd\uff0c\u5426\u5219\u5149\u4f53\u7cfb\u5185\u5404\u6210\u5458\u4f01\u4e1a\u4e4b\u95f4\u7684\u534f\u8c03\u5c31\u4f1a\u8ba9\u5f20\u606a\u6293\u7834\u5934\uff0c\u7edd\u5bf9\u65e0\u6cd5\u5982\u6b64\u60a0\u95f2\u7684\u4eab\u53d7\u751f\u6d3b\u3002</p><p>\u3000\u3000\u5e73\u65f6\u5927\u5bb6\u90fd\u5404\u53f8\u5176\u804c\uff0c\u91cd\u8981\u4e8b\u4ef6\u90fd\u6709\u53ca\u65f6\u7684\u5907\u5fd8\u5f55\u8f6c\u53d1\u7ed9\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u6210\u5458\uff0c\u5fae\u8f6f\u63d0\u51fa\u6536\u8d2d\u5168\u7403\u95f4\u4e50\u5728\u7ebf\u80a1\u6743\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u4e0d\u5f53\u8bdd\u9898\u63d0\u8d77\uff0c\u5b59\u5c1a\u4e49\u3001\u53f6\u5efa\u658c\u6700\u65e9\u8981\u7b49\u5230\u660e\u5929\u4e0a\u5348\u624d\u4f1a\u4ece\u5546\u52a1\u90ae\u7bb1\u91cc\u770b\u5230\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u5012\u662f\u6562\u5bf9\u5fae\u8f6f\u72ee\u5b50\u5927\u5f00\u53e3\uff01\u201d\u53f6\u5efa\u658c\u773c\u775b\u77aa\u5f97\u6e9c\u5706\uff0c\u201c\u4e5d\u4ebf\u7f8e\u5143\uff01\u201d</p><p>\u3000\u3000\u9526\u6e56\u5c06\u6d77\u5916\u4e1a\u52a1\u90e8\u95e8\u90fd\u5e76\u5165\u9526\u6e56\u5546\u4e8b\uff0c\u867d\u7136\u6d77\u5916\u4e1a\u52a1\u76c8\u5229\u89c4\u6a21\u4eca\u5e74\u4ee5\u6765\u5927\u5e45\u589e\u6da8\uff0c\u4f46\u662f\u4e5f\u53ea\u80fd\u52c9\u5f3a\u51b2\u9500\u9526\u6e56\u5546\u4e8b\u6b64\u65f6\u7684\u8d22\u52a1\u4e0e\u7ba1\u7406\u6210\u672c\u2014\u2014\u9526\u6e56\u5546\u4e8b\u6b64\u65f6\u80cc\u8d1f\u7740\u9ad8\u8fbe\u4e8c\u5341\u4e8c\u4ebf\u7f8e\u5143\u7684\u5de8\u503a\uff0c\u4ec5\u516c\u53f8\u503a\u606f\u7684\u8d22\u52a1\u6210\u672c\u652f\u51fa\u6bcf\u5e74\u5c31\u8981\u8d85\u8fc7\u4e00\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u867d\u7136\u4e8c\u5341\u4e8c\u4ebf\u7f8e\u5143\u4e2d\u6709\u4e09\u4ebf\u7f8e\u5143\u5b9e\u9645\u4e0a\u5c31\u662f\u9526\u6e56\u5546\u4e8b\u7684\u81ea\u6709\u8d44\u672c\u91d1\uff0c\u4f46\u662f\u5269\u4e0b\u7684\u5341\u4e5d\u4ebf\u7f8e\u5143\uff0c\u5374\u5b9e\u5b9e\u5728\u5728\u7684\u90fd\u662f\u503a\u52a1\u3002</p><p>\u3000\u3000\u8671\u5b50\u591a\u4e86\u4e0d\u6015\u54ac\uff0c\u80cc\u4e86\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u503a\u52a1\uff0c\u53f6\u5efa\u658c\u4e5f\u4e0d\u89c9\u5f97\u6709\u4ec0\u4e48\uff0c\u518d\u8bf4\u9526\u6e56\u5546\u4e8b\u5728\u5404\u5730\u7684\u6295\u8d44\u5904\u5904\u5f00\u82b1\uff0c\u53ea\u6068\u94b1\u4e0d\u591f\u7528\uff0c\u518d\u591a\u80cc\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u503a\u52a1\uff0c\u53f6\u5efa\u658c\u4f1a\u89c9\u5f97\u66f4\u8212\u670d\u4e9b\u3002</p><p>\u3000\u3000\u53bb\u5e74\u80fd\u501f\u534e\u4eba\u8d44\u672c\u4ed3\u7687\u9003\u79bb\u5370\u5c3c\u4e4b\u673a\u5927\u8086\u878d\u8d44\u7684\u826f\u673a\u53ef\u4ee5\u8bf4\u662f\u6570\u5341\u5e74\u96be\u9047\u4e00\u6b21\uff0c\u73b0\u5728\u8981\u5927\u89c4\u6a21\u878d\u8d44\u4e5f\u53ef\u4ee5\uff0c\u4f46\u662f\u9996\u5148\u8981\u5411\u6295\u8d44\u4eba\u63d0\u4f9b\u53ef\u9760\u7684\u8d22\u52a1\u62a5\u544a\uff0c\u4ee5\u8bc1\u660e\u5411\u9526\u6e56\u5546\u4e8b\u6295\u8d44\u662f\u4e00\u9879\u98ce\u9669\u5c11\u3001\u6536\u76ca\u7a33\u5b9a\u7684\u884c\u4e3a\u2014\u2014\u9526\u6e56\u5546\u4e8b\u6b64\u65f6\u7684\u8d22\u52a1\u7ed3\u6784\u6839\u672c\u5c31\u65e0\u6cd5\u652f\u6301\u5927\u89c4\u6a21\u7684\u878d\u8d44\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u867d\u7136\u5634\u91cc\u8bf4\u5f20\u606a\u662f\u5728\u5bf9\u5fae\u8f6f\u72ee\u5b50\u5927\u5f00\u53e3\uff0c\u4ed6\u5fc3\u91cc\u96be\u9053\u5c31\u4e0d\u5962\u671b\u5fae\u8f6f\u7ba1\u7406\u5c42\u96c6\u4f53\u5f97\u4e86\u5931\u5fc3\u75af\u540c\u610f\u4ed6\u4eec\u7684\u6761\u4ef6\uff1f\u8981\u80fd\u4ece\u5fae\u8f6f\u624b\u91cc\u5957\u4e5d\u4ebf\u7f8e\u5143\uff0cESS\u624b\u4e2d\u6240\u6301\u6606\u817e\u5728\u7ebf\u7684\u80a1\u7968\u5230\u671f\u540e\u518d\u5957\u73b0\u4e09\u56db\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u7684\u73b0\u91d1\u6d41\u5c31\u51ed\u7a7a\u591a\u4e86\u5341\u4e8c\u4e09\u4ebf\u7f8e\u5143\u4e4b\u591a\uff0c\u6709\u6b64\u4e3a\u57fa\u7840\uff0c\u5c31\u53ef\u4ee5\u5728\u4e1c\u5357\u4e9a\u534e\u5546\u5708\u5185\u518d\u878d\u8d44\u5341\u4ebf\u7f8e\u5143\uff0c\u81f3\u5c11\u9526\u6e56\u5546\u4e8b\u4e24\u5e74\u5185\u4e0d\u7528\u4e3a\u8d44\u91d1\u7684\u95ee\u9898\u53d1\u6101\u3002</p><p>\u3000\u3000\u5982\u4eca\u4e25\u5bb6\u7684\u5b8f\u4fe1\u7cfb\u5728\u6d77\u7c9f\u79d1\u6280\u4e0a\u641e\u8fd9\u4e48\u5927\u7684\u624b\u811a\uff0c\u8fd8\u4e0d\u662f\u7ed9\u8d44\u91d1\u9965\u6e34\u75c7\u95f9\u7684\uff1f</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5e7b\u60f3\u4e86\u4e00\u4f1a\u513f\uff0c\u53c8\u81ea\u5df1\u6447\u8d77\u5934\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u53ef\u80fd\uff0c\u4e0d\u8981\u505a\u8fd9\u4e2a\u68a6\u4e86\uff0c\u80fd\u4ece\u5fae\u8f6f\u624b\u91cc\u6572\u5230\u4e09\u4ebf\u7f8e\u5143\uff0c\u6211\u4eec\u5c31\u8be5\u77e5\u8db3\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6f2b\u5929\u8981\u4ef7\u3001\u5750\u5730\u8fd8\u94b1\u2026\u2026\u201d\u5f20\u606a\u8fd8\u662f\u7b11\u772f\u772f\u7684\u8bf4\u8fd9\u53e5\u8bdd\u3002</p><p>\u3000\u3000\u8eab\u5904\u5fae\u8f6f\u7684\u51b3\u7b56\u5c42\u5708\u5b50\u4e4b\u5916\uff0c\u4e0d\u4f1a\u6e05\u695a\u5fae\u8f6f\u51b3\u7b56\u8005\u7684\u6218\u7565\u610f\u56fe\uff0c\u4f46\u662f\u5f20\u606a\u77e5\u9053\u5fae\u8f6f\u4eca\u540e\u8fd1\u5341\u5e74\u7684\u53d1\u5c55\u5386\u7a0b\uff0c\u5dee\u4e0d\u591a\u5c31\u80fd\u63a8\u6d4b\u51fa\u5fae\u8f6f\u51b3\u7b56\u8005\u6b64\u65f6\u7684\u6218\u7565\u610f\u56fe\uff0c\u6240\u4ee5\u77e5\u9053\u4e5d\u4ebf\u7f8e\u5143\u8fd8\u662f\u6709\u8ba8\u4ef7\u8fd8\u4ef7\u7684\u4f59\u5730\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u5728\u8c08\u4ec0\u4e48\uff1f\u201d\u5b59\u9759\u9999\u6362\u4e86\u4fbf\u88c5\u8d70\u4e86\u8fdb\u6765\uff0c\u6328\u7740\u5979\u7238\u7238\u5b59\u5c1a\u4e49\u800c\u5750\u3002</p><p>\u3000\u3000\u201c\u5728\u8c08\u600e\u4e48\u6572\u8bc8\u5fae\u8f6f\u2026\u2026\u201d\u5b59\u5c1a\u4e49\u7b11\u7740\u62cd\u4e86\u62cd\u5927\u5973\u513f\u633d\u7740\u4ed6\u80f3\u818a\u7684\u624b\u80cc\u3002</p><p>\u3000\u3000\u201c\u6572\u8bc8\u5fae\u8f6f\uff1f\u201d\u5b59\u9759\u9999\u7591\u60d1\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u4e5f\u5c31\u8fd9\u6837\u95ee\u4e86\u4e00\u4e0b\uff0c\u6709\u4e9b\u4e8b\u60c5\u8981\u641e\u6e05\u695a\uff0c\u5c11\u8bf4\u8981\u82b1\u534a\u5c0f\u65f6\u8ba4\u771f\u542c\u7740\u89e3\u91ca\u624d\u884c\uff0c\u5979\u624d\u6ca1\u6709\u8fd9\u4e2a\u95f2\u5de5\u592b\u8ffd\u95ee\u9526\u6e56\u7684\u5546\u4e1a\u673a\u5bc6\u7ec6\u8282\u3002</p><p>\u3000\u3000\u201c\u6211\u6536\u9edb\u513f\u4f5c\u5e72\u5973\u513f\u4e86\uff0c\u660e\u5929\u5c31\u529e\u9152\uff0c\u8981\u662f\u8c01\u518d\u6562\u6709\u4ec0\u4e48\u4e0b\u4e09\u6ee5\u7684\u624b\u6bb5\u9488\u5bf9\u9edb\u513f\uff0c\u90a3\u5c31\u662f\u76f4\u63a5\u5c06\u6c61\u6c34\u6cfc\u5728\u6211\u845b\u5bb6\u5934\u4e0a\u3002\u201d\u845b\u660e\u73cf\u8d70\u8fdb\u5305\u53a2\u5fc3\u91cc\u7684\u6012\u6c14\u8fd8\u6ca1\u6709\u6d88\uff0c\u7ad9\u5728\u90a3\u91cc\u5ba3\u5e03\u5979\u7684\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\u9edb\u513f\uff0c\u9edb\u513f\u6362\u4e86\u4e00\u8eab\u6de1\u7d2b\u8272\u7684\u77ed\u88d9\u542b\u7f9e\u7684\u7ad9\u5728\u845b\u660e\u73cf\u7684\u8eab\u540e\uff0c\u773c\u7738\u5b50\u6545\u610f\u7684\u907f\u5f00\u4ed6\uff0c\u518d\u770b\u8d70\u8fdb\u6765\u7684\u5510\u5a67\u3001\u76db\u590f\u5979\u4eec\u7684\u8868\u60c5\uff0c\u5fc3\u60f3\u8fd9\u5e94\u8be5\u662f\u5979\u4eec\u521a\u624d\u56de\u623f\u6362\u8863\u670d\u65f6\u8bf4\u597d\u7684\u4e8b\u60c5\uff0c\u7ad9\u8d77\u6765\u62c9\u8fc7\u5510\u5a67\u7684\u5c0f\u624b\uff0c\u8ba9\u5979\u5750\u5230\u81ea\u5df1\u7684\u8eab\u8fb9\uff0c\u671d\u5b59\u5c1a\u4e49\u7b11\u7740\u8bf4\uff1a\u201c\u606d\u559c\u4f60\u53c8\u591a\u4e86\u4e00\u4e2a\u597d\u5973\u513f\u2026\u2026\u201d</p><p>\u3000\u3000\u9999\u6e2f\u6f14\u827a\u754c\u4e0e\u504f\u95e8\u52bf\u95e8\u5173\u7cfb\u5bc6\u5207\uff0c\u6c5f\u6e56\u6c14\u606f\u6d53\u91cd\uff0c\u9edb\u513f\u6b64\u65f6\u6700\u591a\u53ea\u80fd\u7b97\u4e16\u7eaa\u534e\u97f3\u65d7\u4e0b\u7684\u5f53\u7ea2\u827a\u4eba\u3001\u6447\u94b1\u6811\uff0c\u96be\u4fdd\u8fd8\u4f1a\u6709\u4ec0\u4e48\u9634\u9669\u4e0b\u4f5c\u624b\u6bb5\u671d\u5979\u4f7f\u51fa\u6765\uff0c\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u5c06\u5979\u6536\u4f5c\u5e72\u5973\u513f\uff0c\u610f\u4e49\u5c31\u5927\u4e3a\u4e0d\u540c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u5f88\u9ad8\u5174\u80fd\u8fd9\u4e48\u5b89\u6392\uff0c\u4f46\u662f\u53c8\u4e0d\u80fd\u8868\u73b0\u592a\u8fc7\uff0c\u8ddf\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u9053\u8d3a\uff0c\u5374\u6ca1\u6709\u8ddf\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u592b\u5987\u9053\u8d3a\uff0c\u7a0d\u7a0d\u8868\u73b0\u51fa\u4e9b\u533a\u522b\u6765\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u662f\u53ea\u4e3a\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u6536\u4e86\u5e72\u5973\u513f\u9ad8\u5174\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u7684\u4e8b\u60c5\uff0c\u9edb\u513f\u7236\u6bcd\u6709\u4e9b\u4e0d\u5f53\u7684\u5730\u65b9\uff0c\u4e5f\u8bf7\u4f60\u4f53\u8c05\u4ed6\u4eec\u7231\u5973\u5fc3\u5207\uff0c\u6211\u8ba9\u4ed6\u4eec\u7ed9\u4f60\u656c\u9152\u8d54\u7f6a\u3002\u201d\u845b\u660e\u73cf\u4ee5\u5e72\u5988\u81ea\u5c45\uff0c\u5c06\u4e8b\u60c5\u90fd\u5927\u5305\u5927\u63fd\u4e0b\u6765\uff0c\u5bf9\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u770b\u7740\u9edb\u513f\u5979\u7238\u5988\u62ff\u7740\u7ea2\u9152\u74f6\u771f\u8981\u8fc7\u6765\u656c\u9152\u8d54\u7f6a\uff0c\u5f20\u606a\u614c\u5fd9\u7684\u7ad9\u8d77\u6765\uff0c\u5c06\u9edb\u513f\u5979\u7238\u624b\u91cc\u7684\u9ad8\u811a\u676f\u6293\u4f4f\uff0c\u4e0d\u8ba9\u4ed6\u656c\u9152\uff0c\u563b\u7b11\u7740\u8bf4\uff1a\u201c\u8981\u4e0d\u8ba9\u674e\u59e8\u5728\u6211\u53f3\u8138\u4e0a\u518d\u6293\u4e24\u9053\uff0c\u6211\u5c31\u8ba9\u6c5f\u53d4\u3001\u674e\u59e8\u7ed9\u6211\u656c\u9152\uff1f\u201d\u8981\u518d\u8ba9\u9edb\u513f\u5979\u7238\u5988\u656c\u9152\u966a\u7f6a\uff0c\u90a3\u5728\u533b\u52a1\u5ba4\u4e0d\u662f\u7ed9\u9edb\u513f\u767d\u543b\u4e86\u2014\u2014\u867d\u7136\u4e5f\u4e0d\u6e05\u695a\u4ec0\u4e48\u65f6\u5019\u6709\u673a\u4f1a\u518d\u543b\u4e0a\u90a3\u5a07\u6da6\u8bf1\u4eba\u7684\u5634\u5507\u3002</p><p>\u3000\u3000\u5f20\u606a\u563b\u76ae\u7b11\u8138\u7684\u63d2\u79d1\u6253\u8be8\uff0c\u8ba9\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u96be\u4ee5\u518d\u575a\u6301\u656c\u9152\u8d54\u7f6a\uff0c\u5374\u4e5f\u4e0d\u80af\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u4e0e\u5f20\u606a\u76f8\u8bc6\u6700\u65e9\u662f\u5728\u4e5d\u516d\u5e74\u6625\u8282\u671f\u95f4\uff0c\u4f46\u662f\u63a5\u89e6\u6bd5\u7adf\u4e0d\u591a\uff0c\u6709\u4e9b\u7ec6\u5fae\u5904\u7684\u5c0f\u4e8b\u60c5\u9edb\u513f\u90fd\u4e0d\u8ddf\u4ed6\u4eec\u8bf4\uff0c\u4ed6\u4eec\u770b\u7231\u8fbe\u96c6\u56e2\u3001\u770b\u5f20\u606a\uff0c\u603b\u662f\u96fe\u91cc\u770b\u82b1\u6216\u8005\u8bf4\u662f\u9694\u5cb8\u770b\u82b1\u770b\u4e0d\u51fa\u4ec0\u4e48\u7a76\u7adf\u6765\u3002</p><p>\u3000\u3000\u5c31\u7b97\u662f\u4ee4\u5c0f\u71d5\u8fd9\u4e9b\u5e74\u8ddf\u5f20\u606a\u63a5\u89e6\u591f\u9891\u7e41\u4e86\uff0c\u518d\u52a0\u4e0a\u6731\u5c0f\u519b\u9ad8\u65b0\u533a\u8ddf\u7740\u59da\u6587\u76db\uff0c\u4e5f\u662f\u5728\u521b\u57df\u6b63\u5f0f\u878d\u5165\u9526\u6e56\u4e4b\u540e\uff0c\u5979\u4f5c\u4e3a\u521b\u57df\u7684\u9ad8\u5c42\u624d\u6bd4\u8f83\u6e05\u6670\u7684\u770b\u5230\u6574\u4e2a\u4ea7\u4e1a\u5e1d\u56fd\u7684\u8f6e\u5ed3\u3002</p><p>\u3000\u3000\u674e\u6708\u8339\u8fd8\u662f\u9edb\u513f\u6b63\u5f0f\u51b3\u5b9a\u4ece\u4e1c\u6d77\u5927\u5b66\u4f11\u5b66\u4e4b\u540e\u624d\u8ddf\u5728\u5de6\u53f3\uff0c\u90a3\u65f6\u9edb\u513f\u7684\u6f14\u827a\u751f\u6daf\u5dee\u4e0d\u591a\u5df2\u7ecf\u7ed9\u5b59\u9759\u9999\u4e00\u624b\u5305\u63fd\u4e0b\u6765\uff0c\u5c31\u7b97\u8fd9\u4e9b\u5e74\u6765\u4e00\u76f4\u4e0e\u7231\u8fbe\u96c6\u56e2\u6709\u5e7f\u544a\u4ee3\u8a00\u7684\u5408\u4f5c\uff0c\u90a3\u4e5f\u662f\u4e16\u7eaa\u534e\u97f3\u4e0e\u7231\u8fbe\u96c6\u56e2\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u674e\u6708\u8339\u53c2\u4e0e\u7684\u7a0b\u5ea6\u4e0d\u6df1\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u4e00\u76f4\u7559\u5728\u60e0\u5c71\u7ecf\u8425\u4ed6\u7684\u90a3\u5bb6\u94f8\u4ef6\u5382\uff0c\u5373\u4f7f\u56e0\u4e3a\u5f20\u606a\u7684\u7f18\u6545\u4e0e\u60e0\u5c71\u5e02\u59d4\u5e38\u59d4\u5b8b\u6653\u8fd9\u4e9b\u5b98\u5458\u642d\u4e0a\u5173\u7cfb\uff0c\u5374\u59cb\u7ec8\u6ca1\u80fd\u878d\u8fdb\u60e0\u5c71\u4e0a\u6d41\u793e\u4f1a\u7684\u6838\u5fc3\u5708\uff0c\u81ea\u7136\u4e5f\u4e0d\u53ef\u80fd\u77e5\u9053\u9526\u6e56\u4e8b\u3002</p><p>\u3000\u3000\u5728\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u592b\u5987\u7684\u773c\u91cc\uff0c\u80fd\u9690\u7ea6\u77e5\u9053\u5f20\u606a\u80cc\u540e\u7684\u52bf\u529b\u5f88\u5927\uff0c\u9edb\u513f\u80fd\u53d1\u5c55\u5230\u4eca\u5929\uff0c\u5f20\u606a\u63d0\u4f9b\u4e86\u5f88\u591a\u7684\u52a9\u529b\uff0c\u4f46\u662f\u6709\u4e9b\u60c5\u51b5\u4e0d\u4e86\u89e3\uff0c\u518d\u52a0\u674e\u6708\u8339\u8fd9\u4e24\u5e74\u966a\u5728\u9edb\u513f\u5de6\u53f3\uff0c\u4e0e\u5b59\u5bb6\u63a5\u89e6\u5bc6\u5207\uff0c\u89c1\u8bc6\u5230\u5b59\u5bb6\u65e5\u5e38\u751f\u6d3b\u7684\u5962\u534e\u4e4b\u5904\uff0c\u4e5f\u66f4\u6e05\u6670\u7684\u77e5\u9053\u5b59\u5bb6\u662f\u771f\u6b63\u7684\u8c6a\u5bcc\u4e4b\u5bb6\uff0c\u4ed6\u4eec\u66f4\u4ee5\u4e3a\u9edb\u513f\u6f14\u827a\u751f\u6daf\u4e2d\u771f\u6b63\u7684\u8d35\u4eba\u662f\u5b59\u9759\u9999\u3001\u5b59\u5bb6\u3002</p><p>\u3000\u3000\u8981\u8bf4\u52bf\u5229\uff0c\u674e\u6708\u8339\u8981\u6bd4\u4e08\u592b\u6c5f\u6d9b\u52bf\u5229\u591a\u4e86\uff0c\u4f46\u662f\u4e3a\u4eba\u7236\u6bcd\u518d\u52bf\u5229\uff0c\u603b\u4e0d\u4f1a\u773c\u7741\u7741\u7684\u770b\u7740\u5973\u513f\u7ed9\u4eba\u6252\u8863\u670d\u6b3a\u8d1f\u8fd8\u80fd\u6309\u5948\u4f4f\u6012\u706b\u7684\u4e0d\u53d1\u98da\u7684\u3002\u5f53\u7136\u4e86\uff0c\u7b49\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u592b\u5987\u51b7\u9759\u4e0b\u6765\uff0c\u53c8\u77e5\u9053\u4e8b\u51fa\u6709\u56e0\u3001\u6839\u672c\u5c31\u662f\u4ed6\u4eec\u8bef\u4f1a\u4e86\u4eba\u5bb6\uff0c\u5fc3\u91cc\u624d\u540e\u6015\u8d77\u6765\u3002</p><p>\u3000\u3000\u867d\u7136\u5b59\u9759\u9999\u3001\u8bb8\u7ef4\u3001\u5510\u5a67\u5979\u4eec\u4e00\u518d\u7684\u8ddf\u4ed6\u4eec\u8bf4\u8fd9\u4ef6\u4e8b\u4e0d\u7528\u653e\u5728\u5fc3\u4e0a\uff0c\u751a\u81f3\u9edb\u513f\u4e5f\u8981\u4ed6\u4eec\u653e\u5bbd\u5fc3\uff0c\u4ed6\u4eec\u5fc3\u91cc\u59cb\u7ec8\u653e\u4e0d\u4e0b\u62c5\u5fe7\uff0c\u89c9\u5f97\u4eb2\u81ea\u656c\u8fc7\u9152\u9053\u8fc7\u6b49\u5f20\u606a\u4e5f\u5f53\u9762\u63a5\u53d7\u4e86\u8fd9\u4ef6\u4e8b\u624d\u80fd\u7b97\u5b8c\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u3001\u674e\u6708\u8339\u575a\u6301\u8981\u656c\u8fc7\u9152\u9053\u8fc7\u6b49\u624d\u653e\u5fc3\uff0c\u8fde\u845b\u660e\u73cf\u90fd\u89c9\u5f97\u5f20\u606a\u5730\u4f4d\u3001\u8eab\u4efd\u8fd9\u4e48\u91cd\u8981\uff0c\u4e00\u5b9a\u8981\u7ed9\u4ed6\u4e2a\u9762\u5b50\u4e0b\u53f0\uff0c\u5728\u65c1\u8fb9\u529d\u5f20\u606a\u5c31\u63a5\u8fd9\u676f\u9152\u3002</p><p>\u3000\u3000\u5f20\u606a\u54ea\u91cc\u80af\u8ba9\u9edb\u513f\u7236\u6bcd\u7ed9\u81ea\u5df1\u656c\u9152\u9053\u6b49\uff0c\u9edb\u513f\u7236\u6bcd\u7684\u8fd9\u676f\u9152\u559d\u4e0b\u53bb\uff0c\u5c82\u4e0d\u662f\u9edb\u513f\u7684\u7b2c\u4e8c\u4e2a\u543b\u4ee5\u540e\u5c31\u518d\u6ca1\u6709\u7740\u843d\u4e86\uff1f</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u77e5\u9053\u4eca\u5929\u665a\u4e0a\u53d1\u751f\u7684\u9634\u8c0b\uff0c\u5374\u4e0d\u77e5\u9053\u5f20\u606a\u8138\u7ed9\u6293\u7834\u7684\u7ec6\u8282\uff0c\u542c\u5f20\u606a\u5728\u8fd9\u91cc\u63d2\u79d1\u6253\u8be8\uff0c\u4e00\u65f6\u95f4\u4e5f\u4e0d\u660e\u767d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4f46\u89c1\u4ed6\u8f7b\u63cf\u6de1\u5199\u7684\u5904\u7406\u8fd9\u4e8b\uff0c\u592a\u8ba4\u771f\u53cd\u5012\u7834\u574f\u6c14\u6c1b\u4e86\uff0c\u7ad9\u8d77\u6765\u8bf4\u9053\uff1a\u201c\u5728\u8fd9\u91cc\u5927\u5bb6\u90fd\u662f\u79c1\u4eba\u5173\u7cfb\uff0c\u5f20\u606a\u8ddf\u9759\u9999\u3001\u9759\u6aac\u5979\u4eec\u662f\u670b\u53cb\uff0c\u6211\u4eec\u5c31\u8981\u7b97\u957f\u8f88\u4e86\uff0c\u8981\u662f\u5fc3\u60c5\u597d\u60f3\u591a\u5403\u9152\uff0c\u90a3\u5927\u5bb6\u90fd\u7ad9\u4e0b\u6765\u655e\u5f00\u6000\u5403\uff0c\u656c\u4e0d\u656c\u9152\u5c31\u7b97\uff0c\u4e0d\u8981\u592a\u62d8\u675f\u2026\u2026\u8981\u8bf4\u656c\u9152\uff0c\u4e5f\u5e94\u8be5\u8ba9\u9edb\u513f\u5148\u656c\u6211\u8ddf\u660e\u73cf\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u8fd9\u4e48\u8bf4\uff0c\u5c31\u7b49\u4e8e\u5f00\u53e3\u627f\u8ba4\u6536\u9edb\u513f\u4f5c\u5e72\u5973\u513f\uff0c\u6c5f\u6d9b\u3001\u674e\u6708\u5982\u5fd9\u5c06\u659f\u4e86\u9152\u7684\u9152\u676f\u9012\u7ed9\u9edb\u513f\uff0c\u8ba9\u5979\u7ed9\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u656c\u9152\u3002</p><p>\u3000\u3000\u9edb\u513f\u7ed9\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u73cf\u592b\u5987\u656c\u8fc7\u9152\uff0c\u53c8\u671d\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u4ee3\u8868\u6211\u7238\u5988\u7ed9\u4f60\u656c\u9152\u8d54\u7f6a\uff0c\u8fd9\u676f\u9152\u4f60\u4e00\u5b9a\u8981\u559d\u4e0b\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u9edb\u513f\u79c0\u7f8e\u7684\u7738\u5b50\u542b\u7740\u522b\u6837\u7684\u60c5\u610f\uff0c\u5f20\u606a\u5fc3\u60f3\u7740\u603b\u8981\u8ba9\u9edb\u513f\u7236\u6bcd\u653e\u5bbd\u5fc3\uff0c\u62ff\u8d77\u9152\u676f\u8f7b\u78b0\u4e86\u4e00\u4e0b\uff0c\u53ea\u542c\u7740\u9152\u676f\u8f7b\u51fb\u53d1\u51fa\u60a6\u8033\u7684\u8f7b\u54cd\uff0c\u5c06\u5927\u534a\u676f\u7ea2\u9152\u4e00\u53e3\u6c14\u90fd\u559d\u4e86\u4e0b\u53bb\uff1b\u9edb\u513f\u4e5f\u5c06\u534a\u676f\u7ea2\u9152\u90fd\u559d\u4e0b\u809a\u5b50\uff0c\u8102\u6ed1\u82e5\u7389\u7684\u808c\u80a4\u900f\u51fa\u8bf1\u4eba\u7684\u7ea2\u6655\uff0c\u5fae\u655b\u7740\u4e0b\u9894\uff0c\u5750\u56de\u5230\u5979\u5988\u5988\u8eab\u8fb9\uff0c\u773c\u795e\u907f\u5f00\u4e0d\u518d\u770b\u5f20\u606a\uff0c\u751f\u6015\u518d\u770b\u4e00\u773c\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u63a9\u85cf\u4e0d\u4f4f\u3002</p><p>\u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u5927\u5bb6\u90fd\u7686\u5927\u6b22\u559c\uff0c\u5750\u4e0b\u6765\u8c08\u7b11\uff0c\u8bb8\u7ef4\u5306\u5fd9\u4ece\u5916\u9762\u8d70\u91cc\uff0c\u5979\u624b\u91cc\u62ff\u7740\u5377\u8d77\u6765\u7684\u51e0\u5f20\u539a\u539a\u7684\u7eb8\uff0c\u770b\u7740\u5f20\u606a\u5728\u90a3\u91cc\u559d\u9152\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u4f60\u89c1\u8bc6\u4e00\u4e0b\u9999\u6e2f\u72d7\u4ed4\u961f\u7684\u6c34\u51c6\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u8bb8\u7ef4\u644a\u5f00\u6765\u5374\u662f\u51e0\u5f20\u6253\u5370\u51fa\u6765\u7684\u5f69\u8272\u7167\u7247\uff0c\u6700\u4e0a\u9762\u4e00\u5f20\u662f\u6c5f\u9edb\u513f\u7a7f\u886c\u886b\u5728\u821e\u53f0\u4e0a\u6f14\u5531\u7684\u5f62\u8c61\uff0c\u7b2c\u4e8c\u5f20\u5374\u662f\u4ed6\u8d70\u8fdb\u540e\u53f0\u7684\u7167\u7247\uff0c\u867d\u7136\u53ea\u662f\u4ece\u4fa7\u540e\u9762\u53ea\u9732\u5c0f\u534a\u7247\u8138\uff0c\u4f46\u662f\u5f20\u606a\u77e5\u9053\u62cd\u4e86\u662f\u81ea\u5df1\uff0c\u4ed6\u3001\u5085\u4fca\u4ee5\u53ca\u968f\u884c\u7684\u4fdd\u9556\u90fd\u6ca1\u6709\u610f\u8bc6\u5230\u7ed9\u4eba\u62cd\u7167\u3002\u4e24\u5f20\u7167\u7247\u4e0a\u90fd\u7528\u7ea2\u8272\u8bb0\u53f7\u7b14\u5c06\u4ed6\u4fe9\u4eba\u8eab\u4e0a\u540c\u4e00\u4ef6\u886c\u886b\u9c9c\u660e\u7684\u6807\u51fa\u6765\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u53c8\u5c06\u7167\u7247\u4e0b\u9762\u7684\u4e00\u5f20\u7eb8\u644a\u5f00\u8bfb\uff1a\u201c\u300a\u65b0\u751f\u4ee3\u6b4c\u540e\u6f14\u5531\u4f1a\u3001\u5e55\u540e\u7537\u4eba\u767b\u573a\u300b\uff0c\u8fd9\u6837\u7684\u65b0\u95fb\u6807\u9898\u591f\u4e0d\u591f\u9a87\u4eba\u542c\u95fb\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u54b3\u54b3\u2026\u2026\u201d\u5f20\u606a\u54b3\u55fd\u4e86\u4e24\u58f0\u63a9\u9970\u5fc3\u91cc\u7684\u5c34\u5c2c\uff0c\u5373\u4f7f\u5728\u5ea7\u7684\u5927\u5bb6\u90fd\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u5fc3\u91cc\u8fd8\u662f\u6709\u4e00\u79cd\u505a\u8d3c\u7ed9\u6349\u4f4f\u7684\u5fc3\u865a\uff0c\u90fd\u6709\u4e9b\u4e0d\u6562\u770b\u5510\u5a67\uff0c\u8146\u7740\u8138\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u65f6\u5019\u62cd\u7684\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u5728\u610f\u5230\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u5c06\u7167\u7247\u63a5\u8fc7\u6765\u770b\u4e86\u770b\uff0c\u8ddf\u8bb8\u7ef4\u8bf4\u9053\uff1a\u201c\u8fd9\u662f\u6293\u62cd\u4e0b\u6765\u7684\u7167\u7247\uff0c\u53ef\u80fd\u662f\u5f20\u606a\u5750\u8f66\u5230\u505c\u8f66\u573a\u76f4\u63a5\u5954\u540e\u53f0\u5f15\u8d77\u4e86\u72d7\u725b\u4ed4\u7684\u6ce8\u610f\uff0c\u624d\u7ed9\u4ece\u4fa7\u540e\u6293\u62cd\u4e86\u4fa7\u8138\u2026\u2026\u662f\u54ea\u5bb6\u62a5\u793e\u7684\u72d7\u4ed4\u961f\uff0c\u5c06\u8fd9\u4e9b\u4e1c\u897f\u9001\u8fc7\u6765\uff0c\u662f\u4e0d\u662f\u51c6\u5907\u5c06\u5e95\u7247\u5356\u7ed9\u6211\u4eec\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f00\u4ef7\u5c31\u8981\u4e00\u767e\u4e07\uff01\u201d\u8bb8\u7ef4\u8bf4\u9053\uff0c\u201c\u4ed6\u4eec\u4e5f\u5c06\u8f66\u5b50\u7684\u7167\u7247\u90fd\u62cd\u4e86\u4e0b\u6765\uff0c\u5e94\u8be5\u6ca1\u6709\u62cd\u4e86\u5f20\u606a\u7684\u6b63\u8138\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u8981\u94b1\u4e0d\u8981\u547d\u4e86\uff0c\u201d\u5f20\u606a\u62cd\u4e86\u62cd\u989d\u5934\uff0c\u4e0d\u5c51\u4e00\u987e\u7684\u8bf4\u9053\uff0c\u201c\u901a\u77e5\u8b66\u65b9\u8bf4\u8fd9\u5bb6\u5a92\u4f53\u6709\u4e0e\u56fd\u65d7\u886b\u4e8b\u4ef6\u7275\u8fde\u5e76\u6709\u6572\u8bc8\u52d2\u7d22\u7684\u5acc\u7591\uff0c\u8ba9\u8b66\u65b9\u5c06\u5e95\u7247\u4f5c\u4e3a\u8bc1\u636e\u6536\u56de\u53bb\u5c31\u597d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u60f3\u60f3\u4e5f\u771f\u662f\u60ca\u9669\uff0c\u5e78\u4e8f\u6f14\u5531\u4f1a\u540e\u4ed6\u4e00\u76f4\u7559\u5728\u533b\u52a1\u5ba4\u91cc\u5904\u7406\u8138\u4e0a\u7684\u6293\u4f24\uff0c\u6ca1\u6709\u7acb\u5373\u5c31\u968f\u6c5f\u9edb\u513f\u5979\u4eec\u53bb\u534a\u5c9b\u9152\u5e97\u5e86\u529f\uff0c\u4e0d\u7136\u886c\u886b\u7684\u7834\u7efd\u53ea\u6015\u8981\u7ed9\u5728\u573a\u6240\u6709\u7684\u6444\u5f71\u8bb0\u8005\u53d1\u73b0\u3002\u9999\u6e2f\u7684\u5a31\u4e50\u5a92\u4f53\u7279\u522b\u559c\u6b22\u6b7b\u7f20\u70c2\u6253\u7684\uff0c\u5230\u65f6\u5019\u5c31\u7b97\u5c06\u56fd\u65d7\u886b\u4e8b\u4ef6\u6296\u51fa\u6765\uff0c\u4e5f\u672a\u5fc5\u80fd\u5c06\u7eef\u95fb\u6f84\u6e05\uff0c\u53ea\u4f1a\u8d8a\u7ea0\u7f20\u8d8a\u6df1\uff0c\u6700\u7ec8\u5bfc\u81f4\u4ed6\u7684\u8eab\u4efd\u5f7b\u5e95\u7ed9\u9999\u6e2f\u5a92\u4f53\u66b4\u5149\u3002\u53ea\u6709\u4e00\u5bb6\u5a92\u4f53\u7684\u72d7\u4ed4\u961f\u53d1\u73b0\uff0c\u800c\u4e14\u8fd8\u53ea\u662f\u4ece\u4fa7\u540e\u6293\u62cd\u5230\u5c0f\u534a\u5f20\u8138\uff0c\u4e8b\u60c5\u5c31\u5f88\u5bb9\u6613\u89e3\u51b3\u4e86\uff0c\u901a\u8fc7\u8b66\u65b9\u5c06\u56fd\u65d7\u886b\u4e8b\u4ef6\u539f\u59d4\u544a\u8bc9\u72d7\u4ed4\u961f\uff0c\u72d7\u4ed4\u961f\u8fd8\u6562\u4e71\u5199\u4e71\u62a5\u9053\uff0c\u5c31\u53ef\u4ee5\u544a\u5230\u4ed6\u4eec\u503e\u5bb6\u8361\u4ea7\u3002</p></article>"}}}]);
