(window.webpackJsonp=window.webpackJsonp||[]).push([[932],{955:function(p){p.exports={chapterTitle:null,title:"\u7b2c749\u7ae0 \u8fc7\u6fc0\u53cd\u5e94",content:"<article><h2>\u7b2c749\u7ae0 \u8fc7\u6fc0\u53cd\u5e94</h2><p>\u3000\u3000\u89c1\u9b4f\u4e1c\u5f3a\u4e00\u4ed8\u76ef\u4e0a\u81ea\u5df1\u7684\u773c\u795e\uff0c\u8fd9\u65f6\u5019\u6559\u5ba4\u91cc\u540c\u5b66\u7684\u76ee\u5149\u90fd\u805a\u4e86\u8fc7\u6765\uff0c\u4e24\u540d\u76d1\u8003\u8001\u5e08\u4e0d\u660e\u6240\u4ee5\u7684\u8d70\u8fc7\u6765\uff0c\u5c31\u5dee\u6700\u540e\u4e00\u6839\u7a3b\u8349\u6c88\u7b71\u773c\u6674\u91cc\u7684\u6cea\u6c34\u5c31\u8981\u843d\u4e0b\u6765\uff0c\u5f20\u606a\u89c9\u5f97\u6709\u4e9b\u5934\u75bc\uff0c\u6ca1\u60f3\u5230\u8981\u79bb\u5f00\u5efa\u90ba\u4e4b\u524d\u8fd8\u9047\u4e0a\u8fd9\u79cd\u9ebb\u70e6\u4e8b\uff0c\u4e5f\u5c31\u6ca1\u6709\u60f3\u5728\u6559\u5ba4\u91cc\u8ddf\u9b4f\u4e1c\u5f3a\u4e89\u6267\u4ec0\u4e48\uff0c\u626f\u4e86\u626f\u6c88\u7b71\u7684\u8863\u670d\uff0c\u8ba9\u5979\u5148\u4e0e\u81ea\u5df1\u8ddf\u9b4f\u4e1c\u5f3a\u8d70\u51fa\u6559\u5ba4\u518d\u8bf4\u3002</p><p>\u3000\u3000\u6559\u5ba4\u91cc\u5df2\u7ecf\u6709\u4e9b\u5435\u95f9\uff0c\u4e24\u540d\u76d1\u8003\u8001\u5e08\u6ca1\u6709\u6025\u7740\u8d70\u51fa\u53bb\u95ee\u7a76\u7adf\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u9b4f\u4e1c\u5f3a\u53c8\u4e0d\u662f\u6559\u52a1\u5904\u5b89\u6392\u7684\u5de1\u8003\u8001\u5e08\uff0c\u7a81\u7136\u8dd1\u8fdb\u6765\u63ea\u4f4f\u4f5c\u5f0a\u7684\u5b66\u751f\u8ba9\u4ed6\u4eec\u611f\u89c9\u5f88\u7a81\u5140\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u8981\u5148\u8ba9\u6559\u5ba4\u91cc\u7684\u5b66\u751f\u9759\u4e0b\u5fc3\u6765\u7ee7\u7eed\u7b54\u5377\u3002</p><p>\u3000\u3000\u89c1\u9b4f\u4e1c\u5f3a\u521a\u8d70\u51fa\u6559\u5ba4\u5c31\u5bf9\u6bd4\u624b\u91cc\u7684\u4e24\u5f20\u8bd5\u5377\uff0c\u5f20\u606a\u624d\u786e\u5b9a\u4ed6\u53ef\u80fd\u662f\u771f\u76ef\u4e0a\u81ea\u5df1\u4e86\u3002\u5f20\u606a\u8fd9\u65f6\u5019\u53ea\u662f\u4e0d\u60f3\u7ed9\u6c88\u7b71\u5e26\u53bb\u4ec0\u4e48\u8d1f\u9762\u7684\u5f71\u54cd\uff0c\u7279\u522b\u4e0d\u60f3\u7ed9\u5979\u5fc3\u7406\u4e0a\u7559\u4e0b\u4ec0\u4e48\u9634\u5f71\uff0c\u4e5f\u6ca1\u6709\u60f3\u7740\u8ddf\u9b4f\u4e1c\u5f3a\u77e2\u53e3\u5426\u8ba4\uff0c\u90a3\u6837\u53ea\u4f1a\u8ddf\u9b4f\u4e1c\u5f3a\u5f53\u573a\u4e89\u5435\u8d77\u6765\uff0c\u7a0d\u4f5c\u6743\u8861\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u6211\u5728\u5077\u770b\u5728\u6c88\u7b71\u7684\u5377\u5b50\uff0c\u4f60\u662f\u4e0d\u662f\u8ba9\u6c88\u7b71\u8fdb\u53bb\u7ee7\u7eed\u8003\u8bd5\uff1f\u201d</p><p>\u3000\u3000\u9b4f\u4e1c\u5f3a\u5c31\u62c5\u5fc3\u5f20\u606a\u4f1a\u77e2\u53e3\u5426\u8ba4\uff0c\u8d70\u51fa\u6559\u5ba4\u624d\u60f3\u7740\u5148\u6bd4\u5bf9\u8bd5\u5377\u627e\u51fa\u96f7\u540c\u7684\u8bc1\u636e\uff0c\u542c\u4ed6\u5766\u7136\u627f\u8ba4\uff0c\u773c\u775b\u91cc\u5374\u6ca1\u6709\u5728\u4f5c\u5f0a\u540e\u7ed9\u5f53\u573a\u6293\u4f4f\u5e94\u8be5\u6709\u7684\u60f6\u6050\u4e0d\u5b89\uff0c\u8fd9\u5c31\u50cf\u4e00\u6839\u523a\u624e\u8fdb\u4ed6\u7684\u5fc3\u7a9d\u91cc\uff1a\u201c\u4f60\u5f53\u6211\u773c\u775b\u778e\u4e86\uff0c\u4f60\u4eec\u4fe9\u4eba\u5c31\u662f\u5728\u914d\u5408\u4f5c\u5f0a\u3002\u5b66\u6821\u4e00\u518d\u91cd\u7533\u8003\u8bd5\u7eaa\u5f8b\uff0c\u6293\u5230\u4e00\u4e2a\u5f00\u9664\u4e00\u4e2a\uff0c\u7edd\u4e0d\u59d1\u606f\u517b\u5978\uff0c\u6211\u5148\u63ea\u4f60\u4eec\u4fe9\u53bb\u6559\u52a1\u5904\uff0c\u770b\u770b\u4f60\u90a3\u4e2a\u5f53\u526f\u5e02\u957f\u7684\u8001\u5b50\u80fd\u4e0d\u80fd\u8ba9\u4f60\u8fd9\u6b21\u4e5f\u6df7\u8fc7\u5173\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u5c06\u8fd8\u6ca1\u6709\u7b54\u5b8c\u7684\u8bd5\u5377\u4ea4\u4e86\uff0c\u8d70\u51fa\u6765\u5c31\u542c\u89c1\u9b4f\u4e1c\u5f3a\u6709\u4e9b\u6b47\u65af\u5e95\u91cc\u7684\u53d1\u4f5c\uff0c\u77db\u5934\u6068\u4e0d\u5f97\u5c31\u76f4\u63a5\u6233\u5f20\u606a\u7684\u8138\u4e0a\u53bb\uff0c\u51b7\u51b7\u7684\u8bf4\u9053\uff1a\u201c\u9b4f\u4e3b\u4efb\uff0c\u90fd\u8bf4\u8981\u6309\u5b66\u53f7\u5750\uff0c\u9646\u5929\u53c8\u4f5c\u4e3a\u73ed\u957f\u5374\u5728\u8003\u8bd5\u524d\u8ddf\u5f20\u606a\u6362\u4e86\u5ea7\u4f4d\uff0c\u4e5f\u6709\u914d\u5408\u4f5c\u5f0a\u7684\u5acc\u7591\u554a\uff0c\u60a8\u8001\u662f\u4e0d\u662f\u8981\u5c06\u9646\u5929\u53c8\u4e00\u8d77\u63ea\u5230\u6559\u52a1\u5904\u53bb\uff1f\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u7684\u4ed6\u7236\u4eb2\u9646\u6587\u592b\u662f\u7701\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u5728\u8fd9\u5ea7\u57ce\u5e02\u62e5\u6709\u901a\u5929\u7684\u80fd\u91cf\uff0c\u53c8\u4e0e\u56fd\u5546\u9662\u9662\u957f\u5d14\u56fd\u6853\u662f\u540c\u7a97\u597d\u53cb\uff0c\u9b4f\u4e1c\u5f3a\u8fd8\u4e0d\u81f3\u4e8e\u767d\u76ee\u5230\u8981\u5c06\u9646\u5929\u53c8\u4e00\u8d77\u63ea\u5230\u6559\u52a1\u5904\u53bb\uff0c\u5bf9\u4e8e\u9646\u5929\u53c8\u4e0e\u5f20\u606a\u6362\u5ea7\u4f4d\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u81ea\u7136\u89c6\u800c\u4e0d\u89c1\u3002\u8fd9\u65f6\u5019\u4ed6\u89c1\u675c\u98de\u8dd1\u51fa\u6765\u6405\u6d51\u6c34\uff0c\u5fc3\u706b\u7ed9\u64a9\u5f97\u66f4\u52a0\u65fa\u76db\uff0c\u5389\u58f0\u559d\u65a5\u9053\uff1a\u201c\u4f60\u8dd1\u51fa\u53bb\u505a\u4ec0\u4e48\uff0c\u6211\u6709\u53eb\u4f60\u4e00\u8d77\u51fa\u6765\u5417\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u53ef\u4e0d\u4ecb\u610f\u7531\u4ed6\u6765\u6fc0\u6012\u9b4f\u4e1c\u5f3a\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4ea4\u4e86\u8bd5\u5377\uff0c\u6211\u8fd8\u9700\u8981\u7559\u5728\u6559\u5ba4\u91cc\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4fe9\u4e2a\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\uff0c\u4e0d\u8981\u5f53\u6211\u4e0d\u77e5\u9053\uff0c\u5f20\u606a\u4e0e\u6c88\u7b71\u8003\u8bd5\u4f5c\u5f0a\u7ed9\u6211\u5f53\u573a\u6293\u4f4f\uff0c\u4f60\u8fd8\u60f3\u66ff\u4ed6\u4eec\u63a9\u9970\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u675c\u98de\u4f7f\u4e86\u773c\u8272\uff0c\u793a\u610f\u4ed6\u4e0d\u8981\u5c06\u6c34\u6405\u6d51\uff0c\u4ed6\u4eec\u4fe9\u90fd\u662f\u6b7b\u732a\u4e0d\u6015\u5f00\u6c34\u70eb\u7684\u4e3b\uff0c\u6c88\u7b71\u5973\u5b69\u5b50\u5bb6\u5fc3\u7406\u627f\u53d7\u80fd\u529b\u5dee\uff0c\u5916\u9762\u8bf4\u8bdd\u7684\u58f0\u97f3\u5927\uff0c\u6559\u5ba4\u91cc\u7684\u540c\u5b66\u90fd\u5728\u63a2\u5934\u89c2\u671b\uff0c\u5728\u6559\u5ba4\u5916\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u5bf9\u6c88\u7b71\u4e0d\u597d\u3002</p><p>\u3000\u3000\u675c\u98de\u8fd9\u624d\u95ed\u5634\u4e0d\u8bf4\u8bdd\uff0c\u8fd9\u65f6\u5019\u4e24\u540d\u76d1\u8003\u8001\u5e08\u8dd1\u51fa\u6765\u770b\u7a76\u7adf\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u9646\u5929\u53c8\u4e5f\u5c06\u8bd5\u5377\u4ea4\u4e86\u8d70\u51fa\u6559\u5ba4\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba9\u675c\u98de\u62c9\u6c88\u7b71\u5148\u8d70\uff0c\u9b4f\u4e1c\u5f3a\u8981\u63ea\u4f4f\u6c88\u7b71\uff0c\u5f20\u606a\u6321\u4f4f\u4ed6\uff0c\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff1a\u201c\u662f\u6211\u5fcd\u4e0d\u4f4f\u62ac\u5934\u770b\u4e86\u4e24\u773c\uff0c\u9b4f\u4e3b\u4efb\u65e2\u7136\u8ba4\u5b9a\u8fd9\u5c31\u662f\u5728\u4f5c\u5f0a\uff0c\u90a3\u5c31\u7b97\u4f5c\u5f0a\u597d\u4e86\uff0c\u6211\u4e5f\u6ca1\u6709\u4ec0\u4e48\u597d\u5426\u8ba4\u7684\uff0c\u8981\u5904\u5206\u3001\u8981\u5f00\u9664\u4e5f\u7f62\uff0c\u6211\u90fd\u4f1a\u6001\u5ea6\u826f\u597d\u7684\u63a5\u53d7\u2026\u2026\u6ca1\u6709\u5176\u4ed6\u4e8b\u60c5\uff0c\u6211\u5c31\u5148\u8d70\u4e86\u3002\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8bf4\u5f97\u8f7b\u63cf\u6de1\u5199\uff0c\u9b4f\u4e1c\u5f3a\u773c\u775b\u91cc\u90fd\u5feb\u8981\u5192\u51fa\u706b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u7684\u8f7b\u5de7\uff0c\u4ec0\u4e48\u53eb\u5fcd\u4e0d\u4f4f\u62ac\u5934\u770b\u4e86\u4e24\u773c\uff1f\u201d\u62c9\u8fc7\u9646\u5929\u53c8\uff0c\u201c\u4f60\u8003\u8bd5\u5c31\u5750\u4ed6\u4eec\u540e\u9762\uff0c\u4f60\u8bf4\u4ed6\u4eec\u662f\u4e0d\u662f\u4ece\u5934\u5230\u5c3e\u90fd\u5728\u914d\u5408\u7740\u4f5c\u5f0a\uff1f\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u6709\u4e9b\u4e0d\u5927\u660e\u767d\u9b4f\u4e1c\u5f3a\u7684\u53cd\u5e94\u4e3a\u4ec0\u4e48\u8fd9\u4e48\u5927\uff0c\u5e74\u7ea7\u4e3b\u4efb\u4e0d\u5e94\u8be5\u8981\u7ef4\u62a4\u81ea\u5df1\u7684\u5b66\u751f\u3001\u6709\u4ec0\u4e48\u4e8b\u60c5\u53ef\u4ee5\u5185\u90e8\u6279\u8bc4\u6559\u80b2\u3001\u4e3a\u4ec0\u4e48\u4e00\u5b9a\u8981\u95f9\u5230\u6559\u52a1\u5904\u5c06\u5f20\u606a\u3001\u6c88\u7b71\u5f00\u9664\u7684\u7a0b\u5ea6\uff1f\u8fd9\u5bf9\u89e3\u51b3\u95ee\u9898\u6709\u4ec0\u4e48\u5e2e\u52a9\uff1f\u8981\u662f\u8ba9\u9b4f\u4e1c\u5f3a\u5c06\u4e8b\u60c5\u95f9\u4e0b\u53bb\uff0c\u9646\u5929\u53c8\u80fd\u60f3\u5230\u4e8b\u60c5\u4f1a\u671d\u4ec0\u4e48\u65b9\u5411\u53d1\u5c55\uff0c\u5f20\u606a\u6709\u505a\u526f\u5e02\u957f\u7684\u8001\u5b50\u7f69\u7740\uff0c\u6216\u8bb8\u4f1a\u53d7\u5230\u4e00\u4e9b\u65e0\u5173\u75db\u75d2\u7684\u6559\u8bad\uff0c\u6700\u540e\u5012\u9709\u7684\u53ea\u6709\u6c88\u7b71\u8fd9\u4e2a\u65e0\u8f9c\u53c8\u6ca1\u6709\u4ec0\u4e48\u80cc\u666f\u7684\u5973\u5b69\u5b50\u3002\u5f88\u96be\u60f3\u8c61\u6c88\u7b71\u771f\u8981\u7ed9\u5f00\u9664\u4e86\uff0c\u4f1a\u5bf9\u5979\u7684\u4eba\u751f\u9020\u6210\u600e\u6837\u7684\u6076\u52a3\u5f71\u54cd\u3002\u6709\u4e9b\u53cd\u611f\u9b4f\u4e1c\u5f3a\u8fc7\u4e8e\u6fc0\u70c8\u7684\u53cd\u5e94\uff0c\u9646\u5929\u53c8\u5fae\u5fae\u76b1\u4e86\u76b1\u7709\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6ca1\u6709\u5728\u610f\u5230\uff0c\u5e94\u8be5\u6ca1\u6709\u4f60\u8bf4\u7684\u90a3\u4e48\u4e25\u91cd\u3002\u201d</p><p>\u3000\u3000\u542c\u9646\u5929\u53c8\u8fd9\u4e48\u8bf4\uff0c\u9b4f\u4e1c\u5f3a\u53c8\u4e0d\u80fd\u8868\u793a\u4ec0\u4e48\u4e0d\u6ee1\uff0c\u6124\u6124\u4e0d\u5e73\u7684\u671d\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u4ee5\u4e3a\u4e8b\u60c5\u80fd\u7cca\u5f04\u8fc7\u5173\u2026\u2026\u4f60\u5c31\u7b49\u7740\u5b66\u6821\u7ed9\u4f60\u4eec\u4fe9\u4e25\u5389\u5904\u5206\u5427\u3002\u201d\u8f6c\u8fc7\u8eab\u53bb\u8ddf\u4e24\u540d\u76d1\u8003\u8001\u5e08\u8bf4\u9053\uff0c\u201c\u8003\u8bd5\u524d\u6211\u4e00\u76f4\u8ddf\u6211\u7684\u5b66\u751f\u5f3a\u8c03\u8003\u8bd5\u7eaa\u5f8b\uff0c\u6ca1\u60f3\u5230\u8fd8\u662f\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\u2026\u2026\u6293\u4f4f\u4e00\u4e2a\u5c31\u8981\u4e25\u8083\u5904\u4e00\u4e2a\uff0c\u7edd\u4e0d\u59d1\u606f\u517b\u5978\uff0c\u8fd9\u6837\u624d\u80fd\u4ee5\u5106\u6548\u5c24\uff0c\u4e25\u8083\u8003\u573a\u7eaa\u5f8b\u2026\u2026\u201d\u5546\u8bae\u7740\u5c06\u4f5c\u5f0a\u7684\u4e8b\u60c5\u901a\u62a5\u6559\u52a1\u5904\u3002</p><p>\u3000\u3000\u4e24\u540d\u76d1\u8003\u8001\u5e08\u62ff\u8fc7\u8bd5\u5377\u770b\u4e86\u770b\u8bd5\u5377\u53f3\u4e0a\u89d2\u7684\u540d\u5b57\uff0c\u5fc3\u91cc\u90fd\u60f3\u539f\u6765\u662f\u8fd9\u5bb6\u4f19\uff0c\u5b57\u5199\u5f97\u8fd8\u771f\u6f02\u4eae\uff0c\u4ed6\u5927\u6982\u662f\u5e73\u65f6\u5c06\u9b4f\u4e1c\u5f3a\u72e0\u72e0\u7684\u5f97\u7f6a\u4e86\u3002\u65e2\u7136\u9b4f\u4e1c\u5f3a\u575a\u6301\u8981\u201c\u5927\u516c\u65e0\u79c1\u201d\u7684\u4ea4\u7ed9\u6559\u52a1\u5904\u5904\u7406\uff0c\u53c8\u662f\u4ed6\u7684\u5b66\u751f\uff0c\u4ed6\u4eec\u53ea\u80fd\u6309\u7167\u6b63\u5e38\u7684\u7a0b\u5e8f\u7b49\u6559\u52a1\u5904\u5de1\u8003\u7684\u8001\u5e08\u8fc7\u6765\u8bf4\u660e\u60c5\u51b5\uff0c\u770b\u6559\u52a1\u5904\u600e\u4e48\u5904\u5206\u8fd9\u4e24\u540d\u5b66\u751f\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8ffd\u4e0a\u675c\u98de\u4e0e\u6c88\u7b71\uff0c\u89c1\u9646\u5929\u53c8\u4e5f\u8ddf\u4e86\u8fc7\u6765\uff0c\u7b11\u7740\u8ddf\u4ed6\u8bf4\uff1a\u201c\u521a\u624d\u591a\u8c22\u4f60\u5e2e\u5fd9\u8bf4\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u6001\u5ea6\u6709\u4e9b\u51b7\u6de1\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u505a\u4ec0\u4e48\u5c31\u505a\u4ec0\u4e48\u597d\u4e86\uff0c\u4e0d\u8981\u5c06\u6c88\u7b71\u7275\u7d2f\u8fdb\u53bb\uff0c\u201d\u5374\u53c8\u53bb\u5b89\u6170\u6c88\u7b71\uff0c\u201c\u9b4f\u4e3b\u4efb\u53ea\u662f\u8868\u9762\u4e0a\u4e25\u5389\u4e9b\u800c\u5df2\uff0c\u53ea\u662f\u60f3\u5236\u4e00\u5236\u5f20\u606a\uff0c\u8ddf\u4f60\u6ca1\u4ec0\u4e48\u5173\u7cfb\u3002\u201d\u6c88\u7b71\u60f6\u6050\u7684\u5fc3\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u5b89\u5b9a\u4e0b\u6765\uff0c\u8138\u8272\u6709\u4e9b\u53d1\u767d\uff0c\u5012\u662f\u5fcd\u4f4f\u6ca1\u6709\u54ed\u51fa\u6765\uff0c\u773c\u795e\u60f6\u7136\u4e0d\u5b89\uff0c\u62c5\u5fe7\u5f97\u5f88\u3002</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u8fd9\u5c0f\u5b50\u539f\u6765\u4ec5\u4ec5\u662f\u5173\u5fc3\u6c88\u7b71\uff0c\u603b\u6bd4\u90a3\u4e9b\u201c\u6b63\u4e49\u611f\u201d\u8fc7\u4e8e\u6cdb\u6ee5\u7684\u5bb6\u4f19\u8ba8\u4eba\u559c\u6b22\u4e9b\u3002</p><p>\u3000\u3000\u675c\u98de\u6ca1\u6709\u50cf\u5f20\u606a\u90a3\u6837\u4e25\u91cd\u7684\u53bb\u8003\u8651\u6c88\u7b71\u7684\u611f\u53d7\uff0c\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u4e0e\u5f20\u606a\u8d70\u5230\u4e00\u8fb9\u8bf4\u8bdd\uff1a\u201c\u770b\u8fd9\u6837\u5b50\uff0c\u9b4f\u4e1c\u5f3a\u662f\u65e9\u76ef\u4e0a\u4f60\u4e86\u554a\uff0c\u771f\u662f\u5947\u602a\u4e86\uff0c\u4f60\u4e5f\u6ca1\u6709\u505a\u4ec0\u4e48\u5bf9\u4e0d\u8d77\u4ed6\u7684\u4e8b\u60c5\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u8fd9\u4e2a\u793e\u4f1a\u4fbf\u662f\u5982\u6b64\uff0c\u5c31\u7b97\u4f60\u4ec0\u4e48\u90fd\u6ca1\u6709\u505a\uff0c\u4e5f\u53ef\u80fd\u6b63\u56e0\u4e3a\u4f60\u4ec0\u4e48\u90fd\u6ca1\u6709\u505a\u7684\u7f18\u6545\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u662f\u60f3\u7740\u600e\u4e48\u5584\u540e\u5427\u3002\u201d\u9646\u5929\u53c8\u8d70\u8fc7\u6765\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u597d\u4e0d\u5bb9\u6613\u7ed9\u9b4f\u4e1c\u5f3a\u5b88\u5f97\u4e91\u5f00\u6708\u51fa\uff0c\u4ed6\u54ea\u91cc\u4f1a\u8fd9\u4e48\u5bb9\u6613\u677e\u53e3\uff1f\u201d\u675c\u98de\u65e0\u6240\u8c13\u7684\u8bf4\u9053\uff0c\u5728\u4ed6\u770b\u6765\u5f20\u606a\u7ed9\u9b4f\u4e1c\u5f3a\u8fd9\u79cd\u5c0f\u89d2\u8272\u54ac\u4e0a\u5c31\u8ddf\u7ed9\u8682\u8681\u54ac\u4e0a\u4e00\u53e3\u4f3c\u7684\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u592a\u653e\u5728\u5fc3\u4e0a\uff0c\u4e0e\u5176\u62c5\u5fc3\u5f20\u606a\uff0c\u8fd8\u4e0d\u5982\u62c5\u5fc3\u9b4f\u4e1c\u5f3a\u4ed6\u81ea\u5df1\u600e\u4e48\u6536\u62fe\u6b8b\u5c40\u3002</p><p>\u3000\u3000\u201c\u6c88\u7b71\u600e\u4e48\u529e\uff1f\u201d\u9646\u5929\u53c8\u5f53\u7136\u6653\u5f97\u5f20\u606a\u4e0d\u6015\u86a4\u5b50\u54ac\uff0c\u5c31\u7b97\u7ed9\u5b66\u6821\u5f00\u9664\uff0c\u660e\u5e74\u8bf4\u4e0d\u5b9a\u6362\u6240\u5b66\u6821\u7ee7\u7eed\u8bfb\u5927\u4e00\u3002</p><p>\u3000\u3000\u201c\u80fd\u600e\u4e48\u529e\uff0c\u8981\u4e0d\u4f60\u53bb\u627e\u5d14\u56fd\u6052\u627f\u8ba4\u4f60\u8ddf\u6c88\u7b71\u5728\u5077\u5077\u7684\u8c08\u604b\u7231\uff1f\u201d\u675c\u98de\u5f00\u73a9\u7b11\u8bf4\u3002</p><p>\u3000\u3000\u9646\u5929\u53c8\u7684\u6027\u5b50\u6709\u4e9b\u592a\u4e00\u672c\u6b63\u7ecf\uff0c\u542c\u4e86\u675c\u98de\u8fd9\u8bdd\uff0c\u8138\u6709\u4e9b\u5fae\u7ea2\uff0c\u5fc3\u865a\u7684\u77a5\u4e86\u6c88\u7b71\u4e00\u773c\uff0c\u53c8\u8f6c\u8fc7\u5934\u677f\u7740\u8138\u8bf4\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u5c31\u4e0d\u80fd\u66ff\u522b\u4eba\u8003\u8651\u8003\u8651\uff0c\u90fd\u4ec0\u4e48\u65f6\u5019\u4e86\u8fd8\u5728\u5f00\u73a9\u7b11\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u62cd\u4e86\u62cd\u9646\u5929\u53c8\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u4e8b\uff0c\u9b4f\u4e1c\u5f3a\u7231\u600e\u4e48\u6298\u817e\u5c31\u4efb\u4ed6\u600e\u4e48\u6298\u817e\u53bb\u3002\u6211\u4eec\u53bb\u521b\u57df\u7f51\u5427\uff0c\u4f60\u53bb\u4e0d\u53bb\uff1f\u201d</p><p>\u3000\u3000\u9646\u5929\u53c8\u6709\u4e9b\u4e0d\u60f3\u8ddf\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u201c\u540c\u6d41\u5408\u6c61\u201d\uff0c\u53c8\u4e0d\u653e\u5fc3\u6c88\u7b71\uff0c\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u8ddf\u7740\u4e00\u8d77\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u63a5\u5230\u6559\u52a1\u5904\u7684\u7535\u8bdd\u901a\u77e5\u65f6\uff0c\u5d14\u56fd\u6052\u6b63\u627e\u9662\u56e2\u59d4\u4e66\u8bb0\u3001\u4e5d\u4e94\u7ea7\u5e74\u7ea7\u4e3b\u4efb\u79e6\u521a\u5728\u81ea\u5df1\u7684\u9662\u957f\u529e\u516c\u5ba4\u91cc\u8c08\u4e8b\u60c5\uff0c\u542c\u5b8c\u7535\u8bdd\uff0c\u8138\u8272\u5c31\u6709\u4e9b\u5fae\u5fae\u53d1\u9752\u3002</p><p>\u3000\u3000\u79e6\u521a\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u8ba9\u5d14\u56fd\u6052\u542c\u8fc7\u7535\u8bdd\u8fde\u8138\u8272\u90fd\u53d8\u4e86\uff0c\u53c8\u4e0d\u4fbf\u95ee\uff0c\u8bf4\u9053\uff1a\u201c\u5d14\u9662\u957f\u73b0\u5728\u6709\u4e8b\uff0c\u90a3\u6211\u53e6\u627e\u4e2a\u65f6\u95f4\u8ddf\u4f60\u6c47\u62a5\u521b\u534f\u7684\u5de5\u4f5c\u2026\u2026\u201d\u79e6\u521a\u4e0e\u6821\u56e2\u59d4\u4e66\u8bb0\u5bab\u5982\u6625\u90fd\u662f\u5927\u5b66\u751f\u521b\u4e1a\u534f\u4f1a\u7684\u6307\u5bfc\u8001\u5e08\uff0c\u8fd9\u6b21\u7ed9\u5d14\u56fd\u6052\u627e\u6765\u5c31\u662f\u8c08\u521b\u534f\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e8b\u8ddf\u4f60\u6709\u4e9b\u5173\u7cfb\uff0c\u201d\u5d14\u56fd\u6052\u558a\u4f4f\u79e6\u521a\uff0c\u4e09\u8a00\u4e24\u8bed\u7684\u5c06\u60c5\u51b5\u8ddf\u4ed6\u505a\u4e86\u8bf4\u660e\uff0c\u201c\u6211\u521a\u63a5\u5230\u6559\u52a1\u5904\u7684\u7535\u8bdd\uff0c\u5f20\u606a\u5728\u8003\u573a\u4e0a\u6284\u88ad\u524d\u6392\u5973\u751f\u7684\u8003\u5377\u7ed9\u9b4f\u4e1c\u5f3a\u5f53\u573a\u6293\u4f4f\uff0c\u4e8b\u60c5\u5df2\u7ecf\u901a\u62a5\u6559\u52a1\u5904\u4e86\uff0c\u6559\u52a1\u5904\u6253\u7535\u8bdd\u5546\u8bae\u600e\u4e48\u5904\u7f6e\u8fd9\u4ef6\u4e8b\u2026\u2026\u201d\u5230\u6700\u540e\u5fcd\u4e0d\u4f4f\u53d1\u4e86\u4e00\u53e5\u7262\u9a9a\uff0c\u201c\u4ed6\u771f\u662f\u5403\u9971\u6491\u7740\u4e86\u3002\u201d</p></article>"}}}]);
