(window.webpackJsonp=window.webpackJsonp||[]).push([[937],{960:function(p){p.exports={chapterTitle:null,title:"\u7b2c934\u7ae0 \u5168\u7403\u97f3\u4e50\u5728\u7ebf",content:"<article><h2>\u7b2c934\u7ae0 \u5168\u7403\u97f3\u4e50\u5728\u7ebf</h2><p>\u3000\u3000\u4ee5\u53d1\u5c55\u5168\u7403\u6700\u5927\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u4e3a\u76ee\u6807\u7684\u5168\u7403\u97f3\u4e50\u5728\u7ebf\uff08music online\uff09\u662f\u4ee5iplayer\u786c\u4ef6\u9500\u552e\u4e3a\u652f\u6301\u7684\uff0c\u800c\u4e1c\u829d\u6b64\u65f6\u5360\u4e86\u5168\u7403\u5e02\u573aiplayer\u6708\u51fa\u8d27\u91cf\u768440\uff05\u8fd8\u591a\uff1b\u53e6\u4e00\u65b9\u9762\uff0c\u7f51\u7ad9\u6b64\u65f6\u7684\u6536\u5165\u8fd8\u8fdc\u8fdc\u65e0\u6cd5\u652f\u4ed8\u6301\u7eed\u7684\u6269\u5f20\u4e0e\u65e5\u5e38\u8fd0\u8425\u7684\u5de8\u989d\u6210\u672c\uff0c\u8096\u664b\u6210\u624b\u5934\u5c31\u6709\u4e00\u4efd\u5173\u4e8e\u7f51\u7ad9\u5728\u672a\u6765\u4e24\u5e74\u91cc\u5168\u7403\u4e3b\u8981\u4e92\u8054\u7f51\u56fd\u5bb6\u4e0e\u5730\u65b9\u7684\u6269\u5f20\u4e0e\u8fd0\u8425\u62a5\u544a\u4e66\uff0c\u672a\u6765\u4e24\u5e74\u81f3\u5c11\u8fd8\u9700\u8981\u516d\u5343\u4e07\u7f8e\u5143\u7684\u6295\u5165\u2014\u2014\u8fd9\u4e9b\u5c31\u51b3\u5b9a\u4e86\u9526\u6e56\u8981\u5411\u4e1c\u829d\u8f6c\u8ba9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7f51\u7ad9\u90e8\u5206\u7684\u80a1\u6743\u83b7\u5f97\u8d44\u91d1\u7528\u4e8e\u7f51\u7ad9\u7684\u6301\u7eed\u53d1\u5c55\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u4e1c\u829d\u6ce8\u8d44\u591a\u5c11\u3001\u83b7\u5f97\u591a\u5c11\u6bd4\u4f8b\u7684\u80a1\u6743\u4ee5\u53ca\u7f51\u7ad9\u7684\u7ba1\u7406\u6743\u9650\u5982\u4f55\u9650\u5236\uff0c\u8fd9\u4e9b\u90fd\u662f\u9700\u8981\u9759\u5fc3\u5750\u4e0b\u6765\u6162\u6162\u8c08\u5224\u7684\uff0c\u65f6\u8f6e\u63a8\u8fdb\u5230\u4e5d\u516b\u5e74\u5e95\uff0c\u4e92\u8054\u7f51\u6295\u8d44\u7684\u70ed\u6f6e\u6e10\u6e10\u6c79\u6d8c\uff0c\u5728\u7ec6\u5206\u5e02\u573a\u5360\u636e\u7edd\u5bf9\u5784\u65ad\u5730\u4f4d\u7684\u7f51\u7ad9\uff0c\u5e02\u573a\u4ef7\u503c\u7edd\u5bf9\u4e0d\u4f1a\u88ab\u4f4e\u4f30\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u662f\u4e1c\u829d\u8981\u6c42\u5728\u672a\u6765iplayer\u9886\u57df\u91cc\u7684\u5229\u76ca\u5206\u914d\u505a\u51fa\u7684\u90e8\u5206\u8c03\u6574\u2014\u2014\u603b\u4e4b\uff0c\u5c97\u6751\u6728\u4e0e\u52a9\u624b\u68ee\u5c71\u91ce\u8fc7\u6765\u62dc\u4f1a\u6ca1\u6709\u4e1d\u6beb\u4e0d\u6109\u5feb\u7684\u4f53\u9a8c\uff0c\u4ed6\u4eec\u6240\u63d0\u51fa\u7684\u8bf7\u6c42\uff0c\u90fd\u5f97\u5230\u5f20\u606a\u7684\u79ef\u6781\u56de\u5e94\u3002</p><p>\u3000\u3000\u804a\u4e86\u8bb8\u4e45\uff0c\u5f20\u606a\u4ece\u6c99\u53d1\u4e0a\u7ad9\u8d77\u6765\uff0c\u900f\u8fc7\u843d\u5730\u73bb\u7483\u5e55\u5899\uff0c\u770b\u4e86\u770b\u5915\u9633\u5149\u8f89\u6d12\u843d\u5728\u7ef4\u591a\u5229\u4e9a\u6e2f\u53e3\u7684\u6d77\u6c34\u91cc\uff0c\u5c31\u50cf\u6709\u4e00\u4ebf\u679a\u91d1\u5e01\u85cf\u5728\u6e5b\u84dd\u7684\u6d77\u6c34\u4e4b\u4e0b\u3002</p><p>\u3000\u3000\u4eca\u5e74\u624d\u56db\u5341\u5c81\u51fa\u5934\u7684\u5c97\u6751\u6728\u5728\u4e1c\u829d\u5185\u90e8\u88ab\u89c6\u4e3a\u5c11\u58ee\u6d3e\u7684\u4ee3\u8868\uff0c\u4f46\u662f\u6bd4\u8d77\u773c\u524d\u7684\u9752\u5e74\uff0c\u771f\u662f\u8981\u7b97\u534a\u4e2a\u7cdf\u8001\u5934\u5b50\uff0c\u4e5f\u4e0d\u6e05\u695a\u4ed6\u662f\u5982\u4f55\u6709\u6548\u7684\u7ea6\u675f\u65d7\u4e0b\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u7b49\u4eba\u7ec4\u6210\u7684\u7cbe\u82f1\u7ba1\u7406\u56e2\u961f\uff0c\u4ed6\u89c1\u5f20\u606a\u7ad9\u4e86\u8d77\u6765\uff0c\u8ba4\u4e3a\u4e5f\u662f\u544a\u8f9e\u7684\u65f6\u95f4\uff0c\u4e0e\u52a9\u624b\u68ee\u5c71\u91ce\u7ad9\u4e86\u8d77\u6765\uff0c\u521a\u8981\u5f00\u53e3\u544a\u8f9e\uff0c\u5f20\u606a\u8f6c\u8fc7\u8eab\u6765\u95ee\u4ed6\uff1a\u201c\u6211\u542c\u8bf4\u4e09\u4e95\u7269\u4ea7\u5728\u4e2d\u56fd\u7684\u603b\u4ee3\u8868\u6c60\u4f50\u79c0\u85cf\u8981\u7ec4\u7ec7\u65d7\u4e0b\u7684\u6210\u5458\u4f01\u4e1a\u4e0e\u534e\u590f\u7535\u5b50\u5f00\u5c55\u5927\u89c4\u6a21\u7684\u6218\u7565\u5408\u4f5c\uff0c\u4e0d\u6653\u5f97\u4e1c\u829d\u5728\u4e0d\u5728\u6c60\u4f50\u79c0\u85cf\u7684\u8ba1\u5212\u4e4b\u5185\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u7ed9\u76f4\u622a\u4e86\u5f53\u7684\u95ee\u5230\u8fd9\u4e2a\u95ee\u9898\uff0c\u5c97\u6751\u6728\u611f\u5230\u6709\u4e9b\u7a81\u7136\uff0c\u4ed6\u7a0d\u7a0d\u8fdf\u7591\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u4e1c\u829d\u4e0d\u80fd\u7b97\u662f\u4e09\u4e95\u7684\u6210\u5458\u4f01\u4e1a\uff0c\u4e0d\u8fc7\u6c60\u4f50\u79c0\u85cf\u7684\u8ba1\u5212\u82e5\u6709\u5229\u4e8e\u4e1c\u829d\u7684\u53d1\u5c55\uff0c\u4e1c\u829d\u4e5f\u4e0d\u4f1a\u62d2\u7edd\u2014\u2014\u4e0d\u8fc7\u6709\u5229\u76ca\u7684\u4e8b\u60c5\uff0c\u53ea\u6015\u8981\u7ed9NEC\u3001\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u590f\u666e\u8fd9\u4e9b\u516c\u53f8\u5148\u4e89\u8fc7\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5c97\u6751\u6728\u7684\u8a00\u4e0b\u4e4b\u610f\u5f88\u662f\u6e05\u6670\uff0c\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff1a\u5c97\u6751\u6728\u7684\u8bdd\u4e5f\u4e0d\u80fd\u5b8c\u5168\u5f53\u771f\uff0c\u867d\u8bf4\u4e1c\u829d\u65e9\u5c31\u7ed9\u6392\u65a5\u5728\u4e09\u4e95\u6838\u5fc3\u6210\u5458\u4f01\u4e1a\u7684\u7cfb\u5217\u4e4b\u5916\uff0c\u4f46\u662f\u65e5\u7cfb\u4f01\u4e1a\u4e4b\u95f4\u7684\u5bc6\u5207\u8054\u7cfb\u662f\u5916\u4eba\u5f88\u96be\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u8fd9\u4e00\u70b9\u5c31\u5b66\u4e09\u4e95\u5f88\u5230\u4f4d\uff0c\u674e\u5065\u7199\u5bb6\u65cf\u8868\u9762\u4e0a\u5bf9\u4e09\u661f\u96c6\u56e2\u6301\u80a1\u751a\u81f3\u4e0d\u8db34\uff05\uff0c\u4f46\u662f\u674e\u5065\u7199\u53d7\u884c\u8d3f\u6848\u7275\u7d2f\u3001\u5728\u8f9e\u53bb\u4ed6\u5728\u4e09\u661f\u96c6\u56e2\u7684\u5404\u9879\u4e3b\u8981\u804c\u52a1\u4e4b\u540e\uff0c\u4ecd\u7136\u7262\u7262\u7684\u63a7\u5236\u7740\u6574\u4e2a\u4e09\u661f\u96c6\u56e2\u7684\u8fd0\u8425\u2014\u2014\u5f53\u4ee3\u8d22\u52a1\u4f53\u7cfb\u7684\u53d1\u5c55\u4e0e\u4ea4\u53c9\u6301\u80a1\u8ba1\u5212\u7684\u5b9e\u65bd\uff0c\u80fd\u591f\u5f7b\u5e95\u7684\u63a9\u9970\u4f01\u4e1a\u771f\u5b9e\u7684\u63a7\u80a1\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u65e9\u5df2\u7ecf\u5728\u9526\u6e56\u4f53\u7cfb\u5185\u90e8\u9010\u6b65\u5b9e\u65bd\u4ea4\u53c9\u6301\u80a1\u7684\u8ba1\u5212\uff0c\u4ee5\u514d\u5c06\u6765\u76f4\u63a5\u66b4\u9732\u5728\u516c\u4f17\u9762\u524d\u7684\u8d22\u5bcc\u8fc7\u4e8e\u9a87\u4eba\u542c\u95fb\u4e86\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u6d77\u5916\u4e1a\u52a1\u7684\u7f6e\u5165\uff0c\u7231\u8fbe\u96c6\u56e2\u5c06\u6210\u4e3a\u9526\u6e56\u5546\u4e8b\u540d\u4e49\u4e0a\u7684\u7b2c\u4e00\u5927\u80a1\u4e1c\uff0c\u76f4\u63a5\u6301\u80a1\u8d85\u8fc740\uff05\uff0c\u5f20\u606a\u53c8\u4e0e\u8c22\u665a\u6674\u3001\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u6216\u4ee5\u79c1\u4eba\u516c\u53f8\u6216\u4ee5\u63a7\u80a1\u516c\u53f8\u6216\u4ee5\u5b50\u516c\u53f8\u7684\u540d\u4e49\u6301\u6709\u9526\u6e56\u5546\u4e8b\u4f59\u4e0b\u5c06\u8fd160\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u4e5d\u6708\u4e2d\u65ec\uff0c\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u53c8\u5171\u540c\u5411\u90ed\u5bb6\u3001\u9a6c\u5bb6\u3001\u9648\u5bb6\u3001\u4f55\u5bb6\u7b49\u4e1c\u5357\u4e9a\u534e\u5546\u5bb6\u65cf\u8f6c\u8ba9\u4e86\u7231\u8fbe\u96c6\u56e2\u3001\u8d8a\u79c0\u63a7\u80a1\u7684\u90e8\u5206\u80a1\u6743\uff0c\u7231\u8fbe\u4e0e\u8d8a\u79c0\u63a7\u5236\u7740\u9526\u6e56\u65d7\u4e0b\u6700\u4f18\u8d28\u7684\u4e1a\u52a1\u4e0e\u8d44\u4ea7\uff0c\u8ba9\u90ed\u3001\u9a6c\u3001\u9648\u3001\u4f55\u8bf8\u5bb6\u5206\u4eab\u9526\u6e56\u7684\u6838\u5fc3\u5229\u76ca\uff0c\u4e5f\u662f\u60f3\u7740\u5c06\u4ed6\u4eec\u8fdb\u4e00\u6b65\u62c9\u62e2\u5230\u9526\u6e56\u7684\u65d7\u4e0b\u3002</p><p>\u3000\u3000\u9664\u6b64\u4e4b\u5916\uff0c\u5f20\u606a\u8fd8\u4e00\u76f4\u79ef\u6781\u7684\u63d0\u9ad8\u7ba1\u7406\u5c42\u4e0e\u6838\u5fc3\u5458\u5de5\u5bf9\u65d7\u4e0b\u6210\u5458\u4f01\u4e1a\u7684\u6301\u80a1\u6bd4\u4f8b\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u3001\u9648\u6c49\u748b\u3001\u8096\u664b\u6210\u7b49\u9ad8\u5c42\u4ee5\u53ca\u6838\u5fc3\u5458\u5e72\u5bf9\u7231\u8fbe\u96c6\u56e2\u7684\u6301\u80a1\u6bd4\u4f8b\u5df2\u7ecf\u63d0\u9ad8\u523018\uff05\u3002</p><p>\u3000\u3000\u5728\u8fc7\u53bb\u51e0\u4e2a\u6708\u5185\uff0c\u5f20\u606a\u521d\u6b65\u5b8c\u6210\u9526\u6e56\u5546\u4e8b\u4e0e\u8d8a\u79c0\u63a7\u80a1\u3001\u7231\u8fbe\u96c6\u56e2\u3001\u65b0\u5149\u7eb8\u4e1a\u7b49\u9526\u6e56\u6210\u5458\u4f01\u4e1a\u4e4b\u95f4\u7684\u4ea4\u53c9\u6301\u80a1\uff0c\u9526\u6e56\u4f53\u7cfb\u5728\u8d22\u52a1\u7ed3\u6784\u4e0a\u521d\u6b65\u5f62\u6210\u4ee5\u7231\u8fbe\u96c6\u56e2\u3001\u9526\u6e56\u5546\u4e8b\u4e3a\u6838\u5fc3\u7684\u590d\u6742\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u867d\u7136\u6216\u95f4\u63a5\u6216\u76f4\u63a5\u62e5\u6709\u7684\u603b\u6743\u76ca\u6ca1\u6709\u4ec0\u4e48\u53d8\u5316\uff0c\u4f46\u662f\u5728\u65b0\u7684\u4f53\u7cfb\u5185\uff0c\u5f20\u606a\u4ee5\u79c1\u4eba\u540d\u4e49\u5bf9\u9526\u6e56\u65d7\u4e0b\u6210\u5458\u4f01\u4e1a\u7684\u76f4\u63a5\u6301\u80a1\u964d\u4f4e\u5230\u4e00\u4e2a\u76f8\u5f53\u4f4e\u7684\u6bd4\u4f8b\uff0c\u81f3\u5c11\u7b49\u5230\u660e\u5e74\u82f1\u56fd\u4eba\u80e1\u6da6\u5230\u4e2d\u56fd\u6765\u7ed9\u4e2d\u56fd\u5bcc\u8c6a\u6392\u540d\u5217\u5e8f\u7b79\u63aa\u80e1\u6da6\u5bcc\u699c\u4e4b\u65f6\uff0c\u5373\u4f7f\u6709\u5173\u65b9\u9762\u7684\u6750\u6599\u4fdd\u5bc6\u63aa\u65bd\u4e0d\u591f\u5230\u4f4d\uff0c\u5f20\u606a\u80fd\u591f\u76f4\u63a5\u8ba1\u7b97\u7684\u79c1\u4eba\u8d22\u5bcc\u4e5f\u4e0d\u4f1a\u8ba9\u4ed6\u5360\u636e\u592a\u663e\u773c\u7684\u4f4d\u5b50\u2014\u2014\u5f53\u7136\u4e86\uff0c\u82e5\u662f\u4e00\u540d\u4e8c\u5341\u5c81\u521a\u51fa\u5934\u7684\u9752\u5e74\u5728\u4e5d\u516b\u5e74\u79c1\u4eba\u62e5\u6709\u54ea\u6015\u51e0\u4e2a\u4ebf\u7684\u8d22\u5bcc\u5728\u4e2d\u56fd\u4e5f\u662f\u8981\u60ca\u7206\u7edd\u5927\u591a\u6570\u4eba\u773c\u7403\u7684\u3002</p><p>\u3000\u3000\u5c3d\u53ef\u80fd\u907f\u5f00\u516c\u4f17\u4e0e\u5a92\u4f53\u89c6\u7ebf\u662f\u80af\u5b9a\u8981\u7684\uff0c\u5b9e\u65bd\u5f02\u5e38\u590d\u6742\u7684\u4ea4\u53c9\u6301\u80a1\u8ba1\u5212\uff0c\u5c06\u56fd\u5185\u516c\u53f8\u4e0e\u6d77\u5916\u516c\u53f8\u7684\u6743\u76ca\u4ea4\u53c9\u7ea0\u7f20\u8d77\u6765\uff0c\u5b9e\u8d28\u4e0a\u8fd8\u662f\u8981\u672a\u96e8\u7ef8\u7f2a\uff0c\u907f\u514d\u6709\u4eba\u6216\u673a\u6784\u5bf9\u9526\u6e56\u751f\u51fa\u89ca\u89ce\u4e4b\u5fc3\uff0c\u4e5f\u662f\u907f\u514d\u5f20\u606a\u79c1\u4eba\u540d\u4e0b\u76f4\u63a5\u62e5\u6709\u7684\u8d22\u5bcc\u5728\u9ad8\u5c42\u7684\u773c\u91cc\u8fc7\u4e8e\u7684\u523a\u773c\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u672b\u671f\uff0c\u662f\u56fd\u5185\u6c11\u8425\u7ecf\u6d4e\u4f53\u5927\u53d1\u5c55\u7684\u5173\u952e\u65f6\u671f\uff0c\u5341\u4ebf\u3001\u6570\u5341\u4ebf\u751a\u81f3\u4e0a\u767e\u4ebf\u8eab\u5bb6\u7684\u6c11\u8425\u4f01\u4e1a\u5bb6\u90fd\u4e0d\u518d\u662f\u5929\u65b9\u591c\u8c08\uff0c\u4f46\u662f\u5728\u8fd9\u4e2a\u79c1\u6709\u8d22\u4ea7\u6743\u8fd8\u6ca1\u6709\u5f97\u5230\u5f7b\u5e95\u786e\u8ba4\u7684\u56fd\u5bb6\uff0c\u79c1\u4eba\u8d22\u5bcc\u7d2f\u79ef\u5230\u5e95\u6709\u6ca1\u6709\u4e0a\u9650\uff1f\u5f20\u606a\u90fd\u4e0d\u597d\u8bf4\u3002\u62ac\u5934\u770b\u5929\uff0c\u5934\u9876\u4e4b\u4e0a\u4f3c\u4e4e\u662f\u6ca1\u6709\u4e1d\u6beb\u7684\u963b\u788d\uff0c\u4f46\u6709\u4e00\u79cd\u5929\u82b1\u677f\u8ddf\u900f\u660e\u73bb\u7483\u4f3c\u7684\uff0c\u5728\u4f60\u6ca1\u6709\u8840\u8089\u6dcb\u6f13\u7684\u649e\u4e0a\u53bb\u4e4b\u524d\uff0c\u4f60\u662f\u611f\u89c9\u4e0d\u5230\u5b83\u7684\u5b58\u5728\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u505a\u4e5f\u6709\u4e0d\u5229\u7684\u5730\u65b9\uff0c\u6bd4\u5982\u8bf4\u9526\u6e56\u4f53\u7cfb\u5185\u7684\u4e24\u5927\u6838\u5fc3\u7231\u8fbe\u96c6\u56e2\u4e0e\u9526\u6e56\u5546\u4e8b\u52bf\u5fc5\u4f1a\u56e0\u4e3a\u8fc7\u4e8e\u590d\u6742\u7684\u8d22\u52a1\u5173\u7cfb\u800c\u65e0\u6cd5\u5230\u516c\u5f00\u7684\u8bc1\u5238\u5e02\u573a\u4e0a\u5e02\u878d\u8d44\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9001\u5c97\u6751\u6728\u4e0e\u4ed6\u7684\u52a9\u624b\u68ee\u5c71\u91ce\u79bb\u5f00\uff0c\u5f20\u606a\u7b49\u4eba\u8d70\u56de\u5230\u5ba2\u5385\u3002</p><p>\u3000\u3000\u9648\u4fe1\u751f\u95ee\u9053\uff1a\u201c\u4ee5\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7f51\u7ad930\uff05\u7684\u80a1\u6743\u6362\u53d6\u516d\u5343\u4e07\u7f8e\u5143\u7684\u6ce8\u8d44\uff0c\u4e1c\u829d\u4f1a\u4e0d\u4f1a\u7b54\u5e94\u8fd9\u4e2a\u6761\u4ef6\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ecf\u7814\u6240\u5bf9\u5168\u7403\u4e92\u8054\u7f51\u7ecf\u6d4e\u5728\u672a\u6765\u4e24\u5e74\u7684\u53d1\u5c55\u8d8b\u52bf\u76f8\u5f53\u4e50\u89c2\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u8d77\u5148\u4ed6\u4eec\u662f\u4e0d\u4f1a\u7b54\u5e94\u7684\uff0c\u4f46\u662f\u62d6\u8d8a\u4e45\uff0c\u5bf9\u6211\u4eec\u8d8a\u6709\u5229\uff0c\u5bf9\u4ed6\u4eec\u7684\u538b\u529b\u4e5f\u8d8a\u5927\u2026\u2026\u5927\u4e0d\u4e86\u518d\u62d6\u4e0a\u4e00\u62d6\uff0c\u518d\u8bf4\u4e1c\u829d\u4e0d\u80af\u5c31\u72af\uff0c\u8fd8\u6709\u65af\u9ad8\u67cf\u5462\u3002\u201d</p><p>\u3000\u3000\u6ce8\u8d44\u516d\u5343\u4e07\u7f8e\u5143\u6362\u53d630\uff05\u7684\u6743\u76ca\uff0c\u610f\u5473\u7740\u9526\u6e56\u624b\u91cc\u5269\u4f59\u768470\uff05\u7684\u80a1\u6743\u4ef7\u503c\u4e00\u4ebf\u56db\u5343\u4e07\u7f8e\u5143\uff0c\u800c\u9526\u6e56\u5728\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u5305\u62ec\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7f51\u7ad9\u7684\u6536\u8d2d\u4e0e\u518d\u6ce8\u8d44\u603b\u6295\u5165\u90fd\u8fd8\u6ca1\u6709\u8d85\u8fc7\u4e09\u5343\u4e07\u7f8e\u5143\uff0c\u5c97\u6751\u6728\u4ee3\u8868\u4e1c\u829d\u968f\u968f\u4fbf\u4fbf\u7684\u5c31\u7b54\u5e94\u8fd9\u6837\u7684\u6761\u4ef6\uff0c\u90a3\u624d\u53eb\u9047\u5230\u9b3c\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d76\u5230\u9999\u6e2f\uff0c\u4eba\u4e0d\u5f97\u7a0d\u505c\uff0c\u521a\u5c06\u5c97\u6751\u6728\u4e0e\u68ee\u5c71\u91ce\u9001\u8d70\uff0c\u65af\u9ad8\u67cf\u6267\u884c\u603b\u88c1\u827e\u9ed8\u7684\u7535\u8bdd\u5c31\u6253\u4e86\u8fdb\u6765\uff0c\u5f20\u606a\u7b11\u7740\u8ddf\u9648\u4fe1\u751f\u8bf4\u9053\uff1a\u201c\u4f60\u770b\uff0c\u65af\u9ad8\u67cf\u7684\u52a8\u4f5c\u4e5f\u4e0d\u4f1a\u6162\u591a\u5c11\u3002\u201d\u8fd9\u65f6\u5019\u5085\u4fca\u8dd1\u8fc7\u6765\u544a\u8bc9\u4ed6\uff0c\u6d3e\u53bb\u63a5\u5510\u5a67\u7684\u8f66\u5df2\u7ecf\u5230\u9152\u5e97\u697c\u4e0b\u4e86\u3002</p><p>\u3000\u3000\u629b\u5f00\u516c\u4e8b\u4e0a\u7684\u4e00\u672c\u6b63\u7ecf\u4e0e\u4e25\u8083\uff0c\u8c08\u5410\u8bd9\u8c10\u800c\u6027\u683c\u6e29\u548c\u7684\u827e\u9ed8\u662f\u503c\u5f97\u4ea4\u5f80\u7684\u670b\u53cb\uff0c\u5f20\u606a\u4e5f\u5c31\u6ca1\u6709\u8ba9\u9648\u4fe1\u751f\u6216\u82cf\u6d25\u4e1c\u968f\u4fbf\u54ea\u4e2a\u4eba\u53bb\u5e94\u4ed8\u827e\u9ed8\uff0c\u800c\u662f\u9080\u8bf7\u827e\u9ed8\u4e00\u8d77\u5171\u540c\u665a\u9910\u3002</p><p>\u3000\u3000\u89c2\u5149\u9910\u5385\u4f4d\u4e8e\u4e07\u4e3d\u6d77\u666f\u9152\u5e97\u7684\u9876\u5c42\uff0c\u660e\u5929\u5c31\u662f\u9999\u6e2f\u7535\u5b50\u5c55\u6b63\u5f0f\u5f00\u5c55\u7684\u65e5\u5b50\uff0c\u5230\u8fd9\u4e2a\u89c2\u5149\u9910\u5385\u6765\u7528\u9910\u7684\u4eba\u8863\u51a0\u9c9c\u695a\uff0c\u597d\u4e9b\u4eba\u90fd\u662f\u5728\u4e1a\u5185\u4e22\u4e0b\u697c\u5c31\u80fd\u5f15\u8d77\u8f69\u7136\u5927\u6ce2\u7684\u5927\u4eba\u7269\uff0c\u5f20\u606a\u4ed6\u4eec\u9009\u4e86\u4e00\u4e2a\u4e34\u7a97\u7684\u4f4d\u5b50\uff0c\u827e\u9ed8\u8fd8\u9887\u4e3a\u70ed\u5207\u7684\u8ddf\u5f20\u606a\u4ed6\u6240\u8ba4\u51fa\u7684\u5927\u4eba\u7269\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u4e5f\u8ba4\u51fa\u4e00\u4e9b\u719f\u4eba\u6765\uff0c\u97e9\u56fd\u4e09\u661f\u4e2d\u56fd\u6295\u8d44\u516c\u53f8\u4e13\u52a1\u674e\u5728\u6d19\u4e0e\u7d22\u5c3c\u7684\u6e21\u8fb9\u656c\u4e00\u5750\u5728\u4e00\u8d77\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u771f\u662f\u4e9b\u5927\u4eba\u7269\u5462\uff0c\u201d\u5510\u5a67\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u9976\u6709\u5174\u8da3\u7684\u89c2\u5bdf\u8d77\u8fd9\u4e9b\u5927\u4eba\u7269\u6765\uff0c\u201c\u770b\u4ed6\u4eec\u8ddf\u4f60\u4e5f\u6ca1\u6709\u591a\u5927\u7684\u533a\u522b\uff0c\u6709\u4e9b\u4eba\u957f\u5f97\u597d\u4e11\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u624b\u6309\u7740\u5979\u5149\u6d01\u660e\u4eae\u7684\u989d\u5934\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u666e\u901a\u4eba\u4e5f\u90fd\u662f\u5982\u6b64\uff0c\u4e00\u53ea\u9f3b\u5b50\u4e24\u53ea\u773c\uff0c\u8981\u8bf4\u8ddf\u666e\u901a\u4eba\u6709\u6240\u533a\u522b\uff0c\u4e5f\u53ea\u6709\u5185\u5fc3\u7684\u9f8c\u9f8a\u7a0b\u5ea6\u4e0d\u662f\u666e\u901a\u4eba\u80fd\u6bd4\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u81ea\u5df1\u5462\uff1f\u201d\u5510\u5a67\u4fcf\u76ae\u7684\u8bd8\u95ee\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u4e5f\u5dee\u4e0d\u591a\uff0c\u4ed6\u4e5f\u662f\u4e00\u6837\uff0c\u201d\u5f20\u606a\u6307\u4e86\u6307\u5750\u5728\u5bf9\u9762\u7684\u827e\u9ed8\uff0c\u201c\u6211\u597d\u5fc3\u8bf7\u4ed6\u8fc7\u6765\u4e00\u8d77\u7528\u9910\uff0c\u4ed6\u773c\u795e\u95ea\u70c1\u7684\u5389\u5bb3\uff0c\u6307\u4e0d\u5b9a\u8111\u5b50\u91cc\u5728\u60f3\u7740\u600e\u6837\u624d\u80fd\u8ba9\u9526\u6e56\u7ed9\u65af\u9ad8\u67cf\u8ba9\u51fa\u66f4\u591a\u7684\u5229\u76ca\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54b3\u2026\u2026\u201d\u827e\u9ed8\u6b63\u62ff\u8c03\u7fb9\u559d\u6cd5\u5f0f\u6d53\u6c64\uff0c\u542c\u5230\u5f20\u606a\u8fd9\u53e5\u8bdd\uff0c\u5dee\u70b9\u5c06\u6c64\u55b7\u51fa\u6765\uff0c\u62ff\u9910\u5dfe\u64e6\u53bb\u5634\u89d2\u7684\u6b8b\u6db2\uff0c\u54ed\u7b11\u4e0d\u5f97\u7684\u62ff\u4ed6\u8154\u8c03\u602a\u5f02\u7684\u666e\u901a\u8bdd\u8ddf\u5f20\u606a\u62b1\u6028\uff0c\u201c\u6211\u53ea\u662f\u5728\u8003\u8651\u6211\u4eec\u5982\u4f55\u80fd\u5728\u5408\u4f5c\u7684\u9053\u8def\u4e0a\u8d70\u5f97\u66f4\u957f\u8fdc\u7f62\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u597d\u5427\uff0c\u5c31\u542c\u4f60\u8bf4\u8bf4\u4e24\u5bb6\u516c\u53f8\u5982\u4f55\u624d\u80fd\u5728\u5408\u4f5c\u7684\u9053\u8def\u8d70\u5f97\u66f4\u957f\u8fdc\u2026\u2026\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u6293\u4f4f\u5510\u5a67\u67d4\u82e5\u5ae9\u8351\u7684\u624b\u653e\u5728\u624b\u5fc3\u91cc\u6162\u6162\u7684\u6469\u5a11\uff0c\u8eab\u5b50\u8212\u670d\u7684\u659c\u9760\u5728\u6905\u80cc\u4e0a\u606d\u542c\u827e\u9ed8\u5206\u8fa9\u3002</p><p>\u3000\u3000\u201ciplayer\u672a\u6765\u51e0\u5e74\u7684\u5e02\u573a\u5728\u6b27\u5dde\u3001\u5728\u5317\u7f8e\uff0c\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e5f\u5e94\u8be5\u4e3b\u8981\u53d1\u5c55\u8fd9\u4e9b\u5730\u533a\u7684\u4e1a\u52a1\uff0c\u4f60\u4e0d\u89c9\u5f97\u8ba9\u65af\u9ad8\u67cf\u53c2\u4e0e\u5168\u7403\u97f3\u4e50\u5728\u7ebf\uff0c\u4e5f\u6709\u5229\u7f51\u7ad9\u5728\u4e0a\u8ff0\u5730\u533a\u7684\u4e1a\u52a1\u53d1\u5c55\uff1f\u201d\u827e\u9ed8\u8bf4\u9053\uff0c\u5012\u662f\u4e0d\u63a9\u9970\u65af\u9ad8\u67cf\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u5229\u76ca\u7684\u89ca\u89ce\u3002</p></article>"}}}]);
