(window.webpackJsonp=window.webpackJsonp||[]).push([[938],{961:function(p){p.exports={chapterTitle:null,title:"\u7b2c755\u7ae0 \u591a\u4e86\u4e2a\u59d0\u59d0",content:"<article><h2>\u7b2c755\u7ae0 \u591a\u4e86\u4e2a\u59d0\u59d0</h2><p>\u3000\u3000\u6cbf\u6c5f\u5cb8\u897f\u884c\u6709\u90e8\u5206\u662f\u8def\u51b5\u4e0d\u592a\u597d\u7684\u4e61\u9547\u516c\u8def\uff0c\u8d76\u5230\u65b0\u829c\u5929\u5df2\u7ecf\u9ed1\u4e86\u3002</p><p>\u3000\u3000\u591c\u7a7a\u6708\u6717\u661f\u7a00\uff0c\u4e00\u8f6e\u660e\u6708\u9ad8\u60ac\uff0c\u4f3c\u94f6\u76d8\u6d12\u4e0b\u6e05\u51b7\u7684\u5149\u8f89\u3002\u7ef5\u5ef6\u8d77\u4f0f\u7684\u5c71\u810a\u6a2a\u4e98\u5728\u57ce\u5e02\u7684\u5317\u9762\uff0c\u4eff\u4f5b\u4e00\u9053\u9690\u7ea6\u7684\u754c\u7ebf\u82e5\u9690\u82e5\u73b0\u7684\u6d6e\u5728\u591c\u7a7a\u91cc\uff0c\u5c06\u5317\u65b9\u7684\u591c\u7a7a\u5206\u5272\u6210\u9752\u9ed1\u4e0e\u94c5\u84dd\u7684\u4e24\u8272\u3002</p><p>\u3000\u3000\u591c\u91cc\u7684\u65b0\u829c\u5f88\u5b89\u9759\uff0c\u8fd9\u4e5f\u662f\u5f20\u606a\u559c\u6b22\u65b0\u829c\u7684\u5730\u65b9\uff0c\u9664\u4e86\u591c\u751f\u6d3b\u805a\u96c6\u4e4b\u5730\uff0c\u5176\u4ed6\u8857\u9053\u90fd\u5b89\u9759\u3002\u6709\u5929\u4e91\u5c71\u73af\u62a4\u7740\uff0c\u65b0\u829c\u51ac\u5b63\u7684\u6c14\u6e29\u6781\u5c11\u4f1a\u964d\u5230\u96f6\u5ea6\u4ee5\u4e0b\uff0c\u4e0d\u50cf\u5efa\u90ba\u7684\u51ac\u5929\u6e7f\u5bd2\u523a\u9aa8\uff0c\u8fd9\u79cd\u5929\u6c14\u4f4f\u5728\u65b0\u829c\uff0c\u591c\u91cc\u7761\u89c9\u90fd\u4e0d\u9700\u8981\u95ed\u7d27\u95e8\u7a97\u3002</p><p>\u3000\u3000\u5c0f\u8f66\u9a76\u5165\u5c0f\u533a\uff0c\u5927\u6982\u542c\u5230\u8f66\u5587\u53ed\u58f0\uff0c\u5f20\u606a\u900f\u8fc7\u8f66\u7a97\u770b\u5230\u4ed6\u7238\u5988\u4ece\u5ba2\u5385\u91cc\u8d70\u51fa\u6765\u3002\u6362\u4f5c\u5f80\u65f6\uff0c\u5510\u5a67\u80af\u5b9a\u4f1a\u62a2\u7740\u4e0b\u8f66\uff0c\u5979\u8fd9\u65f6\u5019\u5374\u6709\u7740\u65b0\u5ab3\u89c1\u516c\u5a46\u7684\u5a07\u7f9e\u4e0e\u5fc3\u865a\uff0c\u62c9\u7740\u5f20\u606a\u7684\u624b\u81c2\uff0c\u8eb2\u5728\u4ed6\u540e\u9762\u4e0b\u8f66\u3002\u5f20\u606a\u4e0b\u4e86\u8f66\uff0c\u624d\u770b\u5230\u5ba2\u5385\u91cc\u8fd8\u6709\u5176\u4ed6\u4eba\u5728\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u5012\u6ca1\u6709\u70ed\u5207\u7684\u8d70\u51fa\u6765\u8fce\u63a5\uff0c\u770b\u5230\u8f66\u9a76\u8fdb\u9662\u5b50\uff0c\u5c31\u5e2e\u7740\u536b\u5170\u5979\u5988\u4e00\u8d77\u5c06\u83dc\u7aef\u5230\u9910\u684c\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4eb2\u81ea\u63d0\u7740\u884c\u674e\u7bb1\u8fdb\u5ba2\u5385\uff0c\u5d14\u4e39\u9752\u4e0e\u536b\u751f\u8d70\u8fc7\u6765\u8981\u5e2e\u5fd9\u63a5\u8fc7\u6765\u3002</p><p>\u3000\u3000\u536b\u5170\u8fd8\u672a\u8131\u5c11\u5973\u7684\u6c14\u8d28\uff0c\u7565\u4e30\u8174\u7684\u74dc\u5b50\u8138\uff0c\u6216\u8005\u8bf4\u6709\u4e9b\u5a74\u513f\u80a5\uff0c\u674f\u4ec1\u578b\u7684\u5927\u773c\u775b\uff0c\u79c0\u76f4\u7684\u9f3b\u5b50\uff0c\u6574\u4e2a\u4eba\u6709\u7740\u6676\u83b9\u5254\u900f\u7684\u79c0\u7f8e\uff0c\u5012\u662f\u89c9\u5f97\u5979\u4eca\u5929\u770b\u81ea\u5df1\u7684\u773c\u795e\u6709\u7740\u4e0d\u5bf9\u52b2\uff0c\u95ea\u95ea\u8eb2\u8eb2\u7684\u3002</p><p>\u3000\u3000\u5d14\u4e39\u9752\u7a7f\u7740\u5496\u5561\u8272\u7684\u534a\u957f\u5916\u5957\uff0c\u5373\u4f7f\u8170\u95f4\u7cfb\u7740\u56f4\u88d9\u521a\u624d\u5728\u5e2e\u5fd9\u5e72\u6d3b\uff0c\u8eab\u6750\u9ad8\u6311\u7684\u5979\u4e5f\u4f9d\u65e7\u6c14\u8d28\u4f18\u96c5\u800c\u8273\u4e3d\uff0c\u7738\u5149\u6e05\u4eae\uff0c\u7ea2\u6da6\u7684\u5634\u5507\u6563\u53d1\u51fa\u5973\u6027\u7684\u6781\u81f4\u9b44\u529b\uff0c\u60f3\u8d77\u7fdf\u4e39\u9752\u8fc7\u4e86\u6625\u8282\u4e5f\u8981\u8fdb\u4e1c\u5927\u5c31\u8bfb\u4e1c\u5927\u7684\u6625\u5b63MBA\u73ed\uff0c\u591a\u4e86\u4e00\u4e2a\u7fdf\u4e39\u9752\uff0c\u5efa\u90ba\u7684\u6625\u5b63\u8fd8\u8981\u70ed\u95f9\u51e0\u5206\uff0c\u5f20\u606a\u5374\u611f\u89c9\u6709\u4e9b\u5934\u75bc\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u7740\u884c\u674e\u7bb1\u8ba9\u4e86\u8ba9\uff0c\u4e0d\u8ba9\u5979\u4eec\u5e2e\u5fd9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u95ea\u4e86\u4f60\u4eec\u7684\u8170\uff0c\u6211\u53ef\u8d54\u4e0d\u8d77\u3002\u201d\u6682\u65f6\u5148\u5c06\u884c\u674e\u7bb1\u4e22\u5728\u5ba2\u5385\u7684\u4e00\u89d2\uff0c\u5728\u8def\u4e0a\u5750\u8f66\u7684\u65f6\u95f4\u592a\u957f\uff0c\u90fd\u997f\u762a\u4e86\uff0c\u60f3\u5fc5\u4ed6\u4eec\u5728\u65b0\u829c\u7b49\u7684\u65f6\u95f4\u592a\u957f\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u6253\u7b97\u53ea\u8ba9\u5085\u4fca\u7559\u4e0b\u6765\u7528\u9910\uff0c\u8ba9\u5176\u4ed6\u968f\u884c\u4eba\u5458\u76f4\u63a5\u4f4f\u8fdb\u9152\u5e97\u53bb\uff0c\u5012\u662f\u4ed6\u7238\u5f20\u77e5\u884c\u5148\u5f00\u53e3\u5c06\u6240\u6709\u7684\u4eba\u90fd\u633d\u7559\u4e0b\u6765\u5403\u996d\uff0c\u5f20\u606a\u4fbf\u8ba9\u4ed6\u4eec\u90fd\u7559\u4e0b\u6765\u5403\u8fc7\u665a\u996d\u518d\u53bb\u9152\u5e97\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8bf4\u4e0a\u5348\u5341\u70b9\u949f\u5c31\u80fd\u51fa\u53d1\u7684\uff1f\u8fd9\u65f6\u5019\u90fd\u8fc7\u516b\u70b9\u4e86\uff0c\u201d\u8fdb\u4e86\u9910\u5385\u5750\u4e0b\uff0c\u5f20\u77e5\u884c\u4f38\u624b\u6572\u4e86\u6572\u624b\u8155\u4e0a\u7684\u624b\u8868\uff0c\u8ba9\u5f20\u606a\u770b\u624b\u8868\u4e0a\u663e\u793a\u7684\u65f6\u95f4\uff0c\u201c\u4e3a\u4e86\u7b49\u4f60\uff0c\u6211\u4eec\u5927\u5bb6\u7684\u809a\u5b50\u90fd\u997f\u762a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u6ca1\u8138\u8bf4\u81ea\u5df1\u662f\u8003\u8bd5\u4f5c\u5f0a\u7ed9\u6293\u4f4f\u4e86\uff0c\u5fc3\u91cc\u5728\u60f3\u627e\u4ec0\u4e48\u501f\u53e3\u7cca\u5f04\u8fc7\u53bb\u3002\u5510\u5a67\u4e56\u5de7\u7684\u6253\u8fc7\u62db\u547c\u4e4b\u540e\u5c31\u8f7b\u677e\u4e0b\u6765\uff0c\u8fd9\u65f6\u5019\u4e5f\u5c31\u4e0d\u7559\u60c5\u9762\u7684\u6233\u5f20\u606a\u7684\u8001\u5e95\uff0c\u63f6\u63c4\u7684\u8bf4\u9053\uff1a\u201c\u5f20\u606a\u4eca\u5929\u4e86\u4e0d\u8d77\u5462\uff0c\u672c\u6765\u6211\u4eec\u8ba1\u5212\u597d\u5341\u70b9\u949f\u4e4b\u524d\u5c31\u80fd\u52a8\u8eab\uff0c\u4f46\u662f\u4ed6\u4e0a\u5348\u8003\u8bd5\u65f6\u5077\u770b\u7ed9\u6349\u4f4f\u4e86\uff0c\u8fd8\u80cc\u4e86\u4e00\u4e2a\u8b66\u544a\u5904\u5206\uff0c\u6211\u4eec\u53ea\u80fd\u5728\u5efa\u90ba\u5403\u8fc7\u4e2d\u996d\u518d\u51fa\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e0e\u6881\u683c\u73cd\u8c14\u7136\u76f8\u671b\uff0c\u542c\u5230\u8fd9\u79cd\u4e8b\u90fd\u4e0d\u6653\u5f97\u8be5\u6709\u600e\u6837\u7684\u8868\u60c5\u624d\u5408\u9002\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u751f\u5979\u4eec\u9762\u9762\u76f8\u89d1\uff0c\u7adf\u7136\u662f\u4e3a\u8fd9\u4e8b\u803d\u6401\u4e86\u884c\u7a0b\u8ba9\u4ed6\u4eec\u7a7a\u8179\u4e86\u7b49\u5230\u516b\u70b9\u949f\uff1f</p><p>\u3000\u3000\u201c\u4ee5\u540e\u771f\u662f\u8981\u5c06\u4f60\u7684\u5c0f\u5634\u5df4\u62ff\u80f6\u5e03\u7c98\u4e0a\u624d\u6562\u5e26\u4f60\u5230\u65b0\u829c\u6765\u2026\u2026\u201d\u5f20\u606a\u4f38\u624b\u8981\u53bb\u6390\u5510\u5a67\u7684\u5c0f\u86ee\u8170\uff0c\u201c\u6211\u5c31\u8fd9\u70b9\u4e22\u4eba\u7684\u4e8b\u60c5\uff0c\u4f60\u8fd8\u5e2e\u6211\u5230\u5904\u5ba3\u626c\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u8863\u670d\u539a\uff0c\u626d\u7740\u8170\u8eb2\u5f00\uff0c\u5a07\u7b11\u7740\u8eb2\u5230\u6881\u683c\u73cd\u7684\u8eab\u8fb9\uff0c\u8bf4\u9053\uff1a\u201c\u6881\u59e8\uff0c\u5f20\u606a\u6ca1\u4e8b\u8001\u6b3a\u8d1f\u6211\uff0c\u5728\u5efa\u90ba\u90fd\u6ca1\u6709\u4eba\u5e2e\u6211\uff0c\u5c31\u53ea\u80fd\u7ed9\u4ed6\u6b3a\u8d1f\uff0c\u5230\u65b0\u829c\u4f60\u53ef\u8981\u5e2e\u6211\u6b3a\u8d1f\u56de\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u6390\u7740\u5f20\u606a\u7684\u80f3\u818a\u8ba9\u4ed6\u5750\u597d\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u8003\u8bd5\u5077\u770b\u7ed9\u6349\u5230\u4e86\u8fd8\u597d\u610f\u601d\u62ff\u80f6\u5e03\u7c98\u522b\u4eba\u7684\u5634\u2026\u2026\u4f60\u8981\u6562\u6b3a\u8d1f\u5c0f\u5a67\uff0c\u4f60\u770b\u6211\u600e\u4e48\u6536\u62fe\u4f60\u3002\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u624d\u7b11\u7740\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8ddf\u59bb\u5b50\u8bf4\u9053\uff1a\u201c\u771f\u662f\u60b2\u54c0\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u4ee5\u540e\u90fd\u5168\u7136\u4eab\u53d7\u4e0d\u5230\u7236\u6bcd\u6559\u8bad\u5b50\u5973\u7684\u4e50\u8da3\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u513f\u5b50\uff0c\u6211\u65e9\u5c31\u7070\u5fc3\u4e27\u6c14\u7684\u60f3\u4e22\u6389\u7b97\u4e86\uff0c\u4f60\u770b\u4ed6\u8001\u6c14\u6a2a\u79cb\u7684\u6837\u5b50\uff0c\u4e00\u70b9\u90fd\u6ca1\u6709\u5c0f\u65f6\u5019\u53ef\u7231\uff0c\u4e00\u76f4\u5f88\u540e\u6094\u5f53\u65f6\u600e\u4e48\u4e0d\u518d\u751f\u4e2a\u5973\u513f\uff0c\u5e78\u597d\u6709\u5c0f\u5a67\uff0c\u8fd8\u6709\u536b\u5170\uff0c\u201d\u6881\u683c\u73cd\u8ba9\u5510\u5a67\u5a07\u61a8\u7684\u4f9d\u5728\u6000\u91cc\uff0c\u53c8\u62c9\u8d77\u5750\u5728\u5979\u8eab\u8fb9\u7684\u536b\u5170\u7684\u624b\uff0c\u671d\u5f20\u606a\u7684\u626c\u4e86\u626c\uff0c\u201c\u5fd8\u4e86\u5728\u7535\u8bdd\u91cc\u901a\u77e5\u4f60\uff0c\u4f60\u591a\u4e86\u59d0\u59d0\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u62ac\u5934\u770b\u7740\u536b\u5170\u8138\u5e26\u5a07\u7f9e\uff0c\u4ed6\u6653\u5f97\u4ed6\u5988\u4ece\u90a3\u5e74\u590f\u5929\u536b\u5170\u7ed9\u7f57\u5f52\u6e90\u90a3\u4e2a\u6742\u79cd\u513f\u5b50\u6b3a\u8d1f\u540e\u5c31\u60f3\u6536\u5979\u505a\u5e72\u5973\u513f\uff0c\u536b\u5170\u90a3\u65f6\u5019\u6ca1\u6709\u7b54\u5e94\uff0c\u540e\u6765\u4e24\u5bb6\u5173\u7cfb\u5bc6\u5207\u4e86\uff0c\u6536\u4e0d\u6536\u5e72\u5973\u513f\u7684\u4e8b\u60c5\u4e5f\u4e00\u76f4\u62d6\u7740\u6ca1\u6709\u63d0\u8d77\u8fc7\uff0c\u6ca1\u60f3\u5230\u8fd9\u56de\u5230\u65b0\u829c\u5c31\u591a\u4e86\u4e2a\u59d0\u59d0\uff0c\u600e\u4e48\u8bf4\u521a\u624d\u8fdb\u5c4b\u65f6\u536b\u5170\u7684\u773c\u795e\u95ea\u95ea\u8eb2\u8eb2\u7684\u6709\u4e9b\u4e0d\u5bf9\u52b2\uff1f\u7b11\u7740\u8bf4\uff0c\u201c\u771f\u662f\u610f\u5916\u5462\uff0c\u90a3\u8be5\u662f\u59d0\u59d0\u7ed9\u6211\u89c1\u9762\u793c\uff0c\u8fd8\u662f\u8981\u6211\u7ed9\u59d0\u59d0\u89c1\u9762\u793c\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u536b\u5170\u53ef\u6ca1\u6709\u51c6\u5907\u4ec0\u4e48\u89c1\u9762\u793c\uff0c\u8fd8\u4e0d\u6653\u5f97\u600e\u4e48\u53bb\u9762\u5bf9\u8ddf\u5f20\u606a\u66f4\u4eb2\u8fd1\u4e00\u5c42\u7684\u5173\u7cfb\uff0c\u8ba9\u5f20\u606a\u8fd9\u53e5\u8bdd\u641e\u5f97\u624b\u8db3\u65e0\u63aa\u3001\u9762\u7ea2\u8033\u8d64\uff0c\u5fc3\u614c\u614c\u7684\u770b\u4e86\u770b\u653e\u5728\u4e00\u65c1\u7684\u624b\u888b\uff0c\u60f3\u7740\u624b\u888b\u91cc\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53ef\u62ff\u6765\u505a\u89c1\u9762\u793c\u7684\u4e1c\u897f\u3002</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u6d51\u5c0f\u5b50\uff0c\u89c1\u9762\u5c31\u8981\u89c1\u9762\u793c\uff0c\u8981\u4e0d\u8981\u8001\u5a18\u8d4f\u4f60\u4e2a\u5df4\u638c\uff1f\u201d\u6881\u683c\u73cd\u8f7b\u8f7b\u7684\u513f\u5b50\u8138\u4e0a\u6247\u4e86\u4e00\u8bb0\uff0c\u62c9\u4e86\u62c9\u536b\u5170\u7684\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8981\u542c\u4ed6\u80e1\u8bf4\u516b\u9053\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u5510\u5a67\u5174\u594b\u7684\u62c9\u8d77\u536b\u5170\u7684\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u662f\u4e0d\u662f\u8bf4\u6211\u4ee5\u540e\u4e5f\u6709\u4e2a\u59d0\u59d0\uff1f\u201d</p><p>\u3000\u3000\u201c\u62dc\u6258\uff0c\u4f60\u53c8\u4e0d\u662f\u6211\u5988\u7684\u5973\u513f\uff0c\u201d\u5f20\u606a\u5c06\u5510\u5a67\u62c9\u5230\u8eab\u8fb9\uff0c\u5c06\u5f20\u7259\u821e\u722a\u7684\u5510\u5a67\u6309\u5728\u8eab\u8fb9\u5750\u4e0b\u6765\uff0c\u201c\u5c31\u7b97\u4f60\u6025\u7740\u8fdb\u6211\u5bb6\u7684\u95e8\uff0c\u4e5f\u662f\u5927\u59d1\u5b50\u4e0e\u5f1f\u5ab3\u7684\u5173\u7cfb\uff0c\u4f60\u4eec\u7b97\u54ea\u95e8\u5b50\u59d0\u59b9\uff1f\u4e0d\u8981\u7fa1\u6155\u6211\uff0c\u4f60\u7fa1\u6155\u4e5f\u7fa1\u6155\u4e0d\u6765\uff0c\u6709\u672c\u4e8b\u8ba9\u4f60\u5988\u8ba4\u4e2a\u5e72\u5973\u513f\u53bb\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u4f38\u624b\u8981\u6390\u5f20\u606a\u7684\u8138\u4e0d\u8ba9\u4ed6\u80e1\u8bf4\u516b\u9053\uff0c\u5f20\u606a\u7b11\u7740\u95ea\u8eb2\uff0c\u8bf4\u5510\u5a67\u8981\u80fd\u62ff\u51fa\u89c1\u9762\u793c\u6765\uff0c\u5c31\u7b97\u5979\u591a\u4e86\u4e2a\u59d0\u59d0\u3002\u5510\u5a67\u4ece\u53e3\u888b\u91cc\u638f\u51fa\u5979\u4e00\u8def\u4e0a\u90fd\u5728\u4e0e\u5f20\u606a\u4e00\u8d77\u542c\u6b4c\u7684iplayer\u53cc\u624b\u9012\u7ed9\u536b\u5170\uff0c\u8ba4\u771f\u53c8\u7d27\u5f20\u7684\u8bf4\uff1a\u201c\u4e00\u5b9a\u8981\u8ba4\u6211\u8fd9\u4e2a\u59b9\u59b9\u554a\uff0c\u6211\u4ece\u5c0f\u5c31\u76fc\u671b\u80fd\u6709\u4e2a\u54e5\u54e5\u6216\u59d0\u59d0\u4e86\uff0c\u4ee5\u540e\u5c31\u4e0d\u7528\u6574\u5929\u7ed9\u8fd9\u4e2a\u732a\u5934\u6b3a\u8d1f\u4e86\u3002\u201d\u89c1\u536b\u5170\u5fc3\u614c\u614c\u7684\u4e0d\u6653\u5f97\u600e\u4e48\u529e\u624d\u597d\uff0c\u5c31\u5c06ipayer\u585e\u5979\u624b\u91cc\uff0c\u62ff\u53c8\u53ef\u601c\u53c8\u65e0\u8f9c\u7684\u773c\u795e\u770b\u7740\u5979\u3002</p><p>\u3000\u3000\u536b\u5170\u5979\u5988\u7b11\u5bb9\u6ee1\u9762\uff0c\u770b\u7740\u5f20\u606a\u4e00\u5bb6\u4eba\u7b11\u95f9\uff0c\u5979\u6734\u8d28\u800c\u4e0d\u5584\u8a00\u8f9e\uff1b\u7fdf\u4e39\u9752\u5e2e\u8154\u8bf4\u536b\u5170\u660e\u5929\u518d\u51c6\u5907\u89c1\u9762\u793c\u4e0d\u8fdf\uff0c\u6881\u683c\u73cd\u8ba9\u536b\u5170\u5c06\u4e1c\u897f\u6536\u4e0b\u6765\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u591a\u4e86\u4e24\u4e2a\u5973\u513f\uff0c\u5c31\u7b97\u5c06\u8fd9\u4e2a\u6ca1\u826f\u5fc3\u7684\u513f\u5b50\u4e22\u6389\u90fd\u4e0d\u4f1a\u5fc3\u75bc\u4e86\uff0c\u201d\u53c8\u5bf9\u5510\u5a67\u8bf4\u9053\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u671d\u5357\u7684\u623f\u95f4\u901a\u900f\uff0c\u88ab\u8925\u6211\u8ddf\u536b\u5170\u90fd\u5e2e\u4f60\u6652\u8fc7\u4e86\uff0c\u8ba9\u5f20\u606a\u7761\u5317\u9762\u7684\u623f\u95f4\u3002\u201d</p><p>\u3000\u3000\u5c31\u6653\u5f97\u5230\u65b0\u829c\u540e\u591c\u91cc\u8fd8\u662f\u8981\u5206\u5f00\u7761\uff0c\u5f20\u606a\u65e0\u58f0\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u771f\u53eb\u4eba\u75db\u82e6\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7528\u8fc7\u9910\uff0c\u7fdf\u4e39\u9752\u3001\u536b\u5170\u8fd8\u6709\u536b\u5170\u5979\u5988\u4ee5\u53ca\u5085\u4fca\u7b49\u968f\u884c\u4eba\u5458\u90fd\u79bb\u5f00\u4e86\uff1b\u7fdf\u4e39\u9752\u8fd8\u8981\u627e\u5f20\u606a\u6c47\u62a5\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u4ec0\u4e48\u4e8b\u60c5\u90fd\u8981\u7b49\u5230\u660e\u5929\u518d\u8bf4\uff0c\u5f20\u606a\u53ef\u4e0d\u60f3\u81ea\u5df1\u7ed9\u538b\u8feb\u5f97\u4e00\u53e3\u6c14\u90fd\u4e0d\u6653\u5598\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u8fd8\u6709\u4ed6\u7238\u5988\u5750\u5728\u4e8c\u697c\u7684\u8d77\u5c45\u5ba4\u91cc\u770b\u7535\u89c6\uff0c\u6881\u683c\u73cd\u8ba9\u5510\u5a67\u5750\u5979\u8eab\u8fb9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u5988\u5c31\u5f88\u7fa1\u6155\u6211\u6536\u4e86\u5e72\u5973\u513f\uff0c\u4e00\u5b9a\u4e5f\u4f1a\u7fa1\u5e55\u6211\u4eec\u80fd\u8fd9\u6837\u5b50\u5750\u5728\u4e00\u8d77\u770b\u7535\u89c6\u2026\u2026\u201d</p><p>\u3000\u3000\u7535\u89c6\u91cc\u6b63\u64ad\u653e\u592e\u89c6\u4e00\u5957\u8bb2\u8ff0\u9999\u6e2f\u51e0\u4ee3\u4eba\u8f9b\u9178\u5386\u53f2\u7684\u7535\u89c6\u5267\u300a\u9999\u6e2f\u7684\u6545\u4e8b\u300b\uff0c\u5f20\u606a\u4e0e\u4ed6\u7238\u7238\u90fd\u4e0d\u662f\u6709\u65f6\u95f4\u80fd\u5750\u4e0b\u6765\u6bcf\u5929\u8ffd\u770b\u7535\u89c6\u8fde\u7eed\u5267\u7684\u4eba\uff0c\u786c\u7740\u5934\u76ae\u5728\u7535\u89c6\u524d\u5750\u4e86\u7247\u523b\uff0c\u770b\u4e0d\u51fa\u4ec0\u4e48\u5934\u7eea\u6765\uff0c\u5c31\u8d25\u4e0b\u9635\u6765\u64a4\u5230\u9633\u53f0\u4e0a\uff0c\u7559\u5510\u5a67\u966a\u4ed6\u5988\u5728\u8d77\u5c45\u5ba4\u91cc\u770b\u7535\u89c6\u3002</p><p>\u3000\u3000\u65b0\u829c\u7684\u51ac\u591c\u4e00\u70b9\u90fd\u4e0d\u89c9\u5f97\u5bd2\u51b7\uff0c\u6ca1\u6709\u6253\u5f00\u5eca\u706f\uff0c\u5f53\u7a7a\u660e\u6708\u6e05\u51b7\u7684\u5149\u8f89\u6d12\u4e0b\u6765\uff0c\u8db3\u4ee5\u6620\u7167\u5f97\u4eba\u7ea4\u6beb\u6bd5\u5448\uff0c\u5f20\u606a\u89c1\u4ed6\u7238\u8138\u4e0a\u7565\u6709\u5026\u8272\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5982\u4eca\u771f\u6b63\u7b97\u5e02\u9886\u5bfc\u4e86\uff0c\u5988\u6709\u6ca1\u6709\u5acc\u4f60\u592a\u52b3\u788c\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u80fd\u4e0d\u5acc\uff1f\u65b0\u829c\u7684\u7ecf\u6d4e\u624d\u6709\u8f83\u5927\u7684\u8fdb\u5c55\uff0c\u80fd\u4e0d\u80fd\u9152\u4e1a\u5174\u65b0\u829c\u3001\u65c5\u6e38\u5174\u65b0\u829c\uff0c\u8981\u505a\u7684\u5de5\u4f5c\u592a\u591a\u3001\u592a\u6742\uff0c\u4f60\u5988\u5728\u65b0\u829c\u8ba4\u8bc6\u7684\u4eba\u4e5f\u4e0d\u591a\uff0c\u8fd9\u624d\u6b7b\u7f20\u70c2\u6253\u5c06\u536b\u5170\u6536\u4f5c\u5e72\u5973\u513f\uff0c\u201d\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8bf4\u4f60\u8fd9\u4e2a\u513f\u5b50\u517b\u4e86\u6709\u4ec0\u4e48\u7528\u5904\uff1f\u4ec0\u4e48\u90fd\u4e0d\u7528\u6211\u4eec\u7275\u6302\uff0c\u6211\u8ddf\u4f60\u5988\u53ef\u5168\u7136\u6ca1\u6709\u5f53\u7236\u6bcd\u7684\u4e50\u8da3\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u5176\u5b9e\u6bd4\u8d77\u90a3\u4e9b\u6b63\u5904\u4e8e\u9752\u6625\u53db\u9006\u671f\u7684\u5c0f\u5b69\u6765\u8bf4\uff0c\u6211\u53ef\u4ee5\u86ee\u613f\u610f\u542c\u4f60\u8ddf\u5988\u5988\u4e24\u4e2a\u4eba\u5520\u53e8\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u90a3\u4eca\u5929\u665a\u4e0a\u5c31\u7b97\u4e86\uff0c\u660e\u5929\u4f60\u6709\u65f6\u95f4\u542c\u6211\u5520\u53e8\u5520\u53e8\u5427\u3002\u201d\u5f20\u77e5\u884c\u62cd\u4e86\u62cd\u5f20\u606a\u7684\u80a9\u8180\uff0c\u5176\u4ed6\u505a\u7236\u6bcd\u5728\u770b\u5230\u5b50\u5973\u957f\u5230\u8ddf\u81ea\u5df1\u5dee\u4e0d\u591a\u9ad8\u7684\u65f6\u5019\u90fd\u4f1a\u611f\u6168\u5b50\u5973\u957f\u5927\u4e86\uff0c\u6709\u5f20\u606a\u8fd9\u6837\u7684\u513f\u5b50\u5012\u597d\uff0c\u6839\u672c\u5c31\u4ea7\u751f\u4e0d\u4e86\u8fd9\u6837\u7684\u611f\u6168\u554a\u3002</p></article>"}}}]);
